(window.webpackJsonp=window.webpackJsonp||[]).push([[142],{165:function(p){p.exports={chapterTitle:null,title:"\u7b2c142\u7ae0 \u770b\u7a7f\u5e95\u724c\u7684\u8c08\u5224",content:"<article><h2>\u7b2c142\u7ae0 \u770b\u7a7f\u5e95\u724c\u7684\u8c08\u5224</h2><p>\u3000\u3000\u6b64\u65f6\u7684\u7231\u8fbe\u7535\u5b50\u8ddf\u4e24\u4e2a\u6708\u524d\u8fe5\u7136\u4e0d\u540c\uff0c\u8981\u662f\u7ed9\u76ca\u9686\u9547\u653f\u5e9c\u6d3e\u51fa\u5916\u5730\u89e3\u51b3\u4e09\u89d2\u503a\u52a1\u7684\u539f\u58f0\u8fbe\u7535\u5b50\u5382\u5382\u957f\u9648\u5f3a\u56de\u6765\uff0c\u53ea\u6015\u672a\u5fc5\u80fd\u8ba4\u51fa\u6765\u3002\u5382\u524d\u8349\u576a\u5730\u91cd\u65b0\u6574\u996c\u8fc7\uff0c\u4e0e\u516c\u8def\u4e4b\u95f4\u7684\u6ce5\u6cde\u7a7a\u65b0\u94fa\u4e86\u4e00\u6bb5\u6ca5\u9752\uff0c\u7ea2\u7816\u88f8\u780c\u7684\u56f4\u5899\u7c89\u62b9\u8fc7\uff0c\u53c8\u52a0\u4e86\u4e00\u5c42\u6de1\u9ec4\u8272\u7684\u6d82\u6599\uff0c\u6574\u95f4\u5de5\u5382\u90fd\u52a0\u6d82\u6d82\u6599\uff0c\u589e\u52a0\u6807\u8bc6\u7cfb\u7edf\uff0c\u5982\u8b66\u793a\u3001\u7981\u6b62\u6807\u8bc6\u7b49\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u4f9d\u7167\u8d28\u91cf\u7ba1\u7406\u4f53\u7cfb1SO9001\u7684\u6807\u51c6\u6574\u8083\u7535\u5b50\u5382\u7684\u751f\u4ea7\u7ba1\u7406\uff0c\u6539\u5584\u5de5\u5382\u5de5\u4f5c\u73af\u5883\uff0c\u867d\u7136\u8fd8\u662f\u8fd9\u5bb6\u5360\u5730\u4e0d\u5230\u4e09\u5341\u4ea9\u7684\u5c0f\u5382\u5b50\uff0c\u5374\u7ed9\u4eba\u8033\u76ee\u4e00\u65b0\u7684\u611f\u89c9\uff0c\u6ca1\u6709\u5bf9\u751f\u4ea7\u7ebf\u6295\u5165\u6539\u8fdb\uff0c\u4ec5\u4ec5\u4f9d\u9760\u8d28\u91cf\u7ba1\u7406\uff0c\u5c31\u8ba9\u751f\u4ea7\u7ebf\u4e0b\u6765\u7684\u6536\u5f55\u673a\u4fdd\u6301\u7a33\u5b9a\u7684\u8d28\u91cf\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u5728\u8fdb\u7231\u8fbe\u7535\u5b50\u5382\u7684\u7814\u53d1\u5ba4\u4e4b\u524d\uff0c\u7531\u5f20\u606a\u966a\u7740\u5230\u8f66\u95f4\u8f6c\u4e86\u4e00\u5708\uff0c\u77ed\u6682\u7684\u65f6\u95f4\uff0c\u89c9\u5f97\u7231\u8fbe\u7535\u5b50\u5382\u867d\u7136\u5c0f\uff0c\u4f46\u662f\u751f\u4ea7\u7ba1\u7406\u786e\u5b9a\u4e0d\u9519\uff0c\u653e\u773c\u56fd\u5185\u90fd\u8981\u7b97\u662f\u597d\u7684\u3002</p><p>\u3000\u3000\u968f\u4e01\u69d0\u4ece\u5408\u80a5\u4e07\u71d5\u8df3\u5230\u7231\u8fbe\u7535\u5b50\u7684\u5de5\u7a0b\u5e08\u6709\u516d\u4eba\uff0c\u52a0\u4e0a\u7231\u8fbe\u7535\u5b50\u4e4b\u524d\u81ea\u8eab\u57f9\u517b\u51fa\u6765\u7684\u4e94\u540d\u6280\u672f\u5458\uff0c\u7231\u8fbe\u7535\u5b50\u6280\u672f\u90e8\u5171\u6709\u5341\u4e8c\u540d\u5458\u5de5\uff0c\u8fd9\u6216\u8bb8\u7b97\u4e0d\u4e0a\u4ec0\u4e48\uff0c\u4f46\u5bf9\u4e8e\u4e00\u5bb6\u53ea\u6709\u516d\u5341\u540d\u5de5\u4eba\u7684\u5c0f\u5382\uff0c\u7231\u8fbe\u7535\u5b50\u5728\u6280\u672f\u4e0a\u7684\u6295\u5165\u6bd4\u4f8b\u5c31\u76f8\u5f53\u60ca\u4eba\uff0c\u800c\u9ad8\u5b66\u5386\u6240\u5360\u6bd4\u4f8b\u4e5f\u662f\u56fd\u5185\u5c11\u6709\u7684\u3002</p><p>\u3000\u3000\u7684\u786e\uff0c\u5f20\u606a\u76ee\u524d\u53ea\u652f\u4ed8\u7ed9\u76ca\u9686\u9547\u653f\u5e9c\u9996\u4ed8\u6b3e20\u4e07\u5143\uff0c\u6295\u516520\u4e07\u7528\u4e8e\u6539\u9020\u751f\u4ea7\u8bbe\u65bd\uff0c\u6837\u673a\u5f00\u53d1\u4ed6\u5dee\u4e0d\u591a\u7ed9\u4e86\u4e00\u767e\u4e07\u8d44\u91d1\u3002\u8fd9\u5bf9\u52a8\u8f84\u6295\u5165\u4e0a\u5343\u4e07\u7f8e\u5143\u8fdb\u884c\u67d0\u9879\u4ea7\u54c1\u7814\u53d1\u7684TI\u516c\u53f8\uff0c\u5b9e\u5728\u662f\u5927\u5deb\u89c1\u5c0f\u5deb\uff0c\u4f46\u5bf9\u4e8e\u540c\u65f6\u8fdb\u5165\u5f71\u789f\u673a\u5236\u9020\u884c\u4e1a\u7684\u5176\u4ed6\u56fd\u5185\u4ea7\u4e1a\uff0c\u7231\u8fbe\u7535\u5b50\u4e5f\u7b97\u6709\u4e0d\u9519\u7684\u8d77\u6b65\u3002</p><p>\u3000\u3000\u5f20\u606a\u731c\u6d4b\u7231\u5409\u4f2f\u4e00\u65e6\u5bf9\u4ed6\u624b\u91cc\u7684\u6280\u89e3\u51b3\u65b9\u6848\u611f\u5174\u8da3\uff0c\u5c31\u4f1a\u6d3e\u4eba\u5230\u5382\u5b50\u91cc\u6765\u8003\u5bdf\uff0c\u4ed6\u7279\u610f\u8ba9\u6280\u672f\u90e8\u5728C-Cube\u7b2c\u4e00\u4ee3\u5b9a\u578b\u89e3\u7801\u82af\u7247\u7684\u57fa\u7840\u4e0a\u8d76\u5236\u51fa\u4e00\u53f0\u6837\u673a\uff0c\u76ee\u524d\u4e07\u71d5\u5c31\u662f\u91c7\u7528\u8fd9\u79cd\u6280\u672f\uff0c\u8fd9\u5bf9\u4e01\u69d0\u624b\u4e0b\u7684\u8fd9\u5e2e\u5de5\u7a0b\u5e08\u800c\u8a00\uff0c\u6ca1\u6709\u592a\u5927\u7684\u96be\u5ea6\uff0c\u53ea\u9700\u6c42\u63d0\u4f9b\u76f8\u5e94\u7684\u5143\u914d\u4ef6\u5c31\u80fd\u5728\u5f88\u77ed\u7684\u65f6\u95f4\u5185\u505a\u5230\uff0c\u800c\u4e14\u80fd\u5b8c\u73b0\u76ee\u524d\u5f71\u789f\u673a\u7684\u6240\u6709\u529f\u80fd\u3002</p><p>\u3000\u3000TI\u516c\u53f8\u8981\u7814\u53d1\u7684\u65b0\u4e00\u4ee3\u82af\u7247\u5c31\u8981\u5c06\u4e00\u4e9b\u5e38\u89c4\u529f\u80fd\u96c6\u6210\u5230\u89e3\u7801\u82af\u7247\u4e0a\uff0c\u89e3\u7801\u82af\u7247\u7684\u96c6\u6210\u5ea6\u8d8a\u9ad8\uff0c\u6700\u7ec8\u7684\u6574\u673a\u8d28\u91cf\u8d8a\u7a33\u5b9a\u3002</p><p>\u3000\u3000TI\u516c\u53f8\u8981\u662f\u62ff\u7740\u4e07\u71d5\u516c\u53f8\u7684\u5b9a\u578b\u673a\u56de\u53bb\u89e3\u5256\u7814\u7a76\uff0c\u4e5f\u80fd\u5206\u6790\u51fa\u4e07\u71d5\u6240\u91c7\u7528\u7684\u6240\u6709\u751f\u4ea7\u6280\u672f\uff0c\u4f46\u662f\u9700\u8981\u76f8\u5f53\u957f\u7684\u65f6\u95f4\uff0c\u8981\u662f\u5f88\u7b80\u5355\u7684\u8bdd\uff0cTI\u516c\u53f8\u5f53\u521d\u4e5f\u4e0d\u4f1a\u8f7b\u6613\u7684\u534a\u9014\u800c\u5e9f\uff0c\u65e2\u7136\u91cd\u65b0\u6539\u53d8\u5bf9\u65b0\u5174\u56fd\u5bb6\u5e02\u573a\u7684\u8ba4\u8bc6\uff0c\u7279\u522b\u5728TI\u5df2\u7ecf\u5ef6\u8bef\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\u4e4b\u540e\uff0c\u4e00\u5207\u80fd\u7f29\u77ed\u7814\u53d1\u8fc7\u7a0b\u7684\u624b\u6bb5\u90fd\u662f\u503c\u5f97\u8003\u8651\u7684\u3002</p><p>\u3000\u3000\u9650\u4e8e\u4e07\u71d5\u53ea\u80fd\u5411C-Cube\u516c\u53f8\u516c\u5f00\u751f\u4ea7\u6280\u672f\u7684\u7ea6\u5b9a\uff0c\u4e07\u71d5\u4e0d\u53ef\u80fd\u76f4\u63a5\u5411TI\u516c\u53f8\u63d0\u4f9b\u65b0\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u7684\u6280\u672f\u89e3\u51b3\u65b9\u6848\u3002\u6700\u91cd\u8981\u7684\u662f\uff0c\u8981\u4e0d\u662f\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u7b49\u4eba\u4ece\u4e07\u71d5\u51fa\u6765\uff0c\u8981\u4e0d\u662f\u5f20\u606a\u5728\u4ed6\u5927\u5b66\u7684\u8bfe\u5802\u4e0a\u5bf9\u56fd\u5185\u5f71\u789f\u673a\u4ea7\u4e1a\u4e13\u5229\u95ee\u9898\u6709\u8fc7\u6df1\u5165\u7684\u63a2\u8ba8\uff0c\u5916\u754c\u6682\u65f6\u8fd8\u65e0\u4eba\u77e5\u9053C-Cube\u7684\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u5fc3\u7684\u6280\u672f\u65b9\u6848\u5176\u5b9e\u5c31\u662f\u5bf9\u4e07\u71d5\u5404\u79cd\u72ec\u521b\u6027\u751f\u4ea7\u6280\u672f\u7684\u96c6\u6210\u5316\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u91cc\u8fd9\u4efd\u6280\u672f\u89e3\u51b3\u65b9\u6848\u5bf9\u4e8e\u90a3\u4e9b\u60f3\u8fdb\u519b\u89e3\u7801\u82af\u7247\u5e02\u573a\u7684\u516c\u53f8\u771f\u7684\u5f88\u6709\u4ef7\u503c\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u5de5\u7a0b\u5e08\u5728\u5229\u7528\u7b2c\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u5236\u6210\u7684\u5f71\u789f\u673a\u4e0a\u6f14\u793a\u5b8c\u76ee\u524d\u5f71\u789f\u673a\u6240\u5177\u5907\u6709\u6240\u6709\u529f\u80fd\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5192\u6627\u7684\u7ed9TI\u8463\u4e8b\u957f\u5b89\u5409\u4f2f\u5148\u751f\u53d1\u7b2c\u4e00\u5c01\u7535\u90ae\u7684\u65f6\u5019\uff0c\u6ca1\u6709\u60f3\u5230\u9648\u5148\u751f\u4f1a\u8fd9\u4e48\u5feb\u5c31\u6765\u8bbf\u6d77\u5dde\uff0c\u6211\u4eec\u63d0\u51fa\u6280\u672f\u89e3\u51b3\u65b9\u6848\uff0c\u8fd8\u6709\u8bb8\u591a\u4e0d\u5b8c\u5584\u7684\u5730\u65b9\uff0c\u8981\u4e0d\u518d\u7ed9\u6211\u4eec\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u6211\u4eec\u53ef\u4ee5\u63d0\u4f9b\u4e00\u4efd\u66f4\u5b8c\u5584\u7684\u65b9\u6848\u3002\u201d\u8eab\u4e3aTI\u516c\u53f8\u4e2d\u56fd\u533a\u603b\u88c1\uff0c\u9648\u4fe1\u751f\u5230\u8fd9\u4e48\u5c0f\u7684\u5382\u5b50\u6765\u8003\u5bdf\uff0c\u591a\u5c11\u6709\u4e9b\u5c48\u5c0a\uff0c\u5f20\u606a\u7684\u6001\u5ea6\u8ba9\u4ed6\u5fc3\u7406\u591a\u5c11\u6709\u4e9b\u5f25\u8865\uff0c\u5374\u6ca1\u6709\u6599\u5230\u5f20\u606a\u8bdd\u950b\u4e00\u8f6c\uff0c\u201c\u6211\u4eec\u542c\u8bf4\u6709\u4e2a\u53f0\u6e7e\u534e\u4eba\u5728\u7845\u8c37\u8bbe\u7acb\u7684ESS\u516c\u53f8\u4e5f\u6709\u610f\u8fdb\u5165\u89e3\u7801\u82af\u7247\u5e02\u573a\uff0c\u8ddf\u4ed6\u4eec\u8ba8\u8bba\u8ba8\u8bba\uff0c\u6216\u8bb8\u6709\u5229\u4e8e\u65b9\u6848\u7684\u5b8c\u5584\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u5b9a\u775b\u770b\u7740\u5f20\u606a\uff0c\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u786e\u5b9a\u5f20\u606a\u8fd9\u53e5\u8bdd\u6709\u4e9b\u5a01\u80c1\u7684\u610f\u601d\uff0c\u8bf4\u9053\uff1a\u201cC-Cube\u516c\u53f8\u5c06\u7b2c\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u6280\u672f\u51fa\u552e\u7ed9TI\u65f6\uff0c\u5df2\u7ecf\u7ea6\u5b9a\u4e0d\u80fd\u5411\u7b2c\u4e09\u65b9\u51fa\u552e\u8fd9\u9879\u6280\u672f\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bb0\u5f97\u5230\u4e00\u76f4\u5230\u4e5d\u516b\u5e74\uff0c\u5f71\u789f\u673a\u7684\u4ef7\u683c\u624d\u9010\u6b65\u964d\u5230\u4e24\u5343\u5143\u4ee5\u4e0b\uff0c\u4ece\u8fd9\u4e0a\u9762\u5224\u65adESS\u516c\u53f8\u4e00\u76f4\u5230\u4e5d\u4e03\u5e74\u5e95\u624d\u63a8\u51fa\u6210\u719f\u7684\u89e3\u7801\u82af\u7247\u6253\u7834C-Cube\u7684\u6280\u672f\u5784\u65ad\uff0c\u539f\u6765\u4ed6\u4eec\u662f\u9760\u81ea\u8eab\u7684\u6280\u672f\u529b\u91cf\u4ece\u96f6\u5f00\u59cb\u7814\u53d1\uff0c\u96be\u602a\u901f\u5ea6\u8fd9\u4e48\u6162\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0cESS\u539f\u6765\u662f\u4ece\u5934\u505a\u8d77\uff0c\u90a3\u4ed6\u4eec\u7684\u7814\u53d1\u5de5\u4f5c\u76ee\u524d\u6765\u8bf4\uff0c\u5bf9\u6211\u4eec\u662f\u6ca1\u6709\u5e2e\u52a9\uff0c\u4e0d\u8fc7\u53cd\u8fc7\u6765\uff0c\u6211\u4eec\u5bf9\u4ed6\u4eec\u7684\u5de5\u4f5c\u6216\u8bb8\u6709\u6240\u5e2e\u52a9\u4e5f\u8bf4\u4e0d\u5b9a\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5f20\u606a\u7ad9\u4e86\u8d77\u6765\uff0c\u201c\u9648\u5148\u751f\u4e00\u5230\u6d77\u5dde\u5c31\u5230\u5382\u5b50\u91cc\u6765\uff0c\u665a\u4e0a\u8ba9\u6211\u7565\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u597d\u597d\u62db\u5f85\u4e00\u4e0b\u9648\u5148\u751f\uff0c\u4e0d\u9189\u4e0d\u5f52\u3002\u201d</p><p>\u3000\u3000\u4ece\u77ed\u65f6\u95f4\u7684\u63a5\u89e6\u4e2d\uff0c\u9648\u4fe1\u751f\u65e0\u6cd5\u5224\u65ad\u5f20\u606a\u610f\u56fe\uff0c\u89c1\u5f20\u606a\u4e00\u4ed8\u8981\u53bb\u72c2\u6b22\u7684\u67b6\u5f0f\uff0c\u7709\u5934\u5fae\u5fae\u76b1\u4e86\u8d77\u6765\uff0cTI\u603b\u90e8\u90a3\u8fb9\u6025\u7740\u7b49\u4ed6\u7684\u56de\u5e94\uff0c\u4ed6\u5e73\u9759\u7684\u8bf4\uff1a\u201c\u56fd\u5185\u7684\u4e1a\u52a1\u6bd4\u8f83\u7e41\u5fd9\uff0c\u6211\u62bd\u51fa\u4e24\u5929\u7684\u7a7a\u95f2\u65f6\u95f4\uff0c\u5c31\u5e0c\u671b\u80fd\u8c08\u51fa\u4e00\u4e9b\u6210\u679c\u6765\uff0c\u8981\u559d\u9152\u7684\u8bdd\uff0c\u4e0b\u56de\u6765\u6d77\u5dde\u8ba9\u5f20\u5148\u751f\u8bf7\u662f\u4e86\uff0c\u5176\u5b9e\u6211\u66f4\u5e0c\u671b\u5f20\u5148\u751f\u5230\u5317\u4eac\u65f6\uff0c\u8ba9\u6211\u5c3d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\u3002\u201d</p><p>\u3000\u3000\u201c\u9648\u5148\u751f\u679c\u7136\u662f\u767e\u5fd9\u4e4b\u4eba\uff0c\u8fd9\u6837\u554a\uff0c\u201d\u5f20\u606a\u62cd\u62cd\u989d\u5934\uff0c\u201c\u90a3\u6211\u5c31\u8ba9\u98df\u5802\u5b89\u6392\u7b80\u9910\u5427\uff0c\u9648\u5148\u751f\u4e0d\u8981\u5acc\u7b80\u964b\uff0c\u6211\u4e5f\u4e60\u60ef\u4e00\u8fb9\u7528\u9910\u4e00\u8fb9\u8c08\u5de5\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u70ed\u60c5\u7684\u529d\u8bf4\uff0c\u6001\u5ea6\u7ec6\u5fae\u5904\u51e0\u7ecf\u8f6c\u53d8\uff0c\u8ba9\u9648\u4fe1\u751f\u5c31\u751f\u51fa\u4e0d\u597d\u7684\u9884\u611f\uff1a\u7231\u8fbe\u7535\u5b50\u5bf9TI\u7684\u5e95\u724c\u6478\u5f97\u5f88\u6e05\u695a\u3002</p><p>\u3000\u3000\u80fd\u4eceTI\u4e89\u53d6\u4ec0\u4e48\u7a0b\u5e8f\u7684\u6709\u5229\u6761\u4ef6\uff0c\u9648\u4fe1\u751f\u7684\u6001\u5ea6\u5f88\u91cd\u8981\uff0c\u8981\u662f\u9648\u4fe1\u751f\u5bf9TI\u603b\u90e8\u8bf4\u7231\u8fbe\u7535\u5b50\u4e0d\u503c\u5f97\u5408\u4f5c\u3001\u6280\u672f\u65b9\u6848\u6ca1\u6709\u5b9e\u9645\u4ef7\u503c\uff0c\u8fd9\u573a\u4e70\u5356\u4e5f\u53ef\u80fd\u4f1a\u592d\u6298\u3002</p><p>\u3000\u3000\u5728\u98df\u5802\u7528\u9910\u65f6\uff0c\u5f20\u606a\u5c31\u6ca1\u6709\u7ee7\u7eed\u906e\u906e\u63a9\u63a9\uff0c\u5f00\u59cb\u8bb2\u4ed6\u8981\u505a\u4e00\u4e2a\u4e2d\u56fd\u5f71\u789f\u673a\u54c1\u724c\u7684\u68a6\u60f3\uff0c\u8ba1\u600e\u4e48\u505a\u6e20\u9053\uff0c\u8bb2\u4ed6\u5bf9\u5e02\u573a\u7684\u5206\u6790\u4e0e\u5224\u65ad\uff0c\u8bb2\u4ed6\u5bf9\u5e02\u573a\u7684\u89c1\u89e3\u4e0e\u601d\u8003\uff0c\u8fd9\u987f\u7b80\u9910\u4e00\u76f4\u5403\u5230\u6df1\u591c\u4e00\u70b9\u8fd8\u6ca1\u6709\u5403\u5b8c\u3002\u9648\u4fe1\u751f\u7ec8\u4e8e\u76f8\u4fe1TI\u8463\u4e8b\u957f\u5b89\u5409\u4f2f\u5148\u751f\u5bf9\u4ed6\u7684\u5224\u65ad\uff1a\u5bf9\u5e02\u573a\u611f\u89c9\u975e\u5e38\u654f\u9510\uff0c\u800c\u7231\u8fbe\u7535\u5b50\u6240\u4f53\u73b0\u51fa\u6765\u7684\u7ba1\u7406\u6c34\u5e73\u4e5f\u662f\u56fd\u5185\u6240\u5c11\u89c1\u3002</p><p>\u3000\u3000\u867d\u7136\u7231\u8fbe\u7535\u5b50\u7684\u89c4\u6a21\u8fd8\u975e\u5e38\u7684\u5c0f\uff0c\u5b9e\u529b\u8fd8\u975e\u5e38\u7684\u5f31\uff0c\u4f46\u662f\u4e0d\u5426\u8ba4\u4ed6\u4eec\u62e5\u6709\u503c\u5f97\u671f\u5f85\u7684\u53d1\u5c55\u524d\u9014\u3002\u9648\u4fe1\u751f\u6539\u53d8\u521a\u4e0b\u98de\u673a\u65f6\u5bf9\u5f20\u606a\u7b49\u4eba\u7684\u8ba4\u77e5\uff0c\u76f4\u622a\u4e86\u5f53\u7684\u8bf4\u9053\uff1a\u201cTI\u516c\u53f8\u6709\u610f\u8d2d\u4e70\u4f60\u4eec\u624b\u91cc\u7684\u6280\u672f\u65b9\u6848\uff0c\u4f60\u4eec\u770b\u4ec0\u4e48\u4ef7\u4f4d\u5408\u9002\u3002\u6211\u7684\u65e5\u7a0b\u7684\u786e\u5f88\u7d27\uff0c\u660e\u5929\u4e0b\u5348\u6709\u4e00\u73ed\u98de\u673a\u4f1a\u5317\u4eac\uff0c\u6211\u5e0c\u671b\u5728\u90a3\u4e4b\u524d\u5f97\u5230\u7b54\u590d\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u73b0\u5728\u5c31\u53ef\u4ee5\u7ed9\u4f60\u7b54\u590d\uff0c\u201d\u5f20\u606a\u8f7b\u8f7b\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201cTI\u516c\u53f8\u5bb6\u5927\u4e1a\u5927\uff0c\u6211\u80fd\u7406\u89e3\u9648\u5148\u751f\u7684\u5fd9\u788c\uff0c\u6211\u8ddf\u5b89\u5409\u4f2f\u5148\u751f\u8ba8\u8bba\u89e3\u7801\u82af\u7247\u95ee\u9898\uff0c\u90fd\u662f\u5728\u7f8e\u56fd\u65f6\u95f4\u7684\u6e05\u6668\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u8001\u8138\u5fae\u5fae\u4e00\u7ea2\uff0c\u88ab\u770b\u7a7f\u5e95\u724c\u4e4b\u540e\uff0c\u4efb\u4f55\u8c08\u5224\u6280\u672f\u90fd\u663e\u5f97\u5f88\u65e0\u52a9\uff0c\u5c31\u7b97\u5982\u6b64\uff0c\u9648\u4fe1\u751f\u4e5f\u4e0d\u60f3\u4e3b\u52a8\u62a5\u4ef7\u3002</p><p>\u3000\u3000\u5e2d\u95f4\u53ea\u6709\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u7559\u4e86\u4e0b\u6765\uff0c\u5218\u660e\u8f89\u6709\u4e00\u4e2a\u6708\u6ca1\u56de\u6d77\u5dde\uff0c\u5f20\u606a\u8ba9\u4ed6\u5148\u56de\u5bb6\u53bb\u3002</p><p>\u3000\u3000\u9759\u9ed8\u4e86\u4e00\u4f1a\u513f\uff0c\u9664\u4e86\u5f20\u606a\u5bf9\u4e5d\u516d\u3001\u4e5d\u4e03\u5e74\u5f71\u789f\u673a\u5e02\u573a\u6709\u7740\u6e05\u9192\u7684\u8ba4\u8bc6\uff0c\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u4e5f\u4e0d\u6e05\u695a\u8fd9\u4efd\u6280\u672f\u89e3\u51b3\u65b9\u6848\u7684\u4ef7\u503c\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u9648\u4fe1\u751f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u9648\u5148\u751f\u5bf9\u5f71\u789f\u673a\u5e02\u573a\u6709\u4ec0\u4e48\u770b\u6cd5\uff0c\u6211\u60df\u4e00\u80fd\u786e\u4fe1\u7684\u662f\uff0cTI\u516c\u53f8\u6709\u6211\u4eec\u8fd9\u4efd\u6280\u672f\u89e3\u51b3\u65b9\u6848\uff0c\u6700\u8fdf\u53ef\u4ee5\u5728\u4eca\u5e74\u5e74\u5e95\u4e4b\u524d\u5b8c\u6210\u65b0\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u7684\u5f00\u53d1\uff1b\u8981\u662f\u6211\u4eec\u5168\u529b\u8ddfESS\u5408\u4f5c\uff0c\u4e5f\u662f\u5e2e\u52a9ESS\u52a0\u5feb\u5de5\u4f5c\u8fdb\u5ea6\uff0c\u90a3\u5c31\u8981\u770bTI\u516c\u53f8\u662f\u5e0c\u671b\u8ddfC-ube\u4e24\u5bb6\u516c\u53f8\u6765\u5206\u4eab\u65b0\u5174\u5e02\u573a\u7684\u89e3\u7801\u82af\u7247\u5e02\u573a\uff0c\u8fd8\u662f\u7b49\u5230ESS\u4e00\u8d77\u5165\u5c40\uff0c\u4e09\u5bb6\u516c\u53f8\u4e89\u593a\u65b0\u5174\u5e02\u573a\u7684\u89e3\u7801\u82af\u7247\u5e02\u573a\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u7231\u8fbe\u624b\u91cc\u7684\u8fd9\u4efd\u6280\u672f\u65b9\u6848\u7684\u4ef7\u503c\u5f88\u96be\u5224\u5b9a\uff0c\u4e00\u5b9a\u90fd\u8981\u53d6\u51b3\u6574\u4e2a\u89e3\u7801\u82af\u7247\u5e02\u573a\u7684\u5bb9\u91cf\u3002\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8981\u80fd\u4e0eC-Cube\u4e24\u5bb6\u5206\u4eab\u8fd9\u4e2a\u5e02\u573a\uff0c\u81ea\u7136\u662fTI\u4e50\u610f\u770b\u5230\u7684\u573a\u9762\uff0cESS\u8981\u5b8c\u5168\u51ed\u501f\u81ea\u5df1\u7684\u6280\u672f\u7814\u53d1\u89e3\u7801\u82af\u7247\uff0c\u81f3\u5c11\u9700\u8981\u4e24\u5230\u4e09\u5e74\u7684\u65f6\u95f4\uff0c\u5728\u8fd9\u4e00\u6bb5\u65f6\u95f4\u91cc\uff0cTI\u4e0eC-Cube\u5b8c\u5168\u53ef\u4ee5\u5229\u7528\u624b\u91cc\u6280\u672f\u4f18\u52bf\uff0c\u518d\u5ea6\u7ed9ESS\u8bbe\u7f6e\u6280\u672f\u58c1\u5792\uff0c\u5c06\u7b2c\u4e09\u5bb6\u4f9b\u5e94\u5546\u7684\u52a0\u5165\u65f6\u95f4\u62d6\u5ef6\u5230\u4e5d\u516b\u5e74\u4e4b\u540e\u3002</p><p>\u3000\u3000\u5bf9\u4e5d\u516b\u5e74\u4e4b\u524d\u7684\u89e3\u7801\u82af\u7247\u5e02\u573a\u5bb9\u91cf\u5f88\u96be\u754c\u5b9a\uff0c\u5c31\u7b97\u5982\u6b64TI\u603b\u90e8\u8fd8\u662f\u5f00\u51fa\u4e00\u5343\u4e07\u7684\u7b79\u7801\uff0c\u4f46\u4ece\u5f20\u606a\u5bf9\u5e02\u573a\u524d\u666f\u7684\u6fc0\u60c5\u5206\u6790\uff0c\u4ed6\u4e5f\u6ca1\u6709\u5ffd\u89c6\u8fd9\u4efd\u6280\u672f\u65b9\u6848\u7684\u4ef7\u503c\uff0c\u9648\u4fe1\u751f\u7a81\u7136\u6709\u4e9b\u5fc3\u865a\uff0c\u89c9\u5f97\u5c31\u7b97\u4e00\u5343\u4e07\u7684\u7b79\u7801\u4e5f\u672a\u5fc5\u80fd\u6ee1\u8db3\u8fd9\u4e2a\u5e74\u8f7b\u4eba\u7684\u80c3\u53e3\u3002</p></article>"}}}]);
