(window.webpackJsonp=window.webpackJsonp||[]).push([[1421],{1444:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516b\u8282 \u53ef\u4ee5\u7ee7\u7eed\u559c\u6b22\u4f60\u5417",content:"<article><h2>\u7b2c\u516b\u8282 \u53ef\u4ee5\u7ee7\u7eed\u559c\u6b22\u4f60\u5417</h2><p>\u9b54\u7f51\u7684\u9020\u8be3\u6df1\u6d45\u3001\u6bcf\u5929\u6240\u51c6\u5907\u7684\u6cd5\u672f\u79cd\u7c7b\uff0c\u8fd9\u4e9b\u5bf9\u5deb\u5e08\u6765\u8bf4\u90fd\u662f\u79d8\u5bc6\uff0c\u8f7b\u6613\u4e0d\u80fd\u6cc4\u9732\u7ed9\u522b\u4eba\u77e5\u6653\u7684\u3002\u5982\u679c\u80fd\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u4e94\u5c42\uff0c\u8de8\u5165\u9ad8\u9636\u5deb\u5e08\u7684\u884c\u5217\uff0c\u5230\u65f6\u5019\u8fd8\u4f1a\u62e5\u6709\u201c\u771f\u540d\u201d\uff0c\u90a3\u5c31\u66f4\u662f\u751f\u6b7b\u6538\u5173\u7684\u673a\u5bc6\u4e86\uff0c\u5c31\u7b97\u662f\u81f3\u4eb2\u81f3\u8fd1\u7684\u4eba\uff0c\u901a\u5e38\u4e5f\u662f\u4e0d\u4f1a\u900f\u9732\u7684\u3002</p><p>\u4ee5\u524d\u4e24\u4eba\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u56e0\u4e3a\u5173\u7cfb\u4eb2\u5bc6\uff0c\u743c\u6069\u53c8\u6bd4\u8299\u857e\u72c4\u9ad8\u4e24\u4e2a\u5e74\u7ea7\uff0c\u7ecf\u5e38\u4f1a\u5728\u9b54\u6cd5\u5b66\u4e60\u4e0a\u6307\u70b9\u5979\uff0c\u6240\u4ee5\u4f1a\u8be2\u95ee\u5979\u7684\u9b54\u7f51\u9020\u8be3\uff0c\u8299\u857e\u72c4\u81ea\u7136\u4e5f\u4e0d\u4f1a\u9690\u7792\u3002\u4f46\u5982\u4eca\u4e24\u4eba\u5df2\u7ecf\u4e0d\u662f\u60c5\u4eba\uff0c\u743c\u6069\u518d\u95ee\u8fd9\u79cd\u95ee\u9898\uff0c\u5c31\u4e0d\u514d\u6709\u4e9b\u5192\u6627\u4e86\u3002</p><p>\u4f46\u8299\u857e\u72c4\u4f3c\u4e4e\u5e76\u6ca1\u611f\u89c9\u6709\u4ec0\u4e48\u4e0d\u5bf9\u3002</p><p>\u201c\u672c\u6765\u5df2\u7ecf\u9690\u7ea6\u611f\u5e94\u5230\u7b2c\u4e8c\u5c42\u4e86\uff0c\u201d\u8299\u857e\u72c4\u5782\u7740\u5934\uff0c\u767d\u7389\u822c\u7684\u53cc\u624b\u65e0\u610f\u8bc6\u5730\u5728\u88d9\u9762\u4e0a\u4ea4\u626d\u7740\uff0c\u201c\u8fd9\u51e0\u4e2a\u6708\u6765\uff0c\u5931\u7720\u4e25\u91cd\uff0c\u57fa\u672c\u6ca1\u6cd5\u7ec3\u4e60\uff0c\u53c8\u9000\u6b65\u4e86\u3002\u201d</p><p>\u8fd9\u4e5f\u5c31\u662f\u8bf4\uff0c\u5982\u4eca\u8299\u857e\u72c4\u4f9d\u65e7\u53ea\u6709\u9b54\u7f51\u7b2c\u4e00\u5c42\u7684\u9020\u8be3\uff0c\u5982\u679c\u662f\u5728\u7269\u8d28\u754c\u7684\u5176\u4ed6\u5730\u65b9\uff0c\u8fd9\u5012\u4e5f\u52c9\u5f3a\u80fd\u7b97\u662f\u4e2a\u5deb\u5e08\u4e86\uff1b\u4f46\u5728\u9634\u9b42\u57ce\uff0c\u8fd9\u70b9\u6210\u5c31\u5b8c\u5168\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\u3002</p><p>\u743c\u6069\u5f88\u6e05\u695a\uff0c\u83ab\u5c3c\u5361\u5bb6\u65cf\u4e16\u4ee3\u5deb\u5e08\uff0c\u8840\u8109\u4f20\u627f\uff0c\u7eaf\u4ee5\u201c\u9b54\u6cd5\u5929\u8d4b\u201d\u800c\u8bba\uff0c\u8299\u857e\u72c4\u5176\u5b9e\u8fd8\u80dc\u8fc7\u743c\u6069\u8fd9\u79cd\u5e73\u6c11\u4e00\u7b79\u3002\u5979\u4e4b\u6240\u4ee5\u4f1a\u5f04\u6210\u4eca\u5929\u8fd9\u6837\uff0c\u5168\u662f\u56e0\u4e3a\u5931\u7720\u75c7\u7684\u7f18\u6545\u3002\u800c\u8fd9\u79cd\u56e0\u4e3a\u7cbe\u795e\u7126\u8651\u800c\u5f15\u8d77\u7684\u5931\u7720\uff0c\u518d\u7528\u4ec0\u4e48\u836f\u6c34\u6216\u8005\u6cd5\u672f\u53ea\u6015\u90fd\u6cbb\u4e0d\u597d\uff0c\u6700\u591a\u53ea\u80fd\u7f13\u89e3\uff0c\u53ea\u6015\u8fd8\u4f1a\u6709\u526f\u4f5c\u7528\u3002</p><p>\u8981\u89e3\u51b3\u8fd9\u4e2a\u9ebb\u70e6\uff0c\u5176\u5b9e\u4e5f\u5f88\u5bb9\u6613\u3002\u743c\u6069\u77e5\u9053\u53ea\u8981\u81ea\u5df1\u548c\u8299\u857e\u72c4\u91cd\u5f52\u65e7\u597d\uff0c\u5c0f\u5973\u5b69\u7684\u5931\u7720\u75c7\u5e94\u8be5\u5c31\u4f1a\u4e0d\u6cbb\u81ea\u6108\uff0c\u5c31\u50cf\u4ee5\u524d\u5728\u5b66\u6821\u7684\u65f6\u5019\u4e00\u6837\u3002\u53ea\u662f\u65b9\u6cd5\u867d\u7136\u6e05\u695a\uff0c\u8981\u4e0d\u8981\u8fd9\u4e48\u505a\u5374\u662f\u4e2a\u5f88\u503c\u5f97\u72b9\u8c6b\u7684\u95ee\u9898\u3002</p><p>\u65f6\u8fc7\u5883\u8fc1\uff0c\u773c\u754c\u5f00\u9614\uff0c\u5f80\u65e5\u7684\u4e9b\u8bb8\u4e0d\u5feb\uff0c\u743c\u6069\u4e5f\u65e9\u4e0d\u653e\u5728\u5fc3\u4e0a\u4e86\u3002\u8981\u8bf4\u8d77\u6765\uff0c\u743c\u6069\u5bf9\u8299\u857e\u72c4\uff0c\u5176\u5b9e\u4e00\u76f4\u6ca1\u6709\u771f\u6b63\u6295\u5165\u8fc7\u591a\u6df1\u7684\u611f\u60c5\uff0c\u4f46\u7ec8\u7a76\u4e5f\u662f\u66fe\u7ecf\u559c\u6b22\u7231\u604b\u8fc7\u7684\u60c5\u4eba\u3002\u770b\u5230\u5979\u73b0\u5728\u7684\u6194\u60b4\u6a21\u6837\uff0c\u603b\u8fd8\u662f\u4f1a\u9690\u9690\u6709\u4e9b\u5fc3\u75bc\uff0c\u4e0d\u5e0c\u671b\u5979\u5c31\u8fd9\u6837\u6bc1\u6389\u3002</p><p>\u53ea\u662f\uff0c\u73b0\u5728\u5bb6\u91cc\u6709\u4e00\u4e2a\u73ca\u5609\uff0c\u6709\u4e00\u4e2a\u6885\u83f2\u65af\uff0c\u8fd9\u5df2\u7ecf\u591f\u8ba9\u743c\u6069\u5934\u75bc\uff0c\u5982\u679c\u518d\u589e\u52a0\u4e00\u4e2a\u8299\u857e\u72c4\uff0c\u90a3\u6beb\u65e0\u7591\u95ee\u5c31\u8981\u5f7b\u5e95\u5b8c\u86cb\u4e86\u3002\u8fd9\u53c8\u4e0d\u662f\u5728\u5916\u9762\u5077\u60c5\uff0c\u8fd8\u80fd\u6084\u6084\u9690\u7792\u8fc7\u53bb\uff0c\u8299\u857e\u72c4\u5bb6\u5c31\u5728\u9694\u58c1\uff0c\u548c\u73ca\u5609\u53c8\u662f\u597d\u53cb\uff0c\u7ecf\u5e38\u4f1a\u63a5\u89e6\u6765\u5f80\uff0c\u5979\u662f\u4e2a\u5fc3\u601d\u5355\u7eaf\u7684\u5c0f\u5973\u5b69\uff0c\u5f88\u5bb9\u6613\u88ab\u7ec6\u5fc3\u7684\u73ca\u5609\u5bdf\u89c9\u8ff9\u8c61\u3002</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u5c31\u542c\u59d0\u59d0\u4eca\u665a\u8bf4\u51fa\u201c\u65e9\u5b89\u54ac\u201d\u8fd9\u4e2a\u8bcd\uff0c\u743c\u6069\u5c31\u77e5\u9053\u4e00\u4ef6\u4e8b\uff1a\u5728\u73ca\u5609\u9762\u524d\uff0c\u8299\u857e\u72c4\u6ca1\u6709\u4ec0\u4e48\u79d8\u5bc6\u53ef\u8a00\u3002\u73ca\u5609\u867d\u7136\u6e29\u67d4\uff0c\u5374\u4e0d\u662f\u50cf\u8299\u857e\u72c4\u90a3\u6837\u5355\u7eaf\u4e0d\u61c2\u4e8b\uff0c\u4ece\u5c0f\u7236\u6bcd\u53bb\u4e16\uff0c\u72ec\u7acb\u652f\u6491\u5bb6\u5ead\uff0c\u591a\u5e74\u7684\u8270\u96be\u751f\u6d3b\u953b\u70bc\u51fa\u6765\u7684\u5fc3\u673a\u624b\u8155\uff0c\u7edd\u975e\u8299\u857e\u72c4\u8fd9\u79cd\u8d35\u65cf\u5c11\u5973\u53ef\u6bd4\u7684\u3002</p><p>\u201c\u4e3a\u4e86\u907f\u514d\u59d0\u59d0\u548c\u827e\u5f25\u8587\u5927\u53d1\u96f7\u9706\uff0c\u8299\u857e\u72c4\u2026\u2026\u8fd8\u662f\u5148\u7b97\u4e86\u5427\u3002\u201d</p><p>\u8fd9\u79cd\u60f3\u6cd5\u9887\u4e3a\u81ea\u79c1\uff0c\u4f46\u5728\u743c\u6069\u5fc3\u4e2d\uff0c\u73ca\u5609\u548c\u6885\u83f2\u65af\u7684\u5206\u91cf\uff0c\u786e\u5b9e\u662f\u8981\u6bd4\u8299\u857e\u72c4\u91cd\u5f97\u591a\u7684\u3002\u5982\u679c\u80fd\u8d2a\u5fc3\u5730\u5168\u90fd\u62e5\u6709\uff0c\u90a3\u81ea\u7136\u6700\u597d\uff1b\u4f46\u5982\u679c\u771f\u8981\u53d6\u820d\uff0c\u90a3\u65e0\u7591\u662f\u8981\u653e\u5f03\u8299\u857e\u72c4\u7684\u3002</p><p>\u53ea\u662f\uff0c\u96be\u9053\u771f\u5c31\u8fd9\u6837\u770b\u7740\u8299\u857e\u72c4\u4e00\u5929\u5929\u6d88\u7626\u6194\u60b4\u4e0b\u53bb\uff0c\u50cf\u82b1\u6735\u4e00\u6837\u67af\u840e\u51cb\u96f6\uff1f</p><p>\u4ed6\u6b63\u72b9\u8c6b\u7740\uff0c\u4e00\u76f4\u4f4e\u7740\u5934\u7684\u8299\u857e\u72c4\u7a81\u7136\u62ac\u8d77\u8138\u6765\uff0c\u5b9a\u5b9a\u5730\u770b\u7740\u743c\u6069\uff0c\u50cf\u662f\u7ec8\u4e8e\u4e0b\u5b9a\u4e86\u67d0\u79cd\u51b3\u5fc3\u4f3c\u7684\u3002\u201c\u743c\u6069\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u6211\uff0c\u8fd8\u53ef\u4ee5\u7ee7\u7eed\u559c\u6b22\u4f60\u5417\uff1f\u201d</p><p>\u203b\u203b\u203b</p><p>\u201c\u53d1\u751f\u8fc7\u90a3\u6837\u7684\u4e8b\u60c5\uff0c\u6211\u77e5\u9053\u6211\u73b0\u5728\u8bf4\u7684\u8bdd\uff0c\u6050\u6015\u5f88\u96be\u8ba9\u518d\u4f60\u76f8\u4fe1\u4e86\uff0c\u201d\u8299\u857e\u72c4\u5b9a\u5b9a\u5730\u51dd\u89c6\u7740\u743c\u6069\u7684\u773c\u775b\uff0c\u5979\u662f\u4e2a\u975e\u5e38\u5bb3\u7f9e\u7684\u5973\u5b69\u5b50\uff0c\u5c31\u7b97\u4ee5\u524d\u4e24\u4eba\u5173\u7cfb\u4eb2\u5bc6\u7684\u65f6\u5019\uff0c\u5979\u4e5f\u5f88\u5c11\u8fd9\u4e48\u505a\u7684\uff0c\u201c\u4f46\u6211\u60f3\u8bf4\uff0c\u743c\u6069\uff0c\u6211\u662f\u771f\u7684\u559c\u6b22\u4f60\uff0c\u4ece\u7b2c\u4e00\u773c\u770b\u89c1\u4f60\uff0c\u6211\u5c31\u559c\u6b22\u4e0a\u4e86\u3002\u4ece\u90a3\u4e00\u523b\u8d77\uff0c\u5230\u6b64\u65f6\u6b64\u523b\uff0c\u8fd9\u4efd\u7231\u604b\u6ca1\u6709\u4e00\u5206\u4e00\u6beb\u5730\u51cf\u9000\u8fc7\uff0c\u201d\u5979\u54ac\u7740\u5634\u5507\uff0c\u53cc\u624b\u7528\u529b\u5730\u7ede\u5728\u4e00\u8d77\uff0c\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff0c\u4f46\u662f\u2026\u2026\u6211\u5c31\u662f\u559c\u6b22\u4f60\u561b\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u4f60\u76f8\u4fe1\u6211\u8bf4\u7684\u8bdd\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u76f8\u4fe1\uff0c\u53ea\u662f\u77e5\u9053\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u4f60\u8bf4\u7684\u662f\u4e8b\u5b9e\u7f62\u4e86\u3002\u201d</p><p>\u8299\u857e\u72c4\u8138\u4e0a\u9732\u51fa\u6ee1\u8db3\u7684\u7b11\u5bb9\u6765\uff0c\u201c\u743c\u6069\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u4f60\u77e5\u9053\u4e48\uff0c\u4f60\u5c31\u662f\u8fd9\u70b9\uff0c\u6700\u8ba9\u6211\u559c\u6b22\u5462\u3002\u201d</p><p>\u201c\u54ea\u70b9\uff1f\u201d\u743c\u6069\u6709\u4e9b\u83ab\u540d\u3002</p><p>\u201c\u5c31\u662f\u4f60\u521a\u624d\u8bf4\u8bdd\u7684\u6837\u5b50\u554a\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u4f60\u6c38\u8fdc\u90fd\u662f\u90a3\u6837\u5bf9\u81ea\u5df1\u5145\u6ee1\u4fe1\u5fc3\u5462\u3002\u201d</p><p>\u201c\u5514\uff0c\u8fd9\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4eba\u4eba\u90fd\u6709\u4fe1\u5fc3\uff0c\u4f46\u672a\u5fc5\u4eba\u4eba\u90fd\u6709\u529b\u91cf\u3002\u53ea\u6709\u4fe1\u5fc3\uff0c\u6ca1\u6709\u529b\u91cf\uff0c\u90a3\u4e5f\u4e0d\u8fc7\u662f\u81ea\u6b3a\u6b3a\u4eba\u7f62\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u8299\u857e\u72c4\u6447\u5934\uff0c\u201c\u9053\u7406\u6211\u8bf4\u4e0d\u6e05\u695a\uff0c\u4f46\u4f60\u662f\u4e0d\u4e00\u6837\u7684\u3002\u4f60\u7ed9\u6211\u7684\u611f\u89c9\u2026\u2026\u5c31\u50cf\u662f\u4ece\u6765\u4e0d\u4f1a\u5931\u8d25\uff0c\u6c38\u8fdc\u4e0d\u4f1a\u5931\u8d25\uff0c\u53ea\u8981\u4f60\u76f8\u4fe1\u7684\u4e8b\u60c5\uff0c\u90a3\u5c31\u662f\u4e16\u754c\u4e0a\u6700\u6b63\u786e\u7684\u771f\u7406\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u5c0f\u4e2b\u5934\uff0c\u8fd9\u662f\u56e0\u4e3a\u4f60\u559c\u6b22\u6211\uff0c\u6240\u4ee5\u5728\u4f60\u773c\u91cc\u6211\u5c31\u4ec0\u4e48\u90fd\u597d\u5427\u3002\u201d\u4ed6\u6697\u60f3\u7740\uff0c\u4f46\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u201c\u5728\u4e00\u5f00\u59cb\uff0c\u6211\u7236\u4eb2\u8bf4\u4f60\u7684\u540d\u5b57\uff0c\u8ba9\u6211\u6545\u610f\u53bb\u63a5\u8fd1\u4f60\uff0c\u5f53\u65f6\u6211\u662f\u5f88\u4e0d\u9ad8\u5174\u7684\u3002\u53ef\u662f\u81ea\u4ece\u770b\u5230\u4f60\u7684\u90a3\u523b\u8d77\uff0c\u6211\u5c31\u4ec0\u4e48\u90fd\u5fc3\u7518\u60c5\u613f\u4e86\u3002\u90a3\u6bb5\u65f6\u95f4\uff0c\u6bcf\u5929\u80fd\u966a\u5728\u4f60\u8eab\u8fb9\uff0c\u770b\u7740\u4f60\uff0c\u542c\u4f60\u8bf4\u8bdd\uff0c\u770b\u4f60\u9ad8\u5174\uff0c\u665a\u4e0a\u88ab\u4f60\u62b1\u5728\u6000\u91cc\uff0c\u542c\u4f60\u5fc3\u8df3\u7684\u58f0\u97f3\uff0c\u6162\u6162\u7761\u7740\uff0c\u201d\u5979\u7684\u8138\u8d8a\u6765\u8d8a\u7ea2\uff0c\u5df2\u7ecf\u50cf\u8981\u53d1\u70e7\u8d77\u6765\uff0c\u4f46\u6ee1\u8154\u7684\u8bdd\u5374\u4eff\u4f5b\u915d\u917f\u4e86\u5f88\u4e45\uff0c\u4e00\u53e3\u6c14\u63a5\u4e0b\u53bb\u8bf4\uff0c\u201c\u6211\u559c\u6b22\u88ab\u4f60\u538b\u5728\u8eab\u4f53\u4e0b\u9762\uff0c\u611f\u89c9\u81ea\u5df1\u6574\u4e2a\u4eba\u90fd\u662f\u5c5e\u4e8e\u4f60\u7684\uff1b\u6211\u559c\u6b22\u4f60\u5728\u6211\u8eab\u4f53\u91cc\u9762\uff0c\u6ee1\u6ee1\u5145\u585e\u7740\uff0c\u50cf\u662f\u88ab\u4f60\u5b8c\u5168\u5360\u636e\u4e00\u6837\uff1b\u6211\u559c\u6b22\u6bcf\u5929\u65e9\u4e0a\u5e2e\u4f60\u2026\u2026\u53ea\u8981\u662f\u4f60\u8ba9\u6211\u505a\u7684\uff0c\u65e0\u8bba\u662f\u591a\u4e48\u7f9e\u6da9\u7684\u4e8b\u60c5\uff0c\u6211\u90fd\u4f1a\u52aa\u529b\u5730\u505a\uff0c\u56e0\u4e3a\u6211\u559c\u6b22\u770b\u5230\u4f60\u9ad8\u5174\u7684\u6837\u5b50\u3002\u53ea\u8981\u80fd\u8ba9\u4f60\u9ad8\u5174\uff0c\u6211\u5c31\u505a\u4ec0\u4e48\u90fd\u613f\u610f\u3002\u201d</p><p>\u201c\u90a3\u65f6\u5019\uff0c\u6211\u5728\u5fc3\u91cc\u60f3\uff0c\u6bcf\u5929\u65e9\u4e0a\u9192\u6765\u7741\u5f00\u773c\uff0c\u5c31\u80fd\u770b\u89c1\u4f60\u5728\u6211\u7684\u6795\u5934\u8fb9\uff0c\u201d\u5979\u8f7b\u8f7b\u5730\u8bf4\uff0c\u201c\u90a3\u5c31\u662f\u6211\u6700\u5927\u7684\u5feb\u4e50\u4e86\u3002\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u7740\u3002</p><p>\u4ed6\u76f8\u4fe1\u8299\u857e\u72c4\u6240\u8bf4\u7684\u90fd\u662f\u771f\u5fc3\u8bdd\uff0c\u4ed6\u4e5f\u6e05\u695a\u81ea\u5df1\u5bf9\u8299\u857e\u72c4\u7ec8\u7a76\u8fd8\u662f\u6709\u4efd\u62b9\u4e0d\u53bb\u7684\u7737\u604b\uff0c\u53ea\u662f\u4e8b\u5230\u5982\u4eca\uff0c\u518d\u8bf4\u8fd9\u4e9b\u53c8\u6709\u4ec0\u4e48\u610f\u4e49\u5462\u3002</p><p>\u8299\u857e\u72c4\u4e5f\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\u3002</p><p>\u201c\u6211\u4ece\u5c0f\u5230\u5927\uff0c\u90fd\u60f3\u505a\u4e00\u4e2a\u4e56\u4e56\u7684\u5b69\u5b50\uff0c\u7236\u4eb2\u5f88\u4e25\u5389\uff0c\u6211\u4ece\u4e0d\u6562\u8fdd\u62d7\u4ed6\u7684\u610f\u601d\uff0c\u603b\u662f\u6309\u7167\u4ed6\u7684\u5429\u5490\u529e\u4e8b\u3002\u6211\u4e5f\u4ece\u6ca1\u89c9\u5f97\u8fd9\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff0c\u53ea\u6709\u8fd9\u6700\u540e\u4e00\u4ef6\u4e8b\uff0c\u662f\u6211\u77e5\u9053\u6211\u505a\u9519\u4e86\uff0c\u6211\u628a\u539f\u672c\u63e1\u5728\u624b\u5fc3\u91cc\u7684\u5e78\u798f\u81ea\u5df1\u7ed9\u6bc1\u4e86\u3002\u201d</p><p>\u201c\u6211\u505a\u9519\u4e86\u4e8b\u60c5\uff0c\u81ea\u5df1\u6bc1\u4e86\u81ea\u5df1\u7684\u5feb\u4e50\uff0c\u6240\u4ee5\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u597d\u8bf4\u7684\uff0c\u201d\u5979\u5782\u4e0b\u5934\uff0c\u201c\u73ca\u5609\u59d0\u59d0\u8bf4\uff0c\u611f\u60c5\u5c31\u50cf\u7cbe\u81f4\u5374\u8106\u5f31\u7684\u5668\u76bf\uff0c\u9700\u8981\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u7ef4\u62a4\u7684\u3002\u4e00\u65e6\u5931\u624b\u6253\u7834\u4e86\uff0c\u5c31\u7b97\u7528\u518d\u9ad8\u660e\u7684\u65b9\u6cd5\u4fee\u8865\u8d77\u6765\uff0c\u90a3\u4e5f\u7ec8\u7a76\u4e0d\u53ef\u80fd\u662f\u539f\u6765\u90a3\u4e2a\u4e86\uff0c\u90a3\u4e9b\u88c2\u75d5\uff0c\u5c31\u7b97\u770b\u4e0d\u89c1\uff0c\u5176\u5b9e\u4e5f\u603b\u662f\u5728\u90a3\u91cc\u7684\uff0c\u201d\u5979\u8f7b\u8f7b\u54ac\u7740\u4e0b\u5507\uff0c\u773c\u7736\u91cc\u9690\u9690\u6709\u4e9b\u6cea\u5149\uff0c\u201c\u4e8b\u5230\u5982\u4eca\uff0c\u6211\u5e76\u4e0d\u5962\u671b\u8fd8\u80fd\u91cd\u5f52\u65e7\u597d\uff0c\u4e5f\u4e0d\u5962\u671b\u4f60\u8fd8\u80fd\u518d\u559c\u6b22\u6211\uff0c\u751a\u81f3\u4e0d\u5962\u671b\u4f60\u80fd\u539f\u8c05\u6211\u3002\u6211\u53ea\u60f3\u2026\u2026\u53ea\u60f3\u4f60\u8fd8\u80fd\u5141\u8bb8\u6211\u559c\u6b22\u4f60\uff0c\u597d\u4e0d\u597d\u3002\u201d</p><p>\u50bb\u4e2b\u5934\uff0c\u559c\u6b22\u4e00\u4e2a\u4eba\u662f\u4e0d\u9700\u8981\u5bf9\u65b9\u5141\u8bb8\u7684\u3002</p><p>\u743c\u6069\u60f3\u8fd9\u4e48\u8bf4\uff0c\u4f46\u6700\u7ec8\u653e\u5f03\u4e86\uff0c\u4ed6\u7ad9\u5728\u8299\u857e\u72c4\u8eab\u524d\uff0c\u5c06\u5979\u8f7b\u8f7b\u62b1\u5728\u6000\u91cc\uff0c\u4f4e\u5934\u5728\u5979\u7684\u989d\u5934\u4e0a\u8f7b\u8f7b\u543b\u4e86\u4e00\u4e0b\u3002\u201c\u597d\uff0c\u6211\u5141\u8bb8\u4f60\u559c\u6b22\u6211\u3002\u201d</p><p>\u8299\u857e\u72c4\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u7b11\u4e86\u8d77\u6765\uff0c\u90a3\u662f\u53d1\u81ea\u5185\u5fc3\u7684\u6109\u60a6\uff0c\u4ee5\u53ca\u5982\u91ca\u91cd\u8d1f\u3002\u201c\u8c22\u8c22\u4f60\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u5f88\u665a\u4e86\uff0c\u6211\u8be5\u56de\u53bb\u4e86\u3002\u201d</p><p>\u201c\u55ef\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u7b11\u8d77\u6765\u771f\u597d\u770b\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u771f\u7684\u3002\u201d</p><p>\u743c\u6069\u653e\u5f00\u5979\uff0c\u8299\u857e\u72c4\u4ece\u957f\u6905\u4e0a\u8d77\u8eab\uff0c\u629a\u4e86\u629a\u81ea\u5df1\u7684\u767d\u8272\u957f\u88d9\uff0c\u5979\u51fa\u8eab\u8d35\u65cf\uff0c\u4ece\u5c0f\u7ecf\u53d7\u4e25\u683c\u7684\u793c\u4eea\u8bad\u7ec3\uff0c\u4e3e\u624b\u6295\u8db3\u95f4\u81ea\u7136\u6709\u79cd\u4f18\u96c5\u98ce\u5ea6\uff0c\u4fbf\u662f\u8fd9\u7b80\u5355\u7684\u52a8\u4f5c\u505a\u8d77\u6765\u4e5f\u6f02\u4eae\u5f97\u5f88\u3002\u201c\u6211\u8d70\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u4e5f\u65e9\u70b9\u56de\u53bb\u4f11\u606f\u5427\u3002\u201d</p><p>\u201c\u597d\u3002\u201d</p><p>\u743c\u6069\u770b\u7740\u8299\u857e\u72c4\u8d70\u51fa\u82b1\u56ed\uff0c\u7a81\u7136\u60f3\u8d77\u4ef6\u4e8b\u60c5\u6765\uff0c\u201c\u7b49\u4e0b\uff0c\u8299\u857e\u72c4\uff0c\u201d\u4ed6\u5306\u5fd9\u53eb\u4f4f\u5c0f\u5973\u5b69\uff0c\u201c\u6709\u4ef6\u4e8b\u60c5\uff0c\u6211\u60f3\u4e0d\u592a\u660e\u767d\uff0c\u80fd\u5e2e\u6211\u53c2\u8003\u4e00\u4e0b\u4e48\u3002\u201d</p><p>\u8299\u857e\u72c4\u6709\u4e9b\u60ca\u8bb6\uff0c\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u6211\u60f3\u77e5\u9053\uff0c\u5982\u679c\u4e00\u4e2a\u5973\u5b69\u5b50\u5bf9\u4f60\u8bf4\uff1a\u6211\u5e76\u6ca1\u6709\u4f60\u60f3\u8c61\u5f97\u90a3\u4e48\u575a\u5f3a\u3002\u90a3\u8fd9\u53e5\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\u5462\uff1f\u201d</p><p>\u203b\u203b\u203b</p><p>\u8299\u857e\u72c4\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u7136\u540e\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\u6765\uff0c\u201c\u662f\u73ca\u5609\u59d0\u59d0\u8bf4\u7684\u5427\u3002\u201d</p><p>\u201c\u55ef\uff0c\u662f\u3002\u201d</p><p>\u201c\u73ca\u5609\u59d0\u59d0\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u8fd9\u4e2a\u6211\u4e5f\u4e0d\u597d\u8bf4\u5462\uff0c\u201d\u5979\u601d\u8003\u7740\uff0c\u201c\u4e0d\u8fc7\u6211\u89c9\u5f97\uff0c\u73ca\u5609\u59d0\u59d0\u5176\u5b9e\u672c\u8d28\u4e0a\u5e76\u4e0d\u662f\u771f\u7684\u591a\u4e48\u575a\u5f3a\u2026\u2026\u5514\uff0c\u4e0d\u662f\uff0c\u662f\u4ece\u672c\u8d28\u4e0a\u8bf4\uff0c\u5973\u5b69\u5b50\u5176\u5b9e\u90fd\u4e0d\u662f\u575a\u5f3a\u7684\u5462\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u76b1\u7709\uff0c\u4ed6\u6709\u70b9\u4e0d\u592a\u660e\u767d\u8299\u857e\u72c4\u8981\u8868\u8fbe\u7684\u610f\u601d\u3002</p><p>\u8299\u857e\u72c4\u5fae\u7b11\u7740\uff0c\u201c\u6211\u662f\u4e2a\u5973\u5b69\u5b50\uff0c\u6240\u4ee5\u591a\u5c11\u603b\u80fd\u660e\u767d\u4e9b\u5973\u5b69\u5b50\u7684\u60f3\u6cd5\u5427\u3002\u6211\u89c9\u5f97\u5462\uff0c\u5973\u5b69\u5b50\u4ece\u672c\u8d28\u4e0a\u6765\u8bf4\uff0c\u90fd\u662f\u67d4\u5f31\u7684\uff0c\u559c\u6b22\u88ab\u4eba\u7167\u987e\u7684\uff0c\u559c\u6b22\u88ab\u4eba\u62b1\u5728\u6000\u91cc\u7684\u3002\u81f3\u4e8e\u575a\u5f3a\uff0c\u5f88\u591a\u65f6\u5019\u662f\u65e0\u53ef\u5948\u4f55\u7684\u9009\u62e9\u5462\u3002\u7537\u5b69\u5b50\u8bf4\u575a\u5f3a\uff0c\u90a3\u4f3c\u4e4e\u662f\u7406\u6240\u5f53\u7136\uff0c\u4f46\u5973\u5b69\u5b50\u5982\u679c\u8bf4\u575a\u5f3a\uff0c\u90a3\u5f80\u5f80\u662f\u56e0\u4e3a\u65e0\u53ef\u4f9d\u9760\u4e86\u5427\u3002\u201d</p><p>\u201c\u73ca\u5609\u59d0\u59d0\u4ee5\u524d\uff0c\u662f\u5f88\u8f9b\u82e6\u7684\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5f88\u65e9\u5c31\u8981\u72ec\u7acb\u751f\u6d3b\uff0c\u548c\u4f60\u76f8\u4f9d\u4e3a\u547d\uff0c\u8981\u6512\u94b1\u80fd\u8ba9\u4f60\u8fdb\u5165\u5deb\u5e08\u5b66\u6821\uff0c\u8981\u72ec\u81ea\u4e00\u4e2a\u4eba\u652f\u6491\u5bb6\u5ead\uff0c\u7ef4\u6301\u751f\u6d3b\uff0c\u5979\u4e0d\u5f97\u4e0d\u575a\u5f3a\u3002\u4f46\u662f\uff0c\u8fd9\u672a\u5fc5\u771f\u7684\u5c31\u662f\u5979\u672c\u6765\u7684\u6837\u5b50\uff0c\u672a\u5fc5\u5c31\u662f\u5979\u6240\u559c\u6b22\u7684\u6837\u5b50\u5462\uff0c\u201d\u8299\u857e\u72c4\u9759\u9759\u5730\u770b\u7740\u743c\u6069\uff0c\u201c\u8fd9\u6216\u8bb8\u7b97\u662f\u6211\u4ee5\u5df1\u5ea6\u4eba\uff0c\u56e0\u4e3a\u6211\u81ea\u5df1\u5c31\u4e0d\u591f\u575a\u5f3a\u3002\u4f46\u6211\u8fd8\u662f\u89c9\u5f97\uff0c\u53ea\u8981\u662f\u5973\u5b69\u5b50\uff0c\u5c31\u7ec8\u7a76\u8fd8\u662f\u559c\u6b22\u80fd\u88ab\u4eba\u5ba0\u7740\uff0c\u88ab\u4eba\u5475\u62a4\u3002\u6bd5\u7adf\uff0c\u73b0\u5728\u60c5\u51b5\u4e0d\u4e00\u6837\u4e86\uff0c\u73b0\u5728\u4f60\u6bd5\u4e1a\u4e86\uff0c\u4f60\u5df2\u7ecf\u662f\u6b63\u5f0f\u7684\u5deb\u5e08\u4e86\uff0c\u4f60\u62e5\u6709\u4e86\u8d35\u65cf\u7684\u8eab\u4efd\u548c\u5730\u4f4d\uff0c\u4e5f\u6709\u80fd\u529b\u6765\u652f\u6491\u8d77\u5170\u5c3c\u65af\u7279\u5bb6\uff0c\u90a3\u4e48\u73ca\u5609\u59d0\u59d0\uff0c\u5c31\u4e0d\u9700\u8981\u518d\u50cf\u4ee5\u524d\u90a3\u6837\u575a\u5f3a\u4e86\u5427\u3002\u201d</p><p>\u743c\u6069\u9759\u9759\u5730\u542c\u7740\u3002</p><p>\u201c\u6211\u548c\u73ca\u5609\u59d0\u59d0\u804a\u5929\uff0c\u5979\u5f88\u591a\u6b21\u8bf4\u8d77\u4f60\uff0c\u6211\u542c\u5979\u8bf4\u8bdd\uff0c\u5c31\u89c9\u5f97\u6709\u6709\u8da3\u5462\uff0c\u201d\u8299\u857e\u72c4\u8f7b\u8f7b\u7b11\u8d77\u6765\uff0c\u201c\u600e\u4e48\u8bf4\u5462\uff0c\u611f\u89c9\u4f60\u5728\u73ca\u5609\u59d0\u59d0\u9762\u524d\uff0c\u548c\u5728\u522b\u4eba\u9762\u524d\uff0c\u662f\u5b8c\u5168\u4e24\u6837\u7684\u3002\u201d</p><p>\u201c\u662f\u4e48\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u662f\u554a\uff0c\u4f60\u5728\u522b\u4eba\u9762\u524d\uff0c\u5728\u6211\u9762\u524d\uff0c\u90fd\u662f\u6c89\u7a33\u81ea\u4fe1\u7684\u6210\u5e74\u4eba\uff0c\u4f46\u5728\u73ca\u5609\u59d0\u59d0\u9762\u524d\uff0c\u5c31\u597d\u50cf\u2026\u2026\u73ca\u5609\u59d0\u59d0\u8bf4\uff0c\u4f60\u5728\u5979\u9762\u524d\uff0c\u5c31\u50cf\u4e2a\u6c38\u8fdc\u957f\u4e0d\u5927\u7684\u5b69\u5b50\u4f3c\u7684\u3002\u201d</p><p>\u201c\u6c38\u8fdc\u957f\u4e0d\u5927\u7684\u5b69\u5b50\u4e48\u2026\u2026\u201d</p><p>\u201c\u662f\u554a\uff0c\u73ca\u5609\u59d0\u59d0\u662f\u8fd9\u4e48\u8bf4\u7684\u5462\u3002\u6211\u89c9\u5f97\uff0c\u4f5c\u4e3a\u4e00\u4e2a\u59d0\u59d0\uff0c\u6216\u8bb8\u786e\u5b9e\u5f88\u559c\u6b22\u5f1f\u5f1f\u548c\u81ea\u5df1\u8fd9\u6837\u4eb2\u8fd1\uff0c\u4f46\u4f5c\u4e3a\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u7ec8\u7a76\u8fd8\u662f\u5e0c\u671b\u4f60\u80fd\u53d8\u5f97\u66f4\u52a0\u53ef\u4ee5\u4f9d\u9760\u4e00\u4e9b\u7684\u3002\u8bf4\u81ea\u5df1\u5e76\u4e0d\u771f\u7684\u575a\u5f3a\uff0c\u5c31\u662f\u5e0c\u671b\u4f60\u80fd\u591f\u53d8\u5f97\u575a\u5f3a\uff0c\u53ef\u4ee5\u8ba9\u5979\u4f9d\u9760\uff0c\u73ca\u5609\u59d0\u59d0\u8fd9\u53e5\u8bdd\uff0c\u5927\u81f4\u5e94\u8be5\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\u5427\uff0c\u201d\u5979\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u201c\u5514\uff0c\u5176\u5b9e\u6211\u597d\u50cf\u4e5f\u8bf4\u4e0d\u6e05\u695a\u4e86\u2026\u2026\u201d</p><p>\u201c\u4e0d\uff0c\u6211\u5df2\u7ecf\u660e\u767d\u4e86\u3002\u201d\u743c\u6069\u8bf4\uff0c\u4ed6\u786e\u5b9e\u5df2\u7ecf\u660e\u767d\u4e86\u3002</p><p>\u8299\u857e\u72c4\u867d\u7136\u8bf4\u5f97\u6709\u4e9b\u6563\u4e71\uff0c\u4e5f\u662f\u60c5\u7406\u4e4b\u4e2d\uff0c\u5979\u5e76\u4e0d\u77e5\u9053\u5177\u4f53\u7684\u7f18\u7531\uff0c\u53ea\u662f\u5355\u7eaf\u4ece\u8fd9\u53e5\u8bdd\u4e0a\u6765\u63a8\u6d4b\u3002\u4f46\u540c\u4e3a\u5973\u5b69\u5b50\uff0c\u5728\u6709\u4e9b\u65b9\u9762\u5979\u786e\u5b9e\u66f4\u80fd\u7406\u89e3\u73ca\u5609\u7684\u610f\u601d\u3002</p><p>\u201c\u90a3\uff0c\u6211\u5148\u8d70\u4e86\u3002\u201d\u8299\u857e\u72c4\u8f7b\u58f0\u8bf4\u3002</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u770b\u7740\u8299\u857e\u72c4\u7684\u8eab\u5f71\u6c89\u6ca1\u5728\u9ed1\u6697\u4e2d\u3002\u4ed6\u9759\u9759\u5750\u5728\u957f\u6905\u4e0a\uff0c\u4ece\u5c0f\u5230\u5927\uff0c\u5404\u79cd\u548c\u73ca\u5609\u5171\u540c\u751f\u6d3b\u7ecf\u5386\u8fc7\u7684\u7247\u6bb5\u7eb7\u7eb7\u4ece\u8bb0\u5fc6\u7684\u6700\u6df1\u5904\u6d6e\u73b0\uff0c\u7f16\u7ec7\u6210\u4e00\u5e55\u5e55\u5f80\u65e5\u7684\u7f8e\u597d\u65f6\u5149\uff0c\u4ece\u9762\u524d\u6084\u7136\u6ed1\u8fc7\u3002</p><p>\u7136\u540e\u4ed6\u60f3\u8d77\u4eca\u5929\u665a\u4e0a\uff0c\u81ea\u5df1\u53bb\u73ca\u5609\u623f\u95f4\uff0c\u770b\u89c1\u5979\u5728\u770b\u4e66\u3002\u81ea\u5df1\u628a\u4e66\u53d6\u8fc7\u6765\uff0c\u50ac\u4fc3\u5979\u7761\u89c9\u7684\u65f6\u5019\uff0c\u73ca\u5609\u8bf4\u7684\u90a3\u53e5\u8bdd\u3002</p><p>\u73ca\u5609\u8bf4\uff1a\u201c\u6211\u7684\u5f1f\u5f1f\u957f\u5927\u4e86\u5462\u3002\u201d</p></article>"}}}]);
