(window.webpackJsonp=window.webpackJsonp||[]).push([[1423],{1446:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u8282 \u4ea4\u6613",content:"<article><h2>\u7b2c\u5341\u8282 \u4ea4\u6613</h2><p>\u4fbf\u5982\u743c\u6069\u4e8b\u5148\u6240\u6599\uff0c\u8fd9\u6b21\u7684\u4efb\u52a1\u8fdb\u884c\u5f97\u5f88\u987a\u5229\u3002\u9634\u9b42\u57ce\u548c\u585e\u5c14\u65e2\u7136\u7ed3\u76df\uff0c\u81ea\u7136\u4f1a\u4e92\u76f8\u6d3e\u9063\u5b98\u5458\u5e38\u9a7b\u5bf9\u65b9\u9996\u5e9c\uff0c\u4e13\u95e8\u8d1f\u8d23\u8054\u7edc\u4e8b\u5b9c\u3002\u743c\u6069\u548c\u73ca\u5609\u901a\u8fc7\u4f20\u9001\u95e8\u6765\u5230\u585e\u5c14\u7684\u9996\u90fd\u827e\u5c14\u5854\u67cf\uff0c\u627e\u5230\u8d1f\u8d23\u8054\u7edc\u7684\u5b98\u5458\uff0c\u9a8c\u8bc1\u8eab\u4efd\uff0c\u8bf4\u660e\u6765\u610f\u3002\u5927\u7ea6\u4e0a\u5348\u5341\u70b9\u949f\uff0c\u5728\u4e00\u4f4d\u585e\u5c14\u5b98\u5458\u7684\u966a\u540c\u4e0b\uff0c\u743c\u6069\u59d0\u5f1f\u4fe9\u6765\u5230\u67d0\u5ea7\u8c6a\u534e\u5b85\u90b8\uff0c\u548c\u6b64\u5730\u7684\u4e3b\u4eba\u5f17\u96f7\u65af\u4f1a\u9762\u3002</p><p>\u5f17\u96f7\u65af\u662f\u4e00\u4f4d\u5927\u8179\u4fbf\u4fbf\u7684\u5546\u4eba\uff0c\u4ed6\u5934\u53d1\u82b1\u767d\uff0c\u8138\u4e0a\u76b1\u7eb9\u7eb5\u6a2a\uff0c\u808c\u8089\u677e\u5f1b\uff0c\u770b\u8d77\u6765\u81f3\u5c11\u6709\u4e94\u5341\u4f59\u5c81\u4e86\uff0c\u4f46\u8a00\u8c08\u4e3e\u6b62\u95f4\u5374\u4f9d\u65e7\u7cbe\u795e\u77cd\u94c4\uff0c\u53cd\u5e94\u8fc5\u6377\u3002\u4ed6\u662f\u827e\u5c14\u5854\u67cf\u6392\u540d\u7b2c\u4e09\u7684\u5974\u96b6\u8d29\u5b50\uff0c\u540c\u65f6\u8fd8\u662f\u827e\u5c14\u5854\u67cf\u884c\u7701\u603b\u7763\u5fb7\u871c\u62c9\xb7\u5f17\u96f7\u65af\u5973\u58eb\u7684\u53d4\u7236\uff0c\u7b97\u662f\u6709\u5b98\u65b9\u80cc\u666f\u7684\u4eba\u7269\u3002\u4e4b\u6240\u4ee5\u9009\u62e9\u4ed6\u800c\u4e0d\u662f\u9009\u62e9\u6392\u540d\u66f4\u9760\u524d\u7684\u4e24\u4f4d\u5974\u96b6\u5546\u4eba\uff0c\u4e43\u662f\u56e0\u4e3a\u5f17\u96f7\u65af\u5148\u751f\u7684\u7ecf\u8425\u8303\u56f4\u6700\u7b26\u5408\u8981\u6c42\u2014\u2014\u4ed6\u4e13\u95e8\u8d29\u5356\u77ee\u4eba\u548c\u4f8f\u5112\u5974\u96b6\u3002</p><p>\u8fd9\u6b21\u743c\u6069\u5949\u547d\u800c\u6765\uff0c\u76ee\u6807\u662f\u8981\u8d2d\u4e70\u5728\u77ff\u77f3\u3001\u91d1\u5c5e\u953b\u9020\u6216\u8005\u673a\u68b0\u5de5\u827a\u7b49\u65b9\u9762\u6709\u6240\u4e13\u957f\u7684\u5974\u96b6\uff0c\u8fd9\u5f88\u663e\u7136\u662f\u4e3a\u4e86\u6b63\u5728\u8fdb\u884c\u4e2d\u7684\u9b54\u50cf\u548c\u706b\u67aa\u7814\u5236\u5de5\u4f5c\u3002\u800c\u8fd9\u4e2a\u4e16\u754c\u79d1\u6280\u975e\u5e38\u4e0d\u53d1\u8fbe\uff0c\u706b\u836f\u4e5f\u624d\u521a\u521a\u53d1\u660e\u5341\u4f59\u5e74\uff0c\u4eba\u7c7b\u5f53\u4e2d\u6781\u5c11\u6709\u7814\u7a76\u8fd9\u65b9\u9762\u7684\u4e13\u5bb6\uff0c\u9664\u4e86\u9093\u80af\u8fd9\u79cd\u8d21\u5fb7\u4fe1\u5f92\uff0c\u5f80\u5f80\u90fd\u662f\u519b\u706b\u5546\u6216\u8005\u5927\u53d1\u660e\u5bb6\uff0c\u4e5f\u4e0d\u53ef\u80fd\u88ab\u6293\u6765\u5f53\u5974\u96b6\u8d29\u5356\u3002\u5176\u4ed6\u5982\u7cbe\u7075\u3001\u517d\u4eba\u4e4b\u6d41\uff0c\u5728\u8fd9\u65b9\u9762\u66f4\u662f\u4e00\u7a8d\u4e0d\u901a\uff0c\u5b8c\u5168\u4e0d\u5fc5\u8003\u8651\u3002\u4f46\u77ee\u4eba\u548c\u4f8f\u5112\u5219\u5c31\u4e0d\u540c\uff0c\u4ed6\u4eec\u662f\u5929\u751f\u7684\u77ff\u77f3\u4e13\u5bb6\u3001\u953b\u9020\u5de5\u5320\u548c\u53d1\u660e\u8005\uff0c\u6700\u7b26\u5408\u9634\u9b42\u57ce\u6b64\u6b21\u7684\u8981\u6c42\u3002</p><p>\u8d39\u4f26\u7684\u77ee\u4eba\uff0c\u5927\u4f53\u4e0a\u662f\u91d1\u77ee\u4eba\u3001\u76fe\u77ee\u4eba\u548c\u7070\u77ee\u4eba\u4e09\u79cd\uff0c\u636e\u8bf4\u8fd8\u6709\u4e9b\u5176\u4ed6\u7f55\u89c1\u7684\u4e9a\u79cd\uff1b\u4f8f\u5112\u5219\u5206\u4e3a\u5730\u5e95\u4f8f\u5112\u548c\u5ca9\u4f8f\u5112\u4e24\u79cd\u3002\u65e0\u8bba\u662f\u77ee\u4eba\u8fd8\u662f\u4f8f\u5112\uff0c\u90fd\u662f\u6240\u8c13\u7684\u201c\u77f3\u4e4b\u65cf\u88d4\u201d\uff0c\u64c5\u957f\u548c\u77f3\u5934\u6253\u4ea4\u9053\uff0c\u64c5\u957f\u63a2\u77ff\u3001\u6316\u77ff\u548c\u51b6\u70bc\u5de5\u4f5c\u3002\u53e6\u5916\uff0c\u77ee\u4eba\u8fd8\u4ee5\u91d1\u5c5e\u953b\u9020\u8457\u79f0\uff0c\u65cf\u4e2d\u6709\u5f88\u591a\u4f18\u79c0\u7684\u953b\u9020\u5927\u5e08\uff0c\u4e00\u526f\u540c\u6837\u7684\u6b66\u5668\u6216\u8005\u94e0\u7532\uff0c\u53ea\u8981\u6807\u660e\u662f\u201c\u77ee\u4eba\u5236\u54c1\u201d\uff0c\u5f80\u5f80\u4ef7\u683c\u5c31\u4f1a\u989d\u5916\u9ad8\u51fa\u51e0\u500d\uff08\u5f88\u591a\u5978\u5546\u5c31\u7528\u8fd9\u79cd\u529e\u6cd5\u6b3a\u9a97\u667a\u5546\u4e0d\u591f\u7684\u987e\u5ba2\uff09\uff1b\u800c\u4f8f\u5112\u5219\u5927\u591a\u64c5\u957f\u7cbe\u7ec6\u7684\u673a\u68b0\u5de5\u827a\uff0c\u5c24\u5176\u662f\u5ca9\u4f8f\u5112\uff0c\u4ed6\u4eec\u5c31\u662f\u53d1\u70df\u706b\u836f\u7684\u53d1\u660e\u8005\u3002</p><p>\u5341\u56db\u5e74\u524d\uff0c\u5c31\u662f\u5c45\u4f4f\u5728\u5170\u6a80\u5c9b\uff08\u4f4d\u4e8e\u8fdc\u79bb\u5927\u9646\u7684\u65e0\u75d5\u4e4b\u6d77\u4e0a\uff09\u7684\u5ca9\u4f8f\u5112\u4eec\uff0c\u53d1\u660e\u4e86\u53d1\u70df\u706b\u836f\u8fd9\u79cd\u4e1c\u897f\uff0c\u914d\u65b9\u540e\u6765\u6d41\u4f20\u5230\u5927\u9646\u3002\u5f53\u7136\u53e6\u5916\u4e5f\u6709\u4f20\u8bf4\uff0c\u8bf4\u662f\u953b\u9020\u4e0e\u5de5\u827a\u4e4b\u795e\u8d21\u5fb7\u5f53\u65f6\u5723\u8005\u964d\u4e34\uff0c\u5c31\u843d\u5728\u4e86\u5170\u6a80\u5c9b\uff0c\u53d1\u70df\u706b\u836f\u662f\u8fd9\u4f4d\u795e\u7947\u4f20\u6388\u7ed9\u5ca9\u4f8f\u5112\u7684\u3002</p><p>\u4ea4\u6613\u8c08\u5f97\u5f88\u987a\u5229\uff0c\u8fd9\u6700\u4e3b\u8981\u7684\u539f\u56e0\u662f\u743c\u6069\u61d2\u5f97\u8ba8\u4ef7\u8fd8\u4ef7\uff0c\u53cd\u6b63\u662f\u9634\u9b42\u57ce\u4ed8\u8d26\uff0c\u4e0d\u9700\u8981\u81ea\u5df1\u638f\u8170\u5305\uff0c\u4ed6\u53c8\u4f55\u5fc5\u66ff\u653f\u5e9c\u7701\u94b1\u3002\u4e16\u754c\u4e0a\u7684\u751f\u610f\uff0c\u53ea\u8981\u4e70\u4e3b\u4e0d\u6015\u82b1\u94b1\uff0c\u90a3\u4e5f\u5f88\u5c11\u6709\u8c08\u4e0d\u62e2\u7684\uff0c\u4f55\u51b5\u5f17\u96f7\u65af\u4e5f\u6709\u5fc3\u7ed3\u4ea4\u9634\u9b42\u57ce\u8fd9\u79cd\u5927\u4e70\u4e3b\uff0c\u5728\u4ef7\u683c\u4e0a\u5e76\u4e0d\u82db\u523b\u3002\u6700\u540e\u7684\u7ed3\u679c\uff0c\u662f\u9634\u9b42\u57ce\u4ed8\u51fa\u4e24\u5343\u91d1\u5e01\uff0c\u4e70\u4e0b\u4e86\u516d\u4e2a\u77ee\u4eba\u548c\u4e09\u4e2a\u5ca9\u4f8f\u5112\uff0c\u8fd9\u4e5d\u4eba\u90fd\u662f\u7ecf\u8fc7\u7cbe\u6311\u7ec6\u9009\u7684\uff0c\u6216\u8005\u662f\u51fa\u8272\u7684\u953b\u9020\u5de5\u5320\uff0c\u6216\u8005\u662f\u4f18\u79c0\u7684\u673a\u68b0\u5e08\uff0c\u5176\u4e2d\u6709\u4e00\u4f4d\u5ca9\u4f8f\u5112\u751a\u81f3\u8fd8\u53d1\u660e\u4e86\u949f\u8868\uff0c\u867d\u7136\u6781\u7c97\u7cd9\u4e0d\u51c6\uff0c\u4f46\u5df2\u7ecf\u8db3\u591f\u8ba9\u743c\u6069\u60ca\u8bb6\u4e86\u3002</p><p>\u5dee\u4f7f\u5b8c\u6210\uff0c\u4f46\u743c\u6069\u5e76\u4e0d\u6025\u4e8e\u56de\u9634\u9b42\u57ce\uff0c\u96be\u5f97\u51fa\u6765\u4e00\u8d9f\uff0c\u53c8\u6709\u73ca\u5609\u5728\u8eab\u8fb9\uff0c\u81ea\u7136\u8981\u4e58\u673a\u653e\u677e\u3002\u4ed6\u5c06\u4e70\u4e0b\u7684\u4e5d\u4e2a\u5974\u96b6\u6682\u65f6\u5148\u5b58\u653e\u5728\u5f17\u96f7\u65af\u5148\u751f\u90a3\u91cc\uff0c\u8bf4\u597d\u4e0b\u5348\u6765\u53d6\uff0c\u7136\u540e\u5e26\u7740\u73ca\u5609\u51fa\u95e8\uff0c\u51c6\u5907\u53bb\u5e02\u573a\u4e0a\u770b\u770b\uff0c\u987a\u4fbf\u7ed9\u59d0\u59d0\u4e70\u4e9b\u793c\u7269\u3002</p><p>\u8d70\u51fa\u5f17\u96f7\u65af\u5148\u751f\u7684\u5e9c\u90b8\u65f6\uff0c\u743c\u6069\u7a81\u7136\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u73ca\u5609\u5947\u602a\u5730\u95ee\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u521a\u624d\u5f17\u96f7\u65af\u5148\u751f\u5bf9\u6211\u8bf4\u4e86\u53e5\u8bdd\uff0c\u4f60\u77e5\u9053\u662f\u4ec0\u4e48\u5417\uff1f\u201d</p><p>\u73ca\u5609\u6447\u5934\uff0c\u5979\u4e0d\u61c2\u901a\u7528\u8bed\uff0c\u5728\u8fd9\u91cc\u538b\u6839\u5c31\u6ca1\u6cd5\u548c\u4eba\u4ea4\u6d41\uff0c\u9664\u4e86\u743c\u6069\u4e4b\u5916\uff0c\u201c\u4ed6\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4ed6\u8bf4\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u60a8\u7684\u592b\u4eba\u548c\u60a8\u771f\u5f97\u592a\u50cf\u4e86\uff0c\u7b80\u76f4\u662f\u5929\u9020\u5730\u8bbe\u7684\u4e00\u5bf9\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u662f\u8fd9\u6837\uff0c\u6211\u5728\u4ecb\u7ecd\u4f60\u7684\u65f6\u5019\uff0c\u53ea\u8bf4\u4e86\u4f60\u7684\u540d\u5b57\uff0c\u4f46\u5fd8\u4e86\u8bf4\u4f60\u662f\u6211\u59d0\u59d0\u3002\u7ed3\u679c\u2026\u2026\u201d\u4ed6\u65e0\u8f9c\u5730\u644a\u5f00\u624b\uff0c\u201c\u6211\u60f3\u4ed6\u53ef\u80fd\u662f\u6709\u4ec0\u4e48\u8bef\u4f1a\u4e86\u5427\u3002\u201d</p><p>\u8fd9\u4e2a\u4e16\u754c\u7684\u89c4\u77e9\uff0c\u5973\u5b50\u51fa\u5ac1\u540e\uff0c\u4fbf\u8981\u6539\u968f\u592b\u59d3\uff0c\u653e\u5f03\u539f\u672c\u7684\u59d3\u6c0f\u3002\u5f17\u96f7\u65af\u89c1\u743c\u6069\u548c\u73ca\u5609\u795e\u6001\u4eb2\u5bc6\uff0c\u4fe8\u7136\u7231\u4fa3\uff0c\u53c8\u90fd\u59d3\u201c\u5170\u5c3c\u65af\u7279\u201d\uff0c\u4fbf\u9ed8\u8ba4\u73ca\u5609\u662f\u743c\u6069\u7684\u59bb\u5b50\u3002\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u743c\u6069\u548c\u73ca\u5609\u76f8\u8c8c\u9887\u50cf\uff0c\u5f17\u96f7\u65af\u8fd9\u79cd\u8001\u5546\u4eba\uff0c\u4e5f\u5e94\u8be5\u60f3\u5230\u662f\u59d0\u5f1f\u6216\u8005\u5144\u59b9\u7684\u53ef\u80fd\u6027\u624d\u5bf9\u5427\u3002</p><p>\u73ca\u5609\u77aa\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u5fd8\u4e86\uff1f\u201d\u5979\u88c5\u4f5c\u5f88\u751f\u6c14\u5730\u8bf4\uff0c\u5634\u89d2\u5374\u9690\u9690\u6d6e\u51fa\u7b11\u610f\uff0c\u201c\u5176\u5b9e\u6839\u672c\u5c31\u662f\u4f60\u6545\u610f\u7684\u5427\u3002\u201d</p><p>\u201c\u59d0\u59d0\uff0c\u4e0d\u8981\u80e1\u4e71\u51a4\u6789\u4eba\u5462\uff0c\u8bf4\u8bdd\u662f\u8981\u8bb2\u8bc1\u636e\u7684\u554a\u3002\u201d</p><p>\u201c\u54fc\uff0c\u4f60\u770b\u4f60\u81ea\u5df1\u73b0\u5728\u90a3\u7b11\u5f97\u5f97\u610f\u7684\u6837\u5b50\uff0c\u8fd8\u8981\u4ec0\u4e48\u8bc1\u636e\u3002\u201d</p><p>\u201c\u53cd\u6b63\u4e5f\u6ca1\u5173\u7cfb\u561b\uff0c\u59d0\u59d0\u96be\u5f97\u51fa\u6765\u73a9\uff0c\u8fd9\u91cc\u53c8\u6ca1\u4eba\u8ba4\u8bc6\u6211\u4eec\uff0c\u59d0\u59d0\u5c31\u5f53\u6211\u59bb\u5b50\u4e0d\u597d\u4e48\u3002\u201d</p><p>\u73ca\u5609\u5fae\u5fae\u7b11\u7740\uff0c\u201c\u597d\uff0c\u201d\u5979\u67d4\u58f0\u8bf4\uff0c\u201c\u4eca\u5929\u59d0\u59d0\u5c31\u5f53\u4f60\u59bb\u5b50\u3002\u201d</p><p>\u743c\u6069\u63fd\u7740\u5979\u7684\u7ea4\u8170\uff0c\u4e24\u4eba\u5e76\u80a9\u800c\u884c\uff0c\u4e00\u8def\u95f2\u8c08\u7740\uff0c\u5f88\u5feb\u8d70\u5230\u5e02\u573a\u533a\u3002\u524d\u6bb5\u65f6\u95f4\u7ea2\u888d\u5deb\u5e08\u4f1a\u5185\u90e8\u7684\u6743\u529b\u6597\u4e89\uff0c\u56db\u4f4d\u9996\u5e2d\u5927\u5deb\u5e08\u7684\u9aa4\u7136\u6b7b\u4ea1\uff0c\u770b\u8d77\u6765\u5e76\u6ca1\u6709\u5bf9\u585e\u5c14\u7684\u666e\u901a\u6c11\u4f17\u9020\u6210\u4ec0\u4e48\u5f71\u54cd\uff0c\u4e00\u5207\u90fd\u548c\u743c\u6069\u521d\u5230\u6b64\u5730\u65f6\u770b\u5230\u7684\u4e00\u6837\uff0c\u8857\u4e0a\u4f9d\u65e7\u4eba\u6765\u4eba\u5f80\uff0c\u5e02\u573a\u4e0a\u4f9d\u65e7\u7199\u7199\u6518\u6518\uff0c\u5404\u79cd\u53eb\u5356\u58f0\u6b64\u8d77\u5f7c\u4f0f\uff0c\u6291\u626c\u987f\u632b\u3002\u73ca\u5609\u4ece\u5c0f\u5728\u9634\u9b42\u57ce\u957f\u5927\uff0c\u8fd9\u662f\u7b2c\u4e00\u6b21\u5916\u51fa\uff0c\u4e0d\u7531\u89c9\u5f97\u683c\u5916\u65b0\u9c9c\uff0c\u66f4\u4f55\u51b5\u585e\u5c14\u7684\u7e41\u534e\u7a0b\u5ea6\uff0c\u8fdc\u8fdc\u4e0d\u662f\u9634\u9b42\u57ce\u53ef\u6bd4\u7684\u3002\u743c\u6069\u89c1\u5979\u9ad8\u5174\uff0c\u5fc3\u4e2d\u4fbf\u89c9\u5f97\u8bf4\u4e0d\u51fa\u7684\u5feb\u4e50\uff0c\u6b63\u60f3\u62c9\u7740\u5979\u53bb\u770b\u770b\u8863\u670d\uff0c\u80cc\u540e\u5374\u7a81\u7136\u88ab\u4eba\u53eb\u4f4f\u4e86\u3002</p><p>\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u3002\u201d</p><p>\u8fd9\u662f\u4e00\u4e2a\u5e74\u8f7b\u5973\u5b50\u7684\u58f0\u97f3\uff0c\u6e05\u8106\u4e2d\u7565\u5e26\u7740\u4e9b\u4f4e\u6c89\uff0c\u5374\u662f\u5341\u5206\u964c\u751f\u3002\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u8f6c\u8fc7\u8eab\uff0c\u770b\u89c1\u4e00\u4e2a\u5c11\u5973\u6b63\u671d\u81ea\u5df1\u8d70\u6765\u3002\u5979\u7684\u8863\u9970\u6253\u626e\u50cf\u662f\u4e2a\u4f8d\u5973\uff0c\u5e74\u7eaa\u5927\u7ea6\u5728\u5341\u516d\u4e03\u5c81\uff0c\u8bf4\u4e0d\u4e0a\u5f88\u6f02\u4eae\uff0c\u4f46\u7709\u773c\u6e05\u79c0\uff0c\u4e94\u5b98\u8f6e\u5ed3\u9c9c\u660e\uff0c\u5634\u5507\u5f88\u8584\uff0c\u9762\u90e8\u7ebf\u6761\u9887\u6709\u4e9b\u786c\u6717\u7684\u611f\u89c9\u3002\u743c\u6069\u770b\u7740\u6709\u4e9b\u773c\u719f\uff0c\u4f46\u4e00\u65f6\u600e\u4e48\u4e5f\u60f3\u4e0d\u8d77\u6765\u81ea\u5df1\u4f55\u65f6\u89c1\u8fc7\uff0c\u4ed6\u4e00\u5171\u4e5f\u53ea\u6765\u8fc7\u4e24\u6b21\u585e\u5c14\uff0c\u7406\u5f53\u6ca1\u4ec0\u4e48\u719f\u4eba\u624d\u5bf9\u554a\u3002</p><p>\u6b63\u81ea\u7591\u60d1\uff0c\u73ca\u5609\u6084\u58f0\u8bf4\uff0c\u201c\u597d\u50cf\u662f\u5f17\u96f7\u65af\u5148\u751f\u5bb6\u7684\u4f8d\u5973\u3002\u201d</p><p>\u88ab\u5979\u4e00\u63d0\u9192\uff0c\u743c\u6069\u4e5f\u60f3\u4e86\u8d77\u6765\uff0c\u4e00\u5f00\u59cb\u5728\u5ba2\u5385\u91cc\u95f2\u8c08\u7684\u65f6\u5019\uff0c\u786e\u5b9e\u662f\u6709\u51e0\u4e2a\u4f8d\u5973\u5728\u65c1\u8fb9\uff0c\u8fd9\u4f4d\u5c11\u5973\u4f3c\u4e4e\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u96be\u602a\u6709\u70b9\u5370\u8c61\u3002\u7136\u800c\u5979\u53eb\u81ea\u5df1\u505a\u4ec0\u4e48\uff0c\u96be\u4e0d\u6210\u5f17\u96f7\u65af\u90a3\u8fb9\u51fa\u4e86\u4ec0\u4e48\u95ee\u9898\uff1f</p><p>\u7565\u4e00\u601d\u7d22\uff0c\u5c11\u5973\u5df2\u7ecf\u8d70\u5230\u9762\u524d\uff0c\u201c\u60a8\u597d\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u5192\u6627\u6253\u6270\u4e86\uff0c\u201d\u5979\u4e0d\u5351\u4e0d\u4ea2\u5730\u8bf4\uff0c\u201c\u80fd\u803d\u8bef\u60a8\u51e0\u5206\u949f\u5417\uff1f\u201d</p><p>\u201c\u6709\u4ec0\u4e48\u4e8b\u60c5\u5417\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u6211\u60f3\u548c\u5170\u5c3c\u65af\u7279\u5148\u751f\u8c08\u4e00\u7b14\u4ea4\u6613\u3002\u201d\u5c11\u5973\u8bf4\u3002</p><p>\u201c\u4ea4\u6613\uff1f\u4ec0\u4e48\u4ea4\u6613\uff1f\u201d</p><p>\u5c11\u5973\u56db\u9762\u770b\u770b\uff0c\u201c\u80fd\u8bf7\u4e24\u4f4d\u5230\u90a3\u8fb9\u7684\u9152\u9986\u91cc\u8be6\u8c08\u5417\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8fd9\u91cc\u4eba\u592a\u591a\uff0c\u4e0d\u65b9\u4fbf\u8bf4\u8bdd\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u72b9\u8c6b\uff0c\u4e0d\u77e5\u9053\u8fd9\u5c11\u5973\u5230\u5e95\u662f\u4ec0\u4e48\u76ee\u7684\uff0c\u4ed6\u7528\u8010\u745f\u8bed\u628a\u60c5\u51b5\u5bf9\u73ca\u5609\u8bf4\u4e86\u3002\u201c\u6211\u89c9\u5f97\u5979\u4e0d\u50cf\u6709\u6076\u610f\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u53cd\u6b63\u6211\u4eec\u4e5f\u6ca1\u4e8b\u60c5\uff0c\u542c\u542c\u5979\u8bf4\u4ec0\u4e48\u5427\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u4e09\u4eba\u8d70\u8fdb\u65c1\u8fb9\u7684\u9152\u9986\uff0c\u5c11\u5973\u62db\u6765\u4f8d\u8005\uff0c\u8981\u4e86\u4e00\u95f4\u5355\u72ec\u7684\u623f\u95f4\uff0c\u5979\u51e0\u4e4e\u5c06\u53e3\u888b\u91cc\u6240\u6709\u7684\u94b1\u5168\u90e8\u638f\u5149\u4e86\uff0c\u603b\u7b97\u4ed8\u8d77\u4e86\u8d26\u3002\u743c\u6069\u60f3\u76f4\u63a5\u66ff\u5979\u4ed8\u4e86\u8fd9\u70b9\u94b1\uff0c\u4f46\u88ab\u5ba2\u6c14\u800c\u575a\u51b3\u5730\u62d2\u7edd\u4e86\u3002\u201c\u8fd9\u5e94\u8be5\u662f\u7531\u6211\u6765\u4ed8\u7684\u3002\u201d\u5979\u8bf4\u3002</p><p>\u743c\u6069\u5bf9\u8fd9\u4e2a\u5c11\u5973\u4ea7\u751f\u4e86\u70b9\u5174\u8da3\u3002</p><p>\u8d70\u8fdb\u623f\u95f4\uff0c\u5173\u4e0a\u95e8\uff0c\u4e09\u4eba\u5728\u684c\u524d\u5750\u4e0b\u3002\u201c\u600e\u4e48\u79f0\u547c\u4f60\uff0c\u5c0f\u59d0\u3002\u201d\u743c\u6069\u95ee\u5c11\u5973\u3002</p><p>\u201c\u83f2\u4e9a\u3002\u201d</p><p>\u201c\u90a3\u4e48\uff0c\u627e\u6211\u505a\u4ec0\u4e48\u4ea4\u6613\u5462\uff0c\u83f2\u4e9a\u5c0f\u59d0\u3002\u201d\u743c\u6069\u4e5f\u76f4\u622a\u4e86\u5f53\u5730\u8bf4\u3002</p><p>\u201c\u6211\u6709\u4e00\u4e2a\u54e5\u54e5\uff0c\u4ed6\u53eb\u8fea\u74e6\u514b\uff0c\u73b0\u5728\u5728\u51b3\u6597\u573a\u91cc\u5f53\u89d2\u6597\u58eb\uff0c\u4f46\u4ed6\u5e76\u4e0d\u662f\u4e2a\u51fa\u8272\u7684\u6218\u58eb\uff0c\u6bcf\u5929\u90fd\u8981\u9762\u4e34\u6b7b\u4ea1\u7684\u5a01\u80c1\uff0c\u201d\u83f2\u4e9a\u8bf4\uff0c\u201c\u6211\u60f3\u8bf7\u60a8\u628a\u4ed6\u8d4e\u51fa\u6765\u3002\u201d</p><p>\u201c\u6211\u8fd9\u4e48\u505a\u7684\u7406\u7531\u5462\uff1f\u201d</p><p>\u201c\u8fea\u74e6\u514b\u662f\u4e2a\u51fa\u8272\u7684\u63a2\u77ff\u8005\uff0c\u5bf9\u77f3\u5934\u6709\u7b2c\u4e00\u6d41\u7684\u611f\u5e94\u80fd\u529b\uff0c\u6211\u60f3\u4ed6\u5bf9\u60a8\u5e94\u8be5\u4f1a\u6709\u4ef7\u503c\u3002\u201d</p><p>\u743c\u6069\u7684\u7709\u5934\u5fae\u5fae\u76b1\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d</p><p>\u83f2\u4e9a\u8a00\u4e0b\u4e4b\u610f\uff0c\u5206\u660e\u662f\u77e5\u9053\u743c\u6069\u6b64\u6b21\u7684\u6765\u610f\u3002\u4f46\u521a\u624d\u743c\u6069\u548c\u5f17\u96f7\u65af\u5728\u5ba2\u5385\u7684\u65f6\u5019\u53ea\u662f\u5bd2\u6684\u5ba2\u5957\uff0c\u5e76\u6ca1\u6709\u6d89\u53ca\u6b63\u9898\uff0c\u771f\u6b63\u7684\u4ea4\u6613\u5546\u8c08\uff0c\u662f\u5c4f\u9000\u5de6\u53f3\uff0c\u5728\u5bc6\u5ba4\u91cc\u8fdb\u884c\u7684\u3002\u5bc6\u5ba4\u5468\u56f4\u5e03\u6709\u9759\u97f3\u7ed3\u754c\uff0c\u91cc\u9762\u7684\u4eba\u5c31\u7b97\u5927\u58f0\u55a7\u95f9\uff0c\u5728\u5916\u9762\u5e94\u8be5\u4e5f\u662f\u542c\u4e0d\u89c1\u3002\u8fd9\u4e2a\u53eb\u83f2\u4e9a\u7684\u5c11\u5973\uff0c\u5374\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u96be\u9053\u81ea\u5df1\u8d70\u540e\u5f17\u96f7\u65af\u544a\u8bc9\u5979\u7684\uff1f</p><p>\u8fd9\u79cd\u4e8b\u60c5\uff0c\u5f17\u96f7\u65af\u6ca1\u9053\u7406\u4f1a\u544a\u8bc9\u4e00\u4e2a\u4f8d\u5973\u5427\u3002</p><p>\u201c\u8bf7\u539f\u8c05\uff0c\u6211\u5077\u542c\u5230\u4e86\u4f60\u4eec\u7684\u8c08\u8bdd\uff0c\u201d\u83f2\u4e9a\u4f4e\u5934\u9053\u6b49\u8bf4\uff0c\u201c\u4f46\u6211\u522b\u65e0\u4ed6\u6cd5\u3002\u201d</p><p>\u4f46\u95ee\u9898\u662f\u4f60\u600e\u4e48\u80fd\u5077\u542c\u5230\uff1f</p><p>\u743c\u6069\u5e76\u6ca1\u6709\u8be2\u95ee\u8fd9\u4e2a\u95ee\u9898\uff0c\u201c\u6211\u60f3\u5728\u611f\u5e94\u77ff\u77f3\u8fd9\u65b9\u9762\uff0c\u6211\u66f4\u5e94\u8be5\u76f8\u4fe1\u77ee\u4eba\u6216\u8005\u4f8f\u5112\u7684\u5929\u8d4b\u3002\u201d</p><p>\u201c\u4ed6\u662f\u4e2a\u4f8f\u5112\uff0c\u5730\u5e95\u4f8f\u5112\uff0c\u201d\u83f2\u4e9a\u8bf4\uff0c\u201c\u6765\u81ea\u5e03\u7075\u767b\u77f3\u57ce\uff0c\u66fe\u7ecf\u662f\u63a2\u77ff\u961f\u7684\u4e00\u5458\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u8bb6\u5f02\u5730\u770b\u7740\u5979\uff0c\u201c\u4f60\u8bf4\u4f60\u54e5\u54e5\u8fea\u74e6\u514b\u5148\u751f\u662f\u4e2a\u4f8f\u5112\uff1f\u201d</p><p>\u201c\u6ca1\u6709\u8840\u7f18\u5173\u7cfb\uff0c\u201d\u83f2\u4e9a\u7b80\u6d01\u5730\u89e3\u91ca\uff0c\u201c\u4ed6\u662f\u6211\u517b\u7236\u7684\u513f\u5b50\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u743c\u6069\u70b9\u70b9\u5934\uff0c\u4ed6\u5176\u5b9e\u4e0d\u77e5\u9053\u5e03\u7075\u767b\u77f3\u57ce\u662f\u4e2a\u4ec0\u4e48\u5730\u65b9\uff0c\u6682\u65f6\u4e5f\u4e0d\u60f3\u5bf9\u83f2\u4e9a\u548c\u5979\u90a3\u4f4d\u4f8f\u5112\u54e5\u54e5\u4e4b\u95f4\u7684\u5173\u7cfb\u6765\u5386\u8fc7\u591a\u6df1\u7a76\uff0c\u201c\u4f46\u662f\u83f2\u4e9a\u5c0f\u59d0\uff0c\u65e2\u7136\u4f60\u542c\u5230\u6211\u4eec\u7684\u8c08\u8bdd\uff0c\u5c31\u5e94\u8be5\u6e05\u695a\u6211\u7684\u6765\u610f\u3002\u6211\u66f4\u9700\u8981\u7684\uff0c\u662f\u80fd\u591f\u5728\u91d1\u5c5e\u953b\u9020\u6216\u8005\u673a\u68b0\u5de5\u827a\u65b9\u9762\u7684\u4e13\u5bb6\u3002\u6211\u76f8\u4fe1\u4f60\u7684\u54e5\u54e5\u662f\u4e00\u4f4d\u51fa\u8272\u7684\u63a2\u77ff\u8005\uff0c\u4f46\u6211\u76f8\u4fe1\uff0c\u5f88\u591a\u77ee\u4eba\u6216\u8005\u4f8f\u5112\u90fd\u80fd\u540c\u6837\u505a\u5230\u8fd9\u70b9\uff0c\u6211\u8bf4\u5f97\u5bf9\u5417\uff1f\u201d</p><p>\u743c\u6069\u4eca\u5929\u4e70\u4e86\u4e5d\u4e2a\u5974\u96b6\uff0c\u516d\u4e2a\u77ee\u4eba\u548c\u4e09\u4e2a\u5ca9\u4f8f\u5112\uff0c\u5176\u4e2d\u6ca1\u6709\u5730\u5e95\u4f8f\u5112\uff0c\u8fd9\u662f\u6709\u7f18\u6545\u7684\u3002\u4f8f\u5112\u7684\u4e24\u5927\u4e9a\u79cd\u4e2d\uff0c\u5730\u5e95\u4f8f\u5112\u548c\u77ee\u4eba\u5f88\u8fd1\u4f3c\uff0c\u540c\u6837\u90fd\u64c5\u957f\u63a2\u7d22\u77ff\u4ea7\u3001\u611f\u5e94\u5ca9\u77f3\uff0c\u4f46\u5728\u953b\u9020\u5929\u8d4b\u4e0a\u5374\u900a\u8272\u77ee\u4eba\u4e00\u7b79\uff1b\u800c\u8981\u8bba\u53ca\u673a\u68b0\u5de5\u827a\uff0c\u5730\u5e95\u4f8f\u5112\u53c8\u6bd4\u4e0d\u4e0a\u5ca9\u4f8f\u5112\u3002\u603b\u5f97\u6765\u8bf4\uff0c\u5730\u5e95\u4f8f\u5112\u5c31\u50cf\u662f\u5939\u6742\u5728\u77ee\u4eba\u548c\u5ca9\u4f8f\u5112\u4e4b\u95f4\uff0c\u63a2\u7d22\u77ff\u4ea7\u3001\u611f\u5e94\u5ca9\u77f3\u3001\u91d1\u5c5e\u953b\u9020\u3001\u673a\u68b0\u5de5\u827a\uff0c\u4ed6\u4eec\u90fd\u4f1a\u4e00\u4e9b\uff0c\u4f46\u90fd\u4e0d\u7b97\u6700\u7cbe\u901a\uff0c\u7f3a\u4e4f\u81ea\u5df1\u7684\u7279\u8272\u3002\u9634\u9b42\u57ce\u8981\u7684\u662f\u4e13\u5bb6\u4e0d\u662f\u901a\u624d\uff0c\u81ea\u7136\u5bf9\u5730\u5e95\u4f8f\u5112\u5c31\u4e0d\u751a\u770b\u91cd\u4e86\u3002</p><p>\u201c\u60a8\u8bf4\u5f97\u5bf9\u3002\u201d\u83f2\u4e9a\u6c89\u7740\u5730\u8bf4\u3002</p><p>\u201c\u90a3\u4e48\uff0c\u4f60\u7684\u54e5\u54e5\u8fd8\u62e5\u6709\u53e6\u5916\u7684\u503c\u5f97\u6211\u770b\u91cd\u7684\u80fd\u529b\uff0c\u662f\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u201d\u83f2\u4e9a\u8bf4\uff0c\u201c\u4ed6\u7684\u683c\u6597\u6280\u5de7\u4e0d\u5dee\uff0c\u4f46\u4e5f\u4ec5\u4ec5\u53ea\u662f\u4e0d\u5dee\uff1b\u4ed6\u4f1a\u70b9\u5e7b\u672f\uff0c\u4f46\u4e5f\u5e76\u4e0d\u6bd4\u5176\u4ed6\u4f8f\u5112\u66f4\u9ad8\u660e\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u6ca1\u6709\u4ec0\u4e48\u683c\u5916\u7684\u4e13\u957f\u3002\u201d</p><p>\u201c\u90a3\u4e48\uff0c\u4f60\u7528\u4ec0\u4e48\u6765\u8bf4\u670d\u6211\uff0c\u53bb\u4e70\u4e00\u4e2a\u5bf9\u6211\u800c\u8a00\u6ca1\u6709\u4ef7\u503c\u7684\u4eba\u5462\u3002\u201d</p><p>\u201c\u5e76\u975e\u6ca1\u6709\u4ef7\u503c\uff0c\u53ea\u662f\u4ef7\u503c\u4e0d\u591f\u5927\u3002\u201d\u83f2\u4e9a\u7ea0\u6b63\u3002</p><p>\u201c\u6216\u8bb8\uff0c\u4f46\u8fd9\u4f9d\u65e7\u4e0d\u80fd\u6539\u53d8\u95ee\u9898\u3002\u201d</p><p>\u743c\u6069\u76ef\u7740\u9762\u524d\u7684\u5c11\u5973\uff0c\u53d1\u73b0\u5979\u4e1d\u6beb\u6ca1\u6709\u5c40\u4fc3\u4e0d\u5b89\u4e4b\u8272\uff0c\u4eff\u4f5b\u4e00\u5207\u80f8\u6709\u6210\u7af9\uff0c\u8fd9\u66f4\u4ee4\u4ed6\u597d\u5947\u8d77\u6765\u3002\u201c\u65e2\u7136\u8bf4\u662f\u8981\u505a\u4ea4\u6613\uff0c\u90a3\u4e48\u83f2\u4e9a\u5c0f\u59d0\uff0c\u4f60\u7684\u51fa\u4ef7\u662f\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u83f2\u4e9a\u662f\u5f17\u96f7\u65af\u5bb6\u7684\u4f8d\u5973\uff0c\u5979\u4e0d\u592a\u53ef\u80fd\u6709\u591a\u5c11\u94b1\u8d22\u2014\u2014\u5982\u679c\u6709\u7684\u8bdd\uff0c\u5979\u5b8c\u5168\u53ef\u4ee5\u60f3\u529e\u6cd5\u53e6\u627e\u4eba\u628a\u54e5\u54e5\u8d4e\u51fa\u6765\uff0c\u6ca1\u5fc5\u8981\u7279\u5730\u6765\u627e\u743c\u6069\u3002\u90a3\u4e48\u5979\u80fd\u4ed8\u51fa\u4ec0\u4e48\uff1f\u96be\u9053\u7528\u81ea\u5df1\u505a\u4ea4\u6362\u4e48\uff0c\u4f46\u5979\u7684\u59ff\u8272\u4e5f\u5e76\u4e0d\u51fa\u4f17\uff0c\u66f4\u4f55\u51b5\uff0c\u5982\u679c\u5f53\u771f\u662f\u62b1\u8fd9\u4e2a\u6253\u7b97\uff0c\u5c31\u7edd\u4e0d\u5e94\u8be5\u5f53\u7740\u73ca\u5609\u7684\u9762\u3002</p><p>\u4ece\u5979\u7684\u8868\u73b0\u6765\u770b\uff0c\u4e0d\u50cf\u662f\u611a\u8822\u6216\u8005\u6ca1\u6709\u81ea\u77e5\u4e4b\u660e\u7684\u5973\u5b69\uff0c\u65e2\u7136\u6562\u8bf4\u505a\u4ea4\u6613\uff0c\u90a3\u4e48\u5c31\u5fc5\u5b9a\u662f\u6709\u51e0\u5206\u628a\u63e1\u7684\u3002\u6240\u4ee5\u743c\u6069\u7b49\u5f85\u7740\uff0c\u8981\u770b\u5979\u7684\u5e95\u724c\u5230\u5e95\u662f\u4ec0\u4e48\u3002</p><p>\u201c\u6211\u7684\u517b\u7236\u662f\u4e00\u4f4d\u9884\u8a00\u5deb\u5e08\uff0c\u4ed6\u7559\u4e0b\u4e86\u4e00\u672c\u9b54\u6cd5\u7b14\u8bb0\uff0c\u8bb0\u8f7d\u4e86\u4ed6\u5168\u90e8\u7684\u7814\u7a76\u5fc3\u5f97\uff0c\u201d\u83f2\u4e9a\u8bf4\uff0c\u201c\u4ed6\u559c\u6b22\u548c\u5ca9\u77f3\u6253\u4ea4\u9053\uff0c\u6bd5\u751f\u7684\u7cbe\u529b\u90fd\u8017\u8d39\u4e8e\u6b64\uff0c\u4ed6\u53d1\u660e\u4e86\u4e00\u4e9b\u72ec\u7279\u7684\u9b54\u6cd5\u6280\u5de7\uff0c\u53ef\u4ee5\u8ba9\u4ed6\u6e05\u695a\u611f\u5e94\u5230\u67d0\u4e00\u5730\u57df\u7684\u5ca9\u77f3\u3001\u7ed3\u6784\u3001\u65ad\u5c42\u3001\u8f90\u5c04\u70b9\u548c\u77ff\u7269\u578b\u6001\uff0c\u4ed6\u80fd\u611f\u5e94\u5230\u5927\u5730\u529b\u91cf\u7684\u5954\u6d8c\u6d41\u805a\uff0c\u751a\u81f3\u548c\u5ca9\u77f3\u5bf9\u8bdd\u4ea4\u6d41\uff0c\u5c31\u8fde\u6700\u6709\u7ecf\u9a8c\u7684\u77ee\u4eba\u548c\u5730\u5e95\u4f8f\u5112\u4e5f\u505a\u4e0d\u5230\u8fd9\u6837\u51fa\u8272\u3002\u201d</p><p>\u201c\u6211\u60f3\uff0c\u8fd9\u4efd\u9b54\u6cd5\u7b14\u8bb0\uff0c\u5bf9\u4e8e\u60a8\u548c\u9634\u9b42\u57ce\u800c\u8a00\uff0c\u5176\u4ef7\u503c\u5e94\u8be5\u62b5\u5f97\u8fc7\u4e00\u4e2a\u89d2\u6597\u58eb\u5427\u3002\u201d</p><p>\u5979\u5fae\u5fae\u626c\u7709\uff0c\u9759\u9759\u770b\u7740\u743c\u6069\u3002</p></article>"}}}]);
