(window.webpackJsonp=window.webpackJsonp||[]).push([[1437],{1460:function(p){p.exports={chapterTitle:"\u7b2c\u516d\u5377 \u8718\u86db\u7bc7",title:"\u5e8f\u7ae0",content:"<article><h1>\u7b2c\u516d\u5377 \u8718\u86db\u7bc7</h1><h2>\u5e8f\u7ae0</h2><p>\u7ef4\u5eb7\u5c3c\u4e9a\u62cd\u4e86\u62cd\u8725\u8734\u5750\u9a91\u7684\u8111\u888b\uff0c\u8ba9\u8fd9\u516b\u53ea\u722a\u7684\u5de8\u5927\u602a\u7269\u505c\u4f4f\u811a\u6b65\uff0c\u7136\u540e\u5979\u8f7b\u76c8\u5730\u8df3\u4e86\u4e0b\u6765\u3002\u7cbe\u91d1\u94f8\u9020\u7684\u5927\u95e8\u7d27\u95ed\u7740\uff0c\u4e24\u4fa7\u76d8\u8e1e\u7684\u4e24\u53ea\u5de8\u5927\u8718\u86db\u96d5\u50cf\u6c89\u9ed8\u5730\u8f6c\u52a8\u7740\u773c\u73e0\uff0c\u8fa8\u8ba4\u51fa\u4e86\u6765\u4eba\u8eab\u4efd\uff0c\u4e8e\u662f\u7ee7\u7eed\u530d\u5310\uff0c\u5728\u9ed1\u6697\u4e2d\u4fdd\u6301\u6c89\u5bc2\u3002</p><p>\u5c11\u5973\u8d70\u8fd1\uff0c\u8f7b\u58f0\u5410\u51fa\u4e00\u4e2a\u5492\u8a00\uff0c\u6fc0\u6d3b\u4e86\u957f\u888d\u4e0a\u7684\u767d\u91d1\u80f8\u9488\u3002\u9eef\u6de1\u7684\u9b54\u6cd5\u5fae\u5149\u4ece\u5176\u4e2d\u5f25\u6f2b\u51fa\u6765\uff0c\u5851\u6210\u4e00\u4e2a\u7ea4\u5de7\u7f8e\u4e3d\u7684\u9ed1\u80a4\u7cbe\u7075\uff0c\u7d27\u63a5\u7740\u53c8\u53d8\u5e7b\u6210\u4e00\u53ea\u8718\u86db\uff0c\u201c\u7ef4\u5eb7\u5c3c\u4e9a\xb7\u83f2\u5c14\u4f26\u3002\u201d\u5979\u62a5\u51fa\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u7136\u540e\u5927\u95e8\u81ea\u52a8\u6084\u65e0\u58f0\u606f\u5730\u655e\u5f00\u4e86\u3002</p><p>\u6b22\u8fce\u56de\u5bb6\uff0c\u4e00\u4e2a\u51b7\u51b0\u51b0\u7684\u58f0\u97f3\u5728\u5979\u8111\u4e2d\u54cd\u8d77\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u51dd\u89c6\u7740\u9762\u524d\u7684\u9ed1\u6697\uff0c\u770b\u89c1\u201c\u5b88\u95e8\u8005\u201d\u4ece\u865a\u65e0\u4e2d\u7f13\u7f13\u6d6e\u73b0\uff0c\u90a3\u662f\u4e00\u4e2a\u5e7d\u7075\u2014\u2014\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u591a\u4e2a\u5e7d\u7075\u7684\u878d\u5408\u4f53\u3002\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\uff0c\u6bcf\u4e2a\u5bb6\u65cf\u90fd\u6709\u8fd9\u6837\u4e00\u4f4d\u5b88\u95e8\u8005\uff0c\u5b83\u662f\u5386\u4ee3\u5bb6\u65cf\u5deb\u5e08\u6b7b\u540e\u7684\u552f\u4e00\u5f52\u5bbf\u3002\u65e0\u8bba\u5728\u751f\u524d\u6216\u8005\u6b7b\u540e\uff0c\u5deb\u5e08\u90fd\u53ea\u80fd\u4f5c\u4e3a\u5973\u795e\u7267\u5e08\u7684\u62a4\u536b\u548c\u9644\u5eb8\u800c\u5b58\u5728\uff0c\u8fd9\u4fbf\u662f\u5353\u5c14\u7684\u89c4\u5219\u3002</p><p>\u6b64\u523b\u51fa\u73b0\u5728\u7ef4\u5eb7\u5c3c\u4e9a\u9762\u524d\u7684\u201c\u5b88\u95e8\u8005\u201d\uff0c\u6b63\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\u5386\u53f2\u4e0a\u4e00\u767e\u4e09\u5341\u4e03\u4f4d\u5deb\u5e08\u7684\u7075\u9b42\u805a\u5408\uff0c\u5b83\u770b\u8d77\u6765\u50cf\u662f\u4e00\u4f4d\u82cd\u8001\u7684\u5353\u5c14\u7537\u6027\uff0c\u4f5d\u507b\u7740\u810a\u80cc\uff0c\u6d88\u7626\u7684\u8eab\u8eaf\u88f9\u5728\u7070\u8272\u6597\u7bf7\u91cc\uff0c\u552f\u6709\u53cc\u773c\u6cdb\u51fa\u6697\u7ea2\u8272\u3001\u4eff\u4f5b\u739b\u7459\u4f3c\u7684\u5fae\u5149\uff0c\u80f8\u53e3\u5219\u540c\u6837\u4f69\u6234\u7740\u7ed8\u6709\u83f2\u5c14\u4f26\u5bb6\u65cf\u5fbd\u7ae0\u7684\u767d\u91d1\u80f8\u9488\uff0c\u5c3d\u7ba1\u90a3\u53ea\u662f\u865a\u4f53\uff0c\u5e76\u975e\u5b9e\u7269\uff0c\u4f46\u6829\u6829\u5982\u771f\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u7b49\u5f85\u7740\uff0c\u76f4\u5230\u5b88\u95e8\u8005\u7684\u5f62\u4f53\u53d8\u5f97\u5b8c\u5168\u6e05\u6670\uff0c\u5979\u5fae\u5fae\u8eac\u8eab\u884c\u793c\uff0c\u7136\u540e\u4e3e\u6b65\u4e0a\u524d\uff0c\u7b14\u76f4\u5730\u4ece\u5e7d\u7075\u7684\u8eab\u4f53\u4e2d\u7a7f\u8d8a\uff0c\u8d70\u8fdb\u5b85\u9662\uff0c\u8fd9\u4e2a\u770b\u4f3c\u65e0\u793c\u800c\u5b69\u5b50\u6c14\u7684\u4e3e\u52a8\u5e76\u6ca1\u6709\u4f24\u5bb3\u5230\u5c11\u5973\u2014\u2014\u53ea\u8981\u4f69\u6234\u7740\u5bb6\u65cf\u5fbd\u7ae0\uff0c\u5c31\u4e0d\u4f1a\u88ab\u5b88\u95e8\u8005\u6240\u643a\u5e26\u7684\u8d1f\u80fd\u91cf\u4f24\u5bb3\uff0c\u5f53\u7136\uff0c\u4e5f\u4e0d\u4f1a\u4f24\u5bb3\u5230\u5b88\u95e8\u8005\u3002</p><p>\u8fd9\u4ec5\u4ec5\u662f\u4e00\u79cd\u60ef\u4f8b\uff0c\u6216\u8005\u8bf4\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\u666e\u904d\u901a\u884c\u7684\u4eea\u5f0f\u3002</p><p>\u5b88\u95e8\u8005\u5728\u80cc\u540e\u7f13\u7f13\u6d88\u5931\uff0c\u5b85\u9662\u7684\u5927\u95e8\u518d\u4e00\u6b21\u7d27\u95ed\u8d77\u6765\u3002\u5996\u706b\u65e0\u58f0\u5730\u71c3\u70e7\u7740\uff0c\u4e3a\u6574\u4e2a\u5b85\u9662\u63d0\u4f9b\u4e9b\u8bb8\u7684\u7167\u660e\uff0c\u4f46\u8fd9\u540c\u65f6\u4f1a\u8ba9\u7a7a\u6c14\u6e29\u5ea6\u7f13\u6162\u800c\u6301\u7eed\u5730\u4e0b\u964d\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u88f9\u7d27\u8eab\u4e0a\u7684\u6597\u7bf7\uff0c\u6cbf\u7740\u86db\u7f51\u72b6\u7684\u9053\u8def\u5728\u949f\u4e73\u77f3\u548c\u77f3\u67f1\u95f4\u7a7f\u884c\u7740\uff0c\u6700\u540e\u8d70\u8fdb\u4e86\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u4e3b\u5efa\u7b51\uff0c\u90a3\u662f\u4e00\u5ea7\u5706\u5f62\u795e\u6bbf\uff0c\u9876\u7aef\u95ea\u70c1\u7740\u7d2b\u9ed1\u8272\u7684\u90aa\u5f02\u5149\u8292\uff0c\u5916\u4fa7\u5899\u58c1\u4e0a\u904d\u5e03\u5404\u79cd\u7cbe\u7f8e\u7684\u6d6e\u96d5\uff0c\u5927\u591a\u662f\u4ee5\u8718\u86db\u4f5c\u4e3a\u56fe\u6848\u7684\u4e3b\u8981\u5185\u5bb9\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u5728\u795e\u6bbf\u5165\u53e3\u5904\u9759\u9759\u7ad9\u7acb\u7740\uff0c\u6bcf\u6b21\u8fdb\u5165\u8fd9\u5ea7\u5efa\u7b51\uff0c\u5979\u90fd\u4f1a\u4ece\u5fc3\u5e95\u5347\u8d77\u4e00\u79cd\u9519\u89c9\uff0c\u4eff\u4f5b\u81ea\u5df1\u6b63\u8981\u88ab\u67d0\u79cd\u5de8\u5927\u7684\u602a\u7269\u541e\u566c\u3002\u5979\u8d76\u7d27\u6447\u6447\u5934\uff0c\u9a71\u6563\u4e86\u8fd9\u79cd\u5bf9\u795e\u540e\u4e0d\u656c\u7684\u5ff5\u5934\uff0c\u82b1\u51e0\u79d2\u949f\u65f6\u95f4\u5e73\u590d\u5fc3\u5883\u3002\u8170\u95f4\u86c7\u97ad\u4e0a\u7684\u56db\u53ea\u6bd2\u86c7\u5636\u5636\u5730\u5410\u4fe1\uff0c\u53d1\u51fa\u51b0\u51b7\u7684\u58f0\u97f3\uff0c\u63d0\u9192\u4e3b\u4eba\u65f6\u95f4\u5df2\u7ecf\u4e0d\u65e9\u3002\u5c11\u5973\u8f7b\u8f7b\u629a\u6478\u7740\u5b83\u4eec\u7684\u8111\u888b\uff0c\u8ba9\u6bd2\u86c7\u5b89\u9759\u4e0b\u6765\uff0c\u7136\u540e\u8fdb\u5165\u795e\u6bbf\u3002</p><p>\u548c\u83f2\u5c14\u4f26\u5b85\u9662\u5176\u4ed6\u5730\u65b9\u4e0d\u540c\uff0c\u795e\u6bbf\u4e2d\u4e00\u7247\u9ed1\u6697\uff0c\u6ca1\u6709\u4efb\u4f55\u7167\u660e\uff0c\u4f46\u7ef4\u5eb7\u5c3c\u4e9a\u5e76\u4e0d\u4ecb\u610f\uff0c\u501f\u52a9\u9b54\u6cd5\u5f3a\u5316\u7684\u53cc\u773c\uff0c\u5979\u901a\u8fc7\u72ed\u957f\u66f2\u6298\u7684\u8d70\u5eca\uff0c\u51c6\u786e\u5730\u5728\u6bcf\u4e2a\u62d0\u89d2\u53d8\u6362\u65b9\u5411\uff0c\u6700\u7ec8\u5230\u8fbe\u4e86\u795e\u6bbf\u7684\u4e2d\u5fc3\u90e8\u4f4d\u3002</p><p>\u8fd9\u662f\u4e00\u4e2a\u5bbd\u655e\u7684\u623f\u95f4\uff0c\u6700\u5f15\u4eba\u6ce8\u76ee\u7684\u5730\u65b9\u662f\u796d\u575b\uff0c\u7528\u9ed1\u8272\u5927\u7406\u77f3\u7b51\u6210\uff0c\u8fb9\u6cbf\u71c3\u70e7\u7740\u516b\u4e2a\u5de8\u5927\u7684\u706b\u76c6\uff0c\u9ed1\u8272\u7684\u706b\u7130\u4ece\u5176\u4e2d\u5347\u817e\u51fa\u6765\uff0c\u5e7b\u5316\u6210\u5404\u79cd\u8718\u86db\u6216\u8005\u6076\u9b54\uff0c\u5f53\u4e2d\u4f9b\u5949\u7740\u4e00\u5ea7\u9ad8\u7ea6\u5341\u82f1\u5c3a\u7684\u7f57\u4e1d\u795e\u50cf\u3002\u5728\u9b54\u6cd5\u7684\u4f5c\u7528\u4e0b\uff0c\u9ed1\u66dc\u77f3\u5851\u6210\u7684\u795e\u50cf\u4e0d\u65ad\u53d8\u6362\u7740\u5f62\u6001\uff0c\u6216\u8005\u662f\u4e00\u4f4d\u6210\u719f\u7f8e\u8273\u7684\u9ed1\u6697\u7cbe\u7075\u5973\u5b50\uff0c\u6216\u8005\u662f\u4e00\u53ea\u6709\u7740\u8840\u7ea2\u773c\u73e0\u7684\u9ed1\u5be1\u5987\u8718\u86db\u2014\u2014\u8fd9\u662f\u7f57\u4e1d\u795e\u540e\u6700\u5e38\u5411\u4fe1\u5f92\u5c55\u793a\u7684\u4e24\u4e2a\u795e\u76f8\u3002\u796d\u575b\u524d\u9762\uff0c\u795e\u540e\u4fef\u89c6\u4e4b\u4e0b\uff0c\u662f\u4e00\u5f20\u5bbd\u5927\u6c14\u6d3e\u7684\u738b\u5ea7\uff0c\u5b83\u4f7f\u7528\u7684\u6750\u8d28\u662f\u7cbe\u91d1\u548c\u79d8\u94f6\u7684\u5b8c\u7f8e\u878d\u5408\uff0c\u7528\u5404\u79cd\u94bb\u77f3\u548c\u73e0\u5b9d\u9576\u5d4c\u88c5\u9970\uff0c\u5728\u738b\u5ea7\u6905\u80cc\u7684\u9876\u7aef\uff0c\u662f\u4e00\u53ea\u5de8\u5927\u7684\u7ea2\u9f99\u5934\u9885\u3002\u8fd9\u662f\u4e00\u4ef6\u6218\u5229\u54c1\uff0c\u66fe\u7ecf\u662f\u77ee\u4eba\u56fd\u738b\u7684\u5b9d\u5ea7\uff0c\u5982\u4eca\u5219\u6210\u4e3a\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u8363\u8000\u4e4b\u4e00\u3002</p><p>\u4e00\u4e2a\u4eba\u5750\u5728\u738b\u5ea7\u4e2d\uff0c\u88ab\u795e\u50cf\u6295\u5c04\u4e0b\u6765\u7684\u9634\u5f71\u7b3c\u7f69\uff0c\u5373\u4f7f\u662f\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u65e0\u6cd5\u5728\u8fdc\u5904\u770b\u6e05\u9762\u5bb9\uff0c\u867d\u7136\u5979\u77e5\u9053\u5bf9\u65b9\u7684\u8eab\u4efd\u3002\u738b\u5ea7\u4fa7\u9762\u4f8d\u7acb\u7740\u4e00\u4f4d\u8eab\u6750\u9ad8\u6311\u7684\u9ed1\u6697\u7cbe\u7075\u5973\u5b50\uff0c\u795e\u60c5\u4e25\u8083\uff0c\u7a7f\u7740\u9ed1\u8272\u957f\u888d\uff0c\u9576\u6709\u6697\u7ea2\u8272\u8fb9\uff0c\u7ee3\u7740\u7f51\u72b6\u82b1\u7eb9\uff0c\u5f70\u663e\u7740\u7f57\u4e1d\u9ad8\u9636\u7267\u5e08\u7684\u8eab\u4efd\u3002\u7cbe\u81f4\u957f\u888d\u4e4b\u4e0b\uff0c\u662f\u67d4\u8f6f\u5408\u8eab\u7684\u9501\u7532\uff0c\u6597\u7bf7\u5219\u4e8b\u5148\u8131\u4e0b\uff0c\u653e\u5728\u796d\u575b\u7684\u8fb9\u6cbf\u3002\u5979\u7684\u957f\u888d\u4e0a\u522b\u7740\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u540c\u6837\u7684\u767d\u91d1\u80f8\u9488\uff0c\u663e\u793a\u5979\u4eec\u7684\u540c\u65cf\u8eab\u4efd\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u8d70\u8fc7\u6765\uff0c\u540c\u6837\u5148\u8131\u53bb\u6597\u7bf7\uff0c\u653e\u5230\u796d\u575b\u8fb9\u6cbf\uff0c\u7136\u540e\u6765\u5230\u738b\u5ea7\u524d\u8eac\u8eab\u884c\u793c\u3002\u5979\u7684\u52a8\u4f5c\u4f3c\u4e4e\u6709\u4e9b\u6f2b\u4e0d\u7ecf\u5fc3\uff0c\u5f04\u51fa\u4e86\u4e9b\u54cd\u52a8\uff0c\u6253\u7834\u4e86\u623f\u95f4\u91cc\u7684\u5bc2\u9759\uff0c\u8fd9\u5f15\u8d77\u4e86\u4e0d\u6ee1\u3002</p><p>\u201c\u4f60\u5e94\u8be5\u66f4\u5b89\u9759\u4e9b\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u201d\u738b\u5ea7\u65c1\u7684\u5973\u5b50\u4f4e\u58f0\u5475\u65a5\uff0c\u201c\u8868\u73b0\u5f97\u50cf\u4e2a\u8d35\u65cf\uff0c\u4e0d\u8981\u548c\u90a3\u4e9b\u7537\u6027\u4e00\u6837\u7b28\u624b\u7b28\u811a\u3002\u201d</p><p>\u5979\u662f\u8fe6\u6cd5\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u957f\u5973\uff0c\u540c\u65f6\u4e5f\u662f\u57ce\u4e2d\u4f4d\u9636\u6700\u9ad8\u7684\u4e94\u4f4d\u7267\u5e08\u4e4b\u4e00\uff0c\u8170\u95f4\u7684\u4e03\u9996\u86c7\u97ad\u5c31\u662f\u6700\u597d\u7684\u8bc1\u660e\uff0c\u90a3\u662f\u552f\u6709\u6700\u5f97\u795e\u540e\u5ba0\u7231\u7684\u7267\u5e08\u624d\u6709\u8d44\u683c\u4f7f\u7528\u7684\u6b66\u5668\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u540c\u6837\u4e5f\u662f\u7267\u5e08\uff0c\u4f46\u4f4d\u9636\u8fdc\u8fdc\u4e0d\u5982\uff0c\u53ea\u6709\u8d44\u683c\u4f7f\u7528\u56db\u9996\u86c7\u97ad\u3002</p><p>\u201c\u522b\u8fd9\u4e48\u7cbe\u795e\u7d27\u5f20\uff0c\u59d0\u59d0\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u5fae\u7b11\u7740\uff0c\u6709\u610f\u65e0\u610f\u5730\u5c06\u624b\u653e\u5728\u80f8\u524d\uff0c\u5979\u7684\u624b\u6307\u7cbe\u81f4\u4fee\u957f\uff0c\u6cdb\u7740\u9ed1\u66dc\u77f3\u822c\u7684\u5149\u6cfd\uff0c\u6307\u7532\u4fee\u526a\u5f97\u6574\u6574\u9f50\u9f50\uff0c\u7f1d\u9699\u95f4\u5374\u8fd8\u6b8b\u7559\u7740\u4e9b\u5fae\u94f6\u8272\u7c89\u672b\uff0c\u90a3\u663e\u7136\u662f\u67d0\u79cd\u65bd\u6cd5\u6750\u6599\uff0c\u800c\u7267\u5e08\u65bd\u5c55\u795e\u672f\uff0c\u662f\u4e0d\u9700\u8981\u65bd\u6cd5\u6750\u6599\u7684\u3002\u201c\u5728\u8fd9\u79cd\u654f\u611f\u65f6\u671f\uff0c\u6211\u4eec\u66f4\u5e94\u8be5\u4fdd\u6301\u5e73\u548c\u7684\u5fc3\u6001\u624d\u662f\uff0c\u5426\u5219\u4f1a\u8ba9\u4eba\u770b\u7b11\u8bdd\u7684\u3002\u201d</p><p>\u201c\u4f60\u662f\u5728\u5411\u6211\u793a\u5a01\u5417\uff0c\u59b9\u59b9\uff1f\u201d\u8fe6\u6cd5\u51b7\u7b11\u7740\uff0c\u624b\u6309\u4e0a\u4e86\u8170\u95f4\u86c7\u97ad\u7684\u7cbe\u91d1\u624b\u67c4\uff0c\u4e03\u6761\u6bd2\u86c7\u5636\u5636\u53eb\u56b7\u7740\uff0c\u4e89\u5148\u6050\u540e\u5730\u6e38\u52a8\u7740\u8eab\u4f53\uff0c\u4f3c\u4e4e\u8feb\u4e0d\u53ca\u5f85\u5730\u60f3\u8981\u7545\u996e\u9c9c\u8840\u3002\u4f46\u7ef4\u5eb7\u5c3c\u4e9a\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5979\u77e5\u9053\u8fe6\u6cd5\u4e0d\u8fc7\u662f\u5728\u865a\u5f20\u58f0\u52bf\u3002</p><p>\u201c\u7edd\u65e0\u6b64\u610f\uff0c\u5c0a\u656c\u7684\u59d0\u59d0\uff0c\u201d\u5979\u806a\u660e\u5730\u8868\u793a\u9000\u8ba9\uff0c\u201c\u6211\u53ea\u662f\u6709\u4e9b\u75b2\u5026\u4e86\u3002\u201d</p><p>\u8fe6\u6cd5\u77aa\u7740\u5979\uff0c\u4f46\u6700\u7ec8\u653e\u5f03\u4e86\u6740\u6b7b\u5bf9\u65b9\u7684\u60f3\u6cd5\u3002\u867d\u7136\u4f5c\u4e3a\u9ad8\u9636\u7267\u5e08\uff0c\u5979\u6709\u6743\u529b\u60e9\u6212\u4e00\u4e2a\u4f4d\u9636\u8f83\u4f4e\u7684\u5192\u72af\u8005\uff0c\u795e\u540e\u4e5f\u4f1a\u8d4b\u4e88\u5979\u8fd9\u79cd\u80fd\u529b\uff0c\u4f46\u5982\u4eca\u60c5\u51b5\u548c\u5f80\u65e5\u6709\u4e9b\u4e0d\u540c\u3002</p><p>\u201c\u9003\u8dd1\u7684\u4eba\u8ffd\u5230\u4e86\u5417\uff1f\u201d\u5979\u6362\u4e86\u4e2a\u8bdd\u9898\u3002</p><p>\u201c\u4ed6\u4eec\u5df2\u7ecf\u4e0d\u5b58\u5728\u4e8e\u8fd9\u4e2a\u4e16\u754c\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u56de\u7b54\uff0c\u201c\u795e\u540e\u60f3\u5fc5\u6b63\u5728\u4eab\u7528\u4ed6\u4eec\u7684\u7075\u9b42\u3002\u201d</p><p>\u201c\u4f60\u7684\u4efb\u52a1\u662f\u5c06\u4ed6\u4eec\u5e26\u56de\u6765\u7684\uff0c\u201d\u8fe6\u6cd5\u5389\u58f0\u8bf4\uff0c\u201c\u6211\u4eec\u9700\u8981\u7ed9\u5176\u4ed6\u7537\u6027\u548c\u5974\u96b6\u4e00\u4e2a\u8b66\u544a\u3002\u201d</p><p>\u201c\u4f46\u6211\u62c5\u5fc3\u4ed6\u4eec\u4f1a\u5728\u4e34\u6b7b\u524d\u8bf4\u51fa\u4ec0\u4e48\u4eb5\u6e0e\u795e\u540e\u7684\u8c23\u8a00\uff0c\u800c\u53c8\u6070\u597d\u88ab\u5176\u4ed6\u4eba\u542c\u89c1\uff0c\u6240\u4ee5\u5c31\u81ea\u4f5c\u4e3b\u5f20\u5168\u90e8\u5904\u6b7b\u4e86\u3002\u201d</p><p>\u201c\u8c0e\u8a00\uff01\u201d\u8fe6\u6cd5\u6012\u6c14\u51b2\u51b2\uff0c\u201c\u4f60\u53ea\u662f\u4e0d\u559c\u6b22\u542c\u89c1\u4ed6\u4eec\u7684\u60e8\u53eb\u58f0\u3002\u201d</p><p>\u201c\u5e76\u975e\u5982\u6b64\uff0c\u59d0\u59d0\uff0c\u201d\u9762\u5bf9\u6307\u8d23\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4e0d\u52a8\u58f0\u8272\uff0c\u201c\u53ea\u4e0d\u8fc7\u6211\u66f4\u5584\u4e8e\u7528\u7406\u667a\u6765\u63a7\u5236\u60c5\u611f\u7f62\u4e86\u3002\u5728\u5f53\u524d\u7684\u60c5\u51b5\u4e0b\uff0c\u6bd4\u8d77\u60e9\u6212\uff0c\u6211\u89c9\u5f97\u4fdd\u5b88\u79d8\u5bc6\u66f4\u52a0\u91cd\u8981\u4e00\u4e9b\u3002\u201d</p><p>\u201c\u4e00\u5982\u65e2\u5f80\u7684\u8f6f\u5f31\u3002\u201d\u8fe6\u6cd5\u8bc4\u4ef7\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u6012\u89c6\u7740\u59d0\u59d0\uff0c\u7136\u800c\u738b\u5ea7\u4e0a\u7684\u4eba\u9002\u65f6\u5730\u8bf4\u8bdd\uff0c\u6253\u65ad\u4e86\u59d0\u59b9\u4fe9\u7684\u4e89\u6267\uff0c\u201c\u591f\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u58f0\u97f3\u8f7b\u67d4\u52a8\u542c\uff0c\u4f46\u542c\u8d77\u6765\u9690\u9690\u6709\u4e00\u4e1d\u75b2\u5026\uff0c\u201c\u73b0\u5728\u662f\u56e2\u7ed3\u4e00\u81f4\u7684\u65f6\u5019\uff0c\u628a\u4f60\u4eec\u5e73\u65f6\u5bf9\u5f7c\u6b64\u7684\u4e0d\u6ee1\u90fd\u7ed9\u6211\u6682\u4e14\u653e\u5728\u4e00\u8fb9\u3002\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u4eca\u5929\u53eb\u4f60\u6765\uff0c\u662f\u6709\u66f4\u91cd\u8981\u7684\u4e8b\u60c5\u9700\u8981\u4f60\u53bb\u505a\u3002\u201d</p><p>\u5c11\u5973\u606d\u8c28\u5730\u8eac\u8eab\uff0c\u201c\u6211\u5728\u542c\u5019\u60a8\u7684\u5429\u5490\uff0c\u4e3b\u6bcd\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5373\u4f7f\u4ee5\u9ed1\u6697\u7cbe\u7075\u90a3\u82db\u523b\u6311\u5254\u7684\u773c\u5149\u6765\u770b\uff0c\u83f2\u5c14\u4f26\u4e3b\u6bcd\u4e5f\u7edd\u5bf9\u79f0\u5f97\u4e0a\u662f\u4e00\u4f4d\u7f8e\u4eba\u3002\u5979\u8eab\u6750\u7ea4\u7ec6\uff0c\u9762\u5bb9\u6e05\u79c0\uff0c\u6d45\u84dd\u8272\u7684\u773c\u775b\u72b9\u5982\u6c34\u6676\u822c\u7eaf\u51c0\u900f\u660e\uff0c\u94f6\u8272\u957f\u53d1\u67d4\u987a\u800c\u5bcc\u6709\u5149\u6cfd\u3002\u5982\u679c\u5355\u4ece\u5916\u8868\u5224\u65ad\uff0c\u5979\u5b8c\u5168\u5c31\u662f\u4e00\u4f4d\u5999\u9f84\u5c11\u5973\uff0c\u4f46\u5b9e\u9645\u4e0a\uff0c\u83f2\u5c14\u4f26\u4e3b\u6bcd\u5728\u53bb\u5e74\u5c31\u5ea6\u8fc7\u4e86\u81ea\u5df1\u7684\u56db\u767e\u5c81\u751f\u65e5\uff0c\u5e76\u4e14\u5df2\u7ecf\u662f\u4e03\u4e2a\u5973\u513f\u7684\u6bcd\u4eb2\u2014\u2014\u5979\u6ca1\u6709\u513f\u5b50\uff0c\u8fd9\u5728\u5176\u4ed6\u79cd\u65cf\u6587\u5316\u4e2d\u6216\u8bb8\u662f\u9057\u61be\uff0c\u4f46\u5bf9\u4e8e\u5973\u6027\u81f3\u4e0a\u7684\u5353\u5c14\u6765\u8bf4\uff0c\u8fd9\u6beb\u65e0\u7591\u95ee\u662f\u8718\u86db\u795e\u540e\u7684\u6069\u5ba0\u3002</p><p>\u5f53\u7136\uff0c\u4f5c\u4e3a\u74dc\u7406\u5fb7\u65af\u57ce\u7b2c\u4e00\u5bb6\u65cf\u7684\u73b0\u4efb\u2014\u2014\u8fd9\u4e2a\u8bcd\u5f88\u91cd\u8981\u2014\u2014\u4e3b\u6bcd\uff0c\u8fd9\u5ea7\u5353\u5c14\u57ce\u5e02\u8fd1\u4e03\u4e07\u5b50\u6c11\u7684\u7edf\u6cbb\u8005\uff0c\u83f2\u5c14\u4f26\u5b8c\u5168\u6709\u8d44\u683c\u83b7\u5f97\u8fd9\u4efd\u8363\u8000\u3002\u5728\u5185\u5fc3\u6df1\u5904\uff0c\u5979\u4e5f\u4e00\u76f4\u4ee5\u795e\u540e\u6700\u8654\u8bda\u6700\u5fe0\u5fc3\u7684\u4ec6\u4eba\u81ea\u5c45\uff0c\u4f46\u6700\u8fd1\u53d1\u751f\u7684\u5371\u673a\uff0c\u5374\u4ee4\u5979\u8fd9\u4e2a\u4fe1\u5ff5\u4ea7\u751f\u4e86\u4e9b\u5fae\u52a8\u6447\u3002</p><p>\u96be\u9053\u795e\u540e\u629b\u5f03\u6211\u4eec\u4e86\u5417\uff1f</p><p>\u8fd9\u4e2a\u60f3\u6cd5\u6b62\u4e0d\u4f4f\u5730\u5728\u8111\u4e2d\u6d6e\u73b0\uff0c\u968f\u5373\u53c8\u88ab\u7acb\u523b\u62b9\u6740\u3002\u83f2\u5c14\u4f26\u4e3b\u6bcd\u5728\u5fc3\u4e2d\u5fae\u5fae\u53f9\u6c14\uff0c\u81ea\u5df1\u786e\u5b9e\u662f\u7d2f\u4e86\uff0c\u5426\u5219\u600e\u4e48\u4f1a\u8d77\u8fd9\u79cd\u8352\u8c2c\u7684\u5ff5\u5934\u3002\u5979\u5f3a\u6253\u7cbe\u795e\uff0c\u770b\u7740\u4e0b\u65b9\u7ad9\u7acb\u7684\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u90a3\u662f\u5979\u6700\u5c0f\u7684\u5973\u513f\uff0c\u5b8c\u7f8e\u5730\u7ee7\u627f\u4e86\u6bcd\u4eb2\u7684\u7f8e\u8c8c\u548c\u806a\u6167\uff0c\u4f46\u540c\u65f6\u4e5f\u9057\u4f20\u4e86\u5979\u90a3\u8be5\u8bc5\u5492\u7684\u7236\u4eb2\u7684\u4e00\u4e9b\u4f4e\u52a3\u54c1\u8d28\u3002</p><p>\u201c\u6df1\u6e0a\u5728\u4e0b\uff0c\u6211\u65e9\u5c31\u5e94\u8be5\u628a\u90a3\u4e2a\u5bb6\u4f19\u6254\u53bb\u5582\u8718\u86db\u3002\u201d\u4e3b\u6bcd\u7565\u5e26\u6124\u6068\u5730\u60f3\u3002</p><p>\u5728\u4e3b\u6bcd\u6c89\u9ed8\u7684\u65f6\u5019\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u5728\u6253\u91cf\u7740\u5979\u7684\u6bcd\u4eb2\u3002\u201c\u60a8\u7684\u8138\u8272\u4e0d\u592a\u597d\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5e94\u8be5\u591a\u4f11\u606f\u3002\u201d</p><p>\u201c\u95ed\u5634\uff01\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6253\u65ad\u4e86\u8fd9\u4e2a\u663e\u800c\u6613\u89c1\u7684\u4e8b\u5b9e\u9648\u8ff0\uff0c\u5c3d\u7ba1\u77e5\u9053\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8bdd\u5e76\u6ca1\u6709\u53e6\u5916\u7684\u542b\u4e49\uff0c\u4f46\u5979\u4f9d\u65e7\u975e\u5e38\u4e0d\u5feb\u3002\u201c\u4f60\u5e94\u8be5\u8bb0\u5f97\u7a7f\u9501\u7532\u7684\u3002\u201d\u4e3b\u6bcd\u8bf4\uff0c\u6253\u7b97\u6682\u65f6\u4e0d\u5207\u5165\u6b63\u9898\u3002</p><p>\u201c\u6211\u7684\u6597\u7bf7\u4e5f\u53ef\u4ee5\u63d0\u4f9b\u4fdd\u62a4\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u56de\u7b54\u3002</p><p>\u201c\u5b83\u786e\u5b9e\u5f88\u7262\u9760\uff0c\u4f46\u7ec8\u7a76\u6bd4\u4e0d\u4e0a\u76d4\u7532\u3002\u201d</p><p>\u201c\u53ef\u662f\u5982\u679c\u7a7f\u9501\u7532\u7684\u8bdd\uff0c\u6211\u5c31\u4e0d\u80fd\u6d41\u7545\u5730\u65bd\u6cd5\u4e86\uff0c\u201d\u5c11\u5973\u8fa9\u89e3\uff0c\u201c\u5965\u672f\u548c\u795e\u672f\u4e0d\u540c\uff0c\u5b83\u8bb2\u7a76\u7cbe\u5fae\u590d\u6742\u7684\u59ff\u52bf\u548c\u52a8\u4f5c\uff0c\u60a8\u77e5\u9053\u7684\uff0c\u4e3b\u6bcd\u3002\u201d</p><p>\u201c\u4f60\u662f\u4e2a\u7267\u5e08\uff0c\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u51b7\u9759\u5730\u63d0\u9192\uff0c\u201c\u522b\u5fd8\u4e86\u81ea\u5df1\u7684\u8eab\u4efd\u3002\u201d</p><p>\u201c\u6211\u4e0d\u4f1a\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u7d27\u63a5\u7740\u8ffd\u52a0\u4e86\u4e00\u53e5\u4fdd\u8bc1\uff0c\u201c\u5c31\u7b97\u6211\u4f1a\uff0c\u522b\u4eba\u4e5f\u4e0d\u4f1a\u3002\u201d</p><p>\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6ee1\u610f\u5730\u70b9\u5934\u3002</p><p>\u201c\u5173\u4e8e\u6700\u8fd1\u7684\u4e8b\u60c5\uff0c\u201d\u5979\u7ee7\u7eed\u8bf4\uff0c\u201c\u6267\u653f\u8bae\u4f1a\u5df2\u7ecf\u505a\u51fa\u4e86\u51b3\u8bae\u3002\u6beb\u65e0\u7591\u95ee\uff0c\u6211\u4eec\u5f53\u4e2d\u6709\u4eba\u2014\u2014\u5f53\u7136\u51b3\u4e0d\u4f1a\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\u2014\u2014\u89e6\u6012\u4e86\u795e\u540e\uff0c\u6240\u4ee5\u5979\u4e0d\u518d\u56de\u5e94\u6211\u4eec\u7684\u7948\u7977\uff0c\u4e0d\u518d\u8d50\u4e88\u6211\u4eec\u795e\u672f\u3002\u6211\u4eec\u9700\u8981\u505a\u4e00\u4e9b\u4e8b\u60c5\u6765\u53d6\u60a6\u795e\u540e\uff0c\u91cd\u65b0\u8d62\u5f97\u6b22\u5fc3\u2026\u2026\u201d</p><p>\u201c\u4e3b\u6bcd\uff0c\u6211\u6709\u4e00\u4e2a\u53e6\u5916\u7684\u60f3\u6cd5\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u5927\u80c6\u5730\u63d2\u8bdd\u3002</p><p>\u8fe6\u6cd5\u6012\u89c6\u7740\u59b9\u59b9\uff0c\u6253\u65ad\u5bb6\u65cf\u4e3b\u6bcd\u7684\u8bf4\u8bdd\uff0c\u8fd9\u662f\u975e\u5e38\u4e25\u91cd\u7684\u5192\u72af\uff0c\u5c31\u7b97\u662f\u5973\u6027\u4e5f\u4e0d\u80fd\u88ab\u539f\u8c05\uff0c\u7136\u800c\u83f2\u5c14\u4f26\u4e3b\u6bcd\u5e76\u6ca1\u6709\u8868\u73b0\u5f97\u5341\u5206\u4e0d\u5feb\u3002\u201c\u8bf4\u8bf4\u770b\u3002\u201d\u5979\u547d\u4ee4\uff0c\u8bed\u6c14\u5e73\u6de1\u3002</p><p>\u201c\u5728\u8fc7\u53bb\u7684\u4e09\u5341\u5929\u91cc\uff0c\u5951\u5fb7\xb7\u7eb3\u6492\u57ce\u548c\u6885\u4e3d\u7c73\u5353\u57ce\u7684\u5546\u961f\u90fd\u6ca1\u6709\u6309\u671f\u5230\u6765\uff0c\u4e5f\u6ca1\u6709\u542c\u5230\u5b83\u4eec\u7684\u4efb\u4f55\u6d88\u606f\u3002\u201d</p><p>\u201c\u8fd9\u8bf4\u660e\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6216\u8bb8\u662f\u6218\u4e89\uff0c\u6216\u8bb8\u662f\u602a\u7269\u5207\u65ad\u4e86\u5546\u8def\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u4f46\u663e\u7136\u8fd9\u4e24\u79cd\u731c\u6d4b\u90fd\u4e0d\u8fc7\u662f\u94fa\u57ab\uff0c\u201c\u6216\u8bb8\u2026\u2026\u5b83\u4eec\u548c\u6211\u4eec\u6709\u540c\u6837\u7684\u906d\u9047\u3002\u201d</p><p>\u83f2\u5c14\u4f26\u4e3b\u6bcd\u4f9d\u65e7\u4e0d\u52a8\u58f0\u8272\uff0c\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u5e76\u975e\u4ec5\u4ec5\u53ea\u6709\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u7267\u5e08\u4eec\u4e27\u5931\u4e86\u795e\u540e\u7684\u6069\u5ba0\uff1f\u201d</p><p>\u201c\u81ea\u4ece\u6211\u4eec\u53d1\u73b0\u4e86\u8fd9\u70b9\uff0c\u5c31\u518d\u4e0d\u5141\u8bb8\u4efb\u4f55\u5546\u961f\u4ece\u74dc\u7406\u5fb7\u65af\u57ce\u51fa\u53d1\uff0c\u4ee5\u514d\u5c06\u8fd9\u4e2a\u6d88\u606f\u4f20\u64ad\u51fa\u53bb\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u4ee5\u5df1\u5ea6\u4eba\u7684\u8bdd\uff0c\u5982\u679c\u5951\u5fb7\xb7\u7eb3\u6492\u548c\u6885\u4e3d\u7c73\u5353\u548c\u6211\u4eec\u5883\u9047\u76f8\u540c\uff0c\u90a3\u4e48\u5b83\u4eec\u6781\u53ef\u80fd\u91c7\u53d6\u76f8\u540c\u7684\u63aa\u65bd\uff0c\u800c\u8fd9\u5c31\u80fd\u591f\u5b8c\u7f8e\u5730\u89e3\u91ca\u4e3a\u4ec0\u4e48\u5546\u961f\u6ca1\u6709\u5230\u6765\uff0c\u751a\u81f3\u8fde\u6d88\u606f\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u201c\u5f88\u6709\u8da3\u7684\u60f3\u6cd5\uff0c\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u8bf4\uff0c\u201c\u7136\u800c\u4ec5\u4ec5\u53ea\u662f\u731c\u6d4b\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u8ba4\u4e3a\u795e\u540e\u6ca1\u6709\u9053\u7406\u653e\u5f03\u6211\u4eec\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u74dc\u7406\u5fb7\u65af\u57ce\u662f\u6700\u53e4\u8001\u4e5f\u6700\u5f3a\u5927\u7684\u5353\u5c14\u57ce\u5e02\uff0c\u5728\u795e\u540e\u6240\u6709\u7684\u5b50\u6c11\u4e2d\uff0c\u65e0\u8bba\u662f\u5386\u53f2\uff0c\u662f\u529b\u91cf\uff0c\u662f\u4fe1\u4ef0\u8654\u8bda\u6216\u8005\u5bb6\u65cf\u6570\u76ee\uff0c\u6211\u4eec\u90fd\u662f\u9996\u5c48\u4e00\u6307\uff0c\u201d\u5979\u987f\u4e86\u987f\uff0c\u201c\u6211\u4eec\u9700\u8981\u795e\u540e\uff0c\u540c\u6837\u7684\uff0c\u795e\u540e\u4e5f\u9700\u8981\u6211\u4eec\uff0c\u5c24\u5176\u662f\u81ea\u4ece\u5341\u4e94\u5e74\u4ee5\u540e\u3002\u201d</p><p>\u201c\u73b0\u5728\u672a\u5fc5\u662f\u4e86\uff0c\u201d\u8fe6\u6cd5\u51b7\u51b7\u5730\u8bf4\uff0c\u201c\u9b54\u7d22\u5e03\u83b1\u57ce\u5d1b\u8d77\u5f97\u975e\u5e38\u5feb\uff0c\u5b83\u4eec\u5df2\u7ecf\u53d6\u4ee3\u4e86\u6211\u4eec\u7684\u5730\u4f4d\u3002\u201d</p><p>\u201c\u9b54\u7d22\u5e03\u83b1\u57ce\u7684\u4eba\u53e3\u4e0d\u8db3\u6211\u4eec\u5341\u5206\u4e4b\u4e00\uff0c\u5730\u57df\u4e0d\u5230\u6211\u4eec\u7684\u516b\u5206\u4e4b\u4e00\uff0c\u5386\u53f2\u4e0d\u5230\u6211\u4eec\u7684\u4e00\u534a\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u53cd\u9a73\uff0c\u201c\u5b83\u4eec\u53ea\u6709\u516d\u5341\u4e2a\u5bb6\u65cf\uff0c\u800c\u6211\u4eec\u8d85\u8fc7\u4e24\u767e\uff1b\u5b83\u4eec\u7684\u6267\u653f\u8bae\u4f1a\u5c0f\u5f97\u53ea\u80fd\u4fdd\u6301\u516b\u4e2a\u5e2d\u4f4d\uff0c\u800c\u6211\u4eec\u662f\u4e8c\u5341\u4e00\u5e2d\u3002\u201d</p><p>\u201c\u5355\u7eaf\u6570\u76ee\u7684\u6bd4\u8f83\u5e76\u4e0d\u80fd\u8bf4\u660e\u4ec0\u4e48\uff0c\u6211\u4eec\u6240\u62e5\u6709\u7684\u4e00\u5207\u90fd\u6765\u81ea\u795e\u540e\u7684\u8d50\u4e88\uff0c\u201d\u8fe6\u6cd5\u8bf4\uff0c\u201c\u800c\u9b54\u7d22\u5e03\u83b1\u57ce\u663e\u7136\u5df2\u7ecf\u6210\u529f\u8d62\u5f97\u4e86\u795e\u540e\u66f4\u591a\u7684\u5ba0\u7231\u3002\u201d</p><p>\u201c\u4f60\u6709\u4ec0\u4e48\u8bc1\u636e\uff1f\u201d</p><p>\u201c\u8bc1\u636e\uff1f\u52a8\u8361\u5e74\u4ee3\uff0c\u795e\u540e\u5723\u8005\u964d\u4e34\uff0c\u9009\u62e9\u5728\u9b54\u7d22\u5e03\u83b1\u57ce\u800c\u4e0d\u662f\u74dc\u7406\u5fb7\u65af\u57ce\uff0c\u8fd9\u96be\u9053\u8fd8\u4e0d\u662f\u660e\u663e\u7684\u8bc1\u636e\u5417\uff1f\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u51b7\u7b11\uff0c\u201c\u9009\u62e9\uff1f\u6211\u4eb2\u7231\u7684\u59d0\u59d0\uff0c\u4f60\u4f3c\u4e4e\u5f04\u9519\u4e86\u4ef6\u4e8b\u60c5\u3002\u5341\u4e94\u5e74\u524d\u7684\u5723\u8005\u964d\u4e34\uff0c\u53ef\u4e0d\u4ec5\u4ec5\u53ea\u6709\u6211\u4eec\u7684\u795e\u540e\u3002\u6240\u6709\u7684\u795e\u7947\u90fd\u5760\u5165\u51e1\u95f4\uff0c\u96be\u9053\u4f60\u8ba4\u4e3a\u8fd9\u662f\u4e00\u6b21\u96c6\u4f53\u7684\u6e38\u4e50\u6d3b\u52a8\uff1f\u201d\u5979\u653e\u8086\u5730\u62ac\u8d77\u773c\u775b\uff0c\u76f4\u89c6\u7740\u738b\u5ea7\u4e0a\u7684\u4e3b\u6bcd\uff0c\u201c\u4e8b\u5b9e\u6070\u6070\u76f8\u53cd\uff0c\u8bf8\u795e\u662f\u88ab\u67d0\u79cd\u66f4\u5f3a\u5927\u7684\u529b\u91cf\u6253\u843d\u51e1\u95f4\uff0c\u5b83\u4eec\u6ca1\u6709\u81ea\u7531\u9009\u62e9\u7684\u4f59\u5730\u2014\u2014\u795e\u540e\u4e5f\u4e0d\u4f8b\u5916\u3002\u201d</p><p>\u201c\u4eb5\u6e0e\uff01\u201d\u8fe6\u6cd5\u5927\u558a\uff0c\u4ece\u8170\u95f4\u62d4\u51fa\u86c7\u97ad\uff0c\u4e03\u6761\u6bd2\u86c7\u5174\u594b\u5730\u6d3b\u8dc3\u8d77\u6765\uff0c\u201c\u4eb5\u6e0e\uff01\u4eb5\u6e0e\uff01\u4eb5\u6e0e\uff01\u201d\u5b83\u4eec\u91cd\u590d\u7740\u4e3b\u4eba\u7684\u8bdd\uff0c\u4e89\u5148\u6050\u540e\u63a2\u5934\u63a2\u8111\uff0c\u5176\u4e2d\u6700\u4e2d\u95f4\u6700\u5927\u7684\u90a3\u6761\u6bd2\u86c7\uff08\u5b83\u53eb\u5c3c\u83ab\u65af\uff09\u7684\u7b2c\u4e09\u53ea\u773c\u775b\u5df2\u7ecf\u7741\u5f00\uff0c\u900f\u51fa\u84dd\u5e7d\u5e7d\u7684\u5149\u8292\u3002</p><p>\u201c\u4f60\u80c6\u6562\u5bf9\u795e\u540e\u4e0d\u656c\uff01\u201d</p><p>\u201c\u6216\u8bb8\u4e0d\u656c\uff0c\u4f46\u6211\u53ea\u662f\u5728\u9648\u8ff0\u4e8b\u5b9e\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u8eac\u8eab\u8bf4\uff0c\u5979\u7684\u624b\u6307\u5df2\u7ecf\u6084\u6084\u63a2\u8fdb\u53e3\u888b\uff0c\u89e6\u6478\u5230\u4e86\u4e00\u5757\u51b0\u51b7\u7684\u91d1\u5c5e\u788e\u7247\u3002\u867d\u7136\u5979\u76f8\u4fe1\u8fe6\u6cd5\u4e0d\u6562\u5728\u4e3b\u6bcd\u9762\u524d\u5411\u81ea\u5df1\u52a8\u624b\uff0c\u8fd9\u4e0d\u5408\u5353\u5c14\u7684\u89c4\u77e9\uff0c\u4f46\u8c01\u77e5\u9053\u5462\uff0c\u73b0\u5728\u7684\u60c5\u5f62\u539f\u672c\u5c31\u5df2\u7ecf\u5728\u5931\u63a7\u7684\u8fb9\u7f18\u4e86\u3002</p><p>\u201c\u95ed\u5634\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u3002\u201d\u4e3b\u6bcd\u5475\u65a5\u8bf4\uff0c\u4f46\u8bed\u6c14\u5e76\u4e0d\u4e25\u5389\uff0c\u751a\u81f3\u9690\u9690\u6709\u4e00\u4e1d\u6b23\u6170\u3002\u4e8b\u5b9e\u4e0a\uff0c\u5979\u5f88\u4e50\u610f\u76f8\u4fe1\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8bf4\u6cd5\uff0c\u867d\u7136\u8868\u9762\u4e0a\u4e0d\u80fd\u8868\u793a\u8d5e\u540c\u3002\u201c\u4f60\u5230\u5e95\u60f3\u8bf4\u4ec0\u4e48\uff1f\u201d\u5979\u8be2\u95ee\u3002</p><p>\u201c\u662f\uff0c\u4e3b\u6bcd\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u5176\u5b9e\u6211\u53ea\u662f\u60f3\u8bf4\uff0c\u6211\u4eec\u662f\u795e\u540e\u6700\u5fe0\u5b9e\u7684\u5b50\u6c11\uff0c\u5979\u6ca1\u6709\u9053\u7406\u653e\u5f03\u6211\u4eec\u3002\u5982\u679c\u6211\u4eec\u5f53\u4e2d\u67d0\u4e9b\u4eba\u89e6\u6012\u4e86\u5979\uff0c\u90a3\u4e48\u795e\u540e\u4e3a\u4ec0\u4e48\u4e0d\u76f4\u63a5\u964d\u7f6a\u4e8e\u90a3\u4e2a\u5bb6\u65cf\u2014\u2014\u4ee5\u524d\u662f\u6709\u8fc7\u8fd9\u79cd\u5148\u4f8b\u7684\u3002\u518d\u8054\u7cfb\u5176\u4ed6\u57ce\u5e02\u7684\u5f02\u5e38\uff0c\u6211\u6709\u7406\u7531\u76f8\u4fe1\uff0c\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5883\u9047\u5e76\u975e\u552f\u4e00\uff0c\u800c\u8fd9\u4e00\u5207\u90fd\u6307\u5411\u4e00\u4e2a\u53ef\u80fd\u6027\u3002\u201d</p><p>\u201c\u662f\u4ec0\u4e48\uff1f\u201d\u4e3b\u6bcd\u95ee\uff0c\u867d\u7136\u5979\u5df2\u7ecf\u9690\u7ea6\u731c\u5230\u4e86\u7b54\u6848\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u201c\u6211\u6000\u7591\u795e\u540e\u51fa\u4e8b\u4e86\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u5341\u4e94\u5e74\u524d\u80fd\u591f\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u5341\u4e94\u5e74\u540e\u7684\u4eca\u5929\u540c\u6837\u53ef\u80fd\u53d1\u751f\u3002\u201d</p><p>\u201c\u5927\u80c6\uff01\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6012\u559d\uff0c\u5979\u7ad9\u8d77\u6765\uff0c\u7ea4\u5de7\u7cbe\u81f4\u7684\u8eab\u8eaf\u548c\u80cc\u540e\u5145\u6ee1\u529b\u91cf\u7f8e\u611f\u7684\u77ee\u4eba\u738b\u5ea7\u6784\u6210\u9c9c\u660e\u7684\u5bf9\u6bd4\uff0c\u201c\u6211\u4e00\u76f4\u5f88\u6b23\u8d4f\u4f60\u7684\u5947\u601d\u5999\u60f3\uff0c\u6240\u4ee5\u5bf9\u4f60\u683c\u5916\u5bbd\u5bb9\u4e00\u4e9b\uff0c\u4f46\u4eca\u5929\u770b\u6765\uff0c\u4f60\u5df2\u7ecf\u903e\u8d8a\u4e86\u4e00\u4e2a\u795e\u540e\u7267\u5e08\u7684\u8eab\u4efd\u3002\u4f60\u5fc5\u987b\u4e3a\u6b64\u53d7\u5230\u60e9\u7f5a\uff01\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u4f4e\u7740\u5934\uff0c\u4f46\u663e\u7136\u5e76\u4e0d\u670d\u6c14\u3002</p><p>\u201c\u795e\u540e\u4e0d\u53ef\u80fd\u906d\u9047\u4efb\u4f55\u610f\u5916\uff0c\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u5ba3\u5e03\uff0c\u201c\u5c31\u7b97\u771f\u662f\uff0c\u6211\u4eec\u4e5f\u5e94\u8be5\u80fd\u611f\u5e94\u5230\uff0c\u201d\u5979\u8865\u5145\u8bf4\uff0c\u201c\u73b0\u5728\uff0c\u653e\u4e0b\u90a3\u4e9b\u8352\u5510\u65e0\u7a3d\u7684\u5ff5\u5934\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u6267\u653f\u8bae\u4f1a\u5df2\u7ecf\u505a\u51fa\u4e86\u51b3\u8bae\uff0c\u6211\u4eec\u5fc5\u987b\u5c3d\u4e00\u5207\u65b9\u6cd5\u53bb\u8d62\u56de\u795e\u540e\u7684\u6b22\u5fc3\u3002\u201d</p><p>\u201c\u8fd9\u662f\u5e9f\u8bdd\uff0c\u201d\u5c11\u5973\u5728\u5fc3\u4e2d\u4f4e\u58f0\u8bf4\uff0c\u201c\u5728\u8fc7\u53bb\u7684\u4e09\u5341\u5929\u91cc\uff0c\u6211\u4eec\u5df2\u7ecf\u505a\u4e86\u6240\u6709\u53ef\u80fd\u7684\u5c1d\u8bd5\u52aa\u529b\u3002\u201d</p><p>\u201c\u7c73\u5179\u745e\u56fe\u5c14\u4e3b\u6bcd\u8ba4\u4e3a\uff0c\u795e\u540e\u4e4b\u6240\u4ee5\u964d\u7f6a\uff0c\u662f\u56e0\u4e3a\u6211\u4eec\u5728\u8fc7\u53bb\u7684\u4e00\u4e07\u4e00\u5343\u56db\u767e\u5e74\u91cc\uff0c\u90fd\u672a\u80fd\u6bc1\u706d\u90a3\u7fa4\u77ee\u4eba\u4eec\u7684\u6df1\u9083\u6749\u90a3\u5854\u738b\u56fd\uff0c\u751a\u81f3\u5728\u4ed6\u4eec\u81ea\u76f8\u6b8b\u6740\u4e4b\u540e\uff0c\u6211\u4eec\u4f9d\u65e7\u8fde\u4e00\u5ea7\u4f0a\u5361\u6c99\u57ce\u90fd\u4e0d\u80fd\u653b\u9677\u3002\u5c24\u5176\u662f\u6700\u8fd1\u7684\u4e00\u5343\u4e09\u767e\u5e74\uff0c\u5982\u679c\u8bf4\u6b64\u524d\u6bcf\u6b21\u603b\u7b97\u8fd8\u6709\u6240\u6536\u83b7\uff0c\u73b0\u5728\u5374\u662f\u8fde\u4e00\u5ea7\u8981\u585e\u90fd\u6ca1\u80fd\u518d\u6253\u4e0b\u6765\u3002\u201d</p><p>\u201c\u90a3\u4e9b\u77ee\u4eba\u4eec\u5351\u52a3\u5730\u4f7f\u7528\u9b54\u6cd5\u2026\u2026\u201d</p><p>\u201c\u95ed\u5634\uff01\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6012\u6c14\u51b2\u51b2\uff0c\u201c\u7c73\u5179\u745e\u56fe\u5c14\u4e3b\u6bcd\u7684\u610f\u89c1\uff0c\u5df2\u7ecf\u5f97\u5230\u4e86\u6267\u653f\u8bae\u4f1a\u4e8c\u5341\u4e00\u4e2a\u5bb6\u65cf\u7684\u4e00\u81f4\u8ba4\u53ef\u3002\u6211\u4eec\u9700\u8981\u653b\u9677\u4f0a\u5361\u6c99\u57ce\uff0c\u5c06\u90a3\u4e9b\u987d\u56fa\u7684\u77ee\u4eba\u4eec\u706d\u7edd\u5e72\u51c0\uff0c\u5982\u6b64\u4e00\u6765\u795e\u540e\u4e00\u5b9a\u4f1a\u518d\u6b21\u964d\u6069\u4e8e\u6211\u4eec\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u60f3\u8981\u4e89\u8fa9\uff0c\u4f46\u6700\u7ec8\u8fd8\u662f\u653e\u5f03\u4e86\u3002\u201c\u90a3\u4e48\u6211\u7684\u4efb\u52a1\u662f\u4ec0\u4e48\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u7531\u4f60\u7edf\u5e05\u5bb6\u65cf\u8054\u519b\u3002\u201d</p><p>\u5c11\u5973\u6115\u7136\uff0c\u4ee5\u4e3a\u81ea\u5df1\u542c\u9519\u4e86\uff0c\u201c\u6bcd\u4eb2\u2026\u2026\u201d</p><p>\u201c\u53eb\u6211\u4e3b\u6bcd\uff01\u201d</p><p>\u201c\u662f\uff0c\u4e3b\u6bcd\u5927\u4eba\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8d76\u5feb\u6539\u53e3\uff0c\u201c\u7136\u800c\u6211\u600e\u4e48\u80fd\u62c5\u4efb\u5982\u6b64\u91cd\u4efb\uff1f\u201d\u5979\u8d28\u7591\u7740\uff0c\u201c\u6211\u524d\u5e74\u624d\u521a\u521a\u4ece\u796d\u53f8\u5b66\u6821\u6bd5\u4e1a\uff0c\u65e0\u8bba\u662f\u4f4d\u9636\u3001\u80fd\u529b\u8fd8\u662f\u6307\u6325\u4f5c\u6218\u7684\u7ecf\u9a8c\uff0c\u80dc\u8fc7\u6211\u7684\u90fd\u5927\u6709\u4eba\u5728\u3002\u201d\u5979\u7684\u76ee\u5149\u8f6c\u5411\u738b\u5ea7\u65c1\u8fb9\u7684\u8fe6\u6cd5\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u957f\u5973\u662f\u57ce\u4e2d\u4f4d\u9636\u6700\u9ad8\u7684\u7267\u5e08\u4e4b\u4e00\uff0c\u66fe\u7ecf\u62c5\u4efb\u8fc7\u4e09\u6b21\u8fdc\u5f81\u519b\u7684\u7edf\u5e05\uff0c\u662f\u6bd4\u7ef4\u5eb7\u5c3c\u4e9a\u5408\u9002\u767e\u500d\u7684\u4eba\u9009\u3002</p><p>\u83f2\u5c14\u4f26\u4e3b\u6bcd\u7684\u773c\u775b\u772f\u7f1d\u8d77\u6765\uff0c\u201c\u6211\u4ee5\u4e3a\u4f60\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\u4f1a\u6b23\u559c\u82e5\u72c2\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8fd9\u53ef\u662f\u63d0\u5347\u5730\u4f4d\u7684\u826f\u673a\uff0c\u5973\u513f\uff0c\u5728\u6211\u4eec\u5353\u5c14\u4e2d\u8fd9\u79cd\u673a\u4f1a\u66f4\u52a0\u7f55\u89c1\uff0c\u5343\u8f7d\u96be\u9022\u3002\u201d</p><p>\u201c\u4f46\u6211\u66f4\u6709\u81ea\u77e5\u4e4b\u660e\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bda\u5b9e\u5730\u8bf4\u3002</p><p>\u201c\u771f\u662f\u503c\u5f97\u5938\u5956\u7684\u54c1\u8d28\uff0c\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u968f\u53e3\u8bc4\u4ef7\uff0c\u201c\u81f3\u4e8e\u4e3a\u4ec0\u4e48\u9009\u62e9\u4f60\u6765\u5f53\u7edf\u5e05\uff0c\u539f\u56e0\u5f88\u7b80\u5355\uff0c\u56e0\u4e3a\u4f60\u662f\u6211\u4eec\u5f53\u4e2d\u552f\u4e00\u8fd8\u80fd\u65bd\u5c55\u9b54\u6cd5\u7684\u4eba\uff0c\u201d\u5979\u51dd\u89c6\u7740\u81ea\u5df1\u7684\u5973\u513f\uff0c\u201c\u90a3\u4e9b\u7537\u6027\u4eec\u867d\u7136\u611a\u8822\uff0c\u4f46\u5982\u679c\u770b\u89c1\u7edf\u5e05\u6574\u573a\u6218\u6597\u4e2d\u4e0d\u4f7f\u7528\u4efb\u4f55\u6cd5\u672f\uff0c\u6216\u8005\u5168\u9760\u5377\u8f74\u548c\u9b54\u6cd5\u7269\u54c1\u6765\u65bd\u6cd5\uff0c\u90a3\u4e48\u4ed6\u4eec\u4f1a\u6709\u4ec0\u4e48\u6837\u7684\u731c\u6d4b\uff1f\u201d</p><p>\u201c\u7136\u800c\u6211\u4f7f\u7528\u7684\u4e5f\u53ea\u80fd\u662f\u5965\u672f\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u4e89\u8fa9\uff0c\u201c\u6211\u7684\u795e\u672f\u540c\u6837\u4e27\u5931\u6b86\u5c3d\u3002\u9ad8\u660e\u7684\u5deb\u5e08\u4f1a\u5206\u8fa8\u51fa\u8fd9\u4e24\u8005\u7684\u533a\u522b\uff0c\u8fd9\u540c\u6837\u4f1a\u542f\u4eba\u7591\u7aa6\u3002\u201d</p><p>\u201c\u9ad8\u660e\u7684\u5deb\u5e08\u6570\u91cf\u5f88\u5c11\uff0c\u800c\u4e14\u5728\u6df7\u4e71\u5371\u9669\u7684\u6218\u573a\u4e0a\u6ca1\u6709\u8c01\u4f1a\u53bb\u6ce8\u610f\u8fd9\u4e9b\u7ec6\u8282\uff0c\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u8bf4\uff0c\u201c\u66f4\u4f55\u51b5\uff0c\u6211\u4eb2\u7231\u7684\u5973\u513f\uff0c\u6258\u4f60\u4e00\u8d2f\u7684\u79bb\u7ecf\u53db\u9053\u7684\u540d\u58f0\u6240\u8d50\uff0c\u6211\u76f8\u4fe1\u6ca1\u6709\u8c01\u4f1a\u5bf9\u4f60\u504f\u597d\u4f7f\u7528\u5965\u672f\u800c\u611f\u5230\u60ca\u8bb6\uff0c\u201d\u5979\u9732\u9f7f\u4e00\u7b11\uff0c\u201c\u4e0d\u662f\u4e48\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u5deb\u5e08\u5c0f\u59d0\uff1f\u201d</p><p>\u5c11\u5973\u6c89\u9ed8\u5730\u8eac\u8eab\uff0c\u201c\u5982\u60a8\u6240\u613f\uff0c\u4e3b\u6bcd\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u5c06\u5c3d\u529b\u800c\u4e3a\u3002\u201d</p></article>"}}}]);
