(window.webpackJsonp=window.webpackJsonp||[]).push([[144],{167:function(p){p.exports={chapterTitle:null,title:"\u7b2c1124\u7ae0 \u8bd5\u9a8c\u65b0\u6a21\u5f0f",content:"<article><h2>\u7b2c1124\u7ae0 \u8bd5\u9a8c\u65b0\u6a21\u5f0f</h2><p>\u3000\u3000\u4e4d\u770b\u662f\u62e7\u5728\u4e00\u80a1\u7ef3\u4e0a\u7684\u8682\u86b1\uff0c\u8c22\u5251\u5357\u6bcf\u6b21\u5230\u5efa\u90ba\u6765\uff0c\u90fd\u8981\u4e0e\u5468\u5174\u4e1c\u3001\u6797\u96ea\u3001\u738b\u6d77\u7c9f\u7b49\u4eba\u89c1\u4e0a\u4e00\u9762\uff0c\u8fd9\u6b21\u867d\u7136\u60f3\u907f\u5f00\u4ed6\u4eec\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u4eec\u53c8\u662f\u4ece\u54ea\u91cc\u77e5\u9053\u81ea\u5df1\u5230\u5efa\u90ba\u7684\u6d88\u606f\uff0c\u7535\u8bdd\u65e2\u7136\u90fd\u8ffd\u4e0a\u95e8\u6765\uff0c\u4e5f\u6ca1\u6709\u8eb2\u7740\u4e0d\u89c1\u7684\u5fc5\u8981\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e0e\u8c22\u610f\u3001\u8c22\u5b50\u5609\u91cd\u53d9\u5144\u5f1f\u3001\u5144\u59b9\u4e4b\u60c5\uff0c\u7528\u8fc7\u9910\u540e\uff0c\u8fd8\u662f\u8ba9\u53f8\u673a\u9001\u4ed6\u53bb\u71d5\u5f52\u6e56\u5357\u7554\u7684\u5357\u7fe0\u56ed\u89c1\u6797\u96ea\u7b49\u4eba\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u7740\u4f4d\u4e8e\u8f6f\u4ef6\u56ed\u5185\u90e8\u7684\u522b\u5885\u533a\u91cc\u8f66\u9053\u4e24\u4fa7\u7684\u5c0f\u53f6\u6995\uff0c\u6625\u65e5\u521d\u6696\uff0c\u591c\u8272\u5730\u576a\u706f\u7167\u5c04\u4e0b\u7684\u5c0f\u53f6\u6995\u5df2\u7ecf\u53d1\u51fa\u53f6\u82bd\uff0c\u5357\u7fe0\u56ed\u522b\u5885\u533a\u91cc\u7684\u73af\u5883\u4f18\u96c5\uff0c\u5927\u6982\u4e5f\u53ea\u6709\u71d5\u5f52\u6e56\u5317\u5cb8\u7684\u71d5\u56ed\u80fd\u7a0d\u80dc\u4e00\u7b79\u3002</p><p>\u3000\u3000\u53ea\u662f\u71d5\u56ed\u7ed9\u4e16\u7eaa\u9526\u6e56\u5f00\u53d1\u540e\u90fd\u4f9b\u7ed9\u9526\u6e56\u5458\u5de5\uff0c\u71d5\u5f52\u6e56\u5c71\u533a\u57df\u5185\u7684\u522b\u5885\u7fa4\uff0c\u73b0\u5728\u4e5f\u53ea\u6709\u5357\u7fe0\u56ed\u522b\u5885\u5bf9\u5916\u9500\u552e\uff0c\u5728\u5efa\u90ba\u4f4f\u5b85\u5747\u4ef7\u624d\u4e24\u5343\u4f59\uff0c\u5357\u7fe0\u56ed\u4e34\u6e56\u522b\u5885\u7684\u552e\u4ef7\u5df2\u7ecf\u63a5\u8fd1\u516b\u5343\u4e86\uff0c\u5f53\u771f\u79f0\u5f97\u4e0a\u662f\u8c6a\u5b85\u3002</p><p>\u3000\u3000\u71d5\u56ed\u662f\u4e16\u7eaa\u9526\u6e56\u6b63\u89c4\u5f00\u53d1\u7684\u522b\u5885\u4f4f\u5b85\u533a\uff0c\u5f00\u53d1\u51fa\u6765\u7684\u522b\u5885\u4e3b\u8981\u7528\u6765\u5956\u52b1\u7ed9\u6a61\u6811\u56ed\u767e\u4eba\u8ba1\u5212\u4ece\u6d77\u5916\u62db\u63fd\u56de\u6765\u7684\u79d1\u5b66\u5bb6\u5c45\u4f4f\uff0c\u5e02\u653f\u5e9c\u5728\u522b\u5885\u5f00\u53d1\u7684\u5f81\u5730\u3001\u7a0e\u6536\u7b49\u73af\u8282\u989d\u5916\u7ed9\u4e88\u4e86\u8bb8\u591a\u4f18\u60e0\u653f\u7b56\uff1b\u5357\u7fe0\u56ed\u5374\u662f\u6d77\u7c9f\u79d1\u6280\u5047\u4ee5\u8f6f\u4ef6\u56ed\u4f1a\u52a1\u3001\u5546\u52a1\u3001\u5458\u5de5\u4f4f\u5bbf\u7efc\u5408\u56ed\u7684\u540d\u4e49\u4fee\u5efa\u7684\u9ad8\u6863\u522b\u5885\u533a\u3002</p><p>\u3000\u3000\u5e02\u91cc\u66fe\u6709\u4eba\u60f3\u67e5\u6d77\u7c9f\u79d1\u6280\u5728\u5357\u7fe0\u56ed\u4e0a\u7684\u8fdd\u89c4\u95ee\u9898\uff0c\u53ea\u662f\u5e02\u957f\u8096\u660e\u5efa\u8ba4\u4e3a\u5e02\u91cc\u65e2\u7136\u4e0d\u80fd\u7ed9\u8f6f\u4ef6\u56ed\u66f4\u591a\u7684\u652f\u6301\uff0c\u9ed8\u8bb8\u5176\u641e\u4e9b\u64e6\u8fb9\u7403\u7684\u5c0f\u52a8\u4f5c\u4e5f\u65e0\u4e0d\u53ef\u3002\u8fd9\u5e74\u5934\u641e\u64e6\u8fb9\u7403\u7684\u4e5f\u4e0d\u6b62\u6d77\u7c9f\u79d1\u6280\u4e00\u5bb6\uff0c\u65e0\u975e\u770b\u5404\u81ea\u7684\u540e\u53f0\u8c01\u66f4\u5f3a\u786c\u7f62\u4e86\uff0c\u6d77\u7c9f\u79d1\u6280\u53c8\u662f\u7701\u5e02\u4ee5\u53ca\u591a\u90e8\u59d4\u660e\u786e\u8981\u8054\u5408\u652f\u6301\u7684\u4e0a\u5e02\u516c\u53f8\uff0c\u4e5f\u6ca1\u6709\u8c01\u8f83\u771f\u60f3\u67e5\u3002</p><p>\u3000\u3000\u6d77\u7c9f\u79d1\u6280\u7684\u8f6f\u4ef6\u56ed\u53d1\u5c55\u8f6f\u4ef6\u4ea7\u4e1a\u7a00\u677e\u5f97\u5f88\uff0c\u5012\u662f\u7cbe\u5178\u5730\u4ea7\u5728\u80cc\u540e\u8f7b\u8f66\u719f\u8def\u7684\u501f\u8f6f\u4ef6\u56ed\u7684\u540d\u4e49\u5708\u5730\u5f00\u53d1\u5730\u4ea7\u6bd4\u8f83\u987a\u624b\uff0c\u5c06\u6d77\u7c9f\u79d1\u6280\u7684\u8d22\u52a1\u6c34\u5e73\u652f\u6491\u8d77\u6765\uff0c\u7adf\u7136\u4f7f\u6d77\u7c9f\u79d1\u6280\u4e5d\u4e5d\u5e74\u7684\u51c0\u76c8\u5229\u8fbe\u5230\u4e24\u70b9\u56db\u4ebf\uff0c\u8fd9\u4e3b\u8981\u4e5f\u662f\u5357\u7fe0\u56ed\u522b\u5885\u5bf9\u5916\u9500\u552e\u4ee5\u53ca\u5357\u6e56\u5927\u9152\u5e97\u5bf9\u5916\u8425\u4e1a\u6240\u81f4\u3002</p><p>\u3000\u3000\u6709\u9887\u4e3a\u4e50\u89c2\u7684\u4e1a\u7ee9\u652f\u6491\uff0c\u53c8\u6709\u7f51\u5427\u8fde\u9501\u4ea7\u4e1a\u3001\u7535\u5b50\u5546\u52a1\u3001\u6676\u5706\u5382\u7b49\u591a\u91cd\u65b0\u79d1\u6280\u6982\u5ff5\uff0c\u80a1\u4ef7\u5728\u5341\u4e2a\u6708\u5185\u7206\u6da8\u4e8c\u5341\u516b\u500d\uff0c\u5e02\u503c\u7a81\u7834\u4e00\u767e\u516d\u5341\u4ebf\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u96be\u7406\u89e3\u3002</p><p>\u3000\u3000\u8981\u8bf4\u522b\u5885\u533a\u91cc\u8fd8\u6709\u4f1a\u52a1\u4e0e\u5546\u52a1\u8bbe\u65bd\u7684\u8bdd\uff0c\u90a3\u5c31\u662f\u5357\u7fe0\u56ed\u6c47\u6cc9\u5546\u52a1\u4f1a\u6240\u4e86\uff0c\u662f\u71d5\u5f52\u6e56\u4e34\u6e56\u4e00\u5904\u6781\u81f4\u5bcc\u4e3d\u5802\u7687\u7684\u5730\u65b9\uff0c\u662f\u5de6\u8fd1\u6709\u540d\u7684\u9500\u91d1\u7a9f\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5728\u4f1a\u6240\u524d\u4e0b\u4e86\u8f66\uff0c\u53f8\u673a\u4e0e\u52a9\u624b\u81ea\u6709\u4f11\u606f\u7684\u5730\u65b9\uff0c\u4ed6\u5728\u8863\u670d\u8f7b\u8584\u3001\u8eab\u6750\u9ad8\u6311\u3001\u8138\u86cb\u660e\u4e3d\u7684\u4f8d\u5e94\u5c0f\u59d0\u7684\u5f15\u9886\u4e0b\uff0c\u8d70\u5230\u4f1a\u6240\u91cc\u9762\u5efa\u6210\u6c34\u53f0\u4e0a\u7684\u5385\u91cc\u3002</p><p>\u3000\u3000\u9664\u4e86\u98ce\u60c5\u8273\u4e3d\u7684\u6797\u96ea\u4e4b\u5916\uff0c\u738b\u6d77\u7c9f\u3001\u5468\u5174\u4e1c\u4ee5\u53ca\u5b8f\u4fe1\u5728\u5efa\u90ba\u7684\u4ee3\u7406\u4eba\u5218\u5947\u5cfb\u3001\u5f20\u6587\u4fe1\u7b49\u4eba\u90fd\u5728\uff0c\u8fd8\u6709\u5c31\u662f\u5efa\u90ba\u5e02\u526f\u4e66\u8bb0\u80e1\u5b97\u5e86\u90a3\u4e2a\u4f84\u5b50\u80e1\u91d1\u661f\u3002</p><p>\u3000\u3000\u5df2\u7ecf\u662f\u4e09\u6708\u4e2d\u65ec\u4e86\uff0c\u6d77\u7c9f\u79d1\u6280\u7684\u5e02\u503c\u4e5f\u5df2\u7ecf\u7a81\u7834\u4e00\u767e\u516d\u5341\u4ebf\uff0c\u8ddd\u79bb\u80dc\u5229\u5c31\u5269\u4e0b\u6700\u540e\u4e24\u6b65\uff0c\u5c31\u7b97\u4e25\u6587\u4ecb\u6574\u5929\u5b88\u5728\u5efa\u90ba\uff0c\u8c22\u5251\u5357\u4e5f\u4e0d\u4f1a\u89c9\u5f97\u5947\u602a\uff0c\u773c\u524d\u8fd9\u4e9b\u4eba\u5929\u5929\u5728\u4f1a\u6240\u78b0\u9762\u5bc6\u8bae\uff0c\u90a3\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\u7684\u4e8b\u60c5\u4e86\uff0c\u89c1\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u8fd9\u65f6\u5019\u4e5f\u53c2\u4e0e\u8fdb\u6765\uff0c\u5fc3\u60f3\u4e4b\u524d\u7684\u731c\u6d4b\u591a\u534a\u4e0d\u865a\u3002</p><p>\u3000\u3000\u201c\u542c\u8bf4\u4f60\u4eec\u6253\u7b97\u505a\u624b\u673a\u4e86\uff0c\u6211\u600e\u4e48\u6ca1\u6709\u542c\u8bf4\u8fc7\uff1f\u201d\u5468\u5174\u4e1c\u968f\u610f\u7684\u6307\u7740\u5bf9\u9762\u7684\u4e00\u5f20\u7a7a\u4f4d\u5b50\u8ba9\u8c22\u5251\u5357\u5750\u4e0b\uff0c\u610f\u6001\u61d2\u6563\u7684\u5f00\u53e3\u95ee\u8c22\u5251\u5357\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5f88\u4e0d\u559c\u6b22\u5468\u5174\u4e1c\u8fd9\u79cd\u9ad8\u9ad8\u5728\u4e0a\u7684\u6001\u5ea6\uff0c\u4e5f\u4e0d\u6e05\u695a\u6d88\u606f\u600e\u4e48\u5c31\u8d70\u6f0f\u51fa\u53bb\u4e86\uff0c\u4ed6\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u6709\u4e9b\u60f3\u6cd5\uff0c\u53ea\u662f\u60f3\u6cd5\u8fd8\u6ca1\u6709\u6210\u5f62\uff0c\u7b49\u60f3\u6cd5\u6210\u719f\u4e4b\u540e\uff0c\u6211\u4f1a\u5c06\u8ba1\u5212\u63d0\u4ea4\u7ed9\u8463\u4e8b\u4f1a\u7684\u2026\u2026\u201d\u626d\u5934\u95ee\u738b\u6d77\u7c9f\uff0c\u201c\u521b\u57df\u7f51\u5427\u7684\u8fd0\u8425\u60c5\u51b5\u5982\u4f55\uff1f\u6211\u770b\u521b\u57df\u7f51\u5427\u8fd8\u6709\u4e00\u4f4d\u5f97\u529b\u5c0f\u5c06\u4e5f\u5728\u8fd9\u91cc\u554a\u3002\u201d</p><p>\u3000\u3000\u80e1\u91d1\u661f\u8fd8\u672a\u4ece\u4e1c\u5927\u6bd5\u4e1a\uff0c\u773c\u4e0b\u53ea\u662f\u521b\u57df\u8fde\u9501\u7f51\u5427\u7684\u5b9e\u4e60\u7ecf\u7406\uff0c\u54ea\u91cc\u7b97\u4ec0\u4e48\u5f97\u529b\u5c0f\u5c06\uff1f</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u77e5\u9053\u8c22\u5251\u5357\u8bdd\u9898\u5f80\u80e1\u91d1\u661f\u8eab\u4e0a\u626f\uff0c\u5e76\u4e0d\u662f\u5bf9\u521b\u57df\u7f51\u5427\u7684\u8bdd\u9898\u5f88\u611f\u5174\u8da3\uff0c\u521b\u57df\u7f51\u5427\u7ed9\u6536\u8d2d\u4e4b\u524d\u4e0e\u9526\u6e56\u4ec0\u4e48\u5173\u7cfb\uff0c\u5927\u5bb6\u90fd\u5fc3\u77e5\u809a\u660e\u7684\uff0c\u65e2\u7136\u6d77\u7c9f\u79d1\u6280\u4e3a\u4e86\u5bfb\u627e\u65b0\u7684\u523a\u6fc0\u6982\u5ff5\u53ef\u4ee5\u6536\u8d2d\u521b\u57df\uff0c\u90a3\u4e48\u4ed6\u63a8\u52a8\u79d1\u738b\u4ee5\u9526\u6e56ESS\u57fa\u9891\u82af\u7247\u6280\u672f\u4e3a\u57fa\u7840\u8fdb\u5165\u624b\u673a\u5236\u9020\u9886\u57df\u4e5f\u4e0d\u503c\u4ed6\u4eec\u5728\u8fd9\u91cc\u5927\u60ca\u5c0f\u602a\u3002</p><p>\u3000\u3000\u5468\u5174\u4e1c\u53ea\u662f\u4ee3\u8868\u534e\u590f\u7535\u5b50\u51fa\u4efb\u79d1\u738b\u7684\u8463\u4e8b\u7f62\u4e86\uff0c\u8fd8\u6ca1\u6709\u8d44\u683c\u5bf9\u8c22\u5251\u5357\u6307\u624b\u5212\u811a\uff0c\u8c22\u5251\u5357\u6446\u51fa\u7b11\u8138\uff0c\u5c06\u8bdd\u9898\u626f\u5230\u521b\u57df\u8fde\u9501\u7f51\u5427\u5934\u4e0a\uff0c\u4e5f\u662f\u59d4\u5a49\u7684\u8ddf\u5927\u5bb6\u8868\u660e\u7acb\u573a\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u4e5f\u5047\u88c5\u7cca\u6d82\uff0c\u56de\u7b54\u9053\uff1a\u201c\u8fd8\u7b97\u987a\u5229\uff0c\u6211\u4eec\u63a5\u624b\u540e\u4e0d\u8ba1\u7b97\u4e1a\u52a1\u6269\u5f20\u6295\u5165\uff0c\u4e0b\u534a\u5e74\u8425\u4e1a\u6536\u5165\u8fbe\u5230\u6211\u4eec\u6240\u9884\u671f\u7684\u4e94\u5343\u4e07\u5143\uff0c\u6d77\u7c9f\u79d1\u6280\u7684\u5e74\u62a5\u5df2\u7ecf\u5b8c\u6210\uff0c\u8fc7\u4e24\u5929\u5c31\u516c\u5e03\u51fa\u6765\uff1b\u9664\u4e86\u8fd9\u4e2a\u4e4b\u5916\uff0c\u4fe1\u4ea7\u90e8\u3001\u6587\u5316\u90e8\u7b49\u56fd\u5bb6\u90e8\u59d4\u4eca\u5e74\u53ef\u80fd\u8054\u5408\u5236\u5b9a\u7f51\u5427\u4ea7\u4e1a\u8fd0\u8425\u51c6\u5165\u5236\u5ea6\uff0c\u5c4a\u65f6\u5e02\u573a\u76c8\u5229\u524d\u666f\u5c06\u66f4\u660e\u6717\u2026\u2026\u201d\u8f7b\u677e\u677e\u7684\u5c31\u5c06\u8bdd\u9898\u8f6c\u5230\u522b\u5904\u53bb\u3002</p><p>\u3000\u3000\u201c\u8fd9\u5012\u662f\u4e0d\u9519\u7684\u6d88\u606f\u5462\uff0c\u96be\u602a\u5927\u5bb6\u5750\u5728\u8fd9\u91cc\u90fd\u795e\u6e05\u6c14\u723d\u7684\u3002\u201d\u8c22\u5251\u5357\u88c5\u51fa\u66ff\u4ed6\u4eec\u5f88\u9ad8\u5174\u7684\u6837\u5b50\uff0c\u53cc\u817f\u4ea4\u53e0\u7684\u9677\u5750\u5728\u8212\u8f6f\u7684\u6c99\u53d1\u91cc\uff0c\u770b\u7740\u76f8\u8c8c\u8ff7\u4eba\u7684\u4f8d\u5e94\u5c0f\u59d0\u66ff\u4ed6\u5012\u9152\uff0c\u8fd8\u4eb2\u5207\u7684\u7b11\u4e86\u7b11\uff0c\u7aef\u8d77\u9152\u676f\u9762\u671d\u5411\u738b\u6d77\u7c9f\u505a\u51fa\u4e00\u4ed8\u8ba4\u771f\u8046\u542c\u7684\u6a21\u6837\uff0c\u5c06\u5468\u5174\u4e1c\u4e22\u5728\u4e00\u8fb9\u4e0d\u7406\u3002</p><p>\u3000\u3000\u624b\u673a\u51c6\u5165\u5236\u5ea6\u5728\u77ed\u77ed\u4e09\u5e74\u95f4\u5c31\u6253\u9020\u51fa\u7231\u8fbe\u3001\u8054\u4fe1\u3001\u79d1\u738b\u9ad8\u79d1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u7b49\u4e00\u7cfb\u5217\u7684\u660e\u661f\u7535\u5b50\u4f01\u4e1a\uff0c\u7231\u8fbe\u5728\u56fd\u5185\u5e02\u573a\u7684\u624b\u673a\u4e1a\u52a1\u5e74\u5229\u6da6\u66f4\u662f\u8fbe\u5230\u60ca\u4e16\u9a87\u4fd7\u7684\u56db\u5341\u4ebf\u5143\uff0c\u5982\u4eca\u56fd\u5185\u4e3a\u4e86\u6c42\u4e00\u5f20\u624b\u673a\u724c\u7167\u613f\u63b7\u4ebf\u91d1\u7684\u4f01\u4e1a\u5927\u6709\u4eba\u5728\uff0c\u53ea\u53ef\u60dc\u4fe1\u4ea7\u90e8\u4eca\u5e74\u53c8\u7a81\u7136\u51b3\u5b9a\u5173\u95f8\u4e86\uff0c\u591a\u5c11\u4eba\u8bc5\u9a82\u4e0d\u6b62\uff0c\u8c22\u5251\u5357\u5fc3\u91cc\u60f3\u7f51\u5427\u4ea7\u4e1a\u8fd0\u8425\u51c6\u5165\u5236\u5ea6\u4e00\u7ecf\u786e\u7acb\uff0c\u6216\u8bb8\u4f1a\u8ba9\u8fd9\u4e00\u9886\u57df\u6210\u4e3a\u65b0\u7684\u805a\u91d1\u573a\uff0c\u4e5f\u96be\u602a\u738b\u6d77\u7c9f\u8bf4\u5230\u8fd9\u4e8b\u60c5\u4e0d\u81ea\u7981\u7684\u8138\u9732\u559c\u8272\u3002\u53ea\u662f\u7f51\u5427\u4ea7\u4e1a\u4e0e\u624b\u673a\u4ea7\u4e1a\u6709\u5f88\u5927\u533a\u522b\uff0c\u7f51\u5427\u7ecf\u8425\u5730\u57df\u6027\u5f88\u5f3a\uff0c\u5373\u4f7f\u56fd\u5bb6\u90e8\u59d4\u8981\u5236\u5b9a\u4ec0\u4e48\u51c6\u5165\u5236\u5ea6\uff0c\u6700\u540e\u7684\u5ba1\u6279\u6743\u9650\u4e5f\u4f1a\u653e\u5230\u5730\u65b9\uff0c\u4e5f\u8bb8\u53ea\u6709\u8de8\u7701\u57df\u7684\u7f51\u5427\u8fde\u9501\u8fd0\u8425\u8bb8\u53ef\u5ba1\u6279\u6743\u9650\u4f1a\u5728\u56fd\u5bb6\u90e8\u59d4\u624b\u91cc\uff0c\u53ea\u662f\u8fd9\u4e48\u4e00\u6765\uff0c\u542b\u91d1\u91cf\u5c31\u8981\u4f4e\u5f97\u591a\uff0c\u6839\u636e\u4e0d\u53ef\u80fd\u8ddf\u624b\u673a\u6267\u7167\u7684\u542b\u91d1\u91cf\u76f8\u63d0\u5e76\u8bba\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e5f\u77e5\u9053\u738b\u6d77\u7c9f\u4ed6\u4eec\u5e76\u4e0d\u5173\u5fc3\u7f51\u5427\u4ea7\u4e1a\u51c6\u5165\u5236\u5ea6\u7684\u771f\u6b63\u542b\u91d1\u91cf\uff0c\u53ea\u8981\u63d0\u4f9b\u8db3\u591f\u7684\u7092\u4f5c\u6982\u5ff5\u5c31\u8db3\u591f\u4e86\uff0c\u4ed6\u4eec\u773c\u524d\u53ea\u5173\u5fc3\u4e94\u6708\u4e2d\u65ec\u7684\u65b0\u80a1\u589e\u53d1\u65b9\u6848\u80fd\u4e0d\u80fd\u987a\u5229\u7684\u65bd\u884c\u3002</p><p>\u3000\u3000\u4e09\u6708\u662f\u5168\u7403\u5404\u5927\u4e0a\u5e02\u516c\u53f8\u7684\u5e74\u62a5\u53d1\u5e03\u5bc6\u96c6\u671f\uff0c\u5168\u7403\u8303\u56f4\u4e4b\u5185\uff0c\u65b0\u79d1\u6280\u9886\u57df\u4e0a\u5e02\u516c\u53f8\u53bb\u5e74\u7684\u6574\u4f53\u4e1a\u7ee9\u559c\u4eba\uff0c\u4ea7\u4e1a\u589e\u6da8\u89c4\u6a21\u572830\uff05\u4ee5\u4e0a\u3002</p><p>\u3000\u3000\u5168\u7403\u79d1\u6280\u9f99\u5934\u4f01\u4e1a\u5fae\u8f6f\u4e5d\u4e5d\u5e74\u7684\u5e74\u5229\u6da6\u8fbe\u5230\u60ca\u4eba\u4e5d\u5341\u4ebf\u7f8e\u5143\uff0c\u9884\u8ba1\u4eca\u5e74\u4e00\u5b63\u5ea6\u7684\u4e1a\u7ee9\u540c\u6bd4\u8fd8\u5c06\u670930\uff05\u7684\u589e\u6da8\uff0c\u8fd9\u4e9b\u6d88\u606f\u90fd\u523a\u6fc0\u7eb3\u65af\u8fbe\u514b\u7b49\u6b27\u7f8e\u4e3b\u8981\u65b0\u7ecf\u6d4e\u6307\u6807\u80a1\u518d\u62fe\u5347\u52bf\uff0c\u52bf\u5934\u4e5f\u6108\u53d1\u7684\u5f3a\u52b2\u3002</p><p>\u3000\u3000\u5c31\u7b97\u5728ESS\u591a\u8fbe1700\u4e07\u80a1\u7684\u51cf\u6301\u629b\u5355\u5de8\u538b\u529b\u4e0b\uff0c\u6606\u817e\u5728\u7ebf\u7684\u80a1\u4ef7\u4f9d\u65e7\u987d\u5f3a\u7684\u5b88\u4f4f\u572842\u7f8e\u5143\u4e4b\u4e0a\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u9526\u6e56\u6700\u7ec8\u4ece\u6606\u817e\u5728\u7ebf\u80a1\u7968\u51cf\u6301\u4e2d\u5957\u73b0\u81f3\u5c11\u8d85\u8fc7\u4e03\u4ebf\u4e00\u5343\u4e07\u7f8e\u5143\u3002\u5728ESS\u5ba3\u5e03\u6240\u6301\u80a1\u7968\u5168\u90e8\u51cf\u6301\u5b8c\u7ed3\uff0c\u6606\u817e\u5728\u7ebf\u7684\u80a1\u4ef7\u751a\u81f3\u5728\u4e00\u5929\u4e4b\u5185\u5c31\u53cd\u5f39\u5230\u524d\u671f50\u7f8e\u5143\u7684\u9ad8\u70b9\u3002</p><p>\u3000\u3000\u523a\u6fc0\u6606\u817e\u5728\u7ebf\u80a1\u4ef7\u4e0a\u6da8\u9664\u4e86ESS\u629b\u8d27\u7ed3\u675f\u8fd9\u4e2a\u56e0\u7d20\u4e4b\u5916\uff0c\u5c31\u662f\u6606\u817e\u5728\u7ebf\u5bf9\u7535\u5b50\u5546\u52a1\u7f51\u7ad9\u5353\u57df\u7f51\u6ce8\u8d44\u56db\u767e\u4e07\u7f8e\u91d1\u83b7\u5f9720\uff05\u7684\u80a1\u4efd\u3002</p><p>\u3000\u3000\u5404\u5927\u6295\u8d44\u673a\u6784\u90fd\u7eb7\u7eb7\u9884\u6d4b\u5168\u7403\u7535\u5b50\u5546\u52a1\u4e1a\u52a1\u89c4\u6a21\u96f6\u56db\u3001\u96f6\u4e94\u5e74\u5c06\u8fbe\u5230\u516d\u4e07\u4ebf\u7f8e\u5143\uff0c\u4e9a\u6d32\u5730\u533a\u5c06\u5360\u523020\uff05\u7684\u5e02\u573a\u4efd\u989d\uff0c\u4e2d\u56fd\u5e02\u573a\u7684\u6f5c\u529b\u4ec5\u5728\u65e5\u672c\u4e4b\u540e\uff0c\u5168\u7403\u7535\u5b50\u5546\u52a1\u5de8\u5934\u4e9a\u9a6c\u900a\u4e5f\u6d3e\u51fa\u5546\u52a1\u4ee3\u8868\u5230\u4e2d\u56fd\u5bfb\u89c5\u5408\u9002\u7684\u6536\u8d2d\u5bf9\u8c61\uff0c\u66fe\u5bf9\u4e2d\u56fd\u7535\u5b50\u5546\u52a1\u5e02\u573a\u6392\u540d\u7b2c\u4e00\u76848818\u5f00\u51fa4\u4ebf\u7f8e\u5143\u7684\u6536\u8d2d\u4ef7\u7801\u800c\u88ab\u62d2\u7edd\uff0c\u76f8\u5173\u65b0\u95fb\u7ed9\u56fd\u5185\u7684\u5a92\u4f53\u7092\u5f97\u6cb8\u6cb8\u626c\u626c\u3002</p><p>\u3000\u3000\u5728\u8fd9\u79cd\u4e92\u8054\u7f51\u7ecf\u6d4e\u72c2\u70ed\u7684\u80cc\u666f\u4e0b\uff0c\u56fd\u5185\u6295\u8d44\u8005\u4e00\u81f4\u8ba4\u4e3a\u5f53\u524d\u5728\u4e2d\u56fd\u7535\u5b50\u5546\u52a1\u5e02\u573a\u6392\u540d\u7b2c\u4e09\u7684\u5353\u57df\u7f5120\uff05\u80a1\u4efd\u53ea\u4f30\u4ef7400\u4e07\u7f8e\u5143\u88ab\u4e25\u91cd\u4f4e\u4f30\uff0c\u800c\u6ca1\u6709\u610f\u8bc6\u5230\u4e9a\u9a6c\u900a\u5bf98818\u76844\u4ebf\u7f8e\u5143\u62a5\u4ef7\u5176\u5b9e\u662f\u5219\u5047\u65b0\u95fb\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u6e05\u695a\u4e9a\u9a6c\u900a\u5f00\u51fa\u7684\u4ef7\u7801\u4e8b\u5b9e\u4e0a\u53ea\u6709\u4e00\u4ebf\u516d\u5343\u4e07\u7f8e\u5143\uff0c\u800c\u4e14\u662f\u8981\u6c42\u6536\u8d2d8818\u5168\u90e8\u7684\u80a1\u6743\u3002\u76f8\u6bd4\u8f83\u4e00\u5e74\u6765\u7684\u6301\u7eed\u6295\u5165\uff0c\u63a5\u53d7\u4e9a\u9a6c\u900a\u62a5\u51fa\u7684\u4ef7\u7801\uff0c\u7cbe\u5178\u3001\u5b8f\u4fe1\u4ecd\u6709\u8d85\u8fc7100\uff05\u7684\u6536\u76ca\uff0c\u53ea\u662f\u4ed6\u4eec\u7684\u91ce\u5fc3\u8fdc\u4e0d\u6b62\u4e8e\u6b64\uff0c\u600e\u4e48\u53ef\u80fd\u5728\u8747\u5934\u5c0f\u5229\u9762\u524d\u6b62\u6b65\u4e0d\u524d\u5462\uff1f\u540c\u610f\u4e0e\u4e9a\u9a6c\u900a\u4fdd\u6301\u63a5\u89e6\uff0c\u4e5f\u53ea\u662f\u60f3\u501f\u4e9a\u9a6c\u900a\u7092\u4f5c\u4e00\u756a\u7f62\u4e86\uff0c\u56fd\u5185\u5a92\u4f53\u8ffd\u8e2a\u62a5\u9053\u7684\u70ed\u70c8\u7a0b\u5ea6\u5012\u6bd4\u5f97\u4e0a\u5e74\u524d\u5fae\u8f6f\u6536\u8d2d\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u80a1\u6743\u90a3\u65f6\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u7740\u5728\u5ea7\u7684\u4eba\u90fd\u610f\u6001\u8e0c\u8e87\uff0c\u5fc3\u60f3\u5728\u4ed6\u4eec\u770b\u5230\u5df2\u7ecf\u80dc\u5238\u5728\u63e1\uff0c\u53ea\u7b49\u4e24\u4e2a\u6708\u540e\u516c\u5f00\u589e\u53d1\u65b0\u80a1\u7684\u65f6\u9650\u8fc7\u4e86\u5c31\u53ef\u4ee5\u6536\u5272\u80dc\u5229\u679c\u5b9e\u4e86\u3002\u76f8\u6bd4\u8f83\u7d2f\u6b7b\u7d2f\u6d3b\u79d1\u738b\u4e00\u5e74\u5229\u6da6\u4e5f\u4e0d\u8fc7\u4e24\u4e2a\u4ebf\uff0c\u5728\u8d44\u672c\u5e02\u573a\u6307\u70b9\u6c5f\u5c71\u5219\u8981\u98ce\u5149\u591a\u4e86\uff0c\u6d77\u7c9f\u79d1\u6280\u4ee5\u5f53\u524d\u7684\u80a1\u4ef7\u53d1\u884c\u65b0\u80a1\uff0c\u4e00\u6b21\u5c31\u80fd\u5377\u8d70\u4e94\u5341\u4ebf\u7684\u8d44\u91d1\uff0c\u8fd9\u8fd8\u4e0d\u5305\u62ec\u5b8f\u4fe1\u3001\u7cbe\u5178\u80cc\u540e\u52a8\u7528\u5de8\u8d44\u6258\u5e02\u518d\u9000\u51fa\u7684\u7d2f\u8ba1\u83b7\u5229\uff0c\u96be\u602a\u6709\u4e9b\u4eba\u4e00\u65e6\u8d70\u8fdb\u80a1\u5e02\u5c31\u6ca1\u6709\u5174\u8da3\u518d\u5e72\u5b9e\u4e1a\u4e86\u3002</p><p>\u3000\u3000\u6797\u96ea\u627e\u8c22\u5251\u5357\u8fc7\u6765\uff0c\u8fd8\u662f\u5173\u5fc3\u9526\u6e56\u8fd1\u671f\u5165\u4e3b\u5609\u4fe1\u5730\u4ea7\u7684\u4e8b\u60c5\uff1a\u201c\u4f60\u4e0e\u5609\u4fe1\u5730\u4ea7\u5f88\u719f\u6089\uff0c\u4f60\u89c9\u5f97\u9526\u6e56\u5728\u5609\u4fe1\u5730\u4ea7\u4e0a\u505a\u8fd9\u4e48\u5927\u7684\u6587\u7ae0\u7a76\u7adf\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u8fd8\u771f\u4e0d\u597d\u8bf4\uff0c\u201d\u8c22\u5251\u5357\u542b\u7cca\u7684\u8bf4\u9053\uff0c\u4ed6\u60f3\u8d77\u8c22\u5b50\u5609\u7684\u8bdd\u6765\uff0c\u5373\u4f7f\u8fd9\u65f6\u5019\u4e0d\u80fd\u8ddf\u5b8f\u4fe1\u3001\u7cbe\u5178\u5212\u6e05\u754c\u9650\uff0c\u6709\u4e9b\u4e8b\u60c5\u8fd8\u5c3d\u91cf\u5c11\u63ba\u5408\u8fdb\u53bb\u7684\uff0c\u201c\u6211\u5f97\u77e5\u4e16\u7eaa\u9526\u6e56\u7a81\u7136\u6536\u8d2d\u5609\u4fe1\u5730\u4ea7\u80a1\u6743\u7684\u65b0\u95fb\u65f6\uff0c\u4e5f\u5413\u4e86\u4e00\u8df3\uff0c\u8fd8\u66fe\u7ed9\u845b\u836b\u5747\u6253\u7535\u8bdd\u8be2\u95ee\u8fd9\u4e8b\uff0c\u6ca1\u6709\u7ed9\u7406\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u5373\u4f7f\u731c\u4e0d\u900f\u9526\u6e56\u5728\u623f\u5730\u4ea7\u4e1a\u4e0a\u7684\u5e03\u5c40\uff0c\u4e5f\u80fd\u60f3\u5230\u9526\u6e56\u9700\u6c42\u4e00\u5bb6\u4e0a\u5e02\u516c\u53f8\u8fdb\u884c\u8d44\u672c\u8fd0\u4f5c\u3002</p><p>\u3000\u3000\u5728\u8fc7\u6765\u4e4b\u524d\uff0c\u8c22\u5251\u5357\u8ddf\u8c22\u610f\u53ca\u5b50\u5609\u8c08\u8d77\u8fc7\u8fd9\u4e8b\uff0c\u8c22\u610f\u8ddf\u4ed6\u5206\u6790\u9053\uff1a\u201c\u9526\u6e56\u5728\u6d77\u5dde\u4e3a\u6c99\u7530\u65e7\u57ce\u6539\u9020\u62c6\u8fc1\u6237\u5efa\u8bbe\u5b89\u7f6e\u5c0f\u533a\uff0c\u53c8\u5728\u65b0\u533a\u5efa\u9020\u56fd\u9645\u9526\u6e56\u65b0\u57ce\uff0c\u5c06\u5927\u91cf\u4f4f\u5b85\u90fd\u4f4e\u4ef7\u9500\u552e\u7ed9\u7279\u5b9a\u5bf9\u8c61\uff0c\u6240\u76f4\u63a5\u83b7\u5f97\u7684\u6536\u76ca\u5f88\u4f4e\uff0c\u8fd9\u4e9b\u5e74\uff0c\u4e3b\u8981\u662f\u9760\u9526\u6e56\u80cc\u540e\u7684\u8d44\u672c\u5728\u652f\u6491\u5176\u5927\u89c4\u6a21\u7684\u5730\u4ea7\u5f00\u53d1\u3002\u4e0d\u8fc7\u9664\u4e86\u5c11\u91cf\u7684\u73b0\u91d1\u6536\u76ca\u4e4b\u5916\uff0c\u8fd8\u6709\u5927\u91cf\u7684\u9644\u5c5e\u5546\u4e1a\u8bbe\u65bd\u90fd\u7559\u5728\u624b\u91cc\u7b97\u662f\u989d\u5916\u7684\u6536\u76ca\uff0c\u9526\u6e56\u8fd9\u6b21\u662f\u5c06\u8fd9\u4e9b\u9644\u5c5e\u5546\u4e1a\u8bbe\u65bd\u90fd\u7f6e\u5165\u4e0a\u5e02\u516c\u53f8\u6362\u53d6\u5341\u4e8c\u4ebf\u6e2f\u5143\u7684\u73b0\u91d1\uff0c\u7b97\u662f\u5c06\u524d\u671f\u672a\u83b7\u5f97\u7684\u6536\u76ca\u90fd\u5f25\u8865\u8fc7\u6765\u4e86\u2026\u2026\u4e0a\u5e02\u516c\u53f8\u53ef\u4ee5\u4ece\u8fd9\u4e9b\u9644\u5c5e\u5546\u4e1a\u8bbe\u65bd\u4e2d\u83b7\u5f97\u7a33\u5b9a\u7684\u79df\u91d1\u6536\u76ca\u4ee5\u53ca\u623f\u4ea7\u589e\u503c\u6536\u76ca\u56de\u62a5\u7ed9\u80a1\u4e1c\uff0c\u9526\u6e56\u4f5c\u4e3a\u4e0a\u5e02\u516c\u53f8\u7684\u5927\u80a1\u4e1c\uff0c\u8fd8\u53ef\u4ee5\u83b7\u5f97\u4e8c\u6b21\u5229\u76ca\u5206\u914d\u2026\u2026\u201d</p><p>\u3000\u3000\u5b50\u5609\u7684\u5206\u6790\u66f4\u52a0\u5927\u80c6\uff1a\u201c\u73b0\u5728\uff0c\u9999\u6e2f\u6295\u8d44\u673a\u6784\u5bf9\u5185\u5730\u7684\u5546\u4e1a\u5730\u4ea7\u6709\u6bd4\u8f83\u597d\u7684\u9884\u671f\uff0c\u8981\u662f\u9526\u6e56\u6709\u5fc3\u5c06\u8fd9\u6b21\u7684\u8fd0\u4f5c\u5f53\u6210\u7a33\u5b9a\u7684\u5546\u4e1a\u6a21\u5f0f\u6765\u53d1\u5c55\uff0c\u5c31\u53ef\u4ee5\u5229\u7528\u8fd9\u79cd\u6a21\u5f0f\u4ece\u9999\u6e2f\u6295\u8d44\u5e02\u573a\u50cf\u6eda\u96ea\u7403\u4f3c\u7684\u83b7\u5f97\u5de8\u91cf\u8d44\u91d1\u6765\u6e90\u6e90\u4e0d\u65ad\u7684\u6295\u5165\u5230\u5185\u5730\u7684\u623f\u5730\u4ea7\u5e02\u573a\u4e2d\u53bb\uff0c\u5373\u4f7f\u5728\u4f4f\u5b85\u9500\u552e\u4e2d\u4e0d\u8d5a\u4e00\u5206\u94b1\uff0c\u8be5\u6a21\u5f0f\u4ecd\u7136\u80fd\u4fdd\u8bc1\u4e16\u7eaa\u9526\u6e56\u6709\u5145\u8db3\u7684\u5546\u4e1a\u5229\u6da6\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u4ee5\u4e3a\u8c22\u610f\u3001\u8c22\u5b50\u5609\u7684\u5206\u6790\u6709\u4e9b\u9053\u7406\uff0c\u4e0d\u8fc7\u8fd9\u79cd\u6a21\u5f0f\u7684\u64cd\u4f5c\u96be\u5ea6\u5f88\u5927\uff0c\u5173\u952e\u8981\u4fdd\u8bc1\u7f6e\u5165\u4e0a\u5e02\u516c\u53f8\u7684\u9644\u5c5e\u5546\u4e1a\u8bbe\u65bd\u6709\u8db3\u591f\u9ad8\u7684\u9644\u52a0\u5546\u4e1a\u4ef7\u503c\u624d\u884c\u3002\u65e0\u8bba\u50cf\u6c99\u7530\u65e7\u57ce\u6539\u9020\u5b89\u7f6e\u5c0f\u533a\uff0c\u8fd8\u662f\u56fd\u9645\u9526\u6e56\u65b0\u57ce\uff0c\u5176\u5730\u4ea7\u5f00\u53d1\u7684\u7279\u70b9\u5c31\u662f\u793e\u533a\u89c4\u6a21\u5e9e\u5927\u3001\u5f62\u6210\u5c45\u4f4f\u89c4\u6a21\u7684\u65f6\u95f4\u95f4\u9694\u77ed\uff0c\u5f53\u7136\u95f4\u63a5\u7684\u7ed3\u679c\u5c31\u662f\u5efa\u9020\u5927\u91cf\u4f4f\u5b85\u4ee5\u6210\u672c\u4ef7\u503e\u9500\u3001\u5bf9\u5468\u8fb9\u623f\u4ef7\u4ea7\u751f\u6781\u5f3a\u7684\u6291\u5236\u4f5c\u7528\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u60f3\u8d77\u534a\u4e2a\u6708\u524d\u5728\u91d1\u5c71\u4e0e\u5f20\u606a\u4f1a\u9762\u65f6\u7684\u60c5\u5f62\u8fd8\u6709\u5f20\u606a\u7ad9\u5728\u6668\u66e6\u793e\u533a\u7684\u4e2d\u5fc3\u5e7f\u573a\u4e0a\u63d0\u8d77\u9676\u664b\u4e66\u8bb0\u5e0c\u671b\u63a7\u5236\u623f\u4ef7\u90a3\u4e9b\u7684\u8bdd\u3002</p><p>\u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u8c22\u5251\u5357\u90fd\u5fae\u5fae\u6709\u4e9b\u5fc3\u60ca\u4e86\uff1a\u5f20\u606a\u96be\u9053\u771f\u7684\u60f3\u6291\u5236\u533a\u57df\u623f\u4ef7\u7684\u4e0a\u6da8\uff1f\u8fd9\u79cd\u5546\u4e1a\u6a21\u5f0f\u867d\u7136\u64cd\u4f5c\u8d77\u6765\u9887\u4e3a\u56f0\u96be\uff0c\u4f46\u662f\u603b\u6bd4\u7ecf\u6d4e\u9002\u7528\u623f\u653f\u7b56\u770b\u8d77\u6765\u66f4\u9760\u8c31\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u6797\u96ea\u89c1\u8c22\u5251\u5357\u5fae\u9501\u7740\u7709\u5934\uff0c\u53ea\u5f53\u4ed6\u4e5f\u662f\u63e3\u6d4b\u4e0d\u900f\u9526\u6e56\u7684\u52a8\u673a\uff0c\u5468\u5174\u4e1c\u968f\u610f\u7684\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u8fd8\u80fd\u60f3\u505a\u4ec0\u4e48\uff0c\u65e0\u975e\u4e5f\u60f3\u4ece\u80a1\u5e02\u635e\u94b1\u800c\u5df2\uff0c\u4ed6\u4eec\u4e5d\u4e03\u5e74\u505a\u4e86\u4e00\u6b21\u987a\u624b\uff0c\u6709\u673a\u4f1a\u54ea\u80fd\u9519\u8fc7\u8fd9\u6837\u7684\u597d\u5904\uff1f\u201d</p><p>\u3000\u3000\u71d5\u96c0\u5b89\u77e5\u9e3f\u9e44\u4e4b\u5fd7\uff1f\u8c22\u5251\u5357\u773c\u795e\u8f6c\u5230\u5468\u5174\u4e1c\u8138\u4e0a\uff0c\u6ca1\u6709\u4ec0\u4e48\u505c\u987f\uff0c\u53c8\u8f6c\u8fc7\u53bb\u8ddf\u6797\u96ea\u7b11\u9053\uff1a\u201c\u6216\u8bb8\u5c31\u662f\u5468\u603b\u8bf4\u7684\u90a3\u6837\u3002\u201d</p><p>\u3000\u3000\u201c\u5468\u603b\u771f\u8981\u6709\u8fd9\u80fd\u8010\uff0c\u5c31\u4e0d\u7528\u7559\u5728\u534e\u590f\u7535\u5b50\u6797\u96ea\u5bf9\u8d56\u76ae\u72d7\u4f3c\u7684\u5468\u5174\u4e1c\u6ca1\u6709\u4ec0\u4e48\u597d\u8138\u8272\u3002\u65f6\u4e0d\u65f6\u8ddf\u53ea\u8d56\u76ae\u72d7\u76ae\u76ef\u7740\u5979\u8eab\u4e0a\u51f8\u51fa\u6765\u51f9\u8fdb\u53bb\u7684\u5730\u65b9\uff0c\u4ed6\u7684\u773c\u795e\u770b\u7740\u5c31\u50cf\u6068\u4e0d\u5f97\u5728\u4e0a\u9762\u8214\u4e24\u53e3\uff0c\u771f\u8981\u5728\u5e8a\u4e0a\u5c06\u53cc\u817f\u53c9\u5f00\u6765\u4ed6\u53c8\u6ca1\u6709\u52c7\u6c14\u722c\u4e0a\u6765\uff1b\u8fd9\u79cd\u89d2\u8272\uff0c\u6797\u96ea\u6700\u662f\u770b\u4e0d\u8d77\uff0c\u5979\u77e5\u9053\u8c22\u5251\u5357\u4e0d\u4f1a\u8ddf\u5468\u5174\u4e1c\u4e00\u822c\u89c1\u8bc6\uff0c\u5fc3\u60f3\u4ed6\u6216\u8bb8\u4e0d\u60f3\u641e\u7838\u4e86\u4e0e\u9526\u6e56\u7684\u4e70\u5356\u3002\u77e5\u9053\u8c22\u5251\u5357\u8fd9\u79cd\u4eba\u610f\u5fd7\u575a\u5b9a\uff0c\u6253\u5b9a\u4e3b\u610f\u4e4b\u540e\u5c31\u662f\u6cb9\u76d0\u6cfc\u4e0d\u8fdb\u7684\u77f3\u5934\uff0c\u4e0d\u8981\u8bf4\u5468\u5174\u4e1c\u8fd9\u4e2a\u4e8c\u4e16\u7956\u4e86\uff0c\u4e5f\u4e0d\u662f\u738b\u6d77\u7c9f\u8fd9\u4e2a\u5c0f\u767d\u8138\u80fd\u6bd4\u7684\uff0c\u5927\u6982\u8ddf\u9526\u6e56\u6597\u4e86\u8fd9\u4e9b\u5e74\u4e5f\u610f\u5174\u9611\u73ca\u4e86\uff0c\u6709\u4e9b\u60f3\u907f\u4e8b\u3002\u201d</p><p>\u3000\u3000\u6797\u96ea\u4e5f\u6ca1\u6709\u7ee7\u7eed\u7ea0\u7f20\u95ee\u4e0b\u53bb\uff0c\u8c22\u5251\u5357\u5750\u4e86\u4e00\u4f1a\u513f\u5c31\u544a\u8f9e\u79bb\u5f00\u3002\u5750\u4e0a\u8f66\u8981\u79bb\u5f00\u6c47\u6cc9\u4f1a\u6240\u65f6\uff0c\u8d76\u5de7\u6709\u4e00\u8f86\u94f6\u7070\u8272\u7684\u65b0\u6b3e\u5965\u8fea\u8f7f\u8f66\u8fc7\u6765\u505c\u5728\u4f1a\u6240\u95e8\u524d\uff0c\u8c22\u5251\u5357\u7b49\u4ed6\u7684\u8f66\u9a76\u51fa\u4f1a\u6240\u524d\u7684\u7a7a\u573a\u5730\uff0c\u770b\u89c1\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u80e1\u91d1\u661f\u4ece\u91cc\u9762\u8d70\u51fa\u6765\u5750\u4e0a\u5965\u8fea\u8f66\uff0c\u4ed6\u4eec\u7684\u8f66\u5b50\u542f\u52a8\u5f88\u5feb\uff0c\u5f88\u5feb\u8d85\u8fc7\u53bb\uff0c\u8d85\u8f66\u65f6\uff0c\u80e1\u91d1\u661f\u8fd8\u63a2\u51fa\u5934\u6765\u6253\u62db\u547c\uff1a\u201c\u8c22\u603b\u8981\u4e0d\u8981\u56de\u4e1c\u5927\u6545\u5730\u91cd\u6e38\uff1f\u201d</p><p>\u3000\u3000\u201c\u591a\u8c22\u4e86\uff0c\u56de\u5bbe\u9986\u8fd8\u6709\u4e8b\u60c5\u8981\u5904\u7406\uff0c\u771f\u662f\u82e6\u547d\u3002\u201d\u8c22\u5251\u5357\u5a49\u62d2\u9053\uff0c\u4ed6\u8fd9\u624d\u770b\u6e05\u695a\u9a7e\u8f66\u4e5f\u662f\u65f6\u5e38\u8ddf\u80e1\u91d1\u661f\u80e1\u5728\u4e00\u8d77\u7684\u9752\u5e74\uff0c\u4e5f\u662f\u4e1c\u5927\u5b66\u5c06\u6bd5\u4e1a\u7684\u5b66\u751f\uff0c\u662f\u4e1c\u6d77\u7701\u6559\u80b2\u5385\u67d0\u4e2a\u526f\u5385\u957f\u7684\u513f\u5b50\uff0c\u542c\u8bf4\u4ed6\u4eec\u9760\u7740\u5185\u5e55\u6d88\u606f\u7092\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\u53d1\u4e86\u6a2a\u8d22\uff0c\u6d77\u7c9f\u79d1\u6280\u80a1\u4ef7\u4e00\u5e74\u4e8c\u5341\u516b\u500d\u7684\u589e\u6da8\uff0c\u8fd9\u4e9b\u4eba\u8000\u6b66\u626c\u5a01\u7684\u5f00\u4e0a\u65b0\u6b3e\u5965\u8fea\u8f66\u4e5f\u662f\u7a00\u677e\u5e73\u5e38\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u6211\u90fd\u6ca1\u6709\u673a\u4f1a\u966a\u9676\u664b\u4e66\u8bb0\u4e0b\u68cb\uff0c\u4e0d\u8fc7\u542c\u522b\u4eba\u8bf4\u8d77\u8fc7\u4ed6\u4e0b\u68cb\u7684\u98ce\u683c\uff0c\u4ed6\u4e0b\u68cb\u65f6\u4e0d\u559c\u6b22\u8bf4\u8bdd\uff0c\u4e00\u53e5\u8bdd\u90fd\u4e0d\u8bf4\uff0c\u4ed6\u6c89\u9ed8\u7740\uff0c\u522b\u4eba\u591a\u534a\u4e5f\u8ddf\u7740\u6c89\u9ed8\uff0c\u53ea\u6709\u2018\u4e01\u4e01\u2019\u843d\u6c34\u4e4b\u58f0\u2026\u2026\u201d\u5f90\u5b66\u5e73\u62ac\u624b\u6446\u4e0b\u4e00\u7c92\u68cb\u5b50\uff0c\u8bf4\u8d77\u4e9b\u9676\u664b\u7684\u8f76\u4e8b\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u4ed6\u4e3b\u52a8\u627e\u6211\u7684\u6b21\u6570\u662f\u4e00\u6b21\u8fd8\u662f\u4e24\u6b21\uff0c\u6211\u90fd\u6709\u4e9b\u8bb0\u4e0d\u6e05\u4e86\uff0c\u4e5f\u771f\u662f\u591f\u6c89\u9ed8\u7684\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u8fd9\u6b21\u627e\u6211\u504f\u504f\u53c8\u4e22\u4e86\u4e00\u4e2a\u96be\u9898\u7ed9\u6211\u3002\u201d</p><p>\u3000\u3000\u201c\u56fd\u5185\u7684\u623f\u4ef7\u4e0a\u6da8\u771f\u7684\u5f88\u96be\u6291\u5236\uff1f\u201d\u5f90\u5b66\u5e73\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u5efa\u90ba\u624d\u8fdb\u5165\u623f\u6539\u521d\u671f\uff0c\u4e4b\u524d\u53d6\u6d88\u7684\u662f\u56fd\u6709\u4f01\u4e1a\u804c\u5de5\u5355\u4f4d\u5206\u623f\u798f\u5229\uff0c\u7b49\u4eca\u5e74\u5e74\u4e2d\u5f7b\u5e95\u53d6\u6d88\u673a\u5173\u4e8b\u4e1a\u5355\u4f4d\u5458\u5de5\u5206\u623f\u798f\u5229\uff0c\u8fd8\u4f1a\u6709\u4e00\u6ce2\u4e0a\u6da8\u51b2\u52a8\u3002\u91d1\u5c71\u4e5f\u4f1a\u5982\u6b64\uff0c\u540c\u6837\u7684\u8d8b\u52bf\u4e5f\u5c06\u590d\u5236\u5230\u5176\u4ed6\u4e8c\u4e09\u7ebf\u57ce\u5e02\u2026\u2026\u56fd\u5185\u6c11\u95f4\u8d44\u672c\u8fd9\u51e0\u5e74\u805a\u96c6\u901f\u5ea6\u8f83\u5feb\uff0c\u4f46\u662f\u51fa\u8def\u53c8\u8f83\u7a84\uff0c\u5f53\u623f\u4ef7\u6301\u7eed\u4e0a\u6da8\uff0c\u6c11\u95f4\u8d44\u672c\u5c31\u4f1a\u6709\u5f80\u623f\u5730\u4ea7\u6c89\u6dc0\u52a0\u901f\u7684\u8d8b\u52bf\uff0c\u6295\u673a\u884c\u4e3a\u4e5f\u4f1a\u65e5\u8d8b\u7316\u7357\uff0c\u8fd9\u65f6\u5019\u8ba8\u8bba\u6291\u5236\u623f\u4ef7\u4e3a\u65f6\u8fd8\u6709\u4e9b\u65e9\u4e86\uff0c\u201d\u5f20\u606a\u8ddf\u5f90\u5b66\u5e73\u8bf4\u8bdd\u6bd4\u8f83\u968f\u4fbf\uff0c\u4e5f\u80fd\u5c06\u8bdd\u8bf4\u900f\uff0c\u201c\u4e16\u7eaa\u9526\u6e56\u7684\u8bd5\u9a8c\u6027\u6a21\u5f0f\uff0c\u6216\u8bb8\u80fd\u6291\u5236\u5176\u4ed6\u5f00\u53d1\u5546\u8fc7\u5ea6\u8d2a\u5a6a\u4e4b\u5fc3\uff0c\u4f46\u662f\u5bf9\u6709\u6548\u6291\u5236\u623f\u4ef7\u80fd\u4e0d\u80fd\u53d1\u6325\u4e00\u4e9b\u5f71\u54cd\uff0c\u8fd8\u771f\u662f\u6ca1\u6709\u591a\u5c11\u4fe1\u5fc3\u2026\u2026\u201d</p></article>"}}}]);
