(window.webpackJsonp=window.webpackJsonp||[]).push([[1440],{1463:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u8282 \u535a\u5b66\u4e4b\u57ce",content:"<article><h2>\u7b2c\u4e09\u8282 \u535a\u5b66\u4e4b\u57ce</h2><p>\u8d39\u4f26\u5927\u9646\u4e0a\uff0c\u4efb\u4f55\u4e00\u5ea7\u9b54\u6cd5\u5b66\u6821\uff08\u867d\u7136\u5176\u5b9e\u5e76\u4e0d\u591a\uff09\u7684\u5bfc\u5e08\u90fd\u4f1a\u544a\u8bc9\u5b66\u751f\uff1a\u51e1\u4eba\u65bd\u5c55\u4e00\u5207\u9b54\u6cd5\u7686\u9700\u901a\u8fc7\u9b54\u7f51\uff0c\u65e0\u8bba\u662f\u795e\u672f\u8fd8\u662f\u5965\u672f\u90fd\u4e0d\u4f8b\u5916\uff0c\u53ea\u4e0d\u8fc7\u5965\u672f\u662f\u65bd\u6cd5\u8005\u76f4\u63a5\u81ea\u9b54\u7f51\u4e2d\u63d0\u53d6\u529b\u91cf\u5851\u6210\uff0c\u795e\u672f\u5219\u662f\u795e\u7947\u4e8b\u5148\u5b8c\u6210\uff0c\u901a\u8fc7\u9b54\u7f51\u8d50\u4e88\u4fe1\u5f92\u7f62\u4e86\u3002\u6700\u660e\u663e\u7684\u4f8b\u5b50\u5c31\u662f\u52a8\u8361\u5e74\u4ee3\uff0c\u5f53\u65f6\u7684\u9b54\u6cd5\u5973\u795e\u6b7b\u4ea1\uff0c\u9b54\u7f51\u51e0\u4e4e\u5d29\u6e83\uff0c\u6240\u6709\u7684\u65bd\u6cd5\u8005\u2014\u2014\u5deb\u5e08\u3001\u7267\u5e08\u3001\u5fb7\u9c81\u4f9d\u3001\u541f\u6e38\u8bd7\u4eba\u7b49\u7b49\u5168\u90fd\u56e0\u6b64\u53d7\u5230\u4e25\u91cd\u5f71\u54cd\u3002</p><p>\u4f46\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u6709\u89c4\u5219\u5c31\u6709\u4f8b\u5916\uff0c\u4f8b\u5916\u7684\u5b58\u5728\u624d\u8bc1\u660e\u4e86\u89c4\u5219\u7684\u6b63\u786e\u3002\u6b63\u5982\u773c\u9b54\u91cc\u4f1a\u51fa\u523a\u778e\u81ea\u5df1\u4e3b\u773c\u7684\u773c\u9b54\u5deb\u5e08\uff0c\u77ee\u4eba\u91cc\u4e5f\u6709\u7b26\u7eb9\u5deb\u5e08\uff0c\u751a\u81f3\u542c\u8bf4\u8fd8\u6709\u5584\u826f\u7684\u9ed1\u6697\u7cbe\u7075\u2014\u2014\u867d\u7136\u743c\u6069\u89c9\u5f97\u8fd9\u6050\u6015\u662f\u8c23\u4f20\u3002\u603b\u800c\u8a00\u4e4b\uff0c\u4e00\u65e6\u67d0\u4e2a\u89c4\u5219\u88ab\u5e7f\u6cdb\u8ba4\u53ef\uff0c\u90a3\u4e48\u603b\u4f1a\u6709\u4e9b\u7279\u522b\u7684\u53cd\u4f8b\u51fa\u6765\u6363\u4e71\u3002</p><p>\u8fd9\u4e16\u754c\u4e0a\u786e\u5b9e\u662f\u6709\u67d0\u4e9b\u7279\u522b\u7684\u751f\u7269\uff0c\u4e0d\u9700\u8981\u901a\u8fc7\u9b54\u7f51\u5c31\u80fd\u65bd\u6cd5\u7684\u2014\u2014\u6216\u8005\u8bf4\uff0c\u4ed6\u4eec\u5f04\u51fa\u6765\u7684\u672a\u5fc5\u771f\u662f\u201c\u9b54\u6cd5\u201d\uff0c\u4f46\u53cd\u6b63\u4e00\u6837\u6709\u6548\u679c\uff0c\u8fd9\u5c31\u8db3\u591f\u4e86\u3002\u53ea\u8981\u4e00\u4e2a\u4eba\u6307\u5c16\u8ff8\u51fa\u6765\u7684\u662f\u8000\u773c\u95ea\u7535\uff0c\u80fd\u591f\u5c06\u5bf9\u624b\u70e4\u6210\u7126\u70ad\uff0c\u90a3\u4e48\u4f60\u7ba1\u4ed6\u7528\u7684\u662f\u9b54\u6cd5\u8fd8\u662f\u5176\u4ed6\u4ec0\u4e48\u73a9\u610f\uff0c\u7ed3\u679c\u4e0d\u90fd\u4e00\u6837\u4e48\u3002</p><p>\u81f3\u5c11\u4ece\u5b9e\u7528\u4e3b\u4e49\u7684\u89d2\u5ea6\u6765\u8bf4\u662f\u5982\u6b64\uff0c\u81f3\u4e8e\u7406\u8bba\u63a2\u8ba8\uff0c\u90a3\u8fd8\u662f\u7559\u7ed9\u5b66\u8005\u4eec\u53bb\u7814\u7a76\u5427\u3002</p><p>\u8fd9\u79cd\u6280\u827a\u88ab\u79f0\u4e3a\u201c\u5fc3\u7075\u5f02\u80fd\u201d\uff08\u7b80\u79f0\u7075\u80fd\uff09\uff0c\u56e0\u4e3a\u5b83\u6765\u81ea\u6700\u7eaf\u7cb9\u7684\u5fc3\u7075\u529b\u91cf\uff0c\u800c\u975e\u9b54\u7f51\u3001\u795e\u7947\u3001\u5492\u8a00\u3001\u7b26\u6587\u6216\u8005\u5176\u4ed6\u3002\u7406\u6240\u5f53\u7136\u7684\uff0c\u5b83\u504f\u91cd\u4e8e\u5bf9\u5fc3\u667a\u7684\u5f71\u54cd\uff0c\u5bf9\u7cbe\u795e\u7684\u64cd\u63a7\uff0c\u4f46\u5728\u5176\u4ed6\u65b9\u9762\u4e5f\u6709\u4e00\u5b9a\u7684\u6548\u7528\u3002\u5b83\u4e0d\u5982\u5deb\u5e08\u7684\u5965\u672f\u90a3\u6837\u6d69\u701a\u4e30\u5bcc\uff0c\u79cd\u7c7b\u7e41\u591a\uff0c\u5374\u4e5f\u81ea\u6709\u5176\u72ec\u5230\u4e4b\u5904\uff0c\u4e0d\u53ef\u8f7b\u89c6\u3002</p><p>\u743c\u6069\u5bf9\u8fd9\u65b9\u9762\u4e0d\u662f\u5f88\u4e86\u89e3\uff0c\u53ea\u9690\u7ea6\u77e5\u9053\u5927\u6982\uff0c\u4f46\u81f3\u5c11\u4ed6\u53ef\u4ee5\u786e\u5b9a\u4e00\u70b9\uff1a\u7075\u80fd\u4e5f\u662f\u53ef\u4ee5\u5b9e\u73b0\u4f20\u9001\u6548\u679c\u7684\u3002\u800c\u4e14\u6b63\u5982\u6b27\u51ef\u6240\u8bf4\uff0c\u7075\u80fd\u4e0d\u9700\u8981\u501f\u52a9\u9b54\u7f51\uff0c\u6240\u4ee5\u4e5f\u4e0d\u4f1a\u88ab\u5730\u8109\u8f90\u5c04\u6240\u5f71\u54cd\u5e72\u6270\u3002</p><p>\u7136\u800c\u95ee\u9898\u5728\u4e8e\uff0c\u7075\u80fd\u662f\u6781\u5176\u7f55\u89c1\u7684\u6280\u827a\uff0c\u5b83\u4f9d\u8d56\u7684\u662f\u5929\u8d4b\u800c\u975e\u540e\u5929\u7684\u5b66\u4e60\uff0c\u5c31\u50cf\u51db\u7684\u5b9d\u77f3\u9b54\u6cd5\u4e00\u6837\u3002\u7075\u80fd\u7684\u4f7f\u7528\u8005\u88ab\u79f0\u4e3a\u5fc3\u7075\u672f\u58eb\uff0c\u6570\u91cf\u6781\u5c11\uff0c\u5728\u5730\u8868\u4e16\u754c\u57fa\u672c\u662f\u627e\u4e0d\u5230\u3002\u5728\u8fd9\u5e7d\u6697\u5730\u57df\u91cc\uff0c\u5012\u6709\u4e00\u4e2a\u79cd\u65cf\u662f\u4ee5\u7075\u80fd\u7740\u79f0\uff0c\u5927\u540d\u9f0e\u9f0e\uff0c\u4f46\u743c\u6069\u4e00\u60f3\u8d77\u548c\u5b83\u4eec\u6253\u4ea4\u9053\u5c31\u4e0d\u514d\u80cc\u4e0a\u4e0d\u505c\u5192\u5bd2\u6c14\u3002</p><p>\u201c\u6211\u8bf4\uff0c\u6b27\u51ef\u5148\u751f\uff0c\u201d\u743c\u6069\u6709\u70b9\u4e0d\u6562\u786e\u4fe1\uff0c\u201c\u83ab\u975e\u4f60\u7684\u610f\u601d\u662f\u6307\u90a3\u4e9b\u2026\u2026\u201d</p><p>\u201c\u7075\u5438\u602a\u561b\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u8fd9\u4e16\u754c\u4e0a\u8fd8\u6709\u8c01\u6bd4\u7075\u5438\u602a\u66f4\u64c5\u957f\u7075\u80fd\u5462\u3002\u201d</p><p>\u662f\u6ca1\u9519\uff0c\u7075\u5438\u602a\u6b63\u662f\u6700\u8457\u540d\u7684\u5fc3\u7075\u672f\u58eb\uff0c\u65e2\u64c5\u957f\u7075\u80fd\uff0c\u800c\u4e14\u8fd8\u64c5\u957f\u8bfb\u53d6\u601d\u7ef4\uff0c\u4fdd\u8bc1\u53ef\u4ee5\u7cbe\u786e\u5730\u5c06\u9c81\u6587\u8111\u4e2d\u6709\u5173\u4f0a\u5361\u6c99\u57ce\u7684\u60c5\u666f\u590d\u5236\u51fa\u6765\u3002\u5982\u679c\u5b83\u4eec\u80af\u5e2e\u5fd9\u7684\u8bdd\uff0c\u786e\u5b9e\u662f\u6709\u5e0c\u671b\u5c06\u743c\u6069\u4e00\u884c\u4eba\u76f4\u63a5\u4f20\u9001\u5230\u76ee\u7684\u5730\u7684\u3002\u4f46\u7075\u5438\u602a\u53ef\u662f\u6076\u540d\u662d\u7740\u7684\u751f\u7269\uff0c\u751a\u81f3\u6bd4\u9ed1\u6697\u7cbe\u7075\u8fd8\u4ee4\u4eba\u754f\u60e7\uff0c\u5b83\u4eec\u5438\u566c\u667a\u6167\u751f\u547d\u7684\u5927\u8111\u4f5c\u4e3a\u98df\u7269\uff0c\u4fb5\u5360\u7c7b\u4eba\u751f\u7269\u7684\u8eaf\u4f53\u4ee5\u7e41\u884d\u540e\u4ee3\uff0c\u8fd0\u7528\u5929\u8d4b\u7684\u5fc3\u7075\u5f02\u80fd\u5974\u5f79\u5176\u4ed6\u79cd\u65cf\uff0c\u5c31\u8fde\u9634\u9b42\u57ce\u7684\u519b\u961f\u5728\u51fa\u53d1\u524d\u90fd\u66fe\u7ecf\u8b66\u544a\u8fc7\u58eb\u5175\uff0c\u8f7b\u6613\u4e0d\u8981\u53bb\u62db\u60f9\u8fd9\u4e9b\u7ae0\u9c7c\u8111\u888b\u7684\u5bb6\u4f19\uff0c\u5bf9\u5b83\u4eec\u907f\u8ba9\u4e09\u5206\u3002\u6b27\u51ef\u5c45\u7136\u63d0\u8bae\u53bb\u627e\u5b83\u4eec\uff0c\u96be\u9053\u662f\u89c9\u5f97\u5927\u5bb6\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u4e48\u3002</p><p>\u201c\u6ca1\u90a3\u4e48\u5938\u5f20\u5566\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u7075\u5438\u602a\u867d\u7136\u662f\u68d8\u624b\u4e86\u4e00\u70b9\u2014\u2014\u4f46\u4e5f\u53ea\u662f\u4e00\u70b9\u7f62\u4e86\uff0c\u4f60\u770b\u5b83\u4eec\u5439\u5618\u7740\u8981\u7edf\u6cbb\u4e16\u754c\uff0c\u7ed3\u679c\u5230\u73b0\u5728\u4e3a\u6b62\u8fde\u5e7d\u6697\u5730\u57df\u90fd\u6ca1\u641e\u5b9a\uff0c\u5c31\u77e5\u9053\u5176\u5b9e\u4e5f\u4e0d\u8fc7\u5c14\u5c14\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4f60\u7684\u610f\u601d\u662f\uff0c\u6211\u4eec\u53bb\u6293\u4e00\u53ea\u7075\u5438\u602a\u6765\uff0c\u547d\u4ee4\u5b83\u66ff\u6211\u4eec\u5f00\u4f20\u9001\u95e8\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u4e0d\u4e0d\uff0c\u201d\u6b27\u51ef\u6447\u7740\u624b\u6307\uff0c\u201c\u4f60\u4e5f\u77e5\u9053\uff0c\u8fd9\u91cc\u8ddd\u79bb\u4f0a\u5361\u6c99\u57ce\u5b9e\u5728\u662f\u592a\u8fdc\u4e86\u3002\u5c31\u7b97\u6392\u9664\u6389\u5730\u8109\u8f90\u5c04\u7684\u5f71\u54cd\uff0c\u90a3\u4e5f\u5f97\u9700\u8981\u65bd\u6cd5\u8005\u6709\u8db3\u591f\u7684\u9020\u8be3\uff0c\u4e00\u822c\u7684\u7075\u5438\u602a\u662f\u505a\u4e0d\u5230\u8fd9\u70b9\u7684\u2026\u2026\u5514\uff0c\u6362\u7b97\u6210\u6211\u4eec\u7684\u6807\u51c6\u7684\u8bdd\uff0c\u5c31\u662f\u81f3\u5c11\u9700\u8981\u4e00\u53ea\u5927\u5deb\u5e08\u7ea7\u522b\u7684\u7075\u5438\u602a\u624d\u884c\u2014\u2014\u800c\u4e14\u8fd8\u5f97\u8981\u662f\u4fee\u70bc\u4f20\u9001\u7cfb\u7684\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u6ca1\u542c\u660e\u767d\uff0c\u201c\u4f20\u9001\u7cfb\uff1f\u201d</p><p>\u201c\u7075\u80fd\u5206\u4e3a\u516d\u5927\u5b66\u6d3e\uff0c\u201d\u6b27\u51ef\u89e3\u91ca\uff0c\u201c\u9884\u8a00\u3001\u521b\u9020\u3001\u8f6c\u5316\u3001\u5851\u80fd\u3001\u9644\u9b54\u548c\u4f20\u9001\u3002\u4e00\u822c\u7684\u7075\u5438\u602a\u53ea\u4f1a\u4fee\u70bc\u5176\u4e2d\u4e00\u4e24\u7cfb\uff0c\u6216\u8005\u6700\u591a\u4e24\u4e09\u7cfb\uff0c\u672a\u5fc5\u7b26\u5408\u6211\u4eec\u8981\u6c42\u3002\u201d</p><p>\u201c\u90a3\u4e48\uff0c\u4f60\u4e00\u5b9a\u4e5f\u77e5\u9053\u4ece\u54ea\u91cc\u53ef\u4ee5\u627e\u5230\u8fd9\u79cd\u7075\u5438\u602a\u5427\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u5982\u679c\u6211\u8bb0\u5f97\u6ca1\u9519\uff0c\u8fd9\u9644\u8fd1\u2014\u2014\u5443\uff0c\u597d\u5427\uff0c\u6709\u70b9\u8fdc\uff0c\u4f46\u81f3\u5c11\u4e0d\u6bd4\u4f0a\u5361\u6c99\u57ce\u66f4\u8fdc\u2014\u2014\u6709\u4e00\u5ea7\u57ce\u5e02\uff0c\u5e94\u8be5\u7b97\u662f\u5e7d\u6697\u5730\u57df\u91cc\u6700\u5927\u6700\u8457\u540d\u7684\u7075\u5438\u602a\u805a\u5c45\u5730\u4e86\u3002\u90a3\u91cc\u4e00\u5b9a\u6709\u7b26\u5408\u6211\u4eec\u8981\u6c42\u7684\u7075\u5438\u602a\uff0c\u81f3\u5c11\u5b83\u4eec\u7684\u5143\u8001\u9662\u91cc\u80af\u5b9a\u6709\u3002\u201d</p><p>\u7075\u5438\u602a\u7684\u793e\u4f1a\u7ed3\u6784\uff0c\u4ee5\u201c\u4e3b\u8111\u201d\u4e3a\u7cbe\u795e\u9886\u8896\uff0c\u5176\u4e0b\u662f\u5c31\u5143\u8001\u9662\u4f5c\u4e3a\u884c\u653f\u9886\u8896\uff0c\u7531\u6700\u5f3a\u5927\u7684\u6210\u5458\u7ec4\u6210\u3002</p><p>\u743c\u6069\u76ef\u7740\u6b27\u51ef\u3002</p><p>\u201c\u5148\u751f\uff0c\u6211\u73b0\u5728\u5f88\u75b2\u60eb\uff0c\u5728\u8fd9\u9ed1\u6f06\u6f06\u7684\u5730\u4e0b\u8d70\u4e86\u5c06\u8fd1\u534a\u4e2a\u6708\uff0c\u8ddd\u79bb\u4e0a\u4e00\u6b21\u4f11\u606f\u5df2\u7ecf\u8d85\u8fc7\u5341\u5c0f\u65f6\uff0c\u800c\u4e14\u521a\u521a\u7ecf\u5386\u4e86\u4e00\u573a\u6218\u6597\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5982\u679c\u6211\u6ca1\u7406\u89e3\u9519\u7684\u8bdd\uff0c\u4f60\u662f\u5efa\u8bae\u6211\u4eec\u53bb\u4e00\u5ea7\u7075\u5438\u602a\u57ce\u5e02\u2014\u2014\u800c\u4e14\u8fd8\u662f\u5e7d\u6697\u5730\u57df\u91cc\u6700\u5927\u7684\uff0c\u53bb\u8bf4\u670d\u90a3\u4e9b\u7ae0\u9c7c\u8111\u888b\u5411\u6211\u4eec\u70ed\u60c5\u5730\u4f38\u51fa\u63f4\u624b\u2014\u2014\u62b1\u6b49\uff0c\u662f\u89e6\u624b\u2014\u2014\u751a\u81f3\u53ef\u80fd\u9700\u8981\u53bb\u7ed1\u67b6\u67d0\u4e2a\u5143\u8001\u9662\u7684\u5bb6\u4f19\uff0c\u201d\u5deb\u5e08\u77aa\u7740\u60c5\u62a5\u8d29\u5b50\uff0c\u201c\u662f\u4f60\u8bf4\u9519\u4e86\u8fd8\u662f\u6211\u542c\u9519\u4e86\uff1f\u8fd8\u662f\u4f60\u89c9\u5f97\u73b0\u5728\u6c14\u6c1b\u592a\u7d27\u5f20\uff0c\u6240\u4ee5\u5f00\u4e2a\u73a9\u7b11\u6d3b\u8dc3\u4e00\u4e0b\uff1f\u201d</p><p>\u201c\u90fd\u4e0d\u662f\uff0c\u6211\u5f88\u8ba4\u771f\u7684\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u8bf7\u6ce8\u610f\uff0c\u6211\u8bf4\u7684\u662f\u4e00\u5ea7\u7075\u5438\u602a\u57ce\u5e02\u2014\u2014\u662f\u57ce\u5e02\u3002\u201d</p><p>\u201c\u5bf9\u554a\uff0c\u6240\u4ee5\u91cc\u9762\u7684\u7075\u5438\u602a\u66f4\u591a\u66f4\u5371\u9669\u3002\u201d</p><p>\u201c\u4e0d\u4e0d\uff0c\u743c\u6069\uff0c\u6211\u4eec\u5e94\u8be5\u900f\u8fc7\u73b0\u8c61\u770b\u5230\u672c\u8d28\uff0c\u201d\u6b27\u51ef\u4e25\u8083\u5730\u6307\u51fa\uff0c\u201c\u4efb\u4f55\u4e00\u5ea7\u57ce\u5e02\uff0c\u90fd\u5fc5\u7136\u662f\u6709\u5176\u5f8b\u6cd5\u7684\uff0c\u5426\u5219\u6839\u672c\u5efa\u7acb\u4e0d\u8d77\u6765\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5b58\u7eed\u81f3\u4eca\u3002\u6240\u6709\u7684\u667a\u6167\u751f\u547d\uff0c\u53ea\u8981\u6709\u8db3\u591f\u7684\u89c4\u6a21\u805a\u96c6\uff0c\u90a3\u4e48\u5fc5\u5b9a\u5c31\u4f1a\u5f62\u6210\u89c4\u5219\uff0c\u5426\u5219\u53ea\u4f1a\u81ea\u6211\u6bc1\u706d\u2014\u2014\u81f3\u4e8e\u8fd9\u89c4\u5219\u662f\u6210\u6587\u7684\u4e5f\u597d\uff0c\u4e0d\u6210\u6587\u7684\u4e5f\u597d\uff0c\u53eb\u505a\u6cd5\u5178\u4e5f\u597d\uff0c\u53eb\u505a\u4e60\u4fd7\u4e5f\u597d\uff0c\u672c\u8d28\u4e0a\u90fd\u6ca1\u6709\u533a\u522b\u3002\u201d</p><p>\u201c\u53ea\u8981\u6709\u89c4\u5219\uff0c\u5c31\u6709\u5b89\u5168\u3002\u5c31\u7b97\u662f\u4ee5\u6df7\u4e71\u7740\u79f0\u7684\u5353\u5c14\u57ce\u5e02\uff0c\u540c\u6837\u4e5f\u662f\u6709\u5176\u5f8b\u6cd5\u7684\uff0c\u9075\u5faa\u8005\u4f1a\u83b7\u5f97\u4fdd\u969c\uff0c\u800c\u8fdd\u53cd\u8005\u4f1a\u88ab\u4e25\u5389\u60e9\u5904\uff0c\u201d\u60c5\u62a5\u8d29\u5b50\u8f6c\u8fc7\u5934\u53bb\u770b\u6885\u83f2\u65af\uff0c\u201c\u5723\u6b66\u58eb\u5c0f\u59d0\u4e00\u5b9a\u8d5e\u6210\u6211\u7684\u770b\u6cd5\u5bf9\u4e0d\u5bf9\u3002\u201d</p><p>\u201c\u524d\u63d0\u662f\u6211\u4eec\u5f97\u77e5\u9053\u89c4\u5219\u3002\u201d\u6885\u83f2\u65af\u4e0d\u52a8\u58f0\u8272\u3002</p><p>\u6b27\u51ef\u556a\u5730\u6253\u4e86\u4e2a\u54cd\u6307\u3002</p><p>\u201c\u800c\u6211\u77e5\u9053\uff0c\u201d\u4ed6\u5fae\u7b11\uff0c\u201c\u4e0d\u8fc7\u743c\u6069\uff0c\u6211\u4eec\u4e8b\u5148\u8bf4\u597d\u7684\u5bf9\u4e0d\u5bf9\uff1f\u6211\u52a0\u5165\u4f60\u7684\u961f\u4f0d\uff0c\u6761\u4ef6\u662f\u514d\u8d39\u63d0\u4f9b\u4e09\u4e2a\u60c5\u62a5\u2014\u2014\u8fd9\u6b21\u5c31\u7b97\u7b2c\u4e00\u4e2a\uff0c\u5982\u4f55\uff1f\u201d</p><p>\u743c\u6069\u76b1\u7740\u7709\u5934\u6c89\u601d\u3002</p><p>\u201c\u8bf4\u6765\u542c\u542c\uff0c\u201d\u5deb\u5e08\u6700\u540e\u8bf4\uff0c\u201c\u5982\u679c\u6211\u4eec\u9700\u8981\u7528\u5230\uff0c\u90a3\u4e48\u6211\u5c31\u8ba4\u53ef\u3002\u201d</p><p>\u6b27\u51ef\u5fae\u7b11\uff0c\u201c\u68d2\u6781\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u4e48\u63a5\u4e0b\u6765\uff0c\u5973\u58eb\u4eec\u5148\u751f\u4eec\uff0c\u8bf7\u5141\u8bb8\u6211\u9686\u91cd\u4ecb\u7ecd\u5e7d\u6697\u5730\u57df\u4e2d\u6700\u8457\u540d\u7684\u7075\u5438\u602a\u57ce\u5e02\uff1a\u5965\u7075\u591a\u5c14\uff08Oryndoll\uff09\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u201c\u5965\u7075\u591a\u5c14\u7684\u89c4\u5219\u5f88\u7b80\u5355\uff0c\u9664\u4e86\u7075\u5438\u602a\u4e4b\u5916\u7684\u4e00\u5207\u751f\u7269\u90fd\u662f\u5974\u96b6\uff0c\u201d\u6b27\u51ef\u987f\u4e86\u987f\uff0c\u8865\u5145\u8bf4\uff0c\u201c\u6216\u8005\u98df\u7269\u3002\u201d</p><p>\u201c\u90a3\u4e48\u4f60\u662f\u60f3\u5f53\u524d\u8005\u8fd8\u662f\u540e\u8005\uff1f\u201d</p><p>\u201c\u90fd\u4e0d\u60f3\uff0c\u201d\u6b27\u51ef\u6446\u6446\u624b\uff0c\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u6761\u51cc\u9a7e\u4e8e\u4e4b\u4e0a\u7684\u89c4\u5219\uff1a\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u7528\u5b83\u4eec\u6240\u6ca1\u6709\u638c\u63e1\u7684\u77e5\u8bc6\u2014\u2014\u4efb\u4f55\u77e5\u8bc6\uff0c\u5305\u62ec\u5386\u53f2\u3001\u4f20\u95fb\u3001\u67d0\u79cd\u9152\u7684\u79d8\u5bc6\u914d\u65b9\u3001\u8fdc\u65b9\u7684\u6700\u65b0\u6218\u4e89\u6d88\u606f\uff0c\u751a\u81f3\u5929\u6c14\u72b6\u51b5\u2014\u2014\u6765\u6362\u53d6\u5b83\u4eec\u7684\u5e2e\u52a9\u3002\u5b83\u4eec\u4f1a\u8bc4\u4f30\u77e5\u8bc6\u7684\u4ef7\u503c\uff0c\u4ece\u800c\u7ed9\u4e88\u76f8\u5e94\u7684\u62a5\u916c\u3002\u201d</p><p>\u201c\u62a5\u916c\u662f\u54ea\u4e9b\uff1f\u201d\u6885\u83f2\u65af\u7a81\u7136\u95ee\u3002</p><p>\u201c\u5514\uff0c\u5f88\u591a\u554a\uff0c\u6bd4\u5982\u8bf4\u591a\u7559\u4f60\u6d3b\u4e09\u5929\uff0c\u6216\u8005\u5730\u4f4d\u4ece\u98df\u7269\u4e0a\u5347\u4e3a\u5974\u96b6\uff0c\u6216\u8005\u5728\u5438\u98df\u5927\u8111\u7684\u65f6\u5019\u66f4\u6e29\u67d4\u4e00\u4e9b\uff0c\u6216\u8005\u653e\u4f60\u4e00\u6761\u6d3b\u8def\uff0c\u201d\u6b27\u51ef\u8868\u60c5\u4e25\u8083\uff0c\u201c\u5f53\u7136\u8fd9\u4e00\u5207\u90fd\u662f\u4f9d\u636e\u4f60\u6240\u80fd\u63d0\u4f9b\u7684\u60c5\u62a5\u4ef7\u503c\u9ad8\u4f4e\u800c\u51b3\u5b9a\u7684\u3002\u4ea4\u6613\u561b\uff0c\u53ea\u8981\u4f60\u51fa\u4ef7\u591f\u9ad8\uff0c\u90a3\u4e48\u81ea\u7136\u4e5f\u5c31\u80fd\u4e70\u5230\u66f4\u597d\u7684\u5546\u54c1\u3002\u201d</p><p>\u201c\u5982\u679c\u8981\u6c42\u4ed6\u4eec\u5e2e\u5fd9\u4f20\u9001\uff0c\u90a3\u60f3\u5fc5\u662f\u8981\u4ef7\u5f88\u9ad8\u4e86\u3002\u201d</p><p>\u201c\u81ea\u7136\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6211\u4eec\u9700\u8981\u62c5\u5fc3\u4ec0\u4e48\u5462\uff1f\u6211\u4eec\u53e3\u888b\u91cc\u6709\u592a\u591a\u7684\u65b0\u9c9c\u51fa\u7089\u7684\u8d27\u5e01\u4e86\u3002\u96be\u9053\u4f60\u89c9\u5f97\u8fd8\u6709\u7b2c\u4e8c\u4e2a\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u4f1a\u957f\u9014\u8dcb\u6d89\u5230\u5965\u7075\u591a\u5c14\u53bb\u5417\uff1f\u6216\u8005\u6700\u65b0\u7684\u6218\u51b5\uff1f\u5965\u56fe\u548c\u6c99\u739b\u65af\u7684\u73b0\u72b6\uff1f\u751a\u81f3\uff0c\u201d\u4ed6\u5fae\u7b11\u8d77\u6765\uff0c\u201c\u6211\u8fd8\u6b20\u4f60\u4e24\u4e2a\u6d88\u606f\u3002\u201d</p><p>\u201c\u4f60\u786e\u5b9a\uff1f\u201d\u743c\u6069\u6709\u4e9b\u4e0d\u592a\u76f8\u4fe1\uff0c\u201c\u542c\u8d77\u6765\u8fd9\u7fa4\u7075\u5438\u602a\u50cf\u662f\u5728\u5f00\u56fe\u4e66\u9986\u3002\u201d</p><p>\u201c\u5582\u5582\uff0c\u4f60\u8fd9\u662f\u5728\u6000\u7591\u4e00\u4f4d\u8bda\u5b9e\u5546\u4eba\u7684\u4fe1\u8a89\uff0c\u201d\u6b27\u51ef\u6297\u8bae\uff0c\u201c\u6211\u4eec\u4ee5\u5f80\u7684\u5386\u6b21\u751f\u610f\u4e2d\uff0c\u96be\u9053\u6211\u63d0\u4f9b\u8fc7\u865a\u5047\u5546\u54c1\u5417\uff1f\u96be\u9053\u6211\u6709\u8fc7\u4efb\u4f55\u4e0d\u8bda\u5b9e\u7684\u8bb0\u5f55\u5417\uff1f\u96be\u9053\u6211\u4eec\u7684\u5408\u4f5c\u4e0d\u662f\u4e00\u76f4\u5f88\u6109\u5feb\u5417\uff1f\u201d</p><p>\u201c\u6700\u540e\u4e00\u70b9\u5c31\u7b97\u4e86\u3002\u201d\u743c\u6069\u4f4e\u58f0\u8bf4\u3002</p><p>\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5965\u7075\u591a\u5c14\u8fd9\u4e2a\u8bcd\uff0c\u5728\u7075\u5438\u602a\u7684\u5ed3\u5229\u65af\u8bed\uff08Qualith\uff09\u91cc\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\u3002\u201d</p><p>\u201c\u662f\u535a\u5b66\u591a\u95fb\u7684\u610f\u601d\u3002\u201d\u6885\u83f2\u65af\u5728\u65c1\u8fb9\u8bf4\u3002</p><p>\u201c\u6ca1\u9519\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u6885\u83f2\u65af\u5c0f\u59d0\uff0c\u4f60\u4e5f\u8db3\u4ee5\u79f0\u5f97\u4e0a\u662f\u2018\u5965\u7075\u591a\u5c14\u2019\u4e86\uff0c\u5982\u679c\u6309\u7167\u7075\u5438\u602a\u7684\u53eb\u6cd5\uff0c\u201d\u4ed6\u563b\u563b\u4e00\u7b11\uff0c\u201c\u5965\u7075\u591a\u5c14\u57ce\uff0c\u4e5f\u53ef\u4ee5\u7ffb\u8bd1\u6210\u535a\u5b66\u4e4b\u57ce\uff0c\u90a3\u5e2e\u7075\u5438\u602a\u6700\u70ed\u7231\u7684\u4e8b\u60c5\u5c31\u662f\u6536\u96c6\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7684\u4e00\u5207\u4fe1\u606f\uff0c\u6240\u4ee5\u4f60\u8bf4\u662f\u5f00\u56fe\u4e66\u9986\u7684\u5176\u5b9e\u4e5f\u6ca1\u9519\u3002\u201d</p><p>\u201c\u542c\u8d77\u6765\u548c\u4f60\u662f\u540c\u884c\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u4f3c\u4e4e\u5f88\u6709\u8da3\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u5b83\u4eec\u8ba4\u4e3a\u8fd9\u4e2a\u4e16\u754c\u5e76\u975e\u7269\u8d28\u5316\u7684\u5b58\u5728\uff0c\u800c\u662f\u7531\u5404\u79cd\u610f\u5fd7\u548c\u8868\u8c61\u6784\u6210\u3002\u53ea\u8981\u638c\u63e1\u8db3\u591f\u591a\u7684\u77e5\u8bc6\uff0c\u90a3\u4e48\u7ec8\u6709\u4e00\u5929\uff0c\u5b83\u4eec\u80fd\u591f\u4ece\u4e2d\u7aa5\u89c1\u591a\u5143\u5b87\u5b99\u7684\u7ec8\u6781\u5965\u79d8\uff0c\u638c\u63e1\u751a\u81f3\u521b\u9020\u90a3\u4e2a\u552f\u4e00\u6cd5\u5219\u3002\u201d</p><p>\u201c\u771f\u662f\u5947\u602a\u7684\u60f3\u6cd5\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u4e0d\u8fc7\u786e\u5b9e\u7b26\u5408\u5b83\u4eec\u7684\u8eab\u4efd\u3002\u201d</p><p>\u201c\u7075\u5438\u602a\u561b\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u4f60\u4e0d\u80fd\u6307\u671b\u5b83\u4eec\u548c\u4eba\u7c7b\u4e00\u6837\u601d\u7ef4\u3002\u201d</p><p>\u201c\u6211\u6ca1\u8fd9\u79cd\u4e0d\u5207\u5b9e\u9645\u7684\u671f\u671b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u4e48\u73b0\u5728\u8ba9\u6211\u770b\u770b\u5965\u7075\u591a\u5c14\u5728\u4ec0\u4e48\u5730\u65b9\u3002\u201d</p><p>\u8299\u857e\u72c4\u53d6\u51fa\u5730\u56fe\uff0c\u6b27\u51ef\u7aef\u8be6\u4e86\u4e00\u4f1a\uff0c\u5728\u5176\u4e2d\u4e00\u4e2a\u4f4d\u7f6e\u70b9\u4e86\u4e00\u70b9\uff0c\u201c\u5c31\u5728\u8fd9\u91cc\uff0c\u201d\u4ed6\u80af\u5b9a\u5730\u8bf4\uff0c\u201c\u4ee5\u524d\u6211\u66fe\u7ecf\u62dc\u8bbf\u8fc7\u4e00\u6b21\u3002\u201d</p><p>\u4ece\u5730\u56fe\u4e0a\u770b\uff0c\u662f\u5728\u9c81\u91cc\u838e\u5c14\u57ce\u7684\u5de6\u4e0b\u65b9\uff0c\u786e\u5b9e\u6709\u70b9\u8ddd\u79bb\u3002\u4e0d\u8fc7\u56e0\u4e3a\u5e7d\u6697\u5730\u57df\u91cc\u6ca1\u6cd5\u8d70\u76f4\u7ebf\uff08\u5f53\u7136\u5730\u8868\u65c5\u884c\u540c\u6837\u4e5f\u5f88\u96be\u505a\u5230\uff09\uff0c\u6240\u4ee5\u4ed6\u4eec\u5176\u5b9e\u662f\u4ece\u5de6\u4fa7\u7ed5\u8fc7\u6765\u7684\uff0c\u8fd9\u5bfc\u81f4\u7684\u7ed3\u679c\u662f\u4ed6\u4eec\u4ece\u76ee\u524d\u7684\u4f4d\u7f6e\u51fa\u53d1\uff0c\u5230\u5965\u7075\u591a\u5c14\u53cd\u800c\u6bd4\u9c81\u91cc\u838e\u5c14\u66f4\u8fd1\u4e00\u4e9b\u3002\u800c\u4e14\u82e5\u662f\u771f\u50cf\u6b27\u51ef\u8bf4\u7684\uff0c\u80fd\u591f\u8bf7\u90a3\u5e2e\u7075\u5438\u602a\u5e2e\u5fd9\u76f4\u63a5\u4f20\u9001\u5230\u4f0a\u5361\u6c99\u57ce\uff0c\u5269\u4e0b\u5927\u7ea6\u4e09\u5206\u4e4b\u4e8c\u7684\u8def\u9014\u90fd\u53ef\u4ee5\u76f4\u63a5\u7701\u7565\u4e86\u3002</p><p>\u743c\u6069\u5b9e\u5728\u538c\u5026\u4e86\u5728\u8fd9\u5e7d\u6697\u5730\u57df\u91cc\u8f9b\u82e6\u8dcb\u6d89\uff0c\u4e00\u60f3\u5230\u8fd8\u8981\u8fd9\u6837\u5ea6\u8fc7\u8fd1\u4e24\u4e2a\u6708\uff0c\u4ed6\u4fbf\u611f\u89c9\u81ea\u5df1\u7684\u4eba\u751f\u4ece\u6b64\u9eef\u6de1\u65e0\u5149\u3002\u80fd\u591f\u5feb\u901f\u62b5\u8fbe\u76ee\u7684\u5730\uff0c\u5bf9\u4ed6\u800c\u8a00\u65e0\u7591\u975e\u5e38\u6709\u5438\u5f15\u529b\uff0c\u7136\u800c\u60f3\u5230\u662f\u548c\u6076\u540d\u662d\u7740\u7684\u7075\u5438\u602a\u6253\u4ea4\u9053\uff0c\u4ed6\u5c31\u4e0d\u514d\u6709\u4e9b\u9000\u7f29\u3002</p><p>\u201c\u4f46\u5b83\u4eec\u2026\u2026\u201d</p><p>\u201c\u90aa\u6076\u3001\u5371\u9669\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u6b27\u51ef\u6253\u65ad\uff0c\u201c\u6ca1\u9519\uff0c\u6240\u6709\u4eba\u90fd\u8fd9\u4e48\u8bf4\uff0c\u6240\u6709\u4eba\u4e5f\u90fd\u8fd9\u4e48\u89c9\u5f97\uff0c\u7136\u800c\u95ee\u9898\u5728\u4e8e\uff0c\u5728\u573a\u8bf8\u4f4d\u8c01\u80fd\u8bf4\u51fa\u7075\u5438\u602a\u5230\u5e95\u90aa\u6076\u5230\u4ec0\u4e48\u7a0b\u5ea6\u5462\uff1f\u5371\u9669\u5230\u4ec0\u4e48\u5730\u6b65\u5462\uff1f\u201d\u4ed6\u73af\u89c6\u4f17\u4eba\uff0c\u201c\u5192\u6627\u5730\u8bf4\u53e5\uff0c\u53ea\u6015\u5404\u4f4d\u8fde\u7075\u5438\u602a\u7684\u6a21\u6837\u90fd\u6ca1\u771f\u6b63\u4eb2\u773c\u89c1\u8fc7\u5427\u3002\u201d</p><p>\u8fd9\u662f\u81ea\u7136\uff0c\u7075\u5438\u602a\u53ea\u5728\u5e7d\u6697\u5730\u57df\u6d3b\u52a8\uff0c\u5728\u5730\u8868\u57fa\u672c\u89c1\u4e0d\u5230\u3002\u5bf9\u4e8e\u7edd\u5927\u591a\u6570\u4eba\u7c7b\u6765\u8bf4\uff0c\u5b83\u5176\u5b9e\u53ea\u5b58\u5728\u4e8e\u6545\u4e8b\u548c\u4f20\u95fb\u4e4b\u4e2d\uff0c\u5e76\u6ca1\u4ec0\u4e48\u771f\u5207\u611f\u53d7\u3002\u743c\u6069\u3001\u6885\u83f2\u65af\u548c\u83ab\u5c3c\u5361\u59d0\u59b9\u90fd\u77e5\u9053\u7075\u5438\u602a\uff0c\u90fd\u542c\u8fc7\u5b83\u4eec\u7684\u6050\u6016\u4f20\u95fb\uff0c\u4f46\u786e\u5b9e\u4e5f\u90fd\u6ca1\u771f\u6253\u8fc7\u534a\u70b9\u4ea4\u9053\u3002\u5c31\u7b97\u662f\u5e7d\u6697\u5730\u57df\u7684\u571f\u7740\u5c45\u6c11\u8fea\u74e6\u514b\u548c\u9c81\u6587\u4e5f\u6ca1\u6709\u8fd9\u79cd\u7ecf\u5386\u2014\u2014\u5f53\u7136\uff0c\u8fd9\u4e5f\u53ef\u4ee5\u505a\u53e6\u5916\u4e00\u79cd\u89e3\u91ca\uff1a\u6709\u8fd9\u79cd\u7ecf\u5386\u7684\u90fd\u4e0d\u4f1a\u6d3b\u7740\u7ad9\u5728\u9762\u524d\u4e86\u3002</p><p>\u201c\u6240\u6709\u4f20\u8bf4\u4e2d\u5bf9\u4e8e\u7075\u5438\u602a\u7684\u5f62\u5bb9\u8bcd\uff0c\u96be\u9053\u4e0d\u90fd\u662f\u53ef\u4ee5\u539f\u5c01\u4e0d\u52a8\u5730\u653e\u5728\u9ed1\u6697\u7cbe\u7075\u8eab\u4e0a\u4e48\uff0c\u201d\u6b27\u51ef\u7ee7\u7eed\u8bf4\uff0c\u201c\u4ed6\u4eec\u4e5f\u662f\u6240\u6709\u6545\u4e8b\u4e2d\u7684\u5927\u53cd\u6d3e\uff0c\u662f\u9ed1\u6697\u548c\u68a6\u9b47\uff0c\u7136\u800c\u4e8b\u5b9e\u5982\u4f55\u5462\uff1f\u4e8b\u5b9e\u5c31\u6446\u5728\u6211\u4eec\u9762\u524d\uff0c\u201d\u4ed6\u53cc\u624b\u644a\u5f00\uff0c\u201c\u6211\u4eec\u4e0d\u662f\u521a\u521a\u624d\u4ece\u6c99\u739b\u65af\u57ce\u79bb\u5f00\u5417\uff1f\u90a3\u4e0d\u662f\u4e00\u5ea7\u5353\u5c14\u57ce\u5e02\u5417\uff1f\u8857\u9053\u4e0a\u4e0d\u5168\u662f\u9ed1\u76ae\u80a4\u7684\u9ed1\u6697\u7cbe\u7075\u5417\uff1f\u4f46\u90a3\u91cc\u662f\u9b54\u9b3c\u7684\u5730\u72f1\uff0c\u8fd8\u662f\u6076\u9b54\u7684\u6df1\u6e0a\uff1f\u6211\u89c9\u5f97\u548c\u4efb\u4f55\u4e00\u5ea7\u5730\u8868\u57ce\u5e02\uff0c\u6bd4\u5982\u9634\u9b42\u57ce\uff0c\u4e5f\u6ca1\u591a\u5927\u7684\u533a\u522b\u5427\u3002\u201d</p><p>\u201c\u6c99\u739b\u65af\u57ce\u4e0d\u80fd\u62ff\u6765\u5f53\u4f8b\u5b50\u3002\u201d\u9c81\u6587\u548c\u8fea\u74e6\u514b\u540c\u65f6\u63d2\u5634\uff0c\u7136\u800c\u4ed6\u4eec\u7684\u58f0\u97f3\u88ab\u5ffd\u89c6\u4e86\u3002\u5bf9\u4e8e\u743c\u6069\u7b49\u4eba\u6765\u8bf4\uff0c\u6c99\u739b\u65af\u57ce\u662f\u4ed6\u4eec\u552f\u4e00\u4eb2\u773c\u89c1\u8bc6\u8fc7\u7684\u5353\u5c14\u57ce\u5e02\uff0c\u662f\u4ec5\u6709\u7684\u6837\u672c\u3002</p><p>\u7075\u5438\u602a\u786e\u5b9e\u6076\u540d\u662d\u7740\uff0c\u4f46\u5230\u5e95\u4e5f\u53ea\u662f\u201c\u540d\u58f0\u201d\u7f62\u4e86\uff0c\u8fd9\u91cc\u7684\u4eba\u9664\u4e86\u6b27\u51ef\uff0c\u5176\u4ed6\u8fde\u7075\u5438\u602a\u7684\u6a21\u6837\u90fd\u6ca1\u89c1\u8fc7\u3002\u60f3\u60f3\u5c06\u8981\u5728\u8fd9\u5e7d\u6697\u5730\u57df\u91cc\u7ee7\u7eed\u8d70\u4e24\u4e2a\u6708\u7684\u6050\u6016\u524d\u666f\uff0c\u6b27\u51ef\u7684\u63d0\u8bae\u4fbf\u663e\u5f97\u5206\u5916\u8bf1\u4eba\u8d77\u6765\u3002</p><p>\u201c\u4f60\u662f\u4e2a\u4f18\u79c0\u7684\u6f14\u8bf4\u8005\uff0c\u6b27\u51ef\u5148\u751f\u3002\u201d\u743c\u6069\u8bda\u6073\u5730\u8bf4\u3002</p><p>\u201c\u591a\u8c22\u5938\u5956\uff0c\u201d\u6b27\u51ef\u5fae\u7b11\uff0c\u201c\u5728\u9648\u8ff0\u4e8b\u5b9e\u7684\u65f6\u5019\uff0c\u6211\u4e00\u5411\u4e0d\u9057\u4f59\u529b\u3002\u201d</p><p>\u743c\u6069\u56de\u8fc7\u5934\uff0c\u53bb\u770b\u81ea\u5df1\u7684\u961f\u5458\u4eec\uff0c\u8fd9\u6837\u91cd\u5927\u7684\u51b3\u5b9a\uff0c\u800c\u4e14\u4e0d\u5728\u4e8b\u5148\u8ba1\u5212\u4e4b\u4e2d\uff0c\u81ea\u7136\u4e0d\u80fd\u51ed\u4ed6\u4e00\u8a00\u800c\u51b3\uff0c\u5f97\u5f81\u6c42\u5927\u5bb6\u7684\u610f\u89c1\u3002\u8fea\u74e6\u514b\u575a\u51b3\u53cd\u5bf9\uff0c\u4f5c\u4e3a\u5730\u5e95\u4f8f\u5112\uff0c\u4ed6\u5bf9\u7075\u5438\u602a\u7684\u5370\u8c61\u6781\u5176\u6076\u52a3\u3002\u9c81\u6587\u72b9\u8c6b\u4e86\u4e00\u4f1a\uff0c\u6700\u7ec8\u8fd8\u662f\u8868\u793a\u53ef\u4ee5\u5192\u9669\u8bd5\u8bd5\uff0c\u4f0a\u5361\u6c99\u57ce\u5982\u4eca\u6b63\u5904\u4e8e\u5371\u9669\u65f6\u671f\uff0c\u5916\u6709\u5f3a\u654c\uff0c\u5185\u6709\u7eb7\u4e71\uff0c\u4ed6\u79bb\u5bb6\u5df2\u7ecf\u5f88\u4e45\uff0c\u5b9e\u5728\u62c5\u5fc3\u5f97\u5f88\uff0c\u5e0c\u671b\u80fd\u65e9\u70b9\u56de\u53bb\u3002\u8299\u8389\u5a05\u8868\u793a\u53cd\u5bf9\uff0c\u6885\u83f2\u65af\u5219\u8ba4\u4e3a\u53ef\u4ee5\u8003\u8651\uff0c\u743c\u6069\u89c9\u5f97\u8fd9\u662f\u56e0\u4e3a\u5979\u4ee5\u524d\u66fe\u7ecf\u548c\u6b27\u51ef\u6253\u8fc7\u51e0\u6b21\u4ea4\u9053\uff0c\u77e5\u9053\u8fd9\u4e2a\u60c5\u62a5\u8d29\u5b50\u867d\u7136\u8eab\u4efd\u8be1\u79d8\uff0c\u610f\u56fe\u83ab\u6d4b\uff0c\u7136\u800c\u786e\u5b9e\u8fd8\u4ece\u4e0d\u6492\u8c0e\u3002</p><p>\u201c\u8fdb\u5165\u9ed1\u6697\u4e4b\u5730\u540e\uff0c\u6211\u4eec\u8def\u4e0a\u906d\u9047\u7684\u602a\u7269\u660e\u663e\u53d8\u5f97\u68d8\u624b\u8d77\u6765\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5730\u8109\u8f90\u5c04\u7684\u5f71\u54cd\u52a0\u5f3a\u4e86\uff0c\u602a\u7269\u4eec\u4e5f\u66f4\u52a0\u7684\u51f6\u623e\u5371\u9669\uff0c\u751a\u81f3\u8fd8\u6709\u5f88\u591a\u53d8\u5f02\u54c1\u79cd\u3002\u201d</p><p>\u8fd9\u5012\u662f\u4e8b\u5b9e\u6ca1\u9519\u3002</p><p>\u201c\u5e7d\u6697\u5730\u57df\u91cc\u5176\u5b9e\u6709\u5f88\u591a\u5371\u9669\u81f3\u6781\u7684\u654c\u4eba\uff0c\u53ea\u662f\u6211\u4eec\u5f88\u5e78\u8fd0\uff0c\u4e00\u8def\u4e0a\u90fd\u6ca1\u6709\u649e\u89c1\u2014\u2014\u4f46\u5e78\u8fd0\u4e0d\u4f1a\u6c38\u8fdc\u4f34\u968f\u7740\u6211\u4eec\uff0c\u201d\u5979\u7ee7\u7eed\u8bf4\uff0c\u201c\u867d\u7136\u548c\u7075\u5438\u602a\u6253\u4ea4\u9053\u672a\u5fc5\u662f\u4ec0\u4e48\u597d\u4e3b\u610f\uff0c\u4f46\u76f8\u6bd4\u8d77\u6765\uff0c\u7ee7\u7eed\u4e24\u4e2a\u6708\u7684\u5730\u5e95\u65c5\u884c\u4e5f\u4e0d\u89c1\u5f97\u9ad8\u660e\u5230\u54ea\u91cc\u53bb\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u4e86\u3002\u201d</p><p>\u9664\u4e86\u63d0\u8bae\u8005\u6b27\u51ef\u548c\u743c\u6069\u8fd9\u4e2a\u961f\u957f\u4e4b\u5916\uff0c\u5269\u4e0b\u4e94\u4e2a\u4eba\u4e2d\uff0c\u56db\u4e2a\u4eba\u5df2\u7ecf\u8868\u6001\uff0c\u4e8c\u6bd4\u4e8c\uff0c\u5404\u5360\u4e00\u534a\uff0c\u73b0\u5728\u8f6e\u5230\u8299\u857e\u72c4\u8868\u660e\u6001\u5ea6\u4e86\u3002</p><p>\u201c\u6211\u89c9\u5f97\uff0c\u201d\u5979\u54ac\u7740\u4e0b\u5507\uff0c\u9887\u4e3a\u72b9\u8c6b\u4e0d\u51b3\uff0c\u201c\u53ef\u4ee5\u8003\u8651\u6b27\u51ef\u5148\u751f\u7684\u5efa\u8bae\u5427\u3002\u201d</p><p>\u201c\u662f\u4f60\u7684\u9884\u8a00\u9b54\u6cd5\u544a\u8bc9\u4f60\u8fd9\u6837\u6bd4\u8f83\u5408\u9002\uff1f\u201d\u743c\u6069\u6709\u4e9b\u597d\u5947\uff0c\u8299\u857e\u72c4\u53ef\u5e76\u4e0d\u662f\u4e00\u4e2a\u52c7\u6562\u7684\u5973\u5b69\u5b50\u3002</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u5c0f\u5973\u5b69\u7684\u8138\u66f4\u7ea2\u4e86\uff0c\u5979\u6709\u4e9b\u5c40\u4fc3\u4e0d\u5b89\u5730\u63c9\u7740\u8863\u89d2\uff0c\u201c\u90a3\u4e2a\uff0c\u5176\u5b9e\u6211\u53ea\u662f\u60f3\u5c3d\u5feb\u5230\u8fbe\u76ee\u7684\u5730\uff0c\u56e0\u4e3a\u2026\u2026\u6211\u60f3\u6d17\u6fa1\u4e86\u3002\u201d</p><p>\u743c\u6069\u4e00\u65f6\u6709\u4e9b\u65e0\u8bed\u4e86\uff0c\u7136\u800c\u5f53\u4ed6\u8f6c\u79fb\u76ee\u5149\uff0c\u53d1\u73b0\u5728\u573a\u7684\u5973\u5b69\u5b50\uff0c\u5305\u62ec\u6301\u53cd\u5bf9\u610f\u89c1\u7684\u8299\u8389\u5a05\u548c\u7d20\u6765\u521a\u786c\u7684\u6885\u83f2\u65af\u5728\u5185\uff0c\u4e5f\u90fd\u96be\u5f97\u5730\u9732\u51fa\u4e00\u4e1d\u8d5e\u540c\u7684\u8868\u60c5\u3002\u4e8e\u662f\u4ed6\u644a\u4e86\u644a\u624b\u3002</p><p>\u201c\u5973\u58eb\u4f18\u5148\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u4e48\uff0c\u6211\u4eec\u5c31\u53bb\u548c\u90a3\u7fa4\u7ae0\u9c7c\u5934\u6253\u6253\u4ea4\u9053\u5427\u3002\u201d</p></article>"}}}]);
