(window.webpackJsonp=window.webpackJsonp||[]).push([[1448],{1471:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e00\u8282 \u4e0d\u6b7b\u4e0d\u4f11",content:"<article><h2>\u7b2c\u5341\u4e00\u8282 \u4e0d\u6b7b\u4e0d\u4f11</h2><p>\u867d\u7136\u9c81\u6587\u65e9\u5c31\u63d0\u9192\u8fc7\uff0c\u9ed1\u6697\u7cbe\u7075\u7684\u5e74\u9f84\u548c\u5916\u8868\u4e0d\u80fd\u76f4\u63a5\u6302\u94a9\uff0c\u4ed6\u4eec\u5728\u751f\u547d\u4e2d\u7684\u5927\u591a\u6570\u65f6\u95f4\u91cc\u90fd\u80fd\u4fdd\u6301\u5e74\u8f7b\u7684\u76f8\u8c8c\u3002\u7136\u800c\u5f53\u743c\u6069\u7f13\u6b65\u8d70\u8fdb\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u4f1a\u8bae\u5385\uff0c\u770b\u89c1\u7aef\u5750\u5728\u4e2d\u95f4\u7684\u83f2\u5c14\u4f26\u4e3b\u6bcd\u65f6\uff0c\u4ed6\u4f9d\u65e7\u5fcd\u4e0d\u4f4f\u6697\u4e2d\u8d5e\u53f9\u4e86\u4e00\u58f0\u3002\u5982\u679c\u4e0d\u662f\u65e9\u5c31\u77e5\u9053\u8eab\u4efd\u7684\u8bdd\uff0c\u4ed6\u4e07\u4e07\u4e5f\u4e0d\u4f1a\u628a\u773c\u524d\u8fd9\u4f4d\u770b\u8d77\u6765\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u5b9b\u5982\u59d0\u59b9\u7684\u5e74\u8f7b\u5973\u5b50\uff0c\u4e0e\u4e00\u4f4d\u5df2\u7ecf\u751f\u80b2\u4e86\u4e03\u4e2a\u5973\u513f\u7684\u6bcd\u4eb2\u8054\u7cfb\u8d77\u6765\u3002</p><p>\u201c\u9ed1\u6697\u7cbe\u7075\u771f\u662f\u602a\u80ce\uff0c\u201d\u743c\u6069\u601d\u5fd6\u7740\uff0c\u201c\u5982\u679c\u662f\u4eba\u7c7b\u7684\u8bdd\uff0c\u751f\u80b2\u4e86\u4e03\u4e2a\u5973\u513f\uff0c\u53ea\u6015\u65e9\u5c31\u5df2\u7ecf\u8870\u8001\u4e0d\u582a\u4e86\u5427\u3002\u201d</p><p>\u5728\u83f2\u5c14\u4f26\u4e3b\u6bcd\u7684\u4e24\u65c1\uff0c\u6309\u7167\u6b21\u5e8f\u5750\u7740\u5979\u7684\u516d\u4e2a\u5973\u513f\uff0c\u5305\u62ec\u6700\u672b\u4f4d\u7684\u7ef4\u5eb7\u5c3c\u4e9a\u3002\u5979\u4eec\u5168\u526f\u6b66\u88c5\u5730\u7a7f\u4e0a\u4e86\u7cbe\u91d1\u9501\u7532\uff0c\u5916\u7f69\u7267\u5e08\u888d\uff0c\u80f8\u53e3\u522b\u7740\u767d\u91d1\u80f8\u9488\uff0c\u8170\u95f4\u643a\u5e26\u8c61\u5f81\u86db\u540e\u7267\u5e08\u8eab\u4efd\u7684\u86c7\u9996\u97ad\u3002\u5728\u743c\u6069\u7684\u5965\u672f\u89c6\u89c9\u4e2d\uff0c\u5979\u4eec\u6bcf\u4e2a\u4eba\u8eab\u4e0a\u90fd\u81f3\u5c11\u95ea\u70c1\u7740\u4e94\u516d\u5904\u5f3a\u70c8\u523a\u773c\u7684\u7075\u5149\uff0c\u8bf4\u660e\u643a\u5e26\u4e86\u4e0d\u5c11\u529b\u91cf\u5f3a\u5927\u7684\u9b54\u6cd5\u7269\u54c1\u3002</p><p>\u867d\u7136\u86db\u540e\u6c89\u5bc2\uff0c\u7267\u5e08\u4eec\u5df2\u7ecf\u4e27\u5931\u795e\u672f\uff0c\u4f46\u5e76\u4e0d\u610f\u5473\u7740\u5979\u4eec\u5c31\u6ca6\u4e3a\u5e9f\u4eba\u3002\u9ed1\u6697\u7cbe\u7075\u7684\u5bff\u547d\u8fdc\u8fdc\u6bd4\u4eba\u7c7b\u957f\uff0c\u8fd9\u8ba9\u5979\u4eec\u6709\u5145\u8db3\u7684\u65f6\u95f4\u8fdb\u884c\u5404\u65b9\u9762\u7684\u953b\u70bc\uff0c\u5728\u6765\u4e4b\u524d\uff0c\u743c\u6069\u5c31\u4ece\u7ef4\u5eb7\u5c3c\u4e9a\u53e3\u4e2d\u5f97\u77e5\uff0c\u6240\u6709\u7684\u86db\u540e\u7267\u5e08\uff0c\u9664\u4e86\u8981\u5728\u796d\u53f8\u5b66\u9662\u5b66\u4e60\u5982\u4f55\u4f8d\u5949\u795e\u7947\uff0c\u540c\u65f6\u8fd8\u8981\u82b1\u8d39\u5341\u4e94\u5e74\u5230\u4e8c\u5341\u5e74\u7684\u65f6\u95f4\uff0c\u5728\u6218\u58eb\u5b66\u9662\u4e2d\u8fdb\u4fee\uff0c\u78e8\u70bc\u81ea\u5df1\u7684\u683c\u6597\u6280\u5de7\u2014\u2014\u5f53\u7136\u5979\u4eec\u4e0d\u4f1a\u53bb\u7814\u4e60\u5965\u672f\uff0c\u9664\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u8fd9\u79cd\u7279\u4f8b\u3002\u6240\u4ee5\u5c31\u7b97\u6ca1\u6709\u795e\u672f\u8f85\u52a9\uff0c\u8fd9\u4e9b\u9ed1\u6697\u7cbe\u7075\u7267\u5e08\u4eec\u4f9d\u65e7\u4e5f\u8fd8\u662f\u4ee4\u4eba\u754f\u60e7\u7684\u5bf9\u624b\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u5728\u4ee5\u5f80\u7684\u5c81\u6708\u91cc\uff0c\u5979\u4eec\u8fd8\u50a8\u5907\u4e86\u5927\u91cf\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u5305\u62ec\u5404\u79cd\u5377\u8f74\u3001\u9b54\u6756\u548c\u88c5\u5907\uff0c\u8fd9\u4e9b\u90fd\u662f\u4e0d\u53ef\u8f7b\u5ffd\u7684\u529b\u91cf\u3002</p><p>\u743c\u6069\u9759\u9759\u5730\u7ad9\u5728\u4f1a\u8bae\u5385\u7684\u4e2d\u5fc3\uff0c\u6c89\u9ed8\u4e0d\u8bed\uff0c\u4ed6\u5df2\u7ecf\u9690\u9690\u611f\u53d7\u5230\u4e86\u7a7a\u6c14\u4e2d\u65e0\u5f62\u7684\u538b\u529b\u3002\u9664\u4e86\u83f2\u5c14\u4f26\u4e3b\u6bcd\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u4e4b\u5916\uff0c\u5176\u4ed6\u516d\u4f4d\u5973\u6027\u90fd\u795e\u60c5\u8b66\u60d5\uff0c\u5176\u4e2d\u6709\u51e0\u4eba\u7684\u624b\u6709\u610f\u65e0\u610f\u5730\u6309\u5728\u86c7\u9996\u97ad\u7684\u7cbe\u91d1\u624b\u67c4\u4e0a\u3002\u4eff\u4f5b\u63a5\u4e0b\u6765\u7684\u8c08\u8bdd\uff0c\u53ea\u8981\u4e00\u8a00\u4e0d\u5408\uff0c\u5373\u4fbf\u7ffb\u8138\u52a8\u624b\u3002</p><p>\u548c\u5973\u513f\u4eec\u4e0d\u540c\uff0c\u83f2\u5c14\u4f26\u4e3b\u6bcd\u9762\u8272\u5e73\u6de1\uff0c\u795e\u6001\u60a0\u95f2\u3002\u548c\u7edd\u5927\u591a\u6570\u9ed1\u6697\u7cbe\u7075\u4e00\u6837\uff0c\u5979\u4ece\u672a\u53bb\u8fc7\u5730\u8868\u4e16\u754c\uff0c\u4e5f\u4ece\u6ca1\u6709\u548c\u4eba\u7c7b\u5deb\u5e08\u6253\u8fc7\u4efb\u4f55\u4ea4\u9053\uff0c\u8fd9\u4ee4\u5979\u9762\u5bf9\u743c\u6069\u65f6\u6709\u4e00\u4e9b\u672c\u80fd\u7684\u597d\u5947\u2014\u2014\u4ee5\u53ca\u8c28\u614e\u3002</p><p>\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u7c7b\u7834\u574f\u4e86\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u8ba1\u5212\uff0c\u5728\u6218\u573a\u4e0a\u4fd8\u864f\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u5e76\u4e14\u5728\u56db\u5929\u524d\uff0c\u6740\u4e86\u5979\u7684\u5973\u513f\u5e03\u7eb3\u5951\u65af\u3002</p><p>\u5b9e\u9645\u4e0a\uff0c\u743c\u6069\u4e5f\u4e0d\u60f3\u8fd9\u4e48\u505a\uff0c\u5e73\u767d\u6811\u654c\uff0c\u53ea\u662f\u65e0\u53ef\u5948\u4f55\u3002\u5e03\u7eb3\u5951\u65af\u867d\u7136\u662f\u9ed1\u6697\u7cbe\u7075\u519b\u961f\u7684\u526f\u624b\uff0c\u4f4d\u9636\u5728\u7ef4\u5eb7\u5c3c\u4e9a\u4e4b\u4e0b\uff0c\u5374\u662f\u4e8b\u5b9e\u4e0a\u7684\u6307\u6325\u5b98\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u548c\u743c\u6069\u79c1\u4e0b\u8fbe\u6210\u4e86\u534f\u8bae\uff0c\u51b3\u5b9a\u64a4\u519b\uff0c\u4f46\u5e03\u7eb3\u5951\u65af\u5374\u5728\u4e2d\u95f4\u963b\u6320\uff0c\u53cd\u5bf9\u8fd9\u4e2a\u547d\u4ee4\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u867d\u7136\u662f\u4e3b\u5c06\uff0c\u5374\u539f\u672c\u5c31\u5a01\u4fe1\u4e0d\u9ad8\uff0c\u6b64\u6b21\u88ab\u4fd8\uff0c\u58f0\u671b\u66f4\u8dcc\uff0c\u538b\u5236\u4e0d\u4f4f\u573a\u9762\u3002\u743c\u6069\u773c\u89c1\u60c5\u52bf\u4e0d\u5bf9\uff0c\u7d22\u6027\u4eff\u6548\u4fe1\u9675\u541b\u6545\u4e8b\uff0c\u7a81\u7136\u5077\u88ad\u53d1\u96be\uff0c\u4e00\u4e3e\u51fb\u6740\u4e86\u5e03\u7eb3\u5951\u65af\u3002</p><p>\u9ed1\u6697\u7cbe\u7075\u7d20\u6765\u4f4d\u9636\u4e25\u660e\uff0c\u5973\u6027\u81f3\u4e0a\uff0c\u7267\u5e08\u4e3a\u5c0a\u3002\u6b64\u6b21\u653b\u6253\u4f0a\u5361\u6c99\u57ce\uff0c\u519b\u961f\u4e2d\u7684\u5973\u6027\u7267\u5e08\u53ea\u6709\u7ef4\u5eb7\u5c3c\u4e9a\u548c\u5e03\u7eb3\u5951\u65af\u4e24\u4eba\uff0c\u540e\u8005\u4e00\u6b7b\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4fbf\u7a33\u7a33\u538b\u4f4f\u573a\u9762\uff0c\u9881\u4ee4\u64a4\u519b\u3002\u9ed1\u6697\u7cbe\u7075\u9634\u9669\u72e1\u8bc8\uff0c\u5374\u5e76\u975e\u55dc\u8840\u597d\u6218\u4e4b\u5f92\uff0c\u66f4\u7edd\u65e0\u77ee\u4eba\u90a3\u822c\u608d\u4e0d\u754f\u6b7b\u7684\u82f1\u52c7\u72c2\u70ed\u7cbe\u795e\uff0c\u773c\u89c1\u4f0a\u5361\u6c99\u57ce\u9632\u5b88\u4e25\u5bc6\uff0c\u8fd9\u4e9b\u65e5\u5b50\u4ee5\u6765\u4f24\u4ea1\u4e0d\u5c11\uff0c\u65e9\u5c31\u6697\u4e2d\u840c\u751f\u9000\u610f\uff0c\u5982\u4eca\u4e3b\u5e05\u4e0b\u4e86\u547d\u4ee4\uff0c\u81ea\u7136\u4e50\u5f97\u542c\u4ece\u3002</p><p>\u6b7b\u4e00\u4e2a\u5973\u513f\uff0c\u5bf9\u4e8e\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6765\u8bf4\u5176\u5b9e\u5e76\u975e\u4ec0\u4e48\u4e86\u4e0d\u5f97\u7684\u4e8b\u60c5\uff0c\u9ed1\u6697\u7cbe\u7075\u6700\u64c5\u957f\u7684\u5c31\u662f\u81ea\u76f8\u6b8b\u6740\uff0c\u6781\u5c11\u6709\u4eba\u80fd\u591f\u5bff\u7ec8\u6b63\u5bdd\u3002\u4ee5\u5bff\u547d\u6765\u8bf4\uff0c\u9ed1\u6697\u7cbe\u7075\u80fd\u591f\u6d3b\u5230\u4e03\u767e\u5c81\uff0c\u4f46\u7edd\u5927\u591a\u6570\u90fd\u5728\u56db\u4e94\u767e\u5c81\u5c31\u975e\u6b63\u5e38\u6b7b\u4ea1\u4e86\u3002\u83f2\u5c14\u4f26\u4e3b\u6bcd\u5f53\u5e74\u662f\u5bb6\u65cf\u7684\u6b21\u5973\uff0c\u4fbf\u662f\u5728\u5f1f\u5f1f\u5927\u5deb\u5e08\u4e9a\u5f53\u65af\u7684\u5e2e\u52a9\u4e0b\uff0c\u63a5\u8fde\u8c0b\u6740\u4e86\u6bcd\u4eb2\u548c\u957f\u59d0\uff0c\u968f\u540e\u53c8\u6740\u4e86\u4e24\u4e2a\u59b9\u59b9\uff0c\u65b9\u624d\u5750\u7a33\u4e86\u5982\u4eca\u7684\u4f4d\u7f6e\u3002\u5979\u867d\u7136\u6709\u4e03\u4e2a\u5973\u513f\uff0c\u5374\u4e5f\u4ece\u6765\u5c31\u6ca1\u6307\u671b\u8fc7\u5979\u4eec\u80fd\u957f\u547d\u767e\u5c81\uff0c\u5e73\u5e73\u5b89\u5b89\u2014\u2014\u5bf9\u4e8e\u9ed1\u6697\u7cbe\u7075\uff0c\u5c24\u5176\u662f\u7b2c\u4e00\u5bb6\u65cf\u4e3b\u6bcd\u7684\u5973\u513f\u6765\u8bf4\uff0c\u8fd9\u662f\u5b8c\u5168\u4e0d\u5207\u5b9e\u9645\u7684\u5962\u671b\u3002</p><p>\u5e03\u7eb3\u5951\u65af\u662f\u83f2\u5c14\u4f26\u7684\u7b2c\u4e09\u4e2a\u5973\u513f\uff0c\u6027\u683c\u6c89\u7a33\uff0c\u5934\u8111\u6e05\u6670\uff0c\u5374\u5931\u4e4b\u4e8e\u8fdf\u949d\uff0c\u4e0d\u5982\u5176\u4ed6\u59d0\u59b9\u673a\u654f\u72e1\u8bc8\uff0c\u8fd9\u5bf9\u4e8e\u9ed1\u6697\u7cbe\u7075\u6765\u8bf4\u662f\u81f4\u547d\u7684\u7f3a\u9677\u3002\u83f2\u5c14\u4f26\u4e3b\u6bcd\u65e9\u5c31\u6599\u60f3\u8fc7\u5979\u4f1a\u6b7b\u4e8e\u975e\u547d\u2014\u2014\u7136\u800c\u5374\u4ece\u6ca1\u6599\u5230\u51f6\u624b\u4f1a\u662f\u4e00\u4e2a\u5916\u6765\u7684\u4eba\u7c7b\u3002</p><p>\u8fd9\u5bf9\u4e8e\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u5a01\u4e25\uff0c\u65e0\u7591\u662f\u4e25\u91cd\u7684\u5192\u72af\u3002</p><p>\u7136\u800c\u83f2\u5c14\u4f26\u4e3b\u6bcd\u4f9d\u65e7\u4e0d\u52a8\u58f0\u8272\u3002</p><p>\u743c\u6069\u548c\u83ab\u5c3c\u5361\u59d0\u59b9\u6628\u65e5\u79d8\u5bc6\u5165\u57ce\uff0c\u9664\u4e86\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u9ad8\u9636\u6210\u5458\u5916\uff0c\u5176\u4ed6\u65e0\u4eba\u77e5\u6653\u3002\u5728\u60c5\u51b5\u6ca1\u6709\u5b8c\u5168\u660e\u6717\u4e4b\u524d\uff0c\u83f2\u5c14\u4f26\u4e3b\u6bcd\u5e76\u4e0d\u6253\u7b97\u8ba9\u6267\u653f\u8bae\u4f1a\u77e5\u6653\u8fd9\u4ef6\u4e8b\uff0c\u4ee5\u514d\u7ed9\u5bb6\u65cf\u62db\u6765\u66f4\u591a\u7684\u653b\u51fb\u548c\u9ebb\u70e6\u3002\u5f53\u7136\uff0c\u8fd9\u4ef6\u4e8b\u4e5f\u4e0d\u53ef\u80fd\u4e00\u76f4\u9690\u7792\u4e0b\u53bb\uff0c\u63a5\u4e0b\u6765\u8981\u600e\u4e48\u505a\uff0c\u5c31\u53d6\u51b3\u4e8e\u4eca\u5929\u7684\u4f1a\u89c1\u4e86\u3002</p><p>\u4f1a\u8bae\u5385\u91cc\u5bc2\u9759\u4e00\u7247\u7740\uff0c\u8c01\u4e5f\u6ca1\u6709\u8bf4\u8bdd\uff0c\u743c\u6069\u662f\u5728\u7b49\u5f85\uff0c\u800c\u6309\u7167\u9ed1\u6697\u7cbe\u7075\u7684\u89c4\u77e9\uff0c\u8fd9\u79cd\u573a\u5408\u4e0b\uff0c\u5728\u4e3b\u6bcd\u6ca1\u6709\u8bf4\u8bdd\u4e4b\u524d\uff0c\u5176\u4ed6\u4eba\u662f\u4e0d\u51c6\u53d1\u8a00\u7684\u3002\u6700\u7ec8\uff0c\u83f2\u5c14\u4f26\u4e3b\u6bcd\u5728\u738b\u5ea7\u4e0a\u5fae\u5fae\u6b20\u8eab\uff0c\u6253\u7834\u4e86\u6c89\u9ed8\u3002</p><p>\u201c\u6b22\u8fce\u6765\u5230\u74dc\u7406\u5fb7\u65af\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u5979\u6e29\u548c\u5730\u8bf4\uff0c\u5fae\u5fae\u542b\u7b11\uff0c\u201c\u542c\u8bf4\u60a8\u81ea\u5317\u65b9\u800c\u6765\uff0c\u4e00\u8def\u89c1\u95fb\u5982\u4f55\u3002\u201d</p><p>\u201c\u74dc\u7406\u5fb7\u65af\u662f\u6211\u89c1\u8fc7\u6700\u7e41\u534e\u6700\u7cbe\u81f4\uff0c\u5f53\u7136\u4e5f\u6700\u5f3a\u5927\u7684\u5353\u5c14\u57ce\u5e02\uff0c\u201d\u743c\u6069\u8eac\u8eab\u56de\u7b54\uff0c\u201c\u522b\u8bf4\u6c99\u739b\u65af\u57ce\u8fdc\u8fdc\u4e0d\u5982\uff0c\u53ea\u6015\u5c31\u8fde\u4e45\u8d1f\u76db\u540d\u7684\u95ea\u5149\u86db\u7f51\u4e4b\u57ce\u5951\u5fb7\xb7\u7eb3\u6492\u4e5f\u671b\u5c18\u83ab\u53ca\u3002\u201d</p><p>\u83f2\u5c14\u4f26\u4e3b\u6bcd\u7684\u773c\u775b\u772f\u7f1d\u8d77\u6765\uff0c\u201c\u4f60\u53bb\u8fc7\u5951\u5fb7\xb7\u7eb3\u6492\uff1f\u201d</p><p>\u201c\u53bb\u8fc7\uff0c\u4e0d\u8fc7\u5f88\u53ef\u60dc\uff0c\u5b83\u5df2\u7ecf\u53d8\u6210\u4e86\u4e00\u7247\u5e9f\u589f\uff0c\u201d\u743c\u6069\u5766\u7136\u81ea\u82e5\u5730\u968f\u53e3\u8bf4\uff0c\u201c\u6bc1\u4e8e\u6218\u706b\u3002\u201d</p><p>\u201c\u6218\u706b\uff1f\u201d</p><p>\u201c\u5730\u8868\u7684\u4eba\u7c7b\u519b\u961f\u653b\u5165\u4e86\u5e7d\u6697\u5730\u57df\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4ed6\u4eec\u7528\u6295\u77f3\u8f66\u53d1\u5c04\u4e86\u5927\u91cf\u7684\u70bd\u706b\u80f6\u548c\u71c3\u70e7\u74f6\uff0c\u70e7\u6bc1\u4e86\u5951\u5fb7\xb7\u7eb3\u6492\u7684\u77f3\u7070\u86db\u7f51\u57ce\u57fa\uff0c\u7ed3\u679c\uff0c\u201d\u4ed6\u644a\u4e86\u644a\u624b\uff0c\u201c\u6574\u4e2a\u57ce\u5e02\u5760\u5165\u5927\u88c2\u8c37\u4e4b\u4e2d\u4e86\uff0c\u4ec5\u5b58\u7684\u60ac\u5d16\u4e0a\u7684\u90e8\u5206\u4e5f\u88ab\u5b8c\u5168\u6467\u6bc1\u3002\u201d</p><p>\u743c\u6069\u5176\u5b9e\u6ca1\u6709\u53bb\u8fc7\u5951\u5fb7\xb7\u7eb3\u6492\uff0c\u4f46\u65e2\u7136\u5728\u7b2c\u4e8c\u8fdc\u5f81\u5e08\u91cc\uff0c\u4e8b\u5148\u4e5f\u770b\u8fc7\u5730\u56fe\u8d44\u6599\uff0c\u53c2\u4e0e\u8fc7\u6218\u672f\u63a8\u6f14\uff0c\u77e5\u9053\u5927\u81f4\u60c5\u51b5\u3002\u5951\u5fb7\xb7\u7eb3\u6492\u662f\u4e00\u5ea7\u51cc\u7a7a\u800c\u5efa\u7684\u57ce\u5e02\uff0c\u91cd\u91cd\u53e0\u53e0\u7684\u77f3\u7070\u5316\u86db\u7f51\u8054\u63a5\u7740\u5927\u88c2\u8c37\u7684\u4e24\u9762\u60ac\u5d16\uff0c\u4eff\u4f5b\u4e00\u4e2a\u5de8\u5927\u7684\u7f51\u515c\uff0c\u57ce\u5e02\u5c31\u5efa\u5728\u8fd9\u86db\u7f51\u4e0a\uff0c\u6240\u4ee5\u53c8\u79f0\u201c\u95ea\u5149\u86db\u7f51\u4e4b\u57ce\u201d\u3002\u9634\u9b42\u57ce\u4e8b\u5148\u505a\u4e86\u51e0\u4e2a\u8ba1\u5212\uff0c\u5176\u4e2d\u6700\u540e\u7684\u4e0b\u7b56\uff0c\u5c31\u662f\u5728\u4e07\u4e0d\u5f97\u5df2\u7684\u65f6\u5019\uff0c\u7528\u6295\u77f3\u8f66\u6295\u63b7\u5927\u91cf\u7684\u70bd\u706b\u80f6\u548c\u71c3\u70e7\u74f6\uff0c\u7d22\u6027\u628a\u8fd9\u5ea7\u57ce\u5e02\u5b8c\u5168\u6bc1\u6389\u3002\u53cd\u6b63\u4ed6\u77e5\u9053\u74dc\u7406\u5fb7\u65af\u57ce\u5982\u4eca\u97f3\u8baf\u4e0d\u901a\uff0c\u4e0d\u53ef\u80fd\u77e5\u9053\u4e07\u91cc\u4e4b\u5916\u7684\u5951\u5fb7\xb7\u7eb3\u6492\u7684\u60c5\u5f62\uff0c\u53ea\u8981\u8bf4\u8bdd\u4e0d\u51fa\u5927\u7684\u6f0f\u6d1e\uff0c\u4e5f\u6ca1\u4eba\u80fd\u5bdf\u89c9\u3002</p><p>\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u201c\u6c99\u739b\u65af\u57ce\u7684\u60c5\u51b5\u5982\u4f55\uff1f\u201d</p><p>\u201c\u4e00\u5982\u65e2\u5f80\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u867d\u7136\u90a3\u91cc\u7684\u7267\u5e08\u548c\u5728\u5ea7\u5404\u4f4d\u4e00\u6837\u2026\u2026\u906d\u9047\u4e86\u4e00\u70b9\u5c0f\u5c0f\u7684\u9ebb\u70e6\uff0c\u7136\u800c\u5bf9\u4e8e\u4e00\u5ea7\u4ee5\u5deb\u5e08\u4e3a\u7edf\u6cbb\u8005\u7684\u57ce\u5e02\u800c\u8a00\uff0c\u5e76\u6ca1\u6709\u591a\u5927\u5f71\u54cd\u3002\u201d</p><p>\u201c\u653e\u8086\uff01\u201d</p><p>\u8fe6\u6cd5\u5389\u58f0\u5475\u65a5\u7740\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u6012\u89c6\u7740\u743c\u6069\u5728\u7edd\u5927\u591a\u6570\u9ed1\u6697\u7cbe\u7075\u773c\u4e2d\uff0c\u6c99\u739b\u65af\u57ce\u539f\u672c\u5c31\u662f\u53db\u5f92\u548c\u8d25\u7c7b\uff0c\u4f55\u51b5\u743c\u6069\u7684\u8bdd\u8bed\u91cc\u660e\u663e\u6709\u6240\u55bb\u6307\u3002\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6325\u6325\u624b\uff0c\u793a\u610f\u8fe6\u6cd5\u5750\u4e0b\uff0c\u201c\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u4f60\u4e3a\u6211\u4eec\u5e26\u6765\u4e86\u86db\u540e\u7684\u6d88\u606f\u3002\u201d</p><p>\u201c\u5979\u5df2\u7ecf\u901d\u53bb\u3002\u201d</p><p>\u201c\u4eb5\u6e0e\uff01\u201d</p><p>\u8fd9\u4e0b\u5b50\u4e0d\u4ec5\u4ec5\u662f\u8fe6\u6cd5\uff0c\u51e0\u4e4e\u6240\u6709\u7684\u7267\u5e08\u90fd\u7ad9\u4e86\u8d77\u6765\uff0c\u6709\u4e24\u4e2a\u6027\u6025\u7684\u5df2\u7ecf\u62d4\u51fa\u4e86\u86c7\u9996\u97ad\uff0c\u6bd2\u86c7\u5728\u7cbe\u91d1\u624b\u67c4\u4e0a\u873f\u8712\u76d8\u65cb\u7740\uff0c\u53d1\u51fa\u5636\u5636\u7684\u5c16\u9510\u58f0\u97f3\uff0c\u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\u3002\u5c31\u8fde\u4e00\u76f4\u5e73\u9759\u7684\u83f2\u5c14\u4f26\u4e3b\u6bcd\u90fd\u6709\u6240\u53cd\u5e94\uff0c\u5979\u7684\u8eab\u4f53\u524d\u503e\uff0c\u5c45\u9ad8\u4e34\u4e0b\u5730\u903c\u89c6\u7740\u9762\u524d\u7684\u4eba\u7c7b\uff0c\u201c\u80e1\u8a00\u4e71\u8bed\u4f1a\u4e3a\u4f60\u5e26\u6765\u6740\u8eab\u4e4b\u7978\uff0c\u4eba\u7c7b\u3002\u201d\u5979\u8b66\u544a\u7740\uff0c\u4f46\u8bed\u6c14\u4f3c\u4e4e\u5e76\u4e0d\u4e25\u5389\u3002</p><p>\u201c\u6211\u53ea\u662f\u5728\u9648\u8ff0\u4e8b\u5b9e\u3002\u201d</p><p>\u83f2\u5c14\u4f26\u4e3b\u6bcd\u5fae\u5fae\u76b1\u7709\uff0c\u540c\u6837\u662f\u8fd9\u53e5\u8bdd\uff0c\u8fde\u8bed\u6c14\u90fd\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u5f88\u50cf\u3002\u201c\u4f60\u5982\u4f55\u8bc1\u660e\u8fd9\u662f\u4e8b\u5b9e\uff1f\u201d</p><p>\u201c\u7b80\u5355\u7684\u903b\u8f91\u63a8\u7406\u5c31\u53ef\u4ee5\u5f97\u77e5\uff0c\u5973\u58eb\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u5982\u679c\u4e00\u4f4d\u795e\u7947\u7a81\u7136\u653e\u5f03\u4e86\u5979\u6240\u6709\u7684\u4fe1\u5f92\uff0c\u90a3\u4e48\u8fd9\u610f\u5473\u7740\u4ec0\u4e48\u5462\uff1f\u7ed3\u8bba\u662f\u663e\u800c\u6613\u89c1\u7684\uff0c\u6211\u76f8\u4fe1\u4efb\u4f55\u6709\u57fa\u672c\u7406\u667a\u5224\u65ad\u7684\u4eba\u90fd\u80fd\u5f97\u51fa\u3002\u201d</p><p>\u201c\u795e\u7947\u5e76\u975e\u4f60\u6211\u51e1\u4eba\u53ef\u4ee5\u5984\u81ea\u81c6\u6d4b\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6b63\u662f\u5982\u6b64\uff0c\u5973\u58eb\uff0c\u795e\u7947\u7684\u4e16\u754c\uff0c\u5e76\u975e\u51e1\u4eba\u6240\u80fd\u7aa5\u89c1\uff0c\u6240\u4ee5\u6211\u4eec\u4e5f\u5c31\u53ea\u80fd\u4f9d\u8d56\u81ea\u5df1\u6d45\u8584\u7684\u667a\u8bc6\u6765\u5224\u65ad\u4e86\u3002\u6216\u8005\u8fd9\u4e48\u8bf4\uff0c\u5982\u679c\u6211\u2026\u2026\u6216\u8005\u6211\u7559\u5728\u4f0a\u5361\u6c99\u57ce\u7684\u540c\u4f34\uff0c\u5c06\u8fd9\u4e2a\u6d88\u606f\u6563\u5e03\u51fa\u53bb\uff0c\u8bf7\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5168\u4f53\u5c45\u6c11\u6765\u505a\u8bc4\u5224\u7684\u8bdd\u2026\u2026\u60a8\u89c9\u5f97\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u8ba4\u540c\u6211\u7684\u770b\u6cd5\u5462\u3002\u5982\u679c\u6240\u6709\u4eba\u90fd\u76f8\u4fe1\u67d0\u79cd\u5224\u65ad\u2014\u2014\u5c31\u7b97\u5b83\u4e0d\u7b26\u5408\u4e8b\u5b9e\uff0c\u90a3\u5b83\u4e5f\u5c31\u662f\u4e8b\u5b9e\u4e86\u3002\u201d</p><p>\u83f2\u5c14\u4f26\u4e3b\u6bcd\u4f4e\u6c89\u5730\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u8fd9\u662f\u5a01\u80c1\uff1f\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u60a8\u8a00\u91cd\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u7edd\u65e0\u6b64\u610f\u3002\u201d</p><p>\u77ed\u6682\u7684\u6c89\u9ed8\uff0c\u7136\u540e\u83f2\u5c14\u4f26\u4e3b\u6bcd\u518d\u5ea6\u5f00\u53e3\u3002</p><p>\u201c\u542c\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u60a8\u6709\u610f\u4e8e\u5b9a\u5c45\u4e8e\u74dc\u7406\u5fb7\u65af\uff1f\u201d</p><p>\u201c\u662f\u7684\u3002\u201d</p><p>\u201c\u8fd9\u662f\u4e2a\u660e\u667a\u7684\u9009\u62e9\u3002\u201d</p><p>\u201c\u6211\u4e5f\u8fd9\u6837\u8ba4\u4e3a\u3002\u201d</p><p>\u201c\u90a3\u4e48\u60a8\u6709\u4ec0\u4e48\u5177\u4f53\u6253\u7b97\u5417\uff1f\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u8be2\u95ee\uff0c\u201c\u6211\u5f88\u6709\u5174\u8da3\u542c\u542c\uff0c\u5982\u679c\u4e0d\u4ecb\u610f\u900f\u9732\u7684\u8bdd\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u6211\u5e0c\u671b\u80fd\u52a0\u5165\u83f2\u5c14\u4f26\u5bb6\u65cf\uff0c\u201d\u4ed6\u76f4\u622a\u4e86\u5f53\u5730\u8bf4\uff0c\u201c\u636e\u6211\u6240\u77e5\uff0c\u5353\u5c14\u7684\u793e\u4f1a\u5efa\u7acb\u5728\u5bb6\u65cf\u4e4b\u4e0a\uff0c\u6bcf\u4e2a\u4eba\u7684\u8eab\u4efd\u3001\u5730\u4f4d\u548c\u547d\u8fd0\uff0c\u4e0e\u4ed6\u80cc\u540e\u7684\u5bb6\u65cf\u606f\u606f\u76f8\u8fde\u3002\u6211\u5e0c\u671b\u80fd\u591f\u6709\u4e00\u4e2a\u6bd4\u8f83\u9ad8\u7684\u8d77\u70b9\u3002\u201d</p><p>\u201c\u5408\u60c5\u5408\u7406\u7684\u8981\u6c42\uff0c\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u8bf4\uff0c\u201c\u4f46\u63a5\u7eb3\u4e00\u4f4d\u5730\u8868\u4eba\u7c7b\u4f5c\u4e3a\u5bb6\u65cf\u6210\u5458\uff0c\u8fd9\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5386\u53f2\u4e0a\u8fd8\u662f\u4ece\u65e0\u5148\u4f8b\u7684\u2026\u2026\u201d</p><p>\u201c\u4f5c\u4e3a\u7b2c\u4e00\u5bb6\u65cf\uff0c\u6b63\u662f\u5148\u4f8b\u6700\u5408\u9002\u7684\u521b\u8bbe\u8005\uff0c\u201d\u743c\u6069\u793c\u8c8c\u5730\u6253\u65ad\u4e86\u5979\u7684\u63a8\u6258\uff0c\u201c\u9664\u4e86\u60a8\u4e4b\u5916\uff0c\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\uff0c\u8fd8\u6709\u8c01\u5177\u5907\u8fd9\u79cd\u8d44\u683c\u5462\u3002\u201d</p><p>\u4e3b\u6bcd\u6c89\u541f\u7740\uff0c\u5047\u88c5\u601d\u7d22\uff0c\u867d\u7136\u743c\u6069\u77e5\u9053\u5979\u5176\u5b9e\u65e9\u5c31\u5fc3\u6709\u5b9a\u8ba1\u3002\u201c\u4f60\u6240\u8bf4\u4e5f\u4e0d\u65e0\u9053\u7406\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u7136\u800c\u83f2\u5c14\u4f26\u5bb6\u65cf\u4e5f\u4e0d\u53ef\u80fd\u968f\u610f\u63a5\u7eb3\u65b0\u7684\u6210\u5458\uff0c\u9664\u975e\u6211\u4eec\u80fd\u591f\u786e\u8ba4\u4ed6\u7684\u80fd\u529b\u548c\u5fe0\u8bda\u3002\u201d</p><p>\u201c\u5bf9\u4e8e\u524d\u8005\uff0c\u6211\u76f8\u4fe1\u60a8\u5df2\u7ecf\u6709\u6240\u4e86\u89e3\uff0c\u901a\u8fc7\u60a8\u7684\u5973\u513f\u3002\u201d</p><p>\u201c\u90a3\u4e0d\u591f\uff0c\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6447\u5934\uff0c\u201c\u4f60\u4eec\u4eba\u7c7b\u6709\u4e00\u53e5\u8bdd\uff0c\u773c\u775b\u770b\u5230\u7684\uff0c\u624d\u662f\u771f\u5b9e\u7684\u3002\u201d</p><p>\u743c\u6069\u8038\u4e86\u8038\u80a9\uff0c\u201c\u6089\u542c\u5c0a\u4fbf\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u83f2\u5c14\u4f26\u4e3b\u6bcd\u62cd\u4e86\u62cd\u624b\uff0c\u5de6\u4fa7\u4e00\u4f4d\u5973\u6027\u7267\u5e08\u7ad9\u4e86\u8d77\u6765\uff0c\u5979\u8eab\u6750\u9ad8\u5927\uff0c\u51e0\u4e4e\u548c\u743c\u6069\u5dee\u4e0d\u591a\uff0c\u8170\u95f4\u7684\u86c7\u97ad\u4e0a\u6709\u516d\u9996\uff0c\u4ee3\u8868\u7740\u5979\u5728\u6559\u4f1a\u4e2d\u7684\u4f4d\u9636\u4e0d\u4f4e\u3002\u201c\u4e3d\u5965\uff0c\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u4ecb\u7ecd\u7740\uff0c\u201c\u6211\u7684\u6b21\u5973\uff0c\u5bb6\u65cf\u8725\u8734\u9a91\u5175\u7684\u7edf\u5e05\u3002\u201d</p><p>\u743c\u6069\u8eac\u8eab\u884c\u793c\uff0c\u201c\u5e78\u4f1a\uff0c\u5c0f\u59d0\uff0c\u201d\u4ed6\u5fae\u7b11\u7740\uff0c\u201c\u83ab\u975e\u4e3b\u6bcd\u5927\u4eba\u7684\u610f\u601d\uff0c\u662f\u8981\u6211\u548c\u83f2\u5c14\u4f26\u5c0f\u59d0\u8f83\u91cf\u6bd4\u8bd5\uff1f\u201d</p><p>\u4e3b\u6bcd\u4e5f\u5728\u5fae\u7b11\uff0c\u201c\u90a3\u592a\u65e0\u8da3\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u64c2\u53f0\u683c\u6597\u7684\u628a\u620f\uff0c\u4e0d\u8fc7\u662f\u89d2\u6597\u58eb\u6240\u4e3a\uff0c\u4e0d\u9002\u5408\u6211\u4eec\u3002\u201d</p><p>\u201c\u90a3\u4e48\u2026\u2026\u201d</p><p>\u201c\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u8725\u8734\u9a91\u5175\uff0c\u4ee5\u5341\u4e8c\u4eba\u4e3a\u4e00\u961f\uff0c\u201d\u83f2\u5c14\u4f26\u4e3b\u6bcd\u4e0d\u7d27\u4e0d\u6162\u5730\u8bf4\uff0c\u201c\u4ee5\u4e3d\u5965\u7edf\u5e05\u4e00\u961f\u9a91\u5175\u4f5c\u4e3a\u5bf9\u624b\uff0c\u60a8\u89c9\u5f97\u8fd9\u6837\u7684\u5b89\u6392\u5982\u4f55\uff1f\u201d</p><p>\u743c\u6069\u5fae\u5fae\u76b1\u7709\uff0c\u4ed6\u77e5\u9053\u8725\u8734\u9a91\u5175\u662f\u9ed1\u6697\u7cbe\u7075\u4e2d\u6700\u7cbe\u9510\u7684\u5175\u79cd\uff0c\u8fd1\u4f3c\u4e8e\u4eba\u7c7b\u7684\u91cd\u9a91\u5175\uff0c\u4e00\u65e6\u51b2\u950b\u8d77\u6765\u52bf\u4e0d\u53ef\u6321\u3002\u5f53\u65e5\u5728\u4f0a\u5361\u6c99\u57ce\u4e0b\uff0c\u5c31\u8fde\u90a3\u4e9b\u608d\u52c7\u7684\u77ee\u4eba\u6218\u58eb\u90fd\u62b5\u654c\u4e0d\u4f4f\u3002\u4f55\u51b5\u8fd9\u6b21\u5e76\u975e\u5404\u5927\u5bb6\u65cf\u62fc\u51d1\u8d77\u6765\u7684\u8054\u519b\uff0c\u800c\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\u5185\u90e8\u7684\u8725\u8734\u9a91\u5175\uff0c\u65e0\u7591\u66f4\u52a0\u7cbe\u9510\u3002\u504f\u504f\u9ed1\u6697\u7cbe\u7075\u53c8\u90fd\u6709\u5f88\u5f3a\u7684\u5929\u8d4b\u6297\u9b54\u80fd\u529b\uff0c\u5bf9\u5deb\u5e08\u800c\u8a00\u66f4\u52a0\u4e0d\u5229\uff0c\u66f4\u522b\u63d0\u5341\u4e09\u5bf9\u4e00\u8fd9\u79cd\u538b\u5012\u6027\u7684\u6570\u91cf\u4f18\u52bf\u3002</p><p>\u4e0d\u8fc7\u4e8b\u5df2\u81f3\u6b64\uff0c\u4e5f\u6ca1\u6709\u9000\u7f29\u7684\u4f59\u5730\u4e86\u3002</p><p>\u4ed6\u62ac\u8d77\u5934\uff0c\u770b\u7740\u83f2\u5c14\u4f26\u4e3b\u6bcd\uff0c\u201c\u6709\u4e00\u4e2a\u6761\u4ef6\u3002\u201d</p><p>\u201c\u8bf7\u8bb2\u3002\u201d</p><p>\u201c\u4e0d\u6b7b\u4e0d\u4f11\u3002\u201d</p><p>\u83f2\u5c14\u4f26\u4e3b\u6bcd\u5fae\u5fae\u626c\u4e86\u626c\u7709\u6bdb\uff0c\u5979\u7684\u5634\u89d2\u6cdb\u8d77\u4e00\u4e1d\u51b7\u7b11\uff0c\u4eff\u4f5b\u662f\u5632\u7b11\u743c\u6069\u7684\u72c2\u5984\uff0c\u4f46\u8bed\u6c14\u4f9d\u65e7\u6e29\u548c\uff0c\u201c\u6211\u4eec\u5353\u5c14\u7684\u89c4\u77e9\u4e5f\u662f\u5982\u6b64\uff0c\u201d\u5979\u6de1\u6de1\u5730\u56de\u7b54\uff0c\u201c\u4e0d\u6b7b\u4e0d\u4f11\u3002\u201d</p></article>"}}}]);
