(window.webpackJsonp=window.webpackJsonp||[]).push([[1449],{1472:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e8c\u8282 \u5192\u5145",content:"<article><h2>\u7b2c\u5341\u4e8c\u8282 \u5192\u5145</h2><p>\u743c\u6069\u4e00\u76f4\u90fd\u8ba4\u4e3a\uff0c\u5deb\u5e08\u5728\u6218\u573a\u4e0a\u7684\u89d2\u8272\u5b9a\u4f4d\uff0c\u5e94\u8be5\u662f\u667a\u8005\u3001\u8c0b\u58eb\u548c\u8f85\u52a9\u8005\uff0c\u4ee5\u53ca\u5404\u79cd\u7a81\u53d1\u72b6\u51b5\u7684\u89e3\u51b3\u8005\uff0c\u81f3\u4e8e\u51b2\u950b\u9677\u9635\u3001\u76f4\u9762\u654c\u624b\uff0c\u90a3\u662f\u6218\u58eb\u4eec\u7684\u5206\u5185\u4e4b\u4e8b\uff0c\u5deb\u5e08\u4e0d\u5e94\u8be5\u4e5f\u4e0d\u9700\u8981\u63d2\u624b\uff0c\u53ea\u9700\u5b89\u7a33\u5730\u5750\u5728\u540e\u65b9\u559d\u5496\u5561\uff0c\u7b49\u7740\u524d\u65b9\u7684\u6218\u62a5\u5c31\u597d\u3002\u7136\u800c\u4e16\u95f4\u4e4b\u4e8b\uff0c\u4e0d\u5982\u610f\u8005\u5341\u4e4b\u516b\u4e5d\uff0c\u867d\u7136\u8981\u8bba\u8d77\u6765\uff0c\u743c\u6069\u73b0\u5728\u4e5f\u7b97\u662f\u7b2c\u4e8c\u8fdc\u5f81\u5e08\u7684\u519b\u5b98\u4e86\uff0c\u624b\u4e0b\u5374\u6ca1\u51e0\u4e2a\u58eb\u5175\u53ef\u4f9b\u9a71\u9063\uff0c\u4e00\u65e6\u771f\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f9d\u7136\u8fd8\u662f\u5f97\u81ea\u5df1\u4eb2\u8eab\u4e0a\u9635\u3002</p><p>\u6bd4\u8d5b\u573a\u5730\u662f\u5728\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u540e\u9662\uff0c\u4e00\u4e2a\u692d\u5706\u5f62\u7684\u5de8\u5927\u6d1e\u7a9f\uff0c\u573a\u5730\u7a7a\u65f7\uff0c\u6ca1\u6709\u4efb\u4f55\u949f\u4e73\u77f3\u6216\u8005\u77f3\u7b0b\u963b\u9694\uff0c\u9002\u5408\u9a91\u5175\u51b2\u950b\uff0c\u8fd9\u5bf9\u743c\u6069\u6765\u8bf4\u663e\u7136\u66f4\u52a0\u4e0d\u5229\u3002\u5899\u58c1\u4e0a\u5f00\u51ff\u51fa\u51e0\u5341\u4e2a\u77f3\u5ba4\u4f5c\u4e3a\u89c2\u4f17\u5e2d\uff0c\u83f2\u5c14\u4f26\u4e3b\u6bcd\u548c\u5979\u7684\u5973\u513f\u4eec\u6b64\u65f6\u5c31\u5728\u5176\u4e2d\u3002\u5728\u743c\u6069\u524d\u65b9\uff0c\u8725\u8734\u9a91\u5175\u5df2\u7ecf\u5168\u526f\u6b66\u88c5\uff0c\u51c6\u5907\u5c31\u7eea\uff0c\u4ed6\u4eec\u5168\u90fd\u7a7f\u7740\u5236\u4f5c\u7cbe\u826f\u7684\u7cbe\u91d1\u9501\u7532\uff0c\u624b\u6301\u957f\u67aa\uff0c\u5c31\u8fde\u6240\u9a91\u4e58\u7684\u516b\u722a\u8725\u8734\u90fd\u62ab\u7740\u7ed3\u5b9e\u7684\u91d1\u5c5e\u94e0\u7532\uff0c\u88ab\u5bc6\u4e0d\u900f\u98ce\u7684\u4fdd\u62a4\u7740\uff0c\u4eff\u4f5b\u4e00\u5934\u5934\u94a2\u94c1\u5de8\u517d\u3002</p><p>\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u4e0d\u540c\uff0c\u4e3d\u5965\xb7\u83f2\u5c14\u4f26\u6e05\u695a\u5730\u660e\u767d\u201c\u7edf\u5e05\u201d\u7684\u542b\u4e49\uff0c\u5979\u5e76\u6ca1\u6709\u7ad9\u5728\u961f\u4f0d\u7684\u6700\u524d\u5217\uff0c\u800c\u662f\u5904\u4e8e\u4e2d\u95f4\u4f4d\u7f6e\uff0c\u8fd9\u8ba9\u743c\u6069\u5f88\u96be\u76f4\u63a5\u5bf9\u5979\u8fdb\u884c\u5077\u88ad\u3002\u83f2\u5c14\u4f26\u4e3b\u6bcd\u548c\u5979\u7684\u5973\u513f\u4eec\u5927\u591a\u90fd\u8eab\u6750\u7ea4\u7ec6\uff0c\u4e3d\u5965\u5374\u662f\u7279\u4f8b\uff0c\u5979\u7684\u8eab\u6750\u5341\u5206\u9ad8\u5927\u5f3a\u58ee\uff0c\u7a7f\u4e0a\u9501\u7532\uff0c\u6234\u4e0a\u5934\u76d4\uff0c\u653e\u4e0b\u9762\u7f69\uff0c\u770b\u8d77\u6765\u4fbf\u4eff\u4f5b\u7537\u5b50\u4e00\u822c\u2014\u2014\u5f53\u7136\u8981\u5148\u5ffd\u7565\u6389\u9ad8\u8038\u8bf1\u4eba\u7684\u80f8\u90e8\u3002\u4e0e\u4e4b\u76f8\u914d\u7684\u662f\uff0c\u5979\u7684\u8725\u8734\u5750\u9a91\u4e5f\u660e\u663e\u662f\u5f02\u79cd\uff0c\u6bd4\u5176\u4ed6\u9a91\u5175\u7684\u5750\u9a91\u4f53\u578b\u8981\u5e9e\u5927\u5c06\u8fd1\u4e24\u500d\uff0c\u770b\u8d77\u6765\u51f6\u6076\u5f97\u591a\u3002</p><p>\u201c\u6211\u4f1a\u7559\u4f60\u6700\u540e\u4e00\u53e3\u6c14\u7684\uff0c\u4eba\u7c7b\uff0c\u201d\u5979\u7684\u58f0\u97f3\u4ece\u9762\u7f69\u540e\u4f20\u6765\uff0c\u663e\u5f97\u6709\u4e9b\u4f4e\u6c89\uff0c\u4f34\u968f\u7740\u56de\u58f0\u95f7\u54cd\uff0c\u201c\u4f5c\u4e3a\u4f60\u6740\u6b7b\u5e03\u7eb3\u5951\u65af\u7684\u5956\u8d4f\u3002\u201d</p><p>\u743c\u6069\u4e0d\u751a\u660e\u767d\u8fd9\u53e5\u8bdd\u7684\u610f\u601d\uff0c\u4e0d\u8fc7\u4e5f\u61d2\u5f97\u591a\u95ee\uff0c\u731c\u6d4b\u4e0d\u4f1a\u662f\u4ec0\u4e48\u5584\u610f\u8a00\u8bed\u3002\u4ed6\u9ed8\u4e0d\u4f5c\u58f0\u5730\u4ece\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u6cd5\u6756\uff0c\u505a\u4e86\u4e00\u4e2a\u201c\u51c6\u5907\u5b8c\u6bd5\uff0c\u53ef\u4ee5\u5f00\u59cb\u4e86\u201d\u7684\u624b\u52bf\u3002</p><p>\u4e00\u56e2\u6de1\u84dd\u8272\u548c\u7d2b\u7ea2\u8272\u6df7\u5408\u7684\u5996\u706b\u4ece\u9ad8\u7a7a\u5760\u843d\uff0c\u5728\u5feb\u8981\u63a5\u8fd1\u5730\u9762\u7684\u65f6\u5019\u5316\u4f5c\u5e9e\u5927\u7684\u8718\u86db\u5f62\u72b6\uff0c\u800c\u4e14\u6709\u516b\u4e2a\u5934\u9885\u3002\u5341\u516d\u9897\u8840\u7ea2\u7684\u773c\u73e0\u76ef\u7740\u5728\u573a\u7684\u6240\u6709\u4eba\uff0c\u968f\u5373\u53d8\u6210\u4e00\u56e2\u6de1\u6de1\u7684\u96fe\u6c14\uff0c\u6b63\u963b\u9694\u5728\u743c\u6069\u548c\u4e3d\u5965\u7684\u8725\u8734\u9a91\u5175\u4e2d\u95f4\u3002\u6ca1\u6709\u542c\u89c1\u4efb\u4f55\u811a\u6b65\u58f0\uff0c\u4f46\u743c\u6069\u611f\u89c9\u5230\u4e86\u811a\u4e0b\u5730\u9762\u5728\u9707\u52a8\uff0c\u4ed6\u77e5\u9053\u5bf9\u9762\u7684\u8725\u8734\u9a91\u5175\u5df2\u7ecf\u5f00\u59cb\u51b2\u950b\uff0c\u8fd9\u79cd\u516b\u722a\u602a\u7269\u7684\u811a\u5e95\u6709\u7740\u539a\u539a\u7684\u8089\u57ab\uff0c\u5c31\u50cf\u732b\u4e00\u6837\uff0c\u8ba9\u5b83\u4eec\u5954\u8dd1\u8d77\u6765\u51e0\u4e4e\u6ca1\u6709\u4efb\u4f55\u54cd\u58f0\u3002\u5f53\u7136\uff0c\u5b83\u4eec\u7684\u8eab\u8eaf\u7ec8\u7a76\u8fd8\u662f\u592a\u8fc7\u4e8e\u5e9e\u5927\u6c89\u91cd\u4e86\uff0c\u4e0d\u53ef\u80fd\u505a\u5230\u5168\u65e0\u52a8\u9759\u3002</p><p>\u96fe\u6c14\u6563\u53bb\uff0c\u5341\u4e09\u4e2a\u8725\u8734\u9a91\u5175\u5df2\u7ecf\u663e\u51fa\u8eab\u5f62\uff0c\u4ed6\u4eec\u5206\u505a\u4e09\u961f\uff0c\u4e3d\u5965\u7387\u9886\u516d\u4e2a\u9a91\u5175\u4ece\u6b63\u9762\u51b2\u950b\uff0c\u5176\u4f59\u516d\u4eba\u4ece\u4e24\u9762\u5305\u6284\u3002\u8fd9\u663e\u7136\u662f\u6b63\u786e\u7684\u4f5c\u6cd5\uff0c\u5c31\u7b97\u743c\u6069\u80fd\u91ca\u653e\u4ec0\u4e48\u5a01\u529b\u5f3a\u5927\u7684\u6cd5\u672f\uff0c\u4e5f\u5f88\u96be\u540c\u65f6\u5e94\u4ed8\u6765\u81ea\u4e09\u9762\u7684\u653b\u51fb\u3002</p><p>\u8725\u8734\u9a91\u5175\u7684\u901f\u5ea6\u5feb\u82e5\u95ea\u7535\uff0c\u51e0\u4e4e\u662f\u4e00\u77ac\u95f4\u5c31\u5df2\u7ecf\u540c\u65f6\u51b2\u5230\u4e86\u743c\u6069\u8ddf\u524d\uff0c\u7136\u800c\u5c31\u5728\u4ed6\u4eec\u7684\u957f\u67aa\u5c06\u8981\u523a\u4e2d\u76ee\u6807\u7684\u65f6\u5019\uff0c\u5deb\u5e08\u7a81\u7136\u6d88\u5931\u4e86\u3002</p><p>\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u743c\u6069\u51fa\u73b0\u5728\u6d1e\u7a9f\u7684\u89d2\u843d\u3002</p><p>\u5e7d\u6697\u5730\u57df\u4e2d\u867d\u7136\u65e0\u6cd5\u8fdb\u884c\u8fdc\u8ddd\u79bb\u4f20\u9001\uff0c\u89c6\u91ce\u8303\u56f4\u5185\u7684\u7a7a\u95f4\u8de8\u8d8a\u8fd8\u662f\u6ca1\u6709\u95ee\u9898\u7684\uff0c\u743c\u6069\u51c6\u5907\u7684\u4e00\u4e2a\u4efb\u610f\u95e8\u6cd5\u672f\u53d1\u6325\u4e86\u4f5c\u7528\uff0c\u907f\u5f00\u4e86\u8725\u8734\u9a91\u5175\u7684\u7b2c\u4e00\u6ce2\u653b\u51fb\uff0c\u540c\u65f6\u6682\u65f6\u518d\u5ea6\u62c9\u5f00\u8ddd\u79bb\u3002\u4ed6\u8d81\u673a\u53d1\u52a8\u4e86\u6cd5\u672f\uff0c\u624b\u4e2d\u6c34\u6676\u6cd5\u6756\u4e0a\u7684\u516d\u9897\u7eef\u7ea2\u4e4b\u6cea\u5b9d\u77f3\u540c\u65f6\u4eae\u4e86\u8d77\u6765\uff0c\u53d1\u51fa\u707f\u70c2\u7075\u5149\uff0c\u8054\u6210\u4e00\u7ebf\uff0c\u84c4\u52bf\u5f85\u53d1\u3002</p><p>\u9ed1\u6697\u7cbe\u7075\u7267\u5e08\u4eec\u548c\u77ee\u4eba\u4e0d\u540c\uff0c\u5979\u4eec\u4e00\u65b9\u9762\u8f7b\u89c6\u5965\u672f\uff0c\u4e00\u65b9\u9762\u5374\u53c8\u7ecf\u5e38\u8981\u548c\u5bb6\u65cf\u5185\u7684\u5deb\u5e08\u6253\u4ea4\u9053\uff0c\u5e76\u4e14\u5c0f\u5fc3\u9632\u8303\u7740\uff0c\u6697\u4e2d\u654c\u89c6\u548c\u76d1\u63a7\u7740\u4ed6\u4eec\uff0c\u5bf9\u5deb\u5e08\u7684\u90a3\u4e9b\u4f0e\u4fe9\u5e76\u4e0d\u964c\u751f\u3002\u7136\u800c\u8fd9\u4e00\u6b21\uff0c\u6ca1\u6709\u4eba\u80fd\u770b\u51fa\u743c\u6069\u662f\u51c6\u5907\u65bd\u5c55\u4ec0\u4e48\u6cd5\u672f\u2014\u2014\u9664\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u4e4b\u5916\uff0c\u5979\u4e5f\u662f\u5deb\u5e08\uff0c\u4e0a\u6b21\u5403\u8fc7\u4e00\u6b21\u4e8f\uff0c\u591a\u591a\u5c11\u5c11\u5fc3\u91cc\u603b\u6709\u4e9b\u660e\u767d\u3002</p><p>\u7136\u800c\uff0c\u5c31\u7b97\u662f\u88ab\u589e\u5e45\u8fc7\u7684\u6cd5\u672f\uff0c\u53ea\u6015\u4e5f\u5f88\u96be\u7a7f\u900f\u9ed1\u6697\u7cbe\u7075\u5929\u8d4b\u7684\u9b54\u6cd5\u6297\u529b\u3002</p><p>\u6240\u4ee5\u743c\u6069\u4f9d\u65e7\u8fd8\u662f\u6253\u7740\u8725\u8734\u7684\u4e3b\u610f\u3002</p><p>\u4e00\u51fb\u4e0d\u4e2d\uff0c\u4e3d\u5965\u7387\u9886\u7740\u8725\u8734\u9a91\u5175\u4eec\u518d\u5ea6\u51b2\u950b\u800c\u6765\uff0c\u5979\u4f9d\u65e7\u806a\u660e\u5730\u4fdd\u6301\u7740\u4e2d\u95f4\u4f4d\u7f6e\uff0c\u5e76\u4e0d\u9760\u524d\u3002\u743c\u6069\u5df2\u7ecf\u9000\u5230\u72ed\u7a84\u7684\u89d2\u843d\u91cc\uff0c\u80cc\u540e\u548c\u4e24\u4fa7\u90fd\u662f\u5899\u58c1\uff0c\u518d\u65e0\u53ef\u9003\uff08\u4ed6\u4e5f\u603b\u4e0d\u53ef\u80fd\u5168\u51c6\u5907\u4f20\u9001\u6cd5\u672f\u7528\u4e8e\u4e0d\u65ad\u9003\u8dd1\uff09\uff0c\u4f46\u8fd9\u540c\u6837\u4e5f\u5f71\u54cd\u4e86\u8725\u8734\u9a91\u5175\uff0c\u8ba9\u4ed6\u4eec\u4e27\u5931\u4e86\u56de\u65cb\u7684\u7a7a\u95f4\uff0c\u6ca1\u529e\u6cd5\u518d\u73a9\u4e09\u9762\u5305\u6284\u7684\u6218\u672f\uff0c\u53ea\u80fd\u805a\u6210\u4e00\u961f\u76f4\u63a5\u51b2\u950b\u3002\u800c\u4e14\u4e3a\u4e86\u907f\u514d\u649e\u4e0a\u5899\u58c1\uff0c\u4ed6\u4eec\u7684\u901f\u5ea6\u4e0d\u5f97\u4e0d\u653e\u7f13\u4e0b\u6765\u3002</p><p>\u743c\u6069\u6240\u60f3\u8981\u7684\u5c31\u662f\u8fd9\u4e2a\u6548\u679c\u3002</p><p>\u4ed6\u62ac\u8d77\u6cd5\u6756\uff0c\u671d\u7740\u8725\u8734\u9a91\u5175\u961f\u4f0d\u7684\u4e2d\u5fc3\u4e00\u6307\uff0c\u4e00\u9053\u8000\u773c\u7684\u84dd\u8272\u5149\u7ebf\u4ece\u6756\u5934\u5c04\u51fa\uff0c\u51fb\u4e2d\u4e86\u4e3d\u5965\u90a3\u53ea\u5e9e\u5927\u7684\u8725\u8734\u5750\u9a91\u3002\u9ed1\u6697\u7cbe\u7075\u7684\u5929\u8d4b\u6297\u9b54\u80fd\u529b\u5b9e\u5728\u662f\u592a\u4ee4\u5deb\u5e08\u5934\u75bc\uff0c\u5bf9\u8725\u8734\u5750\u9a91\u4e0b\u624b\u5c31\u6210\u4e86\u987a\u7406\u6210\u7ae0\u7684\u9009\u62e9\uff0c\u8fd9\u5e76\u4e0d\u7b97\u743c\u6069\u7684\u521b\u89c1\uff0c\u53ea\u8981\u7a0d\u6709\u89c1\u8bc6\u8005\u90fd\u4f1a\u8fd9\u4e48\u5e72\u3002</p><p>\u7136\u800c\u8fd9\u6b21\u5374\u4f3c\u4e4e\u6709\u4e9b\u5947\u602a\u3002</p><p>\u84dd\u8272\u5149\u7ebf\u51c6\u786e\u5730\u51fb\u4e2d\u4e86\u76ee\u6807\uff0c\u5374\u6ca1\u6709\u5f15\u53d1\u4efb\u4f55\u53d8\u5316\u3002\u4e3d\u5965\u7684\u8725\u8734\u5750\u9a91\u6ca1\u6709\u6b7b\u4ea1\uff0c\u6ca1\u6709\u77f3\u5316\uff0c\u6ca1\u6709\u53d8\u5f62\uff0c\u6ca1\u6709\u53cd\u53db\uff0c\u4e00\u5207\u7167\u5e38\uff0c\u4eff\u4f5b\u743c\u6069\u521a\u624d\u4e0d\u8fc7\u662f\u73a9\u4e86\u624b\u534e\u800c\u4e0d\u5b9e\u7684\u5e7b\u672f\u628a\u620f\u3002</p><p>\u4e3d\u5965\u4e5f\u6014\u4e86\u6014\uff0c\u4f46\u5979\u6beb\u4e0d\u72b9\u8c6b\u5730\u7ee7\u7eed\u51b2\u950b\uff0c\u201c\u6216\u8bb8\u662f\u6cd5\u672f\u5931\u6548\u4e86\u3002\u201d\u5979\u731c\u6d4b\u7740\uff0c\u8fd9\u4e5f\u5e76\u975e\u4e0d\u53ef\u80fd\uff0c\u5deb\u5e08\u65bd\u6cd5\u662f\u7cbe\u5de7\u7684\u6280\u672f\u6d3b\uff0c\u7a0d\u6709\u4e0d\u614e\u5c31\u4f1a\u51fa\u95ee\u9898\uff0c\u867d\u7136\u9762\u524d\u7684\u8fd9\u4e2a\u4eba\u7c7b\u660e\u663e\u8bad\u7ec3\u6709\u7d20\uff0c\u4f46\u9762\u5bf9\u4e00\u961f\u8725\u8734\u9a91\u5175\u7684\u51f6\u731b\u51b2\u950b\uff0c\u7cbe\u795e\u96be\u514d\u7d27\u5f20\uff0c\u65bd\u6cd5\u51fa\u9519\u4e5f\u53ef\u4ee5\u7406\u89e3\u3002</p><p>\u6b63\u5982\u6b64\u60f3\uff0c\u9661\u7136\u89c9\u5f97\u4e00\u80a1\u5f3a\u5927\u7684\u5438\u529b\u81ea\u8eab\u4e0b\u4f20\u6765\uff0c\u201c\u94ff\u201d\u7684\u4e00\u58f0\uff0c\u81ea\u5df1\u4e0d\u7531\u81ea\u4e3b\u5730\u4f0f\u4e0b\u8eab\u6765\uff0c\u8db4\u5728\u8725\u8734\u5750\u9a91\u7684\u80cc\u4e0a\u3002\u5979\u8fd8\u6ca1\u660e\u767d\u600e\u4e48\u56de\u4e8b\uff0c\u53ea\u542c\u5f97\u8033\u8fb9\u98ce\u58f0\u51cc\u5389\uff0c\u767e\u5fd9\u4e2d\u62ac\u773c\u77a5\u53bb\uff0c\u5c31\u89c1\u51e0\u56e2\u5de8\u5927\u7684\u9ed1\u5f71\u671d\u81ea\u5df1\u76f4\u76f4\u649e\u6765\u3002</p><p>\u203b\u203b\u203b</p><p>\u4e3a\u4e86\u907f\u514d\u91cd\u8e48\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8986\u8f99\uff0c\u4e3d\u5965\u8fd9\u6b21\u8981\u5c0f\u5fc3\u8c28\u614e\u5f97\u591a\uff0c\u5979\u7684\u8eab\u8fb9\u4e00\u76f4\u4fdd\u6301\u7740\u56db\u4e2a\u9a91\u5175\u62a4\u536b\uff0c\u540c\u8fdb\u540c\u9000\uff0c\u8ddd\u79bb\u5f88\u8fd1\uff0c\u4ee5\u514d\u88ab\u743c\u6069\u7a81\u7136\u8fd1\u8eab\u5077\u88ad\u3002\u5fc5\u987b\u8bf4\uff0c\u8fd9\u79cd\u5b89\u6392\u65e0\u53ef\u539a\u975e\uff0c\u7136\u800c\u8ba1\u5212\u662f\u6c38\u8fdc\u8d76\u4e0d\u4e0a\u53d8\u5316\u7684\u3002</p><p>\u743c\u6069\u8fd9\u6b21\u538b\u6839\u6ca1\u6709\u6253\u7b97\u6545\u6280\u91cd\u65bd\uff0c\u4ed6\u53ea\u662f\u5bf9\u7740\u4e3d\u5965\u7684\u8725\u8734\u5750\u9a91\uff0c\u65bd\u5c55\u4e86\u4e00\u4e2a\u82df\u62c9\u53f8\u6258\u65af\u78c1\u5316\u672f\uff08Ghorus Toth's Magnetism\uff09\u3002</p><p>\u8fd9\u4e2a\u6cd5\u672f\u662f\u5f53\u65e5\u4ed6\u5728\u963f\u683c\u62c9\u9686\u7684\u65f6\u5019\uff0c\u4ece\u51db\u7684\u9b54\u6cd5\u4e66\u91cc\u6284\u5f55\u6765\u7684\uff0c\u53ea\u6d41\u4f20\u4e8e\u504f\u8fdc\u7684\u4e1c\u57df\u8bf8\u56fd\uff0c\u7b97\u662f\u6bd4\u8f83\u7f55\u89c1\u7684\u6cd5\u672f\uff0c\u8fd9\u4e9b\u5730\u5e95\u7684\u9ed1\u6697\u7cbe\u7075\u4eec\u81ea\u7136\u66f4\u4e0d\u8ba4\u8bc6\u3002\u5b83\u7684\u6548\u679c\u5c31\u5728\u4e8e\u8ba9\u4e00\u4e2a\u91d1\u5c5e\u7269\u4f53\u2014\u2014\u6216\u8005\u88ab\u91d1\u5c5e\u5305\u88f9\u7684\u7269\u4f53\uff0c\u6bd4\u5982\u5168\u8eab\u94e0\u7532\u7684\u6218\u58eb\u2014\u2014\u6210\u4e3a\u201c\u78c1\u77f3\u201d\uff0c\u4ea7\u751f\u6781\u5176\u5f3a\u5927\u7684\u9b54\u6cd5\u6027\u78c1\u529b\u6548\u5e94\u3002\u4ee5\u76ee\u6807\u4e3a\u4e2d\u5fc3\uff0c\u5468\u56f4\u4e00\u5b9a\u8303\u56f4\u5185\u6210\u4e3a\u78c1\u573a\u533a\u57df\uff0c\u6240\u6709\u8fdb\u5165\u6b64\u533a\u57df\u7684\u91d1\u5c5e\u7269\u4f53\u90fd\u4f1a\u88ab\u5f3a\u529b\u5438\u5f15\uff0c\u5c31\u4eff\u4f5b\u94c1\u5c51\u9047\u4e0a\u4e86\u78c1\u94c1\u4e00\u822c\u3002</p><p>\u78c1\u573a\u7684\u5f3a\u5f31\uff0c\u81ea\u7136\u548c\u201c\u78c1\u77f3\u201d\u7684\u4f53\u79ef\u3001\u8d28\u91cf\u6210\u6b63\u6bd4\u3002\u4e3d\u5965\u7684\u8725\u8734\u5750\u9a91\u4f53\u79ef\u5e9e\u5927\uff0c\u6b63\u662f\u6700\u5408\u9002\u7684\u65bd\u6cd5\u76ee\u6807\u3002\u4e3d\u5965\u548c\u5979\u7684\u62a4\u536b\u4eec\uff0c\u4ee5\u53ca\u8eab\u4e0b\u5750\u9a91\uff0c\u5168\u90fd\u662f\u7528\u7cbe\u91d1\u76d4\u7532\u4ece\u5934\u6b66\u88c5\u5230\u811a\uff0c\u9996\u5f53\u5176\u51b2\u5c31\u88ab\u5438\u4e86\u4e0a\u53bb\u3002\u5982\u679c\u662f\u5e73\u65f6\u51b2\u950b\uff0c\u901f\u5ea6\u6781\u5feb\uff0c\u6216\u8bb8\u8fd8\u80fd\u6323\u8131\u8fd9\u80a1\u5438\u529b\uff0c\u7136\u800c\u8fd9\u6b21\u743c\u6069\u8eb2\u5728\u89d2\u843d\uff0c\u9a91\u5175\u4eec\u4e3a\u4e86\u907f\u514d\u649e\u5899\uff0c\u4e0d\u5f97\u4e0d\u5927\u5927\u653e\u6162\u901f\u5ea6\uff0c\u987f\u65f6\u4fbf\u906d\u4e86\u6b83\u3002</p><p>\u7830\u7830\u7830\u7830\u51e0\u58f0\u8fde\u54cd\uff0c\u4e3d\u5965\u7684\u56db\u4e2a\u62a4\u536b\u4ece\u524d\u540e\u5de6\u53f3\u56db\u4e2a\u65b9\u5411\uff0c\u51e0\u4e4e\u540c\u65f6\u649e\u4e86\u4e0a\u6765\uff0c\u9669\u4e9b\u5c06\u4ed6\u4eec\u7684\u7edf\u5e05\u538b\u6241\u3002\u4e94\u4e2a\u4eba\u548c\u4e94\u53ea\u8725\u8734\u7c98\u5408\u5728\u4e00\u8d77\uff0c\u6324\u6210\u4e00\u56e2\uff0c\u800c\u8fd9\u5bfc\u81f4\u4e86\u66f4\u52a0\u6076\u52a3\u7684\u7ed3\u679c\u2014\u2014\u4ed6\u4eec\u8eab\u4e0a\u7684\u76d4\u7532\u8fc5\u901f\u88ab\u78c1\u5316\uff0c\u78c1\u573a\u5a01\u529b\u8fdb\u4e00\u6b65\u6269\u5927\uff0c\u7728\u773c\u95f4\u628a\u5468\u56f4\u7684\u540c\u4f34\u5168\u90fd\u5438\u4e86\u8fc7\u6765\uff0c\u5c24\u5176\u662f\u540e\u9762\u7684\u9a91\u5175\uff0c\u56e0\u4e3a\u6b63\u662f\u671d\u8fd9\u4e2a\u65b9\u5411\u51b2\u950b\uff0c\u7b49\u4e8e\u662f\u81ea\u5df1\u4e3b\u52a8\u649e\u4e0a\uff0c\u518d\u65e0\u53ef\u9003\u3002</p><p>\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4e3d\u5965\u7684\u5341\u4e8c\u4e2a\u8725\u8734\u9a91\u5175\uff0c\u5df2\u7ecf\u6709\u4e03\u4e2a\u201c\u7d27\u5bc6\u56e2\u7ed3\u201d\u5728\u7edf\u5e05\u5468\u56f4\uff0c\u5269\u4e0b\u4e94\u4e2a\u56e0\u4e3a\u5904\u4e8e\u6700\u5916\u56f4\uff0c\u8ddd\u79bb\u76f8\u5bf9\u8f83\u8fdc\uff0c\u867d\u7136\u4e5f\u53d7\u5f71\u54cd\uff0c\u4f46\u6ca1\u6709\u88ab\u76f4\u63a5\u5438\u8fc7\u53bb\uff0c\u53ea\u662f\u901f\u5ea6\u7f13\u6162\u4e0b\u6765\u3002\u4ed6\u4eec\u52aa\u529b\u9a7e\u9a6d\u7740\u5750\u9a91\uff0c\u5e0c\u671b\u80fd\u6323\u8131\u5438\u529b\uff0c\u7136\u800c\u5c31\u5728\u6b64\u65f6\uff0c\u743c\u6069\u518d\u6b21\u62ac\u8d77\u6cd5\u6756\u3002</p><p>\u201c\u53d8\u5de8\uff01\u201d</p><p>\u5f88\u7b80\u5355\u7684\u6cd5\u672f\uff0c\u53d8\u5316\u5b66\u6d3e\u7684\u5165\u95e8\u4f0e\u4fe9\uff0c\u7136\u800c\u6b64\u65f6\u5374\u8d77\u5230\u4e86\u826f\u597d\u7684\u4f5c\u7528\u3002\u4e3d\u5965\u7684\u8725\u8734\u5750\u9a91\u539f\u672c\u5c31\u5df2\u7ecf\u5e9e\u5927\u5f97\u60ca\u4eba\uff0c\u6b64\u65f6\u5c45\u7136\u53c8\u5439\u6c14\u7403\u4e00\u822c\u81a8\u80c0\u8d77\u6765\uff0c\u5b83\u7684\u4f53\u91cd\u9aa4\u7136\u95f4\u6210\u4e86\u539f\u672c\u7684\u516b\u500d\u3002\u76f8\u5e94\u7684\uff0c\u5b83\u7684\u201c\u78c1\u529b\u201d\u518d\u4e00\u6b21\u6025\u5267\u589e\u5f3a\uff0c\u8fd8\u5728\u52aa\u529b\u6323\u624e\u8bd5\u56fe\u6446\u8131\u7684\u4e94\u4e2a\u5e78\u5b58\u8005\u652f\u6301\u4e0d\u4f4f\uff0c\u6beb\u65e0\u60ac\u5ff5\u5730\u88ab\u5438\u4e86\u8fc7\u6765\u3002</p><p>\u5341\u4e09\u4e2a\u8725\u8734\u9a91\u5175\u6324\u5728\u4e00\u8d77\uff0c\u6210\u4e86\u4e00\u4e2a\u5de8\u5927\u7684\u4e0d\u89c4\u5219\u7403\u4f53\uff0c\u4ed6\u4eec\u5f7c\u6b64\u5438\u5f15\u7740\uff0c\u65e0\u6cd5\u8131\u8eab\u3002\u4f46\u8fd9\u79cd\u72b6\u51b5\u5e76\u4e0d\u4f1a\u4e00\u76f4\u6301\u7eed\u4e0b\u53bb\uff0c\u78c1\u5316\u672f\u4e5f\u662f\u6709\u65f6\u95f4\u9650\u5236\u7684\uff0c\u4ee5\u743c\u6069\u76ee\u524d\u7684\u9020\u8be3\uff0c\u4e0d\u8fc7\u80fd\u7ef4\u6301\u5341\u5206\u949f\u5de6\u53f3\u3002</p><p>\u8db3\u591f\u4e86\u3002</p><p>\u743c\u6069\u624b\u65e0\u5bf8\u94c1\uff0c\u6b66\u6280\u4e5f\u4e0d\u884c\uff0c\u5c31\u7b97\u7ed9\u4ed6\u4e00\u628a\u5200\uff0c\u4e5f\u6ca1\u628a\u63e1\u80fd\u4e0a\u524d\u628a\u8fd9\u4e9b\u4eba\u6740\u6b7b\uff0c\u53ea\u6015\u53cd\u800c\u4f1a\u88ab\u523a\u4e0a\u51e0\u67aa\u3002\u7528\u6cd5\u672f\u653b\u51fb\u7684\u8bdd\uff0c\u56e0\u4e3a\u9ed1\u6697\u7cbe\u7075\u7684\u6297\u9b54\u80fd\u529b\uff0c\u4e5f\u9887\u4e3a\u68d8\u624b\u3002\u5e78\u8fd0\u7684\u662f\uff0c\u8fd9\u91cc\u662f\u5e7d\u6697\u5730\u57df\uff0c\u904d\u5730\u90fd\u662f\u77f3\u5934\u3002</p><p>\u201c\u5316\u77f3\u4e3a\u6ce5\uff01\u201d</p><p>\u5730\u9762\u575a\u786c\u7684\u5ca9\u77f3\u5728\u9b54\u6cd5\u7684\u4f5c\u7528\u4e0b\u8fc5\u901f\u5316\u4f5c\u67d4\u8f6f\u7684\u6ce5\u6d46\uff0c\u518d\u4e5f\u627f\u53d7\u4e0d\u4f4f\u8725\u8734\u9a91\u5175\u4eec\u7684\u5206\u91cf\u3002\u9ed1\u6697\u7cbe\u7075\u53d1\u73b0\u81ea\u5df1\u6b63\u5728\u6025\u901f\u4e0b\u6c89\uff0c\u4ed6\u4eec\u53d1\u51fa\u7edd\u671b\u7684\u558a\u53eb\u58f0\uff0c\u8868\u793a\u8ba4\u8f93\uff0c\u7136\u800c\u8fd9\u662f\u5f92\u52b3\u7684\uff0c\u743c\u6069\u5e76\u6ca1\u6709\u89e3\u9664\u9b54\u6cd5\u7684\u6253\u7b97\u2014\u2014\u4e8b\u5b9e\u4e0a\u4ed6\u81ea\u5df1\u90fd\u505a\u4e0d\u5230\u3002</p><p>\u89c2\u4f17\u5e2d\u4e0a\u7ec8\u4e8e\u6709\u4e86\u53cd\u5e94\uff0c\u201c\u64a4\u9500\u6cd5\u672f\uff01\u201d\u8fe6\u6cd5\u5389\u58f0\u6012\u559d\uff0c\u4ece\u6d1e\u7a9f\u4e2d\u8df3\u4e0b\uff0c\u51b2\u7740\u743c\u6069\u5954\u8dd1\u8fc7\u6765\uff0c\u5979\u7684\u624b\u4e2d\u5df2\u7ecf\u63e1\u7d27\u4e86\u86c7\u9996\u97ad\uff0c\u201c\u5351\u5fae\u7684\u4eba\u7c7b\uff0c\u4f60\u80c6\u6562\u4f24\u5bb3\u5353\u5c14\u2026\u2026\u201d</p><p>\u743c\u6069\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u62ac\u8d77\u6cd5\u6756\uff0c\u53d1\u51fa\u4e00\u9053\u7fe0\u7eff\u8272\u7684\u5c04\u7ebf\uff0c\u6b63\u6253\u5728\u8fe6\u6cd5\u7684\u811a\u6b65\u524d\u65b9\uff0c\u5730\u9762\u4e0a\u77ac\u95f4\u8bde\u751f\u4e86\u4e00\u4e2a\u6df1\u5751\u3002</p><p>\u201c\u9762\u5bf9\u4e00\u4f4d\u5927\u5deb\u5e08\uff0c\u60a8\u5e94\u8be5\u66f4\u793c\u8c8c\u4e00\u4e9b\uff0c\u5c0f\u59d0\u3002\u201d\u4ed6\u865a\u5f20\u58f0\u52bf\u3002</p><p>\u6240\u8c13\u5927\u5deb\u5e08\uff0c\u5e76\u6ca1\u6709\u4e00\u4e2a\u660e\u786e\u7684\u6807\u51c6\uff0c\u53ea\u662f\u6a21\u7cca\u7684\u6982\u5ff5\u3002\u4e00\u822c\u6765\u8bf4\uff0c\u80fd\u591f\u89e6\u6478\u9b54\u7f51\uff0c\u65bd\u5c55\u6cd5\u672f\uff0c\u4fbf\u53ef\u4ee5\u79f0\u4e3a\u5deb\u5e08\uff1b\u80fd\u591f\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u4e94\u5c42\uff0c\u51dd\u6210\u771f\u540d\uff0c\u53ef\u4ee5\u7b97\u662f\u9ad8\u9636\u5deb\u5e08\uff1b\u800c\u80fd\u591f\u89e6\u6478\u5230\u9b54\u7f51\u7684\u7b2c\u516b\u5230\u7b2c\u4e5d\u5c42\uff08\u8fd9\u662f\u51e1\u4eba\u7684\u6781\u9650\uff09\uff0c\u4fbf\u53ef\u4ee5\u88ab\u5c0a\u79f0\u4e3a\u5927\u5deb\u5e08\u3002\u5927\u5deb\u5e08\u7684\u6807\u5fd7\u4e4b\u4e00\uff0c\u5c31\u662f\u4ed6\u4eec\u5bf9\u9b54\u6cd5\u7684\u7814\u7a76\u548c\u638c\u63e1\u5df2\u7ecf\u8fbe\u5230\u4e86\u878d\u4f1a\u8d2f\u901a\u3001\u7089\u706b\u7eaf\u9752\u7684\u5730\u6b65\uff0c\u8db3\u4ee5\u968f\u5fc3\u6240\u6b32\u5730\u65bd\u5c55\u6cd5\u672f\uff0c\u4e0d\u9700\u8981\u501f\u52a9\u4efb\u4f55\u5492\u8bed\u3001\u59ff\u52bf\u6216\u8005\u65bd\u6cd5\u6750\u6599\u3002</p><p>\u743c\u6069\u8ddd\u79bb\u5927\u5deb\u5e08\u7684\u5883\u754c\u8fd8\u5dee\u5f97\u8fdc\uff0c\u4f46\u51ed\u501f\u5965\u6c83\u4f20\u6388\u7684\u90a3\u4e9b\u6280\u5de7\uff0c\u4ed6\u786e\u5b9e\u53ef\u4ee5\u52c9\u5f3a\u5192\u5145\u4e00\u4e0b\u3002\u8fd9\u4e5f\u662f\u4ed6\u9009\u62e9\u4f7f\u7528\u78c1\u5316\u672f\u7684\u7f18\u6545\uff0c\u6295\u673a\u53d6\u5de7\uff0c\u907f\u514d\u88ab\u8bc6\u7834\u771f\u5b9e\u7684\u529b\u91cf\u5dee\u8ddd\u3002\u73b0\u5728\u8981\u8d4c\u7684\uff0c\u5c31\u662f\u8fd9\u4e9b\u7267\u5e08\u4eec\u773c\u5149\u4e0d\u591f\uff0c\u770b\u4e0d\u51fa\u5176\u4e2d\u7684\u7834\u7efd\u3002</p><p>\u9ed1\u6697\u7cbe\u7075\u4e16\u754c\u7684\u89c4\u5219\uff0c\u4ee5\u5973\u6027\u81f3\u4e0a\uff0c\u4ee5\u7267\u5e08\u4e3a\u5c0a\uff0c\u4ee5\u5353\u5c14\u4e3a\u9ad8\u7b49\u79cd\u65cf\uff0c\u8511\u89c6\u5176\u4ed6\u751f\u547d\uff0c\u4f46\u8fd9\u53ea\u662f\u8868\u9762\u73b0\u8c61\uff0c\u5176\u771f\u6b63\u7684\u6838\u5fc3\u89c4\u5219\uff0c\u662f\u201c\u5f3a\u8005\u81f3\u4e0a\u201d\u3002\u56e0\u4e3a\u6709\u86db\u540e\u7f57\u4e1d\u7684\u652f\u6301\uff0c\u7267\u5e08\u4eec\u7684\u529b\u91cf\u666e\u904d\u5f3a\u4e8e\u5deb\u5e08\u548c\u6218\u58eb\u4eec\uff0c\u6240\u4ee5\u624d\u4f1a\u6709\u4e86\u5982\u4eca\u7684\u683c\u5c40\uff0c\u6700\u7b80\u5355\u7684\u53cd\u4f8b\u5c31\u662f\u6c99\u739b\u65af\u57ce\uff0c\u56e0\u4e3a\u5deb\u5e08\u52bf\u529b\u5e9e\u5927\uff0c\u5973\u6027\u7267\u5e08\u53cd\u800c\u88ab\u8d2c\u4f4e\u4e3a\u6b21\u7b49\u516c\u6c11\u4e86\u3002</p><p>\u9762\u5bf9\u4e00\u4f4d\u5927\u5deb\u5e08\uff0c\u5c31\u7b97\u662f\u9a84\u50b2\u7684\u5353\u5c14\u7267\u5e08\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u4fdd\u6301\u656c\u754f\uff0c\u5c24\u5176\u662f\u5728\u5982\u4eca\u5979\u4eec\u4e27\u5931\u795e\u672f\u7684\u60c5\u51b5\u4e0b\u3002</p><p>\u8fe6\u6cd5\u505c\u4f4f\u4e86\u811a\u6b65\uff0c\u5979\u6012\u6c14\u51b2\u51b2\uff0c\u7136\u800c\u4e0d\u6562\u518d\u4e0a\u524d\u3002\u201c\u4f60\u5df2\u7ecf\u83b7\u80dc\uff0c\u201d\u5979\u5a01\u80c1\u7740\uff0c\u201c\u4e0d\u8981\u8bd5\u56fe\u6311\u6218\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u5bb9\u5fcd\u5e95\u7ebf\u3002\u201d</p><p>\u743c\u6069\u644a\u5f00\u624b\uff0c\u201c\u4e0d\u6b7b\u4e0d\u4f11\uff0c\u8fd9\u662f\u5353\u5c14\u7684\u89c4\u77e9\u3002\u6211\u4eec\u4eba\u7c7b\u6709\u4e00\u53e5\u8bdd\uff0c\u5165\u4e61\u968f\u4fd7\uff0c\u5230\u4ec0\u4e48\u5730\u65b9\uff0c\u5c31\u5f97\u6309\u7167\u4ec0\u4e48\u5730\u65b9\u7684\u89c4\u77e9\u529e\u4e8b\u3002\u201d</p><p>\u4ed6\u8bf4\u5f97\u5f88\u6162\uff0c\u6545\u610f\u62d6\u5ef6\u65f6\u95f4\uff0c\u5f88\u5feb\uff0c\u5341\u4e09\u4e2a\u9ed1\u6697\u7cbe\u7075\u548c\u4ed6\u4eec\u7684\u8725\u8734\u5750\u9a91\u5df2\u7ecf\u4ece\u5730\u9762\u6d88\u5931\uff0c\u6c89\u5165\u6ce5\u6d46\u4e4b\u4e2d\u3002</p><p>\u51b3\u6597\u573a\u5185\u4e00\u7247\u6c89\u9ed8\uff0c\u6700\u540e\u662f\u83f2\u5c14\u4f26\u4e3b\u6bcd\u6e05\u8106\u7684\u9f13\u638c\u58f0\u6253\u7834\u4e86\u5bc2\u9759\u3002</p><p>\u201c\u5e72\u5f97\u6f02\u4eae\uff0c\u201d\u4e3b\u6bcd\u5938\u5956\u7740\uff0c\u8f7b\u8f7b\u8d70\u51fa\u6d1e\u7a9f\uff0c\u4f18\u96c5\u5730\u98d8\u843d\u5728\u5730\u9762\uff0c\u671d\u743c\u6069\u8d70\u6765\u3002\u5979\u7684\u795e\u60c5\u8f7b\u677e\u81ea\u82e5\uff0c\u4eff\u4f5b\u743c\u6069\u521a\u624d\u6740\u6b7b\u5979\u7684\u6b21\u5973\uff0c\u4ee5\u53ca\u5341\u4e8c\u4e2a\u7cbe\u9510\u7684\u8725\u8734\u9a91\u5175\u7684\u4e8b\u60c5\u538b\u6839\u5c31\u4e0d\u66fe\u53d1\u751f\u4f3c\u7684\u3002\u201c\u80fd\u529b\u548c\u5fe0\u8bda\uff0c\u4f60\u5df2\u7ecf\u5b8c\u7f8e\u5730\u8bc1\u660e\u4e86\u524d\u8005\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u8eac\u8eab\uff0c\u201c\u6211\u5f88\u4e50\u610f\u5411\u60a8\u8bc1\u660e\u540e\u8005\u3002\u201d</p></article>"}}}]);
