(window.webpackJsonp=window.webpackJsonp||[]).push([[1451],{1474:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u56db\u8282 \u65e7\u68a6",content:"<article><h2>\u7b2c\u5341\u56db\u8282 \u65e7\u68a6</h2><p>\u743c\u6069\u770b\u7740\u8299\u857e\u72c4\uff0c\u5979\u7684\u5934\u53d1\u84ec\u677e\u6563\u7740\uff0c\u663e\u7136\u662f\u521a\u6d17\u5b8c\u6fa1\u5439\u5e72\uff0c\u8eab\u4e0a\u53ea\u7a7f\u4e86\u4e00\u4ef6\u7c89\u7ea2\u8272\u7684\u7761\u8863\uff0c\u4e0a\u9762\u5370\u4e86\u65e0\u6570\u5c0f\u718a\u56fe\u6848\uff0c\u662f\u81ea\u5df1\u968f\u8eab\u5e26\u6765\u7684\u3002\u5979\u5176\u5b9e\u4e0d\u751a\u559c\u6b22\u5728\u8863\u670d\u4e0a\u6709\u592a\u591a\u56fe\u6848\u88c5\u9970\uff0c\u4ee5\u524d\u7684\u7761\u8863\u90fd\u662f\u7d20\u767d\u8272\u7684\uff0c\u662f\u8ba4\u8bc6\u743c\u6069\u4e4b\u540e\u624d\u6539\u4e86\u4e60\u60ef\u3002</p><p>\u201c\u5077\u5077\u6e9c\u51fa\u6765\u7684\uff1f\u201d\u743c\u6069\u95ee\uff0c\u4f38\u624b\u8f7b\u8f7b\u629a\u6478\u5979\u7684\u79c0\u53d1\u3002\u4ed6\u6b63\u5750\u5728\u6905\u4e0a\uff0c\u8299\u857e\u72c4\u534a\u8dea\u5728\u8eab\u65c1\uff0c\u67d4\u987a\u5730\u5c06\u8138\u4f0f\u5728\u4ed6\u817f\u4e0a\uff0c\u4eff\u4f5b\u4e00\u53ea\u4e56\u5de7\u7684\u6ce2\u65af\u732b\uff0c\u6b63\u6ee1\u8db3\u5730\u4eab\u53d7\u7740\u4e3b\u4eba\u7684\u7231\u629a\uff0c\u201c\u55ef\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8299\u8389\u5a05\u7761\u7740\u4e86\u3002\u201d</p><p>\u201c\u60f3\u8981\u4ec0\u4e48\u751f\u65e5\u793c\u7269\u5462\uff1f\u201d</p><p>\u201c\u6211\u8981\u2026\u2026\u4f60\u5148\u7b54\u5e94\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u4f60\u5148\u8bf4\u51fa\u6765\u8ba9\u6211\u770b\u770b\u554a\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u6211\u8981\u4f60\u5148\u7b54\u5e94\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u72b9\u8c6b\uff0c\u4ed6\u4e0d\u559c\u6b22\u8fd9\u6837\u4e0d\u77e5\u9053\u60c5\u51b5\u5c31\u4e8b\u5148\u505a\u8bb8\u8bfa\uff0c\u4f46\u8f6c\u5ff5\u518d\u60f3\u4e5f\u5c31\u7f62\u4e86\uff0c\u8299\u857e\u72c4\u548c\u81ea\u5df1\u8ba4\u8bc6\u4ee5\u6765\uff0c\u4ece\u6765\u5c31\u4e0d\u66fe\u4e3b\u52a8\u8981\u8fc7\u4ec0\u4e48\uff0c\u4eca\u5929\u662f\u5979\u751f\u65e5\uff0c\u5c31\u5c3d\u91cf\u987a\u7740\u5979\u7684\u610f\u597d\u4e86\u3002\u53cd\u6b63\u4ee5\u4ed6\u5bf9\u8299\u857e\u72c4\u7684\u4e86\u89e3\uff0c\u5c0f\u5973\u5b69\u7d20\u6765\u4e56\u5de7\uff0c\u4e5f\u4e0d\u4f1a\u63d0\u51fa\u592a\u8fc7\u5206\u7684\u8981\u6c42\u3002</p><p>\u201c\u597d\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u7b54\u5e94\u4e86\uff0c\u60f3\u8981\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u60f3\u8981\u4f60\u4eca\u665a\u966a\u7740\u6211\u3002\u201d</p><p>\u201c\u5c31\u8fd9\u4e2a\uff1f\u201d\u743c\u6069\u5012\u662f\u6709\u4e9b\u610f\u5916\u3002</p><p>\u201c\u55ef\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5c31\u8fd9\u4e2a\u3002\u201d</p><p>\u743c\u6069\u4e00\u65f6\u6709\u4e9b\u8fdf\u7591\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002\u966a\u5979\u4e00\u665a\u5012\u6ca1\u4ec0\u4e48\uff0c\u4f46\u611f\u60c5\u4e0a\u7684\u4e8b\u60c5\u7d20\u6765\u590d\u6742\uff0c\u5f80\u5f80\u8fd9\u4e9b\u7ec6\u5fae\u4e4b\u5904\u5c31\u610f\u5473\u7740\u5173\u7cfb\u7684\u6084\u7136\u53d8\u5316\u3002\u5982\u679c\u8bf4\u51e0\u4e2a\u6708\u524d\u4ed6\u521a\u56de\u9634\u9b42\u57ce\u7684\u65f6\u5019\uff0c\u5fc3\u4e2d\u8fd8\u4e0d\u514d\u5bf9\u8299\u857e\u72c4\u66fe\u7ecf\u6b3a\u9a97\u81ea\u5df1\u6709\u4e9b\u8bb8\u4e0d\u5feb\uff0c\u5982\u4eca\u8fd9\u4e00\u8def\u76f8\u4f34\u8d70\u6765\uff0c\u8fc7\u53bb\u7684\u4e8b\u60c5\u4e5f\u65e9\u5c31\u538b\u6839\u4e0d\u653e\u5728\u5fc3\u4e0a\u4e86\u3002\u53ea\u662f\u4ece\u76f8\u8bc6\u5230\u60c5\u4eba\uff0c\u518d\u4ece\u60c5\u4eba\u5230\u964c\u8def\uff0c\u5982\u4eca\u4ee5\u53cb\u4eba\u76f8\u5f85\uff0c\u6682\u65f6\u4e5f\u6ca1\u60f3\u8fc7\u8981\u91cd\u5f52\u4e8e\u597d\uff0c\u53ea\u6253\u7b97\u5148\u4fdd\u6301\u73b0\u72b6\uff0c\u5f97\u8fc7\u4e14\u8fc7\uff0c\u4ee5\u540e\u7684\u4e8b\u60c5\u518d\u8bf4\u3002</p><p>\u8299\u857e\u72c4\u89c1\u4ed6\u6c89\u9ed8\uff0c\u4ee5\u4e3a\u62d2\u7edd\uff0c\u4e0d\u7531\u5f97\u60f6\u6025\u8d77\u6765\u3002\u5979\u6bcd\u4eb2\u53bb\u4e16\u5f97\u65e9\uff0c\u7236\u4eb2\u4e3a\u4eba\u4e25\u5389\uff0c\u4e0a\u9762\u53c8\u6709\u59d0\u59d0\u8299\u8389\u5a05\u4fdd\u62a4\u7740\uff0c\u4ee5\u81f3\u4e8e\u517b\u6210\u4e86\u67d4\u5f31\u987a\u4ece\u7684\u6027\u683c\uff0c\u5e73\u65f6\u5904\u4e8b\u6781\u5c11\u6709\u81ea\u5df1\u7684\u4e3b\u5f20\u3002\u8fd9\u6b21\u501f\u7740\u751f\u65e5\u7684\u7531\u5934\u5411\u743c\u6069\u5f00\u53e3\uff0c\u5df2\u7ecf\u662f\u4e8b\u5148\u9f13\u8db3\u4e86\u52c7\u6c14\uff0c\u773c\u89c1\u4ed6\u4e0d\u80af\uff0c\u987f\u65f6\u5fc3\u4e2d\u4e00\u9eef\uff0c\u60f3\u7740\u201c\u4ed6\u7ec8\u7a76\u662f\u4e0d\u559c\u6b22\u6211\u4e86\u3002\u201d\u7f13\u7f13\u7ad9\u8d77\u8eab\u6765\uff0c\u52c9\u5f3a\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u201c\u5f88\u665a\u4e86\uff0c\u6211\u8d70\u4e86\uff0c\u4f60\u4f11\u606f\u5427\u3002\u201d\u5374\u5fcd\u4e0d\u4f4f\u773c\u4e2d\u4e24\u884c\u6e05\u6cea\u6ef4\u4e0b\u6765\uff0c\u8bf4\u5230\u6700\u540e\u4e00\u4e2a\u5b57\u7684\u65f6\u5019\uff0c\u58f0\u97f3\u5df2\u7ecf\u662f\u6897\u54bd\u4e86\u3002</p><p>\u743c\u6069\u4e0b\u610f\u8bc6\u5730\u4f38\u624b\u4e00\u63fd\uff0c\u5c06\u5979\u6402\u5230\u6000\u4e2d\u3002\u4ed6\u521a\u624d\u75b2\u5026\u5f97\u5f88\uff0c\u89c1\u662f\u8299\u857e\u72c4\u8fdb\u6765\uff0c\u4e5f\u4e0d\u751a\u5728\u610f\uff0c\u73b0\u5728\u8d34\u8fd1\u4e86\u770b\uff0c\u53d1\u73b0\u5c0f\u5973\u5b69\u9762\u8272\u82cd\u767d\uff0c\u5634\u5507\u90fd\u6ca1\u4ec0\u4e48\u8840\u8272\uff0c\u773c\u7751\u4f4e\u5782\uff0c\u62fc\u547d\u60f3\u8981\u5fcd\u4f4f\u773c\u6cea\uff0c\u5374\u7ec8\u7a76\u662f\u5f92\u52b3\uff0c\u8138\u4e0a\u5df2\u7ecf\u662f\u660e\u663e\u7684\u4e24\u884c\u6cea\u75d5\u4e86\u3002\u521a\u624d\u63fd\u5979\u7684\u65f6\u5019\uff0c\u4fbf\u89c9\u5f97\u4eff\u4f5b\u8f7b\u4e86\u8bb8\u591a\uff0c\u5982\u4eca\u53cc\u81c2\u73af\u62b1\u7740\uff0c\u8ba9\u5979\u5750\u5728\u81ea\u5df1\u817f\u4e0a\uff0c\u8fd9\u79cd\u611f\u89c9\u66f4\u52a0\u660e\u663e\u3002</p><p>\u201c\u600e\u4e48\u8fd9\u4e48\u8f7b\u4e86\u2026\u2026\u6bd4\u4ee5\u524d\u7626\u4e86\u8bb8\u591a\u3002\u201d</p><p>\u4ed6\u5fc3\u4e2d\u60ca\u8bb6\uff0c\u8f6c\u5ff5\u4e5f\u5c31\u77e5\u9053\u7f18\u6545\u3002\u8299\u857e\u72c4\u51fa\u8eab\u8d35\u65cf\uff0c\u5a07\u751f\u60ef\u517b\uff0c\u4eff\u4f5b\u6e29\u5ba4\u957f\u5927\u7684\u82b1\u6735\u4e00\u822c\uff0c\u4f53\u8d28\u539f\u672c\u5c31\u4e0d\u4f73\uff0c\u5728\u8fd9\u9634\u51b7\u9ed1\u6697\u7684\u5e7d\u6697\u5730\u57df\u5f85\u5f97\u592a\u4e45\uff0c\u81ea\u7136\u4fbf\u6e10\u6e10\u627f\u53d7\u4e0d\u4f4f\u3002</p><p>\u60f3\u5230\u6b64\u5904\uff0c\u601c\u60dc\u4e4b\u610f\u987f\u65f6\u6d8c\u4e0a\u5fc3\u5934\uff0c\u53c8\u8bb0\u8d77\u4ee5\u524d\u4e24\u4eba\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u4e5f\u5e38\u5e38\u8fd9\u6837\u5c06\u5979\u62b1\u5728\u817f\u4e0a\u5750\u7740\uff0c\u54c4\u5979\u9ad8\u5174\u3002\u5f80\u6614\u79cd\u79cd\u751c\u871c\u573a\u666f\u5728\u773c\u524d\u4e00\u65f6\u95ea\u8fc7\uff0c\u604d\u5982\u6628\u65e5\u91cd\u73b0\uff0c\u5fc3\u4e2d\u987f\u65f6\u8f6f\u4e86\u4e0b\u6765\uff0c\u201c\u597d\uff0c\u201d\u4ed6\u6084\u58f0\u8bf4\uff0c\u201c\u4eca\u665a\u6211\u966a\u7740\u4f60\u3002\u201d</p><p>\u8299\u857e\u72c4\u539f\u672c\u5df2\u7ecf\u7edd\u671b\uff0c\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\uff0c\u5374\u53c8\u4e0d\u6562\u518d\u8be2\u95ee\u786e\u8ba4\uff0c\u5fc3\u60f3\u5c31\u7b97\u662f\u542c\u9519\u4e86\uff0c\u4e5f\u60c5\u613f\u4e86\u3002\u7a81\u7136\u611f\u89c9\u8138\u988a\u4f20\u6765\u6e29\u70ed\u7684\u89e6\u611f\uff0c\u6014\u4e86\u6014\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u662f\u88ab\u743c\u6069\u8f7b\u8f7b\u543b\u4e86\u4e00\u4e0b\u3002\u5979\u6b64\u65f6\u610f\u8bc6\u6709\u4e9b\u4e0d\u592a\u6e05\u9192\uff0c\u8111\u4e2d\u8ff7\u8ff7\u7cca\u7cca\u4e86\u534a\u664c\uff0c\u7ec8\u4e8e\u9192\u609f\u8fc7\u6765\uff0c\u201c\u4ed6\u4eb2\u4e86\u6211\uff0c\u90a3\u5c31\u662f\u7b54\u5e94\u4e86\u3002\u201d\u4e00\u65f6\u95f4\u7b11\u9010\u989c\u5f00\uff0c\u53ea\u89c9\u5f97\u6709\u751f\u4ee5\u6765\uff0c\u4f3c\u4e4e\u4ece\u6ca1\u50cf\u4eca\u5929\u8fd9\u822c\u5feb\u4e50\u8fc7\u3002</p><p>\u743c\u6069\u89c1\u5979\u6ee1\u8138\u6cea\u75d5\u672a\u5e72\uff0c\u5374\u7b11\u5f97\u707f\u70c2\u5982\u82b1\uff0c\u7709\u773c\u95f4\u6ee1\u662f\u559c\u60a6\u4e4b\u8272\uff0c\u4e0d\u7531\u5f97\u4e5f\u662f\u5fc3\u4e2d\u4e00\u8361\u3002\u82e5\u8bba\u8d77\u6765\uff0c\u8299\u857e\u72c4\u662f\u4ed6\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7b2c\u4e00\u4e2a\u60c5\u4eba\uff0c\u4e5f\u4eff\u4f5b\u521d\u604b\u4e00\u822c\uff0c\u867d\u7136\u4e0d\u5982\u59d0\u59d0\u73ca\u5609\u4eb2\u539a\uff0c\u4e0d\u53ca\u6885\u83f2\u65af\u521a\u5f3a\u660e\u777f\uff0c\u7ec8\u7a76\u4e5f\u4e0d\u80fd\u771f\u6b63\u5728\u5fc3\u91cc\u62b9\u53bb\u3002\u5018\u82e5\u4e0d\u662f\u90a3\u4ef6\u4e8b\uff0c\u73b0\u5728\u4e5f\u4e0d\u81f3\u4e8e\u5f04\u5230\u8fd9\u79cd\u5730\u6b65\u4e86\u3002\u4ed6\u6084\u6084\u53f9\u4e86\u53e3\u6c14\uff0c\u4f38\u624b\u66ff\u5979\u62b9\u53bb\u773c\u6cea\uff0c\u8bf4\uff1a\u201c\u597d\u5566\uff0c\u522b\u54ed\u4e86\uff0c\u73b0\u5728\u4e5f\u90fd\u5df2\u7ecf\u5341\u4e94\u5c81\u4e86\uff0c\u600e\u4e48\u8fd8\u50cf\u4e2a\u5b69\u5b50\u4f3c\u7684\u3002\u201d</p><p>\u8299\u857e\u72c4\u5f97\u4ed6\u5e94\u5141\uff0c\u65e9\u5c31\u5fc3\u6ee1\u610f\u8db3\uff0c\u88ab\u62b1\u5728\u6000\u4e2d\u53ea\u89c9\u5168\u8eab\u6696\u6696\u7684\uff0c\u4eff\u4f5b\u5728\u4e91\u7aef\u4e00\u822c\uff0c\u5fc3\u60c5\u6781\u4f73\uff0c\u518d\u4e0d\u591a\u60f3\u5176\u4ed6\u3002\u542c\u743c\u6069\u8bf4\u5979\u50cf\u5b69\u5b50\uff0c\u4e0d\u7531\u5f97\u968f\u53e3\u53cd\u9a73\u8bf4\uff1a\u201c\u6211\u672c\u6765\u5c31\u8fd8\u662f\u5b69\u5b50\u561b\u3002\u201d\u5c06\u8138\u8d34\u5728\u4ed6\u6000\u91cc\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u5374\u518d\u6ca1\u542c\u5230\u52a8\u9759\uff0c\u4e0d\u7531\u5f97\u8be7\u5f02\u8d77\u6765\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u89c1\u743c\u6069\u6b63\u5728\u6c89\u601d\uff0c\u6709\u4e9b\u5947\u602a\uff0c\u95ee\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u9ed8\u7136\u534a\u664c\uff0c\u8bf4\uff0c\u201c\u8299\u857e\u72c4\uff0c\u4f60\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8981\u5e26\u4f60\u6765\u4e48\u3002\u201d</p><p>\u201c\u77e5\u9053\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u4f60\u5bf9\u8299\u8389\u5a05\u4e0d\u653e\u5fc3\u561b\uff0c\u6709\u6211\u5728\uff0c\u5979\u5c31\u662f\u591a\u4e86\u987e\u8651\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u4e00\u60ca\uff0c\u51dd\u795e\u770b\u5979\uff0c\u89c1\u5979\u8138\u4e0a\u4e00\u6d3e\u5929\u771f\u5355\u7eaf\uff0c\u4eff\u4f5b\u5168\u4e0d\u5728\u610f\u4f3c\u7684\u3002\u201c\u4f60\u4e0d\u602a\u6211\u4e48\uff1f\u201d\u4ed6\u4f4e\u58f0\u95ee\u3002</p><p>\u8299\u857e\u72c4\u6447\u5934\uff0c\u201c\u6ca1\u6709\u554a\uff0c\u6211\u559c\u6b22\u4f60\uff0c\u613f\u610f\u966a\u5728\u4f60\u8eab\u8fb9\uff0c\u613f\u610f\u968f\u7740\u4f60\uff0c\u5929\u5929\u770b\u7740\u4f60\u3002\u5c31\u7b97\u4f60\u4e0d\u8bf4\uff0c\u6211\u4e5f\u8981\u6c42\u59d0\u59d0\u5e26\u6211\u4e00\u8d77\u5462\u3002\u6211\u81ea\u5df1\u5fc3\u7518\u60c5\u613f\u7684\u561b\u3002\u201d</p><p>\u201c\u5e72\u561b\u90a3\u4e48\u559c\u6b22\u6211\u554a\u3002\u201d</p><p>\u201c\u5c31\u662f\u559c\u6b22\u554a\uff0c\u201d\u8299\u857e\u72c4\u628a\u8eab\u4f53\u6574\u4e2a\u90fd\u8737\u8fdb\u743c\u6069\u7684\u6000\u91cc\uff0c\u201c\u8fd9\u6709\u4ec0\u4e48\u4e3a\u4ec0\u4e48\u4e0d\u4e3a\u4ec0\u4e48\u7684\u3002\u4e16\u754c\u4e0a\u8fd9\u4e48\u591a\u4eba\uff0c\u4f60\u603b\u4f1a\u559c\u6b22\u4e0a\u67d0\u4e2a\uff0c\u6211\u6070\u597d\u5c31\u559c\u6b22\u4f60\u4e86\u800c\u5df2\u3002\u201d</p><p>\u743c\u6069\u629a\u6478\u7740\u5979\u7684\u8138\u86cb\uff0c\u8f7b\u8f7b\u634f\u4e86\u634f\uff0c\u611f\u53d7\u90a3\u5439\u5f39\u53ef\u7834\u7684\u67d4\u5ae9\u808c\u80a4\uff0c\u201c\u6709\u6ca1\u6709\u60f3\u8fc7\uff0c\u6216\u8bb8\u662f\u56e0\u4e3a\u4f60\u8ba4\u8bc6\u7684\u7537\u4eba\u592a\u5c11\u4e86\u554a\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u9634\u9b42\u57ce\u592a\u5c0f\u4e86\uff0c\u4eba\u4e5f\u592a\u5c11\u4e86\uff0c\u53bb\u5916\u9762\u66f4\u5e7f\u9614\u7684\u4e16\u754c\u8d70\u8d70\uff0c\u89c1\u8bc6\u66f4\u591a\u7684\u4eba\uff0c\u8bf4\u4e0d\u5b9a\u4f60\u5c31\u4f1a\u9047\u5230\u5176\u4ed6\u559c\u6b22\u7684\u4eba\u5462\u3002\u201d</p><p>\u201c\u90a3\u53c8\u600e\u4e48\u6837\uff0c\u6211\u5df2\u7ecf\u6709\u6211\u6700\u559c\u6b22\u7684\u4e86\u3002\u201d</p><p>\u201c\u4f60\u53c8\u6ca1\u8bd5\u8fc7\u600e\u4e48\u77e5\u9053\uff0c\u6216\u8bb8\u4f60\u4f1a\u53d1\u73b0\u6709\u66f4\u559c\u6b22\u7684\u5462\uff0c\u201d\u743c\u6069\u9017\u5979\uff0c\u201c\u8fd9\u4e48\u65e9\u5c31\u8f7b\u6613\u4e0b\u65ad\u8a00\uff0c\u662f\u4e0d\u7b26\u5408\u5deb\u5e08\u7684\u4e25\u8c28\u7cbe\u795e\u5462\u3002\u201d</p><p>\u8299\u857e\u72c4\u7b11\u5f97\u5f88\u751c\uff0c\u5979\u6084\u6084\u62e8\u5f00\u4e86\u743c\u6069\u957f\u888d\u7684\u8863\u9886\uff0c\u4eb2\u7740\u4ed6\u7684\u9501\u9aa8\uff0c\u201c\u8fd9\u9053\u7406\u6216\u8bb8\u5bf9\uff0c\u4f46\u6ca1\u6709\u610f\u4e49\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6309\u4f60\u8fd9\u4e48\u8bf4\uff0c\u96be\u9053\u6211\u8fd8\u8981\u628a\u4e16\u754c\u4e0a\u6240\u6709\u7684\u7537\u4eba\u90fd\u5148\u8ba4\u8bc6\u4e00\u904d\uff0c\u7136\u540e\u624d\u80fd\u9009\u62e9\u60c5\u4eba\u4e86\uff1f\u6211\u559c\u6b22\u4f60\uff0c\u5c31\u662f\u8fd9\u6837\u4e86\uff0c\u4ece\u6b64\u5c31\u518d\u4e5f\u770b\u4e0d\u4e0a\u522b\u4eba\u4e86\u3002\u201d</p><p>\u743c\u6069\u4f4e\u5934\u5728\u5979\u5934\u53d1\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u62e6\u8170\u5c06\u5979\u62b1\u4e86\u8d77\u6765\uff0c\u653e\u5728\u5e8a\u4e0a\uff0c\u201c\u6211\u53bb\u6d17\u6fa1\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u7b49\u6211\u56de\u6765\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u60c5\u4eba\u4e4b\u95f4\uff0c\u81ea\u6709\u4e9b\u4e0d\u8db3\u4e3a\u5916\u4eba\u9053\u7684\u9ed8\u5951\u3002\u548c\u5f80\u5e38\u4e00\u6837\uff0c\u5f53\u743c\u6069\u4ece\u6d74\u5ba4\u8d70\u51fa\uff0c\u56de\u5230\u5e8a\u4e0a\u7684\u65f6\u5019\uff0c\u8299\u857e\u72c4\u5df2\u7ecf\u5168\u8eab\u8d64\u88f8\uff0c\u4e00\u4e1d\u4e0d\u6302\uff0c\u5979\u808c\u80a4\u96ea\u767d\uff0c\u5728\u9ed1\u6697\u7684\u623f\u95f4\u91cc\u770b\u8d77\u6765\u4e5f\u5341\u5206\u9192\u76ee\uff0c\u4fbf\u5982\u4e00\u5c0a\u5b8c\u7f8e\u65e0\u6687\u7684\u767d\u7389\u96d5\u5851\u4f3c\u7684\u3002\u89c1\u743c\u6069\u56de\u5230\u5e8a\u4e0a\uff0c\u4e3b\u52a8\u5c06\u8eab\u4f53\u8d34\u4e86\u8fc7\u6765\uff0c\u5c06\u4ed6\u62b1\u7d27\uff0c\u5374\u4e0d\u8bf4\u8bdd\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u957f\u957f\u5401\u4e86\u53e3\u6c14\uff0c\u4eff\u4f5b\u5982\u91ca\u91cd\u8d1f\u4e00\u822c\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u743c\u6069\u95ee\uff0c\u4f38\u81c2\u73af\u5728\u5c11\u5973\u8170\u4e0a\uff0c\u5c06\u5979\u62b1\u5728\u6000\u91cc\u3002</p><p>\u201c\u6211\u597d\u4e45\u6ca1\u5728\u4f60\u6000\u91cc\u4e86\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u6211\u559c\u6b22\u95fb\u4f60\u8eab\u4e0a\u7684\u5473\u9053\u3002\u201d</p><p>\u743c\u6069\u4e00\u65f6\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\uff0c\u53ea\u662f\u5c06\u5979\u62b1\u7740\uff0c\u624b\u8f7b\u8f7b\u4ece\u79c0\u53d1\u4e0a\u5f80\u4e0b\u629a\u6478\uff0c\u6ed1\u8fc7\u5149\u6d01\u7684\u810a\u80cc\uff0c\u5728\u8170\u90e8\u7684\u51f9\u9677\u5904\u7a0d\u7a0d\u505c\u987f\uff0c\u201c\u8170\u6bd4\u4ee5\u524d\u66f4\u7ec6\u4e86\uff0c\u201d\u4ed6\u8f7b\u8f7b\u8bf4\uff0c\u201c\u4f60\u53d8\u7626\u4e86\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u559c\u6b22\u5973\u5b69\u5b50\u82d7\u6761\u4e48\u3002\u201d</p><p>\u201c\u592a\u7626\u4e86\u4e5f\u4e0d\u597d\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd8\u662f\u8981\u6709\u70b9\u8102\u80aa\u7684\uff0c\u5426\u5219\u2026\u2026\u201d\u4ed6\u54ac\u7740\u5973\u5b69\u7684\u8033\u5782\uff0c\u201c\u5426\u5219\u786c\u90a6\u90a6\u7684\uff0c\u538b\u5728\u8eab\u4f53\u4e0b\u9762\uff0c\u6211\u4e5f\u4f1a\u4e0d\u8212\u670d\u7684\u3002\u201d</p><p>\u8299\u857e\u72c4\u8138\u4e0a\u6cdb\u7ea2\uff0c\u547c\u5438\u5df2\u7ecf\u53d8\u5f97\u6eda\u70eb\uff0c\u76ae\u80a4\u4e0a\u5374\u6e10\u6e10\u98a4\u8d77\u5bd2\u7c92\uff0c\u201c\u4eb2\u6211\u3002\u201d\u5979\u6709\u4e9b\u542b\u7cca\u4e0d\u6e05\u5730\u8bf4\uff0c\u7389\u81c2\u62b1\u7740\u743c\u6069\u7684\u8116\u9888\uff0c\u4ef0\u7740\u8138\uff0c\u773c\u775b\u95ed\u7740\uff0c\u7b49\u5f85\u7740\u7537\u4eba\u7684\u4eb2\u543b\u3002\u5979\u7684\u5634\u5507\u539f\u672c\u6ca1\u4ec0\u4e48\u8840\u8272\uff0c\u5982\u4eca\u4e5f\u53d8\u5f97\u7ea2\u6da6\u8d77\u6765\uff0c\u4eff\u4f5b\u82b1\u6735\u4e00\u822c\u5a07\u8273\u6b32\u6ef4\u3002\u743c\u6069\u5374\u6ca1\u6709\u6025\u4e8e\u91c7\u6458\uff0c\u201c\u5c0f\u4e2b\u5934\uff0c\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u201c\u4f60\u59d0\u59d0\u5728\u9694\u58c1\u5462\u3002\u201d</p><p>\u81ea\u4ece\u77e5\u9053\u8fd9\u53cc\u80de\u80ce\u59d0\u59b9\u4fe9\u80fd\u591f\u5fc3\u7075\u611f\u5e94\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u5c31\u660e\u767d\u4e3a\u4ec0\u4e48\u4e00\u76f4\u4ee5\u6765\u8299\u8389\u5a05\u90fd\u5bf9\u81ea\u5df1\u6ca1\u6709\u597d\u8138\u8272\u4e86\u3002\u4ed6\u4e00\u76f4\u4ee5\u4e3a\u81ea\u5df1\u5f88\u65e0\u8f9c\uff0c\u53c8\u6ca1\u62db\u60f9\u5bf9\u65b9\uff0c\u6700\u591a\u4e0d\u8fc7\u662f\u52fe\u642d\u4e86\u5979\u59b9\u59b9\u7f62\u4e86\uff0c\u4e24\u60c5\u76f8\u60a6\uff0c\u53c8\u4e0d\u662f\u4ec0\u4e48\u574f\u4e8b\u3002\u7136\u800c\u4e8b\u5b9e\u662f\u2026\u2026\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u8bf4\uff0c\u743c\u6069\u4e00\u76f4\u662f\u5728\u5f3a\u66b4\u8299\u8389\u5a05\u3002</p><p>\u743c\u6069\u4e0d\u6e05\u695a\u5979\u4eec\u59d0\u59b9\u95f4\u7684\u5fc3\u7075\u611f\u5e94\u80fd\u6e05\u695a\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff0c\u4f3c\u4e4e\u548c\u8ddd\u79bb\u7684\u8fdc\u8fd1\u4e5f\u6709\u4e00\u5b9a\u5173\u7cfb\uff0c\u8ddd\u79bb\u8d8a\u8d34\u8fd1\u5219\u611f\u5e94\u8d8a\u5f3a\uff0c\u8ddd\u79bb\u8d8a\u8fdc\u5219\u611f\u5e94\u8d8a\u5f31\u3002\u4f46\u9634\u9b42\u57ce\u5c31\u90a3\u4e48\u5927\u70b9\u5730\u65b9\uff0c\u81ea\u5df1\u5728\u5deb\u5e08\u5b66\u6821\u91cc\u548c\u8299\u857e\u72c4\u4e0a\u5e8a\uff0c\u8299\u8389\u5a05\u80af\u5b9a\u662f\u6709\u6240\u611f\u5e94\u7684\u2014\u2014\u4e5f\u5c31\u662f\u8bf4\uff0c\u5f53\u743c\u6069\u6bcf\u6b21\u8fdb\u5165\u8299\u857e\u72c4\u7684\u8eab\u4f53\u65f6\uff0c\u8299\u8389\u5a05\u60f3\u5fc5\u4e5f\u4f1a\u611f\u89c9\u5230\u88ab\u5165\u4fb5\u4e86\u5427\u3002\u88ab\u4e00\u4e2a\u4ece\u4e0d\u76f8\u8bc6\uff0c\u7d20\u672a\u8c0b\u9762\u7684\u7537\u4eba\u8086\u610f\u4fb5\u72af\uff0c\u8fd9\u9664\u4e86\u201c\u5f3a\u66b4\u201d\uff0c\u4f3c\u4e4e\u4e5f\u6ca1\u6709\u522b\u7684\u5f62\u5bb9\u8bcd\u4e86\u3002</p><p>\u743c\u6069\u5e76\u975e\u826f\u5584\u4e4b\u8f88\uff0c\u5f3a\u66b4\u5c31\u5f3a\u66b4\u4e86\uff0c\u5012\u4e5f\u8c08\u4e0d\u4e0a\u591a\u5c11\u6127\u759a\uff0c\u4f55\u51b5\u8fd9\u4ef6\u4e8b\u60c5\u5b9e\u5728\u6709\u4e9b\u532a\u5937\u6240\u601d\uff0c\u4f3c\u4e4e\u4e5f\u602a\u4e0d\u5f97\u4ed6\u3002\u53ea\u662f\u65e2\u7136\u77e5\u9053\u8fd9\u524d\u56e0\u540e\u679c\uff0c\u8299\u8389\u5a05\u8981\u6028\u6068\u81ea\u5df1\uff0c\u90a3\u4e5f\u662f\u7406\u6240\u5e94\u5f53\u7684\u4e8b\u60c5\uff0c\u4e0d\u7b97\u81ea\u5df1\u65e0\u8f9c\u8499\u51a4\u4e86\u3002\u5982\u4eca\u59b9\u59b9\u5728\u81ea\u5df1\u5e8a\u4e0a\uff0c\u59d0\u59d0\u5374\u5728\u9694\u58c1\uff0c\u8fd9\u4e48\u8fd1\u7684\u8ddd\u79bb\uff0c\u5018\u82e5\u81ea\u5df1\u5bf9\u8299\u857e\u72c4\u505a\u4ec0\u4e48\uff0c\u8299\u8389\u5a05\u60f3\u5fc5\u662f\u611f\u540c\u8eab\u53d7\u2026\u2026\u867d\u8bf4\u4f3c\u4e4e\u5f88\u9999\u8273\uff0c\u7136\u800c\u8fd9\u79cd\u4e8b\u60c5\uff0c\u7ec8\u7a76\u8fd8\u662f\u6709\u4e9b\u4e0d\u59a5\u3002</p><p>\u201c\u6ca1\u4e8b\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u5979\u7761\u7740\u4e86\u3002\u201d</p><p>\u201c\u786e\u5b9a\uff1f\u201d</p><p>\u8299\u857e\u72c4\u72e1\u9ee0\u5730\u7b11\u4e86\u4e00\u7b11\uff0c\u201c\u6211\u5bf9\u5979\u4e0b\u4e86\u4e00\u4e2a\u7761\u7720\u672f\u3002\u201d</p><p>\u743c\u6069\u54d1\u7136\u5931\u7b11\uff0c\u8f7b\u8f7b\u62cd\u4e86\u62cd\u5979\u7684\u8138\u86cb\uff0c\u201c\u771f\u806a\u660e\uff0c\u201d\u4ed6\u5938\u5956\u8bf4\uff0c\u201c\u4e0d\u6015\u4f60\u59d0\u59d0\u751f\u6c14\uff1f\u201d</p><p>\u5c0f\u5973\u5b69\u6485\u8d77\u5634\uff0c\u201c\u90a3\u6709\u4ec0\u4e48\u529e\u6cd5\uff0c\u603b\u4e0d\u80fd\u56e0\u4e3a\u5979\uff0c\u6211\u5c31\u4e00\u8f88\u5b50\u4e0d\u80fd\u548c\u559c\u6b22\u7684\u4eba\u2026\u2026\u54ea\u6709\u8fd9\u79cd\u9053\u7406\u7684\u2026\u2026\u201d</p><p>\u540e\u9762\u7684\u8bdd\u8fd8\u6ca1\u8bf4\u5b8c\uff0c\u5634\u5507\u5df2\u7ecf\u88ab\u5835\u4f4f\uff0c\u719f\u6089\u7684\u7537\u6027\u6c14\u606f\u6ee1\u6ea2\u800c\u6765\uff0c\u5979\u611f\u89c9\u81ea\u5df1\u51e0\u4e4e\u8981\u88ab\u5b8c\u5168\u5305\u88f9\u3002\u539f\u672c\u5c31\u5df2\u7ecf\u628a\u6301\u4e0d\u4f4f\u7684\u4e00\u70b9\u6e05\u9192\u77ac\u95f4\u74e6\u89e3\u5d29\u6e83\uff0c\u5979\u4e0b\u610f\u8bc6\u5730\u62b1\u7d27\uff0c\u5c06\u8eab\u4f53\u7d27\u7d27\u8d34\u7740\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5df2\u7ecf\u5410\u51fa\u820c\u5c16\uff0c\u56de\u5e94\u7740\u70ed\u70c8\u7684\u4eb2\u543b\u3002</p><p>\u8ff7\u8ff7\u7cca\u7cca\u95f4\uff0c\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5f53\u5979\u518d\u6b21\u6e05\u9192\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u743c\u6069\u5df2\u7ecf\u7ed3\u675f\u4e86\u4eb2\u543b\uff0c\u6b63\u4ed4\u7ec6\u6253\u91cf\u7740\u5979\u7684\u8eab\u4f53\uff0c\u70ed\u8fa3\u8fa3\u7684\u773c\u5149\u6162\u6162\u626b\u8fc7\u6bcf\u4e00\u5bf8\u808c\u80a4\uff0c\u4eff\u4f5b\u9274\u8d4f\u4e00\u4ef6\u827a\u672f\u54c1\u4f3c\u7684\u3002</p><p>\u201c\u522b\u770b\u3002\u201d\u5979\u6709\u4e9b\u5bb3\u7f9e\uff0c\u7528\u624b\u906e\u4f4f\u80f8\u53e3\u548c\u4e0b\u8eab\uff0c\u5374\u88ab\u743c\u6069\u62e8\u5f00\u4e86\u3002\u201c\u6211\u559c\u6b22\u770b\uff0c\u201d\u4ed6\u6709\u610f\u6311\u9017\u7740\uff0c\u4f38\u624b\u5728\u5979\u80f8\u53e3\u8f7b\u8f7b\u63c9\u634f\u7740\uff0c\u201c\u597d\u50cf\u53d8\u5c0f\u4e86\u70b9\u5462\u3002\u201d</p><p>\u201c\u5514\uff0c\u201d\u8299\u857e\u72c4\u6709\u4e9b\u6cae\u4e27\uff0c\u201c\u4eba\u53d8\u7626\u4e86\uff0c\u5979\u4eec\u4e5f\u8ddf\u7740\u7626\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u201d\u743c\u6069\u5b89\u6170\uff0c\u201c\u8fd8\u662f\u5f88\u5927\uff0c\u8db3\u591f\u4e86\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u5bf9\u5976\u725b\u5e76\u65e0\u591a\u5c11\u70ed\u7231\uff0c\u4ed6\u770b\u91cd\u7684\u662f\u5f62\u72b6\uff0c\u8299\u857e\u72c4\u4ee5\u524d\u6709C\u7f69\u676f\uff0c\u5982\u4eca\u867d\u7136\u7f29\u6c34\u4e86\u4e9b\uff0c\u4f9d\u7136\u4fdd\u6301\u5728B\u7684\u6c34\u51c6\uff0c\u6700\u91cd\u8981\u7684\u662f\u4f9d\u65e7\u575a\u633a\u50b2\u4eba\uff0c\u8fd9\u5c31\u8db3\u591f\u4e86\u3002\u4ed6\u4f4e\u5934\u542b\u7740\u7c89\u7ea2\u8272\u7684\u84d3\u857e\u5c16\u7aef\uff0c\u8f7b\u8f7b\u8214\u8210\uff0c\u4e00\u624b\u6402\u7740\u8299\u857e\u72c4\u7684\u8170\uff0c\u7981\u6b62\u5979\u4e71\u52a8\uff0c\u53e6\u5916\u4e00\u53ea\u624b\u5219\u6084\u6084\u987a\u7740\u8179\u90e8\u5f80\u4e0b\uff0c\u6e38\u8fdb\u5979\u7684\u53cc\u817f\u4e4b\u95f4\uff0c\u6307\u5c16\u5df2\u7ecf\u4f20\u6765\u6e7f\u6da6\u6ed1\u817b\u7684\u611f\u89c9\u3002</p><p>\u201c\u6e7f\u900f\u4e86\u5462\u3002\u201d\u4ed6\u53d6\u7b11\u7740\uff0c\u624b\u6307\u5df2\u7ecf\u719f\u7ec3\u5730\u627e\u5230\u5730\u65b9\uff0c\u5f00\u59cb\u5feb\u901f\u5de5\u4f5c\u3002\u5bf9\u4e8e\u4ed6\u800c\u8a00\uff0c\u8299\u857e\u72c4\u7684\u8eab\u4f53\u592a\u719f\u6089\u4e86\uff0c\u6beb\u65e0\u534a\u70b9\u79d8\u5bc6\u53ef\u8a00\uff0c\u4ed6\u6e05\u695a\u5730\u77e5\u9053\u5c0f\u5973\u5b69\u8eab\u4e0a\u6bcf\u4e00\u5904\u654f\u611f\u70b9\u6240\u5728\uff0c\u4e5f\u77e5\u9053\u600e\u6837\u80fd\u4ee5\u6700\u5feb\u901f\u5ea6\u8ba9\u5979\u8fbe\u5230\u6ee1\u8db3\u3002</p><p>\u867d\u7136\u540c\u6837\u7684\u573a\u666f\uff0c\u4ee5\u524d\u66fe\u7ecf\u53d1\u751f\u8fc7\u65e0\u6570\u6b21\uff0c\u8299\u857e\u72c4\u4f9d\u65e7\u7f9e\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u5f85\u8981\u6323\u624e\uff0c\u5374\u53c8\u88ab\u538b\u4f4f\u4e86\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u53ea\u662f\u547c\u5438\u8d8a\u6765\u8d8a\u6025\u4fc3\uff0c\u8d1d\u9f7f\u54ac\u7740\u5634\u5507\uff0c\u6781\u529b\u60f3\u8981\u4e0d\u53d1\u51fa\u58f0\u97f3\uff0c\u5374\u7ec8\u7a76\u6291\u5236\u4e0d\u4f4f\uff0c\u4f4e\u6c89\u7684\u547b\u541f\u58f0\u4ece\u54bd\u5589\u91cc\u6324\u51fa\u6765\uff0c\u65ad\u65ad\u7eed\u7eed\uff0c\u542c\u8d77\u6765\u53cd\u800c\u683c\u5916\u7f8e\u5999\u9500\u9b42\u3002\u8fc7\u4e0d\u591a\u65f6\uff0c\u5979\u7684\u8eab\u4f53\u731b\u7136\u7ef7\u7d27\uff0c\u8170\u8179\u633a\u8d77\uff0c\u6574\u4e2a\u4eba\u5728\u5e8a\u4e0a\u53cd\u5f13\u8d77\u6765\uff0c\u63a5\u7740\u8f6f\u8f6f\u843d\u4e0b\uff0c\u4eff\u4f5b\u521a\u521a\u7ecf\u5386\u4e86\u4e00\u573a\u5927\u6218\u4f3c\uff0c\u7b4b\u75b2\u529b\u5c3d\uff0c\u4e1d\u6beb\u4e0d\u80fd\u52a8\u5f39\uff0c\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\uff0c\u53ea\u662f\u5598\u606f\u7740\uff0c\u8eab\u4e0a\u5fae\u5fae\u6e17\u51fa\u6c57\u6765\u3002</p><p>\u743c\u6069\u5c06\u53f3\u624b\u7f13\u7f13\u62bd\u51fa\uff0c\u5728\u5979\u8138\u4e0a\u8f7b\u8f7b\u634f\u4e86\u634f\uff0c\u5374\u5c06\u98df\u6307\u548c\u4e2d\u6307\u653e\u5728\u5979\u5507\u8fb9\uff0c\u90a3\u4e24\u6839\u624b\u6307\u521a\u521a\u8fdb\u5165\u8fc7\u5979\u7684\u8eab\u4f53\uff0c\u6b64\u65f6\u4e0a\u9762\u8fd8\u6cbe\u6ee1\u4e86\u900f\u660e\u6db2\u4f53\u3002\u8299\u857e\u72c4\u5a07\u7f9e\u5730\u671d\u4ed6\u770b\u4e86\u773c\uff0c\u5f20\u53e3\u5c06\u624b\u6307\u542b\u4e86\u8fdb\u53bb\uff0c\u6162\u6162\u542e\u5438\u3002</p><p>\u201c\u521a\u624d\u6cc4\u51fa\u5f88\u591a\u5462\uff0c\u5c31\u50cf\u6d2a\u6c34\u6cdb\u6ee5\u4f3c\u7684\uff0c\u201d\u743c\u6069\u53d6\u7b11\u5979\uff0c\u201c\u4ec0\u4e48\u5473\u9053\u3002\u201d</p><p>\u5c0f\u5973\u5b69\u55d4\u6012\u5730\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u4e0d\u80af\u56de\u7b54\uff0c\u8138\u4e0a\u795e\u60c5\u5374\u662f\u5a07\u5a9a\u65e0\u6bd4\uff0c\u770b\u5f97\u743c\u6069\u6b32\u5ff5\u5927\u76db\u3002\u201c\u559c\u6b22\u5417\uff1f\u201d</p><p>\u201c\u559c\u6b22\u3002\u201d\u5979\u4f4e\u58f0\u8bf4\u3002</p><p>\u201c\u6211\u4e0d\u5728\u8eab\u8fb9\u7684\u65f6\u5019\uff0c\u6709\u6ca1\u6709\u81ea\u5df1\u5582\u8fc7\u81ea\u5df1\u554a\u3002\u201d</p><p>\u201c\u6ca1\u6709\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d\u743c\u6069\u4e0d\u76f8\u4fe1\uff0c\u201c\u4e0d\u51c6\u8bf4\u8c0e\u54e6\uff0c\u4e0d\u7136\u6211\u4f1a\u751f\u6c14\u7684\u3002\u201d</p><p>\u201c\u6709\u2026\u2026\u4e00\u5f00\u59cb\u6709\u51e0\u6b21\uff0c\u201d\u8299\u857e\u72c4\u541e\u541e\u5410\u5410\u7684\uff0c\u201c\u540e\u6765\u5c31\u6ca1\u4e86\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u4e0d\u7ee7\u7eed\u5462\u3002\u201d</p><p>\u201c\u5c31\u662f\u2026\u2026\u90a3\u4e2a\uff0c\u53cd\u6b63\u548c\u4f60\u2026\u2026\u4e0d\u4e00\u6837\u7684\uff0c\u4e00\u70b9\u597d\u7684\u611f\u89c9\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u201c\u90a3\u73b0\u5728\u6ee1\u8db3\u4e86\uff0c\u8be5\u7761\u4e86\u5427\u3002\u201d\u743c\u6069\u6545\u610f\u8bf4\u3002</p><p>\u201c\u4e0d\u8981\uff0c\u201d\u8299\u857e\u72c4\u4fe1\u4ee5\u4e3a\u771f\uff0c\u7740\u6025\u8d77\u6765\uff0c\u201c\u6211\u2026\u2026\u6211\u60f3\u8981\u4f60\u2026\u2026\u201d</p><p>\u201c\u8981\u6211\u4ec0\u4e48\u554a\uff1f\u201d\u743c\u6069\u6545\u4f5c\u4e0d\u89e3\uff0c\u201c\u8bf4\u660e\u767d\u4e00\u4e9b\u554a\u3002\u201d</p><p>\u5c0f\u5973\u5b69\u54ac\u7740\u4e0b\u5507\uff0c\u8138\u4e0a\u53d1\u70e7\u5f97\u5389\u5bb3\uff0c\u201c\u6c42\u4f60\u5566\u2026\u2026\u201d\u5979\u6073\u6c42\u7740\uff0c\u201c\u4eba\u5bb6\u4f1a\u7f9e\u6b7b\u7684\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u5c31\u559c\u6b22\u770b\u4f60\u5bb3\u7f9e\u7684\u6837\u5b50\u554a\u3002\u201d\u4ed6\u8bf4\uff0c\u5750\u5728\u5e8a\u5934\uff0c\u8f7b\u8f7b\u62cd\u4e86\u62cd\u5979\u7684\u8111\u888b\uff0c\u8299\u857e\u72c4\u677e\u4e86\u53e3\u6c14\uff0c\u805a\u96c6\u521a\u521a\u6062\u590d\u7684\u4e00\u70b9\u529b\u6c14\u722c\u8d77\u8eab\u6765\uff0c\u8dea\u5728\u7537\u4eba\u817f\u95f4\uff0c\u4f4e\u5934\u5f20\u5f00\u6a31\u5507\uff0c\u5410\u51fa\u67d4\u8f6f\u9999\u820c\u4ed4\u7ec6\u670d\u4f8d\u3002</p><p>\u201c\u597d\u50cf\u6280\u5de7\u6ca1\u6709\u9000\u6b65\u5462\u3002\u201d\u743c\u6069\u4e00\u8fb9\u4eab\u53d7\u4e00\u8fb9\u8bc4\u4ef7\u3002</p><p>\u201c\u56e0\u4e3a\u6211\u603b\u5728\u5fc3\u91cc\u6e29\u4e60\u554a\u3002\u201d\u5c0f\u5973\u5b69\u9a84\u50b2\u5730\u56de\u7b54\u3002</p><p>\u8fc7\u4e86\u7247\u523b\uff0c\u743c\u6069\u611f\u89c9\u81ea\u5df1\u90a3\u5bb6\u4f19\u5df2\u7ecf\u81a8\u80c0\u5230\u6781\u9650\uff0c\u4fbf\u5c06\u8299\u857e\u72c4\u62b1\u4e86\u8fc7\u6765\uff0c\u8ba9\u5979\u5e73\u8eba\u7740\uff0c\u81ea\u5df1\u538b\u5728\u8eab\u4e0a\uff0c\u5206\u5f00\u7389\u817f\uff0c\u51c6\u5907\u633a\u8170\u8fdb\u5165\u3002\u8299\u857e\u72c4\u95ed\u7740\u773c\u775b\uff0c\u6b63\u6709\u4e9b\u5fd0\u5fd1\u5730\u671f\u5f85\u7740\uff0c\u5fc3\u4e2d\u53c8\u662f\u6fc0\u52a8\u53c8\u662f\u751c\u871c\uff0c\u7a81\u7136\u611f\u89c9\u81ea\u5df1\u7684\u53cc\u81c2\u62ac\u8d77\uff0c\u4eff\u4f5b\u5168\u7136\u4e0d\u53d7\u63a7\u5236\u4f3c\u7684\uff0c\u8eab\u4e0d\u7531\u5df1\u5730\u7528\u529b\u671d\u8eab\u4e0a\u7684\u743c\u6069\u4e00\u63a8\u3002</p><p>\u743c\u6069\u731d\u4e0d\u53ca\u9632\u4e4b\u4e0b\uff0c\u88ab\u63a8\u5f97\u7ffb\u4e86\u4e2a\u8eab\uff0c\u6070\u597d\u53c8\u5728\u5e8a\u8fb9\uff0c\u7830\u5730\u4e00\u58f0\u6454\u5230\u5730\u4e0a\u53bb\u4e86\u3002\u8299\u857e\u72c4\u614c\u5fd9\u8d77\u8eab\u4e0b\u5e8a\uff0c\u5c06\u4ed6\u6276\u4e86\u8d77\u6765\uff0c\u201c\u5bf9\u4e0d\u8d77\u2026\u2026\u201d\u5979\u60f6\u6025\u5730\u5206\u8fa9\u7740\uff0c\u201c\u521a\u624d\u6211\u4e0d\u662f\u6709\u610f\u2026\u2026\u201d</p><p>\u743c\u6069\u7565\u7565\u5b9a\u795e\uff0c\u5df2\u7ecf\u660e\u767d\u4e86\u662f\u600e\u4e48\u56de\u4e8b\u3002\u8299\u857e\u72c4\u51b3\u4e0d\u4f1a\u6545\u610f\u63a8\u4ed6\uff0c\u4f55\u51b5\u521a\u624d\u90a3\u4e00\u4e0b\u529b\u6c14\u751a\u5927\uff0c\u4e5f\u7edd\u4e0d\u662f\u5979\u80fd\u529e\u5f97\u5230\u7684\u3002\u201c\u662f\u4f60\u59d0\u59d0\u5427\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5979\u9192\u4e86\uff1f\u201d</p><p>\u8299\u857e\u72c4\u6014\u4e86\u4e00\u6014\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u662f\uff0c\u5979\u7a81\u7136\u9192\u4e86\u2026\u2026\u201d\u5979\u72b9\u8c6b\u7740\uff0c\u201c\u5979\u8ba9\u6211\u9a6c\u4e0a\u56de\u53bb\u2026\u2026\u201d</p><p>\u743c\u6069\u54fc\u4e86\u4e00\u58f0\uff0c\u4ed6\u521a\u624d\u7a81\u7136\u88ab\u4ece\u5e8a\u4e0a\u63a8\u4e0b\uff0c\u6454\u5f97\u810a\u80cc\u751f\u75bc\uff0c\u5fc3\u5934\u706b\u8d77\u3002\u5018\u82e5\u662f\u5e73\u65f6\uff0c\u6216\u8bb8\u4e0d\u4e3a\u5df1\u751a\uff0c\u4e5f\u5c31\u7b97\u4e86\uff0c\u5982\u4eca\u5374\u6539\u4e86\u4e3b\u610f\u3002\u76f4\u63a5\u62e6\u8170\u5c06\u5979\u62b1\u4e86\u8d77\u6765\uff0c\u653e\u5728\u5e8a\u4e0a\uff0c\u201c\u4e0d\u7ba1\u5979\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u53cd\u6b63\u53c8\u4e0d\u662f\u7b2c\u4e00\u6b21\u4e86\u3002\u201d</p><p>\u201c\u53ef\u662f\u2026\u2026\u201d</p><p>\u201c\u4e0d\u662f\u4f60\u8bf4\u7684\u4e48\uff0c\u603b\u4e0d\u80fd\u56e0\u4e3a\u5979\uff0c\u4f60\u5c31\u4e00\u8f88\u5b50\u4e0d\u80fd\u548c\u559c\u6b22\u7684\u7537\u4eba\u4ea4\u5408\u5427\u3002\u4f55\u51b5\u5c06\u6765\u5979\u5ac1\u4eba\u4e86\uff0c\u4f60\u4e0d\u4e5f\u662f\u4e00\u6837\u2026\u2026\u201d</p><p>\u8bf4\u5230\u8fd9\u91cc\uff0c\u743c\u6069\u7a81\u7136\u6014\u4f4f\uff0c\u4ed6\u5012\u662f\u4e00\u76f4\u5fd8\u4e86\u8fd9\u4ef6\u4e8b\u3002\u65e2\u7136\u8fd9\u53cc\u80de\u80ce\u59d0\u59b9\u5fc3\u7075\u611f\u5e94\uff0c\u81ea\u5df1\u548c\u8299\u857e\u72c4\u6b22\u597d\u7684\u65f6\u5019\uff0c\u8299\u8389\u5a05\u4f1a\u611f\u540c\u8eab\u53d7\uff0c\u7b49\u4e8e\u662f\u88ab\u81ea\u5df1\u7ed9\u5f3a\u66b4\u4e86\u3002\u90a3\u5c06\u6765\u8299\u8389\u5a05\u51fa\u5ac1\uff0c\u548c\u5979\u4e08\u592b\u4e0a\u5e8a\uff0c\u8299\u857e\u72c4\u5c82\u4e0d\u7b49\u4e8e\u4e5f\u662f\u8981\u540c\u65f6\u88ab\u4eba\u4eab\u7528\u4e86\u3002</p><p>\u5c82\u6709\u6b64\u7406\uff01</p><p>\u743c\u6069\u60f3\u901a\u6b64\u8282\uff0c\u5012\u662f\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u5e78\u597d\u8299\u8389\u5a05\u770b\u4e0d\u4e0a\u5e93\u80af\u90a3\u5bb6\u4f19\uff0c\u9003\u5a5a\u51fa\u6765\uff0c\u5426\u5219\u7684\u8bdd\u2026\u2026\u4e0d\u884c\uff0c\u5fc5\u987b\u963b\u6b62\u8fd9\u4ef6\u4e8b\u3002</p><p>\u7d22\u6027\u628a\u8299\u8389\u5a05\u4e5f\u5f04\u4e0a\u624b\u5f97\u4e86\uff0c\u743c\u6069\u8f6c\u4e86\u51e0\u4e2a\u5ff5\u5934\uff0c\u5fc3\u60f3\uff0c\u548c\u8fd9\u5bf9\u6f02\u4eae\u53cc\u80de\u80ce\u59d0\u59b9\u4fe9\u4e00\u8d77\u73a9\u53cc\u98de\uff0c\u5012\u4e5f\u662f\u4eba\u751f\u4e00\u5927\u5feb\u4e8b\u3002</p><p>\u597d\u5728\u5982\u4eca\u5927\u5bb6\u5728\u8fd9\u74dc\u7406\u5fb7\u65af\u57ce\uff0c\u8ddd\u79bb\u9634\u9b42\u57ce\u76f8\u9694\u4e07\u91cc\uff0c\u8fd9\u4e8b\u4e5f\u4e0d\u6025\u4e8e\u4e00\u65f6\uff0c\u773c\u4e0b\u5148\u628a\u59b9\u59b9\u5403\u4e86\uff0c\u987a\u4fbf\u4e5f\u5f00\u53d1\u4e00\u4e0b\u9694\u58c1\u7684\u59d0\u59d0\u518d\u8bf4\u3002</p><p>\u201c\u53ef\u662f\uff0c\u201d\u8299\u857e\u72c4\u6084\u58f0\u5206\u8fa9\uff0c\u201c\u4ee5\u524d\u8ddd\u79bb\u8fdc\uff0c\u5979\u611f\u5e94\u4e0d\u5f3a\u3002\u73b0\u5728\u5979\u5c31\u5728\u9694\u58c1\uff0c\u8fd9\u4e48\u8fd1\u7684\u8ddd\u79bb\u2026\u2026\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u5374\u4e0d\u7406\u5979\uff0c\u4f38\u624b\u53d6\u8fc7\u5979\u8131\u4e0b\u6765\u7684\u7761\u8863\uff0c\u62e7\u6210\u7ef3\u7d22\uff0c\u5c06\u5979\u53cc\u81c2\u90fd\u7ed1\u4f4f\u4e86\uff0c\u201c\u4ee5\u524d\u6709\u6ca1\u6709\u8ddf\u4f60\u8bf4\u8fc7\u6709\u4e00\u79cd\u60c5\u8da3\u6e38\u620f\u53eb\u505a\u6346\u7ed1\uff1f\u201d\u4ed6\u4eb2\u7740\u5979\u7684\u8138\u988a\uff0c\u201c\u4eca\u665a\u5c31\u8ba9\u4f60\u5c1d\u5c1d\u6ecb\u5473\u2014\u2014\u987a\u4fbf\u8ba9\u4f60\u59d0\u59d0\u4e5f\u4f53\u9a8c\u4e00\u4e0b\uff0c\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u522b\uff0c\u743c\u6069\u2026\u2026\u201d</p><p>\u201c\u8fd9\u662f\u4f60\u81ea\u5df1\u8981\u6c42\u7684\u751f\u65e5\u793c\u7269\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u51b7\u7b11\u8d77\u6765\uff0c\u201c\u5bf9\u4e86\uff0c\u4f60\u4eec\u662f\u53cc\u80de\u80ce\uff0c\u90a3\u4eca\u5929\u4e5f\u662f\u5979\u7684\u751f\u65e5\u2026\u2026\u6b63\u597d\uff0c\u5c31\u8ba9\u5979\u6e05\u695a\u4f53\u9a8c\u4e00\u4e0b\u7537\u4eba\u7684\u6ecb\u5473\u5427\u3002\u201d</p><p>\u8299\u857e\u72c4\u89c1\u4ed6\u795e\u8272\uff0c\u77e5\u9053\u5df2\u7ecf\u52a8\u4e86\u6012\uff0c\u4e00\u65f6\u5fc3\u5934\u7eb7\u4e71\u5982\u9ebb\u3002\u5979\u8fd9\u51e0\u4e2a\u6708\u6765\u671d\u601d\u66ae\u60f3\uff0c\u76fc\u7740\u80fd\u548c\u743c\u6069\u91cd\u6e29\u65e7\u68a6\uff0c\u73b0\u5728\u597d\u4e0d\u5bb9\u6613\u6709\u4e86\u673a\u4f1a\uff0c\u81ea\u7136\u4e0d\u80af\u8f7b\u6613\u653e\u5f03\u3002\u5979\u4e8b\u5148\u5bf9\u8299\u8389\u5a05\u4e0b\u4e86\u7761\u7720\u672f\uff0c\u81ea\u4ee5\u4e3a\u5982\u6b64\u4e00\u6765\u4fbf\u5b89\u6392\u59a5\u5f53\uff0c\u6ca1\u60f3\u5230\u59d0\u59d0\u7a81\u7136\u9192\u4e86\u3002\u5982\u4eca\u4e8b\u60c5\u5f04\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u5979\u672c\u5c31\u4e0d\u64c5\u957f\u6025\u667a\uff0c\u4e5f\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u662f\u597d\uff0c\u5018\u82e5\u4e56\u4e56\u987a\u4ece\u743c\u6069\uff0c\u90a3\u52bf\u5fc5\u8981\u59d4\u5c48\u59d0\u59d0\uff1b\u4f46\u82e5\u662f\u8981\u62d2\u7edd\u743c\u6069\uff0c\u5979\u5374\u53c8\u820d\u4e0d\u5f97\u3002\u6b63\u5f77\u5fa8\u65e0\u63aa\uff0c\u743c\u6069\u4f4e\u5934\uff0c\u5728\u5979\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u4e86\u53e5\u8bdd\u3002</p><p>\u201c\u542c\u8bdd\uff0c\u8299\u857e\u72c4\uff0c\u201d\u4ed6\u6e29\u67d4\u5730\u8bf4\uff0c\u201c\u6211\u559c\u6b22\u6211\u7684\u5973\u4eba\u4e56\u4e56\u7684\u3002\u201d</p><p>\u8299\u857e\u72c4\u539f\u672c\u8fd8\u6709\u51e0\u5206\u72b9\u8c6b\uff0c\u542c\u4e86\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u987f\u65f6\u529b\u6c14\u5168\u6d88\uff0c\u4e00\u65f6\u95f4\u8111\u4e2d\u4ec0\u4e48\u5ff5\u5934\u90fd\u6ca1\u6709\uff0c\u95ed\u7740\u773c\u775b\u518d\u4e0d\u62b5\u6297\uff0c\u4efb\u7531\u743c\u6069\u5c06\u5979\u53cc\u817f\u5206\u5f00\uff0c\u4e00\u70b9\u4e00\u70b9\u4fb5\u5165\u4f53\u5185\u3002</p></article>"}}}]);
