(window.webpackJsonp=window.webpackJsonp||[]).push([[1455],{1478:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516b\u8282 \u5fc3\u8bed\u8005",content:"<article><h2>\u7b2c\u5341\u516b\u8282 \u5fc3\u8bed\u8005</h2><p>\u5728\u4e00\u5ea7\u4ee5\u7f57\u4e1d\u4e3a\u81f3\u9ad8\u4fe1\u4ef0\u7684\u5353\u5c14\u57ce\u5e02\u91cc\uff0c\u5973\u6027\u9ed1\u6697\u7cbe\u7075\u57fa\u672c\u6ca1\u6709\u5356\u8eab\u7684\u5fc5\u8981\uff0c\u56e0\u4e3a\u5979\u4eec\u62e5\u6709\u795e\u540e\u7684\u504f\u7231\u548c\u6069\u5ba0\u3002\u6240\u6709\u7684\u8d35\u65cf\u5973\u6027\u90fd\u662f\u5929\u7136\u7684\u7267\u5e08\u5019\u9009\u4eba\uff0c\u524d\u63d0\u662f\u5979\u4eec\u80fd\u591f\u901a\u8fc7\u4e25\u82db\u7684\u796d\u53f8\u8bad\u7ec3\u3002\u5e73\u6c11\u8eab\u4efd\u7684\u5353\u5c14\u5973\u6027\u540c\u6837\u4e5f\u5b8c\u5168\u6709\u53ef\u80fd\u6210\u4e3a\u795e\u540e\u7267\u5e08\uff0c\u5982\u679c\u5979\u4eec\u8db3\u591f\u8654\u8bda\uff0c\u5e76\u4e14\u62e5\u6709\u503c\u5f97\u5173\u6ce8\u7684\u5929\u8d4b\u7684\u8bdd\u2014\u2014\u901a\u5e38\u60c5\u51b5\u4e0b\uff0c\u5979\u4eec\u4f1a\u5f88\u5feb\u88ab\u67d0\u4e2a\u5bb6\u65cf\u7684\u4e3b\u6bcd\u6536\u517b\uff0c\u4ece\u6b64\u6446\u8131\u5e73\u6c11\u8eab\u4efd\uff0c\u8dfb\u8eab\u8d35\u65cf\u4e00\u5458\u3002\u4e5f\u6709\u5c11\u6570\u5973\u6027\u9009\u62e9\u5200\u5251\uff0c\u6210\u4e3a\u4e0d\u900a\u8272\u4e8e\u7537\u6027\u7684\u5f3a\u608d\u6218\u58eb\uff0c\u751a\u81f3\u53ef\u80fd\u62c5\u4efb\u5bb6\u65cf\u6b66\u6280\u957f\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u4e5f\u6709\u5f88\u591a\u5e73\u6c11\u51fa\u8eab\u7684\u5973\u6027\u6210\u4e3a\u201c\u7f57\u4e1d\u6bd2\u523a\u201d\uff0c\u5979\u4eec\u662f\u6740\u624b\u548c\u7a83\u8d3c\uff0c\u9690\u6ca1\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u4e3a\u795e\u540e\u548c\u4e3b\u6bcd\u4eec\u6548\u52b3\uff0c\u6d88\u706d\u5979\u4eec\u7684\u654c\u4eba\u3002\u603b\u4f53\u800c\u8a00\uff0c\u5973\u6027\u5728\u8fd9\u4e2a\u793e\u4f1a\u4e2d\u62e5\u6709\u5929\u7136\u7684\u4f18\u8d8a\u5730\u4f4d\uff0c\u53d7\u4eba\u656c\u754f\uff0c\u5f88\u96be\u60f3\u50cf\u5979\u4eec\u4f1a\u6ca6\u843d\u5230\u9760\u51fa\u5356\u8eab\u4f53\u4e3a\u751f\u3002</p><p>\u4e0d\u8fc7\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u603b\u662f\u6709\u4f8b\u5916\u7684\u3002</p><p>\u5993\u9662\u8fd9\u79cd\u4e1c\u897f\uff0c\u65e0\u8bba\u5728\u4ec0\u4e48\u4e16\u754c\u90fd\u6709\u2014\u2014\u4e0d\u7ba1\u662f\u5730\u7403\u8fd8\u662f\u8d39\u4f26\uff0c\u662f\u4eba\u7c7b\u793e\u4f1a\u8fd8\u662f\u5353\u5c14\u57ce\u5e02\u2014\u2014\u6700\u591a\u6362\u4e2a\u540d\u5b57\u7f62\u4e86\u3002\u4e0d\u77e5\u4ec0\u4e48\u7f18\u6545\uff0c\u9ed1\u6697\u7cbe\u7075\u7684\u6027\u522b\u7ed3\u6784\u6709\u70b9\u5947\u602a\uff0c\u751f\u80b2\u7537\u6027\u7684\u51e0\u7387\u660e\u663e\u8d85\u8fc7\u5973\u6027\uff0c\u8fd9\u4e5f\u662f\u4e3a\u4ec0\u4e48\u83f2\u5c14\u4f26\u4e3b\u6bcd\u8fde\u751f\u4e03\u4e2a\u5973\u513f\uff0c\u88ab\u8ba4\u4e3a\u662f\u795e\u540e\u6069\u5ba0\u7684\u7f18\u6545\u3002\u65e2\u7136\u7537\u5973\u6bd4\u4f8b\u5982\u6b64\u4e0d\u534f\u8c03\uff0c\u67d0\u4e9b\u5a31\u4e50\u573a\u6240\u5c31\u81ea\u7136\u5f88\u6709\u5fc5\u8981\u5b58\u5728\uff0c\u4ee5\u4fbf\u7f13\u89e3\u538b\u529b\uff0c\u53d1\u6cc4\u60c5\u7eea\uff0c\u6709\u5229\u4e8e\u7edf\u6cbb\u7684\u7a33\u5b9a\u3002\u4f46\u8fd9\u4e9b\u5730\u65b9\u7684\u201c\u5546\u54c1\u201d\uff0c\u57fa\u672c\u90fd\u662f\u7cbe\u7075\u3001\u4eba\u7c7b\u751a\u81f3\u4f8f\u5112\u548c\u77ee\u4eba\u8fd9\u4e9b\u201c\u4f4e\u7b49\u79cd\u65cf\u201d\uff0c\u6765\u6e90\u662f\u6218\u4fd8\u6216\u8005\u5176\u4ed6\u5353\u5c14\u57ce\u5e02\u7684\u5974\u96b6\u5e02\u573a\u2014\u2014\u5076\u5c14\u6709\u51e0\u4e2a\u9ed1\u6697\u7cbe\u7075\u5973\u6027\uff0c\u4e5f\u662f\u5c5e\u4e8e\u8eab\u4f53\u5b71\u5f31\u751a\u81f3\u6709\u6b8b\u75be\uff0c\u76f8\u8c8c\u5e73\u5eb8\uff0c\u6ca1\u6709\u4efb\u4f55\u6280\u80fd\u548c\u4ef7\u503c\uff0c\u9664\u4e86\u51fa\u5356\u8089\u4f53\u522b\u65e0\u4ed6\u9014\u3002</p><p>\u800c\u6c89\u9ed8\u4e4b\u6b4c\u4e0e\u6b64\u4e0d\u540c\uff0c\u5b83\u4e0d\u5c51\u4e8e\u53bb\u7ecf\u8425\u8fd9\u4e9b\u201c\u52a3\u8d28\u54c1\u201d\uff0c\u800c\u662f\u806a\u660e\u5730\u5c06\u76ee\u5149\u6295\u5411\u9ed1\u6697\u7cbe\u7075\u8eab\u4e0a\u3002\u65e2\u7136\u8fd9\u662f\u4e00\u4e2a\u5973\u6743\u4e3b\u4e49\u7684\u793e\u4f1a\uff0c\u65e2\u7136\u7537\u6027\u4eec\u53d7\u5230\u666e\u904d\u7684\u538b\u8feb\u548c\u6b67\u89c6\uff0c\u6210\u5929\u88ab\u4e3b\u6bcd\u548c\u5973\u6027\u7267\u5e08\u4eec\u8dbe\u9ad8\u6c14\u626c\u5730\u5475\u65a5\u8d23\u9a82\uff0c\u751a\u81f3\u97ad\u7b1e\u6bb4\u6253\uff0c\u90a3\u4e48\u53cd\u8fc7\u6765\u8bf4\uff0c\u5982\u679c\u8ba9\u4e00\u4e2a\u7537\u6027\u6709\u673a\u4f1a\u51cc\u9a7e\u4e8e\u90a3\u4e9b\u539f\u672c\u9ad8\u8d35\u7684\u5973\u6027\u5353\u5c14\u4e4b\u4e0a\uff0c\u652f\u914d\u5974\u5f79\u5979\u2014\u2014\u54ea\u6015\u662f\u6682\u65f6\u7684\uff0c\u4ed6\u4e5f\u4e00\u5b9a\u5f88\u4e50\u610f\u4e3a\u6b64\u4ed8\u5927\u4ef7\u94b1\u3002</p><p>\u5fc5\u987b\u8bf4\uff0c\u6c89\u9ed8\u4e4b\u6b4c\u7684\u5e55\u540e\u8001\u677f\uff0c\u5f88\u6709\u5934\u8111\uff0c\u5584\u4e8e\u63e3\u6469\u5fc3\u7406\u3002\u5f53\u7136\uff0c\u4ec5\u4ec5\u6709\u8fd9\u4e9b\u8fd8\u4e0d\u591f\uff0c\u9053\u7406\u5f88\u591a\u4eba\u90fd\u660e\u767d\uff0c\u60f3\u6cd5\u5f88\u591a\u4eba\u90fd\u6709\uff0c\u4f46\u600e\u6837\u624d\u80fd\u529e\u5230\uff0c\u8fd9\u5c31\u662f\u5173\u952e\u6240\u5728\u4e86\u3002</p><p>\u743c\u6069\u5bf9\u6b64\u4e86\u89e3\u4e5f\u4e0d\u591a\uff0c\u53ea\u4ece\u7ef4\u5eb7\u5c3c\u4e9a\u90a3\u91cc\u83b7\u5f97\u4e00\u4e9b\u5927\u6982\u7684\u4fe1\u606f\u3002\u6c89\u9ed8\u4e4b\u6b4c\u7684\u201c\u5546\u54c1\u201d\u6765\u6e90\uff0c\u4e3b\u8981\u662f\u5bb6\u65cf\u5185\u6218\u2014\u2014\u8fd9\u5728\u5353\u5c14\u793e\u4f1a\u662f\u975e\u5e38\u5e38\u89c1\u7684\u2014\u2014\u4e2d\u7684\u5931\u8d25\u8005\u3002\u901a\u5e38\u60c5\u51b5\u4e0b\uff0c\u5185\u6218\u4e2d\u7684\u5931\u8d25\u8005\u4f1a\u88ab\u56da\u7981\u3001\u8be2\u95ee\uff0c\u5728\u69a8\u5e72\u6240\u6709\u4ef7\u503c\u540e\u5904\u6b7b\uff0c\u65a9\u8349\u9664\u6839\u662f\u5353\u5c14\u7684\u6807\u51c6\u98ce\u683c\u3002\u7136\u800c\u6c89\u9ed8\u4e4b\u6b4c\u7684\u5e55\u540e\u8001\u677f\u2014\u2014\u4ed6\u7684\u771f\u5b9e\u8eab\u4efd\u8fd8\u662f\u4e2a\u8ff7\uff0c\u8fde\u7ef4\u5eb7\u5c3c\u4e9a\u90fd\u4e0d\u77e5\u6653\u2014\u2014\u901a\u8fc7\u67d0\u4e9b\u4e0d\u4e3a\u4eba\u77e5\u7684\u624b\u6bb5\uff0c\u6216\u8bb8\u662f\u8d3f\u8d42\uff0c\u6216\u8bb8\u662f\u4ea4\u6613\uff0c\u6216\u8bb8\u662f\u5176\u4ed6\uff0c\u6084\u6084\u5c06\u4e00\u4e9b\u6218\u4fd8\u5077\u8fd0\u51fa\u6765\uff0c\u5e76\u4e14\u4ed6\u8bf4\u670d\u4e86\u6267\u653f\u8bae\u4f1a\u7684\u4e8c\u5341\u4e00\u4f4d\u4e3b\u6bcd\uff0c\u8ba9\u5979\u4eec\u9ed8\u8bb8\u8fd9\u79cd\u884c\u4e3a\uff0c\u867d\u7136\u8fd9\u5206\u660e\u662f\u5bf9\u5353\u5c14\u793e\u4f1a\u79e9\u5e8f\u548c\u5973\u6027\u7edf\u6cbb\u6743\u7684\u6311\u6218\u548c\u4eb5\u6e0e\u3002</p><p>\u8fd9\u5b9e\u5728\u5f88\u4e86\u4e0d\u8d77\u3002</p><p>\u743c\u6069\u8d70\u5230\u697c\u68af\u7684\u5c3d\u5934\uff0c\u770b\u7740\u773c\u524d\u7684\u957f\u5eca\uff0c\u7ad9\u5b9a\u811a\u6b65\u3002\u4e00\u4e2a\u7626\u524a\u7684\u4eba\u5f71\u6084\u65e0\u58f0\u606f\u5730\u4ece\u65c1\u8fb9\u7684\u89d2\u843d\u91cc\u8d70\u51fa\u6765\uff0c\u4ed6\u662f\u4e2a\u9ed1\u6697\u7cbe\u7075\uff0c\u6574\u4e2a\u4eba\u770b\u8d77\u6765\u5e72\u7ec3\u800c\u9510\u5229\uff0c\u5c31\u50cf\u4e00\u67c4\u5315\u9996\uff0c\u4f46\u5bbd\u9614\u7684\u8170\u5e26\u4e0a\u7ed1\u7740\u4e00\u6392\u5c0f\u53e3\u888b\uff0c\u7834\u574f\u4e86\u8fd9\u79cd\u611f\u89c9\u3002\u4ed6\u7528\u6709\u4e9b\u60ca\u5f02\u7684\u76ee\u5149\u4e0a\u4e0b\u6253\u91cf\u7740\u743c\u6069\uff0c\u201c\u4eba\u7c7b\uff1f\u201d\u4ed6\u5fcd\u4e0d\u4f4f\u95ee\u3002</p><p>\u743c\u6069\u8038\u4e86\u8038\u80a9\uff0c\u5c06\u624b\u4e2d\u7684\u94f6\u8272\u94ed\u724c\u5728\u4ed6\u773c\u524d\u6643\u4e86\u6643\u3002</p><p>\u9ed1\u6697\u7cbe\u7075\u95ed\u4e0a\u5634\uff0c\u4f38\u51fa\u624b\u6765\u3002\u743c\u6069\u4f9d\u7167\u6307\u793a\uff0c\u5c06\u90a3\u5757\u7528\u4e94\u679a\u91d1\u5e01\u6362\u6765\u7684\u94f6\u8272\u94ed\u724c\u653e\u5728\u4ed6\u624b\u4e2d\u3002</p><p>\u201c\u51e0\u53f7\u623f\u95f4\uff1f\u201d\u9ed1\u6697\u7cbe\u7075\u95ee\uff0c\u4ed6\u58f0\u97f3\u5636\u54d1\u3002</p><p>\u743c\u6069\u6545\u610f\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u201c\u6211\u7b2c\u4e00\u6b21\u6765\uff0c\u201d\u4ed6\u5766\u767d\u5730\u8bf4\uff0c\u201c\u6216\u8bb8\u4f60\u53ef\u4ee5\u7ed9\u6211\u70b9\u5efa\u8bae\u3002\u201d</p><p>\u201c\u5341\u56db\u53f7\uff0c\u83ab\u5170\u83f2\uff0c\u66fe\u7ecf\u662f\u67d0\u4e2a\u4e0d\u5b58\u5728\u7684\u5bb6\u65cf\u7684\u6b66\u6280\u957f\uff0c\u201d\u9ed1\u6697\u7cbe\u7075\u8bf4\uff0c\u201c\u8eab\u6750\u5f88\u597d\uff0c\u67d4\u97e7\u6027\u4e00\u6d41\u3002\u201d</p><p>\u743c\u6069\u4ece\u53e3\u888b\u91cc\u53d6\u51fa\u4e00\u679a\u91d1\u5e01\uff0c\u7528\u624b\u6307\u5f39\u4e86\u8d77\u6765\u3002\u91d1\u5e01\u5728\u7a7a\u4e2d\u5212\u51fa\u4e00\u9053\u5f27\u7ebf\uff0c\u51c6\u786e\u5730\u6389\u5728\u9ed1\u6697\u7cbe\u7075\u624b\u4e2d\u3002</p><p>\u201c\u6211\u60f3\u4f60\u6709\u66f4\u597d\u7684\u5efa\u8bae\u3002\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u9ed1\u6697\u7cbe\u7075\u6536\u8d77\u91d1\u5e01\uff0c\u201c\u5341\u4e03\u53f7\uff0c\u8fbe\u8d6b\u59ae\uff0c\u5979\u66fe\u7ecf\u662f\u67d0\u4e2a\u5bb6\u65cf\u7684\u957f\u5973\uff0c\u73b0\u5728\u662f\u6211\u4eec\u8fd9\u91cc\u6700\u53d7\u6b22\u8fce\u7684\u7f8e\u4eba\uff0c\u800c\u4e14\u6070\u597d\u5979\u73b0\u5728\u6709\u7a7a\u3002\u201d</p><p>\u201c\u68d2\u6781\u4e86\u3002\u201d\u743c\u6069\u8bf4\uff0c\u53c8\u4e22\u4e86\u4e00\u679a\u91d1\u5e01\u7ed9\u4ed6\u3002\u9ed1\u6697\u7cbe\u7075\u8eac\u8eab\u9053\u8c22\uff0c\u4ece\u8170\u95f4\u53e3\u888b\u91cc\u53d6\u51fa\u4e00\u67c4\u94dc\u94a5\u5319\u3002\u201c\u9601\u4e0b\u6709\u5176\u4ed6\u7279\u522b\u9700\u6c42\u4e48\uff1f\u201d\u4ed6\u6bb7\u52e4\u5730\u95ee\u3002</p><p>\u201c\u7279\u522b\u9700\u6c42\uff1f\u201d\u743c\u6069\u6709\u4e9b\u6115\u7136\uff0c\u4e0d\u592a\u660e\u767d\u5bf9\u65b9\u7684\u610f\u601d\u3002</p><p>\u201c\u662f\u7684\uff0c\u6bd4\u5982\u76ae\u97ad\u3001\u624b\u94d0\uff0c\u6216\u8005\u7ef3\u7d22\uff1f\u201d\u9ed1\u6697\u7cbe\u7075\u5982\u6570\u5bb6\u73cd\uff0c\u201c\u675f\u53e3\u5668\u4e5f\u53ef\u4ee5\u63d0\u4f9b\uff0c\u4e0d\u8fc7\u5ba2\u4eba\u4eec\u90fd\u6bd4\u8f83\u559c\u6b22\u542c\u5979\u7684\u53eb\u58f0\uff1b\u6216\u8005\u6307\u5939\u2026\u2026\u5f53\u7136\u6211\u4eec\u6709\u89c4\u77e9\uff0c\u4e0d\u80fd\u7559\u4e0b\u65e0\u6cd5\u6cbb\u6108\u7684\u4f24\u75d5\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u6026\u7136\u5fc3\u52a8\uff0c\u4ed6\u5012\u8fd8\u4ece\u6765\u6ca1\u5c1d\u8bd5\u8fc7\u8fd9\u4e9b\uff0c\u4e0d\u8fc7\u8f6c\u5ff5\u4e00\u60f3\uff0c\u8fd8\u662f\u653e\u5f03\u4e86\u8fd9\u4e2a\u5ff5\u5934\u3002\u201c\u4e0d\u5fc5\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u591a\u8c22\u3002\u201d</p><p>\u9ed1\u6697\u7cbe\u7075\u8eac\u8eab\u544a\u9000\uff0c\u8eab\u5f71\u77ac\u95f4\u6d88\u5931\u5728\u9634\u5f71\u4e4b\u4e2d\uff0c\u743c\u6069\u62ff\u7740\u94a5\u5319\uff0c\u987a\u7740\u957f\u5eca\u5411\u524d\u8d70\u53bb\uff0c\u5728\u5341\u4e03\u53f7\u623f\u95f4\u95e8\u53e3\u505c\u4e0b\uff0c\u7b49\u4e86\u4e24\u79d2\u949f\uff0c\u7136\u540e\u63a8\u95e8\u8fdb\u5165\u3002</p><p>\u4e00\u4e2a\u5973\u6027\u5353\u5c14\u6b63\u5750\u5728\u5e8a\u5934\uff0c\u8116\u9888\u4e0a\u5957\u7740\u4e00\u4e2a\u6697\u91d1\u8272\u7684\u9879\u5708\uff0c\u7cfb\u7740\u94f6\u94fe\uff0c\u53e6\u4e00\u7aef\u8054\u7ed3\u7740\u5e8a\u5934\u7684\u94c1\u680f\u6746\u4e0a\uff0c\u8ba9\u5979\u65e0\u6cd5\u9003\u8131\u3002\u5979\u8eab\u4e0a\u8fd8\u7a7f\u7740\u7d2b\u9ed1\u8272\u7684\u5e03\u6ee1\u86db\u7f51\u56fe\u6848\u7684\u796d\u53f8\u670d\uff0c\u4f69\u6234\u7740\u5370\u6709\u5bb6\u65cf\u5fbd\u8bb0\u7684\u80f8\u7ae0\uff0c\u4f46\u888d\u5b50\u4e0b\u9762\u660e\u663e\u771f\u7a7a\u3002\u5e8a\u8fb9\u6446\u653e\u7740\u4e00\u67c4\u86c7\u9996\u97ad\u2014\u2014\u5f53\u7136\u662f\u4eff\u5236\u54c1\uff0c\u4e94\u9897\u86c7\u9996\u662f\u7528\u67d4\u97e7\u7684\u6d1b\u65af\u517d\u76ae\u5236\u6210\u7684\uff0c\u743c\u6069\u731c\u6d4b\u5b83\u62bd\u6253\u5728\u8eab\u4e0a\u4e00\u5b9a\u5f88\u75bc\u3002\u5979\u7684\u8138\u4e0a\u6709\u51e0\u9053\u4e0d\u592a\u660e\u663e\u7684\u4f24\u75d5\uff0c\u53f3\u8fb9\u773c\u7736\u5fae\u5fae\u9752\u80bf\uff0c\u4f30\u8ba1\u662f\u4ee5\u524d\u7684\u5ba2\u4eba\u7559\u4e0b\u6765\u7684\uff0c\u4f46\u4f9d\u65e7\u80fd\u770b\u51fa\u662f\u4e2a\u7f8e\u4eba\u3002</p><p>\u542c\u89c1\u52a8\u9759\uff0c\u5979\u62ac\u5934\u671d\u743c\u6069\u770b\u4e86\u4e00\u773c\uff0c\u5fae\u5fae\u6014\u4e86\u4e00\u6014\uff0c\u4eff\u4f5b\u662f\u8be7\u5f02\u4e8e\u6765\u8005\u5c45\u7136\u662f\u4e2a\u4eba\u7c7b\uff0c\u4f46\u968f\u5373\u9762\u8272\u6062\u590d\u5e73\u9759\uff0c\u4f9d\u65e7\u4fdd\u6301\u539f\u672c\u7684\u59ff\u52bf\uff0c\u4eff\u4f5b\u5bf9\u5916\u754c\u7684\u4e00\u5207\u90fd\u5df2\u7ecf\u611f\u89c9\u9ebb\u6728\u3002\u743c\u6069\u8d70\u8fdb\u623f\u95f4\uff0c\u53cd\u624b\u5173\u4e0a\u95e8\uff0c\u5750\u5728\u5e8a\u8fb9\u7684\u6c99\u53d1\u4e0a\uff0c\u201c\u8fbe\u8d6b\u59ae\u5c0f\u59d0\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u8fbe\u8d6b\u59ae\u4fdd\u6301\u6c89\u9ed8\uff0c\u4e0d\u5c51\u4e8e\u56de\u7b54\u3002</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u542c\u8bf4\u60a8\u66fe\u7ecf\u662f\u8fbe\u8d6b\u59ae\u5bb6\u65cf\u7684\u957f\u5973\uff1f\u201d</p><p>\u201c\u4ece\u6765\u5c31\u4e0d\u5b58\u5728\u8fbe\u8d6b\u59ae\u5bb6\u65cf\u3002\u201d\u5bf9\u65b9\u56de\u7b54\u3002</p><p>\u201c\u6211\u660e\u767d\u3002\u201d\u743c\u6069\u7406\u89e3\u5730\u70b9\u70b9\u5934\uff0c\u5353\u5c14\u7684\u89c4\u77e9\u5c31\u662f\u5982\u6b64\uff0c\u5185\u6218\u4e2d\u5931\u8d25\u7684\u5bb6\u65cf\u53ea\u6709\u6bc1\u706d\u4e00\u9014\uff0c\u4ece\u6b64\u5c31\u538b\u6839\u4e0d\u5b58\u5728\u2014\u2014\u4e0d\u4f46\u4ee5\u540e\u4e0d\u5b58\u5728\uff0c\u5728\u8fc7\u53bb\u4e5f\u4e0d\u5b58\u5728\uff0c\u6240\u6709\u7684\u5353\u5c14\u90fd\u4f1a\u8fc5\u901f\u5c06\u5b83\u9057\u5fd8\uff0c\u5b83\u6240\u6709\u7684\u75d5\u8ff9\u4ece\u5386\u53f2\u4e0a\u5b8c\u5168\u62b9\u53bb\u3002\u201c\u7136\u800c\u6b63\u5982\u4f60\u6240\u89c1\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u662f\u4eba\u7c7b\u3002\u6211\u4e0d\u5b88\u5353\u5c14\u7684\u89c4\u77e9\u3002\u201d</p><p>\u201c\u6211\u4e0d\u660e\u767d\u4f60\u7684\u610f\u601d\u3002\u201d</p><p>\u201c\u6211\u60f3\u548c\u60a8\u8c08\u4e00\u7b14\u4ea4\u6613\u3002\u201d\u743c\u6069\u63d0\u8bae\u3002</p><p>\u8fbe\u8d6b\u5c3c\u6c89\u9ed8\u3002</p><p>\u201c\u6765\u676f\u8461\u8404\u9152\u5982\u4f55\uff1f\u201d\u743c\u6069\u8bf4\u3002</p><p>\u8fbe\u8d6b\u5c3c\u4f9d\u7136\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4f46\u5979\u8214\u4e86\u8214\u6709\u4e9b\u5e72\u6db8\u7684\u5634\u5507\uff0c\u8fd9\u66b4\u9732\u4e86\u5979\u7684\u5fc3\u601d\u3002</p><p>\u743c\u6069\u8f6c\u5934\u5728\u5899\u58c1\u4e0a\u5bfb\u627e\u4e86\u4e00\u4f1a\uff0c\u53d1\u73b0\u4e86\u90a3\u4e2a\u5f00\u5173\u3002\u4ed6\u6309\u4e86\u4e0b\u53bb\uff0c\u5bf9\u91cc\u9762\u8bf4\uff1a\u201c\u4e24\u676f\u8461\u8404\u9152\u3002\u201d\u51e0\u5206\u949f\u4e4b\u540e\uff0c\u4e00\u4f4d\u7537\u6027\u9ed1\u6697\u7cbe\u7075\u6572\u95e8\u8fdb\u5165\uff0c\u7aef\u7740\u6258\u76d8\uff0c\u91cc\u9762\u653e\u7740\u4e24\u676f\u7edb\u7ea2\u8272\u6db2\u4f53\u3002</p><p>\u743c\u6069\u4ece\u53e3\u888b\u91cc\u53d6\u51fa\u4e00\u679a\u91d1\u5e01\u4e22\u7ed9\u4ed6\u3002\u9ed1\u6697\u7cbe\u7075\u5c06\u4e24\u676f\u9152\u653e\u5728\u5e8a\u8fb9\u7684\u684c\u5b50\u4e0a\uff0c\u8eac\u8eab\u544a\u9000\uff0c\u5c06\u95e8\u5e26\u597d\u3002\u8fbe\u8d6b\u5c3c\u8f6c\u8138\u770b\u4e86\u4e00\u4e0b\u8461\u8404\u9152\uff0c\u5979\u88ab\u9879\u5708\u548c\u9501\u94fe\u9650\u5236\u4f4f\u4e86\uff0c\u591f\u4e0d\u7740\u3002\u743c\u6069\u62ff\u8d77\u5176\u4e2d\u4e00\u676f\uff0c\u9012\u5230\u5979\u624b\u4e0a\u3002</p><p>\u201c\u8c22\u8c22\u3002\u201d\u8fbe\u8d6b\u5c3c\u751f\u786c\u5730\u8bf4\u3002</p><p>\u743c\u6069\u5fae\u7b11\uff0c\u9000\u56de\u5230\u81ea\u5df1\u7684\u6905\u5b50\u4e0a\uff0c\u89c2\u5bdf\u7740\u5bf9\u65b9\u3002\u5373\u4f7f\u6ca6\u843d\u5230\u73b0\u5728\u8fd9\u79cd\u72fc\u72c8\u5883\u5730\uff0c\u5979\u7684\u52a8\u4f5c\u4f9d\u65e7\u7cbe\u81f4\u4f18\u96c5\uff0c\u900f\u7740\u826f\u597d\u7684\u6559\u517b\uff0c\u5145\u5206\u8bc1\u660e\u5979\u66fe\u7ecf\u7684\u8d35\u65cf\u8eab\u4efd\uff0c\u4f46\u5979\u7684\u624b\u6307\u5728\u6291\u5236\u4e0d\u4f4f\u5730\u8f7b\u5fae\u98a4\u6296\uff0c\u4eff\u4f5b\u4ec5\u4ec5\u7aef\u8d77\u4e00\u676f\u9152\u4e5f\u662f\u6781\u5176\u8d39\u529b\u7684\u5de5\u4f5c\u3002\u743c\u6069\u7b49\u5979\u5c06\u4e00\u676f\u9152\u559d\u5b8c\uff0c\u63a5\u8fc7\u7a7a\u676f\uff0c\u653e\u56de\u684c\u4e0a\u3002</p><p>\u201c\u73b0\u5728\u6211\u4eec\u53ef\u4ee5\u8c08\u8c08\u4e86\u5417\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4ea4\u6613\uff1f\u201d</p><p>\u201c\u6700\u8fd1\u57ce\u5e02\u91cc\u53d1\u751f\u4e86\u4e00\u4e9b\u4e8b\u60c5\uff0c\u6211\u53d7\u6267\u653f\u8bae\u4f1a\u7684\u4e3b\u6bcd\u4eec\u59d4\u6258\u524d\u6765\u8c03\u67e5\uff0c\u201d\u743c\u6069\u4e0d\u7d27\u4e0d\u6162\u5730\u8bf4\uff0c\u201c\u6709\u4e3a\u6570\u4e0d\u5c11\u7684\u7537\u6027\u4ece\u5bb6\u65cf\u91cc\u9003\u8dd1\u4e86\uff0c\u6709\u8ff9\u8c61\u8868\u660e\u4ed6\u4eec\u5728\u9003\u8dd1\u4e4b\u524d\u6765\u8fc7\u8fd9\u91cc\u2026\u2026\u6211\u60f3\u60a8\u6216\u8bb8\u4f1a\u77e5\u9053\u67d0\u4e9b\u6709\u4ef7\u503c\u7684\u4fe1\u606f\u3002\u201d</p><p>\u8fbe\u8d6b\u5c3c\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u53ea\u662f\u4e2a\u4f9b\u4eba\u53d1\u6cc4\u6b32\u671b\u7684\u5993\u5973\uff0c\u6ca1\u6709\u8d44\u683c\u53c2\u4e0e\u8fd9\u6837\u91cd\u5927\u7684\u79d8\u5bc6\u3002\u201d</p><p>\u201c\u5514\uff0c\u7136\u800c\u6211\u89c9\u5f97\uff0c\u7537\u6027\u5728\u60a8\u8fd9\u6837\u5bcc\u6709\u9b45\u529b\u7684\u5973\u6027\u9762\u524d\uff0c\u5927\u591a\u90fd\u4f1a\u53d8\u5f97\u8bda\u5b9e\u548c\u53e3\u9f7f\u7075\u4fbf\u4e00\u4e9b\u7684\u2026\u2026\u5c24\u5176\u662f\u5728\u4ed6\u4eec\u611f\u5230\u5fc3\u6ee1\u610f\u8db3\u7684\u65f6\u5019\uff0c\u201d\u743c\u6069\u5fae\u5fae\u7b11\u7740\uff0c\u201c\u7537\u6027\u96c6\u4f53\u9003\u8dd1\uff0c\u8fd9\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u662f\u5f88\u4e0d\u5bfb\u5e38\u7684\u4e8b\u60c5\uff0c\u7136\u800c\u60a8\u521a\u624d\u4e1d\u6beb\u4e0d\u89c9\u5f97\u60ca\u8bb6\u3002\u663e\u7136\uff0c\u60a8\u77e5\u9053\u4e00\u4e9b\u4e1c\u897f\u3002\u201d</p><p>\u201c\u6211\u53ea\u662f\u6f20\u4e0d\u5173\u5fc3\u800c\u5df2\uff0c\u201d\u8fbe\u8d6b\u5c3c\u8bf4\uff0c\u201c\u6211\u5df2\u7ecf\u4e0d\u662f\u8d35\u65cf\u4e86\uff0c\u8fd9\u5ea7\u57ce\u5e02\u548c\u6211\u65e0\u5173\uff0c\u54ea\u6015\u5b83\u660e\u5929\u5c31\u4f1a\u6bc1\u706d\u3002\u201d</p><p>\u201c\u795e\u540e\u4e0d\u4f1a\u5bb9\u5fcd\u8fd9\u79cd\u60c5\u51b5\u53d1\u751f\u7684\u3002\u201d</p><p>\u8fbe\u8d6b\u5c3c\u54fc\u4e86\u4e00\u58f0\uff0c\u6ca1\u6709\u56de\u7b54\u3002</p><p>\u743c\u6069\u4e5f\u6ca1\u6709\u518d\u8bf4\u8bdd\uff0c\u4ed6\u5728\u7b49\u5f85\u7740\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8fbe\u8d6b\u5c3c\u62ac\u8d77\u5934\u6765\uff0c\u201c\u600e\u4e48\u8bc1\u660e\u4f60\u7684\u8eab\u4efd\u3002\u201d</p><p>\u201c\u6211\u7ad9\u5728\u60a8\u9762\u524d\uff0c\u8fd9\u5c31\u662f\u8bc1\u660e\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5728\u60a8\u7684\u8bb0\u5fc6\u548c\u89c1\u95fb\u4e2d\uff0c\u6709\u4e00\u4e2a\u4eba\u7c7b\u80fd\u591f\u5927\u6447\u5927\u6446\u5730\u8d70\u8fdb\u8fd9\u91cc\u5417\uff1f\u201d</p><p>\u201c\u4f60\u663e\u7136\u662f\u4e2a\u5deb\u5e08\uff0c\u4f2a\u88c5\u5bf9\u4e8e\u4f60\u6765\u8bf4\u5e76\u975e\u96be\u4e8b\u3002\u201d</p><p>\u743c\u6069\u4ece\u6000\u4e2d\u53d6\u51fa\u5370\u6709\u83f2\u5c14\u4f26\u5bb6\u65cf\u5fbd\u8bb0\u7684\u767d\u91d1\u80f8\u9488\uff0c\u653e\u5728\u638c\u5fc3\uff0c\u6258\u7740\u9001\u5230\u8fbe\u8d6b\u5c3c\u9762\u524d\u3002\u201c\u8fd9\u53ef\u65e0\u6cd5\u4f2a\u88c5\u3002\u201d</p><p>\u8fbe\u8d6b\u5c3c\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u201c\u6211\u80fd\u5f97\u5230\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u770b\uff0c\u6211\u4e0d\u4f1a\u5411\u4e3b\u6bcd\u4eec\u544a\u53d1\uff0c\u8bf4\u4f60\u548c\u6b64\u4e8b\u6709\u4efb\u4f55\u5173\u8054\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u81f3\u4e8e\u4f60\u80fd\u5f97\u5230\u4ec0\u4e48\u2026\u2026\u5c0f\u59d0\uff0c\u60a8\u73b0\u5728\u8fd8\u9700\u8981\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u8fbe\u8d6b\u5c3c\u54ac\u7d27\u5634\u5507\uff0c\u201c\u81ea\u7531\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u8981\u81ea\u7531\u3002\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u8fd9\u4e0d\u53ef\u80fd\uff0c\u6211\u529e\u4e0d\u5230\u3002\u201d</p><p>\u201c\u90a3\u6211\u4eec\u6ca1\u4ec0\u4e48\u53ef\u8c08\u7684\uff0c\u201d\u9ed1\u6697\u7cbe\u7075\u8bf4\uff0c\u201c\u9664\u4e86\u81ea\u7531\uff0c\u6211\u522b\u65e0\u6240\u6c42\u3002\u201d</p><p>\u201c\u518d\u60f3\u60f3\u522b\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6bd4\u5982\u8bf4\uff0c\u590d\u4ec7\uff1f\u201d</p><p>\u201c\u590d\u4ec7\uff1f\u201d</p><p>\u201c\u8fbe\u8d6b\u5c3c\u5bb6\u65cf\u66fe\u7ecf\u5728\u6267\u653f\u8bae\u4f1a\u4e2d\u6392\u540d\u7b2c\u5341\u4e03\uff0c\u60a8\u66fe\u7ecf\u662f\u5bb6\u65cf\u7684\u957f\u5973\uff0c\u672a\u6765\u7ee7\u627f\u4eba\uff0c\u795e\u540e\u7684\u9ad8\u9636\u7267\u5e08\uff0c\u8fd9\u5ea7\u57ce\u5e02\u7684\u7edf\u6cbb\u9636\u5c42\u4e4b\u4e00\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u52aa\u529b\u56de\u5fc6\u7740\u7ef4\u5eb7\u5c3c\u4e9a\u63d0\u4f9b\u7684\u90a3\u4e9b\u8d44\u6599\uff0c\u201c\u800c\u5982\u4eca\uff0c\u60a8\u88ab\u795e\u540e\u9057\u5f03\uff0c\u88ab\u56da\u7981\u5728\u8fd9\u72ed\u5c0f\u7684\u623f\u95f4\u91cc\uff0c\u88ab\u90a3\u4e9b\u66fe\u7ecf\u5730\u4f4d\u5728\u60a8\u4e4b\u4e0b\u7684\u7537\u6027\u7f9e\u8fb1\u548c\u4fb5\u72af\uff0c\u88ab\u6bb4\u6253\u548c\u6298\u78e8\u2026\u2026\u8fbe\u8d6b\u5c3c\u5c0f\u59d0\uff0c\u60a8\u6ca1\u6709\u8003\u8651\u8fc7\u62a5\u590d\u5417\uff1f\u201d</p><p>\u8fbe\u8d6b\u5c3c\u51b7\u7b11\u8d77\u6765\uff0c\u201c\u4f60\u80fd\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5f88\u591a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u963b\u6b62\u4e86\u74dc\u7406\u5fb7\u65af\u57ce\u5bf9\u4f0a\u5361\u6c99\u57ce\u7684\u6218\u4e89\uff0c\u6211\u6740\u6b7b\u4e86\u83f2\u5c14\u4f26\u4e3b\u6bcd\u7684\u4e24\u4e2a\u5973\u513f\u2014\u2014\u800c\u5982\u4f60\u6240\u89c1\uff0c\u73b0\u5728\u6211\u4f9d\u7136\u53d7\u5230\u4fe1\u4efb\u2026\u2026\u597d\u5427\uff0c\u81f3\u5c11\u662f\u6682\u65f6\u7684\u793c\u9047\u3002\u6211\u5bf9\u8fd9\u5ea7\u57ce\u5e02\u7684\u73b0\u72b6\u548c\u5b83\u5373\u5c06\u8bde\u751f\u7684\u672a\u6765\u6df1\u611f\u5174\u8da3\uff0c\u5e76\u4e14\u5f88\u4e50\u610f\u53c2\u4e0e\u5176\u4e2d\u3002\u6211\u60f3\uff0c\u4f60\u5bf9\u6b64\u540c\u6837\u62b1\u4e50\u89c1\u5176\u6210\u7684\u6001\u5ea6\u5427\u3002\u201d</p><p>\u201c\u6700\u540e\uff0c\u201d\u4ed6\u4ece\u6000\u4e2d\u7684\u6b21\u5143\u53e3\u888b\u91cc\u53d6\u51fa\u56db\u4e2a\u7ec6\u957f\u7684\u73bb\u7483\u74f6\uff0c\u91cc\u9762\u88c5\u7740\u6697\u7d2b\u8272\u7684\u836f\u6c34\uff0c\u201c\u5b83\u80fd\u591f\u8ba9\u4f60\u5bf9\u75bc\u75db\u7684\u611f\u89c9\u8fdf\u949d\uff0c\u8ba9\u4f60\u7684\u4f24\u53e3\u5feb\u901f\u6cbb\u6108\uff0c\u53ef\u4ee5\u6d88\u9664\u4f60\u4f53\u5185\u7684\u75c5\u75db\u2014\u2014\u5982\u679c\u6211\u6ca1\u770b\u9519\u7684\u8bdd\u2014\u2014\u8ba9\u4f60\u6d3b\u5f97\u66f4\u4e45\u3002\u201d</p><p>\u201c\u610f\u5473\u7740\u66f4\u957f\u4e45\u7684\u6298\u78e8\u3002\u201d</p><p>\u201c\u5bf9\uff0c\u4f46\u540c\u65f6\u4e5f\u610f\u5473\u7740\u5e0c\u671b\u3002\u201d</p><p>\u201c\u5e0c\u671b\u65e9\u5df2\u79bb\u6211\u8fdc\u53bb\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u81ea\u6740\u5462\uff1f\u201d\u743c\u6069\u53cd\u95ee\uff0c\u201c\u5982\u679c\u4f60\u5bf9\u660e\u5929\u6ca1\u6709\u534a\u70b9\u5e0c\u671b\uff0c\u5982\u679c\u4f60\u4e0d\u8ba4\u4e3a\u81ea\u5df1\u7684\u5883\u9047\u6709\u90a3\u4e48\u4e00\u4e1d\u4e00\u6beb\u7684\u53ef\u80fd\u4f1a\u5f97\u5230\u6539\u5584\uff0c\u90a3\u4e48\u987a\u7406\u6210\u7ae0\u7684\u4f5c\u6cd5\uff0c\u662f\u7ed3\u675f\u81ea\u5df1\u7684\u751f\u547d\uff0c\u7ed3\u675f\u8fd9\u6bb5\u75db\u82e6\u7684\u6298\u78e8\u5427\u3002\u201d</p><p>\u8fbe\u8d6b\u5c3c\u6c89\u9ed8\u7247\u523b\uff0c\u201c\u4f60\u8bf4\u5f97\u6709\u70b9\u9053\u7406\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u7136\u800c\u6211\u77e5\u9053\u5f97\u4e5f\u4e0d\u591a\u3002\u201d</p><p>\u201c\u8bf4\u8bf4\u770b\u3002\u201d</p><p>\u201c\u4ed6\u4eec\u5e76\u975e\u65e0\u7ec4\u7ec7\u548c\u8ba1\u5212\u7684\u884c\u52a8\uff0c\u201d\u8fbe\u8d6b\u5c3c\u8bf4\uff0c\u201c\u663e\u7136\u6709\u4eba\u5728\u6697\u4e2d\u9886\u5bfc\u3002\u4ed6\u4eec\u5728\u8a00\u8c08\u4e4b\u95f4\u9690\u7ea6\u63d0\u8d77\uff0c\u4e00\u4e2a\u2018\u5fc3\u8bed\u8005\u2019\u3002\u201d</p><p>\u201c\u5fc3\u8bed\u8005\uff1f\u201d\u743c\u6069\u6014\u4e86\u6014\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4ed6\u4eec\u90fd\u8fd9\u4e48\u79f0\u547c\uff0c\u6211\u60f3\u662f\u67d0\u79cd\u4ee3\u79f0\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u7ee7\u7eed\u3002\u201d</p><p>\u201c\u4ed6\u4eec\u6ca1\u6709\u79bb\u5f00\u57ce\u5e02\uff0c\u6211\u66fe\u7ecf\u4e24\u6b21\u89c1\u5230\u540c\u4e00\u4e2a\u4eba\uff0c\u4e2d\u95f4\u76f8\u9694\u4e86\u56db\u5929\uff0c\u201d\u8fbe\u8d6b\u5c3c\u8bf4\uff0c\u201c\u4ed6\u4eec\u6f5c\u4f0f\u4e0b\u6765\uff0c\u5728\u7b79\u5212\u7740\u4ec0\u4e48\uff0c\u4f3c\u4e4e\u8fd8\u5728\u717d\u52a8\u5974\u96b6\uff0c\u6211\u60f3\u8fc7\u51e0\u5929\u6216\u8bb8\u5c31\u4f1a\u6709\u4e00\u573a\u66b4\u4e71\u3002\u201d</p><p>\u201c\u5f88\u68d2\uff0c\u6211\u4e5f\u8fd9\u4e48\u731c\u6d4b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u4e48\u4f60\u80fd\u544a\u8bc9\u6211\uff0c\u4ed6\u4eec\u8eb2\u5728\u57ce\u5e02\u7684\u54ea\u4e2a\u89d2\u843d\u5417\uff1f\u201d</p><p>\u201c\u8fd9\u6211\u4e0d\u6e05\u695a\uff0c\u4f46\u5f88\u6709\u53ef\u80fd\u662f\u7eb3\u739b\u65af\uff0c\u5982\u679c\u6362\u4e86\u6211\uff0c\u6211\u4e5f\u4f1a\u9009\u62e9\u90a3\u91cc\u3002\u800c\u4e14\u6211\u66fe\u7ecf\u5728\u5176\u4e2d\u4e00\u4e2a\u9003\u4ea1\u8005\u8eab\u4e0a\u95fb\u5230\u4e86\u5e03\u6d85\u9152\u7684\u5473\u9053\uff0c\u8fd9\u79cd\u9152\u53ea\u5728\u7eb3\u739b\u65af\u7684\u7ea2\u5e3d\u5b50\u51fa\u552e\u3002\u201d</p><p>\u743c\u6069\u770b\u7740\u5979\uff0c\u201c\u6211\u771f\u597d\u5947\u4f60\u5f53\u65f6\u600e\u4e48\u4f1a\u5931\u8d25\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u6709\u4e00\u4e2a\u611a\u8822\u7684\u6bcd\u4eb2\u3002\u201d</p><p>\u201c\u5514\uff0c\u201d\u743c\u6069\u5c06\u836f\u6c34\u653e\u5728\u5e8a\u5934\uff0c\u5979\u80fd\u591f\u591f\u5f97\u7740\u7684\u5730\u65b9\uff0c\u5c06\u5269\u4e0b\u4e00\u676f\u8461\u8404\u9152\u4e5f\u653e\u4e86\u8fc7\u53bb\uff0c\u201c\u518d\u89c1\u3002\u201d</p><p>\u4ed6\u8d77\u8eab\u51fa\u95e8\uff0c\u901a\u8fc7\u957f\u5eca\u8d70\u5230\u5c3d\u5934\uff0c\u5c06\u94a5\u5319\u4ea4\u8fd8\u7ed9\u90a3\u4e2a\u7626\u524a\u7684\u9ed1\u6697\u7cbe\u7075\uff0c\u7136\u540e\u8d70\u4e0a\u697c\u68af\uff0c\u91cd\u65b0\u56de\u5230\u697c\u4e0a\u7684\u9152\u5e97\u4e2d\u3002\u8299\u8389\u5a05\u4f9d\u65e7\u5750\u5728\u539f\u4f4d\uff0c\u5979\u5df2\u7ecf\u6362\u4e86\u4e00\u676f\u679c\u6c41\uff0c\u800c\u539f\u672c\u743c\u6069\u7684\u4f4d\u7f6e\u4e0a\u591a\u4e86\u4e00\u4e2a\u9ed1\u6697\u7cbe\u7075\uff0c\u6b63\u5728\u7709\u98de\u8272\u821e\u5730\u5411\u8299\u8389\u5a05\u8bb2\u8ff0\u7740\u4ec0\u4e48\uff0c\u62ac\u5934\u770b\u89c1\u743c\u6069\uff0c\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u8d77\u8eab\u9000\u4e86\u5f00\u53bb\u3002</p><p>\u201c\u4ed6\u662f\u8c01\uff1f\u201d\u743c\u6069\u95ee\u8299\u8389\u5a05\u3002</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u838e\u5c14\u7267\u5e08\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u8f6c\u7740\u624b\u4e2d\u7684\u73bb\u7483\u676f\uff0c\u201c\u4e0d\u8fc7\u8fd9\u676f\u679c\u6c41\u662f\u4ed6\u4ed8\u94b1\u7684\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u679c\u7136\u7f8e\u5973\u5728\u4efb\u4f55\u5730\u65b9\u90fd\u53d7\u6b22\u8fce\u548c\u4f18\u5f85\uff0c\u5f53\u7136\u8fd9\u4e5f\u662f\u56e0\u4e3a\u9ed1\u6697\u7cbe\u7075\u548c\u4eba\u7c7b\u4f53\u578b\u8fd1\u4f3c\uff0c\u5ba1\u7f8e\u89c2\u76f8\u5dee\u65e0\u51e0\u7684\u7f18\u6545\u3002\u5426\u5219\u5728\u4f0a\u5361\u6c99\u57ce\u91cc\uff0c\u5728\u90a3\u4e9b\u77ee\u4eba\u4eec\u773c\u4e2d\uff0c\u8299\u8389\u5a05\u5c31\u5b8c\u5168\u7b97\u4e0d\u4e0a\u7f8e\u5973\u4e86\u3002</p><p>\u201c\u8d70\u5427\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u4ed6\u4eec\u653e\u4e0b\u515c\u5e3d\uff0c\u518d\u6b21\u9690\u85cf\u4e86\u81ea\u5df1\u7684\u9762\u5bb9\uff0c\u4e00\u524d\u4e00\u540e\u8d70\u51fa\u6c89\u9ed8\u4e4b\u6b4c\uff0c\u743c\u6069\u4f4e\u58f0\u5c06\u521a\u624d\u4ece\u8fbe\u8d6b\u5c3c\u90a3\u91cc\u5f97\u5230\u7684\u6d88\u606f\u590d\u8ff0\u4e86\u4e00\u904d\u3002\u201c\u5fc3\u8bed\u8005\uff1f\u201d\u8299\u8389\u5a05\u91cd\u590d\u7740\u8fd9\u4e2a\u5947\u602a\u7684\u8bcd\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\u3002\u201d</p><p>\u201c\u90a3\u6211\u4eec\u73b0\u5728\u53bb\u7eb3\u739b\u65af\u533a\uff1f\u201d</p><p>\u867d\u7136\u5bf9\u743c\u6069\u9887\u591a\u4e0d\u6ee1\uff0c\u4f46\u8299\u8389\u5a05\u7ec8\u7a76\u4e0d\u662f\u90a3\u79cd\u4efb\u6027\u4f7f\u6c14\u7684\u5973\u5b69\u5b50\uff0c\u77e5\u5206\u5bf8\uff0c\u61c2\u8fdb\u9000\uff0c\u4e8b\u60c5\u7684\u8f7b\u91cd\u7f13\u6025\u8fd8\u662f\u5206\u5f97\u5f88\u6e05\u695a\u7684\u3002\u867d\u7136\u5979\u4e0d\u77e5\u9053\u743c\u6069\u4e3a\u4f55\u6267\u610f\u8981\u6765\u74dc\u7406\u5fb7\u65af\uff0c\u66f4\u4e0d\u77e5\u9053\u4ed6\u7684\u60f3\u6cd5\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u5927\u5bb6\u7684\u751f\u6b7b\u5b58\u4ea1\u90fd\u7cfb\u5728\u4e00\u8d77\u3002\u4e8b\u60c5\u5f04\u7838\u4e86\uff0c\u8c01\u4e5f\u522b\u60f3\u987a\u5229\u8131\u8eab\uff0c\u6240\u4ee5\u867d\u7136\u5634\u4e0a\u4e0d\u5ba2\u6c14\uff0c\u771f\u8981\u5e72\u6b63\u4e8b\u7684\u65f6\u5019\uff0c\u4f9d\u65e7\u8fd8\u662f\u8ba4\u771f\u7684\u3002</p><p>\u743c\u6069\u6b63\u51c6\u5907\u56de\u7b54\uff0c\u7a81\u7136\u770b\u89c1\u4fa7\u9762\u6709\u4e24\u4e2a\u718a\u5730\u7cbe\u6447\u6447\u6643\u6643\u5730\u8d70\u8fc7\u6765\uff0c\u62e6\u5728\u8eab\u524d\u3002\u4ed6\u4eec\u6709\u7740\u8910\u8272\u7684\u7c97\u91cd\u6bdb\u53d1\uff0c\u4e0b\u989a\u7a81\u51fa\uff0c\u5c31\u50cf\u672a\u66fe\u8fdb\u5316\u7684\u7c7b\u4eba\u733f\uff0c\u8116\u5b50\u7c97\u77ed\uff0c\u8eab\u6750\u5f88\u9ad8\uff0c\u6bcf\u4eba\u8170\u95f4\u90fd\u522b\u7740\u4e00\u628a\u7c97\u91cd\u7684\u780d\u5200\u3002\u718a\u5730\u7cbe\u662f\u5730\u7cbe\u8fd9\u79cd\u751f\u7269\u4e2d\u7684\u6700\u5f3a\u4e9a\u79cd\uff0c\u4ed6\u4eec\u529b\u6c14\u5f88\u5927\uff0c\u8db3\u4ee5\u548c\u98df\u4eba\u9b54\u5339\u654c\u3002\u8299\u8389\u5a05\u6212\u5907\u7740\uff0c\u624b\u5df2\u7ecf\u6697\u4e2d\u63e1\u4f4f\u4e86\u5723\u5fbd\uff0c\u7136\u800c\u5bf9\u65b9\u4f3c\u4e4e\u6ca1\u6709\u653b\u51fb\u7684\u610f\u601d\u3002\u201c\u542c\u8bf4\u4f60\u4eec\u5728\u6253\u542c\u9003\u4ea1\u8005\u7684\u4e8b\u60c5\uff1f\u201d\u5de6\u8fb9\u90a3\u53ea\u718a\u5730\u7cbe\u74ee\u58f0\u74ee\u6c14\u5730\u8bf4\u3002</p><p>\u201c\u662f\u7684\u3002\u201d\u743c\u6069\u8bf4\uff0c\u4ed6\u5fae\u7b11\u3002\u5728\u5965\u672f\u89c6\u89c9\u4e2d\uff0c\u773c\u524d\u7684\u8fd9\u4e24\u4e2a\u4eba\u8eab\u4e0a\u7f20\u7ed5\u7740\u660e\u663e\u7684\u7eff\u8272\u9b54\u6cd5\u7075\u5149\uff0c\u4ed6\u4eec\u538b\u6839\u5c31\u4e0d\u662f\u4ec0\u4e48\u718a\u5730\u7cbe\uff0c\u800c\u662f\u4e24\u4e2a\u9ed1\u6697\u7cbe\u7075\u3002</p><p>\u201c\u8ddf\u6211\u4eec\u8d70\u3002\u201d\u718a\u5730\u7cbe\u8bf4\uff0c\u8f6c\u8eab\u671d\u4e1c\u5357\u65b9\u8d70\u53bb\u3002\u8299\u8389\u5a05\u770b\u7740\u743c\u6069\uff0c\u7b49\u5f85\u4ed6\u7684\u51b3\u5b9a\u3002</p><p>\u201c\u8ddf\u4e0a\u53bb\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u4ed6\u4eec\u8ddf\u968f\u7740\u4e24\u53ea\u718a\u5730\u7cbe\uff0c\u7a7f\u8fc7\u62e5\u6324\u7684\u95f9\u5e02\uff0c\u8857\u9053\u4e0a\u6e10\u6e10\u53d8\u5f97\u4eba\u8ff9\u7a00\u5c11\u8d77\u6765\u3002\u6700\u540e\u743c\u6069\u770b\u89c1\u4e86\u9ad8\u8038\u7684\u60ac\u5d16\u77f3\u58c1\uff0c\u5e95\u90e8\u6709\u4e00\u4e2a\u4eba\u5de5\u5f00\u51ff\u7684\u752c\u9053\u3002\u718a\u5730\u7cbe\u5f53\u5148\u8d70\u4e86\u8fdb\u53bb\uff0c\u8299\u8389\u5a05\u6709\u4e9b\u72b9\u8c6b\uff0c\u5bb3\u6015\u53d7\u5230\u4f0f\u51fb\uff0c\u4f46\u743c\u6069\u8f7b\u8f7b\u6447\u4e86\u6447\u5934\uff0c\u201c\u6ca1\u4e8b\u3002\u201d\u4ed6\u8bf4\uff0c\u4f38\u624b\u63e1\u4f4f\u8299\u8389\u5a05\u7684\u624b\u8155\u3002\u8299\u8389\u5a05\u5fae\u5fae\u4e00\u60ca\uff0c\u4f46\u968f\u5373\u9547\u5b9a\u4e0b\u6765\uff0c\u6ca1\u6709\u62d2\u7edd\uff0c\u968f\u7740\u743c\u6069\u8d70\u4e86\u8fdb\u53bb\u3002</p><p>\u8d70\u8fc7\u9ed1\u6697\u72ed\u957f\u7684\u752c\u9053\uff0c\u773c\u524d\u970d\u7136\u5f00\u9614\uff0c\u9178\u81ed\u3001\u8f9b\u8fa3\u548c\u6cb9\u70df\u6c14\u606f\u6251\u9762\u800c\u6765\uff0c\u5939\u6742\u7740\u4e71\u4e03\u516b\u7cdf\u7684\u53eb\u56b7\u55a7\u56a3\uff0c\u653e\u773c\u671b\u53bb\u5168\u662f\u5947\u5f62\u602a\u72b6\u7684\u751f\u7269\uff0c\u743c\u6069\u5fc3\u77e5\u5df2\u7ecf\u5230\u4e86\u7eb3\u739b\u65af\u533a\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u7eb3\u739b\u65af\uff0c\u5728\u5353\u5c14\u8bed\u4e2d\u662f\u201c\u8ff7\u9014\u8005\u201d\u7684\u610f\u601d\uff0c\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\u5c31\u662f\u8d2b\u6c11\u7a9f\u7684\u4ee3\u79f0\u3002\u5b83\u4f4d\u4e8e\u74dc\u7406\u5fb7\u65af\u7684\u4e1c\u5357\u90e8\uff0c\u5c45\u4f4f\u7740\u57ce\u4e2d\u6700\u5e95\u5c42\u7684\u751f\u7269\uff0c\u5305\u62ec\u5c11\u6570\u6ca1\u6709\u5bb6\u65cf\u613f\u610f\u6536\u5bb9\u53c8\u8eab\u65e0\u957f\u6280\u7684\u9ed1\u6697\u7cbe\u7075\uff0c\u4ee5\u53ca\u5927\u91cf\u7684\u5974\u96b6\u4eec\uff0c\u5305\u62ec\u72d7\u5934\u4eba\u3001\u5730\u7cbe\u3001\u98df\u4eba\u9b54\u3001\u8c7a\u72fc\u4eba\u3001\u718a\u5730\u7cbe\u3001\u5927\u5730\u7cbe\uff0c\u8fd8\u6709\u5176\u4ed6\u5404\u79cd\u60f3\u5f97\u5230\u7684\u4f4e\u7ea7\u751f\u7269\uff0c\u4ee5\u53ca\u5b83\u4eec\u7684\u540e\u4ee3\u3002\u5353\u5c14\u7684\u793e\u4f1a\u4f53\u7cfb\u662f\u5efa\u7acb\u5728\u5bb6\u65cf\u4e4b\u4e0a\u7684\uff0c\u4ee5\u4e3b\u6bcd\u4e3a\u9886\u8896\uff0c\u5973\u6027\u7267\u5e08\u4e3a\u7edf\u6cbb\u9636\u5c42\uff0c\u5bb6\u65cf\u5deb\u5e08\u4e3a\u52a9\u624b\uff0c\u5f3a\u58ee\u7684\u6218\u58eb\u4f5c\u4e3a\u4e2d\u575a\u529b\u91cf\uff0c\u5e95\u5c42\u5219\u662f\u6570\u91cf\u5e9e\u5927\u7684\u5974\u96b6\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u5927\u91cf\u7684\u6d41\u6c11\uff0c\u56e0\u4e3a\u79cd\u79cd\u539f\u56e0\u6ca1\u6709\u5bb6\u65cf\u8eab\u4efd\uff0c\u6216\u8005\u88ab\u5bb6\u65cf\u5f00\u9664\u3001\u9057\u5f03\uff0c\u6216\u8005\u662f\u4ece\u5176\u4ed6\u5730\u65b9\u88ab\u4fd8\u864f\u800c\u6765\uff0c\u5374\u53c8\u6ca1\u6709\u88ab\u5404\u4f4d\u4e3b\u6bcd\u4eec\u770b\u4e2d\uff0c\u53ea\u80fd\u5728\u57ce\u5e02\u6700\u6df7\u4e71\u7684\u533a\u57df\u6816\u8eab\uff0c\u8270\u96be\u5730\u751f\u5b58\u7740\u3002</p><p>\u5728\u743c\u6069\u7684\u8bb0\u5fc6\u4e2d\uff0c\u4ed6\u4ece\u6765\u4e0d\u66fe\u5230\u8fc7\u8fd9\u6837\u80ae\u810f\u7684\u5730\u65b9\uff0c\u5230\u5904\u90fd\u6563\u53d1\u7740\u6076\u81ed\uff0c\u5783\u573e\u548c\u6c61\u6c34\u5806\u5728\u8857\u9053\u4e0a\u65e0\u4eba\u6536\u62fe\u3002\u4f4e\u77ee\u7684\u7af9\u68da\u548c\u7c97\u7cd9\u7684\u6728\u623f\u5806\u53e0\u5728\u4e00\u8d77\uff0c\u770b\u4e0a\u53bb\u968f\u65f6\u53ef\u80fd\u4f1a\u5012\u584c\u3002\u6b64\u65f6\u662f\u6b63\u5348\uff0c\u867d\u7136\u5e7d\u6697\u5730\u57df\u91cc\u6ca1\u6709\u65e5\u6708\u661f\u5149\uff0c\u4f46\u5927\u4f53\u4e5f\u662f\u9075\u5faa\u591c\u4f0f\u663c\u51fa\u3001\u4e00\u65e5\u4e09\u9910\u7684\u4f5c\u606f\u89c4\u5f8b\u7684\uff0c\u8def\u8fb9\u6709\u4e00\u5bb6\u9910\u9986\u6b63\u5728\u8425\u4e1a\uff0c\u6cb9\u9505\u91cc\u53d1\u51fa\u6ecb\u5566\u7206\u88c2\u7684\u58f0\u97f3\uff0c\u53d1\u51fa\u5e26\u7740\u9178\u81ed\u5473\u7684\u6c14\u4f53\u3002\u4e00\u53ea\u8eab\u4e0a\u957f\u7740\u9cde\u7247\u7684\u72d7\u5934\u4eba\u51b2\u7740\u8001\u677f\uff08\u4e00\u53ea\u7638\u817f\u98df\u4eba\u9b54\uff09\u5927\u558a\u5927\u53eb\uff0c\u4f3c\u4e4e\u662f\u5728\u62b1\u6028\u6d1b\u65af\u517d\u6392\u7684\u5473\u9053\u4e0d\u5bf9\uff0c\u7d27\u63a5\u7740\u5b83\u88ab\u4e00\u4e2a\u4e24\u53ea\u4e94\u5927\u4e09\u7c97\u7684\u8c7a\u72fc\u4eba\u7ed9\u63d0\u4e86\u8d77\u6765\uff0c\u8fdc\u8fdc\u4e22\u5728\u8857\u4e0a\u3002</p><p>\u4e24\u53ea\u9ed1\u6697\u7cbe\u7075\u53d8\u5f62\u6210\u7684\u718a\u5730\u7cbe\u5728\u524d\u9762\u8000\u6b66\u626c\u5a01\u5730\u5f00\u8def\uff0c\u743c\u6069\u548c\u8299\u8389\u5a05\u4e0d\u8fdc\u4e0d\u8fd1\u5730\u8ddf\u5728\u540e\u9762\u3002\u9014\u4e2d\u6709\u51e0\u53ea\u72d7\u5934\u4eba\u8dd1\u8fc7\u6765\uff0c\u5411\u4ed6\u4eec\u515c\u552e\u81ea\u5df1\u7684\u5546\u54c1\uff0c\u4f46\u88ab\u743c\u6069\u62d2\u7edd\u4e86\uff0c\u4ed6\u4e00\u773c\u5c31\u770b\u51fa\u90a3\u4e9b\u6240\u8c13\u7684\u201c\u5b9d\u7269\u201d\u5168\u90fd\u662f\u4e0d\u503c\u4e00\u6587\u7684\u5783\u573e\u3002\u72d7\u5934\u4eba\u6709\u4e9b\u5931\u671b\uff0c\u4ed6\u4eec\u53d1\u51fa\u5431\u5431\u7684\u53eb\u58f0\uff0c\u7f20\u5728\u8eab\u8fb9\uff0c\u6700\u540e\u743c\u6069\u4e0d\u5f97\u4e0d\u5f39\u4e86\u5f39\u624b\u6307\uff0c\u5c06\u4e00\u53ea\u72d7\u5934\u4eba\u51fb\u98de\u5230\u8def\u8fb9\u7684\u81ed\u6c34\u6c9f\u91cc\uff0c\u624d\u7b97\u6446\u8131\u4e86\u7ea0\u7f20\u3002</p><p>\u6700\u7ec8\uff0c\u4ed6\u4eec\u8d70\u5230\u4e00\u5ea7\u4f3c\u4e4e\u5e9f\u5f03\u5f88\u4e45\u7684\u623f\u5b50\u9762\u524d\uff0c\u5b83\u5c45\u7136\u662f\u77f3\u8d28\u7ed3\u6784\uff0c\u8fd9\u5728\u7eb3\u739b\u65af\u533a\u975e\u5e38\u96be\u5f97\u3002\u743c\u6069\u6ce8\u610f\u5230\u4e00\u4e2a\u7ec6\u8282\uff1a\u6765\u6765\u5f80\u5f80\u7684\u884c\u4eba\u8c01\u90fd\u6ca1\u6709\u671d\u8fd9\u4e2a\u623f\u5b50\u770b\u4e00\u773c\uff0c\u4eff\u4f5b\u5b83\u538b\u6839\u5c31\u4e0d\u5b58\u5728\u4f3c\u7684\u3002\u4ed6\u601d\u5fd6\u7740\uff0c\u7136\u540e\u53d1\u73b0\u4e24\u53ea\u718a\u5730\u7cbe\u5df2\u7ecf\u8d70\u4e86\u8fdb\u53bb\uff0c\u5b83\u4eec\u7684\u8eab\u5f71\u51e0\u4e4e\u5728\u4e00\u77ac\u95f4\u6d88\u5931\u3002\u5deb\u5e08\u8003\u8651\u4e86\u51e0\u79d2\u949f\uff0c\u6700\u7ec8\u51b3\u5b9a\u4e00\u63a2\u7a76\u7adf\u3002</p><p>\u4ed6\u643a\u7740\u8299\u8389\u5a05\u7684\u624b\uff0c\u4e24\u4eba\u4eff\u4f5b\u4eb2\u5bc6\u7231\u4fa3\u4e00\u822c\uff0c\u5e76\u80a9\u8d70\u8fdb\u623f\u95e8\u3002\u5728\u521a\u521a\u8e0f\u5165\u7684\u90a3\u4e00\u523b\uff0c\u773c\u524d\u7684\u666f\u8c61\u5c31\u7a81\u7136\u6539\u53d8\u4e86\uff0c\u743c\u6069\u53d1\u73b0\u81ea\u5df1\u7f6e\u8eab\u4e8e\u4e00\u5904\u7a7a\u65f7\u7684\u5927\u5385\u91cc\uff0c\u770b\u8d77\u6765\u50cf\u662f\u4e00\u5ea7\u57ce\u5821\u7684\u4f1a\u5ba2\u5ba4\u3002\u4e24\u4fa7\u5899\u58c1\u4e0a\u6709\u51e0\u6247\u95e8\uff0c\u4e0d\u77e5\u901a\u5f80\u4f55\u5904\u3002\u5927\u5385\u91cc\u6446\u653e\u7740\u4e00\u4e9b\u5bb6\u5177\uff0c\u9020\u578b\u975e\u5e38\u72ec\u7279\uff0c\u53e4\u6734\u4e2d\u900f\u7740\u4e1d\u4e1d\u8be1\u5f02\uff0c\u65e2\u4e0d\u50cf\u662f\u5353\u5c14\u4eec\u7684\u7cbe\u81f4\u4f18\u96c5\u98ce\u683c\uff0c\u4e5f\u4e0d\u662f\u77ee\u4eba\u90a3\u79cd\u539a\u91cd\u8e0f\u5b9e\u7c7b\u578b\uff0c\u5f53\u7136\u66f4\u548c\u4eba\u7c7b\u4e60\u4fd7\u6ca1\u6709\u5173\u8054\uff0c\u4f46\u743c\u6069\u5374\u504f\u504f\u89c9\u5f97\u4f3c\u4e4e\u6709\u4e9b\u773c\u719f\uff0c\u597d\u50cf\u5728\u54ea\u91cc\u66fe\u7ecf\u89c1\u8fc7\u4f3c\u7684\u3002</p><p>\u4e03\u516b\u4e2a\u9ed1\u6697\u7cbe\u7075\u4ece\u7a7a\u6c14\u4e2d\u8d70\u51fa\u6765\uff0c\u5e26\u8def\u7684\u90a3\u4e24\u4e2a\u718a\u5730\u7cbe\u4e5f\u6062\u590d\u4e86\u539f\u8c8c\uff0c\u5f62\u6210\u5bf9\u743c\u6069\u548c\u8299\u8389\u5a05\u7684\u5408\u56f4\u4e4b\u52bf\u3002\u4ed6\u4eec\u5168\u90fd\u5168\u526f\u6b66\u88c5\uff0c\u7a7f\u7740\u7cbe\u91d1\u9501\u7532\uff0c\u88f9\u7d27\u9b54\u6cd5\u6597\u7bf7\uff0c\u624b\u6309\u5728\u8170\u95f4\u7684\u957f\u5251\u6216\u8005\u5f2f\u5200\u67c4\u4e0a\uff0c\u84c4\u52bf\u5f85\u53d1\u3002</p><p>\u201c\u8bf4\u51fa\u4f60\u7684\u6765\u610f\uff0c\u4eba\u7c7b\uff0c\u201d\u4e00\u4e2a\u9ed1\u6697\u7cbe\u7075\u8bf4\uff0c\u201c\u4f60\u5728\u56db\u5904\u6253\u542c\u9003\u4ea1\u8005\u7684\u6d88\u606f\u2014\u2014\u4f60\u53d7\u8c01\u6307\u4f7f\uff1f\u201d</p><p>\u201c\u83f2\u5c14\u4f26\u5bb6\u65cf\uff0c\u5514\uff0c\u51c6\u786e\u5730\u8bf4\u5e94\u8be5\u662f\u6267\u653f\u8bae\u4f1a\u7684\u4e3b\u6bcd\u4eec\u3002\u201d\u743c\u6069\u56de\u7b54\u3002</p><p>\u9ed1\u6697\u7cbe\u7075\u4eec\u7684\u624b\u66f4\u52a0\u7528\u529b\u5730\u6309\u5728\u6b66\u5668\u67c4\u4e0a\uff0c\u6709\u51e0\u4e2a\u4eba\u903c\u8fd1\u4e86\u534a\u6b65\uff0c\u201c\u90a3\u4e48\u4f60\u662f\u654c\u4eba\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u6070\u6070\u76f8\u53cd\uff0c\u662f\u670b\u53cb\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u5728\u5bfb\u6c42\u52a0\u5165\u4f60\u4eec\u7684\u65b9\u6cd5\uff0c\u6211\u5bf9\u4f60\u4eec\u7684\u8ba1\u5212\u5f88\u6709\u5174\u8da3\u3002\u201d</p><p>\u201c\u8bf4\u8c0e\uff01\u201d</p><p>\u201c\u5426\u5219\u6211\u5c31\u4e0d\u4f1a\u5230\u8fd9\u91cc\u6765\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4ece\u6c89\u9ed8\u4e4b\u6b4c\u51fa\u6765\uff0c\u6211\u5c31\u5b8c\u5168\u53ef\u4ee5\u56de\u53bb\u590d\u547d\uff0c\u4f60\u4eec\u7684\u4eba\u62e6\u4e0d\u4f4f\u6211\u3002\u6211\u5df2\u7ecf\u77e5\u9053\u4f60\u4eec\u7684\u5f88\u591a\u4e8b\u60c5\uff0c\u8db3\u4ee5\u8ba9\u4e3b\u6bcd\u4eec\u6ee1\u610f\uff0c\u201d\u4ed6\u865a\u5f20\u58f0\u52bf\u5730\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u4f60\u4eec\u5728\u8fd9\u91cc\u805a\u4f1a\uff0c\u5bc6\u8c0b\u63a8\u7ffb\u7267\u5e08\u4eec\u7684\u7edf\u6cbb\uff1b\u6211\u77e5\u9053\u4f60\u4eec\u53d7\u4eba\u5f15\u5bfc\u548c\u6307\u793a\uff0c\u6b63\u5728\u717d\u52a8\u5916\u9762\u90a3\u4e9b\u4f4e\u8d31\u7684\u5974\u96b6\uff1b\u6211\u8fd8\u77e5\u9053\u2018\u5fc3\u8bed\u8005\u2019\u2026\u2026\u201d</p><p>\u8fd9\u4e2a\u540d\u5b57\u5f15\u8d77\u4e86\u4e00\u9635\u8f7b\u5fae\u7684\u9a9a\u52a8\uff0c\u743c\u6069\u89c2\u5bdf\u5230\u51e0\u4e2a\u9ed1\u6697\u7cbe\u7075\u4e4b\u95f4\u5728\u5feb\u901f\u5730\u7528\u624b\u8bed\u4ea4\u8c08\uff0c\u201c\u6216\u8bb8\u4f60\u662f\u60f3\u627e\u5230\u66f4\u591a\u7684\u60c5\u62a5\uff0c\u201d\u9ed1\u6697\u7cbe\u7075\u8bf4\uff0c\u201c\u4ee5\u4fbf\u56de\u53bb\u9080\u529f\u8bf7\u8d4f\u3002\u201d</p><p>\u743c\u6069\u51b7\u7b11\u3002</p><p>\u9ed1\u6697\u7cbe\u7075\u4eec\u4e92\u76f8\u770b\u7740\uff0c\u72b9\u7591\u4e0d\u51b3\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\u7a81\u7136\u5728\u7a7a\u5bc2\u7684\u5927\u5385\u91cc\u54cd\u8d77\uff0c\u201c\u9000\u4e0b\u5427\uff0c\u6211\u6765\u548c\u5170\u5c3c\u65af\u7279\u5148\u751f\u8c08\u8c08\u3002\u201d</p><p>\u9ed1\u6697\u7cbe\u7075\u4eec\u9000\u540e\uff0c\u4e00\u4e2a\u9ad8\u5927\u7684\u4eba\u5f71\u4ece\u9ed1\u6697\u91cc\u8d70\u51fa\u6765\u3002\u90a3\u662f\u4e00\u4e2a\u602a\u5f02\u7684\u751f\u7269\uff0c\u6709\u7740\u4eba\u7684\u8eaf\u4f53\uff0c\u8111\u888b\u5374\u50cf\u662f\u7ae0\u9c7c\uff0c\u6709\u4e00\u53cc\u767d\u8272\u7684\u6b7b\u9c7c\u773c\uff0c\u957f\u7740\u516d\u6839\u89e6\u624b\u3002\u5b83\u8eab\u4e0a\u7a7f\u7740\u9ed1\u8272\u7684\u5deb\u5e08\u888d\uff0c\u88f8\u9732\u5728\u5916\u7684\u76ae\u80a4\u5448\u6de1\u7d2b\u8272\uff0c\u76b1\u5df4\u5df4\u7684\u4eff\u4f5b\u5e72\u67af\u6811\u76ae\u3002</p><p>\u5239\u90a3\u4e4b\u95f4\uff0c\u743c\u6069\u660e\u767d\u4e86\u201c\u5fc3\u8bed\u8005\u201d\u7684\u610f\u601d\uff0c\u8fd9\u4e9b\u9003\u4ea1\u8005\u7684\u9886\u8896\uff0c\u5c45\u7136\u662f\u4e00\u53ea\u7075\u5438\u602a\u2026\u2026\u4e0d\uff0c\u4e0d\u662f\u7075\u5438\u602a\uff0c\u4ed6\u968f\u5373\u53cd\u5e94\u8fc7\u6765\u3002</p><p>\u5b83\u662f\u963f\u9686\uff08Alhoon\uff09\uff0c\u6216\u8005\u66f4\u901a\u4fd7\u5730\u8bf4\uff0c\u5b83\u662f\u4e00\u53ea\u7075\u5438\u602a\u5deb\u5996\u3002</p></article>"}}}]);
