(window.webpackJsonp=window.webpackJsonp||[]).push([[1458],{1481:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e00\u8282 \u5b66\u9662\u91cc\u7684\u906d\u9047\u6218",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e00\u8282 \u5b66\u9662\u91cc\u7684\u906d\u9047\u6218</h2><p>\u7075\u5438\u602a\u5deb\u5996\u6084\u6084\u8f6c\u52a8\u7740\u53f3\u624b\u4e0a\u7684\u6212\u6307\uff0c\u4e00\u62b9\u6697\u7d2b\u8272\u7684\u5fae\u5149\u50cf\u5229\u7bad\u4e00\u6837\u5c04\u51fa\u6765\uff0c\u6ca1\u5165\u865a\u7a7a\u3002\u5728\u6ca1\u6709\u5965\u672f\u89c6\u89c9\u7684\u4eba\u773c\u4e2d\u770b\u6765\uff0c\u4e00\u5207\u90fd\u6beb\u65e0\u5f02\u72b6\uff0c\u4f46\u743c\u6069\u6ce8\u610f\u5230\u4e86\u3002</p><p>\u7d27\u63a5\u7740\uff0c\u5deb\u5996\u7684\u53cc\u624b\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u9b54\u6cd5\u9635\u7684\u8fb9\u7f18\u95ea\u70c1\u7740\u6d45\u7eff\u8272\u7684\u5fae\u5149\u3002\u5353\u5c14\u6218\u58eb\u4eec\u4ee5\u4e94\u4eba\u4e3a\u4e00\u7ec4\uff0c\u6309\u7167\u79e9\u5e8f\u9c7c\u8d2f\u8d70\u5230\u9635\u4e2d\uff0c\u7136\u540e\u77ac\u95f4\u6d88\u5931\uff0c\u88ab\u4f20\u9001\u5230\u4e86\u53e6\u5916\u4e00\u4e2a\u5730\u65b9\u3002\u6bcf\u5f53\u5b8c\u6210\u4e00\u6b21\u4f20\u9001\uff0c\u9b54\u6cd5\u9635\u7684\u5149\u8292\u5c31\u5fae\u5fae\u524a\u5f31\u4e00\u5206\uff0c\u53d8\u5f97\u66f4\u52a0\u9eef\u6de1\u3002\u6700\u7ec8\uff0c\u8fd1\u767e\u540d\u5353\u5c14\u90fd\u901a\u8fc7\u4e86\u9b54\u6cd5\u9635\uff0c\u53ea\u5269\u4e0b\u65af\u5170\u666e\u548c\u743c\u6069\u3001\u8299\u8389\u5a05\u4e09\u4eba\u3002</p><p>\u743c\u6069\u6709\u4e9b\u7591\u60d1\uff0c\u5deb\u5996\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u5728\u542f\u52a8\u9b54\u6cd5\u9635\u4e4b\u524d\uff0c\u4ed6\u4f1a\u6682\u65f6\u89e3\u9664\u796d\u7940\u5b66\u9662\u7684\u9b54\u6cd5\u9632\u5fa1\uff0c\u4ee5\u4fbf\u987a\u5229\u5b8c\u6210\u4f20\u9001\u3002\u4f46\u5c31\u4ed6\u521a\u624d\u6240\u89c1\uff0c\u90a3\u4e00\u62b9\u6697\u7d2b\u8272\u5fae\u5149\uff0c\u4e0e\u5176\u8bf4\u662f\u89e3\u5f00\u8fdc\u5904\u7684\u796d\u7940\u5b66\u9662\u7684\u9b54\u6cd5\u7981\u5236\uff0c\u5012\u66f4\u50cf\u662f\u5728\u4f20\u9012\u67d0\u79cd\u8baf\u606f\u3002</p><p>\u4ed6\u8fd8\u6709\u53e6\u5916\u7684\u6f5c\u4f0f\u5728\u6697\u4e2d\u7684\u5e2e\u624b\uff1f</p><p>\u201c\u8be5\u6211\u4eec\u4e86\u3002\u201d\u7075\u5438\u602a\u5deb\u5996\u8bf4\uff0c\u5411\u8eab\u8fb9\u7684\u4e24\u4eba\u793a\u610f\u3002\u743c\u6069\u70b9\u70b9\u5934\uff0c\u548c\u8299\u8389\u5a05\u8d70\u5230\u9b54\u6cd5\u9635\u4e2d\uff0c\u65af\u5170\u666e\u4e5f\u7ad9\u4e86\u8fc7\u6765\uff0c\u53cc\u624b\u5fae\u5fae\u5408\u62e2\uff0c\u5f80\u4e0b\u865a\u865a\u4e00\u6309\u3002</p><p>\u661f\u8fb0\u4f3c\u7684\u5fae\u5149\u4ece\u6cd5\u9635\u5e95\u90e8\u5347\u817e\u8d77\u6765\uff0c\u5f25\u6f2b\u56db\u5468\uff0c\u5c06\u4e09\u4eba\u7684\u8eab\u4f53\u88f9\u4f4f\u3002\u773c\u524d\u7684\u666f\u8c61\u6e10\u6e10\u53d8\u5f97\u7070\u6697\u4e0b\u53bb\uff0c\u4eff\u4f5b\u5265\u79bb\u4e86\u6240\u6709\u8272\u5f69\uff0c\u7d27\u63a5\u7740\u4e27\u5931\u5f62\u4f53\uff0c\u53ea\u5269\u4e0b\u65e0\u6570\u82e5\u9690\u82e5\u73b0\u7684\u7ebf\u6761\uff0c\u6700\u7ec8\u9677\u5165\u5b8c\u5168\u7684\u865a\u7a7a\u4e00\u7247\u3002\u743c\u6069\u604d\u60da\u95f4\u6709\u4e9b\u5931\u795e\uff0c\u5f53\u4ed6\u518d\u6b21\u53cd\u5e94\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u5df2\u7ecf\u7f6e\u8eab\u4e8e\u4e00\u4e2a\u6709\u4e9b\u7c7b\u4f3c\u6559\u5ba4\u7684\u5730\u65b9\uff0c\u684c\u6905\u4e0a\u5e03\u6ee1\u539a\u539a\u7684\u7070\u5c18\uff0c\u663e\u7136\u5df2\u7ecf\u5f88\u4e45\u6ca1\u6709\u88ab\u4f7f\u7528\u8fc7\u4e86\u3002</p><p>\u6bd4\u4ed6\u4eec\u63d0\u524d\u62b5\u8fbe\u7684\u5353\u5c14\u6218\u58eb\u4eec\u5df2\u7ecf\u5f00\u59cb\u51fa\u53d1\uff0c\u65e0\u9700\u7075\u5438\u602a\u4e0b\u4ee4\uff0c\u4ed6\u4eec\u81ea\u52a8\u7f16\u7ec4\u6210\u4e03\u516b\u4e2a\u5c0f\u961f\uff0c\u6084\u6084\u8d70\u51fa\u6559\u5ba4\uff0c\u6ca1\u5165\u9ed1\u6697\u3002\u5f88\u663e\u7136\uff0c\u4ed6\u4eec\u4e8b\u5148\u7ecf\u8fc7\u6f14\u7ec3\uff0c\u4e0d\u4f46\u884c\u52a8\u6709\u5e8f\uff0c\u800c\u4e14\u5bf9\u796d\u7940\u5b66\u9662\u91cc\u7684\u5730\u5f62\u7ed3\u6784\u975e\u5e38\u719f\u6089\u3002\u8fd9\u662f\u4e2a\u5f88\u6709\u8da3\u7684\u4e8b\u60c5\uff0c\u56e0\u4e3a\u796d\u7940\u5b66\u9662\u662f\u4ec5\u6b21\u4e8e\u7f57\u4e1d\u795e\u6bbf\u7684\u5723\u5730\uff0c\u662f\u57f9\u517b\u795e\u540e\u4f8d\u5973\u7684\u5730\u65b9\uff0c\u7537\u6027\u5353\u5c14\u6218\u58eb\u662f\u4e0d\u53ef\u80fd\u88ab\u5141\u8bb8\u5165\u5185\u7684\uff0c\u5373\u4f7f\u662f\u5deb\u5e08\u90fd\u5f88\u96be\u6709\u8fd9\u4e2a\u8d44\u683c\uff0c\u9664\u975e\u662f\u5965\u672f\u5b66\u9662\u7684\u67d0\u4e9b\u9ad8\u9636\u5bfc\u5e08\uff0c\u4ed6\u4eec\u80a9\u8d1f\u7740\u7ef4\u6301\u5b88\u62a4\u796d\u7940\u5b66\u9662\u9b54\u6cd5\u5c4f\u969c\u7684\u4efb\u52a1\uff0c\u6240\u4ee5\u88ab\u5141\u8bb8\u8fdb\u5165\u8fd9\u5ea7\u5efa\u7b51\u3002\u53db\u4e71\u8005\u4eec\uff0c\u662f\u4ece\u54ea\u91cc\u5f04\u5230\u796d\u7940\u5b66\u9662\u7684\u5730\u5f62\u56fe\u4e4b\u7c7b\u7684\u4fe1\u606f\u5462\u3002</p><p>\u6240\u6709\u7684\u5353\u5c14\u6218\u58eb\u90fd\u51fa\u53d1\u5b8c\u6bd5\uff0c\u6559\u5ba4\u91cc\u53c8\u6062\u590d\u4e86\u6c89\u5bc2\u3002\u7075\u5438\u602a\u5deb\u5996\u8f6c\u8fc7\u8138\u6765\uff0c\u770b\u7740\u743c\u6069\u3002\u201c\u6211\u8981\u5728\u8fd9\u91cc\u5e03\u7f6e\u4f20\u9001\u6cd5\u9635\uff0c\u201d\u4ed6\u4f20\u5fc3\u8bf4\uff0c\u201c\u4f60\u662f\u6253\u7b97\u7559\u4e0b\u6765\u966a\u6211\uff0c\u8fd8\u662f\u548c\u83ab\u5c3c\u5361\u5c0f\u59d0\u51fa\u53bb\u8d70\u8d70\uff1f\u201d</p><p>\u743c\u6069\u7565\u7565\u601d\u5fd6\u4e86\u51e0\u79d2\u949f\uff0c\u201c\u6211\u60f3\u6211\u6216\u8bb8\u80fd\u5e2e\u4e0a\u70b9\u5fd9\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5728\u9762\u5bf9\u8fd9\u4e9b\u7267\u5e08\u7684\u65f6\u5019\u3002\u201d</p><p>\u5deb\u5996\u70b9\u70b9\u5934\uff0c\u201c\u90a3\u4e48\u8bf7\u4fbf\u3002\u201d</p><p>\u743c\u6069\u548c\u8299\u8389\u5a05\u8d70\u51fa\u6559\u5ba4\uff0c\u9677\u5165\u6c89\u6c89\u9ed1\u6697\u4e4b\u4e2d\u3002\u4ed6\u4eec\u5e76\u4e0d\u719f\u6089\u796d\u7940\u5b66\u9662\u7684\u5e03\u5c40\uff0c\u597d\u5728\u8fd9\u91cc\u7ec8\u7a76\u662f\u4e2a\u57f9\u517b\u672a\u6765\u7267\u5e08\u7684\u5b66\u9662\uff0c\u5e76\u975e\u5bf9\u4ed8\u654c\u4eba\u7684\u8ff7\u5bab\uff0c\u5012\u4e5f\u7b97\u4e0d\u4e0a\u590d\u6742\u3002\u743c\u6069\u6cbf\u7740\u957f\u5eca\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u7ed3\u5408\u4ee5\u524d\u5728\u5916\u9762\u7684\u89c2\u5bdf\uff0c\u6e10\u6e10\u5224\u65ad\u51fa\u4e86\u81ea\u5df1\u6240\u5904\u7684\u4f4d\u7f6e\u3002\u796d\u7940\u5b66\u9662\u5448\u8718\u86db\u5f62\uff0c\u6709\u516b\u53ea\u7c97\u58ee\u7684\u722a\u5b50\uff0c\u6bcf\u4e2a\u722a\u5b50\u5206\u4e0a\u4e2d\u4e0b\u4e09\u622a\uff0c\u5f62\u6210\u4e09\u4e2a\u697c\u5c42\u3002\u4ed6\u4eec\u4f20\u9001\u62b5\u8fbe\u7684\u8fd9\u4e2a\u7a7a\u7f6e\u7684\u6559\u5ba4\uff0c\u5c31\u662f\u5728\u5176\u4e2d\u4e00\u53ea\u722a\u5b50\u7684\u7b2c\u4e09\u5c42\u4e0a\u3002</p><p>\u8f6c\u8fc7\u4e00\u4e2a\u62d0\u89d2\u4e4b\u540e\uff0c\u743c\u6069\u505c\u4e0b\u811a\u6b65\uff0c\u4ed6\u62ac\u5934\u770b\u7740\u7a7a\u6c14\u4e2d\u5bc6\u5bc6\u531d\u531d\u7684\u9b54\u6cd5\u7075\u7ebf\uff0c\u72b9\u8c6b\u4e86\u7247\u523b\uff0c\u201c\u544a\u8bc9\u8299\u857e\u72c4\uff0c\u201d\u4ed6\u4f4e\u58f0\u5bf9\u8eab\u540e\u7684\u8299\u8389\u5a05\u8bf4\uff0c\u201c\u8ba9\u5979\u901a\u77e5\u83f2\u5c14\u4f26\u5bb6\uff0c\u53db\u4e71\u8005\u5df2\u7ecf\u6f5c\u5165\u796d\u7940\u5b66\u9662\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u53cc\u80de\u80ce\u59d0\u59b9\u7684\u5fc3\u7075\u611f\u5e94\uff0c\u5728\u8ddd\u79bb\u8f83\u8fd1\u65f6\u8db3\u4ee5\u76f4\u63a5\u4f20\u9012\u77ed\u6682\u7684\u610f\u5ff5\u3002\u800c\u4e14\u56e0\u4e3a\u5b83\u5e76\u975e\u9b54\u6cd5\uff0c\u5176\u5b9e\u5012\u6709\u4e9b\u8fd1\u4f3c\u4e8e\u5fc3\u7075\u5f02\u80fd\uff0c\u6240\u4ee5\u4e5f\u4e0d\u4f1a\u88ab\u9b54\u6cd5\u963b\u9694\u3002\u65af\u5170\u666e\u5728\u57ce\u5821\u4e2d\u8bbe\u7f6e\u4e86\u5f3a\u5927\u7684\u5965\u672f\u7ed3\u754c\uff0c\u9694\u7edd\u4e00\u5207\u8baf\u606f\u4f20\u9012\uff0c\u4f46\u8fd9\u6321\u4e0d\u4f4f\u5fc3\u7075\u611f\u5e94\u3002\u8fd9\u5ea7\u796d\u7940\u5b66\u9662\u91cc\u4e5f\u6709\u963b\u9694\u9884\u8a00\u6cd5\u672f\u7684\u9b54\u6cd5\u5c4f\u969c\uff0c\u743c\u6069\u539f\u672c\u6253\u7b97\u76f4\u63a5\u4f20\u8baf\u7ed9\u7ef4\u5eb7\u5c3c\u4e9a\u5c31\u884c\u4e0d\u901a\uff0c\u53ea\u80fd\u8ba9\u8299\u8389\u5a05\u4e2d\u8f6c\u4e86\u3002</p><p>\u5728\u5f97\u77e5\u7075\u5438\u602a\u5deb\u5996\u7684\u8ba1\u5212\u540e\uff0c\u743c\u6069\u5c31\u5df2\u7ecf\u8ba9\u8299\u8389\u5a05\u6084\u6084\u901a\u77e5\u4e86\u8299\u857e\u72c4\uff0c\u63d0\u9192\u83f2\u5c14\u4f26\u4e3b\u6bcd\u5373\u5c06\u53d1\u751f\u66b4\u52a8\uff0c\u544a\u77e5\u4e86\u5927\u81f4\u65f6\u95f4\u3002\u4f46\u5374\u6ca1\u60f3\u5230\u5deb\u5996\u5c45\u7136\u4f1a\u76f4\u63a5\u8fdb\u653b\u796d\u7940\u5b66\u9662\uff0c\u5982\u4eca\u518d\u4e34\u65f6\u901a\u77e5\uff0c\u53ea\u6015\u6709\u4e9b\u665a\u4e86\u3002\u4e0d\u8fc7\u8fd9\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u597d\uff0c\u4e24\u8d25\u4ff1\u4f24\uff0c\u6b63\u662f\u743c\u6069\u6240\u4e50\u4e8e\u89c1\u5230\u7684\u3002\u552f\u4e00\u8ba9\u4ed6\u7591\u8651\u7684\uff0c\u662f\u65af\u5170\u666e\u771f\u6b63\u7684\u8ba1\u5212\u5230\u5e95\u662f\u4ec0\u4e48\u3002</p><p>\u505c\u7559\u4e86\u534a\u5206\u949f\uff0c\u8299\u8389\u5a05\u5b8c\u6210\u4e86\u5fc3\u7075\u611f\u5e94\uff0c\u5979\u5df2\u7ecf\u6210\u529f\u5c06\u8baf\u606f\u544a\u8bc9\u4e86\u8299\u857e\u72c4\u3002\u743c\u6069\u89c2\u5bdf\u4e86\u4e00\u4e0b\u56db\u5468\uff0c\u4f30\u6d4b\u81ea\u5df1\u5df2\u7ecf\u6765\u5230\u4e86\u8718\u86db\u7684\u8179\u90e8\uff0c\u4e5f\u5c31\u662f\u796d\u7940\u5b66\u9662\u7684\u4e2d\u5fc3\u5730\u5e26\uff0c\u8fd9\u91cc\u5e94\u8be5\u662f\u7267\u5e08\u4eec\u5e73\u65f6\u7948\u7977\u3001\u805a\u4f1a\u7684\u5730\u65b9\u3002\u4ed6\u8003\u8651\u662f\u5426\u5e26\u7740\u8299\u8389\u5a05\u5c31\u6b64\u8131\u8eab\uff0c\u79bb\u5f00\u796d\u7940\u5b66\u9662\uff0c\u56de\u5230\u83f2\u5c14\u4f26\u5b85\u9662\u4e2d\u53bb\u4f5c\u58c1\u4e0a\u89c2\u3002\u4f46\u7d27\u63a5\u7740\uff0c\u4ed6\u5c31\u653e\u5f03\u4e86\u8fd9\u4e2a\u5ff5\u5934\uff0c\u73b0\u5728\u8fd8\u4e0d\u662f\u62bd\u8eab\u79bb\u53bb\u7684\u65f6\u5019\uff0c\u4e8b\u60c5\u5c1a\u672a\u7ed3\u675f\uff0c\u9634\u8c0b\u8fd8\u672a\u5c55\u5f00\uff0c\u4e00\u5207\u8fd8\u5e76\u4e0d\u5728\u81ea\u5df1\u7684\u638c\u63e1\u4e4b\u4e2d\u3002</p><p>\u8981\u60f3\u65bd\u52a0\u8db3\u591f\u7684\u5f71\u54cd\u529b\uff0c\u90a3\u4e48\u6700\u597d\u7684\u65b9\u6cd5\u5c31\u662f\u4eb2\u81ea\u53c2\u4e0e\u5176\u4e2d\u3002\u8fd9\u4e2a\u9053\u7406\u672a\u5fc5\u666e\u904d\u9002\u7528\uff0c\u4f46\u5c31\u76ee\u524d\u6765\u8bf4\u662f\u6b63\u786e\u7684\u3002</p><p>\u201c\u4f60\u6709\u6ca1\u6709\u53d1\u89c9\uff0c\u90a3\u4e9b\u5353\u5c14\u6218\u58eb\u4e0d\u592a\u5bf9\u52b2\uff1f\u201d\u8299\u8389\u5a05\u7a81\u7136\u95ee\u3002</p><p>\u201c\u5514\uff1f\u201d\u743c\u6069\u5fae\u5fae\u4e00\u6014\uff0c\u4ed6\u6240\u6709\u7684\u6ce8\u610f\u529b\u57fa\u672c\u90fd\u96c6\u4e2d\u5728\u7075\u5438\u602a\u5deb\u5996\u8eab\u4e0a\uff0c\u552f\u6050\u4ed6\u770b\u51fa\u7834\u7efd\uff0c\u53d1\u89c9\u81ea\u5df1\u7684\u610f\u56fe\uff0c\u5012\u662f\u6ca1\u6709\u592a\u5728\u610f\u90a3\u4e9b\u5353\u5c14\u4eec\uff0c\u201c\u600e\u4e48\u4e0d\u5bf9\u52b2\uff1f\u201d</p><p>\u201c\u6211\u611f\u89c9\u4ed6\u4eec\u7cbe\u795e\u72b6\u6001\u6709\u4e9b\u4e0d\u592a\u6b63\u5e38\uff0c\u201d\u8299\u8389\u5a05\u8bf4\uff0c\u201c\u4f60\u4e0d\u89c9\u5f97\u4ed6\u4eec\u592a\u542c\u8bdd\u4e86\u4e48\u3002\u201d</p><p>\u743c\u6069\u8f7b\u8f7b\u76b1\u8d77\u7709\u5934\uff0c\u201c\u4f60\u7684\u610f\u601d\u662f\u2026\u2026\u201d</p><p>\u201c\u6211\u6000\u7591\u4ed6\u4eec\u88ab\u63a7\u5236\u4e86\u2014\u2014\u6216\u8bb8\u6ca1\u8fd9\u4e48\u660e\u663e\uff0c\u4f46\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u53d7\u5230\u4e86\u5f71\u54cd\u3002\u201d</p><p>\u201c\u5353\u5c14\u5bf9\u9b54\u6cd5\u7684\u62b5\u6297\u80fd\u529b\u5f88\u5f3a\u7684\u3002\u201d</p><p>\u201c\u90a3\u662f\u9488\u5bf9\u9b54\u6cd5\uff0c\u201d\u8299\u8389\u5a05\u63d0\u9192\uff0c\u201c\u6ca1\u6709\u4efb\u4f55\u8bb0\u8f7d\u8868\u660e\u5353\u5c14\u5bf9\u7075\u80fd\u4e5f\u6709\u62b5\u6297\u80fd\u529b\u3002\u201d</p><p>\u743c\u6069\u505c\u4e0b\u811a\u6b65\uff0c\u4ed6\u53d1\u89c9\u8299\u8389\u5a05\u8bf4\u5f97\u5bf9\uff0c\u81ea\u5df1\u786e\u5b9e\u5ffd\u7565\u4e86\u8fd9\u4e00\u70b9\u3002\u5353\u5c14\u64c5\u957f\u5bf9\u6297\u9b54\u6cd5\uff0c\u6240\u4ee5\u60f3\u540c\u65f6\u7528\u9b54\u6cd5\u5f71\u54cd\u6570\u91cf\u8fd9\u6837\u591a\u7684\u5353\u5c14\u662f\u884c\u4e0d\u901a\u7684\uff0c\u603b\u6709\u4eba\u80fd\u62b5\u5fa1\u8fc7\u53bb\u3002\u4f46\u5982\u679c\u662f\u7075\u80fd\u7684\u8bdd\u2026\u2026</p><p>\u65af\u5170\u666e\u867d\u7136\u662f\u4e2a\u5deb\u5996\uff0c\u4f46\u6bd5\u7adf\u8fd8\u662f\u4e2a\u7075\u5438\u602a\u3002</p><p>\u201c\u6216\u8bb8\u5427\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u4e00\u9635\u6253\u6597\u58f0\u4ece\u4fa7\u9762\u4f20\u6765\uff0c\u542c\u58f0\u97f3\u5e76\u4e0d\u5f88\u8fdc\uff0c\u743c\u6069\u548c\u8299\u8389\u5a05\u5bf9\u89c6\u4e00\u773c\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u4ed6\u8c28\u614e\u5730\u4ece\u6597\u7bf7\u5185\u4fa7\u62bd\u51fa\u6cd5\u6756\uff0c\u4e00\u9762\u53d6\u51fa\u9f99\u9cde\u76fe\uff0c\u7528\u5de6\u624b\u63e1\u7740\uff0c\u5f53\u5148\u671d\u90a3\u4e2a\u65b9\u5411\u8d70\u53bb\u3002\u8299\u8389\u5a05\u540c\u6837\u63e1\u7d27\u5723\u5fbd\uff0c\u8ddf\u5728\u4ed6\u540e\u9762\u3002</p><p>\u5728\u957f\u5eca\u7684\u5c3d\u5934\uff0c\u6c89\u9ed8\u5730\u77d7\u7acb\u7740\u4e00\u53ea\u5de8\u5927\u7684\u8718\u86db\u5851\u50cf\uff0c\u770b\u8d77\u6765\u4f3c\u4e4e\u662f\u7528\u67d0\u79cd\u7279\u6b8a\u7684\u77f3\u6599\u5236\u6210\uff0c\u5728\u9ed1\u6697\u4e2d\u95ea\u70c1\u7740\u78a7\u7389\u822c\u7684\u5149\u6cfd\u3002\u743c\u6069\u548c\u8299\u8389\u5a05\u8eb2\u5728\u5851\u50cf\u540e\u9762\uff0c\u89c2\u5bdf\u7740\u524d\u65b9\u5927\u5385\u91cc\u7684\u4e00\u573a\u6076\u6218\u3002\u770b\u8d77\u6765\uff0c\u662f\u4e00\u961f\u53db\u4e71\u7684\u5353\u5c14\u6218\u58eb\u548c\u51e0\u4e2a\u7267\u5e08\u7a81\u7136\u906d\u9047\u4e86\uff0c\u4ed6\u4eec\u81ea\u7136\u6beb\u65e0\u60ac\u5ff5\u5730\u7acb\u523b\u62d4\u5200\u76f8\u5411\uff0c\u4e92\u76f8\u653b\u51fb\u3002\u5728\u6df7\u6218\u7684\u4eba\u7fa4\u5f53\u4e2d\uff0c\u743c\u6069\u53d1\u73b0\u4e86\u4e00\u4e2a\u4f3c\u4e4e\u6709\u4e9b\u773c\u719f\u7684\u8eab\u5f71\uff0c\u90a3\u662f\u4e00\u4e2a\u7267\u5e08\uff0c\u800c\u4e14\u663e\u7136\u662f\u5176\u4e2d\u7684\u9886\u8896\u3002\u4ed6\u76b1\u7709\u60f3\u4e86\u534a\u5929\uff0c\u7136\u540e\u7ec8\u4e8e\u56de\u5fc6\u8fc7\u6765\uff0c\u90a3\u662f\u67e5\u4e3d\u4e1d\u745e\uff0c\u83f2\u5c14\u4f26\u4e3b\u6bcd\u7684\u7b2c\u4e94\u4e2a\u5973\u513f\uff0c\u5979\u662f\u796d\u7940\u5b66\u9662\u7684\u6559\u5b98\u4e4b\u4e00\u3002\u6628\u5929\u5728\u83f2\u5c14\u4f26\u5b85\u9662\uff0c\u743c\u6069\u66fe\u7ecf\u89c1\u8fc7\u5979\u4e00\u9762\uff0c\u5370\u8c61\u4e0d\u6df1\uff0c\u6240\u4ee5\u4e00\u65f6\u4e0d\u66fe\u60f3\u8d77\u6765\u3002</p><p>\u743c\u6069\u4e0d\u6253\u7b97\u7acb\u523b\u63d2\u624b\uff0c\u4ed6\u5728\u9ed1\u6697\u4e2d\u7b49\u5f85\u7740\uff0c\u6ce8\u610f\u573a\u5185\u7684\u5c40\u52bf\u53d8\u5316\u3002</p><p>\u203b\u203b\u203b</p><p>\u6309\u7167\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u89c4\u77e9\uff0c\u796d\u7940\u5b66\u9662\u7684\u9996\u5e2d\u6559\u5b98\u5386\u6765\u90fd\u662f\u7531\u7b2c\u4e00\u5bb6\u65cf\u4e3b\u6bcd\u517c\u4efb\uff0c\u4f46\u53ea\u662f\u865a\u8854\uff0c\u5e76\u4e0d\u8d1f\u8d23\u5177\u4f53\u4e8b\u52a1\u3002\u73b0\u4efb\u7684\u6b21\u5e2d\u6559\u5b98\u2014\u2014\u4e5f\u5c31\u662f\u5b66\u9662\u5b9e\u9645\u7684\u9886\u8896\u2014\u2014\u662f\u8fe6\u6cd5\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u957f\u5973\uff0c\u67e5\u4e3d\u4e1d\u745e\u4e5f\u662f\u6559\u5b98\u4e4b\u4e00\u3002\u4e24\u4e2a\u65f6\u8fb0\u524d\uff0c\u4ed6\u4eec\u63a5\u5230\u6d88\u606f\uff0c\u90a3\u4e9b\u9003\u4ea1\u8005\u548c\u5974\u96b6\u4eec\u5c06\u4f1a\u53d1\u751f\u66b4\u4e71\u3002\u8fe6\u6cd5\u548c\u67e5\u4e3d\u4e1d\u745e\u5306\u5fd9\u8d76\u56de\u796d\u7940\u5b66\u9662\uff0c\u5c06\u6240\u6709\u7684\u6559\u5b98\u548c\u5b66\u751f\u4eec\u7ec4\u7ec7\u8d77\u6765\uff0c\u5168\u526f\u6b66\u88c5\uff0c\u5206\u53d1\u88c5\u5907\u548c\u9b54\u6756\u3001\u5377\u8f74\uff0c\u968f\u65f6\u51c6\u5907\u6295\u5165\u6218\u6597\u3002\u5979\u4eec\u5e76\u6ca1\u6709\u6599\u5230\u53db\u4e71\u8005\u4f1a\u7a81\u88ad\u5b66\u9662\uff0c\u8fd9\u8d85\u51fa\u4e86\u7267\u5e08\u4eec\u7684\u60f3\u8c61\u529b\uff0c\u6216\u8005\u8bf4\uff0c\u5979\u4eec\u592a\u4fe1\u4efb\u5b66\u9662\u7684\u9b54\u6cd5\u9632\u5fa1\u4f53\u7cfb\u4e86\uff0c\u4ee5\u4e3a\u5b83\u662f\u7262\u4e0d\u53ef\u7834\u7684\u3002\u6309\u7167\u539f\u8ba1\u5212\uff0c\u5979\u4eec\u662f\u5e94\u8be5\u4f5c\u4e3a\u4e00\u652f\u540e\u5907\u529b\u91cf\uff0c\u5728\u66b4\u4e71\u53d1\u751f\u540e\u89c6\u60c5\u51b5\u6295\u5165\u6218\u573a\u7684\u3002</p><p>\u67e5\u4e3d\u4e1d\u745e\u5e26\u9886\u7740\u516d\u4e2a\u5b66\u751f\uff0c\u6b63\u51c6\u5907\u524d\u5f80\u5b66\u9662\u524d\u65b9\u7684\u9732\u5929\u5e73\u53f0\u4e0a\u96c6\u7ed3\uff0c\u6ca1\u60f3\u5230\u534a\u8def\u649e\u4e0a\u4e86\u4e00\u961f\u53db\u4e71\u8005\u3002\u5728\u77ed\u6682\u7684\u60ca\u6115\u4e4b\u540e\uff0c\u8fc5\u901f\u800c\u60e8\u70c8\u7684\u6218\u6597\u7206\u53d1\u4e86\uff0c\u53db\u4e71\u8005\u6709\u5907\u800c\u6765\uff0c\u800c\u4e14\u5728\u4eba\u6570\u4e0a\u6709\u4f18\u52bf\uff0c\u6700\u7ec8\u4ed6\u4eec\u5360\u4e86\u4e0a\u98ce\u3002\u5f53\u743c\u6069\u548c\u8299\u8389\u5a05\u8d76\u5230\u65f6\uff0c\u5730\u9762\u4e0a\u5df2\u7ecf\u4e00\u7247\u8840\u8165\uff0c\u5230\u5904\u662f\u6b8b\u7834\u7684\u5353\u5c14\u5c38\u4f53\uff0c\u6709\u4e9b\u8fd8\u6ca1\u6709\u65ad\u6c14\uff0c\u6b63\u53d1\u51fa\u75db\u82e6\u7684\u547b\u541f\uff0c\u5782\u6b7b\u6323\u624e\u3002\u7267\u5e08\u4eec\u5df2\u7ecf\u53ea\u5269\u4e0b\u56db\u4eba\uff0c\u800c\u53db\u4e71\u8005\u8fd8\u6709\u4e03\u4e2a\uff0c\u4ece\u6570\u91cf\u4e0a\u6765\u770b\uff0c\u5dee\u8ddd\u5df2\u7ecf\u5f88\u60ac\u6b8a\u3002\u5982\u679c\u4e0d\u51fa\u610f\u5916\u7684\u8bdd\uff0c\u8fd9\u573a\u906d\u9047\u6218\u5f88\u5feb\u5c31\u4f1a\u7ed3\u675f\u3002</p><p>\u5353\u5c14\u7267\u5e08\u4e5f\u64c5\u957f\u683c\u6597\uff0c\u800c\u4e14\u88c5\u5907\u6bd4\u53db\u4e71\u8005\u66f4\u52a0\u7cbe\u826f\uff0c\u5982\u679c\u5728\u5e73\u65f6\uff0c\u5728\u6709\u6cd5\u672f\u8f85\u52a9\u7684\u524d\u63d0\u4e0b\uff0c\u5979\u4eec\u8db3\u4ee5\u6d88\u706d\u4e09\u500d\u6570\u91cf\u7684\u6218\u58eb\uff0c\u7136\u800c\u73b0\u5728\u662f\u7279\u6b8a\u65f6\u671f\uff0c\u7267\u5e08\u4eec\u65e0\u6cd5\u76f4\u63a5\u91ca\u653e\u9b54\u6cd5\uff0c\u53ea\u80fd\u4f9d\u8d56\u5e73\u65f6\u50a8\u5b58\u7684\u9b54\u6756\u548c\u5377\u8f74\u3002\u9b54\u6756\u7684\u52a3\u52bf\u5728\u4e8e\u53ea\u80fd\u91ca\u653e\u5355\u4e00\u79cd\u7c7b\u7684\u6cd5\u672f\uff0c\u5377\u8f74\u7684\u7f3a\u9677\u5219\u662f\u9700\u8981\u9605\u8bfb\uff0c\u6bd4\u76f4\u63a5\u65bd\u6cd5\u6548\u7387\u4f4e\u5f88\u591a\uff0c\u800c\u8fd9\u4e24\u6837\u90fd\u662f\u81f4\u547d\u7684\u3002</p><p>\u67e5\u4e3d\u4e1d\u745e\u53d7\u4f24\u4e86\uff0c\u5979\u5728\u65bd\u5c55\u6cd5\u672f\u7684\u65f6\u5019\uff0c\u80f3\u818a\u4e0a\u88ab\u91cd\u91cd\u523a\u4e86\u4e00\u77db\uff0c\u6b63\u597d\u5d4c\u5165\u9501\u7532\u7684\u7f1d\u9699\u4e4b\u4e2d\u2014\u2014\u4f17\u6240\u5468\u77e5\uff0c\u7528\u65e0\u6570\u7cbe\u91d1\u73af\u8054\u7ed3\u7f00\u6210\u7684\u9501\u7532\u5bf9\u62b5\u6321\u5200\u5251\u7684\u780d\u5288\u5f88\u6709\u6548\u679c\uff0c\u5374\u5bf9\u7a7f\u523a\u7c7b\u578b\u7684\u653b\u51fb\u8d77\u4e0d\u5230\u591a\u5c11\u9632\u5fa1\u4f5c\u7528\u3002\u867d\u7136\u6709\u9b54\u6cd5\u6597\u7bf7\u7684\u963b\u9694\uff0c\u77db\u5c16\u5e76\u4e0d\u66fe\u6df1\u5165\uff0c\u4f24\u52bf\u5f88\u8f7b\uff0c\u4f46\u8fd9\u4f9d\u65e7\u4e25\u91cd\u5f71\u54cd\u4e86\u5979\u7684\u52a8\u4f5c\u3002\u7531\u4e8e\u5979\u7a7f\u7740\u9576\u7d2b\u8fb9\u7684\u9ed1\u8272\u796d\u53f8\u888d\uff08\u8fd9\u662f\u9ad8\u9636\u7267\u5e08\u624d\u5141\u8bb8\u4f7f\u7528\u7684\u670d\u9970\uff0c\u8bc1\u660e\u4e86\u5979\u7684\u8eab\u4efd\uff09\uff0c\u6240\u4ee5\u53d7\u5230\u683c\u5916\u7684\u201c\u7167\u987e\u201d\uff0c\u81f3\u5c11\u6709\u4e09\u4e2a\u5353\u5c14\u6218\u58eb\u5728\u56f4\u653b\u5979\uff0c\u60f3\u5c06\u8fd9\u4f4d\u6559\u5b98\u6740\u6b7b\u3002\u76f8\u6bd4\u8d77\u6765\uff0c\u5e78\u5b58\u7684\u4e09\u4e2a\u5b66\u751f\u538b\u529b\u8981\u8f7b\u4e00\u4e9b\uff0c\u5979\u4eec\u90fd\u51fa\u8eab\u4e8e\u671b\u65cf\uff0c\u5728\u796d\u7940\u5b66\u9662\u91cc\u5df2\u7ecf\u5ea6\u8fc7\u4e86\u8fd1\u4e8c\u5341\u5e74\uff0c\u5373\u5c06\u6bd5\u4e1a\u6210\u4e3a\u6b63\u5f0f\u7684\u795e\u540e\u796d\u53f8\uff0c\u80fd\u529b\u4e0d\u5dee\u3002</p><p>\u5200\u5251\u78b0\u649e\u51fa\u7684\u706b\u661f\u548c\u7eda\u70c2\u7684\u9b54\u6cd5\u6548\u679c\u4ea4\u9519\u5728\u4e00\u8d77\uff0c\u8ba9\u9ed1\u6697\u7684\u5927\u5385\u91cc\u5ffd\u660e\u5ffd\u6697\u3002\u7c97\u91cd\u7684\u5598\u606f\u58f0\u8d8a\u6765\u8d8a\u6e05\u6670\uff0c\u5267\u6597\u8ba9\u53cc\u65b9\u90fd\u5df2\u7ecf\u7cbe\u75b2\u529b\u7aed\u3002\u5f88\u5feb\uff0c\u53c8\u6709\u51e0\u4e2a\u4eba\u5012\u4e0b\u4e86\uff0c\u6b64\u65f6\u7267\u5e08\u53ea\u5269\u4e0b\u5305\u62ec\u67e5\u4e3d\u4e1d\u745e\u5728\u5185\u7684\u4e24\u4eba\uff0c\u53db\u4e71\u8005\u5219\u8fd8\u6709\u56db\u4e2a\uff0c\u5f62\u52bf\u53d8\u5f97\u66f4\u52a0\u4e0d\u5229\u3002\u67e5\u4e3d\u4e1d\u745e\u9000\u5230\u5899\u89d2\uff0c\u4e22\u4e0b\u624b\u4e2d\u7684\u9b54\u6756\uff0c\u4ece\u8170\u95f4\u53d6\u51fa\u4e00\u5f20\u5377\u8f74\u5feb\u901f\u5ff5\u8bf5\u8d77\u6765\uff1b\u5b66\u751f\u5219\u627f\u62c5\u4e86\u4fdd\u62a4\u8005\u7684\u89d2\u8272\uff0c\u5979\u6325\u821e\u7740\u86c7\u9996\u97ad\uff0c\u963b\u6321\u7740\u53db\u4e71\u8005\u7684\u653b\u51fb\u3002\u4e24\u4e2a\u4f7f\u7528\u5de8\u5251\u7684\u5353\u5c14\u6218\u58eb\u5206\u5de6\u53f3\u62a2\u4e0a\uff0c\u62b5\u4f4f\u4e86\u5b66\u751f\uff0c\u8eab\u540e\u53e6\u5916\u4e24\u4e2a\u5353\u5c14\u6218\u58eb\u540c\u65f6\u4e3e\u8d77\u5355\u624b\u5f29\uff0c\u671d\u6b63\u5728\u65bd\u6cd5\u7684\u67e5\u4e3d\u4e1d\u745e\u5c04\u53bb\u3002</p><p>\u7bad\u77e2\u6495\u88c2\u7a7a\u6c14\uff0c\u547c\u5578\u7740\u5c04\u4e2d\u4e86\u7267\u5e08\uff0c\u4f46\u88ab\u7d27\u88f9\u7740\u8eab\u4f53\u7684\u9b54\u6cd5\u6597\u7bf7\u6321\u4e86\u4e0b\u6765\u3002\u67d4\u97e7\u7684\u5e03\u6599\u5728\u4e00\u77ac\u95f4\u53d8\u5f97\u4eff\u4f5b\u7cbe\u94a2\uff0c\u5c06\u7bad\u77e2\u5f39\u4e86\u5f00\u53bb\uff0c\u4f46\u5353\u5c14\u6218\u58eb\u7d27\u63a5\u7740\u63b7\u51fa\u4e86\u4e00\u679a\u71c3\u70e7\u5f39\u3002\u67e5\u4e3d\u4e1d\u745e\u8eab\u4e0a\u7684\u6cd5\u672f\u9632\u5fa1\u8d77\u4e86\u4f5c\u7528\uff0c\u71c3\u70e7\u5f39\u88ab\u65e0\u5f62\u7684\u6c14\u5899\u5f39\u4e86\u5f00\u53bb\uff0c\u5728\u534a\u7a7a\u4e2d\u5316\u4f5c\u4e00\u56e2\u5de8\u5927\u7684\u706b\u7130\uff0c\u6b63\u597d\u843d\u5728\u4e00\u540d\u5353\u5c14\u6218\u58eb\u7684\u5934\u4e0a\u3002\u8fd9\u4e2a\u4e0d\u5e78\u7684\u5bb6\u4f19\u5927\u53eb\u7740\uff0c\u7ffb\u5012\u5728\u5730\uff0c\u77ac\u95f4\u88ab\u718a\u718a\u70c8\u7130\u541e\u566c\u3002</p><p>\u73b0\u5728\u4eba\u6570\u662f\u4e8c\u6bd4\u4e09\u3002</p><p>\u67e5\u4e3d\u4e1d\u745e\u7ec8\u4e8e\u6210\u529f\u5b8c\u6210\u4e86\u6cd5\u672f\uff0c\u5979\u624b\u4e2d\u7684\u5377\u8f74\u5728\u5239\u90a3\u95f4\u53d8\u6210\u5fae\u5c18\u7c89\u672b\uff0c\u56db\u6563\u800c\u53bb\uff0c\u4e00\u9053\u8000\u773c\u7684\u767d\u5149\u4ece\u5979\u638c\u5fc3\u53d1\u51fa\uff0c\u76ee\u6807\u5374\u5e76\u975e\u53db\u4e71\u8005\uff0c\u800c\u662f\u6b63\u4e3a\u5979\u594b\u529b\u62b5\u5fa1\u653b\u51fb\u4ee5\u4e89\u53d6\u65bd\u6cd5\u65f6\u95f4\u7684\u5b66\u751f\u3002\u4e00\u77ac\u95f4\u540e\uff0c\u53d8\u5316\u53d1\u751f\u4e86\uff0c\u7d2b\u8272\u548c\u7ea2\u8272\u7684\u6d53\u70df\u4ece\u5b66\u751f\u7684\u53e3\u9f3b\u4e03\u7a8d\u53d1\u51fa\uff0c\u5c06\u5979\u5305\u88f9\u5728\u5176\u4e2d\uff0c\u7d27\u63a5\u7740\u5f00\u59cb\u5267\u70c8\u53d8\u5f62\u3002\u5979\u7684\u4e0a\u534a\u8eab\u4f9d\u65e7\u5927\u4f53\u4fdd\u6301\u7740\u5353\u5c14\u7684\u5f62\u6001\uff0c\u53ea\u662f\u7a81\u7136\u53d8\u5f97\u6d6e\u80bf\u8d77\u6765\uff0c\u81ea\u8170\u90e8\u4ee5\u4e0b\u5374\u53d8\u6210\u4e86\u5e9e\u5927\u7684\u9ed1\u8272\u8718\u86db\uff0c\u62e5\u6709\u516b\u4e2a\u7c97\u58ee\u7684\uff0c\u6bdb\u8338\u8338\u7684\u8282\u80a2\u817f\u3002\u5b66\u751f\u4f4e\u5934\u770b\u7740\u81ea\u5df1\u8eab\u4f53\u7684\u53d8\u5316\uff0c\u6e05\u79c0\u7684\u8138\u5e9e\u4e0a\u9732\u51fa\u60ca\u9a87\u81f3\u6781\u7684\u8868\u60c5\uff0c\u7136\u800c\u7a81\u7136\u957f\u51fa\u7684\u4e24\u9897\u7360\u7259\u7834\u574f\u4e86\u539f\u672c\u7684\u7f8e\u611f\u3002\u6b63\u5728\u56f4\u653b\u7684\u4e09\u540d\u5353\u5c14\u6218\u58eb\u90fd\u62bd\u4e86\u53e3\u51b7\u6c14\uff0c\u5012\u9000\u534a\u6b65\uff0c\u4ed6\u4eec\u8ba4\u51fa\u4e86\u8fd9\u79cd\u602a\u7269\u3002</p><p>\u201c\u7f57\u4e1d\u7684\u8bc5\u5492\u201d\u2014\u2014\u86db\u5316\u7cbe\u7075\u3002</p><p>\u203b\u203b\u203b</p><p>\u7f57\u4e1d\u7684\u5c0a\u79f0\u662f\u8718\u86db\u795e\u540e\uff0c\u5979\u7684\u6559\u6d3e\u81ea\u7136\u4ee5\u8718\u86db\u4e3a\u5723\u7269\u3002\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\uff0c\u4f24\u5bb3\u8718\u86db\u6216\u8005\u7834\u574f\u8718\u86db\u7f51\u90fd\u662f\u91cd\u7f6a\uff0c\u4f1a\u88ab\u5904\u4ee5\u6b7b\u5211\uff0c\u5e76\u4e14\u7528\u86db\u7f51\u5c06\u5c38\u4f53\u540a\u8d77\u6765\uff0c\u6302\u5728\u57ce\u5e02\u7684\u9ad8\u5904\u6162\u6162\u8150\u70c2\uff0c\u4ee5\u6b64\u8b66\u544a\u5176\u4ed6\u4eba\u3002\u7136\u800c\u5e76\u975e\u6240\u6709\u548c\u8718\u86db\u6709\u5173\u7684\u7269\u4f53\u90fd\u662f\u795e\u5723\u7684\uff0c\u86db\u5316\u7cbe\u7075\u5c31\u662f\u5178\u578b\u7684\u53cd\u4f8b\u3002</p><p>\u6240\u6709\u7684\u5353\u5c14\u90fd\u662f\u79ef\u6781\u5411\u4e0a\uff0c\u5177\u6709\u5f3a\u70c8\u7684\u8fdb\u53d6\u5fc3\u2014\u2014\u6216\u8005\u8bf4\u91ce\u5fc3\u2014\u2014\u7684\u751f\u7269\uff0c\u4ed6\u4eec\u68a6\u5bd0\u4ee5\u6c42\u7684\u4eba\u751f\u76ee\u6807\u5c31\u662f\u9664\u6389\u524d\u9762\u7684\u969c\u788d\uff0c\u722c\u5230\u6743\u4f4d\u7684\u6700\u9ad8\u7aef\uff0c\u628a\u5176\u4ed6\u6240\u6709\u4eba\u90fd\u8e29\u5728\u811a\u4e0b\u3002\u5bf9\u4e8e\u4ed6\u4eec\u6765\u8bf4\uff0c\u5584\u826f\u3001\u6b63\u4e49\u3001\u53cb\u8c0a\u3001\u7231\u60c5\u8fd9\u4e9b\u90fd\u662f\u5c5e\u4e8e\u52a3\u7b49\u79cd\u65cf\u7684\u8f6f\u5f31\u611f\u60c5\uff0c\u9ad8\u8d35\u7684\u5353\u5c14\u5bf9\u6b64\u4e0d\u5c51\u4e00\u987e\u3002\u795e\u540e\u7f57\u4e1d\u9f13\u52b1\u8fd9\u79cd\u60f3\u6cd5\uff0c\u5979\u559c\u6b22\u770b\u5230\u9634\u8c0b\u3001\u6df7\u4e71\u548c\u80cc\u4fe1\u5f03\u4e49\uff0c\u8fd9\u65e2\u8ba9\u5979\u611f\u89c9\u6109\u60a6\uff0c\u540c\u65f6\u66f4\u662f\u5979\u795e\u529b\u7684\u6e90\u6cc9\u3002\u4e3a\u4e86\u66f4\u597d\u5730\u5f70\u663e\u610f\u5fd7\uff0c\u7f57\u4e1d\u4f1a\u8fdb\u884c\u4e00\u9879\u5a31\u4e50\uff1a\u5979\u6beb\u65e0\u4efb\u4f55\u9884\u5146\u548c\u903b\u8f91\u53ef\u8a00\u5730\u5728\u5979\u7684\u5b50\u6c11\u4e2d\u6311\u9009\u67d0\u4e2a\u5e78\u8fd0\u8005\uff0c\u8d4b\u4e88\u4ed6\uff08\u6216\u8005\u5979\uff09\u67d0\u4e2a\u5371\u9669\u7684\u8bd5\u70bc\u3002\u5982\u679c\u6210\u529f\uff0c\u90a3\u4e48\u4f1a\u6709\u4e30\u539a\u7684\u5956\u8d4f\uff0c\u4f46\u5982\u679c\u5931\u8d25\uff0c\u5219\u4f1a\u88ab\u964d\u4e0b\u6076\u6bd2\u7684\u8bc5\u5492\uff0c\u6210\u4e3a\u86db\u5316\u7cbe\u7075\u3002\u540e\u6765\uff0c\u7f57\u4e1d\u5c06\u8fd9\u9053\u795e\u672f\u8d50\u4e88\u5979\u7684\u9ad8\u9636\u7267\u5e08\uff0c\u5141\u8bb8\u5979\u4eec\u8fd0\u7528\u5b83\u53bb\u60e9\u7f5a\u90a3\u4e9b\u4e0d\u656c\u795e\u540e\u7684\u654c\u4eba\u3002</p><p>\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\uff0c\u80fd\u591f\u4f7f\u7528\u8fd9\u9053\u795e\u672f\u7684\u4eba\u4e0d\u5c11\uff0c\u7136\u800c\u4ece\u6765\u6ca1\u6709\u8c01\u80c6\u6562\u7528\u5b83\u53bb\u5bf9\u4ed8\u81ea\u5df1\u7684\u7267\u5e08\u540c\u50da\u2014\u2014\u8fd9\u662f\u4e0d\u5141\u8bb8\u7684\uff0c\u9664\u975e\u80fd\u786e\u5b9a\u5bf9\u65b9\u5df2\u7ecf\u4e27\u5931\u795e\u540e\u7684\u6069\u5ba0\u4e86\u3002</p><p>\u67e5\u4e3d\u4e1d\u745e\u8fdd\u53cd\u4e86\u89c4\u77e9\uff0c\u5f53\u7136\u5979\u8fd9\u4e48\u505a\u4e5f\u6709\u81ea\u5df1\u7684\u7406\u7531\u3002\u4efb\u4f55\u53d8\u5f62\u6cd5\u672f\u90fd\u662f\u5728\u5f3a\u884c\u6539\u53d8\u5bf9\u65b9\u7684\u8eab\u4f53\u7ed3\u6784\uff0c\u90fd\u4f1a\u53d7\u5230\u62b5\u5fa1\u548c\u53cd\u6297\uff0c\u5982\u679c\u5979\u5bf9\u5f3a\u58ee\u7684\u5353\u5c14\u6218\u58eb\u4f7f\u7528\u8fd9\u9053\u201c\u8718\u86db\u8bc5\u5492\u201d\uff0c\u5f88\u53ef\u80fd\u4f1a\u88ab\u62b5\u6297\u6389\uff0c\u4e0d\u8d77\u4f5c\u7528\uff0c\u4f46\u7528\u5728\u4e0d\u66fe\u9632\u5907\u7684\u5b66\u751f\u8eab\u4e0a\uff0c\u90a3\u6210\u529f\u7684\u6982\u7387\u5c31\u9ad8\u5f97\u591a\u4e86\u3002\u800c\u4e8b\u5b9e\u8bc1\u660e\u5979\u662f\u5bf9\u7684\u3002</p><p>\u88ab\u53d8\u6210\u86db\u5316\u7cbe\u7075\u7684\u5b66\u751f\u53d1\u51fa\u51c4\u5389\u7684\u543c\u53eb\uff0c\u8f6c\u8fc7\u5934\u6765\uff0c\u4f3c\u4e4e\u60f3\u8981\u5411\u67e5\u4e3d\u4e1d\u745e\u62a5\u4ec7\uff0c\u7136\u800c\u5f88\u5feb\u5979\u7684\u610f\u8bc6\u5c31\u88ab\u6559\u5b98\u63a7\u5236\u4e86\u3002\u67e5\u4e3d\u4e1d\u745e\u5728\u5fc3\u4e2d\u5475\u65a5\u7740\uff0c\u547d\u4ee4\u86db\u5316\u7cbe\u7075\u53bb\u653b\u51fb\u53db\u4e71\u8005\uff0c\u8fd9\u9053\u547d\u4ee4\u65e0\u6cd5\u88ab\u53cd\u6297\u3002\u86db\u5316\u7cbe\u7075\u8f6c\u800c\u9762\u5bf9\u4e09\u4e2a\u5353\u5c14\u6218\u58eb\uff0c\u5979\u5c06\u6240\u6709\u7684\u6124\u6012\u548c\u6028\u6068\u90fd\u53d1\u6cc4\u5728\u4ed6\u4eec\u8eab\u4e0a\uff0c\u7c97\u58ee\u7684\u524d\u722a\u6325\u821e\u7740\uff0c\u6bd4\u5200\u5251\u7684\u5229\u5203\u66f4\u52a0\u950b\u9510\uff0c\u800c\u4e14\u529b\u5927\u65e0\u6bd4\u3002\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4e00\u4e2a\u5353\u5c14\u6218\u58eb\u7684\u5934\u9885\u88ab\u5979\u524a\u4e86\u4e0b\u6765\uff0c\u9c9c\u8840\u4ece\u8154\u4e2d\u51b2\u51fa\uff0c\u51e0\u4e4e\u5f62\u6210\u7ea2\u8272\u7684\u55b7\u6cc9\u3002\u67e5\u4e3d\u4e1d\u745e\u62e3\u8d77\u5730\u4e0a\u7684\u5355\u624b\u5f29\uff0c\u671d\u5176\u4e2d\u4e00\u4eba\u5c04\u51fb\uff0c\u7bad\u77e2\u51c6\u786e\u5730\u547d\u4e2d\u4e86\u9762\u90e8\uff0c\u8ba9\u4ed6\u6beb\u65e0\u60ac\u5ff5\u5730\u5012\u4e86\u4e0b\u53bb\u3002\u6700\u540e\u4e00\u4e2a\u53db\u4e71\u8005\u89c1\u52bf\u4e0d\u5999\u51c6\u5907\u9003\u8dd1\uff0c\u4f46\u5df2\u7ecf\u8fdf\u4e86\u3002\u7267\u5e08\u634f\u788e\u4e86\u4e00\u679a\u7389\u767d\u8272\u540a\u5760\uff0c\u4ece\u4e2d\u5c04\u51fa\u4e03\u679a\u900f\u660e\u7684\u98de\u9556\uff0c\u5176\u4e2d\u6709\u56db\u679a\u51fb\u7a7a\uff0c\u4f46\u5269\u4e0b\u4e09\u679a\u5168\u9489\u5728\u4e86\u4ed6\u7684\u817f\u4e0a\u3002\u5353\u5c14\u6218\u58eb\u8e09\u8dc4\u6454\u5012\uff0c\u88ab\u86db\u5316\u7cbe\u7075\u8d76\u4e0a\uff0c\u5229\u722a\u5b9b\u5982\u98ce\u8f66\u822c\u51f6\u72e0\u5730\u8f6e\u756a\u5207\u4e0b\uff0c\u5c06\u4ed6\u7684\u8eab\u4f53\u5241\u6210\u4e86\u788e\u5757\u3002</p><p>\u53db\u4e71\u8005\u88ab\u6d88\u706d\u4e86\u3002</p><p>\u4f46\u6218\u6597\u5e76\u6ca1\u6709\u7ed3\u675f\u3002</p><p>\u86db\u5316\u7cbe\u7075\u7f13\u7f13\u8f6c\u8fc7\u8eab\uff0c\u6162\u6162\u671d\u67e5\u4e3d\u4e1d\u745e\u8d70\u53bb\uff0c\u5979\u7684\u773c\u4e2d\u95ea\u70c1\u7740\u4ec7\u6068\u7684\u7ea2\u5149\uff0c\u6b65\u4f10\u6709\u4e9b\u50f5\u786c\uff0c\u4eff\u4f5b\u8981\u514b\u670d\u65e0\u5f62\u7684\u963b\u788d\uff0c\u4f46\u4f9d\u65e7\u575a\u5b9a\u3002\u7267\u5e08\u80fd\u591f\u6682\u65f6\u63a7\u5236\u5979\u53d8\u5f62\u7684\u86db\u5316\u7cbe\u7075\uff0c\u4f46\u6ca1\u529e\u6cd5\u4e00\u76f4\u4fdd\u6301\u4e0b\u53bb\u3002\u521a\u624d\u6709\u5176\u4ed6\u654c\u4eba\u5728\u573a\uff0c\u67e5\u4e3d\u4e1d\u745e\u53ef\u4ee5\u5de7\u5999\u5730\u5c06\u86db\u5316\u7cbe\u7075\u7684\u6012\u706b\u8f6c\u79fb\u5230\u53db\u4e71\u8005\u8eab\u4e0a\uff0c\u4f46\u5982\u4eca\u4ed6\u4eec\u90fd\u5df2\u7ecf\u6b7b\u5149\u4e86\u3002\u5f53\u5728\u573a\u4eba\u6570\u53ea\u5269\u4e0b\u4e24\u4e2a\u65f6\uff08\u5ffd\u7565\u9690\u85cf\u5728\u6697\u5904\u7684\u743c\u6069\u548c\u8299\u8389\u5a05\uff09\uff0c\u77db\u76fe\u4fbf\u5f00\u59cb\u8fdb\u4e00\u6b65\u6fc0\u5316\u3002</p><p>\u82e5\u5728\u5e73\u65f6\uff0c\u6b63\u5e38\u7684\u7a0b\u5e8f\u662f\u7267\u5e08\u5bf9\u654c\u4eba\u65bd\u52a0\u8bc5\u5492\uff0c\u8ba9\u4ed6\u6210\u4e3a\u86db\u5316\u7cbe\u7075\uff0c\u7136\u540e\u7387\u9886\u5353\u5c14\u4eec\u5c06\u4ed6\u9a71\u9010\u51fa\u57ce\u5e02\uff0c\u4efb\u5176\u5728\u5371\u673a\u56db\u4f0f\u7684\u91ce\u5916\u81ea\u751f\u81ea\u706d\u3002\u8fd9\u662f\u4e00\u79cd\u8363\u8000\u86db\u540e\u7684\u4eea\u5f0f\uff0c\u662f\u9700\u8981\u90d1\u91cd\u5176\u4e8b\u5927\u5f20\u65d7\u9f13\u8fdb\u884c\u7684\u3002\u7136\u800c\u5982\u4eca\u67e5\u4e3d\u4e1d\u745e\u592a\u5306\u5fd9\u4e86\uff0c\u5979\u8eab\u8fb9\u751a\u81f3\u6ca1\u6709\u4e00\u4e2a\u52a9\u624b\u3002</p><p>\u4e00\u5bf9\u4e00\uff0c\u4e27\u5931\u4e86\u795e\u672f\u7684\u7267\u5e08\uff0c\u5e76\u4e0d\u80fd\u4fdd\u8bc1\u81ea\u5df1\u4f1a\u80dc\u8fc7\u86db\u5316\u7cbe\u7075\u3002</p><p>\u67e5\u4e3d\u4e1d\u745e\u5f80\u540e\u9000\uff0c\u5979\u7684\u86c7\u9996\u97ad\u5728\u521a\u624d\u7684\u6df7\u6218\u4e2d\u88ab\u51fb\u98de\u4e86\uff0c\u6b64\u65f6\u624b\u4e2d\u9664\u4e86\u4e00\u526f\u5355\u624b\u5f29\u522b\u65e0\u5176\u4ed6\uff0c\u800c\u8fd9\u663e\u7136\u4e0d\u662f\u4e2a\u8d81\u624b\u7684\u6b66\u5668\u3002\u5979\u8003\u8651\u9003\u8dd1\uff0c\u5353\u5c14\u5e76\u6ca1\u6709\u82f1\u52c7\u548c\u8363\u8a89\u7684\u89c2\u5ff5\uff0c\u7136\u800c\u5979\u53ea\u6709\u4e24\u6761\u817f\uff0c\u5bf9\u65b9\u5374\u6709\u516b\u53ea\u811a\uff0c\u800c\u4e14\u5979\u4eec\u540c\u6837\u719f\u6089\u5b66\u9662\u7684\u5730\u5f62\u3002\u5c31\u7b97\u80fd\u6210\u529f\u9003\u8131\uff0c\u5982\u679c\u8ba9\u5176\u4ed6\u7267\u5e08\u53d1\u73b0\u5979\u5c45\u7136\u64c5\u81ea\u5c06\u5b66\u751f\u53d8\u6210\u86db\u5316\u7cbe\u7075\uff0c\u90a3\u6beb\u65e0\u7591\u95ee\u4e5f\u4f1a\u7559\u4e0b\u653b\u51fb\u7684\u501f\u53e3\uff0c\u5f71\u54cd\u5979\u7684\u524d\u9014\u3002</p><p>\u53ea\u80fd\u6740\u6389\u8fd9\u4e2a\u86db\u5316\u7cbe\u7075\u3002</p><p>\u201c\u9547\u9759\u70b9\uff0c\u5384\u52d2\u5a03\uff0c\u201d\u6559\u5b98\u67d4\u58f0\u53eb\u7740\u5979\u4ee5\u524d\u5b66\u751f\u7684\u540d\u5b57\uff0c\u8ba9\u58f0\u97f3\u91cc\u63ba\u6742\u8fdb\u4e9b\u8bb8\u7684\u9b54\u529b\uff0c\u5b89\u629a\u7740\uff0c\u201c\u9547\u9759\u4e9b\uff0c\u522b\u51b2\u52a8\u2026\u2026\u201d</p><p>\u86db\u5316\u7cbe\u7075\u903c\u8fd1\u4e24\u6b65\u3002</p><p>\u201c\u4f60\u77e5\u9053\uff0c\u6709\u4e9b\u4e8b\u60c5\u662f\u8feb\u4e0d\u5f97\u5df2\uff0c\u201d\u6559\u5b98\u6162\u6162\u540e\u9000\uff0c\u201c\u5982\u679c\u6211\u4e0d\u8fd9\u4e48\u505a\uff0c\u6211\u4eec\u4e24\u4e2a\u90fd\u4f1a\u6b7b\u3002\u800c\u73b0\u5728\u6211\u4eec\u90fd\u6d3b\u7740\u2026\u2026\u201d</p><p>\u86db\u5316\u7cbe\u7075\u56de\u5e94\u5979\u7684\u662f\u4e00\u9635\u4f4e\u6c89\u7684\u5636\u543c\u3002</p><p>\u201c\u522b\u51b2\u52a8\uff0c\u5384\u52d2\u5a03\uff0c\u8fd9\u6ca1\u4ec0\u4e48\u5927\u4e0d\u4e86\uff0c\u4f60\u770b\uff0c\u5443\uff0c\u6211\u662f\u8bf4\u2026\u2026\u201d\u67e5\u4e3d\u4e1d\u745e\u5728\u8111\u4e2d\u5feb\u901f\u641c\u5bfb\u7740\u8bed\u53e5\uff0c\u7136\u540e\u5979\u6709\u4e86\u4e3b\u610f\uff0c\u201c\u6211\u7684\u610f\u601d\u662f\u8bf4\uff0c\u6211\u53ef\u4ee5\u628a\u4f60\u518d\u53d8\u56de\u6765\u3002\u201d</p><p>\u86db\u5316\u7cbe\u7075\u505c\u6b62\u903c\u8fd1\uff0c\u5979\u663e\u7136\u88ab\u8fd9\u53e5\u8bdd\u6253\u52a8\u4e86\u3002</p><p>\u201c\u65bd\u2026\u2026\u6cd5\u2026\u2026\u628a\u2026\u2026\u6211\u2026\u2026\u53d8\u56de\u2026\u2026\u6765\uff01\u201d</p><p>\u56e0\u4e3a\u53d8\u5316\u7684\u7f18\u6545\uff0c\u5979\u7684\u53e3\u9f7f\u53d8\u5f97\u542b\u7cca\u4e0d\u6e05\uff0c\u65b0\u957f\u51fa\u7684\u4e24\u9897\u7360\u7259\u4e5f\u4e25\u91cd\u59a8\u788d\u4e86\u8bf4\u8bdd\uff0c\u4f46\u5979\u7684\u610f\u601d\u4f9d\u65e7\u6e05\u695a\u5730\u8868\u8fbe\u4e86\u51fa\u6765\u3002\u67e5\u4e3d\u4e1d\u745e\u5fc3\u4e2d\u6697\u559c\uff0c\u201c\u6211\u9700\u8981\u53bb\u51c6\u5907\u4e00\u4e9b\u6750\u6599\u3002\u201d\u5979\u8bf4\uff0c\u5176\u5b9e\u662f\u62d6\u5ef6\u65f6\u95f4\uff0c\u56e0\u4e3a\u5979\u6839\u672c\u5c31\u529e\u4e0d\u5230\uff0c\u86db\u5316\u7cbe\u7075\u4e0d\u662f\u666e\u901a\u7684\u53d8\u5f62\uff0c\u662f\u795e\u540e\u8d50\u4e0b\u7684\u8bc5\u5492\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u9006\u8f6c\u7684\u3002</p><p>\u201c\u73b0\u5728\uff01\u201d\u86db\u5316\u7cbe\u7075\u4f4e\u543c\u3002</p><p>\u201c\u597d\u5427\uff0c\u597d\u5427\uff0c\u201d\u67e5\u4e3d\u4e1d\u745e\u8bf4\uff0c\u5979\u4ece\u8170\u95f4\u53e3\u888b\u91cc\u53d6\u51fa\u4e00\u5f20\u5377\u8f74\uff0c\u201c\u65e2\u7136\u4f60\u8fd9\u4e48\u8feb\u5207\u5e0c\u671b\u7684\u8bdd\u2026\u2026\u201d</p><p>\u5979\u5f00\u59cb\u6025\u901f\u5730\u8bf5\u8bfb\u5377\u8f74\uff0c\u90a3\u8bed\u53e5\u51b0\u51b7\u800c\u5371\u9669\uff0c\u8574\u542b\u7740\u6d53\u70c8\u7684\u6740\u610f\uff0c\u5373\u4f7f\u662f\u5b8c\u5168\u4e0d\u61c2\u5353\u5c14\u795e\u672f\u7684\u743c\u6069\u90fd\u542c\u4e86\u51fa\u6765\u3002\u86db\u5316\u7cbe\u7075\u6123\u4e86\u4e00\u4e0b\uff0c\u7d27\u63a5\u7740\u5c16\u53eb\u8d77\u6765\uff0c\u6325\u821e\u7740\u722a\u5b50\u51b2\u4e0a\u524d\u3002</p><p>\u94f6\u8272\u7684\u95ea\u7535\u4ece\u7267\u5e08\u7684\u638c\u4e2d\u5c04\u51fa\uff0c\u50cf\u957f\u77db\u4e00\u6837\u6253\u5728\u86db\u5316\u7cbe\u7075\u7684\u8eab\u4e0a\uff0c\u8ba9\u5979\u5168\u8eab\u9ebb\u75f9\u3002\u67e5\u4e3d\u4e1d\u745e\u6293\u7d27\u65f6\u673a\u5954\u8dd1\u8d77\u6765\uff0c\u4ece\u86db\u5316\u7cbe\u7075\u8eab\u8fb9\u8d8a\u8fc7\uff0c\u4e22\u4e0b\u5355\u624b\u5f29\uff0c\u62e3\u8d77\u5730\u4e0a\u7684\u86c7\u9996\u97ad\u3002\u6709\u4e24\u4e2a\u86c7\u5934\u5728\u6b64\u524d\u6218\u6597\u4e2d\u524a\u65ad\u4e86\uff0c\u4f46\u8fd8\u5269\u56db\u9897\u86c7\u5934\uff0c\u5b83\u4eec\u53d1\u51fa\u5636\u5636\u7684\u5174\u594b\u58f0\uff0c\u6b22\u8fce\u7740\u4e3b\u4eba\u3002\u7267\u5e08\u4e3e\u8d77\u97ad\u5b50\uff0c\u91cd\u91cd\u5730\u6253\u5728\u86db\u5316\u7cbe\u7075\u7684\u80cc\u4e0a\uff0c\u56db\u9897\u86c7\u5934\u540c\u65f6\u5f20\u5f00\u5229\u53e3\uff0c\u54ac\u4f4f\u654c\u4eba\uff0c\u5c06\u6bd2\u6db2\u6ce8\u5165\u3002</p><p>\u82e5\u662f\u5e38\u4eba\uff0c\u88ab\u8fd9\u6837\u7684\u6bd2\u6db2\u6ce8\u5165\uff0c\u7acb\u523b\u5c31\u4f1a\u6c14\u7edd\u8eab\u4ea1\uff0c\u4f46\u86db\u5316\u7cbe\u7075\u5728\u8eab\u4f53\u6539\u53d8\u7684\u540c\u65f6\uff0c\u4e5f\u62e5\u6709\u4e86\u5bf9\u6bd2\u7d20\u7684\u62b5\u6297\u80fd\u529b\u3002\u5979\u98a4\u6296\u7740\uff0c\u611f\u89c9\u706b\u8fa3\u8fa3\u7684\u70ed\u6d41\u4ece\u80cc\u4e0a\u900f\u5165\uff0c\u4eff\u4f5b\u706b\u7130\u4e00\u6837\u5728\u8eab\u4f53\u91cc\u51b2\u649e\u7740\uff0c\u711a\u70e7\u4e00\u5207\u3002\u86db\u5316\u7cbe\u7075\u626d\u8f6c\u8eab\u4f53\uff0c\u722a\u5b50\u671d\u654c\u4eba\u5288\u4e0b\uff0c\u7267\u5e08\u5306\u5fd9\u62d4\u51fa\u86c7\u9996\u97ad\uff0c\u9ad8\u4e3e\u4e00\u6321\u3002\u86db\u722a\u649e\u4e0a\u4e86\u7cbe\u91d1\u624b\u67c4\uff0c\u94ff\u9535\u58f0\u4e2d\uff0c\u86c7\u9996\u97ad\u518d\u6b21\u88ab\u51fb\u98de\u51fa\u53bb\u3002\u7267\u5e08\u53cc\u81c2\u9178\u9ebb\uff0c\u5f80\u540e\u9000\u5f00\u4e24\u6b65\u3002</p><p>\u86db\u5316\u7cbe\u7075\u53d1\u8d77\u51b2\u950b\uff0c\u516b\u53ea\u722a\u5b50\u5feb\u901f\u79fb\u52a8\uff0c\u6574\u4e2a\u4eba\u4eff\u4f5b\u4e00\u8f86\u5766\u514b\u3002\u7267\u5e08\u5411\u4fa7\u9762\u8df3\u4e86\u8d77\u6765\uff0c\u907f\u5f00\u653b\u51fb\uff0c\u5979\u8bd5\u56fe\u518d\u6b21\u53bb\u62e3\u8d77\u86c7\u9996\u97ad\uff0c\u4f46\u86db\u5316\u7cbe\u7075\u6ca1\u6709\u7ed9\u4e88\u8fd9\u4e2a\u673a\u4f1a\u3002\u518d\u4e00\u6b21\u7684\u51b2\u950b\uff0c\u8fd9\u6b21\u86db\u5316\u7cbe\u7075\u7684\u4e00\u53ea\u524d\u722a\u51fb\u4e2d\u4e86\u7267\u5e08\u7684\u80f8\u53e3\u3002\u9b54\u6cd5\u6597\u7bf7\u548c\u7cbe\u91d1\u9501\u7532\u4fdd\u62a4\u5979\u7684\u8eab\u4f53\u6ca1\u6709\u88ab\u5f53\u573a\u6d1e\u7a7f\uff0c\u4f46\u5de8\u5927\u7684\u529b\u91cf\u8fd8\u662f\u8ba9\u5979\u98de\u4e86\u8d77\u6765\uff0c\u4eff\u4f5b\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\u8d8a\u8fc7\u534a\u4e2a\u5927\u5385\uff0c\u6b63\u649e\u5728\u743c\u6069\u548c\u8299\u8389\u5a05\u85cf\u8eab\u7684\u90a3\u4e2a\u8718\u86db\u5851\u50cf\u4e0a\uff0c\u63a5\u7740\u91cd\u91cd\u6454\u843d\u5728\u5730\u4e0a\u3002\u86db\u5316\u7cbe\u7075\u7d27\u63a5\u7740\u4ece\u53e3\u4e2d\u5410\u51fa\u4e00\u9053\u7c97\u5982\u624b\u81c2\u7684\u900f\u660e\u86db\u4e1d\uff0c\u5c06\u7267\u5e08\u7f20\u4f4f\uff0c\u7528\u529b\u5f80\u56de\u62c9\u626f\u3002</p><p>\u86db\u5316\u7cbe\u7075\u521a\u624d\u90a3\u4e00\u6454\u529b\u91cf\u662f\u5982\u6b64\u4e4b\u5927\uff0c\u4ee5\u81f3\u4e8e\u6c89\u91cd\u7684\u5851\u50cf\u90fd\u88ab\u7267\u5e08\u649e\u5f97\u9707\u52a8\u8d77\u6765\uff0c\u6643\u6643\u60a0\u60a0\u5f80\u540e\u5012\u4e0b\u3002\u743c\u6069\u89c1\u52bf\u4e0d\u5999\uff0c\u614c\u5fd9\u6293\u8d77\u8299\u8389\u5a05\u7684\u80f3\u818a\uff0c\u5c06\u5979\u62c9\u626f\u51fa\u6765\u3002\u53ea\u662f\u5982\u6b64\u4e00\u6765\uff0c\u4ed6\u4eec\u4e5f\u5c31\u66b4\u9732\u5728\u86db\u5316\u7cbe\u7075\u7684\u89c6\u7ebf\u4e4b\u4e2d\u3002\u743c\u6069\u770b\u4e86\u773c\u6b63\u5728\u86db\u4e1d\u4e2d\u62fc\u547d\u6323\u624e\u7684\u7267\u5e08\uff0c\u5feb\u901f\u8f6c\u4e86\u8f6c\u5ff5\u5934\uff0c\u4e3e\u8d77\u6cd5\u6756\u671d\u86db\u5316\u7cbe\u7075\u4e00\u6307\u3002</p><p>\u201c\u89e3\u79bb\uff01\u201d</p><p>\u55e4\u5730\u4e00\u58f0\uff0c\u7eff\u8272\u5c04\u7ebf\u4ece\u6756\u5934\u53d1\u51fa\uff0c\u6b63\u4e2d\u76ee\u6807\u3002\u65f6\u95f4\u4eff\u4f5b\u9aa4\u7136\u51dd\u56fa\u4e86\u4e00\u4e0b\uff0c\u7d27\u63a5\u7740\u86db\u5316\u7cbe\u7075\u5e9e\u5927\u7684\u8eab\u8eaf\u7eb7\u7136\u5d29\u6e83\uff0c\u5316\u4f5c\u7c89\u672b\u6563\u843d\u3002\u67e5\u4e3d\u4e1d\u745e\u5f97\u6551\u4e86\uff0c\u5979\u7528\u529b\u626f\u5f00\u7c98\u5728\u8eab\u4e0a\u7684\u86db\u4e1d\uff0c\u60f3\u8981\u4ece\u5730\u4e0a\u722c\u8d77\u6765\uff0c\u4f46\u6ca1\u6709\u6210\u529f\u3002\u743c\u6069\u5c06\u6cd5\u6756\u4ea4\u7ed9\u5de6\u624b\uff0c\u4e0a\u524d\u6276\u8d77\u7267\u5e08\u3002</p><p>\u201c\u665a\u4e0a\u597d\uff0c\u5c0a\u656c\u7684\u83f2\u5c14\u4f26\u5c0f\u59d0\uff0c\u201d\u4ed6\u5173\u5fc3\u5730\u95ee\uff0c\u201c\u60a8\u8fd8\u597d\u5427\u3002\u201d</p></article>"}}}]);
