(window.webpackJsonp=window.webpackJsonp||[]).push([[1459],{1482:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e8c\u8282 \u6b7b\u6597",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e8c\u8282 \u6b7b\u6597</h2><p>\u6b7b\u91cc\u9003\u751f\uff0c\u67e5\u4e3d\u4e1d\u745e\u5598\u606f\u7740\uff0c\u7ad9\u7a33\u8eab\u4f53\uff0c\u5c06\u6ee1\u5934\u6ee1\u8138\u7684\u86db\u4e1d\u626f\u6389\u3002\u548c\u743c\u6069\u82b1\u4e86\u534a\u5929\u624d\u60f3\u8d77\u5979\u4e0d\u540c\uff0c\u7267\u5e08\u662f\u4e00\u773c\u5c31\u8ba4\u51fa\u4e86\u5bf9\u65b9\u7684\u8eab\u4efd\u3002\u539f\u56e0\u5f88\u7b80\u5355\uff0c\u5353\u5c14\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u904d\u5730\u90fd\u662f\uff0c\u4eba\u7c7b\u5374\u592a\u7f55\u89c1\u4e86\uff0c\u51e0\u4e4e\u662f\u72ec\u6b64\u4e00\u5bb6\uff0c\u522b\u65e0\u5176\u4ed6\u3002</p><p>\u67e5\u4e3d\u4e1d\u745e\u5bf9\u743c\u6069\u5e76\u65e0\u534a\u70b9\u597d\u611f\uff0c\u4f46\u5979\u4e5f\u77e5\u9053\u6b64\u6b21\u662f\u8fd9\u4e2a\u4eba\u7c7b\u53ca\u65f6\u4f20\u8baf\u56de\u6765\uff0c\u529f\u52b3\u4e0d\u5c0f\u3002\u5c31\u4ee5\u796d\u53f8\u5b66\u9662\u4e3a\u4f8b\uff0c\u5b83\u662f\u6559\u6388\u5b66\u751f\u7684\u5730\u65b9\uff0c\u53c8\u501a\u4ed7\u7740\u7262\u56fa\u7684\u9b54\u6cd5\u9632\u5fa1\uff0c\u5e73\u5e38\u8c01\u4e5f\u4e0d\u4f1a\u795e\u7ecf\u7d27\u5f20\u5730\u5168\u526f\u6b66\u88c5\uff0c\u968f\u65f6\u51c6\u5907\u6295\u5165\u6218\u6597\u3002\u82e5\u4e0d\u662f\u63d0\u524d\u5f97\u5230\u901a\u77e5\uff0c\u505a\u597d\u6218\u5907\uff0c\u73b0\u5728\u65e9\u5c31\u88ab\u7a81\u88ad\u7684\u53db\u4e71\u8005\u6740\u5f97\u4e00\u8d25\u6d82\u5730\u4e86\u3002\u66f4\u4f55\u51b5\uff0c\u4ed6\u521a\u624d\u6551\u4e86\u81ea\u5df1\u4e00\u547d\u2014\u2014\u5353\u5c14\u5e76\u4e0d\u5728\u4e4e\u6551\u547d\u4e4b\u6069\uff0c\u4f46\u8fd9\u8868\u660e\u4e86\u4ed6\u7684\u7acb\u573a\uff1a\u662f\u670b\u53cb\uff0c\u4e0d\u662f\u654c\u4eba\u3002\u5728\u73b0\u5728\u8fd9\u79cd\u5173\u952e\u65f6\u523b\uff0c\u7acb\u573a\u5c24\u5176\u91cd\u8981\u3002</p><p>\u800c\u4e14\u8fd9\u4e2a\u4eba\u7c7b\u4e5f\u5f88\u8bc6\u8da3\u3002</p><p>\u521a\u624d\u743c\u6069\u4e00\u53d1\u89e3\u79bb\u672f\uff0c\u76f4\u63a5\u6467\u6bc1\u4e86\u86db\u5316\u7cbe\u7075\u7684\u8eab\u4f53\uff0c\u8ba9\u5979\u5316\u4f5c\u5fae\u5c18\u6d88\u6563\u3002\u5982\u6b64\u4e00\u6765\uff0c\u8bc1\u636e\u6bc1\u706d\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u67e5\u4e3d\u4e1d\u745e\u64c5\u81ea\u5bf9\u5b66\u751f\u4e0b\u8718\u86db\u8bc5\u5492\u7684\u4e8b\u60c5\u5c31\u7b49\u4e8e\u538b\u6839\u6ca1\u53d1\u751f\u8fc7\u4e86\u2014\u2014\u5353\u5c14\u7684\u5f8b\u6cd5\u89c4\u5219\uff0c\u53ea\u8981\u4e0d\u80fd\u8bc1\u660e\u7684\uff0c\u90a3\u5c31\u662f\u6839\u672c\u4e0d\u5b58\u5728\u3002</p><p>\u201c\u6216\u8bb8\uff0c\u53ef\u4ee5\u8bd5\u8bd5\u62db\u63fd\u4ed6\u3002\u201d\u7267\u5e08\u6697\u60f3\u3002</p><p>\u548c\u6240\u6709\u7684\u5353\u5c14\u4e00\u6837\uff0c\u67e5\u4e3d\u4e1d\u745e\u5bcc\u6709\u91ce\u5fc3\uff0c\u65f6\u523b\u5e0c\u671b\u7740\u80fd\u591f\u66f4\u4e0a\u4e00\u5c42\uff0c\u4f46\u5979\u540c\u65f6\u4e5f\u5f88\u6709\u81ea\u77e5\u4e4b\u660e\u3002\u7267\u5e08\u5f88\u65e9\u5c31\u6e05\u695a\u5730\u8ba4\u8bc6\u5230\uff1a\u4e0d\u7ba1\u662f\u8bba\u4e2a\u4eba\u5b9e\u529b\uff0c\u8bba\u9886\u8896\u624d\u534e\uff0c\u8bba\u795e\u540e\u7684\u5ba0\u7231\uff0c\u8bba\u58f0\u671b\u548c\u5730\u4f4d\uff0c\u81ea\u5df1\u90fd\u8fdc\u8fdc\u4e0d\u53ef\u80fd\u548c\u957f\u5973\u8fe6\u6cd5\u76f8\u6bd4\u3002\u867d\u7136\u5979\u4e5f\u5f88\u60f3\u80fd\u591f\u6210\u4e3a\u5bb6\u65cf\u4e3b\u6bcd\uff0c\u6210\u4e3a\u8fd9\u5ea7\u57ce\u5e02\u7684\u7edf\u6cbb\u8005\uff0c\u4f46\u8fd9\u592a\u4e0d\u73b0\u5b9e\u4e86\u3002\u8ffd\u9010\u8fc7\u4e8e\u4e0d\u5207\u5b9e\u9645\u7684\u76ee\u6807\uff0c\u53ea\u4f1a\u662f\u81ea\u53d6\u706d\u4ea1\u3002\u6240\u4ee5\u5728\u6210\u4e3a\u796d\u53f8\u5b66\u9662\u7684\u6559\u5b98\u4e4b\u540e\uff0c\u5979\u4fbf\u5f00\u59cb\u5411\u8fe6\u6cd5\u5bfb\u6c42\u5408\u4f5c\uff08\u5f53\u7136\u540e\u8005\u8ba4\u4e3a\u8fd9\u662f\u6295\u9760\uff09\u3002\u67e5\u4e3d\u4e1d\u745e\u8bb8\u8bfa\u5168\u529b\u652f\u6301\u8fe6\u6cd5\uff0c\u5de9\u56fa\u5979\u7684\u5730\u4f4d\uff0c\u4fdd\u8bc1\u5979\u987a\u5229\u7ee7\u627f\u5bb6\u65cf\u4e3b\u6bcd\u7684\u4f4d\u7f6e\uff0c\u800c\u4f5c\u4e3a\u56de\u62a5\uff0c\u8fe6\u6cd5\u4f1a\u63a8\u8350\u67e5\u4e3d\u4e1d\u745e\u4f5c\u4e3a\u81ea\u5df1\u7684\u7ee7\u627f\u4eba\uff0c\u62c5\u4efb\u796d\u53f8\u5b66\u9662\u7684\u6b21\u5e2d\u6559\u5b98\uff0c\u4e5f\u5c31\u662f\u5b9e\u9645\u7684\u9886\u8896\u3002\u8fd9\u4e2a\u4f4d\u7f6e\u867d\u7136\u6bd4\u4e0d\u4e0a\u7b2c\u4e00\u5bb6\u65cf\u4e3b\u6bcd\uff0c\u4f46\u4e5f\u5df2\u7ecf\u975e\u5e38\u4e0d\u9519\u4e86\u3002</p><p>\u73b0\u5728\u5b66\u9662\u88ab\u7a81\u88ad\uff0c\u867d\u8bf4\u662f\u51fa\u4e4e\u610f\u6599\uff0c\u4f46\u5018\u82e5\u635f\u5931\u592a\u8fc7\u4e25\u91cd\uff0c\u52bf\u5fc5\u4f1a\u5f71\u54cd\u8fe6\u6cd5\u548c\u67e5\u4e3d\u4e1d\u745e\u7684\u5730\u4f4d\u3002\u5f53\u52a1\u4e4b\u6025\uff0c\u662f\u5c3d\u5feb\u5e73\u5b9a\u53db\u4e71\uff0c\u5c06\u4f24\u4ea1\u964d\u4f4e\u5230\u6700\u5c0f\u7a0b\u5ea6\uff0c\u800c\u9762\u524d\u8fd9\u4e2a\u4eba\u7c7b\u5deb\u5e08\u6b63\u597d\u5e2e\u5f97\u4e0a\u5fd9\u3002\u5982\u679c\u4ed6\u7684\u8868\u73b0\u8db3\u591f\u51fa\u8272\uff0c\u67e5\u4e3d\u4e1d\u745e\u4f1a\u8003\u8651\u7ed9\u4e88\u8fdb\u4e00\u6b65\u7684\u5956\u8d4f\u3002\u743c\u6069\u662f\u4e2a\u4eba\u7c7b\uff0c\u8fd9\u662f\u4e2a\u52a3\u52bf\uff0c\u540c\u65f6\u4e5f\u662f\u4e2a\u4f18\u52bf\uff0c\u610f\u5473\u7740\u4ed6\u4e0d\u53ef\u80fd\u72ec\u81ea\u7acb\u8db3\uff0c\u5fc5\u987b\u4f9d\u9644\u67d0\u4e2a\u5353\u5c14\u8d35\u65cf\u624d\u884c\u2014\u2014\u800c\u8981\u8bba\u4f9d\u9644\u5bf9\u8c61\uff0c\u67e5\u4e3d\u4e1d\u745e\u81ea\u4fe1\u53ef\u6bd4\u5e74\u5e7c\u7684\u59b9\u59b9\u7ef4\u5eb7\u5c3c\u4e9a\u5408\u9002\u5f97\u591a\u4e86\u3002</p><p>\u5ff5\u5934\u6025\u8f6c\uff0c\u7267\u5e08\u7684\u8138\u4e0a\u5df2\u7ecf\u5fae\u5fae\u5e26\u4e0a\u4e86\u77dc\u6301\u7684\u7b11\u5bb9\uff0c\u201c\u611f\u8c22\u60a8\u7684\u63f4\u624b\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u5979\u8bf4\uff0c\u5c3d\u53ef\u80fd\u8ba9\u8bed\u6c14\u663e\u5f97\u4eb2\u5207\uff0c\u8fd9\u4ee4\u5979\u5f88\u4e0d\u4e60\u60ef\uff0c\u6240\u4ee5\u8d76\u5feb\u8f6c\u6362\u4e86\u8bdd\u9898\uff0c\u201c\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u5982\u4f60\u6240\u89c1\uff0c\u5c0f\u59d0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u4e9b\u9003\u4ea1\u8005\u7a81\u88ad\u4e86\u8fd9\u91cc\u3002\u201d</p><p>\u67e5\u4e3d\u4e1d\u745e\u7684\u8138\u6c89\u4e0b\u6765\uff0c\u6709\u4e9b\u4e0d\u5feb\uff0c\u4f46\u9690\u5fcd\u4f4f\u4e86\uff0c\u201c\u6211\u662f\u95ee\u4ed6\u4eec\u600e\u4e48\u4f1a\u7a81\u7136\u51fa\u73b0\uff1f\u201d</p><p>\u201c\u4ed6\u4eec\u7684\u9996\u9886\u662f\u4e00\u4e2a\u963f\u9686\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u963f\u9686\u5c31\u662f\u7075\u5438\u602a\u53d8\u6210\u7684\u5deb\u5996\u2026\u2026\u201d</p><p>\u201c\u6211\u77e5\u9053\uff01\u201d\u67e5\u4e3d\u4e1d\u745e\u4e0d\u8010\u70e6\u5730\u6253\u65ad\uff0c\u201c\u4e0d\u8981\u6000\u7591\u4e00\u4f4d\u796d\u53f8\u7684\u5b66\u8bc6\uff0c\u8bf4\u91cd\u70b9\uff01\u201d</p><p>\u743c\u6069\u8eac\u8eab\u8868\u793a\u6b49\u610f\uff0c\u201c\u5931\u793c\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u7b80\u5355\u6765\u8bf4\uff0c\u6709\u4e00\u4e2a\u53eb\u505a\u65af\u5170\u666e\u7684\u7075\u5438\u602a\u5deb\u5996\u5728\u9886\u5bfc\u8fd9\u4e9b\u9003\u4ea1\u8005\uff0c\u4ed6\u662f\u4e2a\u975e\u5e38\u9ad8\u660e\u7684\u5deb\u5e08\uff0c\u7528\u67d0\u79cd\u65b9\u6cd5\u5c06\u6240\u6709\u4eba\u2014\u2014\u5305\u62ec\u4ed6\u81ea\u5df1\u548c\u6211\u5728\u5185\u2014\u2014\u4f20\u9001\u5230\u4e86\u8fd9\u91cc\u3002\u63a5\u4e0b\u6765\u7684\u4e8b\u60c5\u2026\u2026\u201d\u5deb\u5e08\u8038\u8038\u80a9\uff0c\u201c\u6211\u60f3\u5c31\u4e0d\u7528\u89e3\u91ca\u4e86\u3002\u201d</p><p>\u4ed6\u7684\u8bed\u6c14\u6709\u4e9b\u8f7b\u4f7b\uff0c\u7136\u800c\u7267\u5e08\u6ca1\u6709\u5fc3\u601d\u53bb\u6ce8\u610f\u8fd9\u4e9b\u7ec6\u8282\u3002\u201c\u4ed6\u600e\u4e48\u53ef\u80fd\u5b8c\u6210\u4f20\u9001\uff1f\u201d\u67e5\u4e3d\u4e1d\u745e\u8d28\u95ee\uff0c\u201c\u5b66\u9662\u6709\u9b54\u6cd5\u5c4f\u969c\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u5c31\u4e0d\u6e05\u695a\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u63d0\u524d\u62a5\u544a\u8fd9\u4e9b\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u4e5f\u662f\u4e0d\u4e45\u524d\u624d\u5f97\u77e5\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u800c\u4e14\u6211\u5df2\u7ecf\u901a\u77e5\u4e86\u5c0a\u656c\u7684\u4e3b\u6bcd\uff0c\u76f8\u4fe1\u5f88\u5feb\u5c31\u4f1a\u6709\u63f4\u519b\u8d76\u5230\u3002\u201d</p><p>\u67e5\u4e3d\u4e1d\u745e\u6709\u4e9b\u607c\u706b\uff0c\u4f46\u8fd8\u662f\u6309\u637a\u4f4f\u4e86\u6012\u6c14\uff0c\u201c\u6709\u591a\u5c11\u4eba\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u4e00\u767e\u5de6\u53f3\u3002\u201d</p><p>\u7267\u5e08\u7684\u8138\u8272\u6709\u4e9b\u96be\u770b\uff0c\u5979\u662f\u5f88\u6e05\u695a\u90a3\u4e9b\u9003\u4ea1\u8005\u7684\u6c34\u5e73\u7684\uff0c\u57fa\u672c\u90fd\u662f\u5404\u81ea\u5bb6\u65cf\u4e2d\u7684\u7cbe\u9510\u6218\u58eb\uff0c\u521a\u624d\u7684\u4ea4\u624b\u4e5f\u8bc1\u660e\u4e86\u8fd9\u70b9\u3002\u4e00\u767e\u540d\u5353\u5c14\u6218\u58eb\u7a81\u7136\u88ad\u51fb\uff0c\u5373\u4f7f\u796d\u53f8\u5b66\u9662\u7684\u5168\u4f53\u5e08\u751f\u5df2\u7ecf\u505a\u597d\u4e86\u8fce\u6218\u51c6\u5907\uff0c\u4e5f\u4f1a\u906d\u5230\u4e25\u91cd\u7684\u635f\u5931\u3002</p><p>\u201c\u5deb\u5996\u6b63\u5728\u6784\u5efa\u4f20\u9001\u6cd5\u9635\uff0c\u51c6\u5907\u7528\u4e8e\u64a4\u9000\u2026\u2026\u5514\uff0c\u9003\u8dd1\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u4ed6\u7684\u4f4d\u7f6e\u3002\u201d</p><p>\u201c\u6211\u4eec\u9700\u8981\u66f4\u591a\u7684\u5e2e\u624b\uff0c\u201d\u7267\u5e08\u7565\u7565\u6c89\u541f\uff0c\u5979\u53ef\u6ca1\u4fe1\u5fc3\u53bb\u9762\u5bf9\u4e00\u53ea\u7075\u5438\u602a\u5deb\u5996\uff0c\u201c\u5148\u53bb\u548c\u5176\u4ed6\u4eba\u6c47\u5408\u5427\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u67e5\u4e3d\u4e1d\u745e\u53d6\u56de\u81ea\u5df1\u7684\u86c7\u9996\u97ad\uff0c\u5e26\u9886\u7740\u743c\u6069\u548c\u8299\u8389\u5a05\u7a7f\u8fc7\u66f2\u6298\u7684\u8d70\u5eca\uff0c\u524d\u5f80\u5b66\u9662\u7684\u524d\u90e8\u5e73\u53f0\uff0c\u90a3\u91cc\u662f\u9884\u5b9a\u96c6\u5408\u7684\u5730\u65b9\u3002\u8def\u4e0a\u4ed6\u4eec\u6ca1\u6709\u518d\u906d\u9047\u654c\u4eba\uff0c\u4f46\u770b\u5230\u4e86\u4e09\u5904\u521a\u521a\u6218\u6597\u8fc7\u7684\u75d5\u8ff9\uff0c\u4ee5\u53ca\u6ee1\u5730\u7684\u5c38\u4f53\uff0c\u663e\u7136\u53d7\u5230\u88ad\u51fb\u7684\u5e76\u4e0d\u4ec5\u4ec5\u662f\u67e5\u4e3d\u4e1d\u745e\u4e00\u4eba\u3002\u4f24\u4ea1\u8005\u5927\u90e8\u4efd\u662f\u5b66\u751f\uff0c\u4e5f\u6709\u5c11\u6570\u6559\u5b98\u3002\u53db\u4e71\u8005\u540c\u6837\u635f\u5931\u4e0d\u5c0f\uff0c\u4ed6\u4eec\u4e5f\u6ca1\u6709\u9884\u6599\u5230\u8fd9\u6b21\u7a81\u88ad\u5c45\u7136\u4f1a\u906d\u5230\u5982\u6b64\u5f3a\u6709\u529b\u7684\u62b5\u6297\u3002\u5f80\u65e5\u5b81\u9759\u7684\u5b66\u9662\u53d8\u6210\u4e86\u4fee\u7f57\u6740\u573a\uff0c\u5c38\u4f53\u548c\u8840\u8ff9\u904d\u5730\u90fd\u662f\uff0c\u7a7a\u6c14\u4e2d\u5145\u6ee1\u4e86\u4ee4\u4eba\u7a92\u606f\u7684\u8165\u5473\uff0c\u8ba9\u4eba\u53ea\u60f3\u8d76\u5feb\u79bb\u5f00\u6b64\u5730\u3002</p><p>\u6700\u7ec8\uff0c\u5728\u5feb\u8981\u62b5\u8fbe\u51fa\u53e3\u7684\u5730\u65b9\uff0c\u743c\u6069\u8fdc\u8fdc\u770b\u89c1\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u5979\u8eab\u65c1\u8ddf\u968f\u7740\u56db\u4e2a\u7537\u6027\u6218\u58eb\uff0c\u6b63\u5306\u5306\u5f80\u91cc\u5954\u6765\u3002\u8fce\u9762\u542c\u89c1\u52a8\u9759\uff0c\u4e24\u4e2a\u6218\u58eb\u8fc5\u901f\u4e3e\u8d77\u5355\u624b\u5f29\u7784\u51c6\u3002</p><p>\u201c\u662f\u6211\uff01\u67e5\u4e3d\u4e1d\u745e\uff01\u201d\u67e5\u4e3d\u4e1d\u745e\u9ad8\u58f0\u8bf4\uff0c\u907f\u514d\u4e86\u4e00\u573a\u8bef\u4f1a\uff0c\u201c\u4f60\u600e\u4e48\u5728\u8fd9\u91cc\uff1f\u7ef4\u5eb7\u5c3c\u4e9a\u3002\u201d</p><p>\u201c\u4e3b\u6bcd\u8ba9\u6211\u5e26\u4eba\u6765\u6551\u63f4\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u77a5\u4e86\u4e00\u773c\u540e\u9762\u7684\u743c\u6069\uff0c\u56de\u7b54\u8bf4\u3002</p><p>\u201c\u53ea\u6709\u8fd9\u51e0\u4e2a\u4eba\uff1f\u201d</p><p>\u201c\u6211\u5728\u5916\u9762\u9047\u5230\u4e86\u8fe6\u6cd5\uff0c\u5927\u90e8\u4efd\u88ab\u5979\u8981\u8d70\u4e86\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u987f\u4e86\u987f\uff0c\u663e\u7136\u4e0d\u60f3\u518d\u7ee7\u7eed\u8fd9\u4e2a\u8bdd\u9898\uff0c\u201c\u73b0\u5728\u60c5\u51b5\u600e\u4e48\u6837\u3002\u201d</p><p>\u201c\u5f88\u7cdf\u7cd5\uff0c\u201d\u67e5\u4e3d\u4e1d\u745e\u751f\u786c\u5730\u8bf4\uff0c\u201c\u4ed6\u4eec\u6570\u91cf\u975e\u5e38\u591a\uff0c\u5f88\u591a\u59d0\u59b9\u90fd\u6218\u6b7b\u4e86\u3002\u201d</p><p>\u201c\u771f\u9057\u61be\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u7136\u800c\u743c\u6069\u542c\u5f97\u51fa\u6765\u5979\u5e76\u4e0d\u56e0\u6b64\u611f\u5230\u4e1d\u6beb\u96be\u8fc7\u3002</p><p>\u201c\u6216\u8bb8\u8fd9\u662f\u795e\u540e\u7684\u8003\u9a8c\uff0c\u201d\u67e5\u4e3d\u4e1d\u745e\u8bf4\uff0c\u201c\u6dd8\u6c70\u6389\u6211\u4eec\u5f53\u4e2d\u90a3\u4e9b\u65e0\u80fd\u8005\u2026\u2026\u597d\u5427\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u65e2\u7136\u63f4\u519b\u8d76\u5230\uff0c\u90a3\u4e5f\u5c31\u6ca1\u4ec0\u4e48\u53ef\u62c5\u5fc3\u4e86\u2026\u2026\u201d</p><p>\u201c\u5c0a\u656c\u7684\u6559\u5b98\uff0c\u201d\u743c\u6069\u5728\u4e00\u65c1\u6253\u65ad\uff0c\u201c\u6211\u60f3\u60a8\u592a\u4e50\u89c2\u4e86\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u60a8\u5fd8\u4e86\u6211\u521a\u624d\u6240\u8bf4\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e00\u53ea\u963f\u9686\u6b63\u5728\u5b66\u9662\u91cc\uff0c\u4ed6\u624d\u662f\u771f\u6b63\u5371\u9669\u7684\u654c\u4eba\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u8f6c\u8fc7\u8138\u6765\uff0c\u5979\u9887\u4e3a\u60ca\u8bb6\uff0c\u201c\u963f\u9686\uff1f\u7075\u5438\u602a\u5deb\u5996\uff1f\u201d</p><p>\u743c\u6069\u5c06\u65af\u5170\u666e\u7684\u4e8b\u60c5\u7b80\u8981\u5730\u8bf4\u4e86\u4e00\u904d\uff0c\u57fa\u672c\u4e0a\u662f\u5982\u5b9e\u9648\u8ff0\uff0c\u4f46\u7701\u7565\u4e86\u4e00\u4e9b\u7ec6\u8282\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u7709\u5934\u76b1\u8d77\u6765\uff0c\u4e00\u53ea\u7075\u5438\u602a\u5deb\u5996\uff0c\u80fd\u591f\u7a7f\u900f\u796d\u53f8\u5b66\u9662\u7684\u9b54\u6cd5\u5c4f\u969c\u5b8c\u6210\u5927\u89c4\u6a21\u4f20\u9001\uff0c\u8fd9\u624d\u662f\u6700\u68d8\u624b\u7684\u3002</p><p>\u201c\u6211\u4eec\u5f97\u53bb\u5e72\u6389\u4ed6\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u9a6c\u4e0a\u3002\u201d</p><p>\u201c\u5c31\u51ed\u6211\u4eec\u51e0\u4e2a\uff1f\u201d\u67e5\u4e3d\u4e1d\u745e\u53cd\u5bf9\uff0c\u201c\u6211\u8ba4\u4e3a\u6211\u4eec\u5e94\u8be5\u5148\u6c47\u5408\u66f4\u591a\u7684\u540c\u4f34\u3002\u201d</p><p>\u201c\u6765\u4e0d\u53ca\u4e86\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u5916\u9762\u7684\u63f4\u519b\u5df2\u7ecf\u62b5\u8fbe\uff0c\u53db\u4e71\u8005\u5373\u5c06\u88ab\u6d88\u706d\uff0c\u90a3\u4e2a\u53eb\u65af\u5170\u666e\u7684\u5bb6\u4f19\u5f88\u5feb\u5c31\u4f1a\u5f97\u77e5\u8fd9\u4e00\u5207\uff0c\u4ed6\u4f1a\u9003\u8dd1\uff0c\u800c\u90a3\u6837\u6211\u4eec\u5c31\u518d\u4e5f\u627e\u4e0d\u5230\u4ed6\u4e86\u3002\u73b0\u5728\u53bb\u53ef\u80fd\u8fd8\u6765\u5f97\u53ca\uff0c\u201d\u5979\u8f6c\u8fc7\u8138\u95ee\u743c\u6069\uff0c\u201c\u4f60\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\u5bf9\u5427\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\u3002\u201d</p><p>\u201c\u81ea\u7136\u3002\u201d</p><p>\u67e5\u4e3d\u4e1d\u745e\u8138\u4e0a\u7684\u8868\u60c5\u6709\u4e9b\u96be\u770b\uff0c\u4f46\u6700\u7ec8\u5979\u8fd8\u662f\u70b9\u5934\u8d5e\u540c\u4e86\u3002\u6b64\u6b21\u796d\u53f8\u5b66\u9662\u9047\u88ad\uff0c\u4e0d\u5c11\u6559\u5b98\u4e27\u751f\uff0c\u4e8b\u540e\u5fc5\u5b9a\u4f1a\u4ea7\u751f\u4e00\u7cfb\u5217\u7684\u4eba\u4e8b\u53d8\u52a8\u3002\u5982\u679c\u81ea\u5df1\u80fd\u591f\u6740\u6b7b\u90a3\u4e2a\u7075\u5438\u602a\u5deb\u5996\uff0c\u8fd9\u6b21\u53db\u4e71\u7684\u7f6a\u9b41\u7978\u9996\uff0c\u90a3\u4e48\u6beb\u65e0\u7591\u95ee\u662f\u5927\u529f\u4e00\u4ef6\u3002\u201c\u90a3\u597d\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u4eec\u53bb\u627e\u90a3\u4e2a\u5bb6\u4f19\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u5c06\u90a3\u5ea7\u7a7a\u7f6e\u6559\u5ba4\u5927\u81f4\u63cf\u8ff0\u4e86\u4e00\u4e0b\uff0c\u4e24\u4f4d\u7267\u5e08\u90fd\u7acb\u523b\u5224\u65ad\u51fa\u4e86\u65b9\u4f4d\u3002\u4ed6\u4eec\u539f\u8def\u8fd4\u56de\uff0c\u4eba\u6570\u53d8\u6210\u4e86\u4e03\u4e2a\uff1a\u743c\u6069\u3001\u7ef4\u5eb7\u5c3c\u4e9a\u3001\u67e5\u4e3d\u4e1d\u745e\uff0c\u4ee5\u53ca\u7ef4\u5eb7\u5c3c\u4e9a\u5e26\u6765\u7684\u56db\u4e2a\u62a4\u536b\u3002\u8299\u8389\u5a05\u4e0d\u5728\u5176\u4e2d\uff0c\u743c\u6069\u8ba9\u5979\u5148\u79bb\u5f00\u5b66\u9662\uff0c\u56de\u83f2\u5c14\u4f26\u5b85\u9662\u53bb\u4e86\u3002</p><p>\u5341\u5206\u949f\u540e\uff0c\u4ed6\u4eec\u62b5\u8fbe\u4e86\u76ee\u7684\u5730\uff0c\u7136\u800c\u6709\u4e9b\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u7075\u5438\u602a\u5deb\u5996\u5e76\u4e0d\u5728\u6559\u5ba4\u91cc\uff0c\u4e5f\u6ca1\u6709\u770b\u5230\u4efb\u4f55\u4f20\u9001\u6cd5\u9635\u7684\u8ff9\u8c61\u3002\u201c\u4ed6\u5df2\u7ecf\u9003\u4e86\uff1f\u201d\u67e5\u4e3d\u4e1d\u745e\u95ee\uff0c\u6709\u4e9b\u60cb\u60dc\u53c8\u6709\u4e9b\u5e86\u5e78\u3002</p><p>\u743c\u6069\u6447\u5934\uff0c\u4ed6\u603b\u89c9\u5f97\u4e8b\u60c5\u6ca1\u8fd9\u4e48\u7b80\u5355\uff0c\u4f46\u4e5f\u8bf4\u4e0d\u51fa\u4ec0\u4e48\u6765\u3002\u201c\u6216\u8bb8\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u201c\u4f60\u8bf4\u4ed6\u8eb2\u5728\u7eb3\u739b\u65af\u533a\u7684\u4e00\u5ea7\u57ce\u5821\u91cc\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u95ee\uff0c\u201c\u6216\u8bb8\u90a3\u91cc\u80fd\u627e\u5230\u4ed6\u3002\u201d</p><p>\u743c\u6069\u7565\u7565\u6c89\u541f\uff0c\u4ed6\u6000\u7591\u7075\u5438\u602a\u5deb\u5996\u7684\u90a3\u5ea7\u57ce\u5821\u5176\u5b9e\u662f\u4e00\u4e2a\u534a\u4f4d\u9762\uff0c\u5e76\u4e0d\u771f\u5b9e\u5b58\u5728\u4e8e\u7269\u8d28\u754c\uff0c\u4f46\u8fd9\u79cd\u8bdd\u6ca1\u5fc5\u8981\u5411\u7ef4\u5eb7\u5c3c\u4e9a\u89e3\u91ca\u3002\u201c\u90a3\u6211\u4eec\u5148\u51fa\u53bb\u5427\u3002\u201d</p><p>\u6b63\u8981\u8f6c\u8eab\uff0c\u9661\u7136\u95f4\u611f\u89c9\u773c\u524d\u4e00\u9635\u8f7b\u5fae\u7684\u6655\u7729\uff0c\u4eff\u4f5b\u6709\u731b\u70c8\u7684\u5149\u8292\u7206\u53d1\uff0c\u4f34\u968f\u7740\u5de8\u5927\u7684\u9707\u52a8\u58f0\uff0c\u4f46\u53c8\u4f3c\u4e4e\u662f\u5e7b\u89c9\u3002\u743c\u6069\u8f6c\u5934\u770b\u7740\u7ef4\u5eb7\u5c3c\u4e9a\u548c\u67e5\u4e3d\u4e1d\u745e\uff0c\u60f3\u5224\u65ad\u662f\u4e0d\u662f\u81ea\u5df1\u4eca\u5929\u592a\u7d2f\uff0c\u51fa\u73b0\u9519\u89c9\u4e86\uff0c\u4f46\u4ed6\u5f88\u5feb\u6253\u6d88\u4e86\u8fd9\u4e2a\u60f3\u6cd5\uff0c\u56e0\u4e3a\u4e24\u4f4d\u7267\u5e08\u7684\u8138\u4e0a\u540c\u65f6\u9732\u51fa\u9707\u60ca\u7684\u8868\u60c5\u3002</p><p>\u201c\u8be5\u6b7b\uff01\u201d\u67e5\u4e3d\u4e1d\u745e\u8bc5\u5492\u7740\uff0c\u5979\u770b\u8d77\u6765\u65e2\u607c\u6012\u53c8\u60ca\u6050\uff0c\u201c\u4ed6\u5c45\u7136\u95ef\u8fdb\u4e86\u795e\u5ba4\u2026\u2026\u4ed6\u600e\u4e48\u529e\u5230\u7684\uff1f\u201d</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u795e\u5ba4\u662f\u6307\u4ec0\u4e48\uff0c\u542c\u8d77\u6765\u662f\u67d0\u4e2a\u91cd\u8981\u573a\u6240\u3002\u4ed6\u60f3\u8be2\u95ee\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u7136\u800c\u4e24\u4f4d\u7267\u5e08\u5df2\u7ecf\u987e\u4e0d\u4e0a\u56de\u7b54\uff0c\u5979\u4eec\u5e26\u5934\u51b2\u51fa\u6559\u5ba4\uff0c\u743c\u6069\u548c\u56db\u540d\u62a4\u536b\u53ea\u597d\u7d27\u968f\u5176\u540e\u3002\u4ed6\u4eec\u6cbf\u7740\u957f\u957f\u7684\u8d70\u5eca\u8dd1\u5230\u5c3d\u5934\uff0c\u63a5\u7740\u8f6c\u5f2f\uff0c\u5728\u4e00\u5c0a\u4eff\u4f5b\u7531\u5343\u767e\u4e2a\u5927\u5c0f\u4e0d\u540c\u7684\u773c\u7403\u51dd\u805a\u7ec4\u5408\u800c\u6210\u7684\u8718\u86db\u5851\u50cf\u524d\u505c\u4e86\u4e0b\u6765\u3002\u67e5\u4e3d\u4e1d\u745e\u6293\u8d77\u7f57\u4e1d\u5723\u5fbd\uff0c\u5389\u58f0\u5ff5\u51fa\u4e00\u6bb5\u5492\u8bed\uff0c\u8718\u86db\u5851\u50cf\u53d8\u5f97\u6d3b\u52a8\u8d77\u6765\uff0c\u5927\u5927\u5c0f\u5c0f\u7684\u773c\u7403\u4e2d\u5c04\u51fa\u65e0\u6570\u9053\u5c04\u7ebf\uff0c\u5728\u7a7a\u4e2d\u4ea4\u6c47\uff0c\u5f62\u6210\u4e00\u4e2a\u692d\u5706\u5f62\u7684\u9ed1\u6d1e\u3002</p><p>\u201c\u5feb\u8fdb\u53bb\uff01\u201d\u67e5\u4e3d\u4e1d\u745e\u8bf4\uff0c\u81ea\u5df1\u8df3\u4e86\u8fdb\u53bb\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u7d27\u968f\u5176\u540e\uff0c\u743c\u6069\u548c\u5176\u4ed6\u4eba\u4e5f\u53ea\u80fd\u8ddf\u4e0a\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u4ed6\u770b\u89c1\u4e86\u4e00\u5ea7\u9ad8\u53f0\uff0c\u81ea\u5df1\u548c\u5176\u4ed6\u4eba\u90fd\u5728\u53f0\u4e0b\u3002\u65af\u5170\u666e\u6b63\u7ad9\u5728\u53f0\u4e0a\uff0c\u4ed6\u7684\u9762\u524d\u662f\u4e00\u53ea\u60ac\u6d6e\u7684\u7ea2\u8272\u8718\u86db\uff0c\u6676\u83b9\u5254\u900f\uff0c\u770b\u8d77\u6765\u5c31\u50cf\u662f\u5b9d\u77f3\u2026\u2026\u4e0d\uff0c\u90a3\u5e76\u975e\u6d3b\u7269\uff0c\u800c\u662f\u67d0\u79cd\u5f3a\u529b\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u5b83\u6563\u53d1\u7740\u8000\u773c\u7684\u7075\u5149\uff0c\u4ee4\u743c\u6069\u51e0\u4e4e\u4e0d\u6562\u6b63\u89c6\u3002</p><p>\u65af\u5170\u666e\u4f38\u51fa\u624b\uff0c\u4f3c\u4e4e\u60f3\u8981\u5c06\u8718\u86db\u6293\u5728\u624b\u4e2d\uff0c\u4f46\u5374\u53c8\u660e\u663e\u6709\u4e9b\u754f\u60e7\uff0c\u6b63\u81ea\u72b9\u8c6b\u4e0d\u51b3\uff0c\u6b64\u65f6\u4ed6\u53d1\u73b0\u4e86\u743c\u6069\u7b49\u4eba\u7684\u51fa\u73b0\u3002\u5deb\u5996\u77a5\u4e86\u4e00\u773c\uff0c\u6ca1\u6709\u591a\u52a0\u7406\u776c\uff0c\u4ece\u6000\u4e2d\u6293\u51fa\u4e00\u628a\u788e\u88c2\u7684\u6c34\u6676\uff0c\u6254\u5728\u5730\u4e0a\u3002</p><p>\u5494\u5494\u5494\u5494\u8fde\u4e32\u58f0\u54cd\uff0c\u4e03\u4e2a\u51b0\u9b54\u7ad9\u8d77\u6765\uff0c\u5b83\u4eec\u8eab\u6750\u77ee\u5c0f\uff0c\u7c7b\u4f3c\u733f\u7334\uff0c\u901a\u4f53\u900f\u660e\u4eff\u4f5b\u51b0\u5757\u96d5\u6210\uff0c\u80cc\u4e0a\u957f\u6ee1\u9aa8\u523a\uff0c\u6709\u4e00\u6761\u957f\u6ee1\u5012\u523a\u7684\u957f\u957f\u5c3e\u5df4\u3002\u8fd9\u662f\u4e00\u79cd\u6765\u81ea\u4e5d\u5c42\u5730\u72f1\u7684\u9b54\u9b3c\uff0c\u52a8\u4f5c\u8fc5\u6377\uff0c\u64c5\u957f\u523a\u6740\u3002\u547c\u5578\u4e00\u58f0\uff0c\u51b0\u9b54\u8df3\u4e0b\u9ad8\u53f0\uff0c\u8fce\u9762\u51b2\u8fdb\u5353\u5c14\u5f53\u4e2d\u3002</p><p>\u67e5\u4e3d\u4e1d\u745e\u3001\u7ef4\u5eb7\u5c3c\u4e9a\u548c\u56db\u540d\u5353\u5c14\u62a4\u536b\u7eb7\u7eb7\u62d4\u51fa\u6b66\u5668\uff0c\u5377\u5165\u6218\u56e2\u3002\u743c\u6069\u540e\u9000\uff0c\u5ff5\u51fa\u4e00\u4e2a\u5355\u5b57\uff0c\u6fc0\u6d3b\u957f\u888d\u4e0a\u7684\u80f8\u9488\uff0c\u8eab\u4f53\u6f02\u6d6e\u8d77\u6765\uff0c\u79bb\u5f00\u5730\u9762\u3002\u51b0\u9b54\u6ca1\u6709\u8fdc\u7a0b\u653b\u51fb\u7684\u80fd\u529b\uff0c\u8131\u79bb\u8303\u56f4\u5c31\u4e0d\u8db3\u4e3a\u60e7\uff0c\u4e5f\u6ca1\u6709\u51c6\u5907\u7acb\u523b\u52a8\u624b\u3002</p><p>\u201c\u4e3a\u4ec0\u4e48\u4e0d\u7ad9\u5728\u6211\u4e00\u8fb9\u5462\uff1f\u5170\u5c3c\u65af\u7279\u5148\u751f\u3002\u201d\u7075\u5438\u602a\u5deb\u5996\u7684\u58f0\u97f3\u7a81\u7136\u5728\u8111\u4e2d\u54cd\u8d77\u3002</p><p>\u201c\u6211\u559c\u6b22\u7ad9\u5728\u80dc\u5229\u8005\u4e00\u8fb9\u3002\u201d\u743c\u6069\u5728\u5fc3\u4e2d\u56de\u7b54\uff0c\u4ed6\u77e5\u9053\u5bf9\u65b9\u5176\u5b9e\u542c\u5f97\u89c1\u3002</p><p>\u5deb\u5996\u563f\u563f\u51b7\u7b11\uff0c\u201c\u7f57\u4e1d\u5df2\u7ecf\u901d\u53bb\uff0c\u5979\u7684\u7267\u5e08\u4eec\u5fc5\u5c06\u5931\u8d25\uff0c\u4e0d\u8fc7\u662f\u8fdf\u65e9\u7684\u95ee\u9898\uff0c\u8fd9\u4e2a\u9053\u7406\u4f60\u5e94\u8be5\u5f88\u6e05\u695a\u3002\u201d</p><p>\u201c\u8fdf\u65e9\u662f\u4e2a\u76f8\u5bf9\u7684\u65f6\u95f4\u6982\u5ff5\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u5982\u679c\u653e\u5230\u6f2b\u957f\u7684\u5386\u53f2\u4e2d\u8861\u91cf\uff0c\u6ca1\u6709\u4eba\u662f\u80dc\u5229\u8005\uff0c\u6240\u6709\u4eba\u90fd\u7ec8\u5c06\u5931\u8d25\u3002\u201d</p><p>\u7075\u5438\u602a\u5deb\u5996\u6c89\u9ed8\u3002</p><p>\u201c\u4f60\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u53d1\u95ee\uff0c\u201c\u4e3a\u4e86\u4f60\u773c\u524d\u7684\u90a3\u4e2a\u4e1c\u897f\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u4e2a\u795e\u5668\uff0c\u201d\u5deb\u5996\u56de\u7b54\uff0c\u201c\u6df1\u6e0a\u53ec\u5524\u4e4b\u86db\u3002\u201d</p><p>\u743c\u6069\u6ca1\u542c\u8fc7\u8fd9\u4e2a\u4e1c\u897f\uff0c\u4ece\u540d\u5b57\u63a8\u6d4b\u662f\u67d0\u79cd\u80fd\u591f\u7528\u4e8e\u5f02\u754c\u53ec\u5524\u7684\u9b54\u6cd5\u7269\u54c1\u3002\u201c\u4f60\u60f3\u5f97\u5230\u5b83\uff1f\u201d\u4ed6\u597d\u5947\u3002</p><p>\u201c\u672c\u6765\u662f\uff0c\u4f46\u73b0\u5728\u6211\u6539\u4e3b\u610f\u4e86\uff0c\u201d\u5deb\u5996\u8bf4\u201c\u6bc1\u6389\u5b83\u3002\u201d</p><p>\u4f20\u5fc3\u7684\u597d\u5904\uff0c\u662f\u65e0\u9700\u50cf\u8bf4\u8bdd\u4e00\u6837\u7ecf\u8fc7\u201c\u60f3\u6cd5\u2014\u2014\u8bf4\u51fa\u2014\u2014\u542c\u89c1\u2014\u2014\u7406\u89e3\u201d\u8fd9\u6837\u590d\u6742\u7684\u8fc7\u7a0b\u7684\uff0c\u76f4\u63a5\u5c06\u81ea\u5df1\u8981\u8868\u8fbe\u7684\u610f\u601d\u6253\u8fdb\u5bf9\u65b9\u7684\u601d\u7ef4\u91cc\uff0c\u7701\u7565\u4e86\u4e2d\u95f4\u7684\u6b65\u9aa4\uff0c\u6240\u4ee5\u6548\u7387\u6781\u9ad8\u3002\u743c\u6069\u548c\u7075\u5438\u602a\u5deb\u5996\u4e00\u756a\u4ea4\u8c08\uff0c\u5176\u5b9e\u8fd8\u6ca1\u7528\u5230\u4e24\u79d2\u949f\u3002\u65af\u5170\u666e\u62ac\u8d77\u53cc\u624b\uff0c\u5feb\u901f\u505a\u4e86\u4e2a\u624b\u52bf\uff0c\u4e00\u67c4\u5de8\u5927\u7684\u900f\u660e\u6218\u9524\u5728\u7a7a\u6c14\u4e2d\u7f13\u7f13\u73b0\u5f62\uff0c\u5deb\u5996\u53cc\u624b\u63e1\u4f4f\u624b\u67c4\uff0c\u9ad8\u4e3e\u8fc7\u9876\uff0c\u7d27\u63a5\u7740\u8f70\u7136\u7838\u4e0b\u3002</p><p>\u8000\u773c\u7684\u5149\u8f89\u8ff8\u53d1\u51fa\u6765\uff0c\u8fce\u4e0a\u900f\u660e\u6218\u9524\uff0c\u4e00\u649e\u4e4b\u4e0b\uff0c\u6c14\u6d6a\u7ffb\u6eda\uff0c\u8fde\u8ddd\u79bb\u5f88\u8fdc\u7684\u743c\u6069\u90fd\u6709\u4e9b\u7a33\u4e0d\u4f4f\u8eab\u5f62\uff0c\u4e0d\u5f97\u4e0d\u5f80\u540e\u9000\u907f\u3002\u6e05\u8106\u7684\u7834\u88c2\u58f0\u7a7f\u900f\u6218\u6597\u7684\u55a7\u56a3\uff0c\u4f20\u8fdb\u6bcf\u4e2a\u4eba\u7684\u8033\u4e2d\uff0c\u743c\u6069\u5b9a\u775b\u770b\u53bb\uff0c\u53d1\u73b0\u90a3\u679a\u60ac\u6d6e\u5728\u7a7a\u4e2d\u7684\u7ea2\u8272\u8718\u86db\u4e0a\uff0c\u5df2\u7ecf\u51fa\u73b0\u4e86\u51e0\u9053\u7ec6\u5fae\u7684\u88c2\u75d5\u3002</p><p>\u201c\u5236\u6b62\u4ed6\uff01\u201d\u67e5\u4e3d\u4e1d\u745e\u51b2\u743c\u6069\u5927\u558a\u3002</p><p>\u7075\u5438\u602a\u5deb\u5996\u4e3e\u8d77\u900f\u660e\u6218\u9524\uff0c\u51c6\u5907\u518d\u6b21\u7838\u4e0b\uff0c\u6b64\u65f6\u743c\u6069\u7ec8\u4e8e\u6709\u4e86\u52a8\u4f5c\u3002\u4ed6\u9ed8\u8bf5\u5492\u8bed\uff0c\u4e0b\u4e86\u4e00\u4e2a\u53d8\u5f62\u672f\uff0c\u9b54\u6cd5\u7684\u7075\u5149\u4e1d\u7ebf\u5728\u865a\u7a7a\u51fa\u73b0\uff0c\u50cf\u86c7\u4e00\u6837\u7f20\u7ed5\u5728\u5deb\u5996\u5728\u8eab\u4f53\u4e0a\uff0c\u60f3\u8981\u8feb\u4f7f\u4ed6\u6539\u53d8\u6210\u743c\u6069\u6240\u5e0c\u671b\u7684\u5f62\u6001\u3002\u5f88\u53ef\u60dc\uff0c\u5deb\u5996\u62b5\u5fa1\u4f4f\u4e86\u8fd9\u4e2a\u6cd5\u672f\uff0c\u4ed6\u7684\u8eab\u4f53\u7a0d\u7a0d\u626d\u66f2\uff0c\u7d27\u63a5\u7740\u53c8\u7acb\u523b\u6062\u590d\u539f\u72b6\u3002\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u51c6\u5907\u5c06\u8fd9\u4e2a\u6cd5\u672f\u7684\u6548\u679c\u518d\u53e0\u52a0\u4e00\u6b21\uff0c\u7136\u800c\u6b64\u65f6\u5deb\u5996\u5df2\u7ecf\u5f00\u59cb\u53cd\u51fb\uff0c\u4ed6\u629b\u5f00\u6218\u9524\uff0c\u644a\u5f00\u624b\u638c\uff0c\u7136\u540e\u4f9d\u6b21\u6273\u52a8\u6307\u8282\uff0c\u6bcf\u4e2a\u6307\u5c16\u90fd\u5f39\u8d77\u567c\u556a\u4f5c\u54cd\u7684\u7535\u5f27\uff0c\u7d27\u63a5\u7740\u4e09\u9053\u95ea\u7535\uff08\u7075\u5438\u602a\u7684\u624b\u638c\u53ea\u6709\u4e09\u6839\u624b\u6307\uff09\u75be\u5c04\u51fa\u6765\uff0c\u51b2\u5411\u743c\u6069\u3002</p><p>\u743c\u6069\u6ca1\u6709\u95ea\u907f\uff0c\u4ed6\u6084\u6084\u542f\u52a8\u4e86\u624b\u4e0a\u7684\u6cd5\u672f\u9006\u8f6c\u6212\u6307\uff0c\u540c\u65f6\u6309\u7d27\u6cd5\u6756\uff0c\u518d\u6b21\u4f7f\u7528\u201c\u6cd5\u672f\u589e\u5e45\u201d\u3002\u81ea\u4ece\u4e0a\u6b21\u7528\u8fd9\u4e00\u62db\u6210\u529f\u5236\u4f4f\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u743c\u6069\u4fbf\u53d1\u89c9\u8fd9\u662f\u975e\u5e38\u597d\u7528\u7684\u529e\u6cd5\uff0c\u6cd5\u672f\u9006\u8f6c\u6212\u6307\u8fd9\u79cd\u8fd1\u4e4e\u795e\u5668\u7684\u73cd\u8d35\u7269\u54c1\uff0c\u6574\u4e2a\u4e16\u754c\u4e0a\u4e5f\u672a\u5fc5\u6709\u51e0\u679a\uff0c\u4e00\u822c\u4eba\u5f88\u96be\u60f3\u50cf\u4ed6\u8fd9\u6837\u5e74\u8f7b\u7684\u5deb\u5e08\u4e5f\u4f1a\u6709\u4e00\u679a\u3002\u800c\u6cd5\u672f\u589e\u5e45\u8fd9\u79cd\u9b54\u6cd5\uff0c\u4e0d\u89c1\u4e8e\u5404\u79cd\u9b54\u6cd5\u4e66\uff0c\u662f\u5965\u6c83\u5355\u72ec\u4f20\u6388\u7684\uff0c\u66f4\u52a0\u7f55\u4e3a\u4eba\u77e5\u3002\u4e24\u6837\u914d\u5408\u8d77\u6765\uff0c\u5c31\u5927\u6709\u51fa\u5176\u4e0d\u610f\u7684\u6548\u679c\u3002</p><p>\u95ea\u7535\u539f\u8def\u8fd4\u56de\uff0c\u5e76\u4e14\u901f\u5ea6\u53d8\u5f97\u66f4\u5feb\uff0c\u5b83\u4eec\u6beb\u65e0\u60ac\u5ff5\u5730\u51fb\u4e2d\u4e86\u60ca\u8bb6\u7684\u7075\u5438\u602a\u5deb\u5996\uff0c\u4f46\u5728\u649e\u4e0a\u4ed6\u7684\u888d\u5b50\u65f6\u5c31\u7acb\u523b\u6d88\u5931\u4e86\uff0c\u5e76\u6ca1\u6709\u9020\u6210\u4e1d\u6beb\u4f24\u5bb3\u3002\u743c\u6069\u6709\u4e9b\u5931\u671b\uff0c\u4ed6\u5feb\u901f\u601d\u8003\u7740\uff0c\u56de\u5fc6\u81ea\u5df1\u51c6\u5907\u7684\u6cd5\u672f\uff0c\u7136\u540e\u53d1\u73b0\u5b83\u4eec\u5f88\u96be\u5bf9\u4e00\u4e2a\u5deb\u5996\u8d77\u4f5c\u7528\u3002\u65af\u5170\u666e\u518d\u6b21\u4f7f\u7528\u4e86\u4e00\u4e2a\u5492\u8bed\uff0c\u8fd9\u6b21\u5b83\u8c28\u614e\u5f97\u591a\uff0c\u7528\u7684\u662f\u7edd\u671b\u672f\u3002\u8fd9\u4e2a\u6cd5\u672f\u518d\u4e00\u6b21\u88ab\u743c\u6069\u53cd\u5f39\u4e86\u56de\u53bb\uff0c\u4f46\u5deb\u5996\u81ea\u7136\u534a\u70b9\u4e0d\u53d7\u5f71\u54cd\uff0c\u4ea1\u7075\u538b\u6839\u6ca1\u6709\u7edd\u671b\u8fd9\u79cd\u60c5\u7eea\u3002</p><p>\u201c\u4f60\u7684\u6212\u6307\u5f88\u6f02\u4eae\u3002\u201d\u743c\u6069\u7684\u8111\u4e2d\u54cd\u8d77\u5deb\u5996\u7684\u58f0\u97f3\uff0c\u663e\u7136\u4ed6\u53d1\u73b0\u4e86\u3002</p><p>\u201c\u591a\u8c22\u5938\u5956\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u7684\u8001\u5e08\u9001\u7684\u3002\u201d</p><p>\u201c\u9001\u7ed9\u6211\u5982\u4f55\uff1f\u201d</p><p>\u201c\u6740\u4e86\u6211\uff0c\u7136\u540e\u5b83\u5c31\u662f\u4f60\u7684\u3002\u201d\u743c\u6069\u5f15\u8bf1\u8bf4\uff0c\u4ed6\u77e5\u9053\u4eba\u7c7b\u5deb\u5996\u5f80\u5f80\u90fd\u4f1a\u5bf9\u73cd\u8d35\u7684\u9b54\u6cd5\u7269\u54c1\u7279\u522b\u7740\u8ff7\uff0c\u4e0d\u77e5\u9053\u7075\u5438\u602a\u5deb\u5996\u662f\u5426\u4e5f\u5982\u6b64\uff0c\u4f46\u5c31\u73b0\u5728\u6765\u770b\uff0c\u5f88\u6709\u53ef\u80fd\u3002</p><p>\u5deb\u5996\u9634\u9634\u5730\u51b7\u7b11\u7740\uff0c\u4e00\u9053\u53c8\u4e00\u9053\u7684\u6cd5\u672f\u63a5\u8fde\u4e0d\u65ad\u5730\u4ece\u4ed6\u6307\u5c16\u6d8c\u51fa\uff0c\u7528\u7684\u5168\u662f\u4ea1\u7075\u6216\u8005\u9644\u9b54\u7c7b\u578b\u7684\u9b54\u6cd5\u2014\u2014\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\u5b83\u4eec\u5371\u9669\u800c\u81f4\u547d\uff0c\u4f46\u5bf9\u5deb\u5996\u800c\u8a00\u5219\u6ca1\u6709\u4efb\u4f55\u5f71\u54cd\uff0c\u5c31\u7b97\u88ab\u53cd\u5f39\u4e86\u4e5f\u5168\u65e0\u6240\u8c13\u3002\u6cd5\u672f\u9006\u8f6c\u6212\u6307\u5e76\u4e0d\u80fd\u591f\u65e0\u9650\u53cd\u5f39\uff0c\u5b83\u4e5f\u662f\u6709\u9650\u5ea6\u7684\u3002\u743c\u6069\u6709\u4e9b\u7126\u6025\u8d77\u6765\uff0c\u4ed6\u5c1d\u8bd5\u53cd\u51fb\uff0c\u4f46\u7edd\u5bf9\u5b9e\u529b\u4e0a\u7684\u60ac\u6b8a\u8ba9\u4ed6\u843d\u5728\u4e0b\u98ce\u3002\u518d\u8fd9\u6837\u7ee7\u7eed\u4e0b\u53bb\u7684\u8bdd\uff0c\u4ed6\u575a\u6301\u4e0d\u4e86\u591a\u4e45\u4e86\u3002</p><p>\u6b63\u5728\u6b64\u65f6\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u7838\u788e\u4e86\u6321\u5728\u5979\u9762\u524d\u7684\u51b0\u9b54\uff0c\u7b2c\u4e00\u4e2a\u8df3\u4e0a\u4e86\u9ad8\u53f0\uff0c\u51b2\u5411\u5deb\u5996\u3002\u65af\u5170\u666e\u8f6c\u773c\u770b\u89c1\uff0c\u62ac\u624b\u53d1\u51fa\u4e00\u9053\u7070\u6697\u5c04\u7ebf\uff0c\u8fd9\u662f\u6b7b\u4ea1\u4e00\u6307\uff0c\u6781\u5176\u51f6\u9669\u7684\u6cd5\u672f\uff0c\u4f46\u6253\u5728\u7ef4\u5eb7\u5c3c\u4e9a\u8eab\u4e0a\u5374\u5168\u65e0\u534a\u70b9\u6548\u679c\uff0c\u5353\u5c14\u7684\u5929\u8d4b\u9b54\u6cd5\u6297\u529b\u6070\u5230\u597d\u5904\u5730\u8d77\u4e86\u4f5c\u7528\u3002\u743c\u6069\u6293\u7d27\u65f6\u673a\uff0c\u4e3e\u8d77\u6cd5\u6756\u5bf9\u7ef4\u5eb7\u5c3c\u4e9a\u4e00\u6307\uff1a\u201c\u52a0\u901f\uff01\u201d</p><p>\u5353\u5c14\u5c11\u5973\u7684\u52a8\u4f5c\u53d8\u5f97\u66f4\u52a0\u8fc5\u6377\uff0c\u77ac\u95f4\u51b2\u5230\u5deb\u5996\u8ddf\u524d\u3002\u6240\u6709\u7684\u5deb\u5e08\u90fd\u4e0d\u559c\u6b22\u88ab\u4eba\u903c\u8fd1\uff0c\u65af\u5170\u666e\u4e5f\u4e0d\u4f8b\u5916\uff0c\u4ed6\u5f80\u540e\u9000\uff0c\u5ff5\u4e86\u4e00\u53e5\u5492\u8bed\uff0c\u8eab\u4f53\u5f80\u4e0a\u6f02\u6d6e\uff0c\u4f46\u7d27\u63a5\u7740\u53c8\u88ab\u4e00\u80a1\u5de8\u5927\u7684\u529b\u91cf\u62c9\u626f\u4e0b\u6765\uff0c\u4eff\u4f5b\u5927\u5730\u5f15\u529b\u9661\u7136\u589e\u5f3a\u4f3c\u7684\u3002\u5deb\u5996\u6709\u4e9b\u4e0d\u5feb\u5730\u53d1\u51fa\u523a\u8033\u7684\u6469\u64e6\u58f0\uff0c\u4ed6\u77e5\u9053\u8fd9\u662f\u743c\u6069\u521a\u521a\u5bf9\u81ea\u5df1\u4f7f\u7528\u4e86\u5927\u5730\u675f\u7f1a\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u8d81\u673a\u4e0a\u524d\uff0c\u6325\u821e\u86c7\u9996\u97ad\u8fde\u8fde\u51fb\u6253\uff0c\u5deb\u5996\u95ea\u907f\u4e0d\u7075\uff0c\u4ed3\u4fc3\u95f4\u6328\u4e86\u597d\u51e0\u4e0b\uff0c\u597d\u5728\u4ed6\u662f\u4ea1\u7075\uff0c\u4e0d\u4f1a\u4e2d\u6bd2\uff0c\u8fd9\u5c31\u5927\u5927\u524a\u5f31\u4e86\u86c7\u9996\u97ad\u7684\u5a01\u529b\u3002</p><p>\u201c\u86ee\u725b\u4e4b\u529b\uff01\u201d</p><p>\u201c\u732b\u4e4b\u4f18\u96c5\uff01\u201d</p><p>\u201c\u72ee\u4e4b\u51b2\u950b\uff01\u201d</p><p>\u743c\u6069\u5c06\u4e00\u9053\u9053\u6cd5\u672f\u53e0\u52a0\u5728\u7ef4\u5eb7\u5c3c\u4e9a\u8eab\u4e0a\uff0c\u8fd9\u4e9b\u5168\u90fd\u662f\u589e\u76ca\u5f3a\u5316\u7c7b\u578b\u7684\u9b54\u6cd5\uff0c\u4e0d\u4f1a\u88ab\u5353\u5c14\u7684\u5929\u8d4b\u6297\u529b\u62b5\u5fa1\u6389\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u5c06\u86c7\u9996\u97ad\u6325\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u5979\u7684\u52a8\u4f5c\u53d8\u5f97\u5feb\u82e5\u95ea\u7535\u3002\u5deb\u5996\u8fde\u8fde\u540e\u9000\uff0c\u5b83\u7684\u888d\u5b50\u5df2\u7ecf\u88ab\u6495\u5f00\u51e0\u9053\u53e3\u5b50\uff0c\u5e72\u67af\u7684\u76ae\u8089\u4e5f\u88ab\u6bd2\u86c7\u54ac\u4e0b\u3002\u5728\u8fd9\u6837\u6025\u98ce\u66b4\u96e8\u822c\u7684\u653b\u51fb\u4e0b\uff0c\u4ed6\u6839\u672c\u6765\u4e0d\u53ca\u5ff5\u8bf5\u5492\u8bed\uff0c\u800c\u4e14\u9762\u5bf9\u4e00\u4e2a\u9ed1\u6697\u7cbe\u7075\uff0c\u4ed6\u4e5f\u6ca1\u529e\u6cd5\u4fdd\u8bc1\u6cd5\u672f\u4e00\u5b9a\u4f1a\u6210\u529f\u3002</p><p>\u5deb\u5996\u8ba8\u538c\u8fd9\u79cd\u4e0d\u786e\u5b9a\u6027\u3002</p><p>\u518d\u4e00\u6b21\u7684\uff0c\u4ed6\u88ab\u86c7\u9996\u97ad\u51fb\u4e2d\uff0c\u7ecf\u8fc7\u6cd5\u672f\u5f3a\u5316\u7684\u7ef4\u5eb7\u5c3c\u4e9a\u529b\u6c14\u5927\u5f97\u51fa\u5947\uff0c\u987a\u624b\u4e00\u5e26\u51e0\u4e4e\u5c06\u4ed6\u6454\u5012\u3002\u5deb\u5996\u8e09\u8dc4\u540e\u9000\uff0c\u4ed6\u8003\u8651\u5feb\u901f\u7ed3\u675f\u8fd9\u573a\u6218\u6597\uff0c\u518d\u62d6\u5ef6\u4e0b\u53bb\u7684\u8bdd\uff0c\u7b49\u5b66\u9662\u91cc\u5176\u4ed6\u4eba\u8d76\u6765\uff0c\u90a3\u5c31\u66f4\u96be\u8131\u8eab\u4e86\u3002</p><p>\u4ed6\u6273\u52a8\u624b\u6307\uff0c\u53d1\u51fa\u567c\u556a\u7684\u58f0\u97f3\uff0c\u4e00\u622a\u6307\u9aa8\u88ab\u4ed6\u81ea\u5df1\u62d7\u65ad\u4e0b\u6765\u3002\u743c\u6069\u6ce8\u610f\u5230\u4e86\u8fd9\u4e2a\u52a8\u4f5c\uff0c\u4f46\u4e0d\u660e\u767d\u5deb\u5996\u6253\u7b97\u505a\u4ec0\u4e48\u3002\u6b63\u7591\u60d1\u95f4\uff0c\u4e00\u9053\u5fc3\u7075\u6ce2\u52a8\u6253\u8fdb\u4ed6\u7684\u8111\u6d77\u91cc\u3002</p><p>\u201c\u5e74\u8f7b\u7684\u5deb\u5e08\uff0c\u6211\u4eec\u8fd8\u4f1a\u518d\u6b21\u89c1\u5230\u7684\uff0c\u5728\u4e0d\u4e45\u7684\u5c06\u6765\uff0c\u201d\u65af\u5170\u666e\u8bf4\uff0c\u201c\u7559\u7740\u4f60\u624b\u4e0a\u90a3\u679a\u6212\u6307\uff0c\u7b49\u7740\u6211\u3002\u201d</p><p>\u7136\u540e\u5deb\u5996\u7684\u8eab\u4f53\u70b8\u88c2\u5f00\u6765\u3002</p><p>\u94fa\u5929\u76d6\u5730\u7684\u7ea2\u5149\u4ee5\u5deb\u5996\u4e3a\u4e2d\u5fc3\u5411\u5916\u6025\u5267\u6269\u6563\uff0c\u4eff\u4f5b\u51b3\u5824\u6d2a\u6c34\u4e00\u6837\u6c9b\u7136\u83ab\u5fa1\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u9996\u5f53\u5176\u51b2\uff0c\u88ab\u91cd\u91cd\u51fb\u98de\u4e86\u51fa\u53bb\uff0c\u7830\u5730\u6454\u843d\u53f0\u4e0b\uff0c\u542c\u52a8\u9759\u7b80\u76f4\u8981\u628a\u5730\u9762\u7838\u51fa\u4e00\u4e2a\u5927\u5751\u3002\u743c\u6069\u8ddd\u79bb\u8f83\u8fdc\uff0c\u773c\u89c1\u5f62\u52bf\u4e0d\u5bf9\uff0c\u8fde\u5fd9\u6fc0\u6d3b\u4e86\u7075\u5316\u6597\u7bf7\u3002\u4ed6\u7684\u8eab\u4f53\u77ac\u95f4\u6c14\u5316\uff0c\u53d8\u6210\u4e00\u56e2\u5e7d\u7075\u822c\u7684\u5b58\u5728\u3002\u7ea2\u5149\u8513\u5ef6\u800c\u6765\uff0c\u4e00\u6ce2\u4e00\u6ce2\u5730\u51b2\u8fc7\u743c\u6069\u7684\u8eab\u4f53\uff0c\u5374\u4eff\u4f5b\u5fae\u98ce\u62c2\u9762\uff0c\u6ca1\u6709\u5bf9\u4ed6\u9020\u6210\u4efb\u4f55\u4f24\u5bb3\u3002</p><p>\u7ec8\u4e8e\u4e00\u5207\u5e73\u606f\uff0c\u743c\u6069\u677e\u4e86\u53e3\u6c14\uff0c\u91cd\u65b0\u8f6c\u56de\u5b9e\u4f53\u72b6\u6001\uff0c\u5ba1\u89c6\u5468\u56f4\u3002\u90a3\u4e2a\u50cf\u662f\u7ea2\u5b9d\u77f3\u5236\u6210\u7684\u8718\u86db\u5df2\u7ecf\u6d88\u5931\u4e0d\u89c1\uff0c\u5deb\u5996\u70b8\u6210\u4e86\u788e\u7247\uff0c\u4ed6\u53ec\u5524\u51fa\u7684\u51b0\u9b54\u4e5f\u53d8\u6210\u4e86\u51b0\u6676\u3002\u56db\u4e2a\u5353\u5c14\u62a4\u536b\u5168\u90e8\u5012\u5728\u5730\u4e0a\u4e00\u52a8\u4e0d\u52a8\uff0c\u770b\u6837\u5b50\u662f\u6c14\u7edd\u8eab\u4ea1\u4e86\u3002\u67e5\u4e3d\u4e1d\u745e\u5374\u4e0d\u77e5\u6240\u8e2a\u3002</p><p>\u743c\u6069\u7f13\u7f13\u843d\u4e0b\u5730\u6765\uff0c\u6b63\u8981\u4e0a\u524d\u5bdf\u770b\u3002\u9ad8\u53f0\u4e0b\u7ef4\u5eb7\u5c3c\u4e9a\u52a8\u4e86\u4e00\u52a8\uff0c\u63a5\u7740\u6162\u6162\u722c\u8d77\u8eab\uff0c\u5979\u6447\u6447\u6643\u6643\uff0c\u8840\u4ece\u989d\u5934\u4e0a\u6dcc\u4e0b\u6765\uff0c\u5f04\u5f97\u6ee1\u8138\u90fd\u662f\uff0c\u4f46\u5e94\u8be5\u6ca1\u6709\u5927\u788d\u3002\u743c\u6069\u6709\u4e9b\u60ca\u8bb6\uff0c\u5979\u8ddd\u79bb\u5deb\u5996\u6700\u8fd1\uff0c\u7406\u5f53\u662f\u53d7\u5230\u51b2\u51fb\u6700\u5927\u624d\u5bf9\uff0c\u5982\u4eca\u770b\u8d77\u6765\u53cd\u800c\u53d7\u4f24\u6700\u8f7b\u7684\u6837\u5b50\u3002</p><p>\u201c\u4f60\u6ca1\u4e8b\u5427\u3002\u201d\u743c\u6069\u95ee\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u6447\u6447\u5934\uff0c\u62ed\u53bb\u8138\u4e0a\u7684\u8840\uff0c\u514d\u5f97\u906e\u6321\u89c6\u7ebf\u3002\u201c\u6ca1\u4e8b\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5e78\u597d\u6211\u7a7f\u4e86\u8fd9\u4ef6\u6597\u7bf7\u3002\u201d</p><p>\u743c\u6069\u8fd9\u624d\u6ce8\u610f\u5230\uff0c\u5979\u8eab\u4e0a\u7684\u6597\u7bf7\u548c\u4ee5\u524d\u89c1\u5230\u7684\u4f3c\u4e4e\u6709\u6240\u4e0d\u540c\uff0c\u5b83\u770b\u8d77\u6765\u662f\u9ed1\u8272\u534a\u900f\u660e\u7684\uff0c\u9690\u7ea6\u900f\u51fa\u91cc\u9762\u7684\u9501\u7532\uff0c\u4e0a\u9762\u7684\u82b1\u7eb9\u56fe\u6848\u4e5f\u975e\u5e38\u72ec\u7279\u3002\u201c\u5b83\u770b\u8d77\u6765\u4e0d\u9519\uff0c\u201d\u743c\u6069\u968f\u53e3\u8bf4\uff0c\u201c\u4ec0\u4e48\u540d\u5b57\uff1f\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8138\u8272\u4e00\u9eef\uff0c\u201c\u4f8d\u7236\u6597\u7bf7\uff08Cloak of the Consort\uff09\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u6211\u7236\u4eb2\u7559\u7ed9\u6211\u7684\u3002\u201d</p><p>\u201c\u7236\u4eb2\uff1f\u201d\u743c\u6069\u73a9\u5473\u7740\u8fd9\u4e2a\u8bcd\u3002\u5728\u5353\u5c14\u8fd9\u79cd\u6bcd\u7cfb\u793e\u4f1a\u91cc\uff0c\u7236\u4eb2\u8fd9\u4e2a\u8bcd\u53ef\u771f\u96be\u5f97\u80fd\u542c\u5230\u3002\u4ed6\u6b63\u60f3\u518d\u95ee\uff0c\u7a81\u7136\u542c\u89c1\u9ad8\u53f0\u4fa7\u9762\u4f20\u6765\u547b\u541f\u58f0\u3002</p><p>\u743c\u6069\u8f6c\u8fc7\u53bb\u4e00\u770b\uff0c\u53d1\u73b0\u662f\u67e5\u4e3d\u4e1d\u745e\u3002\u5979\u5b9e\u5728\u5f88\u4e0d\u8d70\u8fd0\uff0c\u5deb\u5996\u81ea\u7206\u7684\u65f6\u5019\uff0c\u5979\u6b63\u51c6\u5907\u51b2\u4e0a\u53f0\uff0c\u4f18\u826f\u7684\u9b54\u6cd5\u6597\u7bf7\u548c\u7cbe\u91d1\u9501\u7532\u5e2e\u52a9\u5979\u62b5\u5fa1\u4f4f\u4e86\u51b2\u51fb\uff0c\u4f46\u88ab\u5de8\u5927\u7684\u529b\u91cf\u9707\u788e\u4e86\u2014\u2014\u7136\u540e\u4e00\u622a\u65ad\u88c2\u7684\u51b0\u9b54\u624b\u81c2\u659c\u523a\u98de\u6765\uff0c\u51c6\u786e\u5730\u624e\u8fdb\u4e86\u5979\u6beb\u65e0\u4fdd\u62a4\u7684\u8179\u90e8\u3002\u5979\u6454\u4e86\u4e0b\u6765\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002</p><p>\u201c\u6551\u6211\uff01\u201d\u5979\u770b\u7740\u743c\u6069\u3002</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u4f38\u624b\u5f80\u6000\u91cc\u6478\u6cbb\u7597\u836f\u6c34\uff0c\u4ed6\u8bb0\u5f97\u5e26\u4e86\u51e0\u74f6\u3002\u6b64\u65f6\u7ef4\u5eb7\u5c3c\u4e9a\u4ece\u540e\u9762\u8d70\u8fc7\u6765\uff0c\u201c\u4f60\u53d7\u4f24\u4e86\uff0c\u59d0\u59d0\u3002\u201d\u5979\u8f7b\u58f0\u8bf4\u3002</p><p>\u201c\u6551\u6551\u6211\uff01\u201d\u67e5\u4e3d\u4e1d\u745e\u91cd\u590d\uff0c\u5979\u7684\u773c\u795e\u5df2\u7ecf\u5f00\u59cb\u6da3\u6563\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u4f38\u624b\uff0c\u63e1\u4f4f\u4e86\u523a\u8fdb\u7267\u5e08\u8179\u90e8\u7684\u534a\u622a\u51b0\u9b54\u624b\u81c2\uff0c\u770b\u8d77\u6765\u4f3c\u4e4e\u8981\u5c06\u5b83\u62d4\u51fa\u3002\u201c\u7b49\u7b49\uff0c\u201d\u743c\u6069\u63d0\u9192\uff0c\u201c\u8fd9\u6837\u5979\u4f1a\u5927\u91cf\u6d41\u8840\u7684\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u70b9\u70b9\u5934\uff0c\u624b\u4e0a\u7528\u529b\uff0c\u5c06\u51b0\u9b54\u624b\u81c2\u91cd\u91cd\u538b\u4e86\u4e0b\u53bb\uff0c\u8d2f\u7a7f\u4e86\u7267\u5e08\u7684\u8eab\u4f53\u3002</p></article>"}}}]);
