(window.webpackJsonp=window.webpackJsonp||[]).push([[146],{169:function(p){p.exports={chapterTitle:null,title:"\u7b2c1126\u7ae0 \u65b0\u5b98\u4e0a\u4efb\u7684\u524d\u594f",content:"<article><h2>\u7b2c1126\u7ae0 \u65b0\u5b98\u4e0a\u4efb\u7684\u524d\u594f</h2><p>\u3000\u3000\u4f5c\u4e3a\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u4e3b\u4f53\u89c4\u5212\u7684\u4e00\u90e8\u5206\uff0c\u4e34\u6e2f\u65b0\u57ce\u65e9\u5728\u4e5d\u516b\u5e74\u4e4b\u524d\u5c31\u6709\u4e86\u89c4\u5212\u7a1a\u5f62\u3002\u4e5d\u516b\u5e74\u51ac\u4e0e\u4e3b\u4f53\u5de5\u7a0b\u89c4\u5212\u4e00\u8d77\u9001\u56fd\u5bb6\u90e8\u59d4\u5ba1\u6279\u3002\u521d\u7a3f\u56e0\u4e3a\u8981\u5360\u7528\u8fd1\u4e07\u4ea9\u57fa\u672c\u519c\u7530\u3001\u89c4\u5212\u9762\u79ef\u8fc7\u5927\u7ed9\u56fd\u5bb6\u8ba1\u5212\u53d1\u5c55\u59d4\u67aa\u6bd9\u6389\uff0c\u7ecf\u8fc7\u4e24\u8f6e\u4fee\u6539\u6700\u7ec8\u7684\u89c4\u5212\u65b9\u6848\u5230\u53bb\u5e74\u51ac\u5929\u624d\u5f97\u4ee5\u5ba1\u6279\u901a\u8fc7\u3002</p><p>\u3000\u3000\u9664\u4e86\u4e00\u671f\u5de5\u7a0b\u5f81\u7528\u56db\u5343\u4ea9\u5730\u5916\uff0c\u4e8c\u671f\u5de5\u7a0b\u6240\u9700\u7684\u516b\u5343\u4ea9\u5730\u5c06\u5168\u90e8\u9700\u8981\u586b\u6d77\u9020\u5730\u3002</p><p>\u3000\u3000\u5373\u4fbf\u5982\u6b64\uff0c\u4e34\u6e2f\u65b0\u57ce\u5b9a\u7a3f\u89c4\u5212\u4e5f\u53ea\u662f\u5bb9\u7eb3\u4e8c\u5341\u4e07\u4eba\u53e3\u89c4\u6a21\u7684\u5c45\u4f4f\u3001\u5546\u8d38\u3001\u529e\u516c\u7efc\u5408\u533a\u57df\uff0c\u4e0e\u9526\u6e56\u6700\u521d\u4e3b\u5bfc\u7684\u767e\u4e07\u4eba\u53e3\u6d77\u6e2f\u90fd\u5e02\u7684\u89c4\u6a21\u76f8\u5dee\u751a\u8fdc\u3002</p><p>\u3000\u3000\u5f20\u6885\u5904\u5728\u6b64\u65f6\u7684\u4f4d\u5b50\u4e0a\uff0c\u5bf9\u4e0a\u4e0b\u7684\u5fc3\u6001\u6478\u5f97\u6700\u4e3a\u6e05\u695a\uff0c\u5e02\u91cc\u867d\u7136\u5bf9\u4e1c\u5c71\u6e2f\u672a\u6765\u7684\u53d1\u5c55\u4e5f\u6ca1\u6709\u7279\u522b\u5f3a\u70c8\u7684\u4fe1\u5fc3\uff0c\u4f46\u662f\u8eab\u4e3a\u9886\u5bfc\u8005\u8c01\u4e0d\u559c\u6b22\u89c4\u6a21\u5b8f\u5927\u7684\u84dd\u56fe\u6765\u6307\u70b9\u6c5f\u5c71\uff1f\u867d\u7136\u5b9a\u7a3f\u662f\u4ee5\u5bb9\u7eb3\u4e8c\u5341\u4e07\u4eba\u53e3\u5c45\u4f4f\u5546\u8d38\u529e\u516c\u89c4\u6a21\u4e3a\u6807\u51c6\uff0c\u8fd8\u662f\u7ed9\u5c06\u6765\u7684\u53d1\u5c55\u7559\u4e0b\u8db3\u591f\u7684\u60f3\u8c61\u7a7a\u95f4\uff0c\u60f3\u7740\u8fc7\u4e0a\u4e94\u516d\u5e74\u518d\u4fee\u6539\u89c4\u5212\u7a3f\u4e0d\u8fdf\u3002</p><p>\u3000\u3000\u961a\u552f\u6d9b\u3001\u9676\u4e66\u827a\u7b49\u5e02\u59d4\u9886\u5bfc\u5fc3\u91cc\u6781\u8feb\u5207\u7684\u5e0c\u671b\u4e1c\u5c71\u6e2f\u7684\u84dd\u56fe\u80fd\u65e9\u65e5\u53d8\u6210\u4e8b\u5b9e\uff0c\u65e2\u662f\u4e3a\u5b98\u4e00\u5730\u7684\u5b8f\u56fe\u4f1f\u4e1a\uff0c\u4e5f\u662f\u5411\u4e0a\u5347\u8fc1\u7684\u575a\u5b9e\u57fa\u7840\uff0c\u5e0c\u671b\u6240\u6709\u6295\u8d44\u8005\u7684\u5efa\u9020\u89c4\u6a21\u8d8a\u5927\u8d8a\u597d\uff0c\u5efa\u9020\u901f\u5ea6\u8d8a\u5feb\u8d8a\u597d\u3002</p><p>\u3000\u3000\u5728\u4e16\u7eaa\u9526\u6e56\u7684\u4e00\u671f\u5efa\u8bbe\u65b9\u6848\u62ff\u51fa\u6765\u4e4b\u524d\uff0c\u751a\u81f3\u5c31\u5728\u6628\u5929\uff0c\u65b0\u4ead\u5e02\u59d4\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u9886\u5bfc\u5c0f\u7ec4\u53ec\u5f00\u4f8b\u4f1a\u8fd8\u63d0\u5230\u4e34\u6e2f\u65b0\u57ce\u7684\u5f00\u53d1\u95ee\u9898\uff0c\u5e02\u59d4\u4e66\u8bb0\u961a\u552f\u6d9b\u5728\u4f1a\u8bae\u4e0a\u751a\u81f3\u660e\u786e\u8981\u6c42\u8499\u5b66\u5e86\u7ed9\u5f20\u606a\u76f4\u63a5\u505a\u5de5\u4f5c\uff0c\u4fdd\u8bc1\u4e16\u7eaa\u9526\u6e56\u5bf9\u4e34\u6e2f\u65b0\u57ce\u7684\u521d\u671f\u5f00\u53d1\u89c4\u6a21\u4e0d\u8981\u4f4e\u4e8e\u4e00\u5343\u4ea9\u3002</p><p>\u3000\u3000\u5e02\u91cc\u7684\u5fc3\u60c5\u6781\u4e3a\u8feb\u5207\uff0c\u5728\u89c4\u5212\u83b7\u5f97\u5ba1\u6279\u901a\u8fc7\u4e4b\u524d\uff0c\u4e00\u671f\u7684\u56db\u5343\u4ea9\u5730\u7684\u5f81\u5730\u3001\u56db\u5e73\u4e00\u901a\u5de5\u7a0b\u6089\u4ee5\u5b8c\u6210\uff0c\u8c01\u77e5\u9053\u4e16\u7eaa\u9526\u6e56\u7684\u4e00\u671f\u5efa\u8bbe\u65b9\u6848\u62ff\u51fa\u6765\uff0c\u8fd9\u56db\u5343\u4ea9\u5730\u751a\u81f3\u90fd\u4e0d\u591f\u4e24\u5e74\u5efa\u8bbe\u6240\u7528\uff0c\u8981\u540c\u65f6\u8fdb\u884c\u586b\u6d77\u5de5\u7a0b\u624d\u884c\u3002</p><p>\u3000\u3000\u961a\u552f\u6d9b\u3001\u9676\u4e66\u827a\u81ea\u7136\u559c\u5f62\u4e8e\u8272\uff0c\u8fd9\u53ef\u8fdc\u8fdc\u8d85\u8fc7\u4ed6\u4eec\u7684\u9884\u671f\uff0c\u76f8\u89c6\u800c\u7b11\uff0c\u9526\u6e56\u8fd9\u662f\u62ff\u5b9e\u9645\u884c\u52a8\u6247\u8ba1\u5212\u53d1\u5c55\u59d4\u7684\u8138\u554a\uff1a\u4f60\u8bf4\u6211\u7684\u89c4\u5212\u7a3f\u597d\u9ad8\u9a9b\u8fdc\u3001\u4e0d\u5207\u5b9e\u9645\uff0c\u538b\u7f29\u53ea\u7ed9\u5efa\u4e8c\u5341\u4e07\u4eba\u53e3\u89c4\u6a21\u7684\u7efc\u5408\u57ce\u533a\uff0c\u6211\u4e24\u5e74\u5c31\u5c06\u89c4\u5212\u5730\u5efa\u6389\u4e00\u534a\uff0c\u8ba1\u5212\u53d1\u5c55\u59d4\u6240\u8c13\u7684\u957f\u671f\u773c\u5149\u4e0d\u4f1a\u53ea\u80fd\u770b\u5230\u56db\u5e74\u4e4b\u540e\u5427\uff1f</p><p>\u3000\u3000\u4e34\u6e2f\u65b0\u57ce\u6838\u5fc3\u533a\u57df\u6574\u4f53\u5f00\u53d1\uff0c\u5f00\u53d1\u89c4\u6a21\u5de8\u5927\uff0c\u5bf9\u5f00\u53d1\u516c\u53f8\u7684\u8981\u6c42\u81ea\u7136\u4e5f\u8981\u6781\u9ad8\u3002\u62db\u6807\u5165\u56f4\u7684\u6761\u4ef6\u9996\u5148\u8981\u6c42\u5f00\u53d1\u516c\u53f8\u7684\u6ce8\u518c\u8d44\u91d1\u4e0d\u4f4e\u4e8e\u4e94\u4ebf\u3001\u7528\u4e8e\u4e34\u6e2f\u65b0\u57ce\u5efa\u8bbe\u7684\u542f\u52a8\u8d44\u91d1\u4e0d\u4f4e\u4e8e\u4e94\u4ebf\uff0c\u4ec5\u4e24\u4e2a\u6761\u4ef6\u5dee\u4e0d\u591a\u5c31\u5c06\u6c5f\u5357\u3001\u4e1c\u6d77\u7701\u5185\u7684\u5730\u4ea7\u4f01\u4e1a\u67aa\u6bd9\u4e86\u4e2a\u5e72\u51c0\u3002</p><p>\u3000\u3000\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u5f71\u54cd\u518d\u5927\uff0c\u6bd5\u7adf\u4e5f\u53ea\u662f\u7ecf\u6d4e\u6b20\u53d1\u8fbe\u7684\u6c5f\u5357\u7701\u4e00\u9685\uff0c\u56fd\u5185\u5927\u5730\u4ea7\u5546\u5bf9\u4e00\u7ebf\u57ce\u5e02\u987e\u4e0d\u8fc7\u6765\uff0c\u54ea\u91cc\u4f1a\u8dd1\u5230\u65b0\u4ead\u8fd9\u65ee\u65ef\u6765\u8bd5\u6df1\u6d45\uff1f\u9664\u4e86\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u4e4b\u5916\uff0c\u4e5f\u6839\u672c\u627e\u4e0d\u5230\u6709\u5176\u4ed6\u516c\u53f8\u6562\u63a5\u624b\u3002</p><p>\u3000\u3000\u5728\u5317\u5c71\u5bbe\u9986\u7528\u8fc7\u665a\u9910\uff0c\u5c31\u53c8\u5750\u8f66\u966a\u540c\u5f20\u606a\u3001\u90b5\u81f3\u521a\u524d\u5f80\u4e34\u6e2f\u65b0\u57ce\u89c4\u5212\u9009\u5740\u53bb\u89c6\u5bdf\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u867d\u8bf4\u89c4\u5212\u53bb\u5e74\u51ac\u5929\u624d\u83b7\u5f97\u901a\u8fc7\uff0c\u56db\u5343\u4ea9\u5730\u7684\u5f81\u5730\u3001\u4e09\u5e73\u4e00\u901a\u5de5\u5730\u5374\u5728\u4e4b\u524d\u5c31\u5df2\u7ecf\u5b8c\u6bd5\u3002\u867d\u7136\u6709\u8fdd\u89c4\u5acc\u7591\uff0c\u4e5f\u53ef\u89c1\u5730\u65b9\u4e0a\u7684\u8feb\u5207\u5fc3\u60c5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6708\u6717\u661f\u7a00\uff0c\u62c6\u8fc1\u4e00\u7a7a\u7684\u3001\u5e7f\u9614\u7684\u539f\u91ce\u671b\u4e4b\u65e0\u57a0\uff0c\u9632\u6ce2\u5824\u5728\u8fdc\u5904\u5c31\u4e00\u9053\u9ed1\u8272\u7684\u5f27\u7ebf\u6d6e\u5728\u534a\u7a7a\u4e2d\u4f3c\u7684\u3002</p><p>\u3000\u3000\u8fd9\u91cc\u662f\u901a\u5c9b\u5de5\u7a0b\u7684\u5357\u7aef\uff0c\u6cbf\u7740\u4fee\u597d\u7684\u901a\u6e2f\u516c\u8def\u5f80\u524d\u5f00\uff0c\u5728\u4e0a\u5f15\u6865\u4e4b\u524d\uff0c\u6709\u4e00\u680b\u5341\u516d\u5c42\u7684\u9ad8\u5c42\u5efa\u7b51\u5b64\u96f6\u96f6\u7684\u77d7\u5728\u8def\u65c1\uff0c\u591c\u91cc\u706f\u706b\u901a\u660e\uff0c\u4eff\u4f5b\u662f\u539f\u91ce\u91cc\u7684\u706f\u5854\u3002\u8fd9\u680b\u9ad8\u697c\u662f\u5728\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u6b63\u5f0f\u542f\u52a8\u4e4b\u524d\u7531\u5b8f\u8fdc\u5efa\u8bbe\u6295\u8d44\u5efa\u9020\uff0c\u662f\u4e1c\u5c71\u9547\u7684\u6700\u9ad8\u5efa\u7b51\u3002\u8bf4\u6765\u5bd2\u9178\uff0c\u8fd9\u680b\u5341\u516d\u5c42\u9ad8\u7684\u697c\u4e5f\u662f\u65b0\u4ead\u5e02\u7684\u6700\u9ad8\u5efa\u7b51\uff0c\u5efa\u6210\u4e4b\u540e\uff0c\u5c31\u4f5c\u4e3a\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u6307\u6325\u90e8\u6240\u5728\u5730\uff0c\u4e1c\u5c71\u6e2f\u5f00\u53d1\u6709\u9650\u516c\u53f8\u4ee5\u53ca\u5404\u5927\u6295\u8d44\u80a1\u4e1c\u8054\u7edc\u529e\u516c\u5ba4\u4e5f\u90fd\u5728\u91cc\u9762\u529e\u516c\u3002</p><p>\u3000\u3000\u4e34\u6e2f\u65b0\u57ce\u7684\u6838\u5fc3\u533a\u57df\u5c31\u5728\u8fd9\u697c\u7684\u5357\u9762\uff0c\u5728\u5de5\u7a0b\u6307\u6325\u90e8\u6709\u4e34\u6e2f\u65b0\u57ce\u89c4\u5212\u7684\u6a21\u578b\uff0c\u7ad9\u5728\u697c\u9876\u773a\u671b\u591c\u8272\u4e0b\u5e73\u9614\u7684\u571f\u5730\uff0c\u4e5f\u66f4\u5bb9\u6613\u60f3\u8c61\u4e24\u5e74\u4e4b\u540e\u4e34\u6e2f\u65b0\u57ce\u7684\u6a21\u6837\u3002</p><p>\u3000\u3000\u524d\u65b9\u505c\u7740\u4e00\u8f86\u8f66\uff0c\u8def\u7259\u5916\u7ad9\u7740\u4e24\u4eba\u4f3c\u4e4e\u4e5f\u5728\u773a\u671b\u591c\u8272\u4e0b\u7684\u571f\u5730\u3002\u8def\u706f\u660f\u6697\uff0c\u8f66\u706f\u7167\u8fc7\u6765\uff0c\u90a3\u4e24\u4eba\u8f6c\u8fc7\u8eab\u6765\uff0c\u4e00\u4eba\u662f\u8eab\u6750\u9ad8\u6311\u7684\u5973\u5b50\uff0c\u7a7f\u7740\u73ab\u7ea2\u8272\u7684\u9f50\u819d\u98ce\u8863\uff0c\u5a77\u5a77\u7389\u7acb\u7684\u7ad9\u5728\u8def\u7259\u4e0a\uff0c\u9732\u51fa\u7ea4\u7ec6\u7684\u5c0f\u817f\uff0c\u4f3c\u4e4e\u7a7f\u7740\u8089\u8272\u7684\u4e1d\u889c\uff0c\u4e09\u6708\u591c\u6e05\u5bd2\uff0c\u5149\u7740\u5c0f\u817f\u5728\u65f7\u91ce\u91cc\u4e5f\u771f\u591f\u597d\u53d7\u7684\uff1b\u53e6\u4e00\u4eba\u662f\u4e2a\u94f6\u53d1\u82cd\u82cd\u3001\u7a7f\u5939\u514b\u7684\u8001\u8005\u3002\u8f66\u706f\u4e00\u626b\u800c\u8fc7\uff0c\u4e5f\u770b\u4e0d\u6e05\u4e24\u4eba\u7684\u957f\u76f8\uff0c\u53ea\u6765\u5f97\u53ca\u770b\u6e05\u505c\u5728\u8def\u8fb9\u7684\u8f66\u662f\u516b\u5341\u5e74\u4ee3\u8001\u6b3e\u5f0f\u5965\u8fea\uff0c\u58a8\u7eff\u8272\uff0c\u7ebf\u6761\u504f\u786c\u3002</p><p>\u3000\u3000\u5085\u4fca\u8ba9\u53f8\u673a\u505c\u4e0b\u8f66\u6765\uff0c\u8ddf\u5f20\u606a\u8bf4\uff1a\u201c\u540e\u9762\u961a\u4e66\u8bb0\u4ed6\u4eec\u505c\u8f66\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u56de\u8f66\u770b\u4e86\u4e00\u773c\uff0c\u540e\u9762\u7684\u8f66\u90fd\u505c\u4e86\u4e0b\u6765\uff1a\u961a\u552f\u6d9b\u4ed6\u4eec\u8f66\u4e00\u505c\uff0c\u90a3\u4e4b\u524d\u505c\u5728\u8def\u8fb9\u7684\u8f66\u91cc\u968f\u5373\u4e0b\u6765\u4e24\u540d\u7a7f\u519b\u670d\u7684\u9752\u5e74\uff0c\u770b\u60c5\u5f62\u50cf\u662f\u8001\u4eba\u4e0e\u5973\u5b50\u7684\u8b66\u536b\u3002</p><p>\u3000\u3000\u201c\u8fd9\u8001\u4eba\u8c01\u554a\uff1f\u201d\u5f20\u606a\u8be7\u5f02\u7684\u95ee\u4e86\u4e00\u58f0\uff0c\u90a3\u4e24\u540d\u8b66\u536b\u4f3c\u4e4e\u8fd8\u4f69\u7740\u67aa\uff0c\u8868\u65f6\u90a3\u8001\u4eba\u7684\u8eab\u4efd\u6781\u9ad8\uff0c\u4ed6\u4e0e\u90b5\u81f3\u521a\u4e5f\u5fd9\u4e0b\u8f66\u6765\u8d70\u8fc7\u53bb\uff0c\u770b\u7740\u961a\u552f\u6d9b\u5df2\u7ecf\u8d70\u8fc7\u53bb\u4eb2\u70ed\u7684\u63e1\u4f4f\u8001\u4eba\u7684\u624b\uff1a\u201c\u6c5f\u8001\u5230\u65b0\u4ead\u6765\uff0c\u600e\u4e48\u4e0d\u63d0\u524d\u901a\u77e5\u4e00\u58f0\uff0c\u4e5f\u597d\u8ba9\u6211\u4eec\u6709\u4e2a\u51c6\u5907\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5c31\u968f\u4fbf\u8fc7\u6765\u770b\u770b\uff1b\u5730\u65b9\u4e0a\u4e0d\u5bb9\u6613\uff0c\u6bcf\u6b21\u90fd\u8981\u51c6\u5907\uff0c\u6211\u8fd9\u6837\u7684\u8001\u5bb6\u4f19\u4f1a\u4e0d\u4f1a\u5f88\u8ba8\u4eba\u538c\u70e6\uff1f\u201d\u8001\u4eba\u723d\u6717\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u8fd8\u81ea\u79f0\u4e3a\u201c\u8001\u5bb6\u4f19\u201d\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u8001\u4eba\u59d3\u201c\u6c5f\u201d\uff0c\u77ac\u65f6\u60f3\u8d77\u6765\u8fd9\u8001\u4eba\u662f\u8c01\u6765\u3002</p><p>\u3000\u3000\u524d\u526f\u603b\u7406\u6c5f\u9759\u6db5\u662f\u65b0\u4ead\u897f\u4e61\u4eba\uff0c\u4ece\u516b\u5341\u5e74\u540e\u671f\u6700\u7ec8\u786e\u8ba4\u4e1c\u5c71\u6df1\u6c34\u822a\u9053\u8d44\u6e90\u6709\u5f00\u53d1\u4ef7\u503c\u4e4b\u540e\u5c31\u4e00\u76f4\u662f\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u7684\u79ef\u6781\u63a8\u52a8\u8005\uff0c\u53ea\u53ef\u60dc\u4e00\u76f4\u5230\u4ed6\u9000\u4e0b\u6765\u524d\uff0c\u7ecf\u6d4e\u4e0a\u90fd\u6ca1\u6709\u5230\u6210\u719f\u7684\u65f6\u673a\uff0c\u4e0d\u8fc7\u8fd9\u6b21\u5efa\u6e2f\u5de5\u7a0b\u80fd\u5982\u6b64\u8fc5\u901f\u7684\u4e0a\u9a6c\uff0c\u6c5f\u9759\u6db5\u8fd8\u662f\u5728\u80cc\u540e\u53d1\u6325\u5f88\u5927\u7684\u5f71\u54cd\u529b\u3002</p><p>\u3000\u3000\u6c5f\u9759\u6db5\u4ece\u4e2d\u987e\u59d4\u526f\u4e3b\u4efb\u7684\u4f4d\u5b50\u5f7b\u5e95\u9000\u4e0b\u6765\u4e4b\u540e\uff0c\u4e0d\u60f3\u60ca\u6270\u5730\u65b9\uff0c\u5c31\u7559\u5728\u5317\u4eac\u5b9a\u5c45\uff1b\u6c5f\u5bb6\u5b50\u5973\u4e5f\u6ca1\u6709\u7559\u5728\u65b0\u4ead\u7684\uff0c\u8ddf\u5730\u65b9\u4e0a\u4e5f\u6ca1\u6709\u4ec0\u4e48\u8054\u7cfb\uff0c\u5f20\u606a\u5373\u4f7f\u77e5\u9053\u6c5f\u9759\u6db5\u5728\u80cc\u540e\u4e3a\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u53d1\u6325\u91cd\u8981\u7684\u63a8\u52a8\u4f5c\u7528\uff0c\u4e5f\u6ca1\u6709\u673a\u4f1a\u62dc\u671b\u4ed6\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u961a\u552f\u6d9b\u7684\u795e\u8272\u5fc3\u60f3\u4ed6\u786e\u5b9e\u4e0d\u77e5\u9053\u6c5f\u9759\u6db5\u4f1a\u591c\u8bbf\u65b0\u4ead\uff0c\u8d70\u5230\u8fd1\u524d\u7b49\u7740\u961a\u552f\u6d9b\u66ff\u4ed6\u4ecb\u7ecd\uff0c\u8001\u4eba\u5374\u5148\u884c\u4e00\u6b65\u7b11\u671b\u7740\u5f20\u606a\uff1a\u201c\u6211\u77e5\u9053\u4f60\uff0c\u8001\u673d\u6c5f\u9759\u6db5\uff0c\u522b\u4eba\u5524\u6211\u6c5f\u8001\uff0c\u5524\u4f60\u606a\u5c11\uff0c\u5012\u662f\u8001\u5c11\u914d\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u662f\u522b\u4eba\u5728\u9a82\u6211\uff0c\u6c5f\u8001\u53eb\u6211\u5c0f\u5f20\u5c31\u597d\u4e86\u2026\u2026\u201d\u5f20\u606a\u8d76\u7d27\u63e1\u7740\u524d\u526f\u603b\u7406\u6c5f\u9759\u6db5\u5bbd\u539a\u800c\u6696\u548c\u7684\u53cc\u624b\uff0c\u4e5f\u6e05\u695a\u8001\u4eba\u4e3a\u4f55\u80fd\u5982\u6b64\u80af\u5b9a\u7684\u5c31\u8ba4\u51fa\u81ea\u5df1\u6765\uff0c\u8fd9\u8fb9\u7684\u8def\u706f\u660f\u6697\uff0c\u4ed6\u4e5f\u8d70\u5230\u8fd1\u5904\u624d\u770b\u89c1\u6c5f\u9759\u6db5\u4e0e\u633d\u4ed6\u80f3\u818a\u5c11\u5973\u7684\u8138\uff0c\u5fc3\u91cc\u5373\u4f7f\u6709\u7591\u95ee\uff0c\u8fd8\u662f\u70ed\u5207\u7684\u8bf4\u9053\uff0c\u201c\u4e00\u76f4\u60f3\u62dc\u671b\u6c5f\u8001\u60a8\uff0c\u542c\u542c\u6c5f\u8001\u60a8\u7684\u6559\u8bf2\uff0c\u53c8\u6015\u592a\u5510\u7a81\u4e86\uff0c\u6c5f\u8001\u4e0d\u4f1a\u602a\u7f6a\u6211\u4e0d\u61c2\u793c\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u5e74\u8f7b\u4eba\u6574\u5929\u5fd9\u4e8b\u4e1a\uff0c\u7406\u4f1a\u6211\u4eec\u8fd9\u4e9b\u8001\u5bb6\u4f19\u505a\u4ec0\u4e48\uff1f\u201d\u6c5f\u9759\u6db5\u968f\u548c\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u4eec\u662f\u8981\u53bb\u54ea\u91cc\uff0c\u4e0d\u4f1a\u4ecb\u610f\u6211\u8fd9\u4e2a\u8001\u5bb6\u4f19\u8ddf\u7740\u8fc7\u53bb\u770b\u4e00\u770b\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u6c5f\u8001\u5173\u5fc3\u65b0\u4ead\u7684\u5efa\u8bbe\uff0c\u6211\u4eec\u6b22\u8fce\u8fd8\u6765\u4e0d\u53ca\uff0c\u201d\u5f20\u606a\u8f6c\u8eab\u6307\u5411\u6c5f\u9759\u6db5\u521a\u624d\u770b\u7684\u65b9\u5411\uff0c\u8bf4\u9053\uff0c\u201c\u5728\u8fd9\u8fb9\u571f\u5730\u4e0a\uff0c\u8981\u5efa\u4e00\u5ea7\u73b0\u4ee3\u5316\u7684\u6e2f\u53e3\u57ce\u5e02\uff0c\u961a\u4e66\u8bb0\u4ed6\u4eec\u662f\u5efa\u8bbe\u8005\uff0c\u8fd8\u662f\u8ba9\u961a\u4e66\u8bb0\u8ddf\u6c5f\u8001\u60a8\u4ecb\u7ecd\u5427\u2026\u2026\u201d\u770b\u5f97\u6e05\u961a\u552f\u6d9b\u5bf9\u6c5f\u9759\u6db5\u7684\u7a81\u7136\u591c\u8bbf\u9887\u4e3a\u60ca\u559c\uff0c\u4ed6\u4e0d\u80fd\u592a\u55a7\u5bbe\u593a\u4e3b\u5c06\u961a\u552f\u6d9b\u6487\u5230\u4e00\u8fb9\u3002</p><p>\u3000\u3000\u90a3\u4e00\u76f4\u633d\u7740\u6c5f\u9759\u6db5\u80f3\u818a\u7684\u7ea2\u8863\u5c11\u5973\u8fd9\u65f6\u5019\u8f7b\u8f7b\u7684\u6447\u8d77\u8001\u4eba\u7684\u80f3\u818a\u6765\uff0c\u5a07\u6028\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u5c31\u5f53\u4eba\u5bb6\u4e0d\u5b58\u5728\u4f3c\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u54c8\u54c8\uff0c\u201d\u6c5f\u9759\u6db5\u723d\u6717\u7684\u7b11\u8d77\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u4eec\u5e74\u8f7b\u4eba\u8981\u8ba4\u8bc6\u4ea4\u670b\u53cb\uff0c\u8fd8\u9700\u8981\u6211\u8fd9\u4e2a\u8001\u5bb6\u4f19\u7275\u9488\u5f15\u7ebf\uff1f\u4f60\u4e0d\u662f\u4e00\u6574\u5929\u90fd\u62b1\u6028\u6211\u601d\u60f3\u8001\u571f\uff0c\u8ddf\u4e0d\u4e0a\u65f6\u4ee3\u4e86\uff0c\u8fd9\u65f6\u5019\u600e\u4e48\u5c31\u4e0d\u4f1a\u81ea\u6211\u4ecb\u7ecd\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4f4d\u662f\u2026\u2026\u201d\u5f20\u606a\u8f6c\u8138\u770b\u7740\u773c\u524d\u5e74\u7eaa\u4e0e\u4ed6\u76f8\u4eff\u7684\u4fcf\u4e3d\u5c11\u5973\uff0c\u5fc3\u60f3\u5979\u5e94\u8be5\u662f\u6c5f\u9759\u6db5\u7684\u5b59\u5973\u6216\u8005\u5916\u5b59\u5973\uff0c\u5979\u73ab\u7ea2\u8272\u98ce\u8863\u5185\u7a7f\u7740\u9e45\u9ec4\u8272\u7684\u7ed2\u7ebf\u8863\uff0c\u5986\u5bb9\u7cbe\u81f4\uff0c\u5341\u5206\u7684\u65f6\u5c1a\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u6c5f\u6e44\uff0c\u6e44\uff0c\u6c34\u8349\u6c5f\u5cb8\u4e4b\u6e44\uff0c\u56e0\u4e3a\u542c\u6211\u7238\u7238\u5634\u91cc\u6574\u5929\u5728\u5ff5\u53e8\u4f60\uff0c\u6240\u4ee5\u60f3\u6709\u673a\u4f1a\u5c31\u6765\u770b\u770b\u4f60\u662f\u4e0d\u662f\u771f\u6bd4\u522b\u4eba\u591a\u957f\u4e86\u4e00\u9897\u8111\u888b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6c5f\u5c0f\u59d0\u771f\u662f\u8bf4\u7b11\u4e86\uff0c\u6bd4\u522b\u4eba\u591a\u957f\u4e00\u9897\u8111\u888b\uff0c\u90a3\u6211\u4e0d\u662f\u6210\u602a\u80ce\u4e86\u5417\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u89c1\u773c\u524d\u8fd9\u5c11\u5973\u542b\u7b11\uff0c\u90a3\u8138\u4e0a\u7684\u610f\u601d\u65e0\u7591\u662f\u8bf4\u4ed6\u53ef\u4e0d\u5c31\u662f\u602a\u80ce\uff1f</p><p>\u3000\u3000\u5f20\u606a\u6447\u5934\u800c\u7b11\uff0c\u5fc3\u91cc\u5374\u5728\u7422\u78e8\u6c5f\u6e44\u7684\u7236\u4eb2\u662f\u8c01\uff1f\u6c5f\u9759\u6db5\u5728\u80cc\u540e\u53d1\u6325\u5f71\u54cd\u529b\u63a8\u52a8\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u987a\u5229\u8fdb\u884c\uff0c\u5f20\u606a\u5373\u4f7f\u6ca1\u6709\u673a\u4f1a\u53bb\u62dc\u671b\u4ed6\uff0c\u5e73\u65f6\u4e5f\u9887\u4e3a\u5173\u6ce8\u6c5f\u5bb6\u7684\u52a8\u5411\u3002\u6c5f\u9759\u6db5\u751f\u6709\u4e09\u7537\u4e24\u5973\uff0c\u4e00\u5973\u90fd\u5728\u9ad8\u7b49\u9662\u6821\u5de5\u4f5c\uff0c\u957f\u5b50\u4e5f\u519b\u961f\u62c5\u4efb\u9ad8\u804c\uff0c\u6b21\u5b50\u4e0e\u5e7c\u5b50\u4e0e\u4e00\u5973\u4ece\u653f\uff0c\u4e0d\u77e5\u9053\u6c5f\u9759\u6db5\u54ea\u4e2a\u513f\u5b50\u6574\u5929\u5728\u5bb6\u91cc\u5ff5\u53e8\u81ea\u5df1\uff1f</p><p>\u3000\u3000\u7ed9\u522b\u4eba\u5ff5\u53e8\u53ef\u4e0d\u89c1\u5f97\u5c31\u662f\u4e00\u4ef6\u597d\u4e8b\uff0c\u6309\u8bf4\u6c5f\u6e44\u8fd9\u53e5\u8bdd\u770b\u4f3c\u65e0\u610f\u900f\u9732\u51fa\u8bb8\u591a\u6709\u7528\u7684\u4fe1\u606f\uff0c\u6c5f\u9759\u6db5\u5374\u4e0d\u4ee5\u4e3a\u5fe4\uff0c\u5f20\u606a\u5fc3\u60f3\uff1a\u5373\u4f7f\u4e0d\u662f\u5728\u8def\u8fb9\u5076\u9047\uff0c\u6c5f\u9759\u6db5\u4e5f\u4f1a\u627e\u4e0a\u81ea\u5df1\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5e76\u4e0d\u662f\u90a3\u79cd\u4e00\u9047\u5230\u8ff7\u9898\u5c31\u8feb\u5207\u60f3\u63ed\u5f00\u8ff7\u5e95\u7684\u4eba\uff0c\u53ea\u5f53\u6ca1\u6709\u7422\u78e8\u51fa\u6c5f\u6e44\u8bdd\u91cc\u7684\u5176\u4ed6\u610f\u5473\uff0c\u6ce8\u610f\u529b\u53c8\u653e\u5230\u6b63\u9898\u4e0a\u6765\uff0c\u8ba9\u961a\u552f\u6d9b\u7ed9\u6c5f\u9759\u6db5\u4ecb\u7ecd\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u7684\u6700\u65b0\u8fdb\u5c55\uff0c\u4e5f\u7b97\u662f\u5730\u65b9\u5b98\u5458\u5411\u56fd\u5bb6\u9886\u5bfc\u4eba\u6c47\u62a5\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u961a\u552f\u6d9b\u5c06\u9676\u4e66\u827a\u3001\u90b5\u81f3\u521a\u4e24\u4eba\u4ecb\u7ecd\u7ed9\u6c5f\u9759\u6db5\u8ba4\u8bc6\uff0c\u4fbf\u4e0e\u5f20\u606a\u4ed6\u4eec\u6cbf\u7740\u8def\u7259\u5f80\u5de5\u7a0b\u6307\u6325\u90e8\u5927\u697c\u8d70\u53bb\uff0c\u8fb9\u8d70\u8fb9\u4ecb\u7ecd\u4e34\u6e2f\u65b0\u57ce\u7684\u5efa\u8bbe\u89c4\u5212\u3002</p><p>\u3000\u3000\u201c\u4e0b\u5348\u5230\u65b0\u4ead\uff0c\u5230\u897f\u4ead\u8001\u5bb6\u8d70\u4e86\u4e00\u8d9f\uff0c\u542c\u8001\u5bb6\u7684\u8bf4\u8d77\u65b0\u4ead\u8fd9\u6bb5\u65f6\u95f4\u6765\u7684\u98de\u901f\u53d1\u5c55\uff0c\u751a\u81f3\u6b23\u6170\u554a\u3002\u8001\u5bb6\u6709\u4e2a\u5c0f\u4f19\u5b50\u5728\u4e1c\u5c71\u94a2\u94c1\u5de5\u4f5c\uff0c\u542c\u4ed6\u8bf4\u4e1c\u5c71\u94a2\u94c1\u53bb\u5e74\u7ed9\u5458\u5de5\u4e24\u6b21\u8c03\u6574\u85aa\u8d44\uff0c\u8fc7\u5e74\u524d\u989d\u5916\u53d1\u653e\u516d\u5230\u5341\u516b\u4e2a\u6708\u4e0d\u7b49\u7684\u5de5\u8d44\u4f5c\u4e3a\u5e74\u7ec8\u5956\uff0c\u5e74\u6536\u5165\u4e00\u4e0b\u5b50\u6bd4\u4ee5\u524d\u63d0\u9ad8\u4e86\u4e94\u516d\u500d\uff0c\u201d\u6c5f\u9759\u6db5\u4f3c\u4e4e\u5728\u968f\u610f\u7684\u8bf4\u8d77\u56de\u8001\u5bb6\u542c\u5230\u7684\u4e00\u4ef6\u8f76\u4e8b\uff0c\u201c\u6211\u5c31\u5f88\u597d\u5947\uff1a\u9526\u6e56\u4e3a\u4ec0\u4e48\u80fd\u505a\u5230\u8fd9\u4e00\u70b9\uff0c\u5176\u4ed6\u4f01\u4e1a\u5374\u5f88\u5c11\u80fd\u505a\u5230\u8fd9\u4e00\u70b9\uff1f\u201d</p><p>\u3000\u3000\u8981\u8bf4\u8d77\u8fd9\u4e2a\uff0c\u975e\u5e38\u7684\u590d\u6742\uff0c\u4e00\u65f6\u534a\u4f1a\u4e5f\u8bf4\u4e0d\u6e05\u695a\uff0c\u60f3\u6765\u6c5f\u9759\u6db5\u4e5f\u4e0d\u662f\u771f\u6709\u8fd9\u4e2a\u8010\u5fc3\uff0c\u5f20\u606a\u4fbf\u7b80\u5355\u7684\u8bf4\u4e86\u8bf4\u4e1c\u5c71\u94a2\u94c1\u7684\u60c5\u51b5\uff1a\u201c\u9526\u6e56\u4e5d\u516b\u5e74\u5bf9\u4e1c\u5c71\u94a2\u94c1\u6ce8\u8d44\u4e00\u4ebf\u4e24\u5343\u4e07\u7f8e\u5143\u8fdb\u884c\u63a7\u80a1\uff0c\u53c8\u6295\u5165\u4e00\u4e9b\u8d44\u91d1\u8fdb\u884c\u6280\u672f\u6539\u9020\uff0c\u4ea7\u80fd\u987a\u5229\u7684\u7a81\u7834\u4e00\u767e\u4e07\u5428\uff1b\u53bb\u5e74\u5c31\u8d76\u4e0a\u597d\u65f6\u673a\uff0c\u56fd\u5185\u542f\u52a8\u5927\u89c4\u6a21\u7684\u57fa\u5efa\u6295\u8d44\uff0c\u94a2\u94c1\u5e02\u573a\u9700\u6c42\u65fa\u76db\uff0c\u94a2\u94c1\u5236\u54c1\u4ef7\u683c\u666e\u904d\u4e0a\u6da830-50\uff05\uff0c\u4e1c\u5c71\u94a2\u94c1\u5229\u6da6\u4e5f\u4e0a\u6da8\u5f88\u5feb\u2026\u2026\u6211\u4eec\u7684\u60f3\u6cd5\u5f88\u7b80\u5355\uff0c\u5229\u6da6\u4e5f\u662f\u5458\u5de5\u5171\u540c\u7684\u52aa\u529b\u521b\u9020\u7684\uff0c\u5c31\u5e94\u8be5\u8ba9\u4ed6\u4eec\u5206\u4eab\u5230\u4ed6\u4eec\u6240\u52aa\u529b\u7684\u6210\u679c\uff0c\u8fd9\u6837\u4e5f\u80fd\u6fc0\u52b1\u4ed6\u4eec\u6765\u5e74\u66f4\u52aa\u529b\u7684\u5de5\u4f5c\u3002\u201d</p><p>\u3000\u3000\u201c\u771f\u8be5\u8ba9\u6c5f\u6e44\u5979\u7238\u7238\u4e5f\u542c\u542c\u4f60\u7684\u8fd9\u756a\u8bdd\uff0c\u6c11\u8425\u4f01\u4e1a\u4e5f\u4f1a\u6709\u6d77\u4e00\u822c\u5bbd\u9614\u7684\u80f8\u6000\u554a\u2026\u2026\u201d\u6c5f\u9759\u6db5\u5728\u9759\u5bc2\u7684\u591c\u91cc\u683c\u5916\u7684\u5b8f\u4eae\u3002</p><p>\u3000\u3000\u201c\u6709\u673a\u4f1a\u81ea\u7136\u4f1a\u53bb\u62dc\u8bbf\u6c5f\u4e3b\u4efb\u3002\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u542c\u6c5f\u9759\u6db5\u7684\u8bed\u6c14\uff0c\u6c5f\u6e44\u7236\u4eb2\u867d\u7136\u6574\u5929\u5ff5\u53e8\u9526\u6e56\uff0c\u5374\u672a\u5fc5\u662f\u597d\u5370\u8c61\uff0c\u8fd9\u8981\u7b97\u4ef6\u574f\u6d88\u606f\u4e86\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u731c\u5230\u6211\u7238\u7238\u662f\u6c5f\u654f\u4e4b\uff1f\u201d\u6c5f\u6e44\u8be7\u5947\u7684\u770b\u7740\u5f20\u606a\uff0c\u5979\u53ef\u4ee5\u4e00\u76f4\u90fd\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u8bf4\u8bdd\uff0c\u201c\u8fd8\u662f\u8bf4\u4f60\u4e4b\u524d\u5c31\u77e5\u9053\u6211\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f1a\u6709\u673a\u4f1a\u89c1\u9762\u7684\u3002\u201d\u6c5f\u9759\u6db5\u6ca1\u6709\u8ba9\u5f20\u606a\u56de\u7b54\u6c5f\u6e44\u8fd9\u4e2a\u9887\u96be\u56de\u7b54\u7684\u95ee\u9898\uff0c\u6709\u4e9b\u4e8b\u60c5\u610f\u4f1a\u5c31\u53ef\u4ee5\u4e86\uff0c\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u8a00\u4f20\u3002</p><p>\u3000\u3000\u961a\u552f\u6d9b\u660e\u767d\u8fc7\u6765\u4e86\uff1a\u9676\u664b\u5378\u4efb\uff0c\u674e\u8fdc\u6e56\u7406\u6240\u5f53\u7136\u7684\u62c5\u4efb\u4e1c\u6d77\u7701\u7701\u59d4\u4e66\u8bb0\uff0c\u7701\u957f\u4e00\u804c\u5374\u8fdf\u8fdf\u60ac\u800c\u672a\u51b3\uff0c\u5404\u65b9\u4eba\u7269\u90fd\u63a2\u542c\u4e0d\u5230\u6d88\u606f\uff0c\u6ca1\u60f3\u5230\u5728\u4e24\u4f1a\u5373\u5c06\u53ec\u5f00\u7684\u524d\u5915\u624d\u900f\u9732\u8fd9\u4e48\u4e00\u70b9\u7f1d\u9699\u6765\uff0c\u539f\u6765\u662f\u6c5f\u9759\u6db5\u7684\u957f\u5b50\u3001\u7ecf\u8d38\u59d4\u6392\u540d\u7b2c\u4e00\u7684\u526f\u4e3b\u4efb\u6c5f\u654f\u4e4b\u8981\u53bb\u4e1c\u6d77\u62c5\u7701\u957f\u3002\u6c5f\u654f\u4e4b\u662f\u4e2d\u592e\u90e8\u59d4\u5185\u90e8\u662f\u4e3b\u5f20\u5f3a\u5316\u56fd\u6709\u8d44\u672c\u5730\u4f4d\u7684\u4ee3\u8868\u4eba\u7269\uff0c\u751a\u81f3\u5f88\u591a\u89c2\u70b9\u4e0e\u4ed6\u7236\u4eb2\u6c5f\u9759\u6db5\u90fd\u6709\u5f88\u591a\u7684\u4e0d\u5408\uff0c\u4e2d\u592e\u8c03\u4ed6\u62c5\u4efb\u4e1c\u6d77\u7701\u957f\uff0c\u679c\u771f\u6709\u5e73\u8861\u7684\u7528\u5fc3\u5728\u91cc\u5934\u3002\u5374\u662f\u4e0d\u77e5\u9053\u6c5f\u9759\u6db5\u5230\u65b0\u4ead\u6765\u6709\u51e0\u5206\u7279\u522b\u7684\u7528\u5fc3\u5728\u91cc\u5934\uff0c\u6216\u8bb8\u662f\u62c5\u5fe7\u6c5f\u654f\u4e4b\u5230\u4e1c\u6d77\u8d74\u4efb\u4f1a\u4e0e\u9526\u6e56\u8d77\u77db\u76fe\u63d0\u524d\u6c9f\u901a\u4e00\u4e8c\u3001\u7a0d\u4f5c\u7f13\u548c\u2014\u2014\u4e5f\u53ef\u8c13\u662f\u7528\u5fc3\u826f\u82e6\u3002</p></article>"}}}]);
