(window.webpackJsonp=window.webpackJsonp||[]).push([[1464],{1487:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e03\u8282 \u653b\u57ce",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e03\u8282 \u653b\u57ce</h2><p>\u743c\u6069\u7ad9\u5728\u94f6\u767d\u8272\u7684\u6f02\u6d6e\u789f\u4e0a\uff0c\u4ece\u7a7a\u4e2d\u770b\u7740\u8fdc\u5904\u7684\u57ce\u5821\u3002\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u5efa\u7b51\u6392\u5217\u5f88\u6709\u6280\u5de7\uff0c\u5341\u4e8c\u4e2a\u6700\u5de8\u5927\u7684\u77f3\u7b0b\u5448\u73af\u72b6\u6392\u5217\uff0c\u4e2d\u95f4\u7528\u7cbe\u91d1\u57ce\u5899\u8fde\u63a5\u7740\uff0c\u6784\u6210\u5bb6\u65cf\u7684\u5916\u56f4\u9632\u5fa1\u3002\u6bcf\u4e2a\u77f3\u7b0b\u90fd\u88ab\u5f00\u51ff\u6210\u4e2d\u7a7a\uff0c\u6709\u7a97\u6237\u3001\u77ad\u671b\u53e3\u548c\u7bad\u5b54\uff0c\u7c7b\u4f3c\u7889\u5821\uff0c\u5982\u679c\u90a3\u91cc\u9762\u5c04\u51fa\u706b\u7403\u6216\u8005\u95ea\u7535\uff0c\u743c\u6069\u4e5f\u4e1d\u6beb\u4e0d\u4f1a\u89c9\u5f97\u60ca\u8bb6\u3002\u5728\u57ce\u5934\u4e0a\uff0c\u6bcf\u9694\u4e00\u6bb5\u8ddd\u79bb\u5c31\u8e72\u8e1e\u7740\u4e00\u53ea\u72f0\u72de\u7684\u6076\u9b54\u96d5\u50cf\uff0c\u770b\u8d77\u6765\u50cf\u662f\u79c3\u9e70\u548c\u4eba\u7c7b\u7684\u6df7\u5408\u4f53\uff0c\u5b83\u6709\u53d1\u8fbe\u7684\u808c\u8089\u548c\u5de8\u5927\u7684\u7070\u8272\u7fc5\u8180\uff0c\u8fd8\u6709\u957f\u957f\u7684\u8116\u9888\uff0c\u4ee5\u53ca\u4e0a\u9762\u7684\u79c3\u9e70\u5934\u9885\u3002\u8fd9\u662f\u5f17\u6d1b\u9b54\uff0c\u6df1\u6e0a\u4e2d\u7684\u4e00\u79cd\u4f4e\u9636\u6076\u9b54\uff0c\u9634\u9669\u51f6\u6b8b\uff0c\u559c\u6b22\u5c45\u9ad8\u4e34\u4e0b\u5730\u6251\u51fb\u730e\u7269\u3002\u5b83\u4eec\u73b0\u5728\u4e00\u52a8\u4e0d\u52a8\uff0c\u4f46\u743c\u6069\u76f8\u4fe1\u5982\u679c\u57ce\u5821\u906d\u53d7\u653b\u51fb\uff0c\u5b83\u4eec\u5c31\u4f1a\u7acb\u523b\u6d3b\u8fc7\u6765\uff0c\u53c2\u52a0\u6218\u6597\u3002</p><p>\u5728\u57ce\u5899\u811a\u4e0b\uff0c\u73af\u7ed5\u7740\u4e00\u9053\u6df1\u6df1\u7684\u6c9f\u58d1\u4f5c\u4e3a\u62a4\u57ce\u6cb3\uff0c\u4f46\u91cc\u9762\u6ca1\u6709\u6c34\uff0c\u53ea\u6709\u7ffb\u817e\u4e0d\u606f\u7684\u9ed1\u8272\u70df\u96fe\uff0c\u53d8\u5e7b\u51fa\u5404\u79cd\u6076\u9b54\u7684\u5f62\u72b6\u3002\u636e\u8bf4\u5b83\u901a\u5f80\u6df1\u6e0a\uff0c\u5982\u679c\u4eba\u6389\u8fdb\u53bb\u7684\u8bdd\uff0c\u4fbf\u4f1a\u76f4\u63a5\u6210\u4e3a\u6076\u9b54\u7684\u98df\u7269\u3002\u8fd9\u79cd\u8bf4\u6cd5\u662f\u4ece\u54ea\u91cc\u6d41\u4f20\u51fa\u6765\u7684\uff0c\u5df2\u7ecf\u65e0\u53ef\u8003\u8bc1\u4e86\uff0c\u4f46\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5c45\u6c11\u90fd\u5f88\u5c0f\u5fc3\uff0c\u6ca1\u6709\u8c01\u60f3\u53bb\u4eb2\u8eab\u4f53\u9a8c\u771f\u5047\u3002</p><p>\u62a4\u57ce\u6cb3\u4e0a\u53ea\u6709\u4e00\u5ea7\u6865\uff0c\u5b83\u770b\u8d77\u6765\u662f\u7cbe\u91d1\u5236\u6210\uff0c\u575a\u56fa\u65e0\u6bd4\uff0c\u6865\u5934\u540c\u6837\u8e72\u8e1e\u7740\u4e24\u53ea\u5f17\u6d1b\u9b54\u96d5\u50cf\u3002\u6865\u540e\u9762\u662f\u4e00\u6247\u6697\u84dd\u8272\u7684\u5927\u95e8\uff0c\u5404\u79cd\u7b26\u6587\u91cd\u53e0\u4ea4\u9519\u5730\u5728\u4e0a\u9762\u6d41\u52a8\u95ea\u70c1\uff0c\u5f7c\u6b64\u95f4\u6709\u4e0d\u540c\u989c\u8272\u7684\u9b54\u6cd5\u7075\u7ebf\u8fde\u63a5\u7f20\u7ed5\u3002\u743c\u6069\u628a\u5965\u672f\u89c6\u89c9\u7684\u80fd\u529b\u53d1\u6325\u5230\u6781\u81f4\uff0c\u4f46\u4f9d\u65e7\u6ca1\u80fd\u6210\u529f\u5206\u6790\u51fa\u95e8\u4e0a\u6240\u6709\u9632\u5fa1\u9b54\u6cd5\u7684\u6784\u6210\uff0c\u81f3\u5c11\u6709\u4e00\u534a\u7684\u7b26\u6587\u4ed6\u538b\u6839\u4e0d\u8ba4\u8bc6\uff0c\u53e6\u5916\u4e00\u534a\u4e2d\u4e5f\u6709\u5f88\u591a\u5fc5\u987b\u901a\u8fc7\u731c\u6d4b\u6765\u5224\u65ad\u5176\u6548\u7528\u3002\u5deb\u5e08\u6709\u4e9b\u6cae\u4e27\uff0c\u4f46\u968f\u5373\u4e5f\u5c31\u6062\u590d\u4e86\u60c5\u7eea\uff0c\u201c\u5353\u5c14\u7684\u5965\u672f\u3002\u201d\u4ed6\u4f4e\u58f0\u81ea\u8a00\u81ea\u8bed\u3002</p><p>\u5353\u5c14\u539f\u672c\u548c\u5730\u8868\u7cbe\u7075\u662f\u540c\u65cf\uff0c\u5965\u672f\u79c9\u627f\u7684\u662f\u7cbe\u7075\u9ad8\u7b49\u9b54\u6cd5\uff0c\u540e\u6765\u53c8\u63ba\u6742\u4e86\u5f88\u591a\u4eba\u7c7b\u7684\u9b54\u6cd5\u6210\u5c31\u3002\u5728\u201c\u5927\u6c89\u964d\u201d\u4e4b\u540e\uff0c\u539f\u672c\u5c45\u4f4f\u5728\u5730\u8868\u68ee\u6797\u7684\u9ed1\u6697\u7cbe\u7075\u88ab\u9a71\u9010\u5230\u5e7d\u6697\u5730\u57df\uff0c\u73af\u5883\u7684\u53d8\u5316\u540c\u65f6\u4e5f\u5f71\u54cd\u9b54\u6cd5\u7684\u6539\u53d8\u548c\u8fdb\u6b65\u3002\u5982\u4eca\u7684\u9ed1\u6697\u7cbe\u7075\u9b54\u6cd5\u5176\u5b9e\u662f\u4e2a\u6df7\u5408\u4f53\uff0c\u9519\u7efc\u590d\u6742\uff0c\u743c\u6069\u8fd9\u79cd\u5916\u4eba\u5f88\u96be\u77e5\u6653\u5176\u4e2d\u7684\u5965\u5999\u3002</p><p>\u5728\u5916\u73af\u57ce\u5899\u4e4b\u5185\uff0c\u662f\u4e00\u6392\u6392\u77f3\u7b0b\u3001\u949f\u4e73\u77f3\uff0c\u4ee5\u53ca\u4eba\u9020\u5efa\u7b51\uff0c\u770b\u8d77\u6765\u6df7\u4e71\u9648\u5217\uff0c\u6beb\u65e0\u89c4\u5219\u53ef\u8a00\uff0c\u4f46\u4ed4\u7ec6\u770b\u7684\u8bdd\u5c31\u4f1a\u53d1\u73b0\u5b83\u4eec\u5b8c\u7f8e\u5730\u6784\u6210\u4e86\u4e00\u4e2a\u4e5d\u8292\u661f\u5f62\u3002\u743c\u6069\u95ed\u4e0a\u773c\u775b\uff0c\u5728\u9ed8\u6570\u4e86\u4e09\u79d2\u949f\u4e4b\u540e\u518d\u6b21\u7741\u5f00\uff0c\u7269\u8d28\u7684\u4e16\u754c\u77ac\u95f4\u4ece\u4ed6\u89c6\u7ebf\u4e2d\u6d88\u5931\u4e86\uff0c\u773c\u524d\u53ea\u5269\u4e0b\u4e00\u7247\u7531\u65e0\u6570\u9053\u900f\u660e\u7075\u7ebf\u7f16\u7ec7\u6210\u7684\u7acb\u4f53\u7f51\u7edc\u3002\u5404\u79cd\u7c7b\u578b\u7684\u9b54\u6cd5\u80fd\u91cf\u5728\u7075\u7ebf\u4e2d\u5954\u6d8c\u4e0d\u606f\uff0c\u4eff\u4f5b\u8840\u7ba1\u4e2d\u7684\u8840\u6db2\u3002\u5b83\u4eec\u76d8\u65cb\u7f20\u7ed5\uff0c\u5bc6\u5bc6\u531d\u531d\uff0c\u51dd\u6210\u4e00\u4e2a\u53c8\u4e00\u4e2a\u7ec6\u5c0f\u7684\u7b26\u6587\u3002\u800c\u8fd9\u4e9b\u7b26\u6587\u53c8\u6309\u7167\u4e00\u5b9a\u7684\u89c4\u5f8b\u6392\u5217\u7740\uff0c\u5f62\u6210\u6570\u4ee5\u767e\u8ba1\u7684\u6b21\u7ea7\u7ed3\u754c\uff0c\u63a5\u7740\u8fdb\u4e00\u6b65\u878d\u5408\u6210\u4e5d\u4e2a\u66f4\u5927\u7684\u4e3b\u63a7\u7ed3\u754c\u2014\u2014\u6700\u540e\u8fd9\u4e9b\u4e3b\u63a7\u7ed3\u754c\u4f5c\u4e3a\u201c\u7ed3\u70b9\u201d\uff0c\u5c06\u6240\u6709\u7684\u9b54\u6cd5\u7075\u7ebf\u8054\u63a5\u8d77\u6765\uff0c\u878d\u5408\u4e3a\u4e00\u4f53\uff0c\u6784\u6210\u4e86\u4e00\u4e2a\u5e9e\u5927\u7684\uff0c\u7b3c\u7f69\u6574\u4e2a\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u9b54\u6cd5\u9635\u3002</p><p>\u5deb\u5e08\u5fae\u5fae\u5012\u5438\u4e86\u4e00\u53e3\u51b7\u6c14\u3002</p><p>\u7b26\u6587\u3001\u7ed3\u754c\u548c\u9b54\u6cd5\u9635\uff0c\u8fd9\u662f\u5492\u6cd5\u5b66\u6d3e\u7684\u4e13\u4e1a\u8303\u56f4\uff0c\u743c\u6069\u4e0d\u662f\u5f88\u7cbe\u901a\uff0c\u4f46\u4e5f\u4e0d\u7b97\u964c\u751f\u3002\u9b54\u6cd5\u9635\u7684\u6700\u9ad8\u7ea7\u5f62\u6001\u5c31\u662f\u8ff7\u9501\uff0c\u4ed6\u4e5f\u89c1\u8fc7\u597d\u51e0\u6b21\u4e86\u3002\u773c\u524d\u7684\u8fd9\u4e2a\u9b54\u6cd5\u9635\uff0c\u901a\u8fc7\u91cd\u91cd\u53e0\u53e0\u7684\u7b26\u6587\u548c\u7ed3\u754c\u7cbe\u81f4\u65e0\u6687\u5730\u5d4c\u5408\u5728\u4e00\u8d77\uff0c\u5f7c\u6b64\u5173\u8054\uff0c\u4e92\u4e3a\u8f85\u52a9\uff0c\u5728\u9b54\u6cd5\u6280\u5de7\u4e0a\u5df2\u7ecf\u8fbe\u5230\u4e86\u532a\u5937\u6240\u601d\u7684\u5883\u754c\u3002\u5982\u679c\u4e0d\u662f\u5f53\u4e2d\u8fd8\u5c11\u4e86\u6700\u5173\u952e\u7684\u201c\u6838\u5fc3\u201d\uff0c\u7b80\u76f4\u5c31\u53ef\u4ee5\u770b\u4f5c\u662f\u4e00\u4e2a\u5f31\u5316\u7248\u672c\u7684\u8ff7\u9501\u4e86\u3002</p><p>\u5728\u6765\u4e4b\u524d\uff0c\u743c\u6069\u8fd8\u542c\u7ef4\u5eb7\u5c3c\u4e9a\u63d0\u8fc7\uff0c\u745e\u8d39\u5fb7\u5bb6\u65cf\u62e5\u6709\u6574\u4e2a\u57ce\u5e02\u4e2d\u6700\u5b8c\u5584\u6700\u5f3a\u5927\u7684\u9b54\u6cd5\u9632\u5fa1\u4f53\u7cfb\u3002\u5f53\u65f6\u4ed6\u8fd8\u4e0d\u592a\u5728\u610f\uff0c\u5982\u4eca\u4eb2\u773c\u6240\u89c1\u624d\u77e5\u9053\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u5deb\u5e08\u4eec\uff0c\u4e00\u5b9a\u4e3a\u6b64\u52aa\u529b\u4e86\u51e0\u5341\u4e2a\u4e16\u7eaa\u3002</p><p>\u4e3a\u4e86\u907f\u514d\u5e72\u6270\uff0c\u66f4\u597d\u5730\u770b\u6e05\u695a\u5e03\u5c40\uff0c\u743c\u6069\u5728\u610f\u8bc6\u4e2d\u5c06\u90a3\u4e9b\u7ec6\u5fae\u7eb7\u4e71\u7684\u7b26\u6587\u548c\u6b21\u7ea7\u7ed3\u754c\u6682\u65f6\u62b9\u53bb\uff0c\u51dd\u89c6\u7740\u6700\u540e\u6784\u6210\u9b54\u6cd5\u9635\u7684\u4e5d\u5927\u4e3b\u63a7\u7ed3\u754c\u3002\u6709\u7684\u7ed3\u754c\u5448\u8000\u773c\u7684\u706b\u7ea2\u8272\uff0c\u610f\u5473\u7740\u5b83\u5177\u6709\u5f3a\u70c8\u7684\u6740\u4f24\u6027\uff0c\u6709\u53ef\u80fd\u4f1a\u91ca\u653e\u51fa\u5a01\u529b\u5de8\u5927\u7684\u706b\u7403\u3001\u95ea\u7535\u6216\u8005\u529b\u573a\u9b54\u6cd5\uff1b\u6709\u7684\u7ed3\u754c\u5448\u6de1\u84dd\u8272\uff0c\u5b83\u5e94\u8be5\u662f\u7528\u6765\u62b5\u5fa1\u653b\u51fb\u6cd5\u672f\uff0c\u963b\u9694\u5916\u6765\u7684\u9884\u8a00\u4fa6\u6d4b\uff0c\u751a\u81f3\u5c01\u9501\u6b21\u5143\u901a\u9053\uff0c\u8ba9\u5916\u4eba\u65e0\u6cd5\u76f4\u63a5\u4f20\u9001\u8fdb\u57ce\u5821\uff1b\u6697\u91d1\u8272\u548c\u7c89\u7ea2\u8272\u7684\u7ed3\u754c\u4f1a\u5f71\u54cd\u654c\u4eba\u7684\u5fc3\u667a\u60c5\u7eea\uff0c\u6216\u8005\u8ba9\u4eba\u9677\u5165\u5c42\u51fa\u4e0d\u7a77\u7684\u5e7b\u8c61\u8ff7\u5bab\u5f53\u4e2d\uff0c\u7eff\u8272\u7684\u7ed3\u754c\u4e2d\u5219\u57cb\u4f0f\u7740\u4e00\u51fb\u5fc5\u6740\u7684\u81f4\u547d\u624b\u6bb5\u3002\u8f83\u5185\u4fa7\u7684\u56db\u4e2a\u7ed3\u754c\u5219\u90fd\u662f\u4e73\u767d\u8272\uff0c\u6df7\u5408\u7740\u7070\u6697\u7684\u9634\u5f71\u5728\u5176\u4e2d\u6d41\u52a8\uff0c\u6beb\u65e0\u7591\u95ee\uff0c\u8fd9\u662f\u8054\u7ed3\u7269\u8d28\u754c\u548c\u5f02\u4f4d\u9762\u7684\u901a\u9053\uff0c\u5982\u679c\u9700\u8981\u7684\u8bdd\uff0c\u745e\u8d39\u5fb7\u5bb6\u7684\u5deb\u5e08\u5c31\u53ef\u4ee5\u901a\u8fc7\u5b83\u4eec\u53ec\u5524\u51fa\u6765\u81ea\u4e0b\u5c42\u754c\u7684\u6076\u9b54\u505a\u5e2e\u624b\u3002</p><p>\u201c\u771f\u4e86\u4e0d\u8d77\u3002\u201d\u743c\u6069\u611f\u53f9\u7740\u3002</p><p>\u201c\u745e\u8d39\u5fb7\u5bb6\u65cf\u80fd\u591f\u5c79\u7acb\u4e0d\u5012\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u8981\u5f52\u529f\u4e8e\u4ed6\u4eec\u7684\u9632\u5fa1\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u5728\u8eab\u65c1\u8bf4\uff0c\u5979\u4e5f\u540c\u6837\u7ad9\u5728\u6f02\u6d6e\u789f\u4e0a\uff0c\u201c\u5373\u4f7f\u662f\u5b83\u6700\u8870\u5f31\u7684\u65f6\u5019\uff0c\u4e5f\u5f88\u5c11\u6709\u4eba\u6562\u8d38\u7136\u53d1\u8d77\u8fdb\u653b\u3002\u201d</p><p>\u5deb\u5e08\u70b9\u5934\uff0c\u201c\u8fd9\u9b54\u6cd5\u9635\u8fd1\u4e4e\u5b8c\u7f8e\u3002\u201d</p><p>\u201c\u8fd1\u4e4e\u5b8c\u7f8e\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u6311\u4e86\u6311\u7709\u6bdb\uff0c\u201c\u4f60\u7684\u610f\u601d\u662f\u5b83\u4ecd\u65e7\u6709\u7f3a\u9677\uff1f\u201d</p><p>\u201c\u5b83\u6700\u5927\u7684\u7f3a\u9677\uff0c\u5c31\u662f\u7ec8\u7a76\u8fd8\u4e0d\u662f\u8ff7\u9501\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5b83\u7f3a\u5c11\u4e00\u4e2a\u6838\u5fc3\uff0c\u6574\u4e2a\u9b54\u6cd5\u9635\u867d\u7136\u5e03\u5c40\u5de7\u5999\uff0c\u73af\u73af\u76f8\u6263\uff0c\u4f46\u7ec8\u7a76\u8fd8\u662f\u6ca1\u6709\u81ea\u6211\u4fee\u590d\u7684\u80fd\u529b\u3002\u201d</p><p>\u8ff7\u9501\u7684\u5f3a\u5927\uff0c\u4e0d\u4ec5\u4ec5\u5728\u4e8e\u76f4\u63a5\u8868\u73b0\u51fa\u6765\u7684\u529b\u91cf\uff0c\u4e5f\u5728\u4e8e\u5b83\u7684\u81ea\u6211\u4fee\u590d\u80fd\u529b\u3002\u53ea\u8981\u6838\u5fc3\u4e0d\u6bc1\uff0c\u8ff7\u9501\u5c31\u4f1a\u4e00\u76f4\u6709\u6548\u3002\u745e\u8d39\u5fb7\u5bb6\u65cf\u867d\u7136\u5deb\u5e08\u8f88\u51fa\uff0c\u4f46\u663e\u7136\u8fd8\u4e0d\u8db3\u4ee5\u6784\u9020\u8ff7\u9501\uff0c\u8fd9\u5957\u9b54\u6cd5\u9635\u5c31\u7b97\u518d\u5de7\u5999\u518d\u7cbe\u81f4\uff0c\u53ea\u8981\u613f\u610f\u4ed8\u51fa\u8db3\u591f\u7684\u4ee3\u4ef7\uff0c\u820d\u5f03\u8db3\u591f\u591a\u7684\u5175\u529b\uff0c\u4f9d\u7136\u662f\u80fd\u591f\u5c06\u5b83\u653b\u7834\u7684\u3002\u7b80\u5355\u6765\u8bf4\uff0c\u5c31\u662f\u7528\u5927\u91cf\u7684\u70ae\u7070\u4e0a\u53bb\u586b\uff0c\u7ec8\u7a76\u6709\u51b2\u57ae\u7684\u65f6\u5019\u3002</p><p>\u8fd9\u5c31\u662f\u6240\u8c13\u7684\u4ee5\u529b\u7834\u5de7\u4e86\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u660e\u767d\u4e86\u743c\u6069\u7684\u610f\u601d\u3002\u201c\u53ef\u60dc\u6211\u4eec\u73b0\u5728\u6ca1\u6709\u591a\u5c11\u5974\u96b6\u4e86\uff0c\u201d\u5979\u53f9\u606f\uff0c\u201c\u7edd\u5927\u90e8\u5206\u90fd\u5728\u53db\u4e71\u4e2d\u6b7b\u53bb\u4e86\u3002\u201d</p><p>\u201c\u603b\u8fd8\u6709\u4e00\u4e9b\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u6709\u4e9b\u5974\u96b6\u4e0d\u66fe\u53c2\u4e0e\u53db\u4e71\u3002\u201d</p><p>\u201c\u592a\u5c11\u4e86\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u6447\u5934\u3002</p><p>\u5974\u96b6\u6570\u91cf\u65e2\u7136\u4e0d\u8db3\uff0c\u90a3\u5c31\u53ea\u80fd\u7528\u5353\u5c14\u4e0a\u53bb\u586b\u4e86\u2026\u2026\u4f46\u8fd9\u663e\u7136\u662f\u4e0d\u53ef\u80fd\u88ab\u91c7\u7eb3\u7684\u8ba1\u5212\u3002\u5353\u5c14\u662f\u9ad8\u8d35\u7684\uff0c\u5373\u4f7f\u662f\u7537\u6027\u7684\u751f\u547d\u90fd\u6bd4\u5974\u96b6\u73cd\u8d35\u767e\u500d\uff0c\u8fd9\u79cd\u7eaf\u7cb9\u7684\u70ae\u7070\u6d88\u8017\u4efb\u52a1\uff0c\u4e0d\u80fd\u7528\u5353\u5c14\u53bb\u627f\u62c5\uff0c\u6ca1\u6709\u54ea\u4e2a\u5bb6\u65cf\u80fd\u591f\u5962\u4f88\u5230\u8fd9\u6837\u6d6a\u8d39\u7684\u7a0b\u5ea6\uff0c\u5373\u4f7f\u662f\u83f2\u5c14\u4f26\u4e5f\u4e0d\u884c\u3002</p><p>\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u53ea\u80fd\u53e6\u60f3\u4e3b\u610f\u3002</p><p>\u743c\u6069\u56de\u5934\u671d\u5730\u9762\u770b\u53bb\uff0c\u4e09\u5927\u5bb6\u65cf\u7684\u58eb\u5175\u5df2\u7ecf\u96c6\u7ed3\u5b8c\u6bd5\uff0c\u51c6\u5907\u53d1\u52a8\u653b\u51fb\u3002\u8fd9\u79cd\u5bb6\u65cf\u5185\u6218\uff0c\u653b\u51fb\u4e00\u65b9\u7684\u4e3b\u6bcd\u901a\u5e38\u4e0d\u4f1a\u4eb2\u81ea\u51fa\u52a8\uff0c\u800c\u662f\u5750\u9547\u540e\u65b9\uff0c\u4e5f\u662f\u4e3a\u4e86\u9632\u5907\u88ab\u5176\u4ed6\u5bb6\u65cf\u4e58\u673a\u5077\u88ad\uff0c\u83ab\u5c3c\u5361\u59d0\u59b9\u4e5f\u88ab\u7559\u5728\u5bb6\u4e2d\uff0c\u867d\u7136\u8299\u857e\u72c4\u5f3a\u70c8\u8981\u6c42\u968f\u540c\uff0c\u4f46\u4f9d\u65e7\u88ab\u743c\u6069\u62d2\u7edd\u4e86\u3002\u83f2\u5c14\u4f26\u5bb6\u65cf\u5e26\u961f\u7684\u662f\u957f\u5973\u8fe6\u6cd5\uff0c\u9996\u5e2d\u5deb\u5e08\u4e9a\u5f53\u65af\u4f5c\u4e3a\u5979\u7684\u53c2\u8c0b\u548c\u52a9\u624b\u3002\u6b64\u65f6\u4ed6\u4eec\u6b63\u5728\u4e0b\u65b9\u6307\u6325\u58eb\u5175\u53d8\u6362\u9635\u578b\uff0c\u4e0e\u743c\u6069\u8fd9\u79cd\u65b0\u6210\u5458\u4e0d\u540c\uff0c\u8fe6\u6cd5\u548c\u4e9a\u5f53\u65af\u5bf9\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u9b54\u6cd5\u9632\u5fa1\u4e86\u7136\u4e8e\u5fc3\uff0c\u5b8c\u5168\u4e0d\u7528\u518d\u4e34\u9635\u89c2\u5bdf\u3002</p><p>\u201c\u6211\u4eec\u66fe\u7ecf\u51e0\u6b21\u63a8\u6f14\u8fc7\u5982\u4f55\u8fdb\u653b\u745e\u8d39\u5fb7\u5bb6\u65cf\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u4ed6\u4eec\u7684\u9b54\u6cd5\u9635\u662f\u6700\u5927\u96be\u9898\u3002\u201d</p><p>\u743c\u6069\u70b9\u5934\u8868\u793a\u8d5e\u540c\uff0c\u4ed6\u77e5\u9053\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u5b9e\u529b\uff0c\u7267\u5e08\u6570\u91cf\u8d85\u8fc7\u745e\u8d39\u5fb7\u5bb6\u65cf\u4e24\u500d\uff0c\u5176\u4e2d\u4e09\u5206\u4e4b\u4e00\u662f\u9ad8\u9636\u796d\u53f8\uff1b\u6218\u58eb\u5219\u63a5\u8fd1\u745e\u8d39\u5fb7\u5bb6\u7684\u4e09\u500d\uff0c\u800c\u4e14\u5168\u90fd\u7ecf\u8fc7\u6700\u4e25\u683c\u7684\u8bad\u7ec3\uff0c\u5bb6\u65cf\u6b66\u6280\u957f\u82ae\u68ee\u7279\u66fe\u7ecf\u5728\u683c\u6597\u5b66\u9662\u62c5\u4efb\u8fc7\u4e03\u5341\u5e74\u7684\u6559\u5b98\uff0c\u88ab\u8ba4\u4e3a\u662f\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\u6700\u9ad8\u660e\u7684\u5251\u624b\u3002\u552f\u4e00\u8fdc\u8fdc\u4e0d\u5982\u4e4b\u5904\u5c31\u662f\u5deb\u5e08\u3002\u800c\u5bf9\u4ed8\u9b54\u6cd5\u9635\uff0c\u7267\u5e08\u53c8\u5e2e\u4e0d\u4e0a\u592a\u591a\u5fd9\u3002</p><p>\u82e5\u975e\u5982\u6b64\uff0c\u745e\u8d39\u5fb7\u5bb6\u65cf\u4e5f\u65e9\u5c31\u5df2\u7ecf\u88ab\u6d88\u706d\u4e86\u5427\u3002</p><p>\u4ed6\u4eec\u53c8\u7ee7\u7eed\u89c2\u5bdf\u4e86\u4e00\u4f1a\uff0c\u964d\u4e0b\u6f02\u6d6e\u789f\uff0c\u56de\u5230\u5730\u9762\uff0c\u6b66\u6280\u957f\u82ae\u68ee\u7279\u6b63\u5728\u5927\u58f0\u547d\u4ee4\u58eb\u5175\u4eec\u5360\u636e\u6709\u5229\u5730\u5f62\u3002\u6b64\u6b21\u653b\u51fb\uff0c\u8fe6\u6cd5\u5e26\u6765\u4e86\u4e8c\u5341\u540d\u7267\u5e08\u548c\u4e24\u5343\u540d\u5bb6\u65cf\u6218\u58eb\uff0c\u4ee5\u53ca\u4e09\u767e\u540d\u5974\u96b6\uff0c\u5305\u62ec\u4e94\u5341\u540d\u725b\u5934\u602a\u3001\u4e03\u5341\u540d\u98df\u4eba\u9b54\u548c\u4e00\u767e\u516b\u5341\u540d\u8c7a\u72fc\u4eba\u5f13\u7bad\u624b\uff0c\u6700\u540e\u8fd8\u6709\u5168\u90e8\u7684\u4e94\u540d\u5deb\u5e08\uff0c\u8fd9\u51e0\u4e4e\u5df2\u7ecf\u662f\u83f2\u5c14\u4f26\u5bb6\u65cf\u4e09\u5206\u4e4b\u4e8c\u7684\u5b9e\u529b\u4e86\u3002\u53e6\u5916\u4e24\u4e2a\u5bb6\u65cf\u4e5f\u6d3e\u51fa\u4e86\u63a5\u8fd1\u4e00\u534a\u7684\u5175\u529b\uff0c\u4ece\u6570\u91cf\u4e0a\u8bf4\u5df2\u7ecf\u8fbe\u5230\u4e86\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u4e09\u500d\u3002</p><p>\u8fd9\u6837\u96c4\u539a\u7684\u5175\u529b\uff0c\u5176\u5b9e\u786c\u51b2\u7684\u8bdd\uff0c\u6253\u8d62\u8fd9\u4e00\u4ed7\u662f\u80af\u5b9a\u6ca1\u95ee\u9898\u7684\u3002\u4f46\u90a3\u6837\u5c31\u635f\u5931\u592a\u5927\u4e86\uff0c\u54ea\u4e2a\u5bb6\u65cf\u90fd\u627f\u53d7\u4e0d\u8d77\uff0c\u5230\u65f6\u5019\u88ab\u5176\u4ed6\u5bb6\u65cf\u6e14\u7fc1\u5f97\u5229\uff0c\u90a3\u5c31\u6210\u7b11\u8bdd\u4e86\u3002</p><p>\u8fe6\u6cd5\u6b63\u5728\u4e3a\u6b64\u4e8b\u72af\u96be\u3002</p><p>\u4e00\u4e2a\u7a7f\u7740\u91cd\u7532\u7684\u77ee\u4eba\u8dd1\u8fc7\u6765\uff0c\u4ed6\u663e\u7136\u6765\u81ea\u7c73\u5179\u745e\u56fe\u5c14\u5bb6\u65cf\uff0c\u6574\u4e2a\u57ce\u5e02\u91cc\u53ea\u6709\u7b2c\u4e09\u5bb6\u65cf\u70ed\u7231\u9a71\u4f7f\u77ee\u4eba\u505a\u5974\u96b6\u3002\u201c\u4e00\u5207\u5df2\u7ecf\u51c6\u5907\u5c31\u7eea\uff01\u201d\u4ed6\u7528\u4e0d\u719f\u7ec3\u7684\u5353\u5c14\u8bed\u5927\u543c\uff0c\u201c\u6211\u4eec\u4f55\u65f6\u53d1\u52a8\u653b\u51fb\uff1f\u201d</p><p>\u8fe6\u6cd5\u4e00\u811a\u8e22\u4e2d\u4ed6\u7684\u8138\uff0c\u8ba9\u77ee\u4eba\u5728\u5730\u4e0a\u6eda\u4e86\u51e0\u4e2a\u5708\u3002</p><p>\u201c\u6ce8\u610f\u4f60\u7684\u8bed\u6c14\uff01\u201d\u83f2\u5c14\u4f26\u5bb6\u7684\u957f\u5973\u8b66\u544a\u7740\uff0c\u6216\u8bb8\u662f\u770b\u5728\u76df\u53cb\u7684\u4efd\u4e0a\uff0c\u5979\u6ca1\u6709\u8fdb\u4e00\u6b65\u52a8\u624b\uff0c\u201c\u56de\u53bb\u544a\u8bc9\u7c73\u5179\u745e\u56fe\u5c14\u6559\u5b98\uff0c\u6211\u4eec\u9a6c\u4e0a\u5c31\u8981\u5f00\u59cb\u8fdb\u653b\u3002\u201d</p><p>\u77ee\u4eba\u5f88\u60f3\u95ee\u201c\u9a6c\u4e0a\u201d\u5177\u4f53\u662f\u6307\u4ec0\u4e48\u65f6\u5019\uff0c\u6bd5\u7adf\u5bf9\u4e8e\u5353\u5c14\u8fd9\u79cd\u957f\u5bff\u7684\u751f\u7269\u6765\u8bf4\uff0c\u65f6\u95f4\u6982\u5ff5\u5f80\u5f80\u4e0d\u662f\u90a3\u4e48\u7cbe\u786e\u7684\u3002\u4f46\u4ed6\u660e\u667a\u5730\u7ba1\u4f4f\u4e86\u81ea\u5df1\u7684\u820c\u5934\uff0c\u6ca1\u6709\u518d\u591a\u95ee\uff0c\u800c\u662f\u4ece\u5730\u4e0a\u722c\u8d77\u6765\uff0c\u8eac\u8eab\u884c\u793c\uff0c\u7136\u540e\u8dd1\u5f00\u3002</p><p>\u201c\u4f60\u600e\u4e48\u770b\uff0c\u9996\u5e2d\u5deb\u5e08\u3002\u201d\u8fe6\u6cd5\u5c06\u76ee\u5149\u8f6c\u79fb\u5230\u65c1\u8fb9\u7684\u4e9a\u5f53\u65af\u8eab\u4e0a\uff0c\u6bd5\u7adf\u5728\u9b54\u6cd5\u9886\u57df\uff0c\u4ed6\u624d\u662f\u6743\u5a01\u3002</p><p>\u4e9a\u5f53\u65af\u6c89\u541f\u4e0d\u7b54\u3002</p><p>\u201c\u9010\u5c42\u63a8\u8fdb\uff0c\u7528\u5deb\u5e08\u4e00\u70b9\u70b9\u53cd\u5236\u6cd5\u672f\u3002\u201d\u743c\u6069\u5728\u65c1\u8fb9\u5efa\u8bae\u3002</p><p>\u9b54\u6cd5\u867d\u7136\u5728\u666e\u901a\u4eba\u773c\u4e2d\u770b\u6765\u7384\u5999\u83ab\u6d4b\uff0c\u5176\u5b9e\u4e5f\u4e0d\u8fc7\u5c31\u662f\u4e00\u95e8\u590d\u6742\u7684\u6280\u827a\u3002\u65e2\u7136\u662f\u6280\u827a\uff0c\u81ea\u7136\u5c31\u6709\u5176\u95e8\u9053\u8bc0\u7a8d\u3002\u7ecf\u9a8c\u4e30\u5bcc\u7684\u5deb\u5e08\u53ef\u4ee5\u5206\u6790\u51fa\u6cd5\u672f\u7684\u6027\u8d28\u3001\u6784\u6210\u548c\u80fd\u91cf\u8109\u7edc\uff0c\u4ece\u800c\u60f3\u529e\u6cd5\u987a\u52bf\u5f15\u5bfc\u6216\u8005\u9006\u5411\u6d88\u89e3\uff0c\u751a\u81f3\u76f4\u63a5\u53cd\u5f39\uff0c\u8fd9\u5c31\u662f\u6240\u8c13\u7684\u53cd\u5236\u6cd5\u672f\u4e86\u3002\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u8fd9\u4e2a\u9b54\u6cd5\u9635\u592a\u8fc7\u7cbe\u5de7\uff0c\u60f3\u4e00\u4e3e\u653b\u7834\u4e0d\u73b0\u5b9e\uff0c\u743c\u6069\u6253\u7684\u4e3b\u610f\u662f\u9010\u6b65\u63a8\u8fdb\uff0c\u7f13\u6162\u8695\u98df\uff0c\u8fd9\u4e5f\u662f\u5175\u6cd5\u6b63\u9053\u3002\u4f46\u8fd9\u79cd\u4e8b\u60c5\uff0c\u6218\u58eb\u548c\u7267\u5e08\u5c31\u5e2e\u4e0d\u4e0a\u592a\u591a\u5fd9\u4e86\uff0c\u4e3b\u8981\u770b\u5deb\u5e08\u3002</p><p>\u201c\u53ef\u662f\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u72b9\u8c6b\uff0c\u201c\u8fd9\u4e2a\u9b54\u6cd5\u9635\u7684\u5185\u90e8\u8054\u7ed3\u592a\u7d27\u5bc6\u4e86\uff0c\u653b\u51fb\u4efb\u4f55\u4e00\u70b9\u90fd\u4f1a\u906d\u5230\u731b\u70c8\u7684\u53cd\u6251\u3002\u201d</p><p>\u201c\u6240\u4ee5\u6211\u4eec\u8981\u540c\u65f6\u53d1\u8d77\u591a\u5904\u653b\u51fb\uff0c\u9b54\u6cd5\u9635\u6709\u4e5d\u4e2a\u7ed3\u70b9\uff0c\u6211\u4eec\u81f3\u5c11\u8981\u653b\u51fb\u5176\u4e2d\u4e94\u4e2a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5e78\u597d\u6211\u4eec\u6709\u8db3\u591f\u7684\u5deb\u5e08\u3002\u201d</p><p>\u73b0\u5728\u9a7b\u624e\u5728\u745e\u8d39\u5fb7\u57ce\u5821\u5916\u9762\u7684\u6709\u4e09\u4e2a\u5bb6\u65cf\uff0c\u6240\u6709\u7684\u5deb\u5e08\u90fd\u5df2\u7ecf\u5728\u573a\uff0c\u4e00\u5171\u5341\u4e00\u540d\u3002\u5176\u4e2d\u83f2\u5c14\u4f26\u5bb6\u65cf\u6709\u4e03\u540d\uff08\u539f\u672c\u4e94\u540d\uff0c\u65b0\u589e\u52a0\u4e86\u743c\u6069\uff0c\u518d\u628a\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u7b97\u4e0a\uff09\uff0c\u53e6\u5916\u4e24\u4e2a\u5bb6\u65cf\u6709\u56db\u540d\u3002\u80fd\u591f\u5728\u8fd9\u79cd\u6392\u540d\u524d\u5341\u7684\u5927\u5bb6\u65cf\u91cc\u62c5\u4efb\u5deb\u5e08\uff0c\u6c34\u51c6\u90fd\u4e0d\u4f1a\u5dee\u5230\u54ea\u91cc\u53bb\uff0c\u5012\u4e5f\u52c9\u5f3a\u591f\u7528\u4e86\u3002</p><p>\u8fe6\u6cd5\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u8fd9\u4e2a\u65b9\u6cd5\u592a\u611a\u8822\u4e86\uff0c\u201d\u5979\u4e0d\u5c51\u5730\u8bf4\uff0c\u201c\u745e\u8d39\u5fb7\u5bb6\u65cf\u6709\u5341\u4e5d\u540d\u5deb\u5e08\uff0c\u96be\u9053\u4ed6\u4eec\u4f1a\u5750\u4ee5\u5f85\u6bd9\uff1f\u6211\u4eec\u80fd\u53cd\u5236\u6cd5\u672f\uff0c\u4ed6\u4eec\u4e5f\u80fd\u7ee7\u7eed\u8865\u5145\u3002\u201d</p><p>\u201c\u6211\u4eec\u8fd8\u6709\u7267\u5e08\u548c\u6218\u58eb\uff0c\u201d\u743c\u6069\u53cd\u9a73\uff0c\u201c\u4ed6\u4eec\u4eba\u6570\u5c11\uff0c\u5deb\u5e08\u5fc5\u987b\u53c2\u4e0e\u4f5c\u6218\uff0c\u6ca1\u6709\u4f59\u529b\u53bb\u4fee\u590d\u9b54\u6cd5\u9635\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u65b9\u6cd5\u5728\u7406\u8bba\u4e0a\u53ef\u884c\u3002\u201d\u4e9a\u5f53\u65af\u8bf4\uff0c\u4f46\u4ece\u8fd9\u53e5\u8bdd\u91cc\u542c\u4e0d\u51fa\u4ed6\u7684\u610f\u601d\u662f\u652f\u6301\u8fd8\u662f\u53cd\u5bf9\u3002</p><p>\u8fe6\u6cd5\u770b\u4e86\u770b\u4e9a\u5f53\u65af\uff0c\u201c\u9700\u8981\u591a\u5c11\u65f6\u95f4\uff1f\u201d\u5979\u95ee\u743c\u6069\u3002</p><p>\u8fd9\u4e2a\u95ee\u9898\u5f88\u96be\u56de\u7b54\uff0c\u5f97\u5177\u4f53\u770b\u9b54\u6cd5\u9635\u7684\u590d\u6742\u7a0b\u5ea6\uff0c\u4ee5\u53ca\u653b\u51fb\u65b9\u5deb\u5e08\u7684\u80fd\u529b\u9ad8\u4f4e\uff0c\u662f\u5426\u64c5\u957f\u8fd9\u4e00\u9886\u57df\uff0c\u8fd8\u5f97\u8003\u8651\u5404\u79cd\u53ef\u80fd\u7684\u610f\u5916\u7a81\u53d1\u60c5\u51b5\u7684\u5e72\u6270\u3002\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u201c\u8fd9\u8bf4\u4e0d\u597d\uff0c\u201d\u4ed6\u627f\u8ba4\uff0c\u201c\u53ef\u80fd\u9700\u8981\u51e0\u4e2a\u65f6\u8fb0\uff0c\u4e5f\u53ef\u80fd\u662f\u5341\u51e0\u4e2a\u751a\u81f3\u51e0\u5341\u4e2a\u65f6\u8fb0\u2026\u2026\u751a\u81f3\u66f4\u4e45\u3002\u201d</p><p>\u201c\u592a\u6162\u4e86\uff0c\u6211\u4eec\u6ca1\u6709\u8fd9\u4e2a\u65f6\u95f4\u3002\u201d</p><p>\u201c\u6211\u4eec\u662f\u653b\u51fb\u7684\u4e00\u65b9\uff0c\u201d\u743c\u6069\u63d0\u9192\uff0c\u201c\u4e3b\u52a8\u6743\u662f\u5728\u6211\u4eec\u624b\u4e2d\uff0c\u4ed6\u4eec\u53ea\u80fd\u88ab\u52a8\u6328\u6253\uff0c\u65f6\u95f4\u4e0d\u662f\u95ee\u9898\u3002\u201d</p><p>\u8fe6\u6cd5\u5927\u7b11\u8d77\u6765\u3002</p><p>\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u663e\u7136\u8981\u6210\u4e3a\u4e00\u4e2a\u5408\u683c\u7684\u83f2\u5c14\u4f26\u5bb6\u65cf\u5deb\u5e08\uff0c\u4f60\u8fd8\u6709\u5f88\u591a\u5730\u65b9\u9700\u8981\u5b66\u4e60\uff0c\u201d\u5979\u8bbd\u523a\u5730\u8bf4\uff0c\u201c\u6216\u8005\uff0c\u6211\u7684\u59b9\u59b9\u6ca1\u6709\u544a\u8bc9\u4f60\u4e00\u4e2a\u7b80\u5355\u7684\u5e38\u8bc6\uff1f\u5bb6\u65cf\u6218\u4e89\u5fc5\u987b\u5728\u4e00\u4e2a\u5faa\u73af\uff08\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff09\u4e4b\u5185\u7ed3\u675f\uff0c\u5982\u679c\u5230\u65f6\u5019\u6211\u4eec\u8fd8\u6ca1\u80fd\u628a\u745e\u8d39\u5fb7\u5bb6\u65cf\u94f2\u9664\uff0c\u90a3\u6b63\u4e49\u7684\u5ba1\u5224\u5c31\u4f1a\u843d\u5230\u6211\u4eec\u5934\u4e0a\u4e86\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u201c\u65f7\u65e5\u6301\u4e45\u7684\u6218\u4e89\u4e0d\u4f1a\u5f97\u5230\u795e\u540e\u7684\u795d\u798f\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u5728\u65c1\u8fb9\u89e3\u91ca\uff0c\u201c\u90a3\u4e0d\u5229\u4e8e\u57ce\u5e02\u7684\u5b89\u5b81\u548c\u7a33\u5b9a\u3002\u201d</p><p>\u867d\u7136\u5353\u5c14\u57ce\u5e02\u91cc\u4ece\u6765\u5c31\u6ca1\u6709\u8fd9\u79cd\u4e1c\u897f\u3002</p><p>\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u743c\u6069\u53f9\u6c14\uff0c\u201c\u6211\u758f\u5ffd\u4e86\u3002\u201d</p><p>\u201c\u6211\u5bf9\u6b64\u6beb\u4e0d\u60ca\u8bb6\uff0c\u201d\u8fe6\u6cd5\u8bc4\u4ef7\uff0c\u5979\u8f6c\u8fc7\u8138\u770b\u7740\u4e9a\u5f53\u65af\uff0c\u201c\u9996\u5e2d\u5deb\u5e08\u7684\u610f\u89c1\u5462\uff1f\u201d</p><p>\u5411\u745e\u8d39\u5fb7\u5bb6\u65cf\u5f00\u6218\uff0c\u6700\u70ed\u5fc3\u79ef\u6781\u7684\u81ea\u7136\u662f\u8fe6\u6cd5\uff0c\u56e0\u4e3a\u5979\u5207\u8eab\u5229\u76ca\u76f8\u5173\u3002\u4f46\u5f53\u65f6\u5728\u5bb6\u65cf\u4f1a\u8bae\u4e0a\uff0c\u4e9a\u5f53\u65af\u4e5f\u662f\u4e00\u529b\u8d5e\u6210\u7684\uff0c\u4ed6\u5e94\u8be5\u4f1a\u6709\u4ec0\u4e48\u597d\u7684\u4e3b\u610f\u5427\u3002</p><p>\u4e9a\u5f53\u65af\u7684\u8138\u4e0a\u6beb\u65e0\u8868\u60c5\uff0c\u201c\u6211\u89c9\u5f97\u5170\u5c3c\u65af\u7279\u5148\u751f\u7684\u529e\u6cd5\u503c\u5f97\u8003\u8651\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5148\u8bd5\u8bd5\u770b\uff0c\u5982\u679c\u8fdb\u5c55\u4e0d\u987a\u5229\u518d\u8bf4\u3002\u201d</p><p>\u8fe6\u6cd5\u6709\u4e9b\u5931\u671b\uff0c\u4f46\u4e8b\u5230\u5982\u4eca\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u529e\u6cd5\u4e86\u3002\u201c\u6211\u4eec\u5fc5\u987b\u83b7\u80dc\uff0c\u4e5f\u5fc5\u5c06\u83b7\u80dc\uff0c\u201d\u5979\u5bf9\u4f17\u4eba\u8bf4\uff0c\u53c8\u50cf\u662f\u7ed9\u81ea\u5df1\u589e\u52a0\u4fe1\u5fc3\uff0c\u201c\u65e0\u8bba\u7528\u4ec0\u4e48\u624b\u6bb5\u3002\u201d\u5979\u6700\u540e\u5f3a\u8c03\u7740\uff0c\u9762\u5e26\u51b7\u7b11\u3002</p><p>\u201c\u83f2\u5c14\u4f26\u5fc5\u80dc\uff01\u201d\u5979\u5927\u543c\u3002</p><p>\u201c\u5fc5\u80dc\uff01\u201d\u5979\u8eab\u540e\u7684\u7267\u5e08\u548c\u5bb6\u65cf\u6218\u58eb\u4eec\u9f50\u58f0\u5e94\u548c\uff0c\u5deb\u5e08\u4e0d\u559c\u6b22\u8fd9\u79cd\u5954\u653e\u7684\u8868\u8fbe\u60c5\u7eea\u7684\u65b9\u5f0f\uff0c\u4ed6\u4eec\u5fae\u5fae\u70b9\u5934\uff0c\u8868\u793a\u8d5e\u540c\u3002\u743c\u6069\u6c89\u9ed8\u5730\u6ce8\u89c6\u7740\u8fd9\u4e00\u5207\uff0c\u4ed6\u77e5\u9053\u4eca\u5929\u5fc5\u5c06\u6709\u4e00\u573a\u8840\u6218\u3002</p><p>\u203b\u203b\u203b</p><p>\u5168\u526f\u6b66\u88c5\u7684\u5353\u5c14\u6218\u58eb\u4eec\u4e0a\u524d\uff0c\u4f46\u5e76\u4e0d\u662f\u7acb\u523b\u53d1\u52a8\u653b\u51fb\u3002\u65e0\u6570\u56e2\u9ed1\u6697\u7ed3\u754c\u88ab\u53ec\u5524\u51fa\u6765\uff0c\u6f02\u6d6e\u7740\uff0c\u805a\u96c6\u5728\u4e00\u8d77\uff0c\u50cf\u6d53\u5bc6\u7684\u4e4c\u4e91\u906e\u853d\u4e86\u745e\u8d39\u5fb7\u57ce\u5821\u7684\u4e0a\u7a7a\u3002\u8fd9\u662f\u5353\u5c14\u7684\u793c\u8282\uff0c\u56e0\u4e3a\u653b\u51fb\u53e6\u5916\u4e00\u4e2a\u5bb6\u65cf\u662f\u4e0d\u5141\u8bb8\u7684\u2014\u2014\u81f3\u5c11\u6cd5\u5f8b\u4e0a\u662f\u4e0d\u5141\u8bb8\u7684\uff0c\u6240\u4ee5\u9700\u8981\u4fdd\u6301\u9690\u5bc6\uff0c\u8981\u6084\u6084\u5730\u8fdb\u884c\u3002\u5f53\u7136\uff0c\u5168\u57ce\u4eba\u90fd\u77e5\u9053\u83f2\u5c14\u4f26\u6b63\u5728\u653b\u51fb\u745e\u8d39\u5fb7\uff0c\u4f46\u8fd9\u6ca1\u6709\u5173\u7cfb\uff0c\u4ed6\u4eec\u4e0d\u7b97\u8bc1\u4eba\uff0c\u53ea\u6709\u88ab\u653b\u51fb\u7684\u5bb6\u65cf\u4e2d\u7684\u8d35\u65cf\u624d\u6709\u8d44\u683c\u5411\u6267\u653f\u8bae\u4f1a\u63a7\u8bc9\uff0c\u56e0\u6b64\u65a9\u8349\u9664\u6839\u662f\u7edd\u5bf9\u6709\u5fc5\u8981\u7684\u3002</p><p>\u83f2\u5c14\u4f26\u4e3b\u6bcd\u7684\u63d0\u6848\u6ca1\u6709\u88ab\u6267\u653f\u8bae\u4f1a\u901a\u8fc7\uff0c\u6240\u4ee5\u8fd9\u6b21\u653b\u51fb\u4e0d\u7b97\u662f\u6267\u884c\u201c\u5ba1\u5224\u201d\uff0c\u800c\u662f\u5bb6\u65cf\u6218\u4e89\u3002\u524d\u8005\u4ece\u6765\u90fd\u662f\u5149\u660e\u6b63\u5927\u5730\u8fdb\u884c\uff0c\u540e\u8005\u5c31\u9700\u8981\u9075\u5b88\u201c\u9690\u5bc6\u201d\u7684\u793c\u8282\u4e86\u3002\u5728\u743c\u6069\u770b\u6765\uff0c\u8fd9\u662f\u6807\u51c6\u7684\u865a\u4f2a\uff0c\u6216\u8005\u8bf4\u63a9\u8033\u76d7\u94c3\uff0c\u4f46\u5bf9\u4e8e\u5353\u5c14\u6765\u8bf4\uff0c\u8fd9\u5c31\u662f\u7406\u6240\u5f53\u7136\u7684\u4f20\u7edf\u3002</p><p>\u5f53\u9ed1\u6697\u7ed3\u754c\u5c06\u745e\u8d39\u5fb7\u57ce\u5821\u5b8c\u5168\u7b3c\u7f69\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u653b\u51fb\u5f00\u59cb\u4e86\u3002\u83f2\u5c14\u4f26\u5bb6\u65cf\u4e34\u65f6\u4ece\u7eb3\u739b\u65af\u533a\u6293\u6765\u4e86\u4e00\u6279\u4f4e\u7b49\u751f\u7269\uff0c\u5927\u591a\u662f\u72d7\u5934\u4eba\u548c\u5730\u7cbe\uff0c\u4e5f\u6709\u5c11\u91cf\u718a\u5730\u7cbe\u548c\u98df\u4eba\u9b54\uff0c\u5b83\u4eec\u4fa5\u5e78\u5728\u524d\u5929\u7684\u53db\u4e71\u4e2d\u9003\u751f\uff0c\u4f46\u597d\u8fd0\u6c14\u5230\u6b64\u4e3a\u6b62\u4e86\u3002\u5728\u5200\u5251\u548c\u76ae\u97ad\u7684\u9a71\u8d76\u4e0b\uff0c\u70ae\u7070\u4eec\u88ab\u8feb\u4e0a\u524d\uff0c\u53bb\u8fce\u63a5\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u7b2c\u4e00\u8f6e\u53cd\u51fb\u3002\u5404\u5927\u5bb6\u65cf\u8bad\u7ec3\u7684\u5974\u96b6\u7ec4\u6210\u7b2c\u4e8c\u653b\u51fb\u68af\u961f\uff0c\u5353\u5c14\u6218\u58eb\u5219\u8eb2\u5728\u540e\u9762\u3002</p><p>\u4e09\u4e2a\u5bb6\u65cf\u7684\u5deb\u5e08\u90fd\u88ab\u96c6\u4e2d\u8d77\u6765\uff0c\u4e9a\u5f53\u65af\u5206\u914d\u4efb\u52a1\u3002\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u88ab\u5b89\u6392\u5728\u4e00\u7ec4\uff0c\u4ece\u4fa7\u540e\u65b9\u53d1\u8d77\u653b\u51fb\u3002\u5728\u52a8\u8eab\u4e4b\u524d\uff0c\u4e9a\u5f53\u65af\u9012\u7ed9\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u4e00\u679a\u7eff\u5b9d\u77f3\u6212\u6307\u3002</p><p>\u201c\u745e\u8d39\u5fb7\u5bb6\u5fbd\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u770b\u4e86\u770b\uff0c\u5927\u5403\u4e00\u60ca\u3002</p><p>\u201c\u662f\u7684\uff0c\u201d\u4e9a\u5f53\u65af\u8bf4\uff0c\u63a5\u7740\u4f4e\u58f0\u5728\u7267\u5e08\u8033\u8fb9\u8bf4\u4e86\u4e00\u4e2a\u8bcd\uff0c\u201c\u8bb0\u4f4f\uff0c\u8fd9\u662f\u53e3\u4ee4\u3002\u201d</p><p>\u6bcf\u4e2a\u5b58\u7eed\u8d85\u8fc7\u4e00\u767e\u5e74\u7684\u5353\u5c14\u5bb6\u65cf\u90fd\u6709\u8d44\u683c\u62e5\u6709\u81ea\u5df1\u7684\u5bb6\u5fbd\uff0c\u745e\u8d39\u5fb7\u5f53\u7136\u4e0d\u4f8b\u5916\u3002\u8fd9\u679a\u7eff\u5b9d\u77f3\u6212\u6307\u4e0a\u954c\u523b\u7740\u4e00\u4e2a\u957f\u5251\u548c\u6cd5\u6756\u4ea4\u53c9\u7684\u56fe\u6848\uff0c\u5728\u4e0a\u65b9\u662f\u4e00\u53ea\u6b63\u5728\u5410\u4e1d\u7684\u8718\u86db\uff0c\u7ebf\u6761\u7b80\u5355\uff0c\u4f46\u6829\u6829\u5982\u751f\uff0c\u6b63\u662f\u7b2c\u56db\u5bb6\u65cf\u7684\u6807\u5fd7\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u8ba4\u8bc6\u4e00\u4e9b\u745e\u8d39\u5fb7\u5bb6\u7684\u6210\u5458\uff0c\u77e5\u9053\u8fd9\u6212\u6307\u5c31\u662f\u4ed6\u4eec\u7684\u5bb6\u5fbd\u3002</p><p>\u53ea\u8981\u662f\u8d35\u65cf\u5c31\u6709\u8d44\u683c\u4f69\u6234\u5bb6\u5fbd\uff0c\u6709\u65f6\u5019\u6ca1\u6709\u8d35\u65cf\u8eab\u4efd\u7684\u5e73\u6c11\u4e5f\u4f1a\u88ab\u8d4f\u8d50\u4e00\u679a\uff0c\u4f5c\u4e3a\u4e00\u79cd\u8363\u8a89\u6027\u8d28\u7684\u5956\u8d4f\u3002\u5bb6\u5fbd\u901a\u5e38\u90fd\u662f\u666e\u901a\u7269\u54c1\uff0c\u6216\u8bb8\u6750\u8d28\u5f88\u73cd\u8d35\uff0c\u4f46\u4e0d\u542b\u6709\u9b54\u529b\uff0c\u4ec5\u4ec5\u53ea\u662f\u8eab\u4efd\u7684\u8c61\u5f81\u2014\u2014\u4f46\u4e5f\u6709\u6781\u5c11\u6570\u4f8b\u5916\u3002\u5bb6\u65cf\u91cc\u7684\u91cd\u8981\u4eba\u7269\uff0c\u4f8b\u5982\u4e3b\u6bcd\u3001\u4e3b\u6bcd\u7684\u76f4\u7cfb\u5973\u513f\u3001\u9ad8\u9636\u796d\u53f8\u3001\u5deb\u5e08\u6216\u8005\u6b66\u6280\u957f\u4e4b\u7c7b\uff0c\u4f1a\u4f69\u6234\u7279\u522b\u7684\u5bb6\u5fbd\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u957f\u888d\u4e0a\u7684\u767d\u91d1\u80f8\u9488\u5c31\u5c5e\u4e8e\u6b64\u7c7b\uff0c\u743c\u6069\u4e5f\u6709\u4e00\u679a\u3002\u8fd9\u79cd\u7279\u5236\u54c1\u8574\u542b\u6709\u9b54\u6cd5\u529b\u91cf\uff0c\u5f80\u5f80\u80fd\u591f\u5bf9\u4f7f\u7528\u8005\u63d0\u4f9b\u4fdd\u62a4\uff0c\u6216\u8005\u5141\u8bb8\u4ed6\u4eec\u8fdb\u5165\u5bb6\u65cf\u7684\u7981\u5730\u800c\u4e0d\u88ab\u4f24\u5bb3\uff0c\u6216\u8005\u6709\u5fc3\u7075\u4f20\u8baf\u7684\u529f\u80fd\uff0c\u4e0d\u4e00\u800c\u8db3\u3002</p><p>\u4e9a\u5f53\u65af\u9012\u7ed9\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8fd9\u679a\u7eff\u5b9d\u77f3\u6212\u6307\uff0c\u6b64\u523b\u6b63\u5728\u743c\u6069\u7684\u5965\u672f\u89c6\u89c9\u91cc\u70c1\u70c1\u653e\u7740\u94f6\u8272\u548c\u7070\u8272\u6df7\u6742\u7684\u9b54\u6cd5\u7075\u5149\uff0c\u663e\u7136\u8fd9\u662f\u7279\u6b8a\u5236\u54c1\u3002\u7136\u800c\u8fd9\u79cd\u4e1c\u897f\uff0c\u4ece\u6765\u90fd\u662f\u9650\u91cf\u7248\u4f9b\u5e94\uff0c\u53ea\u6709\u5bb6\u65cf\u91cc\u7684\u91cd\u8981\u6210\u5458\u624d\u6709\u8d44\u683c\u83b7\u5f97\uff0c\u5982\u679c\u53bb\u4e16\u6216\u8005\u8131\u79bb\u5bb6\u65cf\uff0c\u5b83\u4e5f\u4f1a\u88ab\u6536\u56de\uff0c\u4e9a\u5f53\u65af\u5374\u662f\u4ece\u54ea\u91cc\u5f04\u6765\u7684\uff1f\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u4e3b\u6bcd\u3001\u9ad8\u9636\u7267\u5e08\u548c\u5deb\u5e08\u4eec\uff0c\u6b64\u523b\u90fd\u597d\u7aef\u7aef\u5730\u5728\u57ce\u5821\u91cc\uff0c\u6ca1\u542c\u8bf4\u6709\u8c01\u88ab\u6084\u6084\u8c0b\u6740\u4e86\u554a\u3002\u8fd9\u79cd\u4e1c\u897f\u7684\u5236\u4f5c\u65b9\u6cd5\u4ece\u6765\u90fd\u662f\u5404\u5bb6\u65cf\u7684\u79d8\u5bc6\uff0c\u53c8\u4e0d\u53ef\u80fd\u4f2a\u9020\u3002</p><p>\u800c\u4e14\u4ed6\u8fd8\u77e5\u9053\u53e3\u4ee4\u3002</p><p>\u9996\u5e2d\u5deb\u5e08\u6ca1\u6709\u591a\u505a\u89e3\u91ca\uff0c\u201c\u6234\u4e0a\u8fd9\u679a\u6212\u6307\uff0c\u745e\u8d39\u5fb7\u5bb6\u5927\u90e8\u4efd\u5177\u6709\u653b\u51fb\u6027\u7684\u9b54\u6cd5\u9677\u9631\u5c31\u4e0d\u4f1a\u88ab\u89e6\u53d1\uff0c\u5982\u679c\u4f60\u65e0\u610f\u95f4\u8e0f\u5165\u7684\u8bdd\uff0c\u201d\u4ed6\u544a\u8bc9\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u201c\u4f46\u8fd9\u4ec5\u4ec5\u53ea\u662f\u56de\u907f\uff0c\u5982\u679c\u4f60\u60f3\u89e3\u9664\u5b83\u4eec\uff0c\u90a3\u4e48\u8fd8\u9700\u8981\u53e6\u5916\u7684\u52aa\u529b\u3002\u201d</p><p>\u8fd9\u5df2\u7ecf\u662f\u5f88\u5927\u7684\u5e2e\u52a9\u4e86\uff0c\u743c\u6069\u60f3\uff0c\u53ef\u60dc\u6212\u6307\u53ea\u6709\u4e00\u679a\u3002</p><p>\u201c\u6211\u60f3\u5170\u5c3c\u65af\u7279\u5148\u751f\u6709\u8db3\u591f\u7684\u80fd\u529b\u4fdd\u62a4\u81ea\u5df1\uff0c\u201d\u4eff\u4f5b\u770b\u51fa\u743c\u6069\u7684\u5fc3\u601d\uff0c\u4e9a\u5f53\u65af\u8bf4\uff0c\u201c\u800c\u4e14\u60a8\u5df2\u7ecf\u62e5\u6709\u66f4\u597d\u7684\u6212\u6307\u4e86\u3002\u201d</p><p>\u743c\u6069\u542c\u51fa\u4e86\u4ed6\u7684\u6697\u793a\u3002</p><p>\u201c\u90a3\u4e48\uff0c\u795d\u4f60\u4eec\u597d\u8fd0\u3002\u201d</p><p>\u4e9a\u5f53\u65af\u5fae\u7b11\u7740\uff0c\u8eab\u5f71\u6162\u6162\u6d88\u5931\u5728\u7a7a\u6c14\u4e2d\u3002</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u201c\u9996\u5e2d\u5deb\u5e08\u5bf9\u4f60\u4f3c\u4e4e\u5f88\u53cb\u5584\u3002\u201d\u4ed6\u5bf9\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\u3002</p><p>\u201c\u4ed6\u548c\u6211\u7236\u4eb2\u662f\u670b\u53cb\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u6211\u8bb0\u5f97\u66fe\u7ecf\u544a\u8bc9\u8fc7\u4f60\u7684\u3002\u800c\u4e14\u6211\u7684\u5f88\u591a\u5deb\u5e08\u6cd5\u672f\u4e5f\u662f\u4ed6\u6559\u7684\uff0c\u53ef\u4ee5\u7b97\u662f\u6211\u7684\u8001\u5e08\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u4ed6\u4f3c\u4e4e\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u968f\u53e3\u95ee\uff0c\u201c\u6211\u60f3\u4ed6\u4f1a\u613f\u610f\u770b\u5230\u4f60\u6210\u4e3a\u4e3b\u6bcd\u5427\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u8e0c\u8e87\u4e86\u4e00\u79d2\u949f\uff0c\u201c\u5f53\u7136\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6beb\u65e0\u7591\u95ee\u4ed6\u4f1a\u652f\u6301\u6211\uff0c\u800c\u4e14\u4ed6\u7684\u8bdd\u5728\u6211\u6bcd\u4eb2\u7684\u5fc3\u76ee\u4e2d\u5206\u91cf\u5f88\u91cd\u3002\u201d</p><p>\u743c\u6069\u5fae\u7b11\u8d77\u6765\uff0c\u201c\u8d70\u5427\u3002\u201d</p><p>\u4ed6\u5ff5\u51fa\u4e00\u4e2a\u5b57\u7b26\uff0c\u6fc0\u6d3b\u4e86\u8eab\u4e0a\u7684\u7075\u5316\u6597\u7bf7\uff0c\u628a\u81ea\u5df1\u53d8\u6210\u4e86\u865a\u4f53\u3002</p><p>\u5deb\u5e08\u53d8\u5f97\u50cf\u96fe\u6c14\u4e00\u6837\u6726\u80e7\u8d77\u6765\uff0c\u7136\u540e\u6025\u901f\u6de1\u5316\uff0c\u51e0\u4e4e\u662f\u4e00\u77ac\u95f4\uff0c\u4ed6\u5c31\u5df2\u7ecf\u4ece\u8fd9\u4e2a\u7269\u8d28\u7684\u4e16\u754c\u6d88\u5931\u4e86\u3002\u743c\u6069\u7684\u8eab\u4f53\uff0c\u8fde\u540c\u4ed6\u7684\u957f\u888d\u3001\u9774\u5b50\u4ee5\u53ca\u6240\u6709\u88c5\u5907\uff0c\u90fd\u53d8\u6210\u4e86\u7c7b\u4f3c\u5e7d\u7075\u7684\u5b58\u5728\uff0c\u53d8\u5f97\u865a\u5e7b\u4e0d\u5b9e\u8d77\u6765\u2014\u2014\u5f53\u7136\uff0c\u8fd9\u662f\u76f8\u5bf9\u800c\u8a00\u7684\uff0c\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u4ed6\u4e5f\u5b8c\u5168\u53ef\u4ee5\u8ba4\u4e3a\u81ea\u5df1\u73b0\u5728\u624d\u662f\u5b9e\u4f53\uff0c\u5468\u56f4\u7684\u6574\u4e2a\u4e16\u754c\u90fd\u53d8\u6210\u4e86\u865a\u5f71\u3002\u4ed6\u4f9d\u65e7\u80fd\u591f\u8bf4\u8bdd\uff0c\u4f46\u53ea\u6709\u81ea\u5df1\u80fd\u542c\u89c1\uff1b\u4f9d\u65e7\u6709\u89c6\u89c9\uff0c\u4f46\u6240\u6709\u7684\u7269\u4f53\u90fd\u53d8\u6210\u4e86\u4e00\u5806\u51e0\u4f55\u7ebf\u6761\uff0c\u7f24\u7eb7\u591a\u5f69\u7684\u989c\u8272\u5219\u5316\u4f5c\u6df1\u6d45\u4e0d\u4e00\u7684\u9634\u5f71\uff0c\u5c31\u8fde\u65c1\u8fb9\u7684\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u4e0d\u4f8b\u5916\uff0c\u5979\u73b0\u5728\u770b\u8d77\u6765\u5c31\u50cf\u662f\u7f8e\u672f\u8bfe\u4e0a\u7684\u4e00\u526f\u4eba\u7269\u7b80\u7b14\u7d20\u63cf\u3002\u6240\u5e78\u5404\u79cd\u9b54\u6cd5\u7075\u7ebf\u7684\u5149\u8292\u4f9d\u65e7\u770b\u5f97\u5206\u660e\uff0c\u800c\u4e14\u66f4\u52a0\u6e05\u6670\u4e86\uff0c\u8fd9\u662f\u4ef6\u597d\u4e8b\u3002</p><p>\u4ed6\u4e5f\u6ca1\u529e\u6cd5\u518d\u50cf\u4ee5\u524d\u4e00\u6837\u89e6\u6478\u5230\u5b9e\u4f53\uff0c\u53cd\u8fc7\u6765\uff0c\u5b9e\u4f53\u4e5f\u4e0d\u80fd\u518d\u4f5c\u7528\u4e8e\u4ed6\uff0c\u9b54\u6cd5\u53ef\u4ee5\u5145\u5f53\u8fd9\u5176\u4e2d\u7684\u5a92\u4ecb\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ed6\u4f9d\u7136\u53ef\u4ee5\u6beb\u65e0\u969c\u788d\u5730\u4f7f\u7528\u9b54\u6cd5\uff0c\u5f71\u54cd\u7269\u8d28\u4e16\u754c\uff0c\u800c\u90a3\u4e9b\u9644\u5e26\u9b54\u6cd5\u7684\u5b9e\u4f53\uff0c\u6bd4\u5982\u5200\u5251\uff0c\u8fd8\u662f\u80fd\u591f\u4f24\u5bb3\u5230\u4ed6\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u6234\u4e0a\u6212\u6307\uff0c\u4ece\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u4e00\u67c4\u624e\u514b\u6728\u6cd5\u6756\uff0c\u63e1\u5728\u624b\u4e2d\uff0c\u7136\u540e\u559d\u4e0b\u4e00\u74f6\u836f\u6c34\u3002\u8eab\u4f53\u6162\u6162\u4ece\u7a7a\u6c14\u4e2d\u6d88\u5931\uff0c\u4f46\u5e76\u6ca1\u6709\u53d8\u6210\u865a\u4f53\uff0c\u4ec5\u4ec5\u53ea\u662f\u9690\u5f62\u4e86\u3002\u7a0d\u7a0d\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u5979\u5524\u8d77\u5353\u5c14\u5929\u8d4b\u7684\u6d6e\u7a7a\u672f\uff0c\u6162\u6162\u98de\u8d77\u6765\uff0c\u7d27\u63a5\u7740\u611f\u89c9\u81ea\u5df1\u7684\u53f3\u624b\u624b\u8155\u88ab\u63e1\u4f4f\u4e86\u3002\u5979\u7684\u53f3\u624b\u6234\u7740\u4e00\u4e2a\u79d8\u94f6\u62a4\u8155\uff0c\u90a3\u662f\u9b54\u6cd5\u5236\u54c1\uff0c\u6240\u4ee5\u743c\u6069\u53ef\u4ee5\u78b0\u89e6\u5230\u3002</p><p>\u201c\u8ddf\u7740\u6211\u3002\u201d\u5deb\u5e08\u8f7b\u58f0\u8bf4\u3002</p><p>\u4ed6\u50cf\u5e7d\u7075\u4e00\u6837\u6f02\u6d6e\u8d77\u6765\uff0c\u5347\u5230\u9ad8\u7a7a\uff0c\u4ece\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u62a4\u57ce\u6cb3\u548c\u57ce\u5899\u7684\u8fb9\u7f18\u63a0\u8fc7\uff0c\u524d\u5f80\u57ce\u5821\u7684\u4fa7\u540e\u65b9\u3002\u4ed6\u4eec\u6ca1\u6709\u53d7\u5230\u653b\u51fb\uff0c\u5728\u8d34\u8fd1\u57ce\u5899\u7684\u65f6\u5019\uff0c\u743c\u6069\u611f\u89c9\u90a3\u4e9b\u5f17\u6d1b\u9b54\u96d5\u50cf\u4f3c\u4e4e\u89c9\u5bdf\u5230\u4e86\u4ec0\u4e48\uff0c\u76ef\u7740\u81ea\u5df1\uff0c\u4f46\u6700\u7ec8\u5b83\u4eec\u4ec0\u4e48\u90fd\u6ca1\u505a\u3002</p><p>\u653b\u51fb\u5df2\u7ecf\u5f00\u59cb\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u6293\u6765\u7684\u90a3\u4e9b\u70ae\u7070\u5728\u5341\u79d2\u949f\u5185\u5c31\u4f24\u4ea1\u6b86\u5c3d\uff0c\u5b83\u4eec\u552f\u4e00\u7684\u4ef7\u503c\u662f\u6d88\u8017\u4e86\u745e\u8d39\u5fb7\u5bb6\u65cf\u4e0d\u5c11\u5f29\u7bad\u548c\u51e0\u53d1\u706b\u7403\u3002\u4e09\u5927\u5bb6\u65cf\u7684\u5f13\u7bad\u624b\u8fdb\u884c\u53cd\u51fb\uff0c\u538b\u5236\u4f4f\u4e86\u57ce\u5934\u7684\u706b\u529b\uff0c\u718a\u5730\u7cbe\u548c\u98df\u4eba\u9b54\u5f00\u59cb\u649e\u51fb\u57ce\u95e8\uff0c\u7c73\u5179\u745e\u56fe\u5c14\u5bb6\u7684\u77ee\u4eba\u5974\u96b6\u5236\u4f5c\u4e86\u4e00\u4e9b\u653b\u57ce\u5668\u68b0\uff0c\u6b64\u65f6\u6b63\u6d3e\u4e0a\u7528\u573a\u3002\u5f53\u743c\u6069\u8d8a\u8fc7\u62a4\u57ce\u6cb3\u7684\u65f6\u5019\uff0c\u4ed6\u770b\u89c1\u516d\u4e2a\u98df\u4eba\u9b54\u6b63\u62ac\u7740\u4e00\u4e2a\u5e9e\u5927\u7684\u653b\u57ce\u69cc\uff0c\u8d8a\u8fc7\u62a4\u57ce\u6cb3\u4e0a\u7684\u552f\u4e00\u4e00\u5ea7\u6865\uff0c\u649e\u51fb\u57ce\u95e8\u3002\u653b\u57ce\u69cc\u5728\u743c\u6069\u7684\u773c\u4e2d\u53ea\u6709\u7ebf\u6761\u8f6e\u5ed3\uff0c\u6ca1\u6709\u660e\u4eae\u8272\u6cfd\uff0c\u5f88\u663e\u7136\u90a3\u4e0d\u662f\u9b54\u6cd5\u7269\u54c1\u3002\u4e4b\u6240\u4ee5\u8fd9\u4e48\u505a\uff0c\u662f\u56e0\u4e3a\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u57ce\u95e8\u4e0a\u6709\u5927\u91cf\u7684\u6cd5\u672f\u9677\u9631\uff0c\u5982\u679c\u76f4\u63a5\u7528\u6cd5\u672f\u6216\u8005\u9b54\u6cd5\u7269\u54c1\u653b\u51fb\u53cd\u800c\u4e0d\u8d77\u4f5c\u7528\u3002\u4f46\u7528\u666e\u901a\u7269\u54c1\u7684\u8bdd\uff0c\u60f3\u8981\u649e\u5f00\u90a3\u6247\u7cbe\u91d1\u94f8\u6210\u7684\u5927\u95e8\u540c\u6837\u4e5f\u5f88\u96be\uff0c\u6bd5\u7adf\u7cbe\u91d1\u53ef\u662f\u4e16\u754c\u4e0a\u5df2\u77e5\u6700\u7ed3\u5b9e\u7684\u91d1\u5c5e\u3002</p><p>\u8fd9\u573a\u653b\u51fb\u6ce8\u5b9a\u5f92\u52b3\u65e0\u529f\uff0c\u743c\u6069\u5728\u5fc3\u4e2d\u8bf4\uff0c\u4e00\u5207\u8fd8\u662f\u5f97\u770b\u6211\u4eec\u5deb\u5e08\u7684\u3002</p></article>"}}}]);
