(window.webpackJsonp=window.webpackJsonp||[]).push([[1469],{1492:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e8c\u8282 \u6084\u6084\u7684\u53d8\u5316",content:"<article><h2>\u7b2c\u4e09\u5341\u4e8c\u8282 \u6084\u6084\u7684\u53d8\u5316</h2><p>\u6b63\u5982\u4e9a\u5f53\u65af\u6240\u8bf4\uff0c\u5bb6\u5fbd\u53ef\u4ee5\u8ba9\u4ed6\u4eec\u907f\u5f00\u90a3\u4e9b\u5177\u6709\u653b\u51fb\u6027\u7684\u9b54\u6cd5\u9677\u9631\u3002\u743c\u6069\u4e3a\u4e86\u4ee5\u9632\u4e07\u4e00\uff0c\u63d0\u524d\u7ed9\u81ea\u5df1\u65bd\u52a0\u4e86\u5927\u91cf\u7684\u9b54\u6cd5\u9632\u5fa1\uff0c\u5e76\u4e14\u542f\u52a8\u4e86\u6cd5\u672f\u9006\u8f6c\u6212\u6307\uff0c\u7136\u540e\u624d\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u8e0f\u5165\u9b54\u6cd5\u9635\u7684\u7b2c\u4e8c\u5c42\u3002\u4ed6\u51c6\u5907\u597d\u4e86\uff0c\u4e00\u65e6\u5f62\u52bf\u4e0d\u5bf9\u5c31\u7acb\u523b\u9003\u8dd1\u3002</p><p>\u7136\u540e\u4ec0\u4e48\u90fd\u6ca1\u53d1\u751f\u3002</p><p>\u743c\u6069\u8212\u4e86\u53e3\u6c14\uff0c\u81ea\u5df1\u6709\u70b9\u592a\u8fc7\u5206\u8c28\u614e\u4e86\uff0c\u4ed6\u60f3\uff0c\u4f46\u7d27\u63a5\u7740\u5426\u5b9a\u4e86\u8fd9\u4e2a\u5ff5\u5934\u3002\u5bf9\u4e8e\u5deb\u5e08\u6765\u8bf4\uff0c\u8eab\u5904\u654c\u4eba\u7684\u57ce\u5821\u91cc\uff0c\u600e\u4e48\u8c28\u614e\u90fd\u662f\u4e0d\u8fc7\u5206\u7684\u3002</p><p>\u65c1\u8fb9\u7684\u5353\u5c14\u5c11\u5973\u5374\u6ca1\u6709\u4ed6\u8fd9\u4e48\u7d27\u5f20\uff0c\u5979\u663e\u7136\u5bf9\u4e9a\u5f53\u65af\u7684\u8bdd\u5f88\u6709\u4fe1\u5fc3\u3002\u6bd4\u8d77\u8fd9\u4e2a\uff0c\u5979\u66f4\u5728\u610f\u81ea\u5df1\u7684\u86c7\u9996\u97ad\uff0c\u5728\u521a\u624d\u7684\u6218\u6597\u4e2d\uff0c\u56db\u9897\u86c7\u9996\u88ab\u524a\u65ad\u5176\u4e8c\uff0c\u867d\u7136\u8fd8\u80fd\u7528\uff0c\u4f46\u5a01\u529b\u5df2\u7ecf\u5927\u6253\u6298\u6263\u4e86\u3002</p><p>\u201c\u4e0d\u80fd\u91cd\u65b0\u5236\u4f5c\u5417\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u86c7\u9996\u97ad\u662f\u5973\u796d\u53f8\u7684\u6807\u5fd7\uff0c\u5b83\u4e0d\u662f\u5355\u7eaf\u7684\u4e00\u4ef6\u6b66\u5668\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u56de\u7b54\uff0c\u201c\u6bcf\u4e00\u67c4\u86c7\u9996\u97ad\u90fd\u662f\u795e\u540e\u795d\u798f\u7684\u4ea7\u7269\u3002\u201d</p><p>\u7136\u800c\u73b0\u5728\u7f57\u4e1d\u4e0d\u5728\u4e86\u3002</p><p>\u743c\u6069\u521d\u6765\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u65f6\u5019\uff0c\u7f57\u4e1d\u6c89\u5bc2\u8fd8\u662f\u4e2a\u79d8\u5bc6\uff0c\u4ec5\u9650\u4e8e\u5973\u6027\u7267\u5e08\u5185\u90e8\u548c\u6781\u5c11\u6570\u5916\u4eba\u77e5\u6653\uff1b\u4f46\u77ed\u77ed\u51e0\u5929\uff0c\u7ecf\u5386\u4e86\u524d\u65e5\u7684\u4e00\u573a\u5168\u57ce\u5927\u53db\u4e71\uff0c\u53ea\u8981\u662f\u4e2a\u667a\u5546\u6b63\u5e38\u7684\u4eba\uff0c\u90fd\u5df2\u7ecf\u80fd\u770b\u51fa\u8fd9\u4e2a\u4e8b\u5b9e\u4e86\u3002\u5728\u5e73\u5b9a\u53db\u4e71\u7684\u65f6\u5019\uff0c\u5973\u7267\u5e08\u4eec\u4f9d\u8d56\u5377\u8f74\u3001\u9b54\u6756\u800c\u5e76\u975e\u81ea\u8eab\u7684\u6cd5\u672f\uff0c\u795e\u5723\u7684\u796d\u53f8\u5b66\u9662\u53d7\u5230\u4e86\u653b\u51fb\uff0c\u5927\u91cf\u7684\u6559\u5b98\u548c\u5b66\u751f\u88ab\u6740\u6b7b\uff0c\u8fd9\u4e00\u5207\u90fd\u5145\u5206\u8bf4\u660e\u4e86\u95ee\u9898\u3002\u5c11\u6570\u9ad8\u5c42\u4eba\u5458\uff0c\u4f8b\u5982\u4e9a\u5f53\u65af\u8fd9\u79cd\uff0c\u8fd8\u77e5\u9053\u5723\u7269\u6df1\u6e0a\u53ec\u5524\u86db\u88ab\u6467\u6bc1\u4e86\uff0c\u8fd9\u5c31\u66f4\u65e0\u53ef\u7591\u3002</p><p>\u5982\u679c\u795e\u540e\u8fd8\u5728\u7684\u8bdd\uff0c\u5979\u600e\u4e48\u53ef\u80fd\u5750\u89c6\u8fd9\u79cd\u60c5\u51b5\u53d1\u751f\u800c\u4e0d\u7ba1\u4e0d\u987e\u5462\u3002</p><p>\u6ca1\u6709\u4e86\u7f57\u4e1d\uff0c\u9ed1\u6697\u7cbe\u7075\u7684\u672a\u6765\u53c8\u5c06\u4f1a\u662f\u5982\u4f55\u5462\u3002</p><p>\u743c\u6069\u6447\u4e86\u6447\u5934\uff0c\u628a\u8fd9\u79cd\u65e0\u804a\u7684\u5ff5\u5934\u9a71\u79bb\u81ea\u5df1\u7684\u8111\u6d77\u3002\u5353\u5c14\u7684\u672a\u6765\uff0c\u81ea\u7136\u6709\u5353\u5c14\u81ea\u5df1\u53bb\u8d39\u795e\u8003\u8651\uff0c\u548c\u81ea\u5df1\u6ca1\u6709\u534a\u70b9\u5173\u7cfb\u3002\u8fd9\u4e2a\u4e16\u754c\u4e0a\u51e0\u4e4e\u4efb\u4f55\u4e00\u4e2a\u5927\u7684\u667a\u6167\u65cf\u7fa4\u90fd\u6709\u4fe1\u4ef0\u5d07\u62dc\u7684\u795e\u7947\uff0c\u4ece\u8fd9\u70b9\u6765\u770b\uff0c\u5353\u5c14\u4eec\u5931\u53bb\u4e86\u7f57\u4e1d\uff0c\u5e94\u8be5\u5f88\u5feb\u5c31\u4f1a\u91cd\u65b0\u9009\u62e9\u65b0\u7684\u795e\u7947\u5427\u2014\u2014\u5982\u679c\u5728\u6b64\u4e4b\u524d\u5979\u4eec\u8fd8\u6ca1\u6709\u56e0\u4e3a\u81ea\u76f8\u6b8b\u6740\u800c\u706d\u7edd\u7684\u8bdd\u3002</p><p>\u201c\u591c\u5973\u58eb\u3002\u201d\u4ed6\u5728\u5fc3\u4e2d\u9ed8\u5ff5\u7740\uff0c\u8fd9\u6b21\u9634\u9b42\u57ce\u8d81\u7740\u7f57\u4e1d\u6c89\u5bc2\uff0c\u5927\u4e3e\u8fdb\u653b\u5e7d\u6697\u5730\u57df\uff0c\u5728\u5f88\u5927\u7a0b\u5ea6\u4e0a\u4e5f\u6709\u8fd9\u4e2a\u56e0\u7d20\u5427\u3002\u8bf8\u795e\u4e4b\u4e2d\uff0c\u4ee5\u6697\u591c\u5973\u795e\u838e\u5c14\u548c\u8718\u86db\u5973\u795e\u7f57\u4e1d\u6700\u4e3a\u76f8\u8fd1\uff0c\u6267\u638c\u7684\u795e\u804c\u9690\u9690\u6697\u5408\uff0c\u4f20\u8bf4\u838e\u5c14\u4e00\u76f4\u5c31\u89ca\u89ce\u7f57\u4e1d\u7684\u9ed1\u76ae\u80a4\u5b50\u6c11\uff0c\u8bd5\u56fe\u5c06\u6559\u4f1a\u4fe1\u4ef0\u6269\u5f20\u5230\u5e7d\u6697\u5730\u57df\uff0c\u53ea\u662f\u4e0d\u5f97\u5176\u4fbf\uff0c\u5982\u4eca\u6709\u8fd9\u6837\u5343\u8f7d\u96be\u9022\u7684\u597d\u673a\u4f1a\uff0c\u591c\u5973\u58eb\u53c8\u600e\u4e48\u4f1a\u653e\u8fc7\u3002</p><p>\u53ef\u4ee5\u9884\u89c1\uff0c\u968f\u7740\u9634\u9b42\u57ce\u5728\u5e7d\u6697\u5730\u57df\u7684\u80dc\u5229\uff0c\u838e\u5c14\u7684\u6559\u4f1a\u4e5f\u4f1a\u8fc5\u901f\u6269\u5f20\uff0c\u501f\u7740\u7f57\u4e1d\u7a81\u7136\u6c89\u5bc2\u9020\u6210\u7684\u771f\u7a7a\u8fc5\u901f\u62a2\u5360\u4fe1\u5f92\u5e02\u573a\u3002\u8bf4\u4e0d\u5b9a\u518d\u8fc7\u51e0\u5341\u4e0a\u767e\u5e74\uff0c\u8fd9\u4e9b\u9ed1\u76ae\u80a4\u7cbe\u7075\u771f\u7684\u90fd\u4f1a\u6539\u4fe1\u838e\u5c14\u4e86\u3002</p><p>\u838e\u5c14\u603b\u6bd4\u7f57\u4e1d\u597d\uff0c\u743c\u6069\u60f3\uff0c\u81f3\u5c11\u5979\u4e0d\u641e\u5973\u6743\u4e3b\u4e49\u3002</p><p>\u671d\u7740\u4e8b\u5148\u5224\u65ad\u7684\u65b9\u5411\uff0c\u4ed6\u4eec\u7ee7\u7eed\u524d\u8fdb\u3002\u5f53\u65f6\u743c\u6069\u6307\u8fd9\u6761\u8def\uff0c\u5bf9\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\u662f\u51ed\u76f4\u89c9\uff0c\u5176\u5b9e\u4ed6\u662f\u770b\u5230\u8fd9\u7247\u533a\u57df\u4e2d\u95ea\u70c1\u7eff\u5149\u7684\u9b54\u6cd5\u7075\u7ebf\u6700\u591a\uff0c\u8fd9\u662f\u53d8\u5316\u5b66\u6d3e\u7684\u8868\u5f81\uff0c\u6b63\u662f\u743c\u6069\u7684\u4e13\u4e1a\u8303\u56f4\u3002\u5982\u679c\u8bf4\u8981\u9010\u6b65\u74e6\u89e3\u8fd9\u4e2a\u5e9e\u5927\u9b54\u6cd5\u9635\uff0c\u4ece\u81ea\u5df1\u64c5\u957f\u7684\u9886\u57df\u5f00\u59cb\uff0c\u81ea\u7136\u662f\u660e\u667a\u7684\u4f5c\u6cd5\u3002</p><p>\u8def\u4e0a\u649e\u4e0a\u4e00\u961f\u5de1\u903b\u961f\uff0c\u6b63\u6025\u5306\u5306\u5730\u5f80\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u521a\u624d\u8fc7\u6765\u7684\u65b9\u5411\u8dd1\u53bb\uff0c\u6ca1\u6709\u53d1\u73b0\u4ed6\u4eec\u8981\u627e\u7684\u76ee\u6807\u5176\u5b9e\u5c31\u5728\u8def\u8fb9\u7684\u9634\u5f71\u91cc\u3002\u501f\u52a9\u62a2\u6765\u7684\u5bb6\u5fbd\uff0c\u4e24\u4eba\u4e00\u8def\u987a\u5229\uff0c\u5e73\u5b89\u5730\u907f\u5f00\u4e86\u5404\u79cd\u5371\u9669\u7684\u9b54\u6cd5\u9677\u9631\u3002\u743c\u6069\u5c1d\u8bd5\u7740\uff0c\u8bd5\u9a8c\u6027\u8d28\u5730\u6d88\u89e3\u4e86\u51e0\u4e2a\u4e0d\u592a\u91cd\u8981\u7684\u7b26\u6587\uff0c\u4f46\u7acb\u523b\u5c31\u53c8\u88ab\u91cd\u65b0\u6784\u5efa\u8d77\u6765\u3002\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u9b54\u6cd5\u9635\u4e0d\u662f\u8ff7\u9501\uff0c\u6ca1\u6709\u6e90\u6e90\u4e0d\u65ad\u63d0\u4f9b\u80fd\u91cf\u7684\u6838\u5fc3\uff0c\u5b83\u6240\u8574\u542b\u7684\u80fd\u91cf\u603b\u6570\u662f\u56fa\u5b9a\u7684\uff0c\u4f46\u5374\u5728\u7cbe\u5de7\u7684\u8bbe\u8ba1\u4e4b\u4e0b\u5faa\u73af\u6d41\u901a\uff0c\u4efb\u4f55\u4e00\u5904\u51fa\u73b0\u6f0f\u6d1e\uff0c\u90fd\u4f1a\u7acb\u523b\u4ece\u5176\u4ed6\u5730\u65b9\u83b7\u5f97\u8865\u5145\u3002</p><p>\u5982\u679c\u6709\u8db3\u591f\u7684\u65f6\u95f4\uff0c\u5e76\u4e14\u6ca1\u6709\u4eba\u5e72\u6270\u7684\u8bdd\uff0c\u743c\u6069\u6216\u8bb8\u53ef\u4ee5\u4e00\u70b9\u4e00\u70b9\u5730\u6d88\u8017\u8fd9\u4e2a\u9b54\u6cd5\u9635\u7684\u529b\u91cf\u2014\u2014\u4f30\u8ba1\u6709\u4e24\u4e09\u4e2a\u6708\u5c31\u80fd\u5927\u529f\u544a\u6210\u3002\u4f46\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u5deb\u5e08\u53c8\u4e0d\u662f\u6b7b\u4eba\uff0c\u600e\u4e48\u53ef\u80fd\u4efb\u7531\u4e00\u4e2a\u5916\u4eba\u8fd9\u4e48\u6298\u817e\u3002</p><p>\u9700\u8981\u627e\u51fa\u66f4\u5173\u952e\u7684\u5730\u65b9\u3002</p><p>\u7ee7\u7eed\u524d\u8fdb\uff0c\u7a7f\u8fc7\u4e00\u7247\u77f3\u7b0b\u6797\uff0c\u743c\u6069\u770b\u89c1\u4e86\u4e00\u5ea7\u6709\u7740\u7d2b\u8272\u5706\u9876\u7684\u795e\u5802\uff0c\u8fd9\u5927\u6982\u662f\u6574\u5ea7\u745e\u8d39\u5fb7\u5b85\u9662\u91cc\u552f\u4e00\u4e00\u5ea7\u4eba\u5de5\u5efa\u7b51\u4e86\uff0c\u533a\u522b\u4e8e\u90a3\u4e9b\u5929\u7136\u7684\u949f\u4e73\u77f3\u548c\u77f3\u7b0b\u3002\u74dc\u7406\u5fb7\u65af\u57ce\u662f\u7f57\u4e1d\u4fe1\u4ef0\u7684\u5927\u672c\u8425\uff0c\u6bcf\u4e2a\u5bb6\u65cf\u91cc\u90fd\u5efa\u6709\u796d\u7940\u86db\u540e\u7684\u795e\u5802\uff0c\u745e\u8d39\u5fb7\u81ea\u7136\u4e5f\u4e0d\u4f8b\u5916\u3002</p><p>\u743c\u6069\u6765\u5230\u8fd9\u91cc\uff0c\u81ea\u7136\u4e0d\u662f\u6253\u7b97\u6765\u4e34\u65f6\u671d\u62dc\u4e00\u4e0b\u86db\u540e\u3002\u5728\u5965\u672f\u89c6\u89c9\u4e2d\uff0c\u4ed6\u6e05\u695a\u5730\u770b\u89c1\u5927\u91cf\u7684\u7eff\u8272\u9b54\u6cd5\u7075\u7ebf\u5728\u6b64\u5904\u4ea4\u6c47\uff0c\u7075\u5149\u4eae\u5f97\u523a\u773c\uff0c\u6beb\u65e0\u7591\u95ee\uff0c\u8fd9\u91cc\u662f\u4e00\u4e2a\u201c\u7ed3\u70b9\u201d\uff0c\u662f\u652f\u6301\u6574\u4e2a\u9b54\u6cd5\u9635\u7684\u4e5d\u5927\u652f\u67f1\u4e4b\u4e00\u3002</p><p>\u5982\u679c\u80fd\u60f3\u529e\u6cd5\u7834\u574f\u8fd9\u4e2a\u201c\u7ed3\u70b9\u201d\uff0c\u751a\u81f3\u53ea\u662f\u524a\u5f31\uff0c\u5e94\u8be5\u5c31\u80fd\u5bf9\u9b54\u6cd5\u9635\u9020\u6210\u91cd\u5927\u5f71\u54cd\u3002\u4f46\u8fd9\u6837\u91cd\u8981\u7684\u5730\u65b9\uff0c\u80af\u5b9a\u4f1a\u6709\u4eba\u91cd\u70b9\u5b88\u536b\u3002</p><p>\u4e24\u4eba\u8fdc\u8fdc\u505c\u4f4f\u811a\u6b65\uff0c\u5728\u4e00\u4e2a\u50fb\u9759\u7684\u89d2\u843d\u9634\u5f71\u91cc\u9690\u85cf\u7740\u8eab\u5f62\uff0c\u89c2\u5bdf\u7740\u3002\u6b64\u65f6\u5916\u9762\u4e09\u5927\u5bb6\u65cf\u7684\u58eb\u5175\u653b\u57ce\u6b63\u6025\uff0c\u745e\u8d39\u5fb7\u5bb6\u65cf\u4ee5\u4e00\u654c\u4e09\uff0c\u867d\u7136\u6709\u57ce\u5899\u53ef\u4f9d\u6258\uff0c\u5230\u5e95\u5728\u4eba\u6570\u4e0a\u5927\u5927\u5904\u4e8e\u52a3\u52bf\u3002\u5982\u4eca\u51e0\u4e4e\u6574\u4e2a\u5bb6\u65cf\u7684\u6210\u5458\u90fd\u5df2\u7ecf\u4e0a\u9635\u8fce\u6218\uff0c\u5b85\u9662\u91cc\u6ca1\u6709\u591a\u5c11\u4eba\u9a7b\u5b88\u3002\u8fd9\u5ea7\u795e\u5802\u7684\u6b63\u95e8\u655e\u5f00\u7740\uff0c\u95e8\u53e3\u53ea\u6709\u4e24\u4e2a\u4f8d\u536b\uff0c\u743c\u6069\u6084\u6084\u79fb\u52a8\u4e86\u4e00\u4e0b\u4f4d\u7f6e\uff0c\u7ecf\u8fc7\u5f3a\u5316\u7684\u53cc\u773c\u900f\u8fc7\u5efa\u7b51\u770b\u53bb\uff0c\u770b\u89c1\u4e86\u4e00\u5ea7\u7f57\u4e1d\u7684\u795e\u50cf\uff0c\u796d\u575b\u5468\u56f4\u7684\u8fb9\u6cbf\u6446\u653e\u7740\u516b\u4e2a\u706b\u76c6\uff0c\u8c61\u5f81\u8718\u86db\u7684\u516b\u6761\u817f\u3002</p><p>\u74dc\u7406\u5fb7\u65af\u57ce\u91cc\u904d\u5730\u662f\u7f57\u4e1d\u795e\u50cf\uff0c\u745e\u8d39\u5fb7\u5bb6\u7684\u8fd9\u4e00\u5ea7\u9020\u578b\u4e5f\u6beb\u65e0\u7279\u522b\u4e4b\u5904\uff0c\u4f46\u5728\u743c\u6069\u7684\u5965\u672f\u89c6\u89c9\u91cc\uff0c\u5b83\u6b64\u523b\u6b63\u95ea\u70c1\u7740\u523a\u76ee\u5230\u4e0d\u80fd\u6b63\u89c6\u7684\u7075\u5149\u3002\u5343\u4e07\u9053\u9b54\u6cd5\u7075\u7ebf\u4ea4\u7ec7\u7eb7\u9519\uff0c\u4ece\u56db\u9762\u516b\u65b9\u5ef6\u4f38\u8fc7\u6765\uff0c\u6c47\u805a\u5728\u795e\u50cf\u4e4b\u4e0a\u3002</p><p>\u4e00\u4f4d\u5973\u6027\u7267\u5e08\u6b63\u5728\u795e\u50cf\u524d\u7948\u7977\uff0c\u5979\u7684\u670d\u9970\u662f\u9576\u7740\u7d2b\u8fb9\u7684\u9ed1\u888d\uff0c\u8bf4\u660e\u4f4d\u9636\u5f88\u9ad8\u3002\u8fd9\u662f\u9762\u5bf9\u7267\u5e08\u7684\u4e00\u5927\u597d\u5904\uff0c\u7edd\u5927\u591a\u6570\u6559\u4f1a\uff0c\u90fd\u6709\u4e00\u5957\u6bd4\u8f83\u4e25\u683c\u7684\u4f4d\u9636\u4f53\u7cfb\uff0c\u5e76\u4e14\u5728\u670d\u9970\u4e0a\u5f80\u5f80\u4f1a\u4f53\u73b0\u51fa\u6765\uff0c\u4e00\u822c\u60c5\u51b5\u4e0b\u53ef\u4ee5\u501f\u6b64\u5224\u65ad\u5bf9\u624b\u7684\u4f4d\u9636\u9ad8\u4f4e\uff0c\u80fd\u529b\u5f3a\u5f31\u3002\u7f57\u4e1d\u7684\u7267\u5e08\uff0c\u9ad8\u9636\u8005\u6709\u6743\u7a7f\u9576\u7d2b\u8fb9\u6216\u8005\u6697\u7ea2\u8fb9\u7684\u9ed1\u888d\uff0c\u4f4e\u9636\u8005\u5219\u76f8\u53cd\uff0c\u7a7f\u9576\u9ed1\u8fb9\u7684\u6697\u7ea2\u8272\u6216\u8005\u7d2b\u8272\u957f\u888d\uff0c\u4e00\u671b\u4fbf\u77e5\u3002</p><p>\u5728\u7267\u5e08\u7684\u8eab\u65c1\uff0c\u7ad9\u7740\u4e00\u4e2a\u53e4\u602a\u7684\u5bb6\u4f19\uff0c\u8db3\u8db3\u6709\u516b\u82f1\u5c3a\u9ad8\uff0c\u4e0a\u534a\u8eab\u770b\u8d77\u6765\u662f\u4e2a\u5f3a\u5065\u7684\u5353\u5c14\u7537\u6027\uff0c\u4e0b\u534a\u8eab\u5374\u662f\u5c71\u7f8a\u822c\u7684\u957f\u817f\u3002\u5b83\u76ae\u80a4\u4e4c\u9ed1\uff0c\u5168\u8eab\u808c\u8089\u5757\u5792\uff0c\u4e00\u5934\u6d53\u5bc6\u767d\u53d1\u7565\u5e26\u9ec4\u8272\uff0c\u76ae\u80a4\u770b\u8d77\u6765\u5341\u5206\u575a\u97e7\uff0c\u5e76\u4e14\u8986\u76d6\u7740\u4e00\u5c42\u767d\u8272\u7684\u7ec6\u6bdb\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u5b83\u6709\u56db\u53ea\u624b\uff0c\u4e0a\u65b9\u6bd4\u8f83\u5f3a\u58ee\u7684\u4e00\u5bf9\u624b\u4e0a\u957f\u4e86\u950b\u9510\u7684\u5229\u722a\uff0c\u4e0b\u65b9\u6bd4\u8f83\u77ed\u5c0f\u7684\u4e00\u5bf9\u624b\u5219\u548c\u5353\u5c14\u65e0\u5f02\u3002\u602a\u7269\u7684\u8138\u5411\u524d\u7a81\u51fa\uff0c\u6ee1\u53e3\u5c16\u5229\u7684\u957f\u7259\uff0c\u914d\u5408\u4e0a\u659c\u6311\u7684\u773c\u775b\u548c\u5c16\u957f\u7684\u8033\u6735\uff0c\u8c61\u662f\u5353\u5c14\u548c\u72fc\u7684\u6df7\u8840\u3002</p><p>\u8fd9\u4e00\u5b9a\u662f\u67d0\u79cd\u90aa\u6076\u7684\u975e\u81ea\u7136\u9020\u7269\uff0c\u743c\u6069\u60f3\uff0c\u4ed6\u518d\u770b\u4e86\u4e00\u4f1a\uff0c\u6084\u58f0\u628a\u91cc\u9762\u7684\u60c5\u666f\u544a\u8bc9\u7ef4\u5eb7\u5c3c\u4e9a\u3002</p><p>\u201c\u4ed6\u4eec\u62ff\u795e\u540e\u7684\u795e\u50cf\u505a\u7ed3\u70b9\uff1f\u201d\u5979\u6709\u4e9b\u4e0d\u6562\u7f6e\u4fe1\u5730\u95ee\u3002</p><p>\u201c\u7ed3\u70b9\u8bbe\u5728\u5185\u90e8\uff0c\u88ab\u795e\u50cf\u4fdd\u62a4\u7740\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u8981\u60f3\u53bb\u53cd\u5236\u6d88\u89e3\u8fd9\u4e2a\u7ed3\u70b9\uff0c\u5c31\u5f97\u5148\u628a\u795e\u50cf\u7ed9\u6bc1\u4e86\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u5012\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u72e1\u733e\uff01\u201d\u5979\u4e0d\u65e0\u94a6\u4f69\u5730\u8bf4\uff0c\u201c\u4f46\u8fd9\u662f\u4eb5\u6e0e\uff0c\u5bb6\u65cf\u91cc\u7684\u7267\u5e08\u600e\u4e48\u4f1a\u5141\u8bb8\uff1f\u201d</p><p>\u201c\u7267\u5e08\u4eec\u672a\u5fc5\u77e5\u9053\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6216\u8005\uff0c\u4e00\u5f00\u59cb\u5e76\u975e\u5982\u6b64\uff0c\u53ea\u662f\u53d1\u89c9\u795e\u540e\u6c89\u5bc2\uff0c\u7136\u540e\u624d\u8f6c\u79fb\u5230\u8fd9\u91cc\u3002\u8fd9\u90fd\u6709\u53ef\u80fd\u3002\u201d</p><p>\u201c\u90a3\u6211\u4eec\u73b0\u5728\u600e\u4e48\u505a\uff1f\u201d</p><p>\u201c\u8fdb\u53bb\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u65e2\u7136\u90fd\u5df2\u7ecf\u5230\u4e86\u8fd9\u91cc\uff0c\u96be\u9053\u8fd8\u56de\u53bb\u4e0d\u6210\u2026\u2026\u4e0d\u8fc7\u4ed6\u4eec\u6709\u56db\u4e2a\u4eba\uff0c\u5012\u662f\u6bd4\u8f83\u9ebb\u70e6\u3002\u201d</p><p>\u4e00\u4f4d\u9ad8\u9636\u7267\u5e08\uff0c\u4e00\u4f4d\u770b\u8d77\u6765\u5c31\u5f88\u51f6\u608d\u66b4\u529b\u7684\u602a\u7269\uff0c\u8fd8\u6709\u95e8\u53e3\u7684\u4e24\u4e2a\u5b88\u536b\u3002\u4ee5\u4e8c\u654c\u56db\uff0c\u743c\u6069\u6ca1\u6709\u628a\u63e1\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u6c89\u541f\u7740\uff0c\u201c\u90a3\u4e2a\u7267\u5e08\u7684\u957f\u76f8\uff0c\u6709\u4ec0\u4e48\u7279\u70b9\u5417\uff1f\u201d</p><p>\u201c\u770b\u4e0d\u5230\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5979\u80cc\u5bf9\u7740\u6211\u7684\u65b9\u5411\u5462\u3002\u201d</p><p>\u201c\u5979\u7684\u86c7\u97ad\u662f\u51e0\u9996\u7684\uff1f\u201d</p><p>\u743c\u6069\u518d\u5ea6\u770b\u53bb\uff0c\u53d1\u73b0\u90a3\u4e2a\u7267\u5e08\u4f9d\u65e7\u5728\u7948\u7977\uff0c\u86c7\u97ad\u653e\u5728\u8eab\u65c1\uff0c\u51e0\u6761\u8272\u5f69\u6591\u6593\u7684\u6bd2\u86c7\u61d2\u6d0b\u6d0b\u5730\u7ede\u7f20\u5728\u4e00\u8d77\uff0c\u201c\u4e00\u3001\u4e8c\u3001\u4e09\u2026\u2026\u516d\uff0c\u201d\u4ed6\u597d\u4e0d\u5bb9\u6613\u624d\u6570\u6e05\u695a\uff0c\u201c\u516d\u9996\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u773c\u775b\u4e00\u4eae\uff0c\u201c\u662f\u59ae\u745f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u59ae\u745f\xb7\u745e\u8d39\u5fb7\uff0c\u5979\u4eec\u7684\u73b0\u4efb\u4e3b\u6bcd\u3002\u201d</p><p>\u201c\u4f60\u8ba4\u8bc6\uff1f\u201d</p><p>\u201c\u81ea\u7136\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u5728\u6211\u8fdb\u5165\u796d\u7940\u5b66\u9662\u7684\u90a3\u4e00\u5e74\uff0c\u5979\u6070\u597d\u6bd5\u4e1a\uff0c\u800c\u4e14\u662f\u7b2c\u4e00\u540d\uff0c\u201d\u5979\u683c\u683c\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u5979\u65c1\u8fb9\u662f\u4e0d\u662f\u6709\u4e00\u4e2a\u9b54\u88d4\u5353\u5c14\u3002\u201d</p><p>\u201c\u9b54\u88d4\u5353\u5c14\uff1f\u201d\u743c\u6069\u91cd\u590d\u7740\u8fd9\u4e2a\u8bcd\uff0c\u4ed6\u77e5\u9053\u4e86\u90a3\u4e2a\u602a\u7269\u7684\u540d\u5b57\uff0c\u201c\u662f\u6709\u4e00\u4e2a\uff0c\u5728\u5979\u8eab\u8fb9\u3002\u201d</p><p>\u201c\u90a3\u662f\u5979\u513f\u5b50\u3002\u201d</p><p>\u201c\u513f\u5b50\uff1f\u201d\u743c\u6069\u5927\u5403\u4e00\u60ca\uff0c\u867d\u7136\u6ca1\u770b\u5230\u59ae\u745f\u7684\u6b63\u9762\uff0c\u4f46\u4ece\u8eab\u5f62\u5224\u65ad\uff0c\u5e94\u8be5\u81f3\u5c11\u4e5f\u662f\u79c0\u4e3d\u7684\u5353\u5c14\u5973\u6027\uff0c\u600e\u4e48\u4f1a\u751f\u51fa\u8fd9\u79cd\u5947\u5f62\u602a\u72b6\u7684\u513f\u5b50\u3002</p><p>\u201c\u4ed6\u7684\u7236\u4eb2\u662f\u8c01\uff1f\u201d</p><p>\u201c\u4e00\u53ea\u8ff7\u8bf1\u9b54\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u796d\u7940\u5b66\u9662\u7684\u6bcf\u5c4a\u6bd5\u4e1a\u5178\u793c\u4e0a\uff0c\u90fd\u4f1a\u4e3e\u884c\u4e00\u4e2a\u795e\u5723\u4eea\u5f0f\u3002\u6210\u7ee9\u6700\u4f18\u79c0\u7684\u6bd5\u4e1a\u751f\uff0c\u5c06\u4f1a\u88ab\u8d50\u4e88\u795e\u540e\u7684\u795d\u798f\uff0c\u5979\u4ece\u6df1\u6e0a\u4e2d\u53ec\u5524\u4e00\u53ea\u8ff7\u8bf1\u9b54\uff0c\u5e76\u4e14\u4e0e\u4e4b\u4ea4\u5408\u3002\u6709\u5341\u5206\u4e4b\u4e00\u7684\u51e0\u7387\uff0c\u8fd9\u4e2a\u65b0\u664b\u7267\u5e08\u5c06\u4f1a\u53d7\u5b55\uff0c\u751f\u51fa\u9b54\u88d4\u5353\u5c14\u3002\u201d</p><p>\u743c\u6069\u611f\u89c9\u80cc\u4e0a\u5192\u51b7\u6c14\uff0c\u4ed6\u662f\u89c1\u8fc7\u8ff7\u8bf1\u9b54\u7684\uff0c\u4ee5\u524d\u5728\u5de8\u9b54\u5c71\u8109\u91cc\u5bf9\u4ed8\u5973\u6740\u624b\u838e\u73de\u514b\u7684\u65f6\u5019\uff0c\u5bf9\u65b9\u5c31\u53ec\u5524\u6765\u4e86\u4e24\u53ea\u8ff7\u8bf1\u9b54\uff0c\u8ffd\u5f97\u4ed6\u4ed3\u7687\u9003\u7a9c\uff0c\u6700\u540e\u662f\u9760\u7f29\u7269\u672f\u624d\u4fa5\u5e78\u83b7\u80dc\u3002\u90a3\u79cd\u6709\u7740\u72d7\u7684\u8111\u888b\u548c\u8783\u87f9\u5de8\u87af\u7684\u6076\u9b54\uff0c\u65e0\u8bba\u4ee5\u4eba\u7c7b\u6216\u8005\u5353\u5c14\u7684\u5ba1\u7f8e\u89c2\u6765\u770b\u90fd\u80af\u5b9a\u662f\u4e11\u964b\u5230\u4e86\u6781\u70b9\uff0c\u6ca1\u60f3\u5230\u5c45\u7136\u8fd8\u6709\u5353\u5c14\u5973\u6027\u548c\u8ff7\u8bf1\u9b54\u4ea4\u5408\uff0c\u8bde\u4e0b\u540e\u4ee3\u2014\u2014\u6309\u7167\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8bf4\u6cd5\uff0c\u8fd9\u8fd8\u662f\u65e0\u4e0a\u7684\u8363\u8000\uff0c\u662f\u7b2c\u4e00\u540d\u6bd5\u4e1a\u751f\u624d\u6709\u8d44\u683c\u83b7\u5f97\u7684\u795e\u540e\u795d\u798f\u3002</p><p>\u6709\u751f\u4ee5\u6765\uff0c\u743c\u6069\u7b2c\u4e00\u6b21\u611f\u89c9\u81ea\u5df1\u7684\u60f3\u8c61\u529b\u539f\u6765\u662f\u5982\u6b64\u8d2b\u4e4f\u3002</p><p>\u201c\u518d\u8fc7\u4e09\u5929\uff0c\u5c31\u662f\u795e\u540e\u7684\u5723\u65e5\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u796d\u7940\u5b66\u9662\u4f1a\u5728\u8fd9\u4e00\u5929\u4e3e\u884c\u6bd5\u4e1a\u5178\u793c\u3002\u5982\u679c\u4f60\u5bf9\u6b64\u6709\u5174\u8da3\uff0c\u201d\u5979\u7b11\u541f\u541f\u5730\u770b\u7740\u743c\u6069\uff0c\u201c\u6211\u53ef\u4ee5\u5e26\u4f60\u53bb\u89c1\u8bc6\u89c1\u8bc6\uff0c\u76f8\u4fe1\u6211\uff0c\u90a3\u4e00\u5b9a\u4f1a\u7ed9\u4f60\u7559\u4e0b\u7cbe\u5f69\u96be\u5fd8\u7684\u56de\u5fc6\u3002\u201d</p><p>\u201c\u9b54\u88d4\u5353\u5c14\u5f88\u96be\u5bf9\u4ed8\u4e48\u3002\u201d\u743c\u6069\u56de\u907f\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u63d0\u8bae\u3002</p><p>\u201c\u633a\u9ebb\u70e6\u7684\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u70b9\u5934\uff0c\u201c\u5b83\u517c\u6709\u6076\u9b54\u7684\u5f3a\u608d\u548c\u5353\u5c14\u7684\u7075\u5de7\uff0c\u8111\u888b\u4e5f\u4e0d\u7b28\uff0c\u65e2\u662f\u4e2a\u5f3a\u58ee\u7684\u6218\u58eb\uff0c\u4e5f\u80fd\u4f7f\u7528\u4e00\u4e9b\u6cd5\u672f\u3002\u901a\u5e38\u6765\u8bf4\uff0c\u4e00\u4e2a\u9b54\u88d4\u5353\u5c14\uff0c\u8db3\u53ef\u62b5\u5f97\u4e0a\u5341\u4e2a\u7ecf\u9a8c\u4e30\u5bcc\u7684\u6218\u58eb\u3002\u201d</p><p>\u80fd\u591f\u62b5\u5f97\u4e0a\u5341\u4e2a\u7ecf\u9a8c\u4e30\u5bcc\u7684\u6218\u58eb\uff0c\u610f\u601d\u5e76\u4e0d\u662f\u8bf4\u80fd\u591f\u4ee5\u4e00\u654c\u5341\uff0c\u8fd9\u70b9\u743c\u6069\u5012\u4e5f\u5f88\u6e05\u695a\u3002\u867d\u7136\u5982\u6b64\uff0c\u90a3\u4e5f\u662f\u6781\u5176\u96be\u7f20\u7684\u5bf9\u624b\u4e86\uff0c\u81f3\u5c11\u540c\u65f6\u5bf9\u654c\u4e09\u56db\u4e2a\u6218\u58eb\u6ca1\u6709\u95ee\u9898\u3002</p><p>\u201c\u4e0d\u8fc7\u5b83\u6bd5\u7adf\u662f\u534a\u6076\u9b54\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u63a5\u7740\u53c8\u8865\u5145\u8bf4\uff0c\u201c\u6211\u5b66\u8fc7\u5bf9\u4ed8\u6076\u9b54\u7684\u6280\u5de7\u3002\u5982\u679c\u4e00\u5bf9\u4e00\uff0c\u4e0d\u51fa\u4ec0\u4e48\u610f\u5916\u7684\u8bdd\uff0c\u6211\u80af\u5b9a\u4e0d\u80fd\u83b7\u80dc\uff0c\u4f46\u5e94\u8be5\u80fd\u652f\u6491\u4e00\u6bb5\u65f6\u95f4\u3002\u201d</p><p>\u201c\u597d\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4eec\u8fdb\u53bb\u3002\u6211\u5bf9\u4ed8\u7267\u5e08\uff0c\u4f60\u53bb\u5bf9\u4ed8\u90a3\u4e2a\u9b54\u88d4\u5353\u5c14\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u7a7f\u7740\u4f8d\u7236\u6597\u7bf7\uff0c\u8fd9\u4e1c\u897f\u867d\u7136\u80fd\u591f\u63d0\u4f9b\u975e\u5e38\u4f18\u826f\u7684\u9632\u62a4\uff0c\u4f46\u5374\u6709\u4e2a\u8981\u547d\u7684\u7f3a\u9677\uff1a\u5b83\u9644\u5e26\u4e86\u7f57\u4e1d\u7684\u8bc5\u5492\uff0c\u7267\u5e08\u7684\u795e\u672f\u4f1a\u66f4\u5bb9\u6613\u4f24\u5bb3\u5230\u7a7f\u6234\u8005\u3002\u5982\u679c\u8ba9\u7ef4\u5eb7\u5c3c\u4e9a\u53bb\u5bf9\u4ed8\u59ae\u745f\uff0c\u90a3\u5c31\u7eaf\u7cb9\u662f\u81ea\u66dd\u5176\u77ed\u3002\u800c\u743c\u6069\u5bf9\u9b54\u88d4\u5353\u5c14\u8fd9\u79cd\u602a\u7269\u8fd1\u4e4e\u4e00\u65e0\u6240\u77e5\uff0c\u6ca1\u6709\u534a\u70b9\u4e86\u89e3\uff0c\u5bf9\u4ed8\u8d77\u6765\u5fc5\u5b9a\u4e8b\u500d\u529f\u534a\u3002\u6240\u4ee5\u53cd\u8fc7\u6765\uff0c\u8ba9\u7ef4\u5eb7\u5c3c\u4e9a\u62d6\u4f4f\u9b54\u88d4\u5353\u5c14\uff0c\u7b49\u743c\u6069\u51fb\u6740\u4e86\u59ae\u745f\uff0c\u518d\u6765\u5e2e\u52a9\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u8fd9\u662f\u6b63\u786e\u7684\u6218\u672f\u5b89\u6392\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u660e\u767d\u8fd9\u70b9\uff0c\u6ca1\u6709\u53cd\u5bf9\uff0c\u201c\u90a3\u5916\u9762\u4e24\u4e2a\u536b\u5175\u600e\u4e48\u529e\uff1f\u201d</p><p>\u201c\u6211\u4eec\u5148\u6f5c\u8fdb\u53bb\uff0c\u7136\u540e\u6211\u7528\u6cd5\u672f\u628a\u95e8\u5c01\u95ed\u4f4f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4ed6\u4eec\u4e00\u65f6\u534a\u523b\u51b2\u4e0d\u8fdb\u6765\u3002\u6211\u4eec\u901f\u6218\u901f\u51b3\uff0c\u5148\u89e3\u51b3\u4e86\u795e\u5802\u91cc\u7684\u4e24\u4e2a\u4eba\u518d\u8bf4\u3002\u201d</p><p>\u5206\u5272\u654c\u4eba\uff0c\u5404\u4e2a\u51fb\u7834\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u6ca1\u4ec0\u4e48\u610f\u89c1\uff0c\u201c\u53ea\u80fd\u8fd9\u6837\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4e0d\u8fc7\u4f60\u8981\u5c0f\u5fc3\uff0c\u59ae\u745f\u53ef\u662f\u4e2a\u4f18\u79c0\u7684\u7267\u5e08\u3002\u201d</p><p>\u201c\u5514\u3002\u201d\u743c\u6069\u5e76\u4e0d\u5728\u610f\uff0c\u5728\u5353\u5c14\u8fd9\u79cd\u7ade\u4e89\u6781\u5ea6\u6b8b\u9177\u7684\u4e16\u754c\uff0c\u59ae\u745f\u65e2\u7136\u5f53\u5e74\u80fd\u591f\u4ee5\u796d\u7940\u5b66\u9662\u7b2c\u4e00\u540d\u7684\u6210\u7ee9\u6bd5\u4e1a\uff0c\u80fd\u529b\u81ea\u4e0d\u5f85\u8a00\u3002\u53ea\u770b\u5979\u5e74\u9f84\u5e94\u8be5\u4e5f\u4e0d\u6bd4\u7ef4\u5eb7\u5c3c\u4e9a\u5927\u591a\u5c11\uff0c\u86c7\u97ad\u5df2\u7ecf\u662f\u516d\u9996\uff0c\u4e5f\u5c31\u53ef\u89c1\u4e00\u6591\u4e86\u3002\u4f46\u5982\u4eca\u86db\u540e\u6c89\u5bc2\uff0c\u7267\u5e08\u4e27\u5931\u795e\u672f\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u6015\u7684\u2014\u2014\u66f4\u4f55\u51b5\uff0c\u4ed6\u8fd8\u6709\u79d8\u5bc6\u6b66\u5668\u5462\u3002</p><p>\u5bf9\u4ed8\u522b\u4eba\u6216\u8bb8\u6ca1\u628a\u63e1\uff0c\u5bf9\u4ed8\u7267\u5e08\uff0c\u90a3\u5c82\u4e0d\u6b63\u4e2d\u81ea\u5df1\u4e0b\u6000\u4e48\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u89c1\u4ed6\u7b54\u5e94\u5f97\u6f2b\u4e0d\u7ecf\u5fc3\uff0c\u4e0d\u7531\u5f97\u6709\u4e9b\u607c\u6012\uff0c\u201c\u8c28\u614e\u70b9\uff01\u201d\u5979\u5475\u65a5\uff0c\u201c\u59ae\u745f\u53ef\u4e0d\u50cf\u6211\u90a3\u4e24\u4e2a\u59d0\u59d0\u597d\u5bf9\u4ed8\u3002\u201d</p><p>\u201c\u653e\u5fc3\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u89c1\u5979\u5173\u5207\uff0c\u4e0d\u7531\u5f97\u4e5f\u5fae\u5fae\u6709\u4e9b\u611f\u52a8\uff0c\u201c\u5979\u7684\u6cd5\u672f\u5bf9\u4ed8\u4e0d\u4e86\u2026\u2026\u201d</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u4ed6\u9661\u7136\u6014\u4f4f\u4e86\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u89c1\u4ed6\u8bf4\u8bdd\u7a81\u7136\u4e2d\u65ad\uff0c\u9887\u4e3a\u8be7\u5f02\u3002</p><p>\u201c\u4f60\u524d\u9762\u5e2e\u6211\u79fb\u9664\u8bc5\u5492\uff0c\u7528\u7684\u662f\u4ec0\u4e48\u6cd5\u672f\uff1f\u201d</p><p>\u201c\u590d\u539f\u672f\u554a\u3002\u201d</p><p>\u201c\u662f\u795e\u672f\uff1f\u201d</p><p>\u201c\u5e9f\u8bdd\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u4e0d\u60a6\uff0c\u201c\u5965\u672f\u91cc\u54ea\u6709\u590d\u539f\u672f\uff1f\u201d</p><p>\u590d\u539f\u672f\u662f\u6807\u51c6\u7684\u795e\u672f\uff0c\u8fd9\u70b9\u743c\u6069\u81ea\u7136\u662f\u6e05\u695a\u7684\uff0c\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u4ed6\u624d\u4e0d\u6562\u7f6e\u4fe1\uff0c\u8981\u5411\u7ef4\u5eb7\u5c3c\u4e9a\u786e\u8ba4\u3002\u56e0\u4e3a\u8fd9\u610f\u5473\u7740\uff0c\u4ed6\u80fd\u591f\u88ab\u795e\u672f\u6cbb\u7597\u2014\u2014\u4ed6\u5df2\u7ecf\u4e0d\u80fd\u514d\u75ab\u795e\u672f\u4e86\u3002</p><p>\u8fd9\u2026\u2026\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f</p><p>\u9976\u662f\u743c\u6069\u5fc3\u7406\u7d20\u8d28\u518d\u597d\uff0c\u6b64\u65f6\u4e5f\u4e0d\u7981\u6709\u4e9b\u614c\u4e71\u3002\u81ea\u5df1\u80fd\u591f\u5bf9\u795e\u672f\u514d\u75ab\uff0c\u8fd9\u662f\u65e9\u5c31\u53d1\u73b0\u5e76\u4e14\u51e0\u6b21\u9a8c\u8bc1\u7684\u4e8b\u5b9e\uff0c\u600e\u4e48\u4f1a\u7a81\u7136\u53c8\u6539\u53d8\u4e86\u3002\u8fd9\u5c31\u50cf\u65e9\u4e0a\u9192\u6765\uff0c\u7a81\u7136\u53d1\u73b0\u81ea\u5df1\u4e27\u5931\u4e86\u67d0\u9879\u80fd\u529b\uff0c\u4f8b\u5982\u542c\u89c9\u6216\u8005\u89c6\u89c9\uff0c\u90a3\u4e0d\u5927\u60ca\u5931\u63aa\u624d\u602a\u3002</p><p>\u81ea\u5df1\u80fd\u591f\u514d\u75ab\u795e\u672f\uff0c\u8fd9\u662f\u5f88\u65e9\u4ee5\u524d\u7684\u53d1\u73b0\uff0c\u56e0\u4e3a\u5176\u5b9e\u5728\u4ee5\u540e\u7684\u5386\u6b21\u6218\u6597\u4e2d\u6ca1\u6709\u53d1\u6325\u591a\u5927\u7528\u5904\uff0c\u743c\u6069\u4e5f\u6e10\u6e10\u6709\u4e9b\u5ffd\u7565\u4e86\u3002\u521a\u624d\u4ed6\u4e2d\u4e86\u5deb\u5e08\u7684\u8bc5\u5492\uff0c\u6b63\u5982\u7ef4\u5eb7\u5c3c\u4e9a\u6240\u8bf4\uff0c\u90a3\u4e2a\u6cd5\u672f\u4f1a\u8ba9\u4ed6\u8d8a\u6765\u8d8a\u75b2\u60eb\u8870\u5f31\uff0c\u80f8\u53e3\u53c8\u662f\u5728\u4e00\u9635\u9635\u5730\u75bc\u75db\u52a0\u5267\uff0c\u6cbb\u7597\u836f\u6c34\u90fd\u4e0d\u8d77\u534a\u70b9\u6548\u679c\uff0c\u6405\u5f97\u743c\u6069\u5fc3\u70e6\u610f\u4e71\uff0c\u7cbe\u795e\u5f88\u5dee\uff0c\u8fde\u8bf4\u8bdd\u90fd\u4e0d\u6d41\u7545\u3002\u6b64\u65f6\u7ef4\u5eb7\u5c3c\u4e9a\u65bd\u5c55\u590d\u539f\u672f\u79fb\u9664\u8bc5\u5492\uff0c\u4ee4\u4ed6\u987f\u65f6\u7b80\u76f4\u6709\u91cd\u83b7\u65b0\u751f\u7684\u611f\u89c9\uff0c\u6ee1\u8154\u6109\u5feb\uff0c\u538b\u6839\u5c31\u6ca1\u60f3\u8d77\u6765\u4ed6\u201c\u6309\u9053\u7406\u201d\u662f\u6ca1\u529e\u6cd5\u63a5\u53d7\u795e\u672f\u6cbb\u7597\u7684\u3002\u7136\u540e\u7d27\u63a5\u7740\u6ce8\u610f\u529b\u53c8\u88ab\u7ef4\u5eb7\u5c3c\u4e9a\u548c\u745e\u8d39\u5fb7\u5deb\u5e08\u7684\u6843\u8272\u516b\u5366\u7ed9\u5438\u5f15\u53bb\u4e86\uff0c\u4e09\u8f6c\u4e24\u6298\uff0c\u5168\u7136\u5fd8\u8bb0\u4e86\u8fd8\u6709\u8fd9\u832c\u3002\u76f4\u5230\u521a\u624d\u63d0\u8d77\uff0c\u624d\u7a81\u7136\u53cd\u5e94\u8fc7\u6765\u3002</p><p>\u6df1\u6df1\u547c\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u743c\u6069\u52c9\u5f3a\u8ba9\u81ea\u5df1\u9547\u5b9a\u4e0b\u6765\uff0c\u5728\u8111\u4e2d\u6025\u901f\u56de\u5fc6\u7740\u3002\u5982\u679c\u8bb0\u5f97\u6ca1\u9519\uff0c\u4e3a\u4e86\u627e\u6b7b\u6708\u6cd5\u73e0\uff0c\u81ea\u5df1\u5728\u963f\u683c\u62c9\u9686\u7684\u65f6\u5019\u66fe\u7ecf\u548c\u90a3\u4e2a\u7a83\u6cd5\u8005\u4ea4\u624b\uff0c\u5f53\u65f6\u8fd8\u662f\u80fd\u514d\u75ab\u795e\u672f\u7684\uff0c\u8fd9\u786e\u5b9a\u65e0\u7591\u3002\u7136\u540e\u4fbf\u56de\u5230\u9634\u9b42\u57ce\uff0c\u57fa\u672c\u6ca1\u6709\u548c\u4eba\u52a8\u624b\u8fc7\uff0c\u9664\u4e86\u5e2e\u6885\u83f2\u65af\u548c\u7ad6\u7434\u624b\u6253\u53d1\u4e86\u51e0\u4e2a\u9f99\u5deb\u6559\u5f92\uff0c\u5f53\u65f6\u4e5f\u662f\u76f4\u63a5\u7528\u8be1\u8ba1\u53d6\u80dc\u3002\u540e\u6765\u8fdb\u5165\u5e7d\u6697\u5730\u57df\uff0c\u653b\u4e0b\u5965\u56fe\u57ce\uff0c\u56e0\u4e3a\u81ea\u5df1\u7684\u4efb\u52a1\u662f\u5bfb\u627e\u9b54\u50cf\u5236\u9020\u57fa\u5730\uff0c\u4e5f\u4e0d\u66fe\u968f\u519b\u53c2\u6218\uff0c\u518d\u8bf4\u662f\u5bf9\u4ed8\u773c\u9b54\u3002\u7d27\u63a5\u7740\u4ece\u5965\u56fe\u57ce\u51fa\u53d1\uff0c\u4e00\u8def\u6765\u5230\u8fd9\u5ea7\u57ce\u5e02\uff0c\u9014\u4e2d\u8299\u8389\u5a05\u867d\u7136\u968f\u884c\uff0c\u5374\u4ece\u6ca1\u6709\u5411\u81ea\u5df1\u65bd\u5c55\u8fc7\u795e\u672f\uff0c\u5728\u4f0a\u5361\u6c99\u57ce\u4e0b\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u4ea4\u950b\u4e00\u6b21\uff0c\u4f46\u5f53\u65f6\u5353\u5c14\u5c11\u5973\u7528\u7684\u662f\u5965\u672f\u3002\u518d\u5f80\u540e\u7b97\uff0c\u6765\u5230\u83f2\u5c14\u4f26\u5bb6\u65cf\uff0c\u5bf9\u4ed8\u4e3d\u5965\u548c\u8725\u8734\u9a91\u5175\u7684\u65f6\u5019\uff0c\u76f4\u63a5\u7528\u78c1\u5316\u672f\u548c\u5316\u77f3\u4e3a\u6ce5\u9001\u5979\u4eec\u53bb\u4e86\u5730\u5e95\uff1b\u5728\u5974\u96b6\u53db\u4e71\u4e2d\uff0c\u81ea\u5df1\u4e3b\u8981\u8eb2\u5728\u65c1\u8fb9\u89c2\u6218\uff0c\u6700\u540e\u624d\u548c\u7075\u5438\u602a\u5deb\u5996\u6709\u8fc7\u77ed\u6682\u4ea4\u624b\u3002\u7136\u540e\u5c31\u662f\u8fd9\u6b21\u8fdb\u653b\u745e\u8d39\u5fb7\u5bb6\u65cf\u4e86\uff0c\u521a\u6f5c\u5165\u65f6\u649e\u4e0a\u5de1\u903b\u961f\uff0c\u867d\u7136\u4e2d\u4e86\u7267\u5e08\u7684\u4e00\u51fb\u9eef\u708e\uff0c\u4f46\u5f53\u65f6\u81ea\u5df1\u76f4\u63a5\u542f\u52a8\u6cd5\u672f\u9006\u8f6c\u6212\u6307\u628a\u5b83\u53cd\u5f39\u4e86\u56de\u53bb\uff0c\u4e5f\u65e0\u4ece\u9a8c\u8bc1\u6548\u679c\u3002\u518d\u540e\u6765\u548c\u745e\u8d39\u5fb7\u5deb\u5e08\u4ea4\u624b\uff0c\u5965\u672f\u548c\u5965\u672f\u7684\u5bf9\u51b3\uff0c\u867d\u7136\u83b7\u80dc\uff0c\u5374\u4e2d\u4e86\u8bc5\u5492\uff0c\u6700\u540e\u5c31\u662f\u7ef4\u5eb7\u5c3c\u4e9a\u4e3a\u81ea\u5df1\u65bd\u5c55\u590d\u539f\u672f\u79fb\u9664\u8bc5\u5492\u3002</p><p>\u7b97\u6765\u7b97\u53bb\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5728\u56de\u9634\u9b42\u57ce\u4e4b\u524d\uff0c\u81ea\u5df1\u786e\u5b9a\u8fd8\u662f\u80fd\u591f\u514d\u75ab\u795e\u672f\u7684\uff0c\u800c\u534a\u5c0f\u65f6\u524d\uff0c\u81ea\u5df1\u5374\u53d1\u73b0\u4e27\u5931\u4e86\u8fd9\u79cd\u7279\u6027\u2014\u2014\u5728\u8fd9\u6bb5\u5927\u7ea6\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\u91cc\uff0c\u5728\u743c\u6069\u81ea\u5df1\u90fd\u6ca1\u6709\u5bdf\u89c9\u5230\u7684\u60c5\u51b5\u4e0b\uff0c\u67d0\u79cd\u6539\u53d8\u6084\u6084\u5730\u53d1\u751f\u4e86\u3002</p><p>\u5230\u5e95\u662f\u4ec0\u4e48\u65f6\u5019\u53d1\u751f\u4e86\u8fd9\u79cd\u6539\u53d8\uff1f</p><p>\u201c\u96be\u9053\u662f\u56e0\u4e3a\u4ece\u83f2\u5a05\u90a3\u91cc\u5438\u6536\u4e86\u5df4\u5c14\u795e\u529b\uff1f\u201d</p><p>\u743c\u6069\u77e5\u9053\uff0c\u81ea\u5df1\u8fd9\u79cd\u514d\u75ab\u795e\u672f\u7684\u7279\u6027\u662f\u81ea\u7136\u5c31\u6709\u7684\uff0c\u5b83\u662f\u4e00\u79cd\u4e0e\u751f\u4ff1\u6765\u7684\u201c\u5929\u8d4b\u201d\uff0c\u5e76\u4e0d\u662f\u540e\u5929\u953b\u70bc\u51fa\u6765\u7684\u201c\u6280\u80fd\u201d\u3002\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4e48\u56de\u5fc6\u8fd9\u6bb5\u65f6\u95f4\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u6700\u6709\u53ef\u80fd\u6539\u53d8\u81ea\u5df1\u7684\u201c\u5929\u8d4b\u201d\u7684\uff0c\u663e\u7136\u5c31\u662f\u5438\u6536\u5df4\u5c14\u795e\u529b\u7684\u4e8b\u60c5\u4e86\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u522b\u7684\u4e8b\u60c5\u4f3c\u4e4e\u90fd\u53ef\u80fd\u6027\u4e0d\u9ad8\u3002</p><p>\u5438\u6536\u4e86\u5df4\u5c14\u795e\u529b\uff0c\u4ee4\u81ea\u5df1\u7684\u8eab\u4f53\u8f83\u4ee5\u5f80\u66f4\u52a0\u5f3a\u5065\uff0c\u5728\u9b54\u6cd5\u4e0a\u8fdb\u6b65\u795e\u901f\uff0c\u5728\u5e8a\u4e0a\u7684\u6301\u4e45\u529b\u589e\u5f3a\u2026\u2026\u800c\u4e14\u4f3c\u4e4e\u4e5f\u66f4\u52a0\u7c97\u66b4\u4e86\u3002\u8fd9\u4e9b\u90fd\u662f\u5bf9\u8089\u4f53\u6216\u8005\u7cbe\u795e\u7684\u660e\u663e\u6539\u9020\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u5b83\u6084\u6084\u4e5f\u6539\u53d8\u4e86\u81ea\u5df1\u514d\u75ab\u795e\u672f\u7684\u7279\u6027\uff0c\u8fd9\u4e5f\u4f3c\u4e4e\u5b8c\u5168\u8bf4\u5f97\u901a\u3002</p><p>\u4e0d\uff0c\u4e0d\u5bf9\uff0c\u743c\u6069\u6447\u5934\uff0c\u81ea\u5df1\u5728\u70db\u5821\u7684\u65f6\u5019\uff0c\u4e5f\u66fe\u7ecf\u4ece\u5973\u6740\u624b\u838e\u73de\u514b\u90a3\u91cc\u5438\u6536\u8fc7\u4e00\u70b9\u5df4\u5c14\u795e\u529b\uff0c\u4e3a\u4ec0\u4e48\u6ca1\u6709\u56e0\u6b64\u800c\u4e27\u5931\u5bf9\u795e\u672f\u7684\u514d\u75ab\uff1f\u96be\u4e0d\u6210\u56e0\u4e3a\u90a3\u6b21\u5438\u6536\u7684\u795e\u529b\u592a\u5c11\uff0c\u6240\u4ee5\u6ca1\u6709\u6548\u679c\uff0c\u540e\u6765\u5728\u83f2\u5a05\u8eab\u4e0a\u5438\u6536\u5f97\u8f83\u591a\uff0c\u6240\u4ee5\u53d1\u751f\u6539\u53d8\uff1f\u8fd9\u79cd\u89e3\u91ca\u4e5f\u672a\u514d\u592a\u6ed1\u7a3d\u4e86\uff0c\u600e\u4e48\u770b\u4e5f\u4e0d\u592a\u53ef\u80fd\u7684\u6837\u5b50\u554a\u3002</p><p>\u5230\u5e95\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u5462\uff1f</p><p>\u743c\u6069\u76b1\u7709\u601d\u7d22\u7740\uff0c\u4e00\u65f6\u4e0d\u5f97\u5176\u89e3\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u5947\u602a\u5730\u770b\u7740\u4ed6\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e2a\u4eba\u7c7b\u5230\u5e95\u600e\u4e48\u4e86\uff0c\u539f\u672c\u8fd8\u5728\u7cbe\u795e\u5955\u5955\u5730\u5206\u6790\u5c40\u52bf\uff0c\u5b89\u6392\u6218\u672f\uff0c\u7a81\u7136\u95f4\u5c31\u50cf\u88ab\u4eba\u5f53\u5934\u6572\u4e86\u4e00\u68d2\u4f3c\u7684\uff0c\u4e0d\u77e5\u9053\u5728\u8fdf\u7591\u72b9\u8c6b\u4e9b\u4ec0\u4e48\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u7267\u5e08\u6216\u8bb8\u4f1a\u9976\u6709\u5174\u81f4\u5730\u770b\u770b\u4ed6\u5230\u5e95\u641e\u4ec0\u4e48\u540d\u5802\uff0c\u53cd\u6b63\u5353\u5c14\u5bff\u547d\u957f\uff0c\u6709\u7684\u662f\u65f6\u95f4\u2014\u2014\u4f46\u73b0\u5728\u4ed6\u4eec\u53ef\u6ca1\u6709\u65f6\u95f4\u3002</p><p>\u201c\u8d70\u554a\uff0c\u201d\u5979\u6084\u58f0\u8bf4\uff0c\u201c\u4f60\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u4e00\u51db\uff0c\u53cd\u5e94\u8fc7\u6765\uff0c\u795e\u672f\u514d\u75ab\u7684\u4e8b\u60c5\u4e14\u5148\u653e\u4e0b\uff0c\u4ee5\u540e\u518d\u6162\u6162\u7422\u78e8\uff0c\u5982\u4eca\u81ea\u5df1\u8eab\u5728\u654c\u8425\uff0c\u53ef\u4e07\u4e07\u4e0d\u80fd\u5206\u795e\uff0c\u5426\u5219\u6027\u547d\u4e0d\u4fdd\u3002\u5c31\u7b97\u6709\u5929\u5927\u7684\u4e8b\u60c5\uff0c\u4e5f\u5148\u628a\u773c\u524d\u8fd9\u4e00\u4ed7\u6253\u5b8c\u518d\u8bf4\uff0c\u6b63\u5982\u4ed6\u8bf4\u7684\uff0c\u90fd\u5df2\u7ecf\u597d\u4e0d\u5bb9\u6613\u8d70\u5230\u8fd9\u91cc\u4e86\uff0c\u96be\u9053\u8fd8\u5c31\u6b64\u9000\u7f29\u4e0d\u6210\uff1f</p><p>\u201c\u6211\u4eec\u8fdb\u53bb\u3002\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u5728\u9634\u5f71\u4e2d\u6162\u6162\u7ad9\u76f4\u8eab\u4f53\u3002</p></article>"}}}]);
