(window.webpackJsonp=window.webpackJsonp||[]).push([[1472],{1495:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e94\u8282 \u5927\u88c2\u89e3\u3001\u65f6\u95f4\u505c\u6b62",content:"<article><h2>\u7b2c\u4e09\u5341\u4e94\u8282 \u5927\u88c2\u89e3\u3001\u65f6\u95f4\u505c\u6b62</h2><p>\u743c\u6069\u4e0d\u8ba4\u8bc6\u514b\u52b3\u62d3\uff0c\u4f46\u4e5f\u4e5f\u4e45\u95fb\u5176\u540d\u4e86\u3002\u6d3b\u4e86\u5c06\u8fd1\u4e5d\u767e\u5e74\u7684\u9ed1\u6697\u7cbe\u7075\uff0c\u7ad9\u5728\u4e3b\u6bcd\u5b9d\u5ea7\u4e4b\u540e\u7684\u745e\u8d39\u5fb7\u5bb6\u65cf\u7edf\u6cbb\u8005\uff0c\u6700\u8d1f\u58f0\u671b\u7684\u70bc\u91d1\u5e08\u2014\u2014\u5f88\u591a\u4eba\u751a\u81f3\u8ba4\u4e3a\u4ed6\u624d\u662f\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u7b2c\u4e00\u5deb\u5e08\uff0c\u800c\u4e0d\u662f\u73b0\u4efb\u5965\u672f\u5b66\u9662\u7684\u9886\u8896\u4e9a\u5f53\u65af\u3002\u5bf9\u4e8e\u8fd9\u4e2a\u8bdd\u9898\uff0c\u636e\u8bf4\u5f88\u591a\u4e3b\u6bcd\u90fd\u975e\u5e38\u597d\u5947\uff0c\u66fe\u7ecf\u63d0\u8bae\u8ba9\u4e24\u4f4d\u5deb\u5e08\u6765\u4e00\u6b21\u51b3\u6597\uff0c\u4f46\u5728\u4e24\u4e2a\u5bb6\u65cf\u4e3b\u6bcd\u7684\u540c\u58f0\u53cd\u5bf9\u4e0b\u6700\u540e\u4f5c\u7f62\u3002\u4f46\u6709\u4e00\u70b9\u662f\u4e0d\u80fd\u5ffd\u7565\u7684\uff0c\u514b\u52b3\u62d3\u7684\u5e74\u9f84\u662f\u4e9a\u5f53\u65af\u7684\u4e24\u500d\uff0c\u5f53\u5e74\u4ed6\u8f9e\u53bb\u5965\u672f\u5b66\u9662\u5bfc\u5e08\u804c\u4f4d\u65f6\uff0c\u4e9a\u5f53\u65af\u8fd8\u6ca1\u51fa\u751f\u5462\u3002</p><p>\u5f53\u7136\uff0c\u5e74\u9f84\u3001\u8d44\u5386\uff0c\u8fd9\u4e1c\u897f\u53ea\u80fd\u53c2\u8003\uff0c\u4e0d\u80fd\u4ee3\u8868\u4e00\u5207\u3002\u5982\u679c\u8bf4\u6d3b\u5f97\u4e45\u5c31\u5389\u5bb3\uff0c\u90a3\u4e4c\u9f9f\u65e9\u5c31\u79f0\u9738\u5929\u4e0b\u4e86\uff0c\u743c\u6069\u8fd9\u8f88\u5b50\u8fde\u540c\u4e0a\u8f88\u5b50\uff0c\u52a0\u8d77\u6765\u4e5f\u4e0d\u8d85\u8fc7\u56db\u5341\u5c81\uff0c\u5728\u8fd9\u5ea7\u5353\u5c14\u57ce\u5e02\u91cc\u9047\u5230\u7684\u654c\u4eba\uff0c\u54ea\u4e2a\u4e0d\u6bd4\u4ed6\u5e74\u957f\u51e0\u500d\u751a\u81f3\u5341\u51e0\u500d\uff0c\u5c31\u8fde\u7ef4\u5eb7\u5c3c\u4e9a\u90fd\u6709\u4e0a\u767e\u5c81\uff0c\u4f46\u5982\u4eca\u4ed6\u8fd8\u597d\u7aef\u7aef\u7ad9\u5728\u8fd9\u91cc\uff0c\u90a3\u4e9b\u5bb6\u4f19\u57fa\u672c\u90fd\u5b8c\u86cb\u4e86\u3002\u53cd\u8fc7\u6765\u8bf4\uff0c\u6885\u83f2\u65af\u7684\u5e74\u9f84\u6bd4\u743c\u6069\u8fd8\u5c0f\uff0c\u4f46\u771f\u8981\u4ea4\u624b\u8d77\u6765\uff0c\u743c\u6069\u81ea\u5ea6\u80dc\u7b97\u4e0d\u4f1a\u8d85\u8fc7\u4e09\u6210\u3002</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u514b\u52b3\u62d3\u5fc5\u5b9a\u662f\u8fd9\u5ea7\u57ce\u5e02\u91cc\u6700\u6709\u529b\u91cf\u7684\u5deb\u5e08\u4e4b\u4e00\u3002\u743c\u6069\u4e0d\u7b97\u80c6\u5c0f\u754f\u7f29\uff0c\u5f88\u591a\u65f6\u5019\u751a\u81f3\u53ef\u4ee5\u79f0\u5f97\u4e0a\u6709\u5192\u9669\u7cbe\u795e\uff0c\u4f46\u5982\u679c\u53ef\u4ee5\u7684\u8bdd\uff0c\u4ed6\u4e5f\u4e0d\u5e0c\u671b\u649e\u4e0a\u8fd9\u79cd\u5bf9\u624b\u3002</p><p>\u53ef\u60dc\u4ed6\u6ca1\u5f97\u9009\u62e9\uff0c\u5982\u4eca\u5bf9\u65b9\u5df2\u7ecf\u81ea\u5df1\u627e\u4e0a\u95e8\u6765\u3002</p><p>\u6ca1\u6709\u591a\u8bf4\u5e9f\u8bdd\uff0c\u4e5f\u6ca1\u6709\u548c\u4e9a\u5f53\u65af\u6162\u6162\u6253\u62db\u547c\uff0c\u514b\u52b3\u62d3\u4e00\u6b65\u8e0f\u5165\u795e\u6bbf\uff0c\u624b\u8155\u6781\u5feb\u901f\u5730\u7ffb\u8f6c\uff0c\u4e0d\u77e5\u4f55\u65f6\u638c\u5fc3\u5df2\u7ecf\u591a\u4e86\u4e00\u4e2a\u767d\u8272\u77f3\u82f1\u7403\u3002\u4ed6\u773c\u5149\u4e00\u626b\uff0c\u5c06\u77f3\u82f1\u7403\u5bf9\u51c6\u6b63\u5728\u548c\u9b54\u88d4\u5353\u5c14\u7ea0\u7f20\u7684\u7ef4\u5eb7\u5c3c\u4e9a\u3002</p><p>\u201c\u5fb7\u62c9\u65af\xb7\u9b54\u4e9a\u82e5\xb7\u8d6b\u65af\uff08Delather myarra heth\uff09\uff01\u201d</p><p>\u201c\u95ea\u5f00\uff01\u201d\u4e9a\u5f53\u65af\u5389\u559d\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u4e00\u9053\u8000\u773c\u5c04\u7ebf\u4ece\u77f3\u82f1\u7403\u4e2d\u53d1\u51fa\uff0c\u867d\u7136\u53ea\u662f\u72ed\u957f\u4e00\u675f\uff0c\u5374\u4eff\u4f5b\u70c8\u9633\u9ad8\u7167\uff0c\u6574\u4e2a\u795e\u5802\u91cc\u5239\u90a3\u95f4\u4eae\u5982\u767d\u663c\u3002\u5c31\u8fde\u743c\u6069\u8fd9\u79cd\u5730\u8868\u4eba\u7c7b\u90fd\u6709\u4e9b\u627f\u53d7\u4e0d\u4f4f\uff0c\u66f4\u522b\u8bf4\u5353\u5c14\uff0c\u4e9a\u5f53\u65af\u89c1\u673a\u5f97\u5feb\uff0c\u65e9\u65e9\u5c06\u9b54\u6597\u7bf7\u7684\u515c\u5e3d\u626f\u4e0b\uff0c\u9694\u4f4f\u5f3a\u5149\uff0c\u59ae\u745f\u548c\u9b54\u88d4\u5353\u5c14\u90fd\u88ab\u523a\u5f97\u53cc\u76ee\u6d41\u6cea\uff0c\u4e0d\u80fd\u89c6\u7269\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u9996\u5f53\u5176\u51b2\uff0c\u88ab\u5149\u675f\u51fb\u4e2d\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u4f8d\u7236\u6597\u7bf7\u88ab\u70e7\u7a7f\u4e00\u4e2a\u7a9f\u7abf\uff0c\u5979\u6454\u5012\u5728\u5730\uff0c\u534a\u8fb9\u8eab\u4f53\u77ac\u95f4\u90fd\u88ab\u70c8\u706b\u5305\u88f9\u3002</p><p>\u4e9a\u5f53\u65af\u4ece\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u90a3\u679a\u83f2\u5c14\u677e\u62a4\u7b26\uff0c\u9065\u9065\u4e00\u6309\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u8eab\u4e0a\u7684\u706b\u7130\u987f\u65f6\u7184\u706d\u4e86\u3002\u867d\u7136\u53ea\u6709\u4e0d\u5230\u534a\u79d2\u949f\uff0c\u4f46\u5353\u5c14\u5c11\u5973\u7684\u76ae\u80a4\u5df2\u7ecf\u4e25\u91cd\u70e7\u4f24\uff0c\u534a\u8fb9\u8138\u4e0a\u6ee1\u662f\u7126\u707c\u7684\u6c34\u6ce1\uff0c\u4e00\u53ea\u773c\u775b\u5df2\u7ecf\u65e0\u6cd5\u5f20\u5f00\u4e86\uff0c\u4e0a\u4e0b\u773c\u8138\u50cf\u662f\u88ab\u878d\u5316\u5728\u4e00\u8d77\u3002\u867d\u7136\u53d7\u4e86\u8fd9\u6837\u91cd\u7684\u4f24\uff0c\u4f46\u591a\u5e74\u7684\u4e25\u9177\u8bad\u7ec3\u8ba9\u5979\u5fcd\u53d7\u4f4f\u4e86\u75db\u695a\uff0c\u8d81\u7740\u9b54\u88d4\u5353\u5c14\u4e5f\u56e0\u4e3a\u5f3a\u5149\u6682\u65f6\u5931\u660e\u7684\u673a\u4f1a\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u5c31\u52bf\u7ffb\u8eab\u4e00\u6eda\uff0c\u8eb2\u5728\u4e00\u4e2a\u77f3\u67f1\u540e\u9762\uff0c\u4ece\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u836f\u6c34\u6025\u901f\u704c\u4e0b\u3002</p><p>\u201c\u529b\u573a\u7262\u7b3c\uff01\u201d</p><p>\u201c\u77f3\u5316\uff01\u201d</p><p>\u514b\u52b3\u62d3\u4e0a\u6765\u5148\u58f0\u593a\u4eba\uff0c\u4e00\u51fb\u9633\u708e\u5c04\u7ebf\u91cd\u4f24\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u3002\u867d\u7136\u6309\u9053\u7406\u8bf4\uff0c\u8fd9\u662f\u592a\u9633\u795e\u5170\u68ee\u5fb7\u5c14\u8d50\u4e88\u4fe1\u5f92\u7684\u7279\u522b\u795e\u672f\uff0c\u4ed6\u4e00\u4e2a\u5353\u5c14\u5deb\u5e08\u6ca1\u9053\u7406\u4f1a\uff0c\u4f46\u504f\u504f\u5c31\u7528\u4e86\u51fa\u6765\u3002\u743c\u6069\u548c\u4e9a\u5f53\u65af\u4e0d\u6562\u8fdf\u7591\uff0c\u540c\u65f6\u65bd\u5c55\u5404\u81ea\u62ff\u624b\u7684\u6cd5\u672f\uff0c\u8981\u4ee5\u6700\u5feb\u901f\u5ea6\u5c06\u8fd9\u4e2a\u5371\u9669\u4eba\u7269\u5e72\u6389\u3002</p><p>\u7070\u8272\u5c04\u7ebf\u51fb\u4e2d\u4e86\u514b\u52b3\u62d3\uff0c\u4f46\u6ca1\u6709\u4ea7\u751f\u4efb\u4f55\u5f71\u54cd\uff0c\u4ed6\u7684\u67d0\u4ef6\u88c5\u5907\u628a\u77f3\u5316\u672f\u5438\u6536\u4e86\u3002\u529b\u573a\u4ece\u516d\u4e2a\u65b9\u5411\u5408\u62e2\uff0c\u6025\u901f\u7ec4\u5408\u6210\u4e00\u4e2a\u900f\u660e\u7684\u7262\u7b3c\uff0c\u5c06\u76ee\u6807\u56f0\u5728\u5176\u4e2d\u3002\u201c\u5316\u77f3\u4e3a\u6ce5\uff01\u201d\u4e9a\u5f53\u65af\u5bf9\u743c\u6069\u8bf4\uff0c\u201c\u9001\u4ed6\u5230\u5730\u5fc3\u53bb\u3002\u201d</p><p>\u743c\u6069\u70b9\u5934\uff0c\u53d6\u51fa\u5377\u8f74\u51c6\u5907\u65bd\u6cd5\u3002\u9b54\u88d4\u5353\u5c14\u51b2\u4e86\u8fc7\u6765\uff0c\u5b83\u662f\u534a\u6076\u9b54\u534a\u5353\u5c14\uff0c\u867d\u7136\u5bf9\u5f3a\u5149\u4e5f\u975e\u5e38\u754f\u60e7\uff0c\u7ec8\u7a76\u6bd4\u5353\u5c14\u7684\u62b5\u6297\u80fd\u529b\u5f3a\u4e9b\uff0c\u6700\u5148\u6062\u590d\u89c6\u529b\u3002\u4e9a\u5f53\u65af\u53f3\u624b\u865a\u5f20\uff0c\u968f\u5373\u63e1\u7d27\uff0c\u53ec\u5524\u51fa\u4e00\u53ea\u94f6\u767d\u8272\u5de8\u638c\uff0c\u4e00\u62f3\u7838\u8fc7\uff0c\u516b\u82f1\u5c3a\u9ad8\u7684\u9b54\u88d4\u5353\u5c14\u4fbf\u50cf\u82cd\u8747\u4e00\u6837\u88ab\u62cd\u98de\u51fa\u53bb\uff0c\u91cd\u91cd\u649e\u5728\u5899\u58c1\u4e0a\uff0c\u6454\u843d\u4e0b\u6765\u3002\u4e9a\u5f53\u65af\u63a7\u5236\u7740\u5de8\u638c\uff0c\u8ba9\u5b83\u5411\u524d\u98de\u52a8\uff0c\u51c6\u5907\u5c06\u9b54\u88d4\u5353\u5c14\u634f\u5f97\u7c89\u8eab\u788e\u9aa8\u3002</p><p>\u201c\u82e5\u65af\u5e15\uff01\u201d\uff08Raspra\uff09</p><p>\u4e00\u9053\u4eae\u7ea2\u8272\u5c04\u7ebf\u4ece\u4fa7\u540e\u65b9\u51fb\u6765\uff0c\u6253\u5728\u5de8\u638c\u4e0a\uff0c\u77ac\u95f4\u5c06\u5b83\u5316\u4f5c\u65e0\u5f62\u3002\u4e9a\u5f53\u65af\u56de\u8fc7\u5934\u6765\uff0c\u770b\u89c1\u529b\u573a\u7262\u7b3c\u5df2\u7ecf\u88ab\u7a7f\u900f\u4e86\u4e00\u4e2a\u51fa\u53e3\uff0c\u6b63\u5bb9\u4e00\u4eba\u901a\u8fc7\uff0c\u514b\u52b3\u62d3\u5927\u6b65\u8d70\u51fa\u3002\u4ed6\u624b\u4e0a\u90a3\u679a\u767d\u8272\u77f3\u82f1\u7403\u6b63\u9690\u9690\u6cdb\u7740\u7ea2\u5149\uff0c\u5feb\u901f\u9eef\u6de1\u4e0b\u53bb\uff0c\u521a\u624d\u7684\u7ea2\u5b9d\u77f3\u9006\u8f6c\u5c04\u7ebf\u5c31\u662f\u4ece\u4e2d\u53d1\u51fa\u3002</p><p>\u743c\u6069\u7684\u5316\u77f3\u4e3a\u6ce5\u5c1a\u672a\u5b8c\u6210\uff0c\u4f46\u4e5f\u5df2\u7ecf\u6beb\u65e0\u610f\u4e49\uff0c\u4ed6\u5f84\u76f4\u6254\u4e0b\u5377\u8f74\uff0c\u5feb\u901f\u5224\u65ad\u7740\u573a\u5185\u5c40\u52bf\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u88ab\u9633\u708e\u5c04\u7ebf\u70e7\u4f24\uff0c\u6b64\u65f6\u6682\u65f6\u4e27\u5931\u4e86\u6218\u6597\u529b\u3002\u9b54\u88d4\u5353\u5c14\u88ab\u4e9a\u5f53\u65af\u4e00\u8bb0\u7c89\u788e\u62f3\u7838\u4e2d\uff0c\u867d\u7136\u6ca1\u6b7b\u4e5f\u65ad\u4e86\u534a\u6761\u547d\u3002\u4e3b\u6bcd\u59ae\u745f\u4e5f\u5df2\u7ecf\u91cd\u4f24\u9ebb\u75f9\uff0c\u4e0d\u8db3\u4e3a\u60e7\u3002\u6b64\u65f6\u8fd8\u4fdd\u6301\u6218\u6597\u529b\u7684\uff0c\u5c31\u662f\u81ea\u5df1\u3001\u4e9a\u5f53\u65af\u548c\u514b\u52b3\u62d3\u3002</p><p>\u5f97\u8d76\u5feb\u641e\u5b9a\u8fd9\u4e2a\u5bb6\u4f19\uff0c\u6b64\u65f6\u4ee5\u4e8c\u654c\u4e00\uff0c\u5c40\u9762\u5360\u4f18\uff0c\u65f6\u95f4\u62d6\u957f\u4e86\u518d\u6709\u745e\u8d39\u5fb7\u5bb6\u65cf\u5176\u4ed6\u5deb\u5e08\u8d76\u6765\uff0c\u90a3\u5c31\u9ebb\u70e6\u4e86\u3002</p><p>\u743c\u6069\u63e1\u7d27\u6cd5\u6756\uff0c\u53d1\u52a8\u6cd5\u672f\u589e\u5e45\uff0c\u7d27\u63a5\u7740\u5728\u8111\u4e2d\u6d6e\u73b0\u53d8\u5f62\u672f\u5492\u6587\uff0c\u4e9a\u5f53\u65af\u5219\u518d\u6b21\u91ca\u653e\u4e86\u4e00\u4e2a\u8679\u5149\u55b7\u5c04\u3002\u4f46\u8fd9\u4e24\u4e2a\u6cd5\u672f\u90fd\u6ca1\u6709\u6210\u529f\uff0c\u514b\u52b3\u62d3\u7528\u4e00\u67c4\u6df1\u84dd\u8272\u6743\u6756\u5c06\u5b83\u4eec\u5438\u6536\u4e86\u3002\u63a5\u4e0b\u6765\uff0c\u795e\u5802\u4e2d\u5c55\u5f00\u4e86\u4e00\u573a\u9b54\u6cd5\u5927\u6218\uff0c\u4e09\u4f4d\u5deb\u5e08\u5404\u5c55\u6240\u957f\uff0c\u4e9a\u5f53\u65af\u63a5\u8fde\u53ec\u5524\u706b\u7130\u3001\u5bd2\u51b0\u3001\u95ea\u7535\u3001\u97f3\u6ce2\u548c\u529b\u573a\u9b54\u6cd5\uff0c\u5851\u6210\u5404\u79cd\u5371\u9669\u7684\u6b66\u5668\u8fdb\u884c\u653b\u51fb\uff0c\u743c\u6069\u5219\u63a5\u8fde\u4f7f\u7528\u53d8\u5f62\u3001\u77f3\u5316\u548c\u89e3\u79bb\uff0c\u4f46\u5168\u90fd\u88ab\u62b5\u6321\u4e86\u4e0b\u6765\u3002\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u9996\u5e2d\u5deb\u5e08\u679c\u7136\u4e0d\u6127\u4ee5\u70bc\u91d1\u672f\u8457\u79f0\uff0c\u4ed6\u770b\u8d77\u6765\u4e24\u624b\u7a7a\u7a7a\uff0c\u8eab\u65e0\u957f\u7269\uff0c\u5374\u80fd\u6e90\u6e90\u4e0d\u65ad\u5730\u4ece\u957f\u888d\u91cc\u53d6\u51fa\u5404\u79cd\u65b0\u5947\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u4e00\u6b21\u53c8\u4e00\u6b21\u5730\u62b5\u6321\u4f4f\u653b\u51fb\uff0c\u4eff\u4f5b\u5cbf\u7136\u5de8\u77f3\u4e00\u822c\uff0c\u867d\u7136\u9762\u4e34\u60ca\u6d9b\u9a87\u6d6a\uff0c\u4f9d\u65e7\u5c79\u7acb\u4e0d\u5012\u3002</p><p>\u201c\u8fd9\u6837\u4e0b\u53bb\u4e0d\u884c\uff0c\u201d\u743c\u6069\u60f3\uff0c\u201c\u4ed6\u7684\u6536\u85cf\u7b80\u76f4\u662f\u65e0\u7a77\u65e0\u5c3d\u3002\u201d</p><p>\u5deb\u5e08\u7684\u6cd5\u672f\u6709\u9650\uff0c\u9b54\u6cd5\u7269\u54c1\u80fd\u591f\u6781\u5927\u5730\u5f3a\u5316\u529b\u91cf\uff0c\u591a\u4e00\u5f20\u5377\u8f74\uff0c\u5c31\u662f\u989d\u5916\u591a\u4e86\u4e00\u4e2a\u6cd5\u672f\u3002\u4f46\u5236\u4f5c\u9b54\u6cd5\u7269\u54c1\u4e0d\u4f46\u96be\u5ea6\u5f88\u9ad8\uff0c\u6210\u672c\u4e0d\u83f2\uff0c\u800c\u4e14\u4f1a\u4e25\u91cd\u635f\u8017\u5deb\u5e08\u7684\u7cbe\u529b\uff0c\u751a\u81f3\u4f1a\u51cf\u77ed\u5bff\u547d\uff0c\u5982\u679c\u5728\u5e02\u573a\u4e0a\u51fa\u552e\uff0c\u4ef7\u683c\u4e5f\u6781\u5176\u9ad8\u6602\u3002\u56e0\u4e3a\u8fd9\u4e9b\u7f18\u6545\uff0c\u5deb\u5e08\u6240\u643a\u5e26\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u6709\u4e00\u4e2a\u5927\u81f4\u7684\u8303\u56f4\uff0c\u7136\u800c\u745e\u8d39\u5fb7\u5bb6\u7684\u9996\u5e2d\u5deb\u5e08\u8fdc\u8fdc\u8d85\u51fa\u4e86\u5e38\u7406\u3002</p><p>\u4e9a\u5f53\u65af\u53ec\u5524\u6765\u4e00\u9762\u51b0\u5899\uff0c\u6682\u65f6\u5c01\u4f4f\u514b\u52b3\u62d3\u7684\u884c\u52a8\uff0c\u968f\u5373\u4ed6\u540e\u9000\uff0c\u4ece\u53e3\u888b\u91cc\u53d6\u51fa\u4e00\u5f20\u6563\u53d1\u5fae\u5149\u7684\u5377\u8f74\u3002\u201c\u6321\u4f4f\u4ed6\u3002\u201d\u4ed6\u5bf9\u743c\u6069\u8bf4\uff0c\u7136\u540e\u5c55\u5f00\u5377\u8f74\u5feb\u901f\u5ff5\u8bf5\u3002</p><p>\u514b\u52b3\u62d3\u6467\u6bc1\u4e86\u51b0\u5899\uff0c\u4ed6\u53c8\u4e3e\u8d77\u767d\u8272\u77f3\u82f1\u7403\uff0c\u770b\u4e0a\u53bb\u51c6\u5907\u518d\u5ea6\u91ca\u653e\u9633\u708e\u5c04\u7ebf\uff0c\u7136\u800c\u743c\u6069\u6070\u5230\u597d\u5904\u5730\u4f7f\u7528\u4e86\u4e00\u4e2a\u7f13\u6162\u672f\uff0c\u65e0\u5f62\u7684\u7a7a\u6c14\u538b\u8feb\u7740\u514b\u52b3\u62d3\u7684\u8eab\u4f53\uff0c\u8ba9\u4ed6\u884c\u52a8\u4e0d\u4fbf\u3002\u4e9a\u5f53\u65af\u7ec8\u4e8e\u8bfb\u5b8c\u4e86\u5377\u8f74\uff0c\u4e00\u9053\u6d45\u7eff\u8272\u5149\u8292\u4ece\u6307\u5c16\u5c04\u51fa\uff0c\u51fb\u4e2d\u4e86\u514b\u52b3\u62d3\u3002</p><p>\u8f70\u7136\u4e00\u58f0\uff0c\u6d41\u5149\u56db\u6ea2\uff0c\u5343\u4e07\u70b9\u4e03\u5f69\u6676\u83b9\u788e\u73e0\u7f24\u7eb7\u6563\u5c04\uff0c\u7b80\u76f4\u4fbf\u5982\u76db\u5927\u7684\u70df\u706b\u7efd\u653e\uff0c\u7f8e\u8f6e\u7f8e\u5942\u3002\u7136\u800c\u7e41\u534e\u8fc7\u540e\uff0c\u4fbf\u662f\u6c89\u5bc2\uff0c\u514b\u52b3\u62d3\u4e2d\u4e86\u8fd9\u4e00\u51fb\uff0c\u5168\u7136\u6ca1\u6709\u53d7\u5230\u534a\u70b9\u4f24\u5bb3\uff0c\u4f46\u4ed6\u7684\u5f62\u8c61\u5374\u77ac\u95f4\u201c\u9eef\u6de1\u201d\u4e86\u4e0b\u53bb\u3002</p><p>\u539f\u672c\u5728\u743c\u6069\u7684\u5965\u672f\u89c6\u89c9\u4e2d\uff0c\u514b\u52b3\u62d3\u4eff\u4f5b\u661f\u8fb0\u6c47\u805a\uff0c\u7480\u74a8\u751f\u8f89\uff0c\u610f\u5473\u7740\u4ed6\u5168\u8eab\u5230\u5904\u90fd\u662f\u5f3a\u5927\u7684\u9b54\u6cd5\u7269\u54c1\u3002\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u8eab\u4e0a\u534a\u70b9\u7075\u5149\u90fd\u6ca1\u6709\uff0c\u4e5f\u5c31\u662f\u8bf4\u2026\u2026</p><p>\u4ed6\u6240\u6709\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u6240\u6709\u7684\u88c5\u5907\uff0c\u4ee5\u53ca\u6240\u6709\u7684\u9b54\u6cd5\u9632\u62a4\uff0c\u5168\u90fd\u5728\u4e00\u77ac\u95f4\u88ab\u6467\u6bc1\u4e86\u3002</p><p>\u4e16\u754c\u4e0a\u80fd\u591f\u505a\u5230\u8fd9\u4e2a\u6548\u679c\u7684\u6cd5\u672f\u53ea\u6709\u4e00\u4e2a\uff0c\u743c\u6069\u6070\u597d\u4ee5\u524d\u8fd8\u66fe\u7ecf\u89c1\u8bc6\u8fc7\u4e00\u6b21\u3002</p><p>\u201c\u5927\u88c2\u89e3\uff01\u201d</p><p>\u9b54\u6cd5\u516b\u5927\u5b66\u6d3e\uff0c\u539f\u7406\u4e0d\u540c\uff0c\u5404\u81ea\u7814\u4e60\u65b9\u5411\u4e0d\u540c\uff0c\u5f7c\u6b64\u95f4\u4e5f\u5f88\u96be\u52a0\u4ee5\u6a2a\u5411\u6bd4\u8f83\uff0c\u4f46\u5404\u5927\u5b66\u6d3e\u90fd\u6709\u4e00\u4e9b\u6cd5\u672f\uff0c\u6709\u4eba\u628a\u5b83\u4eec\u7edf\u79f0\u4e3a\u5dc5\u5cf0\u6cd5\u672f\uff0c\u6709\u4eba\u7edf\u79f0\u4e3a\u81f3\u9ad8\u6cd5\u672f\uff0c\u8fd8\u6709\u79f0\u4e3a\u7ec8\u6781\u6cd5\u672f\uff0c\u6216\u8005\u5176\u4ed6\u8bf4\u6cd5\uff0c\u53cd\u6b63\u610f\u601d\u90fd\u662f\u4e00\u81f4\u7684\uff1a\u5b83\u4eec\u4ee3\u8868\u4e86\u5404\u81ea\u5b66\u6d3e\u7684\u6700\u9ad8\u6210\u5c31\u3002</p><p>\u9632\u62a4\u5b66\u6d3e\uff0c\u987e\u540d\u601d\u4e49\u7cbe\u7814\u7684\u662f\u9b54\u6cd5\u9632\u5fa1\u65b9\u9762\u7684\u95ee\u9898\uff0c\u5305\u62ec\u6b63\u53cd\u4e24\u65b9\u9762\uff1a\u653b\u548c\u5b88\uff0c\u4e5f\u5c31\u662f\u7834\u9b54\u548c\u9632\u5fa1\u3002\u5927\u88c2\u89e3\u672f\uff0c\u5c31\u662f\u7834\u9b54\u65b9\u9762\u7684\u6700\u9ad8\u6210\u5c31\u3002</p><p>\u4e00\u5207\u9b54\u6cd5\u9632\u5fa1\uff0c\u65e0\u8bba\u662f\u575a\u5b9e\u7684\u8fd8\u662f\u8106\u5f31\u7684\uff0c\u7e41\u590d\u7684\u8fd8\u662f\u7b80\u5355\u7684\uff0c\u5728\u5927\u88c2\u89e3\u7684\u8f70\u51fb\u4e0b\u90fd\u5fc5\u5b9a\u4f1a\u7acb\u523b\u6d88\u6563\u65e0\u5f62\uff1b\u5404\u79cd\u9b54\u6cd5\u7269\u54c1\uff0c\u65e0\u8bba\u662f\u5377\u8f74\u3001\u9b54\u6756\u3001\u6b66\u5668\u6216\u8005\u9970\u54c1\u7b49\u7b49\uff0c\u5728\u5927\u88c2\u89e3\u7684\u8f70\u51fb\u4e0b\u90fd\u6709\u53ef\u80fd\u4f1a\u6563\u53bb\u9b54\u529b\uff0c\u6210\u4e3a\u666e\u901a\u4e4b\u7269\u3002\u53ef\u4ee5\u8bf4\uff0c\u5bf9\u4e8e\u559c\u6b22\u501f\u52a9\u5916\u7269\uff0c\u501a\u9760\u7cbe\u826f\u88c5\u5907\u3001\u6d77\u91cf\u50a8\u5b58\uff0c\u52a8\u8f84\u5728\u8eab\u4e0a\u5957\u5404\u79cd\u9b54\u6cd5\u9632\u62a4\u7684\u5deb\u5e08\u800c\u8a00\uff0c\u5927\u88c2\u89e3\u5c31\u662f\u68a6\u9b47\u548c\u514b\u661f\u3002</p><p>\u4e9a\u5f53\u65af\u5e94\u8be5\u4e0d\u66fe\u638c\u63e1\u8fd9\u9053\u6cd5\u672f\uff0c\u4f46\u5374\u4e0d\u77e5\u9053\u4ece\u54ea\u91cc\u5f04\u6765\u4e00\u5f20\u5377\u8f74\uff0c\u7b80\u76f4\u53ef\u4ee5\u8bf4\u5c31\u662f\u4e3a\u4e86\u4eca\u65e5\u4e00\u6218\u800c\u91cf\u8eab\u5b9a\u505a\u3002\u4e00\u51fb\u4e4b\u4e0b\uff0c\u65e0\u575a\u4e0d\u6467\uff0c\u77ac\u95f4\u6bc1\u6389\u4e86\u514b\u52b3\u62d3\u8eab\u4e0a\u7684\u6240\u6709\u9b54\u6cd5\u7269\u54c1\u3002</p><p>\u7136\u800c\u6709\u4ef6\u4e8b\u60c5\u5f88\u5947\u602a\u3002</p><p>\u6cd5\u672f\u9632\u5fa1\u4f1a\u88ab\u5927\u88c2\u89e3\u77ac\u95f4\u6467\u7834\uff0c\u6beb\u65e0\u7591\u95ee\uff0c\u4f46\u90a3\u4e9b\u9b54\u6cd5\u7269\u54c1\u5374\u5e76\u975e\u8fd9\u4e48\u5bb9\u6613\u3002\u6bcf\u4e00\u4ef6\u9b54\u6cd5\u7269\u54c1\uff0c\u90fd\u662f\u51dd\u805a\u4e86\u5236\u4f5c\u8005\u65e0\u7a77\u5fc3\u8840\u5728\u5185\uff0c\u5c06\u6cd5\u672f\u9644\u7740\u8fdb\u7269\u4f53\u4e2d\uff0c\u4e0d\u662f\u7b80\u7b80\u5355\u5355\u5c31\u80fd\u7834\u574f\u7684\u3002\u5927\u88c2\u89e3\u786e\u5b9e\u53ef\u80fd\u5c06\u9b54\u6cd5\u7269\u54c1\u8fd4\u5f52\u666e\u901a\u4e4b\u7269\uff0c\u4f46\u53ea\u662f\u53ef\u80fd\uff0c\u4e0d\u662f\u7edd\u5bf9\uff0c\u8d8a\u5f3a\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u62b5\u5fa1\u7684\u53ef\u80fd\u6027\u5c31\u8d8a\u9ad8\u3002\u5f53\u7136\uff0c\u8d8a\u9ad8\u660e\u7684\u5deb\u5e08\u65bd\u6cd5\uff0c\u6210\u529f\u7684\u53ef\u80fd\u6027\u4e5f\u8d8a\u9ad8\uff0c\u4f46\u514b\u52b3\u62d3\u8eab\u4e0a\u8fd1\u767e\u4ef6\u9b54\u6cd5\u7269\u54c1\uff0c\u5c45\u7136\u65e0\u4e00\u4f8b\u5916\u5730\u5168\u90e8\u4e2d\u62db\uff0c\u8fd9\u8fd0\u6c14\u4e5f\u5b9e\u5728\u672a\u514d\u597d\u5f97\u79bb\u8c31\u3002</p><p>\u5c31\u7b97\u771f\u662f\u5e78\u8fd0\u5973\u795e\u7737\u987e\uff0c\u4e9a\u5f53\u65af\u65e2\u7136\u6709\u8fd9\u5f20\u5377\u8f74\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4e00\u5f00\u59cb\u5c31\u62ff\u51fa\u6765\u7528\uff0c\u6216\u8bb8\u662f\u56e0\u4e3a\u5377\u8f74\u73cd\u8d35\u65e0\u6bd4\uff0c\u5f97\u6765\u4e0d\u6613\uff0c\u80fd\u4e0d\u52a8\u7528\u5c31\u4e0d\u52a8\u7528\uff1f</p><p>\u867d\u7136\u6709\u79cd\u79cd\u7591\u8651\uff0c\u4f46\u743c\u6069\u5e76\u6ca1\u6709\u653e\u8fc7\u8fd9\u96be\u5f97\u7684\u826f\u673a\u3002\u518d\u4e00\u6b21\u7684\uff0c\u4ed6\u53d6\u51fa\u6c34\u6676\uff0c\u51dd\u6210\u5c16\u9510\u7ec6\u957f\u7684\u5bd2\u51b0\u957f\u67aa\uff0c\u671d\u514b\u52b3\u62d3\u5f53\u80f8\u5c04\u53bb\u3002</p><p>\u514b\u52b3\u62d3\u4e5f\u88ab\u521a\u624d\u90a3\u4e00\u51fb\u7684\u8f89\u714c\u6210\u679c\u7ed9\u9707\u4f4f\u4e86\uff0c\u4e00\u65f6\u5c45\u7136\u6709\u4e9b\u77ed\u6682\u5931\u795e\u3002\u5bd2\u51b0\u957f\u67aa\u7834\u7a7a\u523a\u6765\uff0c\u5c06\u8fd1\u80f8\u53e3\u65f6\u4ed6\u624d\u9661\u7136\u60ca\u89c9\uff0c\u5306\u5fd9\u95f4\u672c\u80fd\u5730\u4fa7\u8eab\u8eb2\u95ea\uff0c\u4f9d\u65e7\u6ca1\u80fd\u5b8c\u5168\u907f\u5f00\u3002\u539f\u672c\u575a\u56fa\u7684\u6597\u7bf7\u548c\u957f\u888d\u4e5f\u8d77\u4e0d\u5230\u4efb\u4f55\u9632\u5fa1\u4f5c\u7528\uff0c\u957f\u67aa\u523a\u4e2d\u4e86\u5deb\u5e08\u7684\u53f3\u81c2\uff0c\u51b2\u51fb\u529b\u5e26\u7740\u4ed6\u7684\u8eab\u4f53\u8f6c\u4e86\u534a\u4e2a\u5708\uff0c\u9669\u4e9b\u6454\u5012\u3002</p><p>\u201c\u4f60\u5b8c\u86cb\u4e86\uff01\u201d\u4e9a\u5f53\u65af\u5ba3\u79f0\uff0c\u4ed6\u7684\u53cc\u624b\u5728\u6025\u901f\u6325\u52a8\uff0c\u4e00\u56e2\u7403\u72b6\u95ea\u7535\u6b63\u5728\u51dd\u805a\u6210\u578b\u3002</p><p>\u514b\u52b3\u62d3\u7684\u54bd\u5589\u4e2d\u6324\u51fa\u4f4e\u543c\uff0c\u4ee5\u5feb\u5f97\u60ca\u4eba\u7684\u901f\u5ea6\u5ff5\u8bf5\u4e00\u6bb5\u590d\u6742\u7684\u5492\u8bed\uff0c\u8fd9\u662f\u4ed6\u6240\u638c\u63e1\u7684\u6700\u5f3a\u5927\u6cd5\u672f\u3002\u5e78\u8fd0\u7684\u662f\uff0c\u8fd9\u4e2a\u6cd5\u672f\u53ea\u9700\u8981\u5492\u6587\uff0c\u65e0\u9700\u4efb\u4f55\u52a8\u4f5c\u6210\u5206\u6216\u8005\u65bd\u6cd5\u6750\u6599\uff0c\u5426\u5219\u4ee5\u4ed6\u73b0\u5728\u53f3\u81c2\u53d7\u4f24\u7684\u72b6\u6001\uff0c\u53ea\u6015\u8981\u987a\u5229\u65bd\u6cd5\u8fd8\u9887\u6709\u96be\u5ea6\u3002</p><p>\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u6210\u529f\u4e86\uff0c\u62a2\u5728\u4e24\u4e2a\u5bf9\u624b\u4e4b\u524d\u5b8c\u6210\u4e86\u6cd5\u672f\u3002</p><p>\u743c\u6069\u6b63\u5728\u51c6\u5907\u65bd\u5c55\u4e00\u679a\u77f3\u5f39\uff0c\u5982\u4eca\u514b\u52b3\u62d3\u5df2\u7ecf\u88ab\u5927\u88c2\u89e3\u4e00\u51fb\u53d8\u6210\u4e86\u8fd1\u4f3c\u201c\u88f8\u5954\u201d\u7684\u72b6\u6001\uff0c\u6240\u6709\u9632\u5fa1\u5168\u90fd\u88ab\u6467\u6bc1\uff0c\u6b63\u662f\u7a77\u8ffd\u731b\u6253\u7684\u826f\u673a\u3002\u4e00\u65e6\u5bf9\u65b9\u83b7\u5f97\u5598\u606f\uff0c\u91cd\u65b0\u65bd\u6cd5\u6784\u5efa\u9632\u5fa1\uff0c\u90a3\u5c40\u52bf\u5c31\u53c8\u4f1a\u62d6\u5ef6\u4e0b\u53bb\u4e86\u3002\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u77f3\u5f39\u672f\u8fd9\u6837\u5a01\u529b\u867d\u5f31\uff0c\u65bd\u6cd5\u901f\u5ea6\u5374\u5feb\u7684\u6cd5\u672f\u5c31\u6210\u4e86\u9996\u9009\u3002\u4ed6\u5c06\u7ec6\u5c0f\u5706\u6ed1\u7684\u77f3\u5f39\u5728\u624b\u6307\u95f4\u6025\u901f\u8f6c\u52a8\u7740\uff0c\u76ef\u7740\u514b\u52b3\u62d3\uff0c\u6b63\u8981\u5f39\u51fa\uff0c\u7a81\u7136\u611f\u89c9\u773c\u524d\u4e00\u82b1\u3002</p><p>\u4eff\u4f5b\u50cf\u662f\u51fa\u73b0\u4e86\u5e7b\u89c9\uff0c\u773c\u524d\u7684\u514b\u52b3\u62d3\u9661\u7136\u52a8\u4f5c\u901f\u5ea6\u53d8\u5f97\u6781\u5feb\uff0c\u5feb\u5230\u4e86\u4e0d\u53ef\u601d\u8bae\u7684\u7a0b\u5ea6\uff0c\u4ee5\u81f3\u4e8e\u7a7a\u6c14\u4e2d\u7559\u4e0b\u65e0\u6570\u6b8b\u5f71\u3002\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u573a\u5185\u5f62\u52bf\u53d1\u751f\u5267\u53d8\u3002</p><p>\u4e00\u4e2a\u5fae\u5149\u95ea\u95ea\u7684\u9c9c\u7ea2\u8272\u7403\u4f53\u51fa\u73b0\u5728\u514b\u52b3\u62d3\u539f\u672c\u6240\u5728\u7684\u4f4d\u7f6e\uff0c\u7d27\u63a5\u7740\u5b83\u53d8\u6210\u6de1\u6a59\u8272\uff0c\u7136\u540e\u662f\u6df1\u9ec4\u8272\u3001\u6d45\u7eff\u8272\u3001\u5929\u84dd\u8272\u3001\u6697\u9752\u8272\uff0c\u6700\u540e\u662f\u7d2b\u7f57\u5170\u8272\uff0c\u8f6c\u77ac\u4e4b\u95f4\u5df2\u7ecf\u53d8\u5e7b\u4e86\u4e03\u8272\u8679\u5f69\uff0c\u7136\u540e\u53c8\u5f00\u59cb\u65b0\u4e00\u8f6e\u7684\u6d41\u8f6c\uff0c\u6cd5\u7403\u662f\u534a\u900f\u660e\u7684\uff0c\u5f71\u5f71\u7ef0\u7ef0\u80fd\u591f\u770b\u89c1\u91cc\u9762\u7684\u4eba\u5f71\uff0c\u6b63\u662f\u514b\u52b3\u62d3\u3002\u4e00\u67c4\u900f\u660e\u7684\u5251\u5f62\u529b\u573a\u201c\u545b\u201d\u5730\u5728\u534a\u7a7a\u4e2d\u51fa\u73b0\uff0c\u671d\u4e9a\u5f53\u65af\u75be\u901f\u98de\u523a\u3002\u71c3\u70e7\u9aa8\u9abc\u6784\u6210\u7684\u5f02\u754c\u4e4b\u95e8\u8f70\u7136\u5212\u5f00\uff0c\u4e00\u53ea\u534a\u4eba\u534a\u79c3\u9e70\u7684\u5f17\u6d1b\u9b54\u4ece\u4e2d\u8dc3\u51fa\uff0c\u51cc\u7a7a\u5411\u743c\u6069\u6251\u4e0b\uff0c\u6bd4\u57ce\u5899\u4e0a\u7684\u90a3\u4e9b\u540c\u7c7b\u4f53\u578b\u66f4\u52a0\u5de8\u5927\u3002</p><p>\u4ed6\u5c45\u7136\u5728\u4e00\u77ac\u95f4\u540c\u65f6\u91ca\u653e\u4e86\u4e09\u4e2a\u6cd5\u672f\u2026\u2026\u7136\u800c\uff0c\u8fd9\u5b8c\u5168\u8fdd\u80cc\u4e86\u57fa\u672c\u7684\u9b54\u6cd5\u5e38\u8bc6\u3002\u65bd\u5c55\u4e00\u4e2a\u6cd5\u672f\uff0c\u9700\u8981\u5168\u8eab\u5fc3\u5730\u534f\u8c03\u914d\u5408\uff0c\u51dd\u795e\u8d2f\u6ce8\uff0c\u518d\u9ad8\u660e\u7684\u5927\u5deb\u5e08\uff0c\u4e5f\u6ca1\u9053\u7406\u80fd\u4e00\u5fc3\u4e09\u7528\uff0c\u540c\u65f6\u65bd\u5c55\u51fa\u4e09\u4e2a\u622a\u7136\u4e0d\u540c\u7684\u6cd5\u672f\u6765\u2014\u2014\u81f3\u5c11\u4ed6\u8fd8\u6ca1\u957f\u51fa\u516d\u53ea\u624b\u3002</p><p>\u7075\u5149\u4e00\u95ea\uff0c\u743c\u6069\u5239\u90a3\u95f4\u660e\u767d\u4e86\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u3002\u8fd9\u79cd\u532a\u5937\u6240\u601d\u60c5\u5f62\uff0c\u53ea\u6709\u4e00\u79cd\u89e3\u91ca\u3002</p><p>\u65f6\u95f4\u505c\u6b62\u3002</p></article>"}}}]);
