(window.webpackJsonp=window.webpackJsonp||[]).push([[1473],{1496:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u516d\u8282 \u6700\u540e\u7684\u4f0f\u5175",content:"<article><h2>\u7b2c\u4e09\u5341\u516d\u8282 \u6700\u540e\u7684\u4f0f\u5175</h2><p>\u8010\u745f\u65f6\u4ee3\uff0c\u65e0\u6570\u5965\u672f\u5e08\u6b9a\u7cbe\u7aed\u8651\uff0c\u7693\u9996\u7a77\u7ecf\uff0c\u5e0c\u671b\u80fd\u591f\u53d1\u660e\u4e00\u4e2a\u6cd5\u672f\u3002\u8fd9\u4e2a\u6cd5\u672f\u65bd\u5c55\u51fa\u6765\uff0c\u9664\u4e86\u81ea\u5df1\u4e4b\u5916\uff0c\u8fd9\u4e16\u754c\u4e0a\u6240\u6709\u4eba\u7684\u65f6\u7a7a\u90fd\u9677\u5165\u505c\u6ede\u72b6\u6001\u2014\u2014\u5c31\u4eff\u4f5b\u6309\u4e86\u6682\u505c\u952e\u4f3c\u7684\u3002</p><p>\u8fd9\u662f\u4e00\u4e2a\u4f1f\u5927\u7684\u8bbe\u60f3\uff0c\u5982\u679c\u771f\u80fd\u505a\u5230\u7684\u8bdd\uff0c\u7b80\u76f4\u5c31\u662f\u5929\u4e0b\u65e0\u654c\u3002\u60f3\u60f3\u770b\uff0c\u6cd5\u672f\u4e00\u51fa\uff0c\u6574\u4e2a\u4e16\u754c\u90fd\u8fdb\u5165\u6682\u505c\u72b6\u6001\uff0c\u552f\u6709\u65bd\u6cd5\u8005\u81ea\u5df1\u80fd\u591f\u81ea\u7531\u6d3b\u52a8\uff0c\u5230\u65f6\u5019\u4e0d\u9700\u8981\u518d\u7528\u4efb\u4f55\u6cd5\u672f\uff0c\u53ea\u8981\u6709\u628a\u5315\u9996\u5728\u624b\uff0c\u81ea\u7136\u4e00\u5200\u4e00\u4e2a\uff0c\u780d\u74dc\u5207\u83dc\uff0c\u6240\u5411\u62ab\u9761\uff0c\u56e0\u4e3a\u522b\u4eba\u538b\u6839\u5c31\u4e0d\u80fd\u52a8\u3002</p><p>\u53ef\u60dc\u7684\u662f\uff0c\u7ecf\u8fc7\u4e00\u4ee3\u53c8\u4e00\u4ee3\u5deb\u5e08\u7684\u7814\u7a76\uff0c\u8fd9\u4e2a\u8bbe\u60f3\u6700\u7ec8\u8fd8\u662f\u5931\u8d25\u4e86\u3002\u4ee5\u4e00\u5df1\u4e4b\u529b\uff0c\u5f71\u54cd\u6574\u4e2a\u65f6\u7a7a\uff0c\u8fd9\u662f\u8fde\u8bf8\u795e\u90fd\u529e\u4e0d\u5230\u7684\u4e8b\u60c5\u3002\u7136\u800c\u5deb\u5e08\u5927\u90fd\u662f\u4e9b\u7814\u7a76\u72c2\u4eba\uff0c\u51b3\u4e0d\u80af\u8f7b\u8a00\u653e\u5f03\uff0c\u5c31\u7b97\u8fd9\u6761\u8def\u8d70\u4e0d\u901a\uff0c\u90a3\u4e5f\u8981\u53e6\u8f9f\u8e4a\u5f84\u3002</p><p>\u201c\u65e2\u7136\u4e0d\u80fd\u6539\u53d8\u4e16\u754c\uff0c\u90a3\u5c31\u6539\u53d8\u81ea\u8eab\u3002\u201d</p><p>\u6bcf\u4e2a\u4eba\u7684\u65f6\u7a7a\uff0c\u4ece\u7406\u8bba\u4e0a\u6765\u8bf4\u662f\u5b8c\u5168\u4e00\u81f4\uff0c\u7edd\u5bf9\u540c\u6b65\u7684\u3002\u743c\u6069\u5728\u5e7d\u6697\u5730\u57df\u91cc\u5ea6\u8fc7\u5341\u5206\u949f\uff0c\u73ca\u5609\u5728\u9634\u9b42\u57ce\u91cc\u540c\u6837\u4e5f\u662f\u5ea6\u8fc7\u5341\u5206\u949f\uff0c\u4e16\u754c\u4e0a\u5176\u4ed6\u4efb\u4f55\u4eba\uff0c\u4e5f\u90fd\u662f\u5ea6\u8fc7\u5341\u5206\u949f\uff0c\u4e0d\u4f1a\u591a\u4e00\u79d2\uff0c\u4e5f\u4e0d\u4f1a\u5c11\u4e00\u79d2\u3002\u65e2\u7136\u6539\u53d8\u4e16\u754c\u5df2\u7ecf\u88ab\u8bc1\u660e\u4e0d\u73b0\u5b9e\uff0c\u90a3\u4e48\u5c31\u6539\u53d8\u81ea\u5df1\u7684\u65f6\u7a7a\uff0c\u76f8\u5bf9\u6765\u8bf4\u7ed3\u679c\u4e5f\u8fd8\u662f\u4e00\u6837\u3002</p><p>\u53c8\u7ecf\u8fc7\u5386\u4ee3\u5deb\u5e08\u7684\u52aa\u529b\uff0c\u8fd9\u4e2a\u6cd5\u672f\u7ec8\u4e8e\u88ab\u53d1\u660e\u51fa\u6765\u3002\u5b83\u80fd\u591f\u6682\u65f6\u626d\u66f2\u65bd\u6cd5\u8005\u81ea\u8eab\u7684\u65f6\u7a7a\uff0c\u8ba9\u539f\u672c\u77ed\u6682\u7684\u77ac\u95f4\u5ef6\u957f\uff0c\u7b49\u4e8e\u989d\u5916\u6bd4\u522b\u4eba\u591a\u51fa\u4e00\u6bb5\u65f6\u95f4\u2014\u2014\u4f9d\u636e\u80fd\u529b\u9ad8\u4f4e\u4e0d\u540c\uff0c\u77ed\u5219\u5341\u4f59\u79d2\uff0c\u591a\u5219\u534a\u5206\u949f\uff0c\u751a\u81f3\u66f4\u957f\uff0c\u5bf9\u4e8e\u5deb\u5e08\u6765\u8bf4\uff0c\u8db3\u591f\u505a\u5f88\u591a\u4e8b\u60c5\u4e86\u3002\u52a8\u548c\u9759\uff0c\u672c\u6765\u5c31\u662f\u76f8\u5bf9\u6982\u5ff5\uff0c\u5bf9\u4e8e\u5176\u4ed6\u4eba\u6765\u8bf4\uff0c\u65bd\u6cd5\u8005\u4eff\u4f5b\u662f\u52a8\u4f5c\u7a81\u7136\u5feb\u5f97\u4e0d\u53ef\u601d\u8bae\uff1b\u5bf9\u4e8e\u65bd\u6cd5\u8005\u6765\u8bf4\uff0c\u5219\u76f8\u5f53\u4e8e\u9664\u81ea\u5df1\u4e4b\u5916\u7684\u65f6\u7a7a\u90fd\u9677\u5165\u6682\u65f6\u505c\u6ede\uff0c\u56e0\u6b64\u8fd9\u4e2a\u6cd5\u672f\u88ab\u547d\u540d\u4e3a\u201c\u65f6\u95f4\u505c\u6b62\u201d\uff0c\u88ab\u4f5c\u4e3a\u53d8\u5316\u5b66\u6d3e\u7684\u6700\u9ad8\u6210\u5c31\u4e4b\u4e00\u3002</p><p>\u7136\u800c\u2026\u2026\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u4ece\u6765\u6ca1\u6709\u8fd9\u4e48\u987a\u5229\u7684\u3002</p><p>\u6cd5\u672f\u53d1\u660e\uff0c\u5deb\u5e08\u4eec\u6b23\u559c\u82e5\u72c2\uff0c\u7ed3\u679c\u53d1\u73b0\u6709\u4e2a\u4e0d\u5927\u4e0d\u5c0f\u7684\u9ebb\u70e6\u3002\u56e0\u4e3a\u8fd9\u4e2a\u6cd5\u672f\u5e76\u975e\u771f\u662f\u8ba9\u65f6\u95f4\u505c\u6b62\uff0c\u800c\u662f\u6682\u65f6\u626d\u66f2\u65bd\u6cd5\u8005\u81ea\u5df1\u7684\u65f6\u7a7a\u2014\u2014\u8fd9\u5bfc\u81f4\u7684\u7ed3\u679c\uff0c\u662f\u65bd\u6cd5\u8005\u548c\u5176\u4ed6\u4eba\uff0c\u5176\u5b9e\u5e76\u4e0d\u5904\u4e8e\u540c\u4e00\u4e2a\u65f6\u7a7a\u3002\u5728\u65f6\u95f4\u505c\u6b62\u7684\u671f\u95f4\uff0c\u65bd\u6cd5\u8005\u80fd\u591f\u770b\u5230\u5176\u4ed6\u4eba\uff0c\u4f46\u5374\u65e0\u6cd5\u78b0\u89e6\uff0c\u65e0\u6cd5\u4f5c\u7528\u5f71\u54cd\uff0c\u7b49\u4e8e\u5c31\u662f\u4e2a\u5e7b\u5f71\u3002</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u8d81\u7740\u65f6\u95f4\u505c\u6b62\uff0c\u64cd\u5200\u4e0a\u524d\u780d\u4eba\u7684\u60f3\u6cd5\u662f\u884c\u4e0d\u901a\u7684\uff0c\u9b54\u6cd5\u98de\u5f39\u3001\u89e3\u79bb\u3001\u77f3\u5316\u4e4b\u7c7b\u76f4\u63a5\u9488\u5bf9\u4eba\u8fdb\u884c\u653b\u51fb\u7684\u6cd5\u672f\u4e5f\u538b\u6839\u6ca1\u7528\uff0c\u56e0\u4e3a\u5728\u65bd\u6cd5\u8005\u7684\u65f6\u7a7a\u91cc\uff0c\u76ee\u6807\u5176\u5b9e\u4e0d\u5b58\u5728\u3002\u4f46\u8fd9\u5e76\u4e0d\u7b49\u4e8e\u8fd9\u4e2a\u6cd5\u672f\u6210\u4e86\u6beb\u65e0\u7528\u5904\u7684\u5c60\u9f99\u6280\uff0c\u81f3\u5c11\u65bd\u6cd5\u8005\u53ef\u4ee5\u4e3a\u81ea\u5df1\u6784\u5efa\u9632\u5fa1\u3001\u53ec\u5524\u5e2e\u624b\uff0c\u6216\u8005\u4f7f\u7528\u8303\u56f4\u6027\u7684\u6cd5\u672f\uff0c\u4e0d\u5fc5\u9488\u5bf9\u7279\u5b9a\u76ee\u6807\uff0c\u751a\u81f3\u9003\u8dd1\u3002</p><p>\u514b\u52b3\u62d3\u521a\u624d\u4f7f\u7528\u65f6\u95f4\u505c\u6b62\uff0c\u626d\u66f2\u5ef6\u957f\u81ea\u5df1\u7684\u65f6\u7a7a\uff0c\u6309\u7167\u6b21\u5e8f\uff0c\u4e00\u4e2a\u53c8\u4e00\u4e2a\u5730\u63a5\u8fde\u65bd\u5c55\u4e09\u4e2a\u6cd5\u672f\uff08\u4e8b\u5b9e\u4e0a\uff0c\u5982\u679c\u4e0d\u662f\u53f3\u81c2\u53d7\u4f24\uff0c\u4ed6\u6216\u8bb8\u8fd8\u80fd\u505a\u5f97\u66f4\u591a\uff09\u3002\u4f46\u5728\u743c\u6069\u548c\u4e9a\u5f53\u65af\u773c\u4e2d\uff0c\u5374\u53ea\u770b\u89c1\u4ed6\u7684\u52a8\u4f5c\u9661\u7136\u5feb\u5f97\u4e0d\u53ef\u601d\u8bae\uff0c\u540c\u65f6\u5b8c\u6210\u4e86\u4e09\u4e2a\u6cd5\u672f\u3002</p><p>\u529b\u573a\u5251\u5f53\u80f8\u98de\u523a\uff0c\u4e9a\u5f53\u65af\u5306\u5fd9\u62ac\u624b\uff0c\u4ece\u6307\u5c16\u5f39\u51fa\u4e94\u679a\u7ea2\u8272\u98de\u5f39\uff0c\u6253\u5728\u5251\u810a\u4e0a\uff0c\u53d1\u51fa\u6e05\u8106\u7684\u91d1\u5c5e\u94ff\u9535\u58f0\u3002\u529b\u573a\u5251\u7a0d\u7a0d\u505c\u6ede\uff0c\u53c8\u7ee7\u7eed\u523a\u6765\uff0c\u4e9a\u5f53\u65af\u5feb\u901f\u6bd4\u5212\u51e0\u4e2a\u624b\u52bf\uff0c\u8bd5\u56fe\u89e3\u6d88\u8fd9\u4e2a\u6cd5\u672f\uff0c\u4ed6\u5728\u5851\u80fd\u9b54\u6cd5\u4e0a\u7684\u9020\u8be3\u663e\u7136\u80dc\u8fc7\u514b\u52b3\u62d3\uff0c\u4f46\u5931\u4e86\u5148\u673a\uff0c\u53ea\u80fd\u6682\u65f6\u5c06\u529b\u573a\u5251\u56fa\u5b9a\u5728\u7a7a\u4e2d\u3002</p><p>\u5f17\u6d1b\u9b54\u671d\u7740\u743c\u6069\u4fef\u51b2\u800c\u4e0b\uff0c\u4ed6\u7684\u53cc\u7ffc\u6325\u52a8\uff0c\u5e26\u8d77\u4e00\u9635\u8150\u5c38\u822c\u7684\u6076\u81ed\uff0c\u5f3a\u58ee\u53cc\u8db3\u672b\u7aef\u662f\u94a2\u94c1\u822c\u7684\u5229\u722a\uff0c\u95ea\u70c1\u7740\u5371\u9669\u7684\u5149\u6cfd\u3002\u5b83\u901f\u5ea6\u6781\u5feb\uff0c\u743c\u6069\u751a\u81f3\u6765\u4e0d\u53ca\u505a\u51fa\u53cd\u5e94\uff0c\u4ed6\u7684\u955c\u5f71\u3001\u9ad8\u7b49\u5deb\u5e08\u62a4\u7532\u548c\u62a4\u76fe\u672f\u5df2\u7ecf\u5728\u521a\u624d\u7684\u6218\u6597\u4e2d\u88ab\u89e3\u9664\uff0c\u6b64\u65f6\u8eab\u4e0a\u53ea\u6709\u77f3\u80a4\u3002\u773c\u770b\u8eb2\u907f\u4e0d\u53ca\uff0c\u743c\u6069\u65e0\u5948\u4e4b\u4e0b\uff0c\u62ac\u8d77\u53cc\u81c2\u62a4\u4f4f\u5934\u8138\uff0c\u5f80\u4e0a\u4e00\u683c\u3002</p><p>\u77f3\u5c51\u7eb7\u98de\uff0c\u5f17\u6d1b\u9b54\u7684\u5229\u722a\u5c06\u77f3\u80a4\u6495\u5f97\u7c89\u788e\uff0c\u743c\u6069\u53cc\u81c2\u9178\u9ebb\uff0c\u603b\u7b97\u52c9\u5f3a\u8eb2\u5f00\u4e00\u51fb\u3002\u4ed6\u5fc3\u5ff5\u4e00\u52a8\uff0c\u9f99\u9cde\u76fe\u4ece\u65c1\u8fb9\u98de\u65cb\u56de\u821e\uff0c\u9694\u5728\u5f17\u6d1b\u9b54\u9762\u524d\uff0c\u81ea\u5df1\u5feb\u6b65\u540e\u9000\uff0c\u51c6\u5907\u62c9\u5f00\u8ddd\u79bb\u65bd\u6cd5\u3002</p><p>\u7136\u540e\u4ed6\u7684\u80cc\u540e\u88ab\u91cd\u91cd\u4e00\u649e\uff0c\u4eff\u4f5b\u94c1\u9524\u8f70\u7838\uff0c\u867d\u7136\u6709\u77f3\u80a4\u62a4\u8eab\uff0c\u5230\u5e95\u4e5f\u7981\u53d7\u4e0d\u4f4f\u8fd9\u6837\u5de8\u5927\u7684\u51b2\u51fb\uff0c\u987f\u65f6\u6c14\u8840\u4e0a\u6d8c\uff0c\u773c\u524d\u53d1\u9ed1\uff0c\u6251\u5730\u5f80\u524d\u6454\u5012\u3002\u4ed6\u5c31\u52bf\u4e00\u4e2a\u7ffb\u8eab\uff0c\u56de\u5934\u770b\u53bb\uff0c\u53d1\u73b0\u59ae\u745f\u6b63\u9760\u7740\u5899\u58c1\u7ad9\u7acb\uff0c\u53cc\u624b\u5408\u63e1\uff0c\u6307\u5411\u7740\u4ed6\u3002</p><p>\u5f17\u6d1b\u9b54\u8d81\u673a\u62e8\u5f00\u9f99\u9cde\u76fe\uff0c\u518d\u5ea6\u98de\u6251\u4e0b\u6765\uff0c\u59ae\u745f\u53c8\u5728\u5f00\u59cb\u5ff5\u8bf5\u4e00\u4e2a\u65b0\u7684\u5492\u8bed\u3002\u743c\u6069\u6765\u4e0d\u53ca\u8d77\u8eab\u8eb2\u907f\uff0c\u4f59\u5149\u77a5\u89c1\u65c1\u8fb9\u6b63\u662f\u706b\u76c6\uff0c\u4e0d\u5047\u601d\u7d22\u7ffb\u8eab\u4e00\u6eda\uff0c\u6ca1\u5165\u706b\u4e2d\uff0c\u968f\u5373\u4ece\u8fdc\u5904\u53e6\u5916\u4e00\u4e2a\u706b\u76c6\u4e2d\u73b0\u8eab\u51fa\u6765\u3002</p><p>\u7f57\u4e1d\u7684\u795e\u5802\uff0c\u796d\u575b\u5468\u56f4\u90fd\u5fc5\u5b9a\u6709\u516b\u4e2a\u5927\u706b\u76c6\uff0c\u8c61\u5f81\u8718\u86db\u7684\u516b\u6761\u817f\uff0c\u9b54\u6cd5\u706b\u7130\u5347\u817e\uff0c\u7ec8\u5e74\u4e0d\u606f\u3002\u743c\u6069\u501f\u52a9\u706b\u4e2d\u8df3\u8dc3\uff0c\u6210\u529f\u62c9\u5f00\u8ddd\u79bb\uff0c\u4ed6\u518d\u4e0d\u6562\u8fdf\u7591\uff0c\u63a5\u8fde\u53e0\u52a0\u4e24\u4e2a\u53d8\u5f62\u672f\u5728\u5f17\u6d1b\u9b54\u8eab\u4e0a\uff0c\u7b2c\u4e00\u4e2a\u6cd5\u672f\u6ca1\u6709\u6210\u529f\uff0c\u7b2c\u4e8c\u4e2a\u751f\u6548\u4e86\uff0c\u6076\u9b54\u88ab\u53d8\u6210\u4e86\u4e00\u53ea\u6d1b\u65af\u517d\uff0c\u4ece\u7a7a\u4e2d\u6389\u843d\u4e0b\u6765\uff0c\u6454\u5f97\u534a\u6b7b\u3002\u59ae\u745f\u5b8c\u6210\u4e86\u6cd5\u672f\uff0c\u4f46\u88ab\u743c\u6069\u7684\u6212\u6307\u53cd\u5f39\u4e86\u56de\u53bb\uff0c\u5979\u7684\u5934\u90e8\u5982\u906d\u91cd\u51fb\uff0c\u8f6f\u8f6f\u5012\u5730\u3002</p><p>\u4e9a\u5f53\u65af\u53cd\u5236\u6cd5\u672f\u7ec8\u4e8e\u6210\u529f\uff0c\u5c06\u529b\u573a\u5251\u8fd4\u5f52\u65e0\u5f62\uff0c\u4ed6\u5f00\u59cb\u8bd5\u56fe\u89e3\u9664\u7b3c\u7f69\u4fdd\u62a4\u7740\u514b\u52b3\u62d3\u7684\u5f69\u8272\u7403\u4f53\u3002\u90a3\u662f\u8679\u5149\u6cd5\u7403\uff0c\u5171\u6709\u4e03\u5c42\uff0c\u4e03\u8272\u8679\u5f69\u5faa\u73af\u4ea4\u66ff\u51fa\u73b0\uff0c\u6bcf\u4e00\u79cd\u989c\u8272\u4ee3\u8868\u4e00\u79cd\u9b54\u6cd5\u9632\u5fa1\uff0c\u53ef\u4ee5\u963b\u9694\u5404\u79cd\u8fd1\u8eab\u548c\u8fdc\u7a0b\u6b66\u5668\u3001\u5143\u7d20\u548c\u80fd\u91cf\u4f24\u5bb3\u3001\u9884\u8a00\u63a2\u6d4b\u3001\u5fc3\u7075\u653b\u51fb\u4ee5\u53ca\u4ea1\u7075\u672f\uff0c\u53ef\u8c13\u575a\u4e0d\u53ef\u6467\uff0c\u582a\u79f0\u662f\u4e16\u754c\u4e0a\u6700\u575a\u56fa\u7684\u9b54\u6cd5\u9632\u5fa1\u3002\u514b\u52b3\u62d3\u521a\u624d\u88ab\u5bd2\u51b0\u67aa\u523a\u7a7f\u624b\u81c2\uff0c\u53d7\u4f24\u4e0d\u8f7b\uff0c\u6b64\u65f6\u5e94\u8be5\u662f\u8eb2\u5728\u8679\u5149\u6cd5\u7403\u4e2d\u5598\u606f\u4fee\u6574\uff0c\u5916\u9762\u7684\u4eba\u4e0d\u5148\u6253\u7834\u6cd5\u7403\uff0c\u5c31\u6839\u672c\u4e0d\u53ef\u80fd\u4f24\u5bb3\u5230\u4ed6\uff1b\u5f53\u7136\uff0c\u53cd\u8fc7\u6765\uff0c\u514b\u52b3\u62d3\u7684\u6cd5\u672f\u4e5f\u6ca1\u529e\u6cd5\u7a7f\u900f\u6cd5\u7403\u51fa\u6765\u3002</p><p>\u8981\u7834\u89e3\u8679\u5149\u6cd5\u7403\uff0c\u53ea\u6709\u4e24\u79cd\u65b9\u6cd5\u3002\u7b2c\u4e00\u79cd\u662f\u7528\u5927\u88c2\u89e3\u5f3a\u884c\u8f70\u51fb\uff0c\u5f53\u65e5\u5728\u9634\u9b42\u57ce\uff0c\u8679\u5f69\u9f99\u5c31\u66fe\u7ecf\u7528\u8fc7\u8fd9\u9053\u6cd5\u672f\uff0c\u5728\u6f2b\u5929\u7684\u653b\u51fb\u4e4b\u4e2d\u6beb\u53d1\u65e0\u4f24\uff0c\u6700\u540e\u88ab\u970d\u6770\u54c8\u7eb3\u4e00\u51fb\u5927\u88c2\u89e3\u51fb\u7834\uff0c\u94e9\u7fbd\u800c\u5f52\u3002\u7136\u800c\u4e9a\u5f53\u65af\u663e\u7136\u5df2\u7ecf\u6ca1\u6709\u7b2c\u4e8c\u5f20\u5927\u88c2\u89e3\u5377\u8f74\u4e86\uff0c\u65e0\u6cd5\u5f3a\u884c\u6467\u6bc1\uff0c\u53ea\u80fd\u7528\u7b2c\u4e8c\u79cd\u65b9\u6cd5\u3002</p><p>\u8679\u5149\u6cd5\u7403\u662f\u4e03\u5c42\u5149\u7403\u6df7\u5408\uff0c\u6bcf\u79cd\u8272\u5f69\u4ee3\u8868\u4e00\u79cd\u9b54\u6cd5\u9632\u5fa1\uff0c\u4e5f\u6709\u76f8\u5e94\u7684\u7834\u89e3\u65b9\u6cd5\u3002\u6309\u7167\u4e00\u5b9a\u7684\u987a\u5e8f\uff0c\u5c31\u80fd\u9010\u6b21\u62c6\u9664\uff0c\u6700\u7ec8\u5b8c\u5168\u6467\u6bc1\u6cd5\u7403\u3002</p><p>\u4e9a\u5f53\u65af\u76ef\u7740\u8679\u5149\u6cd5\u7403\uff0c\u5f53\u5b83\u53d8\u6362\u5230\u7ea2\u8272\u65f6\uff0c\u9996\u5e2d\u5deb\u5e08\u4f38\u51fa\u53f3\u624b\u98df\u6307\uff0c\u5bd2\u51b0\u96fe\u6c14\u4ee5\u6307\u5c16\u4e3a\u4e2d\u5fc3\u6269\u6563\u51fa\u6765\uff0c\u5f62\u6210\u4e00\u7247\u9525\u5f62\u533a\u57df\uff0c\u5c06\u6cd5\u7403\u51bb\u7ed3\u5176\u4e2d\u3002\u6e05\u8106\u7684\u788e\u88c2\u58f0\u54cd\u8d77\uff0c\u5bd2\u51b0\u6563\u53bb\uff0c\u7ea2\u8272\u5c42\u9762\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u4ed6\u7d27\u63a5\u7740\u6478\u51fa\u4e00\u4e2a\u6781\u5fae\u5c0f\u7684\u76ae\u5236\u98ce\u7bb1\uff0c\u770b\u8d77\u6765\u7b80\u76f4\u50cf\u73a9\u5177\uff0c\u8f7b\u8f7b\u6324\u538b\uff0c\u540c\u65f6\u5ff5\u51fa\u5492\u8bed\u3002\u72c2\u98ce\u547c\u5578\u800c\u8d77\uff0c\u4ece\u6cd5\u7403\u4e0a\u63a0\u8fc7\uff0c\u6a59\u8272\u5c42\u9762\u989c\u8272\u6e10\u6e10\u6de1\u53bb\uff0c\u5c31\u50cf\u5730\u9762\u4e0a\u7684\u7070\u5c18\u88ab\u522e\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002</p><p>\u9ec4\u8272\u5c42\u9762\u663e\u9732\u51fa\u6765\uff0c\u4e9a\u5f53\u65af\u4fa7\u8eab\u9000\u5f00\uff0c\u8ba9\u51fa\u4f4d\u7f6e\uff0c\u743c\u6069\u8d70\u4e0a\u524d\u3002\u867d\u7136\u8fd9\u4e24\u4f4d\u5deb\u5e08\u6b64\u524d\u6ca1\u6709\u5408\u4f5c\u8fc7\uff0c\u4f46\u540c\u4e3a\u9ad8\u9636\u65bd\u6cd5\u8005\uff0c\u5f7c\u6b64\u95f4\u81ea\u6709\u9ed8\u5951\u3002\u4e00\u9053\u7fe0\u7eff\u8272\u5c04\u7ebf\u4ece\u7d2b\u6c34\u6676\u6cd5\u6756\u9876\u7aef\u53d1\u51fa\uff0c\u6467\u6bc1\u4e86\u9ec4\u8272\u5c42\u9762\u3002</p><p>\u7eff\u8272\u5c42\u9762\u9700\u8981\u7528\u7a7f\u5899\u672f\u62b5\u6d88\uff0c\u743c\u6069\u7528\u5377\u8f74\u5b8c\u6210\u4e86\u6cd5\u672f\uff0c\u7d27\u63a5\u7740\u4e9a\u5f53\u65af\u5f39\u51fa\u4e94\u9897\u9b54\u6cd5\u98de\u5f39\uff0c\u51fb\u7834\u4e86\u84dd\u8272\u5c42\u9762\u3002\u4e9a\u5f53\u65af\u62c9\u4e0b\u6597\u7bf7\u7684\u515c\u5e3d\uff0c\u5c06\u624b\u6309\u5728\u6cd5\u7403\u4e0a\uff0c\u5ff5\u51fa\u5492\u8bed\uff0c\u539f\u672c\u6563\u53d1\u9752\u8272\u5fae\u5149\u7684\u7403\u4f53\u7a81\u7136\u7206\u53d1\u51fa\u8000\u773c\u7684\u4eae\u5149\uff0c\u5c06\u5468\u56f4\u534a\u5f84\u516d\u5341\u82f1\u5c3a\u4e4b\u5185\u7167\u5f97\u72b9\u5982\u767d\u663c\u3002\u5f3a\u5149\u655b\u53bb\uff0c\u9752\u8272\u5c42\u9762\u540c\u65f6\u6d88\u5931\u3002</p><p>\u53ea\u5269\u4e0b\u6700\u540e\u7684\u7d2b\u8272\u5c42\u9762\u4e86\uff0c\u5b83\u9700\u8981\u7528\u89e3\u9664\u9b54\u6cd5\u6765\u62b5\u6d88\u3002</p><p>\u901a\u5e38\u6765\u8bf4\uff0c\u4e0d\u7ba1\u4ec0\u4e48\u504f\u597d\u7684\u5deb\u5e08\uff0c\u57fa\u672c\u90fd\u4f1a\u51c6\u5907\u4e00\u4e24\u4e2a\u89e3\u9664\u9b54\u6cd5\uff0c\u743c\u6069\u4e5f\u4e0d\u4f8b\u5916\u3002\u4ed6\u5ff5\u51fa\u5492\u8bed\uff0c\u6307\u5c16\u8513\u5ef6\u51fa\u767d\u8272\u96fe\u6c14\uff0c\u50cf\u6f6e\u6c34\u822c\u51b2\u5237\u800c\u8fc7\u3002\u7d2b\u8272\u7403\u4f53\u6d88\u5931\u4e86\uff0c\u663e\u51fa\u91cc\u9762\u7684\u514b\u52b3\u62d3\u3002</p><p>\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u9996\u5e2d\u5deb\u5e08\u770b\u8d77\u6765\u795e\u60c5\u75b2\u60eb\uff0c\u5bd2\u51b0\u67aa\u5df2\u7ecf\u88ab\u4ece\u53f3\u81c2\u4e0a\u62d4\u4e86\u4e0b\u6765\uff0c\u4e22\u5728\u5730\u4e0a\u3002\u4e9a\u5f53\u65af\u7528\u4e00\u67c4\u95ea\u7535\u5f62\u72b6\u7684\u6cd5\u6756\u5bf9\u51c6\u4ed6\uff0c\u201c\u4f60\u8f93\u4e86\uff0c\u514b\u52b3\u62d3\u3002\u201d\u4ed6\u5fae\u7b11\u7740\u8bf4\uff0c\u7136\u540e\u7a81\u7136\u8138\u8272\u4e00\u53d8\u3002</p><p>\u743c\u6069\u4e5f\u770b\u51fa\u4e86\u95ee\u9898\u6240\u5728\uff0c\u773c\u524d\u7684\u5deb\u5e08\u7c97\u770b\u548c\u514b\u52b3\u62d3\u65e0\u5f02\uff0c\u7a0d\u4e00\u6ce8\u610f\u5c31\u4f1a\u53d1\u73b0\u7ec6\u5fae\u4e4b\u5904\u5927\u6709\u4e0d\u540c\uff0c\u800c\u4e14\u603b\u6709\u4e00\u79cd\u7f25\u7f08\u4e0d\u5b9e\u611f\u2026\u2026</p><p>\u4e9a\u5f53\u65af\u6325\u624b\u4e00\u626b\uff0c\u6cd5\u6756\u4ece\u514b\u52b3\u62d3\u7684\u8eab\u4f53\u91cc\u6beb\u65e0\u6ede\u788d\u5730\u7a7f\u8fc7\uff0c\u7a7a\u6c14\u4e2d\u4e00\u9635\u6ce2\u6298\uff0c\u4ed6\u6d88\u5931\u4e86\u3002</p><p>\u201c\u4e00\u4e2a\u5e7b\u8c61\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4ed6\u9003\u4e86\u3002\u201d</p><p>\u4e8b\u5b9e\u8bc1\u660e\uff0c\u4ed6\u8fd8\u662f\u4f4e\u4f30\u4e86\u5bf9\u624b\uff0c\u514b\u52b3\u62d3\u521a\u624d\u53d1\u52a8\u65f6\u95f4\u505c\u6b62\uff0c\u5e76\u4e0d\u662f\u65bd\u5c55\u4e86\u4e09\u4e2a\u6cd5\u672f\uff0c\u800c\u662f\u4e94\u4e2a\u2014\u2014\u4ed6\u8fd8\u521b\u9020\u4e86\u4e00\u4e2a\u548c\u81ea\u5df1\u4e00\u6a21\u4e00\u6837\u7684\u5e7b\u8c61\uff0c\u7559\u5728\u8679\u5149\u6cd5\u7403\u91cc\uff0c\u4ee5\u53ca\u4e00\u4e2a\u7528\u4e8e\u8131\u9003\u7684\u6cd5\u672f\uff0c\u6709\u53ef\u80fd\u662f\u4f20\u9001\u4e4b\u7c7b\u3002</p><p>\u8fd9\u662f\u660e\u667a\u4e4b\u4e3e\uff0c\u4ed6\u53f3\u81c2\u53d7\u4f24\uff0c\u4e25\u91cd\u5f71\u54cd\u65bd\u6cd5\uff0c\u4e00\u8eab\u9b54\u6cd5\u7269\u54c1\u88c5\u5907\u53c8\u5c3d\u6570\u88ab\u6bc1\uff0c\u4ee5\u4e00\u654c\u4e8c\u7684\u8bdd\u80dc\u7b97\u4e0d\u9ad8\u3002\u53cd\u6b63\u4ed6\u5728\u81ea\u5df1\u7684\u4e3b\u573a\uff0c\u6682\u65f6\u9003\u8d70\uff0c\u4fee\u6574\u597d\u4e86\u5377\u571f\u91cd\u6765\u5c31\u662f\u3002\u5bb6\u65cf\u6218\u4e89\u8fdb\u884c\u5230\u6b64\u65f6\uff0c\u5927\u7ea6\u5df2\u7ecf\u8fc7\u53bb\u4e09\u4e2a\u65f6\u8fb0\uff0c\u8fd8\u6709\u4e5d\u4e2a\u65f6\u8fb0\uff0c\u65f6\u95f4\u8fd8\u7b97\u5145\u88d5\u3002</p><p>\u4e24\u4f4d\u5deb\u5e08\u5bf9\u89c6\u4e86\u4e00\u773c\uff0c\u4e9a\u5f53\u65af\u53ec\u5524\u6765\u4e00\u5835\u575a\u56fa\u7684\u51b0\u5899\uff0c\u518d\u6b21\u5c01\u4f4f\u4e86\u95e8\uff0c\u63a5\u7740\u4ed6\u4eec\u8f6c\u8eab\u6536\u62fe\u6b8b\u5c40\uff0c\u5728\u4e00\u6839\u96d5\u6ee1\u8718\u86db\u82b1\u7eb9\u56fe\u6848\u7684\u77f3\u67f1\u540e\u9762\uff0c\u743c\u6069\u627e\u5230\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u3002</p><p>\u203b\u203b\u203b</p><p>\u5353\u5c14\u5c11\u5973\u5750\u5728\u5730\u677f\u4e0a\uff0c\u80cc\u9760\u7740\u77f3\u67f1\uff0c\u5979\u7684\u53cc\u773c\u7d27\u95ed\u7740\uff0c\u4e00\u65b9\u9762\u662f\u56e0\u4e3a\u707c\u4f24\uff0c\u4e00\u65b9\u9762\u662f\u56e0\u4e3a\u521a\u624d\u7684\u70c8\u65e5\u5f3a\u5149\u7167\u5c04\uff0c\u8fde\u4f8d\u7236\u6597\u7bf7\u90fd\u6ca1\u80fd\u62b5\u6321\u4f4f\u3002\u9633\u708e\u5c04\u7ebf\u70e7\u4f24\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u5927\u534a\u4e2a\u8eab\u4f53\uff0c\u4eff\u4f5b\u6709\u65e0\u6570\u94a2\u9488\u5728\u4e00\u523b\u4e0d\u505c\u5730\u523a\u7740\u5979\u7684\u76ae\u80a4\uff0c\u539f\u672c\u7cbe\u81f4\u4fcf\u4e3d\u7684\u8138\u5e9e\u73b0\u5728\u6ee1\u662f\u9a87\u4eba\u7684\u6c34\u6ce1\uff0c\u6709\u4e9b\u5df2\u7ecf\u7834\u88c2\u4e86\uff0c\u4ece\u4e2d\u6d41\u51fa\u900f\u660e\u7684\u6db2\u4f53\u3002\u5979\u5634\u5507\u4e0a\u7684\u76ae\u80a4\u70e7\u7126\uff0c\u4e0d\u65ad\u6d41\u8840\uff0c\u8eab\u4e0a\u7684\u4e1d\u7ec7\u957f\u888d\u4e5f\u5927\u534a\u88ab\u70e7\u7194\uff0c\u6b8b\u4f59\u7684\u7126\u9ed1\u7ea4\u7ef4\u548c\u8840\u8089\u7c98\u7ed3\u5728\u4e00\u8d77\u3002\u56e0\u4e3a\u6cbb\u7597\u836f\u6c34\u7684\u4f5c\u7528\uff0c\u6709\u4e9b\u5730\u65b9\u6b7b\u76ae\u5df2\u7ecf\u8131\u843d\uff0c\u91cd\u65b0\u957f\u51fa\u5a07\u5ae9\u7684\u76ae\u80a4\uff0c\u8fd9\u4e2a\u901f\u5ea6\u975e\u5e38\u7f13\u6162\u3002</p><p>\u201c\u4f60\u8fd8\u597d\u5427\u3002\u201d\u743c\u6069\u4f4e\u58f0\u95ee\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u52c9\u5f3a\u7741\u5f00\u53f3\u773c\uff08\u5979\u7684\u5de6\u773c\u4e25\u91cd\u707c\u4f24\uff0c\u6682\u65f6\u8fd8\u65e0\u6cd5\u7741\u5f00\uff09\uff0c\u5b9a\u795e\u770b\u4e86\u51e0\u79d2\uff0c\u624d\u8fa8\u8ba4\u51fa\u662f\u743c\u6069\u3002\u201c\u8fd8\u6ca1\u6b7b\uff0c\u201d\u5979\u5fc3\u60c5\u7cdf\u7cd5\u5730\u8bf4\uff0c\u201c\u90a3\u4e2a\u8be5\u4e0b\u6df1\u6e0a\u7684\u5bb6\u4f19\u2026\u2026\u201d</p><p>\u8bf4\u8bdd\u65f6\u808c\u8089\u7275\u626f\uff0c\u8138\u4e0a\u7684\u6c34\u6ce1\u53c8\u7834\u88c2\u4e86\u51e0\u4e2a\uff0c\u6c57\u6c34\u4ece\u989d\u5934\u6ef4\u4e0b\uff0c\u4ee4\u5979\u4e0d\u5f97\u4e0d\u628a\u773c\u775b\u95ed\u4e0a\uff0c\u4ee5\u514d\u6d41\u8fdb\u3002\u201c\u611f\u89c9\u81ea\u5df1\u50cf\u4e00\u53ea\u6b63\u5728\u8715\u76ae\u7684\u8718\u86db\uff0c\u201d\u5353\u5c14\u5c11\u5973\u8bf4\uff0c\u201c\u6211\u73b0\u5728\u7684\u6837\u5b50\u770b\u8d77\u6765\u662f\u4e0d\u662f\u5f88\u6050\u6016\uff1f\u201d</p><p>\u201c\u4f1a\u597d\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u836f\u6c34\u4f1a\u6cbb\u597d\u4f60\u3002\u201d</p><p>\u5979\u8f7b\u8f7b\u6447\u5934\uff0c\u795e\u60c5\u90c1\u90c1\u4e0d\u4e50\uff0c\u201c\u836f\u6c34\u751f\u6548\u592a\u7f13\u6162\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f1a\u7559\u4e0b\u4f24\u75a4\u2026\u2026\u201d</p><p>\u201c\u522b\u8bf4\u8bdd\u3002\u201d\u743c\u6069\u6253\u65ad\u3002</p><p>\u4ed6\u77e5\u9053\u7ef4\u5eb7\u5c3c\u4e9a\u6240\u8bf4\u662f\u5b9e\uff0c\u836f\u6c34\u6cbb\u7597\u901f\u5ea6\u7f13\u6162\uff0c\u6548\u679c\u4e5f\u8fdc\u4e0d\u5982\u795e\u672f\uff0c\u5982\u679c\u4f24\u52bf\u592a\u91cd\uff0c\u5bb9\u6613\u7559\u4e0b\u4f24\u75a4\uff0c\u5982\u679c\u522b\u7684\u5730\u65b9\u5012\u4e5f\u7f62\u4e86\uff0c\u8138\u4e0a\u7684\u95ee\u9898\u5c31\u6bd4\u8f83\u5927\u3002\u5353\u5c14\u7d20\u6765\u4ee5\u7cbe\u81f4\u4f18\u96c5\u8457\u79f0\uff0c\u5973\u6027\u5927\u591a\u5bb9\u8c8c\u4fcf\u4e3d\uff0c\u8fd9\u4e5f\u5f62\u6210\u4e00\u79cd\u793e\u4f1a\u98ce\u6c14\uff0c\u5f80\u5f80\u4f1a\u89c6\u90a3\u4e9b\u4e11\u964b\u8005\u4e3a\u6b8b\u75be\uff0c\u662f\u7f57\u4e1d\u7684\u9057\u5f03\u4e4b\u6c11\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u4e00\u60f3\u5230\u81ea\u5df1\u53ef\u80fd\u6709\u6bc1\u5bb9\u4e4b\u865e\uff0c\u5fc3\u60c5\u597d\u5f97\u8d77\u6765\u624d\u602a\u3002</p><p>\u5982\u4eca\u7f57\u4e1d\u6c89\u5bc2\uff0c\u7267\u5e08\u4e27\u5931\u795e\u672f\uff0c\u6cbb\u7597\u4f24\u52bf\u5927\u591a\u4f9d\u9760\u836f\u6c34\u3002\u6216\u8bb8\u6709\u5c11\u6570\u7267\u5e08\u8fd8\u5b58\u7559\u4e86\u4e00\u70b9\u6cbb\u7597\u795e\u672f\uff0c\u90a3\u4e5f\u80af\u5b9a\u662f\u6253\u7b97\u7528\u6765\u7ed9\u81ea\u5df1\u5173\u952e\u65f6\u523b\u6551\u547d\u7528\u7684\uff0c\u600e\u4e48\u53ef\u80fd\u6d6a\u8d39\u5728\u7ef4\u5eb7\u5c3c\u4e9a\u8eab\u4e0a\uff0c\u5353\u5c14\u5374\u6ca1\u8fd9\u4e48\u65e0\u79c1\u53cb\u7231\u3002</p><p>\u4e00\u53ea\u8718\u86db\u4ece\u67f1\u5b50\u4e0a\u8dcc\u4e0b\u6765\uff0c\u843d\u5728\u7ef4\u5eb7\u5c3c\u4e9a\u88f8\u9732\u7684\u80a9\u8180\u4e0a\uff0c\u5b83\u5212\u5f00\u516b\u53ea\u811a\uff0c\u5306\u5306\u51c6\u5907\u79bb\u5f00\uff0c\u743c\u6069\u4f38\u624b\u62c8\u8d77\u5b83\uff0c\u7529\u5230\u89d2\u843d\u91cc\u53bb\u4e86\u3002</p><p>\u201c\u4f60\u5728\u4f24\u5bb3\u5723\u7269\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u8bed\u6c14\u6709\u4e9b\u4e0d\u5feb\u3002</p><p>\u201c\u795e\u540e\u5df2\u7ecf\u6d88\u5931\u4e86\u3002\u201d\u743c\u6069\u56de\u7b54\u3002</p><p>\u4ed6\u89c1\u7ef4\u5eb7\u5c3c\u4e9a\u5927\u4f53\u6ca1\u4e8b\uff0c\u8d77\u8eab\u518d\u53bb\u770b\u9b54\u88d4\u5353\u5c14\u3002\u8fd9\u4e0d\u5e78\u7684\u5bb6\u4f19\u88ab\u4e9a\u5f53\u65af\u4e00\u51fb\u6bd5\u683c\u6bd4\u7c89\u788e\u62f3\u6b63\u9762\u7838\u4e2d\uff0c\u80f8\u53e3\u584c\u9677\uff0c\u808b\u9aa8\u6574\u9f50\u5730\u65ad\u4e86\u4e00\u6392\uff0c\u5df2\u7ecf\u6c14\u606f\u5944\u5944\u3002\u743c\u6069\u62d4\u51fa\u5315\u9996\uff0c\u5272\u65ad\u5b83\u7684\u54bd\u5589\uff0c\u9001\u8fd9\u90aa\u6076\u751f\u7269\u4e0a\u8def\u3002</p><p>\u59ae\u745f\u4f9d\u65e7\u5728\u6655\u8ff7\u4e2d\uff0c\u5979\u88ab\u4e9a\u5f53\u65af\u7528\u4e00\u4e2a\u7403\u5f62\u900f\u660e\u529b\u573a\u56da\u7981\u8d77\u6765\uff0c\u770b\u8d77\u6765\u8fd8\u6709\u7528\u9014\u3002\u4e9a\u5f53\u65af\u7ad9\u5728\u796d\u575b\u524d\uff0c\u6b63\u53d6\u51fa\u4e00\u4e2a\u624b\u67c4\u4e0a\u9576\u5d4c\u94bb\u77f3\u7684\u76ee\u955c\uff0c\u900f\u8fc7\u5b83\u89c2\u5bdf\u795e\u50cf\u3002</p><p>\u743c\u6069\u8d70\u4e86\u8fc7\u6765\u3002</p><p>\u201c\u7ed3\u70b9\u5c31\u5728\u795e\u50cf\u91cc\uff0c\u201d\u4e9a\u5f53\u65af\u8bf4\uff0c\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u6211\u4eec\u5f97\u6253\u7834\u5b83\u3002\u201d</p><p>\u201c\u6211\u60f3\u4e5f\u662f\u3002\u201d\u743c\u6069\u8bf4\uff0c\u7ad9\u7740\u4e0d\u52a8\u3002</p><p>\u8fd9\u4e2a\u795e\u50cf\u65e2\u7136\u88ab\u7528\u6765\u4fdd\u62a4\u7ed3\u70b9\uff0c\u81ea\u7136\u4e5f\u6709\u6240\u9632\u5fa1\u3002\u743c\u6069\u7b80\u5355\u8fa8\u8bc6\u4e86\u4e00\u4e0b\uff0c\u81f3\u5c11\u5c31\u770b\u89c1\u4e86\u81f3\u5c11\u8d85\u8fc7\u4e94\u4e2a\u7684\u6cd5\u672f\u9677\u9631\uff0c\u867d\u7136\u53ea\u8981\u7ed9\u4ed6\u65f6\u95f4\uff0c\u9010\u4e2a\u53cd\u5236\u89e3\u6d88\u4e5f\u53ef\u4ee5\u529e\u5230\uff0c\u4f46\u73b0\u5728\u65e2\u7136\u6709\u9996\u5e2d\u5deb\u5e08\u5728\u65c1\u8fb9\uff0c\u4ed6\u81ea\u7136\u5c31\u61d2\u5f97\u51fa\u5934\u4e86\u3002</p><p>\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u8fd9\u4e8b\u60c5\u4e5f\u786e\u5b9e\u6709\u70b9\u68d8\u624b\u3002\u5982\u679c\u76f4\u63a5\u7528\u6cd5\u672f\u653b\u51fb\uff0c\u4f1a\u88ab\u795e\u50cf\u4e0a\u7684\u9632\u5fa1\u9b54\u6cd5\u53cd\u5f39\uff0c\u6700\u597d\u662f\u7528\u6b66\u5668\u76f4\u63a5\u7838\u3002\u7136\u800c\u770b\u770b\u56db\u5468\uff0c\u4f3c\u4e4e\u8fd8\u771f\u6ca1\u4ec0\u4e48\u8d81\u624b\u5bb6\u4f19\u2014\u2014\u5c31\u7b97\u6709\u5bb6\u4f19\uff0c\u4e24\u4e2a\u5deb\u5e08\u53ea\u6015\u4e5f\u6ca1\u8fd9\u529b\u6c14\u3002</p><p>\u4e9a\u5f53\u65af\u77a5\u4e86\u743c\u6069\u4e00\u773c\uff0c\u4ece\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u4e00\u67c4\u77ed\u5c0f\u7684\u7cbe\u91d1\u6743\u6756\uff0c\u7136\u540e\u5ff5\u51fa\u4e00\u4e2a\u53e3\u4ee4\u3002\u6743\u6756\u5ef6\u4f38\u3001\u626d\u66f2\uff0c\u6700\u7ec8\u53d8\u6210\u4e00\u53ea\u8d85\u7ea7\u5de8\u5927\u7684\u6218\u9524\u3002\u4ed6\u5c06\u6218\u9524\u653e\u5728\u5730\u4e0a\uff0c\u53c8\u53d6\u51fa\u4e00\u74f6\u50cf\u662f\u6ce5\u6d46\u7684\u836f\u6c34\uff0c\u704c\u8fdb\u81ea\u5df1\u53e3\u4e2d\uff0c\u7d27\u63a5\u7740\u5f00\u59cb\u5ff5\u8bf5\u5492\u8bed\u3002</p><p>\u4e00\u80a1\u72c2\u66b4\u7684\u529b\u91cf\u6d8c\u5165\u9996\u5e2d\u5deb\u5e08\u7684\u8eab\u4f53\uff0c\u4ed6\u7684\u76ae\u80a4\u53d8\u5f97\u66f4\u52a0\u575a\u786c\u7ed3\u5b9e\uff0c\u808c\u8089\u66f4\u52a0\u5f3a\u5065\u6709\u529b\uff0c\u4e00\u5757\u5757\u5730\u4ece\u8863\u670d\u4e0b\u5792\u8d77\uff0c\u539f\u672c\u5bbd\u677e\u7684\u957f\u888d\u90fd\u4eff\u4f5b\u5df2\u7ecf\u7d27\u51d1\u8d34\u8eab\u3002\u72c2\u6012\u7684\u706b\u7130\u4ece\u4e9a\u5f53\u65af\u7684\u773c\u4e2d\u718a\u718a\u71c3\u8d77\uff0c\u4ed6\u8868\u60c5\u72f0\u72de\uff0c\u5c31\u50cf\u4e00\u4e2a\u91ce\u86ee\u4eba\u6218\u58eb\u90a3\u6837\u9ad8\u543c\u8d77\u6765\uff0c\u4ece\u5730\u4e0a\u6293\u8d77\u6218\u9524\uff0c\u6325\u821e\u7740\uff0c\u53d1\u51fa\u547c\u5578\u7684\u98ce\u58f0\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u5927\u6b65\u51b2\u5411\u796d\u575b\u4e0a\u7684\u795e\u50cf\u3002</p><p>\u743c\u6069\u8ba4\u51fa\u4e86\u8fd9\u4e2a\u6cd5\u672f\uff0c\u8c2d\u68ee\u53d8\u5f62\u672f\uff0c\u53d8\u5316\u5b66\u6d3e\u7684\u8457\u540d\u6cd5\u672f\u4e4b\u4e00\uff0c\u4ed6\u7684\u6cd5\u672f\u4e66\u91cc\u4e5f\u6284\u5f55\u4e86\uff0c\u4f46\u8fd8\u6ca1\u6765\u5f97\u53ca\u53bb\u5b66\u3002\u5b83\u53ef\u4ee5\u4ee4\u5deb\u5e08\u53d8\u5f97\u51f6\u66b4\u800c\u5145\u6ee1\u529b\u91cf\uff0c\u77ac\u95f4\u9886\u609f\u5404\u79cd\u7cbe\u5999\u7684\u683c\u6597\u6280\u5de7\uff0c\u5c31\u7b97\u90a3\u4e9b\u73a9\u4e86\u4e00\u8f88\u5b50\u6b66\u5668\u7684\u8001\u6218\u58eb\u4e5f\u672a\u5fc5\u80fd\u6bd4\u5f97\u4e0a\uff0c\u5f53\u7136\u540c\u65f6\u4f1a\u6682\u65f6\u9057\u5fd8\u4e00\u5207\u9b54\u6cd5\u6280\u827a\uff0c\u76f4\u5230\u6cd5\u672f\u7ed3\u675f\u3002\u53ea\u4e0d\u8fc7\uff0c\u8fd9\u4e2a\u6cd5\u672f\u6709\u8da3\u5219\u6709\u8da3\u77e3\uff0c\u5374\u5f88\u5c11\u6709\u5deb\u5e08\u4f1a\u51c6\u5907\uff0c\u6bd5\u7adf\u8981\u8bba\u8d77\u6765\uff0c\u8089\u4f53\u7684\u529b\u91cf\u7ec8\u7a76\u6709\u9650\uff0c\u9b54\u6cd5\u7684\u5a01\u80fd\u624d\u662f\u65e0\u7a77\uff0c\u65e2\u7136\u90fd\u5df2\u7ecf\u80fd\u591f\u65bd\u5c55\u8fd9\u4e2a\u6cd5\u672f\uff0c\u5fc5\u5b9a\u5df2\u7ecf\u662f\u9ad8\u9636\u5deb\u5e08\uff0c\u7cbe\u5999\u7684\u6cd5\u672f\u7ec4\u5408\uff0c\u8db3\u4ee5\u641e\u5b9a\u6446\u5e73\u5404\u79cd\u5c40\u9762\uff0c\u4f55\u5fc5\u8fd8\u8981\u50cf\u6218\u58eb\u4e00\u6837\u53bb\u7528\u86ee\u529b\u780d\u6740\uff1f\u5c82\u4e0d\u662f\u5012\u9000\u56de\u53bb\u4e86\u3002</p><p>\u5f53\u7136\uff0c\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u6c38\u8fdc\u4e0d\u8981\u8bf4\u5f97\u90a3\u4e48\u80af\u5b9a\uff0c\u9762\u4e34\u73b0\u5728\u8fd9\u79cd\u7279\u6b8a\u60c5\u5f62\uff0c\u8c2d\u68ee\u53d8\u5f62\u672f\u5c31\u6709\u4e86\u4ef7\u503c\u3002\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u4e9a\u5f53\u65af\u8fd8\u771f\u662f\u6709\u5907\u800c\u6765\uff0c\u8fde\u9524\u5b50\u90fd\u9884\u5907\u597d\u4e86\u3002</p><p>\u8f70\u8f70\u8fde\u54cd\uff0c\u788e\u77f3\u98de\u6e85\uff0c\u4e9a\u5f53\u65af\u5c06\u7cbe\u91d1\u5de8\u9524\u6325\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u4e00\u8bb0\u53c8\u4e00\u8bb0\u91cd\u91cd\u7838\u4e0b\u3002\u4ed6\u7684\u8eab\u4e0a\u4e0d\u65ad\u7206\u51fa\u706b\u7130\u3001\u95ea\u7535\u548c\u9ed1\u8272\u7684\u5f3a\u9178\uff0c\u8fd9\u662f\u795e\u50cf\u4e0a\u7684\u9632\u62a4\u9b54\u6cd5\u5728\u4f24\u5bb3\u4ed6\uff0c\u4f46\u9996\u5e2d\u5deb\u5e08\u5168\u7136\u65e0\u60e7\uff0c\u53ea\u662f\u66f4\u52a0\u7528\u529b\u5730\u6325\u52a8\u5de8\u9524\u3002\u9ed1\u66dc\u77f3\u5236\u6210\u7684\u7f57\u4e1d\u795e\u50cf\u4e0a\u5f88\u5feb\u51fa\u73b0\u88c2\u75d5\uff0c\u8fc5\u901f\u6269\u5927\uff0c\u6700\u7ec8\u54d7\u5566\u4e00\u58f0\u5d29\u584c\u4e0b\u6765\uff0c\u6df1\u78a7\u8272\u7684\u7ed3\u70b9\u51fa\u73b0\u5728\u743c\u6069\u7684\u5965\u672f\u89c6\u89c9\u91cc\u3002</p><p>\u5c31\u662f\u5b83\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u8c2d\u68ee\u53d8\u5f62\u672f\u6301\u7eed\u65f6\u95f4\u5e76\u4e0d\u957f\uff0c\u4ee5\u4e9a\u5f53\u65af\u7684\u9020\u8be3\u4e5f\u4e0d\u8fc7\u4e00\u4e24\u5206\u949f\u3002\u7838\u6bc1\u7f57\u4e1d\u795e\u50cf\u4e4b\u540e\uff0c\u4ed6\u4e5f\u5c31\u91cd\u65b0\u6062\u590d\u8fc7\u6765\u3002</p><p>\u7ed3\u70b9\u65e2\u7136\u5df2\u7ecf\u66b4\u9732\u5728\u9762\u524d\uff0c\u5269\u4e0b\u6765\u7684\u4e8b\u60c5\u4e5f\u5c31\u597d\u529e\u5f97\u591a\u3002\u4e9a\u5f53\u65af\u662f\u8fd9\u5ea7\u5353\u5c14\u57ce\u5e02\u91cc\u7b2c\u4e00\u7b49\u7684\u5927\u5deb\u5e08\uff0c\u743c\u6069\u867d\u7136\u900a\u8272\u4e00\u4e9b\uff0c\u4f46\u5374\u6b63\u597d\u64c5\u957f\u53d8\u5316\u9b54\u6cd5\uff0c\u4e24\u4eba\u914d\u5408\u8d77\u6765\uff0c\u53c8\u6ca1\u6709\u4eba\u6765\u5e72\u6270\uff0c\u4e00\u70b9\u70b9\u5206\u62c6\u7075\u7ebf\uff0c\u963b\u9694\u9b54\u6cd5\u529b\u91cf\u6d41\u52a8\uff0c\u53cd\u5236\u6cd5\u672f\uff0c\u6709\u6761\u4e0d\u7d0a\uff0c\u5927\u7ea6\u82b1\u4e86\u5927\u534a\u4e2a\u5c0f\u65f6\uff0c\u7ec8\u4e8e\u63a5\u8fd1\u5927\u529f\u544a\u6210\u3002</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u4e5f\u6e10\u6e10\u6062\u590d\u8fc7\u6765\uff0c\u8d70\u5230\u743c\u6069\u65c1\u8fb9\u3002\u5979\u8eab\u4e0a\u7684\u5927\u90e8\u4efd\u6b7b\u76ae\u892a\u4e0b\uff0c\u5448\u788e\u5c51\u72b6\u6389\u843d\u5728\u5730\uff0c\u65b0\u957f\u51fa\u7684\u76ae\u80a4\u7ec6\u5ae9\u800c\u5a07\u5f31\uff0c\u4eff\u4f5b\u5a74\u513f\uff0c\u4f46\u4e5f\u6709\u5c0f\u90e8\u5206\u70e7\u4f24\u4e25\u91cd\u7684\u5730\u65b9\uff08\u4e3b\u8981\u662f\u8138\u4e0a\uff0c\u56e0\u4e3a\u6ca1\u6709\u6597\u7bf7\u548c\u957f\u888d\u7684\u9632\u62a4\uff09\uff0c\u4e00\u65f6\u5c1a\u672a\u80fd\u590d\u539f\u3002\u8fd9\u4e2a\u8fc7\u7a0b\u5c06\u4f1a\u5f88\u6f2b\u957f\uff0c\u5353\u5c14\u5c11\u5973\u611f\u89c9\u5230\u76ae\u80a4\u5f88\u75d2\uff0c\u60f3\u8981\u53bb\u6320\uff0c\u4f46\u52aa\u529b\u514b\u5236\u4f4f\u4e86\uff0c\u8fd9\u662f\u6b63\u5e38\u53cd\u5e94\u3002\u5979\u5c06\u6597\u7bf7\u7684\u515c\u5e3d\u62c9\u4e0b\uff0c\u906e\u4f4f\u5927\u534a\u8fb9\u8138\uff0c\u907f\u514d\u88ab\u743c\u6069\u770b\u89c1\u3002\u957f\u888d\u5927\u534a\u88ab\u70e7\u6bc1\uff0c\u5df2\u7ecf\u6ca1\u6cd5\u518d\u7a7f\uff0c\u7d22\u6027\u8131\u4e0b\u6765\u88f9\u5728\u8eab\u4e0a\uff0c\u52c9\u5f3a\u88f9\u4f4f\u80f8\u8179\u3002\u5979\u679c\u7136\u6ca1\u6234\u6587\u80f8\uff0c\u4e30\u6ee1\u7684\u80f8\u90e8\u9ad8\u8038\u633a\u7acb\uff0c\u5b8c\u5168\u65e0\u89c6\u5730\u5fc3\u5f15\u529b\uff0c\u9694\u7740\u4e1d\u7ec7\u5e03\u6599\u80fd\u591f\u6e05\u695a\u770b\u89c1\u4e24\u9897\u8bf1\u4eba\u51f8\u8d77\u3002\u7b14\u76f4\u4fee\u957f\u7684\u53cc\u817f\u5c3d\u6570\u88f8\u9732\u5728\u5916\uff0c\u53ea\u6709\u4e00\u53ea\u5df4\u638c\u5927\u5c0f\u7684\u7d2b\u8272\u4e01\u5b57\u5185\u88e4\u906e\u63a9\u5173\u952e\u90e8\u4f4d\uff0c\u56e0\u4e3a\u8f7b\u8584\u8d34\u8eab\uff0c\u9690\u9690\u7ea6\u7ea6\u52fe\u52d2\u51fa\u82b1\u56ed\u8f6e\u5ed3\u3002\u552f\u4e00\u53ef\u60dc\u7684\u662f\u6597\u7bf7\u906e\u4f4f\u4e86\u80cc\u90e8\uff0c\u5c06\u633a\u7fd8\u7684\u7f8e\u81c0\u4e5f\u5305\u88f9\u5176\u4e2d\uff0c\u4ee4\u4eba\u4e0d\u7531\u5f97\u591a\u5c11\u6709\u4e9b\u5931\u671b\u3002</p><p>\u6ce8\u610f\u5230\u743c\u6069\u7684\u76ee\u5149\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u5fae\u5fae\u4e00\u7b11\uff0c\u9690\u9690\u6709\u4e9b\u5f97\u610f\u3002\u6bcf\u4e2a\u5973\u4eba\u90fd\u4ee5\u81ea\u5df1\u7684\u9b45\u529b\u800c\u81ea\u8c6a\uff0c\u4ee5\u80fd\u591f\u5438\u5f15\u7537\u6027\u4e3a\u9a84\u50b2\uff0c\u5353\u5c14\u4e5f\u4e0d\u4f8b\u5916\u3002\u53ef\u60dc\u7b11\u5bb9\u7275\u52a8\uff0c\u611f\u89c9\u8138\u4e0a\u4e00\u9635\u706b\u8fa3\u8fa3\u7684\u75bc\u75db\uff0c\u987f\u65f6\u8ba9\u5979\u4ec0\u4e48\u597d\u5fc3\u60c5\u90fd\u6ca1\u4e86\u3002</p><p>\u518d\u4e00\u6b21\u7684\uff0c\u5979\u5728\u5fc3\u4e2d\u8bc5\u5492\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u9996\u5e2d\u5deb\u5e08\u3002</p><p>\u4e9a\u5f53\u65af\u6ca1\u6709\u6ce8\u610f\u4ed6\u7684\u4f84\u5973\uff0c\u201c\u6c38\u6052\u2026\u2026\u957f\u7720\u2026\u2026\u505c\u6ede\u2026\u2026\u201d\u4ed6\u52aa\u529b\u8fa8\u8bc6\u7740\u6700\u540e\u4e00\u4e2a\u6cd5\u672f\u7b26\u6587\uff0c\u4ece\u9519\u4e71\u7684\u7075\u5149\u95ea\u70c1\u4e2d\u5bfb\u627e\u7740\u8ff9\u8c61\uff0c\u201c\u5f98\u5f8a\u4e8e\u751f\u4e0e\u6b7b\u7684\u8fb9\u7f18\u2026\u2026\u201d</p><p>\u201c\u6c38\u6052\u9759\u6ede\u3002\u201d\u743c\u6069\u76f4\u63a5\u8bf4\u51fa\u4e86\u7b54\u6848\uff0c\u5bf9\u8fd9\u65b9\u9762\u4ed6\u6bd4\u4e9a\u5f53\u65af\u66f4\u719f\u6089\u3002\u8fd9\u4e2a\u6cd5\u672f\u5f88\u5371\u9669\uff0c\u78b0\u5230\u7684\u4eba\u4f1a\u9677\u5165\u5047\u6b7b\u72b6\u6001\uff0c\u6655\u8ff7\u4e0d\u9192\uff0c\u8eab\u4f53\u673a\u80fd\u5b8c\u5168\u505c\u6b62\u3002\u901a\u4fd7\u5730\u8bf4\uff0c\u5c31\u662f\u65f6\u95f4\u5728\u8fd9\u4e2a\u4eba\u7684\u8eab\u4e0a\u505c\u6ede\u4e86\u3002</p><p>\u201c\u4f60\u80fd\u6d88\u89e3\u5b83\u5417\uff1f\u201d</p><p>\u201c\u6211\u60f3\u53ef\u4ee5\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u8981\u6682\u4e14\u628a\u5b83\u5206\u9694\u51fa\u6765\u2014\u2014\u548c\u6211\u4e00\u8d77\u3002\u201d</p><p>\u201c\u6ca1\u95ee\u9898\uff0c\u201d\u4e9a\u5f53\u65af\u8bf4\uff0c\u201c\u6211\u53ef\u4ee5\u529e\u5230\u3002\u201d</p><p>\u4ed6\u53d6\u51fa\u4e00\u5757\u534a\u7403\u5f62\u7684\u900f\u660e\u6c34\u6676\uff0c\u4ee5\u53ca\u4e00\u5757\u5f62\u72b6\u76f8\u540c\uff0c\u4f46\u7a0d\u5c0f\u4e00\u4e9b\u7684\u6811\u80f6\uff0c\u5206\u522b\u63e1\u5728\u53cc\u624b\u638c\u5fc3\uff0c\u5ff5\u8bf5\u5492\u8bed\u3002\u4e00\u4e2a\u5fae\u5fae\u53d1\u4eae\u7684\u900f\u660e\u7403\u5f62\u529b\u573a\u51fa\u73b0\uff0c\u628a\u743c\u6069\u548c\u6c38\u6052\u9759\u6ede\u7684\u7b26\u6587\u90fd\u7b3c\u7f69\u5728\u5176\u4e2d\u3002\u539f\u672c\u8054\u7ed3\u5728\u7b26\u6587\u4e0a\u7684\u9b54\u6cd5\u7075\u7ebf\u88ab\u6682\u65f6\u9694\u65ad\u4e86\uff0c\u65e0\u6cd5\u6d41\u901a\u3002</p><p>\u743c\u6069\u56de\u5fc6\u7740\u6709\u5173\u6c38\u6052\u9759\u6ede\u7684\u6240\u6709\u8d44\u6599\uff0c\u9ed8\u8bf5\u51fa\u89e3\u9664\u9b54\u6cd5\u7684\u5492\u6587\uff0c\u6162\u6162\u5c06\u9b54\u529b\u51dd\u805a\u5728\u6307\u5c16\uff0c\u7136\u540e\u4f38\u51fa\u624b\uff0c\u987a\u7740\u7075\u7ebf\u7684\u8f68\u8ff9\u7f13\u7f13\u79fb\u52a8\uff0c\u4eff\u4f5b\u662f\u628a\u8fd9\u4e2a\u7b26\u6587\u91cd\u65b0\u6784\u5efa\u4e00\u904d\u3002\u4f46\u5728\u5b9e\u9645\u4e0a\uff0c\u968f\u7740\u624b\u6307\u79fb\u52a8\uff0c\u7b26\u6587\u7684\u7ebf\u6761\u6e10\u6e10\u9eef\u6de1\u4e0b\u53bb\u3002\u56e0\u4e3a\u529b\u573a\u963b\u9694\uff0c\u5b83\u65e0\u6cd5\u4ece\u5916\u754c\u83b7\u5f97\u8865\u5145\uff0c\u53ea\u4f1a\u6301\u7eed\u524a\u5f31\uff0c\u6700\u7ec8\u6d88\u5931\u3002\u4e00\u5207\u8fdb\u884c\u5f97\u5f88\u987a\u5229\uff0c\u4f46\u5fc5\u987b\u5728\u7403\u5f62\u529b\u573a\u6d88\u5931\u4e4b\u524d\u5b8c\u6210\uff0c\u5426\u5219\u4fbf\u529f\u4e8f\u4e00\u7bd1\u4e86\u3002</p><p>\u5e78\u597d\uff0c\u4e9a\u5f53\u65af\u662f\u4e2a\u9ad8\u9636\u5deb\u5e08\uff0c\u800c\u4e14\u663e\u7136\u5728\u5851\u80fd\u9b54\u6cd5\u4e0a\u522b\u6709\u4e13\u64c5\uff0c\u4ed6\u521b\u9020\u7684\u6cd5\u7403\u6301\u7eed\u65f6\u95f4\u5f88\u4e45\uff0c\u51e0\u4e4e\u7ef4\u6301\u4e86\u5c06\u8fd1\u534a\u4e2a\u5c0f\u65f6\u3002\u743c\u6069\u805a\u7cbe\u4f1a\u795e\uff0c\u7ec8\u4e8e\u62a2\u5728\u529b\u573a\u5931\u6548\u4e4b\u524d\u5b8c\u6210\u4e86\u6700\u540e\u4e00\u6b65\u3002</p><p>\u56db\u9762\u516b\u65b9\u6c47\u805a\u800c\u6765\u7684\u9b54\u6cd5\u7075\u7ebf\u4e27\u5931\u7ed3\u70b9\uff0c\u5b83\u4eec\u53d8\u5f97\u6563\u4e71\u8d77\u6765\uff0c\u8131\u79bb\u539f\u672c\u7684\u8def\u7ebf\u8f68\u8ff9\uff0c\u7eb7\u4e71\u9519\u6742\u5730\u5728\u7a7a\u4e2d\u649e\u51fb\u3001\u7ede\u7f20\u3001\u7cc5\u5408\u751a\u81f3\u78b0\u649e\u62b5\u6d88\uff0c\u5de8\u5927\u7684\u9707\u52a8\u4ece\u5730\u9762\u5f00\u59cb\uff0c\u8fc5\u901f\u8513\u5ef6\u5230\u5899\u58c1\u3001\u7a79\u9876\uff0c\u6574\u4e2a\u795e\u5802\u90fd\u5f00\u59cb\u7c0c\u7c0c\u98a4\u6296\uff0c\u8d8a\u6765\u8d8a\u5267\u70c8\uff0c\u611f\u89c9\u7acb\u523b\u5c31\u8981\u584c\u9677\u3002</p><p>\u201c\u5feb\u8d70\uff01\u201d</p><p>\u4e9a\u5f53\u65af\u8bf4\uff0c\u4f38\u624b\u865a\u865a\u4e00\u6293\uff0c\u56f0\u4f4f\u59ae\u745f\u7684\u7403\u5f62\u529b\u573a\u98de\u4e86\u8d77\u6765\uff0c\u5e26\u7740\u91cc\u9762\u7684\u4fd8\u864f\u5feb\u901f\u5f80\u8fd9\u8fb9\u79fb\u52a8\u3002\u4ed6\u8dd1\u5728\u6700\u524d\u9762\uff0c\u95e8\u53e3\u7684\u51b0\u5899\u5df2\u7ecf\u5728\u9707\u52a8\u4e2d\u7834\u88c2\uff0c\u65e0\u9700\u518d\u89e3\u9664\uff0c\u743c\u6069\u6276\u7740\u7ef4\u5eb7\u5c3c\u4e9a\u8ddf\u968f\u5176\u540e\u3002\u4ed6\u4eec\u8dd1\u51fa\u795e\u6bbf\uff0c\u4e00\u76f4\u5230\u5b89\u5168\u7684\u5730\u65b9\u56de\u5934\u518d\u770b\uff0c\u53ea\u89c1\u5e9e\u5927\u7684\u5efa\u7b51\u6447\u6643\u7740\uff0c\u7ec8\u4e8e\u652f\u6301\u4e0d\u4f4f\uff0c\u8f70\u7136\u5012\u584c\u4e0b\u6765\uff0c\u5728\u5f25\u6f2b\u7684\u70df\u5c18\u4e2d\u53d8\u6210\u4e86\u4e00\u5806\u5f7b\u5e95\u7684\u788e\u77f3\u74e6\u783e\u3002</p><p>\u5de8\u5927\u7684\u91cd\u7269\u5012\u584c\u58f0\u4ece\u5b85\u9662\u6b63\u95e8\u7684\u65b9\u5411\u4f20\u6765\uff0c\u5939\u6742\u7740\u6570\u767e\u4eba\u7684\u540c\u58f0\u547c\u558a\uff0c\u5730\u9762\u90fd\u88ab\u9707\u5f97\u9690\u9690\u6643\u52a8\u3002\u743c\u6069\u6fc0\u6d3b\u80f8\u9488\uff0c\u98d8\u6d6e\u8d77\u6765\uff0c\u7136\u540e\u4ed6\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u9b54\u6cd5\u9635\u7531\u4e5d\u5927\u4e3b\u63a7\u7ed3\u754c\u6784\u6210\uff0c\u73b0\u5728\u88ab\u743c\u6069\u548c\u4e9a\u5f53\u65af\u6467\u6bc1\u5176\u4e2d\u4e4b\u4e00\uff0c\u6574\u4e2a\u6cd5\u9635\u81ea\u7136\u5927\u53d7\u5f71\u54cd\u3002\u8d81\u7740\u8fd9\u4e2a\u673a\u4f1a\uff0c\u4e09\u5927\u5bb6\u65cf\u7684\u653b\u57ce\u90e8\u961f\u75af\u72c2\u51b2\u51fb\uff0c\u7ec8\u4e8e\u649e\u5012\u4e86\u7cbe\u91d1\u57ce\u95e8\uff0c\u8702\u62e5\u800c\u5165\u3002</p><p>\u201c\u80dc\u5229\u4e86\u3002\u201d\u743c\u6069\u60f3\u3002</p><p>\u867d\u7136\u8fd9\u573a\u5bb6\u65cf\u5185\u6218\uff0c\u5176\u5b9e\u548c\u4ed6\u5173\u7cfb\u4e0d\u7b97\u5f88\u5927\uff08\u5f53\u7136\uff0c\u770b\u89c1\u5353\u5c14\u81ea\u76f8\u6b8b\u6740\uff0c\u8fd8\u662f\u5f88\u4ee4\u4eba\u6109\u5feb\u7684\uff09\uff0c\u4f46\u6bd5\u7adf\u662f\u81ea\u5df1\u6240\u5728\u7684\u8fd9\u4e00\u65b9\u83b7\u80dc\uff0c\u5fc3\u60c5\u81ea\u7136\u6bd4\u8f83\u6109\u5feb\u3002\u552f\u4e00\u7684\u9057\u61be\uff0c\u5c31\u662f\u80dc\u5229\u6765\u5f97\u592a\u65e9\u4e86\u70b9\uff0c\u6ca1\u80fd\u66f4\u591a\u5730\u635f\u8017\u53cc\u65b9\u7684\u5b9e\u529b\u3002</p><p>\u201c\u6211\u4eec\u5148\u51fa\u53bb\u3002\u201d\u4ed6\u53d6\u6d88\u6d6e\u7a7a\u672f\uff0c\u7f13\u7f13\u843d\u5730\uff0c\u5bf9\u7ef4\u5eb7\u5c3c\u4e9a\u548c\u4e9a\u5f53\u65af\u8bf4\u3002</p><p>\u51fa\u53bb\u6bd4\u8fdb\u6765\u5bb9\u6613\uff0c\u6bd5\u7adf\u8fd9\u9b54\u6cd5\u9635\u7684\u76ee\u7684\u662f\u9632\u6b62\u5916\u654c\u5165\u4fb5\uff0c\u4e0d\u662f\u9547\u538b\u5185\u90e8\u53db\u4e71\u7684\u3002\u743c\u6069\u6b63\u6253\u7b97\u4ece\u539f\u8def\u8fd4\u56de\uff0c\u7a81\u7136\u773c\u4e2d\u5149\u8292\u95ea\u70c1\uff0c\u4ed6\u6709\u4e9b\u8be7\u5f02\u5730\u8f6c\u5934\u770b\u53bb\uff0c\u53d1\u73b0\u5b85\u9662\u7684\u56db\u89d2\u534a\u7a7a\u4e2d\uff0c\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u591a\u4e86\u56db\u4e2a\u76f4\u5f84\u8d85\u8fc7\u4e94\u5341\u82f1\u5c3a\u7684\u9ed1\u8272\u6f29\u6da1\uff0c\u521a\u624d\u9003\u8d70\u7684\u5bb6\u65cf\u9996\u5e2d\u5deb\u5e08\u514b\u52b3\u62d3\u7ad9\u7acb\u5728\u9ad8\u7a7a\u4e2d\uff0c\u53cc\u624b\u53d1\u51fa\u8000\u773c\u95ea\u7535\uff0c\u67d0\u79cd\u602a\u5f02\u7684\u751f\u7269\u6b63\u4ece\u65cb\u6da1\u4e2d\u6e90\u6e90\u4e0d\u65ad\u5730\u51fa\u73b0\uff0c\u4e00\u4e2a\u53c8\u4e00\u4e2a\uff0c\u843d\u5728\u5b85\u9662\u5f53\u4e2d\uff0c\u52a0\u5165\u6218\u56e2\u3002</p><p>\u743c\u6069\u8c03\u6574\u4e86\u4e00\u4e0b\u89c6\u529b\uff0c\u8ba9\u81ea\u5df1\u770b\u5f97\u66f4\u6e05\u695a\u4e9b\uff0c\u7136\u540e\u4ed6\u8ba4\u51fa\u4e86\u90a3\u79cd\u602a\u7269\uff1a\u4e0a\u534a\u8eab\u662f\u5353\u5c14\uff0c\u4e0b\u534a\u8eab\u662f\u5e9e\u5927\u7684\u9ed1\u8272\u8718\u86db\uff0c\u9762\u5bb9\u6d6e\u80bf\uff0c\u7360\u7259\u7a81\u51fa\u3002</p><p>\u201c\u6df1\u6e0a\u554a\uff0c\u201d\u4e9a\u5f53\u65af\u4e5f\u770b\u6e05\u695a\u4e86\u602a\u7269\uff0c\u4ed6\u4f4e\u4f4e\u8bc5\u5492\u4e86\u4e00\u58f0\uff0c\u201c\u4ed6\u5c45\u7136\u2026\u2026\u201d</p><p>\u514b\u52b3\u62d3\u53ec\u5524\u800c\u6765\u7684\uff0c\u5e76\u975e\u6df1\u6e0a\u4e2d\u7684\u6076\u9b54\uff0c\u800c\u662f\u86db\u5316\u7cbe\u7075\u3002</p></article>"}}}]);
