(window.webpackJsonp=window.webpackJsonp||[]).push([[1480],{1503:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e09\u8282 \u4ec5\u4ec5\u662f\u731c\u6d4b",content:"<article><h2>\u7b2c\u56db\u5341\u4e09\u8282 \u4ec5\u4ec5\u662f\u731c\u6d4b</h2><p>\u7f57\u4e1d\u6ca1\u6709\u6b7b\uff0c\u800c\u4e14\u8fd8\u5feb\u8981\u56de\u6765\u4e86\u3002</p><p>\u8fd9\u53ef\u5b9e\u5728\u4e0d\u662f\u4e2a\u597d\u6d88\u606f\u3002</p><p>\u8bf4\u8d77\u6765\uff0c\u81ea\u4ece\u5927\u6c89\u964d\u4ee5\u6765\uff0c\u5730\u8868\u4e16\u754c\u7684\u519b\u961f\u4e5f\u9887\u6709\u51e0\u6b21\u8bd5\u56fe\u5165\u4fb5\u5e7d\u6697\u5730\u57df\uff0c\u527f\u706d\u8fd9\u4e9b\u90aa\u6076\u7684\u5353\u5c14\uff0c\u4f46\u90fd\u662f\u5927\u8d25\u800c\u5f52\u3002\u9634\u9b42\u57ce\u8fd9\u6b21\u6562\u4e8e\u8fdb\u519b\uff0c\u4e5f\u662f\u56e0\u4e3a\u7f57\u4e1d\u7a81\u7136\u6c89\u5bc2\uff0c\u5353\u5c14\u4eec\u4e00\u7247\u6df7\u4e71\uff0c\u6240\u4ee5\u624d\u6709\u4e86\u53ef\u4e58\u4e4b\u673a\u3002\u800c\u4e14\u743c\u6069\u53ef\u4ee5\u65ad\u5b9a\uff0c\u9634\u9b42\u57ce\u2014\u2014\u6216\u8005\u8bf4\u838e\u5c14\u6559\u4f1a\u2014\u2014\u65e9\u5c31\u5728\u5e7d\u6697\u5730\u57df\u91cc\u6709\u6240\u57cb\u4f0f\uff0c\u91cc\u5e94\u5916\u5408\uff0c\u6c99\u739b\u65af\u57ce\u7684\u9634\u5f71\u9b54\u6cd5\u5b66\u9662\uff0c\u9c81\u91cc\u838e\u5c14\u7684\u9c7c\u4eba\u57ce\u5e02\uff0c\u8fd9\u4e9b\u90fd\u662f\u660e\u8bc1\u3002</p><p>\u867d\u7136\u9634\u9b42\u57ce\u7684\u6269\u5f20\u987a\u5229\uff0c\u8fde\u6218\u8fde\u6377\uff0c\u5df2\u7ecf\u653b\u4e0b\u4e0d\u5c11\u57ce\u5e02\uff0c\u4f46\u6bd5\u7adf\u65f6\u95f4\u592a\u77ed\uff0c\u6839\u57fa\u672a\u56fa\uff0c\u5982\u679c\u6709\u8db3\u591f\u7684\u65f6\u95f4\u6162\u6162\u9547\u538b\u6d88\u5316\u5012\u7f62\u4e86\uff0c\u4f46\u5982\u4eca\u7f57\u4e1d\u5373\u5c06\u5f52\u6765\uff0c\u60c5\u51b5\u5df2\u7ecf\u53d1\u751f\u5267\u53d8\uff0c\u5018\u82e5\u9634\u9b42\u57ce\u4e0d\u77e5\u9053\u89c1\u597d\u5c31\u6536\uff0c\u53ea\u6015\u8981\u5403\u5927\u4e8f\uff0c\u4ee5\u524d\u541e\u4e0b\u7684\u4e5f\u8981\u5410\u51fa\u6765\u3002</p><p>\u5f53\u7136\uff0c\u9634\u9b42\u57ce\u5982\u4f55\uff0c\u743c\u6069\u6682\u65f6\u61d2\u5f97\u5173\u5fc3\uff0c\u4ed6\u771f\u6b63\u5934\u75bc\u7684\u662f\u81ea\u5df1\u7684\u5904\u5883\u3002\u8fd9\u5c31\u597d\u50cf\u770b\u89c1\u4e00\u5e62\u8c6a\u534e\u522b\u5885\uff0c\u7785\u51c6\u4e3b\u4eba\u51fa\u95e8\u65c5\u6e38\u4e86\uff0c\u8d81\u673a\u6e9c\u8fdb\u53bb\uff0c\u773c\u770b\u5c31\u8981\u628a\u91d1\u94f6\u73e0\u5b9d\u5e2d\u5377\u4e00\u7a7a\u6253\u5305\u5e26\u8d70\uff0c\u7ed3\u679c\u4f20\u6765\u6d88\u606f\u8bf4\u4e3b\u4eba\u9a6c\u4e0a\u8981\u56de\u5bb6\u4e86\u3002</p><p>\u662f\u8d81\u65e9\u62bd\u8eab\uff0c\u79bb\u5f00\u8fd9\u662f\u975e\u4e4b\u5730\u5462\uff0c\u8fd8\u662f\u52a0\u5feb\u901f\u5ea6\uff0c\u65e9\u65e9\u628a\u8fd9\u4e2a\u9ebb\u70e6\u7ed9\u89e3\u51b3\u4e86\u5462\uff1f\u743c\u6069\u6c89\u601d\u7740\uff0c\u4e00\u65f6\u8e0c\u8e87\u4e0d\u5b9a\u3002</p><p>\u201c\u4e0d\u7528\u62c5\u5fc3\u5566\uff0c\u201d\u6b27\u51ef\u663e\u7136\u8bef\u89e3\u4e86\u743c\u6069\u7684\u53cd\u5e94\uff0c\u201c\u5c31\u7b97\u7f57\u4e1d\u590d\u82cf\uff0c\u53c8\u6709\u4ec0\u4e48\u597d\u6015\u7684\uff0c\u96be\u9053\u5979\u8fd8\u4f1a\u5723\u8005\u964d\u4e34\u6765\u8ffd\u6740\u4f60\u4e48\uff0c\u65e0\u975e\u8fd8\u662f\u6d3e\u8fd9\u4e9b\u5353\u5c14\u6765\u627e\u9ebb\u70e6\u7f62\u4e86\uff0c\u5bb9\u6613\u6253\u53d1\u3002\u201d</p><p>\u201c\u5e9f\u8bdd\uff0c\u8fd9\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d</p><p>\u8d39\u4f26\u5927\u9646\u4e0a\u4fe1\u4ef0\u904d\u5e03\uff0c\u6559\u4f1a\u4f17\u591a\uff0c\u5c31\u7b97\u540c\u4e00\u4e2a\u795e\u7947\u7684\u6559\u4f1a\u91cc\u5f80\u5f80\u4e5f\u6709\u4e0d\u540c\u652f\u6d3e\uff0c\u76f8\u4e92\u653b\u51fb\u6597\u6740\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u5947\u602a\u7684\u4e8b\u60c5\u3002\u5018\u82e5\u8c01\u5f97\u7f6a\u4e86\u67d0\u4e2a\u795e\u7947\uff0c\u867d\u7136\u80af\u5b9a\u4f1a\u88ab\u6559\u5f92\u8ffd\u6740\uff0c\u4f46\u57fa\u672c\u4e0a\u4e0d\u7528\u62c5\u5fc3\u795e\u7947\u4f1a\u4eb2\u81ea\u8dd1\u6765\u627e\u9ebb\u70e6\u2014\u2014\u80fd\u6709\u8fd9\u79cd\u8d44\u683c\u7684\u4eba\uff0c\u5386\u53f2\u4e0a\u6ca1\u6709\u51e0\u4e2a\u3002</p><p>\u5728\u70db\u5821\u7684\u56fe\u4e66\u9986\u91cc\uff0c\u743c\u6069\u66fe\u7ecf\u7ffb\u9605\u8fc7\u7269\u8d28\u754c\u8457\u540d\u5927\u5deb\u5e08\u7684\u4f20\u8bb0\uff0c\u65e2\u7136\u8d70\u4e86\u5deb\u5e08\u8fd9\u884c\uff0c\u603b\u8981\u77e5\u9053\u672c\u9886\u57df\u5185\u6709\u54ea\u4e9b\u524d\u8f88\u9ad8\u4eba\u3002\u5176\u4e2d\u66fe\u7ecf\u63d0\u53ca\u4e00\u4f4d\u53eb\u505a\u8428\u9a6c\u65af\u7279\u7684\u5deb\u5e08\uff0c\u79f0\u5f97\u4e0a\u662f\u8010\u745f\u5e1d\u56fd\u9668\u706d\u540e\u8fd9\u4e00\u5343\u4e03\u767e\u5e74\u91cc\u8bde\u751f\u7684\u6700\u6770\u51fa\u5929\u624d\uff0c\u4e00\u624b\u521b\u5efa\u9f99\u5deb\u6559\uff0c\u6740\u6b7b\u65e0\u6570\u7ad6\u7434\u624b\u548c\u5170\u68ee\u5fb7\u5c14\u4fe1\u5f92\uff0c\u7ec8\u4e8e\u624d\u5f15\u5f97\u6668\u66e6\u4e4b\u795e\u7684\u5316\u8eab\u964d\u4e34\uff0c\u6218\u8d25\u8eab\u6b7b\u3002\u5c31\u51ed\u743c\u6069\u76ee\u524d\u8fd9\u70b9\u9020\u8be3\uff0c\u5982\u679c\u4e5f\u8981\u62c5\u5fc3\u795e\u7947\u4f1a\u4eb2\u81ea\u5bf9\u4ed6\u4e0b\u624b\uff0c\u90a3\u4e0d\u662f\u8c28\u614e\u5c0f\u5fc3\uff0c\u7eaf\u7cb9\u662f\u6ca1\u6709\u81ea\u77e5\u4e4b\u660e\u3002</p><p>\u795e\u7947\u867d\u7136\u529b\u91cf\u5f3a\u5927\uff0c\u5374\u4e0d\u4f1a\u8f7b\u6613\u964d\u4e34\u7269\u8d28\u754c\uff0c\u90a3\u662f\u9700\u8981\u4ed8\u51fa\u5de8\u5927\u4ee3\u4ef7\u7684\uff0c\u4e0d\u5230\u4e07\u4e0d\u5f97\u5df2\uff0c\u6ca1\u6709\u795e\u7947\u613f\u610f\u505a\u8fd9\u79cd\u5f97\u4e0d\u507f\u5931\u7684\u4e8b\u60c5\uff0c\u5426\u5219\u8fd9\u4e16\u754c\u53ea\u6015\u65e9\u5c31\u88ab\u6253\u6210\u4e00\u7247\u5e9f\u589f\u4e86\u3002</p><p>\u201c\u4f60\u521a\u624d\u8bf4\u7f57\u4e1d\u2018\u590d\u82cf\u2019\uff1f\u201d\u743c\u6069\u95ee\u6b27\u51ef\uff0c\u201c\u8fd9\u4e2a\u8bcd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u4f60\u4e0d\u4e5f\u662f\u7ecf\u8fc7\u9ad8\u7b49\u6559\u80b2\u7684\u8d35\u65cf\u4e48\uff0c\u96be\u9053\u8fde\u590d\u82cf\u7684\u610f\u601d\u90fd\u4e0d\u61c2\uff1f\u201d\u6b27\u51ef\u61d2\u6d0b\u6d0b\u5730\u56de\u7b54\uff0c\u201c\u590d\u82cf\u5c31\u662f\u8bf4\uff0c\u539f\u672c\u5728\u6c89\u7761\uff0c\u73b0\u5728\u9192\u8fc7\u6765\u4e86\u5440\u3002\u201d</p><p>\u201c\u7f57\u4e1d\u4e3a\u4ec0\u4e48\u4f1a\u6c89\u7761\uff1f\u201d</p><p>\u201c\u6309\u89c4\u77e9\u6211\u662f\u5e94\u8be5\u8981\u6536\u8d39\u7684\u2026\u2026\u201d</p><p>\u201c\u4f60\u8bf4\u8fd9\u6b21\u662f\u8981\u6211\u5e2e\u5fd9\u7684\u3002\u201d\u743c\u6069\u63d0\u9192\u3002</p><p>\u201c\u5443\uff0c\u597d\u5427\uff0c\u6211\u7684\u610f\u601d\u5176\u5b9e\u662f\u8bf4\uff0c\u6309\u89c4\u77e9\u662f\u8981\u6536\u8d39\u7684\uff0c\u4e0d\u8fc7\u5076\u5c14\u7834\u4f8b\u4e5f\u65e0\u59a8\uff0c\u201d\u6b27\u51ef\u6539\u53e3\uff0c\u201c\u81f3\u4e8e\u7f57\u4e1d\u4e3a\u4ec0\u4e48\u4f1a\u6c89\u7761\u2026\u2026\u5766\u767d\u5730\u8bf4\uff0c\u6211\u4e5f\u4e0d\u662f\u5f88\u6e05\u695a\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u4e0d\u662f\u81ea\u79f0\u65e0\u6240\u4e0d\u77e5\u4e48\uff1f\u201d</p><p>\u201c\u5546\u4e1a\u5ba3\u4f20\u7528\u8bed\u800c\u5df2\uff0c\u4f60\u8fd8\u5f53\u771f\u4e86\uff1f\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u8bf4\u4f60\u4e0d\u4f1a\u9a97\u4eba\u4e48\uff1f\u201d</p><p>\u201c\u8fd9\u600e\u4e48\u7b97\u9a97\u4eba\uff0c\u54ea\u4e2a\u5546\u4eba\u505a\u751f\u610f\u4e0d\u4f1a\u6765\u70b9\u9002\u5f53\u7684\u4fee\u9970\u5938\u5f20\u7684\uff1f\u8fd9\u5206\u660e\u662f\u4e16\u60c5\u5e38\u7406\u597d\u4e0d\u597d\uff0c\u201d\u6b27\u51ef\u9887\u6709\u4e9b\u4e0d\u5c51\uff0c\u201c\u65e0\u6240\u4e0d\u77e5\uff1f\u5c31\u8fde\u77e5\u8bc6\u4e4b\u795e\u6b27\u683c\u739b\u4e5f\u4e0d\u6562\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u4f55\u51b5\u6211\u4e0d\u8fc7\u662f\u4e2a\u6076\u9b54\u7f62\u4e86\uff0c\u4e5f\u53ea\u6709\u4f60\u624d\u771f\u76f8\u4fe1\u3002\u201d</p><p>\u201c\u884c\uff0c\u884c\uff0c\u201d\u743c\u6069\u4e3e\u624b\u6295\u964d\uff0c\u201c\u4e5f\u5c31\u662f\u8bf4\u5176\u5b9e\u4f60\u6839\u672c\u5c31\u4e0d\u77e5\u9053\uff0c\u90a3\u8fd8\u626f\u4ec0\u4e48\u6536\u8d39\u4e0d\u6536\u8d39\u7684\u3002\u201d</p><p>\u201c\u6211\u53ea\u662f\u8bf4\u6211\u4e0d\u662f\u5f88\u6e05\u695a\uff0c\u4e0d\u7b49\u4e8e\u6211\u5b8c\u5168\u4e0d\u6e05\u695a\uff0c\u66f4\u4e0d\u7b49\u4e8e\u6211\u6839\u672c\u4e0d\u77e5\u9053\uff0c\u201d\u6b27\u51ef\u7ea0\u6b63\uff0c\u201c\u795e\u7947\u7684\u4e8b\u60c5\uff0c\u54ea\u91cc\u662f\u90a3\u4e48\u5bb9\u6613\u5c31\u6253\u63a2\u5230\u7684\uff0c\u4f55\u51b5\u6211\u548c\u7f57\u4e1d\u53c8\u4ece\u6ca1\u4ea4\u60c5\u3002\u4e0d\u8fc7\u603b\u7b97\u5927\u5bb6\u90fd\u4f4f\u5728\u6df1\u6e0a\u91cc\uff0c\u591a\u591a\u5c11\u5c11\u4e5f\u6709\u70b9\u98ce\u58f0\u7f62\u4e86\u3002\u201d</p><p>\u201c\u8bf4\u8bf4\u770b\u3002\u201d</p><p>\u201c\u5927\u7ea6\u4e24\u4e2a\u6708\u591a\u524d\uff0c\u7f57\u4e1d\u7a81\u7136\u9677\u5165\u6c89\u5bc2\uff0c\u4e0d\u8fc7\u4f60\u77e5\u9053\uff0c\u6df1\u6e0a\u91cc\u6574\u5929\u4e71\u4e03\u516b\u7cdf\u7684\uff0c\u8c01\u4e5f\u6ca1\u6709\u592a\u5728\u610f\uff0c\u201d\u6b27\u51ef\u6469\u6332\u7740\u4e0b\u5df4\uff0c\u6162\u6162\u7ec4\u7ec7\u8bed\u8a00\uff0c\u201c\u7136\u540e\u5462\u2026\u2026\u5927\u7ea6\u5728\u4e00\u4e2a\u6708\u524d\uff0c\u6df1\u6e0a\u7b2c\u516d\u5341\u516d\u5c42\u2014\u2014\u4e5f\u5c31\u662f\u7f57\u4e1d\u5c45\u4f4f\u7684\u4f4d\u9762\uff0c\u6df1\u5751\u9b54\u7f51\u2014\u2014\u7a81\u7136\u53d1\u751f\u5267\u53d8\uff0c\u50cf\u662f\u8981\u4ece\u6df1\u6e0a\u4e2d\u5206\u88c2\u51fa\u6765\uff0c\u5f80\u661f\u754c\u98d8\u6d6e\uff0c\u5927\u5bb6\u8fd9\u624d\u5f00\u59cb\u5173\u6ce8\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u77e5\u9053\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0a\u6b21\u5728\u5965\u7075\u591a\u5c14\uff0c\u4f60\u4e0d\u5c31\u548c\u7075\u5438\u602a\u63d0\u8fc7\u4e48\u3002\u4f46\u8fd9\u610f\u5473\u7740\u4ec0\u4e48\uff1f\u96be\u9053\u7f57\u4e1d\u60f3\u8981\u4e3a\u81ea\u5df1\u521b\u5efa\u72ec\u7acb\u7684\u795e\u56fd\uff1f\u201d</p><p>\u6b27\u51ef\u6447\u5934\uff0c\u201c\u8fd9\u4e2a\u6211\u5c31\u771f\u4e0d\u6e05\u695a\u4e86\uff0c\u8c01\u77e5\u9053\u90a3\u53ea\u8718\u86db\u60f3\u5e72\u4ec0\u4e48\u3002\u6211\u4eec\u4e5f\u6d3e\u9063\u4e86\u5c5e\u4e0b\u8fdb\u5165\u516d\u5341\u516d\u5c42\u53bb\u6253\u63a2\uff0c\u6ca1\u4ec0\u4e48\u6536\u83b7\uff0c\u4f46\u6709\u4e00\u70b9\u5f88\u5947\u602a\uff1a\u6240\u6709\u7f57\u4e1d\u7684\u7948\u5e76\u8005\u90fd\u7a81\u7136\u6d88\u5931\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u610f\u5473\u7740\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6309\u7167\u5e38\u7406\uff0c\u8fd9\u5c31\u610f\u5473\u7740\u795e\u7947\u5df2\u7ecf\u6b7b\u53bb\u4e86\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u7948\u5e76\u8005\u662f\u4fe1\u5f92\u7684\u7075\u9b42\u8f6c\u5316\uff0c\u662f\u795e\u56fd\u7684\u5b50\u6c11\uff0c\u662f\u795e\u7947\u8fc7\u53bb\u3001\u73b0\u5728\u548c\u5c06\u6765\u7684\u4e00\u90e8\u5206\u3002\u5c31\u4ee5\u5f80\u7684\u5386\u53f2\u7ecf\u9a8c\u6765\u8bf4\uff0c\u53ea\u6709\u795e\u7947\u88ab\u6467\u6bc1\uff0c\u53c8\u6ca1\u6709\u65b0\u7684\u795e\u7947\u63a5\u638c\u795e\u804c\uff0c\u624d\u4f1a\u51fa\u73b0\u8fd9\u79cd\u7948\u5e76\u8005\u7a81\u7136\u5168\u90e8\u6d88\u5931\u7684\u60c5\u5f62\u3002\u201d</p><p>\u201c\u8fd9\u4e0d\u5c31\u8bc1\u660e\u7f57\u4e1d\u786e\u5b9e\u6b7b\u4e86\u5417\u3002\u201d</p><p>\u201c\u4f46\u5979\u7684\u8718\u86db\u5821\u5792\u88ab\u67d0\u79cd\u529b\u91cf\u7262\u7262\u5c01\u95ed\u7740\uff0c\u8c01\u4e5f\u65e0\u6cd5\u8fdb\u5165\u3002\u800c\u4e14\u6df1\u5751\u9b54\u7f51\u4f9d\u65e7\u7f13\u6162\u4f46\u5374\u575a\u5b9a\u5730\u4ece\u6df1\u6e0a\u4e2d\u5206\u88c2\u3002\u5982\u679c\u7f57\u4e1d\u6b7b\u4e86\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u5176\u4ed6\u5353\u5c14\u795e\u7947\u662f\u4e0d\u53ef\u80fd\u6709\u8fd9\u79cd\u529b\u91cf\u7684\u3002\u201d</p><p>\u743c\u6069\u81ea\u7136\u6ca1\u6cd5\u56de\u7b54\uff0c\u201c\u7136\u540e\u5462\uff1f\u201d\u4ed6\u8f6c\u79fb\u8bdd\u9898\u3002</p><p>\u201c\u5927\u6982\u5728\u4e24\u5929\u2026\u2026\u6216\u8005\u4e09\u5929\u4e4b\u524d\uff0c\u6df1\u5751\u9b54\u7f51\u7684\u6f02\u79fb\u901f\u5ea6\u9661\u7136\u52a0\u5feb\uff0c\u7f57\u4e1d\u7684\u7948\u5e76\u8005\u4e5f\u518d\u5ea6\u51fa\u73b0\uff0c\u867d\u7136\u6570\u91cf\u5927\u5927\u51cf\u5c11\u3002\u8718\u86db\u5821\u5792\u518d\u6b21\u5f00\u59cb\u6cbf\u7740\u86db\u7f51\u79fb\u52a8\u2026\u2026\u201d</p><p>\u201c\u7b49\u7b49\uff0c\u8718\u86db\u5821\u5792\u6cbf\u7740\u86db\u7f51\u79fb\u52a8\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u7f57\u4e1d\u7684\u4e60\u60ef\uff0c\u6df1\u6e0a\u7684\u7b2c\u516d\u5341\u516d\u5c42\u88ab\u5979\u6539\u9020\u6210\u4e86\u4e00\u4e2a\u5e9e\u5927\u7684\u86db\u7f51\u4e16\u754c\uff0c\u800c\u5979\u559c\u6b22\u63a7\u5236\u7740\u5821\u5792\u6cbf\u7740\u86db\u4e1d\u6f2b\u6e38\u2014\u2014\u4e5f\u5c31\u662f\u8bf4\uff0c\u7f57\u4e1d\u6ca1\u6709\u6b7b\u3002\u201d</p><p>\u201c\u5c31\u51ed\u8fd9\u70b9\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\uff0c\u6709\u4e9b\u6076\u9b54\u8fdb\u5165\u516d\u5341\u516d\u5c42\u4e00\u63a2\u7a76\u7adf\uff0c\u636e\u552f\u4e00\u7684\u751f\u8fd8\u8005\u8bf4\uff0c\u4ed6\u770b\u89c1\u4e86\u7f57\u4e1d\u3002\u636e\u8bf4\u2026\u2026\u201d\u6b27\u51ef\u62d6\u957f\u4e86\u58f0\u8c03\uff0c\u201c\u636e\u8bf4\u8fd9\u4f4d\u5973\u795e\u6bd4\u4ee5\u524d\u66f4\u6f02\u4eae\u4e86\u3002\u201d</p><p>\u201c\u662f\u4e48\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u56e0\u4e3a\u90a3\u4e2a\u5012\u9709\u5bb6\u4f19\u770b\u89c1\u7684\u662f\u7f57\u4e1d\u7684\u8718\u86db\u5f62\u6001\u3002\u201d</p><p>\u201c\u90a3\u53c8\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u4ee5\u524d\u7f57\u4e1d\u957f\u4e00\u4e2a\u8111\u888b\uff0c\u73b0\u5728\u957f\u4e86\u516b\u4e2a\u8111\u888b\u3002\u201d</p><p>\u743c\u6069\u89c9\u5f97\u8eab\u4e0a\u6709\u4e9b\u53d1\u51b7\uff0c\u201c\u516b\u4e2a\u5934\u516b\u53ea\u811a\u7684\u8718\u86db\uff1f\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u5728\u8111\u4e2d\u60f3\u50cf\u7740\u5973\u795e\u7684\u5f62\u8c61\uff0c\u7a81\u7136\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6b27\u51ef\u5148\u751f\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u542c\u8bf4\u4f60\u975e\u5e38\u70ed\u7231\u5973\u795e\uff0c\u83ab\u975e\u5bf9\u7f57\u4e1d\u4e5f\u6709\u5174\u8da3\u4e48\uff1f\u201d</p><p>\u8fd9\u6b21\u8f6e\u5230\u6b27\u51ef\u7684\u8138\u8272\u53d8\u4e86\u53d8\uff0c\u4f46\u968f\u5373\u6062\u590d\u5982\u5e38\u3002\u201c\u5148\u4e0d\u8bf4\u8fd9\u4e2a\uff0c\u201d\u4ed6\u542b\u7cca\u7740\uff0c\u201c\u521a\u624d\u6211\u8fd8\u5fd8\u4e86\u8bf4\uff0c\u4ee5\u6df1\u5751\u9b54\u7f51\u73b0\u5728\u7684\u6f02\u79fb\u901f\u5ea6\u6765\u770b\uff0c\u5927\u6982\u518d\u6709\u4e00\u4e2a\u6708\u5230\u4e24\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u5c31\u4f1a\u5b8c\u5168\u4ece\u6df1\u6e0a\u4f4d\u9762\u6495\u88c2\uff0c\u6ed1\u5165\u661f\u754c\uff0c\u6210\u4e3a\u4e00\u4e2a\u65b0\u7684\u72ec\u7acb\u4f4d\u9762\u3002\u6211\u731c\u6d4b\u7684\u8bdd\uff0c\u7f57\u4e1d\u5e94\u8be5\u5728\u4f1a\u90a3\u4e2a\u65f6\u5019\u771f\u6b63\u590d\u82cf\u3002\u201d</p><p>\u201c\u4e00\u4e2a\u6708\u5230\u4e24\u4e2a\u6708\uff1f\u201d\u743c\u6069\u5927\u5927\u677e\u4e86\u53e3\u6c14\uff0c\u65f6\u95f4\u8fd8\u65e9\u3002</p><p>\u201c\u8fd9\u53ea\u662f\u6211\u7684\u731c\u6d4b\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u8bf4\u4e0d\u5b9a\u6f02\u79fb\u901f\u5ea6\u518d\u6b21\u52a0\u5feb\uff0c\u6216\u8005\u7f57\u4e1d\u660e\u5929\u5c31\u56de\u6765\u4e5f\u8bf4\u4e0d\u51c6\u3002\u201d</p><p>\u201c\u81f3\u5c11\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u5979\u7684\u7267\u5e08\u4eec\u4f9d\u65e7\u6ca1\u80fd\u83b7\u5f97\u795e\u672f\u3002\u201d</p><p>\u201c\u7b49\u5979\u56de\u6765\u5c31\u6709\u4e86\u3002\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u201c\u597d\u5427\uff0c\u8fd9\u4e9b\u6211\u90fd\u77e5\u9053\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f46\u6211\u7684\u95ee\u9898\u4f60\u4f9d\u65e7\u6ca1\u6709\u89e3\u91ca\uff0c\u7f57\u4e1d\u4e3a\u4ec0\u4e48\u7a81\u7136\u6c89\u7761\u8fd9\u4e48\u4e45\u5462\uff1f\u53ea\u662f\u4e3a\u4e86\u521b\u9020\u81ea\u5df1\u7684\u72ec\u7acb\u795e\u56fd\uff1f\u201d</p><p>\u6b27\u51ef\u6ca1\u6709\u7acb\u523b\u56de\u7b54\uff0c\u4ed6\u6162\u6162\u6446\u5f04\u7740\u68cb\u76d8\u4e0a\u7684\u68cb\u5b50\uff0c\u743c\u6069\u4e5f\u4e0d\u50ac\u4fc3\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u6b27\u51ef\u518d\u6b21\u62ac\u8d77\u5934\u6765\uff0c\u201c\u7f57\u4e1d\u7684\u60c5\u51b5\uff0c\u6211\u5c31\u77e5\u9053\u8fd9\u4e48\u591a\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6211\u8fd8\u6709\u4e00\u4e9b\u79c1\u4eba\u7684\u731c\u6d4b\uff0c\u4f60\u6709\u6ca1\u6709\u5174\u8da3\u542c\u542c\u3002\u201d</p><p>\u201c\u4f60\u521a\u624d\u4e0d\u5c31\u5df2\u7ecf\u5728\u731c\u6d4b\u4e86\u4e48\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4e0d\u540c\u7684\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u6211\u4e0b\u9762\u8981\u8bf4\u7684\uff0c\u662f\u7f57\u4e1d\u4e3a\u4ec0\u4e48\u8981\u6c89\u7761\u7684\u539f\u56e0\u3002\u201d</p><p>\u201c\u6211\u5728\u542c\u3002\u201d</p><p>\u201c\u4ee5\u5e38\u7406\u800c\u8a00\uff0c\u795e\u7947\u4e0d\u4f1a\u957f\u65f6\u95f4\u6c89\u5bc2\uff0c\u8fd9\u4f1a\u4e25\u91cd\u5f71\u54cd\u5176\u6559\u4f1a\u548c\u4fe1\u5f92\uff0c\u540c\u65f6\u4e5f\u5c31\u610f\u5473\u7740\u524a\u5f31\u81ea\u5df1\u7684\u529b\u91cf\uff0c\u201d\u6b27\u51ef\u659f\u914c\u7740\u8bcd\u8bed\uff0c\u201c\u51fa\u73b0\u8fd9\u79cd\u60c5\u51b5\uff0c\u6700\u53ef\u80fd\u7684\u662f\u56e0\u4e3a\u795e\u7947\u53d7\u5230\u91cd\u521b\uff0c\u4e0d\u5f97\u4e0d\u8fdb\u5165\u6c89\u7761\u72b6\u6001\uff0c\u4f11\u517b\u6062\u590d\u2014\u2014\u4f46\u7f57\u4e1d\u5e94\u8be5\u4e0d\u5c5e\u6b64\u7c7b\u3002\u201d</p><p>\u201c\u90a3\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u90a3\u4e9b\u7a81\u7136\u6d88\u5931\u7684\u7948\u5e76\u8005\uff0c\u5982\u679c\u6211\u731c\u6d4b\u4e0d\u9519\u7684\u8bdd\u2026\u2026\u5e94\u8be5\u662f\u88ab\u5979\u541e\u566c\u4e86\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u5fae\u5fae\u4e00\u60ca\uff0c\u4ed6\u867d\u7136\u5b97\u6559\u5b66\u4e0d\u600e\u4e48\u7cbe\u901a\uff0c\u4f46\u57fa\u672c\u7684\u5e38\u8bc6\u8fd8\u662f\u77e5\u9053\u7684\u3002\u7948\u5e76\u8005\u662f\u4fe1\u5f92\u7684\u7075\u9b42\u6240\u5316\uff0c\u5176\u5b9e\u5c31\u662f\u7cbe\u795e\u548c\u4fe1\u5ff5\u7684\u5177\u73b0\uff0c\u5b83\u4eec\u8fdb\u5165\u795e\u56fd\uff0c\u751f\u6d3b\u5728\u795e\u56fd\uff0c\u6e10\u6e10\u540c\u5316\uff0c\u548c\u795e\u7947\u878d\u4e3a\u4e00\u4f53\u3002\u4ece\u4fe1\u5f92\u7684\u89d2\u5ea6\u6765\u8bf4\uff0c\u795e\u7947\u5c31\u662f\u81ea\u5df1\u7406\u5ff5\u548c\u4fe1\u4ef0\u7684\u5316\u8eab\uff0c\u6210\u4e3a\u7948\u5e76\u8005\uff0c\u548c\u795e\u7947\u878d\u5408\uff0c\u610f\u5473\u7740\u81ea\u5df1\u6210\u4e3a\u6bd5\u751f\u8ffd\u6c42\u7684\u76ee\u6807\u7684\u4e00\u90e8\u5206\uff0c\u8fd9\u662f\u65e0\u4e0a\u7684\u8363\u8000\uff0c\u5c31\u4eff\u4f5b\u82f1\u96c4\u4e3a\u7406\u60f3\u732e\u8eab\uff0c\u5723\u5f92\u4e3a\u4fe1\u4ef0\u6b89\u9053\u4e00\u822c\u3002\u4f46\u5728\u975e\u4fe1\u5f92\u7684\u773c\u4e2d\u770b\u6765\uff0c\u8bf4\u5f97\u4e0d\u5ba2\u6c14\u4e9b\uff0c\u5c31\u662f\u8fd9\u4e9b\u4fe1\u5f92\u7684\u7075\u9b42\u88ab\u795e\u7947\u878d\u5408\u5438\u6536\uff0c\u58ee\u5927\u529b\u91cf\u53bb\u4e86\u3002</p><p>\u743c\u6069\u540d\u4e49\u4e0a\u5d07\u62dc\u591c\u5973\u58eb\u838e\u5c14\uff0c\u5176\u5b9e\u9aa8\u5b50\u91cc\u6ca1\u6709\u591a\u5c11\u771f\u6b63\u7684\u4fe1\u4ef0\uff0c\u5bf9\u4e8e\u795e\u7947\u548c\u7948\u5e76\u8005\u5173\u7cfb\u7684\u8bc4\u4ef7\u4e5f\u9ad8\u5c1a\u4e0d\u5230\u54ea\u91cc\u53bb\u3002\u53ea\u662f\u4ed6\u4e5f\u6709\u81ea\u5df1\u7684\u539f\u5219\uff0c\u56fa\u6267\u5df1\u89c1\u6ca1\u5173\u7cfb\uff0c\u4e0d\u8981\u5f3a\u52a0\u4e8e\u4eba\u5c31\u597d\uff0c\u81ea\u5df1\u4e0d\u613f\u610f\u6b7b\u4e86\u4ee5\u540e\u548c\u795e\u7947\u878d\u5408\uff0c\u5374\u4e5f\u6ca1\u5fc5\u8981\u53bb\u8d2c\u4f4e\u90a3\u4e9b\u4fe1\u5f92\uff0c\u4eba\u5404\u6709\u5fd7\uff0c\u65e0\u6240\u8c13\u5bf9\u9519\u3002</p><p>\u53ea\u662f\uff0c\u795e\u7947\u548c\u7948\u5e76\u8005\u878d\u5408\uff0c\u8fd9\u662f\u4e2a\u6f2b\u957f\u7684\u8fc7\u7a0b\uff0c\u6301\u7eed\u51e0\u5341\u5e74\u4e0a\u767e\u5e74\u90fd\u6b63\u5e38\uff0c\u800c\u4e14\u8fd9\u624d\u662f\u6b63\u9053\uff0c\u54ea\u6709\u6b27\u51ef\u6240\u8bf4\u7684\u8fd9\u79cd\uff0c\u4e00\u53e3\u6c14\u628a\u7948\u5e76\u8005\u5c3d\u6570\u541e\u566c\u4e86\u3002</p><p>\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u4ece\u6765\u90fd\u662f\u6b32\u901f\u5219\u4e0d\u8fbe\uff0c\u5982\u679c\u7f57\u4e1d\u771f\u8fd9\u4e48\u505a\u7684\u8bdd\uff0c\u90a3\u771f\u8981\u8ba9\u4eba\u6000\u7591\u8fd9\u4f4d\u7d20\u6765\u4ee5\u6df7\u4e71\u8457\u79f0\u7684\u5973\u795e\u662f\u4e0d\u662f\u53d1\u75af\u4e86\u3002\u6253\u4e2a\u4e0d\u751a\u6070\u5f53\u7684\u6bd4\u65b9\uff0c\u8fd9\u5c31\u50cf\u6811\u4e0a\u7684\u679c\u5b9e\uff0c\u6210\u719f\u6709\u5148\u540e\uff0c\u53c2\u5dee\u4e0d\u9f50\uff0c\u7b49\u6bcf\u4e2a\u679c\u5b50\u81ea\u7136\u6210\u719f\u518d\u9010\u4e2a\u4eab\u7528\uff0c\u624d\u662f\u6548\u76ca\u6700\u5927\u5316\uff0c\u76f4\u63a5\u4e00\u53e3\u6c14\u628a\u679c\u5b9e\u5168\u5403\u4e86\uff0c\u7eaf\u7cb9\u5c31\u662f\u6d6a\u8d39\u8d44\u6e90\uff0c\u4e8b\u500d\u529f\u534a\u3002</p><p>\u201c\u8fd9\u4e48\u505a\u7684\u8bdd\uff0c\u867d\u7136\u6d6a\u8d39\u4e0d\u5c11\uff0c\u4f46\u5374\u80fd\u591f\u5728\u77ed\u65f6\u95f4\u5185\u6025\u5267\u63d0\u5347\u529b\u91cf\uff0c\u201d\u6b27\u51ef\u89e3\u91ca\uff0c\u201c\u5426\u5219\u7684\u8bdd\uff0c\u5979\u5e94\u8be5\u4e5f\u6ca1\u529e\u6cd5\u5f3a\u884c\u5206\u88c2\u4f4d\u9762\u3002\u201d</p><p>\u201c\u4f46\u5979\u6c89\u5bc2\u8fd9\u4e48\u4e45\uff0c\u6559\u4f1a\u6df7\u4e71\uff0c\u4fe1\u5f92\u5185\u8ba7\uff0c\u53c8\u88ab\u5916\u654c\u8d81\u673a\u5165\u4fb5\uff0c\u635f\u5931\u5f88\u91cd\u554a\uff0c\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u4ec5\u4ee5\u74dc\u7406\u5fb7\u65af\u57ce\u4e3a\u4f8b\uff0c\u5979\u81f3\u5c11\u5c31\u8981\u635f\u5931\u6389\u4e00\u534a\u7684\u4fe1\u5f92\uff0c\u5176\u4e2d\u8fd8\u6709\u5f88\u591a\u7267\u5e08\u2026\u2026\u8fd9\u4e0d\u662f\u592a\u611a\u8822\u4e86\u5417\u3002\u201d</p><p>\u201c\u611a\u8822\u5728\u54ea\u91cc\uff1f\u201d\u6b27\u51ef\u53cd\u95ee\u3002</p><p>\u201c\u795e\u7947\u7684\u529b\u91cf\uff0c\u6765\u6e90\u4e8e\u4fe1\u5f92\u3002\u5979\u83ab\u540d\u5176\u5999\u7a81\u7136\u6c89\u5bc2\uff0c\u653e\u4efb\u4fe1\u5f92\u4e0d\u7406\uff0c\u8fd9\u6837\u53ea\u4f1a\u524a\u5f31\u81ea\u5df1\u7684\u529b\u91cf\u5427\u3002\u201d</p><p>\u6b27\u51ef\u6447\u5934\uff0c\u201c\u743c\u6069\uff0c\u4f60\u5b97\u6559\u5b66\u592a\u5dee\u4e86\u3002\u201d</p><p>\u201c\u5443\uff0c\u8fd9\u4e2a\u6211\u4e0d\u5426\u8ba4\uff0c\u6211\u53c8\u4e0d\u662f\u7267\u5e08\uff0c\u4f46\u96be\u9053\u6211\u8bf4\u9519\u4e86\u4e48\uff1f\u201d</p><p>\u201c\u5f53\u7136\u9519\u4e86\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u795e\u7947\u7684\u529b\u91cf\uff0c\u6765\u6e90\u4e8e\u4fe1\u4ef0\uff0c\u5e76\u975e\u4fe1\u5f92\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u201d\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u4e00\u65f6\u4e5f\u56de\u5fc6\u4e0d\u8d77\u81ea\u5df1\u4ee5\u524d\u770b\u5230\u7684\u539f\u8bdd\u662f\u4ec0\u4e48\uff0c\u201c\u5c31\u7b97\u662f\u5427\uff0c\u4f46\u8fd9\u4e24\u8005\u6709\u4ec0\u4e48\u533a\u522b\uff1f\u201d</p><p>\u201c\u533a\u522b\u5f88\u5927\uff0c\u201d\u6b27\u51ef\u89e3\u91ca\uff0c\u201c\u8fd9\u4e48\u8bf4\u5427\uff0c\u4f60\u60f3\u5fc5\u8ba4\u4e3a\uff0c\u4e00\u4f4d\u795e\u7947\u7684\u4fe1\u5f92\u6570\u91cf\u8d8a\u591a\uff0c\u6559\u4f1a\u89c4\u6a21\u8d8a\u5927\uff0c\u4ed6\u7684\u529b\u91cf\u4e5f\u5c31\u76f8\u5e94\u8d8a\u5f3a\u3002\u201d</p><p>\u201c\u5bf9\u554a\uff0c\u201d\u743c\u6069\u5947\u602a\uff0c\u201c\u96be\u9053\u4e0d\u662f\u5417\uff1f\u201d</p><p>\u201c\u81ea\u7136\u4e0d\u662f\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u6839\u636e\u6211\u524d\u5e74\u505a\u8fc7\u7684\u4e00\u4efd\u7edf\u8ba1\u62a5\u544a\uff0c\u591c\u5973\u58eb\u5728\u7269\u8d28\u754c\u5927\u7ea6\u6709\u5341\u4e07\u540d\u5de6\u53f3\u7684\u4fe1\u5f92\uff0c\u5176\u4e2d\u795e\u804c\u4eba\u5458\u5927\u7ea6\u4e09\u5343\u540d\u5230\u56db\u5343\u540d\uff1b\u9eef\u65e5\u5927\u7ea6\u6709\u4e09\u5341\u4e07\u540d\u4fe1\u5f92\uff0c\u5176\u4e2d\u795e\u804c\u4eba\u5458\u81f3\u5c11\u8d85\u8fc7\u4e00\u4e07\u2014\u2014\u8fd9\u4e24\u4f4d\u540c\u6837\u90fd\u662f\u9ad8\u7b49\u795e\uff0c\u4fe1\u5f92\u6570\u91cf\u5dee\u8ddd\u5374\u5982\u6b64\u5927\uff0c\u6309\u7167\u4f60\u7684\u8bf4\u6cd5\uff0c\u600e\u4e48\u89e3\u91ca\uff1f\u6216\u8005\u66f4\u7b80\u5355\u70b9\uff0c\u8fd9\u4e16\u754c\u4e0a\u7684\u4eba\u7c7b\u6570\u91cf\u7edd\u5bf9\u6bd4\u4e0d\u4e0a\u5730\u7cbe\uff0c\u6309\u4f60\u8fd9\u4e48\u7406\u89e3\uff0c\u5730\u7cbe\u795e\u9a6c\u683c\u9c81\u6bd4\u8036\u5c82\u4e0d\u5c31\u662f\u6700\u5f3a\u795e\u7947\u4e86\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u4eec\u7684\u4fe1\u5f92\u6570\u91cf\u7684\uff1f\u201d</p><p>\u201c\u6ca1\u529e\u6cd5\uff0c\u683c\u62c9\u5179\u7279\u5f53\u65f6\u7a81\u7136\u5fc3\u8840\u6765\u6f6e\uff0c\u8ba9\u6211\u7ed9\u4ed6\u505a\u4e00\u4efd\u62a5\u544a\uff0c\u4e3a\u8fd9\u4e8b\u6211\u53ef\u662f\u6574\u6574\u8dd1\u4e86\u534a\u5e74\u65f6\u95f4\uff0c\u7ed3\u679c\u4e00\u70b9\u597d\u5904\u6ca1\u62ff\u5230\u2026\u2026\u7b97\u4e86\uff0c\u4e0d\u8ddf\u4f60\u8bf4\u8fd9\u4e2a\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u662f\u4ec0\u4e48\u7f18\u6545\uff1f\u201d</p><p>\u201c\u5f88\u7b80\u5355\uff0c\u6211\u4e3e\u4e2a\u4f8b\u5b50\u4f60\u5c31\u660e\u767d\u4e86\uff0c\u6bd4\u5982\u5766\u5e15\u65af\uff0c\u4ed6\u662f\u6218\u795e\u3002\u5982\u679c\u6709\u4e00\u5929\uff0c\u4ed6\u7684\u4fe1\u5f92\u6570\u91cf\u9661\u7136\u589e\u52a0\u5341\u500d\uff0c\u6bcf\u5929\u5728\u795e\u6bbf\u6216\u8005\u5728\u5bb6\u5411\u4ed6\u8654\u8bda\u7948\u7977\u65e9\u665a\u819c\u62dc\uff0c\u4f60\u89c9\u5f97\u4ed6\u7684\u529b\u91cf\u4f1a\u5927\u5927\u63d0\u5347\u5bf9\u5427\uff1f\u201d</p><p>\u201c\u5e94\u8be5\u4f1a\u5427\u3002\u201d</p><p>\u201c\u9519\u4e86\uff0c\u201d\u6b27\u51ef\u6447\u5934\uff0c\u201c\u5982\u679c\u771f\u51fa\u73b0\u6211\u8bf4\u7684\u8fd9\u79cd\u60c5\u51b5\uff0c\u5766\u5e15\u65af\u53ea\u6015\u7acb\u523b\u5c31\u8981\u4ece\u9ad8\u7b49\u795e\u7684\u5e2d\u4f4d\u8dcc\u4e0b\u6765\u3002\u795e\u7947\u7684\u529b\u91cf\uff0c\u6e90\u4e8e\u4ed6\u6267\u638c\u7684\u795e\u804c\uff0c\u800c\u795e\u804c\u5c31\u662f\u4fe1\u4ef0\u7684\u51dd\u805a\u2014\u2014\u6240\u4ee5\u8bf4\u795e\u7947\u7684\u529b\u91cf\u6765\u81ea\u4fe1\u4ef0\u3002\u5766\u5e15\u65af\u662f\u6218\u795e\uff0c\u4ed6\u6240\u9700\u8981\u7684\uff0c\u6240\u671f\u671b\u7684\uff0c\u5c31\u662f\u8fd9\u4e16\u754c\u4e0a\u6218\u706b\u904d\u5e03\uff0c\u71c3\u70e7\u4e0d\u606f\u3002\u5047\u8bbe\u4ed6\u7684\u4fe1\u5f92\u6570\u91cf\u51cf\u534a\uff0c\u4f46\u5927\u9646\u4e0a\u7684\u6218\u4e89\u7206\u53d1\u53d8\u5f97\u9891\u7e41\u4e00\u500d\uff0c\u4ed6\u4f9d\u7136\u4f1a\u4ece\u4e2d\u83b7\u5f97\u5f88\u591a\u529b\u91cf\uff1b\u76f8\u53cd\u7684\uff0c\u5c31\u7b97\u4fe1\u5f92\u6570\u91cf\u589e\u52a0\u5341\u500d\uff0c\u4f46\u6574\u5929\u8eb2\u5728\u5bb6\u91cc\u7948\u7977\u62dc\u795e\uff0c\u5bf9\u4ed6\u54ea\u91cc\u53c8\u6709\u534a\u70b9\u610f\u4e49\u3002\u201d</p><p>\u201c\u795e\u7947\u4e4b\u6240\u4ee5\u9700\u8981\u4fe1\u5f92\uff0c\u9700\u8981\u7267\u5e08\uff0c\u5e76\u975e\u4e3a\u4e86\u8981\u4ed6\u4eec\u53e3\u5934\u7684\u5c0a\u5d07\u548c\u7948\u7977\u819c\u62dc\uff0c\u800c\u5728\u4e8e\u63a8\u884c\u8d2f\u5f7b\u81ea\u5df1\u6240\u4ee3\u8868\u7684\u7406\u5ff5\u2014\u2014\u6216\u8005\u8bf4\u4fe1\u4ef0\u3002\u63d0\u5c14\u7684\u8654\u8bda\u4fe1\u5f92\uff0c\u5927\u591a\u90fd\u662f\u7acb\u6cd5\u8005\u3001\u6cd5\u5b98\u3001\u68c0\u5bdf\u5b98\u6216\u8005\u5f8b\u5e08\u4e4b\u7c7b\uff0c\u6b63\u4e49\u548c\u5f8b\u6cd5\uff0c\u4fbf\u501f\u52a9\u4ed6\u4eec\u800c\u63a8\u884c\uff1b\u540c\u6837\u7684\uff0c\u6b27\u683c\u739b\u7684\u4fe1\u5f92\uff0c\u591a\u6709\u5b66\u8005\u3001\u541f\u6e38\u8bd7\u4eba\u3001\u827a\u672f\u5bb6\u4e4b\u7c7b\uff0c\u4ed6\u4eec\u4fdd\u5b58\u77e5\u8bc6\uff0c\u5efa\u7acb\u56fe\u4e66\u9986\uff0c\u6536\u96c6\u8f76\u95fb\u5178\u7c4d\uff0c\u4f20\u64ad\u8bd7\u6b4c\u827a\u672f\u3002\u5f53\u7136\u4e86\uff0c\u5766\u5e15\u65af\u7684\u7267\u5e08\u5927\u591a\u6218\u4e89\u72c2\uff0c\u8d21\u5fb7\u7684\u4fe1\u5f92\u70ed\u7231\u9f13\u6363\u53d1\u660e\uff0c\u6c83\u91d1\u7684\u624b\u4e0b\u64c5\u957f\u635e\u94b1\u7ecf\u5546\uff0c\u4e5f\u90fd\u662f\u8fd9\u4e2a\u9053\u7406\u4e86\u3002\u201d</p><p>\u201c\u5514\uff0c\u6211\u597d\u50cf\u6709\u70b9\u660e\u767d\u4e86\u3002\u201d</p><p>\u6b27\u51ef\u7684\u8bdd\uff0c\u5982\u679c\u7b80\u5355\u7ffb\u8bd1\u7684\u8bdd\uff0c\u5176\u5b9e\u5c31\u6709\u70b9\u7c7b\u4f3c\u4e8e\u516c\u53f8\u3002\u4e00\u4e2a\u516c\u53f8\u89c4\u6a21\u5927\uff0c\u5458\u5de5\u6570\u91cf\u591a\uff0c\u56fa\u7136\u5e72\u6d3b\u7684\u4eba\u4e5f\u591a\uff0c\u4f46\u4e5f\u5b8c\u5168\u53ef\u80fd\u53ea\u9886\u5de5\u8d44\u4e0d\u5356\u529b\uff0c\u5e76\u4e0d\u610f\u5473\u7740\u6548\u76ca\u4e00\u5b9a\u5c31\u597d\u3002\u4fe1\u4ef0\u548c\u4fe1\u5f92\uff0c\u5c31\u5982\u6548\u76ca\u548c\u6570\u91cf\uff0c\u867d\u7136\u6709\u4e00\u5b9a\u5173\u7cfb\uff0c\u4f46\u4e0d\u80fd\u76f4\u63a5\u7b49\u540c\u3002</p><p>\u4fe1\u5f92\uff0c\u5c24\u5176\u662f\u8654\u8bda\u4fe1\u5f92\uff0c\u5305\u62ec\u795e\u804c\u4eba\u5458\uff0c\u662f\u7528\u6765\u5e72\u6d3b\u7684\uff0c\u4e0d\u662f\u8981\u4ed6\u4eec\u5fd9\u7740\u7948\u7977\u5d07\u62dc\u7684\uff0c\u90a3\u5bf9\u795e\u7947\u800c\u8a00\u6beb\u65e0\u610f\u4e49\u3002</p><p>\u201c\u7f57\u4e1d\u6c89\u5bc2\uff0c\u4e0d\u7ba1\u6709\u4ec0\u4e48\u539f\u56e0\uff0c\u5c31\u76ee\u524d\u7684\u60c5\u51b5\u6765\u770b\uff0c\u5979\u5e76\u6ca1\u6709\u53d7\u5230\u4ec0\u4e48\u5a01\u80c1\uff0c\u4e5f\u5e76\u975e\u88ab\u91cd\u521b\u53d7\u4f24\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u6df1\u5751\u9b54\u7f51\u53d1\u751f\u7684\u4e00\u5207\uff0c\u90fd\u5728\u5979\u7684\u638c\u63e1\u548c\u63a7\u5236\u4e4b\u4e2d\uff0c\u540c\u6837\u7684\uff0c\u8fd9\u4e9b\u5353\u5c14\u4eec\u7684\u6298\u817e\uff0c\u5979\u80af\u5b9a\u4e5f\u65e9\u6709\u9884\u8ba1\u3002\u5982\u679c\u771f\u8981\u907f\u514d\u4eca\u5929\u8fd9\u79cd\u5c40\u9762\uff0c\u4e8e\u5979\u800c\u8a00\u96be\u9053\u5f88\u96be\u5417\uff1f\u6700\u7b80\u5355\u7684\u4f5c\u6cd5\uff0c\u63d0\u524d\u9881\u4e0b\u795e\u8c15\uff0c\u544a\u8bc9\u5979\u7684\u4fe1\u5f92\u4eec\u81ea\u5df1\u5c06\u8981\u6682\u79bb\u51e0\u4e2a\u6708\uff0c\u4ee5\u5979\u5343\u4e07\u5e74\u6765\u5728\u5353\u5c14\u4e2d\u7684\u5a01\u538b\uff0c\u8c01\u6562\u6709\u5f02\u5fc3\uff1f\u8fd9\u6837\u7a81\u7136\u6c89\u5bc2\uff0c\u5206\u660e\u5c31\u662f\u6545\u610f\u9f13\u52b1\u522b\u4eba\u53bb\u731c\u6d4b\u5979\u5df2\u7ecf\u906d\u9047\u4e0d\u5e78\u4e86\u5427\u3002\u201d</p><p>\u743c\u6069\u76b1\u7709\uff0c\u6b27\u51ef\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5012\u786e\u5b9e\u6709\u51e0\u5206\u9053\u7406\u3002\u5982\u679c\u7f57\u4e1d\u786e\u5b9e\u6b7b\u4e86\uff0c\u90a3\u81ea\u7136\u4e00\u5207\u597d\u8bf4\uff1b\u4f46\u5018\u82e5\u5979\u5176\u5b9e\u6ca1\u4e8b\uff0c\u90a3\u8fd9\u79cd\u4f5c\u6cd5\u5c31\u5b8c\u5168\u8bf4\u4e0d\u901a\u4e86\u3002</p><p>\u201c\u90a3\u7f57\u4e1d\u8fd9\u4e48\u505a\u7684\u610f\u601d\u662f\u2026\u2026\u201d</p><p>\u201c\u7f57\u4e1d\u5176\u5b9e\u5c31\u662f\u5353\u5c14\u672c\u6027\u7684\u5177\u73b0\u5316\uff0c\u5979\u6240\u559c\u6b22\u7684\uff0c\u662f\u9634\u8c0b\u3001\u6df7\u4e71\u548c\u80cc\u4fe1\u5f03\u4e49\uff0c\u8fd9\u4e9b\u4e5f\u6b63\u662f\u5979\u7684\u795e\u529b\u6e90\u6cc9\u3002\u6b64\u6b21\u6c89\u5bc2\uff0c\u51e0\u4e4e\u6bcf\u5904\u90fd\u7206\u53d1\u4e86\u5927\u5927\u5c0f\u5c0f\u7684\u5185\u6218\u548c\u53db\u4e71\uff0c\u5353\u5c14\u4eec\u5f7c\u6b64\u731c\u5fcc\u3001\u8c0b\u6740\u3001\u80cc\u53db\uff0c\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u60c5\u666f\uff0c\u5728\u5176\u4ed6\u5353\u5c14\u57ce\u5e02\u540c\u6837\u4e0a\u6f14\u3002\u5982\u679c\u8ba9\u6211\u6765\u8bf4\u7684\u8bdd\uff0c\u770b\u5230\u8fd9\u4e00\u5207\uff0c\u8718\u86db\u5973\u795e\u4e00\u5b9a\u4e3a\u6b64\u800c\u611f\u5230\u4e07\u5206\u6109\u60a6\u5427\u3002\u201d</p><p>\u201c\u4f46\u8fd9\u4e0d\u662f\u6740\u9e21\u53d6\u5375\u4e48\u3002\u201d</p><p>\u201c\u6740\u9e21\u53d6\u5375\uff1f\u201d</p><p>\u201c\u6211\u7684\u610f\u601d\u662f\u8bf4\u8fd9\u672a\u514d\u592a\u76ee\u5149\u77ed\u6d45\u4e86\uff0c\u201d\u743c\u6069\u8d76\u5feb\u89e3\u91ca\uff0c\u201c\u867d\u7136\u77ed\u671f\u5185\u53ef\u80fd\u4f1a\u529b\u91cf\u5267\u589e\uff0c\u4f46\u4ece\u957f\u671f\u770b\u6765\uff0c\u4fe1\u5f92\u4e27\u5931\uff0c\u7267\u5e08\u6b7b\u4f24\uff0c\u8fd9\u5b8c\u5168\u5f97\u4e0d\u507f\u5931\u554a\u3002\u201d</p><p>\u201c\u4f46\u5982\u679c\u5979\u7684\u76ee\u6807\u5c31\u662f\u4e3a\u4e86\u77ed\u671f\u7684\u529b\u91cf\u589e\u5f3a\u5462\uff1f\u201d\u6b27\u51ef\u53cd\u95ee\uff0c\u201c\u90a3\u4e48\u541e\u566c\u7948\u5e76\u8005\uff0c\u653e\u4efb\u4fe1\u5f92\u81ea\u76f8\u6b8b\u6740\uff0c\u8fd9\u4e00\u5207\u90fd\u6709\u4e86\u5408\u7406\u89e3\u91ca\u3002\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u8fd9\u79cd\u89e3\u91ca\u592a\u8352\u8c2c\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4e00\u5207\u90fd\u5efa\u7acb\u5728\u4f60\u7684\u5047\u8bbe\u4e4b\u4e0a\uff0c\u4f60\u5047\u8bbe\u7f57\u4e1d\u76ee\u5149\u77ed\u6d45\u2026\u2026\u201d</p><p>\u6b27\u51ef\u5fae\u5fae\u51b7\u7b11\uff0c\u201c\u672a\u5fc5\uff0c\u8fd9\u53ef\u4e0d\u662f\u76ee\u5149\u77ed\u6d45\uff0c\u800c\u662f\u8feb\u4e0d\u5f97\u5df2\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\uff0c\u201d\u6b27\u51ef\u6162\u60a0\u60a0\u5730\u8bf4\uff0c\u201c\u7b2c\u56db\u7eaa\u5feb\u5230\u4e86\u561b\u3002\u201d</p><p>\u7b2c\u56db\u7eaa\u3002</p><p>\u743c\u6069\u5df2\u7ecf\u4e0d\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u8fd9\u4e2a\u8bcd\uff0c\u4e0a\u6b21\u662f\u5728\u5feb\u5230\u5965\u7075\u591a\u5c14\u7684\u65f6\u5019\uff0c\u8c08\u53ca\u7075\u5438\u602a\u7684\u795e\u7947\u4f0a\u68ee\u8d5b\u6069\u7684\u65f6\u5019\uff0c\u6b27\u51ef\u5c31\u8bf4\u4e86\u53e5\u201c\u7b2c\u56db\u7eaa\u5c31\u5feb\u5230\u4e86\u201d\uff0c\u53ea\u662f\u5f53\u65f6\u6ca1\u6765\u5f97\u53ca\u7ec6\u95ee\u3002\u5982\u4eca\u63d0\u5230\u7f57\u4e1d\uff0c\u53c8\u542c\u5230\u4e86\u8fd9\u4e2a\u8bcd\u3002</p><p>\u201c\u7b2c\u56db\u7eaa\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u4e5f\u6ca1\u4ec0\u4e48\u610f\u601d\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u65e0\u975e\u662f\u53c8\u4e00\u6b21\u6d17\u724c\u3002\u4f60\u53bb\u8fc7\u77ee\u4eba\u57ce\u5e02\uff0c\u5e94\u8be5\u77e5\u9053\u96f7\u9e23\u795d\u798f\u5427\u3002\u201d</p><p>\u201c\u77e5\u9053\u3002\u201d</p><p>\u201c\u5728\u9634\u9b42\u57ce\u7684\u65f6\u5019\uff0c\u6709\u4e00\u5929\u665a\u4e0a\uff0c\u6709\u6ca1\u6709\u770b\u5230\u6d41\u661f\u96e8\uff1f\u201d</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u968f\u5373\u56de\u60f3\u8d77\u6765\uff0c\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u5c31\u5728\u90a3\u4e00\u5929\uff0c\u7cbe\u7075\u8bf8\u795e\u548c\u7a46\u7f55\u745e\u5fb7\u8bf8\u795e\u8054\u5408\u653b\u5165\u517d\u4eba\u4e4b\u571f\uff08Nishrek\uff0c\u517d\u4eba\u795e\u56fd\uff09\uff0c\u517d\u4eba\u8bf8\u795e\u81ea\u683c\u4e4c\u4ec0\u4ee5\u4e0b\u5c3d\u6570\u6218\u6b7b\uff0c\u201d\u6b27\u51ef\u8f7b\u58f0\u7b11\u7740\uff0c\u201c\u5982\u4eca\u4e16\u754c\u4e0a\u5df2\u7ecf\u6ca1\u6709\u517d\u4eba\u795e\u7947\u4e86\uff0c\u90a3\u573a\u6d41\u661f\u96e8\uff0c\u5c31\u662f\u795e\u7947\u5d29\u6e83\u7684\u666f\u8c61\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u6211\u60f3\uff0c\u6211\u5927\u6982\u77e5\u9053\u7b2c\u56db\u7eaa\u662f\u4ec0\u4e48\u4e86\u3002\u201d</p></article>"}}}]);
