(window.webpackJsonp=window.webpackJsonp||[]).push([[1481],{1504:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u56db\u8282 \u6bd5\u4e1a\u5178\u793c",content:"<article><h2>\u7b2c\u56db\u5341\u56db\u8282 \u6bd5\u4e1a\u5178\u793c</h2><p>\u6240\u8c13\u7b2c\u56db\u7eaa\uff0c\u5c31\u662f\u53c8\u4e00\u6b21\u6d17\u724c\u3002</p><p>\u743c\u6069\u4ece\u6765\u4e0d\u8d4c\u535a\uff0c\u4e8e\u9ebb\u5c06\u6251\u514b\u8fd9\u4e9b\u90fd\u4e00\u7a8d\u4e0d\u901a\uff0c\u4f46\u5bf9\u6d17\u724c\u7684\u610f\u601d\u603b\u8fd8\u662f\u660e\u767d\u7684\u3002\u4e00\u8a00\u800c\u853d\u4e4b\uff0c\u8fd9\u4e2a\u4e16\u754c\u8981\u53d1\u751f\u7ffb\u5929\u8986\u5730\u7684\u5267\u53d8\u3002\u517d\u4eba\u8bf8\u795e\u7684\u96c6\u4f53\u8986\u706d\uff0c\u77ee\u4eba\u4e3b\u795e\u83ab\u62c9\u4e01\u964d\u4e0b\u96f7\u9e23\u795d\u798f\uff0c\u9634\u9b42\u57ce\u7a81\u7136\u56de\u5f52\u7269\u8d28\u754c\u53c8\u8fdb\u5165\u5e7d\u6697\u5730\u57df\uff08\u8fd9\u80cc\u540e\u81ea\u7136\u6709\u838e\u5c14\u7684\u64cd\u7eb5\uff09\uff0c\u5982\u4eca\u6309\u7167\u6b27\u51ef\u63a8\u6d4b\uff0c\u7f57\u4e1d\u4e0d\u60dc\u7528\u6740\u9e21\u53d6\u5375\u7684\u65b9\u5f0f\u5f3a\u884c\u63d0\u5347\u529b\u91cf\u2014\u2014\u79cd\u79cd\u8ff9\u8c61\u8868\u660e\uff0c\u4e00\u573a\u7275\u6d89\u6df1\u8fdc\uff0c\u8fde\u795e\u7947\u90fd\u5377\u5165\u5176\u4e2d\u7684\u5927\u52a8\u8361\u5373\u5c06\u5230\u6765\u4e86\u3002</p><p>\u201c\u96be\u4e0d\u6210\u53c8\u662f\u4e00\u6b21\u52a8\u8361\u5e74\u4ee3\uff1f\u8fd9\u624d\u521a\u8fc7\u53bb\u4e0d\u5230\u5341\u4e94\u5e74\u5427\u3002\u201d</p><p>\u5177\u4f53\u7684\u8be6\u60c5\uff0c\u6b27\u51ef\u95ed\u53e3\u4e0d\u8a00\uff0c\u743c\u6069\u4e5f\u4e0d\u518d\u591a\u95ee\u3002\u7b2c\u56db\u7eaa\u4e5f\u7f62\uff0c\u4e16\u754c\u52a8\u8361\u4e5f\u7f62\uff0c\u81ea\u5df1\u53ea\u662f\u4e2a\u5c0f\u4eba\u7269\uff0c\u53cd\u6b63\u53c8\u6ca1\u6709\u4e89\u9738\u5929\u4e0b\u7edf\u4e00\u5927\u9646\u4e4b\u7c7b\u7684\u5b8f\u4f1f\u62b1\u8d1f\uff0c\u65e2\u6ca1\u5fc5\u8981\u4e5f\u6ca1\u8d44\u683c\u5173\u5fc3\u90a3\u4e48\u591a\uff0c\u8fd8\u662f\u8003\u8651\u773c\u524d\u7684\u4e8b\u60c5\u624d\u662f\u6b63\u7ecf\u3002</p><p>\u201c\u597d\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u63c9\u7740\u81ea\u5df1\u7684\u989d\u89d2\uff0c\u201c\u7f57\u4e1d\u7684\u4e8b\u60c5\uff0c\u6211\u5dee\u4e0d\u591a\u4e5f\u77e5\u9053\u4e86\uff0c\u542c\u8d77\u6765\u771f\u662f\u6709\u591f\u6df7\u4e71\u7684\u2026\u2026\u4e0d\u8fc7\u8fd9\u548c\u4f60\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u5982\u679c\u548c\u4f60\u6ca1\u5173\u7cfb\u7684\u8bdd\uff0c\u4f60\u5e72\u561b\u8981\u5173\u5fc3\u8fd9\u4e9b\u5353\u5c14\u7267\u5e08\u7684\u6b7b\u6d3b\u5462\uff0c\u201d\u743c\u6069\u95ee\uff0c\u201c\u96be\u4e0d\u6210\u4f60\u53c8\u6362\u5de5\u4f5c\u4e86\uff0c\u6539\u6295\u5230\u516b\u4e2a\u8111\u888b\u7684\u5973\u795e\u88d9\u4e0b\uff1f\u201d</p><p>\u201c\u600e\u4e48\u53ef\u80fd\uff0c\u201d\u6b27\u51ef\u4e49\u6b63\u8bcd\u4e25\u5730\u8bf4\uff0c\u201c\u6211\u53ef\u662f\u5802\u5802\u6b63\u6b63\u7684\u6076\u9b54\uff0c\u600e\u4e48\u4f1a\u8dd1\u53bb\u7ed9\u795e\u7947\u5f53\u4e0b\u5c5e\uff0c\u8fd9\u79cd\u6ca1\u6709\u7acb\u573a\u539f\u5219\u7684\u4e8b\u60c5\u6211\u662f\u4e0d\u4f1a\u5e72\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u771f\u662f\u4e2a\u4e0d\u9519\u7684\u51b7\u7b11\u8bdd\u3002\u201d</p><p>\u8bdd\u867d\u5982\u6b64\uff0c\u743c\u6069\u5176\u5b9e\u4e5f\u5e76\u4e0d\u771f\u76f8\u4fe1\u6b27\u51ef\u5df2\u7ecf\u5f53\u4e86\u7f57\u4e1d\u7684\u6253\u624b\uff0c\u201c\u90a3\u5c31\u662f\u4f60\u88ab\u6536\u4e70\u4e86\uff0c\u201d\u4ed6\u534a\u5f00\u73a9\u7b11\u5730\u8bf4\uff0c\u201c\u96be\u602a\u5446\u5728\u8fd9\u5730\u65b9\uff0c\u60f3\u5fc5\u662f\u591c\u591c\u7b19\u6b4c\uff0c\u628a\u8fd9\u91cc\u7684\u7f8e\u4eba\u90fd\u5149\u987e\u904d\u4e86\u5427\u3002\u201d</p><p>\u8bf4\u4e5f\u5947\u602a\uff0c\u867d\u7136\u660e\u77e5\u5bf9\u65b9\u662f\u6076\u9b54\uff0c\u800c\u4e14\u662f\u5b9e\u529b\u5f3a\u6a2a\u7684\u5927\u6076\u9b54\uff0c\u743c\u6069\u548c\u6b27\u51ef\u76f8\u5904\uff0c\u5374\u5e76\u65e0\u6709\u4ec0\u4e48\u5bb3\u6015\u6216\u8005\u62d8\u8c28\u4e4b\u5904\uff0c\u53ea\u5f53\u662f\u666e\u901a\u4eba\u4e00\u822c\u3002\u82e5\u8bba\u8d77\u6765\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u5f85\u4eba\u4e5f\u6e29\u548c\u8c26\u900a\uff0c\u5f6c\u5f6c\u6709\u793c\uff0c\u4f46\u743c\u6069\u548c\u4ed6\u5728\u4e00\u8d77\uff0c\u81ea\u7136\u800c\u7136\u4fbf\u6e05\u695a\u5f7c\u6b64\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u4e0a\u9762\u8fd9\u79cd\u8bdd\u662f\u7edd\u5bf9\u4e0d\u6562\u8bf4\u7684\u3002</p><p>\u6b27\u51ef\u55e4\u4e86\u4e00\u58f0\uff0c\u8868\u793a\u5bf9\u743c\u6069\u7684\u8bdd\u5f88\u4e0d\u4ee5\u4e3a\u7136\uff0c\u201c\u5c31\u8fd9\u4e9b\u5353\u5c14\uff1f\u6211\u53ef\u6ca1\u5174\u8da3\u770b\u4e0a\u3002\u201d</p><p>\u201c\u770b\u4e0d\u4e0a\uff1f\u4f60\u4e5f\u592a\u6311\u5254\u4e86\u5427\u3002\u201d</p><p>\u5353\u5c14\u4e5f\u662f\u7cbe\u7075\u4e9a\u79cd\uff0c\u7d20\u4ee5\u5bb9\u8c8c\u7cbe\u81f4\u79c0\u4e3d\u8457\u79f0\uff0c\u867d\u8bf4\u76ae\u80a4\u9ed1\u8fd9\u70b9\u548c\u5730\u8868\u4eba\u7c7b\u7684\u5ba1\u7f8e\u89c2\u6709\u4e9b\u51b2\u7a81\uff0c\u4f46\u53ea\u8981\u770b\u4e60\u60ef\u4e86\u4e5f\u5c31\u522b\u6709\u4e00\u756a\u7f8e\u611f\u3002\u743c\u6069\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u91cc\u5446\u8fd9\u4e48\u4e45\uff0c\u653e\u773c\u671b\u53bb\u65e0\u8bba\u662f\u7537\u5973\u5c0a\u5351\uff0c\u81f3\u5c11\u76f8\u8c8c\u4e0a\u4e2a\u4e2a\u90fd\u79f0\u5f97\u4e0a\u4fca\u7f8e\u3002\u6b27\u51ef\u5c45\u7136\u8bf4\u770b\u4e0d\u4e0a\uff1f</p><p>\u201c\u4e0d\u662f\u6211\u6311\u5254\uff0c\u662f\u4f60\u89c1\u8bc6\u592a\u5c11\uff0c\u773c\u754c\u592a\u7a84\uff0c\u201d\u6b27\u51ef\u6beb\u4e0d\u5ba2\u6c14\u5730\u6279\u8bc4\u7740\u743c\u6069\uff0c\u201c\u6709\u7a7a\u6211\u5e26\u4f60\u53bb\u6df1\u6e0a\u91cc\u89c1\u8bc6\u89c1\u8bc6\u9b45\u9b54\uff0c\u4f60\u5c31\u4f1a\u5bf9\u8fd9\u4e9b\u5353\u5c14\u4e0d\u5c51\u4e00\u987e\u4e86\u3002\u201d</p><p>\u201c\u9b45\u9b54\u6709\u90a3\u4e48\u6f02\u4eae\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u6f02\u4eae\uff0c\u662f\u5b8c\u7f8e\uff0c\u201d\u6b27\u51ef\u7ea0\u6b63\uff0c\u201c\u4f60\u8981\u660e\u767d\uff0c\u9b45\u9b54\u5c31\u662f\u5973\u6027\u6b32\u671b\u548c\u611f\u6027\u7684\u5177\u8c61\uff0c\u662f\u5979\u4eec\u7684\u61a7\u61ac\u548c\u68a6\u5bd0\u4ee5\u6c42\u3002\u4f60\u559c\u6b22\u4ec0\u4e48\u6837\u7684\u5973\u6027\uff0c\u9b45\u9b54\u5c31\u80fd\u4ee5\u5979\u7684\u5f62\u8c61\u5728\u4f60\u9762\u524d\u51fa\u73b0\uff0c\u6ca1\u6709\u7f3a\u9677\uff0c\u6ca1\u6709\u7455\u75b5\u2026\u2026\u5f53\u7136\uff0c\u5927\u591a\u6570\u4eba\u5176\u5b9e\u5e76\u4e0d\u77e5\u9053\u81ea\u5df1\u771f\u6b63\u559c\u6b22\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\u2026\u2026\u4f46\u5353\u5c14\u7684\u5e8a\u4e0a\u6280\u672f\u5e94\u8be5\u8fd8\u662f\u503c\u5f97\u5c1d\u8bd5\u7684\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u9ed1\u6697\u7cbe\u7075\u7684\u60c5\u6b32\u827a\u672f\uff0c\u5c31\u8fde\u6211\u5728\u5730\u8868\u7684\u65f6\u5019\u90fd\u4e45\u95fb\u5176\u540d\u554a\u3002\u201d</p><p>\u201c\u5353\u5c14\u7684\u60c5\u6b32\u827a\u672f\uff1f\u201d\u6b27\u51ef\u53cd\u95ee\uff0c\u201c\u4f60\u662f\u6307\u7537\u6027\uff0c\u8fd8\u662f\u5973\u6027\uff1f\u201d</p><p>\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u6709\u4e00\u4e2a\u7b80\u5355\u7684\u9053\u7406\uff0c\u201d\u6b27\u51ef\u5c48\u8d77\u624b\u6307\uff0c\u201c\u6df1\u6e0a\u662f\u5f31\u8089\u5f3a\u98df\u4e4b\u6240\uff0c\u9b45\u9b54\u662f\u6076\u9b54\u4e2d\u975e\u5e38\u5f31\u5c0f\u7684\u4e00\u7c7b\uff0c\u5979\u5fc5\u987b\u53bb\u52aa\u529b\u53d6\u60a6\u90a3\u4e9b\u66f4\u5f3a\u5927\u7684\u5b58\u5728\uff0c\u624d\u80fd\u591f\u751f\u5b58\u4e0b\u6765\u3002\u800c\u5728\u5353\u5c14\u793e\u4f1a\uff0c\u5973\u6027\u5360\u636e\u7edf\u6cbb\u5730\u4f4d\uff0c\u7537\u6027\u5fc5\u987b\u8ba8\u5979\u4eec\u7684\u6b22\u5fc3\u624d\u80fd\u6d3b\u7740\u2014\u2014\u5728\u8fd9\u79cd\u73af\u5883\u4e0b\uff0c\u4f60\u5bf9\u5973\u6027\u5353\u5c14\u7684\u5e8a\u4e0a\u6280\u672f\u62b1\u591a\u9ad8\u7684\u5e0c\u671b\uff1f\u5f53\u7136\uff0c\u5982\u679c\u4f60\u559c\u6b22\u7537\u6027\uff0c\u90a3\u5012\u662f\u6b63\u4e2d\u4e0b\u6000\u3002\u201d</p><p>\u201c\u2026\u2026\u6eda\uff01\u201d</p><p>\u6b27\u51ef\u54c8\u54c8\u5927\u7b11\uff0c\u9887\u4e3a\u5f97\u610f\uff0c\u7136\u800c\u7b11\u58f0\u672a\u6b47\uff0c\u743c\u6069\u63a5\u4e0b\u6765\u4e00\u53e5\u8bdd\u628a\u4ed6\u564e\u4f4f\u4e86\u3002\u201c\u542c\u8bf4\u683c\u62c9\u5179\u7279\u559c\u597d\u7537\u98ce\uff0c\u7537\u5973\u901a\u5403\uff0c\u4e0d\u77e5\u9053\u4ed6\u4f1a\u4e0d\u4f1a\u5bf9\u81ea\u5df1\u7684\u90e8\u4e0b\u4e0b\u624b\u2026\u2026\u201d</p><p>\u201c\u95ed\u5634\uff01\u201d</p><p>\u743c\u6069\u89c1\u6b27\u51ef\u8138\u8272\u4e0d\u5feb\uff0c\u4e5f\u4fbf\u4e0d\u518d\u5f00\u8fd9\u79cd\u73a9\u7b11\uff0c\u806a\u660e\u5730\u8f6c\u79fb\u8bdd\u9898\u3002\u201c\u6309\u4f60\u8fd9\u4e48\u8bf4\uff0c\u5353\u5c14\u4eec\u786e\u5b9e\u662f\u4e0d\u503c\u5f97\u4e00\u63d0\u4e86\u2026\u2026\u4f46\u6709\u4ef6\u4e8b\uff0c\u6211\u4e0d\u592a\u660e\u767d\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5973\u795e\u53c8\u6bd4\u9b45\u9b54\u5f3a\u5728\u54ea\u91cc\u5462\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u96be\u9053\u5973\u795e\u6bd4\u9b45\u9b54\u66f4\u6f02\u4eae\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u5012\u672a\u5fc5\u3002\u201d</p><p>\u201c\u90a3\u4e48\u662f\u5973\u795e\u6bd4\u9b45\u9b54\u5e8a\u4e0a\u6280\u672f\u66f4\u9ad8\u660e\uff1f\u201d</p><p>\u201c\u8fd9\u600e\u4e48\u53ef\u80fd\u2026\u2026\u201d</p><p>\u201c\u90a3\u4f60\u5e72\u561b\u5bf9\u5973\u795e\u5ff5\u5ff5\u4e0d\u5fd8\u5462\uff0c\u76f4\u63a5\u56de\u6df1\u6e0a\u52fe\u642d\u9b45\u9b54\u5c31\u662f\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4f60\u4e0d\u61c2\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u5f53\u7269\u8d28\u6027\u7684\u8ffd\u6c42\u8fbe\u5230\u9876\u70b9\uff0c\u7cbe\u795e\u6027\u7684\u8ffd\u6c42\u5c31\u4f1a\u5360\u636e\u4e0a\u98ce\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u53e5\u8bdd\u597d\u6709\u54f2\u5b66\u5473\u9053\u3002\u201d</p><p>\u201c\u54f2\u5b66\u5473\u9053\uff1f\u201d</p><p>\u201c\u610f\u601d\u5c31\u662f\u8bf4\u6211\u6ca1\u542c\u61c2\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u4e48\u8bf4\uff0c\u4f60\u559c\u6b22\u6f02\u4eae\u7f8e\u4eba\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u5982\u679c\u73b0\u5728\u4f60\u53d1\u73b0\u8eab\u8fb9\u7f8e\u4eba\u65e0\u6570\uff0c\u4efb\u7531\u53d6\u4e88\uff0c\u503e\u57ce\u7edd\u8272\u553e\u624b\u53ef\u5f97\uff0c\u4e0d\u8d39\u534a\u5206\u529b\u6c14\uff0c\u90a3\u4f60\u8fd8\u6709\u4ec0\u4e48\u53ef\u8ffd\u6c42\u7684\u5462\uff1f\u201d</p><p>\u201c\u6211\u5927\u6982\u61c2\u4e86\u3002\u201d</p><p>\u8fd9\u9053\u7406\u5176\u5b9e\u4e5f\u7b80\u5355\uff0c\u5c31\u50cf\u5973\u660e\u661f\u672a\u5fc5\u591a\u4e48\u6f02\u4eae\uff0c\u4f46\u5982\u679c\u966a\u4eba\u4e0a\u5e8a\u7684\u8bdd\uff0c\u5f00\u4ef7\u80af\u5b9a\u6bd4\u5bfb\u5e38\u5973\u5b50\u9ad8\u5f97\u591a\u3002\u4ee5\u6b27\u51ef\u7684\u8eab\u4efd\u800c\u8a00\uff0c\u6df1\u6e0a\u91cc\u7684\u9b45\u9b54\u518d\u597d\uff0c\u4e5f\u5df2\u7ecf\u5168\u65e0\u610f\u5473\uff0c\u7b97\u8d77\u6765\uff0c\u4e5f\u53ea\u80fd\u628a\u4e3b\u610f\u6253\u5728\u5973\u795e\u8eab\u4e0a\u4e86\u3002</p><p>\u4e0d\u8fc7\uff0c\u8fd9\u4e2d\u95f4\u7ec8\u7a76\u8fd8\u662f\u5ffd\u7565\u4e86\u4e9b\u4e1c\u897f\u3002\u5973\u6027\u4e4b\u6240\u4ee5\u503c\u5f97\u8ffd\u6c42\uff0c\u5e76\u4e0d\u4ec5\u4ec5\u5728\u4e8e\u7f8e\u8c8c\u5bb9\u989c\u548c\u5e8a\u4e0a\u6280\u672f\u3002\u53ea\u662f\u743c\u6069\u61d2\u4e8e\u591a\u8bf4\uff0c\u6b27\u51ef\u5927\u7ea6\u4e5f\u65e0\u6cd5\u7406\u89e3\u3002</p><p>\u201c\u7b49\u7b49\uff0c\u6211\u7a81\u7136\u60f3\u8d77\u6765\uff0c\u201d\u743c\u6069\u7591\u60d1\u7740\uff0c\u201c\u6211\u4eec\u4e0d\u662f\u5728\u8c08\u4f60\u4e3a\u4ec0\u4e48\u8981\u5173\u5fc3\u8fd9\u4e9b\u5353\u5c14\u7684\u95ee\u9898\u4e48\uff0c\u600e\u4e48\u626f\u5230\u5973\u4eba\u8eab\u4e0a\u53bb\u4e86\u2026\u2026\u201d</p><p>\u201c\u8fd9\u6709\u4ec0\u4e48\u4e0d\u6b63\u5e38\u7684\uff0c\u4f60\u662f\u7537\u6027\uff0c\u6211\u662f\u7537\u6027\uff0c\u4e24\u4e2a\u7537\u6027\u5728\u4e00\u8d77\uff0c\u4e0d\u8c08\u5973\u4eba\u8c08\u4ec0\u4e48\uff0c\u96be\u9053\u8c08\u8db3\u7403\u4e0d\u6210\u3002\u201d</p><p>\u201c\u8db3\u7403\uff1f\u201d\u743c\u6069\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e5f\u6709\u8db3\u7403\uff1f</p><p>\u201c\u662f\u554a\uff0c\u6cf0\u745f\u5c14\u548c\u5361\u4e3d\u73ca\u90a3\u8fb9\u5f88\u6d41\u884c\u7684\uff0c\u6211\u6070\u597d\u719f\u6089\u89c4\u5219\uff0c\u4f60\u6709\u6ca1\u6709\u5174\u8da3\u542c\u6211\u4ecb\u7ecd\u2026\u2026\u201d</p><p>\u201c\u522b\uff0c\u6211\u4eec\u8a00\u5f52\u6b63\u4f20\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5973\u4eba\u548c\u8db3\u7403\u7684\u95ee\u9898\u4ee5\u540e\u518d\u8c08\u3002\u8fd8\u662f\u90a3\u53e5\u8bdd\uff0c\u8fd9\u4e9b\u5353\u5c14\u7684\u6b7b\u6d3b\uff0c\u548c\u4f60\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d</p><p>\u201c\u6211\u5982\u679c\u8bf4\u6211\u51c6\u5907\u53bb\u4fe1\u4ef0\u7f57\u4e1d\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u4fe1\uff0c\u6076\u9b54\u662f\u4e0d\u4f1a\u53bb\u4fe1\u4ef0\u795e\u7947\u7684\u5427\u3002\u201d</p><p>\u201c\u4e0d\u662f\u4e0d\u4f1a\uff0c\u800c\u662f\u538b\u6839\u4e0d\u80fd\uff0c\u201d\u6b27\u51ef\u7ea0\u6b63\uff0c\u201c\u5c31\u7b97\u60f3\u5f53\u4e2a\u4fe1\u5f92\u90fd\u529e\u4e0d\u5230\uff0c\u8fd9\u662f\u672c\u8d28\u51b3\u5b9a\u7684\uff0c\u6240\u8c13\u610f\u5fd7\u548c\u8868\u8c61\u7684\u4e16\u754c\u2026\u2026\u201d</p><p>\u201c\u4f60\u4e0d\u4f1a\u662f\u53c8\u60f3\u626f\u5f00\u8bdd\u9898\u5427\uff1f\u201d</p><p>\u201c\u2026\u2026\u597d\u50cf\u88ab\u4f60\u770b\u7a7f\u4e86\u3002\u201d</p><p>\u4e1c\u626f\u897f\u62c9\u4e86\u4e00\u901a\uff0c\u6b27\u51ef\u6700\u7ec8\u8fd8\u662f\u6210\u529f\u56de\u907f\u4e86\u8fd9\u4e2a\u95ee\u9898\uff0c\u743c\u6069\u89c1\u4ed6\u575a\u51b3\u4e0d\u80af\u8bf4\uff0c\u4e5f\u5c31\u4e0d\u518d\u8ffd\u95ee\u3002\u53c8\u95f2\u8c08\u4e86\u51e0\u53e5\uff0c\u6b27\u51ef\u8d77\u8eab\u544a\u8f9e\uff0c\u201c\u6211\u8fd8\u5f97\u53bb\u4e00\u8d9f\u51ef\u6069\u6d77\u59c6\u57ce\uff0c\u201d\u4ed6\u53f9\u6c14\u7740\uff0c\u628a\u90a3\u526f\u8428\u74e6\u68cb\u6536\u8fdb\u6000\u4e2d\uff0c\u201c\u4e0b\u6b21\u518d\u89c1\uff0c\u6050\u6015\u8981\u8fc7\u5f88\u4e45\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u771f\u662f\u6211\u4eca\u5929\u542c\u5230\u7684\u6700\u597d\u6d88\u606f\u3002\u201d</p><p>\u6b27\u51ef\u54c8\u54c8\u5927\u7b11\uff0c\u6325\u6325\u624b\u544a\u8f9e\uff0c\u8f6c\u8eab\u51fa\u95e8\uff0c\u201c\u522b\u5fd8\u4e86\u6211\u7684\u8bdd\u3002\u201d</p><p>\u201c\u6211\u4f1a\u8003\u8651\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u201d</p><p>\u201c\u6211\u6b20\u4f60\u4e00\u4efd\u4eba\u60c5\u3002\u201d\u6b27\u51ef\u8bf4\uff0c\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u203b\u203b\u203b</p><p>\u63a5\u4e0b\u6765\u7684\u4e24\u5929\u6ce2\u6f9c\u4e0d\u60ca\uff0c\u518d\u6ca1\u4ec0\u4e48\u5927\u4e8b\u53d1\u751f\uff0c\u6574\u4e2a\u57ce\u5e02\u4eff\u4f5b\u8fdb\u5165\u4e86\u4fee\u6574\u671f\u3002\u4e0b\u57ce\u533a\u7684\u91cd\u5efa\u5de5\u4f5c\u5728\u9010\u6e10\u8fdb\u884c\uff0c\u4f46\u8fdb\u5c55\u7f13\u6162\uff0c\u56e0\u4e3a\u4e25\u91cd\u7f3a\u4e4f\u5de5\u5320\u3002\u4e3b\u6bcd\u4eec\u53ec\u5f00\u6267\u653f\u8bae\u4f1a\uff0c\u6309\u7167\u89c4\u77e9\u4f9d\u6b21\u9012\u589e\uff0c\u53c8\u8865\u5168\u4e86\u4e8c\u5341\u4e00\u4e2a\u5e2d\u4f4d\uff0c\u4e24\u4e2a\u65b0\u5bb6\u65cf\u642c\u8fdb\u4e86\u4e0a\u57ce\u533a\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u6709\u51e0\u4e2a\u5c0f\u5bb6\u65cf\u4e4b\u95f4\u7206\u53d1\u4e86\u6218\u4e89\uff0c\u5927\u7ea6\u662f\u60f3\u8d81\u4e71\u635e\u4e0a\u4e00\u7b14\u3002\u5176\u4e2d\u4e24\u4e2a\u5bb6\u65cf\u6210\u529f\u987a\u5229\u5f97\u624b\uff0c\u6392\u540d\u4e0a\u5347\uff0c\u5176\u4f59\u5bb6\u65cf\u6216\u8005\u6311\u8d77\u6218\u4e89\u5374\u53c8\u6ca1\u641e\u5b9a\u5bf9\u624b\uff0c\u88ab\u6267\u653f\u8bae\u4f1a\u201c\u516c\u6b63\u201d\u6389\u4e86\uff0c\u6216\u8005\u906d\u53d7\u653b\u51fb\u635f\u5931\u60e8\u91cd\uff0c\u8d81\u673a\u88ab\u5176\u4ed6\u5bb6\u65cf\u541e\u5e76\u3002</p><p>\u51fa\u4e8e\u597d\u5947\u4ee5\u53ca\u589e\u957f\u89c1\u8bc6\u7684\u60f3\u6cd5\uff0c\u743c\u6069\u5e26\u7740\u8299\u857e\u72c4\u89c2\u770b\u4e86\u4e00\u6b21\u6267\u884c\u201c\u516c\u6b63\u201d\u7684\u573a\u9762\uff0c\u4f46\u4e8b\u5b9e\u8bc1\u660e\u90a3\u5e76\u4e0d\u7f8e\u5999\u3002\u6216\u8bb8\u662f\u4e3a\u4e86\u5a01\u6151\uff0c\u6216\u8bb8\u662f\u56e0\u4e3a\u957f\u4e45\u4ee5\u6765\u538b\u6291\u7684\u7d27\u5f20\u611f\u53ef\u4ee5\u540d\u6b63\u8a00\u987a\u5730\u53d1\u6cc4\uff0c\u603b\u4e4b\u88ab\u201c\u516c\u6b63\u201d\u5bb6\u65cf\u7684\u6574\u4e2a\u5b85\u9662\u53d8\u6210\u4e86\u4e00\u7247\u4fee\u7f57\u6740\u573a\uff0c\u6240\u6709\u5931\u8d25\u8005\u7684\u5c38\u4f53\u90fd\u88ab\u7279\u610f\u780d\u6210\u4e86\u788e\u5757\uff0c\u7136\u540e\u7528\u86db\u4e1d\u6346\u7ed1\u8d77\u6765\uff0c\u540a\u5728\u949f\u4e73\u77f3\u6216\u8005\u77f3\u7b0b\u5c16\u7aef\u7b49\u5f85\u98ce\u5e72\uff0c\u6216\u8005\u6210\u4e3a\u8718\u86db\u7684\u7f8e\u9910\uff0c\u8840\u6db2\u5728\u5730\u9762\u4e0a\u6c47\u805a\u6210\u6cb3\uff0c\u6c69\u6c69\u6d41\u6dcc\uff0c\u5c31\u7b97\u662f\u524d\u5929\u83f2\u5c14\u4f26\u548c\u745e\u8d39\u5fb7\u5bb6\u65cf\u4e4b\u6218\u4e5f\u6ca1\u6709\u8fd9\u6837\u8840\u8165\u3002\u8299\u857e\u72c4\u8138\u8272\u82cd\u767d\uff0c\u51e0\u4e4e\u8981\u5f53\u573a\u5455\u5410\u51fa\u6765\uff0c\u743c\u6069\u53ea\u597d\u5e26\u7740\u5979\u63d0\u524d\u9000\u573a\u3002\u4ed6\u81ea\u5df1\u611f\u89c9\u4e5f\u5f88\u7cdf\u7cd5\uff0c\u4f46\u770b\u770b\u5468\u56f4\u7684\u5353\u5c14\u4eec\u90fd\u4e00\u8138\u6cf0\u7136\u81ea\u82e5\uff0c\u4e5f\u5c31\u4e0d\u597d\u8868\u73b0\u51fa\u4ec0\u4e48\u4e86\u3002</p><p>\u6309\u7167\u89c4\u77e9\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u5728\u6218\u80dc\u7684\u7b2c\u4e8c\u5929\u4e3e\u884c\u4eea\u5f0f\uff0c\u5c06\u745e\u8d39\u5fb7\u5bb6\u65cf\u7684\u59ae\u745f\u4e3b\u6bcd\u548c\u5979\u7684\u4e24\u4e2a\u59d0\u59b9\u6740\u6b7b\uff0c\u732e\u7ed9\u795e\u540e\uff0c\u4ee5\u611f\u8c22\u548c\u7948\u6c42\u5979\u7684\u5e87\u4f51\u3002\u5f53\u8718\u86db\u5315\u9996\u7684\u516b\u53ea\u5229\u722a\u5c06\u796d\u54c1\u7684\u5fc3\u810f\u4ece\u8eab\u4f53\u91cc\u94a9\u51fa\u65f6\uff0c\u6240\u6709\u83f2\u5c14\u4f26\u5bb6\u65cf\u7684\u9ed1\u6697\u7cbe\u7075\u4eec\u90fd\u5174\u594b\u8d77\u6765\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u5f88\u4e45\u6ca1\u6709\u770b\u8fc7\u8fd9\u79cd\u573a\u9762\u4e86\u3002</p><p>\u201c\u6211\u4eec\u7a33\u8e1e\u7b2c\u4e00\u5bb6\u65cf\u7684\u5b9d\u5ea7\u4e0a\u4e07\u5e74\uff0c\u6781\u5c11\u6709\u6311\u6218\u8005\uff0c\u800c\u6211\u4eec\u4e5f\u6781\u5c11\u4f1a\u5bf9\u4e0b\u9762\u7684\u5bb6\u65cf\u52a8\u624b\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8f7b\u58f0\u89e3\u91ca\uff0c\u201c\u8fd9\u79cd\u4eea\u5f0f\u4e5f\u5c31\u6781\u5c11\u4e3e\u884c\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u81ea\u6211\u51fa\u751f\u8d77\uff0c\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\u3002\u201d</p><p>\u745e\u8d39\u5fb7\u5bb6\u65cf\u88ab\u4fd8\u7684\u7267\u5e08\u4e0d\u6b62\u88ab\u732e\u796d\u7684\u4e09\u540d\uff0c\u5176\u4f59\u591a\u662f\u65c1\u7cfb\uff0c\u5979\u4eec\u88ab\u5173\u8fdb\u4e86\u5730\u7262\uff0c\u6682\u65f6\u8fd8\u6ca1\u6709\u51b3\u5b9a\u5982\u4f55\u5904\u7f6e\u3002\u6309\u7167\u60ef\u4f8b\uff0c\u6216\u8005\u6539\u540d\u6362\u59d3\uff0c\u653e\u5f03\u539f\u672c\u8eab\u4efd\uff0c\u88ab\u83f2\u5c14\u4f26\u5bb6\u65cf\u6536\u517b\uff08\u4e0d\u8fc7\u8fd9\u79cd\u60c5\u5f62\u6781\u5c11\u53d1\u751f\uff09\uff0c\u6216\u8005\u7528\u5728\u4e0b\u4e00\u6b21\u796d\u7940\u4e0a\u3002\u7f57\u4e1d\u5973\u796d\u53f8\uff0c\u8fd9\u53ef\u662f\u6700\u73cd\u8d35\u7684\u796d\u54c1\u3002</p><p>\u86db\u540e\u5bf9\u796d\u54c1\u7684\u53e3\u5473\u5f88\u72ec\u7279\uff0c\u667a\u6167\u751f\u7269\u6bd4\u611a\u6627\u751f\u7269\u66f4\u597d\uff0c\u7c7b\u4eba\u751f\u7269\u6bd4\u975e\u4eba\u5f62\u751f\u7269\u66f4\u597d\uff0c\u7cbe\u7075\u5219\u6bd4\u5176\u4ed6\u7684\u7c7b\u4eba\u751f\u7269\u8981\u597d\uff0c\u5353\u5c14\u5219\u53c8\u6bd4\u7cbe\u7075\u66f4\u597d\u2014\u2014\u800c\u5979\u81ea\u5df1\u7684\u5973\u796d\u53f8\uff0c\u5219\u53c8\u6bd4\u5176\u4ed6\u5353\u5c14\u66f4\u597d\uff0c\u662f\u6700\u80fd\u8ba8\u5979\u6b22\u5fc3\u7684\u796d\u54c1\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u79cd\u4e8b\u60c5\u4e0d\u53ef\u80fd\u9891\u7e41\u53d1\u751f\uff0c\u5426\u5219\u6574\u4e2a\u6559\u4f1a\u90fd\u8981\u4e92\u76f8\u6740\u622e\uff0c\u5206\u5d29\u79bb\u6790\u4e86\u3002\u4e00\u822c\u6765\u8bf4\u80fd\u591f\u7528\u7f57\u4e1d\u5973\u796d\u53f8\u5f53\u796d\u54c1\u7684\u573a\u5408\uff0c\u4e5f\u5c31\u662f\u5728\u5bb6\u65cf\u5185\u6218\u7ed3\u675f\u540e\u3002</p><p>\u7b2c\u4e09\u5929\u65e9\u4e0a\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u4e00\u65e9\u6765\u6572\u95e8\uff0c\u9080\u8bf7\u743c\u6069\u53bb\u53c2\u52a0\u796d\u53f8\u5b66\u9662\u7684\u6bd5\u4e1a\u5178\u793c\u3002\u8299\u857e\u72c4\u539f\u672c\u4e5f\u6253\u7b97\u968f\u884c\uff0c\u4f46\u88ab\u963b\u6b62\u4e86\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u53ea\u5728\u5979\u8033\u8fb9\u6084\u6084\u8bf4\u4e86\u53e5\u8bdd\uff0c\u5c31\u8ba9\u5979\u4e56\u4e56\u6253\u6d88\u4e86\u5ff5\u5934\u3002</p><p>\u201c\u4f60\u5bf9\u5979\u8bf4\u4ec0\u4e48\u4e86\uff1f\u201d\u743c\u6069\u597d\u5947\u5730\u95ee\u3002</p><p>\u201c\u4f60\u56de\u53bb\u81ea\u5df1\u95ee\u5979\u597d\u4e86\u3002\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u683c\u683c\u5a07\u7b11\u3002</p><p>\u5353\u5c14\u5c11\u5973\u4eca\u5929\u7684\u88c5\u675f\u6709\u4e9b\u7279\u522b\uff0c\u5979\u5e73\u65f6\u603b\u662f\u7a7f\u5deb\u5e08\u888d\uff0c\u5916\u7f69\u9b54\u6597\u7bf7\uff0c\u8fd9\u6b21\u5374\u7a7f\u7740\u9576\u7740\u9ed1\u8fb9\u7684\u6df1\u7d2b\u8272\u957f\u888d\uff0c\u4e0a\u9762\u7ee3\u7740\u5404\u79cd\u86db\u7f51\u56fe\u6848\uff0c\u8fd9\u660e\u663e\u662f\u796d\u53f8\u670d\u3002\u83f2\u5c14\u4f26\u5bb6\u5fbd\u4f69\u6234\u5728\u957f\u888d\u5de6\u4fa7\u80f8\u53e3\u90e8\u4f4d\uff0c\u53f3\u4fa7\u5219\u4f69\u6234\u7740\u4e00\u679a\u76f4\u5f84\u8d85\u8fc7\u4e09\u5bf8\u7684\u7cbe\u91d1\u8718\u86db\u5723\u5fbd\u2014\u2014\u6bd4\u7267\u5e08\u4eec\u5e73\u5e38\u4f7f\u7528\u7684\u5723\u5fbd\u5927\u4e86\u4e24\u500d\u4e0d\u6b62\uff0c\u770b\u8d77\u6765\u66f4\u50cf\u662f\u88c5\u9970\u7528\u54c1\u3002\u5979\u7684\u5934\u4e0a\u6234\u7740\u4e00\u9876\u9576\u6ee1\u73e0\u5b9d\u7684\u534e\u8d35\u5934\u76d4\uff0c\u4e0a\u9762\u96d5\u523b\u4e86\u5f88\u591a\u626d\u66f2\u7684\u8718\u86db\uff0c\u8fd9\u4e5f\u662f\u6781\u5176\u5c11\u89c1\u7684\u3002</p><p>\u7cbe\u7f8e\u7684\u796d\u53f8\u888d\u4e0b\u660e\u663e\u771f\u7a7a\uff0c\u57fa\u672c\u662f\u4ec0\u4e48\u90fd\u6ca1\u7a7f\uff0c\u4e00\u8d70\u8d77\u8def\u6765\uff0c\u4e1d\u7ec7\u5e03\u6599\u5c31\u7d27\u8d34\u7740\u8eab\u4f53\uff0c\u66b4\u9732\u51fa\u73b2\u73d1\u6d6e\u51f8\u7684\u8bf1\u4eba\u66f2\u7ebf\uff0c\u751a\u81f3\u8fde\u80f8\u53e3\u7684\u4e24\u4e2a\u5c0f\u5c0f\u7684\u786c\u786c\u51f8\u8d77\u90fd\u80fd\u770b\u5f97\u6e05\u695a\u3002\u743c\u6069\u6ce8\u610f\u5230\u5979\u7684\u8116\u5b50\u4e0a\u6302\u7740\u4e00\u4e32\u6f02\u4eae\u7684\u9879\u94fe\uff0c\u7531\u9ed1\u73cd\u73e0\u3001\u9ed1\u84dd\u77f3\u3001\u82cd\u7eff\u77f3\u548c\u6696\u8272\u6676\u5404\u4e24\u679a\u7ec4\u6210\uff0c\u8fd9\u662f\u4e2a\u65b0\u9970\u54c1\uff0c\u4ee5\u524d\u4ece\u6765\u6ca1\u89c1\u5979\u6234\u8fc7\u3002</p><p>\u201c\u8fd9\u9879\u94fe\u2026\u2026\u6211\u597d\u50cf\u5728\u54ea\u91cc\u89c1\u8fc7\uff1f\u201d</p><p>\u201c\u81ea\u7136\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u8fe6\u6cd5\u548c\u67e5\u4e3d\u4e1d\u745e\u4e0d\u90fd\u6234\u7740\u4e48\u3002\u201d</p><p>\u743c\u6069\u60f3\u4e86\u8d77\u6765\uff0c\u8fd9\u662f\u796d\u53f8\u5b66\u9662\u6559\u5b98\u7684\u6807\u5fd7\uff0c\u548c\u5deb\u5e08\u5b66\u9662\u5bfc\u5e08\u4f69\u6234\u7684\u94f6\u7070\u8272\u91d1\u5c5e\u62a4\u8155\u3001\u683c\u6597\u5b66\u9662\u6559\u5b98\u6597\u7bf7\u80a9\u90e8\u7684\u5251\u8718\u86db\u56fe\u6848\u4e00\u6837\uff0c\u662f\u8eab\u4efd\u7684\u8c61\u5f81\u3002\u201c\u8fd9\u4e48\u8bf4\u4f60\u6210\u4e3a\u6559\u5b98\u4e86\uff1f\u201d\u4ed6\u7565\u6709\u4e9b\u60ca\u8bb6\uff0c\u5982\u679c\u6ca1\u8bb0\u9519\u7684\u8bdd\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u5e94\u8be5\u624d\u521a\u521a\u6bd5\u4e1a\u6ca1\u51e0\u5e74\u5427\uff0c\u8d44\u5386\u672a\u514d\u592a\u6d45\u3002</p><p>\u201c\u6267\u653f\u8bae\u4f1a\u5728\u4e00\u4e2a\u65f6\u8fb0\u524d\u505a\u51fa\u7684\u51b3\u8bae\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u9887\u6709\u4e9b\u5f97\u610f\uff0c\u201c\u4e0d\u8fc7\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u4e2a\u66f4\u597d\u7684\u6d88\u606f\u5462\u3002\u201d</p><p>\u201c\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8fe6\u6cd5\u88ab\u514d\u804c\u4e86\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u8bf4\uff0c\u201c\u7f6a\u540d\u662f\u5979\u4f5c\u4e3a\u5b66\u9662\u7684\u8d1f\u8d23\u4eba\uff0c\u4fdd\u62a4\u4e0d\u529b\uff0c\u4ee5\u81f3\u4e8e\u5723\u7269\u88ab\u6bc1\u635f\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u8fd9\u662f\u5728\u610f\u6599\u4e4b\u4e2d\u7684\u4e8b\u60c5\u3002\u6df1\u6e0a\u53ec\u5524\u86db\u88ab\u7075\u5438\u602a\u5deb\u5996\u65af\u5170\u666e\u70b8\u6bc1\uff0c\u867d\u7136\u5927\u90e8\u4efd\u7f6a\u8d23\u63a8\u5230\u4e86\u745e\u8d39\u5fb7\u5bb6\u65cf\u5934\u4e0a\uff0c\u4f46\u8fe6\u6cd5\u7ec8\u7a76\u4e0d\u53ef\u80fd\u5b8c\u5168\u4e0d\u53d7\u7275\u8fde\uff0c\u4ec5\u4ec5\u53ea\u662f\u514d\u804c\uff0c\u5df2\u7ecf\u53ef\u4ee5\u559c\u51fa\u671b\u5916\u4e86\u3002</p><p>\u201c\u53ef\u60dc\u53ea\u662f\u514d\u53bb\u6b21\u5e2d\u6559\u5b98\u7684\u804c\u4f4d\uff0c\u5979\u4f9d\u65e7\u8fd8\u662f\u5b66\u9662\u7684\u6559\u5b98\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u4e0d\u751a\u6ee1\u610f\u5730\u8bf4\uff0c\u201c\u800c\u4e14\u4f4d\u9636\u8fd8\u5728\u6211\u4e4b\u4e0a\u3002\u201d</p><p>\u201c\u90a3\u662f\u7406\u6240\u5f53\u7136\u7684\u5427\uff0c\u5979\u597d\u6b79\u8d44\u5386\u6df1\u539a\uff0c\u4f60\u624d\u662f\u4e2a\u65b0\u624b\u3002\u201d\u743c\u6069\u5fc3\u4e2d\u5600\u5495\u7740\uff0c\u4f46\u6ca1\u6709\u8bf4\u51fa\u53e3\u3002</p><p>\u201c\u90a3\u73b0\u5728\u7684\u6b21\u5e2d\u6559\u5b98\u662f\u8c01\uff1f\u201d</p><p>\u201c\u62c9\u6587\xb7\u535a\u514b\u672c\u3002\u201d</p><p>\u201c\u7b2c\u4e8c\u5bb6\u65cf\u7684\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u6709\u65b0\u4efb\u6559\u5b98\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u5e26\u9886\uff0c\u743c\u6069\u4e00\u8def\u7545\u901a\u65e0\u963b\u3002\u4e24\u4eba\u987a\u7740\u9636\u68af\u8fdb\u5165\u5b66\u9662\u7684\u4e3b\u5efa\u7b51\uff0c\u4e00\u8def\u8d70\u8fdb\u4e2d\u592e\u793c\u5802\u3002\u4e00\u4e2a\u96d5\u5851\u6210\u8718\u86db\u6a21\u6837\u7684\u706b\u7089\u6b63\u6446\u653e\u5728\u6b63\u4e2d\uff0c\u9b54\u6cd5\u706b\u7130\u817e\u817e\u71c3\u70e7\uff0c\u5728\u793c\u5802\u5468\u56f4\u73af\u7ed5\u653e\u7f6e\u7740\u516b\u4e2a\u7a0d\u5c0f\u4e00\u4e9b\u7684\u706b\u7089\uff0c\u540c\u6837\u662f\u8718\u86db\u5f62\u72b6\u3002\u6bcf\u4e2a\u706b\u7089\u7684\u5468\u56f4\u90fd\u6709\u516d\u540d\u4f4d\u9636\u8f83\u9ad8\u7684\u5973\u796d\u53f8\u76d8\u819d\u800c\u5750\uff0c\u624b\u6309\u5723\u5fbd\u4f4e\u58f0\u7948\u7977\uff0c\u5176\u4f59\u7267\u5e08\u5219\u8d34\u7740\u5899\u58c1\u7ad9\u7acb\u3002\u5176\u4e2d\u6709\u4e00\u4f4d\u8eab\u6750\u9ad8\u5927\u58ee\u5b9e\u7684\u5973\u796d\u53f8\uff0c\u8138\u4e0a\u6709\u4e00\u9053\u6d45\u6d45\u7684\u4f24\u75a4\uff0c\u5f02\u5e38\u534e\u4e3d\u7684\u670d\u9970\u548c\u8170\u95f4\u7684\u4e03\u9996\u86c7\u97ad\u6e05\u695a\u5730\u6307\u51fa\u4e00\u4e2a\u4e8b\u5b9e\uff1a\u5979\u662f\u6b64\u5730\u8eab\u4efd\u6700\u9ad8\u7684\u7267\u5e08\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u4f4e\u58f0\u544a\u8bc9\u743c\u6069\uff0c\u5979\u6b63\u662f\u65b0\u4efb\u7684\u6b21\u5e2d\u6559\u5b98\uff0c\u7b2c\u4e8c\u5bb6\u65cf\u7684\u957f\u5973\u62c9\u6587\xb7\u535a\u514b\u672c\u3002</p><p>\u5b66\u751f\u4eec\u4ece\u5404\u4e2a\u623f\u95f4\u91cc\u8d70\u8fdb\u6765\uff0c\u5979\u4eec\u6570\u91cf\u6ca1\u6709\u743c\u6069\u9884\u60f3\u5f97\u591a\uff0c\u8fd9\u662f\u56e0\u4e3a\u524d\u51e0\u5929\u53db\u4e71\u7684\u7f18\u6545\u3002\u6240\u6709\u7684\u796d\u53f8\u90fd\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u4e00\u6837\uff0c\u6234\u7740\u534e\u4e3d\u5934\u76d4\uff0c\u7a7f\u7740\u796d\u53f8\u888d\uff0c\u4f69\u6234\u9879\u94fe\uff0c\u800c\u5b66\u751f\u5219\u66b4\u9732\u5f97\u591a\uff0c\u5979\u4eec\u53ea\u62ab\u4e00\u4ef6\u6597\u7bf7\uff0c\u52c9\u5f3a\u80fd\u591f\u906e\u63a9\u8d64\u88f8\u7684\u8eab\u4f53\u3002</p><p>\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e00\u4e9b\u5e74\u8f7b\u7684\u7537\u6027\u4e5f\u8d70\u8fdb\u793c\u5802\uff0c\u4ed6\u4eec\u660e\u663e\u4e0d\u662f\u796d\u7940\u5b66\u9662\u7684\u5b66\u751f\u3002\u743c\u6069\u89c2\u5bdf\u7740\uff0c\u5224\u65ad\u7edd\u5927\u90e8\u4efd\u662f\u64c5\u957f\u683c\u6597\u7684\u6b66\u8005\uff0c\u4ee5\u53ca\u5be5\u5be5\u65e0\u51e0\u7684\u5deb\u5e08\u5b66\u5f92\u3002</p><p>\u201c\u4ed6\u4eec\u662f\u5e72\u561b\u7684\uff1f\u201d\u4ed6\u4f4e\u58f0\u95ee\u3002</p><p>\u201c\u53e6\u5916\u4e24\u5ea7\u5b66\u9662\u7684\u5b66\u751f\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u56de\u7b54\uff0c\u201c\u8fd9\u4e5f\u662f\u4ed6\u4eec\u7684\u6bd5\u4e1a\u5178\u793c\u3002\u201d</p><p>\u743c\u6069\u8fd8\u8981\u518d\u95ee\uff0c\u4f46\u4eea\u5f0f\u5df2\u7ecf\u5f00\u59cb\u4e86\u3002\u5973\u796d\u53f8\u4eec\u5927\u58f0\u5ff5\u8bf5\u7740\u8d5e\u7f8e\u86db\u540e\u7684\u7977\u6587\uff0c\u6b21\u5e2d\u6559\u5b98\u62c9\u6587\xb7\u535a\u514b\u672c\u8d70\u5230\u4e2d\u592e\u706b\u7089\u7684\u65c1\u8fb9\uff0c\u5de6\u624b\u6309\u4f4f\u80f8\u53e3\u7684\u5de8\u5927\u5723\u5fbd\uff0c\u53f3\u624b\u865a\u865a\u62ac\u8d77\uff0c\u5728\u706b\u7130\u4e0a\u7a7a\u79fb\u52a8\u7740\uff0c\u7d27\u63a5\u7740\u5ff5\u51fa\u4e00\u53e5\u5492\u8bed\u3002</p><p>\u5239\u90a3\u95f4\uff0c\u793c\u5802\u91cc\u6240\u6709\u7684\u58f0\u97f3\u90fd\u6d88\u5931\u4e86\uff0c\u53ea\u6709\u706b\u7130\u567c\u556a\u4f5c\u54cd\u3002\u4e00\u4e2a\u5e74\u8f7b\u7684\u5973\u6027\u5353\u5c14\u4ece\u4eba\u7fa4\u4e2d\u8d70\u51fa\u6765\uff0c\u6765\u5230\u4e2d\u592e\u706b\u7089\u65c1\u8fb9\uff0c\u4f38\u624b\u626f\u4e0b\u6597\u7bf7\u7684\u7cfb\u5e26\uff0c\u8ba9\u5b83\u4ece\u80a9\u4e0a\u6ed1\u843d\uff0c\u9732\u51fa\u8d64\u88f8\u7684\u8eab\u4f53\uff0c\u9762\u5bf9\u7740\u718a\u718a\u70c8\u7130\u3002</p></article>"}}}]);
