(window.webpackJsonp=window.webpackJsonp||[]).push([[1485],{1508:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u516b\u8282 \u5c18\u57c3\u843d\u5b9a",content:"<article><h2>\u7b2c\u56db\u5341\u516b\u8282 \u5c18\u57c3\u843d\u5b9a</h2><p>\u8299\u857e\u72c4\u7684\u60f3\u6cd5\uff0c\u5176\u5b9e\u4e00\u5f00\u59cb\u743c\u6069\u4e5f\u8003\u8651\u8fc7\u3002\u5353\u5c14\u7684\u6cd5\u5219\u8840\u8165\u6b8b\u9177\uff0c\u793e\u4f1a\u7b49\u7ea7\u68ee\u4e25\uff0c\u52a8\u8f84\u6740\u4eba\u653e\u706b\uff0c\u6284\u5bb6\u706d\u53e3\uff0c\u663e\u7136\u4e0d\u662f\u4e2a\u9002\u5408\u5c45\u4f4f\u7684\u597d\u5730\u65b9\u3002\u5bf9\u4e8e\u90a3\u4e9b\u65e2\u5f97\u5229\u76ca\u8005\uff0c\u4e5f\u5c31\u662f\u5973\u796d\u53f8\u4eec\uff0c\u6216\u8bb8\u5012\u8fd8\u7f62\u4e86\uff0c\u4f46\u90a3\u4e9b\u5730\u4f4d\u8f83\u4f4e\u7684\u6218\u58eb\u6216\u8005\u5e73\u6c11\uff0c\u53c8\u4e3a\u4f55\u975e\u8981\u505c\u7559\u4e0d\u53bb\u5462\uff1f</p><p>\u8f6c\u5ff5\u518d\u60f3\uff0c\u4e5f\u5c31\u660e\u767d\uff0c\u5bf9\u4e8e\u5353\u5c14\u6765\u8bf4\uff0c\u6545\u571f\u662f\u672a\u5fc5\u4f73\uff0c\u4f46\u9664\u6b64\u4e4b\u5916\u53c8\u5f80\u4f55\u5904\uff1f\u5e7d\u6697\u5730\u57df\u91cc\u5371\u9669\u91cd\u91cd\uff0c\u8352\u91ce\u4e2d\u4e0d\u77e5\u6f5c\u85cf\u7740\u591a\u5c11\u5371\u9669\uff0c\u60f3\u8981\u79bb\u7fa4\u7d22\u5c45\uff0c\u5f53\u4e2a\u9690\u58eb\uff0c\u90a3\u662f\u538b\u6839\u884c\u4e0d\u901a\u7684\u3002\u53bb\u522b\u7684\u5353\u5c14\u57ce\u5e02\uff1f\u90a3\u8fd8\u4e0d\u5982\u7559\u5728\u539f\u5730\uff0c\u81f3\u5c11\u8fd8\u6709\u6839\u57fa\u4f9d\u9760\u3002\u53bb\u5176\u4ed6\u79cd\u65cf\u7684\u805a\u5c45\u5730\uff1f\u90a3\u7b49\u4e8e\u5c31\u662f\u9001\u6b7b\uff0c\u65e0\u8bba\u6bd4\u8f83\u5584\u826f\u4e9b\u7684\u77ee\u4eba\u4f8f\u5112\u4e5f\u597d\uff0c\u90aa\u6076\u7684\u7075\u5438\u602a\u773c\u9b54\u4e4b\u6d41\u4e5f\u7f62\uff0c\u53cd\u6b63\u90fd\u4e0d\u53ef\u80fd\u5bb9\u5f97\u4e0b\u5353\u5c14\u3002\u81f3\u4e8e\u5730\u7cbe\u72d7\u5934\u4eba\u90a3\u4e9b\uff0c\u5c31\u66f4\u662f\u4e0d\u7528\u8c08\u4e86\uff0c\u76f4\u63a5\u4e71\u5200\u5206\u5c38\u5c31\u662f\u552f\u4e00\u4e0b\u573a\u3002</p><p>\u4e16\u754c\u4e0a\u7684\u5353\u5c14\u57ce\u5e02\uff0c\u57fa\u672c\u90fd\u5927\u540c\u5c0f\u5f02\uff0c\u552f\u4e00\u7684\u4f8b\u5916\u662f\u6c99\u739b\u65af\uff0c\u4f46\u6b63\u56e0\u4e3a\u5b83\u592a\u72ec\u7279\uff0c\u6240\u4ee5\u88ab\u96c6\u4f53\u6b67\u89c6\u52a0\u5b64\u7acb\u2014\u2014\u53cd\u8fc7\u6765\u5b83\u4e5f\u6392\u5916\u3002\u5bf9\u4e8e\u6765\u81ea\u5176\u4ed6\u5353\u5c14\u57ce\u5e02\u7684\u540c\u7c7b\uff0c\u6c99\u739b\u65af\u7684\u5deb\u5e08\u4eec\u662f\u975e\u5e38\u654c\u89c6\u7684\uff0c\u9ed8\u8ba4\u5c31\u662f\u95f4\u8c0d\u6216\u8005\u523a\u5ba2\uff08\u8fd9\u5728\u5f88\u5927\u7a0b\u5ea6\u4e0a\u4e5f\u662f\u4e8b\u5b9e\uff09\uff0c\u5f80\u5f80\u52a0\u4ee5\u4e25\u5bc6\u76d1\u63a7\u3002</p><p>\u81f3\u4e8e\u8bf4\u53bb\u5730\u8868\uff0c\u90a3\u5c31\u66f4\u662f\u7eaf\u7cb9\u641e\u7b11\u4e86\u3002\u53bb\u5927\u8857\u4e0a\u6293\u4e00\u5806\u5353\u5c14\u6765\u505a\u6c11\u610f\u8c03\u67e5\uff0c\u95ee\u4ed6\u4eec\u80af\u4e0d\u80af\u53bb\u5730\u8868\uff0c\u4fdd\u8bc1\u7b54\u6848\u662f\u767e\u5206\u4e4b\u767e\u7684\u6447\u5934\u3002\u5728\u5730\u5e95\u751f\u6d3b\u7e41\u884d\u4e86\u5343\u4e07\u5e74\u7684\u79cd\u65cf\uff0c\u8981\u4ed6\u4eec\u9aa4\u7136\u524d\u5f80\u5730\u8868\uff0c\u8fd9\u548c\u8981\u9c7c\u79bb\u5f00\u6c34\u53bb\u9646\u5730\u751f\u6d3b\u6ca1\u591a\u5c11\u533a\u522b\uff0c\u60f3\u60f3\u5c31\u4f1a\u754f\u60e7\u9000\u7f29\uff0c\u751a\u81f3\u538b\u6839\u4e0d\u6562\u8d77\u8fd9\u79cd\u5ff5\u5934\u3002\u867d\u8bf4\u743c\u6069\u542c\u8fc7\u4e00\u4e9b\u4f20\u5947\u6545\u4e8b\uff0c\u8bf4\u662f\u6709\u4e2a\u4f7f\u53cc\u5200\u7684\u5353\u5c14\u6e38\u4fa0\uff0c\u5728\u5730\u8868\u4e16\u754c\u62db\u6447\u8fc7\u5e02\uff0c\u4e1c\u6e38\u897f\u8361\uff0c\u5c45\u7136\u8fd8\u9887\u53d7\u6b22\u8fce\u2014\u2014\u4f46\u743c\u6069\u76f4\u63a5\u65ad\u5b9a\u8fd9\u662f\u80e1\u626f\uff0c\u7b80\u76f4\u662f\u6bd4\u4e0a\u8f88\u5b50\u770b\u8fc7\u7684\u5947\u5e7b\u5c0f\u8bf4\u8fd8\u8981YY\uff0c\u538b\u6839\u5c31\u4e0d\u76f8\u4fe1\u3002</p><p>\u201c\u5353\u5c14\u4e0d\u4f1a\u79bb\u5f00\u57ce\u5e02\uff0c\u201d\u7ef4\u5eb7\u5c3c\u4e9a\u66fe\u7ecf\u5982\u662f\u8bf4\uff0c\u201c\u9664\u975e\u8d1f\u6709\u4f7f\u547d\uff0c\u6216\u8005\u88ab\u6d41\u653e\u3002\u201d</p><p>\u800c\u5982\u679c\u7559\u5728\u81ea\u5df1\u7684\u57ce\u5e02\u91cc\uff0c\u90a3\u4e48\u9664\u4e86\u52aa\u529b\u722c\u5230\u6743\u4f4d\u7684\u9876\u7aef\uff0c\u8fd8\u80fd\u6709\u4ec0\u4e48\u9009\u62e9\uff1f\u96be\u4e0d\u6210\u8fd8\u81ea\u613f\u9000\u8ba9\uff0c\u4efb\u4eba\u5bb0\u5272\u4e48\u3002</p><p>\u8fd9\uff0c\u4e5f\u5c31\u662f\u7ef4\u5eb7\u5c3c\u4e9a\u73b0\u5728\u7684\u5904\u5883\u4e86\uff0c\u9000\u65e0\u53ef\u9000\uff0c\u524d\u8fdb\u5374\u8fd8\u6709\u673a\u4f1a\uff0c\u81ea\u7136\u8981\u640f\u4e0a\u4e00\u640f\u3002</p><p>\u5f53\u7136\uff0c\u5982\u679c\u743c\u6069\u771f\u5fc3\u613f\u610f\u76f8\u52a9\u7684\u8bdd\uff0c\u90a3\u7ef4\u5eb7\u5c3c\u4e9a\u6709\u5f88\u5927\u53ef\u80fd\u5f53\u4e0a\u4e3b\u6bcd\uff0c\u4e4b\u540e\u4e5f\u672a\u5fc5\u4e0d\u80fd\u548c\u4e9a\u5f53\u65af\u4e89\u6301\u3002\u5c31\u7b97\u4e8b\u8d25\u9003\u79bb\u7684\u8bdd\uff0c\u53bb\u4f0a\u5361\u6c99\u57ce\uff0c\u53bb\u9634\u9b42\u57ce\uff0c\u4ee5\u4ed6\u7684\u80fd\u529b\uff0c\u5e87\u62a4\u4e00\u4e2a\u5353\u5c14\u6d41\u4ea1\u8005\u603b\u8fd8\u4e0d\u81f3\u4e8e\u529e\u4e0d\u5230\u3002\u95ee\u9898\u662f\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u505a\uff1f</p><p>\u5982\u4eca\u7684\u5c40\u9762\uff0c\u56e0\u4e3a\u6709\u4e86\u7ef4\u5eb7\u5c3c\u4e9a\uff0c\u6b63\u53ef\u4ee5\u501f\u6b64\u63a8\u52a8\u5f15\u53d1\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u8fdb\u4e00\u6b65\u52a8\u4e71\u3002\u5e7c\u5973\u7ee7\u4f4d\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u5185\u90e8\u5c11\u4e0d\u4e86\u4e00\u573a\u706b\u62fc\uff0c\u7b2c\u4e8c\u5bb6\u65cf\u864e\u89c6\u7708\u7708\uff0c\u5927\u6218\u66f4\u662f\u5728\u6240\u96be\u514d\u3002\u8fd9\u4e00\u5207\u90fd\u662f\u743c\u6069\u6240\u4e50\u4e8e\u89c1\u5230\u7684\u4e8b\u60c5\u3002\u5018\u82e5\u7ef4\u5eb7\u5c3c\u4e9a\u5f53\u771f\u4e0d\u60f3\u5f53\u4ec0\u4e48\u4e3b\u6bcd\u4e86\uff0c\u4e00\u8d70\u4e86\u4e4b\uff0c\u90a3\u4e8b\u60c5\u53cd\u800c\u53d8\u5f97\u66f4\u590d\u6742\uff0c\u5c40\u9762\u66f4\u96be\u628a\u63e1\u3002\u5982\u4eca\u7f57\u4e1d\u5373\u5c06\u82cf\u9192\uff0c\u8ba1\u5212\u5df2\u7ecf\u8fdb\u884c\u5230\u4e86\u6700\u540e\u5173\u952e\u65f6\u523b\uff0c\u81ea\u7136\u4ee5\u4e0d\u51fa\u4ec0\u4e48\u7a81\u7136\u53d8\u6545\u4e3a\u597d\uff0c\u514d\u5f97\u6253\u4e71\u6574\u4e2a\u6b65\u9aa4\u3002\u53cd\u6b63\u7ef4\u5eb7\u5c3c\u4e9a\u53c8\u4e0d\u662f\u4ed6\u7684\u4ec0\u4e48\u4eb2\u670b\u6545\u53cb\uff0c\u727a\u7272\u4e86\u5c31\u727a\u7272\u4e86\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u5927\u4e0d\u4e86\u3002</p><p>\u201c\u5e78\u597d\u81ea\u5df1\u4e0d\u662f\u5353\u5c14\u3002\u201d\u6709\u65f6\u5019\uff0c\u743c\u6069\u5fc3\u5e95\u90fd\u4f1a\u7a81\u7136\u6cdb\u8d77\u8fd9\u6837\u7684\u5ff5\u5934\uff0c\u5f53\u7136\u4e5f\u53ea\u662f\u4e00\u95ea\u5373\u901d\u3002</p><p>\u7f57\u4e1d\u5feb\u8981\u56de\u6765\u4e86\uff0c\u52a8\u4f5c\u5f97\u52a0\u5feb\u4e86\u3002\u4ed6\u5982\u6b64\u60f3\u7740\uff0c\u4f4e\u5934\u4eb2\u543b\u8299\u857e\u72c4\u7684\u8138\uff0c\u5c0f\u5973\u5b69\u5df2\u7ecf\u8ff7\u8ff7\u7cca\u7cca\u7761\u7740\u3002</p><p>\u203b\u203b\u203b</p><p>1372DR\uff0c\u8150\u53f6\u4e4b\u6708\uff08Uktar\uff0cFR\u7684\u7b2c\u5341\u4e00\u6708\uff09\u3002\u5982\u679c\u662f\u5728\u5730\u8868\u4e16\u754c\uff0c\u5c45\u6c11\u4eec\u4f1a\u5fd9\u7740\u51c6\u5907\u8fce\u63a5\u5373\u5c06\u5230\u6765\u7684\u6708\u4eae\u5e86\u5178\uff0c\u8fd9\u662f\u4e00\u5e74\u4e2d\u7684\u6700\u540e\u4e00\u4e2a\u8282\u65e5\uff0c\u6807\u5fd7\u7740\u51db\u51ac\u5230\u6765\uff0c\u540c\u65f6\u4e5f\u662f\u796d\u5178\u4ea1\u8005\u7684\u65e5\u5b50\uff0c\u7c7b\u4f3c\u5730\u7403\u4e0a\u7684\u6e05\u660e\u8282\u3002\u4f46\u5728\u5e7d\u6697\u5730\u57df\u7684\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\uff0c\u6697\u6d41\u6d8c\u52a8\uff0c\u6218\u706b\u4e00\u89e6\u5373\u53d1\u3002</p><p>\u6bd5\u4e1a\u5178\u793c\u4e0a\u53d1\u751f\u7684\u610f\u5916\uff0c\u5f15\u8d77\u4e86\u6267\u653f\u8bae\u4f1a\u7684\u9ad8\u5ea6\u91cd\u89c6\uff0c\u7136\u800c\u6298\u817e\u4e00\u756a\uff0c\u7ed3\u679c\u4e5f\u4e0d\u4e86\u4e86\u4e4b\u3002\u62c9\u6587\xb7\u535a\u514b\u672c\u8eba\u5728\u5e8a\u4e0a\u4f11\u517b\uff0c\u6ca1\u6709\u534a\u4e2a\u6708\u4f30\u8ba1\u4e0d\u80fd\u6062\u590d\uff0c\u5979\u6307\u63a7\u8fe6\u6cd5\u63a7\u5236\u6076\u9b54\uff0c\u7834\u574f\u4eea\u5f0f\uff0c\u7136\u800c\u6ca1\u6709\u8bc1\u636e\u3002\u5353\u5c14\u5e76\u975e\u6cd5\u6cbb\u793e\u4f1a\uff0c\u8bc1\u636e\u4e0d\u8bc1\u636e\u7684\uff0c\u5e73\u65f6\u5012\u4e5f\u6ca1\u90a3\u4e48\u8981\u7d27\uff0c\u4f46\u5982\u4eca\u53cc\u65b9\u662f\u540c\u7b49\u4f4d\u9636\u7684\u5973\u796d\u53f8\uff0c\u80cc\u540e\u5206\u522b\u662f\u7b2c\u4e00\u5bb6\u65cf\u548c\u7b2c\u4e8c\u5bb6\u65cf\uff0c\u81ea\u7531\u5fc3\u8bc1\u5c31\u4e0d\u592a\u884c\u5f97\u901a\u4e86\u3002\u81f3\u4e8e\u4e9a\u5f53\u65af\uff0c\u4ed6\u505a\u4e8b\u5411\u6765\u4ee5\u7a33\u59a5\u8457\u79f0\uff0c\u81ea\u7136\u66f4\u4e0d\u4f1a\u7559\u4e0b\u4ec0\u4e48\u628a\u67c4\u3002</p><p>\u5728\u6709\u5fc3\u4eba\u7684\u6563\u5e03\u4e4b\u4e0b\uff0c\u4e8b\u60c5\u5f88\u5feb\u4f20\u904d\u6574\u4e2a\u57ce\u5e02\u3002\u5353\u5c14\u5f53\u4e2d\u6ca1\u6709\u7b28\u86cb\uff0c\u8fde\u65e5\u4ee5\u6765\u53d1\u751f\u7684\u53d8\u6545\uff0c\u65e9\u5c31\u88ab\u5927\u5bb6\u770b\u5728\u773c\u4e2d\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u6240\u6709\u4eba\u90fd\u6e05\u695a\u5730\u77e5\u9053\uff1a\u795e\u540e\u5df2\u7ecf\u6d88\u5931\u4e86\uff0c\u7267\u5e08\u5df2\u7ecf\u4e27\u5931\u9b54\u6cd5\u4e86\uff0c\u968f\u4e4b\u800c\u6765\u7684\uff0c\u81ea\u7136\u5c31\u662f\u6574\u4e2a\u6743\u529b\u683c\u5c40\u7684\u53d8\u5316\u3002</p><p>\u501f\u7740\u8fd9\u79cd\u5c40\u52bf\uff0c\u4e9a\u5f53\u65af\u7684\u52a8\u4f5c\u52a0\u5feb\u3002\u6bd5\u4e1a\u5178\u793c\u7684\u6b21\u65e5\uff0c\u4e9a\u5f53\u65af\u5411\u6267\u653f\u8bae\u4f1a\u63d0\u4ea4\u540d\u5355\u5e76\u4e14\u83b7\u5f97\u901a\u8fc7\uff0c\u5965\u672f\u5b66\u9662\u589e\u52a0\u4e86\u4e09\u540d\u65b0\u5bfc\u5e08\uff0c\u5176\u4e2d\u4e4b\u4e00\u81ea\u7136\u662f\u743c\u6069\uff0c\u53e6\u5916\u4e24\u540d\u5219\u51fa\u8eab\u4e8e\u4e24\u4e2a\u5c0f\u5bb6\u65cf\uff0c\u66fe\u7ecf\u662f\u4e9a\u5f53\u65af\u7684\u5b66\u751f\uff0c\u65e0\u7591\u4e5f\u662f\u4ed6\u7684\u4eb2\u4fe1\u4e86\u3002</p><p>\u4e3b\u6bcd\u4eec\u4e5f\u6e05\u695a\u5730\u610f\u8bc6\u5230\u4e86\u5f62\u52bf\u7684\u5371\u6025\uff0c\u7136\u800c\u6700\u5927\u7684\u4e5f\u662f\u552f\u4e00\u7684\u540e\u53f0\u7a81\u7136\u6d88\u5931\uff0c\u4efb\u662f\u8c01\u4e5f\u6ca1\u6709\u56de\u5929\u4e4b\u529b\u3002\u4e8c\u5341\u4e00\u65e5\uff0c\u5168\u57ce\u9ad8\u9636\u7267\u5e08\u805a\u96c6\u5728\u86db\u540e\u795e\u6bbf\u4e2d\uff0c\u4e3e\u884c\u89c4\u6a21\u76db\u5927\u7684\u796d\u7940\uff0c\u7948\u6c42\u795e\u540e\u8d50\u6069\u3002\u4e9a\u5f53\u65af\u5e26\u9886\u5b66\u9662\u516b\u540d\u5bfc\u5e08\u548c\u7cbe\u5fc3\u6311\u9009\u7684\u5341\u540d\u5b66\u5f92\u62e6\u8def\u4f0f\u51fb\uff0c\u4e00\u573a\u6df7\u6218\u4e4b\u4e0b\uff0c\u83f2\u5c14\u4f26\u4e3b\u6bcd\u548c\u5979\u7684\u4e24\u4e2a\u5973\u513f\uff0c\u4ee5\u53ca\u535a\u514b\u672c\u5bb6\u65cf\u7684\u4e94\u540d\u9ad8\u9636\u7267\u5e08\u5c3d\u6570\u88ab\u6740\u3002</p><p>\u6309\u7167\u89c4\u77e9\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u987a\u7406\u6210\u7ae0\u7ee7\u627f\u4e3b\u6bcd\u4e4b\u4f4d\uff0c\u6709\u9996\u5e2d\u5deb\u5e08\u4e9a\u5f53\u65af\u548c\u5bb6\u65cf\u6b66\u6280\u957f\u82ae\u68ee\u7279\u4e24\u4eba\u7684\u5f3a\u529b\u652f\u6301\uff0c\u5f88\u5feb\u7a33\u5b9a\u4f4f\u5c40\u9762\uff0c\u968f\u5373\u5411\u7b2c\u4e8c\u5bb6\u65cf\u53d1\u52a8\u653b\u51fb\u3002\u4e9a\u5f53\u65af\u65e9\u5c31\u6697\u4e2d\u548c\u535a\u514b\u672c\u5bb6\u65cf\u7684\u4e24\u540d\u5deb\u5e08\u8fbe\u6210\u534f\u8bae\uff0c\u91cc\u5e94\u5916\u5408\u3002\u4e8c\u5341\u4e8c\u65e5\u51cc\u6668\uff0c\u7b2c\u4e8c\u5bb6\u65cf\u8986\u706d\u3002</p><p>\u4e8c\u5341\u4e8c\u65e5\u508d\u665a\uff0c\u7b2c\u4e03\u5bb6\u65cf\u3001\u7b2c\u516b\u5bb6\u65cf\u548c\u7b2c\u5341\u5bb6\u65cf\u8054\u5408\u5411\u83f2\u5c14\u4f26\u5bb6\u65cf\u53d1\u52a8\u7a81\u88ad\uff0c\u6218\u8d25\u9000\u56de\u3002\u6267\u653f\u8bae\u4f1a\u5df2\u7ecf\u540d\u5b58\u5b9e\u4ea1\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u7d22\u6027\u653e\u5f03\u6307\u63a7\uff0c\u5f84\u76f4\u8054\u5408\u5361\u6d1b\u62c9\u5bb6\u65cf\uff08\u539f\u672c\u662f\u7b2c\u516d\u5bb6\u65cf\uff0c\u5982\u4eca\u535a\u514b\u672c\u3001\u7c73\u5179\u745e\u56fe\u5c14\u548c\u745e\u8d39\u5fb7\u5bb6\u65cf\u90fd\u5df2\u7ecf\u8986\u706d\uff0c\u5b83\u4f9d\u6b21\u9012\u8fdb\u4e3a\u7b2c\u4e09\u5bb6\u65cf\uff09\uff0c\u8bb8\u4ee5\u796d\u53f8\u5b66\u9662\u6b21\u5e2d\u6559\u5b98\u7684\u5e2d\u4f4d\uff0c\u5411\u8fd9\u4e09\u4e2a\u5bb6\u65cf\u53d1\u52a8\u8fdb\u653b\u3002\u4e8c\u5341\u56db\u65e5\uff0c\u7b2c\u4e03\u3001\u7b2c\u516b\u548c\u7b2c\u5341\u4e00\u5bb6\u65cf\u5148\u540e\u8986\u706d\u3002</p><p>\u4e8b\u60c5\u81f3\u6b64\uff0c\u5df2\u7ecf\u7b97\u662f\u57fa\u672c\u5c18\u57c3\u843d\u5b9a\uff0c\u5176\u95f4\u867d\u7136\u4e5f\u6709\u79cd\u79cd\u60ca\u9669\u6ce2\u6298\uff0c\u4e0d\u5c3d\u5982\u4eba\u610f\u4e4b\u5904\uff0c\u4f46\u65e2\u7136\u6700\u7ec8\u6210\u529f\uff0c\u4e5f\u5c31\u4e0d\u5fc5\u7ec6\u7a76\u4e86\u3002\u77ed\u77ed\u51e0\u5929\u4e4b\u5185\uff0c\u6392\u540d\u524d\u4e8c\u5341\u4e00\u4f4d\u7684\u5bb6\u65cf\u6298\u635f\u5176\u516d\uff0c\u83f2\u5c14\u4f26\u4e00\u679d\u72ec\u79c0\uff0c\u6574\u4e2a\u57ce\u5e02\u4e2d\u518d\u65e0\u6297\u624b\uff0c\u7ef4\u5eb7\u5c3c\u4e9a\u8fd9\u4e2a\u4e3b\u6bcd\u7684\u4f4d\u7f6e\uff0c\u6682\u65f6\u7b97\u662f\u5750\u7a33\u4e86\u3002\u4e5f\u5e78\u597d\u74dc\u7406\u5fb7\u65af\u57ce\u5728\u8fd9\u7247\u533a\u57df\u6ca1\u6709\u8db3\u591f\u5206\u91cf\u7684\u5916\u654c\uff0c\u552f\u4e00\u4e00\u4e2a\u4f0a\u5361\u6c99\u57ce\uff0c\u77ee\u4eba\u4eec\u4e5f\u53ea\u56fe\u81ea\u4fdd\uff0c\u538b\u6839\u4e0d\u6562\u60f3\u7740\u53cd\u51fb\uff0c\u5426\u5219\u8fd9\u6837\u60e8\u70c8\u5185\u8ba7\u4e0b\u53bb\uff0c\u53ea\u4f1a\u5927\u5bb6\u4e00\u8d77\u5b8c\u86cb\u3002</p><p>\u63a5\u4e0b\u6765\u7684\u95ee\u9898\u5c31\u662f\u6536\u62fe\u6b8b\u5c40\uff0c\u522b\u7684\u5012\u8fd8\u7f62\u4e86\uff0c\u8be5\u780d\u5934\u7684\u780d\u5934\uff0c\u8be5\u6536\u7f16\u7684\u6536\u7f16\uff0c\u8be5\u6254\u8fdb\u5974\u96b6\u5e02\u573a\u62cd\u5356\u7684\u62cd\u5356\uff0c\u4e0d\u5fc5\u7ec6\u8bba\uff0c\u5012\u662f\u5728\u5bf9\u4e8e\u90a3\u4e9b\u7267\u5e08\u7684\u5904\u7f6e\u95ee\u9898\u4e0a\uff0c\u5206\u6b67\u4ea7\u751f\u4e86\u3002</p><p>\u8fd9\u6b21\u5185\u6218\uff0c\u83f2\u5c14\u4f26\u5bb6\u65cf\u63a5\u8fde\u653b\u706d\u7b2c\u4e8c\u3001\u7b2c\u4e03\u3001\u7b2c\u516b\u548c\u7b2c\u5341\u4e00\u5bb6\u65cf\uff0c\u4fd8\u864f\u4e86\u5927\u91cf\u7267\u5e08\uff0c\u8fde\u5e26\u4e0a\u6b21\u745e\u8d39\u5fb7\u5bb6\u65cf\u5269\u4e0b\u7684\u90a3\u4e9b\uff0c\u603b\u6570\u6574\u6574\u516b\u5341\u540d\uff0c\u5df2\u7ecf\u8fdc\u8fdc\u8d85\u8fc7\u83f2\u5c14\u4f26\u5bb6\u65cf\u73b0\u6709\u7684\u7267\u5e08\u6570\u91cf\u4e86\u3002\u6309\u7167\u4ee5\u5f80\u7684\u60ef\u4f8b\uff0c\u6218\u8d25\u5bb6\u65cf\u7684\u7267\u5e08\u53ea\u6709\u4e24\u6761\u8def\u53ef\u9009\u3002\u5176\u4e00\u662f\u6539\u540d\u6362\u59d3\uff0c\u653e\u5f03\u539f\u672c\u7684\u8eab\u4efd\uff0c\u88ab\u6218\u80dc\u5bb6\u65cf\u7684\u4e3b\u6bcd\u6216\u8005\u9ad8\u9636\u796d\u53f8\u6536\u517b\uff1b\u5176\u4e8c\u5c31\u662f\u7559\u7740\u5f53\u796d\u54c1\uff0c\u795e\u540e\u6700\u559c\u6b22\u81ea\u5df1\u7684\u7267\u5e08\u4e86\u3002\u524d\u8005\u6781\u5c11\u53d1\u751f\uff0c\u540e\u8005\u5219\u662f\u5e38\u6001\u3002</p><p>\u4e9a\u5f53\u65af\u7684\u4e3b\u5f20\uff0c\u662f\u6309\u7167\u8001\u89c4\u77e9\uff0c\u6740\u6389\u5b8c\u4e8b\uff0c\u4e00\u4e86\u767e\u4e86\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u5219\u8868\u793a\u53cd\u5bf9\uff0c\u5979\u8ba4\u4e3a\u83f2\u5c14\u4f26\u5bb6\u65cf\u8fde\u573a\u5927\u6218\uff0c\u867d\u7136\u5f97\u80dc\uff0c\u4f46\u635f\u8017\u4e5f\u5f88\u4e25\u91cd\uff0c\u5e94\u8be5\u6536\u7eb3\u8fd9\u4e9b\u7267\u5e08\u2014\u2014\u81f3\u5c11\u662f\u5176\u4e2d\u4e00\u90e8\u5206\u3002\u53cd\u6b63\u5979\u4eec\u7684\u5bb6\u65cf\u90fd\u5df2\u7ecf\u8986\u706d\uff0c\u4e5f\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u62a5\u4ec7\u4e0d\u62a5\u4ec7\u7684\uff0c\u5353\u5c14\u91cd\u89c6\u5bb6\u65cf\uff0c\u662f\u56e0\u4e3a\u770b\u91cd\u8fde\u5e26\u7684\u5229\u76ca\uff0c\u5374\u5e76\u4e0d\u770b\u91cd\u4eb2\u60c5\u3002</p><p>\u743c\u6069\u51b7\u773c\u65c1\u89c2\uff0c\u4ed6\u6e05\u695a\u53cc\u65b9\u4e3b\u5f20\u7684\u771f\u6b63\u7f18\u7531\u3002\u4e9a\u5f53\u65af\u7684\u56fe\u8c0b\uff0c\u6b63\u662f\u8981\u9010\u6b65\u524a\u5f31\u7267\u5e08\u52bf\u529b\uff0c\u4ee5\u5deb\u5e08\u53d6\u800c\u4ee3\u4e4b\uff0c\u5982\u4eca\u8fd9\u4e48\u597d\u7684\u673a\u4f1a\u5c82\u80fd\u653e\u8fc7\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u5219\u81ea\u7136\u662f\u8981\u5c3d\u53ef\u80fd\u4fdd\u5168\u7267\u5e08\u7684\u52bf\u529b\u3002\u8fd9\u7eaf\u7cb9\u662f\u7acb\u573a\u95ee\u9898\uff0c\u65e0\u5173\u5176\u4ed6\u3002</p><p>\u53cc\u65b9\u4e89\u6267\u4e0d\u4e0b\uff0c\u6700\u7ec8\u8fd8\u662f\u743c\u6069\u51fa\u4e86\u4e2a\u4e3b\u610f\u3002</p><p>\u201c\u6740\u6389\u5176\u5b9e\u6ca1\u5fc5\u8981\uff0c\u5c31\u7b97\u662f\u5f53\u796d\u54c1\uff0c\u516b\u5341\u540d\u7267\u5e08\u4e5f\u592a\u5962\u4f88\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u66f4\u4f55\u51b5\uff0c\u86db\u540e\u5df2\u7ecf\u6d88\u5931\uff0c\u5c31\u7b97\u518d\u597d\u7684\u796d\u54c1\u53c8\u6709\u4ec0\u4e48\u610f\u4e49\uff1f\u4e0d\u8fc7\u662f\u767d\u767d\u6d6a\u8d39\u7f62\u4e86\u3002\u201d</p><p>\u542c\u8fd9\u79cd\u53e3\u6c14\uff0c\u81ea\u7136\u662f\u8981\u8d5e\u6210\u7ef4\u5eb7\u5c3c\u4e9a\u4e86\u3002\u4e9a\u5f53\u65af\u5fae\u5fae\u76b1\u7709\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u743c\u6069\u5374\u53c8\u8f6c\u4e86\u53e3\u98ce\uff0c\u201c\u4f46\u8981\u8bf4\u7559\u4e0b\uff0c\u7ec8\u7a76\u4e5f\u662f\u9690\u60a3\uff0c\u800c\u4e14\u4e5f\u4e0d\u8db3\u4ee5\u9707\u6151\u5176\u4ed6\u5bb6\u65cf\u7684\u5f02\u5fc3\u3002\u201d</p><p>\u7ef4\u5eb7\u5c3c\u4e9a\u548c\u4e9a\u5f53\u65af\u4e24\u4eba\u90fd\u6709\u4e9b\u53d1\u6014\uff0c\u4e0d\u77e5\u9053\u4ed6\u5230\u5e95\u662f\u503e\u5411\u4e8e\u54ea\u4e00\u8fb9\uff0c\u8fd8\u662f\u8bf4\u538b\u6839\u5c31\u5728\u8bf4\u5e9f\u8bdd\u3002\u5e78\u597d\u743c\u6069\u6ca1\u6709\u6253\u592a\u5e73\u62f3\u7684\u4e60\u60ef\uff0c\u65e2\u7136\u53d1\u8a00\uff0c\u603b\u662f\u6709\u6240\u4e3b\u5f20\u3002</p><p>\u201c\u6740\u4e86\u6d6a\u8d39\uff0c\u7559\u7740\u4e0d\u59a5\uff0c\u90a3\u76f4\u63a5\u628a\u5979\u4eec\u90fd\u9001\u5230\u6c89\u9ed8\u4e4b\u6b4c\u91cc\uff0c\u4e0d\u5c31\u662f\u4e86\u3002\u201d</p></article>"}}}]);
