(window.webpackJsonp=window.webpackJsonp||[]).push([[1491],{1514:function(p){p.exports={chapterTitle:"\u7b2c\u4e03\u5377 \u6df1\u6e0a\u7bc7",title:"\u5e8f\u7ae0 \u590d\u82cf\uff08\u4e0a\uff09",content:"<article><h1>\u7b2c\u4e03\u5377 \u6df1\u6e0a\u7bc7</h1><h2>\u5e8f\u7ae0 \u590d\u82cf\uff08\u4e0a\uff09</h2><p>\u6210\u5343\u4e0a\u4e07\u7684\u5353\u5c14\u4ea1\u9b42\u6392\u6210\u6574\u9f50\u7684\u961f\u5217\uff0c\u8de8\u8d8a\u832b\u832b\u661f\u754c\uff0c\u5c31\u50cf\u4e00\u9053\u94f6\u8272\u957f\u6cb3\u822c\u7f13\u7f13\u6d41\u52a8\u7740\uff0c\u6700\u7ec8\u8fdb\u5165\u4e00\u4e2a\u8718\u86db\u5f62\u72b6\u7684\u9ed1\u8272\u6f29\u6da1\uff0c\u90a3\u662f\u4f4d\u9762\u4f20\u9001\u95e8\u3002\u4f20\u9001\u95e8\u7684\u53e6\u4e00\u4fa7\uff0c\u86db\u4e1d\u7eb5\u6a2a\u4ea4\u9519\uff0c\u6784\u6210\u4e86\u8513\u5ef6\u65e0\u9645\u7684\u86db\u7f51\uff0c\u6574\u4e2a\u4e16\u754c\u5c31\u6784\u5efa\u4e8e\u86db\u7f51\u4e4b\u4e0a\u3002</p><p>\u571f\u5730\u3001\u5ca9\u77f3\u3001\u6d1e\u7a74\u548c\u5c71\u5cf0\uff0c\u6240\u6709\u7684\u5730\u5f62\u98ce\u8c8c\uff0c\u5168\u90fd\u662f\u7531\u6b7b\u4ea1\u7684\u6076\u9b54\u8eab\u8eaf\u98ce\u5316\u800c\u6210\uff0c\u5bc6\u5bc6\u9ebb\u9ebb\u5730\u8986\u76d6\u5728\u86db\u7f51\u4e4b\u4e0a\uff0c\u6784\u6210\u4e86\u4e00\u7247\u8352\u829c\u7684\u4e16\u754c\u3002\u5730\u9762\u5728\u4e0d\u65f6\u5730\u9690\u9690\u98a4\u52a8\u7740\uff0c\u4eff\u4f5b\u5730\u9707\u7684\u4f59\u6ce2\u3002\u653e\u773c\u6240\u53ca\uff0c\u770b\u4e0d\u5230\u4efb\u4f55\u7eff\u8272\uff0c\u4e5f\u6ca1\u6709\u6c34\u6e90\uff0c\u5373\u4f7f\u5076\u5c14\u770b\u89c1\u6e56\u6cca\uff0c\u5176\u4e2d\u4e5f\u53ea\u6709\u6cb8\u817e\u7684\u707c\u70ed\u7194\u5ca9\u6216\u8005\u6563\u53d1\u8150\u81ed\u7684\u9178\u6db2\uff0c\u8fd9\u540c\u6837\u4e5f\u662f\u6076\u9b54\u7684\u9057\u9ab8\u6240\u5316\u3002\u5316\u77f3\u8986\u76d6\u4e0d\u5230\u4e4b\u5904\uff0c\u5f80\u5f80\u9732\u51fa\u9ed1\u8272\u7684\u6df1\u5751\uff0c\u5982\u679c\u4e0d\u5c0f\u5fc3\u6389\u8fdb\u53bb\u7684\u8bdd\uff0c\u4fbf\u4f1a\u5760\u5165\u86db\u7f51\u4e0b\u65b9\u6df1\u9083\u65e0\u5e95\u7684\u865a\u7a7a\u3002\u9664\u4e86\u795e\u660e\uff0c\u6ca1\u6709\u4efb\u4f55\u5b58\u5728\u80fd\u591f\u4ece\u4e2d\u9003\u8131\u3002</p><p>\u5929\u7a7a\u4e2d\u6c38\u8fdc\u6f06\u9ed1\u4e00\u7247\uff0c\u6ca1\u6709\u534a\u70b9\u661f\u5149\uff0c\u6d53\u5bc6\u7684\u4e91\u5c42\u53d8\u5e7b\u7740\u5f62\u72b6\uff0c\u516b\u4e2a\u5de8\u5927\u7684\u7ea2\u8272\u7403\u4f53\u5728\u5176\u4e2d\u82e5\u9690\u82e5\u73b0\u3002\u6bcf\u4e2a\u7403\u4f53\u7684\u5149\u4eae\u7a0b\u5ea6\u90fd\u4e0d\u540c\uff0c\u5b83\u4eec\u5c31\u50cf\u8718\u86db\u7684\u773c\u775b\uff0c\u5728\u76d1\u89c6\u7740\u5730\u9762\u7684\u6bcf\u4e00\u5904\u7ec6\u5c0f\u53d8\u52a8\u3002\u4ece\u5176\u4e2d\u4e00\u9897\u7403\u4f53\u5f53\u4e2d\uff0c\u94f6\u8272\u7684\u7075\u9b42\u4e4b\u6cb3\u7f13\u7f13\u6d41\u51fa\uff0c\u6a2a\u4e98\u591c\u7a7a\uff0c\u671d\u7740\u4e00\u4e2a\u65b9\u5411\u524d\u8fdb\u3002</p><p>\u8fd9\u91cc\u662f\u8718\u86db\u9b54\u7f51\uff0c\u66fe\u7ecf\u7684\u65e0\u5c3d\u6df1\u6e0a\u7b2c\u516d\u5341\u516d\u5c42\uff0c\u5982\u4eca\u5b83\u5df2\u7ecf\u4ece\u6df1\u6e0a\u4e2d\u5206\u88c2\u800c\u51fa\uff0c\u6e38\u5f0b\u5728\u661f\u754c\u4e2d\uff0c\u6210\u4e3a\u72ec\u7acb\u7684\u4f4d\u9762\u3002</p><p>\u5982\u679c\u6709\u65c5\u6e38\u8005\u6765\u5230\u6b64\u5730\uff0c\u65e0\u8bba\u843d\u811a\u4f55\u5904\uff0c\u6cbf\u7740\u4efb\u4f55\u65b9\u5411\u4e00\u76f4\u524d\u8fdb\uff0c\u6700\u7ec8\u90fd\u4f1a\u770b\u89c1\u4e00\u7247\u7b3c\u7f69\u5728\u7a00\u8584\u7eff\u96fe\u4e2d\u7684\u5e73\u539f\uff0c\u5730\u9762\u4e0a\u5e03\u6ee1\u4e86\u5927\u5927\u5c0f\u5c0f\u7eb5\u6a2a\u4ea4\u9519\u7684\u88c2\u9699\uff0c\u5f80\u7a7a\u4e2d\u55b7\u53d1\u7740\u706b\u7130\u548c\u6bd2\u6c14\u3002\u88ab\u86db\u540e\u9057\u5f03\u7684\u7075\u9b42\u5316\u4f5c\u5404\u79cd\u4e11\u964b\u7684\u5f62\u6001\uff0c\u5728\u5e73\u539f\u4e0a\u6e38\u8361\u7740\uff0c\u627f\u53d7\u6bd2\u706b\u7684\u7099\u70e4\u548c\u953b\u70bc\uff0c\u5982\u679c\u5b83\u4eec\u8db3\u591f\u5e78\u8fd0\u7684\u8bdd\uff0c\u4fbf\u6709\u53ef\u80fd\u88ab\u53ec\u56de\u8718\u86db\u5821\u5792\u4e4b\u4e2d\uff0c\u7ee7\u7eed\u4e3a\u795e\u7947\u6548\u547d\u3002</p><p>\u5728\u5e73\u539f\u7684\u5c3d\u5934\uff0c\u5ca9\u77f3\u548c\u571f\u5730\u9aa4\u7136\u6d88\u5931\uff0c\u773c\u524d\u662f\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u8272\u865a\u7a7a\uff0c\u5343\u4e07\u9053\u86db\u4e1d\u7f16\u7ec7\u6210\u4e00\u5f20\u5e9e\u5927\u5230\u4e0d\u53ef\u601d\u8bae\u7684\u86db\u7f51\uff0c\u98d8\u6d6e\u5728\u8fd9\u6df1\u6e0a\u4e4b\u4e0a\u3002\u800c\u5728\u86db\u7f51\u7684\u4e2d\u5fc3\uff0c\u4e00\u5ea7\u5de8\u5927\u7684\u8718\u86db\u5f62\u72b6\u5821\u5792\u9759\u9759\u5730\u8e72\u8e1e\uff0c\u516b\u6761\u8282\u80a2\u8e29\u5728\u86db\u4e1d\u4e0a\uff0c\u652f\u6491\u7740\u6c89\u91cd\u7684\u8eaf\u4f53\uff0c\u4e1d\u6beb\u6ca1\u6709\u534a\u70b9\u4e0b\u6c89\u8ff9\u8c61\u3002\u5b83\u901a\u4f53\u662f\u7528\u94a2\u94c1\u6240\u5236\uff0c\u5728\u9ad8\u7a7a\u4e2d\u516b\u9897\u7ea2\u6708\u7684\u6620\u7167\u4e0b\u900f\u7740\u51b7\u51b7\u7684\u91d1\u5c5e\u5149\u6cfd\uff0c\u8fd9\u5c31\u662f\u8718\u86db\u5973\u795e\u7f57\u4e1d\u7684\u5bab\u6bbf\uff0c\u5979\u65f6\u5e38\u4f1a\u64cd\u7eb5\u7740\u8718\u86db\u5821\u5792\u6cbf\u7740\u86db\u7f51\u6f2b\u6e38\uff0c\u5c31\u50cf\u4e00\u53ea\u771f\u6b63\u7684\u8718\u86db\u5728\u5de1\u89c6\u81ea\u5df1\u7684\u9886\u5730\u3002</p><p>\u4efb\u4f55\u60f3\u8981\u8d70\u8fdb\u8718\u86db\u5821\u5792\u7684\u52aa\u529b\u90fd\u4f1a\u88ab\u5ba3\u544a\u5931\u8d25\uff0c\u65e0\u8bba\u662f\u51e1\u4eba\uff0c\u662f\u7075\u9b42\uff0c\u662f\u6076\u9b54\u6216\u8005\u5176\u4ed6\u5b58\u5728\u2014\u2014\u9664\u4e86\u795e\u7947\uff0c\u5982\u679c\u672a\u7ecf\u7f57\u4e1d\u5141\u8bb8\uff0c\u5728\u8e0f\u4e0a\u86db\u4e1d\u65f6\u90fd\u4f1a\u88ab\u7c98\u4f4f\uff0c\u800c\u98de\u884c\u6cd5\u672f\u548c\u4f20\u9001\u6cd5\u672f\u5728\u86db\u7f51\u8303\u56f4\u5185\u66f4\u4e0d\u53ef\u80fd\u5b9e\u73b0\u3002\u9760\u8fd1\u6df1\u6e0a\u8fb9\u7f18\uff0c\u6709\u4e00\u6392\u5200\u5203\u822c\u9510\u5229\u7684\u5c16\u5cf0\uff0c\u9ad8\u9ad8\u8038\u7acb\uff0c\u523a\u5165\u591c\u7a7a\uff0c\u5728\u5c71\u5cf0\u811a\u4e0b\uff0c\u6709\u4e00\u5904\u6df1\u4e0d\u53ef\u6d4b\u7684\u5e7d\u6697\u6d1e\u7a74\uff0c\u90a3\u4fbf\u662f\u901a\u5f80\u86db\u540e\u5821\u5792\u7684\u552f\u4e00\u5165\u53e3\u2014\u2014\u593a\u9b42\u9669\u9698\u3002</p><p>\u7075\u9b42\u4e4b\u6cb3\u81ea\u7a7a\u4e2d\u503e\u659c\u4e0b\u6765\uff0c\u5ddd\u6d41\u4e0d\u606f\u5730\u6ce8\u5165\u6d1e\u7a74\u4e4b\u4e2d\uff0c\u968f\u5373\u5728\u86db\u540e\u5821\u5792\u4e0a\u65b9\u51fa\u73b0\u3002\u6bcf\u4e2a\u7075\u9b42\u90fd\u4f1a\u7acb\u523b\u53d8\u5f97\u6e05\u6670\u8d77\u6765\uff0c\u4e00\u5f20\u5f20\u626d\u66f2\u7684\u9762\u5bb9\u5728\u4ed6\u4eec\u8eab\u4f53\u7684\u6bcf\u5bf8\u76ae\u80a4\u4e0a\u6d6e\u73b0\uff0c\u8fd9\u4e9b\u90fd\u662f\u4ed6\u4eec\u751f\u524d\u4ee5\u86db\u540e\u4e4b\u540d\u800c\u6740\u6b7b\u7684\u6240\u6709\u654c\u4eba\u3002\u86db\u540e\u4f1a\u5ba1\u89c6\u5979\u6240\u6709\u7684\u5b50\u6c11\uff0c\u6839\u636e\u751f\u524d\u7684\u8654\u8bda\u548c\u5949\u732e\uff0c\u51b3\u5b9a\u6b7b\u540e\u7684\u4f4d\u7f6e\u3002</p><p>\u4e00\u4e2a\u7075\u9b42\u6f02\u79fb\u5230\u8718\u86db\u5821\u5792\u7684\u4e0a\u65b9\uff0c\u539f\u672c\u6a21\u7cca\u7684\u9762\u5e9e\u53d8\u5f97\u6e05\u6670\u8d77\u6765\uff0c\u5979\u662f\u4e2a\u5973\u6027\uff0c\u800c\u4e14\u663e\u7136\u751f\u524d\u662f\u4e00\u4f4d\u796d\u53f8\u3002\u7d2b\u8272\u7684\u5996\u706b\u4ece\u811a\u4e0b\u51b2\u51fa\uff0c\u5c06\u5979\u541e\u566c\u5176\u4e2d\uff0c\u7075\u9b42\u53d1\u51fa\u523a\u8033\u7684\u5c16\u53eb\uff0c\u5316\u4f5c\u4e00\u56e2\u706b\u7130\u4ece\u7a7a\u4e2d\u5760\u843d\uff0c\u6389\u8fdb\u5821\u5792\u4e2d\u3002</p><p>\u5f53\u5979\u518d\u5ea6\u6062\u590d\u610f\u8bc6\u6e05\u9192\u65f6\uff0c\u53d1\u73b0\u81ea\u5df1\u6b63\u530d\u5310\u5728\u4e00\u5ea7\u91d1\u5b57\u5854\u5f62\u72b6\u7684\u795e\u6bbf\u524d\uff0c\u5b83\u540c\u6837\u662f\u7528\u5b8c\u5168\u7684\u94a2\u94c1\u94f8\u6210\uff0c\u5404\u79cd\u8718\u86db\u56fe\u6848\u7684\u6d6e\u96d5\u954c\u523b\u5176\u4e0a\uff0c\u5404\u79cd\u989c\u8272\u7684\u5996\u706b\u76d8\u65cb\u8426\u7ed5\u3002\u5973\u796d\u53f8\u832b\u7136\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u4f4e\u5934\u5ba1\u89c6\uff0c\u7136\u540e\u624d\u53d1\u73b0\u81ea\u5df1\u7684\u8eab\u4f53\u5df2\u7ecf\u53d1\u751f\u4e86\u6539\u53d8\u3002\u5979\u73b0\u5728\u770b\u8d77\u6765\u5c31\u50cf\u4e00\u652f\u4e03\u82f1\u5c3a\u9ad8\u7684\u3001\u4e0d\u65ad\u878d\u5316\u7684\u8721\u70db\uff0c\u516b\u6761\u97ad\u5b50\u822c\u7684\u89e6\u624b\u5728\u8eab\u65c1\u626d\u52a8\u7740\uff0c\u8fd9\u526f\u5f62\u8c61\u770b\u8d77\u6765\u65e2\u6050\u6016\u53c8\u6076\u5fc3\uff0c\u7136\u800c\u5973\u796d\u53f8\u7acb\u523b\u53d1\u81ea\u5185\u5fc3\u5730\u5fae\u7b11\u8d77\u6765\u3002</p><p>\u5979\u88ab\u53d8\u6210\u4e86\u8721\u878d\u5996\u3002</p><p>\u5728\u7f57\u4e1d\u7684\u56fd\u5ea6\u4e2d\uff0c\u8721\u878d\u5996\u7684\u5730\u4f4d\u8d85\u51fa\u5176\u4ed6\u6240\u6709\u6076\u9b54\u4e4b\u4e0a\uff0c\u5979\u4eec\u662f\u7f57\u4e1d\u7684\u4f8d\u5973\uff0c\u662f\u795e\u7947\u7684\u4ee3\u8a00\u4eba\uff0c\u5728\u5f88\u591a\u65f6\u5019\u662f\u86db\u540e\u548c\u51e1\u4eba\u6c9f\u901a\u7684\u4fe1\u4f7f\u3002\u80fd\u591f\u6210\u4e3a\u8721\u878d\u5996\uff0c\u610f\u5473\u7740\u86db\u540e\u7684\u6069\u5ba0\uff0c\u662f\u65e0\u4e0a\u7684\u5149\u8f89\u548c\u8363\u8000\u3002</p><p>\u66f4\u591a\u7684\u7075\u9b42\u6389\u4e0b\u6765\uff0c\u843d\u5728\u8721\u878d\u5996\u8eab\u8fb9\uff0c\u5316\u4f5c\u5927\u5927\u5c0f\u5c0f\u7684\u8718\u86db\uff0c\u77ed\u6682\u7684\u832b\u7136\u4e4b\u540e\uff0c\u5b83\u4eec\u5f00\u59cb\u722c\u884c\uff0c\u516b\u53ea\u8282\u80a2\u6572\u51fb\u5730\u9762\uff0c\u5f62\u6210\u4e00\u79cd\u5947\u7279\u7684\u8282\u594f\u548c\u97f5\u5f8b\u3002\u8721\u878d\u5996\u968f\u4e4b\u5f80\u524d\u79fb\u52a8\u4e00\u6b65\uff0c\u5f62\u6001\u6539\u53d8\uff0c\u53d8\u6210\u4e00\u4f4d\u8d64\u88f8\u7684\u5353\u5c14\u5973\u6027\uff0c\u6bd4\u5979\u4f5c\u4e3a\u51e1\u4eba\u65f6\u66f4\u52a0\u7a88\u7a95\u7f8e\u8273\uff0c\u4f46\u8fd9\u4e9b\u5e76\u975e\u5979\u6240\u5173\u6ce8\u3002\u5979\u8d70\u5728\u8718\u86db\u7fa4\u91cc\uff0c\u8f7b\u76c8\u5730\u8dc3\u4e0a\u53f0\u9636\uff0c\u901a\u8fc7\u6b63\u95e8\uff0c\u8d70\u8fdb\u4e86\u91d1\u5b57\u5854\u795e\u6bbf\u4e4b\u4e2d\u3002</p><p>\u503e\u659c\u7684\u5899\u58c1\u4e0a\u7ed3\u6ee1\u86db\u7f51\uff0c\u5927\u5927\u5c0f\u5c0f\u7684\u8718\u86db\u5728\u5176\u95f4\u7a7f\u68ad\uff0c\u5b83\u4eec\u65e0\u4e00\u4f8b\u5916\u90fd\u6709\u7740\u8840\u7ea2\u8272\u7684\u773c\u73e0\u3002\u8721\u878d\u5996\u7b14\u76f4\u5730\u524d\u8fdb\uff0c\u6beb\u4e0d\u72b9\u8c6b\uff0c\u5979\u5728\u5fc3\u4e2d\u542c\u5230\u4e86\u795e\u7947\u7684\u53ec\u5524\u3002</p><p>\u4e00\u5ea7\u7cbe\u81f4\u7684\u516b\u8fb9\u5f62\u796d\u575b\u51fa\u73b0\u5728\u8721\u878d\u5996\u9762\u524d\uff0c\u516b\u53ea\u5de8\u5927\u7684\u9ed1\u8272\u8718\u86db\u76d8\u8e1e\u5176\u4e0a\uff0c\u5b83\u4eec\u6709\u516b\u53ea\u7ea2\u8272\u7684\u5355\u773c\u548c\u957f\u957f\u7684\u3001\u957f\u7740\u9ed1\u8272\u521a\u6bdb\u7684\u8282\u80a2\uff0c\u4eae\u9ed1\u8272\u7684\u8179\u90e8\u4e0a\u6709\u4e00\u4e2a\u7ea2\u8272\u7684\u6c99\u6f0f\u6591\u7eb9\uff0c\u5de8\u989a\u5f20\u5f00\u7740\uff0c\u6ef4\u7740\u7eff\u8272\u6bd2\u6db2\u7684\u7360\u7259\u66b4\u9732\u5507\u5916\u3002\u8718\u86db\u4eec\u4e92\u76f8\u77aa\u89c6\uff0c\u4f3c\u4e4e\u60f3\u8981\u6740\u6b7b\u5176\u4ed6\u540c\u7c7b\uff0c\u4f46\u6ca1\u6709\u771f\u6b63\u52a8\u624b\u3002\u5728\u796d\u575b\u7684\u5468\u56f4\uff0c\u4e03\u4f4d\u8d64\u88f8\u7684\u5353\u5c14\u5973\u6027\u4f8d\u7acb\u7740\uff0c\u6ce8\u89c6\u7740\u8718\u86db\u3002</p><p>\u8fd8\u5dee\u4e00\u4eba\u3002</p><p>\u8721\u878d\u5996\u81ea\u7136\u800c\u7136\u5730\u660e\u767d\u4e86\u8fd9\u70b9\uff0c\u4e8e\u662f\u5979\u8d70\u4e0a\u524d\uff0c\u586b\u8865\u4e86\u6700\u540e\u4e00\u4e2a\u7a7a\u4f4d\u3002\u5979\u4eec\u8dea\u5012\uff0c\u53cc\u624b\u62ac\u8d77\uff0c\u540c\u58f0\u8bf5\u5531\u7740\u8d5e\u7f8e\u7684\u8bd7\u53e5\u3002</p><p>\u7136\u540e\u8718\u86db\u4eec\u5f00\u59cb\u53ae\u6740\u3002</p><p>\u203b\u203b\u203b</p><p>\u523a\u9aa8\u7684\u5bd2\u98ce\u4ece\u8fdc\u5904\u5439\u6765\uff0c\u5377\u8d77\u6f2b\u5929\u7684\u7070\u5c18\u548c\u77f3\u5757\uff0c\u5f62\u6210\u4e00\u4e2a\u4e2a\u9f99\u5377\u6f29\u6da1\uff0c\u7d2b\u9ed1\u8272\u7684\u95ea\u7535\u4ece\u7a7a\u4e2d\u5288\u4e0b\uff0c\u843d\u5165\u6f29\u6da1\u7684\u4e2d\u5fc3\uff0c\u4e00\u79cd\u7075\u9b42\u7684\u58f0\u97f3\u5939\u6742\u5176\u95f4\uff0c\u65ad\u65ad\u7eed\u7eed\uff0c\u82e5\u6709\u82e5\u65e0\uff0c\u50cf\u662f\u547c\u5524\uff0c\u53c8\u50cf\u662f\u5ba3\u544a\u3002\u6210\u5343\u4e0a\u4e07\u7684\u8718\u86db\u4ece\u6ce5\u571f\u3001\u5ca9\u77f3\u3001\u6d1e\u7a74\u548c\u9634\u5f71\u4e2d\u722c\u884c\u51fa\u6765\uff0c\u4ece\u56db\u9762\u516b\u65b9\u5411\u8718\u86db\u9b54\u7f51\u7684\u4e2d\u592e\u5feb\u901f\u6c47\u96c6\u3002\u5b83\u4eec\u5e76\u975e\u90fd\u662f\u771f\u6b63\u7684\u8718\u86db\uff0c\u66f4\u591a\u662f\u4ee5\u6b64\u5f62\u6001\u51fa\u73b0\u7684\u6076\u9b54\u3002\u5728\u8fd9\u4e2a\u4f4d\u9762\uff0c\u8718\u86db\u4f1a\u53d7\u5230\u4f18\u5f85\u3002</p><p>\u50cf\u6d6a\u6f6e\u4e00\u822c\uff0c\u8718\u86db\u6d77\u6d0b\u5f80\u524d\u6beb\u4e0d\u505c\u6b47\u5730\u63a8\u8fdb\u7740\uff0c\u76f4\u81f3\u5c16\u5cf0\u5c71\u8109\u7684\u5916\u56f4\u3002\u65e0\u6570\u53ea\u8282\u80a2\u6709\u8282\u594f\u5730\u6572\u6253\u7740\u5730\u9762\uff0c\u53d1\u51fa\u54d2\u54d2\u54d2\u54d2\u7684\u58f0\u97f3\uff0c\u50cf\u662f\u5728\u7b49\u5f85\u7740\u4e00\u573a\u76db\u5bb4\u7684\u5f00\u5e2d\u3002\u5bd2\u98ce\u518d\u4e00\u6b21\u522e\u8fc7\uff0c\u6784\u6210\u6574\u4e2a\u4f4d\u9762\u7684\u86db\u7f51\u5267\u70c8\u9707\u52a8\u8d77\u6765\uff0c\u6447\u6643\u7740\uff0c\u4eff\u4f5b\u968f\u65f6\u53ef\u80fd\u88ab\u6495\u88c2\uff0c\u6389\u5165\u865a\u65e0\uff0c\u800c\u90a3\u4e2a\u98ce\u4e2d\u7684\u58f0\u97f3\u4e5f\u56e0\u6b64\u53d8\u5f97\u66f4\u52a0\u6e05\u6670\u3002</p><p>\u201c\u83b7\u9009\u8005\u3002\u201d</p><p>\u8718\u86db\u4eec\u6572\u51fb\u7740\u5ca9\u77f3\u5730\u9762\uff0c\u8282\u594f\u53d8\u5f97\u66f4\u6025\u4fc3\u4e86\u3002</p><p>\u54cd\u5e94\u8fd9\u79cd\u8282\u594f\uff0c\u795e\u6bbf\u796d\u575b\u4e0a\u7684\u516b\u53ea\u8718\u86db\u53ae\u6740\u5f97\u66f4\u52a0\u6fc0\u70c8\uff0c\u5b83\u4eec\u6325\u821e\u7740\u516b\u6761\u957f\u817f\uff0c\u7528\u5de8\u989a\u6495\u5f00\u5bf9\u624b\u7684\u7532\u58f3\uff0c\u7528\u7360\u7259\u523a\u8fdb\u76ae\u8089\uff0c\u6ce8\u5165\u6bd2\u6db2\uff0c\u7136\u540e\u556e\u54ac\u541e\u566c\u3002\u5931\u8d25\u8005\u7684\u7684\u8840\u8089\u548c\u7075\u9b42\u5473\u9053\u662f\u5982\u6b64\u9c9c\u7f8e\uff0c\u4ee4\u80dc\u5229\u8005\u66f4\u52a0\u5f3a\u58ee\uff0c\u800c\u6bcf\u5f53\u4e00\u53ea\u8718\u86db\u6b7b\u53bb\uff0c\u796d\u575b\u8fb9\u7684\u4e00\u4f4d\u8721\u878d\u5996\u5c31\u968f\u4e4b\u878d\u5316\uff0c\u5316\u4f5c\u96fe\u6c14\u6d88\u6563\u3002</p><p>\u4f46\u6ca1\u6709\u4eba\u9003\u907f\u3002</p><p>\u8718\u86db\u7684\u6570\u91cf\u5feb\u901f\u51cf\u5c11\uff0c\u6700\u7ec8\u53ea\u5269\u4e0b\u552f\u4e00\u7684\u80dc\u5229\u8005\uff0c\u4e03\u540d\u8721\u878d\u5996\u6d88\u5931\uff0c\u53ea\u6709\u65b0\u52a0\u5165\u8005\u5e78\u5b58\u3002\u5979\u7565\u5e26\u8ff7\u60d1\u4e0d\u89e3\u5730\u62ac\u8d77\u5934\u6765\uff0c\u6b63\u8fce\u4e0a\u8718\u86db\u5c45\u9ad8\u4e34\u4e0b\u7684\u4fef\u89c6\u76ee\u5149\u3002</p><p>\u5979\u770b\u89c1\u4e86\u4e00\u53cc\u7d2b\u8272\u7684\u773c\u775b\uff0c\u8fd9\u4ee4\u8721\u878d\u5996\u6709\u4e9b\u8ff7\u60d1\uff0c\u4f46\u5f88\u5feb\u8fd9\u79cd\u60c5\u7eea\u5c31\u70df\u6d88\u4e91\u6563\u3002\u4e0d\u9700\u8981\u4efb\u4f55\u7406\u7531\uff0c\u8721\u878d\u5996\u77e5\u9053\u7ad9\u5728\u81ea\u5df1\u9762\u524d\u7684\u6b63\u662f\u5973\u795e\uff0c\u540c\u65f6\u4e5f\u660e\u767d\u81ea\u5df1\u5e94\u8be5\u505a\u4ec0\u4e48\u3002\u6740\u622e\u3001\u6355\u98df\u3001\u541e\u566c\uff0c\u8718\u86db\u56e0\u6b64\u800c\u53d8\u5f97\u66f4\u52a0\u5f3a\u58ee\uff0c\u8fd9\u662f\u7f57\u4e1d\u7684\u6559\u5bfc\uff0c\u662f\u5353\u5c14\u7684\u6cd5\u5219\u2014\u2014\u800c\u5973\u795e\u540c\u6837\u4e5f\u5fc5\u987b\u9075\u7167\u5353\u5c14\u7684\u6cd5\u5219\u3002</p><p>\u5979\u5f20\u5f00\u53cc\u81c2\uff0c\u4e00\u6b65\u6b65\u8d70\u4e0a\u796d\u575b\uff0c\u8718\u86db\u6ce8\u89c6\u7740\u5979\uff0c\u7136\u540e\u9732\u51fa\u4e00\u4e2a\u4e11\u964b\u800c\u5371\u9669\u7684\u7b11\u5bb9\u3002\u547c\u51fa\u6bd2\u6c14\u7684\u5de8\u989a\u5f20\u5f00\uff0c\u6bd2\u7259\u51c6\u786e\u5730\u523a\u8fdb\u4e86\u76ae\u80a4\uff0c\u7a7f\u900f\u8840\u8089\uff0c\u707c\u70e7\u548c\u9ebb\u75f9\u611f\u540c\u65f6\u6d41\u904d\u8eab\u4f53\uff0c\u8721\u878d\u5996\u98a4\u6817\u7740\uff0c\u6ee1\u5fc3\u559c\u60a6\u3002</p><p>\u201c\u795e\u540e\u3002\u201d</p><p>\u5979\u4f4e\u541f\u7740\uff0c\u5410\u51fa\u6700\u540e\u7684\u547c\u5438\u3002</p><p>\u203b\u203b\u203b</p><p>\u5728\u8718\u86db\u9b54\u7f51\u7684\u8fb9\u7f18\uff0c\u7ef5\u5ef6\u7684\u4e18\u9675\u5c06\u5e73\u539f\u5206\u9694\u6210\u72ed\u5c0f\u788e\u5757\uff0c\u8302\u5bc6\u7684\u6811\u6797\u548c\u8349\u5730\u70b9\u7f00\u5176\u95f4\uff0c\u95f4\u6216\u8fd8\u6709\u6e56\u6cca\u95ea\u70c1\u7740\u84dd\u5b9d\u77f3\u822c\u7684\u7f8e\u4e3d\u5149\u8292\uff0c\u6784\u6210\u8fd9\u8352\u829c\u4e16\u754c\u4e2d\u7684\u4e00\u65b9\u5c0f\u5c0f\u7eff\u6d32\u3002\u53e4\u8001\u7684\u8513\u85e4\u5728\u7eff\u6d32\u8fb9\u7f18\u751f\u957f\u7740\uff0c\u7f16\u7ec7\u6210\u4e00\u9053\u4e25\u5bc6\u7684\u56f4\u5899\u3002\u800c\u5728\u4e2d\u5fc3\uff0c\u4e00\u68f5\u53c2\u5929\u53e4\u6811\u679d\u53f6\u8212\u5f20\uff0c\u4ead\u4ead\u5982\u76d6\uff0c\u5c06\u6574\u4e2a\u7eff\u6d32\u90fd\u906e\u853d\u5176\u4e0b\uff0c\u963b\u6321\u591c\u7a7a\u4e2d\u516b\u9897\u7ea2\u8272\u7403\u4f53\u7684\u5149\u8292\uff0c\u534a\u70b9\u900f\u4e0d\u8fdb\u6765\u3002</p><p>\u65e0\u6570\u7684\u5353\u5c14\u5728\u53e4\u6811\u4e0b\u65b9\u6f2b\u6e38\u3001\u4ea4\u8c08\u3001\u5b09\u620f\u548c\u4f11\u606f\uff0c\u5927\u591a\u662f\u5973\u6027\uff0c\u4e5f\u6709\u5c11\u6570\u7537\u6027\u3002\u4ed6\u4eec\u6216\u884c\u6216\u7acb\uff0c\u6216\u5750\u6216\u5367\uff0c\u65e0\u4e00\u4f8b\u5916\u90fd\u8d64\u88f8\u7740\u8eab\u4f53\uff0c\u6ca1\u6709\u4eba\u56e0\u6b64\u800c\u611f\u89c9\u4e0d\u9002\uff0c\u4eff\u4f5b\u4e00\u5207\u90fd\u4e60\u4ee5\u4e3a\u5e38\uff0c\u4e5f\u6ca1\u6709\u76f8\u4e92\u653b\u51fb\u548c\u6597\u4e89\uff0c\u6574\u4e2a\u6c14\u6c1b\u7965\u548c\u800c\u5b89\u5b81\uff0c\u8fd9\u4e00\u5e55\u5982\u679c\u53d1\u751f\u5728\u7cbe\u7075\u6216\u8005\u4f8f\u5112\u8eab\u4e0a\u90a3\u4e48\u4e0d\u8db3\u4e3a\u5947\uff0c\u6362\u6210\u4eba\u7c7b\u4e5f\u53ef\u4ee5\u63a5\u53d7\uff0c\u4f46\u51fa\u73b0\u5728\u5353\u5c14\u8eab\u4e0a\uff0c\u5c31\u5f88\u662f\u4ee4\u4eba\u60ca\u8be7\u4e86\u3002</p><p>\u53e4\u6811\u7684\u6811\u51a0\u9876\u7aef\uff0c\u4e00\u4f4d\u5353\u5c14\u5973\u5b50\u6b63\u9759\u9759\u7ad9\u7acb\u7740\uff0c\u4ef0\u671b\u591c\u7a7a\u3002\u5979\u975e\u5e38\u9ad8\u5927\uff0c\u63a5\u8fd1\u5341\u82f1\u5c3a\uff0c\u65e0\u8bba\u4ee5\u5353\u5c14\u8fd8\u662f\u4eba\u7c7b\u7684\u6807\u51c6\u800c\u8a00\u90fd\u662f\u4e2a\u5de8\u4eba\uff0c\u4f46\u5374\u4e1d\u6beb\u4e0d\u663e\u5f97\u7b28\u91cd\uff0c\u53cd\u800c\u900f\u51fa\u4e00\u79cd\u7cbe\u81f4\u7ea4\u7ec6\u7684\u611f\u89c9\u3002\u548c\u6811\u4e0b\u7684\u5353\u5c14\u4eec\u4e00\u6837\uff0c\u5979\u8d64\u88f8\u7740\uff0c\u94f6\u5149\u95ea\u95ea\u7684\u957f\u53d1\u5982\u6d41\u7011\u822c\u5782\u6cfb\u4e0b\u6765\uff0c\u76f4\u5230\u811a\u8e1d\uff0c\u94f6\u8272\u5149\u8f89\u65cb\u8f6c\u95ea\u70c1\u7740\uff0c\u5c06\u5979\u5168\u8eab\u5305\u88f9\uff0c\u4eff\u4f5b\u4e00\u4ef6\u900f\u660e\u7684\u957f\u888d\u3002</p><p>\u4e00\u9635\u4e00\u9635\u7684\u5bd2\u98ce\u4ece\u9065\u8fdc\u5904\u5439\u6765\uff0c\u7802\u77f3\u548c\u65cb\u98ce\u90fd\u88ab\u9b54\u6cd5\u8513\u85e4\u963b\u9694\u5728\u5916\uff0c\u4f46\u98ce\u4e2d\u7684\u4f4e\u541f\u4f9d\u65e7\u5728\u8033\u8fb9\u6e05\u6670\u53ef\u95fb\u3002\u7167\u8000\u6574\u4e2a\u86db\u7f51\u4f4d\u9762\u7684\u516b\u9897\u7ea2\u8272\u7403\u4f53\u5149\u534e\u4e0d\u77e5\u4f55\u65f6\u53d8\u5f97\u9eef\u6de1\u8d77\u6765\uff0c\u8d8a\u6765\u8d8a\u5185\u655b\uff0c\u4eff\u4f5b\u7a7a\u95f4\u584c\u9677\u4e00\u822c\uff0c\u6700\u7ec8\u53d8\u6210\u516b\u4e2a\u6df1\u4e0d\u89c1\u5e95\u7684\u9ed1\u6d1e\u3002\u6d53\u5bc6\u7684\u4e4c\u4e91\u4ece\u5934\u9876\u4e0a\u5f80\u8fd9\u8fb9\u7f13\u7f13\u98d8\u6765\uff0c\u539a\u91cd\u7684\u9ed1\u6697\u5c31\u50cf\u5de8\u5927\u7684\u602a\u517d\uff0c\u4e00\u70b9\u70b9\u5730\u541e\u566c\u7740\u8fd9\u4e2a\u8352\u829c\u4f4d\u9762\u4e0a\u6700\u6709\u4e00\u62b9\u7eff\u8272\u3002\u9752\u78a7\u7684\u82b3\u8349\u548c\u7f8e\u4e3d\u7684\u9c9c\u82b1\u7a81\u7136\u67af\u840e\u3001\u51cb\u8c22\uff0c\u6811\u6728\u8870\u673d\uff0c\u6e56\u6c34\u5e72\u6db8\uff0c\u6240\u6709\u9c9c\u8273\u660e\u4eae\u7684\u8272\u5f69\u5168\u90fd\u6d88\u5931\uff0c\u552f\u6709\u53e4\u6811\u4f9d\u65e7\u4fdd\u6301\u539f\u72b6\u3002</p><p>\u6811\u4e0b\u4f11\u606f\u7684\u5353\u5c14\u4eec\u6c89\u9ed8\u5730\u7ad9\u7acb\u8d77\u6765\uff0c\u867d\u7136\u4f9d\u65e7\u5168\u8eab\u8d64\u88f8\uff0c\u4f46\u6bcf\u4e2a\u4eba\u7684\u624b\u4e2d\u5374\u591a\u4e86\u5404\u81ea\u7684\u6b66\u5668\u3002\u5bf9\u4e8e\u8fd9\u7a81\u7136\u6765\u4e34\u7684\u53d8\u6545\uff0c\u4ed6\u4eec\u50cf\u662f\u65e9\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u5e73\u9759\u7965\u548c\u7684\u6c14\u6c1b\u5b8c\u5168\u6d88\u5931\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u66b4\u98ce\u9aa4\u96e8\u6765\u4e34\u524d\u7684\u77ed\u6682\u6c89\u5bc2\u3002\u7ad9\u5728\u6811\u51a0\u9876\u7aef\u7684\u5353\u5c14\u5973\u5b50\u53f9\u606f\u7740\uff0c\u5fae\u5fae\u4f4e\u4e0b\u5934\uff0c\u6df1\u84dd\u8272\u7684\u773c\u775b\u91cc\u6ee1\u662f\u54c0\u4f24\u3002</p><p>\u201c\u4f60\u7684\u6bcd\u4eb2\u5b8c\u5168\u590d\u82cf\u4e86\uff0c\u4f0a\u8389\u4e1d\u7fe0\u3002\u201d</p><p>\u4e00\u4e2a\u6e05\u6717\u7684\u58f0\u97f3\u4ece\u80cc\u540e\u4f20\u6765\uff0c\u4f0a\u8389\u4e1d\u7fe0\u6ca1\u6709\u56de\u5934\uff0c\u201c\u662f\u554a\u3002\u201d\u5979\u8bf4\u3002</p><p>\u7eb5\u7136\u662f\u5353\u5c14\uff0c\u5176\u4e2d\u4e5f\u6709\u5584\u826f\u7684\u5b58\u5728\uff0c\u800c\u5e7d\u6697\u5c11\u5973\u4f0a\u8389\u4e1d\u7fe0\u6b63\u662f\u4ed6\u4eec\u7684\u795e\u660e\u3002\u5979\u662f\u7f57\u4e1d\u548c\u7cbe\u7075\u4e3b\u795e\u67ef\u745e\u9686\u6240\u751f\u4e4b\u5973\uff0c\u65e0\u610f\u95f4\u53c2\u4e0e\u6bcd\u4eb2\u53d1\u52a8\u7684\u53db\u4e71\uff0c\u867d\u7136\u88ab\u8d66\u514d\uff0c\u4f46\u5979\u4f9d\u65e7\u575a\u6301\u81ea\u6211\u653e\u9010\u3002\u5979\u5b88\u62a4\u7740\u6240\u6709\u5584\u826f\u7684\u5353\u5c14\uff0c\u5f15\u5bfc\u4ed6\u4eec\u8131\u79bb\u86db\u540e\u7684\u7f57\u7f51\uff0c\u91cd\u8fd4\u5730\u8868\u4e16\u754c\uff0c\u4e5f\u56e0\u6b64\u88ab\u5176\u4ed6\u5353\u5c14\u795e\u7947\u6240\u5171\u540c\u654c\u89c6\u3002\u5f88\u591a\u5730\u8868\u7cbe\u7075\u88ab\u5979\u7684\u6559\u4e49\u6240\u5438\u5f15\uff0c\u6210\u4e3a\u4fe1\u5f92\uff0c\u6240\u4ee5\u8fd9\u4f4d\u795e\u7947\u62e5\u6709\u4e24\u79cd\u4e0d\u540c\u7684\u795e\u76f8\uff1a\u5f53\u5979\u5728\u7cbe\u7075\u56fd\u5ea6\u963f\u68b5\u591a\u51fa\u73b0\u65f6\uff0c\u4f0a\u8389\u4e1d\u7fe0\u662f\u4e00\u4f4d\u7f8e\u4e3d\u7684\u6708\u7cbe\u7075\uff1b\u800c\u5f53\u5979\u4e0d\u5f97\u4e0d\u505c\u7559\u5728\u8718\u86db\u9b54\u7f51\u65f6\uff0c\u5973\u795e\u4f1a\u4ee5\u8d64\u88f8\u7684\u5353\u5c14\u5c11\u5973\u5f62\u8c61\u51fa\u73b0\u3002</p><p>\u6765\u4eba\u8d70\u5230\u8eab\u65c1\uff0c\u671b\u7740\u524d\u65b9\uff0c\u4ed6\u6709\u7740\u707f\u70c2\u8000\u773c\u7684\u91d1\u53d1\u548c\u8679\u5f69\u95ea\u70c1\u7684\u53cc\u773c\uff0c\u5728\u8fd9\u9eef\u6de1\u7684\u4e16\u754c\u91cc\u5c31\u50cf\u592a\u9633\u4e00\u6837\u593a\u76ee\u3002\u201c\u6211\u66fe\u7ecf\u4ee5\u4e3a\u7ef4\u4f26\u4f1a\u5e72\u5f97\u66f4\u6f02\u4eae\u4e9b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u81f3\u5c11\u80fd\u591f\u62d6\u5ef6\u4e00\u6bb5\u65f6\u95f4\u3002\u201d</p><p>\u201c\u90a3\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u745e\u6069\u65af\u5766\uff0c\u201d\u4f0a\u8389\u4e1d\u7fe0\u8bf4\uff0c\u201c\u6211\u6bd4\u4f60\u66f4\u4e86\u89e3\u6211\u7684\u5144\u957f\u3002\u4ed6\u559c\u7231\u9634\u8c0b\uff0c\u4e0d\u62e9\u624b\u6bb5\uff0c\u5374\u7f3a\u4e4f\u5b64\u6ce8\u4e00\u63b7\u7684\u52c7\u6c14\u3002\u5f53\u4ed6\u548c\u5e2d\u6587\u5854\u59c6\u540c\u65f6\u4ece\u86db\u7f51\u5760\u4e0b\u65f6\uff0c\u6211\u5c31\u77e5\u9053\u4ed6\u5f7b\u5e95\u5931\u8d25\u4e86\u3002\u201d</p><p>\u5353\u5c14\u7684\u9634\u8c0b\u4e4b\u795e\u7ef4\u4f26\u540c\u6837\u662f\u7f57\u4e1d\u548c\u67ef\u745e\u9686\u7ed3\u5408\u800c\u8bde\u751f\u7684\u795e\u660e\uff0c\u56e0\u4e3a\u53c2\u4e0e\u6bcd\u4eb2\u7684\u53db\u4e71\u800c\u88ab\u653e\u9010\u3002\u4ed6\u618e\u6068\u6240\u6709\u7684\u5353\u5c14\u795e\u7947\uff0c\u5305\u62ec\u4ed6\u7684\u6bcd\u4eb2\u548c\u59b9\u59b9\u3002\u7ef4\u4f26\u559c\u597d\u79d8\u5bc6\u548c\u9634\u8c0b\uff0c\u540c\u65f6\u6781\u5ea6\u50b2\u6162\u81ea\u8d1f\uff0c\u4ed6\u4e5f\u662f\u6240\u6709\u7537\u6027\u5353\u5c14\u7684\u5b88\u62a4\u795e\uff0c\u53cd\u5bf9\u7f57\u4e1d\u6240\u9886\u5bfc\u7684\u6bcd\u7cfb\u793e\u4f1a\u4f53\u5236\uff0c\u8981\u6c42\u6027\u522b\u5e73\u7b49\u3002\u5728\u7f57\u4e1d\u6c89\u5bc2\u5176\u95f4\uff0c\u7ef4\u4f26\u7b79\u5212\u4e86\u4e00\u7cfb\u5217\u9488\u5bf9\u6bcd\u4eb2\u7684\u9634\u8c0b\uff0c\u751a\u81f3\u4eb2\u81ea\u95ef\u5165\u8718\u86db\u5821\u5792\u653b\u51fb\u86db\u540e\uff0c\u4f46\u6700\u7ec8\u7684\u7ed3\u679c\u662f\u88ab\u5e2d\u6587\u5854\u59c6\uff08\u7ef4\u4f26\u4e4b\u5b50\uff0c\u5353\u5c14\u6218\u58eb\u5b88\u62a4\u795e\uff0c\u7f57\u4e1d\u7684\u5fe0\u5b9e\u4ec6\u4ece\uff09\u6240\u963b\u62e6\uff0c\u6fc0\u6218\u4e2d\u53cc\u53cc\u4ece\u86db\u7f51\u5760\u5165\u65e0\u5c3d\u865a\u7a7a\u3002\u5f53\u7136\uff0c\u8fd9\u5e76\u4e0d\u8db3\u4ee5\u6740\u6b7b\u4e24\u4f4d\u795e\u660e\uff0c\u4f46\u7ef4\u4f26\u7684\u5931\u8d25\u5374\u662f\u663e\u800c\u6613\u89c1\u3002</p><p>\u66f4\u731b\u70c8\u7684\u98ce\u547c\u5578\u800c\u8fc7\uff0c\u6574\u4e2a\u4f4d\u9762\u90fd\u5728\u8d77\u4f0f\u6447\u6643\uff0c\u4e00\u79cd\u7c7b\u4f3c\u706b\u5c71\u55b7\u53d1\u7684\u52a8\u9759\u9065\u9065\u4f20\u6765\uff0c\u745e\u6069\u65af\u5766\u6709\u4e9b\u8be7\u5f02\u5730\u5411\u5357\u9762\u770b\u53bb\uff0c\u76ee\u5149\u7a7f\u900f\u5730\u9762\uff0c\u53d1\u73b0\u5728\u86db\u7f51\u4e4b\u4e0b\uff0c\u4e00\u5757\u5145\u6ee1\u6de4\u6ce5\u548c\u8150\u8d25\u6076\u81ed\u7684\u9ed1\u8272\u6cbc\u6cfd\u6b63\u5728\u731b\u70c8\u5730\u6cdb\u7740\u6c61\u6d4a\u6c14\u6ce1\uff0c\u6447\u6643\u7740\uff0c\u9010\u6e10\u8fdc\u79bb\uff0c\u6ed1\u5165\u4e0b\u65b9\u7684\u65e0\u5c3d\u865a\u7a7a\u3002\u5728\u6cbc\u6cfd\u5f53\u4e2d\uff0c\u4f3c\u4e4e\u6709\u4e00\u4e2a\u5e9e\u5927\u7684\u6ce5\u72b6\u602a\u7269\u5728\u7ffb\u817e\u7740\uff0c\u7f13\u7f13\u4e0b\u6c89\uff0c\u5b83\u7684\u80cc\u4e0a\u6709\u4e00\u53ea\u5de8\u5927\u7684\u7d2b\u8272\u773c\u73e0\u3002</p><p>\u201c\u5173\u7eb3\u5fb7\u9003\u8d70\u4e86\u3002\u201d</p><p>\u201c\u610f\u6599\u4e4b\u4e2d\uff0c\u201d\u4f0a\u8389\u4e1d\u7fe0\u8bf4\uff0c\u201c\u4f5c\u4e3a\u6700\u8fdc\u53e4\u7684\u795e\u660e\uff0c\u80fd\u591f\u5b58\u6d3b\u81f3\u4eca\uff0c\u9760\u7684\u53ef\u4e0d\u4ec5\u4ec5\u662f\u8fd0\u6c14\u3002\u201d</p><p>\u201c\u4e5f\u4e0d\u9519\uff0c\u201d\u745e\u6069\u65af\u5766\u8bf4\uff0c\u4f3c\u4e4e\u662f\u5728\u81ea\u6211\u5b89\u6170\uff0c\u201c\u81f3\u5c11\u4ee5\u540e\u4e0d\u7528\u5fcd\u53d7\u90a3\u91cc\u5439\u6765\u7684\u81ed\u6c14\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4f60\u6050\u6015\u8981\u5931\u671b\u4e86\u3002\u201d</p><p>\u745e\u6069\u65af\u5766\u6709\u4e9b\u4e0d\u89e3\uff0c\u4f46\u5f88\u5feb\u5c31\u77e5\u9053\u4e86\u4f0a\u8389\u4e1d\u7fe0\u8fd9\u4e48\u8bf4\u7684\u539f\u56e0\uff0c\u968f\u7740\u7a7a\u4e2d\u7684\u516b\u4e2a\u7403\u4f53\u53d8\u6210\u5b8c\u5168\u7684\u9ed1\u6d1e\uff0c\u4e4c\u4e91\u5c42\u5c42\u7d2f\u79ef\uff0c\u811a\u4e0b\u7684\u53c2\u5929\u53e4\u6811\u4e5f\u5df2\u7ecf\u5f00\u59cb\u6709\u6240\u53d8\u5316\uff0c\u679d\u53f6\u5e72\u67af\uff0c\u6811\u76ae\u7126\u9ed1\uff0c\u800c\u539f\u672c\u90c1\u90c1\u8471\u8471\u7684\u6811\u6797\u548c\u8349\u5730\uff0c\u6b63\u9010\u6e10\u677e\u8f6f\u8150\u70c2\u4e0b\u53bb\uff0c\u5316\u4f5c\u6ce5\u6cbc\u3002</p><p>\u201c\u7f57\u4e1d\u7684\u795e\u529b\u5df2\u7ecf\u4fb5\u67d3\u4e86\u6574\u4e2a\u4f4d\u9762\u2026\u2026\u73b0\u5728\u6574\u4e2a\u8718\u86db\u9b54\u7f51\uff0c\u90fd\u662f\u5979\u7684\u795e\u57df\u6240\u5728\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u662f\u2026\u2026\u8150\u8d25\u7684\u795e\u529b\uff1f\u201d</p><p>\u745e\u6069\u65af\u5766\u7591\u60d1\u7740\uff0c\u80fd\u591f\u91cd\u65b0\u4e3a\u4f4d\u9762\u5851\u578b\uff0c\u552f\u6709\u795e\u660e\u65b9\u80fd\u529e\u5230\uff0c\u4ece\u8ff9\u8c61\u4e0a\u770b\uff0c\u8fd9\u5206\u660e\u662f\u5145\u6ee1\u8150\u673d\u7684\u529b\u91cf\uff0c\u7136\u800c\u7f57\u4e1d\u4f3c\u4e4e\u5e76\u4e0d\u6267\u638c\u6b64\u7c7b\u795e\u80fd\u3002</p><p>\u201c\u6469\u591a\u5b89\uff0c\u201d\u4f0a\u8389\u4e1d\u7fe0\u8f7b\u58f0\u8bf4\uff0c\u4e3a\u4ed6\u89e3\u9664\u7591\u60d1\uff0c\u201c\u7f57\u4e1d\u83b7\u5f97\u4e86\u6469\u591a\u5b89\u7684\u529b\u91cf\u3002\u201d</p><p>\u6469\u591a\u5b89\u662f\u8fdc\u53e4\u7684\u8150\u673d\u4e4b\u795e\uff0c\u5728\u52a8\u8361\u5e74\u4ee3\u540e\u6b7b\u53bb\uff0c\u90e8\u5206\u795e\u80fd\u88ab\u7f57\u4e1d\u6240\u5438\u6536\uff0c\u82e5\u975e\u5982\u6b64\uff0c\u4e5f\u4e0d\u8db3\u4ee5\u5c06\u6574\u4e2a\u516d\u5341\u516d\u5c42\u81ea\u6df1\u6e0a\u4e2d\u6495\u88c2\u5f00\u6765\uff0c\u6210\u4e3a\u661f\u754c\u4e2d\u7684\u72ec\u7acb\u4f4d\u9762\u3002</p><p>\u201c\u662f\u8fd9\u6837\u554a\u3002\u201d</p><p>\u745e\u6069\u65af\u5766\u4f4e\u58f0\u8bf4\uff0c\u9065\u671b\u7740\u8fdc\u5904\uff0c\u9ed1\u538b\u538b\u7684\u9634\u5f71\u51fa\u73b0\u5728\u5730\u5e73\u7ebf\u4e0a\uff0c\u90a3\u662f\u6570\u4ee5\u4e07\u8ba1\u7684\u8718\u86db\u3002\u5927\u5c0f\u4e0d\u4e00\uff0c\u5f62\u6001\u5404\u5f02\uff0c\u5b83\u4eec\u6325\u52a8\u7740\u516b\u6761\u957f\u957f\u7684\u8282\u80a2\uff0c\u50cf\u6d77\u6d6a\u4e00\u6837\u671d\u8fd9\u8fb9\u6d8c\u6765\uff0c\u8981\u5c06\u8fd9\u5757\u6700\u540e\u7684\u7eff\u6d32\u6df9\u6ca1\u3002</p><p>\u86db\u540e\u7684\u8fdb\u653b\u5f00\u59cb\u4e86\u3002</p></article>"}}}]);
