(window.webpackJsonp=window.webpackJsonp||[]).push([[15],{38:function(p){p.exports={chapterTitle:null,title:"\u7b2c1007\u7ae0 \u75af\u72c2\u7684\u624b\u673a\u724c\u7167",content:"<article><h2>\u7b2c1007\u7ae0 \u75af\u72c2\u7684\u624b\u673a\u724c\u7167</h2><p>\u3000\u3000\u4fe1\u4ea7\u90e8\u8fd9\u6b21\u6253\u7b97\u653e\u5f00\u53e3\u5b50\u7684\u51b3\u5b9a\uff0c\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u4e09\u5bb6\u534e\u590f\u7535\u5b50\u4e0b\u5c5e\u7684\u624b\u673a\u4f01\u4e1a\u627f\u53d7\u7684\u538b\u529b\u6700\u5927\uff0c\u4f46\u662f\u4ed6\u4eec\u4f5c\u4e3a\u4f53\u7cfb\u5185\u7684\u4e00\u5458\uff0c\u662f\u6ca1\u6709\u8d44\u683c\u3001\u4e5f\u6ca1\u6709\u529b\u91cf\u53cd\u6297\u7684\uff0c\u4ed6\u4eec\u5012\u662f\u671f\u5f85\u9526\u6e56\u80fd\u6709\u4ec0\u4e48\u4f5c\u4e3a\u3002</p><p>\u3000\u3000\u5c31\u8096\u745e\u6c11\u6765\u8bf4\uff0c\u4ed6\u66f4\u5e0c\u671b\u80fd\u8ddf\u5f20\u606a\u89c1\u9762\u8c08\u4e00\u6b21\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u8096\u745e\u6c11\u4e0e\u845b\u5efa\u5fb7\u4e4b\u95f4\u7684\u77db\u76fe\u6e10\u6df1\uff0c\u4e5f\u5f88\u53ef\u80fd\u4e0d\u518d\u5f97\u803f\u91cd\u9633\u7684\u6b22\u5fc3\uff0c\u8003\u8651\u518d\u4e09\uff0c\u5e76\u4e0d\u80fd\u7ed9\u8096\u745e\u6c11\u4ec0\u4e48\u627f\u8bfa\uff0c\u5c31\u6ca1\u6709\u89c1\u4ed6\u3002\u5bf9\u4e8e\u8096\u745e\u6c11\u5e0c\u671b\u52a0\u5f3a\u8054\u4fe1\u7684\u6280\u672f\u5408\u4f5c\uff0c\u5f20\u606a\u8fd8\u662f\u7ed9\u4e88\u80af\u5b9a\u7684\u56de\u5e94\uff0c\u5f53\u521d\u5c06\u6574\u5957\u7684\u6570\u5b57\u624b\u673a\u5236\u9020\u6280\u672f\u65e0\u507f\u7684\u62ff\u51fa\u6765\uff0c\u4fc3\u4f7f\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\uff0c\u540e\u6765\u53c8\u9646\u7eed\u5c06\u4e00\u4e9b\u6280\u672f\u7eb3\u5165\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u6846\u67b6\u4e4b\u5185\uff0c\u5c31\u662f\u8981\u6210\u7acb\u56fd\u5185\u624b\u673a\u4ea7\u4e1a\u7684\u6280\u672f\u8054\u76df\u2014\u2014\u5f62\u6210\u8fd9\u6837\u7684\u683c\u5c40\uff0c\u56fd\u4ea7\u624b\u673a\u5382\u5546\u624d\u6709\u8d44\u683c\u8ddf\u5916\u8d44\u5382\u5546\u6297\u8861\u3002</p><p>\u3000\u3000\u4fe1\u4ea7\u90e8\u4f3c\u4e4e\u4e5f\u62c5\u5fc3\u9526\u6e56\u4f1a\u5728\u80cc\u5730\u91cc\u7684\u6363\u9b3c\uff0c\u52a0\u4e0a\u7533\u8bf7\u624b\u673a\u751f\u4ea7\u3001\u9500\u552e\u8bb8\u53ef\u724c\u7167\u7684\u4f01\u4e1a\u5728\u4fe1\u4ea7\u90e8\u95e8\u524d\u6392\u961f\u5df2\u6210\u957f\u9f99\uff0c\u5b9e\u9645\u7684\u8d44\u683c\u8003\u5bdf\u5de5\u4f5c\u4ece\u53bb\u5e74\u5e74\u4e2d\u5c31\u5df2\u7ecf\u5728\u8fdb\u884c\u4e86\uff0c\u6b63\u5f0f\u7684\u5ba1\u6279\u5de5\u4f5c\uff0c\u4ece\u4e09\u6708\u4e2d\u65ec\u5c31\u706b\u901f\u5f00\u5c55\u8d77\u6765\u3002</p><p>\u3000\u3000\u591a\u91cd\u56e0\u7d20\u4fc3\u8fdb\u4fe1\u4ea7\u90e8\u51b3\u5b9a\u653e\u5f00\u4eca\u5e74\u8fdb\u5165\u624b\u673a\u4ea7\u4e1a\u7684\u53e3\u5b50\uff0c\u4e0d\u8fc7\u8fd8\u662f\u540c\u610f\u5bf9\u7533\u8bf7\u4f01\u4e1a\u7684\u6280\u672f\u7533\u6838\u8ba9\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u53c2\u4e0e\u8fdb\u6765\u3002</p><p>\u3000\u3000\u591a\u65b9\u535a\u5f08\u7684\u7ed3\u679c\u5c31\u662f\u5c06\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u4f1a\u5458\u4f01\u4e1a\u4f5c\u4e3a\u7533\u6838\u901a\u8fc7\u7684\u95e8\u69db\u4e4b\u4e00\uff0c\u800c\u6570\u5b57\u624b\u673a\u4fc3\u8fdb\u534f\u4f1a\u653e\u5f00\u5bf9\u4f1a\u5458\u4f01\u4e1a\u7684\u6ce8\u518c\u3001\u6295\u8d44\u5730\u6709\u5730\u57df\u4e0a\u7684\u9650\u5236\u3002</p><p>\u3000\u3000\u6570\u5b57\u624b\u673a\u4fc3\u8fdb\u534f\u4f1a\u5bf9\u4f1a\u5458\u7533\u8bf7\u4e5f\u662f\u6709\u8981\u6c42\u7684\uff0c\u4eca\u5e74\u4e0d\u662f\u6570\u5b57\u624b\u673a\u4fc3\u8fdb\u534f\u4f1a\u4f1a\u5458\u4f01\u4e1a\u7684\u4f01\u4e1a\u57fa\u672c\u4e0a\u6ca1\u6709\u5e0c\u671b\u53c2\u52a0\u624b\u673a\u724c\u7167\u7684\u6700\u7ec8\u5ba1\u6838\uff0c\u4eca\u5e74\u83b7\u5f97\u624b\u673a\u724c\u7167\u7684\u4f01\u4e1a\u6216\u8005\u5df2\u7ecf\u5728\u5efa\u90ba\u6216\u8005\u6d77\u5dde\u6295\u8d44\u5efa\u9020\u4e86\u751f\u4ea7\u57fa\u5730\uff0c\u6216\u8005\u53ea\u6295\u8d44\u6210\u7acb\u7684\u7814\u53d1\u4e2d\u5fc3\uff0c\u8fd9\u6837\u4e5f\u662f\u4fdd\u8bc1\u5efa\u90ba\u4e0e\u6d77\u5dde\u7684\u5730\u65b9\u5229\u76ca\uff0c\u4ece\u73b0\u5b9e\u7684\u89d2\u5ea6\u53bb\u8003\u8651\uff0c\u4e5f\u4e0d\u4f1a\u4e25\u683c\u7684\u8981\u6c42\u6bcf\u4e00\u5bb6\u56fd\u4ea7\u624b\u673a\u4f01\u4e1a\u90fd\u5c06\u751f\u4ea7\u57fa\u5730\u3001\u7814\u53d1\u57fa\u5730\u5efa\u5728\u5efa\u90ba\u6216\u6d77\u5dde\u3002</p><p>\u3000\u3000\u4e5f\u662f\u9526\u6e56\u53ea\u8c0b\u6c42\u5f71\u54cd\u529b\u3001\u6e17\u900f\u529b\uff0c\u4e0d\u8c0b\u6c42\u63a7\u5236\u529b\u7684\u7b56\u7565\uff0c\u4f7f\u5f97\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u534a\u5b98\u65b9\u6027\u8d28\u6b63\u5f0f\u5f97\u5230\u4fe1\u4ea7\u90e8\u7684\u627f\u8ba4\u3002</p><p>\u3000\u3000\u5ba1\u6279\u7684\u901f\u5ea6\u975e\u5e38\u5feb\uff0c\u4e09\u6708\u4e0b\u65ec\uff0c\u7b2c\u4e8c\u6279\u5165\u9009\u4f01\u4e1a\u7684\u540d\u5355\u5c31\u516c\u5e03\u4e86\u51fa\u6765\uff0c\u5171\u5341\u4e00\u5bb6\u3002\u5df2\u7ecf\u62e5\u6709\u5b9e\u9645\u751f\u4ea7\u7ecf\u9a8c\u3001\u6280\u672f\u7814\u53d1\u57fa\u7840\uff0c\u6ce8\u518c\u8d44\u91d1\u8fbe\u5230\u4e24\u4ebf\uff0c\u53c8\u662f\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7406\u4e8b\u4f1a\u5458\u4f01\u4e1a\u7684\u79d1\u738b\u9ad8\u79d1\u5f53\u7136\u540d\u5217\u5176\u4e2d\uff0c\u4e09\u6708\u4e8c\u5341\u516d\u65e5\u6b63\u5f0f\u62ff\u5230\u8ba1\u5212\u53d1\u5c55\u59d4\u4e0e\u4fe1\u4ea7\u90e8\u7684\u8054\u5408\u6279\u6587\uff0c\u8054\u60f3\u3001\u957f\u8679\u3001\u6d77\u5c14\u7b49\u56fd\u5185\u77e5\u540d\u7535\u5b50\u3001\u5bb6\u7535\u4f01\u4e1a\u4e5f\u540d\u5217\u5176\u4e2d\uff0c\u4e0e\u5f20\u606a\u9884\u6599\u7684\u4e00\u6837\u3002\u9664\u4e86\u79d1\u738b\u9ad8\u79d1\u4e4b\u5916\uff0c\u5176\u4ed6\u5341\u5bb6\u4f01\u4e1a\u90fd\u4e3a\u90e8\u5c5e\u6216\u5730\u65b9\u56fd\u6709\u4f01\u4e1a\u3002</p><p>\u3000\u3000\u4e0e\u5176\u4ed6\u5341\u5bb6\u83b7\u5f97\u624b\u673a\u724c\u7167\u7684\u4f01\u4e1a\u4e0d\u540c\uff0c\u79d1\u738b\u9ad8\u79d1\u5b9e\u9645\u4e0a\u5df2\u7ecf\u62e5\u6709\u624b\u673a\u751f\u4ea7\u57fa\u5730\u3001\u7814\u53d1\u4e2d\u5fc3\u3001\u9500\u552e\u7f51\u7edc\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u79d1\u738b\u9ad8\u79d1\u4e5f\u5b9e\u9645\u62e5\u6709\u624b\u673a\u4ea7\u54c1\u3002</p><p>\u3000\u3000\u4ece\u4e5d\u4e03\u5e74\u4e0b\u534a\u5e74\u622a\u6b62\u5230\u4e5d\u4e5d\u5e74\u4e09\u6708\u4e4b\u524d\uff0c\u79d1\u738b\u9ad8\u79d1\u603b\u5171\u4e3a\u8054\u4fe1\u8d34\u724c\u751f\u4ea7\u4e86\u56db\u6b3e\u624b\u673a\uff0c\u867d\u7136\u4e4b\u524d\u4e0e\u8054\u4fe1\u7b7e\u7f72\u4e86\u8865\u5145\u534f\u8bae\uff0c\u5728\u8fd9\u56db\u6b3e\u624b\u673a\u81ea\u7136\u6dd8\u6c70\u51fa\u5e02\u573a\u4e4b\u524d\uff0c\u8fd8\u7531\u79d1\u738b\u9ad8\u79d1\u8d34\u724c\u751f\u4ea7\u9500\u552e\uff0c\u4f46\u662f\u79d1\u738b\u9ad8\u79d1\u5728\u62ff\u5230\u6279\u6587\u7684\u7b2c\u4e8c\u5929\uff0c\u5c31\u5411\u6700\u8fd1\u7684\u534e\u4e1c\u5e02\u573a\u6295\u5165\u5230\u4ed6\u4eec\u7684\u4e24\u6b3e\u624b\u673a\u2014\u2014\u53ea\u662f\u5c06\u56db\u6b3e\u8d34\u724c\u624b\u673a\u4e2d\u6700\u6210\u529f\u7684\u4e24\u6b3e\u62ff\u51fa\u6765\u7a0d\u52a0\u6539\u8fdb\u800c\u5df2\uff0c\u8054\u4fe1\u867d\u6709\u610f\u89c1\uff0c\u5374\u4e5f\u4e0d\u80fd\u63d0\u51fa\u6765\u3002</p><p>\u3000\u3000\u5176\u4ed6\u5341\u5bb6\u83b7\u5f97\u624b\u673a\u724c\u7167\u7684\u4f01\u4e1a\u6216\u5728\u5efa\u90ba\u3001\u6d77\u5dde\u5efa\u7acb\u96f6\u914d\u4ef6\u751f\u4ea7\u57fa\u5730\uff0c\u6216\u6210\u7acb\u7684\u7814\u53d1\u4e2d\u5fc3\uff0c\u7531\u4e8e\u5f53\u521d\u9526\u6e56\u4e0e\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u8054\u5408\u8d77\u6765\u5bf9\u8d34\u724c\u884c\u4e3a\u7684\u9650\u5236\uff0c\u8fd9\u5341\u5bb6\u4f01\u4e1a\u90fd\u6ca1\u6709\u8d34\u724c\u751f\u4ea7\u624b\u673a\u7684\u7ecf\u9a8c\uff0c\u62ff\u5230\u6279\u6587\u4e4b\u540e\uff0c\u6700\u7d27\u8981\u7684\u8981\u5c06\u751f\u4ea7\u57fa\u5730\u5148\u5efa\u8bbe\u8d77\u6765\u3002</p><p>\u3000\u3000\u7531\u4e8e\u9526\u6e56\u7684\u52aa\u529b\uff0c\u5efa\u90ba\u5b9e\u9645\u5df2\u7ecf\u6210\u4e3a\u56fd\u5185\u6570\u5b57\u624b\u673a\u6280\u672f\u7814\u53d1\u7684\u4e2d\u5fc3\uff0c\u6d77\u5dde\u53c8\u662f\u5f53\u524d\u56fd\u5185\u6570\u5b57\u624b\u673a\u7684\u6700\u5927\u4ea7\u4e1a\u57fa\u5730\uff0c\u4e5d\u516b\u5e74\u6709\u8d85\u8fc760\uff05\u7684\u56fd\u4ea7\u624b\u673a\u4ece\u6d77\u5dde\u6d41\u5411\u5e02\u573a\u3002</p><p>\u3000\u3000\u83b7\u5f97\u724c\u7167\u7684\u5341\u5bb6\u4f01\u4e1a\uff0c\u9664\u4e86\u8054\u60f3\u51b3\u5b9a\u5c06\u7814\u53d1\u4e2d\u5fc3\u8fc1\u56de\u5317\u4eac\u3001\u6d77\u5c14\u51b3\u5b9a\u5c06\u7814\u53d1\u4e2d\u5fc3\u8fc1\u56de\u9752\u5c9b\u5916\uff0c\u79d1\u5065\u7b49\u5176\u4ed6\u516b\u5bb6\u4f01\u4e1a\u90fd\u51b3\u5b9a\u5728\u5efa\u90ba\u6269\u5927\u5bf9\u7814\u53d1\u4e2d\u5fc3\u7684\u6295\u5165\uff0c\u4f7f\u5efa\u90ba\u771f\u6b63\u7684\u53d1\u5c55\u6210\u6570\u5b57\u624b\u673a\u6280\u672f\u7684\u4e2d\u5fc3\u3002</p><p>\u3000\u3000\u4ece\u4e09\u6708\u4e0b\u65ec\u5230\u56db\u6708\u4e0a\u65ec\uff0c\u5171\u6709\u4e09\u5bb6\u4f01\u4e1a\u51b3\u5b9a\u5728\u5efa\u90ba\u3001\u4e24\u5bb6\u4f01\u4e1a\u51b3\u5b9a\u5728\u6d77\u5dde\u6295\u8d44\u5efa\u9020\u751f\u4ea7\u57fa\u5730\uff0c\u53e6\u4e94\u5bb6\u83b7\u5f97\u724c\u7167\u4f01\u4e1a\u5206\u522b\u51b3\u5b9a\u5c06\u751f\u4ea7\u57fa\u5730\u5efa\u5728\u5317\u4eac\u3001\u9752\u5c9b\u3001\u6df1\u5733\u3001\u5929\u6d25\u7b49\u5730\u3002</p><p>\u3000\u3000\u4e5f\u96be\u602a\u8fd9\u4e9b\u4f01\u4e1a\u5728\u62ff\u5230\u6279\u6587\u540e\u5c31\u8feb\u4e0d\u53ca\u5f85\u7684\u4e0a\u751f\u4ea7\u7ebf\uff0c\u4e5d\u4e5d\u5e74\u7684\u7535\u5b50\u5de5\u4e1a\u767e\u5f3a\u540d\u5355\u8d76\u5728\u4e09\u6708\u4e2d\u65ec\u95ee\u4e16\uff0c\u534e\u590f\u7535\u5b50\u4ee5\u5e74\u9500\u552e\u989d204\u4ebf\u540d\u5217\u699c\u9996\u3002\u534e\u590f\u7535\u5b50\u65d7\u4e0b\u7684\u8054\u4fe1\u7535\u5b50\u7684\u624b\u673a\u4e1a\u52a1\u4e3a\u5176\u8d21\u732e\u4e8642\u4ebf\u7684\u9500\u552e\u989d\u30014.63\u4ebf\u7684\u51c0\u5229\u6da6\uff0c\u8054\u8baf\u7535\u5b50\u7684\u624b\u673a\u4e1a\u52a1\u4e3a\u5176\u8d21\u732e32\u4ebf\u7684\u9500\u552e\u989d\u30013.2\u4ebf\u7684\u51c0\u5229\u6da6\uff0c\u4e1c\u5174\u901a\u8baf\u7684\u624b\u673a\u4e1a\u52a1\u4e3a\u5176\u8d21\u732e\u4e8628\u4ebf\u7684\u9500\u552e\u989d\u30012.9\u4ebf\u7684\u51c0\u5229\u6da6\uff0c\u8981\u77e5\u9053\u8fd9\u4e09\u5bb6\u4f01\u4e1a\u5728\u624b\u673a\u4e1a\u52a1\u4e0a\u7684\u6295\u8d44\u603b\u989d\u751a\u81f3\u90fd\u4e0d\u8d85\u8fc7\u516d\u4ebf\u3002\u7231\u8fbe\u96c6\u56e2\u4ee5\u5e74\u9500\u552e\u989d101\u4ebf\u540d\u5217\u767e\u5f3a\u699c\u7b2c\u4e09\uff0c\u624b\u673a\u4e1a\u52a1\u4e3a\u5176\u8d21\u732e\u4e8690\u4ebf\u7684\u9500\u552e\u989d\uff0c\u867d\u7136\u4e1a\u52a1\u660e\u773c\u4eba\u90fd\u77e5\u9053\u7231\u8fbe\u96c6\u56e2\u7684\u6570\u636e\u662f\u5728\u778e\u626f\uff0c\u5373\u4f7f\u7ec8\u7aef\u4ea7\u54c1\u9500\u552e\u989d\u91cc\uff0c\u90fd\u6ca1\u6709\u5c06\u5f71\u789f\u673a\u4e1a\u52a1\u5408\u5e76\u8fdb\u53bb\uff0c\u53e6\u5916\uff0c\u767e\u5f3a\u699c\u867d\u7136\u4e0d\u4f1a\u516c\u544a\u5229\u6da6\uff0c\u4f46\u662f\u4e1a\u5185\u8fd8\u662f\u4e0d\u96be\u77e5\u9053\u7231\u8fbe\u96c6\u56e2\u4e5d\u516b\u5e74\u5728\u624b\u673a\u4e1a\u52a1\u4e0a\u83b7\u5f97\u4e86\u5341\u516b\u4ebf\u7684\u51c0\u5229\u6da6\uff0c\u867d\u7136\u4e5f\u8fd9\u4e5f\u662f\u4e00\u4efd\u88ab\u7f29\u6c34\u7684\u6570\u636e\uff0c\u4f46\u662f\u9664\u4e86\u6280\u672f\u5f15\u8fdb\u3001\u6280\u672f\u7814\u53d1\u4e0a\u7684\u6295\u8d44\uff0c\u7231\u8fbe\u96c6\u56e2\u5728\u5176\u4ed6\u624b\u673a\u9879\u76ee\u4e0a\u7684\u603b\u6295\u8d44\u4e5f\u53ea\u6709\u516d\u4ebf\u5de6\u53f3\u3002\u4e5d\u516b\u5e74\uff0c\u624b\u673a\u5e02\u573a\u53ef\u4ee5\u8bf4\u662f\u771f\u6b63\u7684\u66b4\u5229\uff0c\u8d44\u672c\u6536\u76ca\u6bd4\u751a\u81f3\u8fbe\u5230200\uff05\u3002</p><p>\u3000\u3000\u7b2c\u4e8c\u6279\u83b7\u5f97\u624b\u673a\u724c\u7167\u7684\u4f01\u4e1a\uff0c\u5bf9\u56fd\u5185\u624b\u673a\u5e02\u573a\u7684\u4fe1\u5fc3\u4e5f\u4e0d\u5f31\u3002\u7a0d\u5927\u80c6\u4e00\u4e9b\u7684\u5e02\u573a\u5206\u6790\u62a5\u544a\u8ba4\u4e3a\u56fd\u5185\u624b\u673a\u5e02\u573a\u4e5d\u4e5d\u5e74\u603b\u9500\u91cf\u80af\u5b9a\u80fd\u7a81\u7834\u4e09\u5343\u4e8c\u767e\u4e07\u53ea\uff0c\u5230\u96f6\u96f6\u5e74\uff0c\u5c06\u7a81\u7834\u516d\u5343\u4e94\u767e\u4e07\u53ea\uff0c\u5c4a\u65f6\uff0c\u56fd\u4ea7\u624b\u673a\u5e02\u573a\u5360\u6709\u7387\u5e94\u572840-50\uff05\u4e4b\u95f4\u5f98\u5f8a\uff0c\u6bd4\u8d77\u4e5d\u516b\u5e74\u4e09\u767e\u516b\u5341\u4e07\u7684\u56fd\u4ea7\u624b\u673a\u4ea7\u91cf\uff0c\u4e24\u5e74\u65f6\u95f4\u5185\u91cc\uff0c\u56fd\u5185\u624b\u673a\u5382\u5546\u5c31\u5c06\u83b7\u5f97\u4e03\u767e\u4ebf\u5230\u516b\u767e\u4e94\u5341\u4ebf\u7684\u5e02\u573a\u589e\u957f\u7a7a\u95f4\u3002</p><p>\u3000\u3000\u5982\u6b64\u4e4b\u9ad8\u7684\u6536\u76ca\u6bd4\uff0c\u5982\u6b64\u4e4b\u5e7f\u9614\u7684\u5e02\u573a\u589e\u957f\u7a7a\u95f4\uff0c\u83b7\u5f97\u724c\u7167\u7684\u4f01\u4e1a\u751a\u81f3\u8ba4\u4e3a\u5efa\u5382\u63a8\u8fdf\u4e00\u5929\u5c31\u7b49\u82e5\u4e8e\u4e8f\u635f\u4e0a\u767e\u4e07\u7684\u5229\u6da6\uff0c\u52a8\u4f5c\u5982\u4f55\u4e0d\u6025\u5207\uff1f\u7279\u522b\u662f\u540c\u4e00\u6279\u83b7\u5f97\u724c\u7167\u7684\u79d1\u738b\u9ad8\u79d1\u51e0\u4e4e\u662f\u5728\u62ff\u5230\u6279\u6587\u7684\u540c\u65f6\u5c31\u63a8\u51fa\u4ed6\u4eec\u7684\u624b\u673a\u4ea7\u54c1\u3002</p><p>\u3000\u3000\u7b2c\u4e8c\u6279\u5341\u4e00\u5f20\u724c\u7167\u8fdc\u8fdc\u4e0d\u591f\uff0c\u6709\u4e9b\u6ca1\u6709\u83b7\u5f97\u624b\u673a\u724c\u7167\u7684\u4f01\u4e1a\u751a\u81f3\u544a\u72b6\u544a\u5230\u5206\u7ba1\u526f\u603b\u7406\u3001\u56fd\u52a1\u59d4\u5458\u90a3\u91cc\u53bb\u4e86\u3002</p><p>\u3000\u3000\u9664\u4e86\u5efa\u5382\u623f\u3001\u5f15\u8fdb\u751f\u4ea7\u7ebf\u4e4b\u5916\uff0c\u62db\u52df\u6709\u7ecf\u9a8c\u7684\u751f\u4ea7\u7ba1\u7406\u4eba\u5458\u4e0e\u5458\u5de5\u4e5f\u662f\u8fd9\u4e9b\u4f01\u4e1a\u5f53\u524d\u6700\u7d27\u8981\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u6bd4\u8d77\u5317\u4eac\u7684\u8054\u60f3\u3001\u8054\u8baf\uff0c\u6d59\u4e1c\u6587\u821f\u7684\u4e1c\u5174\uff0c\u9526\u6e56\u5728\u4e24\u5e74\u65f6\u95f4\u91cc\u4e3a\u56fd\u5185\u57f9\u517b\u4e86\u7b2c\u4e00\u6279\u771f\u6b63\u610f\u4e49\u4e0a\u7684\u6570\u5b57\u624b\u673a\u6280\u672f\u7814\u53d1\u4e0e\u751f\u4ea7\u7ba1\u7406\u9886\u57df\u7684\u4e13\u4e1a\u4eba\u624d\uff0c\u800c\u4e14\u9526\u6e56\u4eba\u4e8b\u90e8\u95e8\u4e00\u76f4\u90fd\u5728\u4e3a\u6269\u4ea7\u505a\u51c6\u5907\uff0c\u4e00\u76f4\u5728\u6e90\u6e90\u4e0d\u65ad\u7684\u62db\u8058\u5458\u5de5\u3001\u57f9\u8bad\u5458\u5de5\u3002</p><p>\u3000\u3000\u4e0d\u53ef\u907f\u514d\u7684\uff0c\u5728\u4e09\u6708\u4e2d\u65ec\u4e4b\u540e\uff0c\u9526\u6e56\u624b\u673a\u90e8\u95e8\u7684\u4e13\u4e1a\u4eba\u624d\u5f00\u59cb\u5927\u91cf\u7684\u4ece\u9526\u6e56\u6d41\u5931\u3002</p><p>\u3000\u3000\u4ee4\u4eba\u53ef\u6068\u7684\u662f\uff0c\u591a\u5bb6\u83b7\u5f97\u624b\u673a\u724c\u7167\u7684\u4f01\u4e1a\u7adf\u7136\u5728\u6d77\u5dde\u5de5\u4e1a\u65b0\u533a\u3001\u5728\u9526\u6e56\u624b\u673a\u5de5\u5382\u7684\u8857\u5bf9\u9762\u79df\u529e\u516c\u5ba4\u6210\u7acb\u4e13\u95e8\u7684\u62db\u8058\u673a\u6784\uff0c\u5468\u672b\u751a\u81f3\u5c06\u4e13\u573a\u62db\u8058\u4f1a\u5f00\u8bbe\u5230\u9526\u6e56\u624b\u673a\u5de5\u5382\u7684\u5927\u95e8\u524d\u3002</p><p>\u3000\u3000\u7279\u6b8a\u65f6\u671f\uff0c\u9c81\u5e86\u751f\u4e0d\u5f97\u4e0d\u4eb2\u81ea\u6293\u4e8b\u4e1a\u90e8\u7684\u4eba\u4e8b\u5de5\u4f5c\uff0c\u4f46\u662f\u6bcf\u5929\u90fd\u80fd\u6536\u5230\u5341\u51e0\u4e8c\u5341\u4efd\u8f9e\u804c\u62a5\u544a\u4e66\u7684\u9c81\u5e86\u751f\u6068\u4e0d\u5f97\u64cd\u8d77\u5bb6\u4f19\u53bb\u7838\u573a\u5b50\u3002</p><p>\u3000\u3000\u4eba\u5f80\u9ad8\u5904\u8d70\uff0c\u6c34\u5f80\u5730\u5904\u6d41\uff0c\u522b\u7684\u4f01\u4e1a\u80fd\u63d0\u4f9b\u4e24\u500d\u4e09\u500d\u7684\u85aa\u6c34\uff0c\u9526\u6e56\u4e5f\u4e0d\u80fd\u5c06\u5458\u5de5\u5f3a\u7559\u4e0b\u6765\uff0c\u4e5f\u4e0d\u53ef\u80fd\u6bcf\u4e2a\u5458\u5de5\u9012\u4ea4\u8f9e\u804c\u62a5\u544a\u5c31\u7ed9\u4ed6\u52a0\u5de5\u8d44\u3001\u63d0\u5347\u5230\u66f4\u5173\u952e\u7684\u5c97\u4f4d\u3002</p><p>\u3000\u3000\u9526\u6e56\u867d\u7136\u7ed9\u666e\u901a\u5458\u5de5\u7684\u85aa\u8d44\u5f85\u9047\u5728\u6d77\u5dde\u5730\u533a\u76f8\u5bf9\u8f83\u9ad8\uff0c\u4f46\u4e0e\u5317\u4eac\u3001\u6df1\u5733\u7b49\u5730\u533a\u76f8\u6bd4\uff0c\u8fd8\u8981\u5dee\u4e00\u622a\uff0c\u7279\u522b\u662f\u8fd9\u4e9b\u4f01\u4e1a\u5bf9\u4e13\u4e1a\u4eba\u624d\u6781\u4e3a\u6e34\u6c42\uff0c\u9664\u4e86\u9ad8\u5de5\u8d44\u4e4b\u5916\uff0c\u9526\u6e56\u4e00\u540d\u666e\u901a\u5458\u5de5\u8df3\u69fd\u8fc7\u53bb\uff0c\u51e0\u4e4e\u5c31\u80fd\u5230\u7ba1\u7406\u5c97\u4f4d\u4e0a\u53bb\uff0c\u8fd9\u662f\u975e\u5e38\u6709\u8bf1\u60d1\u529b\u7684\u3002</p><p>\u3000\u3000\u9488\u5bf9\u8fd9\u4e00\u73b0\u8c61\uff0c\u5f20\u606a\u4e5f\u4e13\u95e8\u53ec\u96c6\u7231\u8fbe\u96c6\u56e2\u9ad8\u5c42\u7ba1\u7406\u4eba\u5458\u5f00\u4f1a\u5546\u8bae\uff0c\u8981\u6c42\u9c81\u5e86\u751f\u4e0e\u96c6\u56e2\u7684\u4eba\u4e8b\u90e8\u95e8\u79ef\u6781\u8010\u5fc3\u7684\u505a\u5458\u5de5\u7684\u601d\u60f3\u5de5\u4f5c\uff0c\u5bf9\u90a3\u4e9b\u94c1\u4e86\u5fc3\u8981\u8f9e\u804c\u7684\u5458\u5de5\u4e5f\u4e0d\u8981\u989d\u5916\u7684\u8bbe\u7f6e\u4ec0\u4e48\u969c\u788d\uff0c\u8fd9\u65f6\u5019\u8bb2\u4e00\u4e0b\u6c14\u5ea6\u3001\u8bb2\u4e00\u4e0b\u98ce\u683c\uff0c\u8fd8\u662f\u80fd\u7ef4\u62a4\u957f\u8fdc\u5229\u76ca\u7684\u3002</p><p>\u3000\u3000\u4e09\u6708\u4e2d\u65ec\u5230\u56db\u6708\u4e0b\u65ec\u4e00\u4e2a\u591a\u6708\u7684\u65f6\u95f4\u91cc\uff0c\u9526\u6e56\u5728\u6d77\u5dde\u7684\u624b\u673a\u5de5\u5382\u5458\u5de5\u6d41\u5931\u7387\u7adf\u7136\u8d85\u8fc720\uff05\u3002\u552f\u4e00\u8ba9\u4eba\u5bbd\u6170\u7684\u662f\uff0c\u9aa8\u5e72\u5458\u5de5\u867d\u7136\u4e5f\u6709\u6d41\u5931\uff0c\u4f46\u8981\u4f4e\u5f97\u591a\uff0c\u6bd5\u7adf\u9526\u6e56\u7ed9\u8fd9\u4e9b\u9aa8\u5e72\u5458\u5de5\u63d0\u4f9b\u7684\u85aa\u8d44\u3001\u798f\u5229\u7b49\u5f85\u9047\u90fd\u76f8\u5bf9\u4e30\u539a\uff0c\u800c\u662f\u9526\u6e56\u7684\u53d1\u5c55\uff0c\u8fd9\u4e9b\u9aa8\u5e72\u5458\u5de5\u4e5f\u76f8\u5bf9\u80fd\u770b\u5f97\u66f4\u6e05\u695a\u4e00\u4e9b\u3002\u80fd\u62ff\u5230\u80a1\u6743\u6fc0\u52b1\u7684\u7ba1\u7406\u4e2d\u5c42\u6d41\u5931\u7387\u66f4\u4f4e\uff0c\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u91cc\u624d\u6709\u4e94\u4eba\u9012\u4ea4\u8f9e\u62a5\u544a\uff0c\u5176\u4e2d\u8fd8\u6709\u4e24\u4eba\u662f\u5f97\u5230\u9526\u6e56\u9ed8\u8ba4\u53c2\u5165\u79d1\u738b\u9ad8\u79d1\u7684\uff0c\u8fd9\u4e24\u4eba\u867d\u7136\u5728\u5408\u540c\u671f\u5185\u8f9e\u804c\uff0c\u4f46\u662f\u8fd8\u4fdd\u7559\u6709\u4e4b\u524d\u7684\u6fc0\u52b1\u80a1\u6743\uff0c\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u5176\u4ed6\u4e09\u540d\u4e2d\u5c42\u8df3\u69fd\u79bb\u5f00\u9526\u6e56\u5c31\u80fd\u5230\u5176\u4ed6\u4f01\u4e1a\u62c5\u4efb\u4e8b\u4e1a\u90e8\u7684\u526f\u603b\u7ecf\u7406\u3002</p><p>\u3000\u3000\u4e3a\u6b64\uff0c\u9526\u6e56\u4eba\u4e8b\u90e8\u95e8\u4e0d\u5f97\u4e0d\u4e34\u65f6\u589e\u52a0\u4e09\u767e\u4eba\u7684\u62db\u8058\u8ba1\u5212\u3002</p></article>"}}}]);
