(window.webpackJsonp=window.webpackJsonp||[]).push([[1504],{1527:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e8c\u8282 \u7f6a\u72af",content:"<article><h2>\u7b2c\u5341\u4e8c\u8282 \u7f6a\u72af</h2><p>\u795e\u4f1a\u6b7b\u4ea1\uff0c\u51e1\u4eba\u4e5f\u53ef\u80fd\u4e0d\u673d\u3002</p><p>\u8fd9\u662f\u6d41\u884c\u4e8e\u56fd\u5ea6\u4e2d\u7684\u4e24\u53e5\u8bdd\uff0c\u636e\u8bf4\u51fa\u81ea\u67d0\u4e2a\u5927\u8d24\u8005\u4e4b\u53e3\uff0c\u8fd1\u4e4e\u662f\u65e0\u4eba\u4e0d\u77e5\u3002\u4e8b\u5b9e\u4e0a\uff0c\u51e1\u4eba\u80fd\u5426\u4e0d\u673d\uff0c\u8fd9\u70b9\u6682\u65f6\u65e0\u53ef\u8003\u8bc1\uff0c\u5982\u679c\u628a\u5deb\u5996\u3001\u5438\u8840\u9b3c\u8fd9\u79cd\u4e5f\u7b97\u4e0a\uff0c\u90a3\u4e48\u5012\u4e5f\u52c9\u5f3a\u53ef\u8bf4\uff0c\u53e6\u5916\u50cf\u9634\u9b42\u57ce\u4e3b\u548c\u4ed6\u7684\u513f\u5b50\u4eec\uff0c\u4f3c\u4e4e\u4e5f\u90fd\u662f\u6d3b\u4e86\u8fd1\u4e24\u5343\u5e74\u8fd8\u6ca1\u6b7b\uff0c\u8bf4\u4e0d\u5b9a\u662f\u638c\u63e1\u4e86\u4ec0\u4e48\u957f\u751f\u4e0d\u8001\u7684\u6cd5\u95e8\uff0c\u4e5f\u672a\u53ef\u77e5\u3002</p><p>\u4f46\u795e\u786e\u5b9e\u662f\u4f1a\u6b7b\u4ea1\u7684\u3002</p><p>\u6885\u83f2\u65af\u66fe\u7ecf\u8bf4\uff0c\u795e\u7947\u662f\u534a\u4eba\u683c\u534a\u89c4\u5219\uff0c\u534a\u7406\u5ff5\u534a\u4fe1\u4ef0\u7684\u5b58\u5728\uff0c\u6c38\u5b58\u800c\u4e0d\u673d\uff0c\u65e0\u6cd5\u771f\u6b63\u88ab\u6740\u6b7b\u3002\u4f46\u8fd9\u6d89\u53ca\u5230\u4e00\u4e2a\u540d\u8bcd\u8fa8\u6790\u7684\u95ee\u9898\uff0c\u4e3e\u6700\u7b80\u5355\u7684\u4f8b\u5b50\uff0c\u81ea\u6709\u53f2\u7c4d\u8bb0\u8f7d\uff0c\u6709\u6587\u660e\u4f20\u627f\u4ee5\u6765\uff0c\u6b7b\u4ea1\u4e4b\u795e\u90fd\u662f\u4e00\u76f4\u5b58\u5728\u7684\u2014\u2014\u4f46\u5750\u5728\u795e\u4f4d\u4e4b\u4e0a\u7684\u4eba\uff0c\u5374\u5df2\u7ecf\u6362\u4e86\u4e09\u6b21\u3002</p><p>\u6b7b\u4ea1\u4e4b\u795e\u662f\u6c38\u5b58\u800c\u4e0d\u673d\uff0c\u4f46\u7c73\u5c14\u5bc7\u5374\u5df2\u7ecf\u5728\u52a8\u8361\u5e74\u4ee3\u4e2d\u88ab\u6467\u6bc1\u4e86\u3002\u5f53\u7136\uff0c\u4ed6\u4e5f\u6709\u53ef\u80fd\u50cf\u73ed\u6069\u4e00\u6837\u590d\u6d3b\uff0c\u90a3\u662f\u540e\u8bdd\u3002\u6bd5\u7adf\u8fd9\u662f\u4e2a\u795e\u5947\u7684\u4e16\u754c\uff0c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6709\u53ef\u80fd\u53d1\u751f\u3002</p><p>\u5173\u4e8e\u8fd9\u4e2a\u95ee\u9898\uff0c\u6885\u83f2\u65af\u66fe\u7ecf\u6709\u4e00\u4e2a\u6bd4\u55bb\uff1a\u201c\u795e\u804c\u5c31\u50cf\u662f\u4e00\u4e2a\u738b\u5ea7\u2014\u2014\u5f53\u7136\u4e5f\u6709\u53ef\u80fd\u662f\u591a\u4e2a\u738b\u5ea7\u7684\u878d\u5408\uff0c\u4f60\u80fd\u591f\u5750\u4e0a\u53bb\uff0c\u81ea\u7136\u5c31\u6743\u67c4\u5728\u63e1\uff0c\u62e5\u6709\u795e\u529b\uff0c\u90a3\u4e48\u4f60\u5c31\u662f\u795e\u7947\u4e86\u3002\u201d</p><p>\u738b\u5ea7\u662f\u5f88\u96be\u88ab\u7838\u788e\u9500\u6bc1\u7684\uff0c\u4f46\u672a\u5fc5\u4e0d\u80fd\u878d\u5408\uff0c\u4e0d\u80fd\u62c6\u5206\uff0c\u4e0d\u80fd\u8f6c\u79fb\u2014\u2014\u6700\u5173\u952e\u7684\u4e00\u70b9\u662f\uff1a\u738b\u5ea7\u4e0a\u7684\u4eba\u672a\u5fc5\u4e0d\u4f1a\u6b7b\u3002</p><p>\u800c\u4e14\uff0c\u7b2c\u56db\u7eaa\u5feb\u8981\u5230\u4e86\u5462\u3002</p><p>\u5c31\u5728\u8fc7\u53bb\u7684\u77ed\u77ed\u51e0\u4e2a\u6708\u91cc\uff0c\u5df2\u7ecf\u6709\u5f88\u591a\u795e\u660e\u88ab\u6bc1\u706d\uff1a\u517d\u4eba\u8bf8\u795e\u3001\u7ef4\u4f26\u3001\u9f50\u96c5\u6e29\u7eb1\u4e3d\uff0c\u5f53\u7136\u4e5f\u6709\u590d\u6d3b\u7684\uff0c\u4f8b\u5982\u73ed\u6069\u3002\u743c\u6069\u5c31\u7b97\u518d\u8fdf\u949d\uff0c\u4e5f\u77e5\u9053\u5982\u4eca\u6240\u5904\u65f6\u4ee3\uff0c\u53ea\u6015\u662f\u5343\u5e74\u672a\u6709\u7684\u5927\u53d8\u5c40\u3002\u5982\u679c\u660e\u5929\u4f20\u6765\u6d88\u606f\uff0c\u8bf4\u7f57\u4e1d\u7a81\u7136\u88ab\u6467\u6bc1\u4e86\uff0c\u90a3\u4e48\u743c\u6069\u4e5f\u672a\u5fc5\u4f1a\u591a\u4e48\u60ca\u8bb6\uff0c\u5f53\u7136\u8fd9\u79cd\u53ef\u80fd\u6027\u4e0d\u9ad8\u5c31\u662f\u4e86\u3002\u6bd5\u7adf\u5982\u4eca\u7f57\u4e1d\u7684\u5bf9\u624b\u6b7b\u4ea1\u7684\u6b7b\u4ea1\uff0c\u6c89\u7720\u7684\u6c89\u7720\uff0c\u9003\u79bb\u7684\u9003\u79bb\uff0c\u5979\u5df2\u7ecf\u7b49\u4e8e\u662f\u7edf\u4e00\u4e86\u5353\u5c14\u795e\u7cfb\uff0c\u6682\u65f6\u6ca1\u6709\u4ec0\u4e48\u80fd\u591f\u5a01\u80c1\u5230\u5979\u7684\u5bf9\u624b\u4e86\u3002</p><p>\u4e0d\uff0c\u672a\u5fc5\u2026\u2026\u591c\u5973\u58eb\uff1f</p><p>\u5982\u4eca\u5e7d\u6697\u5730\u57df\u91cc\u6700\u5927\u7684\u4e24\u80a1\u52bf\u529b\uff0c\u4e00\u65b9\u662f\u7f57\u4e1d\u6559\u4f1a\uff0c\u4e00\u65b9\u4fbf\u662f\u9634\u9b42\u57ce\uff0c\u800c\u9634\u9b42\u57ce\u7684\u80cc\u540e\uff0c\u5219\u662f\u591c\u5973\u58eb\u838e\u5c14\u3002\u5982\u4eca\u7684\u5c40\u52bf\u6765\u770b\uff0c\u53cc\u65b9\u548c\u5e73\u5171\u5904\u662f\u4e0d\u592a\u53ef\u80fd\uff0c\u800c\u51e1\u4eba\u7684\u4e89\u6597\uff0c\u5fc5\u5b9a\u53c8\u5173\u8054\u5230\u795e\u660e\u7684\u5174\u8870\u3002</p><p>\u4ece\u8fd9\u70b9\u6765\u8bf4\uff0c\u5fb7\u53e4\u62c9\u6253\u7b97\u548c\u7f57\u4e1d\u6bd4\u5bff\u547d\uff0c\u4e5f\u5e76\u975e\u662f\u7edd\u65e0\u53ef\u80fd\u3002\u8eab\u5c45\u9ad8\u4f4d\u8005\uff0c\u8bf4\u4e0d\u5b9a\u53cd\u800c\u906d\u53d7\u653b\u51fb\u66f4\u591a\uff0c\u6b7b\u5f97\u66f4\u5feb\uff1b\u800c\u50cf\u5fb7\u53e4\u62c9\u8fd9\u79cd\u65e0\u540d\u5c0f\u5352\uff0c\u53ea\u8981\u4f4e\u8c03\u518d\u4f4e\u8c03\uff0c\u627e\u4e2a\u5730\u65b9\u8eb2\u8d77\u6765\u9690\u5c45\uff0c\u957f\u751f\u4e0d\u6b7b\u8fd8\u771f\u4e0d\u662f\u5962\u671b\u3002</p><p>\u4e0d\u8fc7\u4ed6\u613f\u610f\u4f4e\u8c03\u4e48\uff1f</p><p>\u201c\u4f60\u524d\u9762\u8bf4\uff0c\u4f60\u4e00\u5f00\u59cb\u5c31\u4e0d\u8d5e\u6210\u5357\u4e0b\uff0c\u662f\u4e48\uff1f\u201d\u743c\u6069\u6c89\u541f\u7247\u523b\uff0c\u7a81\u7136\u95ee\u3002</p><p>\u201c\u662f\u3002\u201d</p><p>\u201c\u4e0d\u5357\u4e0b\uff0c\u90a3\u53bb\u54ea\u91cc\uff1f\u201d</p><p>\u743c\u6069\u770b\u8fc7\u5730\u56fe\uff0c\u5fb7\u53e4\u62c9\u7b49\u4eba\u662f\u4ece\u827e\u745e\u5fb7\u6797\u57ce\u6765\u7684\uff0c\u800c\u4ece\u90a3\u91cc\u51fa\u53d1\uff0c\u53ea\u6709\u4e24\u4e2a\u65b9\u5411\uff1a\u5357\u4e0b\u6216\u8005\u5317\u4e0a\u3002\u5e7d\u6697\u5730\u57df\u5357\u90e8\u57ce\u5e02\u8f83\u5c11\uff0c\u7f57\u4e1d\u6559\u4f1a\u7684\u52bf\u529b\u4e0d\u5927\uff0c\u6bd4\u8f83\u5229\u4e8e\u751f\u5b58\uff0c\u8fd9\u5927\u6982\u4e5f\u662f\u5f53\u65f6\u51ef\u83ab\u7b49\u4eba\u51b3\u5b9a\u5357\u4e0b\u7684\u539f\u56e0\u3002\u81f3\u4e8e\u5317\u4e0a\u7684\u8bdd\u2026\u2026\u5317\u65b9\u9634\u9b42\u57ce\u548c\u7f57\u4e1d\u6559\u4f1a\u6b63\u6253\u5f97\u6fc0\u70c8\u5462\u3002</p><p>\u201c\u53bb\u5317\u65b9\uff0c\u201d\u5fb7\u53e4\u62c9\u6de1\u6de1\u8bf4\uff0c\u201c\u76f8\u4fe1\u96c5\u8fbe\u738b\u5b50\u4e0d\u4f1a\u62d2\u7edd\u6211\u4eec\u5427\u3002\u201d</p><p>\u96c5\u8fbe\u662f\u7b2c\u4e8c\u8fdc\u5f81\u5e08\u7684\u6700\u9ad8\u6307\u6325\u5b98\uff0c\u770b\u6765\u5982\u4eca\u5728\u5e7d\u6697\u5730\u57df\u5df2\u7ecf\u540d\u58f0\u5353\u8457\u4e86\u3002\u5fb7\u53e4\u62c9\u6240\u8bf4\u81ea\u7136\u4e5f\u6ca1\u9519\uff0c\u8fd9\u79cd\u4e0d\u540c\u79cd\u65cf\u4e4b\u95f4\u7684\u6218\u4e89\uff0c\u6700\u9700\u8981\u7684\u5c31\u662f\u53db\u5f92\u2014\u2014\u6216\u8005\u8bf4\u5f03\u6697\u6295\u660e\u7684\u4e49\u58eb\u3002</p><p>\u201c\u4e5f\u6ca1\u5dee\uff0c\u201d\u743c\u6069\u5b89\u6170\uff0c\u201c\u53cd\u6b63\u8f6c\u4e86\u4e00\u5708\u53c8\u56de\u6765\u4e86\uff0c\u73b0\u5728\u4f60\u4e5f\u662f\u9634\u9b42\u57ce\u7684\u519b\u5b98\u4e86\u2026\u2026\u5176\u5b9e\u4f60\u65e9\u5c31\u77e5\u9053\u6211\u4eec\u7684\u8eab\u4efd\u662f\u5427\u3002\u201d</p><p>\u201c\u81ea\u7136\uff0c\u83ab\u857e\u7279\u8bf4\u77ee\u4eba\u6d3e\u6765\u4e24\u4e2a\u4eba\u7c7b\u505a\u4f7f\u8005\uff0c\u6211\u600e\u4e48\u53ef\u80fd\u4e0d\u597d\u5947\u4e00\u4e0b\u6765\u5386\u80cc\u666f\u3002\u201d</p><p>\u743c\u6069\u9ed8\u9ed8\u70b9\u5934\uff0c\u5728\u8111\u4e2d\u5927\u81f4\u7406\u6e05\u4e86\u4e00\u4e0b\u8f6e\u5ed3\u3002\u201c\u5fb7\u53e4\u62c9\u4e2d\u5c09\uff0c\u201d\u4ed6\u5ffd\u7136\u62ac\u8d77\u5934\uff0c\u201c\u4f60\u6709\u6ca1\u6709\u542c\u5230\u4ec0\u4e48\u5947\u602a\u7684\u58f0\u97f3\u3002\u201d</p><p>\u5fb7\u53e4\u62c9\u4e00\u6014\uff0c\u201c\u5947\u602a\u7684\u58f0\u97f3\uff1f\u6ca1\u6709\u554a\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u4fa7\u8033\u503e\u542c\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6709\u6ca1\u6709\u542c\u89c1\u54af\u5431\u54af\u5431\u7684\u58f0\u97f3\u3002\u201d</p><p>\u5fb7\u53e4\u62c9\u52aa\u529b\u503e\u542c\uff0c\u4ed6\u662f\u5438\u8840\u9b3c\uff0c\u542c\u529b\u8f83\u4e4b\u5e38\u4eba\u654f\u9510\u51e0\u500d\uff0c\u7136\u800c\u4ed6\u542c\u4e86\u534a\u5929\uff0c\u4f9d\u65e7\u832b\u7136\u5730\u6447\u4e86\u6447\u5934\u3002</p><p>\u201c\u6ca1\u542c\u89c1\uff1f\u90a3\u6216\u8bb8\u662f\u5431\u5440\u5431\u5440\u7684\u58f0\u97f3\u3002\u201d</p><p>\u201c\u6ca1\u6709\u3002\u201d</p><p>\u201c\u6ca1\u6709\u4e48\uff0c\u201d\u743c\u6069\u76b1\u7709\u201c\u5e94\u8be5\u662f\u6709\u547d\u8fd0\u7684\u9f7f\u8f6e\u5728\u8f6c\u52a8\u624d\u5bf9\u554a\u3002\u201d</p><p>\u5fb7\u53e4\u62c9\u83ab\u540d\u5176\u5999\uff0c\u201c\u547d\u8fd0\u7684\u9f7f\u8f6e\uff1f\u60a8\u5728\u8bf4\u4ec0\u4e48\u554a\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u8bfb\u4eba\u7c7b\u5199\u7684\u5947\u5e7b\u5c0f\u8bf4\u5417\uff1f\u201d</p><p>\u201c\u81ea\u7136\u4e0d\u8bfb\u3002\u201d</p><p>\u201c\u96be\u602a\uff0c\u201d\u743c\u6069\u7406\u89e3\u5730\u70b9\u70b9\u5934\uff0c\u201c\u7b97\u4e86\uff0c\u6ca1\u6709\u547d\u8fd0\u7684\u9f7f\u8f6e\u4e5f\u65e0\u6240\u8c13\uff0c\u90a3\u6709\u6ca1\u6709\u540e\u4e16\u53f2\u5b66\u5bb6\u8eb2\u5728\u6697\u5904\u8bb0\u8f7d\uff1f\u201d</p><p>\u5438\u8840\u9b3c\u5feb\u901f\u7528\u9510\u5229\u7684\u773c\u795e\u626b\u89c6\u4e86\u4e00\u904d\u56db\u5468\uff0c\u9664\u4e86\u51e0\u53ea\u5ca9\u9f20\u4e4b\u5916\u4e00\u65e0\u6240\u83b7\u3002</p><p>\u201c\u771f\u626b\u5174\uff0c\u201d\u743c\u6069\u53f9\u6c14\uff0c\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u8180\uff0c\u201c\u4e2d\u5c09\uff0c\u6709\u4e00\u4ef6\u4e8b\u6211\u5fc5\u987b\u544a\u8bc9\u4f60\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u4f60\u8981\u6709\u5fc3\u7406\u51c6\u5907\u3002\u201d</p><p>\u201c\u8bf7\u8bf4\u5427\u3002\u201d</p><p>\u201c\u5f88\u663e\u7136\uff0c\u4f60\u5c06\u6765\u4e0d\u4f1a\u662f\u4ec0\u4e48\u5927\u4eba\u7269\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5fb7\u53e4\u62c9\u81ea\u7136\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u77e5\u9053\u743c\u6069\u5230\u5e95\u5728\u8bf4\u4ec0\u4e48\uff0c\u4f46\u4ed6\u6027\u5b50\u6c89\u7a33\uff0c\u4e5f\u61d2\u5f97\u591a\u95ee\uff0c\u53ea\u5f53\u662f\u6ca1\u542c\u89c1\u3002\u743c\u6069\u4e5f\u4e0d\u518d\u5f00\u73a9\u7b11\uff0c\u8f6c\u800c\u8be2\u95ee\u6709\u5173\u5438\u8840\u9b3c\u7684\u4e8b\u60c5\uff0c\u4e00\u65b9\u9762\u662f\u597d\u5947\uff0c\u53e6\u4e00\u65b9\u9762\u4e5f\u662f\u60f3\u501f\u6b64\u591a\u4e86\u89e3\u4e00\u4e9b\u5bf9\u65b9\u7684\u60c5\u51b5\u3002\u4e0a\u8f88\u5b50\u5728\u5730\u7403\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u5f88\u559c\u6b22\u770b\u6709\u5173\u5438\u8840\u9b3c\u7684\u5f71\u89c6\u548c\u6587\u5b66\u4f5c\u54c1\uff0c\u53ea\u662f\u4f17\u8bf4\u7eb7\u7ead\uff0c\u5404\u79cd\u63cf\u8ff0\u90fd\u6709\uff0c\u751a\u81f3\u76f8\u4e92\u51b2\u7a81\u77db\u76fe\uff0c\u4ee4\u4eba\u96be\u4ee5\u91c7\u4fe1\uff0c\u672a\u514d\u5931\u671b\u3002\u5982\u4eca\u6709\u8fd9\u79cd\u673a\u4f1a\uff0c\u5012\u662f\u4e0d\u613f\u653e\u8fc7\u3002</p><p>\u8ba1\u7b97\u8d77\u6765\uff0c\u743c\u6069\u8fc4\u4eca\u4e3a\u6b62\u5df2\u7ecf\u8ba4\u8bc6\u4e86\u4e94\u4e2a\u5deb\u5996\uff1a\u62c9\u6c83\u514b\u3001\u745e\u54c8\u683c\u62c9\u65af\u3001\u5965\u6c83\u3001\u8428\u624e\u65af\u5766\u548c\u65af\u5170\u666e\uff0c\u90fd\u7b97\u662f\u6253\u8fc7\u4e9b\u4ea4\u9053\uff0c\u5176\u4e2d\u5965\u6c83\u8fd8\u662f\u4ed6\u8001\u5e08\uff0c\u65af\u5170\u666e\u7684\u547d\u5323\u66f4\u5728\u4ed6\u6000\u91cc\u3002\u4f46\u5438\u8840\u9b3c\u5374\u89c1\u5f97\u4e0d\u591a\uff0c\u9664\u4e86\u8389\u6cd5\u513f\uff0c\u5c31\u662f\u5750\u5728\u9762\u524d\u7684\u8fd9\u4f4d\u5fb7\u53e4\u62c9\u4e86\u3002\u8389\u6cd5\u513f\u662f\u62c9\u6c83\u514b\u5f3a\u884c\u521b\u9020\u7684\uff0c\u5176\u5b9e\u5e76\u4e0d\u80fd\u7b97\u662f\u7eaf\u6b63\u7684\u5438\u8840\u9b3c\uff0c\u5979\u81ea\u5df1\u4e5f\u8bf4\u8fc7\uff0c\u5f88\u591a\u5438\u8840\u9b3c\u7684\u5929\u8d4b\u5f02\u80fd\u5979\u90fd\u4e0d\u5177\u5907\uff0c\u4e5f\u4e0d\u600e\u4e48\u60e7\u6015\u9633\u5149\uff0c\u5bf9\u9c9c\u8840\u7684\u6e34\u671b\u4e5f\u4e0d\u5f3a\uff0c\u8fd8\u662f\u4fdd\u7559\u4e86\u5f88\u591a\u6d3b\u4eba\u7684\u7279\u5f81\u3002\u800c\u4e14\u8389\u6cd5\u513f\u4e00\u76f4\u4f4f\u5728\u5deb\u5e08\u4e4b\u5893\u91cc\uff0c\u5bf9\u4e8e\u5438\u8840\u9b3c\u8fd9\u4e2a\u65cf\u7fa4\u5e76\u4e0d\u662f\u7279\u522b\u4e86\u89e3\uff0c\u8fd9\u65b9\u9762\u7684\u89c1\u8bc6\u5e76\u4e0d\u591a\u3002\u5fb7\u53e4\u62c9\u5374\u662f\u4e00\u76f4\u8ddf\u7740\u51ef\u83ab\uff0c\u53c8\u662f\u9f50\u96c5\u6e29\u7eb1\u4e3d\u6559\u4f1a\u6210\u5458\uff0c\u53ef\u8c13\u662f\u8d27\u771f\u4ef7\u5b9e\u7684\u8001\u9b3c\u3002</p><p>\u201c\u603b\u611f\u89c9\u5438\u8840\u9b3c\u5f88\u7f55\u89c1\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6309\u9053\u7406\u8bf4\uff0c\u53d8\u6210\u5438\u8840\u9b3c\u591a\u7b80\u5355\uff0c\u65e2\u5feb\u6377\u53c8\u5b89\u5168\uff0c\u8f6c\u5316\u5deb\u5996\u90fd\u8fd8\u6709\u5931\u8d25\u7684\u53ef\u80fd\u5462\uff0c\u4e3a\u4ec0\u4e48\u4e16\u754c\u4e0a\u7684\u5438\u8840\u9b3c\u597d\u50cf\u8fd9\u4e48\u5c11\u5462\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u591a\u7684\u90fd\u88ab\u6740\u5149\u4e86\u3002\u201d</p><p>\u201c\u5438\u8840\u9b3c\u6ca1\u90a3\u4e48\u5bb9\u6613\u6740\u5427\u3002\u201d</p><p>\u201c\u5f53\u7136\u6709\u90a3\u4e48\u5bb9\u6613\uff0c\u201d\u5fb7\u53e4\u62c9\u8bf4\uff0c\u201c\u9996\u5148\uff0c\u5deb\u5996\u4e0d\u7528\u5438\u8840\uff0c\u627e\u4e2a\u575f\u5893\u4f4f\u4e0a\u5341\u5e74\u4e0d\u51fa\u95e8\u90fd\u6ca1\u4e8b\uff0c\u5438\u8840\u9b3c\u5374\u5fc5\u987b\u5b9a\u671f\u5438\u8840\uff0c\u4e00\u822c\u8fd8\u5f97\u5438\u540c\u7c7b\u7684\u8840\u3002\u51fa\u6ca1\u5f97\u65e2\u9891\u7e41\uff0c\u4f5c\u6848\u53c8\u591a\uff0c\u81ea\u7136\u4e5f\u5c31\u6b7b\u5f97\u5feb\u4e86\u3002\u201d</p><p>\u201c\u5514\uff0c\u8fd9\u4e48\u8bf4\u4e5f\u6709\u9053\u7406\u3002\u201d</p><p>\u201c\u5176\u6b21\uff0c\u80fd\u591f\u8f6c\u5316\u6210\u5deb\u5996\u7684\uff0c\u539f\u672c\u5b9a\u7136\u90fd\u662f\u7b2c\u4e00\u6d41\u7684\u65bd\u6cd5\u8005\uff0c\u8c01\u80fd\u8f7b\u6613\u53bb\u6740\uff1f\u5438\u8840\u9b3c\u5c31\u5bb9\u6613\u5bf9\u4ed8\u591a\u4e86\uff0c\u201d\u5fb7\u53e4\u62c9\u8bf4\uff0c\u201c\u522b\u628a\u5438\u8840\u9b3c\u60f3\u50cf\u5f97\u591a\u4e48\u5f3a\u3002\u5982\u679c\u539f\u672c\u662f\u4e2a\u666e\u901a\u4eba\uff0c\u5c31\u7b97\u53d8\u6210\u5438\u8840\u9b3c\uff0c\u4e5f\u4e0d\u8fc7\u5c31\u662f\u529b\u6c14\u5927\u4e9b\uff0c\u53cd\u5e94\u5feb\u4e9b\uff0c\u6709\u70b9\u81ea\u6108\u80fd\u529b\u7f62\u4e86\uff0c\u8fd8\u6015\u9633\u5149\u3001\u6015\u5723\u6c34\u3001\u6015\u5404\u79cd\u5723\u6cd5\u672f\uff0c\u8981\u6740\u8d77\u6765\u6839\u672c\u4e0d\u96be\u3002\u5176\u5b9e\u5927\u90e8\u4efd\u5438\u8840\u9b3c\uff0c\u90fd\u662f\u6b7b\u5728\u4e86\u96cf\u513f\u548c\u65b0\u751f\u9636\u6bb5\u3002\u201d</p><p>\u201c\u6211\u542c\u8bf4\u5438\u8840\u9b3c\u8fd8\u5bb3\u6015\u5927\u849c\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u5bb3\u6015\uff0c\u53ea\u662f\u8ba8\u538c\uff0c\u201d\u5fb7\u53e4\u62c9\u7ea0\u6b63\uff0c\u201c\u9633\u5149\u3001\u5723\u6c34\u8fd9\u79cd\uff0c\u80fd\u591f\u5bf9\u5438\u8840\u9b3c\u9020\u6210\u4e25\u91cd\u4f24\u5bb3\uff0c\u6240\u4ee5\u6211\u4eec\u5bb3\u6015\uff1b\u4f46\u5927\u849c\u53ea\u662f\u6c14\u5473\u4ee4\u4eba\u6076\u5fc3\uff0c\u4e0d\u613f\u610f\u9760\u8fd1\u7f62\u4e86\u3002\u5982\u679c\u4e00\u4e2a\u4eba\u8eab\u4e0a\u5e26\u7740\u5927\u849c\uff0c\u4e00\u822c\u6211\u4eec\u4e5f\u5c31\u4e0d\u613f\u610f\u53bb\u7406\u776c\uff0c\u4f46\u5982\u679c\u4ed6\u89c9\u5f97\u6302\u7740\u51e0\u74e3\u5927\u849c\u6211\u4eec\u5c31\u6015\u4e86\uff0c\u90a3\u9876\u591a\u6740\u4e86\u4ed6\u518d\u53bb\u6d17\u6fa1\u5c31\u662f\u3002\u201d</p><p>\u201c\u5341\u5b57\u67b6\u6015\u4e0d\u6015\uff1f\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d\u5fb7\u53e4\u62c9\u4e00\u6014\uff0c\u201c\u5341\u5b57\u67b6\uff1f\u201d</p><p>\u743c\u6069\u7528\u624b\u6307\u8638\u4e86\u70b9\u6c34\uff0c\u5728\u684c\u9762\u4e0a\u5212\u4e86\u4e2a\u5341\u5b57\u67b6\u7684\u7b26\u53f7\u3002</p><p>\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u5fb7\u53e4\u62c9\u770b\u4e0d\u660e\u767d\uff0c\u201c\u5deb\u5e08\u7684\u7b26\u6587\uff1f\u201d</p><p>\u201c\u4f60\u89c1\u8fc7\u8fd9\u4e48\u7b80\u5355\u7684\u9b54\u6cd5\u7b26\u6587\uff1f\u201d</p><p>\u201c\u90a3\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u67d0\u4f4d\u795e\u7947\u7684\u5723\u5fbd\uff0c\u4f60\u770b\u7740\u6ca1\u4ec0\u4e48\u80c6\u6218\u5fc3\u60ca\u7684\u611f\u89c9\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u201d\u5fb7\u53e4\u62c9\u6447\u5934\uff0c\u201c\u8fd9\u662f\u54ea\u4f4d\u795e\u7947\u7684\u5723\u5fbd\uff1f\u4ece\u6ca1\u542c\u8bf4\u8fc7\u554a\u3002\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u5927\u6982\u662f\u6211\u8bb0\u9519\u4e86\u3002\u201d</p><p>\u5f88\u663e\u7136\uff0c\u8fd9\u4e2a\u4e16\u754c\u91cc\u56e0\u4e3a\u6ca1\u6709\u4e0a\u5e1d\u548c\u57fa\u7763\u6559\uff0c\u5438\u8840\u9b3c\u8fde\u5341\u5b57\u67b6\u90fd\u4e0d\u5728\u610f\u4e86\u3002</p><p>\u201c\u8fd9\u4e48\u8bf4\uff0c\u771f\u6b63\u80fd\u591f\u5bf9\u4f60\u4eec\u9020\u6210\u4f24\u5bb3\u7684\uff0c\u9664\u4e86\u5200\u5251\u548c\u6cd5\u672f\uff0c\u5176\u5b9e\u5c31\u662f\u9633\u5149\u3001\u5723\u6c34\u4e4b\u7c7b\u4e86\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u4e0d\u8fc7\u4e00\u822c\u7684\u5200\u5251\u80fd\u591f\u4f24\u5bb3\u4f60\u4eec\u5417\uff1f\u201d\u743c\u6069\u7591\u60d1\uff0c\u201c\u6211\u597d\u50cf\u5728\u54ea\u91cc\u542c\u8bf4\u8fc7\uff0c\u53ea\u6709\u94f6\u8d28\u6b66\u5668\u624d\u884c\u3002\u201d</p><p>\u201c\u90fd\u53ef\u4ee5\u7684\uff0c\u94f6\u8d28\u6b66\u5668\u53ea\u662f\u6548\u679c\u66f4\u597d\u7f62\u4e86\uff0c\u5176\u5b9e\u9540\u94f6\u5c31\u884c\u3002\u201d\u5fb7\u53e4\u62c9\u5766\u8bda\u5f97\u60ca\u4eba\uff0c\u5bf9\u81ea\u5df1\u7684\u5f31\u70b9\u4e1d\u6beb\u4e0d\u52a0\u9690\u7792\u3002</p><p>\u201c\u9540\u94f6\u7684\u6548\u679c\u4f1a\u6bd4\u7eaf\u94f6\u5dee\u4e48\uff1f\u201d</p><p>\u201c\u6ca1\u90a3\u56de\u4e8b\uff0c\u201d\u5fb7\u53e4\u62c9\u8bf4\uff0c\u201c\u6211\u4eec\u6015\u94f6\uff0c\u4e0d\u662f\u56e0\u4e3a\u5b83\u950b\u5229\uff0c\u800c\u662f\u56e0\u4e3a\u6211\u4eec\u4f53\u8d28\u7279\u6b8a\uff0c\u94f6\u548c\u8840\u6db2\u6df7\u5408\u5c31\u4f1a\u4e2d\u6bd2\u3002\u7eaf\u94f6\u592a\u67d4\u8f6f\uff0c\u90fd\u672a\u5fc5\u80fd\u780d\u5f97\u4f24\u4eba\uff0c\u9540\u94f6\u5176\u5b9e\u6548\u679c\u66f4\u597d\u3002\u201d</p><p>\u201c\u79d8\u94f6\u884c\u4e0d\u884c\uff1f\u201d</p><p>\u5fb7\u53e4\u62c9\u77a5\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u79d8\u94f6\u53ea\u662f\u50cf\u94f6\uff0c\u53c8\u4e0d\u771f\u7684\u662f\u94f6\u2014\u2014\u96be\u4e0d\u6210\u4f60\u8fd8\u80fd\u62ff\u7cbe\u91d1\u94f8\u91d1\u5e01\u7528\uff1f\u5f53\u7136\u80af\u5b9a\u6709\u4eba\u4e50\u610f\u6536\u5c31\u662f\u4e86\u3002\u201d</p><p>\u90a3\u662f\u81ea\u7136\uff0c\u7cbe\u91d1\u53ef\u6bd4\u9ec4\u91d1\u73cd\u7a00\u8d35\u91cd\u5f97\u591a\u4e86\u3002</p><p>\u201c\u4e0d\u8fc7\u636e\u8bf4\u953b\u70bc\u5230\u4eb2\u738b\u4f4d\u9636\u7684\u5438\u8840\u9b3c\uff0c\u4e0d\u4f46\u4e0d\u60e7\u6015\u65e5\u5149\uff0c\u4e5f\u4e0d\u60e7\u6015\u94f6\u8d28\u6b66\u5668\u4e86\uff0c\u201d\u5fb7\u53e4\u62c9\u8865\u5145\uff0c\u201c\u5f53\u7136\uff0c\u8fd9\u4f9d\u7136\u8fd8\u53ea\u662f\u2018\u636e\u8bf4\u2019\uff0c\u53cd\u6b63\u6211\u662f\u6ca1\u771f\u6b63\u89c1\u8fc7\u3002\u201d</p><p>\u6839\u636e\u5fb7\u53e4\u62c9\u7684\u89e3\u91ca\uff0c\u867d\u7136\u540c\u4e3a\u9ad8\u9636\u4ea1\u7075\uff0c\u4f46\u5438\u8840\u9b3c\u786e\u5b9e\u8fd8\u662f\u6bd4\u5deb\u5996\u5dee\u5f97\u8fdc\u4e86\uff0c\u5404\u79cd\u5f31\u70b9\u7f3a\u9677\u6742\u4e03\u6742\u516b\u7684\u4e00\u5806\uff0c\u9664\u975e\u953b\u70bc\u5230\u6700\u9876\u7ea7\u7684\u4eb2\u738b\u5438\u8840\u9b3c\u624d\u52c9\u5f3a\u591f\u770b\u3002\u4f46\u5c31\u7b97\u662f\u4eb2\u738b\u5438\u8840\u9b3c\uff0c\u4f9d\u65e7\u4e5f\u4e0d\u662f\u91d1\u521a\u4e0d\u574f\u4e4b\u8eaf\uff0c\u8111\u888b\u88ab\u780d\u6389\u7167\u6837\u4f1a\u6b7b\uff0c\u800c\u5deb\u5996\u5374\u662f\u53ef\u4ee5\u5ba2\u4e32\u6050\u6016\u5206\u5b50\uff0c\u62ff\u81ea\u5df1\u7684\u8eab\u4f53\u81ea\u7206\u73a9\u7684\u3002</p><p>\u201c\u5438\u8840\u9b3c\u81ea\u6709\u5438\u8840\u9b3c\u7684\u597d\u5904\uff0c\u201d\u5fb7\u53e4\u62c9\u61d2\u6d0b\u6d0b\u5730\u8bf4\uff0c\u201c\u81f3\u5c11\u80fd\u548c\u5973\u5b69\u5b50\u4e0a\u5e8a\uff0c\u800c\u4e14\u4f60\u60f3\u575a\u6301\u591a\u4e45\u5c31\u591a\u4e45\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u6240\u4ee5\u6211\u624d\u8fd9\u6837\u53d7\u5973\u5b69\u5b50\u6b22\u8fce\u554a\u3002\u201d</p><p>\u6709\u90a3\u4e48\u4e00\u77ac\u95f4\uff0c\u743c\u6069\u51e0\u4e4e\u771f\u7684\u52a8\u5fc3\u4e86\uff0c\u8003\u8651\u662f\u4e0d\u662f\u4e5f\u53d8\u6210\u5438\u8840\u9b3c\u8bd5\u8bd5\uff0c\u4f46\u4ed6\u968f\u5373\u627c\u6740\u4e86\u8fd9\u4e2a\u8352\u8bde\u7684\u5ff5\u5934\u3002\u201c\u7b97\u4e86\u5427\uff0c\u201d\u4ed6\u53f9\u6c14\uff0c\u201c\u6211\u8fd8\u60f3\u80fd\u6652\u6652\u592a\u9633\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u8fd9\u4e2a\u8bdd\u9898\u6ca1\u6709\u7ee7\u7eed\u8fdb\u884c\u4e0b\u53bb\uff0c\u4e00\u65b9\u9762\u56e0\u4e3a\u5df2\u7ecf\u8c08\u8bba\u5f97\u591f\u591a\uff0c\u53e6\u4e00\u65b9\u9762\u662f\u56e0\u4e3a\u6885\u83f2\u65af\u6070\u597d\u56de\u6765\u4e86\u3002\u5728\u5973\u6027\u9762\u524d\uff0c\u6709\u4e9b\u8c08\u8bdd\u8fd8\u662f\u6536\u655b\u4e9b\u6bd4\u8f83\u597d\u3002</p><p>\u6668\u70bc\u57ce\u5c40\u52bf\u5e73\u5b9a\uff0c\u5185\u90e8\u7684\u5a01\u80c1\u6d88\u9664\uff0c\u63a5\u4e0b\u6765\u9762\u4e34\u7684\u95ee\u9898\uff0c\u4e3b\u8981\u5c31\u662f\u5982\u4f55\u7acb\u8db3\uff0c\u5e94\u4ed8\u5916\u90e8\u5a01\u80c1\u4e86\u3002\u4e3a\u6b64\u6885\u83f2\u65af\u548c\u963f\u5fd2\u59ae\u524d\u65e5\u53c8\u53bb\u4e86\u4e00\u8d9f\u4f0a\u5361\u6c99\u57ce\u3002\u5e78\u597d\u77ee\u4eba\u4eec\u7ec8\u7a76\u6ca1\u6709\u6b7b\u8111\u7b4b\u5230\u5e95\uff0c\u4ee5\u524d\u5c40\u52bf\u6ca1\u6709\u660e\u6670\uff0c\u81ea\u7136\u4e0d\u80af\u5984\u52a8\uff1b\u5982\u4eca\u5c18\u57c3\u843d\u5b9a\uff0c\u518d\u5546\u8c08\u5408\u4f5c\u4e8b\u5b9c\uff0c\u90a3\u5c31\u987a\u5229\u5f97\u591a\u4e86\u3002\u5f53\u7136\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u4f9d\u65e7\u53ea\u662f\u6709\u9650\u5ea6\u7684\u5408\u4f5c\uff0c\u66f4\u8c08\u4e0d\u4e0a\u7ed3\u76df\u3002\u77ee\u4eba\u6ca1\u6709\u6d3e\u9063\u534a\u4e2a\u58eb\u5175\u524d\u6765\u534f\u9632\uff0c\u4ec5\u4ec5\u53ea\u662f\u8fd0\u9001\u4e86\u4e00\u6279\u7cae\u98df\u548c\u5bb6\u755c\uff0c\u5012\u4e5f\u7b97\u662f\u6551\u6025\u3002\u4e00\u767e\u591a\u540d\u5353\u5c14\u5728\u8fd9\u91cc\u9a7b\u624e\u7740\uff0c\u5168\u9760\u72e9\u730e\u91c7\u96c6\u4e3a\u751f\uff0c\u7ec8\u7a76\u4e0d\u662f\u957f\u4e45\u4e4b\u8ba1\uff0c\u5982\u4eca\u6668\u70bc\u5468\u8fb9\u533a\u57df\u7684\u52a8\u7269\u90fd\u65e5\u6e10\u7a00\u5c11\uff0c\u5feb\u8981\u7edd\u8ff9\u4e86\u3002\u53e6\u5916\u77ee\u4eba\u8fd8\u6d3e\u9063\u4e86\u51e0\u4e2a\u5de5\u5320\uff0c\u524d\u6765\u6307\u5bfc\u57ce\u9632\u7684\u4fee\u7f2e\u4e8b\u5b9c\uff0c\u5728\u8fd9\u65b9\u9762\u4ed6\u4eec\u662f\u4e13\u5bb6\u3002</p><p>\u6b64\u5916\uff0c\u6885\u83f2\u65af\u8fd8\u5f04\u5230\u4e86\u4e00\u6279\u88c5\u5907\uff0c\u6b66\u5668\u62a4\u7532\u9f50\u5168\uff0c\u5171\u6709\u4e00\u767e\u591a\u5957\u3002</p><p>\u548c\u4e0a\u6b21\u4e0d\u540c\uff0c\u8fd9\u6279\u88c5\u5907\u5e76\u975e\u77ee\u4eba\u6253\u9020\u7684\uff0c\u5b83\u5176\u5b9e\u6765\u6e90\u4e8e\u5353\u5c14\u3002\u51e0\u4e2a\u6708\u524d\u7684\u5927\u6218\uff0c\u74dc\u7406\u5fb7\u65af\u5927\u8d25\uff0c\u77ee\u4eba\u4fd8\u864f\u4e86\u4e0d\u5c11\u5353\u5c14\uff0c\u5927\u591a\u9a71\u8d76\u53bb\u77ff\u6d1e\u505a\u82e6\u529b\u4e86\uff0c\u4ed6\u4eec\u7684\u5175\u5668\u76d4\u7532\u81ea\u7136\u4e5f\u90fd\u5168\u90e8\u6ca1\u6536\u3002\u4ee5\u77ee\u4eba\u7684\u773c\u5149\u6765\u770b\uff0c\u8fd9\u4e9b\u5353\u5c14\u953b\u9020\u7684\u88c5\u5907\u8d28\u91cf\u592a\u5dee\uff0c\u4e0d\u5c51\u4f7f\u7528\uff08\u5176\u5b9e\u4e5f\u7528\u4e0d\u4e86\uff0c\u77ee\u4eba\u600e\u4e48\u80fd\u7a7f\u4e0a\u5353\u5c14\u7684\u9501\u7532\uff09\uff0c\u6240\u4ee5\u4e00\u76f4\u6254\u5728\u4ed3\u5e93\u91cc\uff0c\u7a7a\u95f2\u65f6\u4fbf\u53d6\u4e9b\u56de\u7089\u91cd\u70bc\uff0c\u5f53\u4f5c\u539f\u6599\u4f7f\u7528\u3002\u6885\u83f2\u65af\u6b64\u6b21\u56de\u4f0a\u5361\u6c99\u57ce\uff0c\u4fbf\u8bf4\u670d\u4e86\u56fd\u738b\u548c\u8bae\u5458\uff0c\u4ece\u8fd9\u6279\u201c\u5e9f\u54c1\u201d\u91cc\u6311\u9009\u62fc\u51d1\u4e86\u4e00\u767e\u591a\u5957\u8f83\u4e3a\u5b8c\u6574\u7684\uff0c\u5e26\u4e86\u56de\u6765\u3002</p><p>\u6bd5\u7adf\u66fe\u7ecf\u662f\u74dc\u7406\u5fb7\u65af\u7684\u7cbe\u9510\u6218\u58eb\u6240\u7528\uff0c\u54c1\u8d28\u6709\u4fdd\u8bc1\uff0c\u867d\u7136\u77ee\u4eba\u770b\u4e0d\u4e0a\u773c\uff0c\u4f46\u5bf9\u4e8e\u6668\u70bc\u57ce\u91cc\u7684\u8fd9\u6279\u5353\u5c14\u4eec\u6765\u8bf4\u5df2\u7ecf\u662f\u610f\u5916\u4e4b\u559c\u4e86\u3002\u8981\u77e5\u9053\uff0c\u4ed6\u4eec\u53ef\u90fd\u662f\u4e00\u8def\u9003\u4ea1\u6765\u6b64\u7684\uff0c\u5728\u77ee\u4eba\u63d0\u4f9b\u63f4\u52a9\u4e4b\u524d\uff0c\u5927\u7ea6\u6709\u56db\u5206\u4e4b\u4e00\u7684\u4eba\u6b66\u5668\u635f\u574f\u3001\u65ad\u6298\u6216\u8005\u660e\u663e\u4e0d\u8d81\u624b\uff0c\u6709\u8d85\u8fc7\u4e09\u5206\u4e4b\u4e00\u7684\u4eba\u6ca1\u6709\u62a4\u7532\u6216\u8005\u6b8b\u7834\u4e0d\u582a\uff0c\u5982\u4eca\u603b\u7b97\u662f\u5347\u7ea7\u6362\u4ee3\u4e86\u3002</p><p>\u9664\u4e86\u8fd9\u4e9b\u4e4b\u5916\uff0c\u6885\u83f2\u65af\u8fd8\u5e26\u56de\u6765\u4e00\u4e2a\u5ba2\u4eba\u3002</p><p>\u201c\u6b27\u51ef\uff1f\u201d</p><p>\u201c\u65e9\u4e0a\u597d\uff0c\u743c\u6069\uff0c\u201d\u6076\u9b54\u517c\u60c5\u62a5\u8d29\u5b50\u5927\u6447\u5927\u6446\u5730\u8d70\u8fdb\u6765\uff0c\u6458\u4e0b\u5bbd\u6cbf\u5e3d\uff0c\u201c\u5443\uff0c\u4f60\u7684\u8868\u60c5\u2026\u2026\u5bf9\u4e8e\u6211\u7684\u5230\u6765\u4f3c\u4e4e\u5e76\u4e0d\u6b22\u8fce\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u4f60\u8bef\u4f1a\u4e86\uff0c\u201d\u743c\u6069\u8d76\u5feb\u8bf4\uff0c\u201c\u6765\u627e\u6211\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u592a\u5f00\u95e8\u89c1\u5c71\u4e86\u5427\uff0c\u96be\u9053\u6211\u4eec\u4e0d\u5e94\u8be5\u5148\u5bd2\u6684\u51e0\u53e5\uff0c\u8c08\u8c08\u4e0a\u6b21\u5206\u522b\u540e\u7684\u60c5\u5f62\uff0c\u8bf4\u8bf4\u6700\u8fd1\u7684\u751f\u6d3b\uff0c\u987a\u4fbf\u804a\u804a\u56fd\u9645\u5f62\u52bf\u2026\u2026\u201d</p><p>\u201c\u5443\uff0c\u6211\u4eec\u662f\u8001\u670b\u53cb\u4e86\uff0c\u8fd8\u6765\u8fd9\u5957\u5c31\u672a\u514d\u592a\u89c1\u5916\u4e86\u5427\u3002\u201d</p><p>\u201c\u55ef\u55ef\uff0c\u6211\u4e5f\u8fd9\u4e48\u89c9\u5f97\uff0c\u201d\u6b27\u51ef\u8fde\u8fde\u70b9\u5934\uff0c\u201c\u6211\u4eec\u662f\u8001\u670b\u53cb\u561b\u2014\u2014\u6240\u4ee5\u5982\u679c\u6211\u8bf7\u4f60\u5e2e\u70b9\u5c0f\u5fd9\uff0c\u4f60\u4e00\u5b9a\u4e0d\u4f1a\u62d2\u7edd\u7684\u5bf9\u4e0d\u5bf9\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u2026\u2026\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u522b\u7d27\u5f20\uff0c\u4e3e\u624b\u4e4b\u52b3\u800c\u5df2\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u65af\u5170\u666e\u7684\u547d\u5323\uff0c\u5728\u4f60\u624b\u91cc\u5bf9\u5427\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u4f60\u5bf9\u5b83\u6709\u5174\u8da3\uff1f\u201d\u743c\u6069\u6765\u4e86\u70b9\u7cbe\u795e\uff0c\u201c\u53ea\u8981\u4f60\u51fa\u4ef7\u5408\u9002\uff0c\u8fd9\u4e2a\u6211\u5012\u53ef\u4ee5\u8003\u8651\u8f6c\u8ba9\u2014\u2014\u5c31\u7b97\u6253\u70b9\u6298\u6263\u4e5f\u65e0\u59a8\u4e86\u3002\u201d</p><p>\u547d\u5323\u653e\u5728\u4ed6\u624b\u91cc\uff0c\u7b49\u4e8e\u5c31\u662f\u4e2a\u9e21\u808b\uff0c\u98df\u4e4b\u65e0\u5473\uff0c\u5f03\u4e4b\u53ef\u60dc\uff0c\u5982\u679c\u80fd\u591f\u4ea4\u6613\u51fa\u53bb\u6362\u70b9\u522b\u7684\u597d\u5904\uff0c\u743c\u6069\u81ea\u7136\u662f\u5f88\u4e50\u610f\u7684\u3002</p><p>\u6b27\u51ef\u4ece\u6000\u91cc\u6478\u4e86\u6478\uff0c\u53d6\u51fa\u4e00\u4e2a\u56db\u56db\u65b9\u65b9\u7684\u767d\u8272\u4e1c\u897f\u6765\uff0c\u5f80\u77f3\u684c\u4e0a\u4e00\u653e\uff0c\u63a8\u5230\u743c\u6069\u9762\u524d\uff0c\u201c\u8fd9\u4e2a\u5982\u4f55\uff1f\u201d</p><p>\u743c\u6069\u4f4e\u5934\u4e00\u770b\uff0c\u539f\u6765\u5c31\u662f\u6b27\u51ef\u4ece\u4e9a\u5f53\u65af\u624b\u91cc\u6572\u8bc8\u6765\u7684\u90a3\u526f\u8428\u74e6\u68cb\uff0c\u636e\u8bf4\u662f\u74dc\u7406\u5fb7\u65af\u57ce\u5deb\u5e08\u5b66\u9662\u91cc\u6700\u6709\u4ef7\u503c\u7684\u5b9d\u7269\u3002\u6b27\u51ef\u8fd8\u66fe\u7ecf\u5728\u6c89\u9ed8\u4e4b\u6b4c\u6f14\u793a\u8fc7\u4f5c\u7528\uff0c\u4e8c\u5341\u56db\u679a\u68cb\u5b50\uff0c\u53ef\u4ee5\u53d8\u5316\u6210\u4e8c\u5341\u56db\u4e2a\u5f3a\u529b\u62a4\u536b\uff0c\u786e\u5b9e\u662f\u5f88\u4e0d\u9519\u7684\u4e1c\u897f\u3002\u743c\u6069\u4e0a\u6b21\u770b\u89c1\u90fd\u5f88\u52a8\u5fc3\uff0c\u53ea\u662f\u65e2\u7136\u843d\u5230\u4e86\u6b27\u51ef\u624b\u4e2d\uff0c\u4e5f\u5c31\u65ad\u4e86\u5ff5\u60f3\uff0c\u6ca1\u60f3\u5230\u51e0\u4e2a\u6708\u540e\u53c8\u8f6c\u56de\u6765\u4e86\u3002</p><p>\u201c\u6210\u4ea4\uff01\u201d\u743c\u6069\u7acb\u523b\u8bf4\uff0c\u638f\u51fa\u65af\u5170\u666e\u7684\u547d\u5323\u5c31\u4e22\u4e86\u8fc7\u53bb\uff0c\u751f\u6015\u4ed6\u53c8\u53cd\u6094\u3002\u6b27\u51ef\u7b11\u4e86\u8d77\u6765\uff0c\u62e3\u8d77\u547d\u5323\u7565\u7565\u770b\u4e86\u4e00\u773c\uff0c\u6536\u5165\u6000\u4e2d\u3002</p><p>\u201c\u4f60\u8981\u5b83\u505a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u987a\u53e3\u95ee\u3002</p><p>\u201c\u505a\u793c\u7269\u3002\u201d</p><p>\u201c\u793c\u7269\uff1f\u201d\u743c\u6069\u4e00\u6014\uff0c\u201c\u9001\u7ed9\u8c01\uff1f\u201d</p><p>\u201c\u5965\u7075\u591a\u5c14\u7684\u7075\u5438\u602a\u554a\uff0c\u201d\u6b27\u51ef\u63d0\u9192\uff0c\u201c\u4f60\u5fd8\u4e86\uff0c\u8fd9\u5bb6\u4f19\u662f\u5965\u7075\u591a\u5c14\u60ac\u8d4f\u6349\u62ff\u7684\u901a\u7f09\u72af\u5462\u3002\u201d</p><p>\u743c\u6069\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u4ed6\u8fd8\u771f\u628a\u8fd9\u4e00\u832c\u7ed9\u5fd8\u4e86\uff0c\u5426\u5219\u5012\u53ef\u4ee5\u76f4\u63a5\u62ff\u8fd9\u547d\u5323\u53bb\u9886\u8d4f\u2026\u2026\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u5c31\u7b97\u60f3\u8d77\u6765\uff0c\u4ed6\u4e5f\u4e0d\u613f\u610f\u518d\u548c\u7075\u5438\u602a\u6253\u4ea4\u9053\uff0c\u800c\u4e14\u8fd9\u91cc\u8ddd\u79bb\u5965\u7075\u591a\u5c14\u4e5f\u592a\u8fdc\u4e86\u70b9\uff0c\u61d2\u5f97\u8dd1\u8fd9\u4e00\u8d9f\u8def\u3002</p><p>\u201c\u7075\u5438\u602a\u4f1a\u7ed9\u4ec0\u4e48\u62a5\u916c\uff1f\u201d\u743c\u6069\u597d\u5947\uff0c\u201c\u80fd\u6bd4\u8fd9\u526f\u8428\u74e6\u68cb\u4ef7\u503c\u66f4\u9ad8\uff1f\u201d</p><p>\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u4e3b\u8981\u662f\u6211\u6700\u8fd1\u9047\u5230\u70b9\u5c0f\u9ebb\u70e6\uff0c\u9700\u8981\u90a3\u7fa4\u5bb6\u4f19\u5e2e\u5fd9\u3002\u6240\u4ee5\u4e8b\u5148\u9001\u9001\u793c\uff0c\u641e\u597d\u5173\u7cfb\u5c31\u5f88\u6709\u5fc5\u8981\u4e86\u3002\u201d</p><p>\u201c\u8fd8\u6709\u4f60\u90fd\u641e\u4e0d\u5b9a\u7684\u9ebb\u70e6\uff1f\u201d</p><p>\u201c\u6211\u4e5f\u4e0d\u662f\u4e07\u80fd\u7684\u561b\u3002\u201d</p><p>\u5bf9\u4e8e\u8fd9\u4e2a\u95ee\u9898\uff0c\u6b27\u51ef\u660e\u663e\u4e0d\u613f\u591a\u8bf4\uff0c\u743c\u6069\u6709\u70b9\u597d\u5947\uff0c\u4f46\u4e5f\u660e\u667a\u5730\u4e0d\u518d\u8ffd\u95ee\uff0c\u6539\u4e3a\u8be2\u95ee\u8fd9\u526f\u8428\u74e6\u68cb\u7684\u4f7f\u7528\u65b9\u6cd5\u3002\u6b27\u51ef\u628a\u5492\u8bed\u548c\u6fc0\u6d3b\u53e3\u4ee4\u544a\u8bc9\u743c\u6069\uff0c\u8ba9\u4ed6\u8bd5\u9a8c\u4e24\u904d\u65e0\u8bef\uff0c\u4fbf\u8d77\u8eab\u544a\u8f9e\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u201d\u4ed6\u8d70\u51fa\u4e24\u6b65\uff0c\u7a81\u7136\u53c8\u50cf\u662f\u60f3\u8d77\u4ec0\u4e48\u4f3c\u7684\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u201c\u4f60\u4e0a\u6b21\u4e0d\u662f\u95ee\u8fd9\u526f\u68cb\u662f\u600e\u4e48\u505a\u51fa\u6765\u7684\u5417\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u4f60\u8bf4\u4f60\u4e5f\u4e0d\u77e5\u9053\uff0c\u662f\u4ee5\u524d\u4e00\u4e2a\u53eb\u68b5\u94ce\u5c14\u7684\u5353\u5c14\u5927\u5deb\u5e08\u5236\u4f5c\u7684\u3002\u201d</p><p>\u201c\u6211\u6700\u8fd1\u67e5\u4e86\u4e00\u4e0b\u8d44\u6599\uff0c\u53d1\u73b0\u5b83\u597d\u50cf\u7528\u7684\u662f\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u6280\u672f\u3002\u201d</p><p>\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\uff1f</p><p>\u201c\u55ef\uff0c\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u5947\u68b0\u5e08\uff0c\u539f\u672c\u5c31\u4ee5\u5236\u4f5c\u9b54\u6cd5\u7269\u54c1\u8457\u79f0\uff0c\u5c24\u5176\u662f\u8fd9\u79cd\u9b54\u50cf\u5080\u5121\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u5b9e\u9645\u4e0a\uff0c\u9b54\u50cf\u7684\u5236\u4f5c\u6280\u672f\uff0c\u5c31\u662f\u4ed6\u4eec\u53d1\u660e\u7684\u561b\u3002\u201d</p><p>\u743c\u6069\u770b\u4e86\u770b\u90a3\u526f\u8428\u74e6\u68cb\uff0c\u6b27\u51ef\u8bf4\u5f97\u6ca1\u9519\uff0c\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u6765\u8bf4\uff0c\u8fd9\u4e8c\u5341\u56db\u679a\u68cb\u5b50\uff0c\u5176\u5b9e\u5c31\u662f\u4e8c\u5341\u56db\u4e2a\u9b54\u50cf\uff0c\u6ca1\u6709\u72ec\u7acb\u610f\u8bc6\uff0c\u53d7\u4eba\u63a7\u5236\u7684\u5080\u5121\uff0c\u53ea\u4e0d\u8fc7\u5916\u5f62\u4e0a\u6709\u4e9b\u7279\u522b\uff0c\u540c\u65f6\u80fd\u591f\u53d8\u5316\u7f29\u5c0f\u6210\u68cb\u5b50\u7f62\u4e86\u2014\u2014\u8fd9\u5f53\u7136\u5df2\u7ecf\u662f\u5f88\u4e86\u4e0d\u8d77\u7684\u9b54\u6cd5\u6280\u827a\uff0c\u743c\u6069\u5c31\u4e0d\u77e5\u9053\u600e\u4e48\u80fd\u628a\u7406\u5e94\u9b54\u6cd5\u514d\u75ab\u7684\u9b54\u50cf\u53d8\u5f62\uff0c\u4f46\u7a76\u5176\u6839\u672c\uff0c\u5b83\u4eec\u8fd8\u662f\u9b54\u50cf\u3002</p><p>\u201c\u6700\u5173\u952e\u7684\u662f\uff0c\u6211\u67e5\u5230\u90a3\u4e2a\u68b5\u94ce\u5c14\uff0c\u662f\u4e2a\u5047\u540d\u3002\u201d</p><p>\u201c\u5047\u540d\uff1f\u201d</p><p>\u201c\u4ed6\u5176\u5b9e\u6839\u672c\u4e0d\u662f\u5353\u5c14\uff0c\u800c\u662f\u4e2a\u4eba\u7c7b\u53d8\u5f62\u4f2a\u88c5\u7684\uff0c\u4ed6\u539f\u672c\u662f\u4e2a\u4f0a\u739b\u65af\u5361\u7684\u5947\u68b0\u5e08\uff0c\u72af\u4e86\u7f6a\uff0c\u9003\u4ea1\u5230\u4e86\u5e7d\u6697\u5730\u57df\uff0c\u540e\u6765\u88ab\u74dc\u7406\u5fb7\u65af\u7684\u83f2\u5c14\u4f26\u5bb6\u65cf\u6536\u7559\uff0c\u5e76\u4e14\u8fd8\u5f53\u4e0a\u4e86\u5deb\u5e08\u5b66\u9662\u7684\u9886\u8896\uff0c\u8fd9\u526f\u8428\u74e6\u68cb\u5c31\u662f\u4ed6\u540e\u6765\u5236\u4f5c\u7684\u3002\u201d</p><p>\u743c\u6069\u4ece\u68cb\u76d8\u4e0a\u62e3\u8d77\u4e00\u679a\u201c\u5deb\u5e08\u201d\uff0c\u8f7b\u8f7b\u629a\u6478\u7740\uff0c\u611f\u89c9\u6307\u5c16\u4f20\u6765\u7684\u51b0\u51b7\u5bd2\u610f\uff0c\u7a81\u7136\u95f4\u9690\u7ea6\u6709\u6240\u660e\u609f\u3002\u201c\u4f60\u8bf4\u4ed6\u662f\u72af\u4e86\u7f6a\uff0c\u6240\u4ee5\u9003\u5230\u5e7d\u6697\u5730\u57df\u2014\u2014\u4ed6\u72af\u4e86\u4ec0\u4e48\u7f6a\uff1f\u201d</p><p>\u201c\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u4ed6\u62ff\u51e1\u4eba\u7684\u7075\u9b42\u6765\u5236\u4f5c\u9b54\u50cf\u3002\u201d</p></article>"}}}]);
