(window.webpackJsonp=window.webpackJsonp||[]).push([[1505],{1528:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e09\u8282 \u6697\u793a",content:"<article><h2>\u7b2c\u5341\u4e09\u8282 \u6697\u793a</h2><p>\u9b54\u50cf\u5236\u4f5c\u662f\u96be\u5ea6\u975e\u5e38\u9ad8\u7684\u9b54\u6cd5\u6280\u827a\uff0c\u4f46\u5236\u4f5c\u65b9\u6cd5\u5374\u4e0d\u7b97\u7e41\u6742\uff0c\u57fa\u672c\u4e0a\u662f\u4e2a\u5deb\u5e08\u90fd\u77e5\u9053\u3002\u4ee5\u5236\u4f5c\u94c1\u9b54\u50cf\u4e3a\u4f8b\uff0c\u7b2c\u4e00\u6b65\u662f\u8bf7\u5de5\u5320\uff08\u6216\u8005\u81ea\u5df1\u52a8\u624b\uff09\u5236\u4f5c\u4e00\u4e2a\u9b54\u50cf\u7684\u8eaf\u4f53\uff0c\u4f53\u79ef\u5927\u5c0f\u3001\u5206\u91cf\u8f7b\u91cd\u4ee5\u53ca\u5404\u90e8\u4f4d\u7684\u89c4\u683c\uff0c\u90fd\u662f\u6709\u5176\u95e8\u9053\u8bb2\u7a76\u7684\uff0c\u4e0d\u662f\u968f\u4fbf\u94f8\u9020\u4e00\u4e2a\u4eba\u5f62\u94c1\u5757\u5c31\u884c\u3002\u7b2c\u4e8c\u6b65\u662f\u5728\u9b54\u50cf\u8eaf\u4f53\u7684\u8868\u9762\u548c\u5185\u90e8\u523b\u4e0a\u5404\u79cd\u7b26\u7eb9\u3001\u5fbd\u8bb0\u548c\u5c0f\u578b\u9b54\u6cd5\u9635\uff0c\u540c\u6837\u4e5f\u90fd\u662f\u6709\u660e\u786e\u7684\u9650\u5b9a\uff0c\u9ad8\u660e\u7684\u5deb\u5e08\u6216\u8bb8\u4f1a\u989d\u5916\u589e\u52a0\u4e00\u4e9b\u53d8\u5316\u3002\u7b2c\u4e09\u6b65\u5219\u662f\u53ec\u5524\u4e00\u53ea\u571f\u5143\u7d20\uff0c\u901a\u8fc7\u4e00\u5b9a\u7684\u9b54\u6cd5\u4eea\u5f0f\uff0c\u5c06\u5b83\u548c\u5236\u4f5c\u5b8c\u6210\u7684\u9b54\u50cf\u8eab\u8eaf\u878d\u4e3a\u4e00\u4f53\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u539f\u672c\u9759\u6b62\u7684\u6b7b\u7269\u5c31\u62e5\u6709\u4e86\u6700\u57fa\u7840\u7684\u667a\u80fd\u548c\u4e00\u5b9a\u7684\u6d3b\u52a8\u80fd\u529b\uff0c\u53d7\u5deb\u5e08\u7684\u63a7\u5236\u9a71\u9063\uff0c\u771f\u6b63\u6210\u4e3a\u4e00\u4e2a\u9b54\u50cf\u4e86\u3002</p><p>\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u8bf4\uff0c\u9b54\u50cf\u5176\u5b9e\u5c31\u662f\u4e00\u4e2a\u7531\u571f\u5143\u7d20\u4f5c\u4e3a\u52a8\u529b\u6838\u5fc3\u7684\u5080\u5121\uff0c\u6216\u8005\u66f4\u901a\u4fd7\u5730\u8bf4\uff0c\u571f\u5143\u7d20\u4e3a\u7075\u9b42\uff0c\u7c98\u571f\u91d1\u5c5e\u5851\u6210\u8eab\u8eaf\uff0c\u4e24\u8005\u5408\u4e8c\u4e3a\u4e00\u4fbf\u662f\u9b54\u50cf\u2014\u2014\u81f3\u4e8e\u4e3a\u4ec0\u4e48\u7279\u522b\u9650\u5b9a\u8981\u571f\u5143\u7d20\uff0c\u81ea\u7136\u4e5f\u662f\u6709\u7f18\u6545\u7684\u3002\u9b54\u50cf\u5927\u591a\u90fd\u4f53\u79ef\u5e9e\u5927\u3001\u5206\u91cf\u6c89\u91cd\uff0c\u4e00\u4e2a\u6807\u51c6\u89c4\u683c\u7684\u94c1\u9b54\u50cf\u8d85\u8fc7\u4e94\u5343\u78c5\uff0c\u63a5\u8fd1\u4e00\u5934\u5927\u8c61\u7684\u91cd\u91cf\u4e86\uff0c\u5018\u82e5\u529b\u6c14\u4e0d\u591f\uff0c\u6839\u672c\u5c31\u65e0\u6cd5\u9a71\u52a8\uff0c\u56db\u5927\u5143\u7d20\u4e4b\u4e2d\uff0c\u552f\u6709\u571f\u5143\u7d20\u6700\u4e3a\u5f3a\u58ee\uff0c\u53ef\u582a\u80dc\u4efb\uff1b\u800c\u4e14\u4ece\u5b89\u5168\u6027\u7684\u89d2\u5ea6\u6765\u8bf4\uff0c\u706b\u5143\u7d20\u66b4\u70c8\uff0c\u98ce\u5143\u7d20\u8fc5\u6377\uff0c\u6c34\u5143\u7d20\u4f7b\u8131\uff0c\u552f\u6709\u571f\u5143\u7d20\u6700\u4e3a\u539a\u91cd\u8e0f\u5b9e\uff0c\u6c89\u7a33\u53ef\u9760\u3002</p><p>\u5f53\u7136\uff0c\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u603b\u662f\u6709\u5229\u6709\u5f0a\uff0c\u5143\u7d20\u751f\u7269\u6700\u5927\u7684\u7f3a\u9677\u5c31\u5728\u4e8e\u5fc3\u667a\u6df7\u6c8c\uff0c\u53ea\u51ed\u672c\u6027\u884c\u4e8b\uff0c\u4ee5\u81f3\u4e8e\u9b54\u50cf\u667a\u80fd\u4f4e\u4e0b\uff0c\u9664\u4e86\u6267\u884c\u6700\u7b80\u5355\u7684\u547d\u4ee4\uff0c\u4f8b\u5982\u201c\u524d\u8fdb\u201d\u3001\u201c\u540e\u9000\u201d\u3001\u201c\u9a7b\u5b88\u201d\uff0c\u6216\u8005\u7a0d\u590d\u6742\u4e00\u70b9\u7684\uff0c\u4f8b\u5982\u201c\u653b\u51fb\u8fdb\u5165\u6b64\u623f\u95f4\u7684\u6240\u6709\u4eba\u201d\u4e4b\u7c7b\uff0c\u4e5f\u5c31\u6ca1\u6cd5\u505a\u66f4\u591a\u7684\u4e8b\u60c5\u4e86\u3002\u9274\u4e8e\u6b64\u5f0a\uff0c\u5012\u4e5f\u4e0d\u662f\u6ca1\u6709\u4eba\u8003\u8651\u8fc7\uff0c\u7528\u66f4\u806a\u660e\u7684\u751f\u7269\u6765\u4ee3\u66ff\u571f\u5143\u7d20\u3002</p><p>\u53ea\u662f\u8bbe\u60f3\u5f52\u8bbe\u60f3\uff0c\u5b9e\u884c\u8d77\u6765\u81ea\u6709\u65e0\u7a77\u7684\u96be\u5904\uff0c\u4efb\u4f55\u4e00\u9879\u9b54\u6cd5\u6280\u827a\u90fd\u51dd\u805a\u7740\u51e0\u4ee3\u751a\u81f3\u5341\u51e0\u4ee3\u3001\u51e0\u5341\u4ee3\u5deb\u5e08\u7684\u5fc3\u8840\uff0c\u60f3\u521b\u65b0\u5e76\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\u7684\u4e8b\u60c5\u3002\u5c31\u743c\u6069\u6240\u8bfb\u5230\u7684\u8bb0\u8f7d\uff0c\u5386\u53f2\u4e0a\u6ca1\u6709\u8c01\u80fd\u591f\u6210\u529f\uff0c\u76f4\u5230\u4eca\u5929\uff0c\u5deb\u5e08\u4eec\u4f9d\u65e7\u8fd8\u662f\u4f7f\u7528\u571f\u5143\u7d20\u6765\u5236\u4f5c\u9b54\u50cf\u3002</p><p>\u201c\u8fd9\u5f88\u6b63\u5e38\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u9b54\u50cf\u6280\u672f\uff0c\u5176\u5b9e\u662f\u4f0a\u739b\u65af\u5361\u7684\u5947\u68b0\u5e08\u53d1\u660e\u7684\uff0c\u867d\u7136\u6d41\u4f20\u51fa\u53bb\uff0c\u4f46\u771f\u6b63\u6838\u5fc3\u7684\u5173\u952e\u673a\u5bc6\uff0c\u552f\u6709\u7687\u5ba4\u624d\u6e05\u695a\u3002\u540e\u6765\u7684\u5deb\u5e08\u4eec\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u662f\u77e5\u5176\u7136\u4e0d\u77e5\u5176\u6240\u4ee5\u7136\uff0c\u81ea\u7136\u4e5f\u5c31\u4e0d\u6307\u671b\u8fd8\u80fd\u6709\u4ec0\u4e48\u521b\u65b0\u4e86\u3002\u201d</p><p>\u6b27\u51ef\u63a5\u7740\u89e3\u91ca\uff0c\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u867d\u7136\u662f\u5947\u68b0\u5e08\u7edf\u6cbb\uff0c\u4f46\u53c8\u5206\u4e24\u4e2a\u9636\u5c42\uff1a\u7687\u5ba4\u548c\u5b66\u8005\u2014\u2014\u524d\u8005\u7684\u610f\u601d\u81ea\u7136\u5f88\u597d\u7406\u89e3\uff0c\u540e\u8005\u5219\u662f\u6307\u90a3\u4e9b\u6ca1\u6709\u7687\u5ba4\u8840\u8109\u7684\u5947\u68b0\u5e08\uff0c\u201c\u5b66\u8005\u201d\u662f\u5bf9\u4ed6\u4eec\u7684\u4e00\u79cd\u5c0a\u79f0\u3002\u8fd9\u79cd\u5212\u5206\u5012\u5e76\u4e0d\u5355\u7eaf\u662f\u4ece\u8eab\u4efd\u5c0a\u5351\u8003\u8651\uff0c\u800c\u662f\u56e0\u4e3a\u4f0a\u739b\u65af\u5361\u6709\u4e0d\u5c11\u795e\u5947\u7384\u5999\u7684\u9b54\u6cd5\u6280\u827a\uff0c\u662f\u8840\u8109\u9650\u5b9a\u7684\uff0c\u552f\u6709\u7687\u5ba4\u5b50\u5f1f\u624d\u80fd\u638c\u63e1\uff1b\u6709\u5f88\u591a\u7279\u6b8a\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u5728\u201c\u5b66\u8005\u201d\u624b\u4e2d\u4fbf\u4e0d\u80fd\u8fd0\u7528\uff0c\u6216\u8005\u4e0d\u80fd\u53d1\u6325\u51fa\u5b8c\u5168\u7684\u6548\u679c\u3002\u4f0a\u739b\u65af\u5361\u88ab\u795e\u738b\u6467\u6bc1\uff0c\u867d\u7136\u4e5f\u6709\u4e00\u4e9b\u5947\u68b0\u5e08\u9003\u51fa\uff0c\u4f46\u7687\u5ba4\u5168\u4f53\u8986\u706d\uff0c\u76f8\u5173\u7684\u8bb0\u5f55\u53c8\u5c3d\u6570\u9500\u6bc1\uff0c\u5f88\u591a\u79d8\u5bc6\u4e5f\u90fd\u5c31\u6b64\u5931\u4f20\u4e86\u3002</p><p>\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u5b58\u7eed\u8fd1\u516d\u5343\u5e74\u7684\u5386\u53f2\uff0c\u7acb\u56fd\u8fdc\u5728\u8010\u745f\u745e\u5c14\u4e4b\u524d\uff0c\u7687\u5e1d\u81ea\u5c01\u5947\u68b0\u5e08\u4e4b\u738b\uff0c\u867d\u7136\u4ece\u6574\u4f53\u4e0a\u8bf4\uff0c\u9b54\u6cd5\u5b66\u8fd8\u5f88\u7c97\u7cd9\uff0c\u6bd4\u4e0d\u4e0a\u540e\u4e16\u7cbe\u6df1\u53d1\u8fbe\uff0c\u4f46\u5728\u70bc\u91d1\u9020\u7269\u3001\u6784\u88c5\u5080\u5121\u8fd9\u65b9\u9762\uff0c\u786e\u5b9e\u662f\u6709\u72ec\u5230\u4e4b\u79d8\u7684\u3002\u65e2\u7136\u80fd\u591f\u53d1\u660e\u9b54\u50cf\u6280\u672f\uff0c\u81ea\u7136\u4e5f\u4f1a\u6709\u4eba\u60f3\u5230\u53ef\u4ee5\u7528\u66f4\u806a\u660e\u7684\u751f\u7269\u4ee3\u66ff\u5143\u7d20\u505a\u6750\u6599\uff0c\u6539\u5584\u9b54\u50cf\u667a\u80fd\u7684\u95ee\u9898\u3002</p><p>\u6bd4\u5143\u7d20\u66f4\u806a\u660e\uff0c\u667a\u80fd\u66f4\u9ad8\u7684\u751f\u7269\u81ea\u7136\u591a\u5f97\u662f\uff0c\u4eba\u7c7b\u3001\u7cbe\u7075\u3001\u5353\u5c14\u3001\u77ee\u4eba\u3001\u4f8f\u5112\u7b49\u7b49\u7b49\u7b49\uff0c\u751a\u81f3\u5c31\u8fde\u517d\u4eba\u4e4b\u7c7b\u90fd\u53ef\u4ee5\u7b97\u4e0a\u3002\u4f46\u8981\u60f3\u7528\u4ed6\u4eec\u5236\u4f5c\u9b54\u50cf\uff0c\u9762\u4e34\u7684\u95ee\u9898\u5c31\u5f88\u591a\u3002</p><p>\u7b2c\u4e00\u4e2a\u95ee\u9898\uff0c\u5236\u4f5c\u9b54\u50cf\uff0c\u7c98\u571f\u3001\u91d1\u5c5e\u7b49\u6750\u6599\u662f\u5851\u6210\u8eab\u8eaf\uff0c\u571f\u5143\u7d20\u7684\u4f5c\u7528\u662f\u7075\u9b42\u6838\u5fc3\u3002\u5143\u7d20\u751f\u7269\u6ca1\u6709\u53cc\u91cd\u672c\u8d28\uff0c\u8eaf\u4f53\u548c\u7075\u9b42\u662f\u4e00\u4f53\u7684\uff0c\u6240\u4ee5\u53ef\u4ee5\u76f4\u63a5\u6293\u6765\u7528\u3002\u4f46\u5018\u82e5\u7528\u51e1\u4eba\uff0c\u90a3\u5c31\u5f97\u5148\u6740\u4eba\uff0c\u652b\u53d6\u7075\u9b42\uff0c\u8fd9\u662f\u89e6\u72af\u4e86\u795e\u660e\u7684\u5927\u5fcc\u3002\u5f53\u7136\uff0c\u4f0a\u739b\u65af\u5361\u662f\u65e0\u4fe1\u8005\u56fd\u5ea6\uff0c\u666e\u904d\u4e0d\u5d07\u62dc\u795e\u7947\uff08\u540e\u671f\u7684\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u5974\u96b6\u9664\u5916\uff09\uff0c\u8fd9\u4e00\u70b9\u5012\u53ef\u4ee5\u5927\u81f4\u5ffd\u7565\u8fc7\u53bb\u3002</p><p>\u7b2c\u4e8c\u4e2a\u95ee\u9898\uff0c\u5219\u662f\u4eba\u7c7b\u4e5f\u7f62\uff0c\u7cbe\u7075\u4e5f\u7f62\uff0c\u5353\u5c14\u4e5f\u7f62\uff0c\u751a\u81f3\u517d\u4eba\u4e5f\u597d\uff0c\u7ec8\u7a76\u90fd\u8fd8\u662f\u592a\u5f31\u5c0f\uff0c\u667a\u529b\u662f\u591f\u4e86\uff0c\u5374\u8fdc\u8fdc\u6bd4\u4e0d\u5f97\u571f\u5143\u7d20\u5f3a\u58ee\uff0c\u6ca1\u529e\u6cd5\u7528\u6765\u9a71\u52a8\u7b28\u91cd\u7684\u9b54\u50cf\u3002\u9488\u5bf9\u8fd9\u8fd9\u4e2a\u96be\u9898\uff0c\u6709\u4e24\u79cd\u65b9\u6848\u53ef\u4f9b\u9009\u62e9\uff1a\u5176\u4e00\u662f\u628a\u9b54\u50cf\u5c3d\u91cf\u5fae\u7f29\u5316\uff0c\u4f53\u79ef\u66f4\u5c0f\uff0c\u5206\u91cf\u66f4\u8f7b\uff0c\u8fd9\u53c8\u7275\u6d89\u5230\u4e00\u7cfb\u5217\u590d\u6742\u7684\u6280\u672f\u96be\u9898\uff1b\u5176\u4e8c\u5219\u662f\u5bfb\u627e\u90a3\u4e9b\u65e2\u806a\u660e\u53c8\u5f3a\u58ee\u7684\u751f\u7269\u6765\u505a\u6750\u6599\uff0c\u4f8b\u5982\u9f99\u5c31\u6700\u5408\u9002\u4e0d\u8fc7\u2026\u2026</p><p>\u7136\u800c\u9f99\u662f\u80fd\u8f7b\u6613\u6293\u6765\u505a\u8bd5\u9a8c\u7684\u4e48\u3002</p><p>\u76f8\u6bd4\u8d77\u6765\uff0c\u7b2c\u4e8c\u79cd\u65b9\u6cd5\u96be\u5ea6\u592a\u9ad8\uff0c\u800c\u4e14\u4e0d\u7b26\u5408\u666e\u53ca\u5316\u7684\u8981\u6c42\uff0c\u6240\u4ee5\u6709\u5fd7\u4e8e\u6b64\u7684\u5947\u68b0\u5e08\uff0c\u57fa\u672c\u90fd\u9009\u62e9\u4e86\u7b2c\u4e00\u79cd\u3002</p><p>\u652b\u53d6\u7075\u9b42\u662f\u96be\u5ea6\u5f88\u9ad8\u7684\u5de5\u4f5c\uff0c\u975e\u7cbe\u64c5\u4ea1\u7075\u672f\u7684\u5927\u5deb\u5e08\u96be\u4ee5\u529e\u5230\uff0c\u4f0a\u739b\u65af\u5361\u5947\u68b0\u5e08\u7684\u64c5\u957f\u5374\u666e\u904d\u96c6\u4e2d\u5728\u7a7a\u95f4\u6cd5\u672f\u548c\u70bc\u91d1\u9020\u7269\u8fd9\u4e24\u65b9\u9762\uff0c\u8981\u5f00\u4f20\u9001\u95e8\u662f\u62ff\u624b\u597d\u620f\uff0c\u8981\u5236\u4f5c\u6784\u88c5\u4f53\u4e5f\u4e0d\u662f\u95ee\u9898\uff0c\u4f46\u4ea1\u7075\u672f\u7684\u9020\u8be3\u5c31\u666e\u904d\u4e0d\u9ad8\u4e86\u3002\u6781\u5c11\u6570\u8fd9\u65b9\u9762\u7684\u4eba\u624d\uff0c\u4e5f\u90fd\u96c6\u4e2d\u5728\u7687\u5ba4\uff0c\u6240\u4ee5\u9b54\u50cf\u667a\u80fd\u5316\u7684\u7814\u7a76\uff0c\u57fa\u672c\u662f\u7687\u5ba4\u5728\u8fdb\u884c\u3002</p><p>\u201c\u4f20\u8bf4\u5728\u5e1d\u56fd\u8986\u706d\u7684\u524d\u5915\uff0c\u7687\u5ba4\u7ec8\u4e8e\u6210\u529f\u7814\u7a76\u51fa\u4e86\u628a\u9b54\u50cf\u5fae\u578b\u5316\u7684\u6280\u672f\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u743c\u6069\u89c9\u5f97\u4ed6\u7684\u58f0\u97f3\u4f3c\u4e4e\u6709\u70b9\u9634\u6c89\u6c89\u7684\uff0c\u4f46\u6216\u8bb8\u662f\u9519\u89c9\uff0c\u201c\u7528\u975e\u5e38\u8f7b\u5de7\u7684\u91d1\u5c5e\uff0c\u4f8b\u5982\u79d8\u94f6\u8fd9\u79cd\u505a\u6750\u6599\uff0c\u5c3d\u53ef\u80fd\u51cf\u8f7b\u91cd\u91cf\uff0c\u540c\u65f6\u4f53\u79ef\u4e5f\u5927\u5927\u7f29\u5c0f\uff0c\u63a5\u8fd1\u4eba\u7c7b\u7684\u4f53\u578b\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u4fbf\u53ef\u4ee5\u7528\u4eba\u7c7b\u6216\u8005\u7c7b\u4f3c\u751f\u7269\u7684\u7075\u9b42\u6765\u9a71\u52a8\uff0c\u9020\u51fa\u6765\u7684\u9b54\u50cf\u7075\u5de7\u3001\u654f\u6377\uff0c\u6709\u8db3\u591f\u7684\u667a\u80fd\uff0c\u5982\u679c\u4f2a\u88c5\u5f97\u597d\uff0c\u5192\u5145\u6d3b\u4eba\u90fd\u4e0d\u662f\u95ee\u9898\u2014\u2014\u4ed6\u4eec\u628a\u8fd9\u79cd\u9b54\u50cf\u53eb\u505a\u79d8\u5076\uff08Nimblewright\uff09\u3002\u201d</p><p>\u5f53\u7136\uff0c\u8fd9\u4ec5\u4ec5\u53ea\u662f\u4f20\u8bf4\uff0c\u771f\u76f8\u5df2\u7ecf\u968f\u7740\u4f0a\u739b\u65af\u5361\u7684\u8986\u706d\u800c\u65e0\u4ece\u8003\u8bc1\u3002</p><p>\u4efb\u4f55\u7fa4\u4f53\u4e2d\uff0c\u603b\u4f1a\u6709\u79bb\u7ecf\u53db\u9053\u4e4b\u8f88\uff0c\u5c31\u8fde\u732a\u91cc\u9762\u90fd\u4f1a\u6709\u7279\u7acb\u72ec\u884c\u7684\u3002\u6709\u90a3\u4e48\u51e0\u4f4d\u201c\u5b66\u8005\u201d\uff0c\u504f\u504f\u8981\u548c\u7687\u5ba4\u5531\u53cd\u8c03\uff0c\u6ca1\u6709\u9009\u62e9\u9b54\u50cf\u5fae\u7f29\u5316\u7684\u9053\u8def\uff0c\u800c\u662f\u8003\u8651\u5982\u4f55\u589e\u5f3a\u7075\u9b42\u3002\u5f53\u7136\uff0c\u6293\u9f99\u6765\u4f5c\u5b9e\u9a8c\u662f\u80af\u5b9a\u4e0d\u53ef\u80fd\uff0c\u5de8\u4eba\u52c9\u5f3a\u9002\u5408\uff0c\u4f46\u4e5f\u592a\u7f55\u89c1\u4e86\u3002\u4ed6\u4eec\u6700\u540e\u6253\u7684\u4e3b\u610f\uff0c\u662f\u201c\u7075\u9b42\u878d\u5408\u201d\u3002</p><p>\u4fbf\u5982\u4e00\u4e2a\u94c1\u9b54\u50cf\uff0c\u5355\u72ec\u4e00\u4e2a\u4eba\u7c7b\u7684\u7075\u9b42\u4e0d\u8db3\u4ee5\u9a71\u52a8\uff0c\u90a3\u4e48\u5c31\u591a\u585e\u51e0\u4e2a\u8fdb\u53bb\u3002\u51e0\u5341\u4eba\u4e0a\u767e\u4eba\u7684\u7075\u9b42\u5408\u5728\u4e00\u8d77\uff0c\u81ea\u7136\u529b\u91cf\u5927\u589e\uff0c\u8fd9\u5c31\u662f\u6240\u8c13\u7684\u7075\u9b42\u878d\u5408\u4e86\u3002</p><p>\u201c\u7b49\u4e0b\uff0c\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u4f60\u524d\u9762\u8bf4\uff0c\u64c5\u957f\u4ea1\u7075\u672f\u7684\u5947\u68b0\u5e08\u6781\u5c11\uff0c\u90fd\u88ab\u7687\u5ba4\u5784\u65ad\u4e86\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u6211\u867d\u7136\u4e0d\u61c2\u4ea1\u7075\u672f\uff0c\u4f46\u8fd9\u7075\u9b42\u878d\u5408\uff0c\u5206\u660e\u6bd4\u7075\u9b42\u652b\u53d6\u8fd8\u8981\u96be\u5f97\u591a\u3002\u90a3\u4e9b\u5b66\u8005\u53c8\u600e\u4e48\u2026\u2026\u201d</p><p>\u201c\u6240\u4ee5\u4ed6\u4eec\u627e\u5230\u4e86\u6076\u9b54\u3002\u201d</p><p>\u7075\u9b42\u4e0d\u662f\u6a61\u76ae\u6ce5\uff0c\u53ef\u4ee5\u968f\u4fbf\u634f\u6765\u634f\u53bb\uff0c\u4e3a\u4e86\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\uff0c\u8fd9\u51e0\u4e2a\u201c\u5b66\u8005\u201d\u4fbf\u79d8\u5bc6\u4e0e\u6076\u9b54\u8054\u624b\u5408\u4f5c\uff0c\u6076\u9b54\u662f\u73a9\u5f04\u7075\u9b42\u7684\u884c\u5bb6\uff0c\u6700\u64c5\u957f\u6b64\u9053\u3002\u4f46\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u5f8b\u6cd5\u660e\u6587\u7981\u6b62\u51e1\u4eba\u52fe\u7ed3\u6076\u9b54\uff0c\u8fd9\u4e48\u505a\u4fbf\u662f\u72af\u7f6a\u4e86\u3002</p><p>\u201c\u4f0a\u739b\u65af\u5361\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u53cd\u611f\u6076\u9b54\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u5176\u5b9e\u66fe\u7ecf\u6709\u4e00\u6bb5\u65f6\u95f4\uff0c\u4f0a\u739b\u65af\u5361\u4eba\u548c\u6076\u9b54\u7684\u5173\u7cfb\u662f\u4e0d\u9519\u7684\uff0c\u53ea\u662f\u4e16\u4e8b\u65e0\u5e38\u2026\u2026\u53cd\u6b63\u540e\u6765\u662f\u7ffb\u8138\u4e86\u3002\u201d</p><p>\u201c\u4e00\u5b9a\u662f\u4f60\u4eec\u505a\u574f\u4e8b\u4e86\u5427\u3002\u201d</p><p>\u201c\u54ea\u6709\u2026\u2026\u4f60\u8fd9\u662f\u8bfd\u8c24\uff01\u201d</p><p>\u4e00\u65b9\u9762\u662f\u52fe\u7ed3\u6076\u9b54\uff0c\u53e6\u4e00\u65b9\u9762\u662f\u6740\u4eba\u592a\u591a\u3002\u8d70\u8fd9\u79cd\u7075\u9b42\u878d\u5408\u7684\u9014\u5f84\uff0c\u4e00\u6b21\u8bd5\u9a8c\u5f80\u5f80\u5c31\u8981\u6d88\u8017\u6389\u51e0\u5341\u4e0a\u767e\u6761\u4eba\u547d\uff0c\u867d\u8bf4\u4f0a\u739b\u65af\u5361\u662f\u5deb\u5e08\u6267\u653f\uff0c\u5947\u68b0\u5e08\u7684\u5730\u4f4d\u9ad8\uff0c\u5e73\u6c11\u767e\u59d3\u548c\u5974\u96b6\u7684\u6027\u547d\u4e0d\u503c\u94b1\uff0c\u4f46\u8fd9\u4e48\u73a9\u4e5f\u672a\u514d\u592a\u8fc7\u706b\u3002\u957f\u6b64\u4e0b\u53bb\uff0c\u4e0d\u7b49\u667a\u80fd\u9b54\u50cf\u7814\u7a76\u51fa\u6765\uff0c\u5e1d\u56fd\u5148\u8981\u5b8c\u86cb\u4e86\u3002</p><p>\u7406\u6240\u5f53\u7136\u7684\uff0c\u8fd9\u51e0\u4e2a\u5b66\u8005\u6210\u4e86\u7f6a\u72af\uff0c\u5927\u90fd\u88ab\u6293\u6355\u5904\u6b7b\uff0c\u4f46\u5176\u4e2d\u6709\u4e00\u4e2a\u5374\u9003\u4e86\u51fa\u6765\uff0c\u6f5c\u5165\u5e7d\u6697\u5730\u57df\uff0c\u5316\u540d\u68b5\u94ce\u5c14\uff0c\u4ee5\u5353\u5c14\u7684\u8eab\u4efd\u6765\u5230\u74dc\u7406\u5fb7\u65af\uff0c\u88ab\u83f2\u5c14\u4f26\u5bb6\u65cf\u63a5\u7eb3\u2014\u2014\u4ed6\u5c31\u662f\u743c\u6069\u624b\u4e0a\u8fd9\u526f\u8428\u74e6\u68cb\u7684\u5236\u4f5c\u8005\u4e86\u3002</p><p>\u201c\u4ed6\u7684\u7814\u7a76\u663e\u7136\u6ca1\u6709\u4e2d\u6b62\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u8fd9\u526f\u8428\u74e6\u68cb\u5c31\u662f\u8bc1\u660e\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u6210\u529f\u4e86\uff1f\u201d</p><p>\u201c\u4e0d\u80fd\u7b97\u6210\u529f\u3002\u201d</p><p>\u6b27\u51ef\u8bf4\u7740\uff0c\u4f38\u624b\u4ece\u68cb\u76d8\u4e0a\u62c8\u8d77\u4e00\u679a\u68cb\u5b50\uff0c\u4e22\u5728\u5730\u4e0a\uff0c\u6d53\u70df\u817e\u8d77\u4e2d\u53d8\u6210\u4e86\u4e00\u4e2a\u5168\u526f\u6b66\u88c5\u7684\u5353\u5c14\u6218\u58eb\uff0c\u4e00\u624b\u6267\u5251\u4e00\u624b\u6301\u76fe\uff0c\u5757\u5934\u5927\u7ea6\u662f\u5bfb\u5e38\u5353\u5c14\u7684\u4e09\u56db\u500d\uff0c\u76ae\u80a4\u900f\u7740\u91d1\u5c5e\u5149\u6cfd\uff0c\u660e\u663e\u4e0d\u662f\u8840\u8089\u4e4b\u8eaf\u3002\u6b27\u51ef\u6307\u4e86\u6307\u77f3\u684c\uff0c\u4e0b\u4e86\u4e2a\u6307\u4ee4\uff0c\u5353\u5c14\u6218\u58eb\u5e94\u58f0\u4e0a\u524d\uff0c\u4e00\u5251\u780d\u5728\u77f3\u684c\u7684\u8fb9\u6cbf\uff0c\u767b\u65f6\u706b\u661f\u56db\u6e85\uff0c\u63a5\u7740\u5feb\u6b65\u540e\u9000\uff0c\u77ac\u95f4\u56de\u5230\u539f\u5730\uff0c\u52a8\u4f5c\u5341\u5206\u5e72\u8106\u5229\u843d\u3002</p><p>\u201c\u770b\u4ed6\u7684\u773c\u775b\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u770b\u773c\u795e\u3002\u201d</p><p>\u6ca1\u6709\u773c\u795e\uff0c\u4e00\u7247\u6f20\u7136\u3002</p><p>\u201c\u8fd9\u5c31\u662f\u95ee\u9898\u6240\u5728\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u68b5\u94ce\u5c14\u9020\u51fa\u6765\u7684\u9b54\u50cf\uff0c\u4f9d\u65e7\u6ca1\u6709\u667a\u529b\u3002\u4ed6\u628a\u591a\u4e2a\u7075\u9b42\u5f3a\u884c\u878d\u5408\u5230\u4e00\u8d77\uff0c\u4e00\u4e9b\u6280\u80fd\u91cd\u590d\u5f3a\u5316\uff0c\u6240\u4ee5\u4fdd\u7559\u4e86\u4e0b\u6765\uff0c\u4f46\u5374\u5bfc\u81f4\u76f8\u4e92\u51b2\u7a81\uff0c\u610f\u8bc6\u3001\u601d\u7ef4\u3001\u8bb0\u5fc6\u3001\u60c5\u611f\uff0c\u6240\u6709\u7684\u4e00\u5207\u5168\u90fd\u4e27\u5931\u4e86\u2014\u2014\u6ca1\u6709\u4e86\u8fd9\u4e9b\uff0c\u8fd8\u8c08\u4ec0\u4e48\u667a\u80fd\u3002\u201d</p><p>\u201c\u4e5f\u4e0d\u9519\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u81f3\u5c11\u5f88\u7075\u6d3b\uff0c\u6bd4\u4e00\u822c\u7684\u9b54\u50cf\u654f\u6377\u591a\u4e86\u3002\u201d</p><p>\u9b54\u50cf\u7684\u4e24\u5927\u7f3a\u9677\uff0c\u6700\u91cd\u8981\u7684\u81ea\u7136\u662f\u7f3a\u4e4f\u667a\u80fd\uff0c\u5176\u6b21\u662f\u592a\u7b28\u62d9\u3002\u68b5\u94ce\u5c14\u7684\u8fd9\u4e9b\u68cb\u5b50\u9b54\u50cf\uff0c\u81f3\u5c11\u514b\u670d\u4e86\u7b2c\u4e8c\u4e2a\u7f3a\u9677\uff0c\u5df2\u7ecf\u8db3\u4ee5\u81ea\u8c6a\u4e86\u3002\u53ea\u4e0d\u8fc7\uff0c\u68b5\u94ce\u5c14\u7684\u8fd9\u79cd\u6280\u672f\uff0c\u4f3c\u4e4e\u6ca1\u6709\u4f20\u627f\u4e0b\u6765\uff0c\u5426\u5219\u74dc\u7406\u5fb7\u65af\u7684\u5353\u5c14\u4eec\u8fd9\u4e48\u591a\u5e74\uff0c\u603b\u8be5\u5f04\u51fa\u4e00\u652f\u9b54\u50cf\u5927\u519b\uff0c\u65e9\u5c31\u53ef\u4ee5\u628a\u4f0a\u5361\u6c99\u57ce\u63a8\u5e73\u4e86\u3002</p><p>\u201c\u90a3\u6ca1\u529e\u6cd5\uff0c\u53ea\u80fd\u602a\u4ed6\u4eec\u81ea\u5df1\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u68b5\u94ce\u5c14\u8fd8\u6ca1\u5b8c\u6210\u8fd9\u526f\u8428\u74e6\u68cb\uff0c\u5c31\u88ab\u5904\u6b7b\u4e86\uff0c\u4ed6\u63d0\u524d\u6bc1\u6389\u4e86\u6240\u6709\u7684\u7b14\u8bb0\u548c\u6574\u7406\u8d44\u6599\uff0c\u6240\u4ee5\uff0c\u201d\u4ed6\u644a\u5f00\u624b\uff0c\u201c\u5931\u4f20\u4e86\u3002\u201d</p><p>\u201c\u4ed6\u53c8\u72af\u4e86\u4ec0\u4e48\u7f6a\uff1f\u201d</p><p>\u201c\u6740\u5bb3\u5973\u796d\u53f8\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u8fd9\u91cc\u6709\u5341\u679a\u98df\u4eba\u9b54\u68cb\u5b50\uff0c\u7528\u4e86\u5927\u7ea6\u4e09\u767e\u4e2a\u98df\u4eba\u9b54\u5974\u96b6\u7684\u7075\u9b42\uff1b\u6709\u56db\u679a\u6218\u58eb\u68cb\u5b50\uff0c\u7528\u4e86\u5927\u7ea6\u4e24\u767e\u4e2a\u5353\u5c14\u6218\u58eb\u7075\u9b42\uff1b\u6709\u56db\u679a\u5deb\u5e08\u68cb\u5b50\uff0c\u636e\u8bf4\u68b5\u94ce\u5c14\u63a5\u4efb\u5deb\u5e08\u5b66\u9662\u9996\u5e2d\u6559\u5b98\u4ee5\u6765\uff0c\u6bcf\u5e74\u7684\u6b7b\u4ea1\u540d\u989d\u90fd\u88ab\u4ed6\u4e00\u4e2a\u4eba\u7528\u5149\u4e86\u8fd8\u4e0d\u591f\u2014\u2014\u8fd8\u6709\u56db\u679a\u7267\u5e08\uff0c\u4e24\u679a\u4e3b\u6bcd\u2026\u2026\u201d</p><p>\u201c\u660e\u767d\u4e86\u2026\u2026\u7b49\u7b49\uff0c\u4f60\u521a\u624d\u8bf4\uff0c\u68b5\u94ce\u5c14\u8fd8\u6ca1\u5b8c\u6210\u8fd9\u526f\u68cb\u5c31\u6b7b\u4e86\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u526f\u68cb\u2026\u2026\u201d</p><p>\u201c\u662f\u4e2a\u6b8b\u7f3a\u54c1\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u98df\u4eba\u9b54\u3001\u6218\u58eb\u3001\u5deb\u5e08\u3001\u7267\u5e08\u90fd\u53ef\u4ee5\u53d8\u5f62\u6210\u76f8\u5e94\u7684\u9b54\u50cf\uff0c\u4f46\u2018\u4e3b\u6bcd\u2019\u4e0d\u80fd\u3002\u201d</p><p>\u201c\u4f60\u62ff\u5c45\u7136\u62ff\u4f2a\u52a3\u4ea7\u54c1\u6765\u6b3a\u9a97\u6211\uff01\u201d</p><p>\u201c\u9996\u5148\uff0c\u5b83\u4e0d\u4f2a\uff0c\u7edd\u5bf9\u771f\u54c1\uff1b\u5176\u6b21\uff0c\u5b83\u662f\u6709\u8fd9\u4e48\u70b9\u5c0f\u7f3a\u9677\uff0c\u6211\u672c\u6765\u662f\u60f3\u544a\u8bc9\u4f60\u7684\uff0c\u8c01\u6599\u5230\u4f60\u8fd9\u4e48\u7740\u6025\uff0c\u4e0d\u7b49\u6211\u89e3\u91ca\u5c31\u8feb\u4e0d\u53ca\u5f85\u62a2\u8fc7\u53bb\u4e86\u2014\u2014\u8fd9\u660e\u663e\u4e0d\u662f\u6211\u7684\u8d23\u4efb\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u7b97\u4e86\u3002\u201d</p><p>\u68b5\u94ce\u5c14\u4e3a\u4e86\u8fd9\u526f\u8428\u74e6\u68cb\u5927\u5f00\u6740\u6212\uff0c\u7ec8\u7a76\u62db\u81f4\u4f17\u6012\u8eab\u6b7b\uff0c\u4e5f\u662f\u60c5\u7406\u4e4b\u4e2d\u3002\u74dc\u7406\u5fb7\u65af\u7684\u5353\u5c14\u518d\u591a\uff0c\u4e5f\u7ecf\u4e0d\u8d77\u8fd9\u6837\u7684\u6d88\u8017\uff0c\u5018\u82e5\u53ea\u662f\u98df\u4eba\u9b54\u5974\u96b6\u6216\u8005\u7537\u6027\u6218\u58eb\u5012\u7f62\u4e86\uff0c\u5c31\u7b97\u662f\u5411\u5deb\u5e08\u5b66\u5f92\u4e0b\u624b\u4e5f\u672a\u5fc5\u4e0d\u80fd\u5bb9\u5fcd\uff0c\u4f46\u6d89\u53ca\u5230\u5973\u796d\u53f8\uff0c\u90a3\u5c31\u662f\u5e95\u7ebf\u4e86\u3002\u8fd9\u4f4d\u5947\u68b0\u5e08\u660e\u663e\u5df2\u7ecf\u5316\u8eab\u4e3a\u79d1\u5b66\u72c2\u4eba\uff0c\u5982\u679c\u8ba9\u4ed6\u518d\u7ee7\u7eed\u4e0b\u53bb\uff0c\u6574\u4e2a\u57ce\u5e02\u90fd\u8981\u53d8\u6210\u5893\u5730\u4e86\u3002</p><p>\u53ef\u60dc\u4e86\uff0c\u743c\u6069\u539f\u672c\u8fd8\u60f3\u80fd\u628a\u8fd9\u79cd\u6280\u672f\u63a8\u5e7f\u5f00\u6765\uff0c\u9634\u9b42\u57ce\u6b63\u5728\u51c6\u5907\u5efa\u7acb\u9b54\u50cf\u519b\u56e2\uff0c\u5012\u662f\u7528\u5f97\u4e0a\uff0c\u5230\u65f6\u5019\u4e5f\u662f\u4e00\u4ef6\u5927\u529f\u3002\u5982\u4eca\u542c\u8bf4\u65b9\u6cd5\u65e9\u5c31\u5931\u4f20\uff0c\u4e0d\u514d\u6709\u4e9b\u9057\u61be\uff0c\u4e0d\u8fc7\u4e5f\u662f\u6ca1\u529e\u6cd5\u3002</p><p>\u5514\uff0c\u7b49\u7b49\u2026\u2026\u603b\u89c9\u5f97\u54ea\u91cc\u4e0d\u592a\u5bf9\u52b2\u3002</p><p>\u7136\u800c\u63a5\u4e0b\u6765\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u53d1\u751f\uff0c\u6b27\u51ef\u628a\u8fd9\u526f\u8428\u74e6\u68cb\u7684\u6765\u5386\u89e3\u91ca\u5b8c\uff0c\u4fbf\u4e0d\u518d\u591a\u7559\uff0c\u8bf4\u8981\u8d76\u53bb\u5965\u7075\u591a\u5c14\u3002\u5012\u662f\u4e34\u8d70\u4e4b\u524d\uff0c\u4eff\u4f5b\u6709\u610f\u65e0\u610f\u5730\u900f\u9732\u4e86\u70b9\u6d88\u606f\u3002</p><p>\u201c\u8981\u6ce8\u610f\u5b89\u5168\u554a\uff0c\u743c\u6069\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u86db\u540e\u5df2\u7ecf\u590d\u82cf\uff0c\u7ecf\u8fc7\u8fd9\u6bb5\u65f6\u95f4\u7684\u86f0\u4f0f\uff0c\u5f88\u5feb\u5c31\u8981\u6709\u52a8\u4f5c\u4e86\u3002\u5317\u65b9\u6709\u4e24\u4e2a\u5353\u5c14\u57ce\u5e02\uff0c\u56e0\u4e3a\u5d07\u62dc\u7684\u662f\u5176\u4ed6\u5353\u5c14\u795e\u7947\uff0c\u524d\u51e0\u5929\u88ab\u5979\u7684\u6559\u4f1a\u7ed9\u6467\u6bc1\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5bf9\u4e8e\u90a3\u4e24\u4e2a\u4e0d\u5e78\u7684\u5353\u5c14\u57ce\u5e02\uff0c\u743c\u6069\u6df1\u8868\u540c\u60c5\uff0c\u4f46\u4e5f\u5c31\u4ec5\u6b64\u800c\u5df2\uff0c\u81ea\u5df1\u7684\u9ebb\u70e6\u5df2\u7ecf\u591f\u591a\uff0c\u5b9e\u5728\u6ca1\u5fc5\u8981\u8fd8\u53bb\u5173\u5fc3\u522b\u4eba\u3002\u86db\u540e\u590d\u82cf\uff0c\u8fd9\u662f\u65e9\u5c31\u77e5\u9053\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u81ea\u7136\u4e5f\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u5e76\u4e0d\u5982\u4f55\u62c5\u5fc3\u3002</p><p>\u6b27\u51ef\u521a\u8d70\uff0c\u6885\u83f2\u65af\u63a8\u95e8\u8fdb\u6765\uff0c\u4e00\u773c\u770b\u89c1\u77f3\u684c\u4e0a\u7684\u8428\u74e6\u68cb\uff0c\u201c\u4f60\u5728\u4e0b\u68cb\u2026\u2026\u55ef\uff0c\u8fd9\u662f\u9ed1\u6697\u7cbe\u7075\u7684\u8428\u74e6\u68cb\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u743c\u6069\u628a\u4e8b\u60c5\u7ecf\u8fc7\u5927\u81f4\u8bb2\u8ff0\u4e86\u4e00\u904d\uff0c\u6885\u83f2\u65af\u5012\u5e76\u4e0d\u592a\u5728\u610f\uff0c\u4f46\u89c1\u4ed6\u795e\u8272\u95f4\u9690\u7ea6\u6709\u90c1\u90c1\u4e4b\u8272\uff0c\u4e0d\u7531\u5f97\u6709\u4e9b\u5947\u602a\u3002\u201c\u600e\u4e48\u4e86\uff1f\u201d\u5979\u95ee\uff0c\u201c\u8fd9\u4e0d\u662f\u5f88\u597d\u7684\u4e8b\u60c5\u5417\uff0c\u53cd\u6b63\u90a3\u547d\u5323\u4f60\u62ff\u7740\u4e5f\u6ca1\u7528\u3002\u201d</p><p>\u201c\u4e0d\u662f\u8bf4\u8fd9\u4e2a\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u6211\u603b\u89c9\u5f97\u4ec0\u4e48\u5730\u65b9\u4e0d\u592a\u5bf9\u52b2\u3002\u201d</p><p>\u201c\u4f60\u5728\u62c5\u5fc3\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u8bf4\u4e0d\u6e05\u695a\u3002\u201d</p><p>\u6885\u83f2\u65af\u7565\u7565\u6c89\u601d\uff0c\u201c\u4f60\u662f\u89c9\u5f97\u4ed6\u8fd9\u6b21\u6765\uff0c\u53e6\u6709\u5176\u4ed6\u76ee\u7684\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u5e94\u8be5\u662f\uff0c\u201d\u743c\u6069\u88ab\u5979\u4e00\u63d0\u9192\uff0c\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u6211\u603b\u89c9\u5f97\uff0c\u4ed6\u6765\u8fd9\u4e00\u8d9f\uff0c\u5176\u5b9e\u6700\u4e3b\u8981\u7684\u76ee\u7684\uff0c\u5e76\u4e0d\u662f\u4e3a\u4e86\u62ff\u547d\u5323\u53bb\u7ed9\u7075\u5438\u602a\u9001\u793c\uff0c\u800c\u662f\u8981\u628a\u8fd9\u526f\u8428\u74e6\u68cb\u7ed9\u6211\u3002\u201d</p><p>\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u505a\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u8fd9\u53ea\u662f\u4e00\u79cd\u611f\u89c9\u3002\u4ed6\u597d\u50cf\u662f\u5728\u501f\u8fd9\u4e2a\u673a\u4f1a\u6697\u793a\u6211\u4ec0\u4e48\u2026\u2026\u201d\u743c\u6069\u6447\u6447\u5934\uff0c\u201c\u6211\u4e5f\u8bf4\u4e0d\u597d\u3002\u201d</p><p>\u201c\u6697\u793a\uff1f\u201d\u6885\u83f2\u65af\u79c0\u7709\u5fae\u5fae\u4e00\u6311\uff0c\u201c\u96be\u9053\u662f\u6697\u793a\u4f60\u4eff\u6548\u90a3\u4f4d\u5947\u68b0\u5e08\u4e48\uff1f\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u5386\u53f2\u4e0a\u68b5\u94ce\u5c14\u662f\u548c\u6076\u9b54\u5408\u4f5c\uff0c\u7814\u7a76\u51fa\u4e86\u8fd9\u79cd\u9b54\u50cf\u6280\u672f\u3002\u73b0\u5728\u743c\u6069\u662f\u70bc\u91d1\u5e08\uff0c\u6b27\u51ef\u662f\u6076\u9b54\uff0c\u8981\u8bf4\u8d77\u6765\u5012\u8fd8\u771f\u6709\u70b9\u50cf\u2014\u2014\u5f53\u7136\uff0c\u4eba\u8981\u6709\u81ea\u77e5\u4e4b\u660e\u3002</p><p>\u201c\u6211\u54ea\u91cc\u591f\u8d44\u683c\u548c\u68b5\u94ce\u5c14\u76f8\u6bd4\u3002\u4ed6\u5982\u679c\u771f\u6709\u6b64\u610f\uff0c\u5e94\u8be5\u76f4\u63a5\u53bb\u627e\u5e03\u96f7\u7eb3\u65af\u6bbf\u4e0b\u624d\u5bf9\u3002\u201d</p><p>\u201c\u90a3\u4f60\u8fd8\u62c5\u5fc3\u4ec0\u4e48\u5462\uff0c\u201d\u6885\u83f2\u65af\u7565\u5e26\u8f9b\u8fa3\u5730\u8bf4\uff0c\u201c\u4f60\u6839\u672c\u5c31\u6ca1\u6709\u62c5\u5fc3\u7684\u2018\u8d44\u683c\u2019\u554a\u3002\u518d\u8bf4\u4ed6\u662f\u4e2a\u6076\u9b54\uff0c\u6076\u9b54\u505a\u4e8b\u60c5\uff0c\u672c\u6765\u5c31\u672a\u5fc5\u6709\u4ec0\u4e48\u9053\u7406\u53ef\u8a00\uff0c\u4f60\u504f\u8981\u53bb\u63a8\u6d4b\u5206\u6790\uff0c\u7eaf\u7cb9\u81ea\u627e\u6ca1\u8da3\u3002\u201d</p><p>\u201c\u4e5f\u662f\u3002\u201d</p><p>\u65e2\u7136\u4e00\u65f6\u7406\u4e0d\u6e05\u5934\u7eea\uff0c\u7d22\u6027\u4e5f\u5c31\u4e0d\u518d\u591a\u60f3\uff0c\u743c\u6069\u5f00\u59cb\u548c\u6885\u83f2\u65af\u7814\u7a76\u8fd9\u526f\u8428\u74e6\u68cb\u3002\u6b27\u51ef\u8d70\u5f97\u5306\u5fd9\uff0c\u867d\u7136\u544a\u8bc9\u4e86\u6fc0\u6d3b\u53e3\u4ee4\uff0c\u4f46\u5177\u4f53\u7684\u8be6\u60c5\u8fd8\u8981\u81ea\u5df1\u7422\u78e8\uff0c\u597d\u5728\u8fd9\u539f\u672c\u4e5f\u5c31\u662f\u5deb\u5e08\u7684\u4e50\u8da3\u4e4b\u4e00\uff0c\u5c31\u597d\u50cf\u53e4\u73a9\u5bb6\u6536\u5230\u4e86\u65b0\u6587\u7269\uff0c\u603b\u8981\u628a\u73a9\u4e00\u6bb5\u65f6\u95f4\uff0c\u5982\u679c\u9644\u5e26\u4e00\u4efd\u8be6\u7ec6\u8bf4\u660e\u4e66\uff0c\u90a3\u5c31\u662f\u5bb6\u7535\u4e86\u3002</p><p>\u4e00\u7814\u7a76\u624d\u53d1\u73b0\uff0c\u8fd9\u526f\u68cb\u8fd8\u5927\u6709\u95e8\u9053\uff0c\u5e76\u975e\u60f3\u50cf\u5f97\u90a3\u6837\u7b80\u5355\u3002\u743c\u6069\u8f6e\u6d41\u8bd5\u9a8c\u4e86\u6bcf\u4e00\u679a\u68cb\u5b50\uff08\u9664\u4e86\u4e24\u679a\u2018\u4e3b\u6bcd\u2019\u4e4b\u5916\uff09\uff0c\u90fd\u80fd\u6210\u529f\u53d8\u6210\u76f8\u5e94\u7684\u9b54\u50cf\uff0c\u4e00\u5207\u6b63\u5e38\uff0c\u4f46\u5f53\u4ed6\u60f3\u540c\u65f6\u53ec\u51fa\u591a\u4e2a\u9b54\u50cf\uff0c\u73a9\u4e00\u628a\u201c\u6492\u8c46\u6210\u5175\u201d\u7684\u65f6\u5019\uff0c\u95ee\u9898\u5c31\u6765\u4e86\u3002\u9996\u5148\u53d1\u73b0\u7684\uff0c\u5c31\u662f\u4e0d\u540c\u9635\u8425\u7684\u68cb\u5b50\u65e0\u6cd5\u540c\u65f6\u4f7f\u7528\u3002</p><p>\u8428\u74e6\u68cb\u5206\u7ea2\u9ed1\u53cc\u8272\uff0c\u5404\u6709\u4e00\u679a\u201c\u4e3b\u6bcd\u201d\uff0c\u4e24\u679a\u201c\u7267\u5e08\u201d\uff0c\u4e24\u679a\u201c\u5deb\u5e08\u201d\uff0c\u4e24\u679a\u201c\u6218\u58eb\u201d\u548c\u4e94\u679a\u201c\u98df\u4eba\u9b54\u5974\u96b6\u201d\uff0c\u4e00\u5171\u4e8c\u5341\u56db\u679a\u68cb\u5b50\u3002\u743c\u6069\u5148\u6254\u4e0b\u4e00\u679a\u9ed1\u8272\u201c\u98df\u4eba\u9b54\u201d\uff0c\u53d8\u6210\u9b54\u50cf\uff0c\u7136\u540e\u4ed6\u53ef\u4ee5\u7ee7\u7eed\u518d\u6fc0\u6d3b\u5176\u4ed6\u9ed1\u8272\u68cb\u5b50\uff0c\u4f46\u5374\u6ca1\u529e\u6cd5\u52a8\u7528\u7ea2\u8272\u68cb\u5b50\u4e86\u3002</p><p>\u201c\u539f\u6765\u8fd8\u6709\u8fd9\u79cd\u9650\u5236\uff0c\u201d\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u540d\u4e49\u4e0a\u662f\u4e8c\u5341\u56db\u679a\u68cb\u5b50\uff0c\u5176\u5b9e\u53ea\u80fd\u7528\u4e00\u534a\uff0c\u8fd8\u8981\u518d\u53bb\u6389\u4e00\u679a\u4e3b\u6bcd\uff0c\u771f\u662f\u6d6a\u8d39\u2026\u2026\u6709\u529e\u6cd5\u4e86\uff01\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u6211\u4eec\u4e00\u4eba\u63a7\u5236\u4e00\u534a\u68cb\u5b50\u4e0d\u5c31\u662f\u4e86\u3002\u201d</p><p>\u8fd9\u662f\u4e2a\u597d\u4e3b\u610f\uff0c\u7136\u800c\u5b9e\u884c\u7684\u65f6\u5019\u9047\u5230\u4e86\u610f\u5916\u969c\u788d\u3002\u6885\u83f2\u65af\u8bb0\u7262\u4e86\u53e3\u4ee4\uff0c\u4f46\u65e0\u8bba\u600e\u4e48\u52aa\u529b\uff0c\u5374\u6ca1\u529e\u6cd5\u6210\u529f\u6fc0\u6d3b\u68cb\u5b50\u53d8\u6210\u9b54\u50cf\uff0c\u5979\u6839\u672c\u65e0\u6cd5\u4f7f\u7528\u8fd9\u526f\u8428\u74e6\u68cb\u3002</p><p>\u201c\u5947\u602a\uff0c\u96be\u9053\u8fd9\u526f\u68cb\u662f\u9650\u5b9a\u5deb\u5e08\u4f7f\u7528\u7684\uff1f\u201d</p><p>\u9b54\u6cd5\u7269\u54c1\u4e4b\u4e2d\uff0c\u786e\u5b9e\u662f\u6709\u8fd9\u79cd\u7c7b\u578b\uff0c\u9650\u5b9a\u4f7f\u7528\u5bf9\u8c61\u3002\u6700\u5178\u578b\u7684\u4f8b\u5982\u6cd5\u6756\uff0c\u62ff\u5728\u5deb\u5e08\u624b\u91cc\u624d\u771f\u6b63\u6709\u7528\uff0c\u843d\u5230\u6218\u58eb\u624b\u4e2d\uff0c\u4e5f\u4e0d\u8fc7\u5c31\u7528\u6765\u5f53\u68cd\u5b50\u6572\u4eba\u7f62\u4e86\u3002\u4f46\u8fd9\u79cd\u5176\u5b9e\u5f88\u5c11\uff0c\u5927\u90e8\u4efd\u9b54\u6cd5\u7269\u54c1\u90fd\u662f\u901a\u7528\u7684\uff0c\u6885\u83f2\u65af\u7684\u5bd2\u51b0\u624b\u5957\u3001\u6d6e\u7a7a\u9774\uff0c\u4fbf\u662f\u4f8b\u5b50\u3002</p><p>\u6216\u8bb8\u662f\u5947\u68b0\u5e08\u5728\u5236\u4f5c\u7684\u65f6\u5019\u8bbe\u5b9a\u4e86\u4ec0\u4e48\u7279\u522b\u9650\u5236\uff1f</p><p>\u8fd9\u4e2a\u95ee\u9898\u6682\u65f6\u65e0\u89e3\uff0c\u6668\u70bc\u57ce\u91cc\u867d\u7136\u8fd8\u6709\u51e0\u4e2a\u5deb\u5e08\uff0c\u4f46\u4e5f\u4e0d\u65b9\u4fbf\u627e\u6765\u4f5c\u5b9e\u9a8c\uff0c\u4f55\u51b5\u743c\u6069\u4e5f\u4e0d\u60f3\u628a\u8fd9\u4ef6\u4e8b\u5f04\u5f97\u5c3d\u4eba\u7686\u77e5\u3002\u66f4\u91cd\u8981\u7684\u662f\uff0c\u7d27\u63a5\u7740\u743c\u6069\u5c31\u53d1\u73b0\u4e86\u53e6\u4e00\u4e2a\u66f4\u9ebb\u70e6\u7684\u95ee\u9898\u3002</p><p>\u4ed6\u5e76\u4e0d\u80fd\u628a\u5341\u4e00\u679a\u68cb\u5b50\u540c\u65f6\u53d8\u6210\u9b54\u50cf\uff0c\u5373\u4f7f\u5b83\u4eec\u662f\u540c\u4e00\u9635\u8425\u3002</p><p>\u4ed6\u53ef\u4ee5\u628a\u4e00\u679a\u98df\u4eba\u9b54\u68cb\u5b50\u6fc0\u6d3b\uff0c\u7136\u540e\u662f\u7b2c\u4e8c\u679a\uff0c\u7b2c\u4e09\u679a\u2014\u2014\u7136\u540e\u5c31\u4e0d\u884c\u4e86\uff0c\u68cb\u76d8\u4e0a\u660e\u660e\u6709\u5341\u679a\u98df\u4eba\u9b54\uff0c\u7ea2\u9ed1\u53cc\u65b9\u5404\u4e94\u679a\uff0c\u4f46\u743c\u6069\u5728\u540c\u4e00\u65f6\u95f4\u53ea\u80fd\u53ec\u51fa\u4e09\u4e2a\u98df\u4eba\u9b54\u6765\u3002</p><p>\u6362\u79cd\u65b9\u5f0f\uff0c\u743c\u6069\u628a\u4e24\u679a\u6218\u58eb\u5168\u90e8\u6fc0\u6d3b\uff0c\u7136\u540e\u4ed6\u5c31\u518d\u4e5f\u6ca1\u6cd5\u4f7f\u7528\u5176\u4ed6\u68cb\u5b50\u4e86\uff1b\u4ed6\u5c1d\u8bd5\u53d6\u6d88\u4e86\u4e00\u679a\u6218\u58eb\uff0c\u6539\u4e3a\u53ec\u5524\u98df\u4eba\u9b54\uff0c\u6210\u529f\u4e86\uff0c\u4f46\u8fd9\u6b21\u53ea\u80fd\u53ec\u6765\u4e24\u540d\u98df\u4eba\u9b54\u3002</p><p>\u7ecf\u8fc7\u591a\u6b21\u5c1d\u8bd5\uff0c\u743c\u6069\u53d1\u73b0\u6700\u7ec8\u7684\u7ed3\u679c\u662f\uff1a\u5728\u540c\u4e00\u65f6\u95f4\u5185\uff0c\u4ed6\u53ef\u4ee5\u62e5\u6709\u4e00\u540d\u5deb\u5e08\uff0c\u6216\u8005\u4e00\u540d\u7267\u5e08\u52a0\u4e00\u53ea\u98df\u4eba\u9b54\uff0c\u6216\u8005\u4e24\u540d\u6218\u58eb\uff0c\u6216\u8005\u4e00\u540d\u6218\u58eb\u52a0\u4e24\u53ea\u98df\u4eba\u9b54\uff0c\u6216\u8005\u4e09\u53ea\u98df\u4eba\u9b54\u3002</p><p>\u201c\u8fd9\u4e2d\u95f4\u6709\u89c4\u5f8b\uff0c\u201d\u6885\u83f2\u65af\u4e00\u76f4\u5728\u65c1\u89c2\uff0c\u7a81\u7136\u8bf4\uff0c\u201c\u8fd9\u4e9b\u68cb\u5b50\u4f3c\u4e4e\u90fd\u6709\u6570\u503c\uff0c\u800c\u4e14\u8fd8\u4e0d\u4e00\u6837\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u4ed6\u4e5f\u5df2\u7ecf\u770b\u4e86\u51fa\u6765\uff0c\u8fd9\u6709\u70b9\u50cf\u662f\u81ea\u5df1\u5e26\u7740\u4e00\u7b14\u94b1\u53bb\u8d2d\u4e70\u4e0d\u540c\u79cd\u7c7b\u7684\u5546\u54c1\uff0c\u6839\u636e\u81ea\u5df1\u7684\u8d44\u91d1\u6570\u989d\uff0c\u4ee5\u53ca\u4e0d\u540c\u5546\u54c1\u7684\u4ef7\u683c\uff0c\u5bfc\u81f4\u4e86\u5404\u79cd\u53ef\u80fd\u7684\u7ec4\u5408\u3002\u4f5c\u4e3a\u5deb\u5e08\uff0c\u6570\u5b66\u4ed6\u4e00\u5411\u5b66\u5f97\u4e0d\u9519\uff0c\u62ff\u8d77\u7b14\u5728\u7eb8\u4e0a\u5217\u4e86\u51e0\u4e2a\u65b9\u7a0b\uff0c\u5f88\u5feb\u4e5f\u5c31\u7b97\u51fa\u4e86\u7ed3\u679c\u3002</p><p>\u201c\u5deb\u5e08\u4e3a\u4e5d\uff0c\u7267\u5e08\u4e3a\u4e03\uff0c\u6218\u58eb\u4e3a\u4e94\uff0c\u98df\u4eba\u9b54\u4e3a\u4e09\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4e3a\u5341\u4e00\u3002\u201d</p><p>\u201c\u5deb\u5e08\u7684\u6570\u503c\u8fd8\u5728\u7267\u5e08\u4e4b\u4e0a\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u5927\u6982\u56e0\u4e3a\u5236\u4f5c\u8005\u5176\u5b9e\u662f\u4e2a\u5947\u68b0\u5e08\uff0c\u5e76\u4e0d\u662f\u771f\u7684\u5353\u5c14\u3002\u4ed6\u4eec\u65e2\u7136\u4e0d\u4fe1\u795e\uff0c\u81ea\u7136\u4e5f\u4e0d\u4f1a\u5c0a\u656c\u7267\u5e08\uff0c\u6240\u4ee5\u6545\u610f\u8fdd\u53cd\u8428\u74e6\u68cb\u7684\u89c4\u5219\uff0c\u628a\u5deb\u5e08\u8bbe\u5728\u7267\u5e08\u4e4b\u4e0a\u4e86\u3002\u201d</p><p>\u201c\u4e5f\u6709\u9053\u7406\uff0c\u4f46\u4f60\u8fd9\u4e2a\u5341\u4e00\uff0c\u662f\u6307\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u53ef\u80fd\u662f\u6307\u6211\u7684\u9b54\u6cd5\u9020\u8be3\u5427\u3002\u201d\u743c\u6069\u731c\u6d4b\u3002</p></article>"}}}]);
