(window.webpackJsonp=window.webpackJsonp||[]).push([[1506],{1529:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u56db\u8282 \u51b3\u6597",content:"<article><h2>\u7b2c\u5341\u56db\u8282 \u51b3\u6597</h2><p>\u5728\u63a5\u4e0b\u6765\u7684\u51e0\u5929\u91cc\uff0c\u743c\u6069\u57fa\u672c\u6ca1\u5e72\u522b\u7684\u4e8b\u60c5\uff0c\u4e13\u5fc3\u7814\u7a76\u8fd9\u526f\u8428\u74e6\u68cb\uff0c\u53c8\u53d1\u73b0\u4e86\u66f4\u591a\u7684\u95e8\u9053\u3002\u9996\u5148\u662f\u8fd9\u4e9b\u68cb\u5b50\u9b54\u50cf\u5e76\u975e\u80fd\u65e0\u9650\u6b21\u53ec\u51fa\u7684\uff0c\u6709\u6570\u91cf\u9650\u5236\uff0c\u800c\u4e14\u540c\u6837\u4e5f\u662f\u4f9d\u636e\u79cd\u7c7b\u9ad8\u4f4e\u4e0d\u540c\uff0c\u4ee5\u5e73\u5747\u6bcf\u65e5\u8ba1\u7b97\u7684\u8bdd\uff0c\u98df\u4eba\u9b54\u80fd\u91cd\u590d\u53ec\u51fa\u4e94\u6b21\uff0c\u6218\u58eb\u56db\u6b21\uff0c\u7267\u5e08\u4e09\u6b21\uff0c\u5deb\u5e08\u4e24\u6b21\uff0c\u4e3b\u6bcd\u2026\u2026\u4e0d\u80fd\u7528\u3002</p><p>\u8fd9\u4e9b\u9b54\u50cf\u539f\u6765\u5e76\u4e0d\u80fd\u514d\u75ab\u9b54\u6cd5\u3002\u4ee5\u5e38\u7406\u6765\u8bf4\uff0c\u4efb\u4f55\u9b54\u50cf\uff0c\u5305\u62ec\u9ad8\u7ea7\u7684\u91d1\u5c5e\u9b54\u50cf\u548c\u4f4e\u7ea7\u7684\u77f3\u9b54\u50cf\u7c98\u571f\u9b54\u50cf\uff0c\u751a\u81f3\u6700\u5dee\u52b2\u7684\u8089\u8eaf\u9b54\u50cf\uff0c\u53ea\u8981\u4e25\u683c\u6309\u7167\u7a0b\u5e8f\u5236\u4f5c\uff0c\u5b8c\u6210\u4eea\u5f0f\uff0c\u4fbf\u80fd\u514d\u75ab\u7edd\u5927\u591a\u6570\u9b54\u6cd5\uff0c\u9664\u4e86\u6781\u5c11\u6570\u7279\u4f8b\u3002\u4f46\u8fd9\u4e9b\u8428\u74e6\u68cb\u5b50\u53d8\u6210\u7684\u9b54\u50cf\uff0c\u5374\u5e76\u6ca1\u6709\u8fd9\u79cd\u80fd\u529b\uff0c\u53ea\u662f\u5bf9\u6cd5\u672f\u6709\u8f83\u5f3a\u7684\u62b5\u6297\u80fd\u529b\u800c\u5df2\u3002</p><p>\u201c\u6750\u6599\u7684\u539f\u56e0\u3002\u201d\u743c\u6069\u601d\u7d22\u534a\u5929\uff0c\u5f97\u51fa\u4e86\u7ed3\u8bba\uff0c\u8fd9\u526f\u8428\u74e6\u68cb\u7ea2\u9ed1\u53cc\u8272\uff0c\u5206\u522b\u662f\u7528\u7ea2\u7389\u9ad3\u548c\u9ed1\u66dc\u77f3\u5236\u6210\uff0c\u4f5c\u4e3a\u68cb\u5b50\u7684\u65f6\u5019\u662f\u5982\u6b64\uff0c\u53d8\u6210\u9b54\u50cf\u7684\u65f6\u5019\u6750\u8d28\u4e5f\u6ca1\u6539\u53d8\u3002\u8fd9\u4e24\u79cd\u6750\u6599\u5f88\u73cd\u8d35\uff0c\u4e5f\u8db3\u591f\u7ed3\u5b9e\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u94a2\u94c1\uff0c\u4e5f\u4e0d\u5dee\u592a\u591a\uff0c\u4f46\u5b83\u4eec\u6700\u91cd\u8981\u7684\u7279\u6027\uff0c\u662f\u5bf9\u9b54\u6cd5\u6bd4\u8f83\u6709\u4eb2\u548c\u529b\uff0c\u6362\u53e5\u8bdd\u8bf4\uff0c\u76f8\u6bd4\u8d77\u5176\u4ed6\u7684\u6750\u6599\uff0c\u9b54\u6cd5\u66f4\u5bb9\u6613\u5bf9\u5b83\u4eec\u8d77\u4f5c\u7528\u3002</p><p>\u81f3\u4e8e\u4e3a\u4ec0\u4e48\u9009\u62e9\u8fd9\u4e24\u79cd\u6750\u6599\uff0c\u800c\u4e0d\u7528\u94a2\u94c1\u751a\u81f3\u7cbe\u91d1\u79d8\u94f6\uff0c\u5e94\u8be5\u5c31\u662f\u4e3a\u4e86\u80fd\u591f\u8ba9\u5b83\u4eec\u5e73\u65f6\u53d8\u5f62\u6210\u68cb\u5b50\uff0c\u65b9\u4fbf\u643a\u5e26\u3002\u5426\u5219\u8d70\u5230\u54ea\u91cc\u90fd\u5e26\u7740\u4e8c\u5341\u56db\u4e2a\u9b54\u50cf\u524d\u547c\u540e\u62e5\uff0c\u6216\u8bb8\u5f88\u5a01\u98ce\uff0c\u5374\u4e00\u770b\u5c31\u662f\u6b20\u6241\u3002</p><p>\u867d\u7136\u6709\u79cd\u79cd\u7f3a\u9677\uff0c\u4f46\u603b\u4f53\u6765\u8bf4\u8fd8\u662f\u4ee4\u4eba\u6ee1\u610f\u7684\uff0c\u743c\u6069\u4e5f\u4e0d\u662f\u8d2a\u5fc3\u4e0d\u8db3\u7684\u4eba\u3002\u5728\u4ed6\u95ed\u5173\u7814\u7a76\u8428\u74e6\u68cb\u7684\u8fd9\u51e0\u5929\u91cc\uff0c\u6668\u70bc\u7684\u57ce\u9632\u4fee\u7f2e\u5de5\u4f5c\u5df2\u7ecf\u5b8c\u6210\uff0c\u7ecf\u8fc7\u4e00\u756a\u6574\u987f\uff0c\u539f\u672c\u8352\u5e9f\u7684\u53e4\u57ce\u4e5f\u7b97\u662f\u521d\u5177\u89c4\u6a21\uff0c\u6709\u4e86\u4e9b\u65b0\u6c14\u8c61\u3002\u57ce\u5916\u6709\u6e56\u6cca\uff0c\u6709\u5730\u4e0b\u6cb3\u6d41\u7ecf\uff0c\u5353\u5c14\u4eec\u5f00\u8f9f\u4e86\u4e00\u5757\u519c\u573a\uff0c\u79cd\u690d\u84dd\u5e3d\u83cc\u548c\u624e\u514b\u6728\u5b62\uff0c\u8fd9\u662f\u5e7d\u6697\u5730\u57df\u4e2d\u6700\u5e38\u89c1\u7684\u4e24\u79cd\u98df\u7528\u690d\u7269\uff0c\u5176\u4e2d\u624e\u514b\u6728\u5b62\u7684\u6839\u830e\u8fd8\u53ef\u4ee5\u4f5c\u4e3a\u6728\u6750\u4f7f\u7528\u3002\u57ce\u897f\u5212\u51fa\u4e00\u5757\u533a\u57df\u4f5c\u4e3a\u755c\u680f\uff0c\u517b\u6b96\u4ece\u77ee\u4eba\u5904\u83b7\u5f97\u7684\u6d1b\u65af\u517d\u3002\u6885\u83f2\u65af\u5728\u5fb7\u53e4\u62c9\u7684\u534f\u52a9\u4e0b\uff0c\u4ece\u5353\u5c14\u4fd8\u864f\u4e2d\u6311\u9009\u4e86\u4e8c\u5341\u540d\u7cbe\u9510\u6218\u58eb\uff0c\u5176\u4ed6\u4eba\u5219\u4f9d\u65e7\u6253\u53d1\u53bb\u52b3\u52a8\u6539\u9020\u4e86\uff0c\u6b63\u597d\u519c\u573a\u755c\u680f\u90fd\u6025\u9700\u82e6\u529b\u3002</p><p>\u4fbf\u5728\u6b64\u65f6\uff0c\u53c8\u6709\u4e00\u4e2a\u65b0\u6d88\u606f\u4f20\u6765\uff1a\u4f0a\u8389\u4e1d\u7fe0\u6559\u4f1a\u7684\u9886\u8896\u8475\u9732\u5f97\u77e5\u4e86\u8fd9\u91cc\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u975e\u5e38\u8d5e\u540c\u628a\u6668\u70bc\u57ce\u5efa\u8bbe\u6210\u53cd\u6297\u7f57\u4e1d\u9738\u6743\u7edf\u6cbb\u7684\u524d\u6cbf\u9635\u5730\u7684\u8bbe\u60f3\uff0c\u6307\u4ee4\u5176\u4ed6\u5353\u5c14\u4fe1\u5f92\u6539\u53d8\u539f\u5b9a\u9003\u5f80\u5730\u8868\u7684\u8ba1\u5212\uff0c\u6539\u5411\u6668\u70bc\u6c47\u5408\u3002\u8ba1\u7b97\u8def\u7a0b\uff0c\u5982\u679c\u4e00\u5207\u987a\u5229\u7684\u8bdd\uff0c\u5927\u6982\u534a\u5e74\u4e4b\u540e\uff0c\u6668\u70bc\u57ce\u91cc\u5c31\u4f1a\u6709\u81f3\u5c11\u56db\u4e94\u767e\u540d\u5353\u5c14\uff0c\u4e5f\u5df2\u7ecf\u662f\u4e00\u80a1\u4e0d\u5c0f\u7684\u52bf\u529b\u4e86\u3002</p><p>\u5e26\u6765\u8fd9\u4e2a\u6d88\u606f\u7684\uff0c\u662f\u4e00\u961f\u6765\u81ea\u5730\u8868\u7684\u4f0a\u8389\u4e1d\u7fe0\u4fe1\u5f92\uff0c\u4e8c\u5341\u4f59\u4eba\uff0c\u7cbe\u7075\u548c\u5353\u5c14\u6df7\u6742\u3002\u5979\u4eec\u539f\u672c\u5df2\u7ecf\u64a4\u51fa\u4e86\u5e7d\u6697\u5730\u57df\uff0c\u5230\u8fbe\u5730\u8868\uff0c\u6b63\u5728\u79d1\u7d22\u66fc\u68ee\u6797\u91cc\u4fee\u6574\uff0c\u56e0\u4e3a\u8475\u9732\u7684\u65b0\u6307\u793a\uff0c\u53c8\u8f6c\u4e86\u56de\u6765\u3002</p><p>\u4eba\u6570\u591a\u4e86\uff0c\u529b\u91cf\u589e\u5f3a\uff0c\u8fd9\u81ea\u7136\u662f\u6c42\u4e4b\u4e0d\u5f97\u7684\u597d\u4e8b\uff0c\u53ea\u662f\u968f\u4e4b\u5f15\u53d1\u7684\u9ebb\u70e6\u4e5f\u4e0d\u5c11\u3002\u4f0a\u8389\u4e1d\u7fe0\u6559\u4f1a\u7684\u7ec4\u7ec7\u6027\u592a\u5dee\uff0c\u5e73\u65f6\u5404\u81ea\u4e3a\u653f\uff0c\u8fd8\u6ca1\u4ec0\u4e48\u611f\u89c9\uff0c\u5982\u4eca\u96c6\u4e2d\u5728\u4e00\u8d77\uff0c\u8fd9\u4e2a\u7f3a\u9677\u5c31\u660e\u663e\u66b4\u9732\u51fa\u6765\u3002\u5982\u4eca\u57ce\u91cc\u4e0d\u5230\u767e\u540d\u4fe1\u5f92\uff0c\u53bb\u6389\u88ab\u5904\u6b7b\u7684\u83ab\u857e\u7279\uff0c\u52a0\u4e0a\u65b0\u6765\u7684\u8fd9\u4e00\u961f\uff0c\u6709\u516b\u4e2a\u5934\u76ee\uff0c\u5f7c\u6b64\u5730\u4f4d\u5e73\u7b49\uff0c\u53ea\u8981\u667a\u529b\u8d85\u8fc7\u5730\u7cbe\u7684\u4eba\uff0c\u90fd\u77e5\u9053\u8fd9\u6837\u4e0b\u53bb\u80af\u5b9a\u4f1a\u51fa\u95ee\u9898\u3002</p><p>\u5728\u963f\u5fd2\u59ae\u7684\u63d0\u8bae\u4e0b\uff0c\u516b\u4f4d\u9996\u9886\u53ec\u5f00\u4f1a\u8bae\uff0c\u5546\u91cf\u7edf\u4e00\u9886\u5bfc\u6743\u4e8b\u5b9c\u3002\u53ea\u662f\u6743\u529b\u8fd9\u79cd\u4e1c\u897f\uff0c\u8c01\u80af\u8f7b\u6613\u653e\u624b\uff0c\u8c01\u90fd\u77e5\u9053\u73b0\u5728\u8fd9\u6837\u5b50\u4e0d\u884c\uff0c\u4f46\u5982\u679c\u8981\u81ea\u5df1\u505a\u51fa\u727a\u7272\uff0c\u90a3\u53c8\u53e6\u4f5c\u522b\u8bba\u4e86\u3002\u7cbe\u7075\u548c\u5353\u5c14\u90fd\u662f\u957f\u5bff\u79cd\u65cf\uff0c\u5929\u6027\u4e2d\u5c31\u6709\u4e00\u79cd\u60a0\u95f2\u6563\u6f2b\uff0c\u6216\u8005\u8bf4\u62d6\u6c93\uff0c\u4e00\u76f4\u626f\u76ae\u4e86\u4e03\u516b\u5929\uff0c\u4f9d\u65e7\u6ca1\u6709\u4ec0\u4e48\u7ed3\u679c\u3002</p><p>\u8fd9\u79cd\u6559\u4f1a\u5185\u90e8\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u4f5c\u4e3a\u5916\u4eba\uff0c\u81ea\u7136\u662f\u65e2\u6ca1\u8d44\u683c\u4e5f\u6ca1\u5174\u8da3\u53bb\u53c2\u4e0e\uff0c\u4f9d\u65e7\u505a\u81ea\u5df1\u7684\u4e8b\u60c5\u3002\u8fd9\u4e00\u5929\uff0c\u4ed6\u8d70\u8fdb\u6885\u83f2\u65af\u7684\u623f\u95f4\uff0c\u53d1\u73b0\u963f\u5fd2\u59ae\u4e5f\u5728\uff0c\u6b63\u5411\u6885\u83f2\u65af\u62b1\u6028\u3002\u5176\u5b9e\u8981\u8bba\u8d77\u6765\uff0c\u963f\u5fd2\u59ae\u548c\u6885\u83f2\u65af\u7b97\u4e0d\u4e0a\u670b\u53cb\uff0c\u53ea\u662f\u76f8\u8bc6\u7f62\u4e86\uff0c\u8fdc\u8fdc\u6bd4\u4e0d\u5f97\u51db\u90a3\u6837\u4ece\u5c0f\u4e00\u8d77\u957f\u5927\u7684\u4ea4\u60c5\u3002\u4f46\u8eab\u5728\u5f02\u4e61\uff0c\u80fd\u770b\u89c1\u719f\u4eba\uff0c\u81ea\u7136\u5c31\u683c\u5916\u4eb2\u8fd1\u51e0\u5206\uff0c\u8fd9\u662f\u4eba\u4e4b\u5e38\u60c5\u3002</p><p>\u4e00\u95ee\u624d\u77e5\u9053\uff0c\u7ecf\u8fc7\u8fd9\u4e9b\u5929\u7684\u4e89\u5435\uff0c\u603b\u7b97\u662f\u521d\u6b65\u8fbe\u6210\u4e86\u59a5\u534f\uff0c\u5404\u4eba\u7684\u5730\u4f4d\u4e0d\u53d8\uff0c\u4f46\u4ece\u4e2d\u63a8\u4e3e\u4e00\u540d\u4e3b\u5e2d\uff0c\u4f5c\u4e3a\u5c45\u4e2d\u534f\u8c03\u8005\u3002\u4f46\u56f4\u7ed5\u8fd9\u4f4d\u4e3b\u5e2d\u7684\u4eba\u9009\uff0c\u5927\u5bb6\u53c8\u5f00\u59cb\u4e89\u6267\u4e0d\u4e0b\uff0c\u76f4\u5230\u4eca\u5929\u8fd8\u6ca1\u4e2a\u7ed3\u679c\u3002</p><p>\u201c\u6700\u9ebb\u70e6\u7684\u5c31\u662f\u90a3\u4e2a\u91d1\u95ea\u95ea\u3002\u201d\u963f\u5fd2\u59ae\u62b1\u6028\u3002</p><p>\u6240\u8c13\u91d1\u95ea\u95ea\uff0c\u662f\u6307\u65b0\u6765\u7684\u90a3\u961f\u4f0a\u8389\u4e1d\u7fe0\u4fe1\u5f92\u7684\u9996\u9886\uff0c\u4e00\u4e2a\u5973\u6027\u65e5\u7cbe\u7075\uff0c\u8fd9\u662f\u975e\u5e38\u7f55\u89c1\u7684\u7cbe\u7075\u4e9a\u79cd\uff0c\u53c8\u53eb\u505a\u91d1\u7cbe\u7075\uff0c\u76ae\u80a4\u6de1\u91d1\u8272\uff0c\u6ee1\u5934\u91d1\u53d1\uff0c\u773c\u775b\u90fd\u662f\u91d1\u8272\u7684\uff0c\u6574\u4e2a\u4eba\u7ad9\u5728\u9ed1\u6697\u4e2d\u90fd\u4f1a\u6cdb\u7740\u5149\u3002\u5728\u521a\u8fdb\u57ce\u7684\u65f6\u5019\uff0c\u743c\u6069\u548c\u5979\u6253\u8fc7\u4e00\u6b21\u4ea4\u9053\uff0c\u751a\u662f\u4e0d\u5feb\uff0c\u5c31\u968f\u53e3\u66ff\u5979\u53d6\u4e86\u4e2a\u5916\u53f7\u53eb\u505a\u91d1\u95ea\u95ea\uff0c\u6ca1\u60f3\u5230\u5f88\u5feb\u6d41\u4f20\u51fa\u53bb\u3002\u5982\u4eca\u5927\u5bb6\u9664\u4e86\u5f53\u9762\u4e0d\u63d0\uff0c\u80cc\u540e\u8bf4\u8d77\u6765\uff0c\u5f80\u5f80\u90fd\u76f4\u63a5\u8bf4\u91d1\u95ea\u95ea\uff0c\u672c\u540d\u662f\u4ec0\u4e48\u53cd\u5012\u90fd\u8981\u5fd8\u4e86\u3002</p><p>\u201c\u91d1\u95ea\u95ea\u600e\u4e48\u4e86\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u5979\u4e00\u4e2a\u65b0\u6765\u8005\uff0c\u575a\u6301\u8981\u81ea\u5df1\u505a\u8fd9\u4e2a\u4e3b\u5e2d\uff0c\u5927\u5bb6\u81ea\u7136\u4e0d\u670d\u3002\u800c\u4e14\u2026\u2026\u201d\u963f\u5fd2\u59ae\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u201c\u5979\u5e0c\u671b\u4f60\u4eec\u79bb\u5f00\u2026\u2026\u201d</p><p>\u201c\u5e0c\u671b\uff1f\u201d\u743c\u6069\u51b7\u7b11\uff0c\u201c\u662f\u9a71\u9010\u5427\u3002\u201d</p><p>\u4ed6\u5bf9\u90a3\u4e2a\u65e5\u7cbe\u7075\u5168\u65e0\u534a\u70b9\u597d\u5370\u8c61\uff0c\u6240\u4ee5\u8bf4\u8bdd\u4e5f\u4e0d\u5ba2\u6c14\u3002\u6885\u83f2\u65af\u5374\u7b11\u4e86\u7b11\uff0c\u201c\u5e0c\u671b\u54ea\u4e9b\u4eba\u79bb\u5f00\uff1f\u6211\u3001\u743c\u6069\uff0c\u8fd8\u6709\u5462\uff1f\u201d</p><p>\u201c\u90a3\u51e0\u4e2a\u77ee\u4eba\u5de5\u5320\u3002\u201d</p><p>\u201c\u90a3\u4e9b\u5de5\u5320\u662f\u6765\u5e2e\u52a9\u4fee\u7f2e\u57ce\u9632\u7684\uff0c\u5982\u4eca\u4e8b\u60c5\u505a\u5b8c\u4e86\uff0c\u5c31\u8d76\u4eba\u8d70\u8def\uff1f\u201d\u6885\u83f2\u65af\u53cd\u95ee\uff0c\u201c\u5979\u96be\u9053\u5c31\u4e0d\u8003\u8651\u4e00\u4e0b\u4f0a\u5361\u6c99\u57ce\u7684\u53cd\u5e94\uff1f\u201d</p><p>\u201c\u5979\u53cd\u5bf9\u548c\u77ee\u4eba\u7ed3\u76df\uff0c\u201d\u963f\u5fd2\u59ae\u8bf4\uff0c\u663e\u7136\u5bf9\u6b64\u4e5f\u5f88\u4e0d\u6ee1\uff0c\u201c\u5979\u8bf4\u5f88\u5feb\u5c31\u4f1a\u6709\u8d8a\u6765\u8d8a\u591a\u7684\u6559\u53cb\u6c47\u96c6\uff0c\u65e0\u9700\u4f9d\u8d56\u77ee\u4eba\u3002\u201d</p><p>\u743c\u6069\u542c\u4e86\uff0c\u53ea\u662f\u51b7\u7b11\uff0c\u5e76\u672a\u591a\u8bf4\uff0c\u6885\u83f2\u65af\u5374\u82e5\u6709\u6240\u601d\u3002\u201c\u65e5\u7cbe\u7075\u90fd\u8fd9\u6837\u4e48\uff1f\u201d\u5979\u5ffd\u7136\u95ee\uff0c\u201c\u6211\u542c\u8bf4\u8fc7\u4e00\u4e9b\u6709\u5173\u4ed6\u4eec\u7684\u4e8b\u60c5\uff0c\u4f46\u4ece\u6ca1\u771f\u6b63\u6253\u8fc7\u4ea4\u9053\u3002\u201d</p><p>\u963f\u5fd2\u59ae\u7b80\u5355\u89e3\u91ca\u4e86\u4e00\u4e0b\uff0c\u539f\u6765\u7cbe\u7075\u65cf\u6709\u516d\u5927\u4e9a\u79cd\uff1a\u65e5\u7cbe\u7075\u3001\u6708\u7cbe\u7075\u3001\u6728\u7cbe\u7075\u3001\u91ce\u7cbe\u7075\u3001\u6d77\u7cbe\u7075\u548c\u9ed1\u6697\u7cbe\u7075\u3002\u963f\u5fd2\u59ae\u662f\u6708\u7cbe\u7075\uff0c\u53c8\u53eb\u505a\u94f6\u7cbe\u7075\uff0c\u662f\u6700\u5e38\u89c1\u5230\u7684\u7cbe\u7075\u4e9a\u79cd\uff0c\u548c\u4eba\u7c7b\u5173\u7cfb\u53cb\u5584\uff0c\u4ea4\u5f80\u751a\u591a\uff0c\u751a\u81f3\u4f1a\u548c\u4eba\u7c7b\u604b\u7231\u5a5a\u914d\u2014\u2014\u57fa\u672c\u4e0a\u6240\u6709\u7684\u534a\u7cbe\u7075\uff0c\u90fd\u662f\u6708\u7cbe\u7075\u548c\u4eba\u7c7b\u6240\u751f\u7684\u3002\u6728\u7cbe\u7075\u53c8\u79f0\u94dc\u7cbe\u7075\uff0c\u91ce\u7cbe\u7075\u53c8\u79f0\u7eff\u7cbe\u7075\uff0c\u6570\u91cf\u90fd\u5f88\u7a00\u5c11\uff0c\u800c\u4e14\u5f80\u5f80\u9690\u5c45\u5728\u68ee\u6797\u6df1\u5904\uff0c\u6781\u5c11\u5916\u51fa\u3002\u9ed1\u6697\u7cbe\u7075\u81ea\u4e0d\u7528\u591a\u8bf4\uff0c\u6d77\u7cbe\u7075\u5219\u662f\u751f\u6d3b\u5728\u6d77\u4e2d\uff0c\u5e38\u4eba\u54ea\u91cc\u80fd\u89c1\u5230\u3002\u81f3\u4e8e\u65e5\u7cbe\u7075\uff0c\u53c8\u79f0\u91d1\u7cbe\u7075\uff0c\u57fa\u672c\u90fd\u5c45\u4f4f\u5728\u6d77\u5916\u7684\u6c38\u805a\u5c9b\uff0c\u5927\u9646\u4e0a\u6781\u5c11\uff0c\u6240\u4ee5\u6885\u83f2\u65af\u8fd9\u79cd\u89c1\u591a\u8bc6\u5e7f\u7684\u5723\u6b66\u58eb\u4e5f\u6ca1\u89c1\u8fc7\u3002</p><p>\u65e5\u7cbe\u7075\u6570\u91cf\u867d\u7136\u4e0d\u591a\uff0c\u4f46\u5730\u4f4d\u5f88\u9ad8\uff0c\u5411\u6765\u88ab\u8ba4\u4e3a\u662f\u7cbe\u7075\u65cf\u4e2d\u6700\u9ad8\u8d35\u6700\u4f18\u79c0\u7684\u4e00\u652f\uff0c\u5343\u4e07\u5e74\u6765\uff0c\u7cbe\u7075\u738b\u5ef7\u4e00\u76f4\u90fd\u662f\u7531\u4ed6\u4eec\u638c\u63e1\uff0c\u5973\u738b\u4e5f\u4ece\u6765\u90fd\u662f\u7531\u65e5\u7cbe\u7075\u62c5\u4efb\uff0c\u53ef\u4ee5\u8bf4\u4ed6\u4eec\u662f\u6574\u4e2a\u7cbe\u7075\u65cf\u4e2d\u7684\u8d35\u65cf\u3002\u65e5\u7cbe\u7075\u6027\u60c5\u9ad8\u50b2\uff0c\u770b\u4e0d\u8d77\u9664\u7cbe\u7075\u4e4b\u5916\u7684\u4efb\u4f55\u751f\u7269\uff0c\u5f53\u7136\u4e5f\u5305\u62ec\u4eba\u7c7b\uff0c\u6240\u4ee5\u8fd9\u4e2a\u91d1\u95ea\u95ea\u6709\u8fd9\u79cd\u6001\u5ea6\uff0c\u867d\u7136\u4ee4\u4eba\u607c\u6012\uff0c\u4f46\u5012\u4e5f\u5e76\u4e0d\u51fa\u5947\u3002</p><p>\u201c\u5927\u5bb6\u90fd\u662f\u7cbe\u7075\uff0c\u65e5\u7cbe\u7075\u51ed\u4ec0\u4e48\u9ad8\u8d35\u4e9b\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u636e\u8bf4\u7cbe\u7075\u4e3b\u795e\u67ef\u745e\u9686\u521b\u9020\u5b50\u6c11\u65f6\uff0c\u6700\u521d\u521b\u9020\u7684\u4fbf\u662f\u65e5\u7cbe\u7075\uff0c\u6700\u5f97\u795e\u7947\u6069\u5ba0\uff0c\u201d\u6885\u83f2\u65af\u8bf4\u7740\uff0c\u8f6c\u8fc7\u8138\u95ee\u963f\u5fd2\u59ae\uff0c\u201c\u662f\u8fd9\u4e48\u8bf4\u6ca1\u9519\u5427\u3002\u201d</p><p>\u963f\u5fd2\u59ae\u70b9\u70b9\u5934\uff0c\u201c\u786e\u5b9e\u6709\u8fd9\u79cd\u8bf4\u6cd5\u3002\u201d</p><p>\u201c\u771f\u7684\u5417\uff1f\u201d</p><p>\u201c\u771f\u5047\u4e0d\u6e05\u695a\uff0c\u4f46\u65e5\u7cbe\u7075\u786e\u5b9e\u662f\u62e5\u6709\u4e00\u4e9b\u7279\u522b\u7684\u5929\u8d4b\u80fd\u529b\uff0c\u662f\u5176\u4ed6\u7cbe\u7075\u6240\u8fdc\u8fdc\u4e0d\u5177\u5907\u7684\u3002\u201d</p><p>\u901a\u5e38\u6765\u8bf4\uff0c\u7cbe\u7075\u957f\u4e8e\u7075\u5de7\uff0c\u62d9\u4e8e\u529b\u91cf\uff0c\u8eab\u4f53\u4e0d\u5982\u4eba\u7c7b\u5f3a\u5065\uff0c\u6b66\u6280\u953b\u70bc\u8d70\u7684\u4e5f\u90fd\u662f\u8f7b\u5de7\u7075\u52a8\u7684\u8def\u5b50\uff0c\u4f46\u65e5\u7cbe\u7075\u5374\u5929\u8d4b\u5f02\u7980\uff0c\u529b\u91cf\u7075\u5de7\u517c\u5907\uff0c\u5c31\u7b97\u7a7f\u91cd\u7532\u4e0a\u9635\u51b2\u950b\u90fd\u53ef\u4ee5\u3002\u800c\u4e14\u7cbe\u7075\u9ad8\u7b49\u9b54\u6cd5\u4e2d\u7684\u5f88\u591a\u5185\u5bb9\uff0c\u552f\u6709\u65e5\u7cbe\u7075\u624d\u80fd\u5b66\u4e60\u8fd0\u7528\uff0c\u8fd9\u662f\u8840\u8109\u9650\u5b9a\u7684\u3002</p><p>\u6240\u8c13\u7cbe\u7075\u9ad8\u7b49\u9b54\u6cd5\uff0c\u5176\u5b9e\u4e5f\u5c31\u662f\u7cbe\u7075\u65cf\u7684\u9b54\u6cd5\u3002\u7cbe\u7075\u7684\u6587\u660e\u8fdc\u6bd4\u4eba\u7c7b\u60a0\u4e45\uff0c\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u521d\u671f\uff0c\u4eba\u7c7b\u90fd\u662f\u4ee5\u7cbe\u7075\u4e3a\u5e08\uff0c\u5b66\u4e60\u9b54\u6cd5\u7684\uff0c\u201c\u8ff7\u9501\u201d\u5c31\u662f\u7cbe\u7075\u9b54\u6cd5\u7684\u8457\u540d\u6210\u5c31\u4e4b\u4e00\u3002\u540e\u6765\u83b7\u5f97\u4e86\u8010\u745f\u5377\u8f74\uff0c\u4ee5\u6b64\u4e3a\u7406\u8bba\u57fa\u7840\uff0c\u5965\u672f\u5e08\u4eec\u629b\u5f03\u4e86\u7cbe\u7075\u9b54\u6cd5\uff0c\u81ea\u521b\u683c\u5c40\uff0c\u6700\u7ec8\u6f14\u5316\u6210\u5982\u4eca\u7684\u516b\u5927\u5b66\u6d3e\uff0c\u867d\u7136\u672a\u5fc5\u6bd4\u7cbe\u7075\u9b54\u6cd5\u66f4\u9ad8\u660e\uff0c\u4f46\u5374\u66f4\u666e\u53ca\u3002\u5982\u4eca\u6574\u4e2a\u5927\u9646\uff0c\u4e0d\u4f46\u662f\u4eba\u7c7b\uff0c\u5c31\u8fde\u77ee\u4eba\u3001\u4f8f\u5112\u3001\u534a\u8eab\u4eba\uff0c\u751a\u81f3\u5f88\u591a\u7cbe\u7075\u5deb\u5e08\u90fd\u5df2\u7ecf\u63a5\u53d7\u4eba\u7c7b\u9b54\u6cd5\u3002\u65e5\u7cbe\u7075\u4eec\u4fbf\u53c8\u52a0\u4e86\u201c\u9ad8\u7b49\u201d\u4e00\u8bcd\uff0c\u4ee5\u793a\u533a\u522b\u3002</p><p>\u7cbe\u7075\u9b54\u6cd5\u4e4b\u6240\u4ee5\u6ca1\u843d\uff0c\u539f\u56e0\u81ea\u7136\u5f88\u591a\uff0c\u4f46\u5176\u4e25\u91cd\u53d7\u8840\u8109\u9650\u5236\uff0c\u5f88\u591a\u6cd5\u672f\u552f\u6709\u65e5\u7cbe\u7075\u624d\u80fd\u5b66\u4e60\uff0c\u5176\u4ed6\u7cbe\u7075\u600e\u4e48\u52aa\u529b\u4e5f\u65e0\u6cd5\u638c\u63e1\uff0c\u8fd9\u4e5f\u662f\u4e00\u4e2a\u5f88\u5927\u7684\u7f18\u6545\u3002\u4e8b\u5b9e\u8bc1\u660e\uff0c\u552f\u6709\u80fd\u591f\u666e\u53ca\u7684\uff0c\u624d\u662f\u771f\u6b63\u5177\u6709\u751f\u547d\u529b\u7684\u3002</p><p>\u65e5\u7cbe\u7075\u5929\u8d4b\u8fc7\u4eba\uff0c\u81ea\u89c6\u4fbf\u9ad8\uff0c\u770b\u4e0d\u8d77\u4eba\u7c7b\uff0c\u4e43\u662f\u4f20\u7edf\u548c\u4e60\u60ef\u4f7f\u7136\u3002\u743c\u6069\u5fc3\u60f3\u201c\u6211\u539f\u672c\u5c31\u6ca1\u5174\u8da3\u548c\u4f60\u4eec\u8fd9\u4e9b\u4e27\u5bb6\u4e4b\u72ac\u6253\u4ea4\u9053\uff0c\u4e0d\u8fc7\u662f\u770b\u5728\u827e\u5f25\u8587\u7684\u4efd\u4e0a\u5e2e\u5fd9\u7f62\u4e86\uff0c\u5982\u4eca\u65e2\u7136\u5982\u6b64\u8bf4\uff0c\u90a3\u4fbf\u4e00\u62cd\u4e24\u6563\u5c31\u662f\uff0c\u6b63\u597d\u843d\u5f97\u81ea\u5728\u201d\uff0c\u6709\u5fc3\u8ba5\u8bbd\u51e0\u53e5\uff0c\u4f46\u8bf4\u51fa\u6765\u53c8\u672a\u514d\u4f24\u4e86\u963f\u5fd2\u59ae\uff0c\u53ea\u5f97\u7f62\u4e86\u3002\u6885\u83f2\u65af\u5374\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u4f3c\u4e4e\u6709\u4e86\u4e3b\u610f\u3002</p><p>\u53c8\u8fc7\u51e0\u65e5\uff0c\u743c\u6069\u6b63\u5728\u770b\u4e66\uff0c\u5374\u6709\u536b\u5175\u8fdb\u6765\u62a5\u544a\uff0c\u8bf4\u6885\u83f2\u65af\u5c0f\u59d0\u8981\u548c\u90a3\u4e2a\u91d1\u95ea\u95ea\u51b3\u6597\u3002</p><p>\u203b\u203b\u203b</p><p>\u6668\u70bc\u57ce\u91cc\u81ea\u7136\u662f\u6709\u8bad\u7ec3\u573a\u5730\u7684\uff0c\u743c\u6069\u8d76\u5230\u4e00\u770b\uff0c\u89c1\u6885\u83f2\u65af\u5df2\u7ecf\u5168\u526f\u6b66\u88c5\uff0c\u7ad9\u5728\u573a\u5185\uff0c\u94f6\u5251\u63d2\u5728\u8eab\u65c1\uff1b\u65e5\u7cbe\u7075\u5219\u5728\u7a7f\u62a4\u7532\uff0c\u5468\u56f4\u805a\u96c6\u4e86\u4e00\u7fa4\u4eba\uff0c\u5168\u662f\u542c\u5230\u6d88\u606f\u8d76\u6765\u770b\u70ed\u95f9\u7684\u3002\u743c\u6069\u4e00\u773c\u770b\u89c1\u5438\u8840\u9b3c\u5fb7\u53e4\u62c9\uff0c\u628a\u4ed6\u62c9\u4e86\u8fc7\u6765\uff0c\u8be2\u95ee\u8fd9\u5230\u5e95\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u3002\u539f\u6765\u662f\u4e24\u4eba\u5728\u8def\u4e0a\u9047\u89c1\uff0c\u65e5\u7cbe\u7075\u6709\u4e9b\u51fa\u8a00\u4e0d\u900a\uff0c\u6885\u83f2\u65af\u5374\u4e5f\u4e00\u53cd\u5e38\u6001\uff0c\u51b7\u5632\u70ed\u8bbd\uff0c\u987f\u65f6\u51b2\u7a81\u52a0\u5267\u77db\u76fe\u5347\u7ea7\uff0c\u6700\u540e\u4fbf\u5f04\u6210\u8fd9\u526f\u573a\u9762\u4e86\u3002</p><p>\u743c\u6069\u542c\u5b8c\uff0c\u5fc3\u4e2d\u5df2\u7ecf\u660e\u767d\uff0c\u6885\u83f2\u65af\u6b64\u4e3e\uff0c\u65e0\u975e\u662f\u8981\u501f\u6545\u6253\u538b\u5bf9\u65b9\u7684\u6c14\u7130\uff0c\u5426\u5219\u4ee5\u5979\u7684\u6027\u60c5\uff0c\u53c8\u600e\u4e48\u4f1a\u548c\u4eba\u505a\u53e3\u820c\u4e4b\u4e89\uff0c\u66f4\u4e0d\u4f1a\u95f9\u5230\u51b3\u6597\u7684\u5730\u6b65\u3002</p><p>\u6b63\u8bf4\u8bdd\u95f4\uff0c\u65e5\u7cbe\u7075\u5df2\u7ecf\u7a7f\u597d\u62a4\u7532\uff0c\u6ca1\u6234\u5934\u76d4\uff0c\u53d6\u8fc7\u81ea\u5df1\u7684\u5175\u5668\u6765\u3002\u53cc\u65b9\u51c6\u5907\u5b8c\u6bd5\uff0c\u51b3\u6597\u5373\u5c06\u5f00\u59cb\uff0c\u743c\u6069\u5bf9\u6885\u83f2\u65af\u5012\u662f\u6781\u6709\u4fe1\u5fc3\uff0c\u5e76\u4e0d\u62c5\u5fe7\uff0c\u627e\u4e86\u4e2a\u4f4d\u7f6e\u4fbf\u5750\u4e0b\u6765\u65c1\u89c2\u3002</p><p>\u65e5\u7cbe\u7075\u7528\u7684\u6b66\u5668\u662f\u4e00\u67c4\u5f2f\u5200\uff0c\u5f62\u5982\u6708\u7259\uff0c\u521a\u62bd\u51fa\u9798\u65f6\u5bd2\u5149\u51db\u51bd\uff0c\u7136\u540e\u4fbf\u6e10\u6e10\u5185\u655b\uff0c\u5200\u8eab\u660e\u6f88\uff0c\u4eff\u4f5b\u4e00\u6cd3\u79cb\u6c34\u3002\u5979\u624b\u8155\u7ffb\u8f6c\uff0c\u5feb\u901f\u5728\u7a7a\u6c14\u4e2d\u5288\u51fa\u4e24\u5200\uff0c\u968f\u5373\u6536\u56de\u80f8\u524d\uff0c\u6446\u51fa\u4e00\u4e2a\u67b6\u5f0f\u6765\u3002\u5979\u5e73\u65f6\u603b\u662f\u4e00\u526f\u5028\u50b2\u795e\u8272\uff0c\u8ba9\u4eba\u770b\u7740\u5c31\u60f3\u6bb4\u6253\uff0c\u73b0\u5728\u62d4\u5200\u5728\u624b\uff0c\u767b\u65f6\u5c31\u50cf\u6362\u4e86\u4e2a\u4eba\u4f3c\u7684\uff0c\u867d\u7136\u8fd8\u6ca1\u771f\u6b63\u52a8\u624b\uff0c\u4e3e\u52a8\u4e4b\u95f4\u6c14\u5ea6\u9690\u9690\uff0c\u4fbf\u662f\u743c\u6069\u8fd9\u79cd\u5916\u884c\u90fd\u770b\u4e86\u51fa\u6765\u3002\u5fb7\u53e4\u62c9\u5750\u5728\u65c1\u8fb9\uff0c\u4e5f\u8f7b\u8f7b\u559d\u5f69\uff0c\u8bf4\u4e86\u58f0\u201c\u597d\uff01\u201d</p><p>\u743c\u6069\u5fae\u5fae\u76b1\u7709\uff0c\u4ed6\u539f\u672c\u5bf9\u6885\u83f2\u65af\u5f88\u6709\u4fe1\u5fc3\uff0c\u5982\u4eca\u5374\u4e5f\u6709\u4e9b\u62c5\u5fe7\u3002\u201c\u4f60\u89c9\u5f97\u8c01\u80fd\u8d62\uff1f\u201d\u4ed6\u95ee\u5fb7\u53e4\u62c9\u3002</p><p>\u201c\u8fd9\u4e2a\u53ef\u4e0d\u597d\u8bf4\uff0c\u201d\u5438\u8840\u9b3c\u5f88\u8bda\u5b9e\u5730\u56de\u7b54\uff0c\u201c\u751f\u6b7b\u76f8\u640f\uff0c\u770b\u7684\u672a\u5fc5\u662f\u6b66\u6280\uff0c\u5f88\u591a\u65f6\u5019\u8981\u770b\u8fd0\u6c14\u3002\u201d</p><p>\u743c\u6069\u7709\u5934\u76b1\u5f97\u66f4\u7d27\uff0c\u95ee\u9898\u5c31\u5728\u8fd9\u91cc\uff0c\u51b3\u6597\u4e0d\u662f\u6bd4\u6b66\u8f83\u6280\uff0c\u70b9\u5230\u4e3a\u6b62\uff0c\u5f80\u5f80\u662f\u8981\u5224\u751f\u6b7b\u7684\u3002\u6885\u83f2\u65af\u4e0d\u8fc7\u662f\u60f3\u6559\u8bad\u65e5\u7cbe\u7075\uff0c\u4e0d\u4f1a\u4e0b\u6740\u624b\uff0c\u5bf9\u65b9\u53ea\u6015\u5374\u4e0d\u662f\u8fd9\u4e48\u60f3\u3002</p><p>\u4ed6\u4f30\u7b97\u8ddd\u79bb\uff0c\u6697\u4e2d\u53d6\u51fa\u6cd5\u6756\uff0c\u7b3c\u5728\u8896\u4e2d\uff0c\u610f\u8bc6\u4e2d\u5df2\u7ecf\u6e05\u6670\u6d6e\u73b0\u51fa\u89e3\u79bb\u672f\u7684\u5492\u6587\uff0c\u4e07\u4e00\u6709\u4ec0\u4e48\u53d8\u6545\uff0c\u5c31\u5148\u4e0b\u624b\u628a\u65e5\u7cbe\u7075\u6740\u4e86\uff0c\u81f3\u4e8e\u51b3\u6597\u89c4\u77e9\uff0c\u4ed6\u538b\u6839\u5c31\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u5fb7\u53e4\u62c9\u5c31\u5750\u5728\u4ed6\u8eab\u8fb9\uff0c\u5e94\u8be5\u5bdf\u89c9\u5230\u4e86\u4ec0\u4e48\uff0c\u77a5\u4e86\u4e00\u773c\uff0c\u4e5f\u6ca1\u591a\u8bf4\u8bdd\u3002</p><p>\u573a\u4e0a\u6885\u83f2\u65af\u89c1\u5bf9\u65b9\u5df2\u7ecf\u51c6\u5907\u5b8c\u6bd5\uff0c\u63d0\u8d77\u94f6\u5251\uff0c\u5251\u67c4\u5728\u81ea\u5df1\u7684\u80f8\u53e3\u76d4\u7532\u4e0a\u8f7b\u8f7b\u4e00\u649e\uff0c\u968f\u5373\u731b\u7136\u524d\u51b2\uff0c\u4e00\u5251\u671d\u7740\u65e5\u7cbe\u7075\u7684\u80a9\u90e8\u659c\u5288\u4e0b\u6765\u3002\u65e5\u7cbe\u7075\u7a7f\u7740\u4e00\u5957\u80f8\u7532\uff0c\u80a9\u90e8\u867d\u6709\u9632\u62a4\uff0c\u5374\u4e0d\u662f\u91cd\u70b9\uff0c\u5b9a\u7136\u6321\u4e0d\u4f4f\u8fd9\u4e00\u5251\uff0c\u5979\u4e0d\u9000\u53cd\u8fdb\uff0c\u8e0f\u524d\u534a\u6b65\uff0c\u5f2f\u5200\u78d5\u5728\u94f6\u5251\u7684\u4e2d\u6bb5\u9760\u8fd1\u62a4\u67c4\u7684\u4f4d\u7f6e\uff0c\u5c06\u8fd9\u4e00\u51fb\u5378\u5f00\u3002</p><p>\u4e00\u649e\u4e4b\u4e0b\uff0c\u94f6\u5251\u53bb\u52bf\u504f\u659c\uff0c\u4ece\u65c1\u8fb9\u5212\u8fc7\uff0c\u65e5\u7cbe\u7075\u5374\u501f\u7740\u53cd\u5f39\u4e4b\u529b\uff0c\u8d81\u52bf\u6536\u56de\u5f2f\u5200\uff0c\u6b63\u8981\u653b\u51fb\uff0c\u5374\u89c1\u6885\u83f2\u65af\u659c\u8de8\u4e00\u6b65\uff0c\u4ee5\u5de6\u811a\u811a\u8ddf\u4e3a\u8f74\u5fc3\uff0c\u6574\u4e2a\u4eba\u8f6c\u4e86\u534a\u5708\uff0c\u94f6\u5251\u987a\u52bf\u7ffb\u8f6c\uff0c\u547c\u5730\u4e00\u58f0\u518d\u5ea6\u76f4\u5288\u4e0b\u6765\u3002</p><p>\u6885\u83f2\u65af\u7684\u94f6\u5251\u89c4\u683c\u4ecb\u4e4e\u4e8e\u957f\u5251\u548c\u53cc\u624b\u5de8\u5251\u4e4b\u95f4\uff0c\u6bd4\u5f2f\u5200\u8981\u957f\u5f97\u591a\uff0c\u653b\u51fb\u8303\u56f4\u4e5f\u5927\u5f97\u591a\uff0c\u65e5\u7cbe\u7075\u6765\u4e0d\u53ca\u653b\u51fb\uff0c\u53ea\u5f97\u6325\u5200\u683c\u6321\uff0c\u4e00\u649e\u4e4b\u4e0b\u7acb\u8db3\u4e0d\u7a33\uff0c\u63a5\u8fde\u540e\u9000\u4e86\u4e24\u6b65\u3002\u6885\u83f2\u65af\u8d81\u52bf\u8fdb\u903c\uff0c\u94f6\u5251\u81ea\u4e0b\u800c\u4e0a\u53cd\u524a\uff0c\u88ab\u8eb2\u95ea\u5f00\u53bb\u3002</p><p>\u4e00\u4e2a\u7167\u9762\uff0c\u65e5\u7cbe\u7075\u5df2\u7ecf\u8bd5\u51fa\u6885\u83f2\u65af\u7684\u529b\u91cf\u8fdc\u80dc\u81ea\u5df1\uff0c\u6b63\u9762\u51b2\u649e\u663e\u7136\u4e0d\u662f\u660e\u667a\u4e4b\u4e3e\u3002\u5728\u63a5\u4e0b\u6765\u7684\u51e0\u4e2a\u56de\u5408\u4e2d\uff0c\u5979\u501f\u52a9\u7075\u5de7\u7684\u6b65\u6cd5\uff0c\u5728\u573a\u5730\u4e2d\u5feb\u901f\u53d8\u6362\u4f4d\u7f6e\uff0c\u4ece\u5404\u4e2a\u65b9\u4f4d\u5c55\u5f00\u653b\u51fb\u3002\u4e0d\u5f97\u4e0d\u8bf4\u8fd9\u4f4d\u201c\u91d1\u95ea\u95ea\u201d\u786e\u5b9e\u6709\u81ea\u50b2\u7684\u8d44\u672c\uff0c\u5979\u884c\u52a8\u5feb\u5f97\u4eff\u4f5b\u9b3c\u9b45\uff0c\u5f2f\u5200\u5728\u7a7a\u4e2d\u5212\u51fa\u4e00\u9053\u9053\u5bd2\u5149\uff0c\u5f80\u5f80\u780d\u51fa\u4e0a\u4e00\u5200\u7684\u65f6\u5019\u8fd8\u5728\u5de6\u4fa7\uff0c\u780d\u51fa\u4e0b\u4e00\u5200\u7684\u65f6\u5019\u5df2\u7ecf\u5230\u4e86\u53f3\u4fa7\u3002\u5f2f\u5200\u4e0d\u77e5\u662f\u4ec0\u4e48\u6750\u8d28\u5236\u6210\uff0c\u867d\u7136\u770b\u8d77\u6765\u7ec6\u8584\uff0c\u5374\u6781\u5177\u97e7\u59d3\uff0c\u4e0d\u4f46\u80fd\u591f\u683c\u4f4f\u6885\u83f2\u65af\u7684\u94f6\u5251\uff0c\u800c\u4e14\u8fd8\u80fd\u501f\u52bf\u53cd\u5f39\u53d8\u62db\uff0c\u4ece\u800c\u80fd\u591f\u4e00\u5200\u63a5\u7740\u4e00\u5200\uff0c\u6784\u6210\u4e86\u8fde\u7ef5\u4e0d\u65ad\u7684\u8fdb\u653b\u2014\u2014\u8fd9\u662f\u5979\u81ea\u521b\u51fa\u6765\u7684\u5f97\u610f\u62db\u672f\u3002\u6885\u83f2\u65af\u4f3c\u4e4e\u8ddf\u4e0d\u4e0a\u5bf9\u624b\u7684\u901f\u5ea6\uff0c\u987f\u65f6\u5904\u4e8e\u5b88\u52bf\uff0c\u5076\u5c14\u6325\u51fa\u51e0\u5251\uff0c\u4e0d\u662f\u88ab\u907f\u5f00\u5c31\u662f\u88ab\u5de7\u5999\u5730\u5378\u6389\u529b\u9053\uff0c\u4e00\u65f6\u95f4\u9690\u9690\u843d\u5728\u4e0b\u98ce\u3002</p><p>\u743c\u6069\u770b\u5f97\u6709\u4e9b\u7d27\u5f20\uff0c\u51e0\u6b21\u90fd\u9669\u4e9b\u8981\u53d1\u51fa\u89e3\u79bb\u672f\uff0c\u5e78\u597d\u6885\u83f2\u65af\u5b88\u5fa1\u5f97\u4e25\u5bc6\uff0c\u867d\u7136\u60ca\u9669\uff0c\u5374\u603b\u80fd\u5728\u6700\u540e\u5173\u5934\u5316\u89e3\u3002\u518d\u8fc7\u7247\u523b\uff0c\u65e5\u7cbe\u7075\u7684\u653b\u51fb\u66f4\u52a0\u51cc\u5389\uff0c\u7a7a\u6c14\u4e2d\u5df2\u7ecf\u9690\u7ea6\u51fa\u73b0\u6b8b\u50cf\uff0c\u743c\u6069\u77e5\u9053\u90a3\u662f\u56e0\u4e3a\u9ad8\u901f\u8fd0\u52a8\u7684\u4ea7\u7269\u3002\u6885\u83f2\u65af\u5b88\u5fa1\u5f97\u8d8a\u53d1\u8270\u96be\uff0c\u7a81\u7136\u4f4e\u559d\u4e00\u58f0\uff0c\u53cc\u624b\u63e1\u5251\uff0c\u7167\u7740\u65e5\u7cbe\u7075\u5f53\u5934\u5288\u4e0b\u3002</p><p>\u201c\u4e0d\u5bf9\uff01\u201d\u5fb7\u53e4\u62c9\u4f4e\u58f0\u8bf4\u3002</p><p>\u743c\u6069\u4e0d\u901a\u6b66\u6280\uff0c\u770b\u4e0d\u51fa\u4ec0\u4e48\u95ee\u9898\uff0c\u4ed6\u5374\u662f\u6e05\u695a\u7684\u3002\u6885\u83f2\u65af\u8fd9\u4e00\u51fb\u770b\u4f3c\u51cc\u5389\uff0c\u4f46\u89d2\u5ea6\u5e76\u4e0d\u5201\u94bb\uff0c\u5f88\u5bb9\u6613\u88ab\u5c01\u4f4f\u3002\u65e5\u7cbe\u7075\u6b64\u65f6\u5df2\u7ecf\u8fd1\u8eab\uff0c\u53ea\u8981\u5378\u5f00\u8fd9\u4e00\u5251\uff0c\u6885\u83f2\u65af\u4fbf\u7a7a\u95e8\u5927\u9732\uff0c\u65e0\u6cd5\u62b5\u6321\u63a5\u4e0b\u6765\u7684\u653b\u51fb\u3002</p><p>\u201c\u5979\u600e\u4e48\u4f1a\u72af\u8fd9\u79cd\u9519\u8bef\uff1f\u201d</p><p>\u6b63\u81ea\u60ca\u8bb6\uff0c\u573a\u4e0a\u5f62\u52bf\u53c8\u53d8\uff0c\u6b63\u5982\u5fb7\u53e4\u62c9\u6240\u6599\uff0c\u65e5\u7cbe\u7075\u6ca1\u6709\u653e\u8fc7\u8fd9\u4e2a\u673a\u4f1a\uff0c\u53cd\u624b\u4e00\u5200\u5288\u51fa\uff0c\u6253\u7b97\u5c06\u94f6\u5251\u5411\u5916\u4fa7\u649e\u5f00\uff0c\u800c\u5f2f\u5200\u5219\u53cd\u5f39\u800c\u56de\uff0c\u7d27\u63a5\u7740\u4fbf\u80fd\u53d1\u51fa\u81f4\u547d\u4e00\u51fb\u3002</p><p>\u4f46\u5979\u8fd9\u4e00\u5200\u843d\u4e86\u4e2a\u7a7a\u3002</p><p>\u6885\u83f2\u65af\u7684\u94f6\u5251\u7a81\u7136\u6d88\u5931\u5728\u638c\u4e2d\uff0c\u4ee5\u81f3\u4e8e\u65e5\u7cbe\u7075\u7684\u5f2f\u5200\u65e2\u6ca1\u6709\u8d77\u5230\u4efb\u4f55\u683c\u6321\u7684\u4f5c\u7528\uff0c\u66f4\u6ca1\u80fd\u501f\u52bf\u53cd\u5f39\uff0c\u800c\u662f\u76f4\u63a5\u5288\u5728\u7a7a\u6c14\u4e2d\u3002\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u94f6\u5251\u518d\u5ea6\u51fa\u73b0\u5728\u6885\u83f2\u65af\u624b\u4e2d\uff0c\u5251\u5c16\u5df2\u7ecf\u62b5\u4f4f\u4e86\u5bf9\u624b\u7684\u54bd\u5589\u3002</p><p>\u8f6c\u77ac\u4e4b\u95f4\uff0c\u80dc\u8d1f\u5df2\u5206\u3002</p></article>"}}}]);
