(window.webpackJsonp=window.webpackJsonp||[]).push([[1510],{1533:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516b\u8282 \u5f71\u706b",content:"<article><h2>\u7b2c\u5341\u516b\u8282 \u5f71\u706b</h2><p>\u8475\u9732\u62b5\u8fbe\u6668\u70bc\u57ce\u7684\u7b2c\u4e09\u5929\uff0c\u6b63\u662f\u4f0a\u8389\u4e1d\u7fe0\u6559\u4f1a\u7684\u5723\u65e5\u3002\u6309\u7167\u89c4\u77e9\uff0c\u5353\u5c14\u4eec\u4e3e\u884c\u4e86\u796d\u7940\u4eea\u5f0f\u3002</p><p>\u6668\u70bc\u57ce\u4e2d\u8fd8\u6ca1\u6765\u5f97\u53ca\u5efa\u8d77\u795e\u6bbf\uff0c\u7070\u77ee\u4eba\u7684\u5efa\u7b51\u98ce\u683c\u4e5f\u5b8c\u5168\u4e0d\u7b26\u5408\u8981\u6c42\uff0c\u6240\u4ee5\u8fd9\u6b21\u796d\u7940\u53ea\u80fd\u53e6\u5bfb\u522b\u5730\u3002\u5357\u90e8\u4e34\u8fd1\u5730\u4e0b\u6cb3\uff0c\u6709\u4e00\u5ea7\u8302\u5bc6\u7684\u5c0f\u8548\u6797\u3002\u5de8\u5927\u8548\u7c7b\u548c\u4f4e\u77ee\u7684\u8611\u83c7\u7fa4\u6bd4\u90bb\u800c\u4f34\uff0c\u5939\u6742\u7740\u84dd\u5e3d\u5b50\u3001\u8840\u679c\u6811\u548c\u624e\u514b\u6728\u5b62\uff0c\u84ec\u52c3\u751f\u957f\u3002\u706b\u5730\u8863\u6cbf\u7740\u6d1e\u7a74\u7684\u77f3\u58c1\u548c\u7a79\u9876\u751f\u957f\u7740\uff0c\u5728\u5730\u8109\u8f90\u5c04\u7684\u53d8\u5f02\u4f5c\u7528\u4e0b\u6563\u53d1\u51fa\u7cbc\u7cbc\u51b7\u5149\u3002\u5728\u6797\u5730\u7684\u4e2d\u95f4\uff0c\u6709\u4e00\u5904\u5c0f\u5c0f\u7684\u7a7a\u5730\uff0c\u51e0\u68f5\u7c7b\u4f3c\u5564\u9152\u6876\u7684\u6876\u83c7\u751f\u957f\u8054\u7ed3\u5728\u4e00\u8d77\uff0c\u7ecf\u8fc7\u5de7\u5999\u7684\u4fee\u9970\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u7b80\u964b\u7684\u796d\u575b\u3002</p><p>\u8475\u9732\u4e3b\u6301\u7740\u4eea\u5f0f\uff0c\u5979\u4f9d\u65e7\u7a7f\u7740\u90a3\u4ef6\u4e0d\u65ad\u53d8\u5e7b\u94f6\u5149\u7684\u957f\u888d\uff0c\u7cbe\u81f4\u7684\u9762\u5e9e\u6b64\u523b\u770b\u8d77\u6765\u65e2\u7f8e\u4e3d\u53c8\u5a01\u4e25\u3002\u4e00\u67c4\u94f6\u8272\u7684\u957f\u5251\u4ece\u5979\u8eab\u4f53\u4e2d\u6d6e\u51fa\uff0c\u6e38\u79bb\u5728\u796d\u575b\u4e0a\uff0c\u5251\u8eab\u8f7b\u5fae\u5730\u98a4\u52a8\u7740\uff0c\u8f7b\u5feb\u67d4\u548c\u7684\u97f3\u4e50\u4eff\u4f5b\u6f7a\u6f7a\u6eaa\u6c34\u822c\u54cd\u8d77\uff0c\u7d27\u63a5\u7740\uff0c\u4eff\u4f5b\u5e7b\u89c9\u4e00\u822c\uff0c\u90a3\u67c4\u957f\u5251\u53d8\u6210\u4e86\u4e00\u8f6e\u660e\u6708\uff0c\u5189\u5189\u4e0a\u5347\uff0c\u60ac\u6302\u534a\u7a7a\uff0c\u768e\u6d01\u7684\u6708\u5149\u6d41\u6cfb\u4e0b\u6765\uff0c\u8ba9\u4eba\u51e0\u4e4e\u5fd8\u4e86\u8fd9\u91cc\u662f\u5e7d\u6697\u5730\u57df\u3002</p><p>\u6240\u6709\u7684\u5353\u5c14\u4eec\u56f4\u7740\u796d\u575b\u6392\u6210\u73af\u72b6\uff0c\u624b\u7275\u7740\u624b\uff0c\u541f\u5531\u8d77\u7948\u7977\u7684\u7075\u6b4c\u3002\u5982\u679c\u4ed4\u7ec6\u503e\u542c\u7684\u8bdd\uff0c\u4fbf\u4f1a\u53d1\u73b0\u6bcf\u4e2a\u4eba\u5531\u7684\u6b4c\u90fd\u5e76\u4e0d\u76f8\u540c\uff0c\u4f46\u5374\u6ca1\u6709\u663e\u5f97\u6742\u4e71\u65e0\u7ae0\uff0c\u53cd\u800c\u662f\u5f88\u5947\u5999\u5730\u6df7\u5408\u6210\u4e86\u4e00\u79cd\u7279\u522b\u7684\u97f5\u5f8b\u3002\u8475\u9732\u8d70\u4e0a\u796d\u575b\uff0c\u4f38\u624b\u5728\u80f8\u524d\u8f7b\u8f7b\u4e00\u626f\uff0c\u94f6\u8272\u957f\u888d\u8f7b\u76c8\u5730\u4ece\u8eab\u4f53\u4e0a\u6ed1\u843d\uff0c\u892a\u5728\u811a\u8fb9\uff0c\u73b0\u51fa\u5b8c\u7f8e\u7684\u88f8\u4f53\u3002\u5979\u7684\u52a8\u4f5c\u4f18\u7f8e\uff0c\u4e3e\u624b\u6295\u8db3\u95f4\u4eff\u4f5b\u821e\u8e48\u4e00\u822c\uff0c\u770b\u8d77\u6765\u4ee4\u4eba\u8d4f\u5fc3\u60a6\u76ee\u3002</p><p>\u7948\u7977\u7684\u7075\u6b4c\u9010\u6e10\u5639\u4eae\u8d77\u6765\uff0c\u548c\u7a7a\u4e2d\u6708\u4eae\u7684\u8f7b\u5feb\u97f3\u8c03\u76f8\u5408\uff0c\u8475\u9732\u5f00\u59cb\u5728\u796d\u575b\u4e0a\u76d8\u65cb\u8d77\u821e\u3002\u5728\u4e00\u5f00\u59cb\uff0c\u5979\u7684\u52a8\u4f5c\u5341\u5206\u7f13\u6162\uff0c\u8f7b\u67d4\u4e2d\u9690\u542b\u7740\u5a01\u4e25\uff0c\u7136\u540e\u901f\u5ea6\u6e10\u6e10\u52a0\u5feb\uff0c\u6c57\u6c34\u5f00\u59cb\u70b9\u70b9\u6e17\u51fa\uff0c\u4f7f\u9ed1\u7389\u822c\u7684\u808c\u80a4\u770b\u4e0a\u53bb\u66f4\u5177\u5149\u6cfd\uff0c\u67d4\u6ed1\u800c\u7ec6\u817b\u3002\u968f\u7740\u6b4c\u58f0\u8282\u594f\u7684\u8fdb\u4e00\u6b65\u7d27\u5bc6\uff0c\u5979\u7684\u821e\u59ff\u4e5f\u8d8a\u6765\u8d8a\u6fc0\u70c8\u72c2\u91ce\uff0c\u8eab\u4f53\u524d\u4fef\u540e\u4ef0\uff0c\u6446\u81c2\u6447\u81c0\uff0c\u4ece\u6307\u5c16\u5230\u8db3\u8e1d\uff0c\u5168\u8eab\u4eff\u4f5b\u7075\u86c7\u4e00\u822c\u6ce2\u6ce2\u6298\u6298\u5730\u626d\u52a8\u7740\uff0c\u505a\u51fa\u5404\u79cd\u7cbe\u5de7\u590d\u6742\u7684\u52a8\u4f5c\uff0c\u65e2\u5a40\u5a1c\u59a9\u5a9a\u5374\u53c8\u539f\u59cb\u91ce\u6027\uff0c\u8ba9\u4eba\u773c\u82b1\u7f2d\u4e71\uff0c\u76ee\u4e0d\u6687\u7ed9\u3002</p><p>\u201c\u771f\u6f02\u4eae\u3002\u201d\u743c\u6069\u79f0\u8d5e\u8bf4\u3002</p><p>\u8fd9\u79cd\u6559\u4f1a\u5185\u90e8\u7684\u796d\u7940\u4eea\u5f0f\uff0c\u81ea\u7136\u4e0d\u4f1a\u9080\u8bf7\u4ed6\u8fd9\u79cd\u5916\u4eba\u53c2\u52a0\uff0c\u4e8b\u5b9e\u4e0a\u4ed6\u539f\u672c\u90fd\u4e0d\u77e5\u9053\u8fd8\u6709\u8fd9\u4e8b\uff0c\u76f4\u5230\u5fb7\u53e4\u62c9\u8dd1\u6765\u5411\u4ed6\u501f\u671b\u8fdc\u955c\u3002</p><p>\u743c\u6069\u4ece\u9634\u9b42\u57ce\u51fa\u53d1\u7684\u65f6\u5019\uff0c\u5f04\u4e86\u4e00\u526f\u671b\u8fdc\u955c\uff0c\u4f46\u4e00\u76f4\u6ca1\u6709\u6d3e\u4e0a\u592a\u591a\u7528\u573a\uff0c\u53ea\u80fd\u653e\u5728\u6000\u91cc\u5f53\u73a9\u5177\u3002\u4f46\u5728\u8fd9\u4e2a\u4e16\u754c\uff0c\u671b\u8fdc\u955c\u6bd5\u7adf\u7b97\u662f\u524d\u6240\u672a\u6709\u7684\u65b0\u53d1\u660e\uff0c\u743c\u6069\u6709\u65f6\u5019\u4f1a\u62ff\u51fa\u6765\u70ab\u8000\uff0c\u6240\u4ee5\u5fb7\u53e4\u62c9\u77e5\u9053\u3002</p><p>\u201c\u4f60\u8981\u671b\u8fdc\u955c\u505a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u6709\u4e9b\u5947\u602a\u3002</p><p>\u201c\u5f53\u7136\u662f\u53bb\u89c2\u6469\u90a3\u7fa4\u5353\u5c14\u8df3\u8131\u8863\u821e\u554a\u3002\u201d</p><p>\u201c\u8131\u8863\u821e\uff1f\u6211\u600e\u4e48\u4e0d\u77e5\u9053\u3002\u201d</p><p>\u201c\u4eca\u5929\u662f\u5979\u4eec\u7684\u5723\u65e5\uff0c\u8981\u5728\u57ce\u5916\u4e3e\u884c\u796d\u7940\uff0c\u4f60\u6ca1\u542c\u8bf4\uff1f\u201d</p><p>\u201c\u5443\uff0c\u4e0d\u6e05\u695a\u2026\u2026\u4e0d\u8fc7\u8fd9\u548c\u8131\u8863\u821e\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u201d</p><p>\u201c\u4f0a\u8389\u4e1d\u7fe0\u6559\u4f1a\u7684\u796d\u7940\uff0c\u4e0d\u662f\u88f8\u5954\u5c31\u662f\u88f8\u821e\uff0c\u8fd9\u4f60\u90fd\u4e0d\u77e5\u9053\uff1f\u201d</p><p>\u88ab\u5438\u8840\u9b3c\u7528\u9119\u89c6\u7684\u773c\u795e\u770b\u7740\uff0c\u743c\u6069\u90fd\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u8d77\u6765\uff0c\u56de\u60f3\u81ea\u5df1\u770b\u8fc7\u7684\u6709\u5173\u4f0a\u8389\u4e1d\u7fe0\u6559\u4f1a\u8d44\u6599\uff0c\u4f3c\u4e4e\u786e\u5b9e\u63d0\u5230\u8fd9\u4e48\u4e00\u4e24\u53e5\uff0c\u53ea\u662f\u5f53\u65f6\u6ca1\u5728\u610f\uff0c\u76f4\u63a5\u5ffd\u7565\u8fc7\u53bb\u4e86\u3002\u201c\u8fd9\u6837\u554a\uff0c\u4e0d\u8fc7\u4f60\u6709\u5fc5\u8981\u53bb\u5077\u770b\u4e48\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53cd\u6b63\u4f60\u5e73\u65f6\u5728\u5e8a\u4e0a\u90fd\u770b\u8fc7\u4e86\u3002\u201d</p><p>\u5fb7\u53e4\u62c9\u52fe\u642d\u7f8e\u5973\u7684\u6218\u7ee9\u4e00\u5982\u65e2\u5f80\u5730\u8f89\u714c\uff0c\u5c31\u743c\u6069\u7684\u4e0d\u5b8c\u5168\u4f30\u8ba1\uff0c\u8fd9\u6bb5\u65f6\u95f4\u6765\u4ed6\u5f04\u4e0a\u624b\u7684\u5353\u5c14\u5973\u5b50\u5df2\u7ecf\u8d85\u8fc7\u4e86\u4e8c\u5341\u540d\uff0c\u751a\u81f3\u4e09\u5341\u540d\u90fd\u6709\u53ef\u80fd\u3002\u636e\u8bf4\u4ed6\u8fd8\u66fe\u7ecf\u60f3\u6362\u6362\u53e3\u5473\uff0c\u5bf9\u91d1\u95ea\u95ea\u4e0b\u624b\uff0c\u7ed3\u679c\u88ab\u75db\u6253\u4e86\u4e00\u987f\u3002\u5bf9\u4e8e\u8fd9\u4e00\u70b9\uff0c\u743c\u6069\u89c9\u5f97\u4ed6\u5b8c\u5168\u662f\u81ea\u4f5c\u81ea\u53d7\uff0c\u91d1\u95ea\u95ea\u4e00\u770b\u5c31\u6709\u857e\u4e1d\u8fb9\u7684\u5acc\u7591\uff0c\u5fb7\u53e4\u62c9\u6253\u5979\u7684\u4e3b\u610f\uff0c\u7eaf\u5c5e\u4e0d\u957f\u773c\u775b\u3002</p><p>\u201c\u8fd9\u5b8c\u5168\u662f\u4e24\u7801\u4e8b\uff0c\u201d\u5fb7\u53e4\u62c9\u4e49\u6b63\u8bcd\u4e25\u5730\u8bf4\uff0c\u201c\u540c\u6837\u662f\u88f8\u4f53\uff0c\u5728\u5e8a\u4e0a\u90a3\u662f\u6027\u548c\u6b32\u671b\uff0c\u5728\u821e\u8e48\u65f6\u90a3\u5c31\u662f\u7f8e\u7684\u6b23\u8d4f\u2026\u2026\u800c\u4e14\u6700\u5173\u952e\u7684\u662f\uff0c\u8fd9\u6b21\u6709\u8475\u9732\u5440\u3002\u201d</p><p>\u201c\u6700\u540e\u4e00\u53e5\u624d\u662f\u4f60\u7684\u5fc3\u58f0\u5427\u3002\u201d</p><p>\u201c\u522b\u8bf4\u8fd9\u4e48\u591a\u4e86\uff0c\u671b\u8fdc\u955c\u5230\u5e95\u501f\u4e0d\u501f\uff0c\u201d\u5438\u8840\u9b3c\u6709\u4e9b\u6cf1\u6cf1\uff0c\u201c\u5982\u679c\u4f60\u4e0d\u501f\uff0c\u6211\u5c31\u65e9\u4f5c\u5176\u4ed6\u51c6\u5907\u3002\u201d</p><p>\u201c\u4f60\u505a\u4ec0\u4e48\u5176\u4ed6\u51c6\u5907\uff1f\u201d</p><p>\u201c\u90a3\u5730\u65b9\u5728\u6cb3\u8fb9\uff0c\u6211\u6f5c\u5728\u6c34\u5e95\u770b\u5c31\u662f\u4e86\uff0c\u5148\u53bb\u5f04\u4e2a\u597d\u4f4d\u7f6e\u3002\u201d</p><p>\u743c\u6069\u5927\u7b11\u8d77\u6765\uff0c\u53d6\u51fa\u671b\u8fdc\u955c\uff0c\u5374\u6ca1\u6709\u8fc7\u7ed9\u4ed6\uff0c\u201c\u4f60\u4e0d\u7528\u6f5c\u6c34\u4e86\uff0c\u4e2d\u5c09\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4eec\u4e00\u8d77\u53bb\u3002\u201d</p><p>\u6240\u4ee5\u73b0\u5728\u8fd9\u4e24\u4e2a\u4eba\u6b63\u7ad9\u5728\u6668\u70bc\u521a\u521a\u4fee\u8865\u597d\u7684\u57ce\u5899\u4e0a\uff0c\u8f6e\u6d41\u7528\u671b\u8fdc\u955c\u5728\u6d25\u6d25\u6709\u5473\u5730\u6b23\u8d4f\u7740\u8475\u9732\u7684\u821e\u8e48\u3002</p><p>\u201c\u8be5\u8f6e\u5230\u6211\u4e86\u3002\u201d\u5fb7\u53e4\u62c9\u628a\u671b\u8fdc\u955c\u4ece\u743c\u6069\u624b\u91cc\u62a2\u8fc7\u6765\u3002</p><p>\u968f\u7740\u821e\u8e48\u8fbe\u5230\u9ad8\u6f6e\uff0c\u94f6\u5251\u5316\u6210\u7684\u6708\u4eae\u4e5f\u8d8a\u53d1\u5149\u660e\u768e\u6d01\uff0c\u7480\u74a8\u593a\u76ee\uff0c\u4eff\u4f5b\u95ea\u70c1\u7684\u94f6\u76d8\u3002\u4e00\u4e2a\u6a21\u7cca\u7684\u4eba\u5f71\u5728\u94f6\u76d8\u4e2d\u51fa\u73b0\uff0c\u6e10\u6e10\u6e05\u6670\uff0c\u90a3\u4e5f\u540c\u6837\u662f\u4e00\u4e2a\u88f8\u4f53\u7684\u5353\u5c14\u5973\u6027\uff0c\u6709\u7740\u76f4\u5230\u811a\u8e1d\u7684\u94f6\u5149\u6d41\u66f3\u957f\u53d1\uff0c\u624b\u6267\u94f6\u5251\u7fe9\u7fe9\u8d77\u821e\u3002\u90a3\u6b63\u662f\u5979\u4eec\u4fe1\u4ef0\u7684\u795e\u660e\uff0c\u5e7d\u6697\u5c11\u5973\u4f0a\u8389\u4e1d\u7fe0\u3002</p><p>\u795e\u7947\u7684\u73b0\u8eab\uff0c\u51fa\u4e4e\u6240\u6709\u5353\u5c14\u4eec\u7684\u610f\u6599\uff0c\u5728\u96c6\u4f53\u5446\u6ede\u4e86\u4e00\u4e0b\u4e4b\u540e\uff0c\u7d27\u63a5\u7740\u5c31\u662f\u6b23\u559c\u82e5\u72c2\u3002\u56e0\u4e3a\u8fd9\u610f\u5473\u7740\u5973\u795e\u5df2\u7ecf\u4ece\u6c89\u7720\u4e2d\u9010\u6e10\u6062\u590d\uff0c\u867d\u7136\u6682\u65f6\u8fd8\u662f\u6ca1\u6709\u8d50\u4e88\u7267\u5e08\u795e\u672f\uff0c\u5e94\u8be5\u662f\u5f88\u865a\u5f31\uff0c\u4f46\u81f3\u5c11\u5979\u5df2\u7ecf\u53ef\u4ee5\u73b0\u8eab\u4e8e\u796d\u7940\u4eea\u5f0f\u4e0a\uff0c\u56de\u5e94\u81ea\u5df1\u7684\u4fe1\u5f92\u7948\u7977\u4e86\u3002\u8fd9\u81ea\u7136\u662f\u4e00\u4e2a\u5929\u5927\u7684\u597d\u6d88\u606f\u3002</p><p>\u548c\u4f0a\u8389\u4e1d\u7fe0\u4fe1\u5f92\u4e00\u6837\uff0c\u743c\u6069\u548c\u5fb7\u53e4\u62c9\u4e5f\u901a\u8fc7\u671b\u8fdc\u955c\u770b\u89c1\u4e86\u5973\u795e\u51fa\u73b0\uff0c\u867d\u7136\u4ec5\u4ec5\u53ea\u662f\u4e2a\u5f71\u50cf\uff0c\u4f46\u5df2\u7ecf\u8db3\u4ee5\u8bf4\u660e\u95ee\u9898\u3002\u743c\u6069\u5fae\u5fae\u76b1\u7740\u7709\u5934\uff0c\u6c89\u541f\u7247\u523b\uff0c\u5c06\u671b\u8fdc\u955c\u9012\u7ed9\u5fb7\u53e4\u62c9\uff0c\u81ea\u5df1\u8f6c\u8eab\u4e0b\u4e86\u57ce\u5899\u3002</p><p>\u4f0a\u8389\u4e1d\u7fe0\u7684\u590d\u82cf\uff0c\u52bf\u5fc5\u5927\u5927\u589e\u5f3a\u5979\u7684\u6559\u4f1a\u7684\u529b\u91cf\u548c\u4fe1\u5fc3\u3002\u6309\u9053\u7406\u8bf4\uff0c\u743c\u6069\u5982\u4eca\u548c\u8fd9\u4e9b\u5353\u5c14\u662f\u76df\u53cb\uff0c\u76df\u53cb\u529b\u91cf\u7684\u589e\u5f3a\uff0c\u6216\u8bb8\u662f\u597d\u4e8b\uff0c\u4f46\u4e5f\u6216\u8bb8\u4e0d\u662f\u3002\u5982\u679c\u5728\u524d\u51e0\u5929\uff0c\u90a3\u4e48\u4ed6\u8fd8\u4e0d\u4f1a\u8fd9\u4e48\u795e\u7ecf\u8fc7\u654f\uff0c\u4f46\u8475\u9732\u7684\u6001\u5ea6\uff0c\u5374\u5df2\u7ecf\u6e05\u695a\u5c55\u793a\u4e86\u5979\u5bf9\u743c\u6069\u7684\u654c\u610f\uff0c\u81f3\u5c11\u662f\u4e0d\u4fe1\u4efb\u3002</p><p>\u201c\u4e5f\u65e0\u6240\u8c13\uff0c\u201d\u4ed6\u60f3\uff0c\u7a81\u7136\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u53cd\u6b63\u81ea\u5df1\u539f\u672c\u5c31\u6ca1\u6709\u6253\u7b97\u63d2\u624b\u5176\u4e2d\uff0c\u4e00\u5f00\u59cb\u7eaf\u7cb9\u662f\u770b\u5728\u6885\u83f2\u65af\u7684\u4efd\u4e0a\uff0c\u4e49\u52a1\u5e2e\u5fd9\u7f62\u4e86\u3002\u65e2\u7136\u4e0d\u62b1\u6240\u6c42\u800c\u6765\uff0c\u4f55\u5fc5\u73b0\u5728\u53c8\u60a3\u5f97\u60a3\u5931\u3002\u201d</p><p>\u81f3\u4e0d\u6d4e\uff0c\u79bb\u5f00\u6668\u70bc\uff0c\u518d\u56de\u4f0a\u5361\u6c99\u57ce\u53bb\u5c31\u662f\u3002\u5353\u5c14\u72e1\u8bc8\uff0c\u77ee\u4eba\u5374\u5b9e\u8bda\uff0c\u8fd9\u6bb5\u65f6\u95f4\u81ea\u5df1\u4e3b\u8981\u5c45\u4f4f\u5728\u6668\u70bc\uff0c\u9c81\u6587\u56fd\u738b\u51e0\u6b21\u6d3e\u9063\u4f7f\u8005\u95ee\u5019\uff0c\u5076\u5c14\u56de\u53bb\u51e0\u6b21\uff0c\u77ee\u4eba\u4f9d\u65e7\u70ed\u60c5\u76f8\u5f85\uff0c\u9b54\u50cf\u57fa\u5730\u4e5f\u6b63\u5728\u52a0\u7d27\u8425\u5efa\u4e4b\u4e2d\uff0c\u5e76\u65e0\u4ec0\u4e48\u62d6\u6c93\u63a8\u8bff\u3002\u5982\u4eca\u60f3\u8d77\u6765\uff0c\u867d\u7136\u6ee1\u57ce\u7684\u5353\u5c14\u5973\u5b50\u770b\u7740\u517b\u773c\uff0c\u4f46\u77ee\u4eba\u4e5f\u6709\u77ee\u4eba\u7684\u597d\u5904\uff0c\u6253\u4ea4\u9053\u4e0d\u8d39\u5fc3\u673a\u3002</p><p>\u4e3b\u610f\u5b9a\u4e86\uff0c\u5fc3\u4e0b\u4e5f\u5c31\u5e73\u9759\uff0c\u8475\u9732\u7684\u821e\u8e48\u5df2\u7ecf\u6b23\u8d4f\u8fc7\u4e86\uff0c\u5176\u4f59\u5353\u5c14\u4ed6\u4e5f\u6ca1\u4ec0\u4e48\u5174\u8da3\uff0c\u61d2\u5f97\u56de\u53bb\u548c\u90a3\u4e2a\u5438\u8840\u9b3c\u51d1\u70ed\u95f9\u3002\u5f84\u76f4\u56de\u5230\u81ea\u5df1\u623f\u95f4\uff0c\u63a8\u5f00\u95e8\uff0c\u53d1\u73b0\u6885\u83f2\u65af\u6b63\u5750\u5728\u5e8a\u4e0a\uff0c\u9760\u7740\u5e8a\u5934\u770b\u4e66\uff0c\u5979\u8eab\u4e0a\u53ea\u7a7f\u4e86\u4e00\u4ef6\u743c\u6069\u7684\u886c\u886b\uff0c\u867d\u7136\u5bbd\u5927\uff0c\u5374\u4e5f\u4ec5\u4ec5\u80fd\u906e\u63a9\u4f4f\u4e0b\u8eab\u5173\u952e\u90e8\u4f4d\uff0c\u8fde\u7f8e\u81c0\u90fd\u5927\u534a\u66b4\u9732\u5728\u5916\uff0c\u770b\u7740\u743c\u6069\u5fc3\u5934\u4e00\u70ed\u3002</p><p>\u201c\u8475\u9732\u8df3\u821e\u5f88\u597d\u770b\u5417\uff1f\u201d\u5979\u4eff\u4f5b\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u95ee\u3002</p><p>\u201c\u5f88\u6f02\u4eae\uff0c\u201d\u743c\u6069\u987a\u53e3\u56de\u7b54\uff0c\u7136\u540e\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u5443\uff0c\u90a3\u4e2a\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u5fae\u7b11\u8d77\u6765\uff0c\u653e\u4e0b\u4e66\uff0c\u5f20\u5f00\u53cc\u81c2\uff0c\u201c\u62b1\u62b1\u6211\u3002\u201d</p><p>\u743c\u6069\u5750\u5728\u5e8a\u4e0a\uff0c\u5c06\u5979\u62b1\u5728\u6000\u91cc\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u201c\u6211\u53ea\u662f\u6709\u4e9b\u597d\u5947\uff0c\u53bb\u770b\u770b\u2026\u2026\u201d</p><p>\u201c\u55ef\uff0c\u5176\u5b9e\u6211\u4e5f\u5f88\u60f3\u53bb\u770b\u770b\u5462\uff0c\u201d\u6885\u83f2\u65af\u67d4\u58f0\u8bf4\uff0c\u201c\u53bb\u5b66\u5b66\u600e\u4e48\u8df3\u821e\u3002\u201d</p><p>\u201c\u4f60\u6ca1\u5b66\u8fc7\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6bcd\u4eb2\u6ca1\u6559\u8fc7\uff0c\u6559\u4f1a\u91cc\u66f4\u4e0d\u4f1a\u6559\u8fd9\u4e9b\u2026\u2026\u5176\u5b9e\u6211\u5f88\u7fa1\u6155\u90a3\u4e9b\u4f1a\u8df3\u821e\u7684\u5973\u5b69\u5b50\u7684\uff0c\u50cf\u51db\u3002\u201d</p><p>\u201c\u5979\u4f1a\u8df3\u821e\u5417\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u8df3\u5f97\u5f88\u597d\u7684\u3002\u201d</p><p>\u201c\u8ba9\u5979\u6559\u4f60\u554a\u3002\u201d</p><p>\u6885\u83f2\u65af\u8f7b\u8f7b\u6447\u5934\uff0c\u201c\u5979\u90a3\u79cd\u821e\u5f88\u7279\u522b\uff0c\u4f60\u770b\u4e86\u5c31\u77e5\u9053\uff0c\u6ca1\u6cd5\u6559\u7684\u3002\u8fde\u5979\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u5b66\u4f1a\u7684\uff0c\u66f4\u50cf\u662f\u5929\u8d4b\u4e00\u6837\u3002\u6211\u8bd5\u7740\u60f3\u6a21\u4eff\uff0c\u600e\u4e48\u90fd\u5b66\u4e0d\u4f1a\u3002\u201d</p><p>\u201c\u5176\u5b9e\uff0c\u201d\u5979\u4f4e\u58f0\u7b11\u7740\uff0c\u201c\u5982\u679c\u8bf4\u8d77\u6765\uff0c\u6211\u4f3c\u4e4e\u5f88\u5dee\u52b2\u5462\uff0c\u5f88\u591a\u5973\u5b69\u5b50\u4f1a\u7684\u4e1c\u897f\uff0c\u6211\u90fd\u4e0d\u4f1a\u3002\u821e\u8e48\u6211\u4e0d\u4f1a\uff0c\u4e50\u5668\u4e5f\u4e0d\u4f1a\uff0c\u7ed8\u753b\u4e5f\u4e0d\u4f1a\u2026\u2026\u6211\u4f1a\u9a91\u9a6c\u3001\u6218\u6597\u3001\u7528\u5251\u6740\u4eba\uff0c\u80fd\u80cc\u8bf5\u51e0\u4e4e\u6240\u6709\u6587\u660e\u57ce\u90a6\u548c\u56fd\u5bb6\u7684\u5f8b\u6cd5\uff0c\u901a\u6653\u5b83\u4eec\u7684\u5b98\u50da\u4f53\u5236\uff0c\u6211\u80fd\u719f\u7ec3\u8fd0\u7528\u8fd9\u4e16\u754c\u4e0a\u51e0\u4e4e\u6240\u6709\u7684\u8bed\u8a00\uff0c\u4e86\u89e3\u5404\u79cd\u6559\u4f1a\u3001\u56e2\u4f53\u548c\u9ed1\u6697\u7ec4\u7ec7\u7684\u8d44\u6599\u2026\u2026\u53ef\u60dc\u8fd9\u4e9b\u4f3c\u4e4e\u90fd\u4e0d\u50cf\u4e00\u4e2a\u5973\u5b69\u5b50\u5e94\u8be5\u4f1a\u7684\u554a\u3002\u201d</p><p>\u201c\u4f60\u4f1a\u4e0b\u68cb\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6bcf\u6b21\u90fd\u628a\u6211\u6253\u5f97\u4e00\u8d25\u6d82\u5730\u3002\u201d</p><p>\u201c\u8fd9\u4e9b\u90fd\u592a\u2026\u2026\u592a\u7406\u6027\u5316\u4e86\uff0c\u201d\u6885\u83f2\u65af\u6c89\u541f\u7740\uff0c\u6700\u7ec8\u9009\u62e9\u4e86\u4e00\u4e2a\u5408\u9002\u7684\u5f62\u5bb9\u8bcd\uff0c\u201c\u6211\u6240\u6709\u64c5\u957f\u7684\uff0c\u90fd\u662f\u8bb2\u7a76\u903b\u8f91\uff0c\u9700\u8981\u667a\u529b\u7684\u6280\u80fd\u2014\u2014\u8fd9\u8ba9\u6211\u611f\u89c9\u81ea\u5df1\u50cf\u4f8f\u5112\u5236\u9020\u7684\u673a\u68b0\uff0c\u7cbe\u5bc6\uff0c\u5374\u7f3a\u4e4f\u7075\u6c14\uff0c\u6211\u5e0c\u671b\u80fd\u4f1a\u4e00\u4e9b\u611f\u6027\u5316\u7684\u4e1c\u897f\u3002\u201d</p><p>\u743c\u6069\u8f7b\u8f7b\u629a\u6478\u7740\u5979\u7684\u79c0\u53d1\uff0c\u201c\u4f60\u5bf9\u81ea\u5df1\u8981\u6c42\u592a\u9ad8\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u53ea\u662f\u6ca1\u6709\u65f6\u95f4\u53bb\u5b66\u7f62\u4e86\u3002\u4f60\u662f\u6211\u89c1\u8fc7\u6700\u806a\u660e\u7684\u5973\u5b69\u5b50\uff0c\u65e0\u8bba\u5b66\u4ec0\u4e48\u90fd\u4e00\u5b9a\u5f88\u5feb\u7684\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u771f\u7684\u3002\u201d</p><p>\u201c\u55ef\uff0c\u201d\u6885\u83f2\u65af\u6e29\u67d4\u5730\u628a\u5934\u9760\u5728\u743c\u6069\u6000\u91cc\uff0c\u201c\u4f60\u4f1a\u4ec0\u4e48\u5462\uff0c\u743c\u6069\uff0c\u201d\u5979\u95ee\uff0c\u201c\u4f60\u53ef\u4ee5\u6559\u6211\u554a\u3002\u201d</p><p>\u743c\u6069\u8e0c\u8e87\u8d77\u6765\uff0c\u8fd9\u4e2a\u95ee\u9898\u8fd8\u771f\u96be\u4ee5\u56de\u7b54\u3002\u4ed6\u4f1a\u4ec0\u4e48\uff1f\u4ed6\u4f1a\u4e0b\u56f4\u68cb\uff0c\u7136\u800c\u8fd9\u4e16\u754c\u4e0a\u54ea\u6765\u7684\u56f4\u68cb\uff0c\u800c\u4e14\u8fd9\u4e5f\u548c\u6885\u83f2\u65af\u7684\u201c\u611f\u6027\u5316\u201d\u7684\u8981\u6c42\u4e0d\u5408\uff1b\u4ed6\u4f1a\u62c9\u4e8c\u80e1\uff0c\u7136\u800c\u8fd8\u662f\u4e0a\u8f88\u5b50\u7684\u4e8b\u60c5\uff0c\u8fd9\u4e48\u591a\u5e74\u4e5f\u65e9\u5c31\u751f\u758f\u4e86\uff0c\u800c\u4e14\u4ed6\u53ea\u4f1a\u62c9\uff0c\u4e0d\u77e5\u9053\u600e\u4e48\u81ea\u5df1\u52a8\u624b\u505a\u4e00\u4e2a\u51fa\u6765\uff0c\u5de5\u5e8f\u5e94\u8be5\u8fd8\u5f88\u590d\u6742\u3002\u4e66\u6cd5\uff1f\u522b\u5f00\u73a9\u7b11\u4e86\uff0c\u4ed6\u5199\u5f97\u5b57\u53ea\u80fd\u8ba9\u4eba\u8fa8\u8ba4\u6e05\u695a\uff0c\u4ec5\u6b64\u800c\u5df2\uff1b\u81f3\u4e8e\u7ed8\u753b\u5c31\u66f4\u4e0d\u7528\u8c08\uff0c\u5982\u679c\u7ed9\u9053\u58eb\u753b\u9a71\u9b3c\u7b26\u5012\u8fd8\u5dee\u4e0d\u591a\u3002</p><p>\u201c\u5443\uff0c\u90a3\u4e2a\uff0c\u4f60\u770b\uff0c\u201d\u743c\u6069\u644a\u5f00\u624b\uff0c\u201c\u5176\u5b9e\u6211\u6bd4\u4f60\u8fd8\u5dee\u52b2\uff0c\u6211\u4ec0\u4e48\u90fd\u4e0d\u4f1a\u3002\u201d</p><p>\u6885\u83f2\u65af\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u4f1a\u6b3a\u8d1f\u6211\u3002\u201d\u5979\u5728\u8033\u8fb9\u8f7b\u58f0\u8bf4\uff0c\u5410\u6c14\u5982\u5170\u3002</p><p>\u203b\u203b\u203b</p><p>\u65f6\u95f4\u4e00\u5929\u5929\u6d41\u901d\uff0c\u56e0\u4e3a\u662f\u5728\u5e7d\u6697\u5730\u57df\uff0c\u6ca1\u6709\u65e5\u5347\u6708\u843d\uff0c\u6240\u4ee5\u611f\u89c9\u90fd\u6709\u4e9b\u6df7\u6df7\u6c8c\u6c8c\u3002\u8475\u9732\u5230\u6765\u4e4b\u540e\uff0c\u53c8\u6709\u4e24\u6279\u4f0a\u8389\u4e1d\u7fe0\u4fe1\u5f92\u62b5\u8fbe\u6668\u70bc\uff0c\u8fd9\u5ea7\u8352\u5e9f\u7684\u53e4\u57ce\u6e10\u6e10\u4e5f\u6709\u4e86\u4e9b\u6c14\u8c61\u3002\u539f\u672c\u4e34\u65f6\u5f62\u6210\u7684\u4e09\u4e2a\u6d3e\u7cfb\u81ea\u7136\u4e5f\u4e0d\u590d\u5b58\u5728\uff0c\u201c\u6545\u571f\u201d\u548c\u201c\u65c5\u5c45\u201d\u7684\u9886\u5bfc\u6743\u7edf\u4e00\u90fd\u6536\u5f52\u8475\u9732\u624b\u4e2d\uff0c\u5979\u5730\u4f4d\u5c0a\u5d07\uff0c\u58f0\u671b\u53c8\u9ad8\uff0c\u4e45\u8d1f\u76db\u540d\u5782\u6570\u767e\u5e74\uff0c\u9886\u8896\u5730\u4f4d\u65e0\u4eba\u53ef\u4ee5\u52a8\u6447\u3002\u743c\u6069\u7b49\u4eba\u5360\u636e\u57ce\u5e02\u7684\u4e1c\u5317\u89d2\uff0c\u7b97\u662f\u81ea\u6210\u4e00\u56fd\uff0c\u751a\u5c11\u6765\u5f80\u3002\u6885\u83f2\u65af\u548c\u5fb7\u53e4\u62c9\u4ece\u4fd8\u864f\u4e2d\u6311\u9009\u51fa\u4f18\u79c0\u8005\u7f16\u5165\u519b\u961f\uff0c\u52a0\u4ee5\u8bad\u7ec3\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\uff0c\u624b\u4e2d\u6709\u4e00\u4e9b\u529b\u91cf\uff0c\u603b\u6bd4\u5355\u67aa\u5339\u9a6c\u5f3a\uff0c\u4e0d\u8bf4\u5efa\u529f\u7acb\u4e1a\uff0c\u6210\u5c31\u4ec0\u4e48\uff0c\u54ea\u6015\u662f\u4ee5\u540e\u9690\u5c45\u5e84\u56ed\uff0c\u603b\u4e5f\u8981\u62a4\u536b\u6253\u624b\u7684\u3002</p><p>\u76f8\u5bf9\u6765\u8bf4\uff0c\u743c\u6069\u5c31\u60a0\u95f2\u591a\u4e86\uff0c\u9605\u8bfb\u8d44\u6599\uff0c\u7814\u4e60\u9b54\u6cd5\uff0c\u8fd8\u62bd\u7a7a\u56de\u4e86\u4f0a\u5361\u6c99\u57ce\u4e00\u8d9f\uff0c\u548c\u77ee\u4eba\u53d9\u53d9\u65e7\uff0c\u4e3a\u81ea\u5df1\u7684\u624b\u4e0b\u5f04\u70b9\u63f4\u52a9\u8fc7\u6765\uff0c\u540c\u65f6\u4e5f\u5173\u5fc3\u4e00\u4e0b\u9b54\u50cf\u57fa\u5730\u7684\u5efa\u8bbe\u60c5\u51b5\u2014\u2014\u5f53\u7136\u8fd9\u4e9b\u5176\u5b9e\u90fd\u53ea\u662f\u987a\u5e26\uff0c\u4e0d\u662f\u771f\u6b63\u7684\u76ee\u7684\u3002</p><p>\u771f\u6b63\u7684\u76ee\u7684\u662f\u8299\u857e\u72c4\u3002</p><p>\u743c\u6069\u53bb\u6668\u70bc\u57ce\uff0c\u539f\u672c\u5e76\u65e0\u957f\u4e45\u5c45\u4f4f\u7684\u6253\u7b97\uff0c\u5728\u4ed6\u7684\u5f53\u521d\u8bbe\u60f3\u4e2d\uff0c\u4e0d\u8fc7\u662f\u5e2e\u4e2a\u5fd9\uff0c\u4e8b\u6bd5\u4f9d\u7136\u56de\u4f0a\u5361\u6c99\u57ce\uff0c\u6240\u4ee5\u4e5f\u6ca1\u6709\u628a\u83ab\u5c3c\u5361\u59d0\u59b9\u5e26\u4e0a\u3002\u53ea\u662f\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u4ece\u6765\u662f\u8ba1\u5212\u8d76\u4e0d\u4e0a\u53d8\u5316\uff0c\u5f80\u5f80\u8d70\u7740\u8d70\u7740\u5c31\u504f\u79bb\u4e86\u9884\u671f\uff0c\u7ed3\u679c\u4e00\u76f4\u5728\u6668\u70bc\u4f4f\u4e86\u8fd9\u4e48\u4e45\uff0c\u5012\u662f\u628a\u8299\u857e\u72c4\u51b7\u843d\u4e86\u3002</p><p>\u201c\u6ca1\u5173\u7cfb\u554a\uff0c\u201d\u8299\u857e\u72c4\u8f7b\u8f7b\u8bf4\uff0c\u201c\u6211\u5728\u8fd9\u91cc\u5f88\u597d\u5462\u3002\u201d</p><p>\u8bf4\u8fd9\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u5979\u6b63\u8d64\u88f8\u7740\u88ab\u743c\u6069\u62b1\u5728\u6000\u91cc\uff0c\u624b\u81c2\u7d27\u7d27\u6402\u7740\u7537\u4eba\u7684\u8116\u9888\uff0c\u4e00\u53cc\u7389\u817f\u65e0\u529b\u5730\u6263\u5728\u5bf9\u65b9\u8170\u95f4\u3002\u4e24\u4eba\u7684\u4e0b\u4f53\u7d27\u5bc6\u7ed3\u5408\u5728\u4e00\u8d77\uff0c\u743c\u6069\u7ad9\u7acb\u7740\uff0c\u624b\u81c2\u7a7f\u8fc7\u5979\u7684\u817f\u5f2f\uff0c\u53cc\u624b\u6258\u7740\u5c11\u5973\u7684\u81c0\u90e8\uff0c\u638c\u63e1\u7740\u8282\u594f\u548c\u6df1\u6d45\u3002\u8fd9\u79cd\u59ff\u52bf\u5bf9\u7537\u6027\u8981\u6c42\u5f88\u9ad8\uff0c\u5018\u82e5\u4f53\u529b\u4e0d\u8db3\uff0c\u505a\u5230\u4e00\u534a\u652f\u6301\u4e0d\u4f4f\uff0c\u90a3\u8fb9\u5927\u715e\u98ce\u666f\uff1b\u6240\u5e78\u8299\u857e\u72c4\u662f\u5c5e\u4e8e\u5a07\u5c0f\u7c7b\u578b\u7684\u5973\u5b69\u5b50\uff0c\u4f53\u91cd\u5f88\u8f7b\uff0c\u5e76\u65e0\u6b64\u865e\u3002</p><p>\u201c\u4f60\u6700\u8fd1\u5728\u505a\u4ec0\u4e48\u5462\u3002\u201d\u743c\u6069\u95ee\uff0c\u4e00\u8fb9\u91cd\u91cd\u633a\u4e86\u633a\u8170\u3002</p><p>\u8299\u857e\u72c4\u54ac\u7d27\u5634\u5507\uff0c\u7ec8\u7a76\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u4ece\u54bd\u5589\u4e2d\u6324\u51fa\u4e00\u4e1d\u4f4e\u541f\uff0c\u201c\u4e3b\u8981\u662f\u5e2e\u4ed6\u4eec\u63a2\u77ff\uff0c\u201d\u5979\u5598\u606f\u7740\u8bf4\uff0c\u201c\u90a3\u4efd\u6df1\u571f\u9884\u8a00\u7684\u7b14\u8bb0\uff0c\u6211\u5df2\u7ecf\u57fa\u672c\u9605\u8bfb\u660e\u767d\uff0c\u6700\u8fd1\u548c\u5ca9\u77f3\u4ea4\u6d41\u7684\u80fd\u529b\u4e5f\u63d0\u9ad8\u4e0d\u5c11\u2026\u2026\u80fd\u591f\u611f\u5e94\u5230\u4e00\u4e9b\u77ff\u77f3\u7684\u5b58\u5728\u2026\u2026\u201d</p><p>\u201c\u4e0d\u9519\uff0c\u201d\u743c\u6069\u79f0\u8d5e\u7740\uff0c\u5728\u5979\u5507\u4e0a\u8f7b\u8f7b\u4eb2\u4e86\u4e00\u53e3\uff0c\u201c\u8fdb\u6b65\u5f88\u5feb\u5462\u3002\u201d</p><p>\u201c\u56fe\u4e66\u9986\u91cc\u6709\u5f88\u591a\u8d44\u6599\uff0c\u800c\u4e14\u4ed6\u4eec\u4f20\u6388\u4e86\u6211\u5f88\u591a\u7ecf\u9a8c\uff0c\u5927\u90e8\u4efd\u90fd\u53ef\u4ee5\u548c\u9b54\u6cd5\u76f8\u5370\u8bc1\u7684\uff0c\u6240\u4ee5\u8fdb\u6b65\u5c31\u6bd4\u8f83\u5feb\u4e86\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u7a81\u7136\u60f3\u8d77\u4e00\u4ef6\u4e8b\u60c5\u6765\uff1a\u4ed6\u4ece\u4e9a\u5f53\u65af\u90a3\u91cc\u5f04\u5230\u4e86\u4e00\u4efd\u9b54\u6cd5\u8d44\u6599\uff0c\u662f\u5173\u4e8e\u5982\u4f55\u514b\u670d\u5730\u8109\u8f90\u5c04\u7684\u5f71\u54cd\uff0c\u5728\u5e7d\u6697\u5730\u57df\u8fdb\u884c\u65e0\u9519\u4f20\u9001\u7684\u3002\u5f53\u65f6\u7c97\u7565\u7ffb\u4e86\u7ffb\uff0c\u53d1\u73b0\u6d89\u53ca\u5230\u5f88\u591a\u6709\u5173\u201c\u5ca9\u77f3\u201d\u3001\u201c\u571f\u5c42\u201d\u3001\u201c\u77ff\u8109\u5f71\u54cd\u201d\u4e4b\u7c7b\u7684\u4e1c\u897f\uff0c\u9887\u4e3a\u5934\u5927\uff0c\u76f4\u63a5\u4e22\u8fdb\u8d44\u6599\u5806\u91cc\u3002\u4e8b\u540e\u81ea\u5df1\u4e00\u76f4\u5fd9\u7740\u5e72\u522b\u7684\uff0c\u5012\u5dee\u70b9\u628a\u5b83\u7ed9\u5fd8\u4e86\u3002</p><p>\u6682\u65f6\u4e0d\u53bb\u60f3\u8fd9\u4e2a\uff0c\u5148\u628a\u6000\u91cc\u7684\u5c0f\u4e2b\u5934\u5582\u9971\u518d\u8bf4\u3002</p><p>\u8299\u857e\u72c4\u4f53\u8d28\u4e0d\u7b97\u5f3a\u5065\uff0c\u5979\u7684\u8eab\u4f53\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\u53c8\u6beb\u65e0\u534a\u70b9\u79d8\u5bc6\u53ef\u8a00\uff0c\u51e0\u4e4e\u662f\u4e86\u5982\u6307\u638c\uff0c\u51e0\u6b21\u5f81\u631e\u4fbf\u6709\u4e9b\u627f\u53d7\u4e0d\u4f4f\uff0c\u53ea\u89c9\u5168\u8eab\u9178\u8f6f\u65e0\u529b\uff0c\u610f\u8bc6\u90fd\u6e10\u6e10\u6709\u4e9b\u6726\u80e7\u8d77\u6765\uff0c\u8ff7\u8ff7\u7cca\u7cca\u95f4\u4f3c\u4e4e\u81ea\u5df1\u53c8\u88ab\u653e\u5230\u4e86\u5e8a\u4e0a\uff0c\u53cc\u817f\u5c48\u8d77\uff0c\u6446\u51fa\u4e00\u4e2a\u7f9e\u4eba\u7684\u59ff\u52bf\uff0c\u63a5\u7740\u4fbf\u662f\u6eda\u70ed\u7c97\u58ee\u7684\u4e1c\u897f\u957f\u9a71\u76f4\u5165\uff0c\u6beb\u4e0d\u601c\u60dc\u5730\u6a2a\u51b2\u731b\u649e\uff0c\u51e0\u4e4e\u5c06\u5a07\u5ae9\u7684\u8eab\u4f53\u5b8c\u5168\u8d2f\u7a7f\uff0c\u4e00\u80a1\u80a1\u70ed\u6d41\u81ea\u4f53\u5185\u4ea4\u5408\u5904\u6d8c\u8d77\uff0c\u6d41\u904d\u5168\u8eab\uff0c\u70eb\u7684\u5979\u6574\u4e2a\u4eba\u4eff\u4f5b\u90fd\u8981\u878d\u5316\u4e86\u4f3c\u7684\u3002</p><p>\u5979\u751a\u81f3\u4e0d\u77e5\u9053\u81ea\u5df1\u4f55\u65f6\u6655\u7729\u8fc7\u53bb\uff0c\u53ea\u77e5\u9053\u9192\u6765\u7684\u65f6\u5019\u6b63\u8737\u5728\u743c\u6069\u6000\u4e2d\uff0c\u5979\u7684\u8138\u8d34\u7740\u4ed6\u7684\u80f8\u819b\uff0c\u611f\u53d7\u7740\u719f\u6089\u7684\u4f53\u6e29\uff0c\u95fb\u7740\u719f\u6089\u7684\u7537\u6027\u5473\u9053\uff0c\u542c\u7740\u4ed6\u5e73\u7f13\u7684\u547c\u5438\u58f0\uff0c\u5fc3\u4e2d\u4fbf\u5b89\u5b89\u5b9a\u5b9a\u7684\uff0c\u518d\u6ca1\u5176\u4ed6\u5ff5\u5934\u3002</p><p>\u5341\u4e94\u5c81\u7684\u5e74\u7eaa\uff0c\u8981\u8bf4\u7231\u60c5\uff0c\u5927\u7ea6\u4e5f\u5e76\u975e\u662f\u90a3\u4e48\u6e05\u695a\u7684\u3002\u5929\u957f\u5730\u4e45\uff0c\u6d77\u67af\u77f3\u70c2\uff0c\u523b\u9aa8\u94ed\u5fc3\uff0c\u537f\u537f\u6211\u6211\u2014\u2014\u8fd9\u4e9b\u6216\u8bb8\u90fd\u7b97\u662f\u7f62\uff0c\u4f46\u5bf9\u4e8e\u5979\u6765\u8bf4\uff0c\u6240\u6709\u7684\u8fd9\u4e00\u5207\uff0c\u5176\u5b9e\u90fd\u62b5\u4e0d\u8fc7\u6b64\u523b\u7684\u4e00\u70b9\u6e29\u6696\u548c\u5b89\u5fc3\u3002</p><p>\u53ea\u8981\u5728\u4ed6\u6000\u91cc\uff0c\u4fbf\u662f\u5b89\u5fc3\u7684\uff1b\u53ea\u8981\u77e5\u9053\u8fd9\u70b9\uff0c\u90a3\u4fbf\u8db3\u591f\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u539f\u672c\u60f3\u628a\u8299\u857e\u72c4\u4e5f\u5e26\u5230\u6668\u70bc\u53bb\uff0c\u5374\u88ab\u62d2\u7edd\u4e86\uff0c\u5c0f\u5973\u5b69\u8bf4\u8fd9\u8fb9\u6b63\u5728\u8fdb\u884c\u7684\u4e00\u6b21\u63a2\u77ff\u5230\u4e86\u5173\u952e\u65f6\u523b\uff0c\u6682\u65f6\u4e0d\u80fd\u8131\u8eab\u3002\u743c\u6069\u8f6c\u5ff5\u60f3\u4e86\u60f3\uff0c\u5982\u4eca\u81ea\u5df1\u548c\u8475\u9732\u5173\u7cfb\u4e0d\u6d3d\uff0c\u53c8\u4f3c\u4e4e\u6709\u88ab\u74dc\u7406\u5fb7\u65af\u57ce\u53d1\u73b0\u7684\u8ff9\u8c61\uff0c\u53ea\u6015\u5728\u6668\u70bc\u4e5f\u672a\u5fc5\u8fd8\u4f1a\u518d\u4f4f\u591a\u4e45\uff0c\u8bf4\u4e0d\u5b9a\u8fc7\u6bb5\u65f6\u65e5\u4fbf\u8981\u56de\u6765\uff0c\u5f53\u4e0b\u4e5f\u5c31\u7f62\u4e86\u3002\u5728\u79bb\u5f00\u7684\u65f6\u5019\uff0c\u5c06\u90a3\u4efd\u5e7d\u6697\u5730\u57df\u65e0\u9519\u4f20\u9001\u7684\u9b54\u6cd5\u8d44\u6599\u7ed9\u4e86\u8299\u857e\u72c4\uff0c\u8ba9\u5979\u6709\u7a7a\u770b\u770b\uff0c\u5018\u82e5\u80fd\u7422\u78e8\u51fa\u4ec0\u4e48\uff0c\u4e5f\u662f\u4e00\u6869\u597d\u4e8b\u3002</p><p>\u56de\u5230\u6668\u70bc\uff0c\u53d1\u73b0\u57ce\u4e2d\u5fd9\u5fd9\u4e71\u4e71\uff0c\u4e00\u95ee\u624d\u77e5\u9053\u539f\u6765\u662f\u53c8\u6709\u5546\u961f\u4ece\u74dc\u7406\u5fb7\u65af\u51fa\u53d1\uff0c\u4f9d\u65e7\u662f\u524d\u5f80\u827e\u745e\u5fb7\u6797\u3002\u8475\u9732\u6536\u5230\u60c5\u62a5\uff0c\u4e8e\u662f\u518d\u5ea6\u6d3e\u5175\u53bb\u6253\u52ab\uff0c\u867d\u7136\u987a\u5229\u5f97\u624b\uff0c\u4f46\u5728\u56de\u57ce\u7684\u8def\u4e0a\u5374\u649e\u4e0a\u4e86\u4e00\u7fa4\u86f0\u4f0f\u602a\uff0c\u6298\u635f\u4e86\u4e0d\u5c11\u4eba\u624b\u3002</p><p>\u201c\u5e78\u597d\u6709\u8475\u9732\u5728\uff0c\u5426\u5219\u7684\u8bdd\u81f3\u5c11\u4e00\u534a\u7684\u4eba\u4f1a\u4f24\u91cd\u800c\u6b7b\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u201c\u5514\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u5979\u4eec\u6062\u590d\u795e\u672f\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u4f46\u8475\u9732\u62e5\u6709\u94f6\u706b\uff0c\u662f\u53ef\u4ee5\u7528\u4e8e\u6cbb\u7597\u7684\u554a\u3002\u201d</p><p>\u201c\u54e6\uff0c\u6211\u5fd8\u4e86\u5979\u540c\u65f6\u8fd8\u662f\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\u3002\u201d</p><p>\u743c\u6069\u7684\u624b\u4e0b\u6b64\u6b21\u6ca1\u6709\u53c2\u4e0e\u6253\u52ab\uff0c\u81ea\u7136\u4e5f\u6ca1\u6709\u635f\u4f24\uff0c\u6240\u4ee5\u4ed6\u5c3d\u53ef\u4ee5\u4f5c\u58c1\u4e0a\u89c2\u3002\u53ea\u4e0d\u8fc7\u5927\u5bb6\u6bd5\u7adf\u540c\u5728\u4e00\u5ea7\u57ce\u91cc\uff0c\u867d\u7136\u5f7c\u6b64\u90fd\u6ca1\u4ec0\u4e48\u597d\u611f\uff0c\u603b\u662f\u96be\u514d\u8981\u6253\u4ea4\u9053\u7684\u3002\u8fd9\u4e00\u5929\uff0c\u8475\u9732\u524d\u6765\u62dc\u8bbf\u743c\u6069\u3002</p><p>\u5979\u6765\u7684\u65f6\u5019\uff0c\u743c\u6069\u6b63\u5728\u7422\u78e8\u90a3\u4e2a\u83f2\u5c14\u4f26\u62a4\u7b26\uff0c\u7ecf\u8fc7\u8fd9\u4e9b\u5929\u6765\u7684\u7814\u7a76\uff0c\u5df2\u7ecf\u6709\u4e9b\u6210\u679c\u4e86\u3002\u9664\u4e86\u4f5c\u4e3a\u706d\u706b\u5668\u4e4b\u5916\uff0c\u5b83\u8fd8\u62e5\u6709\u201c\u901a\u6653\u8bed\u8a00\u201d\u7684\u80fd\u529b\uff0c\u53ea\u8981\u628a\u62a4\u7b26\u4f69\u6234\u5728\u8eab\u4e0a\u6216\u8005\u63e1\u5728\u624b\u4e2d\uff0c\u4e00\u4e2a\u6ca1\u6709\u7ecf\u8fc7\u4efb\u4f55\u6559\u80b2\u7684\u4eba\u90fd\u80fd\u542c\u61c2\u8fd9\u4e16\u754c\u4e0a\u7edd\u5927\u591a\u6570\u8bed\u8a00\u2014\u2014\u5f53\u7136\uff0c\u53ea\u80fd\u542c\u61c2\u8bfb\u61c2\uff0c\u4e0d\u4f1a\u8bf4\u4e5f\u4e0d\u4f1a\u5199\u3002\u6b64\u5916\u5b83\u8fd8\u80fd\u591f\u521b\u9020\u5c0f\u8303\u56f4\u7684\u9b54\u6cd5\u62a4\u7f69\uff0c\u6682\u65f6\u906e\u853d\u7269\u54c1\uff0c\u963b\u9694\u9b54\u6cd5\u7684\u63a2\u6d4b\u3002\u8fd9\u4e9b\u90fd\u7b97\u4e0d\u4e0a\u7279\u522b\u5f3a\u5927\u7684\u80fd\u529b\uff0c\u4f46\u5f88\u6709\u8da3\uff0c\u800c\u4e14\u743c\u6069\u8ba4\u4e3a\u5b83\u8fd8\u6709\u66f4\u591a\u7684\u6f5c\u529b\u6ca1\u6709\u88ab\u53d1\u6398\u51fa\u6765\u3002</p><p>\u542c\u8bf4\u8475\u9732\u5230\u6765\uff0c\u743c\u6069\u968f\u624b\u628a\u62a4\u7b26\u653e\u5728\u684c\u4e0a\uff0c\u548c\u6885\u83f2\u65af\u4e00\u8d77\u8d77\u8eab\u8fce\u63a5\u3002\u968f\u540c\u8475\u9732\u800c\u6765\u7684\u8fd8\u6709\u963f\u5fd2\u59ae\uff0c\u663e\u7136\u662f\u56e0\u4e3a\u5979\u548c\u6885\u83f2\u65af\u4ea4\u597d\uff0c\u53ef\u4ee5\u4f5c\u4e3a\u7f13\u548c\u6c14\u6c1b\u6240\u7528\u3002\u7b80\u5355\u7684\u5bd2\u6684\u4e4b\u540e\uff0c\u8475\u9732\u9053\u660e\u6765\u610f\uff0c\u539f\u6765\u5979\u4eec\u6253\u7b97\u5728\u6668\u70bc\u57ce\u4e2d\u5efa\u4e00\u5ea7\u6b63\u5f0f\u7684\u4f0a\u8389\u4e1d\u7fe0\u795e\u6bbf\uff0c\u4f5c\u4e3a\u4fe1\u5f92\u671d\u62dc\u96c6\u4f1a\u4e4b\u6240\u3002\u8fd9\u539f\u672c\u4e5f\u662f\u610f\u6599\u4e4b\u4e2d\u7684\u4e8b\u60c5\uff0c\u65e2\u7136\u4f0a\u8389\u4e1d\u7fe0\u6559\u4f1a\u5df2\u7ecf\u628a\u6668\u70bc\u57ce\u5f53\u4f5c\u5927\u672c\u8425\uff0c\u5efa\u7acb\u795e\u6bbf\u5c31\u662f\u9898\u4e2d\u5e94\u6709\u4e4b\u4e49\u3002\u4f46\u8fd9\u79cd\u5927\u5174\u571f\u6728\u7684\u5efa\u7b51\u5de5\u7a0b\uff0c\u5353\u5c14\u4eec\u5e76\u4e0d\u662f\u5341\u5206\u64c5\u957f\uff0c\u5982\u679c\u80fd\u591f\u6709\u77ee\u4eba\u7684\u76f8\u52a9\uff0c\u90a3\u4fbf\u4e8b\u534a\u529f\u500d\u4e86\u3002</p><p>\u524d\u65e5\u4f0a\u5361\u6c99\u57ce\u652f\u63f4\u4e86\u51e0\u4e2a\u77ee\u4eba\u5de5\u5320\uff0c\u5e2e\u52a9\u6668\u70bc\u4fee\u7f2e\u57ce\u9632\uff0c\u5982\u4eca\u8fd8\u7559\u5728\u57ce\u4e2d\uff0c\u96b6\u5c5e\u743c\u6069\u6240\u90e8\u3002\u8475\u9732\u6b64\u6765\uff0c\u610f\u601d\u5c31\u662f\u5e0c\u671b\u80fd\u501f\u8fd9\u51e0\u4e2a\u5de5\u5320\uff0c\u5f53\u7136\u5982\u679c\u77ee\u4eba\u80fd\u591f\u63d0\u4f9b\u66f4\u591a\u7684\u5e2e\u52a9\uff0c\u5979\u4e5f\u4e0d\u4f1a\u62d2\u7edd\u4fbf\u662f\u3002</p><p>\u4e9b\u8bb8\u5c0f\u4e8b\uff0c\u743c\u6069\u4e5f\u4e0d\u60f3\u6545\u610f\u5201\u96be\uff0c\u5f53\u4e0b\u4fbf\u7b54\u5e94\u4e86\u3002\u4e8b\u60c5\u8c08\u6bd5\uff0c\u8475\u9732\u6b63\u8981\u544a\u8f9e\uff0c\u7a81\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9762\u8272\u5fae\u53d8\uff0c\u731b\u7136\u7ad9\u8d77\u8eab\u6765\uff0c\u76ef\u7740\u4fa7\u9762\u4e00\u5904\u3002\u5176\u4ed6\u4eba\u4e0d\u77e5\u4f55\u6545\uff0c\u7eb7\u7eb7\u987a\u7740\u5979\u7684\u773c\u5149\u65b9\u5411\u770b\u53bb\uff0c\u5374\u89c1\u662f\u4f0a\u8389\u96c5\u4e0d\u77e5\u4f55\u65f6\u722c\u5230\u4e86\u77f3\u684c\u4e0a\uff0c\u6b63\u5728\u628a\u73a9\u743c\u6069\u521a\u624d\u968f\u624b\u4e22\u4e0b\u7684\u83f2\u5c14\u677e\u62a4\u7b26\u3002</p><p>\u8fd9\u6bb5\u65f6\u95f4\u4ee5\u6765\uff0c\u4f0a\u8389\u96c5\u5df2\u7ecf\u6210\u4e86\u6668\u70bc\u57ce\u4e2d\u8bf8\u591a\u4eba\u773c\u4e2d\u7684\u5c0f\u6076\u9b54\uff0c\u5979\u5e74\u9f84\u5e7c\u5c0f\uff0c\u504f\u53c8\u6ee1\u8111\u5b50\u7684\u673a\u7075\u8be1\u8ba1\uff0c\u603b\u80fd\u6709\u5c42\u51fa\u4e0d\u7a77\u7684\u9b3c\u70b9\u5b50\uff0c\u7ecf\u5e38\u628a\u8eab\u8fb9\u4eba\u6349\u5f04\u5f97\u9e21\u98de\u72d7\u8df3\uff0c\u53c8\u4e0d\u597d\u8ddf\u5979\u4e00\u4e2a\u5c0f\u5b69\u5b50\u8ba1\u8f83\uff0c\u53ea\u80fd\u656c\u800c\u8fdc\u4e4b\u3002\u963f\u5fd2\u59ae\u539f\u672c\u66ff\u5979\u5b89\u6392\u4e86\u4e00\u4f4d\u4f0a\u8389\u4e1d\u7fe0\u7267\u5e08\uff0c\u636e\u8bf4\u662f\u96be\u5f97\u7684\u597d\u813e\u6c14\uff0c\u4f46\u4e5f\u5728\u575a\u6301\u4e86\u4e00\u661f\u671f\u4e4b\u540e\u4e3b\u52a8\u8f9e\u804c\uff0c\u58f0\u79f0\u5426\u5219\u5c31\u6709\u751f\u547d\u5371\u9669\u4e86\u3002\u65e0\u5948\u4e4b\u4e0b\uff0c\u53ea\u597d\u4f9d\u7167\u743c\u6069\u7684\u539f\u8bae\uff0c\u628a\u5979\u4ea4\u7ed9\u5fb7\u53e4\u62c9\u76d1\u62a4\u3002\u5fb7\u53e4\u62c9\u53d7\u5230\u7684\u5f85\u9047\u5e76\u4e0d\u6bd4\u522b\u4eba\u6709\u6240\u6539\u5584\uff0c\u4f46\u5e78\u597d\u4ed6\u662f\u4e2a\u5438\u8840\u9b3c\uff0c\u8010\u6253\u8010\u6298\u817e\uff0c\u53c8\u628a\u4f0a\u8389\u96c5\u5f53\u59b9\u59b9\u5ba0\u6eba\u7740\uff0c\u5012\u4e5f\u76f8\u5b89\u65e0\u4e8b\u3002</p><p>\u81f3\u4e8e\u743c\u6069\uff0c\u5012\u6ca1\u600e\u4e48\u53d7\u5230\u9a9a\u6270\uff0c\u4e3b\u8981\u56e0\u4e3a\u4ed6\u57fa\u672c\u90fd\u548c\u6885\u83f2\u65af\u5728\u4e00\u8d77\u2014\u2014\u800c\u6885\u83f2\u65af\u662f\u552f\u4e00\u80fd\u591f\u538b\u5236\u5f97\u4f4f\u4f0a\u8389\u96c5\u7684\u4eba\u3002\u4e0d\u7ba1\u518d\u600e\u4e48\u987d\u76ae\uff0c\u53ea\u8981\u4e00\u5230\u6885\u83f2\u65af\u9762\u524d\uff0c\u539f\u672c\u7684\u5c0f\u6076\u9b54\u7acb\u523b\u5c31\u53d8\u6210\u4e86\u4e56\u4e56\u6dd1\u5973\uff0c\u6216\u8bb8\u8fd9\u5c31\u662f\u6240\u8c13\u7684\u5929\u6027\u76f8\u514b\u3002</p><p>\u521a\u624d\u743c\u6069\u548c\u8475\u9732\u7b49\u4eba\u6b63\u5728\u8c08\u4e8b\u60c5\uff0c\u5c45\u7136\u8c01\u90fd\u4e0d\u66fe\u6ce8\u610f\u4f0a\u8389\u96c5\u4f55\u65f6\u6e9c\u4e86\u8fdb\u6765\uff0c\u8fd8\u722c\u4e0a\u4e86\u77f3\u684c\u3002\u53ea\u662f\u8fd9\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5c0f\u5b69\u5b50\u559c\u6b22\u73a9\uff0c\u539f\u672c\u4fbf\u662f\u5929\u6027\uff0c\u4e0d\u7ba1\u4eba\u7c7b\u8fd8\u662f\u5353\u5c14\u90fd\u4e00\u6837\uff0c\u8475\u9732\u4f55\u5fc5\u5982\u6b64\u60ca\u8bb6\u3002</p><p>\u6b63\u4e0d\u89e3\u95f4\uff0c\u4e00\u9053\u7d2b\u5149\u5728\u623f\u95f4\u91cc\u4eae\u8d77\uff0c\u7ec6\u770b\u5374\u662f\u4f0a\u8389\u96c5\u624b\u4e2d\u7684\u62a4\u7b26\u5728\u53d1\u5149\u3002\u743c\u6069\u6b64\u65f6\u624d\u5403\u4e86\u4e00\u60ca\uff0c\u4f5c\u4e3a\u5deb\u5e08\u4ed6\u5f53\u7136\u660e\u767d\u8fd9\u610f\u5473\u7740\u4ec0\u4e48\u3002\u201c\u5feb\u653e\u4e0b\uff01\u201d\u4ed6\u5389\u58f0\u8bf4\uff0c\u201c\u653e\u4e0b\u5b83\uff0c\u4f0a\u8389\u96c5\uff01\u201d</p><p>\u4f0a\u8389\u96c5\u51b2\u4ed6\u5fae\u5fae\u7b11\u4e86\u4e00\u7b11\u3002</p><p>\u4e00\u5239\u90a3\u95f4\uff0c\u5979\u50cf\u662f\u5b8c\u5168\u53d8\u4e86\u4e2a\u4eba\u4f3c\u7684\uff0c\u8138\u5e9e\u4e0a\u539f\u672c\u7684\u7a1a\u6c14\u4e00\u626b\u800c\u7a7a\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u548c\u5e74\u9f84\u4e0d\u76f8\u7b26\u7684\u6210\u719f\u548c\u6c89\u7a33\uff0c\u4e94\u5b98\u4f9d\u65e7\u7cbe\u81f4\uff0c\u4f46\u5374\u5c11\u4e86\u539f\u672c\u7684\u7eaf\u51c0\uff0c\u591a\u4e86\u51e0\u5206\u7f8e\u8273\u548c\u5a9a\u60d1\u3002\u552f\u4e00\u6ca1\u6709\u6539\u53d8\u7684\u4f3c\u4e4e\u662f\u53cc\u773c\uff0c\u4f9d\u65e7\u8fd8\u662f\u7d2b\u8272\u2014\u2014\u4f46\u4e5f\u53d8\u5f97\u66f4\u52a0\u660e\u4eae\u8d77\u6765\uff0c\u4eff\u4f5b\u661f\u8fb0\u4e00\u822c\u95ea\u95ea\u70c1\u70c1\uff0c\u7480\u74a8\u751f\u8f89\uff0c\u4ee4\u4eba\u4e0d\u6562\u903c\u89c6\u3002</p><p>\u201c\u8718\u86db\uff01\u201d</p><p>\u4f34\u968f\u7740\u8475\u9732\u7684\u6012\u559d\uff0c\u5e9e\u5927\u7684\u865a\u50cf\u81ea\u4f0a\u8389\u96c5\u8eab\u540e\u6d6e\u73b0\uff0c\u90a3\u662f\u4e00\u4f4d\u7f8e\u8273\u7684\u5353\u5c14\u5973\u5b50\uff0c\u8eab\u6750\u4fee\u957f\u800c\u73b2\u73d1\u6d6e\u51f8\uff0c\u88ab\u900f\u660e\u86db\u4e1d\u7f16\u7ec7\u6210\u7684\u957f\u888d\u548c\u6597\u7bf7\u5305\u88f9\u7740\uff0c\u5927\u90e8\u4efd\u533a\u57df\u90fd\u906e\u63a9\u4e0d\u4f4f\u3002\u5979\u4e30\u6ee1\u7684\u5507\u89d2\u5fae\u5fae\u5f80\u540e\u62c9\u626f\uff0c\u9732\u51fa\u6de1\u6de1\u7684\u7b11\u610f\uff0c\u7d2b\u8272\u7684\u53cc\u7738\u4e2d\u5374\u6f20\u7136\u51b0\u51b7\u3002\u4e00\u67c4\u78a7\u7eff\u8272\u7684\u5315\u9996\u5728\u5979\u624b\u4e2d\u628a\u73a9\u7740\uff0c\u5ffd\u800c\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u65e0\u9700\u518d\u591a\u505a\u89e3\u91ca\uff0c\u6240\u6709\u4eba\u90fd\u5728\u4e00\u5239\u90a3\u95f4\u660e\u767d\u4e86\u8fd9\u4e2a\u5353\u5c14\u5973\u5b50\u7684\u8eab\u4efd\uff1a\u86db\u540e\u7f57\u4e1d\u3002</p><p>\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u743c\u6069\u548c\u8475\u9732\u540c\u65f6\u91ca\u653e\u4e86\u81ea\u5df1\u638c\u63e1\u7684\u6700\u5f3a\u5927\u7684\u6cd5\u672f\uff0c\u6885\u83f2\u65af\u5df2\u7ecf\u8dc3\u8eab\u51b2\u4e0a\uff0c\u963f\u5fd2\u59ae\u6284\u8d77\u539f\u672c\u653e\u5728\u4e00\u65c1\u7684\u77ed\u5f13\uff0c\u4e00\u652f\u94f6\u8272\u957f\u7bad\u5df2\u7ecf\u642d\u4e0a\u5f13\u5f26\u3002\u4f46\u6240\u6709\u7684\u653b\u51fb\u90fd\u88ab\u4e00\u5c42\u65e0\u5f62\u7684\u5c4f\u969c\u6321\u4e86\u4e0b\u6765\uff0c\u4f0a\u8389\u96c5\u4f9d\u65e7\u7d27\u7d27\u5730\u63e1\u7740\u62a4\u7b26\uff0c\u7d2b\u5149\u8d8a\u6765\u8d8a\u660e\u4eae\uff0c\u8d8a\u6765\u8d8a\u76db\uff0c\u5df2\u7ecf\u5c06\u6574\u4e2a\u623f\u95f4\u90fd\u7167\u5f97\u4eff\u4f5b\u767d\u663c\u3002</p><p>\u6e05\u8106\u7684\u7834\u88c2\u58f0\u54cd\u8d77\uff0c\u62a4\u7b26\u5316\u4f5c\u4e86\u65e0\u6570\u788e\u672b\uff0c\u6c79\u6d8c\u6f8e\u6e43\u7684\u7d2b\u5149\u5728\u623f\u95f4\u91cc\u75be\u901f\u51b2\u649e\uff0c\u5316\u4f5c\u4e00\u4e2a\u5e9e\u5927\u7684\u6f29\u6da1\uff0c\u4e2d\u5fc3\u662f\u6df1\u4e0d\u89c1\u5e95\u7684\u9ed1\u6d1e\uff0c\u8981\u5c06\u5728\u573a\u7684\u6240\u6709\u4eba\u90fd\u62c9\u626f\u8fdb\u53bb\u3002\u201c\u6b22\u8fce\u6765\u5230\u8718\u86db\u9b54\u7f51\u3002\u201d\u4f0a\u8389\u96c5\u8f7b\u58f0\u8bf4\uff0c\u5979\u7684\u58f0\u97f3\u4e5f\u5b8c\u5168\u53d8\u4e86\uff0c\u518d\u4e0d\u662f\u5e73\u65f6\u90a3\u79cd\u6e05\u8106\uff0c\u800c\u662f\u67d4\u8f6f\u5a07\u5a9a\uff0c\u4eff\u4f5b\u80fd\u52fe\u4eba\u9b42\u9b44\u4e00\u822c\u3002</p><p>\u5979\u8f7b\u58f0\u7b11\u7740\uff0c\u5728\u6f29\u6da1\u7684\u9ed1\u6d1e\u4e2d\u5fc3\u534a\u9690\u534a\u73b0\uff0c\u82e5\u6709\u82e5\u65e0\u3002\u6f29\u6da1\u8d8a\u6765\u8d8a\u6025\uff0c\u529b\u91cf\u8d8a\u6765\u8d8a\u5927\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u53cc\u624b\u63e1\u5728\u4e00\u8d77\uff0c\u52c9\u5f3a\u7a33\u4f4f\u8eab\u5f62\uff0c\u4f46\u611f\u89c9\u5373\u5c06\u652f\u6491\u4e0d\u4f4f\u3002\u963f\u5fd2\u59ae\u5df2\u7ecf\u8981\u88ab\u5377\u5165\u4e2d\u5fc3\uff0c\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u88ab\u8475\u9732\u626f\u4e86\u56de\u6765\u3002</p><p>\u4e24\u9053\u8000\u773c\u5149\u8292\u4ece\u8475\u9732\u4f53\u5185\u6d8c\u51fa\uff0c\u4e00\u9053\u662f\u84dd\u767d\u76f8\u95f4\u7684\u95ea\u7535\u957f\u675f\uff0c\u4e00\u9053\u662f\u4e0a\u6b21\u743c\u6069\u5728\u796d\u7940\u4eea\u5f0f\u4e0a\u89c1\u8fc7\u7684\u94f6\u8272\u957f\u5251\u3002\u5b83\u4eec\u4ea4\u9519\u7740\u649e\u5165\u6f29\u6da1\u7684\u4e2d\u5fc3\uff0c\u6574\u4e2a\u623f\u95f4\u4f3c\u4e4e\u90fd\u88ab\u9707\u52a8\u8d77\u6765\uff0c\u7d2b\u5149\u7565\u7565\u6709\u6240\u9eef\u6de1\uff0c\u6f29\u6da1\u7684\u901f\u5ea6\u4eff\u4f5b\u4e5f\u51cf\u7f13\u4e86\u51e0\u5206\uff0c\u4f46\u968f\u5373\u53c8\u6709\u6062\u590d\u7684\u8d8b\u52bf\u3002</p><p>\u201c\u7528\u5f71\u706b\uff01\u201d\u8475\u9732\u671d\u743c\u6069\u5927\u558a\u3002</p><p>\u7136\u800c\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u4ed6\u751a\u81f3\u8fde\u5f71\u706b\u662f\u4ec0\u4e48\u90fd\u4e0d\u6e05\u695a\uff0c\u66f4\u4e0d\u77e5\u9053\u8475\u9732\u5230\u5e95\u60f3\u8ba9\u4ed6\u505a\u4ec0\u4e48\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u4ed6\u7684\u6000\u4e2d\u4e00\u9635\u9707\u52a8\uff0c\u4e00\u4e2a\u65b9\u65b9\u6b63\u6b63\u7684\u767d\u8272\u7269\u54c1\u98de\u4e86\u51fa\u6765\uff0c\u6b63\u662f\u90a3\u526f\u8428\u74e6\u68cb\u3002</p><p>\u4e8c\u5341\u56db\u679a\u68cb\u5b50\u540c\u65f6\u5347\u8d77\u5728\u534a\u7a7a\uff0c\u5316\u4f5c\u9b54\u50cf\u843d\u4e0b\u6765\uff0c\u5305\u62ec\u539f\u672c\u7406\u5e94\u662f\u4e0d\u80fd\u52a8\u7528\u7684\u4e24\u679a\u201c\u4e3b\u6bcd\u201d\u3002\u5b83\u4eec\u6574\u6574\u9f50\u9f50\uff0c\u4eff\u4f5b\u5217\u6210\u4e00\u4e2a\u9635\u52bf\u4f3c\u7684\uff0c\u53c8\u50cf\u662f\u4e00\u6839\u6839\u77f3\u67f1\u77d7\u7acb\u5728\u6d2a\u6d41\u4e4b\u4e2d\uff0c\u5c06\u6f29\u6da1\u7684\u4e2d\u5fc3\u5305\u56f4\u5728\u5185\u3002\u8be1\u5f02\u7684\u9ed1\u8272\u7b26\u6587\u4ece\u6bcf\u4e2a\u9b54\u50cf\u8eab\u4e0a\u6e90\u6e90\u4e0d\u65ad\u5730\u6cdb\u51fa\uff0c\u8fc5\u901f\u8054\u7ed3\u6210\u4e00\u4e2a\u5706\u73af\uff0c\u9006\u7740\u6f29\u6da1\u65b9\u5411\u8f6c\u52a8\u8d77\u6765\uff0c\u901f\u5ea6\u8d8a\u6765\u8d8a\u5feb\u3002</p><p>\u4e00\u65f6\u4e4b\u95f4\uff0c\u623f\u95f4\u91cc\u4eff\u4f5b\u51fa\u73b0\u4e86\u4e24\u4e2a\u6f29\u6da1\uff0c\u7d2b\u8272\u5728\u5916\uff0c\u9ed1\u8272\u5728\u5185\uff0c\u5f7c\u6b64\u65b9\u5411\u9006\u53cd\uff0c\u901f\u5ea6\u5374\u90fd\u662f\u8d8a\u6765\u8d8a\u5feb\u3002\u5728\u8fd9\u4e24\u79cd\u5f3a\u5927\u529b\u91cf\u7684\u4ea4\u9519\u62c9\u626f\u4e4b\u4e0b\uff0c\u51e1\u4eba\u6839\u672c\u65e0\u529b\u6297\u8861\uff0c\u5c31\u8fde\u65bd\u6cd5\u9003\u8131\u7684\u95f4\u9699\u90fd\u6ca1\u6709\uff0c\u8475\u9732\u518d\u5ea6\u91ca\u653e\u51fa\u84dd\u767d\u95ea\u7535\u548c\u94f6\u5251\uff0c\u7a0d\u7a0d\u7f13\u89e3\u4e86\u6f29\u6da1\u7684\u901f\u5ea6\uff0c\u201c\u5feb\u7528\u5f71\u706b\uff01\u201d\u5979\u518d\u6b21\u51b2\u7740\u743c\u6069\u5927\u543c\u3002</p><p>\u95ee\u9898\u662f\u743c\u6069\u538b\u6839\u4e0d\u77e5\u9053\u4ec0\u4e48\u662f\u5f71\u706b\uff0c\u66f4\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\u8475\u9732\u5c31\u504f\u504f\u8ba4\u51c6\u4e86\u4ed6\u6709\u201c\u5f71\u706b\u201d\u53ef\u4ee5\u8fd0\u7528\uff0c\u6b63\u81ea\u65e2\u607c\u6012\u53c8\u60ca\u60f6\u4e4b\u95f4\uff0c\u9661\u7136\u4e00\u80a1\u51b0\u51b7\u7684\u5bd2\u610f\u81ea\u8179\u4e2d\u5347\u8d77\u3002</p><p>\u8fd9\u79cd\u611f\u89c9\u5e76\u4e0d\u662f\u7b2c\u4e00\u6b21\uff0c\u53bb\u5e74\u5728\u74dc\u7406\u5fb7\u65af\u7684\u65f6\u5019\uff0c\u8fdb\u653b\u745e\u8d39\u5fb7\u5bb6\u65cf\uff0c\u66fe\u7ecf\u4e2d\u4e86\u5bf9\u65b9\u5deb\u5e08\u4e00\u51fb\u6697\u591c\u629a\u6478\uff0c\u6b63\u5f53\u5c06\u6b7b\u4e4b\u65f6\uff0c\u8179\u90e8\u4e5f\u662f\u8fd9\u6837\u4e00\u80a1\u9634\u51b7\u51b0\u5bd2\u7684\u6c14\u606f\u5347\u8d77\uff0c\u6551\u4e86\u81ea\u5df1\u4e00\u547d\u3002\u743c\u6069\u5fc3\u4e2d\u4e00\u559c\uff0c\u201c\u5df4\u5c14\u795e\u529b\uff1f\u201d\u4ed6\u6697\u4e2d\u601d\u5fd6\u7740\uff0c\u201c\u6216\u8bb8\u8fd9\u6b21\u6709\u6551\u4e86\u3002\u201d</p><p>\u5bd2\u6c14\u5347\u817e\u76f4\u4e0a\uff0c\u77ac\u95f4\u6d41\u904d\u5168\u8eab\uff0c\u7136\u540e\u6ea2\u51fa\uff0c\u5728\u4f53\u5916\u51dd\u805a\u5e7b\u5316\u6210\u4e00\u4e2a\u6f29\u6da1\u72b6\u9634\u5f71\u6597\u7bf7\uff0c\u9010\u6e10\u6269\u5927\u5f00\u6765\uff0c\u649e\u4e0a\u4e86\u7d2b\u8272\u548c\u9ed1\u8272\u6f29\u6da1\u3002\u4e09\u79cd\u529b\u91cf\u4ea4\u9519\u5728\u4e00\u8d77\uff0c\u4eff\u4f5b\u5de8\u5927\u7684\u78e8\u76d8\u4e00\u822c\uff0c\u623f\u95f4\u91cc\u6240\u6709\u7684\u5bb6\u5177\u90fd\u88ab\u7ede\u6210\u788e\u672b\uff0c\u77f3\u58c1\u548c\u5929\u82b1\u677f\u4e5f\u5728\u4e0d\u65ad\u88ab\u4fb5\u8680\uff0c\u5c42\u5c42\u5265\u79bb\uff0c\u552f\u6709\u8eab\u5904\u65cb\u6da1\u4e2d\u7684\u51e0\u4e2a\u4eba\u5b89\u7136\u65e0\u6059\u3002</p><p>\u8475\u9732\u518d\u5ea6\u53d1\u51fa\u84dd\u767d\u8272\u95ea\u7535\u548c\u94f6\u5251\uff0c\u5979\u7684\u8138\u8272\u5728\u4e00\u5239\u90a3\u90fd\u4eff\u4f5b\u82cd\u767d\u4e86\u51e0\u5206\u3002\u94f6\u5251\u77ac\u95f4\u88ab\u7d2b\u5149\u541e\u6ca1\uff0c\u4f46\u84dd\u767d\u95ea\u7535\u5374\u548c\u9634\u5f71\u6597\u7bf7\u731b\u70c8\u5730\u649e\u5728\u4e00\u5904\uff0c\u8f70\u7136\u7eb7\u7eb7\u788e\u88c2\uff0c\u7d27\u63a5\u7740\u4fbf\u662f\u9707\u8033\u6b32\u804b\u7684\u5927\u7206\u70b8\u3002</p><p>\u9ed1\u6697\u964d\u4e34\u3002</p></article>"}}}]);
