(window.webpackJsonp=window.webpackJsonp||[]).push([[1513],{1536:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e00\u8282 \u6545\u4eba",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e00\u8282 \u6545\u4eba</h2><p>\u201c\u4e09\u4e2a\u5938\u8d5b\u9b54\uff0c\u4e24\u4e2a\u5df4\u5e03\u9b54\uff0c\u4e00\u4e2a\u9b45\u9b54\uff0c\u201d\u8475\u9732\u7cbe\u786e\u5730\u62a5\u51fa\u4e86\u5bf9\u65b9\u7684\u4eba\u5458\u6784\u6210\uff0c\u201c\u4e00\u5171\u516d\u4e2a\u6076\u9b54\u2026\u2026\u5947\u602a\uff0c\u96be\u9053\u5b83\u4eec\u662f\u5546\u961f\u4e0d\u6210\uff1f\u201d</p><p>\u4e4b\u6240\u4ee5\u505a\u51fa\u8fd9\u4e2a\u5224\u65ad\uff0c\u662f\u56e0\u4e3a\u8fd9\u4e00\u884c\u6076\u9b54\u5f53\u4e2d\u8fd8\u6709\u516d\u8f86\u5927\u8f66\uff0c\u7531\u4e00\u79cd\u957f\u5f97\u50cf\u7266\u725b\u4f3c\u7684\u9a6e\u517d\u62c9\u7740\uff0c\u6b63\u671d\u8fd9\u8fb9\u8d70\u8fc7\u6765\u3002\u4e09\u53ea\u5938\u8d5b\u9b54\u6b63\u5f20\u7740\u7fc5\u8180\u5728\u7a7a\u4e2d\u98de\u6765\u98de\u53bb\uff0c\u4e24\u53ea\u5df4\u5e03\u9b54\u5de6\u53f3\u62a4\u536b\u7740\u8f66\u961f\uff0c\u9b45\u9b54\u5219\u7fd8\u7740\u957f\u817f\uff0c\u5750\u5728\u7b2c\u4e00\u8f86\u5927\u8f66\u4e0a\uff0c\u4f4e\u7740\u5934\uff0c\u5979\u7684\u8eab\u540e\u4f3c\u4e4e\u8fd8\u6709\u4e00\u4e2a\u65d7\u6746\uff0c\u63d2\u5728\u8f66\u5c3e\uff0c\u4e0a\u9762\u6709\u9762\u65d7\u5e1c\uff0c\u53ea\u662f\u72c2\u98ce\u7ffb\u8f6c\uff0c\u538b\u6839\u770b\u4e0d\u6e05\u695a\u4e0a\u9762\u5230\u5e95\u662f\u4ec0\u4e48\u3002</p><p>\u8475\u9732\u62ff\u7740\u671b\u8fdc\u955c\u53c8\u56db\u5904\u770b\u4e86\u770b\uff0c\u89c6\u91ce\u4e4b\u5185\u6ca1\u6709\u5176\u4ed6\u6076\u9b54\uff0c\u9664\u4e86\u5929\u4e0a\u7684\u5f17\u6d1b\u9b54\u4e4b\u5916\u3002\u5979\u8df3\u4e0b\u5730\u6765\uff0c\u628a\u60c5\u51b5\u5feb\u901f\u8bf4\u4e86\u4e00\u4e0b\u3002\u8fd9\u662f\u4e2a\u597d\u673a\u4f1a\uff0c\u5927\u5bb6\u6b63\u6101\u627e\u4e0d\u5230\u5411\u5bfc\uff0c\u73b0\u5728\u5c31\u6709\u4eba\u4e3b\u52a8\u9001\u4e0a\u95e8\u6765\u3002\u867d\u7136\u5bf9\u65b9\u6709\u516d\u4e2a\u6076\u9b54\uff0c\u81ea\u5df1\u8fd9\u8fb9\u53ea\u6709\u4e94\u4e2a\uff0c\u6885\u83f2\u65af\u8fd8\u4e0d\u80fd\u53c2\u6218\uff0c\u4f0a\u8389\u96c5\u53ea\u6015\u4e5f\u4e0d\u80fd\u6307\u671b\uff0c\u4f46\u5176\u5b9e\u6839\u672c\u6ca1\u4ec0\u4e48\u53ef\u62c5\u5fc3\u7684\u3002</p><p>\u201c\u5938\u8d5b\u9b54\u53ef\u4ee5\u7b97\u662f\u6df1\u6e0a\u5404\u79cd\u6076\u9b54\u4e2d\u6700\u5dee\u52b2\u7684\u4e00\u79cd\uff0c\u80c6\u5c0f\u61e6\u5f31\uff0c\u9664\u4e86\u5938\u5938\u5176\u8c08\u3001\u72d0\u5047\u864e\u5a01\u548c\u9690\u5f62\u9003\u8dd1\u4e4b\u5916\uff0c\u522b\u65e0\u5176\u4ed6\u672c\u4e8b\uff0c\u201d\u8475\u9732\u8bf4\uff0c\u201c\u53ea\u8981\u52bf\u5934\u7a0d\u6709\u4e0d\u5bf9\uff0c\u5b83\u4eec\u9003\u5f97\u6bd4\u5154\u5b50\u8fd8\u5feb\uff0c\u5b8c\u5168\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\u3002\u201d</p><p>\u4e09\u4e2a\u5938\u8d5b\u9b54\u76f4\u63a5\u88ab\u65e0\u89c6\uff0c\u5269\u4e0b\u4e09\u4e2a\u6076\u9b54\u4e2d\uff0c\u4e24\u4e2a\u5df4\u5e03\u9b54\u5206\u914d\u7ed9\u963f\u5fd2\u59ae\u5bf9\u4ed8\uff0c\u9b45\u9b54\u5219\u7531\u8475\u9732\u4eb2\u81ea\u51fa\u9a6c\u3002\u201c\u5df4\u5e03\u9b54\u64c5\u957f\u5077\u88ad\uff0c\u4e0d\u8981\u7ed9\u5b83\u4eec\u53cd\u5e94\u8fc7\u6765\u7684\u673a\u4f1a\uff0c\u201d\u8475\u9732\u8bf4\uff0c\u201c\u5938\u8d5b\u9b54\u548c\u9b45\u9b54\u5e94\u8be5\u90fd\u61c2\u901a\u7528\u8bed\uff0c\u5c3d\u91cf\u6293\u6d3b\u7684\u3002\u201d</p><p>\u81f3\u4e8e\u743c\u6069\uff0c\u7559\u4e0b\u6765\u7167\u987e\u6885\u83f2\u65af\uff0c\u987a\u4fbf\u770b\u7740\u4f0a\u8389\u96c5\u5c31\u597d\u3002</p><p>\u201c\u6211\u53bb\u5bf9\u4ed8\u90a3\u53ea\u9b45\u9b54\u5427\u3002\u201d\u743c\u6069\u8bf4\uff0c\u8fd9\u4e00\u8def\u4e0a\u7684\u6218\u6597\u57fa\u672c\u90fd\u662f\u8475\u9732\u51fa\u624b\uff0c\u5979\u662f\u4f0a\u8389\u4e1d\u7fe0\u7684\u7267\u5e08\uff0c\u4f46\u5973\u795e\u8fd8\u5728\u6c89\u7720\u4e2d\uff0c\u65e0\u6cd5\u6388\u4e88\u795e\u672f\u3002\u867d\u7136\u51ed\u501f\u7740\u9009\u6c11\u7684\u80fd\u529b\u4f9d\u65e7\u5f88\u5f3a\u5927\uff0c\u4f46\u94f6\u706b\u4e5f\u4e0d\u662f\u80fd\u65e0\u9650\u5236\u52a8\u7528\u7684\uff0c\u770b\u5979\u7684\u60c5\u5f62\uff0c\u660e\u663e\u5df2\u7ecf\u8017\u529b\u592a\u591a\uff0c\u5f88\u662f\u75b2\u5026\u4e86\u3002</p><p>\u201c\u4f60\u7684\u795e\u529b\u5bb9\u6613\u628a\u9b45\u9b54\u76f4\u63a5\u6740\u6b7b\uff0c\u201d\u4ed6\u53c8\u8865\u5145\u4e86\u4e00\u53e5\uff0c\u201c\u6211\u6765\u7528\u6cd5\u672f\u6355\u83b7\u5979\u6bd4\u8f83\u597d\u3002\u201d</p><p>\u8fd9\u4e2a\u7406\u7531\u5408\u60c5\u5408\u7406\uff0c\u4f46\u8475\u9732\u6447\u6447\u5934\uff0c\u201c\u4f60\u4e0d\u884c\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u662f\u7537\u6027\u3002\u201d</p><p>\u542c\u5230\u524d\u534a\u53e5\u8bdd\uff0c\u743c\u6069\u6709\u4e9b\u607c\u6012\uff0c\u4f46\u542c\u5230\u540e\u534a\u53e5\u4fbf\u660e\u767d\u8fc7\u6765\u3002\u9b45\u9b54\u4e0d\u64c5\u957f\u683c\u6597\uff0c\u7eaf\u4ee5\u6218\u529b\u800c\u8bba\u5176\u5b9e\u5dee\u5f97\u5f88\uff0c\u5979\u4eec\u6700\u559c\u6b22\u7684\u662f\u5229\u7528\u7edd\u7f8e\u7684\u5bb9\u989c\u548c\u98ce\u60c5\u6765\u5a9a\u60d1\u5bf9\u624b\uff0c\u63a7\u5236\u5fc3\u795e\uff0c\u5728\u65f6\u673a\u6070\u5f53\u7684\u65f6\u5019\u732e\u4e0a\u81f4\u547d\u7684\u6bd2\u543b\uff0c\u5f15\u8bf1\u7075\u9b42\u5815\u843d\u3002\u7b80\u5355\u6765\u8bf4\uff0c\u5979\u4eec\u7684\u6218\u6597\u65b9\u5f0f\u662f\u6807\u51c6\u7684\u201c\u5973\u4eba\u201d\uff0c\u800c\u8fd9\u663e\u7136\u5bf9\u7537\u6027\u975e\u5e38\u6709\u6548\uff0c\u78b0\u5230\u5973\u6027\u5c31\u5927\u6253\u6298\u6263\u4e86\uff0c\u9664\u975e\u9047\u4e0a\u857e\u4e1d\u8fb9\u3002</p><p>\u201c\u6211\u53bb\u6211\u53bb\u3002\u201d\u4f0a\u8389\u96c5\u81ea\u544a\u594b\u52c7\u3002</p><p>\u201c\u4f60\uff1f\u201d</p><p>\u867d\u7136\u5df2\u7ecf\u77e5\u9053\u5979\u5e76\u4e0d\u662f\u5f31\u4e0d\u7981\u98ce\u7684\u5c0f\u5973\u5b69\uff0c\u800c\u662f\u7f57\u4e1d\u7684\u201c\u6bd2\u523a\u201d\uff0c\u4f46\u5355\u770b\u5916\u8868\uff0c\u4e00\u526f\u5929\u771f\u70c2\u6f2b\u7684\u7a1a\u6c14\uff0c\u4f9d\u65e7\u5f88\u96be\u548c\u6740\u624b\u4e4b\u7c7b\u8054\u7cfb\u8d77\u6765\u3002\u9b45\u9b54\u867d\u8bf4\u201c\u4e0d\u64c5\u6218\u6597\u201d\uff0c\u4f46\u90a3\u662f\u76f8\u5bf9\u800c\u8a00\uff0c\u5979\u7ec8\u7a76\u662f\u4e2a\u6076\u9b54\uff0c\u6325\u821e\u8d77\u76ae\u97ad\u6765\u4e0d\u4f46\u53ef\u4ee5\u5728\u5e8a\u4e0a\u5ba2\u4e32\u5973\u738b\uff0c\u4e5f\u662f\u53ef\u4ee5\u6740\u4eba\u7684\u3002</p><p>\u201c\u4e0d\u8981\u5c0f\u770b\u6211\u54e6\uff0c\u201d\u4f0a\u8389\u96c5\u6485\u7740\u5634\uff0c\u6325\u821e\u7740\u5c0f\u62f3\u5934\uff0c\u201c\u6211\u53ef\u662f\u5f88\u5f3a\u7684\u5462\u3002\u201d</p><p>\u743c\u6069\u548c\u8475\u9732\u4e92\u76f8\u770b\u770b\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u90a3\u5c31\u8fd9\u6837\u5427\uff0c\u201d\u8475\u9732\u6700\u540e\u8bf4\uff0c\u201c\u4f0a\u8389\u96c5\u53bb\u5bf9\u4ed8\u9b45\u9b54\uff0c\u6211\u5bf9\u4ed8\u90a3\u4e24\u53ea\u5df4\u5e03\u9b54\uff0c\u963f\u5fd2\u59ae\u5728\u540e\u9762\u7b56\u5e94\u3002\u5bf9\u4e86\uff0c\u6709\u4e00\u70b9\u5f97\u63d0\u9192\uff0c\u5c3d\u91cf\u653b\u51fb\u5934\u90e8\u6216\u8005\u5fc3\u810f\u8fd9\u79cd\u8981\u5bb3\uff0c\u8fd9\u91cc\u662f\u6df1\u6e0a\uff0c\u6076\u9b54\u7684\u6062\u590d\u81ea\u6108\u80fd\u529b\u4f1a\u975e\u5e38\u60ca\u4eba\u3002\u201d</p><p>\u4efb\u52a1\u5206\u914d\u5c31\u7eea\uff0c\u5404\u81ea\u627e\u4f4d\u7f6e\u9690\u85cf\uff0c\u51c6\u5907\u4f0f\u51fb\uff0c\u4f0a\u8389\u96c5\u5374\u4f38\u51fa\u624b\u6765\uff0c\u201c\u53ef\u662f\uff0c\u54e5\u54e5\uff0c\u201d\u5979\u5bf9\u743c\u6069\u8bf4\uff0c\u201c\u6211\u6ca1\u6709\u6b66\u5668\u5440\uff0c\u603b\u4e0d\u80fd\u8ba9\u6211\u7a7a\u7740\u624b\u53bb\u5bf9\u4ed8\u90a3\u4e48\u5371\u9669\u7684\u5927\u6076\u9b54\u5427\u3002\u201d</p><p>\u201c\u9b45\u9b54\u7b97\u4ec0\u4e48\u5371\u9669\u5927\u6076\u9b54\u3002\u201d\u743c\u6069\u5600\u5495\u7740\uff0c\u4f46\u4f0a\u8389\u96c5\u7684\u8bf4\u6cd5\u4e5f\u6709\u9053\u7406\uff0c\u603b\u4e0d\u80fd\u8ba9\u4eba\u8d64\u624b\u7a7a\u62f3\u4e0a\u9635\u3002\u4ed6\u60f3\u8d77\u81ea\u5df1\u8fd8\u6709\u4e00\u67c4\u8718\u86db\u5315\u9996\uff0c\u4ece\u6000\u91cc\u6478\u4e86\u6478\uff0c\u53d6\u4e86\u51fa\u6765\u9012\u7ed9\u4f0a\u8389\u96c5\uff0c\u201c\u62ff\u7740\u3002\u201d</p><p>\u201c\u54ce\u5440\uff0c\u662f\u796d\u5200\u5462\uff0c\u201d\u4f0a\u8389\u96c5\u6709\u4e9b\u5403\u60ca\uff0c\u201c\u53ea\u6709\u5730\u4f4d\u5f88\u9ad8\u7684\u8d35\u65cf\u624d\u80fd\u6709\u796d\u5200\uff0c\u54e5\u54e5\u4f60\u4ece\u54ea\u91cc\u5f04\u5230\u7684\u554a\u3002\u201d</p><p>\u201c\u62a2\u7684\uff0c\u201d\u743c\u6069\u968f\u53e3\u6577\u884d\uff0c\u201c\u5feb\u53bb\u5427\u3002\u201d</p><p>\u6240\u6709\u4eba\u5404\u5c31\u5404\u4f4d\uff0c\u90a3\u8fb9\u516d\u4e2a\u6076\u9b54\u62bc\u7740\u8f66\u961f\u5df2\u7ecf\u8fd1\u524d\u3002\u743c\u6069\u7b49\u4eba\u6240\u5728\u7684\u4f4d\u7f6e\uff0c\u662f\u4e24\u5904\u5c71\u5d16\u4e2d\u95f4\u7684\u5c0f\u5ce1\u8c37\uff0c\u800c\u6076\u9b54\u4e5f\u6b63\u662f\u6253\u7b97\u4ece\u4e2d\u7a7f\u8fc7\u3002\u5938\u8d5b\u9b54\u8dbe\u9ad8\u6c14\u626c\u5730\u547c\u559d\u5404\u79cd\u5185\u5bb9\u4e92\u76f8\u51b2\u7a81\u7684\u547d\u4ee4\uff0c\u6307\u6325\u7740\u9a6e\u517d\uff0c\u5df4\u5e03\u9b54\u4e00\u58f0\u4e0d\u542d\u5730\u6b65\u884c\u8ddf\u968f\u3002\u9b45\u9b54\u5750\u5728\u8f66\u4e0a\uff0c\u5de8\u5927\u7684\u9ed1\u8272\u8759\u8760\u7fc5\u8180\u6536\u62e2\u7740\uff0c\u5c06\u81ea\u5df1\u4e0a\u534a\u8eab\u88f9\u5f97\u4e25\u4e25\u5b9e\u5b9e\uff0c\u50cf\u662f\u5728\u906e\u6321\u9633\u5149\uff0c\u770b\u4e0d\u5230\u9762\u5bb9\uff0c\u50cf\u662f\u5728\u4f11\u606f\uff0c\u8170\u80cc\u66f2\u7ebf\u975e\u5e38\u4f18\u7f8e\uff0c\u5979\u662f\u4fa7\u5750\u7740\uff0c\u4ece\u743c\u6069\u8fd9\u4e2a\u89d2\u5ea6\u53ea\u80fd\u770b\u5230\u534a\u622a\u66b4\u9732\u5728\u5916\u7684\u5927\u817f\uff0c\u4e30\u6ee1\u5706\u6da6\uff0c\u808c\u80a4\u5149\u6d01\uff0c\u6beb\u65e0\u534a\u70b9\u7455\u75b5\uff0c\u4e00\u6bb5\u706b\u7ea2\u8272\u7684\u97ad\u68a2\u4ece\u7fc5\u8180\u4e0b\u9732\u51fa\u6765\uff0c\u8d34\u5728\u5927\u817f\u5185\u4fa7\uff0c\u8d8a\u53d1\u886c\u6258\u5f97\u76ae\u80a4\u4e73\u767d\u5982\u7389\u3002</p><p>\u201c\u522b\u770b\u50bb\u4e86\u3002\u201d\u6885\u83f2\u65af\u7565\u5e26\u4e0d\u6ee1\u5730\u8bf4\u3002</p><p>\u743c\u6069\u7b11\u4e86\u7b11\uff0c\u5c06\u5979\u62b1\u7d27\uff0c\u4fbf\u5728\u6b64\u65f6\u4e00\u652f\u94f6\u8272\u957f\u7bad\u98d5\u5730\u5c04\u51fa\uff0c\u6b63\u4e2d\u4e00\u53ea\u5df4\u5e03\u9b54\u7684\u9762\u95e8\uff0c\u7830\u5730\u4e00\u58f0\u70b8\u88c2\u5f00\u6765\u3002\u963f\u5fd2\u59ae\u662f\u9b54\u5c04\u624b\uff0c\u53ef\u4ee5\u4e34\u65f6\u704c\u6ce8\u4e00\u4e9b\u4f4e\u9636\u6cd5\u672f\u5728\u7bad\u77e2\u4e2d\uff0c\u9020\u6210\u66f4\u5927\u7684\u6740\u4f24\u529b\uff0c\u5f53\u7136\u8fd9\u79cd\u80fd\u529b\u4e00\u5929\u53ea\u80fd\u7528\u4e00\u6b21\u3002\u5df4\u5e03\u9b54\u7684\u534a\u4e2a\u8111\u888b\u90fd\u88ab\u70b8\u788e\uff0c\u987f\u65f6\u6bd9\u547d\uff0c\u5f80\u540e\u5c31\u5012\u3002\u5176\u4ed6\u6076\u9b54\u8fd9\u624d\u60ca\u89c9\u6709\u57cb\u4f0f\uff0c\u7eb7\u7eb7\u547c\u559d\u8d77\u6765\uff0c\u53e6\u4e00\u53ea\u5df4\u5e03\u9b54\u7684\u8eab\u4f53\u4e0a\u9661\u7136\u6d8c\u8d77\u4e00\u5c42\u679c\u51bb\u72b6\u7684\u7ea2\u8272\u9ecf\u7a20\u6db2\u4f53\uff0c\u5c16\u9510\u7684\u6307\u7532\u5f39\u51fa\uff0c\u4eff\u4f5b\u53cc\u624b\u5b89\u4e86\u4e24\u5957\u8155\u5203\uff0c\u4e00\u9635\u65cb\u98ce\u822c\u671d\u94f6\u7bad\u5c04\u6765\u7684\u4f4d\u7f6e\u51b2\u53bb\u3002</p><p>\u4e09\u4e2a\u5938\u8d5b\u9b54\u50cf\u6ca1\u5934\u82cd\u8747\u4e00\u6837\uff0c\u60ca\u614c\u5931\u63aa\u5730\u5728\u7a7a\u4e2d\u4e71\u649e\u7740\uff0c\u53eb\u56b7\u7684\u58f0\u97f3\u53cd\u5012\u6bd4\u8c01\u90fd\u5927\u3002\u4e00\u76f4\u4f4e\u5934\u4f11\u606f\u7684\u9b45\u9b54\u4e5f\u7ad9\u8d77\u8eab\u6765\uff0c\u8df3\u4e0b\u8f66\uff0c\u53cc\u7ffc\u5728\u80cc\u540e\u8212\u5f20\uff0c\u53f3\u624b\u638c\u4e2d\u63e1\u7740\u4e00\u67c4\u706b\u7ea2\u8272\u7684\u957f\u97ad\uff0c\u556a\u5730\u5728\u7a7a\u4e2d\u54cd\u4eae\u62bd\u4e86\u4e00\u8bb0\uff0c\u5938\u8d5b\u9b54\u4eec\u4fbf\u7acb\u523b\u5b89\u9759\u4e0b\u6765\uff0c\u754f\u754f\u7f29\u7f29\u5730\u843d\u5728\u5730\u4e0a\uff0c\u5c3d\u53ef\u80fd\u8eb2\u5728\u9b45\u9b54\u8eab\u540e\uff0c\u4e1c\u5f20\u897f\u671b\u3002</p><p>\u8475\u9732\u5df2\u7ecf\u8fce\u4e0a\u4e86\u90a3\u53ea\u5df4\u5e03\u9b54\uff0c\u5979\u6ca1\u6709\u4f7f\u7528\u795e\u529b\uff0c\u800c\u662f\u76f4\u63a5\u6325\u7740\u94f6\u8272\u957f\u5251\u8fd1\u8eab\u640f\u6597\uff0c\u4f5c\u4e3a\u4f0a\u8389\u4e1d\u7fe0\u7684\u7267\u5e08\uff0c\u5979\u5728\u5251\u672f\u4e0a\u4e5f\u662f\u9887\u6709\u9020\u8be3\u7684\u3002\u963f\u5fd2\u59ae\u53c8\u5c04\u51fa\u4e00\u7bad\uff0c\u547d\u4e2d\u4e86\u5df4\u5e03\u9b54\u7684\u80f3\u818a\uff0c\u4f46\u7acb\u523b\u8fde\u7bad\u5934\u5e26\u7bad\u6746\u90fd\u88ab\u8150\u8680\u6389\u4e86\uff0c\u6ca1\u6709\u9020\u6210\u4ec0\u4e48\u4f24\u5bb3\u3002</p><p>\u5145\u6ee1\u8bf1\u60d1\u7684\u751c\u7f8e\u55d3\u97f3\u4ece\u9b45\u9b54\u53e3\u4e2d\u5410\u51fa\uff0c\u5979\u5728\u51c6\u5907\u65bd\u6cd5\u3002\u9b45\u9b54\u6781\u529b\u907f\u514d\u548c\u654c\u4eba\u5200\u5251\u76f8\u5411\uff0c\u76f8\u5e94\u5730\u5728\u9b54\u6cd5\u4e0a\u7a0d\u6709\u9020\u8be3\uff0c\u80fd\u591f\u4f7f\u7528\u4e00\u4e9b\u4f4e\u9636\u7684\u6cd5\u672f\u3002\u968f\u7740\u5492\u8bed\u7684\u5feb\u901f\u5b8c\u6210\uff0c\u5979\u7684\u7ea2\u8272\u957f\u97ad\u4e0a\u817e\u8d77\u4e00\u6e9c\u9ed1\u7130\uff0c\u9b45\u9b54\u56de\u8eab\u4e00\u626b\uff0c\u556a\u556a\u8fde\u54cd\uff0c\u4e09\u53ea\u5938\u8d5b\u9b54\u8eab\u4e0a\u6bcf\u4e2a\u90fd\u6328\u4e86\u4e00\u8bb0\u3002</p><p>\u201c\u53bb\uff01\u201d\u5979\u5a07\u7b11\u7740\u4e0b\u4ee4\u3002</p><p>\u4e09\u53ea\u5938\u8d5b\u9b54\u88ab\u957f\u97ad\u4e0a\u7684\u9ed1\u7130\u6cbe\u4e0a\uff0c\u987f\u65f6\u5168\u8eab\u808c\u8089\u50cf\u5439\u6c14\u7403\u4e00\u6837\u9f13\u80c0\u8d77\u6765\uff0c\u4f53\u578b\u5239\u90a3\u95f4\u589e\u5927\u4e86\u51e0\u500d\uff0c\u8fde\u4e00\u53e3\u7259\u9f7f\u90fd\u53d8\u5f97\u5c16\u9510\u5982\u5200\uff0c\u4ece\u5507\u8fb9\u66b4\u51fa\uff0c\u53cc\u773c\u4e5f\u53d8\u5f97\u8d64\u7ea2\u4e00\u7247\uff0c\u539f\u672c\u7684\u602f\u61e6\u754f\u7f29\u795e\u60c5\u4e00\u626b\u800c\u7a7a\uff0c\u54c7\u54c7\u602a\u53eb\u7740\uff0c\u5c31\u8981\u51b2\u4e0a\u53bb\u5e2e\u52a9\u5df4\u5e03\u9b54\u5bf9\u4ed8\u8475\u9732\u3002</p><p>\u7136\u540e\u5176\u4e2d\u4e00\u4e2a\u5f80\u524d\u6251\u5012\u3002</p><p>\u4e00\u62b9\u6de1\u6de1\u7684\u9634\u5f71\u5728\u5938\u8d5b\u9b54\u80cc\u540e\u51fa\u73b0\uff0c\u968f\u5373\u51dd\u805a\u6210\u5b9e\u4f53\uff0c\u6b63\u662f\u4f0a\u8389\u96c5\uff0c\u5979\u51b7\u9759\u5730\u5c06\u8718\u86db\u5315\u9996\u4ece\u5938\u8d5b\u9b54\u80cc\u540e\u62bd\u51fa\uff0c\u516b\u53ea\u8718\u86db\u957f\u817f\u5df2\u7ecf\u7262\u7262\u6263\u4f4f\u4e86\u4e00\u9897\u9ed1\u8272\u7684\u5fc3\u810f\uff0c\u4f3c\u4e4e\u8fd8\u5728\u9690\u9690\u8df3\u52a8\u3002</p><p>\u201c\u54e6\u54e6\u3002\u201d</p><p>\u5c0f\u841d\u8389\u968f\u610f\u7529\u4e86\u7529\u624b\uff0c\u5c06\u90a3\u9897\u5fc3\u810f\u4e22\u5230\u4e00\u8fb9\uff0c\u8fce\u4e0a\u4e86\u53e6\u5916\u4e24\u53ea\u72c2\u66b4\u5316\u7684\u5938\u8d5b\u9b54\uff0c\u5979\u8eab\u6750\u672c\u5c31\u5a07\u5c0f\uff0c\u52a8\u4f5c\u53c8\u8fc5\u6377\u5f97\u51fa\u5947\uff0c\u5938\u8d5b\u9b54\u5018\u82e5\u4fdd\u6301\u539f\u672c\u4f53\u578b\u7684\u8bdd\uff0c\u6216\u8bb8\u8fd8\u5bb9\u6613\u5bf9\u4ed8\u4e9b\uff0c\u5982\u4eca\u88ab\u9b45\u9b54\u7684\u6cd5\u672f\u53d8\u5f97\u5de8\u5927\u5316\uff0c\u867d\u7136\u66f4\u52a0\u51f6\u66b4\u52c7\u731b\uff0c\u529b\u6c14\u66f4\u5927\uff0c\u8f97\u8f6c\u817e\u632a\u95f4\u5374\u4e5f\u66f4\u52a0\u72fc\u4f09\u4e0d\u4fbf\uff0c\u7a7a\u6709\u4e00\u5bf9\u7fc5\u8180\u98de\u4e0d\u8d77\u6765\uff0c\u4ee5\u4e8c\u654c\u4e00\u90fd\u843d\u5728\u4e0b\u98ce\u3002</p><p>\u53c8\u4e00\u652f\u94f6\u7bad\u7834\u7a7a\u5c04\u6765\uff0c\u9489\u5728\u4e00\u53ea\u5938\u8d5b\u9b54\u7684\u540e\u8111\u4e0a\uff0c\u4f0a\u8389\u96c5\u8d81\u673a\u4ece\u5b83\u8eab\u65c1\u4e00\u8dc3\u800c\u8fc7\uff0c\u5315\u9996\u624e\u8fdb\u4e86\u6076\u9b54\u7684\u54bd\u5589\uff0c\u987a\u624b\u659c\u4e0b\u4e00\u62d6\u3002\u201c\u771f\u5dee\u52b2\u5462\u3002\u201d\u5979\u683c\u683c\u7b11\u7740\uff0c\u907f\u5f00\u4e86\u6700\u540e\u4e00\u53ea\u5938\u8d5b\u9b54\u7684\u88ad\u51fb\u3002</p><p>\u8f6c\u773c\u4e4b\u95f4\uff0c\u5df2\u7ecf\u6b7b\u4e86\u4e09\u540d\u540c\u4f34\uff0c\u9b45\u9b54\u660e\u663e\u6709\u4e9b\u72b9\u8c6b\uff0c\u671d\u5df4\u5e03\u9b54\u770b\u4e86\u4e00\u773c\uff0c\u89c1\u5b83\u5df2\u7ecf\u5904\u5904\u53d7\u4f24\uff0c\u773c\u770b\u5feb\u8981\u62b5\u5fa1\u4e0d\u4f4f\uff0c\u4fbf\u559d\u4ee4\u5938\u8d5b\u9b54\u524d\u53bb\u76f8\u52a9\u3002\u81ea\u5df1\u6325\u821e\u957f\u97ad\uff0c\u62e6\u4f4f\u4e86\u4f0a\u8389\u96c5\u3002\u4ee4\u4eba\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u5979\u7684\u8eab\u624b\u5c45\u7136\u5341\u5206\u654f\u6377\uff0c\u8fdb\u9000\u4e4b\u95f4\u5927\u6709\u7ae0\u6cd5\uff0c\u5206\u660e\u662f\u8eab\u7ecf\u767e\u6218\u7684\u8001\u624b\uff0c\u548c\u201c\u9b45\u9b54\u4e0d\u64c5\u683c\u6597\u201d\u7684\u8bf4\u6cd5\u5b8c\u5168\u4e0d\u7b26\u5408\u2014\u2014\u800c\u4e14\u4f5c\u6218\u98ce\u683c\u548c\u4f0a\u8389\u96c5\u975e\u5e38\u8fd1\u4f3c\uff0c\u4e00\u770b\u4fbf\u77e5\u9053\u662f\u5178\u578b\u7684\u6740\u624b\u98ce\u683c\u3002</p><p>\u96be\u9053\u73b0\u5728\u6df1\u6e0a\u91cc\u7684\u9b45\u9b54\u4e5f\u90fd\u5f00\u59cb\u63a5\u53d7\u523a\u5ba2\u8bad\u7ec3\u4e86\u4e48\uff1f</p><p>\u9b45\u9b54\u7684\u51fa\u624b\u8ba9\u5c40\u52bf\u518d\u5ea6\u53d8\u5316\uff0c\u501f\u52a9\u8759\u8760\u5de8\u7ffc\uff0c\u5979\u7684\u79fb\u52a8\u901f\u5ea6\u5feb\u5f97\u60ca\u4eba\uff0c\u706b\u7ea2\u957f\u97ad\u4eff\u4f5b\u6709\u4e86\u7075\u6027\u4e00\u822c\uff0c\u77eb\u592d\u98de\u821e\uff0c\u7ffb\u817e\u5982\u9f99\uff0c\u4eff\u4f5b\u4e00\u9053\u706b\u7f51\u822c\u5bc6\u5bc6\u531d\u531d\u671d\u4f0a\u8389\u96c5\u7f69\u4e0b\u6765\uff0c\u5de6\u624b\u5219\u4e0d\u77e5\u4f55\u65f6\u5192\u51fa\u4e86\u4e00\u67c4\u9ed1\u8272\u86c7\u5f62\u77ed\u5251\uff0c\u7a81\u523a\u7a7f\u63d2\uff0c\u5feb\u82e5\u95ea\u7535\u3002\u539f\u672c\u5927\u5360\u4e0a\u98ce\u7684\u4f0a\u8389\u96c5\u8fde\u8fde\u540e\u9000\uff0c\u5904\u4e8e\u52a3\u52bf\uff0c\u9760\u7740\u963f\u5fd2\u59ae\u5728\u8fdc\u5904\u4e0d\u65f6\u53d1\u7bad\u652f\u63f4\u624d\u52c9\u5f3a\u80fd\u591f\u652f\u6301\u3002\u90a3\u8fb9\u8475\u9732\u5219\u88ab\u5df4\u5e03\u9b54\u51ed\u501f\u5f3a\u608d\u7684\u529b\u91cf\u548c\u8089\u4f53\u6062\u590d\u80fd\u529b\u6b7b\u6b7b\u7f20\u4f4f\uff0c\u4e00\u65f6\u4e0d\u80fd\u8131\u8eab\u3002</p><p>\u743c\u6069\u770b\u770b\u6218\u5c40\uff0c\u8ba9\u6885\u83f2\u65af\u8f7b\u8f7b\u501a\u9760\u5728\u77f3\u58c1\u4e0a\uff0c\u201c\u7a0d\u7b49\u4e0b\u3002\u201d\u4ed6\u8bf4\uff0c\u6478\u51fa\u7d2b\u6c34\u6676\u6cd5\u6756\uff0c\u5bf9\u51c6\u573a\u5185\u3002</p><p>\u4f0a\u8389\u96c5\u548c\u9b45\u9b54\u7684\u6218\u6597\u5df2\u7ecf\u8fdb\u5165\u767d\u70ed\u5316\uff0c\u4e00\u9ad8\u4e00\u77ee\u4e24\u4e2a\u8eab\u5f71\u500f\u5ffd\u5206\u5408\uff0c\u5728\u7a7a\u6c14\u4e2d\u7559\u4e0b\u6a21\u7cca\u7684\u6b8b\u50cf\u3002\u743c\u6069\u770b\u51c6\u65f6\u673a\uff0c\u8ba9\u4e00\u6bb5\u5492\u6587\u6e05\u6670\u5730\u5728\u8111\u4e2d\u6d6e\u73b0\uff0c\u7136\u540e\u6cd5\u6756\u4e0a\u53d1\u51fa\u4e00\u9053\u78a7\u5149\u51fb\u4e2d\u4e86\u5730\u9762\u3002\u4eff\u4f5b\u88ab\u65e0\u5f62\u7684\u5de8\u529b\u62c9\u626f\uff0c\u575a\u786c\u7684\u5ca9\u77f3\u5730\u9762\u50cf\u6ce5\u6cbc\u4e00\u822c\u7ffb\u6d8c\u5347\u817e\u8d77\u6765\uff0c\u9661\u7136\u4ece\u4e2d\u4f38\u51fa\u56db\u53ea\u7c97\u58ee\u7684\u5ca9\u77f3\u624b\u81c2\uff0c\u5feb\u82e5\u95ea\u7535\u822c\u6263\u4f4f\u4e86\u6b63\u5728\u6fc0\u6597\u4e2d\u4e24\u4eba\u7684\u811a\u8155\u3002</p><p>\u8fd9\u4e00\u6263\u6765\u5f97\u7a81\u5982\u5176\u7136\uff0c\u539f\u672c\u5728\u9ad8\u901f\u8fd0\u52a8\u7684\u4e24\u4eba\u9aa4\u7136\u9759\u6b62\u4e0b\u6765\uff0c\u603b\u7b97\u53cd\u5e94\u90fd\u5feb\uff0c\u6ca1\u6709\u5f53\u573a\u6454\u5012\u3002\u9b45\u9b54\u548c\u4f0a\u8389\u96c5\u90fd\u662f\u4ee5\u901f\u5ea6\u89c1\u957f\uff0c\u800c\u975e\u529b\u91cf\u578b\u7684\u4eba\u7269\uff0c\u53cc\u817f\u811a\u8155\u540c\u65f6\u88ab\u575a\u786c\u7684\u77f3\u722a\u6263\u4f4f\uff0c\u4fbf\u5982\u4e0a\u4e86\u7cbe\u91d1\u811a\u9563\uff0c\u4e1d\u6beb\u4e5f\u65e0\u6cd5\u6323\u8131\u3002</p><p>\u9b45\u9b54\u6b32\u5f85\u65bd\u6cd5\uff0c\u88ab\u963f\u5fd2\u59ae\u8fdc\u8fdc\u4e00\u652f\u94f6\u7bad\u5c04\u6765\uff0c\u64e6\u7740\u8138\u988a\u63a0\u8fc7\uff0c\u77e5\u9053\u662f\u8b66\u544a\uff0c\u767b\u65f6\u4e0d\u6562\u518d\u52a8\u5f39\u3002\u6b64\u65f6\u5938\u8d5b\u9b54\u7684\u72c2\u66b4\u6548\u679c\u4e5f\u5df2\u7ecf\u6d88\u9664\uff0c\u53c8\u53d8\u56de\u4e86\u539f\u672c\u7684\u80c6\u602f\u5c0f\u6076\u9b54\uff0c\u54c6\u54c6\u55e6\u55e6\u9760\u7740\u5899\u58c1\u8737\u7f29\u53d1\u6296\uff0c\u8475\u9732\u8d81\u673a\u4e00\u5251\u524a\u4e0b\u5df4\u5e03\u9b54\u7684\u5934\u9885\uff0c\u5c06\u5b83\u5f7b\u5e95\u6740\u6b7b\u3002</p><p>\u743c\u6069\u4ece\u85cf\u8eab\u4e4b\u5904\u8d70\u51fa\u6765\u3002</p><p>\u201c\u54e5\u54e5\uff0c\u4f60\u7684\u6cd5\u672f\u600e\u4e48\u628a\u6211\u4e5f\u6263\u4f4f\u4e86\uff01\u201d\u4f0a\u8389\u96c5\u4e0d\u9ad8\u5174\u5730\u5435\u95f9\u7740\uff0c\u201c\u5feb\u628a\u6211\u653e\u5f00\u3002\u201d</p><p>\u201c\u6cd5\u672f\u6ca1\u63a7\u5236\u597d\u3002\u201d\u743c\u6069\u6de1\u6de1\u89e3\u91ca\uff0c\u8d70\u5230\u65c1\u8fb9\uff0c\u547d\u4ee4\u4e24\u53ea\u5ca9\u77f3\u80f3\u818a\u64a4\u56de\uff0c\u653e\u5f00\u4e86\u4f0a\u8389\u96c5\u3002</p><p>\u5176\u5b9e\u4e0d\u662f\u6cd5\u672f\u6ca1\u63a7\u5236\u597d\uff0c\u800c\u662f\u4ed6\u539f\u672c\u5c31\u6545\u610f\u5982\u6b64\uff0c\u76ee\u7684\u662f\u7ed9\u4f0a\u8389\u96c5\u4e00\u4e2a\u8b66\u544a\u3002\u5c31\u521a\u624d\u6765\u770b\uff0c\u5353\u5c14\u5c0f\u841d\u8389\u7684\u80fd\u529b\u8d85\u51fa\u9884\u8ba1\uff0c\u4f55\u51b5\u5979\u8fd8\u662f\u4e2a\u6740\u624b\u7c7b\u578b\u7684\u4eba\u7269\uff0c\u5018\u82e5\u6709\u4ec0\u4e48\u5f02\u5fc3\u7684\u8bdd\uff0c\u662f\u4e2a\u5927\u5927\u7684\u5a01\u80c1\u3002\u867d\u8bf4\u5982\u4eca\u5927\u5bb6\u540c\u5c5e\u843d\u96be\uff0c\u4e0d\u80fd\u4e0d\u9632\u4e0a\u4e00\u624b\u3002\u53ea\u662f\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u81ea\u5df1\u7684\u8fd9\u4e2a\u6cd5\u672f\u6548\u679c\u597d\u5f97\u8fdc\u8fdc\u8d85\u4e4e\u60f3\u50cf\uff0c\u4ee5\u524d\u8bd5\u9a8c\u7684\u65f6\u5019\uff0c\u53ea\u80fd\u5851\u6210\u4e24\u53ea\u77f3\u722a\uff0c\u72b6\u6001\u6781\u597d\u65f6\u4e5f\u53ea\u6709\u4e09\u53ea\uff0c\u4f46\u521a\u624d\u5374\u6beb\u4e0d\u8d39\u529b\u5c31\u540c\u65f6\u5851\u6210\u56db\u53ea\uff0c\u8fd9\u662f\u4ec0\u4e48\u7f18\u6545\uff0c\u96be\u9053\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u81ea\u5df1\u7684\u9b54\u6cd5\u9020\u8be3\u53c8\u6709\u7a81\u7834\uff1f</p><p>\u6682\u65f6\u4e0d\u53bb\u8003\u8651\u8fd9\u4e9b\uff0c\u743c\u6069\u8f6c\u8fc7\u8eab\u6765\uff0c\u770b\u7740\u88ab\u6293\u4f4f\u7684\u9b45\u9b54\u3002\u521a\u624d\u4e00\u6218\u5b8c\u5168\u8fbe\u5230\u9884\u671f\u6548\u679c\uff0c\u516d\u4e2a\u6076\u9b54\u6740\u6b7b\u5176\u56db\uff0c\u5269\u4e0b\u4e00\u4e2a\u9b45\u9b54\u548c\u4e00\u4e2a\u5938\u8d5b\u9b54\uff0c\u5b83\u4eec\u90fd\u7b97\u662f\u667a\u529b\u578b\u800c\u975e\u86ee\u529b\u578b\u7684\u6076\u9b54\uff0c\u53ef\u4ee5\u5bf9\u8bdd\u4ea4\u6d41\u3002\u5938\u8d5b\u9b54\u867d\u7136\u80c6\u602f\u61e6\u5f31\uff0c\u5bb9\u6613\u903c\u8feb\uff0c\u4f46\u5374\u592a\u559c\u6b22\u5439\u725b\uff0c\u8bf4\u8bdd\u57fa\u672c\u4e0d\u53ef\u4fe1\uff0c\u76f8\u6bd4\u8d77\u6765\uff0c\u53cd\u800c\u628a\u5e0c\u671b\u653e\u5728\u8fd9\u9b45\u9b54\u8eab\u4e0a\u53cd\u800c\u5927\u4e9b\u3002</p><p>\u743c\u6069\u4ee5\u524d\u66fe\u7ecf\u542c\u8bf4\u9b45\u9b54\u867d\u7136\u90fd\u662f\u7edd\u4e16\u7f8e\u5973\uff0c\u5374\u957f\u7740\u5c71\u7f8a\u8e44\u5b50\uff0c\u5927\u715e\u98ce\u666f\uff0c\u5982\u4eca\u5b9e\u5730\u89c1\u5230\u771f\u4eba\uff0c\u672c\u80fd\u5730\u5c31\u5148\u671d\u811a\u4e0a\u770b\u53bb\uff0c\u53d1\u73b0\u662f\u4e00\u53cc\u7ea4\u7ea4\u7389\u8db3\uff0c\u7cbe\u81f4\u7c89\u5ae9\uff0c\u5012\u662f\u6697\u81ea\u677e\u4e86\u53e3\u6c14\u3002\u773c\u5149\u4e0a\u79fb\uff0c\u9b45\u9b54\u4e00\u53cc\u7389\u817f\u5b8c\u5168\u88f8\u9732\u5728\u5916\uff0c\u7b14\u633a\u4fee\u957f\uff0c\u53ea\u5728\u5927\u817f\u6839\u90e8\u6709\u4e00\u5757\u9ed1\u8272\u5e03\u7247\u7a0d\u4f5c\u906e\u63a9\uff0c\u9971\u6ee1\u5f97\u4ee4\u4eba\u5fc3\u52a8\u3002\u4e0a\u8eab\u5219\u662f\u7d27\u675f\u7684\u9ed1\u8272\u76ae\u9769\u9a6c\u7532\uff0c\u8ba9\u80f8\u53e3\u6c9f\u58d1\u683c\u5916\u6df1\u6df1\u8bf1\u4eba\u3002\u4e00\u5934\u68d5\u8272\u6ce2\u6d6a\u5377\u53d1\u534a\u906e\u63a9\u7740\u8138\u86cb\uff0c\u743c\u6069\u7b2c\u4e00\u773c\u770b\u53bb\uff0c\u53ea\u89c9\u4f3c\u4e4e\u6709\u4e9b\u773c\u719f\uff0c\u4ed4\u7ec6\u518d\u770b\uff0c\u731b\u7136\u4e00\u4e2a\u540d\u5b57\u4ece\u8bb0\u5fc6\u4e2d\u8df3\u51fa\u6765\uff0c\u767b\u65f6\u5403\u4e86\u4e00\u60ca\u3002</p><p>\u201c\u838e\u73de\u514b\uff1f\u201d</p></article>"}}}]);
