(window.webpackJsonp=window.webpackJsonp||[]).push([[1514],{1537:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e8c\u8282 \u8bb0\u5fc6",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e8c\u8282 \u8bb0\u5fc6</h2><p>\u743c\u6069\u89c1\u8fc7\u7684\u5973\u6027\u4e0d\u5c11\uff0c\u838e\u73de\u514b\u7edd\u5bf9\u53ef\u4ee5\u7b97\u662f\u5370\u8c61\u6700\u6df1\u523b\u7684\u4e4b\u4e00\uff0c\u539f\u56e0\u5f88\u7b80\u5355\uff0c\u4ed6\u5f53\u65e5\u88ab\u6d3e\u9063\u53bb\u7ed9\u62c9\u6c83\u514b\u9001\u4fe1\uff0c\u5728\u70db\u5821\u9047\u4e0a\u8fd9\u4f4d\u5973\u6740\u624b\uff0c\u5148\u662f\u8272\u8bf1\u4e0a\u5e8a\uff0c\u63a5\u7740\u7ffb\u8138\u52a8\u624b\uff0c\u6700\u540e\u8fd8\u4e00\u8def\u8ddf\u8e2a\u8ffd\u6740\u5230\u4e86\u5de8\u9b54\u5c71\u8109\uff0c\u4eff\u4f5b\u6709\u4ec0\u4e48\u4e0d\u5171\u6234\u5929\u7684\u6df1\u4ec7\u5927\u6068\u4e00\u822c\u2014\u2014\u6700\u540e\u624d\u5f04\u660e\u767d\uff0c\u539f\u6765\u4e0d\u5173\u4ed6\u7684\u4e8b\uff0c\u4eba\u5bb6\u538b\u6839\u5c31\u662f\u51b2\u7740\u6885\u83f2\u65af\u6765\u7684\u3002</p><p>\u4f5c\u4e3a\u4e00\u53ea\u88ab\u65e0\u8f9c\u6b83\u53ca\u7684\u6c60\u9c7c\uff0c\u743c\u6069\u81ea\u7136\u5bf9\u838e\u73de\u514b\u6ca1\u4ec0\u4e48\u597d\u611f\uff0c\u800c\u4e14\u5979\u8fd8\u662f\u81ea\u5df1\u60c5\u4eba\u7684\u6b7b\u654c\u3002\u5979\u548c\u6885\u83f2\u65af\u90fd\u662f\u5df4\u5c14\u7684\u5973\u513f\uff0c\u5f7c\u6b64\u6b8b\u6740\u662f\u6ce8\u5b9a\u7684\u5bbf\u547d\u2014\u2014\u6885\u83f2\u65af\u4e0d\u80af\u7406\u776c\u8fd9\u4e2a\u5bbf\u547d\uff0c\u838e\u73de\u514b\u5374\u662f\u9075\u4ece\u7684\u3002</p><p>\u5f53\u65e5\u5728\u5de8\u9b54\u5c71\u8109\uff0c\u838e\u73de\u514b\u79d8\u5bc6\u52fe\u7ed3\u91d1\u773c\u4f7f\u8005\u79d1\u6069\u548c\u5815\u843d\u6b66\u50e7\u9686\u5947\uff0c\u9669\u4e9b\u6210\u529f\u5f97\u624b\uff0c\u540e\u6765\u88ab\u743c\u6069\u770b\u7834\uff0c\u6697\u4e2d\u4e0b\u9634\u624b\uff0c\u7528\u5f3a\u5236\u541e\u91d1\u6cd5\u5e72\u6389\u4e86\u6b66\u50e7\uff0c\u79d1\u6069\u4e5f\u88ab\u65a9\u6740\uff0c\u7834\u574f\u4e86\u8ba1\u5212\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u838e\u73de\u514b\u5374\u4e0d\u77e5\u4ece\u4f55\u5904\u501f\u6765\u4e86\u708e\u9b54\u4e4b\u529b\uff0c\u51e0\u4e4e\u6740\u4e86\u6885\u83f2\u65af\uff0c\u5e78\u597d\u5f97\u6b27\u51ef\u76f8\u52a9\uff0c\u7528\u5bd2\u51b0\u624b\u5957\u5413\u9000\u4e86\u708e\u9b54\uff0c\u5c01\u4f4f\u838e\u73de\u514b\uff0c\u505a\u6210\u4e86\u51b0\u96d5\u3002\u968f\u540e\u6885\u83f2\u65af\u6253\u7b97\u6740\u6b7b\u838e\u73de\u514b\uff0c\u5973\u6740\u624b\u5374\u5728\u51b0\u4e2d\u81ea\u71c3\uff0c\u70c8\u706b\u711a\u8eab\u800c\u6b7b\uff0c\u5979\u7684\u90a3\u4efd\u5df4\u5c14\u795e\u529b\u6d8c\u5165\u6885\u83f2\u65af\u4f53\u5185\uff0c\u9669\u4e9b\u5bfc\u81f4\u6740\u622e\u4e4b\u795e\u5f53\u573a\u590d\u6d3b\u3002</p><p>\u4e8b\u60c5\u867d\u7136\u8fc7\u53bb\u4e5f\u6709\u5c06\u8fd1\u4e00\u5e74\uff0c\u743c\u6069\u4f9d\u7136\u6ca1\u6709\u5fd8\u8bb0\u838e\u73de\u514b\u3002\u6bd5\u7adf\u8fd9\u662f\u4e00\u4e2a\u65e2\u548c\u4ed6\u4e0a\u8fc7\u5e8a\uff0c\u53c8\u6253\u7b97\u8981\u4ed6\u7684\u547d\uff0c\u6700\u540e\u8fd8\u5728\u4ed6\u9762\u524d\u4ee5\u81ea\u711a\u8fd9\u79cd\u534e\u4e3d\u65b9\u5f0f\u6302\u6389\u7684\u5973\u4eba\u3002\u4e0d\u7ba1\u662f\u8c01\u5982\u679c\u4eb2\u8eab\u7ecf\u5386\u8fd9\u4e00\u7cfb\u5217\u906d\u9047\uff0c\u76f8\u4fe1\u90fd\u5f88\u96be\u4ece\u8111\u6d77\u4e2d\u8f7b\u6613\u62b9\u6389\u3002\u53ea\u662f\u4eba\u65e2\u7136\u90fd\u5df2\u7ecf\u6b7b\u4e86\uff0c\u81ea\u7136\u4e5f\u5c31\u6ca1\u4ec0\u4e48\u597d\u60f3\u7684\u2014\u2014\u5374\u6ca1\u60f3\u5230\u65f6\u9694\u4e00\u5e74\uff0c\u5728\u65e0\u5c3d\u6df1\u6e0a\u4e4b\u4e2d\uff0c\u4ed6\u53c8\u770b\u5230\u4e86\u8fd9\u5f20\u719f\u6089\u7684\u9762\u5bb9\u3002</p><p>\u662f\u81ea\u5df1\u770b\u82b1\u773c\u4e86\uff0c\u8fd8\u662f\u957f\u5f97\u76f8\u4f3c\u800c\u5df2\uff1f</p><p>\u8fd9\u4e2a\u5ff5\u5934\u521a\u521a\u5347\u8d77\uff0c\u968f\u5373\u5c31\u88ab\u6392\u9664\u4e86\u3002\u957f\u76f8\u6216\u8bb8\u6709\u8fd1\u4f3c\u7684\uff0c\u6c14\u8d28\u603b\u4e0d\u4f1a\u4e5f\u4e00\u6a21\u4e00\u6837\uff0c\u4ec5\u4ee5\u5bb9\u8c8c\u800c\u8bba\uff0c\u838e\u73de\u514b\u7b97\u4e0d\u5f97\u975e\u5e38\u6f02\u4eae\uff0c\u4f46\u7709\u773c\u95f4\u5374\u81ea\u6709\u4e00\u79cd\u7279\u522b\u7684\u5996\u5a9a\uff0c\u52fe\u9b42\u593a\u9b44\uff0c\u8ba9\u4eba\u4e00\u89c1\u96be\u5fd8\uff0c\u51b3\u4e0d\u4f1a\u8ba4\u9519\uff0c\u4f55\u51b5\u743c\u6069\u548c\u5979\u8fd8\u662f\u6709\u8fc7\u201c\u4eb2\u5bc6\u63a5\u89e6\u201c\u7684\u3002\u800c\u4e14\u5979\u521a\u624d\u548c\u4f0a\u8389\u96c5\u7f20\u6597\u65f6\uff0c\u4e00\u624b\u957f\u97ad\u4e00\u624b\u77ed\u5251\uff0c\u5f53\u65f6\u8fd8\u4e0d\u89c9\u5f97\uff0c\u73b0\u5728\u56de\u60f3\u8d77\u6765\uff0c\u548c\u5979\u5f53\u65e5\u5728\u5de8\u9b54\u5c71\u8109\u7684\u8eab\u5f71\u5b8c\u5168\u91cd\u5408\uff0c\u5206\u660e\u5c31\u662f\u540c\u4e00\u4eba\u3002</p><p>\u4f46\u5979\u600e\u4e48\u53d8\u6210\u4e86\u9b45\u9b54\uff1f</p><p>\u201c\u838e\u73de\u514b\uff1f\u201d\u4ed6\u7591\u60d1\u5730\u95ee\u3002</p><p>\u9b45\u9b54\u4e5f\u5728\u770b\u7740\u4ed6\uff0c\u8138\u4e0a\u795e\u60c5\u6709\u4e9b\u5947\u602a\uff0c\u4eff\u4f5b\u7565\u5e26\u8ff7\u60d8\uff0c\u201c\u6211\u597d\u50cf\u5728\u54ea\u91cc\u89c1\u8fc7\u4f60\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u7684\u540d\u5b57\uff1f\u201d</p><p>\u4e0d\u4f1a\u5427\uff0c\u8bb0\u6027\u8fd9\u4e48\u7cdf\u7cd5\uff1f\u8fd9\u624d\u8fc7\u4e86\u4e0d\u5230\u4e00\u5e74\u800c\u5df2\u3002\u201c\u5170\u5c3c\u65af\u7279\uff0c\u6211\u4eec\u5728\u70db\u5821\u89c1\u8fc7\uff0c\u201d\u743c\u6069\u63d0\u9192\uff0c\u201c\u540e\u6765\u5728\u5de8\u9b54\u5c71\u8109\u2026\u2026\u201d</p><p>\u201c\u5de8\u9b54\u5c71\u8109\uff1f\u201d\u9b45\u9b54\u4f3c\u4e4e\u5bf9\u8fd9\u4e2a\u5730\u540d\u8fd8\u6709\u4e9b\u5370\u8c61\uff0c\u201c\u6211\u597d\u50cf\u662f\u66fe\u7ecf\u53bb\u8fc7\u90a3\u91cc\u2026\u2026\u201d</p><p>\u201c\u4f60\u8ba4\u8bc6\u5979\uff1f\u201d\u8475\u9732\u4e5f\u8d70\u8fc7\u6765\uff0c\u201c\u4f60\u7684\u670b\u53cb\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u6b63\u76f8\u53cd\uff0c\u662f\u654c\u4eba\uff0c\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u6211\u4ee5\u4e3a\u5979\u65e9\u5c31\u53bb\u4e16\u4e86\uff0c\u6ca1\u60f3\u5230\u53d8\u6210\u9b45\u9b54\u2026\u2026\u4e0d\u8fc7\u5979\u597d\u50cf\u4e0d\u8bb0\u5f97\u6211\u4e86\u3002\u201d</p><p>\u201c\u5f88\u6b63\u5e38\uff0c\u53d8\u6210\u6076\u9b54\u4e4b\u540e\uff0c\u51e1\u4eba\u65f6\u7684\u8bb0\u5fc6\u5c31\u4f1a\u6162\u6162\u6d88\u5931\u3002\u201d</p><p>\u201c\u5b8c\u5168\u6d88\u5931\uff1f\u201d</p><p>\u201c\u57fa\u672c\u90fd\u4f1a\u6d88\u5931\uff0c\u8fd9\u662f\u6df1\u6e0a\u7684\u6cd5\u5219\u6240\u51b3\u5b9a\u7684\uff0c\u9664\u975e\u5979\u79bb\u5f00\u8fd9\u4e2a\u4f4d\u9762\uff0c\u201d\u8475\u9732\u987f\u4e86\u987f\uff0c\u201c\u9664\u975e\u662f\u4e00\u4e9b\u7279\u522b\u5f3a\u70c8\u7684\u6267\u5ff5\uff0c\u6216\u8bb8\u4f1a\u4fdd\u7559\u4e0b\u6765\u3002\u201d</p><p>\u5f88\u663e\u7136\uff0c\u743c\u6069\u4e0d\u5c5e\u4e8e\u8fd9\u201c\u7279\u522b\u5f3a\u70c8\u7684\u6267\u5ff5\u201d\u4e4b\u4e00\uff0c\u8bf4\u4e0d\u5b9a\u5728\u838e\u73de\u514b\u5fc3\u4e2d\uff0c\u6839\u672c\u5c31\u662f\u7c7b\u4f3c\u8def\u4eba\u7532\u7684\u89d2\u8272\u7f62\u4e86\uff0c\u73b0\u5728\u8fd8\u80fd\u6709\u4e9b\u8bb8\u5370\u8c61\uff0c\u5df2\u7ecf\u5f88\u96be\u5f97\u4e86\u3002</p><p>\u201c\u6211\u6765\u548c\u5979\u4ea4\u6d89\uff0c\u201d\u8475\u9732\u8bf4\uff0c\u201c\u65e2\u7136\u4f60\u4eec\u66fe\u7ecf\u662f\u4ec7\u654c\uff0c\u5373\u4fbf\u5979\u5931\u53bb\u4e86\u5bf9\u4f60\u7684\u8bb0\u5fc6\uff0c\u4f46\u6f5c\u610f\u8bc6\u91cc\u7684\u53cd\u611f\u662f\u4e0d\u4f1a\u6d88\u9664\u7684\uff0c\u5979\u4f1a\u672c\u80fd\u5730\u6392\u65a5\u4f60\u3002\u201d</p><p>\u5979\u540e\u9762\u8fd8\u6709\u534a\u53e5\u8bdd\u6ca1\u8bf4\u51fa\u6765\uff1a\u4f60\u662f\u7537\u6027\u3002</p><p>\u743c\u6069\u660e\u767d\u5979\u7684\u610f\u601d\uff0c\u4e5f\u5b8c\u5168\u8d5e\u540c\u8fd9\u70b9\uff0c\u6240\u4ee5\u4ed6\u9000\u5f00\u4e86\uff0c\u628a\u9b45\u9b54\u4ea4\u7ed9\u8475\u9732\u3002\u4f0a\u8389\u96c5\u8fd8\u5728\u4e3a\u521a\u624d\u743c\u6069\u7684\u77f3\u722a\u672f\u628a\u5979\u4e00\u8d77\u5c01\u4f4f\u7684\u4e8b\u60c5\u800c\u4e0d\u9ad8\u5174\uff0c\u6485\u7740\u5634\u8dd1\u5230\u963f\u5fd2\u59ae\u90a3\u8fb9\uff0c\u5e2e\u5979\u903c\u95ee\u90a3\u53ea\u4e0d\u5e78\u7684\u5938\u8d5b\u9b54\u3002\u743c\u6069\u8d70\u56de\u6885\u83f2\u65af\u8eab\u8fb9\uff0c\u8ba9\u5979\u9760\u5728\u81ea\u5df1\u6000\u91cc\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u6885\u83f2\u65af\u8f7b\u8f7b\u95ee\uff0c\u5979\u770b\u8d77\u6765\u66f4\u52a0\u865a\u5f31\u4e86\uff0c\u800c\u4e14\u542c\u89c9\u4e5f\u660e\u663e\u4e0d\u5982\u4ee5\u524d\u654f\u9510\uff0c\u53ea\u77e5\u9053\u521a\u624d\u90a3\u8fb9\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f46\u5177\u4f53\u5e76\u4e0d\u6e05\u695a\uff0c\u201c\u90a3\u4e2a\u9b45\u9b54\u6709\u4ec0\u4e48\u95ee\u9898\uff1f\u201d</p><p>\u201c\u6211\u4eec\u9047\u5230\u8001\u670b\u53cb\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5979\u662f\u838e\u73de\u514b\uff0c\u90a3\u4e2a\u5973\u6740\u624b\u3002\u201d</p><p>\u201c\u5979\u53d8\u6210\u4e86\u9b45\u9b54\uff1f\u201d</p><p>\u201c\u770b\u8d77\u6765\u60c5\u51b5\u662f\u5982\u6b64\u3002\u201d</p><p>\u201c\u5979\u8fd8\u8bb0\u5f97\u4f60\u5417\uff1f\u201d\u6885\u83f2\u65af\u95ee\uff0c\u5979\u663e\u7136\u4e5f\u77e5\u9053\u51e1\u4eba\u6210\u4e3a\u6076\u9b54\uff0c\u4ee5\u5f80\u7684\u8bb0\u5fc6\u4f1a\u9010\u6e10\u6d88\u9664\u7684\u77e5\u8bc6\u3002</p><p>\u201c\u8fd8\u6709\u70b9\u5370\u8c61\uff0c\u4f46\u4e0d\u592a\u6e05\u695a\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u9ed8\u9ed8\u601d\u7d22\u4e86\u7247\u523b\uff0c\u201c\u62b1\u6211\u8fc7\u53bb\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u53bb\u770b\u770b\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u8be7\u5f02\uff0c\u4f46\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\uff0c\u5c06\u5979\u62b1\u8d77\uff0c\u53c8\u8d70\u4e86\u56de\u6765\u3002\u8475\u9732\u548c\u9b45\u9b54\u6b63\u5728\u4ea4\u6d89\uff0c\u4f46\u663e\u7136\u8fdb\u5c55\u5e76\u4e0d\u987a\u5229\uff0c\u4e8b\u5b9e\u8bc1\u660e\u4e24\u4e2a\u5973\u4eba\u5728\u4e00\u8d77\uff0c\u66f4\u5bb9\u6613\u4ea7\u751f\u4e89\u6267\u548c\u51b2\u7a81\u3002\u8fd9\u8ba9\u743c\u6069\u60f3\u8d77\u4e0a\u8f88\u5b50\uff0c\u966a\u5973\u53cb\u53bb\u8d2d\u7269\uff0c\u5973\u53cb\u603b\u662f\u4f1a\u523b\u610f\u56de\u907f\u90a3\u4e9b\u7531\u5973\u6027\u7ecf\u8425\u7684\u5546\u5e97\uff0c\u8bf4\u5979\u4eec\u96be\u4ee5\u6253\u4ea4\u9053\u3002</p><p>\u8475\u9732\u662f\u9a84\u50b2\u7684\uff0c\u5f53\u7136\u5979\u4e5f\u6709\u9a84\u50b2\u7684\u8d44\u672c\uff0c\u4efb\u4f55\u4eba\u957f\u4e45\u5730\u5750\u5728\u5979\u8fd9\u4e2a\u4f4d\u7f6e\u4e0a\uff0c\u81ea\u7136\u800c\u7136\u4fbf\u4f1a\u6709\u4e00\u79cd\u5c45\u9ad8\u4e34\u4e0b\u7684\u59ff\u6001\u2014\u2014\u66f4\u4f55\u51b5\u73b0\u5728\u5979\u662f\u80dc\u5229\u8005\u3002\u7136\u800c\u9b45\u9b54\u4f3c\u4e4e\u4e5f\u5168\u65e0\u9636\u4e0b\u4e4b\u56da\u7684\u81ea\u89c9\uff0c\u4eff\u4f5b\u6709\u6043\u65e0\u6050\u4e00\u822c\u3002</p><p>\u201c\u6211\u4e50\u610f\u63a5\u53d7\u4e00\u9879\u5e73\u7b49\u7684\u4ea4\u6613\uff0c\u4f46\u4e0d\u4f1a\u7406\u776c\u865a\u5f20\u58f0\u52bf\u7684\u606b\u5413\uff0c\u201d\u9b45\u9b54\u7528\u6175\u61d2\u7684\u58f0\u97f3\u8bf4\uff0c\u62ac\u624b\u5c06\u56e0\u4e3a\u6218\u6597\u800c\u6709\u4e9b\u6563\u4e71\u7684\u5377\u53d1\u62c2\u5230\u80a9\u540e\uff0c\u5979\u867d\u7136\u8db3\u8155\u88ab\u7d27\u7d27\u6263\u4f4f\uff0c\u53ea\u80fd\u7ad9\u5728\u539f\u5730\u4e0d\u80fd\u52a8\u5f39\uff0c\u4f46\u4f9d\u65e7\u98ce\u60c5\u4e07\u79cd\uff0c\u4e00\u4e9b\u8f7b\u5fae\u7684\u4e3e\u52a8\u90fd\u900f\u51fa\u8ff7\u4eba\u7684\u9b45\u529b\uff0c\u201c\u4f60\u5e76\u4e0d\u6bd4\u6211\u5360\u4f18\u52bf\uff0c\u5c0f\u59d0\uff0c\u73b0\u5728\u6211\u4eec\u7684\u5904\u5883\u76f8\u540c\u3002\u201d</p><p>\u201c\u5979\u79f0\u547c\u8475\u9732\u4e3a\u5c0f\u59d0\u3002\u201d\u6885\u83f2\u65af\u6084\u58f0\u8bf4\u3002</p><p>\u201c\u55ef\uff0c\u8fd9\u600e\u4e48\u4e86\u3002\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u5c0f\u59d0\u662f\u5f88\u5e73\u5e38\u7684\u79f0\u547c\u2014\u2014\u5728\u8fd9\u4e2a\u4e16\u754c\u5c24\u5176\u5e73\u5e38\uff0c\u8fde\u67d0\u79cd\u6b67\u4e49\u90fd\u6ca1\u6709\u3002</p><p>\u201c\u901a\u5e38\u6765\u8bf4\uff0c\u4e0b\u5c42\u754c\u7684\u6076\u9b54\u548c\u9b54\u9b3c\u4f1a\u4e60\u60ef\u4ee5\u2018\u51e1\u4eba\u2019\u6765\u79f0\u547c\u7269\u8d28\u754c\u7684\u751f\u7075\uff0c\u5982\u679c\u8d2c\u79f0\u7684\u8bdd\u5c31\u662f\u2018\u5df4\u4f6c\u2019\uff0c\u56e0\u4e3a\u5b83\u4eec\u5e76\u4e0d\u5c06\u81ea\u5df1\u548c\u6211\u4eec\u89c6\u4e3a\u540c\u7c7b\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u5f88\u591a\u6076\u9b54\u548c\u9b54\u9b3c\u5176\u5b9e\u90fd\u66fe\u7ecf\u662f\u51e1\u4eba\uff0c\u4f46\u5b83\u4eec\u7684\u8bb0\u5fc6\u88ab\u4f4d\u9762\u89c4\u5219\u9010\u6e10\u6cef\u706d\u4e86\uff0c\u201d</p><p>\u743c\u6069\u660e\u767d\u4e86\u5979\u7684\u610f\u601d\u3002</p><p>\u4ece\u8fd9\u4e2a\u7b80\u5355\u7684\u79f0\u547c\uff0c\u53ef\u4ee5\u5224\u65ad\u51fa\u838e\u73de\u514b\u8fd8\u5e76\u6ca1\u6709\u5b8c\u5168\u88ab\u6df1\u6e0a\u6240\u540c\u5316\uff0c\u6210\u4e3a\u771f\u6b63\u7684\u6076\u9b54\u3002\u5979\u4f9d\u65e7\u6b8b\u7559\u7740\u4e00\u4e9b\u51e1\u4eba\u7684\u8bb0\u5fc6\u548c\u4e60\u60ef\u3002\u5979\u5bf9\u743c\u6069\u8fd8\u6709\u70b9\u5370\u8c61\uff0c\u5bf9\u5de8\u9b54\u5c71\u8109\u8fd9\u4e2a\u5730\u540d\u8fd8\u6709\u53cd\u5e94\u2026\u2026\u5979\u5e94\u8be5\u8fd8\u4f1a\u8bb0\u5f97\u6885\u83f2\u65af\u5427\u3002</p><p>\u53ea\u4e0d\u8fc7\uff0c\u5979\u4e3a\u4ec0\u4e48\u8bf4\u201c\u73b0\u5728\u6211\u4eec\u7684\u5904\u5883\u76f8\u540c\u201d\u5462\uff1f\u4f5c\u4e3a\u4e00\u4f4d\u806a\u660e\u7684\u9b45\u9b54\uff0c\u5979\u96be\u9053\u4e0d\u660e\u767d\u73b0\u5728\u5979\u5df2\u7ecf\u662f\u9636\u4e0b\u4e4b\u56da\uff0c\u6027\u547d\u5b8c\u5168\u64cd\u7eb5\u5728\u743c\u6069\u7b49\u4eba\u624b\u4e2d\u3002\u8bb0\u5fc6\u6cef\u706d\uff0c\u603b\u4e0d\u81f3\u4e8e\u667a\u5546\u4e5f\u4f1a\u964d\u4f4e\u5427\u3002\u5c31\u8fde\u65c1\u8fb9\u90a3\u53ea\u6700\u5dee\u52b2\u7684\u5938\u8d5b\u9b54\u90fd\u5df2\u7ecf\u6e05\u695a\u610f\u8bc6\u5230\u8fd9\u4e00\u70b9\uff0c\u5bf9\u963f\u5fd2\u59ae\u7684\u95ee\u9898\u662f\u6709\u95ee\u5fc5\u7b54\u2014\u2014\u552f\u4e00\u7684\u95ee\u9898\u53ea\u5728\u4e8e\uff0c\u5b83\u7684\u5730\u4f4d\u592a\u4f4e\uff0c\u89c1\u8bc6\u4e5f\u4e0d\u591f\uff0c\u5f80\u5f80\u8bf4\u7740\u8bf4\u7740\u5c31\u5f00\u59cb\u4e60\u60ef\u6027\u80e1\u4e71\u5439\u5618\u81ea\u5df1\u7f16\u9020\uff0c\u57fa\u672c\u6ca1\u80fd\u63d0\u4f9b\u4ec0\u4e48\u6709\u4ef7\u503c\u7684\u4fe1\u606f\u3002</p><p>\u201c\u4f60\u7684\u751f\u6b7b\u73b0\u5728\u638c\u63e1\u5728\u6211\u7684\u624b\u4e2d\uff0c\u201d\u8475\u9732\u51b7\u7b11\u7740\uff0c\u4e00\u56e2\u84dd\u767d\u76f8\u95f4\u7684\u706b\u7130\u4ece\u5979\u638c\u5fc3\u5f39\u51fa\uff0c\u53d8\u5e7b\u7740\u5404\u79cd\u5371\u9669\u7684\u5f62\u6001\uff0c\u201c\u6216\u8bb8\u4f60\u6253\u7b97\u8bd5\u8bd5\uff1f\u201d</p><p>\u9b45\u9b54\u4e0d\u8ba4\u8bc6\u94f6\u706b\uff0c\u4f46\u5979\u80fd\u6e05\u695a\u5730\u610f\u8bc6\u5230\u5b83\u5176\u4e2d\u8574\u542b\u7684\u795e\u5723\u529b\u91cf\uff0c\u8fd9\u6b63\u662f\u6240\u6709\u4e0b\u5c42\u754c\u90aa\u9b54\u7684\u514b\u661f\u3002\u5982\u679c\u88ab\u5b83\u70e7\u8fc7\uff0c\u81ea\u5df1\u5c31\u4f1a\u5f7b\u5e95\u4ece\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6d88\u5931\uff0c\u8fde\u4e00\u4e1d\u6b8b\u6e23\u90fd\u4e0d\u4f1a\u7559\u4e0b\uff0c\u5c31\u7b97\u662f\u6700\u5f3a\u5927\u7684\u6076\u9b54\u4e5f\u4e0d\u53ef\u80fd\u5c06\u5979\u590d\u6d3b\u3002</p><p>\u4f46\u5979\u4f9d\u65e7\u5fae\u7b11\u7740\u3002</p><p>\u201c\u6211\u7684\u751f\u6b7b\u786e\u5b9e\u638c\u63e1\u5728\u4f60\u4eec\u624b\u4e2d\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u540c\u6837\u7684\uff0c\u4f60\u4eec\u7684\u751f\u6b7b\u4e5f\u638c\u63e1\u5728\u6211\u7684\u624b\u4e2d\u3002\u6ca1\u6709\u6211\u7684\u5e2e\u52a9\uff0c\u4f60\u4eec\u4f1a\u6bd4\u6211\u6d3b\u5f97\u66f4\u957f\u4e00\u70b9\u2014\u2014\u4e5f\u4ec5\u4ec5\u662f\u4e00\u70b9\u3002\u201d</p><p>\u201c\u4f60\u592a\u9ad8\u4f30\u81ea\u5df1\u7684\u4ef7\u503c\u4e86\uff0c\u201d\u8475\u9732\u8bf4\uff0c\u4e00\u8fb9\u5c06\u638c\u5fc3\u4e2d\u7684\u94f6\u706b\u8d34\u8fd1\u4e86\u9b45\u9b54\uff0c\u201c\u77e5\u9053\u65ad\u57df\u9547\u65b9\u4f4d\u7684\u6076\u9b54\u53ef\u4e0d\u6b62\u4f60\u4e00\u4e2a\u3002\u201d</p><p>\u201c\u6709\u5f88\u591a\uff0c\u4f46\u4f60\u4eec\u627e\u4e0d\u5230\u5b83\u4eec\uff0c\u201d\u9b45\u9b54\u683c\u683c\u7b11\u7740\uff0c\u201c\u5f88\u663e\u7136\u4f60\u4eec\u5df2\u7ecf\u5bfb\u627e\u4e86\u5f88\u4e45\uff0c\u4f46\u4e00\u65e0\u6240\u83b7\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u8840\u6218\u53c8\u5f00\u59cb\u4e86\uff1f\u201d\u8475\u9732\u8bd5\u63a2\u5730\u95ee\u3002</p><p>\u201c\u65e0\u53ef\u5949\u544a\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u4f46\u53e6\u5916\u6709\u4e00\u4e2a\u6d88\u606f\u53ef\u4ee5\u514d\u8d39\u8d60\u9001\uff1a\u5982\u679c\u4f60\u4eec\u6740\u6b7b\u4e86\u6211\uff0c\u90a3\u4e48\u5f88\u5feb\u5c31\u4f1a\u6709\u4eba\u6765\u6740\u6b7b\u4f60\u4eec\u2014\u2014\u76f8\u4fe1\u6211\uff0c\u5f88\u5feb\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u65d7\u5b50\u5f88\u5947\u602a\u5462\uff0c\u201d\u65c1\u8fb9\u7a81\u7136\u4f20\u6765\u4f0a\u8389\u96c5\u7684\u58f0\u97f3\uff0c\u5979\u4e0d\u77e5\u4f55\u65f6\u722c\u5230\u4e86\u7b2c\u4e00\u8f86\u8f66\u4e0a\uff0c\u5c06\u90a3\u9762\u65d7\u5b50\u62d4\u4e86\u4e0b\u6765\uff0c\u201c\u4e0a\u9762\u753b\u7740\u4e2a\u597d\u96be\u770b\u7684\u624b\u638c\u3002\u201d</p><p>\u624b\u638c\uff1f</p><p>\u5979\u7ad9\u5728\u8f66\u4e0a\uff0c\u5c06\u65d7\u5e1c\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u5c55\u5f00\u7ed9\u5927\u5bb6\u770b\u3002\u767d\u8272\u7684\u5e03\u9762\u4e0a\uff0c\u753b\u7740\u4e00\u53ea\u9ed1\u8272\u7684\u624b\u638c\uff0c\u8fb9\u7f18\u7528\u94f6\u7ebf\u52fe\u52d2\uff0c\u4e4b\u6240\u4ee5\u8bf4\u5947\u602a\uff0c\u662f\u56e0\u4e3a\u5b83\u662f\u7578\u5f62\u7684\uff0c\u6709\u516d\u4e2a\u624b\u6307\u3002</p><p>\u201c\u683c\u62c9\u5179\u7279\uff0c\u201d\u6885\u83f2\u65af\u4f4e\u58f0\u8bf4\uff0c\u201c\u683c\u62c9\u5179\u7279\u7684\u5fbd\u8bb0\u3002\u201d</p><p>\u8475\u9732\u663e\u7136\u4e5f\u8ba4\u51fa\u4e86\u8fd9\u4e2a\u6807\u5fd7\uff0c\u5979\u8138\u4e0a\u95ea\u8fc7\u4e00\u4e1d\u60ca\u8bb6\uff0c\u4f46\u968f\u5373\u6062\u590d\u5982\u521d\uff0c\u201c\u539f\u6765\u4f60\u4e3a\u4e00\u4f4d\u8457\u540d\u7684\u6076\u9b54\u9886\u4e3b\u6548\u529b\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f46\u6211\u76f8\u4fe1\u4e00\u53ea\u5c0f\u5c0f\u7684\u9b45\u9b54\uff0c\u8fd8\u4e0d\u4f1a\u653e\u5728\u4e4c\u9eef\u541b\u4e3b\u7684\u5fc3\u4e0a\u3002\u201d</p><p>\u201c\u90a3\u662f\u5f53\u7136\u7684\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u4ed6\u751a\u81f3\u4e0d\u8bb0\u5f97\u6211\u7684\u540d\u5b57\u3002\u4e4c\u9eef\u541b\u4e3b\u7684\u624b\u4e0b\u6709\u6210\u5343\u4e0a\u4e07\u7684\u9b45\u9b54\uff0c\u591a\u4e00\u4e2a\u5c11\u4e00\u4e2a\u6839\u672c\u65e0\u6240\u8c13\u2014\u2014\u4f46\u5982\u679c\u4ed6\u6240\u5173\u6ce8\u7684\u4e00\u6279\u91cd\u8981\u7269\u54c1\uff0c\u56e0\u4e3a\u67d0\u51e0\u4e2a\u7269\u8d28\u754c\u51e1\u4eba\u7684\u963b\u6320\u800c\u6ca1\u6709\u6309\u65f6\u62b5\u8fbe\u76ee\u7684\u5730\uff0c\u90a3\u4e48\u4f60\u89c9\u5f97\u4ed6\u4f1a\u4e0d\u4f1a\u56e0\u6b64\u5927\u53d1\u96f7\u9706\u5462\u3002\u201d</p><p>\u8475\u9732\u5fae\u5fae\u76b1\u7709\uff0c\u201c\u4f60\u8ba4\u4e3a\u4ec5\u51ed\u4e00\u9762\u65d7\u5b50\u5c31\u80fd\u4f5c\u4e3a\u62a4\u8eab\u7b26\u5417\uff1f\u201d\u5979\u8bf4\uff0c\u201c\u627e\u4e00\u5757\u5e03\uff0c\u5728\u4e0a\u9762\u8bdd\u4e00\u4e2a\u516d\u6307\u624b\u638c\uff0c\u8fd9\u53ea\u9700\u8981\u82b1\u4e0a\u4e94\u5206\u949f\uff0c\u8c01\u90fd\u80fd\u529e\u5230\u3002\u201d</p><p>\u201c\u4f46\u90a3\u4e9b\u65d7\u5b50\u80fd\u591f\u8ba9\u4f60\u7a7f\u884c\u6574\u4e2a\u4e07\u6e0a\u5e73\u539f\u800c\u7545\u901a\u65e0\u963b\u5417\uff1f\u201d\u9b45\u9b54\u53cd\u95ee\uff0c\u201c\u770b\u770b\u5929\u4e0a\u7684\u90a3\u4e9b\u5f17\u6d1b\u9b54\uff0c\u5b83\u4eec\u662f\u5982\u6b64\u7684\u9965\u6e34\u548c\u8feb\u4e0d\u53ca\u5f85\uff0c\u65f6\u523b\u7b49\u5f85\u7740\u6355\u98df\u730e\u7269\u3002\u6211\u4eec\u53ea\u6709\u516d\u4e2a\uff0c\u4eba\u6570\u7a00\u5c11\uff0c\u5b9e\u529b\u5f31\u5c0f\uff0c\u5f17\u6d1b\u9b54\u8981\u6740\u6b7b\u6211\u4eec\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\u2014\u2014\u4f46\u4f60\u8ba4\u4e3a\u5b83\u4eec\u56e0\u4e3a\u4ec0\u4e48\u7406\u7531\u800c\u514b\u5236\uff0c\u770b\u7740\u6211\u4eec\u5927\u6447\u5927\u6446\u5730\u5728\u9762\u524d\u8d70\u8fc7\u5462\uff1f\u201d</p><p>\u8475\u9732\u6c89\u9ed8\u7740\uff0c\u5979\u77e5\u9053\u5bf9\u65b9\u8bf4\u5f97\u662f\u4e8b\u5b9e\uff0c\u6b64\u65f6\u963f\u5fd2\u59ae\u4e5f\u5df2\u7ecf\u653e\u5f03\u4e86\u5bf9\u5938\u8d5b\u9b54\u7684\u8baf\u95ee\uff0c\u9010\u4e2a\u68c0\u67e5\u8f66\u4e0a\u88c5\u8f7d\u7684\u8d27\u7269\uff0c\u5168\u90fd\u662f\u4ece\u672a\u89c1\u8fc7\u7684\u5947\u602a\u7269\u54c1\uff0c\u770b\u4e0d\u51fa\u7528\u9014\uff0c\u4f46\u6beb\u65e0\u7591\u95ee\u90fd\u8574\u542b\u7740\u5f3a\u5927\u800c\u90aa\u6076\u7684\u529b\u91cf\uff0c\u5e94\u8be5\u662f\u4e13\u95e8\u4e3a\u6076\u9b54\u6240\u8bbe\u8ba1\uff0c\u4ece\u4fa7\u9762\u8fdb\u4e00\u6b65\u8bc1\u5b9e\u4e86\u9b45\u9b54\u6240\u8bf4\u7684\u771f\u5b9e\u6027\u3002</p><p>\u201c\u53ea\u6709\u771f\u6b63\u662f\u4e4c\u9eef\u541b\u4e3b\u4eb2\u81ea\u6388\u4e88\u7684\u65d7\u53f7\uff0c\u624d\u80fd\u591f\u5728\u4e07\u6e0a\u5e73\u539f\u5b89\u5168\u901a\u884c\uff0c\u8fd9\u662f\u6bcf\u4e2a\u6076\u9b54\u90fd\u6e05\u695a\u7684\u5e38\u8bc6\uff0c\u201d\u9b45\u9b54\u7b11\u541f\u541f\u5730\u8bf4\uff0c\u201c\u5f53\u7136\uff0c\u4f60\u4eec\u660e\u663e\u7f3a\u4e4f\u8fd9\u79cd\u5e38\u8bc6\u3002\u201d</p><p>\u5c40\u9762\u53d8\u5f97\u5fae\u5999\u8d77\u6765\uff0c\u8bda\u5982\u9b45\u9b54\u6240\u8bf4\uff0c\u743c\u6069\u7b49\u4eba\u53ef\u4ee5\u8f7b\u6613\u5730\u6740\u6b7b\u5979\uff1b\u4f46\u5982\u679c\u771f\u6740\u6b7b\u4e86\u5979\uff0c\u65e2\u5931\u53bb\u4e86\u76ee\u524d\u552f\u4e00\u80fd\u627e\u5230\u7684\u5411\u5bfc\uff0c\u53c8\u8981\u5192\u7740\u89e6\u6012\u683c\u62c9\u5179\u7279\u7684\u5371\u9669\u3002\u8475\u9732\u662f\u9b54\u6cd5\u5973\u795e\u548c\u5e7d\u6697\u5c11\u5973\u7684\u9009\u6c11\uff0c\u5979\u81ea\u7136\u4e0d\u4f1a\u9762\u5bf9\u4efb\u4f55\u90aa\u6076\u800c\u9000\u7f29\uff0c\u4f46\u95ee\u9898\u662f\u6709\u6ca1\u6709\u5fc5\u8981\u8fd9\u4e48\u505a\u2014\u2014\u6709\u6ca1\u6709\u5fc5\u8981\u5728\u6df1\u6e0a\u4e4b\u4e2d\uff0c\u5728\u6025\u4e8e\u627e\u5230\u56de\u7269\u8d28\u754c\u7684\u8def\u5f84\u7684\u65f6\u5019\uff0c\u5f97\u7f6a\u6df1\u6e0a\u4e09\u5927\u6076\u9b54\u9886\u4e3b\u4e4b\u4e00\u3002</p><p>\u539f\u672c\u7684\u8ba1\u5212\uff0c\u662f\u627e\u5230\u4e00\u4f4d\u6076\u9b54\uff0c\u903c\u8feb\u4ed6\u9886\u8def\uff0c\u5e26\u4ed6\u4eec\u53bb\u65ad\u57df\u9547\u3002\u8c01\u6599\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6076\u9b54\u4eec\u4e00\u4e2a\u4e2a\u5168\u90fd\u6ca1\u4e86\u5f71\u5b50\uff0c\u6839\u672c\u627e\u4e0d\u5230\u4eba\uff1b\u597d\u4e0d\u5bb9\u6613\u649e\u4e0a\u4e86\u4e00\u4e2a\u9b45\u9b54\uff0c\u5c45\u7136\u8fd8\u662f\u683c\u62c9\u5179\u7279\u7684\u624b\u4e0b\uff0c\u8fd9\u8fd0\u6c14\u5b9e\u5728\u662f\u7cdf\u7cd5\u5f97\u5f88\u3002</p><p>\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u6211\u4eec\u786e\u5b9e\u5904\u5883\u76f8\u540c\uff0c\u201d\u6885\u83f2\u65af\u7a81\u7136\u8bf4\uff0c\u201c\u800c\u4e14\u6211\u5b8c\u5168\u8d5e\u6210\u4f60\u7684\u770b\u6cd5\uff0c\u5e73\u7b49\u7684\u4ea4\u6613\u624d\u662f\u6b63\u786e\u7684\u9009\u62e9\u3002\u201d</p><p>\u8475\u9732\u548c\u9b45\u9b54\u9488\u950b\u76f8\u5bf9\u65f6\uff0c\u743c\u6069\u4e3a\u4e86\u4e0d\u6253\u6270\u5979\u4eec\uff0c\u62b1\u7740\u6885\u83f2\u65af\u8fdc\u8fdc\u7ad9\u7740\uff0c\u6709\u4e00\u6bb5\u8ddd\u79bb\u3002\u9b45\u9b54\u4e00\u76f4\u6ca1\u6709\u6ce8\u610f\u5230\u6885\u83f2\u65af\uff0c\u7a81\u7136\u542c\u89c1\u8bf4\u8bdd\uff0c\u4e8e\u662f\u8f6c\u8fc7\u5934\u6765\u3002\u7136\u540e\u5979\u660e\u663e\u5730\u6014\u4f4f\u4e86\uff0c\u539f\u672c\u5e73\u9759\u81ea\u82e5\u7684\u8138\u4e0a\u5239\u90a3\u95f4\u6d8c\u4e0a\u4e86\u4e00\u79cd\u590d\u6742\u7684\u795e\u60c5\uff0c\u4eff\u4f5b\u81ea\u5df1\u90fd\u65e0\u6cd5\u63a7\u5236\u4e00\u822c\u3002</p><p>\u201c\u6885\u83f2\u65af\uff01\u201d\u5979\u96be\u4ee5\u6291\u5236\u5730\u5927\u543c\u8d77\u6765\u3002</p><p>\u201c\u771f\u9ad8\u5174\u4f60\u8fd8\u8bb0\u5f97\u6211\uff0c\u838e\u73de\u514b\uff0c\u201d\u6885\u83f2\u65af\u5fae\u7b11\u7740\uff0c\u201c\u90a3\u4e48\uff0c\u6211\u4eec\u53ef\u4ee5\u8c08\u8c08\u5417\uff1f\u201d</p></article>"}}}]);
