(window.webpackJsonp=window.webpackJsonp||[]).push([[1518],{1541:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u516d\u8282 \u9677\u9631",content:"<article><h2>\u7b2c\u4e8c\u5341\u516d\u8282 \u9677\u9631</h2><p>\u65ad\u57df\u9547\u5176\u5b9e\u66f4\u50cf\u662f\u4e00\u5ea7\u519b\u4e8b\u8981\u585e\uff0c\u9ad8\u8038\u539a\u91cd\u7684\u57ce\u5899\u5708\u6210\u5706\u73af\uff0c\u4fdd\u62a4\u7740\u51e0\u5341\u5ea7\u4eff\u4f5b\u788e\u77f3\u5792\u780c\u800c\u6210\u7684\u9ad8\u5854\uff0c\u5168\u526f\u6b66\u88c5\u7684\u6076\u9b54\u5728\u4e0a\u9762\u5de1\u903b\u8d70\u52a8\u3002\u57ce\u95e8\u536b\u5175\u662f\u4e24\u53ea\u72c2\u6218\u9b54\uff0c\u838e\u73de\u514b\u5411\u5b83\u4eec\u5c55\u793a\u4e86\u683c\u62c9\u5179\u7279\u7684\u65d7\u5e1c\uff0c\u7136\u540e\u6beb\u65e0\u7559\u96be\u5730\u4fbf\u8fdb\u4e86\u9547\u5b50\u3002</p><p>\u548c\u65ad\u57df\u9547\u5916\u7684\u8352\u829c\u5e73\u539f\u622a\u7136\u76f8\u53cd\u7684\u662f\uff0c\u9547\u4e2d\u70ed\u95f9\u975e\u51e1\uff0c\u8857\u9053\u4e0a\u5230\u5904\u90fd\u662f\u6765\u6765\u5f80\u5f80\u7684\u6076\u9b54\uff0c\u5404\u79cd\u5404\u6837\u7684\u90fd\u6709\uff0c\u5947\u5f62\u602a\u72b6\uff0c\u72f0\u72de\u53ef\u6016\uff0c\u4f46\u6c14\u6c1b\u5374\u4f3c\u4e4e\u5f88\u597d\u5f88\u548c\u8c10\uff0c\u5f7c\u6b64\u95f4\u90fd\u4fdd\u6301\u7740\u76f8\u5f53\u9650\u5ea6\u7684\u514b\u5236\u548c\u793c\u8ba9\uff0c\u770b\u5230\u743c\u6069\u7b49\u4eba\u8fdb\u6765\uff0c\u867d\u7136\u90fd\u6709\u4e9b\u60ca\u8bb6\uff0c\u4f46\u5374\u6ca1\u6709\u534a\u4e2a\u4e0a\u6765\u653b\u51fb\u6216\u8005\u6311\u8845\uff0c\u4e0d\u8fc7\u662f\u8fdc\u8fdc\u770b\u770b\u6216\u8005\u5e72\u8106\u4e0d\u5c51\u4e00\u987e\u3002\u57ce\u95e8\u5927\u9053\u4e24\u65c1\u662f\u4e00\u773c\u671b\u4e0d\u5230\u5c3d\u5934\u7684\u5546\u94fa\uff0c\u6574\u6574\u9f50\u9f50\uff0c\u987e\u5ba2\u4f17\u591a\uff0c\u743c\u6069\u8def\u8fc7\u65f6\u968f\u610f\u77a5\u4e86\u4e00\u773c\uff0c\u53d1\u73b0\u5356\u7684\u5168\u90fd\u662f\u4ed6\u53eb\u4e0d\u51fa\u540d\u5b57\u7684\u7a00\u5947\u53e4\u602a\u7684\u4e1c\u897f\uff0c\u548c\u838e\u73de\u514b\u62bc\u8fd0\u7684\u8fd9\u51e0\u8f66\u8d27\u7269\u5012\u5e94\u8be5\u662f\u540c\u7c7b\u3002</p><p>\u201c\u5148\u53bb\u8001\u5934\u9aa8\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u90a3\u91cc\u662f\u65ad\u57df\u9547\u4e2d\u6700\u5927\u7684\u65c5\u5e97\u3002\u201d</p><p>\u5176\u4ed6\u4eba\u81ea\u7136\u6ca1\u6709\u5f02\u8bae\u3002</p><p>\u201c\u8fd9\u91cc\u548c\u6211\u60f3\u50cf\u4e2d\u7684\u6076\u9b54\u57ce\u9547\u5b8c\u5168\u4e0d\u540c\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5012\u548c\u4eba\u7c7b\u4e16\u754c\u5dee\u4e0d\u591a\u3002\u201d</p><p>\u201c\u6211\u8bf4\u4e86\uff0c\u65ad\u57df\u9547\u662f\u6574\u4e2a\u4e07\u6e0a\u5e73\u539f\u76f8\u5bf9\u6700\u6709\u2018\u79e9\u5e8f\u2019\u7684\u6240\u5728\uff0c\u201d\u838e\u73de\u514b\u56de\u7b54\uff0c\u4e00\u8fb9\u6307\u6325\u7740\u9a6e\u517d\u6539\u53d8\u65b9\u5411\uff0c\u201c\u4f60\u770b\u770b\u4f60\u7684\u5c0f\u60c5\u4eba\u5c31\u77e5\u9053\u3002\u201d</p><p>\u743c\u6069\u4e0d\u770b\u4e5f\u77e5\u9053\uff0c\u81ea\u4ece\u8fdb\u5165\u65ad\u57df\u9547\u4e4b\u540e\uff0c\u6885\u83f2\u65af\u7684\u60c5\u51b5\u786e\u5b9e\u5927\u6709\u597d\u8f6c\uff0c\u5979\u539f\u672c\u5df2\u7ecf\u865a\u5f31\u5230\u4e86\u6781\u9650\uff0c\u8fde\u8bf4\u8bdd\u7684\u529b\u6c14\u90fd\u6ca1\u6709\uff0c\u95ed\u7740\u773c\u775b\u5904\u4e8e\u6655\u6655\u6c89\u6c89\u534a\u9192\u534a\u7761\u7684\u72b6\u6001\uff0c\u4eff\u4f5b\u4e0b\u4e00\u77ac\u95f4\u5c31\u518d\u4e5f\u4e0d\u4f1a\u9192\u6765\u3002\u7136\u800c\u521a\u521a\u901a\u8fc7\u57ce\u95e8\uff0c\u6885\u83f2\u65af\u5c31\u4eff\u4f5b\u7cbe\u795e\u4e00\u632f\uff0c\u82cd\u767d\u7684\u8138\u86cb\u5f00\u59cb\u6cdb\u8d77\u5fae\u5fae\u7ea2\u6655\uff0c\u5634\u5507\u4e5f\u6709\u4e86\u4e9b\u8840\u8272\uff0c\u867d\u7136\u8fd8\u662f\u4e0d\u80fd\u8bf4\u8bdd\uff0c\u4f46\u5df2\u7ecf\u53ef\u4ee5\u7741\u5f00\u773c\u775b\u4e86\u3002\u5f88\u660e\u663e\uff0c\u81ea\u4ece\u8fdb\u5165\u6df1\u6e0a\u4ee5\u6765\u5c31\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u538b\u5236\u7740\u5979\u7684\u4f4d\u9762\u89c4\u5219\uff0c\u5f71\u54cd\u529b\u524a\u5f31\u4e86\u3002</p><p>\u201c\u4e0d\u8fc7\u8bf4\u5b9e\u8bdd\uff0c\u5979\u5f88\u4e86\u4e0d\u8d77\uff0c\u5c45\u7136\u80fd\u575a\u6301\u5c06\u8fd1\u4e00\u5929\u3002\u6211\u66fe\u7ecf\u89c1\u8fc7\u4e00\u540d\u5723\u6b66\u58eb\uff0c\u6389\u5230\u6df1\u6e0a\u6765\uff0c\u4e0d\u5230\u516b\u4e2a\u5c0f\u65f6\u5c31\u5f7b\u5e95\u5d29\u6e83\u4e86\u3002\u201d</p><p>\u201c\u5d29\u6e83\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u5c31\u662f\u53d1\u75af\u4e86\uff0c\u7136\u540e\u88ab\u6076\u9b54\u6495\u6210\u4e86\u788e\u7247\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4f60\u80af\u5b9a\u611f\u89c9\u4e0d\u5230\uff0c\u5bf9\u4e8e\u4ed6\u4eec\u5723\u6b66\u58eb\u6765\u8bf4\uff0c\u6574\u4e2a\u4f4d\u9762\u7684\u529b\u91cf\u90fd\u538b\u5236\u5728\u8eab\u4e0a\u548c\u7075\u9b42\u4e0a\uff0c\u5c31\u50cf\u662f\u628a\u4f60\u4e22\u8fdb\u7194\u7089\u7145\u70e7\u540c\u65f6\u53c8\u62ff\u94c1\u9524\u6572\u6253\uff0c\u7136\u540e\u518d\u7528\u94c1\u677f\u6324\u538b\uff0c\u6ca1\u6709\u51e0\u4e2a\u4eba\u80fd\u575a\u6301\u4e0b\u6765\u3002\u201d</p><p>\u201c\u4f60\u597d\u50cf\u5f88\u719f\u6089\u3002\u201d</p><p>\u201c\u81ea\u7136\uff0c\u201d\u838e\u73de\u514b\u5634\u89d2\u9732\u51fa\u5fae\u5fae\u51b7\u7b11\uff0c\u201c\u4f60\u77e5\u9053\u683c\u62c9\u5179\u7279\u600e\u4e48\u60e9\u7f5a\u89e6\u6012\u4ed6\u7684\u6076\u9b54\u5417\uff1f\u4ed6\u4f1a\u6253\u5f00\u4f4d\u9762\u901a\u9053\uff0c\u628a\u4f60\u4e22\u5230\u5929\u5802\u5c71\u7684\u67d0\u4e2a\u89d2\u843d\u53bb\uff0c\u8fc7\u516d\u4e2a\u5c0f\u65f6\u518d\u81ea\u52a8\u53ec\u56de\u6df1\u6e0a\uff0c\u524d\u63d0\u662f\u4f60\u8fd8\u6ca1\u6b7b\u7684\u8bdd\u3002\u7edd\u5927\u591a\u6570\u60c5\u51b5\u4e0b\uff0c\u53ec\u56de\u7684\u90fd\u53ea\u662f\u5c38\u4f53\uff0c\u6211\u662f\u90a3\u6781\u5c11\u6570\u5e78\u8fd0\u8005\u4e4b\u4e00\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u8bf4\u4f60\u4e5f\u5f88\u4e86\u4e0d\u8d77\u3002\u201d</p><p>\u201c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6211\u8fd9\u4e2a\u6076\u9b54\u8fd8\u4e0d\u591f\u5f7b\u5e95\u7f62\u4e86\u3002\u201d</p><p>\u743c\u6069\u6ca1\u6709\u63a5\u8bdd\uff0c\u7528\u8896\u5b50\u66ff\u6885\u83f2\u65af\u5c06\u989d\u5934\u7684\u6c57\u73e0\u62ed\u53bb\uff0c\u201c\u611f\u89c9\u597d\u70b9\u6ca1\u6709\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u6885\u83f2\u65af\u9732\u51fa\u4e00\u4e2a\u52c9\u5f3a\u7684\u7b11\u5bb9\uff0c\u201c\u6211\u6ca1\u4e8b\u4e86\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u522b\u62c5\u5fc3\u3002\u201d</p><p>\u5979\u4f9d\u65e7\u8fd8\u662f\u5f88\u865a\u5f31\uff0c\u58f0\u97f3\u8f7b\u5fae\u5f97\u51e0\u4e0d\u53ef\u95fb\uff0c\u4f46\u838e\u73de\u514b\u8fd8\u662f\u6e05\u695a\u542c\u89c1\u4e86\uff0c\u201c\u653e\u5fc3\uff0c\u5979\u6ca1\u4e8b\u4e86\uff0c\u53ea\u8981\u5728\u8fdb\u9547\u4e4b\u524d\u4e0d\u65ad\u6c14\uff0c\u73b0\u5728\u5c31\u80af\u5b9a\u6b7b\u4e0d\u4e86\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u4f60\u4eec\u5f88\u5e78\u8fd0\uff0c\u9047\u5230\u7684\u662f\u6211\uff0c\u5426\u5219\u5c31\u7b97\u80fd\u627e\u5230\u5176\u4ed6\u6076\u9b54\u5e26\u8def\uff0c\u4e5f\u7edd\u6ca1\u6709\u8fd9\u4e48\u5feb\u3002\u6df1\u6e0a\u4e2d\u6bcf\u4e2a\u5730\u70b9\u90fd\u662f\u5728\u968f\u65f6\u504f\u79fb\u7684\uff0c\u683c\u62c9\u5179\u7279\u7684\u8fd9\u9762\u65d7\u5e1c\u80fd\u81ea\u52a8\u5b9a\u4f4d\u65ad\u57df\u9547\u7684\u4f4d\u7f6e\uff0c\u5176\u4ed6\u6076\u9b54\u6700\u591a\u77e5\u9053\u5927\u6982\u7684\u65b9\u5411\uff0c\u7b49\u5b83\u4eec\u627e\u5230\u5730\u65b9\uff0c\u4f60\u7684\u5c0f\u60c5\u4eba\u4e5f\u65e9\u5c31\u6b7b\u4e86\u3002\u201d</p><p>\u201c\u591a\u8c22\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u7528\u4e0d\u7740\uff0c\u4ea4\u6613\u800c\u5df2\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u53c8\u770b\u4e86\u770b\u6885\u83f2\u65af\uff0c\u8bed\u6c14\u4e2d\u4fbf\u9690\u9690\u6709\u4e86\u51e0\u5206\u5ac9\u5992\u7684\u5473\u9053\uff0c\u201c\u770b\u4e0d\u51fa\u4f60\u8fd8\u771f\u559c\u6b22\u5979\uff0c\u4e3a\u4e86\u5979\u8fde\u7075\u9b42\u5951\u7ea6\u90fd\u613f\u610f\u7b7e\u3002\u201d</p><p>\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u4e9b\u4e0d\u80fd\u820d\u5f03\u7684\u4e1c\u897f\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u5bf9\u4e8e\u6211\u800c\u8a00\uff0c\u5979\u5c31\u662f\u3002\u201d</p><p>\u201c\u5c31\u6ca1\u6709\u4e00\u70b9\u72b9\u8c6b\uff1f\u201d\u9b45\u9b54\u95ee\uff0c\u201c\u522b\u5fd8\u4e86\uff0c\u7075\u9b42\u5951\u7ea6\u53ef\u662f\u751f\u547d\u7ed1\u5b9a\u7684\uff0c\u6781\u5c11\u6709\u4eba\u613f\u610f\u627f\u53d7\u8fd9\u4e2a\u4ee3\u4ef7\u3002\u201d</p><p>\u201c\u65e2\u7136\u6ca1\u6709\u66f4\u597d\u7684\u9009\u62e9\uff0c\u90a3\u4e48\u72b9\u8c6b\u4e0d\u8fc7\u662f\u6d6a\u8d39\u65f6\u95f4\uff0c\u800c\u6211\u6070\u597d\u62d6\u4e0d\u8d77\u65f6\u95f4\uff0c\u201d\u743c\u6069\u6de1\u6de1\u8bf4\uff0c\u201c\u81f3\u4e8e\u7075\u9b42\u5951\u7ea6\uff0c\u7b7e\u4e86\u53c8\u4e0d\u7b49\u4e8e\u5c31\u4f1a\u6b7b\uff0c\u4f60\u603b\u4e0d\u4f1a\u4e0d\u7231\u60dc\u81ea\u5df1\u7684\u6027\u547d\u5427\u3002\u201d</p><p>\u201c\u90a3\u5f53\u7136\uff0c\u6211\u96be\u5f97\u518d\u6d3b\u4e00\u6b21\uff0c\u53ef\u4e0d\u60f3\u968f\u4fbf\u5c31\u6b7b\u4e86\uff0c\u201d\u9b45\u9b54\u683c\u683c\u7b11\u7740\uff0c\u6307\u4e86\u6307\u524d\u65b9\uff0c\u201c\u8001\u5934\u9aa8\u5230\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u8001\u5934\u9aa8\u662f\u4e00\u5ea7\u5916\u8868\u770b\u8d77\u6765\u7834\u70c2\u4e0d\u582a\uff0c\u8d70\u8fdb\u53bb\u5374\u53d1\u73b0\u5bcc\u4e3d\u5802\u7687\u7684\u65c5\u5e97\u3002\u838e\u73de\u514b\u4f3c\u4e4e\u5e38\u6765\u6b64\u5730\uff0c\u9a7e\u8f7b\u5c31\u719f\u5730\u8df3\u4e0b\u8f66\uff0c\u53ec\u6765\u4e00\u53ea\u602f\u9b54\u4f8d\u8005\uff0c\u4ed8\u7ed9\u5b83\u4e24\u9897\u5b9d\u77f3\uff0c\u5148\u5f00\u4e86\u4e09\u4e2a\u623f\u95f4\uff0c\u8ba9\u8475\u9732\u3001\u963f\u5fd2\u59ae\u548c\u4f0a\u8389\u96c5\u5148\u4f4f\u8fdb\u53bb\u4f11\u606f\u3002\u201c\u6211\u8981\u53bb\u89c1\u7ea2\u8272\u5bff\u8863\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u628a\u8fd9\u6279\u8d27\u7269\u4ea4\u63a5\u4e86\uff0c\u4f60\u8981\u966a\u6211\u53bb\u5417\uff1f\u201d</p><p>\u743c\u6069\u672c\u60f3\u8bf4\u4f60\u81ea\u5df1\u53bb\u597d\u4e86\uff0c\u6211\u5148\u966a\u6885\u83f2\u65af\u4f11\u606f\uff0c\u8f6c\u5ff5\u60f3\u8d77\u6b27\u51ef\u66fe\u7ecf\u63d0\u8fc7\uff0c\u8fd9\u4f4d\u201c\u7ea2\u8272\u5bff\u8863\u201d\u662f\u65ad\u57df\u9547\u7684\u521b\u5efa\u8005\uff0c\u5efa\u8bae\u53ef\u4ee5\u76f4\u63a5\u627e\u5979\u5e2e\u5fd9\uff0c\u8bf4\u201c\u5979\u76f8\u5bf9\u6bd4\u8f83\u5bb9\u6613\u6253\u4ea4\u9053\u201d\u3002\u770b\u8fd9\u65ad\u57df\u9547\u867d\u7136\u91cc\u9762\u5f80\u6765\u7684\u90fd\u662f\u6076\u9b54\uff0c\u4f46\u5374\u4e5f\u7b97\u662f\u4e95\u7136\u6709\u5e8f\uff0c\u53ef\u89c1\u786e\u5b9e\u4e0d\u51e1\uff0c\u6709\u8fd9\u4e2a\u673a\u4f1a\uff0c\u5012\u662f\u5e94\u8be5\u89c1\u4e0a\u4e00\u89c1\u3002</p><p>\u6885\u83f2\u65af\u867d\u7136\u6709\u6240\u597d\u8f6c\uff0c\u4f46\u8fd8\u4e0d\u80fd\u81ea\u5df1\u884c\u8d70\uff0c\u743c\u6069\u603b\u4e5f\u4e0d\u80fd\u62b1\u7740\u5979\u53bb\u89c1\u7ea2\u8272\u5bff\u8863\uff0c\u4f46\u7559\u4e0b\u5374\u53c8\u6709\u4e9b\u4e0d\u653e\u5fc3\uff0c\u6bd5\u7adf\u6ee1\u773c\u90fd\u662f\u6076\u9b54\uff0c\u800c\u8475\u9732\u7b49\u4eba\u56e0\u4e3a\u5df2\u7ecf\u5c06\u8fd1\u4e00\u5929\u6c34\u7c73\u672a\u8fdb\uff0c\u53c8\u5728\u9ad8\u6e29\u70c8\u65e5\u4e0b\u8d76\u8def\u6218\u6597\uff0c\u518d\u52a0\u4e0a\u4f4d\u9762\u89c4\u5219\u7684\u538b\u5236\uff0c\u65e9\u5c31\u75b2\u60eb\u4e0d\u582a\uff0c\u4e5f\u4e0d\u53ef\u80fd\u4fdd\u62a4\u6885\u83f2\u65af\u3002\u6b63\u81ea\u8e0c\u8e87\uff0c\u838e\u73de\u514b\u770b\u51fa\u4e86\u4ed6\u7684\u5fc3\u601d\uff0c\u62ac\u624b\u53ec\u8fc7\u602f\u9b54\u4f8d\u8005\u6765\uff0c\u201c\u4e3a\u8fd9\u4f4d\u5c0f\u59d0\u5355\u72ec\u5f00\u4e00\u95f4\u8d35\u5bbe\u623f\uff0c\u201d\u5979\u8bf4\uff0c\u540c\u65f6\u4eae\u51fa\u4e86\u4e00\u679a\u683c\u62c9\u5179\u7279\u7684\u516d\u6307\u5fbd\u7ae0\uff0c\u201c\u6211\u4e0d\u5e0c\u671b\u5979\u53d1\u751f\u4efb\u4f55\u610f\u5916\uff0c\u4efb\u4f55\uff0c\u5426\u5219\u6211\u5c31\u4f1a\u5f52\u548e\u5230\u4f60\u4eec\u5934\u4e0a\uff0c\u660e\u767d\u5417\uff1f\u201d</p><p>\u602f\u9b54\u8eac\u8eab\uff0c\u8f6c\u8fc7\u8138\u53eb\u6765\u4e24\u53ea\u5938\u8d5b\u9b54\uff0c\u547d\u4ee4\u5b83\u4eec\u628a\u6885\u83f2\u65af\u62ac\u5230\u8d35\u5bbe\u623f\u53bb\u3002\u201c\u6ca1\u95ee\u9898\u5417\uff1f\u201d\u743c\u6069\u6709\u4e9b\u72b9\u8c6b\u5730\u95ee\uff0c\u201c\u5b83\u4eec\u53ef\u9760\uff1f\u201d</p><p>\u201c\u6c38\u8fdc\u4e0d\u8981\u6307\u671b\u6076\u9b54\u4f1a\u53ef\u9760\uff0c\u4f46\u5b83\u4eec\u51b3\u4e0d\u6562\u5192\u72af\u683c\u62c9\u5179\u7279\uff0c\u8fd9\u5c31\u8db3\u591f\u4e86\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u65ad\u57df\u9547\u662f\u7ea2\u8272\u5bff\u8863\u6240\u4e00\u624b\u6267\u638c\uff0c\u800c\u683c\u62c9\u5179\u7279\u5c31\u662f\u5979\u552f\u4e00\u7684\u5408\u4f5c\u4f19\u4f34\uff0c\u8fd9\u4e5f\u662f\u6211\u4e3a\u4ec0\u4e48\u4f1a\u6765\u8fd9\u91cc\u7684\u7f18\u6545\u3002\u4e4c\u9eef\u541b\u4e3b\u7684\u90aa\u5fbd\uff0c\u5728\u6b64\u5730\u6548\u529b\u4ec5\u6b21\u4e8e\u7ea2\u8272\u5bff\u8863\u672c\u4eba\u7684\u547d\u4ee4\u3002\u201d</p><p>\u743c\u6069\u770b\u7740\u838e\u73de\u514b\uff0c\u7136\u540e\u786e\u5b9a\u5bf9\u65b9\u6240\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u8fd9\u4e5f\u662f\u771f\u540d\u5951\u7ea6\u7684\u4f5c\u7528\u4e4b\u4e00\u3002\u524d\u9762\u5df2\u7136\u51e0\u6b21\u63d0\u5230\uff0c\u8fd9\u79cd\u5951\u7ea6\u4e4b\u6240\u4ee5\u88ab\u53d1\u660e\u51fa\u6765\uff0c\u6700\u521d\u7684\u672c\u610f\u5c31\u662f\u4e3a\u4e86\u8ba9\u5deb\u5e08\u80fd\u83b7\u5f97\u201c\u53ef\u9760\u201d\u7684\u90aa\u9b54\u52a9\u624b\uff0c\u867d\u7136\u56e0\u4e3a\u540e\u6765\u53d1\u73b0\u4e86\u201c\u751f\u547d\u7ed1\u5b9a\u201d\u7684\u6f5c\u5728\u9ed8\u8ba4\u89c4\u5219\uff0c\u5bfc\u81f4\u5b83\u88ab\u675f\u4e4b\u9ad8\u9601\uff0c\u65e0\u4eba\u52a8\u7528\uff0c\u4f46\u4e0d\u7b49\u4e8e\u8bf4\u5b83\u539f\u672c\u7684\u4f5c\u7528\u5c31\u4e0d\u5b58\u5728\u4e86\u3002\u5deb\u5e08\u5bf9\u4e8e\u201c\u52a9\u624b\u201d\uff0c\u4f9d\u65e7\u62e5\u6709\u4e00\u5b9a\u7a0b\u5ea6\u7684\u4f18\u52bf\uff0c\u800c\u80fd\u591f\u76f4\u63a5\u611f\u5e94\u5bf9\u65b9\u7684\u7075\u9b42\uff0c\u5224\u65ad\u8bed\u8a00\u7684\u771f\u5047\uff0c\u539f\u672c\u5c31\u662f\u201c\u53ef\u9760\u201d\u4e00\u8bcd\u7684\u9898\u4e2d\u5e94\u6709\u4e4b\u4e49\uff0c\u53ea\u662f\u8fd9\u79cd\u80fd\u529b\u4e5f\u6709\u6b21\u6570\u9650\u5236\uff0c\u4e0d\u80fd\u65e0\u9650\u5236\u4f7f\u7528\u7f62\u4e86\u3002</p><p>\u4ed6\u5c06\u6885\u83f2\u65af\u5c0f\u5fc3\u5730\u4ea4\u7ed9\u5938\u8d5b\u9b54\uff0c\u770b\u7740\u5b83\u4eec\u8d70\u8fdb\u623f\u95f4\uff0c\u201c\u73b0\u5728\u53bb\u89c1\u7ea2\u8272\u5bff\u8863\u5427\u3002\u201d\u4ed6\u5bf9\u838e\u73de\u514b\u8bf4\u3002</p><p>\u9b45\u9b54\u6307\u6325\u7740\u9a6e\u517d\u518d\u6b21\u4e0a\u8def\uff0c\u4ece\u7199\u7199\u6518\u6518\u7684\u6076\u9b54\u7fa4\u4e2d\u7ecf\u8fc7\u3002\u201c\u4e3a\u4ec0\u4e48\u8fd9\u4e9b\u6076\u9b54\u90fd\u96c6\u4e2d\u5728\u65ad\u57df\u9547\u91cc\uff0c\u201d\u743c\u6069\u7a81\u7136\u5f00\u53e3\u95ee\uff0c\u201c\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u8840\u6218\u3002\u201d\u838e\u73de\u514b\u7b80\u77ed\u5730\u56de\u7b54\u3002</p><p>\u201c\u8840\u6218\u53c8\u8981\u5f00\u59cb\u4e86\uff1f\u201d</p><p>\u201c\u4e8b\u5b9e\u4e0a\uff0c\u5b83\u4ece\u6ca1\u505c\u6b62\u8fc7\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u8bb0\u4f4f\u4e00\u70b9\uff0c\u8fd9\u91cc\u6ca1\u6709\u548c\u5e73\uff0c\u53ea\u6709\u6218\u4e89\u7684\u95f4\u9699\u3002\u201d</p><p>\u743c\u6069\u9ed8\u7136\u70b9\u5934\uff0c\u660e\u767d\u4e3a\u4ec0\u4e48\u81ea\u5df1\u7b49\u4eba\u5728\u9047\u5230\u838e\u73de\u514b\u4e4b\u524d\uff0c\u4e00\u8def\u4e0a\u9664\u4e86\u5f17\u6d1b\u9b54\uff0c\u534a\u4e2a\u5176\u4ed6\u6076\u9b54\u4e5f\u6ca1\u649e\u4e0a\u7684\u7f18\u6545\u3002\u201c\u683c\u62c9\u5179\u7279\u6d3e\u4f60\u6765\u9001\u7684\u8fd9\u6279\u4e1c\u897f\u662f\u505a\u4ec0\u4e48\u7528\u7684\uff1f\u201d</p><p>\u201c\u519b\u706b\uff0c\u6076\u9b54\u4e13\u7528\u7684\u519b\u706b\uff0c\u683c\u62c9\u5179\u7279\u6700\u65b0\u7814\u5236\u51fa\u6765\u7684\u4e1c\u897f\uff0c\u4f60\u77e5\u9053\u4ed6\u4e00\u5411\u559c\u6b22\u6298\u817e\u65b0\u53d1\u660e\u3002\u201d</p><p>\u201c\u5f88\u91cd\u8981\uff1f\u201d</p><p>\u201c\u7b97\u662f\u5427\u3002\u201d</p><p>\u201c\u7ea2\u8272\u5bff\u8863\u5728\u8fd9\u5176\u4e2d\u7684\u89d2\u8272\u5462\uff1f\u201d</p><p>\u201c\u65ad\u57df\u9547\u539f\u672c\u5c31\u662f\u8840\u6218\u7684\u5f81\u53ec\u8425\uff0c\u6362\u53e5\u8bdd\u8bf4\uff0c\u5979\u5c31\u662f\u8d1f\u8d23\u53ec\u96c6\u70ae\u7070\u4eec\u7684\uff0c\u201d\u838e\u73de\u514b\u56de\u7b54\uff0c\u201c\u5426\u5219\u4f60\u8ba4\u4e3a\u5979\u5982\u4f55\u80fd\u5728\u8fd9\u8840\u6218\u7684\u524d\u7ebf\u5b89\u7a33\u7acb\u8db3\u81f3\u4eca\uff1f\u5f53\u7136\uff0c\u5979\u80cc\u540e\u6709\u4eba\u652f\u6301\uff0c\u4f46\u90a3\u8fdc\u8fdc\u4e0d\u591f\u3002\u201d</p><p>\u201c\u4f60\u548c\u5979\u5173\u7cfb\u5982\u4f55\u3002\u201d</p><p>\u9b45\u9b54\u5fae\u7b11\u8d77\u6765\uff0c\u201c\u89c1\u8fc7\u51e0\u6b21\u9762\uff0c\u7b97\u662f\u6709\u70b9\u4ea4\u60c5\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6bd5\u7adf\u6bcf\u6b21\u90fd\u662f\u6211\u6765\u548c\u5979\u4ea4\u6d89\u3002\u201d</p><p>\u743c\u6069\u4e5f\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u770b\u8d77\u6765\u4f60\u5728\u683c\u62c9\u5179\u7279\u773c\u4e2d\u7684\u4f4d\u7f6e\u5f88\u4e0d\u4f4e\u5462\u3002\u201d</p><p>\u838e\u73de\u514b\u6014\u4e86\u6014\uff0c\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u53ea\u662f\u5728\u60f3\uff0c\u65e2\u7136\u8840\u6218\u5373\u5c06\u5f00\u59cb\u2026\u2026\u597d\u5427\uff0c\u6309\u7167\u4f60\u4eec\u7684\u8bf4\u6cd5\u662f\u505c\u6218\u671f\u9a6c\u4e0a\u8981\u8fc7\u53bb\u4e86\uff0c\u683c\u62c9\u5179\u7279\u7279\u5730\u8fd0\u9001\u8fd9\u6837\u4e00\u6279\u519b\u706b\u6765\u5230\u524d\u7ebf\uff0c\u7406\u5f53\u662f\u5f88\u91cd\u8981\u7684\u4e1c\u897f\u3002\u8fd9\u79cd\u4efb\u52a1\u5f88\u91cd\u8981\uff0c\u5982\u679c\u6211\u662f\u683c\u62c9\u5179\u7279\uff0c\u90a3\u4e48\u51b3\u4e0d\u4f1a\u968f\u610f\u6307\u6d3e\u4e00\u4eba\u8d1f\u8d23\u8fd0\u9001\uff0c\u5fc5\u5b9a\u662f\u6211\u76f8\u5bf9\u719f\u6089\u5e76\u4e14\u4fe1\u4efb\u7684\u624b\u4e0b\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u6216\u8bb8\u5427\u3002\u201d</p><p>\u201c\u4f46\u6211\u8bb0\u5f97\u4f60\u5bf9\u8475\u9732\u8bf4\uff0c\u4f60\u4e0d\u8fc7\u662f\u683c\u62c9\u5179\u7279\u624b\u4e0b\u6210\u5343\u4e0a\u4e07\u9b45\u9b54\u4e2d\u7684\u4e00\u4e2a\uff0c\u4ed6\u90fd\u672a\u5fc5\u8bb0\u5f97\u4f60\u7684\u540d\u5b57\u3002\u201d</p><p>\u201c\u597d\u5427\uff0c\u90a3\u662f\u6211\u9a97\u5979\u7684\uff0c\u4e00\u79cd\u8c08\u5224\u6280\u5de7\u800c\u5df2\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u6211\u80fd\u7406\u89e3\uff0c\u4f46\u73b0\u5728\u4f60\u6ca1\u529e\u6cd5\u6b3a\u9a97\u6211\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u662f\u683c\u62c9\u5179\u7279\u6d3e\u4f60\u6765\u627e\u6211\u7684\uff1f\u201d</p><p>\u201c\u6211\u662f\u4e0d\u80fd\u6b3a\u9a97\u4f60\uff0c\u4f46\u6211\u53ef\u4ee5\u9009\u62e9\u4e0d\u56de\u7b54\uff0c\u201d\u9b45\u9b54\u8138\u4e0a\u7b11\u610f\u4e0d\u51cf\uff0c\u201c\u6240\u4ee5\u6211\u7684\u56de\u7b54\u662f\uff1a\u65e0\u53ef\u5949\u544a\u3002\u201d</p><p>\u201c\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u4e48\u89c9\u5f97\u5462\uff1f\u201d\u5979\u53cd\u95ee\uff0c\u201c\u56e0\u4e3a\u6211\u7684\u51fa\u73b0\u592a\u5de7\u5408\uff1f\u201d</p><p>\u201c\u6709\u8fd9\u4e2a\u539f\u56e0\u3002\u201d</p><p>\u201c\u53ef\u662f\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6c38\u8fdc\u90fd\u662f\u4f1a\u5b58\u5728\u5de7\u5408\u7684\u5440\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u800c\u4e14\u8fd9\u91cc\u662f\u6df1\u6e0a\uff0c\u8fd9\u91cc\u539f\u672c\u5c31\u662f\u65e0\u5e8f\u4e4b\u5730\uff0c\u6df7\u4e71\u4e4b\u6240\uff0c\u5de7\u5408\u548c\u5076\u9047\u624d\u662f\u5e38\u6001\u3002\u201d</p><p>\u201c\u4f46\u4f60\u5bf9\u771f\u540d\u5951\u7ea6\u592a\u719f\u6089\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u662f\u6211\u4eec\u5deb\u5e08\u624d\u4f1a\u53bb\u5b66\u4e60\u7684\u77e5\u8bc6\uff0c\u5e76\u4e0d\u662f\u4eba\u4eba\u90fd\u77e5\u9053\u7684\uff0c\u4f60\u4ee5\u524d\u662f\u4e2a\u6740\u624b\uff0c\u73b0\u5728\u662f\u4e2a\u6076\u9b54\uff0c\u800c\u4e14\u624d\u4e0d\u5230\u4e00\u5e74\uff0c\u65e0\u8bba\u54ea\u4e00\u79cd\u8eab\u4efd\uff0c\u4f60\u90fd\u6ca1\u9053\u7406\u5bf9\u5b83\u8fd9\u4e48\u4e86\u89e3\u3002\u201d</p><p>\u201c\u6216\u8bb8\u6211\u66fe\u7ecf\u89c1\u4eba\u7528\u8fc7\uff1f\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u5728\u8bf4\u8c0e\u3002\u4f60\u6211\u90fd\u5f88\u6e05\u695a\uff0c\u8fd9\u79cd\u5951\u7ea6\u521a\u53d1\u660e\u51fa\u6765\u5c31\u88ab\u675f\u4e4b\u9ad8\u9601\uff0c\u6839\u672c\u5c31\u4e0d\u4f1a\u6709\u4eba\u7528\uff0c\u53ea\u6015\u81ea\u4ece\u5b83\u53d1\u660e\u4ee5\u6765\uff0c\u6211\u4eec\u662f\u7b2c\u4e00\u5bf9\u7b7e\u7ea6\u8005\u3002\u201d</p><p>\u201c\u662f\u8fd9\u6837\u5417\uff1f\u201d</p><p>\u201c\u6076\u9b54\u6216\u8bb8\u90fd\u662f\u6df7\u4e71\u65e0\u5e8f\uff0c\u968f\u5fc3\u5984\u4e3a\u7684\uff0c\u4f46\u6211\u77e5\u9053\u683c\u62c9\u5179\u7279\u4e0d\u662f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u522b\u5fd8\u4e86\uff0c\u6211\u53ef\u662f\u66fe\u7ecf\u548c\u4ed6\u6b63\u9762\u6253\u8fc7\u4ea4\u9053\u7684\uff0c\u6211\u77e5\u9053\u4ed6\u7684\u5916\u53f7\uff0c\u6700\u50cf\u9b54\u9b3c\u7684\u6076\u9b54\u3002\u6211\u4e0d\u76f8\u4fe1\u4ed6\u7f3a\u4e4f\u8ba1\u5212\uff0c\u4e0d\u76f8\u4fe1\u4ed6\u6ca1\u6709\u9884\u8c0b\uff0c\u66f4\u4e0d\u76f8\u4fe1\u4ed6\u624b\u4e0b\u4e00\u4f4d\u9b45\u9b54\u5728\u6211\u6700\u9700\u8981\u7684\u65f6\u5019\u81ea\u5df1\u9001\u4e0a\u95e8\u6765\uff0c\u5979\u8fd8\u51fa\u4e4e\u610f\u6599\u5730\u5bf9\u771f\u540d\u5951\u7ea6\u975e\u5e38\u719f\u6089\uff0c\u800c\u8fd9\u4e00\u5207\u4ec5\u4ec5\u662f\u5de7\u5408\u2014\u2014\u6216\u8bb8\u786e\u5b9e\u662f\u5de7\u5408\uff0c\u4f46\u6211\u5fc5\u987b\u505a\u6700\u574f\u7684\u6253\u7b97\u3002\u201d</p><p>\u9b45\u9b54\u5410\u51fa\u820c\u5c16\uff0c\u8214\u4e86\u8214\u9c9c\u7ea2\u7684\u5634\u5507\uff0c\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u613f\u610f\u548c\u6211\u7b7e\u7ea6\u5462\uff1f\u201d\u5979\u95ee\uff0c\u201c\u5982\u679c\u4f60\u8ba4\u4e3a\u8fd9\u662f\u683c\u62c9\u5179\u7279\u7684\u9677\u9631\u7684\u8bdd\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u4f3c\u4e4e\u6ca1\u6709\u9009\u62e9\u5427\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d</p><p>\u201c\u6211\u4eec\u5df2\u7ecf\u8d70\u4e86\u4e94\u516d\u4e2a\u5c0f\u65f6\uff0c\u6ca1\u6709\u9047\u4e0a\u4efb\u4f55\u53ef\u4ee5\u4ea4\u6d41\u7684\u6076\u9b54\uff0c\u867d\u7136\u4e0d\u77e5\u9053\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f46\u6211\u4e5f\u4e0d\u6562\u5962\u671b\u7ee7\u7eed\u5bfb\u627e\u4e0b\u53bb\uff0c\u5c31\u4e00\u5b9a\u4f1a\u6709\u7ed3\u679c\uff0c\u201d\u743c\u6069\u6162\u6162\u8bf4\uff0c\u201c\u6211\u4eec\u662f\u51e1\u4eba\uff0c\u4e0d\u662f\u6076\u9b54\uff0c\u65e0\u8bba\u662f\u73af\u5883\u8fd8\u662f\u4f4d\u9762\u89c4\u5219\uff0c\u90fd\u5728\u6301\u7eed\u5730\u4f24\u5bb3\u538b\u5236\u7740\u6211\u4eec\u3002\u8475\u9732\u662f\u7267\u5e08\uff0c\u4f46\u5979\u7684\u795e\u7947\u6b63\u5728\u6c89\u7720\uff0c\u4e0d\u80fd\u8d50\u4e88\u795e\u672f\uff0c\u5979\u552f\u4e00\u80fd\u591f\u501a\u9760\u7684\u662f\u795e\u529b\uff0c\u4f46\u90a3\u5e76\u4e0d\u662f\u80fd\u65e0\u9650\u52a8\u7528\u7684\uff0c\u6211\u770b\u5f97\u51fa\u6765\u5979\u5df2\u7ecf\u662f\u5f3a\u5f29\u4e4b\u672b\u4e86\uff0c\u5426\u5219\u5bf9\u4ed8\u4e00\u53ea\u5df4\u5e03\u9b54\u4f55\u987b\u90a3\u6837\u8d39\u52b2\u3002\u963f\u5fd2\u59ae\u662f\u795e\u7bad\u624b\uff0c\u7136\u800c\u5979\u7684\u7bad\u77e2\u6570\u91cf\u8fd8\u5269\u4e0d\u5230\u4e09\u5206\u4e4b\u4e00\uff0c\u6211\u7684\u6cd5\u672f\u4e5f\u662f\u6709\u9650\u7684\uff0c\u6700\u591a\u80fd\u652f\u6301\u4e24\u5230\u4e09\u6b21\u6218\u6597\uff0c\u827e\u5f25\u8587\u7684\u60c5\u51b5\u5728\u4e0d\u65ad\u6076\u5316\uff0c\u4f0a\u8389\u96c5\u5e76\u4e0d\u53ef\u9760\uff0c\u800c\u4e14\u6211\u4eec\u6ca1\u6709\u643a\u5e26\u4efb\u4f55\u98df\u7269\u548c\u6c34\u3002\u201d</p><p>\u201c\u542c\u8d77\u6765\u771f\u7cdf\u7cd5\u3002\u201d\u9b45\u9b54\u70b9\u5934\u3002</p><p>\u201c\u662f\u5f88\u7cdf\u7cd5\uff0c\u201d\u743c\u6069\u627f\u8ba4\uff0c\u201c\u5982\u679c\u4f60\u4eec\u4e0d\u51fa\u73b0\uff0c\u6211\u60f3\u6211\u4eec\u4e5f\u575a\u6301\u4e0d\u4e86\u591a\u4e45\u3002\u5929\u4e0a\u7684\u5f17\u6d1b\u9b54\u6210\u7fa4\u7ed3\u961f\uff0c\u864e\u89c6\u7708\u7708\uff0c\u5b83\u4eec\u53ea\u662f\u5728\u7b49\u5f85\uff0c\u7b49\u5f85\u6211\u4eec\u75b2\u5026\uff0c\u81ea\u5df1\u5012\u4e0b\u53bb\uff0c\u8fd9\u5e76\u4e0d\u9700\u8981\u592a\u957f\u65f6\u95f4\u3002\u201d</p><p>\u201c\u6240\u4ee5\u5462\uff1f\u201d</p><p>\u201c\u6240\u4ee5\u6211\u522b\u65e0\u9009\u62e9\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u662f\u6211\u4eec\u552f\u4e00\u7684\u673a\u4f1a\u3002\u5982\u679c\u4e0d\u662f\u9677\u9631\uff0c\u90a3\u4e48\u6211\u5fc5\u987b\u6293\u4f4f\uff1b\u5982\u679c\u662f\u9677\u9631\uff0c\u90a3\u4e48\u6211\u66f4\u8981\u6293\u4f4f\u3002\u201d</p><p>\u9b45\u9b54\u76b1\u8d77\u7709\u5934\uff0c\u201c\u540e\u9762\u8fd9\u70b9\u6211\u4e0d\u662f\u5f88\u660e\u767d\u3002\u201d</p><p>\u201c\u5982\u679c\u662f\u9677\u9631\uff0c\u90a3\u4e48\u663e\u7136\u7ad9\u5728\u4f60\u80cc\u540e\u7684\u662f\u683c\u62c9\u5179\u7279\uff0c\u201d\u743c\u6069\u8f7b\u8f7b\u626d\u52a8\u7740\u624b\u6307\uff0c\u53d1\u51fa\u6e05\u8106\u7684\u58f0\u97f3\uff0c\u201c\u8fd9\u91cc\u662f\u6df1\u6e0a\uff0c\u4ed6\u662f\u6df1\u6e0a\u4e09\u5927\u6076\u9b54\u541b\u4e3b\u4e4b\u4e00\u3002\u65e2\u7136\u4ed6\u5b89\u6392\u4e86\u9677\u9631\uff0c\u5e0c\u671b\u6211\u8d70\u8fdb\u53bb\uff0c\u90a3\u4e48\u5982\u679c\u6211\u8d70\u8fdb\u53bb\uff0c\u81f3\u5c11\u6682\u65f6\u662f\u5b89\u5168\u7684\uff0c\u5f53\u7136\u5c06\u6765\u6216\u8bb8\u4f1a\u6709\u5f88\u591a\u9ebb\u70e6\u3002\u4f46\u5018\u82e5\u6211\u4e0d\u8d70\u8fdb\u53bb\uff0c\u90a3\u4e48\u7ed3\u679c\u4f3c\u4e4e\u5f88\u660e\u663e\uff0c\u201d\u4ed6\u5fae\u7b11\u7740\uff0c\u201c\u6211\u4e0d\u76f8\u4fe1\u683c\u62c9\u5179\u7279\u4e0d\u6562\u6740\u51e0\u4e2a\u4e0d\u8bc6\u62ac\u4e3e\u7684\u51e1\u4eba\u3002\u201d</p><p>\u201c\u4ed6\u5f53\u7136\u6562\uff0c\u201d\u838e\u73de\u514b\u53f9\u6c14\uff0c\u201c\u4ed6\u662f\u53ef\u4ee5\u5339\u654c\u9ad8\u7b49\u795e\u7684\u5b58\u5728\u3002\u201d</p><p>\u201c\u90a3\u4e48\u6211\u8fd8\u80fd\u8bf4\u4ec0\u4e48\u5462\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u6837\u7834\u7efd\u767e\u51fa\u7684\u9677\u9631\uff0c\u6211\u770b\u4e0d\u51fa\u6765\u624d\u662f\u6709\u95ee\u9898\uff1b\u53cd\u8fc7\u6765\u8bf4\uff0c\u8fd9\u6837\u7834\u7efd\u767e\u51fa\u7684\u9677\u9631\u4ed6\u90fd\u6562\u62ff\u51fa\u6765\uff0c\u6446\u660e\u4e86\u4e0d\u6015\u6211\u4e0d\u5949\u966a\u3002\u4ed6\u603b\u8fd8\u7b97\u5ba2\u6c14\uff0c\u6d3e\u4e86\u4f60\u800c\u4e0d\u662f\u6d3e\u4e2a\u72c2\u6218\u9b54\u5df4\u5e03\u9b54\u6765\u8ddf\u6211\u7b7e\u7ea6\uff0c\u8fd9\u6837\u5982\u679c\u6211\u90fd\u8fd8\u4e0d\u4e0a\u9053\uff0c\u90a3\u5c31\u522b\u5728\u4e16\u4e0a\u6df7\u4e86\u3002\u5728\u6211\u7684\u6545\u4e61\uff0c\u6709\u4e00\u53e5\u8c1a\u8bed\uff1a\u5982\u679c\u6709\u4eba\u5411\u4f60\u656c\u9152\uff0c\u90a3\u4e48\u8bf7\u559d\u4e0b\uff0c\u4e0d\u8981\u5f04\u5f97\u88ab\u7f5a\u9152\uff0c\u6700\u540e\u81ea\u53d6\u5176\u8fb1\u3002\u201d</p><p>\u838e\u73de\u514b\u8f7b\u8f7b\u6447\u5934\uff0c\u770b\u7740\u4ed6\uff0c\u201c\u53ef\u662f\u4f60\u5bf9\u6b64\u4f3c\u4e4e\u5e76\u4e0d\u607c\u6012\u3002\u201d</p><p>\u201c\u6211\u6781\u5c11\u4f1a\u607c\u6012\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u662f\u6211\u7a0d\u7a0d\u53ef\u4ee5\u81ea\u50b2\u7684\u4e00\u9879\u54c1\u5fb7\u3002\u6ca1\u6709\u610f\u4e49\u7684\u4e8b\u60c5\u6211\u61d2\u5f97\u505a\uff0c\u6ca1\u6709\u7528\u7684\u8bdd\u6211\u61d2\u5f97\u8bf4\uff0c\u6ca1\u6709\u5fc5\u8981\u5b58\u5728\u7684\u60c5\u7eea\u6211\u4e5f\u61d2\u5f97\u6709\u2014\u2014\u66f4\u4f55\u51b5\uff0c\u8fd9\u5bf9\u4e8e\u6211\u800c\u8a00\u5e76\u4e0d\u662f\u7b2c\u4e00\u6b21\uff0c\u65e9\u5c31\u4e60\u60ef\u4e86\u3002\u201d</p><p>\u201c\u5982\u679c\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u4f60\u73b0\u5728\u5bf9\u6211\u8bf4\u8fd9\u4e9b\u8bdd\uff0c\u53c8\u6709\u4ec0\u4e48\u610f\u4e49\u5462\uff1f\u201d</p><p>\u201c\u6709\u610f\u4e49\uff0c\u6211\u4e0d\u4ecb\u610f\u8d70\u8fdb\u9677\u9631\uff0c\u5982\u679c\u522b\u65e0\u9009\u62e9\uff1b\u6211\u4e5f\u4e0d\u4ecb\u610f\u5c48\u670d\u4e8e\u4eba\uff0c\u5982\u679c\u529b\u91cf\u4e0d\u5982\u3002\u4f46\u6211\u4e0d\u5e0c\u671b\u88ab\u4eba\u5f53\u4f5c\u50bb\u74dc\uff0c\u201d\u743c\u6069\u76ef\u7740\u9b45\u9b54\uff0c\u201c\u6211\u53ea\u662f\u60f3\u8bf4\uff1a\u9996\u5148\uff0c\u6211\u5f88\u6e05\u695a\u8fd9\u662f\u4e2a\u9677\u9631\uff1b\u5176\u6b21\uff0c\u6211\u4e5f\u5f88\u6e05\u695a\u53e6\u5916\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u8ba8\u538c\u683c\u62c9\u5179\u7279\u3002\u201d</p><p>\u9b45\u9b54\u53ef\u7231\u5730\u76b1\u76b1\u9f3b\u5b50\uff0c\u201c\u8fd9\u5e76\u4e0d\u662f\u4ec0\u4e48\u79d8\u5bc6\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u5f53\u7136\u8ba8\u538c\u4ed6\uff0c\u96be\u9053\u4f1a\u6709\u4eba\u559c\u6b22\u4e00\u4e2a\u6b3a\u9a97\u4f60\uff0c\u6740\u4e86\u4f60\uff0c\u6700\u540e\u8fd8\u6574\u5929\u5728\u4f60\u5934\u4e0a\u547c\u6765\u559d\u53bb\u6307\u624b\u753b\u811a\u7684\u5bb6\u4f19\u5417\uff1f\u6bcf\u4e2a\u6076\u9b54\u90fd\u8ba8\u538c\u5b83\u7684\u4e0a\u7ea7\uff0c\u8fd9\u662f\u4eba\u4eba\u7686\u77e5\u7684\u4e8b\u60c5\uff0c\u6ca1\u6709\u4f8b\u5916\u3002\u201d</p><p>\u201c\u90a3\u5c31\u597d\u6781\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4e0d\u9700\u8981\u95ee\u4f60\u683c\u62c9\u5179\u7279\u6709\u4ec0\u4e48\u8ba1\u5212\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u4e5f\u4e0d\u6e05\u695a\u3002\u4ed6\u5f88\u53ef\u80fd\u53ea\u4e0d\u8fc7\u662f\u5bf9\u4f60\u4e0b\u4e86\u4e00\u4e2a\u6307\u4ee4\uff0c\u8ba9\u4f60\u524d\u6765\u4e07\u6e0a\u5e73\u539f\uff0c\u548c\u9047\u4e0a\u7684\u51e1\u4eba\u5deb\u5e08\u7b7e\u4e00\u4efd\u7075\u9b42\u5951\u7ea6\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u5927\u81f4\u662f\u8fd9\u6837\u6ca1\u9519\uff0c\u201d\u838e\u73de\u514b\u627f\u8ba4\uff0c\u201c\u6211\u4e8b\u5148\u5e76\u4e0d\u77e5\u9053\u662f\u4f60\u4eec\u3002\u201d</p><p>\u201c\u5f88\u6b63\u5e38\uff0c\u800c\u4e14\u4f60\u96be\u9053\u81ea\u5df1\u6ca1\u6709\u53d1\u89c9\u5417\uff0c\u4f60\u7684\u8bb0\u5fc6\u6062\u590d\u5f97\u592a\u5feb\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u827e\u5f25\u8587\u548c\u8475\u9732\u90fd\u8bf4\uff0c\u51e1\u4eba\u53d8\u6210\u6076\u9b54\uff0c\u8bb0\u5fc6\u4f1a\u9010\u6e10\u6cef\u706d\u2014\u2014\u662f\u6cef\u706d\uff0c\u4e0d\u662f\u6682\u65f6\u9057\u5fd8\uff0c\u6211\u76f8\u4fe1\u5979\u4eec\u4e0d\u81f3\u4e8e\u90fd\u5f04\u9519\u4e86\u3002\u4f60\u4e0d\u8bb0\u5f97\u6211\uff0c\u90a3\u5f88\u6b63\u5e38\uff0c\u6211\u80fd\u7406\u89e3\uff1b\u4f60\u80fd\u8bb0\u5f97\u827e\u5f25\u8587\uff0c\u90a3\u4e5f\u5f88\u6b63\u5e38\uff0c\u6211\u80fd\u7406\u89e3\u2014\u2014\u4f46\u4f60\u8ba4\u51fa\u827e\u5f25\u8587\u4e4b\u540e\uff0c\u7acb\u523b\u6240\u6709\u7684\u8bb0\u5fc6\u5c31\u90fd\u6062\u590d\u4e86\uff0c\u8fd9\u4e2a\u6211\u53ef\u5c31\u4e0d\u80fd\u7406\u89e3\u4e86\u2026\u2026\u8fd9\u7ed9\u6211\u7684\u611f\u89c9\uff0c\u5e76\u4e0d\u662f\u4f60\u7684\u8bb0\u5fc6\u5728\u9010\u6e10\u6cef\u706d\uff0c\u800c\u662f\u88ab\u4eba\u5c01\u5b58\u8d77\u6765\uff0c\u53ea\u7b49\u4e00\u4e2a\u5408\u9002\u7684\u5951\u673a\uff0c\u5c31\u7830\u5730\u4e00\u4e0b\uff0c\u91cd\u65b0\u5f00\u653e\u800c\u5df2\u3002\u201d</p><p>\u838e\u73de\u514b\u54ac\u7740\u5634\u5507\uff0c\u201c\u683c\u62c9\u5179\u7279\u3002\u201d</p><p>\u201c\u663e\u7136\u662f\u4ed6\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u73b0\u5728\u8a00\u5f52\u6b63\u4f20\uff0c\u683c\u62c9\u5179\u7279\u5230\u5e95\u60f3\u505a\u4ec0\u4e48\uff0c\u6211\u4eec\u4e0d\u77e5\u9053\uff0c\u4f46\u6709\u4e00\u70b9\u662f\u786e\u5b9a\u7684\uff1a\u6211\u4eec\u7684\u771f\u540d\u5951\u7ea6\u662f\u6709\u6548\u7684\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u90a3\u53c8\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u90a3\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u5982\u679c\u6211\u6b7b\u4e86\uff0c\u4f60\u5c31\u4e00\u5b9a\u4f1a\u6b7b\uff0c\u8fd9\u6b21\u662f\u771f\u6b63\u7684\u6b7b\u4ea1\uff1b\u5982\u679c\u683c\u62c9\u5179\u7279\u6253\u7b97\u7528\u4f60\u6765\u5236\u7ea6\u6211\u7684\u8bdd\uff0c\u663e\u7136\u4ed6\u6700\u76f4\u63a5\u6709\u6548\u7684\u5a01\u80c1\u624b\u6bb5\u5c31\u662f\u6740\u6b7b\u4f60\u2014\u2014\u603b\u800c\u8a00\u4e4b\u4e00\u53e5\u8bdd\uff0c\u6211\u5bf9\u683c\u62c9\u5179\u7279\u6709\u7528\uff0c\u503c\u5f97\u4ed6\u82b1\u8d39\u529b\u6c14\u6765\u5e03\u7f6e\u9677\u9631\uff0c\u5f15\u8bf1\u6211\u4e0a\u94a9\uff0c\u4ed6\u4e0d\u4f1a\u8f7b\u6613\u5730\u6bc1\u706d\u6211\u3002\u800c\u4f60\u7684\u4ef7\u503c\uff0c\u4e0d\u8fc7\u662f\u4e00\u4e2a\u9053\u5177\uff0c\u4e00\u4e2a\u53ef\u4ee5\u968f\u65f6\u4e22\u5f03\u7684\u68cb\u5b50\uff0c\u4e00\u4e2a\u9644\u5e26\u54c1\u3002\u201d</p><p>\u838e\u73de\u514b\u7684\u8138\u8272\u53d8\u5f97\u96be\u770b\u8d77\u6765\uff0c\u201c\u5982\u679c\u4f60\u662f\u60f3\u8d2c\u4f4e\u6211\uff0c\u90a3\u4e48\u73b0\u5728\u4f60\u8fbe\u5230\u76ee\u7684\u4e86\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u4e0d\uff0c\u522b\u8bef\u4f1a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u53e6\u5916\u4e00\u9879\u54c1\u5fb7\u5c31\u662f\uff0c\u4ece\u6765\u4e0d\u4f1a\u5728\u6ca1\u6709\u5fc5\u8981\u7684\u65f6\u5019\u8d2c\u4f4e\u5973\u58eb\u3002\u4f60\u770b\uff0c\u4e0d\u7ba1\u683c\u62c9\u5179\u7279\u60f3\u505a\u4ec0\u4e48\uff0c\u4f60\u90fd\u662f\u5904\u5883\u6700\u5371\u9669\u7684\uff0c\u6700\u5bb9\u6613\u88ab\u8fde\u7d2f\uff0c\u6700\u5bb9\u6613\u88ab\u5229\u7528\uff0c\u4e5f\u6700\u5bb9\u6613\u88ab\u4f5c\u4e3a\u5a01\u80c1\u624b\u6bb5\u3002\u6211\u8fd8\u53ef\u4ee5\u901a\u8fc7\u987a\u4ece\u4ed6\u800c\u6d3b\u547d\uff0c\u4f60\u7684\u6027\u547d\u5219\u5b8c\u5168\u4f9d\u8d56\u4e8e\u6211\u548c\u4ed6\u7684\u5fc3\u60c5\u2014\u2014\u8fd9\u6837\u8bf4\u6ca1\u9519\u5427\u3002\u201d</p><p>\u201c\u90a3\u53c8\u600e\u4e48\u6837\uff1f\u201d</p><p>\u201c\u8fd9\u8fd8\u4e0d\u6e05\u695a\u5417\uff1f\u73b0\u5728\u6211\u4eec\u7684\u9635\u8425\u662f\u4e00\u81f4\u7684\uff0c\u201d\u743c\u6069\u644a\u5f00\u624b\uff0c\u201c\u800c\u4e14\u6709\u4e00\u70b9\u4f60\u6ca1\u8bf4\u9519\uff0c\u5bf9\u4e8e\u4f60\u6211\u800c\u8a00\uff0c\u771f\u540d\u5951\u7ea6\u5c31\u662f\u81f3\u9ad8\u89c4\u5219\uff0c\u5b83\u62b9\u6d88\u4e86\u683c\u62c9\u5179\u7279\u5bf9\u4f60\u7684\u771f\u540d\u53ec\u5524\u80fd\u529b\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u5982\u679c\u4f60\u8fd8\u60f3\u6d3b\u4e0b\u53bb\uff0c\u5f53\u7136\u6211\u4e5f\u60f3\u6d3b\u4e0b\u53bb\uff0c\u90a3\u4e48\u53ea\u8981\u79bb\u5f00\u6df1\u6e0a\uff0c\u79bb\u5f00\u8fd9\u91cc\uff0c\u8fd4\u56de\u5230\u7269\u8d28\u754c\uff0c\u90a3\u4e48\u683c\u62c9\u5179\u7279\u53c8\u80fd\u628a\u6211\u4eec\u600e\u4e48\u6837\u5462\uff1f\u4ed6\u96be\u9053\u80fd\u6765\u7269\u8d28\u754c\u8ffd\u6740\u6211\u4eec\uff1f\u5982\u679c\u4ed6\u8fd9\u4e48\u505a\uff0c\u6211\u60f3\u90a3\u4e9b\u795e\u660e\u4e00\u5b9a\u4f1a\u5f88\u9ad8\u5174\u3002\u201d</p><p>\u838e\u73de\u514b\u6c89\u541f\u7740\uff0c\u201c\u4e8b\u60c5\u4e0d\u4f1a\u50cf\u4f60\u60f3\u50cf\u5f97\u90a3\u4e48\u7b80\u5355\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u683c\u62c9\u5179\u7279\u4e0d\u4f1a\u8fd9\u4e48\u5bb9\u6613\u5bf9\u4ed8\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u4f46\u6211\u8bf4\u7684\u662f\u5927\u5c40\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u771f\u540d\u5951\u7ea6\u7684\u7b7e\u8ba2\uff0c\u56fa\u7136\u662f\u7ea6\u675f\uff0c\u540c\u65f6\u4e5f\u672a\u5fc5\u4e0d\u662f\u4f18\u52bf\u3002\u56e0\u4e3a\u6211\u4eec\u7684\u6027\u547d\u73b0\u5728\u90fd\u662f\u8054\u63a5\u5728\u4e00\u8d77\u7684\uff0c\u8fd8\u6709\u4ec0\u4e48\u76df\u7ea6\u6bd4\u8fd9\u66f4\u53ef\u9760\uff1f\u8fd8\u6709\u4ec0\u4e48\u5408\u4f5c\u6bd4\u8fd9\u66f4\u4eb2\u5bc6\uff1f\u8fd8\u6709\u4ec0\u4e48\u5229\u76ca\u80fd\u591f\u8ba9\u6211\u4eec\u53cd\u76ee\u6210\u4ec7\uff1f\u6ca1\u6709\uff0c\u4e5f\u4e0d\u53ef\u80fd\u6709\u3002\u6240\u4ee5\u4ece\u6b64\u4e4b\u540e\uff0c\u6211\u4eec\u5c31\u662f\u6700\u5fe0\u8bda\u7684\u76df\u53cb\u3002\u201d</p><p>\u201c\u76df\u53cb\uff1f\u201d\u838e\u73de\u514b\u55e4\u55e4\u51b7\u7b11\uff0c\u201c\u771f\u540d\u5951\u7ea6\u7ec8\u7a76\u5c31\u662f\u771f\u540d\u5951\u7ea6\uff0c\u5b83\u662f\u4f60\u4eec\u5deb\u5e08\u4e3a\u4e86\u5974\u5f79\u90aa\u9b54\u800c\u53d1\u660e\u51fa\u6765\u7684\uff0c\u8fd9\u4e2a\u672c\u8d28\u6c38\u8fdc\u4e0d\u4f1a\u6539\u53d8\u3002\u5c31\u7b97\u6709\u751f\u547d\u7ed1\u5b9a\u7684\u9ed8\u8ba4\u89c4\u5219\u5728\uff0c\u6211\u4e5f\u4e0d\u53ef\u80fd\u548c\u4f60\u662f\u5e73\u7b49\u7684\u76df\u53cb\u3002\u201d</p><p>\u201c\u4e0d\u662f\u5974\u5f79\uff0c\u53ea\u662f\u52a9\u624b\uff0c\u201d\u743c\u6069\u7ea0\u6b63\uff0c\u201c\u603b\u4e4b\u73b0\u5728\u6211\u4eec\u662f\u4e00\u4f53\u7684\uff0c\u4e00\u6b7b\u4ff1\u6b7b\uff0c\u4e00\u751f\u4ff1\u751f\u3002\u201d</p><p>\u838e\u73de\u514b\u770b\u7740\u743c\u6069\uff0c\u5979\u50cf\u662f\u91cd\u65b0\u8ba4\u8bc6\u4e86\u8fd9\u4f4d\u5deb\u5e08\uff0c\u7136\u540e\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u660e\u767d\u8fd9\u4e9b\u7684\uff1f\u7b7e\u7ea6\u4e4b\u524d\uff1f\u201d</p><p>\u201c\u6709\u4e9b\u76f4\u63a5\u5c31\u731c\u5230\u4e86\uff0c\u6709\u4e9b\u7ec6\u8282\u662f\u4e8b\u540e\u624d\u7406\u6e05\u695a\u7684\uff0c\u201d\u743c\u6069\u5766\u767d\u627f\u8ba4\uff0c\u201c\u6211\u7684\u53cd\u5e94\u8fd8\u6ca1\u6709\u90a3\u4e48\u5feb\uff0c\u800c\u4e14\u6211\u6ca1\u6709\u65f6\u95f4\u8003\u8651\u3002\u201d</p><p>\u201c\u4f60\u8ba9\u6211\u5931\u671b\u4e86\uff0c\u5deb\u5e08\u3002\u201d</p><p>\u201c\u662f\u4e48\uff1f\u201d</p><p>\u201c\u6211\u539f\u672c\u4ee5\u4e3a\uff0c\u4f60\u771f\u7684\u662f\u56e0\u4e3a\u6885\u83f2\u65af\u624d\u613f\u610f\u7b7e\u8ba2\u90a3\u4efd\u7075\u9b42\u5951\u7ea6\uff0c\u6211\u4ee5\u4e3a\u4f60\u771f\u7684\u613f\u610f\u4e3a\u4e86\u7231\u4eba\u4ed8\u51fa\u4e00\u5207\u3002\u201d</p><p>\u201c\u4e8b\u5b9e\u4e5f\u6b63\u662f\u5982\u6b64\u3002\u201d</p><p>\u201c\u4e0d\u662f\uff01\u201d\u9b45\u9b54\u7528\u529b\u6447\u5934\uff0c\u201c\u4f60\u4e3a\u7684\u5176\u5b9e\u662f\u4f60\u81ea\u5df1\uff0c\u5c31\u50cf\u4f60\u521a\u624d\u8bf4\u7684\uff0c\u4e0d\u7b7e\u5951\u7ea6\uff0c\u4f60\u4f1a\u6b7b\uff1b\u7b7e\u4e86\u5951\u7ea6\uff0c\u4f60\u8fd8\u6709\u673a\u4f1a\u2014\u2014\u4f60\u6240\u76d8\u7b97\u7684\u4e00\u5207\uff0c\u90fd\u4e0d\u8fc7\u662f\u4e3a\u4e86\u4f60\u81ea\u5df1\u800c\u5df2\u3002\u201d</p><p>\u201c\u65e2\u4e3a\u81ea\u5df1\uff0c\u4e5f\u4e3a\u827e\u5f25\u8587\uff0c\u201d\u743c\u6069\u51b7\u9759\u5730\u56de\u7b54\uff0c\u201c\u8fd9\u4e24\u8005\u5e76\u4e0d\u51b2\u7a81\u3002\u201d</p><p>\u201c\u4f46\u5df2\u7ecf\u4e0d\u7eaf\u7cb9\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4f3c\u4e4e\u4e0d\u662f\u4f60\u6709\u8d44\u683c\u8bc4\u4ef7\u7684\uff0c\u5c0f\u59d0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u800c\u4e14\u4f60\u60f3\u8bf4\u4ec0\u4e48\u5462\uff1f\u6307\u8d23\u6211\u4e0d\u591f\u5fe0\u8bda\u4e8e\u7231\u4eba\uff1f\u60f3\u548c\u6211\u8ba8\u8bba\u4ec0\u4e48\u662f\u7231\u60c5\uff1f\u8fd9\u662f\u4e00\u4f4d\u9b45\u9b54\u5e94\u8be5\u8bf4\u7684\u8bdd\u5417\uff1f\u201d</p><p>\u838e\u73de\u514b\u77aa\u7740\u4ed6\uff0c\u4e00\u62b9\u6012\u8272\u4ece\u8138\u4e0a\u63a0\u8fc7\uff0c\u77ac\u95f4\u6d88\u901d\uff0c\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u201d\u5979\u7b11\u610f\u76c8\u76c8\u5730\u70b9\u5934\uff0c\u201c\u9b45\u9b54\u6709\u4ec0\u4e48\u8d44\u683c\u8c08\u8bba\u7231\u60c5\u5462\uff0c\u7136\u800c\u6700\u540e\u6709\u4e00\u4ef6\u4e8b\u6211\u60f3\u8bf7\u6559\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u65e2\u7136\u4f60\u90fd\u660e\u767d\uff0c\u4e3a\u4ec0\u4e48\u6700\u540e\u8fd8\u8981\u8ba9\u5979\u6765\u9009\u62e9\u5462\u3002\u5f53\u7136\uff0c\u662f\u6211\u8981\u5979\u9009\u62e9\uff0c\u4f46\u4f60\u5b8c\u5168\u53ef\u4ee5\u963b\u6b62\uff0c\u201d\u838e\u73de\u514b\u76ef\u7740\u4ed6\uff0c\u201c\u4f60\u77e5\u9053\u8fd9\u4efd\u5951\u7ea6\u662f\u975e\u7b7e\u4e0d\u53ef\uff0c\u90a3\u4e48\u4e3a\u4ec0\u4e48\u53c8\u8981\u5979\u6765\u505a\u6700\u540e\u7684\u51b3\u5b9a\uff0c\u8ba9\u5979\u4e3a\u6b64\u75db\u82e6\uff1f\u201d</p><p>\u201c\u4f60\u5f04\u9519\u4e86\u4e00\u4ef6\u4e8b\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u827e\u5f25\u8587\u662f\u6211\u7684\u60c5\u4eba\uff0c\u771f\u6b63\u7684\u60c5\u4eba\uff0c\u548c\u901a\u8fc7\u5951\u7ea6\u786e\u7acb\u7684\u67d0\u4eba\u4e0d\u540c\u3002\u6211\u4e86\u89e3\u5979\uff0c\u56e0\u4e3a\u6211\u4eec\u5728\u672c\u8d28\u4e0a\u662f\u4e00\u7c7b\u2014\u2014\u5728\u505a\u51b3\u5b9a\u7684\u65f6\u5019\uff0c\u6211\u4eec\u4ece\u6765\u4e0d\u4f1a\u75db\u82e6\u3002\u201d</p><p>\u201c\u4e0d\u4f1a\u75db\u82e6\u4e48\uff1f\u201d\u9b45\u9b54\u8f7b\u58f0\u7b11\u7740\uff0c\u201c\u522b\u88c5\u4e86\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u8bf4\u8d77\u6765\u5bb9\u6613\uff0c\u505a\u8d77\u6765\u53ef\u6ca1\u90a3\u4e48\u7b80\u5355\u5462\uff1b\u6709\u4e9b\u9053\u7406\uff0c\u7eb5\u7136\u5fc3\u4e2d\u660e\u767d\uff0c\u7ec8\u7a76\u4e5f\u662f\u4f1a\u4e0d\u9ad8\u5174\u5462\u2026\u2026\u201d</p><p>\u201c\u6211\u4eec\u5df2\u7ecf\u803d\u8bef\u4e86\u5f88\u591a\u65f6\u95f4\uff0c\u201d\u743c\u6069\u6253\u65ad\uff0c\u201c\u8be5\u53bb\u89c1\u7ea2\u8272\u5bff\u8863\u4e86\u3002\u201d</p></article>"}}}]);
