(window.webpackJsonp=window.webpackJsonp||[]).push([[1522],{1545:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u8282 \u8c08\u5224",content:"<article><h2>\u7b2c\u4e09\u5341\u8282 \u8c08\u5224</h2><p>\u6709\u5965\u6c83\u8fd9\u79cd\u5b9e\u529b\u65e2\u5f3a\u53c8\u5fc3\u7406\u53d8\u6001\u2014\u2014\u5173\u952e\u662f\u4ed6\u8fd8\u603b\u81ea\u4ee5\u4e3a\u5f88\u6709\u578b\u5f88\u5584\u826f\u5f88\u6b63\u5e38\u2014\u2014\u7684\u8001\u5e08\uff0c\u4f60\u5c31\u5fc5\u987b\u505a\u597d\u603b\u80fd\u542c\u5230\u4ed6\u53c8\u641e\u51fa\u4ec0\u4e48\u60ca\u5929\u52a8\u5730\u90aa\u6076\u52fe\u5f53\u7684\u5fc3\u7406\u51c6\u5907\u3002\u5c31\u50cf\u8fd9\u4e00\u6b21\uff0c\u5982\u679c\u8428\u9a6c\u65af\u7279\u771f\u80fd\u6210\u529f\u53d1\u52a8\u9f99\u72c2\u8ff7\u9501\uff0c\u5965\u6c83\u867d\u7136\u4e0d\u662f\u4e3b\u8c0b\u4e5f\u662f\u5e2e\u51f6\uff0c\u81f3\u5c11\u8981\u8d1f\u4e0a\u4e00\u534a\u8d23\u4efb\uff0c\u56e0\u4e3a\u5173\u952e\u6027\u6280\u672f\u5c31\u662f\u4ed6\u63d0\u4f9b\u7684\u3002</p><p>\u5e78\u597d\u8475\u9732\u548c\u6b23\u5e03\u4f3c\u4e4e\u90fd\u4e0d\u77e5\u9053\u5965\u6c83\u548c\u743c\u6069\u7684\u5173\u7cfb\uff0c\u51db\u6216\u8bb8\u662f\u77e5\u9053\u7684\uff0c\u6885\u83f2\u65af\u6709\u53ef\u80fd\u5bf9\u5979\u8bf4\u8fc7\uff0c\u4f46\u8fd9\u79cd\u4e8b\u60c5\u5979\u5e94\u8be5\u4e5f\u4e0d\u81f3\u4e8e\u518d\u5916\u4f20\u3002\u53cd\u6b63\u5c31\u76ee\u524d\u6765\u770b\uff0c\u4e24\u4f4d\u9009\u6c11\u6ca1\u6709\u8868\u73b0\u51fa\u4ec0\u4e48\u8ff9\u8c61\uff0c\u5e94\u8be5\u662f\u4e0d\u77e5\u60c5\u3002\u5e78\u597d\u5982\u6b64\uff0c\u5426\u5219\u522b\u8bf4\u8fd9\u6b21\u8428\u9a6c\u65af\u7279\u7684\u4e8b\u60c5\uff0c\u5c31\u7b97\u770b\u5728\u83b1\u62c9\u7684\u4efd\u4e0a\uff0c\u4e5f\u8981\u628a\u743c\u6069\u75db\u6253\u4e00\u987f\u4e86\u2014\u2014\u83b1\u62c9\u4e5f\u662f\u4e03\u59d0\u59b9\u4e4b\u4e00\uff0c\u5965\u6c83\u8fd9\u4e2a\u8272\u5fc3\u4e0d\u6b7b\u7684\u5bb6\u4f19\u66fe\u7ecf\u6253\u8fc7\u5979\u7684\u4e3b\u610f\uff0c\u5f53\u7136\u6700\u540e\u6ca1\u6210\u529f\uff0c\u5426\u5219\u73b0\u5728\u5c31\u662f\u743c\u6069\u7684\u5e08\u5a18\u4e86\u3002</p><p>\u6709\u5173\u5965\u6c83\u7684\u8bdd\u9898\u6ca1\u6709\u7ee7\u7eed\uff0c\u743c\u6069\u672c\u80fd\u5730\u7ed9\u56de\u907f\u6389\u4e86\u3002\u201c\u8428\u9a6c\u65af\u7279\u5e0c\u671b\u62ff\u8fd9\u679a\u5370\u7ae0\u4f5c\u65b0\u547d\u5323\uff0c\u201d\u4ed6\u660e\u77e5\u6545\u95ee\uff0c\u201c\u90a3\u4ed6\u5df2\u7ecf\u62ff\u5230\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u636e\u8bf4\u5370\u7ae0\u4e00\u76f4\u4fdd\u7ba1\u5728\u65ad\u57df\u9547\u7684\u9b45\u9b54\u4e3b\u4eba\u7ea2\u8272\u5bff\u8863\u624b\u4e2d\uff0c\u4f46\u521a\u624d\u6b23\u5e03\u5f97\u5230\u7684\u6d88\u606f\uff0c\u7ea2\u8272\u5bff\u8863\u628a\u5b83\u62ff\u51fa\u6765\u4e86\uff0c\u4f5c\u4e3a\u51b3\u6597\u5927\u8d5b\u7b2c\u4e00\u540d\u7684\u5956\u54c1\u3002\u201d</p><p>\u90a3\u4e48\u2026\u2026</p><p>\u201c\u65e2\u7136\u662f\u4ed6\u4eba\u4e4b\u7269\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u5f3a\u884c\u593a\u53d6\u3002\u201d</p><p>\u201c\u800c\u4e14\u4f60\u4eec\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u5b9e\u529b\u3002\u201d\u743c\u6069\u66ff\u5979\u628a\u540e\u9762\u534a\u53e5\u8bdd\u8865\u5145\u5b8c\u6574\u3002</p><p>\u201c\u6ca1\u9519\uff0c\u201d\u8475\u9732\u5766\u7136\u627f\u8ba4\uff0c\u201c\u800c\u5982\u679c\u8981\u901a\u8fc7\u6b63\u5e38\u6e20\u9053\u7684\u8bdd\uff0c\u90a3\u5c31\u53ea\u80fd\u53bb\u6253\u51b3\u6597\u5927\u8d5b\uff0c\u62ff\u5230\u51a0\u519b\u3002\u201d</p><p>\u8fd9\u4e48\u8bf4\uff0c\u6b23\u5e03\u548c\u51db\u4e4b\u6240\u4ee5\u5728\u51b3\u6597\u573a\uff0c\u662f\u5728\u6536\u96c6\u8d5b\u524d\u4fe1\u606f\u4e86\uff1f</p><p>\u201c\u90a3\u597d\u6781\u4e86\uff0c\u6211\u4eec\u90fd\u662f\u9075\u7eaa\u5b88\u6cd5\u7684\u826f\u6c11\uff0c\u6b63\u8be5\u5982\u6b64\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6709\u60a8\u548c\u5973\u738b\u965b\u4e0b\u4e24\u4f4d\u9009\u6c11\uff0c\u5e72\u6389\u90a3\u4e9b\u6076\u9b54\u81ea\u7136\u662f\u4e3e\u624b\u4e4b\u52b3\uff0c\u4e0d\u5728\u8bdd\u4e0b\u2026\u2026\u90a3\u4e48\u80fd\u5426\u5148\u9001\u6211\u4eec\u56de\u7269\u8d28\u754c\u5462\uff0c\u53cd\u6b63\u4e5f\u6ca1\u4ec0\u4e48\u80fd\u5e2e\u4e0a\u5fd9\u7684\u3002\u201d</p><p>\u8475\u9732\u548c\u6b23\u5e03\u8dd1\u6765\u8c08\u8fd9\u79cd\u4e8b\u60c5\uff0c\u660e\u663e\u5c31\u662f\u8981\u628a\u81ea\u5df1\u4e5f\u62d6\u4e0b\u624b\uff0c\u5426\u5219\u96be\u9053\u662f\u95f2\u7740\u65e0\u804a\u4e48\u3002\u7136\u800c\u65e2\u7136\u90a3\u679a\u5370\u7ae0\u662f\u8428\u9a6c\u65af\u7279\u7684\u5fd7\u5728\u5fc5\u5f97\u4e4b\u7269\uff0c\u81ea\u5df1\u8fd8\u5377\u5165\u5176\u4e2d\uff1f\u90a3\u7eaf\u7cb9\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u3002</p><p>\u867d\u8bf4\u743c\u6069\u4e5f\u5f88\u60f3\u8981\u90a3\u679a\u767d\u7389\u5370\u7ae0\uff0c\u9690\u9690\u89c9\u5f97\u90a3\u4e1c\u897f\u548c\u81ea\u5df1\u5927\u6709\u7275\u8fde\uff0c\u5982\u679c\u53ea\u662f\u666e\u901a\u7684\u51b3\u6597\u5927\u8d5b\uff0c\u5012\u4e5f\u4e0d\u662f\u4e0d\u53ef\u4ee5\u8003\u8651\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u5c31\u7b54\u5e94\u4e86\uff0c\u53cd\u6b63\u6709\u4e24\u4e2a\u9009\u6c11\u5728\u524d\u9762\u9876\u7740\u5462\u3002\u4f46\u8981\u4ed6\u5192\u7740\u53bb\u9762\u5bf9\u8428\u9a6c\u65af\u7279\u7684\u98ce\u9669\uff0c\u90a3\u8fd8\u662f\u7b97\u4e86\u5427\u3002</p><p>\u5f53\u7136\u4e86\uff0c\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u4f30\u8ba1\u5f53\u771f\u649e\u4e0a\u7684\u8bdd\uff0c\u8428\u9a6c\u65af\u7279\u4e5f\u53ea\u4f1a\u5173\u6ce8\u4e24\u4e2a\u9009\u6c11\uff0c\u538b\u6839\u5c31\u65e0\u89c6\u4ed6\u8fd9\u79cd\u5c0f\u89d2\u8272\uff0c\u76f4\u63a5\u5f53\u4f5c\u7a7a\u6c14\u3002\u4f46\u5c31\u7b97\u4e24\u4e2a\u9009\u6c11\u8054\u624b\uff0c\u8db3\u4ee5\u641e\u5b9a\u8428\u9a6c\u65af\u7279\uff0c\u743c\u6069\u6ca1\u4ec0\u4e48\u5371\u9669\uff0c\u4f3c\u4e4e\u4e5f\u6ca1\u4ec0\u4e48\u597d\u5904\u3002\u4ed6\u662f\u60f3\u8981\u90a3\u679a\u5370\u7ae0\uff0c\u95ee\u9898\u662f\u4ed6\u53c8\u4e0d\u662f\u4e3b\u529b\uff0c\u5373\u4fbf\u6253\u8d62\u4e86\uff0c\u8475\u9732\u548c\u6b23\u5e03\u4e0d\u4f1a\u81ea\u5df1\u62ff\u8d70\uff0c\u4f5c\u4e3a\u6218\u5229\u54c1\u6536\u7f34\uff0c\u53cd\u800c\u8fd8\u7559\u7ed9\u4ed6\uff1f</p><p>\u201c\u4e0d\uff0c\u4f60\u592a\u81ea\u8c26\u4e86\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u8475\u9732\u8bf4\uff0c\u201c\u5b9e\u9645\u4e0a\uff0c\u4f60\u548c\u4f60\u7684\u670b\u53cb\u80fd\u591f\u5e2e\u4e0a\u5f88\u5927\u7684\u5fd9\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u6211\u89c9\u5f97\u6211\u8fd8\u662f\u6bd4\u8f83\u6709\u81ea\u77e5\u4e4b\u660e\u7684\u3002\u81f3\u4e8e\u6211\u7684\u670b\u53cb\uff0c\u60a8\u4e5f\u770b\u5230\u4e86\uff0c\u827e\u5f25\u8587\u8eab\u4f53\u6b20\u4f73\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u53c2\u52a0\u4ec0\u4e48\u51b3\u6597\u5927\u8d5b\u3002\u838e\u73de\u514b\u4e48\uff0c\u4f60\u4eec\u89c9\u5f97\u4e00\u4e2a\u9b45\u9b54\u80fd\u6d3e\u4e0a\u4ec0\u4e48\u7528\u573a\u5462\u2026\u2026\u201d</p><p>\u201c\u80fd\u591f\u51d1\u591f\u4eba\u6570\u3002\u201d\u8475\u9732\u8bf4\u3002</p><p>\u201c\u90a3\u90fd\u4e0d\u884c\u2026\u2026\u5514\uff0c\u4ec0\u4e48\uff1f\u51d1\u591f\u4eba\u6570\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u51b3\u6597\u5927\u8d5b\u5fc5\u987b\u4ee5\u56e2\u4f53\u4e3a\u5355\u4f4d\u62a5\u540d\uff0c\u6bcf\u4e2a\u56e2\u4f53\u5fc5\u987b\u662f\u516d\u4e2a\u4eba\uff0c\u800c\u6211\u4eec\u73b0\u5728\u8fde\u62a5\u540d\u4eba\u6570\u90fd\u4e0d\u591f\u3002\u201d</p><p>\u6b23\u5e03\u548c\u8475\u9732\uff0c\u4e24\u4e2a\u9009\u6c11\uff0c\u52a0\u4e0a\u963f\u5fd2\u59ae\u662f\u8475\u9732\u7684\u624b\u4e0b\uff0c\u51db\u662f\u6b23\u5e03\u7684\u5b66\u751f\uff0c\u4e00\u5171\u56db\u4e2a\uff0c\u8fd8\u5dee\u4e24\u4e2a\u540d\u989d\u3002\u5982\u679c\u8981\u62c9\u6885\u83f2\u65af\u6216\u8005\u838e\u73de\u514b\uff0c\u80af\u5b9a\u8981\u5148\u901a\u8fc7\u743c\u6069\uff0c\u81f3\u4e8e\u4f0a\u8389\u96c5\uff0c\u5979\u66f4\u4e0d\u53ef\u80fd\u542c\u8475\u9732\u7684\u8bdd\uff0c\u552f\u4e00\u80fd\u9547\u5f97\u4f4f\u5c0f\u841d\u8389\u7684\u662f\u6885\u83f2\u65af\u3002</p><p>\u201c\u6211\u4eec\u603b\u4e0d\u53ef\u80fd\u53bb\u8def\u4e0a\u4e34\u65f6\u62c9\u51e0\u4e2a\u6076\u9b54\u6765\u505a\u961f\u53cb\uff0c\u201d\u8475\u9732\u8bf4\uff0c\u201c\u4f46\u6709\u4e00\u70b9\u53ef\u4ee5\u653e\u5fc3\u7684\u662f\uff0c\u6211\u4eec\u4e00\u5b9a\u4f1a\u4fdd\u8bc1\u4f60\u548c\u4f60\u670b\u53cb\u7684\u5b89\u5168\u3002\u6240\u6709\u7684\u6bd4\u8d5b\u90fd\u7531\u6211\u548c\u6b23\u5e03\u4f18\u5148\u51fa\u8d5b\uff0c\u5982\u679c\u6211\u4eec\u5931\u624b\uff0c\u90a3\u4e48\u4f60\u53ef\u4ee5\u76f4\u63a5\u5f03\u6743\u9000\u51fa\uff0c\u653e\u5f03\u6bd4\u8d5b\uff0c\u5370\u7ae0\u7684\u4e8b\u60c5\u53e6\u60f3\u529e\u6cd5\u3002\u201d</p><p>\u5514\uff0c\u5982\u679c\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u4f3c\u4e4e\u4e5f\u672a\u5c1d\u4e0d\u80fd\u5546\u91cf\u2026\u2026\u8bf4\u5230\u5e95\uff0c\u5927\u5bb6\u8981\u60f3\u56de\u5bb6\uff0c\u5f97\u9760\u6b23\u5e03\u5973\u738b\u965b\u4e0b\uff0c\u8fc7\u5206\u5f97\u7f6a\u4e86\u5979\uff0c\u7eaf\u5c5e\u7ed9\u81ea\u5df1\u627e\u4e0d\u81ea\u5728\u3002</p><p>\u201c\u6211\u6bd4\u8f83\u5173\u5fc3\u7684\u662f\uff0c\u6211\u548c\u6211\u7684\u670b\u53cb\u4ec0\u4e48\u65f6\u5019\u80fd\u591f\u8fd4\u56de\u7269\u8d28\u754c\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u65ad\u57df\u9547\u4e2d\u7684\u4f4d\u9762\u538b\u5236\u867d\u7136\u5c0f\u4e00\u4e9b\uff0c\u4f46\u5bf9\u827e\u5f25\u8587\u4f9d\u65e7\u8fd8\u662f\u6709\u4f24\u5bb3\u3002\u5982\u679c\u4ec5\u4ec5\u662f\u4f5c\u4e3a\u2018\u540d\u989d\u2019\u7684\u610f\u4e49\u5b58\u5728\uff0c\u8fd9\u79cd\u4e3e\u624b\u4e4b\u52b3\uff0c\u6211\u4eec\u81ea\u7136\u4e5f\u4e0d\u5e94\u8be5\u62d2\u7edd\uff0c\u4f46\u662f\u2026\u2026\u201d</p><p>\u201c\u53ea\u8981\u6211\u4eec\u62ff\u5230\u5370\u7ae0\uff0c\u90a3\u4e48\u5c31\u7acb\u523b\u52a8\u8eab\u8fd4\u56de\u7269\u8d28\u754c\uff0c\u201d\u8475\u9732\u8bf4\uff0c\u201c\u6211\u4eec\u548c\u4f60\u4e00\u6837\uff0c\u4e0d\u60f3\u5728\u8fd9\u91cc\u591a\u505c\u7559\u7247\u523b\uff0c\u6b23\u5e03\u53ef\u4ee5\u6253\u5f00\u4f4d\u9762\u4f20\u9001\u95e8\u3002\u201d</p><p>\u201c\u62ff\u5230\u5370\u7ae0\uff1f\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u90a3\u5018\u82e5\u6211\u4eec\u5728\u6bd4\u8d5b\u4e2d\u9047\u5230\u5f3a\u654c\uff0c\u6ca1\u80fd\u62ff\u5230\u51a0\u519b\uff1b\u5018\u82e5\u4e2d\u9014\u6709\u4efb\u4f55\u610f\u5916\u53d8\u6545\uff0c\u5373\u4f7f\u5e76\u975e\u6211\u4eec\u7684\u8d23\u4efb\u2026\u2026\u6211\u4eec\u4e5f\u5f97\u8ddf\u7740\u4f60\u4eec\u4e00\u8d77\u7559\u5728\u8fd9\u91cc\uff0c\u76f4\u5230\u53d6\u5f97\u90a3\u679a\u5370\u7ae0\uff1f\u201d</p><p>\u201c\u95ee\u9898\u662f\u4f60\u6709\u9009\u62e9\u5417\uff1f\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u4e86\u4e24\u79d2\u949f\uff0c\u201c\u90a3\u5982\u679c\u5370\u7ae0\u88ab\u6bc1\u6389\u4e86\u5462\uff1f\u5982\u679c\u843d\u5230\u5965\u5580\u65af\u683c\u62c9\u5179\u7279\u62dc\u5c14\u751a\u81f3\u963f\u65af\u8482\u83ab\u65af\u8fd9\u79cd\u5b58\u5728\u624b\u4e2d\u5462\uff1f\u5982\u679c\u8428\u9a6c\u65af\u7279\u5e26\u7740\u5370\u7ae0\u8fd4\u56de\u4e86\u7269\u8d28\u754c\u5462\uff1f\u201d</p><p>\u201c\u90a3\u6211\u4eec\u81ea\u7136\u4e5f\u4f1a\u56de\u53bb\uff0c\u201d\u8475\u9732\u8bf4\uff0c\u201c\u6211\u4eec\u53c8\u4e0d\u662f\u4e0d\u8fd1\u60c5\u7406\u3002\u201d</p><p>\u743c\u6069\u6ca1\u6709\u7acb\u523b\u7b54\u5e94\u6216\u8005\u62d2\u7edd\uff0c\u4ed6\u60f3\u4e86\u60f3\uff0c\u628a\u838e\u73de\u514b\u53eb\u4e86\u8fc7\u6765\uff0c\u8be2\u95ee\u8fd9\u51b3\u6597\u5927\u8d5b\u7684\u5177\u4f53\u89c4\u5219\uff0c\u5230\u5e95\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u3002</p><p>\u201c\u51b3\u6597\u5927\u8d5b\u4ee5\u56e2\u4f53\u4e3a\u5355\u4f4d\u62a5\u540d\uff0c\u6bcf\u56e2\u5fc5\u987b\u516d\u4eba\uff0c\u201d\u838e\u73de\u514b\u786e\u8ba4\u4e86\u8475\u9732\u7684\u8bf4\u6cd5\uff0c\u201c\u6bd4\u8d5b\u65b9\u5f0f\u662f\u5404\u56e2\u62bd\u7b7e\u5bf9\u51b3\uff0c\u80dc\u8005\u8fdb\u5165\u4e0b\u4e00\u8f6e\u56e2\u6218\u3002\u6bcf\u8f6e\u6bd4\u8d5b\uff0c\u53cc\u65b9\u4e00\u5bf9\u4e00\u8f6e\u6d41\u4e0a\u9635\uff0c\u80dc\u5229\u8005\u53ef\u4ee5\u7ee7\u7eed\u8fdb\u884c\u540e\u9762\u7684\u6bd4\u8d5b\uff0c\u76f4\u5230\u88ab\u6253\u5012\u6216\u8005\u81ea\u5df1\u653e\u5f03\u3002\u201d</p><p>\u201c\u6bcf\u8f6e\u51e0\u573a\u6bd4\u8d5b\u3002\u201d</p><p>\u201c\u65e2\u7136\u6bcf\u56e2\u516d\u4eba\uff0c\u90a3\u81ea\u7136\u6bcf\u8f6e\u5c31\u662f\u516d\u573a\u4e86\u3002\u201d</p><p>\u201c\u516d\u573a\uff1f\u201d\u743c\u6069\u8be7\u5f02\uff0c\u201c\u54ea\u6709\u8fd9\u79cd\u9053\u7406\uff0c\u6211\u53ea\u77e5\u9053\u4e09\u5c40\u4e24\u80dc\u4e94\u5c40\u4e09\u80dc\u751a\u81f3\u4e03\u5c40\u56db\u80dc\u7684\uff0c\u516d\u573a\u6bd4\u8d5b\u90a3\u5c82\u4e0d\u662f\u8981\u8d62\u56db\u573a\u624d\u884c\u3002\u201d</p><p>\u201c\u4f60\u8bf4\u9519\u4e86\uff0c\u8d62\u56db\u573a\u90fd\u4e0d\u884c\uff0c\u9664\u975e\u4e00\u65b9\u516d\u4eba\u5168\u90fd\u6218\u8d25\u6216\u8005\u653e\u5f03\u9000\u51fa\uff0c\u5426\u5219\u4e0d\u80fd\u5b9a\u80dc\u8d1f\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u53d8\u6001\u7684\u89c4\u5219\u2026\u2026\u4e0d\u4f1a\u662f\u683c\u62c9\u5179\u7279\u5b9a\u7684\u5427\u3002\u201d</p><p>\u201c\u7b54\u5bf9\u4e86\uff0c\u683c\u62c9\u5179\u7279\u662f\u65ad\u57df\u9547\u51b3\u6597\u5927\u8d5b\u7684\u63d0\u8bae\u8005\u3001\u521b\u529e\u4eba\u517c\u9996\u5e2d\u987e\u95ee\uff0c\u89c4\u5219\u4e5f\u662f\u8bf7\u4ed6\u5236\u5b9a\u7684\u3002\u4f60\u8981\u77e5\u9053\uff0c\u6df1\u6e0a\u91cc\u60f3\u627e\u80fd\u591f\u4e0a\u9635\u6253\u67b6\u7684\u6076\u9b54\u591a\u4e86\u53bb\uff0c\u60f3\u627e\u51e0\u4e2a\u80fd\u5236\u5b9a\u89c4\u5219\u7684\u6076\u9b54\u53ef\u771f\u4e0d\u5bb9\u6613\u3002\u201d</p><p>\u201c\u53ef\u662f\u4ed6\u8fd9\u4e2a\u5fc3\u7406\u53d8\u6001\u80fd\u4e0d\u80fd\u4e0d\u8981\u628a\u81ea\u5df1\u7684\u4e2a\u4eba\u7231\u597d\u5230\u5904\u5f3a\u52a0\u4e8e\u4eba\u554a\u3002\u201d</p><p>\u201c\u4f60\u4e5f\u8bf4\u4e86\u554a\uff0c\u4ed6\u662f\u4e2a\u5fc3\u7406\u53d8\u6001\uff0c\u65e2\u7136\u8fd9\u6837\u4f60\u8fd8\u80fd\u8981\u6c42\u4ec0\u4e48\u5462\u3002\u201d</p><p>\u5f88\u663e\u7136\u743c\u6069\u6ca1\u6cd5\u8981\u6c42\u4ec0\u4e48\uff0c\u8fd9\u5e74\u5934\u53d8\u6001\u7684\u4eba\u592a\u591a\uff0c\u6b63\u5e38\u7684\u53cd\u800c\u5c11\uff0c\u683c\u62c9\u5179\u7279\u4ec5\u4ec5\u53ea\u662f\u5728\u9047\u5230\u201c\u516d\u201d\u8fd9\u4e2a\u6570\u5b57\u7684\u65f6\u5019\u4f1a\u72af\u5f3a\u8feb\u75c7\uff0c\u8fd9\u5df2\u7ecf\u5f88\u4e0d\u9519\u4e86\u3002\u6bd4\u8d77\u67d0\u4f4d\u6b63\u7b79\u5212\u7740\u8ba9\u5168\u4e16\u754c\u7684\u5de8\u9f99\u53d1\u75af\u7684\u5deb\u5996\uff0c\u4ee5\u53ca\u53e6\u5916\u4e00\u4f4d\u5145\u5f53\u5e2e\u51f6\u548c\u6280\u672f\u652f\u6301\u7684\u6b7b\u80d6\u5b50\uff0c\u683c\u62c9\u5179\u7279\u5b8c\u5168\u53ef\u4ee5\u7b97\u5f97\u4e0a\u662f\u5584\u826f\u4e86\uff0c\u5c31\u8fd9\u4e48\u70b9\u5c0f\u7231\u597d\uff0c\u5b9e\u5728\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\u3002</p><p>\u53cd\u6b63\u8475\u9732\u8bf4\u4e86\uff0c\u5979\u548c\u6b23\u5e03\u6253\u5934\u9635\uff0c\u516d\u573a\u6bd4\u8d5b\u5168\u7531\u5979\u4eec\u4e00\u8def\u6253\u4e0b\u53bb\u2014\u2014\u5982\u679c\u6253\u4e0d\u4e0b\u53bb\uff0c\u743c\u6069\u5c31\u76f4\u63a5\u8ba4\u8f93\u4e86\u4e8b\u3002\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u8bd5\u8bd5\u770b\u5427\uff0c\u81f3\u4e8e\u51fa\u54ea\u4e24\u4e2a\u540d\u989d\uff0c\u6885\u83f2\u65af\u81ea\u7136\u6392\u9664\uff0c\u4f0a\u8389\u96c5\u4e5f\u4e0d\u80fd\u4ee4\u4eba\u653e\u5fc3\uff0c\u8fd8\u662f\u81ea\u5df1\u548c\u838e\u73de\u514b\u6765\u597d\u4e86\uff0c\u8d77\u7801\u77e5\u9053\u5171\u540c\u8fdb\u9000\u3002</p><p>\u201c\u4e0d\u8fc7\u8fd9\u8fd8\u6709\u4e2a\u95ee\u9898\u554a\uff0c\u6211\u4eec\u8fd9\u79cd\u5584\u826f\u6b63\u6d3e\u7684\u4eba\u58eb\u4f1a\u8001\u8001\u5b9e\u5b9e\u5b88\u89c4\u77e9\uff0c\u6253\u6bd4\u8d5b\u53bb\u62ff\u5956\u54c1\uff0c\u8428\u9a6c\u65af\u7279\u96be\u9053\u4e5f\u4f1a\u8fd9\u6837\u5417\uff1f\u4ed6\u8bf4\u4e0d\u5b9a\u73b0\u5728\u5df2\u7ecf\u95ef\u8fdb\u7ea2\u8272\u5bff\u8863\u5bb6\u91cc\uff0c\u76f4\u63a5\u62a2\u4e86\u4e1c\u897f\u8d70\u4eba\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u4f1a\uff0c\u5982\u679c\u7ea2\u8272\u5bff\u8863\u8fd9\u4e48\u5bb9\u6613\u5c31\u88ab\u6253\u5012\uff0c\u90a3\u65ad\u57df\u9547\u7684\u4e3b\u4eba\u4f4d\u7f6e\u4e0a\u65e9\u5c31\u6362\u4eba\u4e86\uff0c\u201d\u8475\u9732\u8bf4\uff0c\u201c\u8fd9\u91cc\u53ef\u662f\u5979\u7684\u5bb6\uff0c\u5979\u53ef\u4ee5\u52a8\u5ff5\u4e4b\u95f4\u53ec\u96c6\u65e0\u6570\u7684\u6076\u9b54\u4ec6\u4ece\uff0c\u4ee5\u6211\u4eec\u5bf9\u8428\u9a6c\u65af\u7279\u7684\u4e86\u89e3\uff0c\u4ed6\u529e\u4e0d\u5230\u3002\u201d</p><p>\u201c\u6240\u4ee5\u4ed6\u4e5f\u53ea\u80fd\u4e00\u573a\u4e00\u573a\u6253\u6bd4\u8d5b\uff1f\u8bf4\u4e0d\u5b9a\u8fd9\u5176\u5b9e\u662f\u4e2a\u9677\u9631\uff0c\u7ea2\u8272\u5bff\u8863\u65e9\u5c31\u548c\u4ed6\u52fe\u7ed3\uff0c\u79c1\u4e0b\u628a\u5370\u7ae0\u7ed9\u4ed6\u4e86\u3002\u201d</p><p>\u201c\u6211\u76f8\u4fe1\u8fd9\u79cd\u53ef\u80fd\u6027\u4e5f\u4e0d\u9ad8\uff0c\u4ee5\u5f80\u7684\u8bb0\u5f55\u663e\u793a\uff0c\u7ea2\u8272\u5bff\u8863\u4e0e\u5176\u8bf4\u662f\u6076\u9b54\uff0c\u66f4\u50cf\u662f\u9b54\u9b3c\uff0c\u8fd9\u79cd\u516c\u7136\u8fdd\u7ea6\u7684\u4e8b\u60c5\u5979\u4e0d\u4f1a\u505a\u3002\u201d</p><p>\u5207\uff0c\u9b54\u9b3c\u5c31\u7b97\u6bd4\u6076\u9b54\u597d\u70b9\uff0c\u5230\u5e95\u4e5f\u4e0d\u662f\u597d\u4e1c\u897f\uff0c\u5c45\u7136\u8fd8\u4f1a\u6709\u4eba\u76f8\u4fe1\u4ed6\u4eec\u7684\u4fe1\u8a89\uff0c\u90a3\u771f\u662f\u8111\u888b\u8fdb\u6c34\u4e86\u2026\u2026\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u81ea\u5df1\u4f3c\u4e4e\u4e5f\u5f88\u5927\u7a0b\u5ea6\u5730\u76f8\u4fe1\u6b27\u51ef\u7684\u4fe1\u8a89\uff0c\u90a3\u5bb6\u4f19\u8fd8\u662f\u4e2a\u6076\u9b54\u5462\uff0c\u770b\u6765\u4fe1\u8a89\u679c\u7136\u662f\u8981\u9760\u5386\u53f2\u8bb0\u5f55\u6765\u5efa\u7acb\u7684\u3002</p><p>\u201c\u90a3\u6211\u5c31\u6ca1\u4ec0\u4e48\u95ee\u9898\u4e86\uff0c\u4e0d\u8fc7\u53e6\u5916\u6709\u4e00\u4e2a\u8981\u6c42\u3002\u201d</p><p>\u201c\u8bf7\u8bf4\u3002\u201d</p><p>\u201c\u5982\u679c\u6700\u540e\u80fd\u591f\u5f97\u5230\u7b2c\u4e00\u540d\uff0c\u62ff\u5230\u5370\u7ae0\uff0c\u5e76\u4e14\u6b64\u524d\u5df2\u7ecf\u6740\u6b7b\u4e86\u8428\u9a6c\u65af\u7279\u7684\u8bdd\uff0c\u201d\u743c\u6069\u8c28\u614e\u5730\u63aa\u8f9e\uff0c\u201c\u6211\u5e0c\u671b\u8fd9\u679a\u5370\u7ae0\u80fd\u5f52\u6211\u6240\u6709\u3002\u201d</p><p>\u201c\u4f60\u8981\u5b83\uff1f\u201d\u8475\u9732\u8be7\u5f02\uff0c\u201c\u4f60\u65e2\u7136\u77e5\u9053\u5b83\u7684\u6765\u5386\uff0c\u90a3\u5c31\u5e94\u8be5\u6e05\u695a\uff0c\u5b83\u5bf9\u4f60\u800c\u8a00\u6839\u672c\u5c31\u662f\u5e9f\u7269\u3002\u8428\u9a6c\u65af\u7279\u4e5f\u53ea\u662f\u770b\u4e2d\u5b83\u672c\u8eab\u8574\u542b\u7684\u529b\u91cf\u8db3\u591f\u5f3a\uff0c\u7528\u6765\u505a\u7075\u9b42\u5bb9\u5668\u800c\u5df2\u3002\u201d</p><p>\u201c\u6211\u5f53\u7136\u4e0d\u9700\u8981\u547d\u5323\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u6211\u559c\u6b22\u6536\u96c6\u6587\u7269\u3002\u201d</p><p>\u8475\u9732\u7591\u60d1\u5730\u770b\u7740\u4ed6\uff0c\u201c\u6211\u4e0d\u662f\u5f88\u80fd\u7406\u89e3\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u65e2\u7136\u5b83\u5bf9\u4f60\u5e76\u6ca1\u6709\u4ec0\u4e48\u7528\u5904\uff0c\u53c8\u4f55\u5fc5\u653e\u5728\u624b\u4e2d\u62db\u6765\u5371\u9669\u3002\u201d</p><p>\u201c\u6240\u4ee5\u6211\u8bf4\u4e86\uff0c\u524d\u63d0\u662f\u5df2\u7ecf\u6740\u6b7b\u4e86\u8428\u9a6c\u65af\u7279\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u5f53\u7136\u4e0d\u60f3\u88ab\u4ed6\u76ef\u4e0a\u3002\u81f3\u4e8e\u5b83\u5bf9\u6211\u6709\u6ca1\u6709\u7528\u5904\uff0c\u6211\u60f3\u8bc4\u5224\u8005\u5e94\u8be5\u662f\u6211\u800c\u4e0d\u662f\u522b\u4eba\uff0c\u6211\u559c\u6b22\u5b83\uff0c\u90a3\u81ea\u7136\u5c31\u662f\u6709\u7528\u7684\uff0c\u8fd9\u662f\u6211\u7684\u7231\u597d\u3002\u201d</p><p>\u8475\u9732\u770b\u770b\u5750\u5728\u6c99\u53d1\u4e0a\u7684\u6b23\u5e03\uff0c\u89c1\u5979\u6ca1\u4ec0\u4e48\u53cd\u5e94\uff0c\u4fbf\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u4e00\u8a00\u4e3a\u5b9a\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5982\u679c\u6211\u4eec\u80fd\u591f\u6740\u6b7b\u8428\u9a6c\u65af\u7279\uff0c\u62ff\u5230\u5370\u7ae0\uff0c\u90a3\u4e48\u5b83\u5c31\u662f\u4f60\u7684\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u4ea4\u6613\u8fbe\u6210\uff0c\u6c14\u6c1b\u4e8e\u662f\u91cd\u5f52\u548c\u8c10\uff0c\u8475\u9732\u8d77\u8eab\u544a\u8f9e\uff0c\u548c\u6b23\u5e03\u4e00\u8d77\u79bb\u5f00\u3002\u838e\u73de\u514b\u4e5f\u88ab\u743c\u6069\u6e05\u9000\uff0c\u623f\u95f4\u91cc\u53c8\u53ea\u5269\u4e0b\u6885\u83f2\u65af\u3001\u51db\u548c\u4ed6\u81ea\u5df1\u4e09\u4e2a\u4eba\u3002</p><p>\u6b23\u5e03\u5728\u7684\u65f6\u5019\uff0c\u51db\u4e00\u76f4\u5782\u624b\u4f8d\u7acb\u5728\u65c1\u8fb9\uff0c\u4eff\u4f5b\u4e00\u4e2a\u4e56\u4e56\u5973\uff1b\u7b49\u6b23\u5e03\u4e00\u8d70\uff0c\u51db\u660e\u663e\u5c31\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u6574\u4e2a\u4eba\u90fd\u4e00\u4e0b\u5b50\u53d8\u5f97\u707f\u70c2\u8d77\u6765\u4f3c\u7684\u3002\u201c\u54c7\uff0c\u201d\u5979\u5938\u5f20\u5730\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u5f80\u6c99\u53d1\u4e0a\u4e00\u8eba\uff0c\u8ba9\u6574\u4e2a\u8eab\u4f53\u90fd\u9677\u8fdb\u67d4\u8f6f\u7684\u76ae\u57ab\u4e4b\u4e2d\uff0c\u201c\u4f60\u4eec\u7ec8\u4e8e\u8bf4\u5b8c\u4e86\u554a\u3002\u201d</p><p>\u201c\u4f60\u4e5f\u522b\u603b\u50cf\u4e2a\u5c0f\u5b69\u5b50\u4f3c\u7684\uff0c\u201d\u6885\u83f2\u65af\u5fae\u5fae\u7b11\u7740\uff0c\u201c\u8be5\u957f\u5927\u70b9\u4e86\u3002\u201d</p><p>\u201c\u53bb\u53bb\uff0c\u522b\u8bf4\u5f97\u8fd9\u4e48\u4e00\u672c\u6b63\u7ecf\uff0c\u4f60\u8fd8\u6bd4\u6211\u5c0f\u5462\uff0c\u201d\u51db\u52aa\u529b\u8212\u5c55\u7740\u56db\u80a2\uff0c\u51e0\u4e4e\u628a\u6574\u4e2a\u6c99\u53d1\u90fd\u5360\u6ee1\u4e86\uff0c\u6885\u83f2\u65af\u4e0d\u5f97\u4e0d\u7a0d\u7a0d\u5f80\u540e\u8eb2\u907f\uff0c\u514d\u5f97\u88ab\u5979\u7684\u80f3\u818a\u78b0\u5230\uff0c\u201c\u6211\u4e0d\u662f\u4e00\u76f4\u90fd\u8fd9\u6837\u4e48\u3002\u201d</p><p>\u201c\u6709\u5916\u4eba\u5728\uff0c\u4f60\u603b\u4e5f\u8be5\u6ce8\u610f\u4e9b\u5427\u3002\u201d</p><p>\u201c\u8c01\u662f\u5916\u4eba\uff0c\u201d\u51db\u968f\u53e3\u53cd\u95ee\uff0c\u201c\u743c\u6069\uff1f\u4ed6\u4e0d\u7b97\uff0c\u89c6\u4e3a\u4e0d\u5b58\u5728\u3002\u201d</p><p>\u201c\u53ef\u662f\u6211\u5b58\u5728\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u6211\u5f53\u4f60\u4e0d\u5b58\u5728\u4e0d\u5c31\u662f\u4e86\uff0c\u201d\u51db\u6487\u6487\u5634\uff0c\u201c\u771f\u65e0\u8da3\uff0c\u4e0d\u77e5\u9053\u827e\u5f25\u8587\u600e\u4e48\u5c31\u4f1a\u770b\u4e0a\u4f60\u3002\u201d</p><p>\u201c\u5404\u4eba\u6709\u5404\u4eba\u7684\u2026\u2026\u201d\u743c\u6069\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u672c\u6765\u60f3\u8bf4\u201c\u7f18\u6cd5\u201d\uff0c\u7136\u800c\u5728\u901a\u7528\u8bed\u91cc\u641c\u5bfb\u4e86\u534a\u5929\uff0c\u627e\u4e0d\u5230\u5bf9\u5e94\u7684\u8bcd\u6c47\uff0c\u201c\u8fd0\u6c14\uff0c\u201d\u4ed6\u6700\u540e\u8bf4\uff0c\u201c\u5404\u6709\u5404\u7684\u8fd0\u6c14\u3002\u201d</p><p>\u201c\u90a3\u4f60\u7684\u8fd0\u6c14\u53ef\u771f\u597d\uff0c\u6211\u90fd\u5ac9\u5992\u4e86\uff0c\u201d\u51db\u4e00\u9aa8\u788c\u4ece\u6c99\u53d1\u4e0a\u722c\u8d77\u6765\uff0c\u8dea\u5750\u5728\u6885\u83f2\u65af\u8eab\u524d\uff0c\u201c\u8bf4\u771f\u7684\uff0c\u827e\u5f25\u8587\uff0c\u4f60\u5e72\u561b\u559c\u6b22\u4ed6\u5462\uff0c\u6211\u89c9\u5f97\u6211\u6bd4\u4ed6\u5f3a\u591a\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u6709\u53ef\u6bd4\u6027\u5417\uff1f\u201d</p><p>\u201c\u6709\u554a\uff0c\u6211\u4eec\u90fd\u662f\u4eba\u561b\uff0c\u53ea\u4e0d\u8fc7\u4f60\u662f\u4e2a\u666e\u901a\u4eba\u7c7b\uff0c\u6211\u662f\u9f99\u8109\u2014\u2014\u827e\u5f25\u8587\uff0c\u6211\u76f8\u4fe1\u4f60\u80af\u5b9a\u6ca1\u6709\u79cd\u65cf\u6b67\u89c6\u5bf9\u5427\u3002\u201d</p><p>\u5e9f\u8bdd\uff0c\u6885\u83f2\u65af\u81ea\u5df1\u5c31\u662f\u795e\u4eba\u7ed3\u5408\u800c\u751f\u7684\u795e\u5b50\uff0c\u5979\u96be\u9053\u8fd8\u6b67\u89c6\u81ea\u5df1\u4e0d\u6210\u3002\u5176\u5b9e\u6211\u4e5f\u672a\u5fc5\u662f\u666e\u901a\u4eba\u7c7b\uff0c\u81f3\u5c11\u662f\u4e2a\u7a7f\u8d8a\u8005\u3002\u5f53\u7136\u4f60\u662f\u4e2a\u5c0f\u9f99\u5973\u5012\u6ca1\u9519\u2014\u2014\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u5927\u5bb6\u8fd8\u771f\u90fd\u4e0d\u662f\u6b63\u5e38\u4eba\uff0c\u9694\u58c1\u8fd8\u6709\u4e24\u4e2a\u795e\u5b50\u9009\u6c11\u52a0\u4e00\u53ea\u66fe\u7ecf\u662f\u795e\u5b50\u7684\u9b45\u9b54\u2026\u2026</p><p>\u795e\u5b50\u3001\u9f99\u5973\u548c\u7a7f\u8d8a\u8005\u7684\u8c08\u8bdd\u6ca1\u6709\u7ee7\u7eed\u8fdb\u884c\u592a\u4e45\uff0c\u56e0\u4e3a\u6700\u4e3b\u8981\u7684\u5f53\u4e8b\u4eba\u6885\u83f2\u65af\u8fd8\u5f88\u865a\u5f31\uff0c\u4e0d\u80fd\u591a\u8bf4\u8bdd\u3002\u51db\u53ea\u662f\u6027\u5b50\u8df3\u8131\u968f\u610f\u4e9b\uff0c\u4e0d\u662f\u6ca1\u773c\u8272\uff0c\u89c1\u72b6\u4e5f\u5c31\u65e9\u65e9\u544a\u8f9e\uff0c\u81ea\u5df1\u5148\u56de\u623f\u95f4\u4f11\u606f\u3002</p><p>\u743c\u6069\u5173\u4e0a\u95e8\uff0c\u66ff\u6885\u83f2\u65af\u892a\u4e0b\u8863\u88f3\uff0c\u62b1\u5979\u8fdb\u6d74\u5ba4\u3002\u81ea\u4ece\u5230\u4e86\u6df1\u6e0a\uff0c\u9ad8\u6e29\u9177\u70ed\uff0c\u53c8\u8981\u8d76\u8def\uff0c\u5168\u8eab\u65e9\u5c31\u88ab\u6c57\u6e7f\u900f\uff0c\u5982\u4eca\u6ce1\u5728\u70ed\u6c34\u4e2d\uff0c\u53ea\u89c9\u5168\u8eab\u90fd\u6696\u6d0b\u6d0b\u7684\uff0c\u8bf4\u4e0d\u51fa\u7684\u8212\u7545\u3002</p><p>\u201c\u4f60\u8981\u90a3\u679a\u5370\u7ae0\u505a\u4ec0\u4e48\uff0c\u201d\u6885\u83f2\u65af\u968f\u53e3\u95ee\uff0c\u201c\u6211\u6ca1\u542c\u8bf4\u4f60\u6709\u6536\u96c6\u6587\u7269\u7684\u7231\u597d\u554a\u3002\u201d</p><p>\u201c\u90a3\u4e1c\u897f\u6709\u70b9\u7279\u522b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u4e00\u8fb9\u66ff\u5979\u64e6\u62ed\u8eab\u4f53\uff0c\u201c\u6211\u603b\u89c9\u5f97\u6709\u4e9b\u7279\u522b\u719f\u6089\u7684\u611f\u89c9\u3002\u201d</p><p>\u201c\u4f60\u89c1\u8fc7\uff1f\u201d</p><p>\u201c\u5c31\u5728\u4e00\u5c0f\u65f6\u524d\u89c1\u8fc7\u3002\u201d</p><p>\u743c\u6069\u628a\u62dc\u8bbf\u7ea2\u8272\u5bff\u8863\u7684\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\uff0c\u91cd\u70b9\u662f\u63cf\u8ff0\u90a3\u679a\u767d\u7389\u5370\u7ae0\uff0c\u81f3\u4e8e\u6b64\u524d\u8def\u4e0a\u548c\u838e\u73de\u514b\u7684\u8c08\u8bdd\u5c31\u6ca1\u5fc5\u8981\u63d0\u8d77\u4e86\u3002\u201c\u90a3\u5370\u7ae0\u6211\u770b\u7740\u611f\u89c9\u5f88\u773c\u719f\uff0c\u201d\u743c\u6069\u542b\u7cca\u5730\u8bf4\uff0c\u201c\u603b\u89c9\u5f97\u50cf\u662f\u66fe\u7ecf\u5728\u54ea\u91cc\u89c1\u8fc7\u4f3c\u7684\u3002\u201d</p><p>\u6885\u83f2\u65af\u60f3\u4e86\u60f3\uff0c\u201c\u65e2\u7136\u662f\u5370\u7ae0\uff0c\u90a3\u5e94\u8be5\u6709\u6587\u5b57\u5427\uff0c\u201d\u5979\u95ee\uff0c\u201c\u662f\u4ec0\u4e48\u5185\u5bb9\uff1f\u201d</p><p>\u201c\u662f\u6709\u51e0\u4e2a\u5b57\uff0c\u4f46\u6211\u770b\u4e0d\u61c2\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53ef\u80fd\u662f\u4f0a\u739b\u65af\u5361\u7684\u6587\u5b57\u5427\u3002\u201d</p><p>\u201c\u53ef\u60dc\u6211\u5f53\u65f6\u4e0d\u5728\u573a\uff0c\u201d\u6885\u83f2\u65af\u6709\u4e9b\u9057\u61be\uff0c\u201c\u5982\u679c\u662f\u7a46\u7f55\u745e\u5fb7\u8bed\u6216\u8005\u6069\u745f\u8bed\uff0c\u90a3\u6211\u867d\u7136\u770b\u4e0d\u61c2\u5185\u5bb9\uff0c\u81f3\u5c11\u4e5f\u80fd\u8ba4\u51fa\u6765\u3002\u9664\u975e\u662f\u4f0a\u739b\u65af\u5361\u8bed\uff0c\u90a3\u6211\u5c31\u5b8c\u5168\u6ca1\u89c1\u8fc7\u4e86\u3002\u201d</p><p>\u201c\u65e2\u7136\u662f\u7687\u5ba4\u5947\u68b0\u5e08\u6240\u4f69\u4e4b\u7269\uff0c\u5f53\u7136\u662f\u4f0a\u739b\u65af\u5361\u8bed\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u786e\u5b9e\u662f\u5f88\u5947\u602a\u7684\u5b57\u7b26\uff0c\u5b8c\u5168\u770b\u4e0d\u61c2\u3002\u201d</p><p>\u201c\u90a3\u672a\u5fc5\u7684\uff0c\u6709\u4e00\u79cd\u8bf4\u6cd5\uff0c\u8bf4\u4f0a\u739b\u65af\u5361\u5176\u5b9e\u662f\u56db\u79cd\u8bed\u8a00\uff0c\u9664\u4e86\u901a\u884c\u7684\u4f0a\u739b\u65af\u5361\u8bed\u548c\u540e\u6765\u52a0\u5165\u7684\u7a46\u7f55\u745e\u5fb7\u3001\u6069\u745f\u8bed\uff0c\u8fd8\u6709\u4e00\u79cd\u79d8\u5bc6\u8bed\u8a00\uff0c\u662f\u53ea\u5728\u7687\u5ba4\u5185\u90e8\u901a\u7528\u7684\u3002\u8bf4\u4e0d\u5b9a\u662f\u90a3\u79cd\u79d8\u5bc6\u6587\u5b57\u5462\u3002\u201d</p><p>\u201c\u90a3\u79cd\u6587\u5b57\u53eb\u4ec0\u4e48\uff1f\u6709\u4ec0\u4e48\u7279\u5f81\u4e48\uff1f\u201d</p><p>\u6885\u83f2\u65af\u8f7b\u8f7b\u6447\u5934\uff0c\u201c\u4e0d\u6e05\u695a\u3002\u201d</p><p>\u201c\u6709\u6ca1\u6709\u63d0\u5230\u2026\u2026\u6bd4\u5982\u8bf4\u5927\u91cf\u523b\u5728\u9f9f\u7532\u517d\u9aa8\u4e0a\u4e4b\u7c7b\u7684\u8bf4\u6cd5\uff1f\u201d</p><p>\u201c\u6ca1\u542c\u8fc7\uff0c\u4f60\u600e\u4e48\u4f1a\u8fd9\u4e48\u95ee\uff1f\u201d\u6885\u83f2\u65af\u5947\u602a\uff0c\u201c\u54ea\u79cd\u6587\u5b57\u4e5f\u6ca1\u8fd9\u79cd\u8bf4\u6cd5\u5427\u3002\u201d</p><p>\u201c\u5514\uff0c\u6211\u968f\u53e3\u95ee\u95ee\u3002\u201d</p><p>\u743c\u6069\u628a\u8fd9\u4e2a\u95ee\u9898\u542b\u7cca\u8fc7\u53bb\uff0c\u6885\u83f2\u65af\u4e5f\u6ca1\u591a\u95ee\uff0c\u201c\u7136\u540e\u4f60\u56de\u6765\u8def\u4e0a\u5c31\u9047\u4e0a\u51db\u4e86\uff1f\u201d\u5979\u95ee\uff0c\u201c\u600e\u4e48\u548c\u6b23\u5e03\u6253\u8d77\u6765\uff1f\u5979\u4ee5\u4e3a\u4f60\u662f\u591c\u5973\u58eb\u7684\u9009\u6c11\uff1f\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5982\u679c\u4e0d\u662f\u6211\u53cd\u5e94\u5f97\u5feb\uff0c\u73b0\u5728\u53ea\u6015\u5df2\u7ecf\u88ab\u70e7\u6210\u7126\u70ad\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u4e8b\u5427\u3002\u201d</p><p>\u201c\u6ca1\u4e8b\u3002\u201d</p><p>\u201c\u90a3\u5c31\u597d\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u7565\u5e26\u6b49\u610f\u5730\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u5176\u5b9e\u6211\u4e5f\u4e0d\u592a\u559c\u6b22\u5979\uff0c\u4f46\u5979\u662f\u51db\u7684\u8001\u5e08\u2026\u2026\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u201d\u743c\u6069\u6253\u65ad\uff0c\u201c\u5176\u5b9e\u8981\u771f\u8bf4\u8d77\u6765\uff0c\u6211\u8fd8\u5f97\u611f\u8c22\u5979\u3002\u201d</p><p>\u201c\u554a\uff1f\u201d</p><p>\u201c\u5979\u8fd9\u6b21\u88ad\u51fb\uff0c\u8ba9\u6211\u7ec8\u4e8e\u80fd\u591f\u786e\u5b9a\u4e00\u4ef6\u4e8b\u60c5\uff0c\u201d\u743c\u6069\u6162\u6162\u8bf4\uff0c\u201c\u6211\u60f3\uff0c\u73b0\u5728\u6211\u77e5\u9053\u81ea\u5df1\u8eab\u4f53\u91cc\u5f71\u706b\u5230\u5e95\u662f\u600e\u4e48\u6765\u7684\u4e86\u3002\u201d</p></article>"}}}]);
