(window.webpackJsonp=window.webpackJsonp||[]).push([[1523],{1546:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e00\u8282 \u9985\u997c",content:"<article><h2>\u7b2c\u4e09\u5341\u4e00\u8282 \u9985\u997c</h2><p>\u743c\u6069\u9047\u89c1\u6b23\u5e03\u548c\u51db\uff0c\u88ab\u6b23\u5e03\u7a81\u7136\u88ad\u51fb\uff0c\u867d\u7136\u6700\u540e\u6ca1\u4e8b\uff0c\u5374\u4e5f\u7ec8\u7a76\u4ee4\u4eba\u4e0d\u5feb\u3002\u5f53\u7136\uff0c\u5982\u679c\u8981\u8bf4\u9053\u7406\u7684\u8bdd\uff0c\u9b54\u6cd5\u5973\u795e\u548c\u6697\u591c\u5973\u795e\u662f\u6b7b\u654c\uff0c\u6b23\u5e03\u53d1\u73b0\u743c\u6069\u6709\u5f71\u706b\uff0c\u5224\u65ad\u5bf9\u65b9\u662f\u591c\u5973\u58eb\u7684\u9009\u6c11\uff0c\u76f4\u63a5\u52a8\u624b\uff0c\u5012\u4e5f\u4e0d\u80fd\u8bf4\u6709\u4ec0\u4e48\u4e0d\u5bf9\u3002\u8fd9\u5c31\u50cf\u743c\u6069\u5728\u8def\u4e0a\u649e\u5230\u4e00\u53ea\u6076\u9b54\uff0c\u4ed6\u4e5f\u80af\u5b9a\u4e8c\u8bdd\u4e0d\u8bf4\u5c31\u52a8\u624b\uff0c\u96be\u9053\u8fd8\u4f1a\u5750\u4e0b\u6765\u559d\u8336\u804a\u5929\u4e0d\u6210\u3002\u53ea\u662f\u5bf9\u65b9\u56fa\u7136\u6ca1\u4ec0\u4e48\u9519\uff0c\u81ea\u5df1\u5374\u53ef\u4e5f\u66f4\u6ca1\u505a\u9519\u4ec0\u4e48\uff0c\u5982\u679c\u771f\u7684\u662f\u838e\u5c14\u9009\u6c11\uff0c\u90a3\u4e48\u88ab\u6253\u4e86\u5012\u4e5f\u662f\u672c\u5206\uff0c\u6ca1\u8bdd\u53ef\u8bf4\u2014\u2014\u7136\u800c\u743c\u6069\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u6709\u8fd9\u56de\u4e8b\u3002</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u98ce\u66b4\u5973\u738b\u7684\u813e\u6c14\u679c\u7136\u662f\u4e00\u5982\u4f20\u95fb\u4e2d\u7684\u706b\u7206\u3002\u540c\u6837\u90fd\u662f\u9009\u6c11\uff0c\u8475\u9732\u5f53\u65e5\u89c1\u5230\u743c\u6069\uff0c\u4e0d\u8fc7\u662f\u8138\u8272\u5fae\u53d8\uff0c\u56de\u53bb\u5429\u5490\u624b\u4e0b\u52a0\u500d\u63d0\u9632\uff0c\u4ec5\u6b64\u800c\u5df2\uff0c\u6b23\u5e03\u5374\u662f\u4e8c\u8bdd\u4e0d\u8bf4\u5c31\u6740\u4e86\u8fc7\u6765\uff0c\u5018\u82e5\u4e0d\u662f\u6709\u51db\u963b\u62e6\uff0c\u4e0d\u662f\u6b63\u597d\u5728\u65ad\u57df\u9547\u4e2d\uff0c\u6709\u6076\u9b54\u536b\u5175\u6765\u6405\u5c40\uff0c\u743c\u6069\u53ea\u6015\u8fd8\u771f\u51f6\u591a\u5409\u5c11\u2014\u2014\u5f53\u7136\uff0c\u5982\u679c\u4e0d\u662f\u5728\u65ad\u57df\u9547\uff0c\u81f3\u5c11\u4ed6\u4e5f\u80fd\u7528\u5b9d\u77f3\u8df3\u8dc3\u6212\u6307\u9003\u547d\u4e86\u3002</p><p>\u743c\u6069\u7b97\u4e0d\u4e0a\u775a\u7726\u5fc5\u62a5\u7684\u4eba\uff0c\u4e9b\u8bb8\u5192\u72af\uff0c\u53ea\u8981\u540e\u679c\u4e0d\u4e25\u91cd\uff0c\u5f80\u5f80\u4e5f\u5c31\u5ffd\u7565\u8fc7\u53bb\uff0c\u5f53\u6ca1\u53d1\u751f\uff1b\u4f46\u4ed6\u5374\u4e5f\u4e0d\u662f\u4ee5\u5fb7\u62a5\u6028\u7684\u541b\u5b50\uff0c\u4e0d\u4f1a\u88ab\u4eba\u6253\u4e86\u53cd\u800c\u8fd8\u9ad8\u5174\uff0c\u5c31\u7b97\u770b\u5728\u51db\u7684\u4efd\u4e0a\u4e0d\u505a\u8ba1\u8f83\uff0c\u5fc3\u4e2d\u4e0d\u5feb\u662f\u96be\u514d\u7684\u3002\u6885\u83f2\u65af\u548c\u4ed6\u76f8\u5904\u65e5\u4e45\uff0c\u77e5\u9053\u6027\u60c5\uff0c\u6240\u4ee5\u542c\u4ed6\u4e00\u5f00\u59cb\u8bf4\u8981\u201c\u611f\u8c22\u201d\u6b23\u5e03\uff0c\u81ea\u7136\u5927\u662f\u60ca\u8bb6\uff0c\u51e0\u4e4e\u6000\u7591\u81ea\u5df1\u592a\u8fc7\u75b2\u5026\uff0c\u542c\u89c9\u51fa\u9519\u3002\u7b49\u518d\u542c\u4ed6\u8bf4\u201c\u77e5\u9053\u81ea\u5df1\u8eab\u4f53\u91cc\u5f71\u706b\u5230\u5e95\u662f\u600e\u4e48\u6765\u7684\u4e86\u201d\uff0c\u4e0d\u7531\u5f97\u7cbe\u795e\u4e00\u632f\u3002</p><p>\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u743c\u6069\u6ca1\u5fd9\u7740\u56de\u7b54\uff0c\u4ece\u80cc\u540e\u5c06\u5979\u62b1\u4f4f\uff0c\u4f4e\u5934\u4eb2\u543b\u5c11\u5973\u7684\u8138\u988a\uff0c\u6162\u6162\u79fb\u5230\u8033\u540e\uff0c\u8f7b\u8f7b\u54ac\u556e\u7cbe\u81f4\u7684\u8033\u5782\u3002\u6885\u83f2\u65af\u8eab\u4f53\u4e0d\u7531\u81ea\u4e3b\u8f7b\u98a4\u8d77\u6765\uff0c\u8fd9\u5730\u65b9\u662f\u5979\u7684\u654f\u611f\u70b9\uff0c\u8eab\u4f53\u865a\u5f31\u4e4b\u4e0b\u611f\u89c9\u66f4\u52a0\u5f3a\u70c8\uff0c\u4e00\u9635\u9635\u7684\u9165\u9ebb\u611f\u50cf\u7535\u6d41\u4e00\u6837\u4ea4\u7ec7\u5168\u8eab\uff0c\u201c\u522b\u95f9\u3002\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u5374\u662f\u5168\u8eab\u8f6f\u7ef5\u7ef5\u7684\uff0c\u63d0\u4e0d\u8d77\u534a\u70b9\u529b\u6c14\uff0c\u53ea\u80fd\u4efb\u4ed6\u8f7b\u8584\u3002</p><p>\u201c\u6709\u4e9b\u57fa\u672c\u524d\u63d0\uff0c\u6211\u4eec\u5f97\u5148\u786e\u5b9a\uff0c\u201d\u743c\u6069\u5728\u5979\u8033\u8fb9\u4f4e\u58f0\u8bf4\uff0c\u53f3\u624b\u63fd\u7740\u5979\u7684\u8170\u80a2\uff0c\u5de6\u624b\u5374\u6084\u6084\u79fb\u4e0a\u80f8\u53e3\uff0c\u611f\u89c9\u4e00\u56e2\u9999\u8f6f\u67d4\u5ae9\u76c8\u6ee1\u638c\u5fc3\uff0c\u628a\u63e1\u4e0d\u4f4f\uff0c\u201c\u5514\uff0c\u4f3c\u4e4e\u53d8\u5927\u4e86\u2026\u2026\u201d</p><p>\u201c\u8bf4\u6b63\u4e8b\u3002\u201d\u6885\u83f2\u65af\u5a07\u55d4\u3002</p><p>\u201c\u8fd9\u5c31\u662f\u6b63\u4e8b\u2026\u2026\u597d\u5427\uff0c\u9996\u5148\u7b2c\u4e00\u70b9\u8981\u786e\u5b9a\u7684\u4e8b\u5b9e\u662f\uff1a\u6211\u6709\u5f71\u706b\u3002\u201d</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u70b9\u5934\uff0c\u8fd9\u662f\u5979\u610f\u6599\u4e4b\u4e2d\u7684\u4e8b\u60c5\u3002\u8475\u9732\u8fd9\u4e48\u8bf4\uff0c\u6b23\u5e03\u4e5f\u8fd9\u4e48\u8bf4\uff0c\u5979\u4eec\u90fd\u662f\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\uff0c\u5bf9\u5f71\u706b\u6709\u8d85\u4e4e\u5e38\u4eba\u7684\u654f\u9510\uff0c\u800c\u4e14\u603b\u4e0d\u81f3\u4e8e\u4e24\u4e2a\u4eba\u4e00\u9f50\u90fd\u8ba4\u9519\u4e86\uff0c\u8fd9\u79cd\u6982\u7387\u5b9e\u5728\u592a\u4f4e\uff0c\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\u3002</p><p>\u53ea\u662f\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u743c\u6069\u4fbf\u5f53\u771f\u662f\u62e5\u6709\u5f71\u706b\uff0c\u800c\u5f71\u706b\u53c8\u786e\u786e\u5b9e\u5b9e\u662f\u6697\u591c\u5973\u795e\u838e\u5c14\u7684\u795e\u529b\uff0c\u548c\u9b54\u6cd5\u5973\u795e\u6388\u4e88\u9009\u6c11\u7684\u94f6\u706b\u662f\u7c7b\u578b\u76f8\u540c\u6027\u8d28\u9006\u53cd\u7684\u5b58\u5728\uff0c\u8fd9\u662f\u65e0\u53ef\u5426\u8ba4\u7684\u4e8b\u5b9e\uff0c\u795e\u529b\u53ea\u6709\u795e\u7947\u624d\u80fd\u6388\u4e88\uff0c\u800c\u63a5\u53d7\u795e\u529b\u7684\u51e1\u4eba\u662f\u4e3a\u9009\u6c11\u2014\u2014\u8fd9\u4e9b\u662f\u5e38\u8bc6\uff0c\u6216\u8bb8\u5bf9\u4e8e\u666e\u901a\u4eba\u4e0d\u662f\uff0c\u4f46\u5bf9\u4e8e\u6885\u83f2\u65af\u800c\u8a00\u5c31\u662f\u5e38\u8bc6\u3002\u90a3\u4e48\u987a\u7406\u6210\u7ae0\u7684\u63a8\u8bba\uff0c\u5c31\u662f\u201c\u743c\u6069\u662f\u838e\u5c14\u7684\u9009\u6c11\u201d\u3002</p><p>\u7136\u800c\u743c\u6069\u5426\u8ba4\u81ea\u5df1\u662f\u9009\u6c11\u3002</p><p>\u6240\u4ee5\u6885\u83f2\u65af\u76f8\u4fe1\u4ed6\u4e0d\u662f\u9009\u6c11\u3002</p><p>\u4f46\u8fd9\u6837\u4e00\u6765\uff0c\u77db\u76fe\u4fbf\u4ea7\u751f\u4e86\uff0c\u4e00\u8fb9\u662f\u5e38\u8bc6\u548c\u903b\u8f91\uff0c\u4e00\u8fb9\u662f\u60c5\u4eba\u95f4\u7684\u4fe1\u4efb\uff0c\u7eb5\u7136\u82f1\u9510\u679c\u51b3\u5982\u6885\u83f2\u65af\uff0c\u4e5f\u4e0d\u77e5\u9053\u5230\u5e95\u5982\u4f55\u9009\u62e9\uff0c\u53ea\u80fd\u6682\u65f6\u9003\u907f\uff0c\u4e0d\u53bb\u8ffd\u95ee\uff0c\u4e0d\u53bb\u60f3\u5b83\u2014\u2014\u6216\u8005\u8bf4\uff0c\u5979\u5728\u7b49\u5f85\u743c\u6069\u7684\u89e3\u91ca\u3002</p><p>\u5979\u76f8\u4fe1\u743c\u6069\u4f1a\u7ed9\u5979\u4e00\u4e2a\u89e3\u91ca\u3002</p><p>\u4f46\u743c\u6069\u5e76\u4e0d\u7740\u6025\uff0c\u4ed6\u53ea\u662f\u8f7b\u8f7b\u5730\u63c9\u634f\u7740\u5c11\u5973\u7684\u80f8\u90e8\uff0c\u611f\u53d7\u90a3\u5c16\u5cf0\u4e00\u70b9\u6e10\u6e10\u575a\u633a\uff0c\u6469\u64e6\u7740\u638c\u5fc3\uff0c\u201c\u7b2c\u4e8c\u70b9\u8981\u786e\u5b9a\u7684\u4e8b\u5b9e\u662f\uff0c\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u201c\u6211\u4e0d\u662f\u591c\u5973\u58eb\u7684\u9009\u6c11\uff0c\u56e0\u4e3a\u6211\u5e76\u4e0d\u771f\u6b63\u62e5\u6709\u5f71\u706b\u3002\u201d</p><p>\u5e76\u4e0d\u771f\u6b63\u62e5\u6709\uff1f</p><p>\u201c\u8475\u9732\u548c\u6b23\u5e03\u7684\u94f6\u706b\u5a01\u529b\uff0c\u6211\u4eec\u90fd\u5df2\u7ecf\u6e05\u695a\u770b\u5230\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u94f6\u706b\u5728\u5979\u4eec\u624b\u4e2d\u53ef\u4ee5\u5316\u4f5c\u5229\u7bad\uff0c\u53ef\u4ee5\u5851\u6210\u76fe\u724c\uff0c\u53ef\u4ee5\u62b5\u5fa1\u9b54\u6cd5\uff0c\u53ef\u4ee5\u6cbb\u7597\u75ca\u6108\uff0c\u65e0\u6240\u4e0d\u80fd\uff0c\u65e0\u6240\u4e0d\u6210\uff0c\u968f\u5fc3\u6240\u6b32\uff0c\u53d8\u5316\u4e07\u7aef\u3002\u4f46\u6211\u8eab\u4f53\u91cc\u7684\u5f71\u706b\uff0c\u80fd\u505a\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u743c\u6069\u5b8c\u5168\u4e0d\u80fd\u4e3b\u52a8\u8c03\u7528\u5f71\u706b\uff0c\u751a\u81f3\u5e73\u65f6\u90fd\u65e0\u6cd5\u5bdf\u89c9\u5230\u5f71\u706b\u7684\u5b58\u5728\uff0c\u53ea\u5728\u751f\u6b7b\u5371\u6025\u7684\u5173\u5934\u624d\u4f1a\u81ea\u52a8\u5192\u51fa\u6765\u6551\u547d\u3002\u8fd9\u4e0e\u5176\u8bf4\u662f\u743c\u6069\u201c\u62e5\u6709\u201d\u5f71\u706b\uff0c\u8fd8\u4e0d\u5982\u8bf4\u662f\u4ed6\u6682\u65f6\u201c\u4fdd\u7ba1\u201d\uff0c\u522b\u8bf4\u6240\u6709\u6743\uff0c\u5c31\u8fde\u4f7f\u7528\u6743\u90fd\u6ca1\u6709\u2014\u2014\u4e16\u754c\u4e0a\u54ea\u6709\u8fd9\u6837\u7684\u9009\u6c11\u3002</p><p>\u5176\u5b9e\u8fd9\u4e9b\u8ff9\u8c61\uff0c\u5982\u679c\u7559\u5fc3\u89c2\u5bdf\u5f88\u5bb9\u6613\u53d1\u89c9\uff0c\u53ea\u662f\u8475\u9732\u3001\u6b23\u5e03\u4efd\u5c5e\u654c\u5bf9\uff0c\u5c31\u7b97\u4f1a\u5947\u602a\u743c\u6069\u4e0d\u7528\u5f71\u706b\u62b5\u6297\uff0c\u6700\u591a\u4e5f\u662f\u89c9\u5f97\u838e\u5c14\u8bc6\u4eba\u4e0d\u660e\uff0c\u6311\u4e86\u4e2a\u7b28\u86cb\u5f53\u9009\u6c11\u7f62\u4e86\u3002\u5018\u82e5\u654c\u4eba\u5168\u90fd\u662f\u8fd9\u79cd\u7b28\u86cb\uff0c\u90a3\u7b80\u76f4\u662f\u5929\u5e95\u4e0b\u6700\u7406\u60f3\u7684\u4e8b\u60c5\u4e86\uff0c\u9ad8\u5174\u90fd\u6765\u4e0d\u53ca\uff0c\u54ea\u91cc\u8fd8\u4f1a\u53bb\u4ed4\u7ec6\u8ffd\u7a76\u539f\u56e0\u3002\u6885\u83f2\u65af\u5173\u7cfb\u4eb2\u8fd1\uff0c\u539f\u672c\u662f\u5f88\u5bb9\u6613\u53d1\u73b0\u8fd9\u4e2a\u95ee\u9898\u7684\uff0c\u53ea\u662f\u5979\u81ea\u4ece\u5760\u5165\u6df1\u6e0a\u4ee5\u6765\uff0c\u4e00\u76f4\u5c31\u88ab\u4f4d\u9762\u89c4\u5219\u538b\u5236\uff0c\u865a\u5f31\u4e0d\u582a\uff0c\u4ec5\u6709\u7684\u4e00\u70b9\u7cbe\u529b\u4e5f\u90fd\u5728\u8def\u4e0a\u5e94\u4ed8\u838e\u73de\u514b\u65f6\u6d88\u8017\u6389\u4e86\uff0c\u6b64\u540e\u4fbf\u662f\u6655\u6655\u6c89\u6c89\uff0c\u5c45\u7136\u4e5f\u4e0d\u66fe\u60f3\u5230\u8fd9\u70b9\u3002</p><p>\u201c\u5bf9\u554a\uff0c\u201d\u5979\u604d\u7136\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u4f60\u6839\u672c\u5c31\u4e0d\u80fd\u8fd0\u7528\u5f71\u706b\uff0c\u53c8\u7b97\u4ec0\u4e48\u9009\u6c11\u3002\u201d</p><p>\u5728\u6b64\u4e4b\u524d\uff0c\u6885\u83f2\u65af\u548c\u838e\u73de\u514b\u8c08\u5224\u65f6\uff0c\u5c31\u66fe\u7ecf\u63d0\u53ca\u8fc7\u201c\u9009\u6c11\u62e5\u6709\u7684\u795e\u529b\u201d\u548c\u201c\u795e\u5b50\u62e5\u6709\u7684\u795e\u529b\u201d\u4e4b\u95f4\u7684\u533a\u522b\uff0c\u524d\u8005\u662f\u6743\u529b\uff0c\u540e\u8005\u662f\u80fd\u529b\u3002\u9009\u6c11\u7684\u795e\u529b\u4e43\u662f\u795e\u7947\u8d50\u4e88\uff0c\u7b49\u4e8e\u6743\u529b\u4e0b\u653e\uff0c\u8ba9\u4e0b\u5c5e\u81ea\u7531\u8fd0\u7528\uff0c\u4e5f\u968f\u65f6\u53ef\u4ee5\u6536\u56de\u2014\u2014\u743c\u6069\u867d\u7136\u6709\uff0c\u5374\u4e0d\u80fd\u8fd0\u7528\uff0c\u90a3\u5c31\u662f\u6839\u672c\u6ca1\u6709\u88ab\u795e\u7947\u5f00\u542f\u8fd9\u4e2a\u201c\u6743\u9650\u201d\uff0c\u53ea\u8d77\u5230\u4e00\u4e2a\u4fdd\u547d\u62a4\u7b26\u7684\u4f5c\u7528\u3002</p><p>\u90a3\u4e48\u95ee\u9898\u53c8\u4ea7\u751f\u4e86\uff0c\u65e2\u7136\u743c\u6069\u786e\u5b9e\u6709\u5f71\u706b\uff0c\u4f46\u53c8\u4e0d\u80fd\u4e3b\u52a8\u8fd0\u7528\u2014\u2014\u90a3\u8fd9\u5f71\u706b\u662f\u4ece\u54ea\u91cc\u5f97\u6765\u7684\u5462\uff1f</p><p>\u201c\u8fd9\u5c31\u662f\u63a5\u4e0b\u6765\u6700\u5173\u952e\u7684\u95ee\u9898\uff0c\u201d\u743c\u6069\u7684\u53f3\u624b\u8f7b\u8f7b\u987a\u7740\u5c11\u5973\u5e73\u5766\u7684\u5c0f\u8179\u4e0b\u6ed1\uff0c\u6307\u5c16\u6ca1\u5165\u5927\u817f\u6839\u90e8\u6700\u67d4\u5ae9\u6ed1\u817b\u7684\u6240\u5728\uff0c\u66ff\u5979\u6e05\u6d01\u4e0b\u8eab\u3002\u6885\u83f2\u65af\u6b63\u542c\u5f97\u4e13\u6ce8\uff0c\u4e5f\u4e0d\u6297\u62d2\u3002\u201c\u6211\u662f\u4ec0\u4e48\u65f6\u5019\u83b7\u5f97\u5f71\u706b\u7684\u5462\uff1f\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e00\u76f4\u5c31\u6709\uff0c\u53ea\u662f\u6211\u4e0d\u77e5\u9053\uff0c\u8fd8\u662f\u540e\u6765\u83b7\u5f97\u7684\uff1f\u201d</p><p>\u4ed6\u7684\u624b\u6307\u8f7b\u8f7b\u6e38\u52a8\uff0c\u63a2\u7d22\u7740\u719f\u6089\u5374\u4ece\u672a\u88ab\u771f\u6b63\u4fb5\u5165\u7684\u79d8\u5bc6\u82b1\u56ed\uff0c\u6885\u83f2\u65af\u4f4e\u4f4e\u5a07\u5598\uff0c\u89c9\u5f97\u6574\u4e2a\u8eab\u4f53\u90fd\u5f00\u59cb\u53d8\u5f97\u53d1\u70eb\u8d77\u6765\uff0c\u201c\u662f\u4ec0\u4e48\u65f6\u5019\uff1f\u201d\u5979\u52aa\u529b\u4fdd\u6301\u7740\u6e05\u9192\uff0c\u4f46\u5df2\u7ecf\u65e0\u6cd5\u601d\u8003\u3002</p><p>\u201c\u5c31\u5728\u53bb\u5e74\u6211\u4eec\u4ece\u585e\u5c14\u56de\u5230\u9634\u9b42\u57ce\u4e4b\u540e\uff0c\u53bb\u5e7d\u6697\u5730\u57df\u4e4b\u524d\uff0c\u5c31\u662f\u8fd9\u6bb5\u65f6\u95f4\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6b23\u5e03\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u5fd8\u4e86\uff0c\u6211\u4eec\u8fd9\u5e76\u4e0d\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u6b23\u5e03\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u6885\u83f2\u65af\u542b\u542b\u7cca\u7cca\u5730\u5e94\u7740\uff0c\u5df2\u7136\u6709\u4e9b\u795e\u667a\u8ff7\u4e71\uff0c\u743c\u6069\u7b11\u7740\uff0c\u8f7b\u8f7b\u543b\u4e0a\u5979\u7684\u5507\u74e3\uff0c\u4e00\u6cbe\u5373\u9000\uff0c\u201c\u5728\u963f\u683c\u62c9\u9686\uff0c\u201d\u4ed6\u63d0\u9192\uff0c\u201c\u5bf9\u4ed8\u5deb\u5996\u795e\u7ef4\u6c99\u4f26\u7684\u65f6\u5019\uff0c\u5f53\u65f6\u62c9\u6c83\u514b\u3001\u5e03\u96f7\u7eb3\u65af\u6bbf\u4e0b\u3001\u6b27\u51ef\u3001\u7ea2\u888d\u9996\u5e2d\u4eec\u90fd\u5728\u573a\uff0c\u6b64\u5916\u8fd8\u6709\u4e00\u4e2a\u4eba\uff0c\u5c31\u662f\u6b23\u5e03\u3002\u201d</p><p>\u201c\u5514\uff0c\u5979\u662f\u5728\u573a\u2026\u2026\u201d</p><p>\u201c\u5f53\u65f6\u6b23\u5e03\u662f\u4e3a\u51db\u800c\u6765\u7684\uff0c\u51db\u5c31\u5728\u6211\u65c1\u8fb9\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6b23\u5e03\u53ea\u8981\u770b\u5230\u51db\uff0c\u90a3\u5c31\u4e0d\u53ef\u80fd\u4e0d\u6ce8\u610f\u5230\u6211\u2014\u2014\u5c31\u4ece\u4eca\u5929\u8fd9\u79cd\u60c5\u51b5\u6765\u770b\uff0c\u4ee5\u5979\u7684\u6027\u683c\uff0c\u5982\u679c\u5f53\u65f6\u770b\u5230\u6211\u662f\u4e2a\u838e\u5c14\u9009\u6c11\uff0c\u4f60\u8bf4\u5979\u4f1a\u4e0d\u4f1a\u7acb\u523b\u4e00\u53d1\u94f6\u706b\u6254\u8fc7\u6765\uff1f\u201d</p><p>\u663e\u7136\u4f1a\u3002</p><p>\u201c\u90a3\u5979\u4e3a\u4ec0\u4e48\u6ca1\u6709\u8fd9\u4e48\u505a\u5462\uff1f\u201d</p><p>\u53ea\u6709\u4e00\u4e2a\u89e3\u91ca\uff0c\u5c31\u662f\u5f53\u65f6\u743c\u6069\u8fd8\u6ca1\u6709\u5f71\u706b\u3002</p><p>\u201c\u4ece\u963f\u683c\u62c9\u9686\u56de\u585e\u5c14\uff0c\u6ca1\u6709\u518d\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u7d27\u63a5\u7740\u6211\u4eec\u5c31\u56de\u4e86\u9634\u9b42\u57ce\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u7136\u540e\u8fc7\u4e86\u4e24\u4e2a\u6708\uff0c\u6211\u4eec\u53c8\u5230\u4e86\u5e7d\u6697\u5730\u57df\u3002\u201d</p><p>\u201c\u90a3\u4e3a\u4ec0\u4e48\u662f\u53bb\u5e7d\u6697\u5730\u57df\u4e4b\u524d\u5462\u3002\u201d</p><p>\u743c\u6069\u7684\u6307\u5c16\u987a\u7740\u5c11\u5973\u4e0b\u4f53\u6ea2\u51fa\u7684\u6da6\u6ed1\u6db2\u4f53\uff0c\u7f13\u7f13\u63a2\u5165\u5e7d\u8c37\uff0c\u7a0d\u7a0d\u6ca1\u5165\u4e00\u70b9\uff0c\u6709\u4e9b\u8f7b\u5fae\u7684\u9ebb\u75f9\u523a\u75db\u611f\u4f20\u6765\uff0c\u4f46\u4ed6\u6ca1\u5728\u4e4e\uff0c\u201c\u56e0\u4e3a\u5728\u6211\u4eec\u4ece\u5965\u56fe\u57ce\u51fa\u53d1\uff0c\u53bb\u4f0a\u5361\u6c99\u57ce\u4e4b\u524d\uff0c\u96c5\u8fbe\u4e0a\u5c09\u548c\u6211\u8bf4\u4e86\u4e00\u53e5\u8bdd\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4ed6\u8bf4\uff1a\u5973\u795e\u4e0e\u4f60\u540c\u5728\u3002\u201d</p><p>\u5f88\u666e\u901a\u7684\u95ee\u5019\u8bed\uff0c\u5f53\u65f6\u743c\u6069\u4e5f\u6ca1\u5728\u610f\uff0c\u4f46\u73b0\u5728\u542c\u8d77\u6765\u611f\u89c9\u4fbf\u5927\u6709\u4e0d\u540c\u3002\u5982\u679c\u4ec5\u4ec5\u7406\u89e3\u4e3a\u5ba2\u5957\uff0c\u90a3\u6ca1\u95ee\u9898\uff1b\u4f46\u5982\u679c\u8f83\u771f\u7684\u8bdd\uff0c\u743c\u6069\u5e76\u4e0d\u662f\u4e2a\u8654\u8bda\u4fe1\u5f92\uff0c\u5973\u795e\u5982\u4f55\u548c\u4ed6\u540c\u5728\uff1f\u4ee5\u4ec0\u4e48\u65b9\u5f0f\u548c\u4ed6\u540c\u5728\uff1f\u8fd9\u4fbf\u5927\u5927\u503c\u5f97\u63a8\u6572\u4e86\u3002</p><p>\u96c5\u8fbe\u4e0a\u5c09\u539f\u672c\u662f\u9634\u9b42\u57ce\u838e\u5c14\u795e\u6bbf\u7684\u536b\u961f\u957f\uff0c\u5927\u7267\u5e08\u745e\u74e6\u5170\u738b\u5b50\u7684\u5de6\u8180\u53f3\u81c2\uff0c\u5f97\u529b\u52a9\u624b\uff0c\u4e5f\u662f\u591c\u5973\u58eb\u6700\u8654\u8bda\u7684\u4fe1\u5f92\u4e4b\u4e00\u3002\u5982\u679c\u8bf4\u4ed6\u770b\u51fa\u4ec0\u4e48\uff0c\u6216\u8005\u77e5\u9053\u4ec0\u4e48\uff0c\u5411\u743c\u6069\u5145\u6ee1\u6697\u793a\u5730\u8bf4\u51fa\u8fd9\u53e5\u8bdd\uff0c\u90a3\u662f\u5b8c\u5168\u6709\u53ef\u80fd\u7684\u3002</p><p>\u4e0d\uff0c\u66f4\u51c6\u786e\u5730\u8bf4\u2014\u2014\u90a3\u7b80\u76f4\u5c31\u662f\u4e00\u5b9a\u7684\u3002</p><p>\u575a\u786c\u6eda\u70eb\u7684\u4e1c\u897f\u5728\u540e\u9762\u9876\u7740\u5c11\u5973\u7684\u81c0\u90e8\uff0c\u8d34\u7740\u76ae\u80a4\uff0c\u6885\u83f2\u65af\u81ea\u7136\u6e05\u695a\u5b83\u662f\u4ec0\u4e48\uff0c\u4e5f\u975e\u5e38\u719f\u6089\uff0c\u5c3d\u7ba1\u5982\u6b64\uff0c\u5979\u4f9d\u65e7\u611f\u89c9\u5230\u4e00\u9635\u9635\u672c\u80fd\u7684\u5fc3\u614c\uff0c\u201c\u90a3\u6bb5\u65f6\u95f4\u91cc\u2026\u2026\u201d\u5979\u52aa\u529b\u4fdd\u6301\u7740\u9547\u5b9a\uff0c\u601d\u7d22\u7740\uff0c\u201c\u5177\u4f53\u662f\u54ea\u4ef6\u4e8b\u60c5\u5462\u2026\u2026\u201d</p><p>\u90a3\u6bb5\u65f6\u95f4\u91cc\u53d1\u751f\u4e86\u5f88\u591a\u4e8b\u60c5\uff0c\u4f46\u771f\u6b63\u591f\u5206\u91cf\uff0c\u80fd\u63d0\u8d77\u6765\u8bf4\u7684\uff0c\u5176\u5b9e\u4e5f\u53ea\u6709\u5be5\u5be5\u51e0\u4ef6\u800c\u5df2\u3002\u201c\u4f60\u8bb0\u4e0d\u8bb0\u5f97\uff0c\u6211\u8ddf\u4f60\u8bf4\u8fc7\uff0c\u6211\u7a81\u7136\u4e27\u5931\u795e\u672f\u514d\u75ab\u80fd\u529b\u7684\u4e8b\u60c5\uff0c\u201d\u743c\u6069\u8f7b\u8f7b\u5728\u5979\u8033\u8fb9\u8bf4\uff0c\u539f\u672c\u63e1\u7740\u80f8\u90e8\u7684\u5de6\u624b\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u79fb\u4e0b\uff0c\u6258\u7740\u5979\u7684\u81c0\u90e8\uff0c\u53f3\u624b\u624b\u6307\u6162\u6162\u6e38\u52a8\uff0c\u6ed1\u5165\u81c0\u4e18\u4e4b\u95f4\uff0c\u7136\u540e\u627e\u5230\u719f\u6089\u7684\u4f4d\u7f6e\uff0c\u7f13\u7f13\u65cb\u8f6c\u7740\u6324\u5165\uff0c\u201c\u5c31\u662f\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u65f6\u5019\uff0c\u5bf9\u4ed8\u7b2c\u56db\u5bb6\u65cf\u65f6\u2026\u2026\u201d</p><p>\u5f53\u65f6\u743c\u6069\u548c\u7ef4\u5eb7\u5c3c\u4e9a\u6f5c\u5165\u7b2c\u56db\u5bb6\u65cf\u57ce\u5821\uff0c\u649e\u4e0a\u4e00\u4f4d\u5deb\u5e08\u3002\u743c\u6069\u4e2d\u4e86\u4e00\u51fb\u6697\u591c\u629a\u6478\uff0c\u9760\u7740\u5f71\u706b\u4fdd\u4f4f\u4e86\u6027\u547d\uff0c\u4f46\u4f9d\u65e7\u88ab\u6cd5\u672f\u4e0a\u7684\u8bc5\u5492\u4f24\u5bb3\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u52a8\u7528\u590d\u539f\u672f\u4e3a\u4ed6\u6cbb\u7597\uff0c\u8ba9\u743c\u6069\u60ca\u8bb6\u5730\u53d1\u73b0\u81ea\u5df1\u5df2\u7ecf\u53ef\u4ee5\u63a5\u53d7\u795e\u672f\u4e86\u3002\u6885\u83f2\u65af\u81ea\u7136\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u743c\u6069\u540e\u6765\u548c\u5979\u63d0\u8d77\u8fc7\uff0c\u4e24\u4eba\u4e3a\u6b64\u7422\u78e8\u7814\u7a76\u4e86\u534a\u5929\uff0c\u4f46\u6700\u540e\u4e5f\u6ca1\u80fd\u5f04\u660e\u767d\u5230\u5e95\u662f\u4ec0\u4e48\u7f18\u6545\uff0c\u201c\u8bb0\u5f97\uff0c\u600e\u4e48\u4e86\uff0c\u201d\u5979\u5598\u606f\u7740\u95ee\uff0c\u743c\u6069\u7684\u98df\u6307\u5df2\u7ecf\u6709\u4e24\u4e2a\u6307\u8282\u6ca1\u5165\u5979\u7684\u8eab\u4f53\uff0c\u8ba9\u5979\u611f\u89c9\u4e00\u9635\u9635\u8f7b\u5fae\u7684\u80c0\u75db\uff0c\u4f34\u968f\u7740\u719f\u6089\u7684\u6655\u7729\u548c\u5e78\u798f\u611f\uff0c\u8eab\u4f53\u4e0d\u7531\u81ea\u4e3b\u5730\u7d27\u7ef7\u8d77\u6765\uff0c\u201c\u8fd9\u4e24\u4ef6\u4e8b\u2026\u2026\u201d</p><p>\u201c\u8fd9\u4e24\u4ef6\u4e8b\u5982\u679c\u5bf9\u7167\u8d77\u6765\u770b\uff0c\u90a3\u5c31\u4e00\u5207\u90fd\u5f88\u6e05\u695a\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4e4b\u6240\u4ee5\u4e27\u5931\u795e\u672f\u514d\u75ab\u7684\u80fd\u529b\uff0c\u5e94\u8be5\u5c31\u662f\u5f71\u706b\u5728\u6363\u9b3c\u3002\u201d</p><p>\u5f53\u65f6\u4e24\u4eba\u4e5f\u63a8\u7b97\u8fc7\uff0c\u5728\u963f\u683c\u62c9\u9686\u5bf9\u4ed8\u90a3\u4e2a\u7a83\u6cd5\u8005\u7684\u65f6\u5019\uff0c\u743c\u6069\u8fd8\u62e5\u6709\u795e\u672f\u514d\u75ab\u80fd\u529b\uff0c\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u4e2d\u53d1\u73b0\u4e27\u5931\uff0c\u8fd9\u5176\u4e2d\u53ef\u80fd\u6027\u6700\u5927\u7684\uff0c\u5c31\u662f\u5728\u9634\u9b42\u57ce\u7684\u90a3\u6bb5\u65f6\u95f4\u2014\u2014\u6b63\u597d\u548c\u743c\u6069\u524d\u9762\u63a8\u65ad\u7684\u83b7\u5f97\u5f71\u706b\u7684\u65f6\u95f4\u76f8\u91cd\u5408\u3002</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u8fd9\u4e24\u4ef6\u4e8b\uff0c\u5176\u5b9e\u6781\u6709\u53ef\u80fd\u5c31\u662f\u4e00\u4ef6\u4e8b\u3002</p><p>\u6885\u83f2\u65af\u6d45\u6d45\u5730\u547b\u541f\u7740\uff0c\u56e0\u4e3a\u743c\u6069\u7684\u98df\u6307\u5df2\u7ecf\u5b8c\u5168\u6ca1\u5165\u5979\u7684\u4f53\u5185\uff0c\u7136\u540e\u7f13\u7f13\u62bd\u79bb\u51fa\u6765\uff0c\u5728\u6d1e\u53e3\u8f7b\u8f7b\u6253\u7740\u8f6c\uff0c\u201c\u4e24\u6839\u53ef\u4ee5\u5417\uff1f\u201d\u4ed6\u4f4e\u58f0\u95ee\uff0c\u5f81\u6c42\u5c11\u5973\u7684\u53cd\u5e94\u3002</p><p>\u201c\u5514\u3002\u201d</p><p>\u98df\u6307\u548c\u4e2d\u6307\u5e76\u62e2\uff0c\u7f13\u6162\u800c\u575a\u5b9a\u5730\u4fb5\u5165\u5c11\u5973\u7684\u5e7d\u9053\uff0c\u6885\u83f2\u65af\u7d27\u7d27\u54ac\u7740\u4e0b\u5507\uff0c\u8ba9\u8eab\u4f53\u9010\u6e10\u653e\u677e\u9002\u5e94\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u81ea\u5df1\u6700\u540e\u6240\u8981\u5bb9\u7eb3\u7684\u4f53\u79ef\u8fd8\u8fdc\u4e0d\u6b62\u8fd9\u70b9\u3002\u743c\u6069\u4eab\u53d7\u7740\u624b\u6307\u4e0a\u4f20\u6765\u7684\u6e29\u70ed\u548c\u6324\u538b\u611f\uff0c\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5728\u9634\u9b42\u57ce\u7684\u65f6\u5019\uff0c\u53d1\u751f\u4e86\u67d0\u4ef6\u4e8b\u60c5\uff0c\u201d\u4ed6\u4e0d\u7d27\u4e0d\u6162\u5730\u8bf4\uff0c\u201c\u81ea\u6b64\u4e4b\u540e\uff0c\u6211\u83b7\u5f97\u4e86\u65e0\u6cd5\u81ea\u7531\u4f7f\u7528\u7684\u5f71\u706b\uff0c\u540c\u65f6\u4e5f\u56e0\u6b64\u4e27\u5931\u4e86\u795e\u672f\u514d\u75ab\u7684\u80fd\u529b\u3002\u201d</p><p>\u201c\u4f46\u2026\u2026\u5230\u5e95\u2026\u2026\u5230\u5e95\u662f\u54ea\u4ef6\u4e8b\u5462\uff1f\u201d</p><p>\u4e24\u6839\u624b\u6307\u4e00\u70b9\u4e00\u70b9\u5730\u7f13\u7f13\u4fb5\u5165\uff0c\u7136\u540e\u53c8\u4e00\u70b9\u4e00\u70b9\u5730\u6162\u6162\u9000\u51fa\uff0c\u5982\u662f\u53cd\u590d\uff0c\u6885\u83f2\u65af\u5df2\u7ecf\u51e0\u4e4e\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u201c\u4ee5\u524d\u6211\u4eec\u63a8\u7b97\u795e\u672f\u514d\u75ab\u6d88\u5931\u7684\u4e8b\u60c5\uff0c\u5f53\u65f6\u6700\u53ef\u80fd\u7684\u731c\u6d4b\u662f\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u6700\u53ef\u80fd\u7684\u731c\u6d4b\uff0c\u662f\u743c\u6069\u5728\u9634\u9b42\u57ce\u90a3\u6bb5\u65f6\u95f4\u91cc\uff0c\u53d1\u73b0\u4e00\u4f4d\u53eb\u505a\u83f2\u5a05\u7684\u4f8d\u5973\uff0c\u4ece\u5979\u4f53\u5185\u5438\u6536\u5230\u5df4\u5c14\u7684\u795e\u529b\uff0c\u4ece\u800c\u4e00\u4e3e\u7a81\u7834\u7edd\u5927\u591a\u6570\u5deb\u5e08\u4e00\u8f88\u5b50\u90fd\u4e0d\u80fd\u7a81\u7834\u7684\u95e8\u69db\uff0c\u89e6\u6478\u5230\u9b54\u7f51\u7b2c\u4e94\u5c42\uff0c\u51dd\u6210\u771f\u540d\uff0c\u8dfb\u8eab\u9ad8\u9636\u5deb\u5e08\u7684\u884c\u5217\u3002\u8fd9\u79cd\u5438\u6536\u3001\u6d88\u5316\u795e\u529b\u7684\u4f5c\u6cd5\uff0c\u65e2\u7136\u80fd\u591f\u5f71\u54cd\u743c\u6069\u7684\u8eab\u4f53\uff0c\u5f71\u54cd\u4ed6\u7684\u9b54\u6cd5\u9020\u8be3\uff0c\u751a\u81f3\u9690\u9690\u5f71\u54cd\u5230\u4ed6\u7684\u60c5\u7eea\uff0c\u90a3\u4e48\u56e0\u6b64\u6539\u53d8\u4e86\u4ed6\u7684\u7279\u6b8a\u5929\u8d4b\uff0c\u4ee4\u4ed6\u4e27\u5931\u514d\u75ab\u795e\u672f\u7684\u80fd\u529b\uff0c\u662f\u6781\u6709\u53ef\u80fd\u7684\u4e8b\u60c5\u3002</p><p>\u4f46\u8fd9\u79cd\u731c\u6d4b\u6709\u4e00\u4e2a\u6700\u5927\u7684\u95ee\u9898\uff0c\u5c31\u662f\u743c\u6069\u662f\u6709\u8fc7\u4e24\u6b21\u5438\u6536\u795e\u529b\u7684\u7ecf\u5386\u7684\uff0c\u5728\u70db\u5821\u7684\u65f6\u5019\u5c31\u5df2\u7ecf\u548c\u838e\u73de\u514b\u6709\u8fc7\u4e00\u6b21\uff0c\u7136\u540e\u4ed6\u7167\u6837\u514d\u75ab\u795e\u672f\uff0c\u5f53\u7136\u4e5f\u7167\u6837\u88ab\u6b23\u5e03\u65e0\u89c6\uff0c\u4ec0\u4e48\u90fd\u6ca1\u53d1\u751f\u3002\u4e3a\u4ec0\u4e48\u7b2c\u4e8c\u6b21\u5c31\u53d8\u4e86\u5462\uff1f\u8fd9\u4e2a\u95ee\u9898\u89e3\u91ca\u4e0d\u901a\uff0c\u90a3\u4e00\u5207\u4f9d\u65e7\u662f\u5f92\u52b3\u3002</p><p>\u201c\u6211\u66fe\u7ecf\u60f3\uff0c\u6216\u8bb8\u662f\u56e0\u4e3a\u7b2c\u4e00\u6b21\u5438\u6536\u7684\u795e\u529b\u592a\u5c11\uff0c\u4e0d\u8db3\u4ee5\u5f62\u6210\u8d28\u53d8\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u79cd\u89e3\u91ca\u6709\u4e9b\u7275\u5f3a\uff0c\u5012\u4e5f\u672a\u5fc5\u8bf4\u4e0d\u901a\u2014\u2014\u4f46\u518d\u548c\u8fd9\u6b21\u7684\u5f71\u706b\u8054\u7cfb\u8d77\u6765\uff0c\u90a3\u5c31\u5b8c\u5168\u8bf4\u4e0d\u901a\u4e86\u3002\u201d</p><p>\u795e\u529b\u5438\u6536\u591a\u6216\u8005\u5c11\uff0c\u6216\u8bb8\u4f1a\u5f71\u54cd\u91cf\u53d8\u5230\u8d28\u53d8\uff0c\u8ba9\u743c\u6069\u4fdd\u7559\u6216\u8005\u4e27\u5931\u795e\u672f\u514d\u75ab\u7684\u5929\u8d4b\uff0c\u4f46\u603b\u4e0d\u53ef\u80fd\u4f1a\u8ba9\u743c\u6069\u7684\u8eab\u4f53\u91cc\u5e73\u767d\u65e0\u6545\u751f\u51fa\u5f71\u706b\u6765\u3002\u65e0\u8bba\u600e\u4e48\u60f3\uff0c\u743c\u6069\u4e5f\u4e0d\u76f8\u4fe1\u5df4\u5c14\u7684\u6740\u622e\u795e\u529b\u4f1a\u6447\u8eab\u4e00\u53d8\uff0c\u6210\u4e3a\u838e\u5c14\u7684\u5f71\u706b\uff0c\u90a3\u4e5f\u672a\u514d\u592a\u8fc7\u795e\u5947\u4e86\u3002</p><p>\u201c\u6240\u4ee5\u53ea\u6709\u4e00\u79cd\u89e3\u91ca\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u4ed6\u9677\u5165\u5c11\u5973\u4f53\u5185\u7684\u624b\u6307\u7a81\u7136\u52a0\u5feb\u4e86\u901f\u5ea6\uff0c\u6885\u83f2\u65af\u731d\u4e0d\u53ca\u9632\uff0c\u4e00\u65f6\u95f4\u51e0\u4e4e\u6655\u7729\u8fc7\u53bb\uff0c\u8fc7\u4e86\u534a\u664c\u624d\u60a0\u60a0\u56de\u8fc7\u795e\u6765\uff0c\u201c\u4f60\u574f\u6b7b\u4e86\uff0c\u201d\u5979\u5a07\u55d4\u7740\uff0c\u6162\u6162\u56de\u5fc6\u4e86\u4e00\u4e0b\u521a\u624d\u6240\u8bf4\uff0c\u201c\u4f60\u662f\u8bf4\uff0c\u90a3\u4e2a\u83f2\u5a05\u5c31\u662f\u591c\u5973\u58eb\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u591c\u5973\u58eb\u7684\u5723\u8005\u3002\u201d</p><p>\u795e\u7947\u4ee5\u5b9e\u4f53\u65b9\u5f0f\u51fa\u73b0\u5728\u7269\u8d28\u754c\uff0c\u4e0d\u8fc7\u5316\u8eab\u548c\u5723\u8005\u4e24\u79cd\u5f62\u6001\u3002\u5982\u679c\u8bf4\u591c\u5973\u58eb\u7684\u5316\u8eab\u53d8\u5316\u6210\u4e00\u4e2a\u51e1\u4eba\uff0c\u90a3\u81ea\u7136\u662f\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\u60c5\uff0c\u4f46\u5316\u8eab\u7b49\u4e8e\u5c31\u662f\u5f31\u5316\u7248\u7684\u795e\u7947\uff0c\u4f9d\u65e7\u8fd8\u662f\u201c\u795e\u201d\uff0c\u795e\u7947\u81ea\u6709\u4e00\u79cd\u5929\u7136\u5a01\u538b\uff0c\u79f0\u4e3a\u201c\u795e\u5723\u7b3c\u7f69\u201d\uff0c\u743c\u6069\u5c31\u7b97\u611f\u89c9\u4e0d\u5230\uff0c\u96be\u9053\u6885\u83f2\u65af\u8fd9\u79cd\u5723\u6b66\u58eb\u4e5f\u4e00\u65e0\u6240\u89c9\uff1f\u6700\u6709\u53ef\u80fd\u7684\uff0c\u81ea\u7136\u662f\u5723\u8005\u4e86\uff0c\u5723\u8005\u662f\u795e\u7947\u9644\u4f53\u51e1\u4eba\uff0c\u6682\u65f6\u4e0d\u5c45\u795e\u4f4d\uff0c\u4e0d\u638c\u795e\u804c\uff0c\u4e25\u683c\u6765\u8bf4\u5df2\u7136\u5e76\u975e\u795e\u7947\uff0c\u6885\u83f2\u65af\u5bdf\u89c9\u4e0d\u51fa\u5f88\u6b63\u5e38\u3002</p><p>\u7f57\u4e1d\u4ee5\u4f0a\u8389\u96c5\u4e3a\u9644\u4f53\u5bb9\u5668\uff0c\u5723\u8005\u964d\u4e34\uff0c\u5f53\u65f6\u5176\u4ed6\u4eba\u4e5f\u90fd\u6ca1\u53d1\u89c9\uff0c\u53ea\u6709\u8475\u9732\u7acb\u523b\u53cd\u5e94\u8fc7\u6765\uff0c\u90a3\u662f\u56e0\u4e3a\u8475\u9732\u662f\u4f0a\u8389\u4e1d\u7fe0\u7684\u9009\u6c11\uff0c\u4f0a\u8389\u4e1d\u7fe0\u53c8\u6b63\u662f\u7f57\u4e1d\u7684\u6b7b\u654c\u3002\u6885\u83f2\u65af\u65e2\u4e0d\u662f\u63d0\u5c14\u7684\u9009\u6c11\uff0c\u63d0\u5c14\u548c\u838e\u5c14\u867d\u7136\u4e0d\u7766\uff0c\u5374\u8fd8\u7b97\u4e0d\u4e0a\u6b7b\u654c\uff0c\u611f\u5e94\u4fbf\u4e0d\u5f3a\u70c8\u2014\u2014\u838e\u5c14\u771f\u6b63\u7684\u6b7b\u654c\uff0c\u662f\u6708\u5973\u795e\u82cf\u4f26\u3001\u9b54\u6cd5\u5973\u795e\u5bc6\u65af\u62c9\u548c\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u3002</p><p>\u201c\u591c\u5973\u58eb\u7684\u5723\u8005\u2026\u2026\u53ef\u662f\u6211\u786e\u5b9e\u80fd\u611f\u5e94\u5230\u5979\u548c\u6211\u4e00\u6837\uff0c\u90fd\u662f\u5df4\u5c14\u7684\u5b50\u55e3\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u8fd9\u5e76\u4e0d\u77db\u76fe\uff0c\u663e\u7136\u83f2\u5a05\u5c0f\u59d0\u65e2\u662f\u5df4\u5c14\u7684\u5b50\u55e3\uff0c\u540c\u65f6\u4e5f\u662f\u591c\u5973\u58eb\u7684\u8654\u8bda\u4fe1\u5f92\uff0c\u8c01\u89c4\u5b9a\u8fd9\u4e24\u8005\u4e0d\u80fd\u662f\u540c\u4e00\u4f53\u5462\uff1f\u81f3\u5c11\u5df4\u5c14\u548c\u591c\u5973\u58eb\u90fd\u662f\u90aa\u795e\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5982\u679c\u8981\u8bf4\u7684\u8bdd\uff0c\u6211\u4f3c\u4e4e\u8fd8\u8ba4\u8bc6\u4e00\u4f4d\u5df4\u5c14\u7684\u5b50\u55e3\uff0c\u540c\u65f6\u662f\u6b63\u4e49\u4e4b\u795e\u7684\u5723\u6b66\u58eb\u5462\u3002\u201d</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u70b9\u70b9\u5934\uff0c\u627f\u8ba4\u8fd9\u4e2a\u8bf4\u6cd5\u3002</p><p>\u201c\u83f2\u5a05\u786e\u5b9e\u662f\u5df4\u5c14\u5b50\u55e3\uff0c\u6211\u4e5f\u786e\u5b9e\u4ece\u5979\u4f53\u5185\u83b7\u5f97\u4e86\u795e\u529b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u73b0\u5728\u60f3\u8d77\u6765\uff0c\u53ea\u6015\u5e76\u4e0d\u90a3\u4e48\u7eaf\u7cb9\u3002\u9664\u4e86\u5df4\u5c14\u7684\u6740\u622e\u795e\u529b\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u6df7\u8fdb\u4e86\u53e6\u5916\u67d0\u4e9b\u4e1c\u897f\u3002\u201d</p><p>\u201c\u5f71\u706b\uff1f\u201d</p><p>\u201c\u53ef\u80fd\u662f\uff0c\u4e5f\u53ef\u80fd\u4e0d\u662f\uff0c\u5177\u4f53\u7684\u8be6\u60c5\u8fd8\u4e0d\u6e05\u695a\uff0c\u4f46\u81f3\u5c11\u662f\u901a\u8fc7\u8fd9\u4e00\u6b21\uff0c\u591c\u5973\u58eb\u6697\u4e2d\u5bf9\u6211\u505a\u4e86\u67d0\u4e9b\u624b\u811a\uff0c\u8fd9\u662f\u53ef\u4ee5\u80af\u5b9a\u7684\u3002\u201d</p><p>\u201c\u4f60\u662f\u600e\u4e48\u60f3\u5230\u662f\u83f2\u5a05\u7684\uff1f\u201d</p><p>\u201c\u7ecf\u5386\u4e86\u8fd9\u4e48\u591a\u4e8b\u60c5\u4e4b\u540e\uff0c\u6211\u5df2\u7ecf\u5f88\u96be\u518d\u76f8\u4fe1\u4e16\u754c\u4e0a\u6709\u5de7\u5408\u8fd9\u79cd\u4e8b\u60c5\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u83f2\u5a05\u7684\u51fa\u73b0\u5c31\u592a\u5de7\u5408\u4e86\uff0c\u4e3b\u52a8\u9001\u5230\u9762\u524d\uff0c\u5c31\u50cf\u662f\u6211\u6b63\u9700\u8981\u4e00\u5757\u9985\u997c\uff0c\u7136\u540e\u5929\u4e0a\u5c31\u6070\u597d\u6389\u4e0b\u4e00\u5757\uff0c\u800c\u4e14\u8fd8\u6389\u5728\u6211\u624b\u4e2d\u3002\u5176\u5b9e\u6211\u5f53\u65f6\u5c31\u6709\u70b9\u6000\u7591\uff0c\u4f46\u4e5f\u6ca1\u592a\u591a\u60f3\uff0c\u4e5f\u5c31\u8fc7\u53bb\u4e86\uff0c\u90a3\u65f6\u5019\u6211\u8fd8\u592a\u5929\u771f\uff0c\u89c9\u5f97\u2018\u597d\u8fd0\u6c14\u2019\u8fd9\u79cd\u4e1c\u897f\uff0c\u4e5f\u662f\u6709\u53ef\u80fd\u4f1a\u843d\u5230\u6211\u5934\u4e0a\u7684\u3002\u73b0\u5728\u6211\u6539\u53d8\u770b\u6cd5\u4e86\uff0c\u5929\u4e0a\u4e0d\u4f1a\u6389\u9985\u997c\u2014\u2014\u5982\u679c\u6389\uff0c\u90a3\u80af\u5b9a\u662f\u4e0b\u8fc7\u836f\u7684\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4ece\u4ec0\u4e48\u65f6\u5019\u8d77\u786e\u5b9a\u662f\u5979\uff1f\u201d</p><p>\u201c\u4ece\u6211\u4eec\u8fdb\u5165\u6df1\u6e0a\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u5982\u679c\u5355\u5355\u662f\u795e\u672f\u514d\u75ab\u7684\u7a81\u7136\u6d88\u5931\u8fd9\u4e00\u4ef6\u4e8b\uff0c\u6211\u6216\u8bb8\u8fd8\u4e0d\u4f1a\u628a\u76ee\u6807\u786e\u5b9a\u5728\u5979\u8eab\u4e0a\uff0c\u6bd5\u7adf\u522b\u7684\u89e3\u91ca\u4e5f\u662f\u6709\u53ef\u80fd\u7684\u3002\u4f46\u81ea\u4ece\u8475\u9732\u8bf4\u6211\u662f\u9009\u6c11\uff0c\u8bf4\u6211\u6709\u5f71\u706b\u4e4b\u540e\uff0c\u6211\u57fa\u672c\u5c31\u63a8\u6d4b\u5230\u5e94\u8be5\u662f\u5979\u4e86\u3002\u56e0\u4e3a\u5f71\u706b\u662f\u591c\u5973\u58eb\u7684\u795e\u529b\uff0c\u53ea\u53ef\u80fd\u901a\u8fc7\u591c\u5973\u58eb\u83b7\u5f97\uff0c\u8fd9\u4e00\u70b9\u662f\u786e\u5b9a\u65e0\u7591\u7684\u3002\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u6211\u5c31\u5fc5\u5b9a\u5728\u4ec0\u4e48\u65f6\u5019\uff0c\u548c\u591c\u5973\u58eb\u6709\u8fc7\u4e00\u6b21\u8db3\u591f\u2018\u4eb2\u5bc6\u2019\u7684\u63a5\u89e6\u3002\u201d</p><p>\u795e\u7947\u6311\u9009\u51e1\u4eba\uff0c\u6388\u4e88\u795e\u529b\uff0c\u6210\u4e3a\u9009\u6c11\uff0c\u5e76\u4e0d\u662f\u52a8\u5ff5\u5373\u53ef\uff0c\u964d\u4e2a\u795e\u8c15\u5c31\u80fd\u641e\u5b9a\u7684\u4e8b\u60c5\u3002\u4fbf\u4ee5\u521a\u63d0\u5230\u8fc7\u7684\u8428\u9a6c\u65af\u7279\u4e3a\u4f8b\uff0c\u5f53\u5e74\u9b54\u6cd5\u5973\u795e\u5bc6\u65af\u62c9\u610f\u6b32\u63d0\u5347\u4ed6\u4e3a\u9009\u6c11\uff0c\u4e5f\u662f\u5728\u4e00\u8d77\u5ea6\u8fc7\u6574\u6574\u5341\u5929\uff0c\u6700\u540e\u624d\u6388\u4e88\u94f6\u706b\uff0c\u8fd9\u662f\u70db\u5821\u8d44\u6599\u4e0a\u660e\u6587\u8bb0\u8f7d\u7684\u3002\u743c\u6069\u867d\u7136\u4e0d\u662f\u771f\u6b63\u7684\u9009\u6c11\uff0c\u4f46\u4ed6\u786e\u5b9e\u5f97\u5230\u4e86\u5f71\u706b\uff0c\u8fd9\u603b\u4e0d\u53ef\u80fd\u662f\u968f\u4fbf\u7761\u4e0a\u4e00\u89c9\uff0c\u7b2c\u4e8c\u5929\u65e9\u4e0a\u8d77\u6765\u5c31\u80fd\u7a81\u7136\u62e5\u6709\u7684\u3002</p><p>\u6240\u6709\u7684\u7ebf\u7d22\u5f52\u7eb3\u8d77\u6765\uff0c\u83f2\u5a05\u5c31\u6210\u4e86\u6700\u7ec8\u7684\u6307\u5411\u76ee\u6807\u3002\u800c\u743c\u6069\u518d\u770b\u770b\u65c1\u8fb9\u7684\u4f0a\u8389\u96c5\uff0c\u60f3\u8d77\u7f57\u4e1d\u521a\u521a\u73a9\u8fc7\u7684\u5723\u8005\u964d\u4e34\uff0c\u987f\u65f6\u4fbf\u9192\u609f\u8fc7\u6765\u3002\u53ea\u8981\u4e00\u60f3\u901a\u201c\u83f2\u5a05\u5176\u5b9e\u5c31\u662f\u591c\u5973\u58eb\u201d\u8fd9\u70b9\uff0c\u4ee5\u524d\u6240\u6709\u7591\u60d1\u7684\u5730\u65b9\u5168\u90fd\u8fce\u5203\u800c\u89e3\u3002\u5f71\u706b\u7684\u6765\u6e90\u3001\u795e\u672f\u514d\u75ab\u7684\u7a81\u7136\u6d88\u5931\uff0c\u5168\u90fd\u6709\u4e86\u6700\u5408\u7406\u7684\u89e3\u91ca\u3002</p><p>\u201c\u867d\u7136\u5982\u6b64\uff0c\u6211\u4f9d\u65e7\u8fd8\u662f\u4e0d\u6562\u5341\u5206\u786e\u8ba4\uff0c\u76f4\u5230\u6b23\u5e03\u7684\u51fa\u73b0\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u5982\u679c\u6b23\u5e03\u5bf9\u743c\u6069\u4f9d\u65e7\u65e0\u52a8\u4e8e\u8877\uff0c\u4e0d\u7406\u4e0d\u776c\uff0c\u90a3\u743c\u6069\u5012\u8981\u65e0\u6240\u9002\u4ece\uff0c\u6000\u7591\u81ea\u5df1\u524d\u9762\u7684\u63a8\u6d4b\u6709\u8bef\u3002\u4f46\u6b23\u5e03\u4e00\u89c1\u4e4b\u4e0b\uff0c\u9aa4\u8d77\u53d1\u96be\uff0c\u8ba9\u743c\u6069\u53cd\u800c\u771f\u6b63\u786e\u5b9a\u4e86\u81ea\u5df1\u83b7\u5f97\u5f71\u706b\u7684\u5927\u81f4\u65f6\u95f4\u6bb5\uff0c\u76f8\u4e92\u5bf9\u7167\uff0c\u987f\u65f6\u518d\u65e0\u7591\u8651\u3002</p><p>\u201c\u662f\u8fd9\u6837\u2026\u2026\u4f46\u662f\u591c\u5973\u58eb\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\u5462\u3002\u201d</p><p>\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u6162\u6162\u5c06\u624b\u6307\u62bd\u51fa\uff0c\u5c11\u5973\u7684\u8eab\u4f53\u5df2\u7ecf\u5b8c\u5168\u653e\u677e\uff0c\u7b49\u5f85\u7740\u4fb5\u5165\uff0c\u201c\u4f46\u6211\u6709\u4e00\u4e9b\u731c\u60f3\u3002\u201d</p></article>"}}}]);
