(window.webpackJsonp=window.webpackJsonp||[]).push([[1526],{1549:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u56db\u8282 \u7279\u8272",content:"<article><h2>\u7b2c\u4e09\u5341\u56db\u8282 \u7279\u8272</h2><p>\u201c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6885\u83f2\u65af\u4ece\u743c\u6069\u6000\u4e2d\u8f7b\u8f7b\u62ac\u8d77\u8138\u6765\uff0c\u201c\u501a\u9760\u522b\u4eba\uff0c\u7ec8\u7a76\u662f\u4e0d\u591f\u7a33\u56fa\u7684\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u559c\u6b22\uff0c\u552f\u6709\u52aa\u529b\u63d0\u9ad8\u81ea\u5df1\uff0c\u52a0\u91cd\u781d\u7801\uff0c\u51cf\u5c11\u88ab\u653e\u5f03\u6216\u8005\u727a\u7272\u7684\u51e0\u7387\uff0c\u589e\u52a0\u62b5\u5fa1\u5371\u9669\u7684\u80fd\u529b\uff0c\u624d\u662f\u6b63\u7ecf\u3002\u81f3\u4e8e\u773c\u4e0b\uff0c\u4f60\u65e2\u7136\u89c9\u5f97\u90a3\u679a\u5370\u7ae0\u548c\u81ea\u5df1\u5927\u6709\u7275\u8fde\uff0c\u5c31\u5148\u5168\u529b\u4ee5\u8d74\u62ff\u5230\u5b83\u518d\u8bf4\uff0c\u5982\u679c\u771f\u80fd\u901a\u8fc7\u5b83\u5f04\u660e\u767d\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u88ab\u770b\u91cd\uff0c\u90a3\u4e5f\u80fd\u66f4\u6e05\u695a\u628a\u63e1\u6574\u4e2a\u68cb\u5c40\u4e86\u3002\u201d</p><p>\u201c\u55ef\uff0c\u6211\u4e5f\u8fd9\u4e48\u60f3\uff0c\u201d\u743c\u6069\u5728\u5979\u989d\u5934\u4e0a\u4eb2\u4e86\u4e00\u53e3\uff0c\u7a81\u7136\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6709\u4f60\u5728\u8eab\u8fb9\u771f\u597d\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5c31\u50cf\u4e00\u4f4d\u5973\u53c2\u8c0b\u4f3c\u7684\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4f1a\u4ed8\u6211\u53cc\u4efd\u7684\u5de5\u8d44\u5417\uff1f\u201d\u5979\u4e00\u672c\u6b63\u7ecf\u5730\u95ee\uff0c\u201c\u60c5\u4eba\u4e00\u4efd\uff0c\u53c2\u8c0b\u4e00\u4efd\u3002\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u743c\u6069\u4e5f\u5f88\u8ba4\u771f\u5730\u70b9\u5934\uff0c\u201c\u60c5\u4eba\u7684\u4e00\u4efd\u6628\u5929\u5df2\u7ecf\u4ed8\u8fc7\u4e86\uff0c\u53c2\u8c0b\u7684\u4e00\u4efd\u73b0\u5728\u4ed8\u600e\u4e48\u6837\u2026\u2026\u201d</p><p>\u201c\u8ba8\u538c\uff01\u201d</p><p>\u201c\u4e0d\u8fc7\u4f60\u90fd\u4ec0\u4e48\u65f6\u5019\u60f3\u5230\u8fd9\u4e9b\u7684\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u6211\u662f\u4ee5\u524d\u4e00\u76f4\u5c31\u5728\u9690\u9690\u7ea6\u7ea6\u731c\u6d4b\uff0c\u53ea\u662f\u73b0\u5728\u57fa\u672c\u786e\u8ba4\u4e86\uff1b\u4f60\u5728\u6628\u5929\u6211\u8bf4\u4e4b\u524d\u90fd\u57fa\u672c\u4e0d\u6e05\u695a\u5427\uff0c\u600e\u4e48\u4e00\u89c9\u7761\u9192\u8fc7\u6765\uff0c\u5c31\u4eff\u4f5b\u6bd4\u6211\u770b\u5f97\u6d77\u6e05\u695a\u4e86\u3002\u201d</p><p>\u201c\u90a3\u81ea\u7136\uff0c\u6211\u5728\u8fd9\u65b9\u9762\u53cd\u5e94\u6bd4\u4f60\u5feb\u591a\u4e86\uff0c\u201d\u5c11\u5973\u7b11\u5bb9\u707f\u70c2\uff0c\u201c\u522b\u5fd8\u4e86\uff0c\u8bba\u4e0b\u68cb\u7684\u672c\u4e8b\uff0c\u4f60\u53ef\u8fdc\u8fdc\u4e0d\u662f\u6211\u7684\u5bf9\u624b\u5462\u3002\u201d</p><p>\u201c\u5514\uff0c\u4f60\u8fd9\u4e48\u8bf4\u4f1a\u8ba9\u6211\u89c9\u5f97\u5f88\u6ca1\u9762\u5b50\u7684\u3002\u201d</p><p>\u4e3a\u4e86\u633d\u56de\u9762\u5b50\uff0c\u5728\u63a5\u4e0b\u6765\u7684\u51e0\u5206\u949f\u91cc\u743c\u6069\u5bf9\u6885\u83f2\u65af\u5c55\u5f00\u4e86\u88ad\u51fb\uff0c\u76f4\u5230\u628a\u5979\u5f04\u5f97\u6c14\u5598\u5401\u5401\u5a07\u58f0\u6c42\u9976\uff0c\u603b\u7b97\u624d\u5fc3\u6ee1\u610f\u8db3\u5730\u6536\u624b\u3002\u201c\u8be5\u8d77\u5e8a\u4e86\uff0c\u201d\u4ed6\u6700\u540e\u8bf4\uff0c\u201c\u65f6\u5019\u4e0d\u65e9\u4e86\u3002\u201d</p><p>\u6b63\u5728\u6b64\u65f6\uff0c\u95e8\u88ab\u63a8\u5f00\u4e86\uff0c\u838e\u73de\u514b\u8d70\u8fdb\u6765\uff0c\u5979\u53c8\u6362\u4e86\u4e00\u8eab\u6253\u626e\uff0c\u9ed1\u8272\u540a\u5e26\u957f\u88d9\u76c8\u76c8\u53ca\u5730\uff0c\u9732\u51fa\u5149\u6d01\u7684\u80a9\u90e8\u548c\u7cbe\u81f4\u9501\u9aa8\uff0c\u8d8a\u53d1\u6620\u886c\u5f97\u76ae\u80a4\u767d\u7699\u8bf1\u4eba\uff0c\u539f\u672c\u7684\u68d5\u8272\u5377\u53d1\u53d8\u6210\u4e86\u8910\u8272\u76f4\u53d1\uff0c\u76d8\u6210\u53d1\u9afb\uff0c\u6709\u51e0\u7f15\u6563\u6563\u5782\u4e0b\u6765\u62c2\u7740\u8138\u988a\uff0c\u663e\u5f97\u65e2\u5178\u96c5\u53c8\u59a9\u5a9a\u3002\u624b\u4e2d\u63d0\u7740\u4e00\u4e2a\u767d\u8272\u7684\u888b\u5b50\uff0c\u811a\u4e0a\u7a7f\u4e00\u53cc\u9ed1\u8272\u7684\u76ae\u8d28\u51c9\u978b\uff0c\u9576\u7740\u4e24\u679a\u94f6\u82b1\uff0c\u9732\u51fa\u811a\u8dbe\uff0c\u8bf4\u4e0d\u51fa\u7684\u4fcf\u76ae\u53ef\u7231\u3002\u743c\u6069\u770b\u7740\u90fd\u6014\u4e86\u6014\uff0c\u51e0\u4e4e\u8981\u6000\u7591\u8fd9\u662f\u4e0d\u662f\u81ea\u5df1\u8ba4\u8bc6\u7684\u90a3\u4e2a\u9b45\u9b54\uff0c\u201c\u838e\u73de\u514b\u2026\u2026\u201d</p><p>\u201c\u5514\uff1f\u201d\u9b45\u9b54\u8d70\u5230\u5e8a\u8fb9\uff0c\u4ece\u624b\u4e2d\u767d\u8272\u888b\u5b50\u91cc\u53d6\u51fa\u4e24\u5957\u8863\u670d\uff0c\u653e\u5728\u6795\u5934\u65c1\uff0c\u201c\u8be5\u8d77\u5e8a\u4e86\uff0c\u4e24\u4f4d\uff0c\u201d\u5979\u7b11\u541f\u541f\u7684\uff0c\u201c\u6211\u4eec\u5f97\u53bb\u51b3\u6597\u5927\u8d5b\u62a5\u540d\u4e86\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u2026\u2026\u201d</p><p>\u201c\u5bf9\u6211\u4eca\u5929\u7684\u6253\u626e\u5f88\u60ca\u8bb6\uff1f\u201d\u9b45\u9b54\u53cd\u95ee\uff0c\u201c\u5f88\u6b63\u5e38\u5566\uff0c\u4f5c\u4e3a\u5973\u5b69\u5b50\u5f53\u7136\u8981\u8ba8\u60c5\u4eba\u6b22\u5fc3\u561b\u3002\u6211\u770b\u4f60\u6628\u5929\u5bf9\u6211\u7684\u6027\u611f\u5957\u88c5\u4f3c\u4e4e\u6ca1\u4ec0\u4e48\u53cd\u5e94\uff0c\u4f30\u8ba1\u4f60\u4e0d\u559c\u6b22\u90a3\u79cd\uff0c\u6240\u4ee5\u4eca\u5929\u5c31\u6362\u79cd\u8def\u7ebf\uff0c\u6539\u8d70\u6dd1\u5973\u98ce\u683c\u3002\u201d</p><p>\u201c\u6ca1\u6709\u554a\uff0c\u6211\u5f88\u559c\u6b22\u6628\u5929\u4f60\u90a3\u8eab\u2026\u2026\u4e0d\u5bf9\uff0c\u600e\u4e48\u626f\u5230\u8fd9\u4e2a\u4e86\uff0c\u6211\u662f\u8981\u544a\u8bc9\u4f60\u2026\u2026\u201d</p><p>\u201c\u53d1\u73b0\u6211\u7684\u5934\u53d1\u53d8\u56de\u6765\u4e86\uff1f\u5f88\u6b63\u5e38\u5566\uff0c\u6211\u73b0\u5728\u662f\u9b45\u9b54\uff0c\u5982\u679c\u8fde\u8fd9\u70b9\u57fa\u672c\u7684\u53d8\u5316\u80fd\u529b\u90fd\u6ca1\u6709\uff0c\u90a3\u5c82\u4e0d\u662f\u592a\u5931\u8d25\u4e86\u3002\u201d</p><p>\u201c\u6211\u4e5f\u4e0d\u662f\u8bf4\u4f60\u7684\u5934\u53d1\u2026\u2026\u201d</p><p>\u201c\u90a3\u4f60\u662f\u89c9\u5f97\u6211\u7684\u773c\u775b\u6ca1\u6709\u53d8\u56de\u8fc7\u53bb\u90a3\u6837\uff1f\u53ef\u662f\u6211\u89c9\u5f97\u73b0\u5728\u8fd9\u6837\u66f4\u597d\u770b\u554a\uff0c\u50cf\u7ea2\u5b9d\u77f3\u4e00\u6837\u6676\u83b9\u5254\u900f\uff0c\u8ba9\u4eba\u4e00\u671b\u5c31\u6709\u6df1\u6df1\u9677\u5165\u5176\u4e2d\u7684\u611f\u89c9\u3002\u201d</p><p>\u201c\u6211\u3001\u662f\u3001\u8bf4\u3001\u4f60\u3001\u4e0b\u3001\u6b21\u3001\u80fd\u3001\u4e0d\u3001\u80fd\u3001\u5148\u3001\u6572\u3001\u95e8\u3001\u518d\u3001\u8fdb\u3001\u6765\uff01\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u9b45\u9b54\u5947\u602a\u5730\u53cd\u95ee\u3002</p><p>\u201c\u8fd9\u662f\u57fa\u672c\u7684\u793c\u8c8c\u95ee\u9898\u5427\uff0c\u800c\u4e14\u4f60\u6ca1\u770b\u5230\u6211\u73b0\u5728\u90fd\u6ca1\u7a7f\u8863\u670d\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u5173\u7cfb\uff0c\u53cd\u6b63\u6211\u53c8\u4e0d\u662f\u7b2c\u4e00\u6b21\u770b\u89c1\u4e86\uff0c\u800c\u4e14\u4e5f\u4e0d\u4f1a\u662f\u6700\u540e\u4e00\u6b21\uff0c\u800c\u4e14\u4f60\u4eec\u4e0d\u8fd8\u76d6\u7740\u6bef\u5b50\u4e48\uff0c\u53c8\u4e0d\u662f\u5168\u88f8\u3002\u81f3\u4e8e\u8bf4\u793c\u8c8c\u95ee\u9898\uff0c\u201d\u9b45\u9b54\u6485\u8d77\u5c0f\u5634\uff0c\u201c\u6211\u662f\u4f60\u7684\u60c5\u4eba\u554a\uff0c\u96be\u9053\u8fdb\u623f\u95f4\u8fd8\u9700\u8981\u6572\u95e8\u5417\uff1f\u96be\u9053\u6885\u83f2\u65af\u5c0f\u59d0\u8fdb\u4f60\u7684\u623f\u95f4\u6bcf\u6b21\u90fd\u6572\u95e8\u5417\uff1f\u90a3\u4f60\u4eec\u53ef\u771f\u591f\u65e0\u8da3\u7684\u3002\u201d</p><p>\u201c\u5979\u5f53\u7136\u4e0d\u7528\u6572\u95e8\u2026\u2026\u201d</p><p>\u201c\u90a3\u4f60\u7684\u610f\u601d\u5c31\u662f\u8bf4\u5bf9\u6211\u7279\u522b\u6b67\u89c6\u4e86\uff1f\u201d</p><p>\u201c\u522b\u95f9\u4e86\uff0c\u201d\u6885\u83f2\u65af\u5728\u65c1\u8fb9\u8bf4\uff0c\u201c\u5148\u8d77\u5e8a\u5427\uff0c\u522b\u803d\u6401\u4e86\u6b63\u4e8b\u3002\u201d</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u8fd9\u6b21\u6df1\u6e0a\u4e4b\u884c\u6765\u5f97\u4ed3\u4fc3\uff0c\u4e8b\u5148\u538b\u6839\u5c31\u6ca1\u51c6\u5907\u6362\u6d17\u7684\u8863\u670d\uff0c\u8eab\u4e0a\u7a7f\u7684\u4e00\u5957\u6628\u5929\u8d76\u8def\u4e5f\u65e9\u5c31\u5df2\u7ecf\u88ab\u6c57\u6e7f\u900f\u4e86\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u6d17\uff0c\u6b63\u6254\u5728\u5730\u4e0a\u3002\u743c\u6069\u539f\u672c\u8fd8\u5728\u60f3\u8981\u4e0d\u8981\u8ba9\u838e\u73de\u514b\u53bb\u5f04\u4e24\u5957\u8863\u670d\u6765\u5462\uff0c\u6ca1\u60f3\u5230\u9b45\u9b54\u5982\u6b64\u5584\u89e3\u4eba\u610f\uff0c\u5df2\u7ecf\u90fd\u51c6\u5907\u597d\u4e86\uff0c\u7ed9\u743c\u6069\u7684\u81ea\u7136\u662f\u5185\u8863\u548c\u5deb\u5e08\u888d\uff0c\u7ed9\u6885\u83f2\u65af\u7684\u5219\u662f\u4e00\u5957\u6574\u9f50\u7684\u84dd\u8272\u886c\u886b\u957f\u88e4\uff0c\u5c3a\u7801\u5927\u5c0f\u5b8c\u5168\u5408\u8eab\u2014\u2014\u53e6\u5916\u8fd8\u6709\u4e00\u5957\u6027\u611f\u5185\u8863\uff0c\u5305\u62ec\u9ed1\u8272\u8584\u7eb1\u5185\u88e4\u548c\u9542\u7a7a\u82b1\u7eb9\u7684\u6587\u80f8\u3002</p><p>\u201c\u6211\u76ee\u6d4b\u4f60\u5e94\u8be5\u662f\u4e59\u7f69\u676f\u7684\uff0c\u6240\u4ee5\u4e70\u4e86\u8fd9\u5957\uff0c\u5982\u679c\u4f60\u89c9\u5f97\u5c0f\u4e86\u6211\u518d\u62ff\u53bb\u6362\uff0c\u201d\u9b45\u9b54\u7528\u4e00\u79cd\u6311\u9017\u7684\u773c\u795e\u5728\u6885\u83f2\u65af\u80f8\u53e3\u6253\u91cf\u7740\uff0c\u201c\u2026\u2026\u5f53\u7136\u6211\u60f3\u5e94\u8be5\u7528\u4e0d\u7740\u3002\u201d</p><p>\u6885\u83f2\u65af\u4e5f\u4e0d\u4ecb\u610f\uff0c\u5fae\u5fae\u7b11\u7740\uff0c\u743c\u6069\u7565\u6709\u4e9b\u4e0d\u5feb\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u7a81\u7136\u53d1\u73b0\u5979\u521a\u624d\u8bdd\u8bed\u4e2d\u7684\u4e00\u4e2a\u8baf\u606f\uff0c\u201c\u4f60\u521a\u624d\u8bf4\u4ec0\u4e48\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u4e59\u7f69\u676f\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u8fd9\u662f\u6076\u9b54\u4eec\u5212\u5206\u5973\u6027\u4e73\u623f\u5927\u5c0f\u7684\u4e00\u4e2a\u6807\u51c6\uff0c\u5206\u7532\u4e59\u4e19\u4e01\u620a\u5df1\u2026\u2026\u540e\u9762\u6211\u8bb0\u4e0d\u6e05\u4e86\uff0c\u4f60\u53ef\u4ee5\u89c6\u4e3a\u7b49\u540c\u4e8e\u7269\u8d28\u754c\u7684\u5b57\u6bcd\u5212\u5206\u6cd5\u3002\u6211\u662f\u4e01\u7f69\u676f\uff0c\u6700\u8fd1\u6709\u8fbe\u5230\u620a\u7f69\u676f\u7684\u8d8b\u52bf\uff0c\u6076\u9b54\u4eec\u5927\u591a\u662f\u8fd9\u4e2a\u578b\u53f7\uff0c\u5546\u5e97\u91cc\u90fd\u6ca1\u5356\u4e59\u7f69\u676f\u8fd9\u4e48\u5c0f\u7684\uff0c\u5bb3\u5f97\u6211\u8dd1\u904d\u4e86\u6574\u4e2a\u65ad\u57df\u9547\u624d\u627e\u5230\u3002\u201d</p><p>\u743c\u6069\u548c\u838e\u73de\u514b\u8bf4\u8bdd\uff0c\u7528\u7684\u662f\u901a\u7528\u8bed\u3002\u901a\u7528\u8bed\u91cc\u81ea\u7136\u6ca1\u6709\u201c\u7532\u4e59\u4e19\u4e01\u201d\u8fd9\u79cd\u8bcd\uff0c\u4f46\u838e\u73de\u514b\u521a\u624d\u8bf4\u7684\u65f6\u5019\uff0c\u7528\u7684\u5206\u660e\u5c31\u662f\u8fd9\u51e0\u4e2a\u5b57\u7684\u6c49\u8bed\u53d1\u97f3\uff0c\u867d\u7136\u6709\u4e9b\u522b\u626d\uff0c\u4f46\u5927\u81f4\u7684\u8bfb\u97f3\u5dee\u4e0d\u591a\uff0c\u5982\u679c\u662f\u5355\u4e2a\u8bf4\u5012\u8fd8\u7f62\u4e86\uff0c\u5c24\u5176\u662f\u201c\u7532\u4e59\u4e19\u4e01\u201d\u8fde\u7740\u8bfb\u51fa\u6765\uff0c\u66f4\u52a0\u8ba9\u743c\u6069\u786e\u5b9a\u65e0\u7591\uff0c\u6709\u90a3\u4e48\u4e00\u5239\u90a3\uff0c\u4ed6\u51e0\u4e4e\u90fd\u8981\u6000\u7591\u81ea\u5df1\u4f11\u606f\u4e0d\u597d\u53c8\u51fa\u73b0\u5e7b\u542c\u4e86\u3002</p><p>\u201c\u8fd9\u6807\u51c6\u662f\u8c01\u8ba2\u7684\uff1f\u522b\u544a\u8bc9\u6211\u53c8\u662f\u90a3\u4e2a\u6570\u5b57\u516d\u5f3a\u8feb\u75c7\u3002\u201d</p><p>\u201c\u8fd9\u6b21\u4f60\u731c\u9519\u4e86\uff0c\u4e0d\u662f\u683c\u62c9\u5179\u7279\uff0c\u662f\u4ed6\u624b\u4e0b\u4e00\u4e2a\u638c\u7ba1\u60c5\u62a5\u5de5\u4f5c\u7684\u5c06\u519b\u5f04\u51fa\u6765\u7684\u2014\u2014\u5728\u6211\u6765\u4e4b\u524d\u5c31\u8dd1\u5230\u5965\u5580\u65af\u90a3\u8fb9\u53bb\u4e86\uff0c\u6211\u6ca1\u89c1\u8fc7\uff0c\u53eb\u4ec0\u4e48\u540d\u5b57\u6765\u7740\u2026\u2026\u201d</p><p>\u201c\u6b27\u51ef\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u6ca1\u9519\uff0c\u5c31\u8fd9\u4e2a\u540d\u5b57\u3002\u201d</p><p>\u743c\u6069\u4e00\u65f6\u65e0\u8bed\uff0c\u5fc3\u4e2d\u7591\u56e2\u53c8\u589e\u52a0\u4e86\u51e0\u5206\uff0c\u4f46\u4e5f\u6ca1\u518d\u591a\u95ee\u4ec0\u4e48\u3002\u201c\u8fd9\u4e2a\u6b27\u51ef\u5f88\u6709\u8da3\uff0c\u201d\u4ed6\u6de1\u6de1\u8bf4\uff0c\u201c\u6709\u7a7a\u5e2e\u6211\u5c3d\u91cf\u6536\u96c6\u4e00\u4e0b\u4ed6\u7684\u4fe1\u606f\uff0c\u8fd9\u4e48\u6709\u8da3\u7684\u6076\u9b54\uff0c\u5e94\u8be5\u591a\u591a\u4e86\u89e3\u3002\u201d</p><p>\u201c\u9075\u547d\uff0c\u4e3b\u4eba\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u60a8\u662f\u60f3\u8981\u548c\u4ed6\u63a2\u8ba8\u4e00\u4e0b\u6709\u5173\u5973\u5b69\u5b50\u80f8\u90e8\u7f69\u676f\u7684\u8bdd\u9898\uff1f\u6211\u89c9\u5f97\u6ca1\u5fc5\u8981\u627e\u4ed6\uff0c\u6211\u5c31\u662f\u66f4\u5408\u9002\u7684\u4eba\u9009\u2026\u2026\u201d</p><p>\u201c\u6211\u6ca1\u6253\u7b97\u548c\u90a3\u5bb6\u4f19\u8ba8\u8bba\u8fd9\u4e2a\u2026\u2026\u7b49\u7b49\uff0c\u4f60\u521a\u624d\u53eb\u6211\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4e3b\u4eba\u554a\uff0c\u6211\u4eec\u7b7e\u4e86\u7075\u9b42\u5951\u7ea6\uff0c\u4f60\u4e3a\u4f60\u5de5\u4f5c\uff0c\u4f60\u5f53\u7136\u662f\u6211\u7684\u4e3b\u4eba\u4e86\u3002\u201d</p><p>\u201c\u80fd\u4e0d\u80fd\u6362\u4e2a\u79f0\u547c\u3002\u201d</p><p>\u201c\u554a\uff0c\u4f60\u4e0d\u559c\u6b22\uff1f\u6211\u4ee5\u4e3a\u7537\u4eba\u90fd\u559c\u6b22\u73a9\u8fd9\u79cd\u4e3b\u5974\u6e38\u620f\u2026\u2026\u54e6\uff0c\u6211\u660e\u767d\u4e86\uff0c\u4f60\u80af\u5b9a\u559c\u6b22\u5c0f\u59b9\u59b9\uff0c\u96be\u602a\u6765\u6df1\u6e0a\u65c5\u6e38\u90fd\u8fd8\u5e26\u7740\u4e2a\u5c0f\u5353\u5c14\u2026\u2026\u90a3\u4eca\u5929\u8fd9\u8eab\u6253\u626e\u771f\u662f\u5931\u8d25\uff0c\u6211\u5e94\u8be5\u6362\u4e00\u8eab\u6ce1\u6ce1\u8896\u516c\u4e3b\u88d9\u6765\u53eb\u4f60\u5927\u54e5\u54e5\u2026\u2026\u201d</p><p>\u201c\u505c\uff01\u201d\u743c\u6069\u6253\u65ad\uff0c\u201c\u9996\u5148\uff0c\u6211\u4e0d\u662f\u6765\u6df1\u6e0a\u65c5\u6e38\u7684\uff0c\u8c01\u613f\u610f\u6765\u8fd9\u79cd\u9b3c\u5730\u65b9\uff1b\u5176\u6b21\uff0c\u4f0a\u8389\u96c5\u4e0d\u662f\u6211\u5e26\u6765\u7684\u2014\u2014\u51c6\u786e\u5730\u8bf4\u662f\u5979\u5e26\u6211\u6765\u7684\uff1b\u6700\u540e\uff0c\u6211\u4e0d\u559c\u6b22\u5c0f\u59b9\u59b9\uff0c\u6211\u5bf9\u5144\u59b9\u8fd9\u79cd\u6ca1\u611f\u89c9\uff0c\u800c\u4e14\u4f60\u600e\u4e48\u770b\u4e5f\u4e0d\u50cf\u5c0f\u841d\u8389\u2026\u2026\u201d</p><p>\u201c\u90a3\u4f60\u559c\u6b22\u4ec0\u4e48\uff1f\u201d\u9b45\u9b54\u770b\u7740\u65c1\u8fb9\u7684\u6885\u83f2\u65af\uff0c\u201c\u4f60\u559c\u6b22\u5979\uff0c\u559c\u6b22\u5979\u4ec0\u4e48\u5462\uff1f\u559c\u6b22\u63a8\u5012\u5723\u6b66\u58eb\uff1f\u4f60\u771f\u90aa\u6076\uff0c\u6211\u559c\u6b22\uff0c\u53ef\u662f\u8fd9\u7231\u597d\u592a\u7279\u522b\u4e86\u70b9\uff0c\u6211\u529e\u4e0d\u5230\uff1b\u559c\u6b22\u5979\u6f02\u4eae\uff1f\u6211\u4e5f\u5f88\u6f02\u4eae\u554a\u2014\u2014\u4f60\u603b\u4e0d\u4f1a\u662f\u559c\u6b22\u80f8\u5c0f\u7684\u5973\u5b69\u5b50\u5427\u2026\u2026\u201d</p><p>\u201c\u6211\u8bf4\uff0c\u9b45\u9b54\u5c0f\u59d0\uff0c\u5173\u4e8e\u4f60\u5bf9\u6211\u7684\u79f0\u547c\uff0c\u4ee5\u53ca\u6211\u7684\u4e2a\u4eba\u7231\u597d\u95ee\u9898\uff0c\u6211\u4eec\u7a0d\u540e\u627e\u65f6\u95f4\u518d\u8c08\uff0c\u201d\u743c\u6069\u4e00\u5b57\u4e00\u987f\u5730\u8bf4\uff0c\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5e94\u8be5\u5148\u56de\u907f\u4e00\u4e0b\u5462\uff0c\u6211\u4eec\u8981\u6362\u8863\u670d\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5982\u679c\u838e\u73de\u514b\u771f\u8981\u7559\u5728\u623f\u95f4\u91cc\u4e0d\u80af\u79bb\u5f00\uff0c\u743c\u6069\u5012\u8fd8\u771f\u6ca1\u529e\u6cd5\uff0c\u56e0\u4e3a\u771f\u540d\u5951\u7ea6\u4e0a\u5f53\u65f6\u5199\u660e\u5979\u662f\u743c\u6069\u7684\u60c5\u4eba\uff0c\u7406\u6240\u5f53\u7136\u5730\u6709\u8fd9\u4e2a\u6743\u529b\u3002\u743c\u6069\u6ca1\u529e\u6cd5\u5f3a\u884c\u547d\u4ee4\u5979\u79bb\u5f00\uff0c\u53c8\u4e0d\u80fd\u6740\u4e86\u5979\uff0c\u9664\u975e\u628a\u5979\u6253\u6655\u3002\u5e78\u597d\u8fd9\u79cd\u5927\u715e\u98ce\u666f\u7684\u4e8b\u60c5\u4e0d\u9700\u8981\u53d1\u751f\uff0c\u838e\u73de\u514b\u5f88\u8bc6\u8da3\uff0c\u4e56\u4e56\u5730\u51fa\u4e86\u5367\u5ba4\uff0c\u987a\u624b\u628a\u95e8\u63a9\u4e0a\u3002</p><p>\u201c\u4f60\u53ef\u771f\u62e3\u4e86\u4e2a\u597d\u60c5\u4eba\u5462\u3002\u201d\u6885\u83f2\u65af\u770b\u7740\u838e\u73de\u514b\u79bb\u5f00\uff0c\u5c06\u6bef\u5b50\u6380\u5f00\uff0c\u5750\u5728\u5e8a\u5934\uff0c\u62ff\u8d77\u6795\u8fb9\u7684\u886c\u886b\u7a7f\u4e0a\uff0c\u6162\u6162\u6263\u4e0a\u7ebd\u6263\u3002</p><p>\u743c\u6069\u4ece\u8eab\u540e\u62b1\u4f4f\u5979\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u201c\u827e\u5f25\u8587\uff0c\u6211\u2026\u2026\u201d</p><p>\u201c\u6ca1\u4e8b\u5566\uff0c\u6211\u968f\u53e3\u8bf4\u8bf4\u800c\u5df2\uff0c\u4f60\u5c31\u5f53\u662f\u4e2a\u5c0f\u5973\u5b69\u5b50\u5728\u5ac9\u5992\u597d\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u4fa7\u8fc7\u8138\u51b2\u4ed6\u7b11\u7b11\uff0c\u201c\u9053\u7406\u6211\u90fd\u77e5\u9053\uff0c\u4f60\u4e0d\u8bf4\u6211\u4e5f\u660e\u767d\uff0c\u4e5f\u6ca1\u6709\u66f4\u597d\u7684\u9009\u62e9\uff0c\u53ea\u662f\u60f3\u8d77\u6765\uff0c\u5fc3\u91cc\u603b\u96be\u514d\u6709\u70b9\u4e0d\u8212\u670d\u5427\u3002\u5c31\u50cf\u672c\u6765\u53ea\u6709\u6211\u4eec\u4e24\u4e2a\u4eba\u7684\u7a7a\u95f4\uff0c\u7a81\u7136\u591a\u8fdb\u6765\u4e00\u4e2a\u4eba\uff0c\u603b\u662f\u4e0d\u81ea\u5728\uff0c\u5982\u679c\u662f\u51db\u6216\u8005\u73ca\u5609\u59d0\u59d0\u8fd9\u79cd\uff0c\u6211\u6216\u8bb8\u8fd8\u2026\u2026\u4f46\u50cf\u5979\u8fd9\u6837\u2026\u2026\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u201d\u743c\u6069\u4f4e\u58f0\u8bf4\uff0c\u201c\u6211\u660e\u767d\u3002\u201d</p><p>\u4ed6\u7684\u624b\u79fb\u5230\u5c11\u5973\u80f8\u524d\uff0c\u963b\u6b62\u5979\u7684\u52a8\u4f5c\uff0c\u201c\u5148\u522b\u7a7f\u886c\u886b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u5fd8\u4e86\u6587\u80f8\u3002\u201d</p><p>\u201c\u6211\u6ca1\u8fd9\u4e60\u60ef\u3002\u201d</p><p>\u201c\u6234\u4e0a\u5427\u3002\u201d</p><p>\u6885\u83f2\u65af\u770b\u4e86\u770b\u4ed6\uff0c\u201c\u4f60\u559c\u6b22\u90a3\u6211\u5c31\u6234\u4e86\u3002\u201d</p><p>\u5979\u5c06\u886c\u886b\u892a\u4e0b\uff0c\u62ff\u8d77\u6587\u80f8\uff0c\u5374\u4e0d\u592a\u77e5\u9053\u7528\u6cd5\uff0c\u867d\u7136\u770b\u8fc7\u522b\u7684\u5973\u5b69\u5b50\u6234\uff0c\u7ec8\u7a76\u81ea\u5df1\u6ca1\u8bd5\u8fc7\u3002\u743c\u6069\u4ece\u5979\u624b\u4e2d\u63a5\u8fc7\u6765\uff0c\u66ff\u5979\u7a7f\u4e0a\uff0c\u6587\u80f8\u4fa7\u62e2\u6548\u679c\u5f88\u597d\uff0c\u987f\u65f6\u663e\u5f97\u6885\u83f2\u65af\u7684\u80f8\u524d\u6c9f\u58d1\u6df1\u4e86\u51e0\u5206\uff0c\u201c\u4e0d\u9519\u5462\u3002\u201d\u743c\u6069\u8bf4\uff0c\u5728\u5c11\u5973\u8138\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u7136\u540e\u53d6\u8fc7\u886c\u886b\u5e2e\u5979\u7a7f\u4e0a\uff0c\u66ff\u5979\u4e00\u7c92\u7c92\u6263\u597d\u7ebd\u6263\uff0c\u6885\u83f2\u65af\u4e56\u4e56\u504e\u4f9d\u5728\u4ed6\u6000\u91cc\u3002</p><p>\u201c\u886c\u886b\u7a7f\u597d\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u817f\u62ac\u8d77\u6765\u3002\u201d</p><p>\u6885\u83f2\u65af\u8eab\u6750\u6781\u9ad8\uff0c\u53ea\u6bd4\u743c\u6069\u7a0d\u77ee\u4e9b\uff0c\u4e00\u53cc\u7389\u817f\u7b14\u633a\u4fee\u957f\uff0c\u8fd1\u4e4e\u5b8c\u7f8e\u3002\u743c\u6069\u867d\u7136\u770b\u8fc7\u65e0\u6570\u904d\uff0c\u6bcf\u6b21\u89c1\u5230\u4f9d\u65e7\u7231\u4e0d\u91ca\u624b\uff0c\u78e8\u78e8\u8e6d\u8e6d\u66ff\u5979\u7a7f\u597d\u8584\u7eb1\u5185\u88e4\u548c\u957f\u88e4\uff0c\u8d81\u673a\u4e0d\u77e5\u5360\u4e86\u591a\u5c11\u4fbf\u5b9c\uff0c\u201c\u8fd9\u4e2a\u4e16\u754c\u6709\u4e1d\u7ef8\uff0c\u53ea\u662f\u4e3a\u4ec0\u4e48\u6ca1\u770b\u5230\u4e1d\u889c\uff0c\u96be\u9053\u4e0d\u662f\u540c\u4e00\u79cd\u6750\u8d28\u4e48\uff0c\u6709\u7a7a\u5012\u8981\u6253\u542c\u4e00\u4e0b\uff0c\u201d\u4ed6\u9ed8\u5fd6\u7740\uff0c\u201c\u8fd9\u6837\u6f02\u4eae\u7684\u7f8e\u817f\uff0c\u5c31\u5e94\u8be5\u7a7f\u4e0a\u4e1d\u889c\u624d\u5b8c\u7f8e\u554a\u3002\u201d</p><p>\u6885\u83f2\u65af\u4e0d\u77e5\u9053\u4ed6\u5728\u8f6c\u4ec0\u4e48\u5ff5\u5934\uff0c\u4f46\u77e5\u9053\u60c5\u4eba\u559c\u6b22\u81ea\u5df1\u7684\u53cc\u817f\uff0c\u603b\u662f\u5fc3\u91cc\u751c\u871c\u3002\u201c\u597d\u5566\uff0c\u201d\u5979\u55d4\u602a\u7740\uff0c\u201c\u90fd\u5f04\u534a\u5929\u4e86\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u66ff\u5979\u628a\u957f\u88e4\u4e0a\u6700\u540e\u4e00\u7c92\u7ebd\u6263\u6263\u597d\uff0c\u8f6c\u8eab\u53d6\u8fc7\u81ea\u5df1\u7684\u8863\u670d\u7a7f\u4e0a\uff0c\u4ed6\u5e2e\u6885\u83f2\u65af\u7a7f\u8863\u6781\u5c3d\u8f7b\u67d4\uff0c\u8f6e\u5230\u81ea\u5df1\u5c31\u4e09\u4e0b\u4e94\u9664\u4e8c\uff0c\u6ca1\u8fc7\u534a\u5206\u949f\u5c31\u5168\u7a7f\u597d\u4e86\u3002\u201c\u8d70\u5427\u3002\u201d</p><p>\u6885\u83f2\u65af\u4f11\u606f\u4e00\u591c\uff0c\u679c\u7136\u7cbe\u795e\u6062\u590d\u4e86\u5f88\u591a\uff0c\u81f3\u5c11\u884c\u8d70\u5df2\u7ecf\u6ca1\u6709\u4ec0\u4e48\u5927\u788d\u4e86\u3002\u8001\u5934\u9aa8\u867d\u7136\u662f\u6076\u9b54\u7ecf\u8425\uff0c\u5374\u4e5f\u5949\u884c\u987e\u5ba2\u662f\u4e0a\u5e1d\u7684\u539f\u5219\uff0c\u5404\u79cd\u670d\u52a1\u9f50\u5907\uff0c\u5f53\u743c\u6069\u548c\u6885\u83f2\u65af\u8d70\u5230\u5ba2\u5385\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u65e9\u9910\u90fd\u5df2\u7ecf\u5b89\u6392\u597d\uff0c\u838e\u73de\u514b\u6b63\u5750\u5728\u65c1\u8fb9\uff0c\u4eff\u4f5b\u5973\u4e3b\u4eba\u4e00\u822c\u7b49\u5f85\u7740\u3002</p><p>\u201c\u8fd9\u5bb6\u65c5\u5e97\u7684\u670d\u52a1\u8fd8\u771f\u4e0d\u9519\u3002\u201d\u743c\u6069\u8bc4\u4ef7\u3002</p><p>\u201c\u56e0\u4e3a\u5b83\u771f\u6b63\u7684\u8001\u677f\u662f\u7ea2\u8272\u5bff\u8863\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4f5c\u4e3a\u4e00\u4e2a\u9b45\u9b54\uff0c\u7ea2\u8272\u5bff\u8863\u662f\u5904\u5904\u8ffd\u6c42\u4f18\u96c5\u3001\u8212\u9002\u3001\u7cbe\u81f4\u548c\u5b8c\u7f8e\u7684\u3002\u201d</p><p>\u201c\u597d\u4e60\u60ef\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u89c9\u5f97\u5982\u679c\u5c06\u6765\u6709\u4e00\u5929\u6df1\u6e0a\u91cc\u5168\u90fd\u662f\u9b45\u9b54\uff0c\u90a3\u5929\u4e0b\u5c31\u592a\u5e73\u4e86\u3002\u201d</p><p>\u201c\u90a3\u5929\u4e0b\u5c31\u4e0d\u592a\u5e73\u4e86\uff0c\u201d\u838e\u73de\u514b\u7b11\u76c8\u76c8\u5730\u53cd\u9a73\uff0c\u201c\u6bcf\u5929\u90fd\u4f1a\u6709\u65e0\u6570\u7684\u51e1\u4eba\u81ea\u6740\uff0c\u53ea\u4e3a\u4e86\u80fd\u6765\u6df1\u6e0a\u5f53\u6076\u9b54\u3002\u201d</p><p>\u743c\u6069\u6ca1\u63a5\u8fd9\u4e2a\u8bdd\u9898\uff0c\u201c\u5176\u4ed6\u4eba\u5462\uff1f\u201d\u4ed6\u95ee\u838e\u73de\u514b\uff0c\u201c\u96be\u9053\u8fd8\u6ca1\u8d77\u5e8a\uff1f\u201d</p><p>\u201c\u4f60\u4ee5\u4e3a\u90fd\u50cf\u4f60\u4eec\u4fe9\u4e00\u6837\u4e48\uff0c\u201d\u9b45\u9b54\u7fd8\u8d77\u5634\u89d2\uff0c\u201c\u5979\u4eec\u65e9\u5c31\u8d77\u6765\u4e86\uff0c\u6b23\u5e03\u3001\u8475\u9732\u3001\u963f\u5fd2\u59ae\u548c\u90a3\u4e2a\u7a7f\u77ed\u88d9\u7684\u5c0f\u5973\u5deb\u5df2\u7ecf\u53bb\u51b3\u6597\u573a\u4e86\uff0c\u4f0a\u8389\u96c5\u4e5f\u8ddf\u7740\u8fc7\u53bb\uff0c\u73b0\u5728\u5c31\u7b49\u4f60\u4eec\u5462\u3002\u201d</p><p>\u743c\u6069\u4e0d\u518d\u591a\u8bf4\uff0c\u5f00\u59cb\u5bf9\u4ed8\u65e9\u9910\u3002\u636e\u838e\u73de\u514b\u8bf4\uff0c\u6076\u9b54\u662f\u65e0\u9700\u901a\u8fc7\u8fdb\u98df\u6765\u8865\u5145\u4f53\u529b\u7684\uff0c\u4f46\u4ed6\u4eec\u5927\u591a\u8d2a\u56fe\u53e3\u8179\u4e4b\u6b32\uff0c\u6240\u4ee5\u65ad\u57df\u9547\u4e2d\u4e5f\u4f1a\u6709\u5404\u79cd\u7269\u8d28\u754c\u7684\u7f8e\u98df\u51fa\u552e\uff0c\u5c24\u5176\u662f\u7531\u51e1\u4eba\u6b7b\u540e\u8f6c\u5316\u800c\u6765\u7684\u6076\u9b54\uff0c\u867d\u7136\u8bb0\u5fc6\u6cef\u706d\uff0c\u4f46\u6f5c\u610f\u8bc6\u91cc\u7684\u4e00\u4e9b\u4e60\u6027\u4f9d\u65e7\u4fdd\u7559\uff0c\u5bf9\u98df\u7269\u66f4\u52a0\u70ed\u8877\u3002\u743c\u6069\u997f\u4e86\u4e00\u5929\uff0c\u80c3\u53e3\u6b63\u597d\uff0c\u6885\u83f2\u65af\u662f\u795e\u5b50\uff0c\u4f53\u8d28\u7279\u6b8a\uff0c\u5411\u6765\u5c31\u5403\u5f97\u5f88\u5c11\uff0c\u51e0\u5929\u4e0d\u8fdb\u98df\u4e5f\u6ca1\u5173\u7cfb\uff0c\u52a0\u4e0a\u8fd8\u6bd4\u8f83\u865a\u5f31\uff0c\u53ea\u662f\u559d\u4e86\u70b9\u725b\u5976\uff0c\u5176\u4ed6\u57fa\u672c\u6ca1\u78b0\u3002</p><p>\u65e9\u9910\u6bd5\uff0c\u743c\u6069\u5f97\u548c\u838e\u73de\u514b\u53bb\u51b3\u6597\u573a\u548c\u6b23\u5e03\u7b49\u4eba\u6c47\u5408\uff0c\u96c6\u4f53\u62a5\u540d\u53c2\u52a0\u90a3\u4ec0\u4e48\u51b3\u6597\u5927\u8d5b\uff0c\u4ed6\u4e0d\u653e\u5fc3\u6885\u83f2\u65af\u4e00\u4eba\u7559\u4e0b\uff0c\u7d22\u6027\u90fd\u540c\u53bb\u3002\u5230\u4e86\u5730\u65b9\uff0c\u4ece\u7199\u7199\u6518\u6518\u7684\u4eba\u7fa4\u4e2d\u4e00\u773c\u627e\u5230\u4e86\u6b23\u5e03\u7b49\u4eba\u2014\u2014\u539f\u56e0\u65e0\u4ed6\uff0c\u4ece\u4e00\u5806\u5947\u5f62\u602a\u72b6\u7684\u6076\u9b54\u91cc\u5bfb\u627e\u51e0\u4e2a\u4eba\u7c7b\uff0c\u90a3\u5b9e\u5728\u662f\u5bb9\u6613\u5f97\u4e0d\u80fd\u518d\u5bb9\u6613\u4e86\uff0c\u4f55\u51b5\u6b23\u5e03\u8475\u9732\u90fd\u662f\u975e\u5e38\u9ad8\u6311\u7684\u5973\u5b50\u3002</p><p>\u62a5\u540d\u5904\u7684\u524d\u9762\u5df2\u7ecf\u6392\u6ee1\u4e86\u957f\u961f\uff0c\u4e0d\u65f6\u6709\u6076\u9b54\u63d2\u961f\uff0c\u7136\u540e\u53c8\u4e0d\u65f6\u6709\u4eba\u88ab\u6324\u51fa\u6765\uff0c\u5927\u6982\u662f\u754f\u60e7\u65ad\u57df\u9547\u7684\u89c4\u77e9\uff0c\u5012\u8fd8\u6ca1\u6709\u8c01\u5927\u6253\u51fa\u624b\uff0c\u53ea\u662f\u6697\u4e2d\u641e\u70b9\u5c0f\u52a8\u4f5c\u3002\u743c\u6069\u628a\u6885\u83f2\u65af\u7d27\u7d27\u62a4\u5728\u8eab\u540e\uff0c\u6709\u6076\u9b54\u89c1\u8fd9\u91cc\u6709\u51e0\u4e2a\u4eba\u7c7b\uff0c\u4ee5\u4e3a\u5bb9\u6613\u6b3a\u8d1f\uff0c\u8fc7\u6765\u63d2\u961f\uff0c\u6b23\u5e03\u51b7\u54fc\u4e00\u58f0\uff0c\u7834\u7834\u70c2\u70c2\u7684\u9ed1\u888d\u4e0a\u9661\u7136\u94f6\u706b\u817e\u8d77\uff0c\u7f2d\u7ed5\u8eab\u5468\uff0c\u6240\u6709\u9644\u8fd1\u7684\u6076\u9b54\u611f\u53d7\u5230\u90a3\u80a1\u5f3a\u70c8\u7684\u795e\u5723\u6c14\u606f\uff0c\u672c\u80fd\u5730\u540e\u9000\uff0c\u987f\u65f6\u5468\u56f4\u7a7a\u5f00\u4e86\u4e00\u5c0f\u5757\u5730\u65b9\u3002</p><p>\u201c\u6211\u5728\u8003\u8651\u8981\u4e0d\u8981\u4e00\u53d1\u6d41\u661f\u7206\u628a\u8fd9\u4e9b\u866b\u5b50\u4eec\u90fd\u70e7\u6b7b\u3002\u201d\u6b23\u5e03\u5bf9\u8475\u9732\u8bf4\u3002</p><p>\u5979\u58f0\u97f3\u4e0d\u5927\uff0c\u4f46\u4e5f\u6ca1\u6709\u523b\u610f\u538b\u4f4e\uff0c\u8fd9\u4e9b\u6076\u9b54\u4eec\u4e2a\u4e2a\u8033\u76ee\u7075\u654f\uff0c\u5168\u90fd\u542c\u89c1\u4e86\uff0c\u767b\u65f6\u96c6\u4f53\u5bf9\u5979\u6012\u76ee\u800c\u89c6\uff0c\u53ea\u662f\u4e00\u5219\u754f\u60e7\u6cd5\u5f8b\uff0c\u4e8c\u5219\u754f\u60e7\u94f6\u706b\uff0c\u4e0d\u6562\u4e0a\u524d\u6311\u8845\u3002\u201c\u597d\u4e86\uff0c\u201d\u8475\u9732\u4f4e\u58f0\u8bf4\uff0c\u201c\u522b\u591a\u60f9\u9ebb\u70e6\uff0c\u6211\u4eec\u8fd8\u6709\u6b63\u4e8b\u5462\u3002\u201d</p><p>\u51b3\u6597\u5927\u8d5b\u7684\u62a5\u540d\u5fc5\u987b\u4ee5\u56e2\u4f53\u65b9\u5f0f\u8fdb\u884c\uff0c\u6bcf\u4e2a\u56e2\u5fc5\u987b\u516d\u4eba\uff0c\u8fd9\u4e9b\u6076\u9b54\u4eec\u5e73\u7d20\u90fd\u72ec\u6765\u72ec\u5f80\u60ef\u4e86\uff0c\u5982\u4eca\u4e3a\u4e86\u5956\u54c1\uff0c\u4e5f\u53ea\u5f97\u6682\u65f6\u5408\u4f5c\u8d77\u6765\u3002\u201c\u6211\u4e0d\u660e\u767d\u683c\u62c9\u5179\u7279\u4e3a\u4ec0\u4e48\u5b9a\u8fd9\u4e2a\u89c4\u77e9\uff0c\u201d\u743c\u6069\u95ee\u838e\u73de\u514b\uff0c\u201c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u4ee5\u56e2\u4f53\u65b9\u5f0f\u51fa\u8d5b\u5462\u3002\u201d</p><p>\u201c\u4ed6\u8bf4\u8fd9\u662f\u4e3a\u4e86\u57f9\u517b\u6076\u9b54\u4eec\u7684\u56e2\u961f\u7cbe\u795e\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u7406\u7531\u771f\u5f3a\u2026\u2026\u53ef\u662f\u5982\u679c\u8fd9\u6837\u7684\u8bdd\uff0c\u90a3\u4e3a\u4ec0\u4e48\u4e0d\u89c4\u5b9a\u6bcf\u4e2a\u4eba\u6bcf\u8f6e\u53ea\u80fd\u51fa\u8d5b\u4e00\u573a\uff0c\u8ba9\u961f\u91cc\u6bcf\u4e2a\u4eba\u90fd\u80fd\u591f\u4e0a\u573a\uff0c\u4ee5\u6700\u540e\u80dc\u8d25\u591a\u5c11\u5b9a\u664b\u7ea7\u5462\u3002\u73b0\u5728\u8fd9\u79cd\u89c4\u77e9\uff0c\u5c82\u4e0d\u662f\u53ea\u8981\u961f\u91cc\u6709\u8db3\u591f\u5206\u91cf\u7684\u5f3a\u8005\uff0c\u5c31\u80fd\u4e00\u4eba\u5305\u56ca\u4e86\u5417\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u56e0\u4e3a\u683c\u62c9\u5179\u7279\u8bf4\uff0c\u5982\u679c\u592a\u6709\u56e2\u961f\u7cbe\u795e\u4e86\uff0c\u90a3\u5c31\u53c8\u592a\u50cf\u9b54\u9b3c\u4e86\uff0c\u6211\u4eec\u6bd5\u7adf\u662f\u6076\u9b54\uff0c\u5f97\u8981\u6709\u6076\u9b54\u7279\u8272\u2014\u2014\u4ed6\u8bf4\u4ed6\u5b9a\u51fa\u6765\u7684\u8fd9\u5957\u89c4\u5219\u53eb\u505a\u6709\u6df1\u6e0a\u7279\u8272\u7684\u56e2\u4f53\u4e3b\u4e49\u3002\u201d</p><p>\u201c\u90a3\u5956\u54c1\u600e\u4e48\u529e\u5462\uff0c\u6211\u4e0a\u6b21\u8fd8\u5fd8\u4e86\u95ee\uff0c\u65e2\u7136\u56e2\u961f\u53c2\u8d5b\uff0c\u6bcf\u4e2a\u540d\u6b21\u53c8\u53ea\u6709\u4e00\u4e2a\u5956\u54c1\uff0c\u90a3\u600e\u4e48\u5206\u914d\u2026\u2026\u96be\u9053\u8bf4\u2026\u2026\u201d</p><p>\u201c\u4f60\u731c\u5bf9\u4e86\uff0c\u81ea\u7136\u662f\u5927\u5bb6\u6bd4\u8d5b\u5b8c\u4e86\u5185\u90e8\u518d\u6253\u67b6\uff0c\u51b3\u5b9a\u5956\u54c1\u5f52\u5c5e\uff0c\u5f53\u7136\u4f60\u613f\u610f\u79c1\u4e0b\u534f\u5546\u4e5f\u6ca1\u95ee\u9898\uff0c\u4f46\u6ca1\u54ea\u4e2a\u6076\u9b54\u6709\u8fd9\u79cd\u597d\u4e60\u60ef\u3002\u683c\u62c9\u5179\u7279\u8bf4\u8fd9\u53eb\u505a\u9f13\u52b1\u7ade\u4e89\uff0c\u6709\u7ade\u4e89\u624d\u6709\u6d3b\u529b\uff0c\u6709\u7ade\u4e89\u624d\u6709\u8fdb\u6b65\uff0c\u6709\u7ade\u4e89\u624d\u6709\u672a\u6765\u548c\u660e\u5929\u3002\u201d</p><p>\u201c\u2026\u2026\u73b0\u5728\u6211\u767e\u5206\u4e4b\u767e\u786e\u5b9a\u90a3\u5bb6\u4f19\u662f\u4e2a\u5fc3\u7406\u53d8\u6001\u4e86\u3002\u201d</p></article>"}}}]);
