(window.webpackJsonp=window.webpackJsonp||[]).push([[1527],{1550:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e94\u8282 \u8c03\u6559",content:"<article><h2>\u7b2c\u4e09\u5341\u4e94\u8282 \u8c03\u6559</h2><p>\u5173\u4e8e\u201c\u683c\u62c9\u5179\u7279\u662f\u4e2a\u5fc3\u7406\u53d8\u6001\u201d\u8fd9\u70b9\uff0c\u663e\u7136\u5927\u5bb6\u65e9\u5c31\u5df2\u7ecf\u8fbe\u6210\u4e86\u5171\u8bc6\uff0c\u743c\u6069\u8bf4\u51fa\u8fd9\u53e5\u8bdd\uff0c\u4e0d\u4f46\u838e\u73de\u514b\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u53cd\u5e94\uff0c\u65c1\u8fb9\u7684\u5176\u4ed6\u6076\u9b54\u4e5f\u90fd\u7eb7\u7eb7\u70b9\u5934\uff0c\u8fde\u8fde\u8868\u793a\u8d5e\u540c\uff0c\u4e00\u526f\u201c\u4e8e\u6211\u5fc3\u6709\u621a\u621a\u7109\u201d\u7684\u6837\u5b50\uff0c\u751a\u81f3\u8fd8\u6709\u60f3\u8fc7\u6765\u63e1\u624b\u62cd\u80a9\u8868\u793a\u5fd7\u540c\u9053\u5408\u7684\uff0c\u5f53\u7136\u90fd\u88ab\u6b23\u5e03\u7684\u94f6\u706b\u5413\u9000\u4e86\u3002\u7531\u6b64\u53ef\u89c1\u683c\u62c9\u5179\u7279\u8fd9\u5bb6\u4f19\u7684\u540d\u58f0\u548c\u4eba\u7f18\u6709\u591a\u7cdf\u7cd5\uff0c\u4ed6\u8ba2\u7684\u8fd9\u89c4\u77e9\u6709\u591a\u4e48\u7684\u4e0d\u5f97\u4eba\u5fc3\uff0c\u504f\u504f\u8fd9\u5fc3\u7406\u53d8\u6001\u8fd8\u662f\u6df1\u6e0a\u4e09\u5927\u6076\u9b54\u9886\u4e3b\u4e4b\u4e00\uff0c\u5426\u5219\u53ea\u6015\u65e9\u5c31\u88ab\u8fd9\u91cc\u62a5\u540d\u53c2\u8d5b\u7684\u6076\u9b54\u9009\u624b\u4eec\u51b2\u8fdb\u5367\u5ba4\uff0c\u4ece\u5e8a\u4e0a\u63ea\u8d77\u6765\uff0c\u8e29\u4e0a\u4e00\u5343\u53ea\u8e44\u5b50\u4e86\u2014\u2014\u6076\u9b54\u5927\u591a\u90fd\u6709\u8e44\u5b50\u3002</p><p>\u5f53\u7136\uff0c\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u683c\u62c9\u5179\u7279\u603b\u7b97\u662f\u505a\u4e86\u4ef6\u597d\u4e8b\uff0c\u8981\u4e0d\u662f\u8fd9\u79cd\u53d8\u6001\u7684\u89c4\u77e9\uff0c\u4ed6\u5c82\u4e0d\u662f\u4e5f\u8981\u4e0a\u573a\u53bb\u548c\u6076\u9b54\u6253\u67b6\uff1f\u90a3\u592a\u5371\u9669\u4e86\u3002\u73b0\u5728\u8fd9\u79cd\u5b89\u6392\u5c31\u5f88\u597d\uff0c\u6253\u67b6\u7684\u4e8b\u60c5\u6b23\u5e03\u548c\u8475\u9732\u53bb\uff0c\u6253\u8d62\u4e86\u7684\u6218\u5229\u54c1\u5f52\u4ed6\u6240\u6709\u2014\u2014\u5f53\u7136\uff0c\u743c\u6069\u4e0d\u662f\u7b28\u86cb\uff0c\u66f4\u4e0d\u4f1a\u628a\u522b\u4eba\u5f53\u7b28\u86cb\uff0c\u6b23\u5e03\u548c\u8475\u9732\u8981\u4ed6\u6765\u53c2\u8d5b\uff0c\u81ea\u7136\u4e0d\u53ef\u80fd\u771f\u7684\u4ec5\u4ec5\u53ea\u662f\u51d1\u4e2a\u6570\u800c\u5df2\uff0c\u4e07\u4e00\u6709\u9700\u8981\u7684\u65f6\u5019\uff0c\u770b\u5728\u51db\u7684\u4efd\u4e0a\u743c\u6069\u8fd8\u662f\u4f1a\u5e2e\u5fd9\u7684\u3002\u800c\u4e14\u540e\u6765\u743c\u6069\u53c8\u660e\u663e\u8868\u73b0\u51fa\u4e86\u5bf9\u90a3\u679a\u5370\u7ae0\u7684\u6d53\u539a\u5174\u8da3\uff0c\u8475\u9732\u4fbf\u6b63\u597d\u628a\u5956\u54c1\u7684\u672a\u6765\u6240\u6709\u6743\u8bb8\u8bfa\u7ed9\u4ed6\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u5927\u5bb6\u5c31\u66f4\u7b97\u662f\u5229\u76ca\u4e00\u81f4\u4e86\uff0c\u4e0d\u6015\u4ed6\u4e0d\u5c3d\u529b\u3002</p><p>\u5f7c\u6b64\u5404\u81ea\u6709\u5404\u81ea\u7684\u76d8\u7b97\uff0c\u4f46\u65e2\u7136\u5904\u5883\u7c7b\u4f3c\uff0c\u76ee\u6807\u4e00\u81f4\uff0c\u90a3\u5927\u5bb6\u5408\u4f5c\u4e5f\u81ea\u65e0\u4e0d\u53ef\u3002\u6076\u9b54\u4eec\u7684\u529e\u4e8b\u6548\u7387\u5f88\u9ad8\uff0c\u4e0d\u5230\u4e8c\u5341\u5206\u949f\uff0c\u5df2\u7ecf\u8f6e\u5230\u743c\u6069\u7b49\u4eba\u4e86\uff0c\u6309\u7167\u4e8b\u5148\u7684\u534f\u8bae\uff0c\u8475\u9732\u3001\u6b23\u5e03\u3001\u963f\u5fd2\u59ae\u3001\u51db\u3001\u743c\u6069\u548c\u838e\u73de\u514b\u516d\u4eba\u4e0a\u524d\u5206\u522b\u7b7e\u4e86\u5404\u81ea\u7684\u540d\u5b57\uff0c\u9886\u5230\u4e00\u679a\u8bc1\u660e\u8eab\u4efd\u7684\u94c1\u724c\uff0c\u5230\u65f6\u5019\u4fbf\u53ef\u4ee5\u636e\u6b64\u53c2\u52a0\u6bd4\u8d5b\u3002</p><p>\u4e8b\u60c5\u529e\u59a5\uff0c\u4f17\u4eba\u4fbf\u5404\u81ea\u6563\u53bb\uff0c\u6b23\u5e03\u548c\u8475\u9732\u662f\u8981\u53bb\u7ee7\u7eed\u5bfb\u627e\u8428\u9a6c\u65af\u7279\u7684\u884c\u8e2a\uff0c\u6bd5\u7adf\u5979\u4eec\u771f\u6b63\u7684\u76ee\u6807\u4e0d\u662f\u90a3\u679a\u5370\u7ae0\uff0c\u800c\u662f\u8428\u9a6c\u65af\u7279\uff0c\u53ea\u8981\u628a\u8fd9\u4e2a\u6b63\u7b79\u5212\u7740\u6bc1\u706d\u4e16\u754c\u7684\u75af\u5b50\u641e\u5b9a\uff0c\u987a\u4fbf\u518d\u628a\u4ed6\u7684\u547d\u5323\u9500\u6bc1\uff0c\u90a3\u5929\u4e0b\u5c31\u771f\u6b63\u592a\u5e73\u4e86\u3002\u4e0d\u8fc7\u8fd9\u79cd\u641c\u5bfb\u5176\u5b9e\u5e0c\u671b\u975e\u5e38\u6e3a\u832b\uff0c\u7b80\u76f4\u4fbf\u5982\u5927\u6d77\u635e\u9488\uff0c\u4e24\u4f4d\u9009\u6c11\u81ea\u5df1\u4e5f\u6e05\u695a\uff0c\u62b1\u7740\u8bd5\u8bd5\u770b\u7684\u6001\u5ea6\u7eaf\u7cb9\u78b0\u8fd0\u6c14\u7f62\u4e86\u3002\u963f\u5fd2\u59ae\u662f\u4e2a\u795e\u7bad\u624b\uff0c\u773c\u529b\u654f\u9510\uff0c\u88ab\u8475\u9732\u62c9\u53bb\u5e2e\u5fd9\uff0c\u51db\u5219\u8d56\u5728\u6885\u83f2\u65af\u8eab\u8fb9\u8981\u966a\u5979\uff0c\u6b23\u5e03\u4e5f\u5c31\u7b97\u4e86\u3002\u4e8e\u662f\u5206\u9053\u626c\u9573\uff0c\u6b23\u5e03\u3001\u8475\u9732\u548c\u963f\u5fd2\u59ae\u53bb\u627e\u8428\u9a6c\u65af\u7279\uff0c\u743c\u6069\u5e26\u7740\u6885\u83f2\u65af\u3001\u838e\u73de\u514b\u3001\u51db\u4e09\u4f4d\u7f8e\u5973\u53bb\u901b\u8857\uff0c\u4e3b\u8981\u662f\u4e3a\u660e\u5929\u7684\u6bd4\u8d5b\u505a\u70b9\u51c6\u5907\uff0c\u5353\u5c14\u5c0f\u841d\u8389\u4f0a\u8389\u96c5\u81ea\u7136\u8ddf\u968f\u3002</p><p>\u201c\u963f\u5fd2\u59ae\uff0c\u8981\u6211\u4eec\u5e2e\u4f60\u4e70\u4e9b\u7bad\u77e2\u5417\uff1f\u201d\u6885\u83f2\u65af\u95ee\uff0c\u201c\u6211\u770b\u4f60\u7684\u7bad\u56ca\u5feb\u7a7a\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u7528\u4e86\uff0c\u6211\u81ea\u5df1\u53bb\u5c31\u597d\uff0c\u201d\u963f\u5fd2\u59ae\u7b11\u7740\u6447\u6447\u624b\uff0c\u8ddf\u7740\u8475\u9732\u5411\u8857\u9053\u4e00\u7aef\u8d70\u53bb\uff0c\u201c\u6211\u5bf9\u7bad\u77e2\u53ef\u662f\u5f88\u6709\u4e9b\u6311\u5254\u7684\u5462\u3002\u201d</p><p>\u65ad\u57df\u9547\u5546\u4e1a\u53d1\u8fbe\uff0c\u56e0\u4e3a\u8840\u6218\u7684\u5173\u7cfb\uff0c\u8857\u9053\u4e24\u65c1\u7684\u5546\u5e97\u5f88\u591a\u662f\u5356\u519b\u706b\u88c5\u5907\uff0c\u4f46\u8d70\u8fdb\u53bb\u7ec6\u770b\uff0c\u53d1\u73b0\u57fa\u672c\u90fd\u662f\u6076\u9b54\u624d\u80fd\u4f7f\u7528\u7684\u4e1c\u897f\uff0c\u5bf9\u4e8e\u743c\u6069\u7b49\u4eba\u6765\u8bf4\u6839\u672c\u6ca1\u6709\u610f\u4e49\uff0c\u51db\u62c9\u7740\u6885\u83f2\u65af\u8dd1\u5230\u65c1\u8fb9\u7684\u5546\u5e97\u91cc\u770b\u5c0f\u9970\u54c1\uff0c\u4f0a\u8389\u96c5\u4e5f\u8ddf\u4e86\u8fc7\u53bb\u3002\u838e\u73de\u514b\u662f\u9b45\u9b54\uff0c\u6309\u9053\u7406\u5979\u662f\u53ef\u4ee5\u8fd9\u4e9b\u88c5\u5907\u7684\uff0c\u4f46\u5979\u4e5f\u51e0\u4e4e\u90fd\u4e0d\u7406\u4e0d\u776c\uff0c\u76f4\u63a5\u5ffd\u7565\uff0c\u8fd9\u8ba9\u743c\u6069\u6709\u4e9b\u5947\u602a\u3002</p><p>\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u4e70\u4e9b\u88c5\u5907\uff1f\u201d\u5deb\u5e08\u95ee\uff0c\u867d\u8bf4\u4ed6\u5bf9\u9b45\u9b54\u6ca1\u6709\u592a\u591a\u597d\u611f\uff0c\u4f46\u6bd5\u7adf\u660e\u5929\u5c31\u8981\u5f00\u59cb\u53c2\u8d5b\u4e86\uff0c\u5c31\u7b97\u6709\u4e24\u4e2a\u9009\u6c11\u5728\u524d\u9762\u9876\u7740\uff0c\u5e94\u8be5\u6ca1\u4ec0\u4e48\u673a\u4f1a\u4e0a\u573a\uff0c\u4f46\u591a\u5f04\u70b9\u4fdd\u969c\uff0c\u603b\u662f\u4e0d\u574f\u7684\uff0c\u5200\u5175\u65e0\u773c\uff0c\u8c01\u8bf4\u5f97\u51c6\u5462\u3002\u4e07\u4e00\u5979\u6709\u4e8b\uff0c\u81ea\u5df1\u5c82\u4e0d\u8ddf\u7740\u906d\u6b83\u3002</p><p>\u201c\u6211\u4e70\u4e0d\u8d77\u3002\u201d\u9b45\u9b54\u8bf4\u3002</p><p>\u201c\u4e70\u4e0d\u8d77\uff1f\u201d\u743c\u6069\u8be7\u5f02\uff0c\u201c\u6211\u770b\u8fd9\u4e9b\u4e1c\u897f\u4ef7\u683c\u4e5f\u5f88\u4fbf\u5b9c\u554a\u3002\u201d</p><p>\u201c\u4f60\u5f04\u9519\u4e86\u4e00\u4ef6\u4e8b\uff0c\u8fd9\u4e9b\u6807\u4ef7\u7684\u6570\u5b57\u786e\u5b9e\u4e0d\u5927\uff0c\u4f46\u5355\u4f4d\u5f88\u5927\uff0c\u201d\u838e\u73de\u514b\u56de\u7b54\uff0c\u201c\u7269\u8d28\u754c\u7684\u91d1\u5e01\u5728\u65ad\u57df\u9547\u662f\u65e0\u7528\u7684\uff0c\u8fd9\u91cc\u901a\u7528\u7684\u8d27\u5e01\u662f\u5b9d\u77f3\u3002\u201d</p><p>\u201c\u5514\uff0c\u5b9d\u77f3\u6211\u5012\u4e5f\u5e26\u4e86\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u201c\u6211\u8bf4\u7684\u662f\u7075\u9b42\u5b9d\u77f3\uff0c\u201d\u838e\u73de\u514b\u6253\u65ad\uff0c\u201c\u50a8\u5b58\u4e86\u51e1\u4eba\u7075\u9b42\u7684\u5b9d\u77f3\u3002\u201d</p><p>\u743c\u6069\u4e0d\u8bf4\u8bdd\u4e86\u3002</p><p>\u201c\u683c\u62c9\u5179\u7279\u867d\u7136\u662f\u4e2a\u5fc3\u7406\u53d8\u6001\uff0c\u4f46\u7ed9\u624b\u4e0b\u53d1\u85aa\u6c34\u8fd8\u662f\u4ece\u4e0d\u62d6\u6b20\u7684\uff0c\u4e0d\u50cf\u5965\u5580\u65af\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5965\u5580\u65af\u662f\u90a3\u79cd\u660e\u660e\u5230\u4e86\u53d1\u85aa\u65e5\uff0c\u5374\u4e0d\u7ed9\u4f60\u53d1\u85aa\u6c34\uff0c\u8fc7\u51e0\u5929\u8fd8\u4f1a\u5f88\u60ca\u8bb6\u5730\u95ee\u4f60\u2018\u4f60\u6ca1\u94b1\u4e86\uff1f\u6628\u5929\u4e0d\u662f\u4f60\u7684\u53d1\u85aa\u65e5\u5417\u2019\u7684\u8001\u677f\uff0c\u683c\u62c9\u5179\u7279\u5728\u8fd9\u65b9\u9762\u7684\u54c1\u683c\u5c31\u597d\u591a\u4e86\u3002\u6211\u4e3a\u4ed6\u5de5\u4f5c\u8fd1\u4e00\u5e74\uff0c\u539f\u672c\u4e5f\u662f\u6709\u4e9b\u79ef\u84c4\u7684\uff0c\u4f46\u6628\u5929\u5df2\u7ecf\u5dee\u4e0d\u591a\u90fd\u82b1\u5149\u4e86\u3002\u201d</p><p>\u201c\u4f60\u6628\u5929\u4e70\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u4e70\u4ec0\u4e48\uff1f\u201d\u838e\u73de\u514b\u53cd\u95ee\uff0c\u201c\u4f60\u4eec\u4f4f\u7684\u623f\u95f4\uff0c\u5403\u7684\u65e9\u9910\uff0c\u6240\u6709\u7684\u8fd9\u4e00\u5207\uff0c\u4e0d\u5168\u90fd\u662f\u6211\u4ed8\u94b1\u7684\u4e48\uff1f\u4ee5\u524d\u6211\u53ea\u8981\u8d1f\u62c5\u81ea\u5df1\u4e00\u4e2a\u4eba\u2014\u2014\u4e0d\uff0c\u4ee5\u524d\u6211\u624b\u4e0b\u7684\u90a3\u51e0\u4e2a\u6076\u9b54\u90fd\u4f1a\u62a2\u7740\u66ff\u6211\u4ed8\u8d26\uff0c\u7136\u800c\u73b0\u5728\u4ed6\u4eec\u90fd\u88ab\u4f60\u6740\u4e86\uff0c\u4e8e\u662f\u6211\u73b0\u5728\u4e0d\u4f46\u8981\u8d1f\u62c5\u81ea\u5df1\u7684\u90a3\u4e00\u4efd\uff0c\u8fd8\u5f97\u989d\u5916\u8d1f\u62c5\u4f60\u7684\uff0c\u8fd8\u6709\u4f60\u7684\u670b\u53cb\uff0c\u4e00\u4e8c\u4e09\u56db\u4e94\u516d\uff0c\u4e00\u5171\u516d\u4e2a\u4eba\uff0c\u7136\u540e\u4f60\u53c8\u5e26\u56de\u6765\u4e24\u4f4d\uff0c\u4e00\u5171\u516b\u4e2a\u2026\u2026\u201d</p><p>\u201c\u6211\u548c\u827e\u5f25\u8587\u662f\u540c\u4e00\u95f4\u623f\u3002\u201d\u743c\u6069\u63d0\u9192\u3002</p><p>\u201c\u5bf9\uff0c\u6240\u4ee5\u662f\u4e03\u4e2a\u623f\u95f4\uff0c\u4f46\u65e9\u9910\u5206\u91cf\u8fd8\u662f\u516b\u4efd\uff0c\u800c\u4e14\u4f60\u4f4f\u7684\u90a3\u95f4\u662f\u8d35\u5bbe\u623f\uff0c\u5b83\u4e00\u95f4\u7684\u82b1\u8d39\u5c31\u9876\u5f97\u4e0a\u666e\u901a\u623f\u95f4\u7684\u5341\u95f4\uff0c\u6240\u4ee5\u5176\u5b9e\u662f\u5341\u516d\u95f4\u3002\u6240\u6709\u7684\u8fd9\u4e9b\u8d39\u7528\uff0c\u5168\u90fd\u662f\u4ece\u6211\u7684\u94b1\u5305\u91cc\u638f\u51fa\u6765\u3002\u4f60\u8ba9\u4e00\u4e2a\u5973\u5b69\u5b50\u4e3a\u4f60\u548c\u4f60\u7684\u670b\u53cb\u751a\u81f3\u7b97\u4e0d\u4e0a\u670b\u53cb\u7684\u4eba\u4ed8\u94b1\uff0c\u5fc3\u5b89\u7406\u5f97\uff0c\u4e0d\u95fb\u4e0d\u95ee\uff0c\u201d\u9b45\u9b54\u6012\u6c14\u51b2\u51b2\uff0c\u201c\u6211\u4e70\u4ec0\u4e48\u4e86\uff1f\u6211\u4e70\u4e86\u4e00\u95f4\u5bcc\u4e3d\u5802\u7687\u7684\u623f\u95f4\uff0c\u4f9b\u4f60\u548c\u4f60\u7684\u5c0f\u60c5\u4eba\u4f11\u606f\uff1b\u6211\u4e70\u4e86\u4e00\u5f20\u8212\u9002\u5bbd\u5927\u7684\u5e8a\uff0c\u4f9b\u4f60\u4eec\u6574\u665a\u505a\u7231\uff1b\u6211\u4e70\u4e86\u4e24\u4efd\u7cbe\u81f4\u53ef\u53e3\u7684\u65e9\u9910\uff0c\u4f9b\u4f60\u4eec\u6062\u590d\u7cbe\u529b\u2014\u2014\u7136\u540e\u518d\u7b49\u5f85\u7740\u4f60\u6765\u95ee\u6211\uff1a\u4f60\u90fd\u4e70\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u4e0d\u7528\u8fd9\u4e48\u751f\u6c14\u5427\u2026\u2026\u201d</p><p>\u201c\u4f60\u89c9\u5f97\u6211\u4e0d\u5e94\u8be5\u4e3a\u6b64\u751f\u6c14\uff1f\u201d</p><p>\u201c\u5514\uff0c\u597d\u5427\uff0c\u6211\u7684\u610f\u601d\u662f\u8bf4\uff0c\u53cd\u6b63\u4f60\u5f88\u5feb\u4e5f\u8981\u56de\u5230\u7269\u8d28\u754c\u4e86\uff0c\u4ee5\u540e\u53c8\u4e0d\u4f1a\u6765\u6df1\u6e0a\uff0c\u8d27\u5e01\u65e2\u7136\u4e0d\u80fd\u901a\u7528\uff0c\u90a3\u4e48\u7528\u6389\u4e5f\u5c31\u7528\u6389\u597d\u4e86\u3002\u201d</p><p>\u201c\u6211\u56de\u7269\u8d28\u754c\uff1f\u4f60\u5e26\u6211\u56de\u53bb\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u8fd9\u4e0d\u662f\u90fd\u8bf4\u597d\u4e86\u5417\uff1f\u201d</p><p>\u201c\u6211\u6015\u4f60\u53cd\u6094\u3002\u201d</p><p>\u201c\u6211\u6839\u672c\u4e0d\u53ef\u80fd\u53cd\u6094\u597d\u4e0d\u597d\uff0c\u201d\u743c\u6069\u644a\u624b\uff0c\u201c\u7075\u9b42\u5951\u7ea6\u7b7e\u5728\u90a3\u91cc\u5462\u3002\u201d</p><p>\u201c\u90a3\u5982\u679c\u6ca1\u6709\u7075\u9b42\u5951\u7ea6\u5462\uff1f\u201d\u838e\u73de\u514b\u95ee\uff0c\u201c\u5982\u679c\u6ca1\u6709\u5b83\uff0c\u73b0\u5728\u6211\u95ee\u4f60\uff1a\u6211\u60f3\u56de\u7269\u8d28\u754c\uff0c\u4f60\u613f\u610f\u5e2e\u6211\u5417\uff1f\u201d</p><p>\u743c\u6069\u770b\u4e86\u5979\u4e00\u773c\uff0c\u201c\u6211\u80fd\u5e2e\uff0c\u4e00\u5b9a\u5e2e\u3002\u201d</p><p>\u201c\u771f\u7684\uff0c\u6211\u4eec\u4ee5\u524d\u4f3c\u4e4e\u8fd8\u662f\u654c\u4eba\u5427\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u201c\u4f60\u6211\u90a3\u70b9\u4ec7\u6028\uff0c\u7b97\u5f97\u4e86\u4ec0\u4e48\uff0c\u201d\u4ed6\u6709\u4e9b\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u4e0d\u63d0\uff0c\u6211\u51e0\u4e4e\u90fd\u7ed9\u5fd8\u4e86\u3002\u201d</p><p>\u201c\u610f\u601d\u662f\u8bf4\u6211\u65e0\u8db3\u8f7b\u91cd\u662f\u5417\uff1f\u201d</p><p>\u5deb\u5e08\u53f9\u6c14\uff0c\u201c\u4f60\u592a\u654f\u611f\u4e86\uff0c\u5c0f\u59d0\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4e0d\u662f\u4f60\u65e0\u8db3\u8f7b\u91cd\uff0c\u53ea\u662f\u6211\u2026\u2026\u5514\uff0c\u662f\u6211\u5bf9\u5f88\u591a\u4e8b\u60c5\u5df2\u7ecf\u6ca1\u611f\u89c9\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u611f\u89c9\uff1f\u90a3\u53ef\u592a\u7cdf\u7cd5\u4e86\uff0c\u201d\u9b45\u9b54\u8bc4\u4ef7\uff0c\u201c\u5e74\u7eaa\u8fd9\u4e48\u8f7b\u8f7b\u5c31\u6ca1\u611f\u89c9\u4e86\uff0c\u4f60\u7684\u5c0f\u60c5\u4eba\u4f1a\u5f88\u4f24\u5fc3\u7684\u3002\u201d</p><p>\u201c\u6211\u4e0d\u662f\u90a3\u4e2a\u610f\u601d\uff01\u201d</p><p>\u201c\u90a3\u5c31\u662f\u4f60\u7684\u5c0f\u60c5\u4eba\u505a\u5f97\u4e0d\u591f\u597d\uff0c\u8ba9\u4f60\u63d0\u4e0d\u8d77\u5174\u8da3\u4e86\uff1f\u201d\u9b45\u9b54\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u8d34\u4e0a\u4e86\u5deb\u5e08\u7684\u8eab\u4f53\uff0c\u80f8\u53e3\u4e24\u56e2\u636e\u79f0\u201c\u4ecb\u4e4e\u4e8e\u4e01\u7f69\u676f\u548c\u620a\u7f69\u676f\u4e4b\u95f4\u7684\u201d\u9999\u8f6f\u8089\u4f53\u9694\u7740\u8f7b\u8584\u7684\u8863\u670d\u5728\u4ed6\u80cc\u540e\u6469\u64e6\u7740\uff0c\u201c\u90a3\u4e48\u4eca\u665a\u8981\u4e0d\u8981\u6765\u8bd5\u8bd5\u6211\u7684\u6280\u672f\uff0c\u76f8\u4fe1\u4f60\u8fd8\u6ca1\u6709\u54c1\u5c1d\u8fc7\u9b45\u9b54\u7684\u7f8e\u5999\u6ecb\u5473\u5427\u2026\u2026\u6216\u8005\u52a0\u4e0a\u4f60\u7684\u5c0f\u60c5\u4eba\u4e00\u8d77\uff0c\u600e\u4e48\u6837\uff1f\u5979\u5c31\u7b97\u6bd4\u6211\u6f02\u4eae\uff0c\u4f46\u4e00\u5b9a\u6ca1\u6709\u5b66\u8fc7\u5982\u4f55\u53d6\u60a6\u7537\u4eba\u7684\u6280\u5de7\u5427\uff0c\u90a3\u771f\u662f\u592a\u53ef\u60dc\u4e86\uff0c\u4f1a\u5c11\u5f88\u591a\u5f88\u591a\u4e50\u8da3\u7684\uff0c\u8ba9\u6211\u6765\u5e2e\u4f60\u597d\u597d\u5730\u2018\u8c03\u6559\u2019\u5979\uff0c\u600e\u4e48\u6837\uff1f\u201d</p><p>\u8bda\u5982\u838e\u73de\u514b\u6240\u8bf4\uff0c\u6885\u83f2\u65af\u867d\u7136\u7edd\u8272\u7f8e\u4e3d\uff0c\u5374\u6ca1\u5b66\u8fc7\u8fd9\u65b9\u9762\u7684\u6280\u5de7\u2014\u2014\u63d0\u5c14\u7684\u6559\u4f1a\u867d\u7136\u4e0d\u8bb2\u7a76\u7981\u6b32\uff0c\u5374\u4e5f\u4e0d\u4f1a\u6559\u8fd9\u4e9b\u4e1c\u897f\uff0c\u5728\u5e8a\u4e0a\u4fbf\u4e0d\u514d\u603b\u6709\u4e9b\u8fdf\u949d\uff0c\u57fa\u672c\u90fd\u662f\u88ab\u52a8\u63a5\u53d7\u7684\u4e00\u65b9\uff0c\u6bcf\u6b21\u6b22\u597d\u723d\u5219\u723d\u77e3\uff0c\u7ec8\u7a76\u50cf\u662f\u5dee\u4e86\u4e9b\u5473\u9053\u4f3c\u7684\u3002\u7537\u4eba\u90fd\u662f\u8d2a\u5fc3\u7684\u52a8\u7269\uff0c\u65e2\u5e0c\u671b\u81ea\u5df1\u7684\u60c5\u4eba\u662f\u5723\u5973\uff0c\u53c8\u5e0c\u671b\u5979\u5728\u5e8a\u4e0a\u50cf\u8361\u5987\uff0c\u743c\u6069\u4e5f\u4e0d\u4f8b\u5916\uff0c\u542c\u5230\u838e\u73de\u514b\u8fd9\u4e48\u8bf4\uff0c\u8111\u4e2d\u4e0d\u7531\u5f97\u4fbf\u7acb\u523b\u6d6e\u73b0\u51fa\u6885\u83f2\u65af\u5728\u81ea\u5df1\u8eab\u4e0b\u5a49\u8f6c\u5a07\u541f\u4e3b\u52a8\u6c42\u6b22\u7684\u753b\u9762\u6765\uff0c\u987f\u65f6\u5fc3\u5934\u4e00\u8361\u3002</p><p>\u9b45\u9b54\u6700\u64c5\u5bdf\u8a00\u89c2\u8272\uff0c\u6295\u5176\u6240\u597d\uff0c\u5426\u5219\u53c8\u5982\u4f55\u5f15\u8bf1\u51e1\u4eba\uff0c\u8fd9\u662f\u6df1\u6e0a\u89c4\u5219\u8d4b\u4e88\u7684\u5929\u6027\u672c\u80fd\u3002\u838e\u73de\u514b\u867d\u7136\u53d8\u6210\u9b45\u9b54\u7684\u65f6\u95f4\u4e0d\u957f\uff0c\u4f46\u5979\u5728\u51e1\u4eba\u65f6\u5c31\u662f\u5e38\u4ee5\u7f8e\u8272\u5f15\u8bf1\u654c\u4eba\u7684\u9ad8\u660e\u6740\u624b\uff0c\u8fd9\u65b9\u9762\u7684\u80fd\u529b\u5e76\u4e0d\u900a\u8272\u4e8e\u4e00\u4f4d\u771f\u6b63\u7684\u9b45\u9b54\u3002\u743c\u6069\u867d\u7136\u6ca1\u8bf4\u8bdd\uff0c\u4f46\u838e\u73de\u514b\u7acb\u523b\u6e05\u695a\u4ed6\u5df2\u7ecf\u610f\u52a8\uff0c\u201c\u600e\u4e48\u6837\uff1f\u201d\u5979\u8d81\u70ed\u6253\u94c1\uff0c\u201c\u5728\u8fd9\u65b9\u9762\u6211\u53ef\u662f\u4e13\u4e1a\u4eba\u58eb\uff0c\u4f60\u4e0d\u53ef\u80fd\u627e\u5230\u6bd4\u6211\u66f4\u4f18\u79c0\u7684\u8c03\u6559\u5e08\u4e86\u3002\u201d</p><p>\u743c\u6069\u77e5\u9053\u5979\u8fd9\u672a\u514d\u662f\u5728\u81ea\u5439\u81ea\u64c2\uff0c\u6df1\u6e0a\u4e2d\u9b45\u9b54\u65e0\u6570\uff0c\u6bd4\u5979\u66f4\u9ad8\u660e\u7684\u591a\u4e86\u53bb\u4e86\uff0c\u53ea\u662f\u65e2\u7136\u773c\u524d\u8fd9\u4e2a\u5c31\u6446\u5728\u8fd9\u91cc\uff0c\u90a3\u53c8\u4f55\u5fc5\u820d\u8fd1\u6c42\u8fdc\uff0c\u53e6\u627e\u4ed6\u4eba\u3002\u201c\u4f60\u51c6\u5907\u600e\u4e48\u505a\uff1f\u201d\u4ed6\u8bd5\u63a2\u5730\u95ee\uff0c\u201c\u8bf4\u6765\u542c\u542c\u3002\u201d</p><p>\u201c\u653e\u5fc3\uff0c\u201d\u838e\u73de\u514b\u770b\u51fa\u4ed6\u7684\u5fc3\u601d\uff0c\u5a07\u7b11\u8d77\u6765\uff0c\u5634\u89d2\u7fd8\u8d77\uff0c\u52fe\u52d2\u51fa\u7565\u5e26\u81ea\u50b2\u7684\u5f27\u7ebf\uff0c\u201c\u4fdd\u8bc1\u4e0d\u4f1a\u6709\u4e00\u4e1d\u4e00\u6beb\u5f04\u4f24\u4f60\u7684\u5c0f\u60c5\u4eba\u7684\uff0c\u201d\u5979\u5145\u6ee1\u81ea\u4fe1\u5730\u8bf4\uff0c\u201c\u5982\u679c\u4f24\u5230\u4e86\u8c03\u6559\u5bf9\u8c61\uff0c\u90a3\u8fd8\u7b97\u4ec0\u4e48\u6280\u672f\uff0c\u76f4\u63a5\u53bb\u5927\u8857\u4e0a\u6293\u51e0\u4e2a\u6076\u9b54\u90fd\u80fd\u5e72\u4e86\u3002\u6240\u8c13\u4e13\u4e1a\uff0c\u81ea\u7136\u5c31\u8981\u6709\u4e13\u4e1a\u7684\u98ce\u8303\u548c\u6837\u5b50\u3002\u201d</p><p>\u201c\u5177\u4f53\u7684\u624b\u6cd5\u5462\u3002\u201d</p><p>\u201c\u8fd9\u81ea\u7136\u8981\u56e0\u4eba\u800c\u5f02\uff0c\u56e0\u65f6\u5236\u5b9c\uff0c\u968f\u673a\u5e94\u53d8\uff0c\u4e0d\u80fd\u4e00\u6982\u800c\u8bba\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u5982\u679c\u8981\u8bf4\u5927\u81f4\u65b9\u5411\u7684\u8bdd\uff0c\u5979\u662f\u4e2a\u5723\u6b66\u58eb\uff0c\u8fd9\u79cd\u4eba\u6211\u5728\u683c\u62c9\u5179\u7279\u7684\u540e\u5bab\u91cc\u4e5f\u89c1\u8fc7\u51e0\u4e2a\u3002\u5723\u6b66\u58eb\u5168\u90fd\u5c5e\u4e8e\u90a3\u79cd\u521a\u6bc5\u679c\u51b3\u3001\u6781\u5ea6\u81ea\u4fe1\u7684\u7c7b\u578b\uff0c\u5bf9\u4ed8\u5979\u4eec\u666e\u901a\u7684\u65b9\u5f0f\u662f\u884c\u4e0d\u901a\u7684\uff0c\u5355\u7eaf\u8089\u4f53\u4e0a\u7684\u8c03\u6559\u4e5f\u6839\u672c\u4e0d\u591f\uff0c\u5fc5\u987b\u4ece\u7cbe\u795e\u4e0a\u4e0b\u624b\uff0c\u7f9e\u8fb1\u8df5\u8e0f\u5979\u4eec\u7684\u5c0a\u4e25\uff0c\u98a0\u8986\u6bc1\u706d\u5979\u4eec\u7684\u4fe1\u4ef0\uff0c\u4e00\u70b9\u70b9\u5730\u53cd\u590d\u3001\u5f15\u8bf1\u3001\u6d88\u78e8\uff0c\u8ba9\u5979\u4eec\u5fc3\u4e2d\u6700\u540e\u4ec5\u5b58\u7684\u5e0c\u671b\u4e4b\u706b\u5b8c\u5168\u7184\u706d\uff0c\u6700\u7ec8\u5f7b\u5e95\u5d29\u6e83\u3001\u6c89\u6ca6\uff0c\u5760\u5165\u9ed1\u6697\u7684\u6df1\u6e0a\u2026\u2026\u201d</p><p>\u743c\u6069\u7b11\u7740\u6447\u4e86\u6447\u5934\u3002</p><p>\u9b45\u9b54\u8fd9\u6b21\u5374\u8bef\u4f1a\u4e86\u4ed6\u7684\u610f\u601d\uff0c\u201c\u5fc3\u75bc\u4e86\uff1f\u201d\u5979\u683c\u683c\u5a07\u7b11\u8d77\u6765\uff0c\u201c\u522b\u62c5\u5fc3\uff0c\u5979\u4e0d\u4f1a\u6709\u4e8b\uff0c\u6b63\u662f\u8981\u8fd9\u6837\u7ecf\u8fc7\u6323\u624e\u3001\u5815\u843d\u3001\u7edd\u671b\u3001\u6c89\u6ca6\uff0c\u5728\u9ed1\u6697\u4e2d\u91cd\u751f\u7efd\u653e\u7684\u82b1\u6735\uff0c\u624d\u662f\u6700\u8273\u4e3d\u6700\u751c\u871c\u7684\uff0c\u4fdd\u8bc1\u4f1a\u8ba9\u4f60\u56de\u5473\u65e0\u7a77\uff0c\u6c38\u751f\u96be\u5fd8\u3002\u201d</p><p>\u201c\u6211\u7684\u610f\u601d\u662f\u8bf4\uff0c\u4f60\u6839\u672c\u5c31\u529e\u4e0d\u5230\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u827e\u5f25\u8587\u4e0d\u662f\u5e73\u5e38\u7684\u5723\u6b66\u58eb\uff0c\u5979\u662f\u7279\u522b\u7684\u3002\u201d</p><p>\u201c\u662f\u5417\u3002\u201d</p><p>\u201c\u6211\u4e86\u89e3\u5979\uff0c\u56e0\u4e3a\u6211\u8bf4\u8fc7\uff0c\u6211\u4eec\u672c\u8d28\u4e0a\u662f\u4e00\u7c7b\u4eba\uff0c\u201d\u743c\u6069\u7b11\u7740\uff0c\u201c\u5979\u6709\u8db3\u591f\u7684\u806a\u660e\uff0c\u4e5f\u6709\u8db3\u591f\u7684\u7406\u667a\uff0c\u5728\u4efb\u4f55\u65f6\u5019\u90fd\u80fd\u9547\u5b9a\u81ea\u82e5\uff0c\u5ba0\u8fb1\u4e0d\u60ca\u3002\u5979\u4f1a\u542c\u53d6\u4ed6\u4eba\u7684\u610f\u89c1\uff0c\u4f1a\u91c7\u7eb3\u4e0d\u540c\u7684\u89c2\u70b9\uff0c\u4f1a\u4fee\u6b63\u81ea\u5df1\u7684\u770b\u6cd5\uff0c\u4f1a\u6539\u53d8\u539f\u672c\u7684\u6001\u5ea6\uff0c\u4f46\u5c31\u672c\u8d28\u800c\u8a00\uff0c\u5979\u662f\u6781\u5ea6\u7684\u9a84\u50b2\u81ea\u8d1f\uff0c\u76ee\u7a7a\u4e00\u5207\u3002\u5979\u4e0d\u5728\u4e4e\u540d\u58f0\uff0c\u4e0d\u754f\u60e7\u6b7b\u4ea1\uff0c\u4e0d\u62c5\u5fe7\u5931\u8d25\uff0c\u4e0d\u7948\u6c42\u56de\u62a5\uff0c\u4e5f\u4e0d\u5bf9\u4ed6\u4eba\u62b1\u6709\u4efb\u4f55\u5e0c\u671b\u2014\u2014\u7eb5\u7136\u4f60\u6709\u5343\u822c\u672c\u4e8b\uff0c\u53c8\u80fd\u5948\u6211\u4f55\uff1f\u201d</p><p>\u201c\u4e0d\u81f3\u4e8e\u5427\uff0c\u201d\u838e\u73de\u514b\u7b11\u7740\uff0c\u201c\u4f60\u8bf4\u5f97\u672a\u514d\u592a\u8fc7\u5938\u5f20\u2026\u2026\u201d</p><p>\u201c\u5938\u5f20\uff1f\u4e00\u70b9\u4e5f\u4e0d\uff0c\u201d\u743c\u6069\u8fde\u8fde\u6447\u5934\uff0c\u201c\u4e3e\u4e2a\u7b80\u5355\u7684\u4f8b\u5b50\uff0c\u5979\u548c\u4f60\u4e00\u6837\u662f\u5df4\u5c14\u7684\u5b50\u55e3\uff0c\u662f\u4e3a\u4e86\u90aa\u795e\u590d\u6d3b\u800c\u751f\u7684\u4eba\uff0c\u4f46\u90a3\u53c8\u5982\u4f55\u5462\uff1f\u5979\u65e9\u5c31\u62e5\u6709\u4e86\u8db3\u591f\u7684\u6740\u622e\u795e\u529b\uff0c\u4f46\u4e3a\u4ec0\u4e48\u5230\u73b0\u5728\u4e3a\u6b62\u8fd8\u6ca1\u6709\u6210\u4e3a\u795e\u7947\uff1f\u4f60\u6050\u6015\u4f1a\u89c9\u5f97\u4e0d\u53ef\u601d\u8bae\u5427\uff0c\u795e\u660e\u7684\u5e2d\u4f4d\u548c\u8363\u8000\u5c31\u6446\u5728\u5979\u9762\u524d\uff0c\u53ea\u8981\u613f\u610f\uff0c\u4fbf\u662f\u553e\u624b\u53ef\u5f97\uff0c\u6613\u5982\u53cd\u638c\u2014\u2014\u4f46\u5979\u5c31\u662f\u653e\u5f03\u4e86\uff0c\u5b81\u613f\u53bb\u505a\u5723\u6b66\u58eb\u3002\u4f60\u8981\u95ee\u4ec0\u4e48\u9053\u7406\uff1f\u6ca1\u6709\u9053\u7406\uff0c\u4efb\u4f60\u8bf4\u5f97\u5929\u82b1\u4e71\u5760\uff0c\u4efb\u4f60\u5c01\u795e\u6709\u5343\u822c\u597d\u5904\uff0c\u5c31\u4e00\u53e5\u8bdd\uff1a\u6211\u4e0d\u5e72\u3002\u5bf9\u4e8e\u8fd9\u79cd\u4eba\uff0c\u4f60\u90a3\u4e9b\u65b9\u6cd5\u53c8\u6709\u4ec0\u4e48\u7528\u5462\uff1f\u201d</p><p>\u201c\u5018\u82e5\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u4f60\u53c8\u662f\u600e\u4e48\u5f97\u5230\u5979\u7684\u5462\u3002\u201d</p><p>\u201c\u5f88\u7b80\u5355\uff0c\u56e0\u4e3a\u5979\u662f\u5723\u6b66\u58eb\uff0c\u4e0d\u662f\u75af\u5b50\u3002\u867d\u7136\u8fd9\u4e24\u8005\u770b\u8d77\u6765\u90fd\u5dee\u4e0d\u591a\uff0c\u4f46\u5723\u6b66\u58eb\u6709\u5979\u6240\u575a\u6301\u7684\u51c6\u5219\u3002\u516c\u5e73\u3001\u5951\u7ea6\u548c\u6700\u4f4e\u9650\u5ea6\u7684\u5584\u826f\u2014\u2014\u6216\u8005\u8bf4\u4e0d\u90aa\u6076\uff0c\u8fd9\u5c31\u662f\u5979\u79c9\u6301\u7684\u51c6\u5219\u3002\u6211\u548c\u5979\u5728\u672c\u8d28\u4e0a\u662f\u540c\u4e00\u7c7b\u4eba\uff0c\u6240\u4ee5\u6211\u80fd\u4e00\u773c\u770b\u7a7f\uff0c\u800c\u5979\u7684\u8fd0\u6c14\u4e0d\u597d\uff0c\u6211\u7684\u8fd0\u6c14\u597d\uff0c\u6240\u4ee5\u5979\u5c31\u662f\u6211\u7684\u4e86\u3002\u201d</p><p>\u838e\u73de\u514b\u6c89\u541f\u7740\uff0c\u201c\u5979\u6709\u8db3\u591f\u7684\u81ea\u4fe1\u548c\u9a84\u50b2\uff0c\u90a3\u662f\u56e0\u4e3a\u5979\u592a\u5f3a\u5927\uff0c\u592a\u987a\u5229\uff0c\u4ece\u672a\u53d7\u5230\u8fc7\u771f\u6b63\u7684\u632b\u6298\u2026\u2026\u201d</p><p>\u201c\u7b97\u4e86\u5427\uff0c\u201d\u743c\u6069\u7b11\u8d77\u6765\uff0c\u201c\u6211\u544a\u8bc9\u4f60\u5979\u5df2\u7ecf\u7ecf\u5386\u8fc7\u4ec0\u4e48\uff1f\u5979\u662f\u5df4\u5c14\u7684\u5b50\u55e3\uff0c\u8eab\u8d1f\u90aa\u795e\u7684\u8840\u8109\uff0c\u5373\u4fbf\u5728\u6559\u4f1a\u5185\u90e8\u90fd\u88ab\u5b64\u7acb\uff1b\u5979\u88ab\u6211\u4fae\u8fb1\uff0c\u88ab\u6559\u4f1a\u5f00\u9664\u6559\u7c4d\uff0c\u62a4\u9001\u4efb\u52a1\u5931\u8d25\uff0c\u9ece\u660e\u4e4b\u77f3\u8fd9\u79cd\u5723\u7269\u88ab\u6bc1\u635f\u2014\u2014\u90a3\u53c8\u600e\u4e48\u6837\u5462\uff1f\u5979\u4f9d\u7136\u8fd8\u662f\u90a3\u4e2a\u5723\u6b66\u58eb\uff0c\u5979\u4f9d\u65e7\u8fd8\u662f\u90a3\u4e2a\u827e\u5f25\u8587\xb7\u6885\u83f2\u65af\uff0c\u4f60\u53c8\u8fd8\u80fd\u8ba9\u5979\u53d7\u4ec0\u4e48\u632b\u6298\uff1f\u6216\u8005\u66f4\u76f4\u63a5\u5730\u8bf4\uff0c\u5979\u5c31\u8fd9\u6837\u4e00\u4e2a\u4eba\uff1a\u597d\u6076\u7531\u4e4b\uff0c\u6bc1\u8a89\u542c\u4e4b\uff0c\u5f97\u5931\u4efb\u4e4b\uff0c\u6210\u8d25\u968f\u4e4b\uff0c\u6267\u7740\u672c\u5fc3\uff0c\u4e00\u5f80\u65e0\u524d\uff0c\u4efb\u4f60\u5982\u4f55\uff0c\u6211\u4e0d\u5e0c\u5180\uff0c\u4e0d\u5962\u6c42\uff0c\u4e0d\u754f\u60e7\uff0c\u4e0d\u6307\u671b\uff0c\u4e0d\u540e\u6094\uff0c\u4e0d\u5728\u4e4e\u2014\u2014\u4f60\u8fd8\u80fd\u628a\u6211\u600e\u4e48\u6837\uff1f\u201d</p><p>\u838e\u73de\u514b\u6c89\u601d\u534a\u664c\uff0c\u7a81\u7136\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u786e\u5b9e\u662f\u4e0d\u80fd\u628a\u5979\u600e\u4e48\u6837\uff0c\u4f46\u4f60\u80fd\uff0c\u4e3b\u4eba\uff0c\u4f60\u80fd\u3002\u201d</p><p>\u201c\u6211\u80fd\uff1f\u201d</p><p>\u201c\u4f60\u80fd\uff0c\u56e0\u4e3a\u5979\u559c\u6b22\u4f60\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u5c31\u50cf\u4f60\u521a\u624d\u8bf4\u7684\uff0c\u4f60\u4eec\u672c\u8d28\u4e0a\u662f\u4e00\u7c7b\u4eba\uff0c\u4e5f\u53ea\u6709\u4f60\u624d\u80fd\u660e\u767d\u5979\uff0c\u7406\u89e3\u5979\uff0c\u9002\u5408\u5979\u3002\u6bcf\u4e2a\u4eba\u90fd\u662f\u5bb3\u6015\u5b64\u72ec\u7684\uff0c\u6bcf\u4e2a\u4eba\u90fd\u662f\u9700\u8981\u7406\u89e3\u7684\uff0c\u518d\u575a\u6bc5\u679c\u51b3\u7684\u4eba\u4e5f\u4e0d\u4f8b\u5916\uff0c\u5979\u4e5f\u4e0d\u4f1a\u4f8b\u5916\u3002\u4f60\u8bf4\u5979\u6781\u5ea6\u81ea\u8d1f\uff0c\u81ea\u8d1f\u5c31\u610f\u5473\u7740\u81ea\u604b\uff0c\u6bcf\u4e2a\u4eba\u5728\u672c\u8d28\u4e0a\u90fd\u662f\u81ea\u604b\u7684\uff0c\u6240\u4ee5\u5979\u4f1a\u559c\u6b22\u4e0a\u4f60\u2014\u2014\u522b\u90a3\u4e48\u5f97\u610f\uff0c\u6211\u7684\u4e3b\u4eba\uff0c\u5979\u53ea\u662f\u559c\u6b22\u81ea\u5df1\uff0c\u800c\u4f60\u548c\u5979\u6700\u50cf\u800c\u5df2\u3002\u201d</p><p>\u201c\u6211\u4e0d\u5426\u8ba4\u8fd9\u70b9\uff0c\u4f46\u90a3\u53c8\u5982\u4f55\uff1f\u201d</p><p>\u201c\u6240\u4ee5\u4f60\u5c31\u80fd\u591f\u4f24\u5bb3\u5979\u554a\u3002\u5c31\u50cf\u4f60\u6240\u8bf4\uff0c\u5979\u4e0d\u5e0c\u5180\u3001\u4e0d\u5962\u6c42\u3001\u4e0d\u754f\u60e7\u3001\u4e0d\u6307\u671b\u3001\u4e0d\u540e\u6094\u3001\u4e0d\u5728\u4e4e\uff0c\u6ca1\u6709\u8c01\u80fd\u771f\u6b63\u6253\u5012\u5979\uff0c\u53ea\u6709\u81ea\u5df1\u2014\u2014\u800c\u5bf9\u4e8e\u5979\u800c\u8a00\uff0c\u4f60\u5c31\u662f\u5979\u81ea\u5df1\uff0c\u662f\u5979\u7684\u6298\u5c04\uff0c\u6216\u8005\u8bf4\u662f\u5979\u5728\u955c\u5b50\u91cc\u7684\u6295\u5f71\u3002\u201d</p><p>\u201c\u5979\u628a\u4f60\u89c6\u4e3a\u5979\u81ea\u5df1\u7684\u6298\u5c04\u548c\u6295\u5f71\uff0c\u90a3\u4e48\u5979\u5c31\u4f1a\u671f\u671b\u4f60\u53d8\u5f97\u548c\u5979\u5fc3\u76ee\u4e2d\u4e00\u6837\u5b8c\u7f8e\uff0c\u56e0\u4e3a\u90a3\u5c31\u610f\u5473\u7740\u81ea\u5df1\u7684\u5b8c\u7f8e\uff0c\u201d\u838e\u73de\u514b\u53c8\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u5931\u8d25\uff0c\u5979\u6216\u8bb8\u5c31\u4f1a\u611f\u89c9\u5230\u6cae\u4e27\uff1b\u5982\u679c\u4f60\u504f\u79bb\uff0c\u5979\u6216\u8bb8\u5c31\u4f1a\u611f\u89c9\u5230\u5931\u671b\uff1b\u5982\u679c\u4f60\u4f24\u5bb3\u5979\uff0c\u90a3\u4e48\u5979\u5c31\u4f1a\u771f\u6b63\u53d7\u4f24\u5bb3\u3002\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u201c\u6211\u4e0d\u77e5\u9053\u539f\u6765\u4f60\u8fd8\u5bf9\u54f2\u5b66\u8fd9\u4e48\u6709\u5174\u8da3\u3002\u201d</p><p>\u201c\u6211\u5bf9\u54f2\u5b66\u6ca1\u5174\u8da3\uff0c\u6211\u53ea\u5bf9\u4eba\u7684\u5fc3\u7075\u6709\u5174\u8da3\uff0c\u201d\u9b45\u9b54\u8138\u4e0a\u9732\u51fa\u80dc\u5229\u7684\u7b11\u5bb9\uff0c\u201c\u600e\u4e48\u6837\uff0c\u4e3b\u4eba\uff0c\u8981\u4e0d\u8981\u6765\u8bd5\u8bd5\u770b\uff0c\u6765\u9a8c\u8bc1\u4e00\u4e0b\u6211\u8bf4\u7684\u8bdd\u3002\u8ba9\u6211\u4eec\u5408\u4f5c\uff0c\u6765\u770b\u770b\u4f60\u7684\u5c0f\u60c5\u4eba\u662f\u4e0d\u662f\u771f\u7684\u65e0\u61c8\u53ef\u51fb\u3002\u201d</p><p>\u201c\u8c01\u65e0\u61c8\u53ef\u51fb\uff1f\u201d</p><p>\u7a81\u7136\u4e4b\u95f4\uff0c\u91d1\u53d1\u5c11\u5973\u7684\u58f0\u97f3\u5728\u80cc\u540e\u8f7b\u8f7b\u54cd\u8d77\u3002</p></article>"}}}]);
