(window.webpackJsonp=window.webpackJsonp||[]).push([[1528],{1551:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u516d\u8282 \u8001\u8005",content:"<article><h2>\u7b2c\u4e09\u5341\u516d\u8282 \u8001\u8005</h2><p>\u743c\u6069\u95fb\u58f0\u56de\u8fc7\u5934\u6765\uff0c\u53d1\u73b0\u6885\u83f2\u65af\u6b63\u7ad9\u5728\u5546\u5e97\u7684\u95e8\u53e3\uff0c\u4f3c\u7b11\u975e\u7b11\u5730\u770b\u7740\u4ed6\u3002\u660e\u4eae\u7684\u9633\u5149\u4ece\u5979\u8eab\u540e\u7167\u8fdb\u6765\uff0c\u6d12\u5728\u6de1\u91d1\u8272\u957f\u53d1\u548c\u84dd\u8272\u886c\u8863\u4e0a\uff0c\u95ea\u95ea\u751f\u8f89\uff0c\u6620\u5f97\u8138\u8272\u82cd\u767d\u4e2d\u900f\u51fa\u51e0\u5206\u7ea2\u6655\u3002\u5979\u8eab\u6750\u6781\u9ad8\uff0c\u7a7f\u7684\u53c8\u662f\u886c\u886b\u957f\u88e4\u8fd9\u79cd\u504f\u7537\u6027\u670d\u9970\uff0c\u7eb5\u7136\u73b0\u5728\u865a\u5f31\uff0c\u53ea\u9759\u9759\u7ad9\u5728\u90a3\u91cc\uff0c\u4fbf\u81ea\u6709\u4e00\u79cd\u51db\u51db\u82f1\u6c14\uff0c\u4eff\u4f5b\u8fd8\u662f\u90a3\u4e2a\u5728\u6218\u573a\u4e0a\u624b\u63d0\u94f6\u5251\u7765\u7768\u5343\u519b\u7684\u5723\u6b66\u58eb\u4e00\u822c\uff0c\u78a7\u7eff\u7684\u773c\u7738\u4e2d\u5149\u534e\u707f\u70c2\uff0c\u4eae\u82e5\u6668\u661f\uff0c\u8ba9\u4eba\u4e0d\u6562\u6b63\u89c6\u3002</p><p>\u4e16\u754c\u4e0a\u7684\u96be\u582a\u4e8b\u60c5\uff0c\u83ab\u8fc7\u4e8e\u6b63\u5728\u80cc\u540e\u8c08\u8bba\u5982\u4f55\u5982\u4f55\u5bf9\u4ed8\u4eba\uff0c\u7ed3\u679c\u88ab\u5f53\u573a\u649e\u7834\u7684\u3002\u5c24\u5176\u50cf\u743c\u6069\u73b0\u5728\u8fd9\u6837\uff0c\u5dee\u4e0d\u591a\u7c7b\u4f3c\u4e8e\u548c\u5916\u9762\u7684\u60c5\u4eba\u5bc6\u8c0b\u5982\u4f55\u6697\u7b97\u6b63\u5ba4\uff0c\u7ed3\u679c\u56de\u5934\u4e00\u770b\u5bf9\u65b9\u5c31\u5728\u8eab\u540e\uff0c\u7eb5\u7136\u4ed6\u5411\u6765\u81ea\u8be9\u9547\u5b9a\uff0c\u6b64\u65f6\u4e5f\u4e0d\u7981\u5fc3\u5934\u4e00\u614c\u3002\u603b\u7b97\u548c\u6885\u83f2\u65af\u76f8\u5904\u65e5\u4e45\uff0c\u719f\u6089\u6027\u60c5\uff0c\u89c1\u5979\u8138\u4e0a\u795e\u60c5\u867d\u7136\u7565\u6709\u4e0d\u5feb\uff0c\u5374\u4e5f\u5e76\u4e0d\u5982\u4f55\u6120\u6012\uff0c\u4e5f\u4fbf\u677e\u4e86\u53e3\u6c14\u3002</p><p>\u743c\u6069\u548c\u838e\u73de\u514b\u5728\u8fd9\u95f4\u5546\u5e97\u91cc\u95f2\u8c08\uff0c\u8001\u677f\u662f\u4e00\u76f4\u6ca1\u770b\u89c1\uff0c\u4e0d\u77e5\u9053\u8eb2\u5728\u54ea\u91cc\u547c\u547c\u5927\u7761\uff0c\u6885\u83f2\u65af\u539f\u672c\u662f\u548c\u51db\u3001\u4f0a\u8389\u96c5\u4e00\u8d77\u53bb\u65c1\u8fb9\u7684\u5546\u5e97\u91cc\u770b\u9970\u54c1\uff0c\u4e0d\u77e5\u4f55\u65f6\u56de\u6765\u4e86\u3002\u743c\u6069\u662f\u80cc\u5bf9\u7740\u95e8\u53e3\u7684\uff0c\u56e0\u4e3a\u89d2\u5ea6\u7684\u5173\u7cfb\uff0c\u4ed6\u6ca1\u770b\u89c1\u6885\u83f2\u65af\u8d70\u8fdb\u6765\uff0c\u800c\u4e14\u65ad\u57df\u9547\u91cc\u6076\u9b54\u73b0\u5728\u8d85\u7ea7\u591a\uff0c\u6bd4\u8f83\u5435\u95f9\uff0c\u4ed6\u6ca1\u542c\u89c1\u4ec0\u4e48\u52a8\u9759\u4e5f\u5f88\u6b63\u5e38\uff0c\u4f46\u838e\u73de\u514b\u662f\u7ad9\u5728\u743c\u6069\u4fa7\u9762\uff0c\u5979\u7406\u5f53\u662f\u80fd\u770b\u5230\u6885\u83f2\u65af\u8d70\u8fdb\u6765\u7684\uff0c\u4f46\u5979\u5374\u4e1d\u6beb\u6ca1\u63d0\u9192\u3002</p><p>\u5deb\u5e08\u770b\u4e86\u9b45\u9b54\u4e00\u773c\uff0c\u89c1\u5979\u8138\u4e0a\u8f7b\u8f7b\u8be1\u7b11\uff0c\u4fbf\u77e5\u9053\u5979\u5206\u660e\u5c31\u662f\u6545\u610f\u7684\uff0c\u53ea\u662f\u4e5f\u4e0d\u597d\u53d1\u4f5c\u3002\u201c\u51db\u548c\u4f0a\u8389\u96c5\u5462\uff0c\u201d\u743c\u6069\u8d70\u5230\u95e8\u8fb9\uff0c\u6276\u4f4f\u6885\u83f2\u65af\uff0c\u201c\u5979\u4eec\u600e\u4e48\u6ca1\u548c\u4f60\u5728\u4e00\u8d77\u3002\u201d</p><p>\u201c\u8fd8\u5728\u90a3\u8fb9\u6311\u9009\u5462\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u51db\u5012\u662f\u770b\u4e2d\u4e86\u51e0\u4ef6\uff0c\u4e0d\u8fc7\u8fd9\u91cc\u53ea\u6536\u7075\u9b42\u5b9d\u77f3\uff0c\u4e0d\u6536\u91d1\u5e01\uff0c\u6839\u672c\u6ca1\u6cd5\u4e70\u3002\u201d</p><p>\u743c\u6069\u76b1\u7740\u7709\uff0c\u8f6c\u8fc7\u5934\u6765\uff0c\u201c\u838e\u73de\u514b\uff0c\u201d\u4ed6\u95ee\uff0c\u201c\u96be\u9053\u5c31\u4ece\u6765\u6ca1\u6709\u7269\u8d28\u754c\u7684\u51e1\u4eba\u6765\u65ad\u57df\u9547\u5417\uff1f\u65e2\u7136\u8fd9\u91cc\u662f\u4e07\u6e0a\u5e73\u539f\u4e2d\u6700\u7a33\u5b9a\u7684\u5730\u65b9\uff0c\u90a3\u603b\u8be5\u4f1a\u6709\u4e00\u4e9b\u4f4d\u9762\u65c5\u884c\u8005\u6765\u5f80\u5427\u3002\u201d</p><p>\u201c\u4e0d\u591a\uff0c\u4f46\u786e\u5b9e\u662f\u6709\u7684\u3002\u201d</p><p>\u201c\u90a3\u7269\u8d28\u754c\u7684\u8d27\u5e01\u5728\u8fd9\u91cc\u5c31\u5168\u65e0\u7528\u5904\uff1f\u90a3\u4e9b\u51e1\u4eba\u600e\u4e48\u529e\uff0c\u96be\u9053\u5c31\u6ca1\u6709\u7269\u8d28\u754c\u7684\u8d27\u5e01\u548c\u7075\u9b42\u5b9d\u77f3\u7684\u5151\u6362\u4e1a\u52a1\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u6709\u8fd9\u79cd\u4e1a\u52a1\uff0c\u201d\u838e\u73de\u514b\u8bd5\u56fe\u7b80\u6d01\u5730\u56de\u7b54\u95ee\u9898\uff0c\u4f46\u743c\u6069\u770b\u7740\u5979\uff0c\u4e8e\u662f\u5979\u4e0d\u5f97\u4e0d\u53c8\u7ee7\u7eed\u8865\u5145\uff0c\u201c\u5982\u679c\u4f60\u60f3\u83b7\u5f97\u7075\u9b42\u5b9d\u77f3\uff0c\u6216\u8005\u662f\u627e\u4e00\u4efd\u5de5\u4f5c\uff0c\u6216\u8005\u53bb\u5e02\u573a\u51fa\u552e\u4f60\u7684\u8d27\u7269\uff0c\u6709\u4e9b\u5546\u4eba\u4f1a\u6536\u8d2d\u6765\u81ea\u7269\u8d28\u754c\u7684\u9b54\u6cd5\u7269\u54c1\u6216\u8005\u5176\u4ed6\u7f55\u89c1\u73a9\u610f\uff0c\u5f53\u7136\u4f60\u4e5f\u53ef\u4ee5\u8db4\u5728\u5730\u4e0a\u4e5e\u8ba8\uff0c\u5982\u679c\u4f60\u89c9\u5f97\u6076\u9b54\u4eec\u4e5f\u4f1a\u5927\u53d1\u5584\u5fc3\u7684\u8bdd\u2026\u2026\u53e6\u5916\uff0c\u6211\u4eec\u62a5\u540d\u7684\u51b3\u6597\u5927\u8d5b\uff0c\u6bcf\u8f6e\u6bd4\u8d5b\u5b8c\u6bd5\uff0c\u80dc\u5229\u8005\u90fd\u4f1a\u53d1\u653e\u4e00\u7b14\u5956\u91d1\u3002\u201d</p><p>\u627e\u5de5\u4f5c\u81ea\u7136\u662f\u6765\u4e0d\u53ca\uff0c\u743c\u6069\u7b49\u4eba\u6765\u5f97\u5306\u5fd9\uff0c\u8eab\u4e0a\u4e5f\u6ca1\u643a\u5e26\u4ec0\u4e48\u53ef\u4ee5\u51fa\u552e\u7684\u4e1c\u897f\uff0c\u81f3\u4e8e\u4e5e\u8ba8\u66f4\u662f\u80e1\u626f\uff0c\u51b3\u6597\u5927\u8d5b\u7684\u5956\u91d1\u5012\u662f\u53ef\u4ee5\u671f\u5f85\uff0c\u53cd\u6b63\u4e5f\u5c31\u662f\u660e\u5929\u7684\u4e8b\u60c5\u4e86\u2026\u2026</p><p>\u201c\u90a3\u5c31\u660e\u5929\u518d\u8bf4\u5427\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u53cd\u6b63\u5979\u4eec\u4e5f\u4e0d\u6025\u4e8e\u8fd9\u4e00\u65f6\u3002\u201d</p><p>\u5979\u77a5\u4e86\u838e\u73de\u514b\u4e00\u773c\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u9b45\u9b54\u8f7b\u58f0\u7b11\u7740\uff0c\u5fae\u5fae\u6b20\u8eab\u65bd\u793c\uff0c\u8d70\u5230\u8d27\u67b6\u7684\u5185\u4fa7\u53bb\u4e86\uff0c\u7ee7\u7eed\u5bfb\u627e\u5979\u76ee\u524d\u7684\u7ecf\u6d4e\u72b6\u51b5\u80fd\u591f\u627f\u62c5\u7684\u5546\u54c1\u3002\u201c\u6211\u60f3\u5148\u56de\u53bb\u4f11\u606f\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6709\u70b9\u7d2f\u4e86\u3002\u201d</p><p>\u743c\u6069\u6276\u7740\u5979\uff0c\u5148\u5230\u65c1\u8fb9\u7684\u5546\u5e97\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u51db\u8fd8\u5728\u5fd9\u7740\u6311\u9009\uff0c\u5979\u770b\u4e2d\u4e86\u51e0\u679a\u8033\u5760\uff0c\u4f46\u56e0\u4e3a\u6ca1\u6709\u8fd9\u91cc\u901a\u7528\u7684\u8d27\u5e01\uff0c\u53ea\u80fd\u5e72\u770b\u7740\uff0c\u604b\u604b\u4e0d\u820d\uff0c\u88ab\u6885\u83f2\u65af\u53eb\u4e86\u56de\u6765\u3002\u4e09\u4eba\u8d70\u51fa\u5e97\u95e8\u5341\u51e0\u6b65\uff0c\u6885\u83f2\u65af\u7a81\u7136\u60f3\u8d77\u4f0a\u8389\u96c5\u600e\u4e48\u6ca1\u770b\u89c1\uff0c\u6b63\u8981\u8be2\u95ee\uff0c\u5c0f\u841d\u8389\u4ece\u5e97\u91cc\u8dd1\u4e86\u51fa\u6765\uff0c\u201c\u59d0\u59d0\u59d0\u59d0\uff0c\u201d\u5979\u8e66\u8e66\u8df3\u8df3\u5730\u8dd1\u5230\u9762\u524d\uff0c\u201c\u4f60\u4eec\u56de\u53bb\u4e86\u554a\u3002\u201d</p><p>\u201c\u6211\u6709\u70b9\u7d2f\uff0c\u56de\u53bb\u4f11\u606f\uff0c\u201d\u6885\u83f2\u65af\u5fae\u7b11\u7740\u6478\u6478\u5979\u7684\u5934\uff0c\u201c\u8981\u4e48\u8ba9\u51db\u966a\u4f60\u518d\u7ee7\u7eed\u901b\u901b\uff1f\u201d</p><p>\u201c\u6211\u4e5f\u56de\u53bb\u5427\uff0c\u201d\u51db\u53f9\u6c14\uff0c\u201c\u770b\u5230\u4e00\u5806\u81ea\u5df1\u559c\u6b22\u7684\u6446\u5728\u9762\u524d\uff0c\u5374\u53c8\u4e0d\u80fd\u4e70\uff0c\u90a3\u611f\u89c9\u5b9e\u5728\u4e0d\u597d\uff0c\u6211\u8fd8\u662f\u4e0d\u7ed9\u81ea\u5df1\u627e\u7f6a\u53d7\u4e86\u3002\u201d</p><p>\u201c\u55ef\uff0c\u90a3\u6211\u4e5f\u4e0d\u53bb\u4e86\uff0c\u201d\u4f0a\u8389\u96c5\u4f38\u4f38\u61d2\u8170\uff0c\u53ef\u7231\u5730\u63c9\u63c9\u773c\u775b\uff0c\u4eff\u4f5b\u8fd8\u6ca1\u7761\u9192\u4f3c\u7684\uff0c\u201c\u4eca\u5929\u9192\u5f97\u592a\u65e9\uff0c\u6211\u8981\u56de\u53bb\u8865\u5145\u7761\u7720\uff0c\u4e0d\u7136\u4f1a\u957f\u76b1\u7eb9\u7684\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u79cd\u5c0f\u5b69\u5b50\u62c5\u5fc3\u4ec0\u4e48\u3002\u201d\u6240\u6709\u4eba\u90fd\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u6b64\u65f6\u838e\u73de\u514b\u4e5f\u4ece\u5546\u5e97\u4e2d\u8d70\u51fa\uff0c\u770b\u5979\u7684\u795e\u60c5\u50cf\u662f\u7ec8\u4e8e\u6311\u9009\u5230\u4e86\u4ec0\u4e48\u5408\u9002\u7684\u4e1c\u897f\uff0c\u4f46\u5927\u5bb6\u4e5f\u90fd\u6ca1\u6709\u591a\u95ee\u3002\u4e94\u4eba\u4e00\u8d77\u5f80\u56de\u8d70\uff0c\u51db\u3001\u4f0a\u8389\u96c5\u548c\u838e\u73de\u514b\u8d70\u5728\u524d\u9762\uff0c\u4ea4\u6d41\u8c08\u8bba\u4e00\u4e9b\u8d2d\u7269\u5fc3\u5f97\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u8fdc\u8fdc\u843d\u5728\u8eab\u540e\u3002</p><p>\u201c\u4e3b\u4eba\uff0c\u4f60\u8d70\u5f97\u592a\u6162\u4e86\u3002\u201d</p><p>\u838e\u73de\u514b\u56de\u5934\u671d\u743c\u6069\u62b1\u6028\uff0c\u5979\u7684\u58f0\u97f3\u53c8\u5a07\u53c8\u5a9a\uff0c\u8857\u9053\u4e0a\u6076\u9b54\u6765\u6765\u5f80\u5f80\uff0c\u65e0\u4e0d\u4fa7\u76ee\u800c\u89c6\uff0c\u4e03\u516b\u5341\u53ea\u773c\u775b\uff08\u672a\u5fc5\u662f\u53cc\u6570\uff0c\u56e0\u4e3a\u6709\u7684\u6076\u9b54\u5355\u773c\u6216\u8005\u4e09\u53ea\u773c\uff09\u671d\u7740\u743c\u6069\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\uff0c\u4f3c\u4e4e\u60f3\u770b\u770b\u8fd9\u4eba\u7c7b\u662f\u4f55\u65b9\u795e\u5723\uff0c\u5c45\u7136\u6562\u5728\u6076\u9b54\u805a\u5c45\u7684\u65ad\u57df\u9547\u91cc\u628a\u4e00\u4f4d\u9b45\u9b54\u8c03\u6559\u6210\u5974\u96b6\uff0c\u7b80\u76f4\u662f\u53ef\u5fcd\u5b70\u4e0d\u53ef\u5fcd\u3002\u9887\u6709\u51e0\u4e2a\u5f3a\u58ee\u7684\u6076\u9b54\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u60f3\u4e0a\u6765\u627e\u743c\u6069\u201c\u7406\u8bba\u201d\uff0c\u4f46\u754f\u4e8e\u65ad\u57df\u9547\u7684\u89c4\u77e9\uff0c\u6700\u7ec8\u90fd\u8fd8\u662f\u653e\u5f03\u4e86\u3002\u743c\u6069\u61d2\u5f97\u7406\u776c\u5b83\u4eec\uff0c\u968f\u610f\u671d\u838e\u73de\u514b\u6325\u4e86\u6325\u624b\uff0c\u201c\u4f60\u4eec\u5148\u8d70\uff0c\u4e0d\u7528\u7ba1\u6211\u4eec\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u838e\u73de\u514b\u56de\u8fc7\u8eab\u53bb\uff0c\u7ee7\u7eed\u548c\u51db\u70ed\u70c8\u63a2\u8ba8\u9970\u54c1\u7684\u95ee\u9898\u3002\u6885\u83f2\u65af\u77a5\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u4f60\u73b0\u5728\u4e60\u60ef\u5979\u53eb\u4f60\u4e3b\u4eba\u4e86\uff1f\u201d\u5979\u7565\u5e26\u8ba5\u8bbd\u5730\u8bf4\uff0c\u201c\u9002\u5e94\u5f97\u771f\u5feb\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u4e00\u65f6\u6ca1\u592a\u5728\u610f\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u5fd8\u4e86\u8ddf\u5979\u8bf4\u8fd9\u56de\u4e8b\u4e86\uff0c\u56de\u53bb\u5c31\u8ba9\u5979\u6539\u53e3\u3002\u201d</p><p>\u201c\u4e0d\u7528\u4e86\uff0c\u6211\u89c9\u5f97\u4f60\u5f88\u4eab\u53d7\u8fd9\u79cd\u79f0\u547c\u3002\u201d</p><p>\u201c\u5443\uff0c\u4f60\u8fd9\u4e48\u8bf4\u7684\u8bdd\u2026\u2026\u597d\u50cf\u4e5f\u662f\u6709\u90a3\u4e48\u70b9\u3002\u201d</p><p>\u201c\u88ab\u4e00\u4e2a\u7f8e\u5973\u53eb\u505a\u4e3b\u4eba\uff0c\u5fc3\u7406\u611f\u89c9\u4e00\u5b9a\u5f88\u4e0d\u9519\u5427\u3002\u201d</p><p>\u201c\u7b97\u662f\u5427\u3002\u201d</p><p>\u201c\u90a3\u4f60\u662f\u4e0d\u662f\u5e0c\u671b\u6211\u4e5f\u8fd9\u4e48\u53eb\u4f60\u554a\u3002\u201d</p><p>\u201c\u90a3\u5c31\u7b97\u4e86\uff0c\u201d\u743c\u6069\u8fde\u8fde\u6447\u5934\uff0c\u201c\u2026\u2026\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u5076\u5c14\u73a9\u73a9\u89d2\u8272\u626e\u6f14\u6e38\u620f\u4f3c\u4e4e\u4e5f\u4e0d\u9519\u2026\u2026\u201d</p><p>\u201c\u89d2\u8272\u626e\u6f14\u6e38\u620f\uff1f\u90a3\u4e3a\u4ec0\u4e48\u4e0d\u662f\u6211\u626e\u6f14\u5973\u738b\u4f60\u626e\u6f14\u5974\u96b6\u5462\uff1f\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u54ea\u6709\u5973\u738b\u7684\u6c14\u8d28\u554a\u3002\u201d</p><p>\u201c\u90a3\u6211\u96be\u9053\u5c31\u6709\u6027\u5974\u7684\u6c14\u8d28\u4e86\uff1f\u201d</p><p>\u6885\u83f2\u65af\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u5f88\u51b7\uff0c\u743c\u6069\u7acb\u523b\u5bdf\u89c9\u5230\u4e86\uff0c\u987f\u65f6\u6536\u655b\u8d77\u5168\u526f\u7684\u73a9\u7b11\u5fc3\u601d\uff0c\u201c\u827e\u5f25\u8587\uff0c\u6211\u6ca1\u8fd9\u4e2a\u610f\u601d\u2026\u2026\u201d</p><p>\u201c\u90a3\u4f60\u521a\u624d\u5728\u5546\u5e97\u91cc\uff0c\u548c\u5979\u5728\u8bf4\u4ec0\u4e48\u5462\uff1f\u201d\u6885\u83f2\u65af\u53cd\u95ee\uff0c\u201c\u8c03\u6559\u3001\u51cc\u8fb1\u3001\u5815\u843d\u3001\u6c89\u6ca6\u2026\u2026\u96be\u9053\u6211\u542c\u5230\u7684\u4e0d\u662f\u8fd9\u4e9b\u8bcd\u5417\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u8fd9\u4e48\u4e00\u56de\u4e8b\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u4f60\u4ece\u4ec0\u4e48\u65f6\u5019\u8fdb\u6765\u7684\uff0c\u90fd\u542c\u5230\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u542c\u5230\u5979\u6b63\u5728\u4e3a\u4f60\u51fa\u4e3b\u610f\uff0c\u600e\u4e48\u8ba9\u6211\u5815\u843d\u5d29\u6e83\uff0c\u201d\u6885\u83f2\u65af\u9690\u9690\u6709\u4e9b\u607c\u6012\uff0c\u201c\u6211\u624d\u521a\u79bb\u5f00\u4e0d\u5230\u51e0\u5206\u949f\uff0c\u4f60\u5c31\u548c\u522b\u4eba\u5728\u80cc\u540e\u5546\u91cf\u600e\u4e48\u6b3a\u8fb1\u6211\uff0c\u8fd9\u5c31\u662f\u4f60\u6240\u8c13\u7684\u7231\u6211\u662f\u5417\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u90a3\u56de\u4e8b\uff0c\u201d\u743c\u6069\u53f9\u6c14\uff0c\u628a\u524d\u540e\u7f18\u7531\u5411\u5979\u590d\u8ff0\u4e86\u4e00\u904d\uff0c\u201c\u5176\u5b9e\u6211\u4e00\u5f00\u59cb\u6839\u672c\u6ca1\u8fd9\u79cd\u610f\u601d\uff0c\u6211\u53ea\u662f\u60f3\u6211\u4eec\u4e4b\u95f4\u80fd\u2026\u2026\u80fd\u589e\u52a0\u70b9\u66f4\u591a\u7684\u4e50\u8da3\uff0c\u4ec5\u6b64\u800c\u5df2\u3002\u8c01\u77e5\u9053\u5979\u8d8a\u626f\u8d8a\u8fdc\uff0c\u8d8a\u8bf4\u8d8a\u5938\u5f20\u79bb\u8c31\uff0c\u4e0d\u77e5\u600e\u4e48\u5c31\u626f\u5230\u90a3\u4e9b\u4e1c\u897f\u4e0a\u53bb\u4e86\uff0c\u4f60\u6070\u597d\u542c\u5230\u7684\u662f\u540e\u9762\u90e8\u5206\u3002\u6211\u4ece\u6765\u5c31\u6ca1\u8fd9\u79cd\u6253\u7b97\u7684\u3002\u201d</p><p>\u201c\u6ca1\u8fd9\u79cd\u6253\u7b97\u4f60\u8fd8\u548c\u5979\u8bf4\u5f97\u90a3\u4e48\u5174\u9ad8\u91c7\u70c8\u3002\u201d</p><p>\u201c\u6211\u6ca1\u5174\u9ad8\u91c7\u70c8\uff0c\u201d\u743c\u6069\u5206\u8fa9\uff0c\u201c\u800c\u4e14\u6211\u771f\u7684\u4ece\u6765\u6ca1\u8fd9\u4e48\u60f3\u8fc7\uff0c\u827e\u5f25\u8587\uff0c\u76f8\u4fe1\u6211\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u60f3\u6309\u5979\u8bf4\u5f97\u8bd5\u8bd5\uff1f\u201d\u6885\u83f2\u65af\u5fae\u5fae\u6311\u4e86\u6311\u773c\u89d2\uff0c\u201c\u4e0d\u60f3\u5c1d\u5c1d\u5815\u843d\u7684\u5723\u6b66\u58eb\u7684\u6ecb\u5473\uff0c\u662f\u4e0d\u662f\u771f\u7684\u5982\u5979\u6240\u8bf4\uff1a\u5728\u9ed1\u6697\u4e2d\u91cd\u751f\u7efd\u653e\u7684\u82b1\u6735\uff0c\u624d\u662f\u6700\u8273\u4e3d\u6700\u751c\u871c\u7684\u3002\u201d</p><p>\u201c\u4e0d\u60f3\u3002\u201d\u743c\u6069\u65a9\u9489\u622a\u94c1\u5730\u8bf4\u3002</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u771f\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5979\u6709\u53e5\u8bdd\u8bf4\u5f97\u6ca1\u9519\uff0c\u6211\u4eec\u5728\u672c\u8d28\u4e0a\u8fd1\u4f3c\uff0c\u6240\u4ee5\u4fbf\u4f1a\u4e0d\u7531\u81ea\u4e3b\u5730\u628a\u5bf9\u65b9\u89c6\u4e3a\u81ea\u5df1\u3002\u5979\u8bf4\u4f60\u628a\u6211\u89c6\u4e3a\u4f60\u7684\u6298\u5c04\u548c\u6295\u5f71\uff0c\u4f46\u53cd\u8fc7\u6765\u8bf4\uff0c\u6211\u4f55\u5c1d\u53c8\u4e0d\u662f\u5982\u6b64\uff1f\u6211\u60f3\u6bcf\u4e2a\u4eba\u90fd\u66fe\u7ecf\u6709\u8fc7\u81f3\u5584\u7684\u61a7\u61ac\u548c\u68a6\u60f3\uff0c\u81f3\u5c11\u6211\u6709\u8fc7\uff0c\u867d\u7136\u5df2\u7ecf\u5f7b\u5e95\u4e0d\u53ef\u80fd\u4e86\u3002\u4f46\u5728\u67d0\u79cd\u610f\u4e49\u4e0a\uff0c\u4f60\u5c31\u662f\u6211\u7684\u61a7\u61ac\uff0c\u4f60\u5c31\u662f\u6211\u7684\u68a6\u60f3\uff0c\u4f60\u5c31\u662f\u6211\u66fe\u7ecf\u5e0c\u5180\u4f46\u6c38\u8fdc\u4e0d\u53ef\u80fd\u518d\u5b9e\u73b0\u7684\u5e0c\u671b\u2014\u2014\u6211\u4eec\u662f\u4e00\u4f53\u7684\uff0c\u6211\u4e0d\u4f1a\u4f24\u5bb3\u4f60\uff0c\u90a3\u4e5f\u662f\u4f24\u5bb3\u6211\u81ea\u5df1\uff1b\u6211\u66f4\u4e0d\u4f1a\u6253\u788e\u81ea\u5df1\u7684\u61a7\u61ac\u548c\u68a6\u60f3\u3002\u5723\u6b66\u58eb\u5c31\u8981\u6c38\u8fdc\u662f\u5723\u6b66\u58eb\uff0c\u6c38\u6c38\u8fdc\u8fdc\u90fd\u662f\u3002\u201d</p><p>\u6885\u83f2\u65af\u7684\u8138\u8272\u603b\u7b97\u67d4\u548c\u4e0b\u6765\uff0c\u201c\u6211\u4eec\u76f8\u8bc6\uff0c\u90a3\u662f\u5076\u9047\uff0c\u6b64\u524d\u4f60\u4e0d\u77e5\u9053\u4e16\u754c\u4e0a\u6709\u6211\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u4e16\u754c\u4e0a\u6709\u4f60\u3002\u65e2\u7136\u76f8\u8bc6\uff0c\u4e5f\u662f\u547d\u8fd0\uff0c\u6b64\u540e\u8fd9\u4e48\u957f\u65f6\u95f4\uff0c\u4f60\u6211\u4e5f\u7b97\u662f\u4e00\u8def\u5171\u7ecf\u60a3\u96be\uff0c\u751f\u6b7b\u76f8\u968f\u8d70\u8fc7\u6765\u3002\u5728\u9634\u9b42\u57ce\u7684\u65f6\u5019\uff0c\u6211\u56de\u4e86\u4e00\u8d9f\u8ff7\u65af\u5353\u8bfa\uff0c\u56de\u6765\u5bf9\u4f60\u9690\u7792\u4e86\u4e9b\u4e8b\u60c5\uff0c\u4f46\u4e5f\u6ca1\u4ec0\u4e48\u6076\u610f\u3002\u6709\u4e9b\u4e8b\u60c5\uff0c\u6211\u4e5f\u8eab\u4e0d\u7531\u5df1\u2026\u2026\u201d</p><p>\u201c\u6211\u660e\u767d\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u63e1\u7740\u5979\u7684\u624b\uff0c\u201c\u6211\u660e\u767d\uff0c\u827e\u5f25\u8587\uff0c\u4e0d\u7528\u8bf4\u8fd9\u4e2a\u3002\u201d</p><p>\u201c\u6211\u4e5f\u662f\u51e1\u4eba\uff0c\u4e0d\u662f\u795e\u660e\uff0c\u6211\u4e5f\u6709\u5f31\u70b9\uff0c\u4e5f\u6709\u6781\u9650\uff0c\u4e5f\u6709\u529e\u4e0d\u5230\u7684\u4e8b\u60c5\uff0c\u4e5f\u6709\u575a\u6301\u4e0d\u4e0b\u53bb\u7684\u65f6\u5019\uff0c\u201d\u6885\u83f2\u65af\u6de1\u6de1\u8bf4\uff0c\u201c\u6211\u628a\u81ea\u5df1\u4ea4\u7ed9\u4f60\uff0c\u5982\u679c\u4f60\u60f3\u4f24\u5bb3\u6211\uff0c\u90a3\u662f\u8f7b\u800c\u6613\u4e3e\u3002\u4f46\u5982\u679c\u4f60\u771f\u7684\u90a3\u6837\u505a\uff0c\u6211\u4f1a\u5f88\u751f\u6c14\u3002\u4f5c\u4e3a\u60c5\u4eba\uff0c\u6211\u6216\u8bb8\u505a\u5f97\u4e0d\u597d\uff0c\u5982\u679c\u4f60\u559c\u6b22\u4ec0\u4e48\uff0c\u60f3\u8981\u4ec0\u4e48\uff0c\u4f60\u544a\u8bc9\u6211\uff0c\u6211\u4f1a\u8bd5\u7740\u53bb\u5b66\uff0c\u8bd5\u7740\u53bb\u53d6\u60a6\u4f60\uff0c\u8ba9\u4f60\u9ad8\u5174\u3002\u4f46\u6709\u4e9b\u4e8b\u60c5\uff0c\u90a3\u662f\u5e95\u7ebf\u3002\u201d</p><p>\u743c\u6069\u7ad9\u5b9a\u811a\u6b65\uff0c\u5c06\u5979\u62b1\u4f4f\uff0c\u5728\u5979\u6a31\u5507\u4e0a\u8f7b\u8f7b\u4e00\u543b\uff0c\u201c\u6211\u77e5\u9053\uff0c\u827e\u5f25\u8587\uff0c\u76f8\u4fe1\u6211\uff0c\u6211\u4e0d\u4f1a\u4f24\u5bb3\u4f60\uff0c\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u201c\u6c38\u751f\u6c38\u4e16\uff0c\u6c38\u6c38\u8fdc\u8fdc\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u70ed\u70c8\u7684\u9f13\u638c\u58f0\u7a81\u7136\u54cd\u8d77\u6765\uff0c\u5012\u628a\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u624d\u53d1\u73b0\u539f\u6765\u4e00\u7fa4\u6076\u9b54\u90fd\u8fdc\u8fdc\u56f4\u5728\u65c1\u8fb9\uff0c\u6b63\u5728\u5927\u529b\u62cd\u624b\uff0c\u5176\u4e2d\u6709\u53ea\u86c7\u9b54\u751a\u81f3\u628a\u516d\u53ea\u624b\u81c2\u5168\u90fd\u4e00\u8d77\u7528\u4e0a\u4e86\u3002\u8fd9\u4e00\u5e55\u8ba9\u743c\u6069\u54d1\u7136\u5931\u7b11\uff0c\u540c\u65f6\u5fc3\u4e2d\u4e00\u65f6\u4e5f\u9690\u9690\u6709\u4e9b\u611f\u52a8\uff0c\u8fde\u5e26\u770b\u8fd9\u4e9b\u5947\u5f62\u602a\u72b6\u7684\u6076\u9b54\u4e5f\u90fd\u987a\u773c\u4e86\u5f88\u591a\u3002</p><p>\u552f\u4e00\u80fd\u591f\u8d85\u8d8a\u79cd\u65cf\uff0c\u8d85\u8d8a\u65f6\u7a7a\uff0c\u653e\u4e4b\u56db\u6d77\u800c\u7686\u51c6\u7684\uff0c\u662f\u7406\u6027\uff1b\u800c\u552f\u4e00\u80fd\u591f\u5bf9\u6297\u7406\u6027\u7684\uff0c\u662f\u7231\u60c5\u3002</p><p>\u51db\u548c\u838e\u73de\u514b\u3001\u4f0a\u8389\u96c5\u5df2\u7ecf\u8d70\u51fa\u5f88\u8fdc\uff0c\u542c\u5230\u540e\u9762\u52a8\u9759\uff0c\u8fd8\u4ee5\u4e3a\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8d76\u5fd9\u56de\u5934\u6765\u770b\uff0c\u5374\u89c1\u743c\u6069\u548c\u6885\u83f2\u65af\u6b63\u65c1\u82e5\u65e0\u4eba\u5730\u5f53\u8857\u62e5\u543b\uff0c\u8fd8\u6709\u4e00\u7fa4\u6076\u9b54\u56f4\u7740\u567c\u91cc\u556a\u5566\u70ed\u70c8\u9f13\u638c\u3002\u201c\u54c7\uff0c\u597d\u7fa1\u6155\uff01\u201d\u51db\u5fcd\u4e0d\u4f4f\u611f\u53f9\uff0c\u4e24\u4e2a\u773c\u775b\u91cc\u4f3c\u4e4e\u90fd\u5192\u51fa\u7c89\u7ea2\u8272\u5fc3\u578b\u6765\uff0c\u201c\u4e3a\u4ec0\u4e48\u5c31\u6ca1\u6709\u4eba\u8fd9\u4e48\u5bf9\u6211\u5462\u3002\u201d</p><p>\u9b45\u9b54\u548c\u5353\u5c14\u841d\u8389\u5bf9\u6b64\u7684\u53cd\u5e94\u6ca1\u6709\u9f99\u5973\u90a3\u4e48\u5f3a\u70c8\uff0c\u201c\u771f\u662f\u6709\u591f\u767d\u75f4\u7684\u3002\u201d\u838e\u73de\u514b\u8bc4\u4ef7\uff0c\u7136\u540e\u4f0a\u8389\u96c5\u5728\u65c1\u8fb9\u8fde\u8fde\u70b9\u5934\uff0c\u8868\u793a\u8d5e\u540c\u3002</p><p>\u201c\u8fd9\u53eb\u6d6a\u6f2b\u4f60\u4eec\u61c2\u4e0d\u61c2\uff1f\u201d\u51db\u5f88\u4e0d\u6ee1\u4e24\u4f4d\u540c\u4f34\u7684\u6001\u5ea6\u3002</p><p>\u201c\u4e0d\u61c2\u3002\u201d\u4e24\u4eba\u4e00\u8d77\u6447\u5934\u3002</p><p>\u201c\u6ca1\u52b2\uff0c\u771f\u65e0\u8da3\u3002\u201d</p><p>\u6d6a\u6f2b\u5f52\u6d6a\u6f2b\uff0c\u603b\u4e0d\u80fd\u4e00\u76f4\u5728\u5927\u8857\u4e0a\u5f53\u4f17\u8868\u6f14\uff0c\u4fbf\u662f\u6885\u83f2\u65af\u8fd9\u79cd\u5973\u5b69\u5b50\uff0c\u88ab\u4f17\u76ee\u777d\u777d\u770b\u7740\uff0c\u90fd\u6709\u4e9b\u8138\u4e0a\u53d1\u70e7\u8d77\u6765\u3002\u201c\u8d70\u5566\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u8fd8\u7ad9\u5728\u8fd9\u91cc\u7b49\u7740\u88ab\u53c2\u89c2\u554a\u3002\u201d</p><p>\u4f17\u4eba\u56de\u5230\u65c5\u5e97\uff0c\u4f0a\u8389\u96c5\u4e00\u9a6c\u5f53\u5148\u51b2\u4e0a\u697c\uff0c\u5148\u8865\u5145\u7761\u89c9\u53bb\u4e86\uff0c\u838e\u73de\u514b\u8868\u793a\u8981\u9002\u5e94\u4e00\u4e0b\u65b0\u4e70\u7684\u88c5\u5907\uff0c\u4e5f\u5f84\u76f4\u56de\u623f\u3002\u51db\u5219\u5728\u6885\u83f2\u65af\u65c1\u8fb9\u53fd\u53fd\u55b3\u55b3\uff0c\u5c31\u50cf\u5c0f\u62a5\u8bb0\u8005\u4f3c\u7684\u91c7\u8bbf\u5979\u521a\u624d\u88ab\u5f53\u4f17\u793a\u7231\u7684\u611f\u53d7\uff0c\u5c31\u5dee\u624b\u91cc\u62ff\u7740\u8bdd\u7b52\u4e86\uff0c\u6885\u83f2\u65af\u6b63\u6709\u4e9b\u7a77\u4e8e\u5e94\u4ed8\uff0c\u7a81\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u795e\u8272\u5fae\u5fae\u4e00\u51db\uff0c\u671d\u65c1\u8fb9\u770b\u8fc7\u53bb\u3002</p><p>\u743c\u6069\u662f\u5979\u60c5\u4eba\uff0c\u51db\u548c\u5979\u662f\u76f8\u4ea4\u591a\u5e74\u7684\u631a\u53cb\uff0c\u5f7c\u6b64\u90fd\u662f\u719f\u6089\u65e0\u6bd4\uff0c\u4e00\u89c1\u5979\u7684\u795e\u60c5\uff0c\u7acb\u523b\u4fbf\u77e5\u9053\u6709\u4e8b\uff0c\u987f\u65f6\u9f50\u9f50\u987a\u7740\u5979\u7684\u773c\u5149\u65b9\u5411\u770b\u8fc7\u53bb\u3002\u53ea\u89c1\u8fdc\u5904\u5927\u5802\u7684\u89d2\u843d\u91cc\uff0c\u5750\u7740\u4e00\u4e2a\u7070\u888d\u8001\u4eba\uff0c\u4ed6\u770b\u8d77\u6765\u8eab\u4f53\u975e\u5e38\u7626\u5f31\uff0c\u9ed1\u8272\u5934\u53d1\uff0c\u8138\u8272\u6697\u7070\uff0c\u624b\u4e2d\u7aef\u7740\u4e00\u53ea\u9152\u676f\uff0c\u6b63\u5728\u6162\u6162\u54c1\u5c1d\u8461\u8404\u9152\uff0c\u743c\u6069\u6ce8\u610f\u5230\u90a3\u53ea\u624b\u5728\u4e0d\u505c\u5730\u8f7b\u5fae\u98a4\u6296\uff0c\u4ee5\u81f3\u4e8e\u7edb\u7ea2\u8272\u7684\u6db2\u4f53\u5728\u900f\u660e\u6c34\u6676\u676f\u4e2d\u4e0d\u65ad\u6643\u8361\uff0c\u4eff\u4f5b\u968f\u65f6\u53ef\u80fd\u6cfc\u6d12\u5728\u5730\uff0c\u4ed6\u5f88\u53ef\u80fd\u80ba\u90e8\u6709\u6bdb\u75c5\uff0c\u4e00\u8fb9\u559d\u4e00\u8fb9\u54b3\u55fd\u4e2a\u4e0d\u505c\uff0c\u4f46\u6700\u540e\u5c45\u7136\u8fd8\u662f\u6162\u6162\u628a\u9152\u4e00\u6ef4\u4e0d\u6f0f\u5730\u5168\u90e8\u559d\u5149\u4e86\u3002\u4e24\u53ea\u5938\u8d5b\u9b54\u6b63\u6bd5\u606d\u6bd5\u656c\u5730\u7ad9\u5728\u4ed6\u8eab\u8fb9\uff0c\u63d0\u7740\u9152\u74f6\uff0c\u89c1\u4ed6\u559d\u5b8c\u4e00\u676f\uff0c\u8d76\u7d27\u53c8\u66ff\u4ed6\u659f\u4e0a\u3002</p><p>\u201c\u4eba\u7c7b\u2026\u2026\u96be\u9053\u662f\u8428\u9a6c\u65af\u7279\uff1f\u201d</p><p>\u8fd9\u662f\u743c\u6069\u8111\u4e2d\u5192\u51fa\u7684\u7b2c\u4e00\u4e2a\u5ff5\u5934\uff0c\u4ed6\u5b8c\u5168\u6ca1\u89c1\u8fc7\u8428\u9a6c\u65af\u7279\uff0c\u53ea\u662f\u4ece\u4e66\u672c\u4e0a\u770b\u8fc7\u4e00\u4e9b\u8bb0\u8f7d\uff0c\u81ea\u7136\u4e5f\u4e0d\u77e5\u9053\u4ed6\u957f\u4ec0\u4e48\u6a21\u6837\u3002\u4f46\u8fd9\u91cc\u662f\u65ad\u57df\u9547\uff0c\u904d\u5730\u6076\u9b54\uff0c\u7a81\u7136\u5192\u51fa\u4e2a\u4eba\u7c7b\u6765\uff0c\u672a\u514d\u592a\u8fc7\u7a81\u5140\u4e86\uff0c\u672c\u80fd\u5730\u5c31\u60f3\u5230\u662f\u4ed6\u3002\u53ea\u662f\u518d\u4ed4\u7ec6\u770b\uff0c\u4ed6\u8fd9\u526f\u8870\u8001\u6a21\u6837\u53c8\u4e0d\u4f3c\u88c5\u51fa\u6765\u7684\uff0c\u90a3\u4f4d\u4f20\u5947\u5929\u624d\u5927\u5deb\u5e08\uff0c\u603b\u4e0d\u81f3\u4e8e\u5c31\u662f\u8fd9\u526f\u5fb7\u884c\u5427\u3002\u522b\u7684\u4e0d\u8bf4\uff0c\u5c31\u770b\u4ed6\u90a3\u4e0d\u505c\u98a4\u6296\u7684\u624b\uff0c\u4fe8\u7136\u5df2\u7ecf\u6709\u4e86\u5e15\u91d1\u68ee\u7efc\u5408\u75c7\u7684\u8ff9\u8c61\uff0c\u60f3\u65bd\u6cd5\u5c31\u5f88\u8d39\u52b2\uff0c\u53ea\u6015\u4e00\u4e2a\u52a8\u4f5c\u6bd4\u5212\u9519\uff0c\u6cd5\u672f\u6548\u679c\u53cd\u566c\u628a\u81ea\u5df1\u7ed9\u6bc1\u4e86\u3002\u66f4\u522b\u8bf4\u4ed6\u8fd8\u54b3\u55fd\uff0c\u867d\u7136\u4e00\u4e2a\u5deb\u5e08\u5230\u4e86\u767b\u5cf0\u9020\u6781\u7684\u5730\u6b65\uff0c\u5df2\u7ecf\u672a\u5fc5\u9700\u8981\u518d\u901a\u8fc7\u8bf5\u5492\u6765\u65bd\u6cd5\uff0c\u4f46\u603b\u4e5f\u4e0d\u80fd\u54b3\u55fd\u4e2a\u4e0d\u505c\u5427\u3002</p><p>\u5bf9\u4e86\uff0c\u51db\u662f\u8ddf\u7740\u6b23\u5e03\u6765\u7684\uff0c\u5979\u5e94\u8be5\u89c1\u8fc7\u8428\u9a6c\u65af\u7279\u624d\u5bf9\u3002</p><p>\u743c\u6069\u56de\u5934\u770b\u51db\uff0c\u89c1\u5979\u5fae\u5fae\u6447\u6447\u5934\uff0c\u201c\u6211\u89c1\u5230\u7684\u8428\u9a6c\u65af\u7279\u662f\u526f\u9ab7\u9ac5\u9aa8\u67b6\uff0c\u201d\u51db\u6084\u58f0\u8bf4\uff0c\u201c\u4e0d\u662f\u8fd9\u6837\u3002\u201d</p><p>\u5dee\u70b9\u5fd8\u4e86\uff0c\u8428\u9a6c\u65af\u7279\u5982\u4eca\u65e9\u5c31\u662f\u4e2a\u5deb\u5996\u4e86\u2014\u2014\u5f53\u7136\uff0c\u5deb\u5996\u4e5f\u5b8c\u5168\u53ef\u4ee5\u53d8\u5316\u4f2a\u88c5\u6210\u6d3b\u4eba\uff0c\u8fd9\u4e0d\u662f\u95ee\u9898\uff0c\u7ea2\u888d\u5deb\u5e08\u4f1a\u7684\u9996\u5e2d\u8428\u624e\u65af\u5766\u8fd8\u7ecf\u5e38\u4ee5\u4e2d\u5e74\u4eba\u7684\u9762\u76ee\u51fa\u73b0\u5462\u3002\u4e0d\u8fc7\u4ed6\u5982\u679c\u771f\u662f\u8428\u9a6c\u65af\u7279\uff0c\u660e\u77e5\u9053\u6b23\u5e03\u5728\u8ffd\u4ed6\uff0c\u4e0d\u81f3\u4e8e\u8fd8\u6562\u8fd9\u4e48\u5802\u800c\u7687\u4e4b\u5927\u6a21\u5927\u6837\u5730\u51fa\u73b0\u5427\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u4f60\u53d1\u73b0\u4ec0\u4e48\u4e86\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u6885\u83f2\u65af\u6447\u6447\u5934\uff0c\u201c\u6211\u53ea\u662f\u521a\u624d\u7a81\u7136\u6709\u79cd\u76f4\u89c9\uff0c\u4eff\u4f5b\u6709\u4eba\u5728\u76ef\u7740\u6211\u4eec\uff0c\u6240\u4ee5\u624d\u987a\u7740\u770b\u8fc7\u53bb\uff0c\u5012\u4e0d\u662f\u53d1\u73b0\u4e86\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u5982\u679c\u4ed6\u771f\u662f\u5deb\u5996\uff0c\u4f60\u5e94\u8be5\u80fd\u611f\u89c9\u5230\u5427\u3002\u201d</p><p>\u201c\u4e0d\u597d\u8bf4\uff0c\u8ddd\u79bb\u592a\u8fdc\uff0c\u800c\u4e14\u6211\u73b0\u5728\u5b8c\u5168\u4e0d\u5728\u72b6\u6001\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u4f4e\u58f0\u89e3\u91ca\uff0c\u56e0\u4e3a\u53d7\u5230\u4f4d\u9762\u89c4\u5219\u7684\u538b\u5236\uff0c\u5979\u7684\u611f\u5e94\u80fd\u529b\u5927\u5927\u524a\u5f31\uff0c\u4e0d\u80fd\u5982\u5e73\u5e38\u90a3\u6837\u654f\u9510\u4e86\u3002\u53ea\u662f\u8fd9\u6837\u4e00\u6765\uff0c\u5bf9\u65b9\u7684\u8eab\u5206\u5c31\u66f4\u4e0d\u597d\u786e\u8ba4\uff0c\u6b23\u5e03\u548c\u8475\u9732\u504f\u504f\u53c8\u4e0d\u5728\u4e00\u8d77\uff0c\u5426\u5219\u5979\u4eec\u80af\u5b9a\u662f\u80fd\u5206\u8fa8\u7684\u3002</p><p>\u6b63\u7591\u8651\u95f4\uff0c\u90a3\u4e2a\u7070\u888d\u8001\u4eba\u6709\u610f\u65e0\u610f\u5730\u671d\u8fd9\u8fb9\u770b\u8fc7\u6765\uff0c\u4ed6\u7684\u773c\u5149\u76f4\u63a5\u4ece\u51db\u548c\u6885\u83f2\u65af\u7684\u8138\u4e0a\u63a0\u8fc7\uff0c\u53cd\u5012\u662f\u5728\u743c\u6069\u8eab\u4e0a\u505c\u7559\u4e86\u51e0\u79d2\u949f\uff0c\u628a\u4ed6\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u4e86\u4e00\u904d\u3002\u4ed6\u7684\u773c\u795e\u4e0d\u7b97\u9510\u5229\uff0c\u4e5f\u50cf\u666e\u901a\u8001\u8005\u4e00\u822c\u6709\u4e9b\u6df7\u6d4a\uff0c\u4f46\u5374\u591a\u4e86\u51e0\u5206\u9634\u68ee\u68ee\u7684\u5473\u9053\uff0c\u743c\u6069\u88ab\u4ed6\u770b\u5f97\u5168\u8eab\u53d1\u6bdb\uff0c\u5fc3\u4e2d\u552f\u4e00\u7684\u5ff5\u5934\u662f\u53ea\u60f3\u8d76\u5feb\u79bb\u5f00\u8fd9\u8001\u4eba\u7684\u89c6\u7ebf\u3002\u4f46\u8fd9\u6837\u4e00\u6765\uff0c\u5374\u4e5f\u5927\u81f4\u6392\u9664\u4e86\u5bf9\u65b9\u662f\u8428\u9a6c\u65af\u7279\u7684\u53ef\u80fd\uff0c\u5426\u5219\u4ed6\u4e0d\u8ba4\u8bc6\u6885\u83f2\u65af\uff0c\u603b\u8be5\u5bf9\u51db\u6709\u5370\u8c61\uff0c\u4e0d\u81f3\u4e8e\u8fd9\u6837\u4e00\u63a0\u800c\u8fc7\uff0c\u53cd\u800c\u76ef\u7740\u743c\u6069\u3002</p><p>\u4fbf\u5728\u6b64\u65f6\uff0c\u8001\u4eba\u5927\u6982\u4e00\u53e3\u9152\u559d\u5f97\u6025\u4e86\uff0c\u7a81\u7136\u731b\u70c8\u5730\u54b3\u55fd\u8d77\u6765\uff0c\u6574\u4e2a\u8eab\u8eaf\u90fd\u4f5d\u507b\u6210\u4e00\u56e2\uff0c\u624b\u4e2d\u7684\u9152\u676f\u7830\u5730\u6389\u843d\u5728\u5730\uff0c\u788e\u6210\u4e00\u7247\u3002\u4ed6\u8d8a\u54b3\u8d8a\u6025\uff0c\u5230\u540e\u6765\u751a\u81f3\u90fd\u54af\u51fa\u5927\u91cf\u7684\u767d\u8272\u75f0\u6cab\uff0c\u9f3b\u5b54\u547c\u6c14\u58f0\u6e05\u6670\u53ef\u95fb\uff0c\u4f34\u968f\u7740\u201c\u547c\u54e7\u547c\u54e7\u201d\u7684\u54ee\u9e23\u97f3\uff0c\u5341\u5206\u5413\u4eba\u3002\u4e24\u53ea\u5938\u8d5b\u9b54\u5728\u65c1\u8fb9\u5413\u5f97\u4e0d\u77e5\u6240\u63aa\uff0c\u624b\u5fd9\u811a\u4e71\u5730\u66ff\u4ed6\u6376\u80cc\u63c9\u80f8\uff0c\u597d\u534a\u5929\u624d\u7b97\u6e10\u6e10\u5e73\u7f13\u4e0b\u6765\uff0c\u8138\u8272\u6108\u52a0\u7070\u8d25\u4e86\u51e0\u5206\uff0c\u6447\u6447\u5934\uff0c\u6162\u6162\u8d77\u8eab\u5f84\u81ea\u79bb\u5f00\u5927\u5802\uff0c\u5e94\u8be5\u662f\u56de\u623f\u95f4\u53bb\u4e86\u3002</p><p>\u4ed6\u7684\u8868\u73b0\u6253\u6d88\u4e86\u743c\u6069\u7684\u6700\u540e\u4e00\u4e1d\u7591\u8651\uff0c\u5982\u679c\u8428\u9a6c\u65af\u7279\u5c31\u662f\u8fd9\u79cd\u7cdf\u8001\u5934\u7684\u8bdd\uff0c\u4ed6\u4f30\u8ba1\u81ea\u5df1\u90fd\u80fd\u5bf9\u4ed8\uff0c\u8fde\u90a3\u4e24\u4e2a\u9009\u6c11\u90fd\u4e0d\u9700\u8981\u4e86\u3002\u6447\u6447\u5934\uff0c\u63fd\u7740\u6885\u83f2\u65af\u4fbf\u51c6\u5907\u56de\u623f\u95f4\uff0c\u51db\u5374\u4f9d\u65e7\u6709\u4e9b\u4e0d\u653e\u5fc3\u7684\u6837\u5b50\uff0c\u201c\u4ed6\u771f\u7684\u4e0d\u662f\u88c5\u7684\uff1f\u201d</p><p>\u201c\u8428\u9a6c\u65af\u7279\u9700\u8981\u88c5\u4e48\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53cd\u6b63\u660e\u5929\u5c31\u5f00\u59cb\u6bd4\u8d5b\uff0c\u5230\u65f6\u5019\u4ed6\u80af\u5b9a\u4e5f\u8981\u9732\u9762\u7684\uff0c\u9664\u975e\u5956\u54c1\u4e0d\u60f3\u8981\u4e86\u3002\u5c31\u7b97\u4ed6\u73b0\u5728\u5149\u660e\u6b63\u5927\u5730\u7ad9\u5728\u4f60\u8001\u5e08\u9762\u524d\uff0c\u8c01\u53c8\u6562\u52a8\u624b\uff1f\u90a3\u4e9b\u6076\u9b54\u536b\u5175\u5c31\u591f\u96be\u7f20\u4e86\u3002\u518d\u8bf4\u4e86\uff0c\u8428\u9a6c\u65af\u7279\u4e5f\u4e0d\u89c1\u5f97\u5c31\u6015\u4e86\u4f60\u8001\u5e08\u5427\uff0c\u6211\u77e5\u9053\u4ed6\u5f53\u5e74\u9762\u5bf9\u4e09\u4e2a\u9009\u6c11\u90fd\u4e0d\u843d\u4e0b\u98ce\u7684\u3002\u201d</p><p>\u201c\u4e5f\u662f\u3002\u201d</p><p>\u201c\u800c\u4e14\u5c31\u521a\u624d\u8001\u4eba\u90a3\u6a21\u6837\uff0c\u80fd\u662f\u5927\u5deb\u5e08\uff1f\u5c31\u7b97\u8bf4\u4ed6\u662f\u9b54\u6cd5\u5b66\u5f92\u6211\u90fd\u4e0d\u4f1a\u4fe1\u554a\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u5012\u4e0d\u597d\u8bf4\u7684\uff0c\u6211\u66fe\u7ecf\u8bfb\u8fc7\u4e00\u672c\u5c0f\u8bf4\uff0c\u91cc\u9762\u6709\u4e2a\u5916\u53f7\u53eb\u505a\u5154\u5b50\u7684\u9ed1\u888d\u5deb\u5e08\uff0c\u5c31\u4e00\u5929\u5230\u665a\u54b3\u55fd\uff0c\u4f46\u5374\u662f\u91cc\u9762\u6700\u5389\u5bb3\u7684\u5927\u5deb\u5e08\u3002\u201d</p><p>\u201c\u90a3\u662f\u4ed6\u4f2a\u88c5\u51fa\u6765\u7684\u5047\u8c61\uff0c\u5c31\u662f\u4e3a\u4e86\u9ebb\u75f9\u5bf9\u624b\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u90a3\u672c\u4e66\u6211\u4e5f\u8bfb\u8fc7\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u4e2a\u9ed1\u888d\u5deb\u5e08\u770b\u8d77\u6765\u603b\u662f\u54b3\u55fd\uff0c\u50cf\u662f\u8106\u5f31\u5f97\u4e0d\u884c\uff0c\u4f46\u4ed6\u6709\u6ca1\u6709\u5728\u5173\u952e\u65f6\u523b\u54b3\u8fc7\uff1f\u4ed6\u6709\u6ca1\u6709\u56e0\u4e3a\u54b3\u55fd\u800c\u65bd\u6cd5\u5931\u8d25\uff1f\u6211\u5c31\u4ece\u6765\u6ca1\u89c1\u8fc7\u4e00\u6b21\uff0c\u53ef\u89c1\u5206\u660e\u662f\u88c5\u7684\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u2026\u2026\u201d</p><p>\u201c\u800c\u4e14\u521a\u624d\u90a3\u8001\u4eba\u662f\u54b3\u55fd\u4e48\uff1f\u90a3\u5206\u660e\u5df2\u7ecf\u662f\u4e25\u91cd\u54ee\u5598\u4e86\u597d\u4e0d\u597d\uff0c\u5deb\u5996\u5df2\u7ecf\u662f\u4ea1\u7075\uff0c\u5c31\u7b97\u53d8\u6210\u6d3b\u4eba\uff0c\u96be\u9053\u8fd8\u4f1a\u54ee\u5598\u4e0d\u6210\uff1f\u5982\u679c\u4ed6\u90fd\u80fd\u662f\u8428\u9a6c\u65af\u7279\uff0c\u90a3\u6211\u5c31\u662f\u5361\u5c14\u8428\u65af\u4e86\u3002\u201d</p><p>\u51db\u88ab\u743c\u6069\u8bf4\u670d\u4e86\uff0c\u6885\u83f2\u65af\u4f3c\u4e4e\u8fd8\u6709\u4e00\u4e1d\u7591\u8651\uff0c\u4f46\u4e5f\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\u3002\u6bd5\u7adf\u9a6c\u4e0a\u5c31\u662f\u6bd4\u8d5b\uff0c\u6bd4\u8d5b\u5b8c\u4e86\u4e89\u53d6\u641e\u5b9a\u90a3\u679a\u5370\u7ae0\uff0c\u8d76\u7d27\u56de\u7269\u8d28\u754c\u518d\u8bf4\u3002\u81f3\u4e8e\u8fd9\u4e2a\u5947\u602a\u7684\u8001\u4eba\uff0c\u7ba1\u4ed6\u662f\u8c01\u4e5f\u597d\uff0c\u54ea\u6015\u5c31\u662f\u6df1\u6e0a\u91cc\u67d0\u4f4d\u5927\u6076\u9b54\u5047\u626e\u7684\uff0c\u90a3\u4e5f\u4e0d\u7528\u7406\u776c\u4e86\u3002</p><p>\u56de\u5230\u623f\u95f4\uff0c\u53d1\u73b0\u6b23\u5e03\u3001\u8475\u9732\u548c\u963f\u5fd2\u59ae\u8fd8\u6ca1\u56de\u6765\uff0c\u51db\u62c9\u7740\u6885\u83f2\u65af\u8bf4\u8bdd\uff0c\u4f0a\u8389\u96c5\u672c\u6765\u8bf4\u8981\u8865\u5145\u7761\u7720\uff0c\u73b0\u5728\u4e5f\u8ddf\u7740\u8fc7\u6765\u51d1\u70ed\u95f9\u3002\u51e0\u4e2a\u5973\u5b69\u5b50\u53fd\u53fd\u55b3\u55b3\u8bf4\u6210\u4e00\u7247\uff0c\u743c\u6069\u5728\u65c1\u8fb9\u538b\u6839\u63d2\u4e0d\u4e0a\u5634\uff0c\u800c\u4e14\u8c08\u8bba\u7684\u8bdd\u9898\u4ed6\u4e5f\u8fd1\u4e4e\u5b8c\u5168\u65e0\u77e5\uff0c\u7d22\u6027\u8d77\u8eab\u53bb\u697c\u4e0b\uff0c\u6253\u7b97\u7ed9\u5979\u4eec\u8981\u70b9\u996e\u6599\u3002</p><p>\u8d70\u4e0b\u697c\u68af\u7684\u65f6\u5019\uff0c\u6070\u5de7\u53c8\u6b63\u9047\u4e0a\u4e86\u521a\u624d\u90a3\u4e2a\u7070\u888d\u8001\u4eba\uff0c\u6b63\u4e00\u6b65\u4e00\u6b65\u8e52\u8dda\u7740\u5f80\u4e0a\u8d70\u3002\u697c\u68af\u4e0d\u5bbd\uff0c\u743c\u6069\u4fa7\u8eab\u907f\u8ba9\uff0c\u8ba9\u4ed6\u5148\u8fc7\u3002\u201c\u8c22\u8c22\u3002\u201d\u8001\u4eba\u64e6\u80a9\u800c\u8fc7\u7684\u65f6\u5019\u4f4e\u58f0\u8bf4\u3002</p><p>\u201c\u60a8\u5ba2\u6c14\u4e86\u3002\u201d</p><p>\u743c\u6069\u4e5f\u6ca1\u5728\u610f\uff0c\u6b63\u51c6\u5907\u5f80\u4e0b\u8d70\uff0c\u7a81\u7136\u80cc\u540e\u9634\u51b7\u7684\u58f0\u97f3\u4f4e\u4f4e\u4f20\u6765\uff0c\u201c\u73b0\u5728\u9000\u51fa\u8fd8\u6765\u5f97\u53ca\uff0c\u5c0f\u5bb6\u4f19\uff0c\u201d\u8001\u4eba\u8bf4\uff0c\u201c\u770b\u5728\u4f60\u8001\u5e08\u7684\u4efd\u4e0a\uff0c\u6211\u4e0d\u96be\u4e3a\u4f60\u3002\u201d</p></article>"}}}]);
