(window.webpackJsonp=window.webpackJsonp||[]).push([[1530],{1553:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u516b\u8282 \u7231\u60c5",content:"<article><h2>\u7b2c\u4e09\u5341\u516b\u8282 \u7231\u60c5</h2><p>\u201c\u9b54\u6cd5\u5973\u795e\uff1f\u201d</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u90fd\u6709\u4e9b\u60ca\u8bb6\u3002\u8428\u9a6c\u65af\u7279\u867d\u7136\u662f\u5386\u53f2\u4e0a\u58f0\u540d\u8d6b\u8d6b\u7684\u5927\u5deb\u5e08\uff0c\u4f46\u6709\u5173\u4ed6\u7684\u8d44\u6599\u5374\u975e\u5e38\u7a00\u5c11\uff0c\u6d89\u53ca\u79c1\u4eba\u751f\u6d3b\u7684\u66f4\u662f\u51e0\u4e4e\u6ca1\u6709\u3002\u70db\u5821\u5df2\u7ecf\u662f\u5927\u9646\u6700\u5e9e\u5927\u6700\u8457\u540d\uff0c\u8d44\u6599\u6700\u9f50\u5168\u6700\u5b8c\u5907\u7684\u56fe\u4e66\u9986\u4e86\uff0c\u743c\u6069\u5728\u91cc\u9762\u9605\u8bfb\u5230\u7684\u4e1c\u897f\u4e5f\u4e0d\u591a\uff0c\u53ea\u77e5\u9053\u7c97\u7565\u7684\u5927\u81f4\u751f\u5e73\uff0c\u57fa\u672c\u90fd\u662f\u5be5\u5be5\u51e0\u7b14\uff0c\u5373\u4fbf\u662f\u6700\u540e\u4ed6\u548c\u6668\u66e6\u4e4b\u795e\u90a3\u60ca\u5929\u52a8\u5730\u5149\u8f89\u707f\u70c2\u7684\u4e00\u6218\uff0c\u4e5f\u4e0d\u8fc7\u51e0\u884c\u5b57\u5c31\u63cf\u8ff0\u8fc7\u53bb\uff08\u800c\u4e14\u4e3b\u8981\u662f\u5728\u4fee\u9970\u5938\u8d5e\u6668\u66e6\u4e4b\u795e\u7684\u65e0\u4e0a\u795e\u5a01\uff09\u3002\u53ea\u770b\u4eca\u5929\u5927\u5bb6\u5bf9\u8428\u9a6c\u65af\u7279\u7684\u76f8\u8c8c\u7279\u5f81\u3001\u8eab\u4f53\u72b6\u51b5\u3001\u7cbe\u795e\u75be\u75c5\u8fd9\u4e9b\u5168\u90fd\u4e00\u65e0\u6240\u77e5\uff0c\u5f53\u9762\u9047\u5230\u90fd\u4e0d\u8ba4\u8bc6\uff0c\u4fbf\u53ef\u89c1\u4e00\u6591\uff0c\u6362\u4e86\u5176\u4ed6\u540d\u4eba\uff0c\u53ea\u6015\u8fde\u8eab\u4e0a\u6709\u51e0\u5757\u80ce\u8bb0\u90fd\u641e\u6e05\u695a\u4e86\u3002</p><p>\u4e4b\u6240\u4ee5\u9020\u6210\u8fd9\u79cd\u72b6\u51b5\uff0c\u662f\u56e0\u4e3a\u8428\u9a6c\u65af\u7279\u5148\u4e3a\u9b54\u6cd5\u5973\u795e\u9009\u6c11\uff0c\u540e\u53c8\u53db\u6559\u800c\u51fa\uff0c\u521b\u7acb\u9f99\u5deb\u6559\uff0c\u5f04\u5f97\u5929\u6012\u4eba\u6028\u3002\u4ed6\u88ab\u8f70\u6740\u4e4b\u540e\uff0c\u5deb\u5e08\u4e4b\u795e\u963f\u7956\u65af\u4fbf\u5949\u9b54\u6cd5\u5973\u795e\u7684\u610f\u65e8\uff0c\u6307\u793a\u6559\u4f1a\u62b9\u53bb\u4e00\u5207\u6709\u5173\u8428\u9a6c\u65af\u7279\u7684\u4fe1\u606f\uff0c\u5e76\u7981\u6b62\u5728\u4efb\u4f55\u9b54\u6cd5\u4e66\u7c4d\u4e2d\u63d0\u8d77\u3002\u70db\u5821\u662f\u4e2d\u7acb\u4e4b\u5730\uff0c\u5c0a\u5949\u7684\u662f\u77e5\u8bc6\u4e4b\u795e\u6b27\u683c\u739b\uff0c\u6240\u4ee5\u80fd\u591f\u6536\u96c6\u5230\u4e00\u4e9b\u6b8b\u7ae0\u788e\u7247\uff0c\u4fdd\u7559\u4e0b\u6765\uff0c\u6362\u4e86\u5176\u4ed6\u56fe\u4e66\u9986\uff0c\u57fa\u672c\u5c31\u662f\u6ca1\u6709\u3002\u548c\u8428\u9a6c\u65af\u7279\u540c\u65f6\u4ee3\u7684\uff0c\u66fe\u7ecf\u6709\u8fc7\u5bc6\u5207\u4ea4\u5f80\u7684\u4eba\uff0c\u5982\u4eca\u4e0d\u662f\u6545\u53bb\uff0c\u4fbf\u662f\u6b23\u5e03\u548c\u8475\u9732\u8fd9\u79cd\u5973\u795e\u9009\u6c11\uff0c\u81ea\u7136\u66f4\u4e0d\u4f1a\u8f7b\u6613\u5230\u5904\u4e71\u8bf4\u2014\u2014\u5176\u5b9e\u8981\u8bf4\u8d77\u6765\uff0c\u6b23\u5e03\u5df2\u7ecf\u7b97\u662f\u6027\u683c\u53db\u9006\uff0c\u65e0\u6240\u987e\u5fcc\uff0c\u6240\u4ee5\u8fd8\u80af\u8c08\u8bba\uff0c\u5982\u679c\u6362\u4e86\u8c28\u614e\u7684\u8475\u9732\uff0c\u53ea\u6015\u9664\u4e86\u5fc5\u8981\u4fe1\u606f\u4e4b\u5916\uff0c\u5176\u4ed6\u534a\u53e5\u4e0d\u63d0\u3002</p><p>\u5f53\u7136\uff0c\u5b98\u65b9\u5386\u53f2\u90fd\u88ab\u9500\u6bc1\u6216\u7981\u6b62\uff0c\u79c1\u4e0b\u4f20\u8a00\u603b\u8fd8\u662f\u6709\u7684\uff0c\u867d\u7136\u5927\u591a\u5185\u5bb9\u6df7\u4e71\uff0c\u5f7c\u6b64\u51b2\u7a81\uff0c\u4f46\u4e5f\u4e0d\u7b49\u4e8e\u5c31\u5b8c\u5168\u6ca1\u6709\u4efb\u4f55\u771f\u5b9e\u7684\u4e1c\u897f\u3002\u5728\u6240\u6709\u7684\u5c0f\u9053\u6d88\u606f\u91cc\uff0c\u51e1\u662f\u6d89\u53ca\u5230\u8428\u9a6c\u65af\u7279\u7684\u611f\u60c5\u7ecf\u5386\u7684\uff0c\u5168\u90fd\u4f17\u53e3\u4e00\u8bcd\u5730\u6307\u5411\u94f6\u6708\u57ce\u57ce\u4e3b\u827e\u62c9\u65af\u5353\uff0c\u5979\u88ab\u8ba4\u4e3a\u66fe\u7ecf\u662f\u8428\u9a6c\u65af\u7279\u7684\u60c5\u4eba\u2014\u2014\u5e76\u4e14\u662f\u8428\u9a6c\u65af\u7279\u540e\u6765\u80cc\u53db\u6559\u4f1a\u7684\u6700\u91cd\u8981\u539f\u56e0\u3002</p><p>\u827e\u62c9\u65af\u5353\u4e5f\u662f\u4e03\u59d0\u59b9\u4e4b\u4e00\uff0c\u6392\u884c\u7b2c\u4e8c\uff0c\u6b23\u5e03\u7684\u59d0\u59d0\uff0c\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\uff0c\u4e0e\u8428\u9a6c\u65af\u7279\u81ea\u7136\u4e5f\u662f\u591a\u6709\u4ea4\u5f80\u7684\u3002\u548c\u6b23\u5e03\u7684\u53db\u9006\u3001\u66b4\u8e81\u3001\u4e0d\u4fee\u8fb9\u5e45\u4e0d\u540c\uff0c\u827e\u62c9\u65af\u5353\u662f\u516c\u8ba4\u7684\u6c14\u8d28\u9ad8\u8d35\u3001\u7f8e\u4e3d\u5178\u96c5\uff0c\u88ab\u79f0\u4e3a\u201c\u5149\u8f89\u5973\u58eb\u201d\uff0c\u636e\u8bf4\u8428\u9a6c\u65af\u7279\u4e00\u89c1\u5230\u5979\u5c31\u88ab\u8ff7\u4f4f\u4e86\uff0c\u4ece\u6b64\u62dc\u5012\u5728\u77f3\u69b4\u88d9\u4e0b\uff0c\u800c\u827e\u62c9\u65af\u5353\u4e5f\u88ab\u8428\u9a6c\u65af\u7279\u7684\u5929\u624d\u6240\u5438\u5f15\uff0c\u4e24\u4eba\u5f88\u5feb\u5760\u5165\u7231\u6cb3\u3002\u53ea\u662f\u597d\u666f\u4e0d\u957f\uff0c\u4e24\u4f4d\u9009\u6c11\u7684\u7231\u60c5\u5f88\u5feb\u7834\u88c2\u4e86\uff0c\u8428\u9a6c\u65af\u7279\u81ea\u6b64\u4e4b\u540e\u5f7b\u5e95\u5760\u5165\u9ed1\u6697\uff0c\u6700\u7ec8\u6210\u4e3a\u88ab\u8f70\u6740\u7684\u5927\u53cd\u6d3e\u3002</p><p>\u81f3\u4e8e\u7231\u60c5\u4e3a\u4ec0\u4e48\u7834\u88c2\uff0c\u539f\u56e0\u5c31\u4f17\u8bf4\u7eb7\u7ead\u4e86\uff0c\u8f83\u4e3a\u6709\u5f71\u54cd\u529b\u7684\u8bf4\u6cd5\u662f\u56e0\u4e3a\u827e\u62c9\u65af\u5353\u9b45\u529b\u8d85\u7fa4\uff0c\u60c5\u4eba\u4f17\u591a\uff0c\u5b50\u55e3\u4f17\u591a\uff0c\u4ec5\u4ec5\u513f\u5b50\u5c31\u8d85\u8fc7\u5341\u4e8c\u4e2a\uff0c\u800c\u8428\u9a6c\u65af\u7279\u5374\u662f\u4e2a\u6709\u6781\u5f3a\u7684\u5360\u6709\u6b32\u7684\u4eba\uff0c\u4ed6\u8bd5\u56fe\u72ec\u5360\u548c\u63a7\u5236\u827e\u62c9\u65af\u5353\uff0c\u8fd9\u79cd\u51b2\u7a81\u6700\u7ec8\u5bfc\u81f4\u53cc\u65b9\u5173\u7cfb\u6076\u5316\uff0c\u76f4\u81f3\u5206\u624b\u3002</p><p>\u56e0\u4e3a\u662f\u5c0f\u9053\u6d88\u606f\uff0c\u743c\u6069\u867d\u7136\u542c\u95fb\u8fc7\uff0c\u4f46\u4ece\u6765\u6ca1\u592a\u5f53\u56de\u4e8b\u3002\u4eca\u5929\u6b23\u5e03\u65e0\u610f\u95f4\u9732\u51fa\u6765\u7684\u53e3\u98ce\uff0c\u5012\u662f\u8bc1\u5b9e\u4e86\u827e\u62c9\u65af\u5353\u548c\u8428\u9a6c\u65af\u7279\u786e\u5b9e\u662f\u60c5\u4eba\u2014\u2014\u81f3\u5c11\u66fe\u7ecf\u662f\uff0c\u8fd9\u4e5f\u6ca1\u4ec0\u4e48\u53ef\u60ca\u8bb6\u7684\u3002\u4f46\u73b0\u5728\u51db\u8dd1\u8fdb\u6765\uff0c\u8bf4\u8428\u9a6c\u65af\u7279\u7684\u7231\u60c5\u6545\u4e8b\uff0c\u5973\u4e3b\u89d2\u5c45\u7136\u4e0d\u662f\u827e\u62c9\u65af\u5353\u800c\u662f\u9b54\u6cd5\u5973\u795e\uff0c\u8fd9\u4fbf\u5b9e\u5728\u8ba9\u4eba\u89c9\u5f97\u532a\u5937\u6240\u601d\uff0c\u751a\u81f3\u6478\u4e0d\u7740\u5934\u8111\u4e86\u3002</p><p>\u201c\u8fd9\u548c\u9b54\u6cd5\u5973\u795e\u53c8\u600e\u4e48\u626f\u4e0a\u4e86\u5173\u7cfb\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u51db\u4e8e\u662f\u5f88\u5f97\u610f\uff0c\u5728\u65c1\u8fb9\u5750\u4e0b\u6765\uff0c\u53c8\u62ff\u8d77\u53e6\u5916\u4e00\u676f\u679c\u6c41\uff0c\u4e00\u8fb9\u559d\u4e00\u8fb9\u5f00\u59cb\u8f6c\u8ff0\u5979\u521a\u624d\u4ece\u6b23\u5e03\u90a3\u91cc\u6253\u542c\u5230\u7684\u516b\u5366\u3002</p><p>\u201c\u5176\u5b9e\u5728\u8428\u9a6c\u65af\u7279\u5728\u9047\u4e0a\u827e\u62c9\u65af\u5353\u4e4b\u524d\uff0c\u4ed6\u5c31\u5df2\u7ecf\u6709\u8fc7\u4e00\u6b21\u7231\u60c5\uff0c\u5bf9\u8c61\u5c31\u662f\u9b54\u6cd5\u5973\u795e\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u90a3\u662f\u5728\u4ed6\u6210\u4e3a\u9009\u6c11\u7684\u65f6\u5019\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u201c\u5728\u8428\u9a6c\u65af\u7279\u4e94\u5341\u5c81\u7684\u65f6\u5019\uff0c\u4ed6\u63a5\u5230\u4e86\u4e00\u4efd\u795e\u79d8\u7684\u9080\u8bf7\uff0c\u8fd9\u6539\u53d8\u4e86\u4ed6\u4ee5\u540e\u7684\u547d\u8fd0\u3002\u6309\u7167\u6307\u793a\uff0c\u8428\u9a6c\u65af\u7279\u524d\u5f80\u9634\u5f71\u8c37\u4e2d\u4e00\u5904\u9690\u79d8\u5730\u70b9\uff0c\u5728\u90a3\u91cc\u4ed6\u89c1\u5230\u4e86\u9b54\u6cd5\u5973\u795e\u7684\u5316\u8eab\uff0c\u5e76\u4e14\u5171\u540c\u5ea6\u8fc7\u4e86\u5341\u5929\uff0c\u6700\u7ec8\u6210\u4e3a\u9009\u6c11\u2026\u2026\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u77e5\u9053\u554a\u3002\u201d</p><p>\u201c\u8010\u5fc3\u70b9\uff0c\u542c\u6211\u8bf4\u5b8c\uff0c\u8fd9\u8fd8\u6ca1\u5230\u91cd\u70b9\u5462\uff0c\u201d\u51db\u5f88\u4e0d\u6ee1\u5730\u77aa\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u95ee\u9898\u7684\u5173\u952e\u4e0d\u662f\u8428\u9a6c\u65af\u7279\u6210\u4e3a\u9009\u6c11\uff0c\u800c\u662f\u5728\u8fd9\u5341\u5929\u91cc\uff0c\u4ed6\u7231\u4e0a\u4e86\u9b54\u6cd5\u5973\u795e\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u5f53\u7136\u662f\u771f\u7684\uff0c\u8fd9\u662f\u6211\u8001\u5e08\u521a\u521a\u4eb2\u53e3\u8bf4\u7684\uff0c\u201d\u51db\u5f97\u610f\u5730\u8bf4\uff0c\u201c\u8428\u9a6c\u65af\u7279\u6765\u5230\u9634\u5f71\u8c37\uff0c\u5973\u795e\u964d\u4e34\u5728\u4ed6\u7684\u9762\u524d\uff0c\u8428\u9a6c\u65af\u7279\u767b\u65f6\u610f\u4e71\u795e\u8ff7\uff0c\u65e0\u6cd5\u81ea\u62d4\uff0c\u5f53\u573a\u5c31\u8db4\u5728\u5973\u795e\u811a\u4e0b\u75db\u54ed\u8d77\u6765\u3002\u5973\u795e\u628a\u4ed6\u6276\u8d77\u6765\uff0c\u8ba9\u4ed6\u7684\u773c\u775b\u6b63\u5bf9\u81ea\u5df1\u7684\u76ee\u5149\uff0c\u6df1\u6df1\u51dd\u89c6\uff0c\u7136\u540e\u628a\u4ed6\u62e5\u5165\u6000\u4e2d\u2026\u2026\u201d</p><p>\u201c\u90a3\u4e2a\uff0c\u51db\uff0c\u4f60\u8bf4\u5f97\u662f\u4e0d\u662f\u592a\u5938\u5f20\u4e86\u3002\u201d</p><p>\u201c\u96be\u9053\u4f60\u4eec\u89c9\u5f97\u8fd9\u4e0d\u662f\u5f88\u6d6a\u6f2b\u5417\uff1f\u201d</p><p>\u201c\u6d6a\u6f2b\u662f\u6ca1\u9519\uff0c\u4f46\u4f60\u786e\u5b9a\u8fd9\u771f\u662f\u4f60\u8001\u5e08\u7684\u539f\u8bdd\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u662f\uff0c\u6709\u4e9b\u662f\u6211\u81ea\u5df1\u7684\u8054\u60f3\u53d1\u6325\u548c\u827a\u672f\u52a0\u5de5\u4e86\uff0c\u4f46\u5927\u4f53\u4e0a\u5c31\u662f\u8fd9\u6837\u5566\uff0c\u522b\u8ba1\u8f83\uff0c\u522b\u6253\u5c94\uff0c\u201d\u51db\u6325\u6325\u624b\uff0c\u201c\u603b\u4e4b\uff0c\u5973\u795e\u548c\u8428\u9a6c\u65af\u7279\u4e00\u89c1\u949f\u60c5\uff0c\u5760\u5165\u7231\u6cb3\uff0c\u5979\u4eec\u5728\u4e00\u8d77\u6574\u6574\u5ea6\u8fc7\u4e86\u5341\u5929\u2014\u2014\u4f60\u8981\u77e5\u9053\uff0c\u5973\u795e\u662f\u4ee5\u5316\u8eab\u5f62\u6001\u964d\u4e34\u7684\uff0c\u5e76\u975e\u5723\u8005\u9644\u4f53\uff0c\u5341\u5929\u5df2\u7ecf\u662f\u6781\u9650\u4e86\uff0c\u53ef\u89c1\u4ed6\u4eec\u5f53\u65f6\u7684\u4eb2\u5bc6\u7a0b\u5ea6\u2026\u2026\u201d</p><p>\u201c\u6253\u4f4f\uff0c\u201d\u743c\u6069\u4e0d\u5f97\u4e0d\u5236\u6b62\u5c0f\u5973\u5b69\u5b50\u7684\u6d6a\u6f2b\u53d1\u6325\uff0c\u201c\u8bf4\u6b63\u7ecf\u7684\u3002\u201d</p><p>\u201c\u8fd9\u5c31\u662f\u6b63\u7ecf\u7684\uff0c\u8428\u9a6c\u65af\u7279\u7231\u4e0a\u4e86\u5973\u795e\uff0c\u5973\u795e\u663e\u7136\u5bf9\u4ed6\u4e5f\u9887\u5177\u597d\u611f\u3002\u7136\u540e\u4ed6\u88ab\u8d50\u4e88\u94f6\u706b\uff0c\u6210\u4e3a\u9009\u6c11\uff0c\u90a3\u4e9b\u4ece\u5c0f\u5c31\u5728\u6298\u78e8\u4ed6\u7684\u5404\u79cd\u75be\u75c5\u5168\u90fd\u88ab\u6cbb\u6108\uff0c\u8428\u9a6c\u65af\u7279\u611f\u52a8\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u4ed6\u628a\u94f6\u706b\u5f53\u4f5c\u5973\u795e\u7684\u5b9a\u60c5\u4fe1\u7269\uff0c\u751a\u81f3\u5929\u771f\u5730\u8ba4\u4e3a\u4ed6\u4f1a\u6210\u4e3a\u5973\u795e\u7684\u4e08\u592b\u2026\u2026\u201d</p><p>\u201c\u53ef\u662f\u5973\u795e\u65e9\u5c31\u5df2\u7ecf\u6709\u6b63\u5f0f\u7684\u914d\u5076\u4e86\u5440\uff0c\u201d\u6885\u83f2\u65af\u5947\u602a\uff0c\u201c\u65e9\u5728\u5dee\u4e0d\u591a\u4e00\u5343\u524d\uff0c\u5979\u5c31\u5df2\u7ecf\u9009\u62e9\u4e86\u963f\u7956\u65af\u4f5c\u4e3a\u5979\u7684\u4e08\u592b\uff0c\u8fd9\u70b9\u5927\u5bb6\u90fd\u77e5\u9053\u5427\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u6240\u4ee5\u8bf4\u4ed6\u5929\u771f\u561b\uff0c\u201d\u51db\u6447\u5934\uff0c\u201c\u6216\u8005\u8bf4\u88ab\u7231\u60c5\u51b2\u660f\u5934\u8111\u7684\u4eba\u662f\u6ca1\u6709\u7406\u667a\u7684\u3002\u4ed6\u6210\u4e3a\u9009\u6c11\u4e4b\u540e\uff0c\u53bb\u4e86\u9634\u5f71\u8c37\u5927\u8d24\u8005\u4f0a\u5c14\u660e\u65af\u7279\u90a3\u91cc\uff0c\u5b66\u4e60\u5982\u4f55\u5177\u4f53\u8fd0\u7528\u94f6\u706b\u3002\u4ed6\u4eec\u7684\u5173\u7cfb\u5904\u5f97\u975e\u5e38\u7cdf\u7cd5\uff0c\u8428\u9a6c\u65af\u7279\u4f9d\u65e7\u505a\u7740\u4e0d\u5207\u5b9e\u9645\u7684\u767d\u65e5\u68a6\uff0c\u800c\u4f0a\u5c14\u660e\u65af\u7279\u5bf9\u6b64\u975e\u5e38\u4e0d\u4ee5\u4e3a\u7136\u3002\u540e\u6765\u6709\u4e00\u6b21\uff0c\u4f0a\u5c14\u660e\u65af\u7279\u7ec8\u4e8e\u6253\u7834\u4e86\u8428\u9a6c\u65af\u7279\u7684\u7f8e\u68a6\uff0c\u76f4\u622a\u4e86\u5f53\u5730\u544a\u8bc9\u4ed6\uff1a\u963f\u7956\u65af\u65e9\u5c31\u5df2\u7ecf\u662f\u5973\u795e\u7684\u4e08\u592b\uff0c\u8fd9\u70b9\u6839\u672c\u4e0d\u53ef\u80fd\u6539\u53d8\u6216\u52a8\u6447\uff0c\u4e5f\u5c31\u662f\u8bf4\u8428\u9a6c\u65af\u7279\u662f\u7edd\u65e0\u5e0c\u671b\u7684\uff0c\u529d\u4ed6\u8d81\u65e9\u6253\u6d88\u4e0d\u5207\u5b9e\u9645\u7684\u5e7b\u60f3\u3002\u201d</p><p>\u201c\u4ed6\u5e61\u7136\u9192\u609f\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u4ed6\u5927\u53d1\u813e\u6c14\uff0c\u5f88\u5feb\u5c31\u79bb\u5f00\u4e86\u9634\u5f71\u8c37\uff0c\u7136\u540e\u4ed6\u7684\u5fc3\u667a\u5c31\u5f00\u59cb\u53d8\u5f97\u626d\u66f2\u2014\u2014\u51c6\u786e\u5730\u8bf4\u662f\u6bd4\u4ee5\u524d\u66f4\u52a0\u626d\u66f2\uff0c\u66f4\u52a0\u75af\u72c2\uff0c\u800c\u4e14\u5404\u79cd\u75be\u75c5\u4e5f\u90fd\u590d\u53d1\u4e86\u3002\u201d</p><p>\u201c\u75be\u75c5\u590d\u53d1\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u636e\u6211\u8001\u5e08\u8bf4\uff0c\u5979\u6709\u4e00\u6b21\u53bb\u62dc\u8bbf\u4f0a\u5c14\u660e\u65af\u7279\uff0c\u90a3\u65f6\u5019\u8428\u9a6c\u65af\u7279\u521a\u521a\u6210\u4e3a\u9009\u6c11\uff0c\u8fd8\u5728\u9634\u5f71\u8c37\u4e2d\u5b66\u4e60\u3002\u8001\u5e08\u89c1\u5230\u4e86\u8428\u9a6c\u65af\u7279\uff0c\u90a3\u65f6\u5019\u4ed6\u65e0\u8bba\u8eab\u4f53\u8fd8\u662f\u7cbe\u795e\u72b6\u51b5\u90fd\u975e\u5e38\u826f\u597d\uff0c\u5168\u65e0\u75c5\u75c7\uff0c\u5b8c\u5168\u7b26\u5408\u4e00\u4e2a\u9009\u6c11\u7684\u6b63\u5e38\u6a21\u6837\u3002\u540e\u6765\u8428\u9a6c\u65af\u7279\u79bb\u5f00\u9634\u5f71\u8c37\u4e4b\u540e\uff0c\u5927\u6982\u8fc7\u4e86\u4e00\u5e74\uff0c\u8001\u5e08\u5076\u7136\u649e\u89c1\u4ed6\uff0c\u53d1\u73b0\u4ed6\u53c8\u5f7b\u5e95\u57ae\u4e86\u4e0b\u6765\uff0c\u5c31\u662f\u4eca\u5929\u6211\u4eec\u770b\u5230\u7684\u8fd9\u526f\u6837\u5b50\u3002\u201d</p><p>\u201c\u7231\u60c5\u771f\u662f\u6298\u78e8\u4eba\u554a\u3002\u201d\u743c\u6069\u62ff\u8fc7\u51db\u4e00\u5f00\u59cb\u559d\u679c\u6c41\u7684\u676f\u5b50\uff08\u90a3\u662f\u4ed6\u7684\uff0c\u73b0\u5728\u51db\u62ff\u7740\u7684\u662f\u6885\u83f2\u65af\u7684\uff09\uff0c\u7ed9\u81ea\u5df1\u5012\u4e0a\uff0c\u611f\u53f9\u7740\u3002</p><p>\u201c\u8bf4\u5f97\u4f60\u597d\u50cf\u5386\u7ecf\u6ca7\u6851\u4f3c\u7684\uff0c\u201d\u6885\u83f2\u65af\u7b11\u7740\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u7136\u540e\u5462\uff0c\u51db\uff0c\u90a3\u827e\u62c9\u65af\u5353\u53c8\u662f\u600e\u4e48\u56de\u4e8b\u3002\u201d</p><p>\u201c\u540e\u6765\u8428\u9a6c\u65af\u7279\u9047\u5230\u4e86\u827e\u62c9\u65af\u5353\uff0c\u4ed6\u4e00\u89c1\u503e\u5fc3\uff0c\u559c\u6b22\u4e0a\u4e86\u3002\u7136\u540e\u5927\u5bb6\u611f\u60c5\u4e0d\u5408\u5206\u624b\uff0c\u5c31\u662f\u8fd9\u6837\u4e86\u3002\u201d</p><p>\u201c\u53c8\u4e00\u6b21\u7231\u60c5\u5931\u8d25\u3002\u201d\u743c\u6069\u8bc4\u4ef7\u3002</p><p>\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8bf4\u53c8\u5462\u3002\u201d</p><p>\u201c\u5514\uff1f\u8fd9\u4e0d\u662f\u7b2c\u4e8c\u6b21\u5417\uff1f\u201d</p><p>\u201c\u8fd9\u5c31\u662f\u8fd9\u4e2a\u6545\u4e8b\u6700\u5173\u952e\u7684\u5730\u65b9\u4e86\uff0c\u201d\u51db\u6d0b\u6d0b\u5f97\u610f\u5730\u5356\u7740\u5173\u5b50\uff0c\u201c\u6709\u6ca1\u6709\u5174\u8da3\u542c\u3002\u201d</p><p>\u201c\u8bf4\u5427\u3002\u201d</p><p>\u201c\u90a3\u4f60\u53eb\u6211\u4e00\u58f0\u59d0\u59d0\u3002\u201d</p><p>\u201c\u5657\uff01\u201d</p><p>\u743c\u6069\u6b63\u5728\u559d\u679c\u6c41\uff0c\u5dee\u70b9\u4e00\u53e3\u545b\u4e86\u51fa\u6765\uff0c\u201c\u4f60\uff1f\u4f60\u54ea\u91cc\u50cf\u59d0\u59d0\u4e86\uff0c\u5f53\u6211\u59b9\u59b9\u8fd8\u5dee\u4e0d\u591a\u3002\u201d</p><p>\u201c\u5207\uff0c\u6211\u6bd4\u4f60\u8fd8\u5927\u4e00\u5c81\u5462\uff0c\u600e\u4e48\u4e0d\u80fd\u505a\u4f60\u59d0\u59d0\u3002\u201d</p><p>\u201c\u59d0\u59d0\u770b\u7684\u4e0d\u662f\u5e74\u9f84\uff0c\u662f\u6c14\u8d28\uff0c\u5c31\u50cf\u4f60\u548c\u827e\u5f25\u8587\u7ad9\u5728\u4e00\u8d77\uff0c\u4fdd\u8bc1\u4eba\u4eba\u90fd\u8ba4\u4e3a\u4f60\u662f\u59b9\u59b9\u3002\u201d</p><p>\u6885\u83f2\u65af\u4e00\u76f4\u5728\u5fae\u5fae\u7b11\u7740\u65c1\u89c2\uff0c\u7a81\u7136\u8f7b\u8f7b\u5f00\u53e3\uff0c\u201c\u4e4b\u6240\u4ee5\u4e0d\u7b97\u7b2c\u4e8c\u6b21\uff0c\u662f\u56e0\u4e3a\u8428\u9a6c\u65af\u7279\u6839\u672c\u5c31\u662f\u628a\u827e\u62c9\u65af\u5353\u5f53\u4f5c\u4e86\u5973\u795e\u7684\u66ff\u4ee3\u54c1\u5427\u3002\u201d</p><p>\u201c\u554a\uff0c\u201d\u51db\u60ca\u8bb6\uff0c\u201c\u827e\u5f25\u8587\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d</p><p>\u201c\u731c\u7684\uff0c\u8428\u9a6c\u65af\u7279\u660e\u663e\u662f\u4e2a\u504f\u6267\u3001\u987d\u56fa\uff0c\u5176\u5b9e\u53c8\u975e\u5e38\u8106\u5f31\u7684\u4eba\uff0c\u8fd8\u5e26\u6709\u660e\u663e\u7684\u5929\u771f\u548c\u5b69\u5b50\u6c14\uff0c\u4e0d\u80af\u627f\u8ba4\u73b0\u5b9e\u3002\u8fd9\u79cd\u4eba\u4e00\u65e6\u8ba4\u51c6\u4e86\u67d0\u4ef6\u4e1c\u897f\uff0c\u90a3\u5c31\u5f88\u96be\u8f7b\u6613\u6492\u624b\u6216\u6539\u53d8\u3002\u800c\u4e14\u6211\u4e5f\u542c\u5230\u8fc7\u6709\u79cd\u8bf4\u6cd5\uff1a\u827e\u62c9\u65af\u5353\u662f\u6700\u50cf\u5973\u795e\u7684\u51e1\u4eba\u3002\u201d</p><p>\u51db\u53f9\u6c14\uff0c\u201c\u827e\u5f25\u8587\uff0c\u4f60\u771f\u806a\u660e\uff0c\u731c\u5f97\u4e00\u70b9\u90fd\u6ca1\u9519\uff0c\u8428\u9a6c\u65af\u7279\u786e\u5b9e\u628a\u827e\u62c9\u65af\u5353\u5f53\u4f5c\u4e86\u9b54\u6cd5\u5973\u795e\u7684\u66ff\u8eab\u2014\u2014\u5176\u5b9e\u8fd9\u624d\u662f\u4ed6\u4eec\u5173\u7cfb\u6700\u7ec8\u7834\u88c2\u7684\u771f\u6b63\u539f\u56e0\u3002\u6240\u4ee5\u8bf4\uff0c\u8428\u9a6c\u65af\u7279\u7684\u7231\u60c5\u6545\u4e8b\u91cc\uff0c\u5176\u5b9e\u6839\u672c\u5c31\u6ca1\u6709\u827e\u62c9\u65af\u5353\uff0c\u6709\u7684\u53ea\u662f\u9b54\u6cd5\u5973\u795e\u548c\u6700\u8fd1\u4f3c\u9b54\u6cd5\u5973\u795e\u7684\u66ff\u8eab\u3002\u201d</p><p>\u6885\u83f2\u65af\u6447\u6447\u5934\u3002</p><p>\u201c\u4ed6\u592a\u5929\u771f\u4e86\uff0c\u201d\u5723\u6b66\u58eb\u8bc4\u4ef7\uff0c\u201c\u5c45\u7136\u8ba4\u4e3a\u795e\u660e\u4f1a\u7231\u4e0a\u51e1\u4eba\uff0c\u8fd9\u5728\u5386\u53f2\u4e0a\u662f\u4ece\u65e0\u5148\u4f8b\u7684\u3002\u201d</p><p>\u201c\u800c\u4e14\u4ed6\u8ba4\u4e3a\u5973\u795e\u4e5f\u53ea\u7231\u4ed6\u4e00\u4e2a\u4eba\u3002\u201d</p><p>\u201c\u90a3\u66f4\u662f\u5e7c\u7a1a\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u795e\u7947\u662f\u534a\u4eba\u683c\u534a\u89c4\u5219\u7684\u5b58\u5728\uff0c\u4f46\u5386\u53f2\u8d8a\u60a0\u4e45\u7684\u795e\u7947\uff0c\u4eba\u683c\u5c31\u8d8a\u88ab\u89c4\u5219\u6240\u540c\u5316\uff0c\u9b54\u6cd5\u5973\u795e\u662f\u521b\u4e16\u4e4b\u521d\u8bde\u751f\u7684\u795e\u660e\uff0c\u54ea\u91cc\u80fd\u548c\u540e\u6765\u8fd9\u4e9b\u51e1\u4eba\u5c01\u795e\u8005\u76f8\u6bd4\u3002\u800c\u4e14\u5728\u4ed6\u4e4b\u524d\uff0c\u5973\u795e\u5c31\u5df2\u7ecf\u6311\u9009\u4e86\u963f\u7956\u65af\u4f5c\u4e3a\u6b63\u5f0f\u914d\u5076\uff0c\u5728\u6b64\u4e4b\u540e\uff0c\u5979\u53c8\u548c\u51e1\u4eba\u751f\u4e0b\u4e86\u4e03\u59d0\u59b9\uff0c\u81f3\u4e8e\u5979\u548c\u5927\u8d24\u8005\u4f0a\u5c14\u660e\u65af\u7279\u7684\u5173\u7cfb\u66f4\u662f\u4f17\u6240\u5468\u77e5\u2026\u2026\u8fd9\u4e9b\u90fd\u6e05\u6e05\u695a\u695a\u6446\u5728\u4ed6\u9762\u524d\uff0c\u4ed6\u5c45\u7136\u8fd8\u4f1a\u62b1\u8fd9\u79cd\u4e0d\u5207\u5b9e\u9645\u7684\u5e7b\u60f3\uff0c\u771f\u662f\u6789\u79f0\u9b54\u6cd5\u53f2\u4e0a\u7684\u5929\u624d\u3002\u201d</p><p>\u201c\u6709\u65f6\u5019\uff0c\u5929\u624d\u4e5f\u5c31\u662f\u767d\u75f4\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u7684\u8fd9\u53e5\u603b\u7ed3\u9648\u8bcd\u5f97\u5230\u4e86\u4e24\u4f4d\u5973\u5b69\u7684\u4e00\u81f4\u8ba4\u540c\uff0c\u4e8b\u5b9e\u4e5f\u786e\u5b9e\u5982\u6b64\u3002\u4f46\u95ee\u9898\u5728\u4e8e\uff1a\u8428\u9a6c\u65af\u7279\u6216\u8bb8\u5728\u611f\u60c5\u4e0a\u662f\u4e2a\u767d\u75f4\uff0c\u4f46\u5728\u9b54\u6cd5\u4e0a\u786e\u5b9e\u662f\u9707\u53e4\u70c1\u4eca\u7684\u5927\u5929\u624d\uff0c\u9a6c\u4e0a\u5c31\u8981\u548c\u8fd9\u79cd\u4eba\u4ea4\u624b\uff0c\u5927\u5bb6\u5fc3\u4e2d\u90fd\u4e0d\u514d\u6709\u4e9b\u5fd0\u5fd1\u3002\u6bd4\u8d77\u743c\u6069\uff0c\u51db\u8fd8\u8981\u62c5\u5fc3\u6b23\u5e03\u7684\u5b89\u5371\uff0c\u867d\u7136\u5979\u603b\u8bf4\u8fd9\u4e2a\u8001\u5e08\u70e6\uff0c\u4f46\u5176\u5b9e\u770b\u5f97\u51fa\u6765\uff0c\u5979\u5728\u5185\u5fc3\u8fd8\u662f\u5f88\u6709\u611f\u60c5\u7684\u3002</p><p>\u201c\u8428\u9a6c\u65af\u7279\u786e\u5b9e\u975e\u5e38\u5389\u5bb3\uff0c\u201d\u51db\u8bf4\uff0c\u795e\u8272\u95f4\u5f00\u59cb\u6709\u4e86\u4e9b\u5fe7\u8651\uff0c\u201c\u4ed6\u548c\u827e\u62c9\u65af\u5353\u5206\u624b\u4e4b\u540e\uff0c\u539f\u672c\u5df2\u7ecf\u5927\u6709\u597d\u8f6c\u7684\u8eab\u4f53\u548c\u7cbe\u795e\u72b6\u51b5\u518d\u5ea6\u6076\u5316\uff0c\u4eba\u53d8\u5f97\u66f4\u52a0\u75af\u72c2\uff0c\u4f46\u4e5f\u66f4\u5371\u9669\u66f4\u5f3a\u5927\u4e86\u3002\u540e\u6765\u4ed6\u5c45\u7136\u627e\u4e0a\u4e86\u827e\u62c9\u65af\u5353\uff0c\u5492\u9a82\u5979\uff0c\u88ad\u51fb\u5979\uff0c\u827e\u62c9\u65af\u5353\u731d\u4e0d\u53ca\u9632\uff0c\u4e00\u4e0a\u6765\u5c31\u53d7\u4e86\u91cd\u4f24\uff0c\u5e78\u597d\u6b64\u65f6\u53e6\u5916\u4e24\u4f4d\u9009\u6c11\u8d76\u5230\uff0c\u5c31\u662f\u6df1\u6c34\u57ce\u4e3b\u51ef\u5c14\u672c\u548c\u4ed6\u7684\u592b\u4eba\u83b1\u62c9\u3002\u201d</p><p>\u51ef\u5c14\u672c\u4e5f\u662f\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\u4e4b\u4e00\uff0c\u58f0\u671b\u8fd8\u5728\u4e03\u59d0\u59b9\u4e4b\u4e0a\uff0c\u548c\u5927\u8d24\u8005\u4f0a\u5c14\u660e\u65af\u7279\u76f8\u5f53\uff1b\u83b1\u62c9\u4e5f\u662f\u4e03\u59d0\u59b9\u4e4b\u4e00\uff0c\u662f\u51ef\u5c14\u672c\u7684\u592b\u4eba\uff0c\u8fd9\u6bb5\u5a5a\u59fb\u8fd8\u662f\u5965\u6c83\u4fc3\u6210\u7684\uff0c\u5018\u82e5\u4e0d\u662f\u90a3\u6b7b\u80d6\u5b50\u8272\u5fc3\u4e0d\u6b7b\uff0c\u8bd5\u56fe\u7528\u89d2\u4e4b\u51a0\u6765\u63a7\u5236\u83b1\u62c9\uff0c\u54ea\u91cc\u6709\u51ef\u5c14\u672c\u82f1\u96c4\u6551\u7f8e\u7684\u673a\u4f1a\u3002</p><p>\u4e09\u4f4d\u9009\u6c11\u8054\u624b\u5939\u653b\uff0c\u7ec8\u4e8e\u51fb\u8d25\u4e86\u8428\u9a6c\u65af\u7279\uff0c\u6b64\u65f6\u5deb\u5e08\u4e4b\u795e\u963f\u7956\u65af\u964d\u4e34\uff0c\u4ed6\u5949\u9b54\u6cd5\u5973\u795e\u7684\u547d\u4ee4\u524d\u6765\uff0c\u6536\u56de\u4e86\u8428\u9a6c\u65af\u7279\u7684\u94f6\u706b\uff0c\u5265\u593a\u4ed6\u9009\u6c11\u7684\u8eab\u5206\u3002\u8428\u9a6c\u65af\u7279\u5e76\u6ca1\u6709\u6b7b\u53bb\uff0c\u81ea\u6b64\u4e4b\u540e\u4fbf\u771f\u6b63\u5730\u5760\u5165\u9ed1\u6697\uff0c\u9500\u58f0\u533f\u8ff9\uff0c\u5341\u4e8c\u5e74\u4e4b\u540e\u4ed6\u518d\u5ea6\u51fa\u73b0\uff0c\u53d1\u5e03\u201c\u6b7b\u4ea1\u5de8\u9f99\u9884\u8a00\u201d\uff0c\u521b\u5efa\u9f99\u5deb\u6559\uff0c\u6700\u7ec8\u88ab\u6668\u66e6\u4e4b\u795e\u6740\u6b7b\u3002</p><p>\u4ece\u4e0a\u9762\u7684\u6218\u4f8b\u6765\u5206\u6790\uff0c\u867d\u7136\u8428\u9a6c\u65af\u7279\u662f\u7a81\u88ad\u5728\u5148\uff0c\u4e0a\u6765\u5c31\u91cd\u4f24\u4e86\u827e\u62c9\u65af\u5353\uff0c\u4f46\u6b64\u540e\u4ed6\u72ec\u529b\u8fce\u6218\u51ef\u5c14\u672c\u548c\u83b1\u62c9\uff0c\u5374\u662f\u8d27\u771f\u4ef7\u5b9e\u7684\u672c\u4e8b\u3002\u4e03\u59d0\u59b9\u4e4b\u4e2d\uff0c\u53ea\u6709\u56db\u4eba\u662f\u771f\u6b63\u7684\u5deb\u5e08\uff1a\u827e\u62c9\u65af\u5353\u3001\u83b1\u62c9\u3001\u6b23\u5e03\u548c\u5e0c\u4f26\uff0c\u5176\u4e2d\u5e0c\u4f26\u65e9\u5df2\u53bb\u4e16\uff0c\u5269\u4e0b\u7684\u4e09\u4eba\u5927\u81f4\u76f8\u5f53\uff0c\u6b23\u5e03\u540d\u58f0\u867d\u7136\u54cd\u4eae\uff0c\u672a\u89c1\u5f97\u80fd\u80dc\u8fc7\u83b1\u62c9\uff1b\u800c\u8475\u9732\u662f\u4e2a\u7267\u5e08\uff0c\u6b64\u65f6\u4e27\u5931\u4e86\u4f0a\u8389\u4e1d\u7fe0\u7684\u652f\u6301\uff0c\u66f4\u8fdc\u8fdc\u4e0d\u80fd\u548c\u51ef\u5c14\u672c\u76f8\u6bd4\u3002\u5c31\u7b97\u8bf4\u8428\u9a6c\u65af\u7279\u5931\u53bb\u4e86\u94f6\u706b\uff0c\u6b64\u6d88\u5f7c\u957f\u4e4b\u4e0b\uff0c\u4e5f\u672a\u5fc5\u6709\u51e0\u5206\u80dc\u7b97\u3002\u6b23\u5e03\u548c\u8475\u9732\u90fd\u662f\u90a3\u79cd\u4e45\u5c45\u9ad8\u4f4d\uff0c\u6781\u5ea6\u81ea\u4fe1\u7684\u4eba\uff0c\u65e0\u8bba\u906d\u9047\u4f55\u7b49\u5f3a\u654c\uff0c\u7167\u6837\u633a\u8eab\u800c\u4e0a\uff0c\u4e1d\u6beb\u4e0d\u4f1a\u76b1\u4e00\u76b1\u7709\u5934\uff0c\u51db\u5374\u4e0d\u80fd\u4e0d\u4e3a\u5979\u4eec\u62c5\u5fc3\u4e86\u3002</p><p>\u201c\u743c\u6069\uff0c\u201d\u51db\u6709\u4e9b\u72b9\u8c6b\uff0c\u201c\u6709\u4ef6\u4e8b\u60c5\u6211\u60f3\u6c42\u4f60\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d\u743c\u6069\u6709\u4e9b\u60ca\u8bb6\uff0c\u201c\u4f60\u8bf4\u5c31\u662f\u3002\u201d</p><p>\u201c\u4f60\u4e5f\u77e5\u9053\u7684\uff0c\u771f\u8981\u8bba\u6cd5\u672f\u6bd4\u62fc\uff0c\u6211\u7684\u8001\u5e08\u53ea\u6015\u4e5f\u672a\u5fc5\u80fd\u80dc\u8fc7\u8428\u9a6c\u65af\u7279\uff0c\u8475\u9732\u5c0f\u59d0\u66f4\u4e0d\u7528\u8bf4\uff0c\u771f\u6b63\u53ef\u4ee5\u501a\u9760\u7684\u5c31\u662f\u94f6\u706b\u3002\u4f46\u94f6\u706b\u4e5f\u5e76\u4e0d\u662f\u80fd\u65e0\u9650\u4f7f\u7528\u7684\uff0c\u5982\u679c\u4e00\u5f00\u59cb\u6d88\u8017\u5f97\u592a\u5389\u5bb3\uff0c\u540e\u9762\u649e\u4e0a\u8428\u9a6c\u65af\u7279\u2026\u2026\u201d</p><p>\u201c\u4f60\u662f\u8bf4\uff0c\u6539\u53d8\u6211\u4eec\u539f\u672c\u8bf4\u7684\u6bd4\u8d5b\u987a\u5e8f\uff0c\u6211\u548c\u4f60\u5148\u4e0a\u573a\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u6211\u662f\u8fd9\u4e48\u60f3\uff0c\u6240\u4ee5\u6765\u8ddf\u4f60\u5546\u91cf\u2026\u2026\u5f53\u7136\u4e86\uff0c\u201d\u51db\u8d76\u5feb\u89e3\u91ca\uff0c\u201c\u5176\u4ed6\u8fd8\u662f\u548c\u6211\u4eec\u4ee5\u524d\u8bf4\u7684\u90a3\u6837\uff0c\u5982\u679c\u60c5\u52bf\u4e0d\u5bf9\uff0c\u6211\u4eec\u53ef\u4ee5\u76f4\u63a5\u653e\u5f03\u9000\u51fa\uff0c\u518d\u8ba9\u8001\u5e08\u4e0a\uff0c\u5c31\u5f53\u662f\u5148\u63a2\u63a2\u5bf9\u624b\u7684\u5e95\u7ec6\u4e5f\u597d\u2026\u2026\u201d</p><p>\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u89c9\u5f97\u5979\u8bf4\u5f97\u4e5f\u6709\u9053\u7406\uff0c\u201c\u884c\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u4e5f\u5c31\u4e0d\u7528\u9884\u5b9a\u4ec0\u4e48\u51fa\u573a\u987a\u5e8f\u4e86\uff0c\u5230\u65f6\u5019\u770b\u60c5\u5f62\u518d\u8bf4\uff0c\u6709\u5408\u9002\u7684\u5bf9\u624b\u6211\u4eec\u5c31\u5148\u6253\u53d1\u5b8c\u4e8b\u3002\u201d</p><p>\u201c\u743c\u6069\u4f60\u592a\u597d\u4e86\uff0c\u8c22\u8c22\u4f60\uff01\u201d\u51db\u5174\u9ad8\u91c7\u70c8\u5730\u628a\u676f\u5b50\u4e00\u653e\uff0c\u6251\u8fc7\u6765\u5c31\u5728\u4ed6\u8138\u4e0a\u4eb2\u4e86\u4e00\u53e3\uff0c\u201c\u4f60\u771f\u662f\u4e2a\u597d\u4eba\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u4e2a\uff0c\u540e\u9762\u4e00\u53e5\u8bdd\u80fd\u4e0d\u80fd\u4e0d\u8981\u8bf4\uff0c\u6211\u5f88\u5fcc\u8bb3\u7684\u3002\u201d</p><p>\u51db\u563b\u563b\u7b11\u7740\uff0c\u7a81\u7136\u60f3\u8d77\u6885\u83f2\u65af\u8fd8\u5728\u65c1\u8fb9\uff0c\u6709\u4e9b\u5fc3\u865a\u5730\u8f6c\u8fc7\u5934\uff0c\u5374\u89c1\u5723\u6b66\u58eb\u6b63\u5728\u6162\u6162\u559d\u4e00\u676f\u725b\u5976\uff0c\u795e\u8272\u5e73\u6de1\uff0c\u4f3c\u4e4e\u5168\u7136\u6ca1\u770b\u89c1\u3002\u201c\u90a3\u5c31\u8fd9\u6837\u4e86\uff0c\u6211\u5148\u8d70\u4e86\uff0c\u4e0d\u6253\u6270\u4f60\u4eec\u3002\u201d\u51db\u6325\u6325\u624b\uff0c\u4e00\u6e9c\u70df\u53c8\u8dd1\u4e86\u51fa\u53bb\u3002</p><p>\u201c\u5979\u633a\u559c\u6b22\u4f60\u5462\u3002\u201d\u6885\u83f2\u65af\u653e\u4e0b\u676f\u5b50\uff0c\u8f7b\u8f7b\u8bf4\u3002</p><p>\u201c\u90a3\u4e2a\uff0c\u827e\u5f25\u8587\u2026\u2026\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u201d\u6885\u83f2\u65af\u5fae\u7b11\uff0c\u201c\u5982\u679c\u5979\u771f\u559c\u6b22\u4f60\uff0c\u6211\u4e5f\u4e0d\u4ecb\u610f\u2014\u2014\u53ea\u662f\u4f60\u6050\u6015\u6ca1\u8fd9\u4e2a\u672c\u4e8b\u4e86\u3002\u5979\u771f\u6b63\u559c\u6b22\u7684\uff0c\u662f\u90a3\u79cd\u9633\u5149\u7684\u3001\u6d3b\u6cfc\u7684\u3001\u5e7d\u9ed8\u6709\u8da3\u7684\u7537\u5b69\u5b50\uff0c\u50cf\u4f60\u8fd9\u6837\u6b7b\u677f\u7684\uff0c\u53ea\u6015\u6ca1\u5e0c\u671b\u3002\u201d</p><p>\u201c\u5514\uff0c\u6211\u4e5f\u4e0d\u81f3\u4e8e\u50cf\u4f60\u8bf4\u5f97\u90a3\u4e48\u5dee\u5427\u3002\u201d</p><p>\u201c\u4f60\u4ee5\u4e3a\u81ea\u5df1\u6709\u591a\u4f18\u79c0\u4e48\uff0c\u201d\u6885\u83f2\u65af\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u4e5f\u53ea\u6709\u6211\u8fd9\u79cd\u5fc3\u5730\u5584\u826f\u7684\u5973\u5b69\u5b50\u624d\u4f1a\u601c\u60af\u4f60\uff0c\u6015\u4f60\u6ca1\u4eba\u8981\uff0c\u51db\u53ef\u6ca1\u6211\u8fd9\u4e48\u597d\u5fc3\u3002\u201d</p><p>\u201c\u542c\u8d77\u6765\u6211\u771f\u5931\u8d25\u3002\u201d</p><p>\u6885\u83f2\u65af\u6ca1\u7406\u4ed6\uff0c\u53d6\u8fc7\u51db\u7b2c\u4e8c\u6b21\u62ff\u8d77\u7684\u679c\u6c41\u676f\u5b50\uff0c\u201c\u8fd8\u662f\u90a3\u574f\u6bdb\u75c5\uff0c\u201d\u5979\u53f9\u6c14\uff0c\u201c\u89c1\u4e86\u5c31\u76f4\u63a5\u62ff\u8d77\u6765\u559d\uff0c\u4e5f\u4e0d\u7ba1\u662f\u8c01\u7684\uff0c\u8fdf\u65e9\u6709\u4e00\u5929\u8981\u4e2d\u6bd2\u3002\u201d</p><p>\u201c\u4e2d\u6bd2\u5012\u4e0d\u81f3\u4e8e\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u522b\u88ab\u4eba\u4e0b\u5a9a\u836f\u5c31\u597d\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u5c31\u8fd9\u4e48\u5e72\u8fc7\u5417\u3002\u201d</p><p>\u201c\u90a3\u6b21\u4e0d\u80fd\u602a\u6211\u5427\uff0c\u672c\u6765\u662f\u7ed9\u4f60\u51c6\u5907\u7684\uff0c\u8c01\u77e5\u9053\u5979\u62ff\u8d77\u6765\u5c31\u559d\u2026\u2026\u201d</p><p>\u201c\u53cd\u6b63\u6700\u540e\u4f60\u662f\u5360\u4e86\u4fbf\u5b9c\u5427\uff0c\u90a3\u8fd8\u6709\u4ec0\u4e48\u610f\u89c1\u5462\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u5982\u679c\u8bf4\u6709\u4ec0\u4e48\u610f\u89c1\u7684\u8bdd\u2026\u2026\u90a3\u5c31\u662f\u80fd\u518d\u6765\u4e00\u6b21\u5c31\u597d\u4e86\u3002\u201d</p></article>"}}}]);
