(window.webpackJsonp=window.webpackJsonp||[]).push([[1531],{1554:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e5d\u8282 \u8fd0\u6c14",content:"<article><h2>\u7b2c\u4e09\u5341\u4e5d\u8282 \u8fd0\u6c14</h2><p>\u8fd9\u4e2a\u8d2a\u5fc3\u7684\u60f3\u6cd5\u7406\u6240\u5f53\u7136\u5730\u906d\u5230\u4e86\u6885\u83f2\u65af\u7684\u62b5\u5236\uff0c\u6240\u4ee5\u53ea\u80fd\u662f\u4e2a\u5962\u671b\u3002\u51db\u662f\u6885\u83f2\u65af\u7684\u631a\u53cb\uff0c\u662f\u5979\u771f\u6b63\u770b\u91cd\u7684\u4e3a\u6570\u4e0d\u591a\u7684\u51e0\u4e2a\u4eba\u4e4b\u4e00\uff0c\u4e0a\u6b21\u5728\u963f\u683c\u62c9\u9686\u90a3\u662f\u610f\u5916\uff0c\u6885\u83f2\u65af\u4e5f\u4e0d\u4f1a\u8bf4\u4ec0\u4e48\uff0c\u4f46\u5018\u82e5\u743c\u6069\u4e3b\u52a8\u53bb\u4e0b\u836f\uff0c\u53ea\u6015\u5c31\u4f1a\u6709\u5bb6\u5ead\u66b4\u529b\u8981\u53d1\u751f\u4e86\u3002</p><p>\u4e0e\u5176\u8003\u8651\u8fd9\u79cd\u4e0d\u5207\u5b9e\u9645\u7684\uff0c\u8fd8\u4e0d\u5982\u60f3\u60f3\u600e\u4e48\u5bf9\u6885\u83f2\u65af\u8fdb\u884c\u5fc5\u8981\u7684\u6559\u80b2\uff0c\u8ba9\u5979\u5728\u5e8a\u4e0a\u66f4\u52a0\u4e3b\u52a8\u8fd8\u66f4\u53ef\u884c\u4e9b\u3002\u5173\u4e8e\u8fd9\u4e2a\u95ee\u9898\uff0c\u743c\u6069\u4e0d\u662f\u5f88\u6709\u5934\u7eea\uff0c\u597d\u5728\u65c1\u8fb9\u5c31\u4f4f\u7740\u4e00\u4f4d\u4e13\u4e1a\u4eba\u58eb\uff0c\u6b63\u53ef\u4ee5\u8bf7\u6559\u3002</p><p>\u6885\u83f2\u65af\u7cbe\u795e\u4e0d\u4f73\uff0c\u5348\u9910\u540e\u53c8\u53bb\u4f11\u606f\uff0c\u743c\u6069\u5750\u5728\u5e8a\u8fb9\u6c99\u53d1\u4e0a\u7814\u8bfb\u9b54\u6cd5\u4e66\uff0c\u9759\u9759\u966a\u7740\u5979\u3002\u65f6\u95f4\u6162\u6162\u6d41\u901d\uff0c\u5927\u7ea6\u8fc7\u4e86\u4e24\u4e2a\u5c0f\u65f6\uff0c\u743c\u6069\u4ece\u5404\u79cd\u5492\u8bed\u3001\u7b26\u7eb9\u3001\u6cd5\u672f\u539f\u7406\u548c\u63cf\u8ff0\u6784\u6210\u7684\u6d77\u6d0b\u4e2d\u6323\u624e\u51fa\u6765\uff0c\u5408\u4e0a\u9b54\u6cd5\u4e66\uff0c\u8f6c\u5934\u770b\u89c1\u6885\u83f2\u65af\u8fd8\u5728\u5e8a\u4e0a\u6c89\u7761\uff0c\u4e0d\u53bb\u6253\u6270\uff0c\u6084\u6084\u8d70\u51fa\u623f\u95f4\uff0c\u5c06\u95e8\u63a9\u4e0a\u3002</p><p>\u838e\u73de\u514b\u4f4f\u7684\u623f\u95f4\u5c31\u5728\u9694\u58c1\uff0c\u743c\u6069\u8d70\u8fc7\u53bb\u8f7b\u8f7b\u6572\u95e8\uff0c\u201c\u8bf7\u8fdb\uff0c\u201d\u9b45\u9b54\u7684\u58f0\u97f3\u4ece\u91cc\u9762\u4f20\u51fa\uff0c\u6175\u61d2\u65e0\u529b\uff0c\u50cf\u662f\u6625\u7761\u65b9\u9192\u4f3c\u7684\uff0c\u201c\u95e8\u6ca1\u5173\u3002\u201d</p><p>\u743c\u6069\u63a8\u95e8\u8fdb\u5165\uff0c\u53d1\u73b0\u838e\u73de\u514b\u6b63\u5750\u5728\u5e8a\u5934\uff0c\u624b\u4e2d\u628a\u73a9\u7740\u4e00\u4e2a\u91d1\u707f\u707f\u7684\u4e1c\u897f\u3002\u743c\u6069\u8d70\u5230\u8fd1\u524d\u770b\u4e86\u770b\uff0c\u611f\u89c9\u50cf\u662f\u4e2a\u4e09\u68f1\u91d1\u9525\uff0c\u53c8\u50cf\u662f\u4e2a\u86fe\u7709\u523a\u4e4b\u7c7b\uff0c\u4e0a\u9762\u4f3c\u4e4e\u8fd8\u9690\u9690\u6cdb\u7740\u84dd\u5149\uff0c\u5f88\u53ef\u80fd\u6dec\u6bd2\u3002\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u4f60\u65b0\u4e70\u7684\u6b66\u5668\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u4f46\u8fd9\u4e1c\u897f\u600e\u4e48\u7528\uff1f\u201d\u743c\u6069\u5947\u602a\uff0c\u201c\u63e1\u67c4\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u201c\u4e0d\u9700\u8981\u63e1\u67c4\uff0c\u5b83\u672c\u6765\u5c31\u4e0d\u662f\u624b\u6301\u7684\u3002\u201d</p><p>\u201c\u4e0d\u7528\u624b\u6301\uff0c\u96be\u9053\u8fd8\u7528\u811a\u4e0d\u6210\u3002\u201d</p><p>\u201c\u5f53\u7136\u4e5f\u4e0d\u662f\uff0c\u662f\u7528\u5c3e\u5df4\u3002\u201d</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u201c\u5c3e\u5df4\uff1f\u201d</p><p>\u4eff\u4f5b\u4e3a\u4e86\u9a8c\u8bc1\u5979\u7684\u8bdd\uff0c\u4ece\u9b45\u9b54\u7684\u88d9\u4e0b\u7a81\u7136\u5192\u51fa\u4e00\u6761\u957f\u957f\u7684\u9ed1\u8272\u5c3e\u5df4\u6765\uff0c\u5728\u7a7a\u4e2d\u8fd8\u8f6c\u4e86\u4e2a\u5708\uff0c\u642d\u5728\u5979\u7684\u80a9\u8180\u4e0a\u3002\u5c3e\u5df4\u5149\u6ed1\u5982\u7ef3\uff0c\u672b\u7aef\u5c16\u9510\uff0c\u8fd8\u95ea\u7740\u91d1\u5c5e\u5bd2\u5149\uff0c\u4eff\u4f5b\u874e\u5b50\u7684\u6bd2\u523a\u4f3c\u7684\u3002\u743c\u6069\u731d\u7136\u4e0d\u610f\uff0c\u5012\u662f\u5fae\u5fae\u4e00\u60ca\uff0c\u201c\u6211\u600e\u4e48\u4ee5\u524d\u4ece\u6ca1\u770b\u5230\u8fc7\u4f60\u6709\u5c3e\u5df4\u3002\u201d</p><p>\u201c\u9b45\u9b54\u90fd\u6709\u5c3e\u5df4\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5c31\u50cf\u90fd\u6709\u8760\u7ffc\uff0c\u5e73\u65f6\u53ef\u4ee5\u6536\u8d77\u6765\u800c\u5df2\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u4e1c\u897f\u7684\u7528\u6cd5\u662f\u2026\u2026\u201d</p><p>\u9b45\u9b54\u628a\u624b\u4e2d\u7684\u91d1\u9525\u9012\u7ed9\u743c\u6069\uff0c\u201c\u653e\u5fc3\uff0c\u867d\u7136\u6709\u6bd2\uff0c\u4f46\u5bf9\u51e1\u4eba\u6ca1\u6548\u679c\uff0c\u53ea\u5bf9\u6076\u9b54\u6709\u7528\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u66ff\u6211\u5b89\u4e0a\u3002\u201d</p><p>\u201c\u5b89\u4e0a\uff1f\u201d</p><p>\u743c\u6069\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u9b45\u9b54\u5df2\u7ecf\u6446\u597d\u4e86\u59ff\u52bf\uff0c\u5979\u8dea\u4f0f\u5728\u5e8a\u4e0a\uff0c\u4e0a\u534a\u8eab\u8d34\u7740\u5e8a\u5355\uff0c\u9ed1\u8272\u957f\u88d9\u6380\u8d77\uff0c\u7ffb\u5728\u8170\u4e0a\uff0c\u9732\u51fa\u9ad8\u7fd8\u7684\u81c0\u90e8\uff0c\u4e73\u767d\u8272\u7684\u76ae\u80a4\u4e2d\u5939\u6742\u7740\u4e00\u62b9\u7d2b\u8272\uff0c\u90a3\u662f\u9677\u5165\u81c0\u6c9f\u7684\u4e01\u5b57\u5185\u88e4\u3002\u5c3e\u5df4\u4ece\u810a\u690e\u672b\u7aef\u5ef6\u4f38\u51fa\u6765\uff0c\u5145\u6ee1\u6311\u9017\u5730\u5728\u7a7a\u4e2d\u6447\u6446\u7740\uff0c\u672b\u7aef\u8f7b\u8f7b\u98a4\u52a8\u3002\u743c\u6069\u770b\u4e86\u770b\u624b\u4e2d\u7684\u91d1\u9525\uff0c\u53d1\u73b0\u5b83\u662f\u7a7a\u5fc3\u7684\uff0c\u4e8e\u662f\u77e5\u9053\u4e86\u600e\u4e48\u505a\u3002\u4ed6\u4e00\u624b\u63e1\u7740\u5c3e\u5df4\uff0c\u4e00\u624b\u62ff\u7740\u7a7a\u5fc3\u7684\u91d1\u9525\uff0c\u5f80\u672b\u7aef\u5957\u4e0a\uff0c\u53d1\u73b0\u5c45\u7136\u4e25\u4e1d\u5408\u7f1d\uff0c\u4eff\u4f5b\u91cf\u8eab\u5b9a\u505a\u4f3c\u7684\u3002</p><p>\u201c\u5b89\u597d\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u987a\u624b\u5728\u9b45\u9b54\u7684\u81c0\u90e8\u8f7b\u8f7b\u634f\u4e86\u634f\uff0c\u611f\u53d7\u90a3\u5ae9\u6ed1\u5230\u6781\u70b9\u7684\u808c\u80a4\uff0c\u201c\u5514\uff0c\u8bb0\u5f97\u4ee5\u524d\u624b\u611f\u4f3c\u4e4e\u8fd8\u6ca1\u8fd9\u4e48\u597d\u3002\u201d</p><p>\u201c\u6362\u4e86\u526f\u8eab\u4f53\u561b\uff0c\u201d\u9b45\u9b54\u4f9d\u65e7\u4fdd\u6301\u7740\u8dea\u4f0f\u7684\u59ff\u52bf\uff0c\u8f6c\u8fc7\u8138\u6765\uff0c\u820c\u5c16\u6027\u611f\u5730\u8214\u4e86\u8214\u5634\u5507\uff0c\u5a9a\u773c\u5982\u4e1d\uff0c\u201c\u8981\u4e0d\u8981\u5c1d\u8bd5\u4e00\u4e0b\u6ecb\u5473\u3002\u201d</p><p>\u201c\u4f60\u7ecf\u5e38\u5411\u7537\u6027\u53d1\u51fa\u8fd9\u79cd\u9080\u8bf7\u5417\uff1f\u201d\u743c\u6069\u7684\u624b\u6307\u8f7b\u8f7b\u6e38\u52a8\uff0c\u626f\u5f00\u4e01\u5b57\u88e4\u7684\u7d2b\u8272\u7ec6\u5e26\uff0c\u6307\u5c16\u6ca1\u5165\u4e00\u7247\u67d4\u5ae9\u6ed1\u817b\u5230\u6781\u81f4\u7684\u6ce5\u6cbc\u91cc\uff0c\u4eff\u4f5b\u6709\u4e00\u79cd\u9690\u9690\u7684\u5438\u5f15\u529b\uff0c\u50cf\u8981\u628a\u624b\u6307\u541e\u566c\u8fdb\u53bb\u4f3c\u7684\uff0c\u201c\u6709\u591a\u5c11\u4eba\u4eab\u7528\u8fc7\u8fd9\u5177\u8eab\u4f53\u5462\u3002\u201d</p><p>\u201c\u4e0d\u591a\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u5979\u7684\u5c3e\u5df4\u6162\u6162\u5f80\u56de\u7f29\uff0c\u6700\u7ec8\u5b8c\u5168\u6ca1\u5165\u4f53\u5185\uff0c\u8fde\u7740\u672b\u7aef\u521a\u521a\u5957\u4e0a\u7684\u91d1\u9525\u4e5f\u6d88\u5931\u4e0d\u89c1\uff0c\u201c\u51c6\u786e\u5730\u8bf4\u53ea\u6709\u683c\u62c9\u5179\u7279\u4e00\u4e2a\uff0c\u6bd5\u7adf\u6211\u8fd8\u7b97\u53d7\u4ed6\u770b\u91cd\uff0c\u6216\u8005\u8bf4\u5bf9\u4ed6\u6765\u8bf4\u65b0\u9c9c\u611f\u8fd8\u6ca1\u8fc7\u53bb\uff0c\u522b\u7684\u6076\u9b54\u4e0d\u6562\u78b0\u6211\u3002\u6bd4\u8d77\u6211\u5c0f\u65f6\u5019\uff0c\u5df2\u7ecf\u7b97\u662f\u5e78\u8fd0\u591a\u4e86\u3002\u201d</p><p>\u5979\u8bdd\u8bf4\u5f97\u4e0d\u7d27\u4e0d\u6162\uff0c\u8bed\u6c14\u5e73\u6de1\uff0c\u743c\u6069\u542c\u5728\u8033\u4e2d\u5374\u611f\u89c9\u6709\u51e0\u5206\u8bf4\u4e0d\u51fa\u7684\u51c4\u51c9\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e5f\u662f\u4e00\u9eef\uff0c\u51e0\u4e4e\u90fd\u8981\u53f9\u53e3\u6c14\u51fa\u6765\u3002\u201c\u4ee5\u540e\u4e0d\u4f1a\u4e86\uff0c\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u201c\u5bf9\u4e0d\u8d77\u3002\u201d</p><p>\u201c\u6709\u4ec0\u4e48\u5bf9\u4e0d\u8d77\u7684\uff0c\u6211\u8fd0\u6c14\u4e0d\u597d\u7f62\u4e86\uff0c\u201d\u9b45\u9b54\u7b11\u7b11\uff0c\u5750\u8d77\u8eab\u6765\uff0c\u80cc\u9760\u7740\u5899\u58c1\uff0c\u53cc\u81c2\u73af\u4fdd\u819d\u76d6\uff0c\u201c\u6211\u4e0d\u50cf\u6885\u83f2\u65af\u5c0f\u59d0\u90a3\u6837\u6709\u4e2a\u597d\u6bcd\u4eb2\uff0c\u4e0d\u50cf\u5979\u80fd\u9047\u4e0a\u63d0\u5c14\u6559\u4f1a\u7684\u9886\u8896\uff0c\u800c\u4e14\u8fd8\u80fd\u8d4f\u8bc6\u5979\uff0c\u57f9\u517b\u5979\u6210\u4e3a\u5723\u6b66\u58eb\u3002\u5c31\u7b97\u662f\u5979\u540e\u6765\u9047\u5230\u4f60\uff0c\u90a3\u4e5f\u6bd4\u6211\u8fd0\u6c14\u597d\u554a\uff0c\u81f3\u5c11\u4f60\u771f\u559c\u6b22\u5979\u3002\u5927\u5bb6\u90fd\u662f\u90aa\u795e\u5b50\u55e3\uff0c\u90fd\u662f\u4e00\u4e2a\u7236\u4eb2\uff0c\u5979\u5c31\u662f\u5929\u4e4b\u9a84\u5b50\uff0c\u795e\u9009\u6218\u58eb\uff0c\u6211\u5c31\u53ea\u80fd\u4ece\u5c0f\u5728\u8d2b\u6c11\u7a9f\u91cc\u6323\u624e\uff0c\u76d7\u7a83\u3001\u6b3a\u9a97\u3001\u6740\u4eba\u3001\u51fa\u5356\u8eab\u4f53\uff0c\u4e00\u5207\u53ea\u4e3a\u80fd\u6d3b\u547d\u3002\u751a\u81f3\u5c31\u8fde\u6b7b\u4e86\u90fd\u4f1a\u88ab\u53d8\u6210\u6076\u9b54\uff0c\u7ee7\u7eed\u53d7\u6b3a\u538b\u2014\u2014\u8fd9\u5c31\u662f\u547d\u8fd0\u554a\u3002\u201d</p><p>\u201c\u547d\u8fd0\u4e48\u3002\u201d</p><p>\u201c\u5f53\u7136\uff0c\u6211\u4e0d\u89c9\u5f97\u6211\u6bd4\u8c01\u5dee\uff0c\u96be\u9053\u6211\u6bd4\u4f60\u7684\u5c0f\u60c5\u4eba\u5dee\uff1f\u6211\u662f\u6bd4\u5979\u7b28\uff0c\u8fd8\u662f\u6bd4\u5979\u4e11\uff0c\u8fd8\u662f\u6bd4\u5979\u5f31\uff1f\u806a\u660e\u7684\u5934\u8111\u3001\u4eba\u5fc3\u7684\u63e3\u6469\u3001\u5bf9\u7537\u4eba\u7684\u5438\u5f15\u529b\u3001\u6b66\u6280\u953b\u70bc\u548c\u6740\u4eba\u6280\u5de7\uff0c\u6211\u54ea\u4e00\u4ef6\u4e0d\u5982\u5979\u5462\uff1f\u5979\u9664\u4e86\u6709\u4e2a\u597d\u6bcd\u4eb2\uff0c\u80fd\u591f\u4e00\u5f00\u59cb\u5c31\u66ff\u5979\u5728\u8eab\u4f53\u91cc\u51dd\u805a\u4e86\u5927\u91cf\u7684\u6740\u622e\u795e\u529b\uff0c\u8fd8\u6709\u54ea\u70b9\u6bd4\u5f97\u4e0a\u6211\u7684\uff1f\u65e2\u7136\u6211\u6837\u6837\u4e0d\u6bd4\u5979\u5dee\uff0c\u4e3a\u4ec0\u4e48\u5979\u5c31\u80fd\u5728\u5929\u5802\uff0c\u53d7\u4eba\u5c0a\u91cd\uff0c\u88ab\u4eba\u7231\u62a4\uff0c\u6709\u4eba\u559c\u6b22\uff0c\u800c\u6211\u7684\u4e0b\u573a\u5c31\u843d\u5f97\u8fd9\u4e48\u7cdf\u7cd5\u5462\uff0c\u5230\u73b0\u5728\uff0c\u751a\u81f3\u8fde\u4e2a\u670b\u53cb\u90fd\u6ca1\u6709\u3002\u9664\u4e86\u547d\u8fd0\uff0c\u8fd8\u6709\u4ec0\u4e48\u80fd\u89e3\u91ca\u8fd9\u4e00\u5207\u5462\u3002\u201d</p><p>\u5979\u8bf4\u7740\u8bf4\u7740\uff0c\u7a81\u7136\u81ea\u5df1\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4e0d\u8bf4\u4e86\uff0c\u600e\u4e48\u641e\u7684\uff0c\u5c45\u7136\u81ea\u6028\u81ea\u827e\u8d77\u6765\uff0c\u8fd9\u53ef\u4ece\u6765\u4e0d\u662f\u6211\u7684\u98ce\u683c\u554a\uff0c\u201d\u5979\u62ac\u8d77\u8138\uff0c\u987f\u65f6\u53c8\u662f\u5e73\u5e38\u90a3\u526f\u6ee1\u662f\u76c8\u76c8\u7b11\u610f\u7684\u6a21\u6837\uff0c\u82e5\u6709\u82e5\u65e0\u5730\u5e26\u7740\u51e0\u5206\u5996\u5a9a\uff0c\u201c\u6709\u4ec0\u4e48\u4e8b\u5462\uff0c\u4e3b\u4eba\uff0c\u6211\u60f3\u4f60\u653e\u5f03\u548c\u5c0f\u60c5\u4eba\u76f8\u5904\u7684\u5b9d\u8d35\u65f6\u95f4\uff0c\u7279\u5730\u6765\u627e\u6211\uff0c\u603b\u4e0d\u4f1a\u5c31\u662f\u4e3a\u4e86\u542c\u4e00\u4e2a\u547d\u8fd0\u7684\u5931\u8d25\u8005\u5728\u53d1\u7262\u9a9a\u5427\u3002\u201d</p><p>\u743c\u6069\u672c\u6765\u662f\u60f3\u54a8\u8be2\u4e00\u4e0b\u5979\u8fd9\u4f4d\u4e13\u4e1a\u4eba\u58eb\u7684\u610f\u89c1\uff0c\u770b\u770b\u5982\u4f55\u8ba9\u6885\u83f2\u65af\u80fd\u5728\u5e8a\u4e0a\u53d8\u5f97\u66f4\u52a0\u201c\u5973\u4eba\u201d\u4e00\u70b9\uff0c\u5982\u4eca\u8fd9\u4e48\u4e00\u5f04\uff0c\u53cd\u5012\u6ca1\u4e86\u5fc3\u601d\uff0c\u6b63\u60f3\u968f\u4fbf\u627e\u4e2a\u7406\u7531\u63a9\u9970\u8fc7\u53bb\uff0c\u838e\u73de\u514b\u5374\u5df2\u7ecf\u731c\u4e86\u51fa\u6765\uff0c\u201c\u662f\u60f3\u8c03\u6559\u4f60\u90a3\u4f4d\u5c0f\u60c5\u4eba\uff1f\u201d\u5979\u95ee\uff0c\u201c\u8fd9\u4e2a\u6211\u5012\u662f\u5f88\u4e50\u610f\u6548\u52b3\u3002\u201d</p><p>\u201c\u8c03\u6559\u8fd9\u4e2a\u8bcd\u8fd8\u662f\u7b97\u4e86\u5427\uff0c\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u4e0d\u7528\u8bf4\u5f97\u90a3\u4e48\u5938\u5f20\uff0c\u6211\u6ca1\u90a3\u79cd\u53d8\u6001\u7231\u597d\u3002\u201d</p><p>\u201c\u662f\u4e48\uff0c\u6211\u8fd8\u4ee5\u4e3a\u7537\u4eba\u90fd\u4f1a\u559c\u6b22\u5462\uff0c\u53ea\u662f\u6709\u6ca1\u6709\u673a\u4f1a\uff0c\u80fd\u4e0d\u80fd\u529e\u5230\u7684\u95ee\u9898\u800c\u5df2\uff0c\u201d\u838e\u73de\u514b\u5634\u89d2\u7fd8\u8d77\uff0c\u9732\u51fa\u5a07\u5a9a\u7684\u7b11\u5bb9\u6765\uff0c\u201c\u5982\u679c\u73b0\u5728\u6709\u4e00\u4e2a\u88ab\u8c03\u6559\u597d\u7684\u7f8e\u4eba\u513f\u653e\u5728\u4f60\u9762\u524d\uff0c\u53ea\u8981\u4f60\u613f\u610f\uff0c\u5979\u5c31\u80fd\u4e3a\u4f60\u505a\u4efb\u4f55\u670d\u52a1\uff0c\u6ca1\u6709\u4efb\u4f55\u4ee3\u4ef7\uff0c\u6ca1\u6709\u4efb\u4f55\u98ce\u9669\uff0c\u4ec0\u4e48\u90fd\u4e0d\u7528\u4ed8\u51fa\u2026\u2026\u4f60\u4f1a\u4e0d\u60f3\u8981\uff1f\u201d</p><p>\u201c\u60f3\u8981\uff0c\u201d\u743c\u6069\u627f\u8ba4\uff0c\u201c\u4f46\u6211\u4e0d\u60f3\u628a\u827e\u5f25\u8587\u53d8\u6210\u8fd9\u6837\uff0c\u8fd9\u6837\u89e3\u91ca\u591f\u6e05\u695a\u4e86\u5427\u3002\u201d</p><p>\u201c\u591f\u6e05\u695a\u4e86\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u4e0d\u8fc7\u4f60\u522b\u5fd8\u4e86\u4e00\u4ef6\u4e8b\uff0c\u8089\u4f53\u548c\u7075\u9b42\uff0c\u5e76\u4e0d\u662f\u90a3\u4e48\u622a\u7136\u5206\u5f00\u7684\u3002\u4e00\u65e6\u6c89\u8ff7\u4e8e\u8089\u4f53\u7684\u5feb\u611f\uff0c\u7cbe\u795e\u540c\u6837\u4e5f\u4f1a\u53d7\u5230\u5f71\u54cd\uff0c\u4e0d\u8981\u4ee5\u4e3a\u8fd9\u4e16\u754c\u4e0a\u771f\u6709\u90a3\u79cd\u5b8c\u5168\u6deb\u8361\u7684\u8089\u4f53\u548c\u7eaf\u6d01\u65e0\u7455\u7684\u7cbe\u795e\u7ed3\u5408\u4f53\uff0c\u6211\u60f3\u4e16\u754c\u4e0a\u7edd\u6ca1\u6709\u8fd9\u6837\u7684\u4eba\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u591a\u8c22\u63d0\u9192\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4f1a\u628a\u63e1\u5206\u5bf8\u3002\u201d</p><p>\u201c\u5404\u79cd\u6280\u5de7\u548c\u77e5\u8bc6\u4e48\uff0c\u6211\u81ea\u7136\u662f\u4e0d\u7f3a\u7684\uff0c\u5b8c\u5168\u53ef\u4ee5\u6559\u7ed9\u5979\uff0c\u53ea\u662f\u5979\u6050\u6015\u4e0d\u4f1a\u559c\u6b22\uff0c\u201d\u9b45\u9b54\u6175\u61d2\u5730\u8212\u5c55\u7740\u8eab\u4f53\uff0c\u201c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u9996\u5148\u662f\u4e0d\u80fd\u6709\u6297\u62d2\u5fc3\u7406\uff0c\u6240\u4ee5\u6211\u4e00\u5f00\u59cb\u8fd8\u662f\u4e0d\u8981\u51fa\u73b0\u5f97\u4e3a\u597d\u2026\u2026\u5982\u679c\u8fd9\u6837\u7684\u8bdd\uff0c\u90a3\u5c31\u53ea\u6709\u7528\u70b9\u836f\u7269\u4e86\u3002\u201d</p><p>\u201c\u5a9a\u836f\u6211\u4e5f\u4f1a\u914d\uff0c\u4f46\u8ddd\u79bb\u6211\u60f3\u8981\u7684\u6548\u679c\u7ec8\u7a76\u8fd8\u5dee\u4e86\u4e00\u70b9\u2026\u2026\u201d</p><p>\u201c\u4f60\u4f1a\u914d\u7684\u90a3\u4e9b\u90fd\u662f\u7269\u8d28\u754c\u7684\u836f\u7269\uff0c\u5f53\u7136\u4e0d\u591f\u770b\uff0c\u6076\u9b54\u81ea\u6709\u6076\u9b54\u7684\u65b9\u6cd5\uff0c\u201d\u9b45\u9b54\u81ea\u4fe1\u6ee1\u6ee1\u5730\u8bf4\uff0c\u201c\u4ea4\u7ed9\u6211\u5427\uff0c\u4fdd\u8bc1\u8ba9\u4f60\u6ee1\u610f\uff0c\u7ed9\u6211\u6700\u591a\u4e09\u5929\u65f6\u95f4\u5c31\u884c\u3002\u201d</p><p>\u743c\u6069\u76ef\u7740\u5979\uff0c\u201c\u6211\u5148\u8bf4\u660e\uff0c\u6211\u6240\u9700\u8981\u7684\uff0c\u662f\u5bf9\u5979\u7684\u7cbe\u795e\u6216\u8eab\u4f53\u6ca1\u6709\u4efb\u4f55\u4f24\u5bb3\u7684\uff0c\u4e0d\u4f1a\u9020\u6210\u4efb\u4f55\u526f\u4f5c\u7528\u548c\u540e\u9057\u75c7\u7684\uff0c\u7edd\u65e0\u4efb\u4f55\u4e0d\u826f\u6548\u679c\u6216\u8005\u6211\u4e0d\u671f\u671b\u53d1\u751f\u7684\u6548\u679c\u7684\u836f\u7269\u2014\u2014\u6211\u76f8\u4fe1\u4f60\u80fd\u6e05\u695a\u660e\u767d\u6211\u7684\u610f\u601d\u3002\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u9b45\u9b54\u56de\u7b54\uff0c\u201c\u5f53\u7136\u660e\u767d\uff0c\u4e3b\u4eba\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u65f6\u95f4\u5f88\u5feb\u8fc7\u53bb\uff0c\u51b3\u6597\u5927\u8d5b\u6b63\u5f0f\u5f00\u59cb\u4e86\uff0c\u743c\u6069\u7b49\u516d\u4eba\u4f5c\u4e3a\u6bd4\u8d5b\u9009\u624b\u5165\u573a\uff0c\u6885\u83f2\u65af\u548c\u4f0a\u8389\u96c5\u4e5f\u90fd\u5750\u5728\u524d\u6392\u89c2\u4f17\u5e2d\u4e0a\uff0c\u5728\u4e00\u7fa4\u6076\u9b54\u4e2d\u683c\u5916\u663e\u773c\uff0c\u9887\u6709\u51e0\u4e2a\u5bb6\u4f19\u8272\u772f\u772f\u5730\u8fc7\u6765\u642d\u8baa\uff0c\u4f46\u90fd\u65e0\u529f\u800c\u8fd4\u3002\u6885\u83f2\u65af\u867d\u7136\u6b64\u65f6\u865a\u5f31\uff0c\u8fde\u6325\u5251\u90fd\u4e0d\u80fd\uff0c\u4f46\u957f\u5e74\u517b\u6210\u7684\u6c14\u52bf\u5df2\u7ecf\u6df1\u5165\u9aa8\u9ad3\uff0c\u6574\u4e2a\u4eba\u4fbf\u662f\u4e00\u67c4\u950b\u9510\u5229\u5251\uff0c\u53ea\u88ab\u5979\u51b7\u51b7\u626b\u4e0a\u4e00\u773c\uff0c\u4e24\u4e2a\u5de8\u725b\u9b54\u7acb\u523b\u5c31\u4ed3\u7687\u8d25\u9000\u4e86\u3002</p><p>\u5927\u6982\u662f\u88ab\u5956\u54c1\u6240\u5438\u5f15\uff0c\u53c8\u6216\u8bb8\u662f\u6076\u9b54\u4eec\u5929\u6027\u5c31\u55dc\u8840\u597d\u6597\uff0c\u6765\u62a5\u540d\u53c2\u8d5b\u7684\u9009\u624b\u6781\u591a\uff0c\u5927\u7ea6\u6709\u8fd1\u5343\u652f\u961f\u4f0d\uff0c\u597d\u5728\u53cd\u6b63\u662f\u6dd8\u6c70\u8d5b\u5f62\u5f0f\uff0c\u6bcf\u8f6e\u81f3\u5c11\u90fd\u4f1a\u5237\u6389\u4e00\u534a\u2014\u2014\u800c\u4e14\u8fd8\u4f1a\u6709\u56e0\u4e3a\u961f\u4f0d\u6210\u5458\u635f\u5931\u8fc7\u91cd\u53c8\u65e0\u6cd5\u53ca\u65f6\u8865\u5145\uff0c\u88ab\u8feb\u9000\u51fa\u7684\u60c5\u5f62\uff0c\u6240\u4ee5\u5176\u5b9e\u4e5f\u53ea\u8981\u51e0\u5929\u529f\u592b\u5c31\u80fd\u6253\u5230\u51b3\u8d5b\u4e86\u3002</p><p>\u6709\u838e\u73de\u514b\u5728\uff0c\u5c31\u76f8\u5f53\u4e8e\u6709\u4e86\u4e00\u4e2a\u6df1\u6e0a\u5411\u5bfc\uff0c\u5979\u80fd\u591f\u7cbe\u786e\u5730\u8fa8\u8ba4\u5404\u79cd\u6076\u9b54\u7684\u79cd\u7c7b\uff0c\u89e3\u8bf4\u5b83\u4eec\u7684\u4e60\u6027\u3001\u4f18\u52bf\u548c\u5f31\u70b9\uff0c\u8fd9\u8ba9\u6218\u6597\u53d8\u5f97\u4e8b\u534a\u529f\u500d\u3002\u743c\u6069\u7b49\u4eba\u9047\u5230\u7684\u7b2c\u4e00\u8f6e\u5bf9\u624b\u662f\u516d\u53ea\u72c2\u6218\u9b54\uff0c\u5df2\u7ecf\u7b97\u4e2d\u9636\u7684\u6076\u9b54\u4e86\uff0c\u542c\u540d\u5b57\u5c31\u77e5\u9053\u662f\u90a3\u79cd\u75af\u72c2\u6218\u6597\u70ed\u7231\u6740\u622e\u7684\u7c7b\u578b\uff0c\u636e\u838e\u73de\u514b\u8bf4\uff0c\u5b83\u4eec\u559c\u6b22\u8fd1\u8eab\u640f\u6597\uff0c\u559c\u6b22\u7528\u957f\u81c2\u733f\u4e00\u6837\u7684\u80f3\u818a\u5c06\u4eba\u6b7b\u6b7b\u64d2\u62b1\uff0c\u559c\u6b22\u7528\u5934\u4e0a\u7684\u5c16\u89d2\u9876\uff0c\u7528\u950b\u5229\u7684\u7259\u9f7f\u54ac\uff0c\u603b\u4e4b\u65e0\u6240\u4e0d\u7528\u5176\u6781\u3002</p><p>\u6b23\u5e03\u542c\u4e86\u9b45\u9b54\u7684\u4ecb\u7ecd\uff0c\u55e4\u7b11\u4e00\u58f0\uff0c\u8f6c\u8fc7\u5934\u6765\uff0c\u201c\u51db\uff0c\u4f60\u4e0a\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4ec0\u4e48\u90fd\u4e0d\u7528\u8003\u8651\uff0c\u4fdd\u6301\u8ddd\u79bb\u7528\u706b\u7403\u8f70\u3002\u201d</p><p>\u51db\u7b54\u5e94\u4e00\u58f0\uff0c\u8dc3\u4e0a\u64c2\u53f0\uff0c\u4f9d\u65e7\u6309\u7167\u5979\u7684\u4e60\u60ef\uff0c\u5148\u70b9\u4e86\u56db\u4e2a\u706b\u5806\uff0c\u51ed\u501f\u7740\u5979\u65e0\u9650\u706b\u4e2d\u8df3\u8dc3\u7684\u5929\u8d4b\uff0c\u72c2\u6218\u9b54\u538b\u6839\u5c31\u8fde\u5979\u7684\u88d9\u89d2\u90fd\u78b0\u4e0d\u5230\u3002\u5979\u8eab\u4e0a\u53c8\u5e26\u4e86\u4e00\u5927\u628a\u706b\u7403\u9b54\u6756\uff0c\u4e0d\u8981\u94b1\u5730\u72c2\u8f70\u6ee5\u70b8\uff0c\u7ed9\u743c\u6069\u7684\u611f\u89c9\u5c31\u662f\u4e00\u4e2a\u673a\u67aa\u624b\u5728\u8650\u5f85\u4e00\u53ea\u867d\u7136\u51f6\u608d\u65e0\u6bd4\u5374\u4e0d\u80fd\u8fdc\u7a0b\u653b\u51fb\u7684\u91ce\u517d\u3002\u6ca1\u8fc7\u534a\u5c0f\u65f6\uff0c\u516d\u573a\u6bd4\u8bd5\u90fd\u7ed3\u675f\u4e86\uff0c\u56db\u53ea\u72c2\u6218\u9b54\u88ab\u70e4\u6210\u4e86\u4e73\u732a\uff0c\u8fd8\u6709\u4e24\u53ea\u76f4\u63a5\u5f03\u6743\u9000\u51fa\uff0c\u64c2\u53f0\u4e0a\u5f25\u6f2b\u7740\u4e00\u79cd\u5947\u602a\u7684\u5473\u9053\uff0c\u50cf\u662f\u8089\u9999\u548c\u81ed\u6c14\u6df7\u6742\u3002</p><p>\u201c\u8fd9\u5473\u9053\u597d\u53e4\u602a\u3002\u201d\u51db\u76b1\u7740\u7709\u5934\u3002</p><p>\u201c\u72c2\u6218\u9b54\u4e00\u65e6\u5267\u70c8\u8fd0\u52a8\u5c31\u4f1a\u5927\u6c57\u6dcb\u6f13\uff0c\u81ed\u4e0d\u53ef\u95fb\uff0c\u201d\u838e\u73de\u514b\u6de1\u6de1\u89e3\u91ca\uff0c\u201c\u5c31\u8fde\u6076\u9b54\u90fd\u672a\u5fc5\u80fd\u5fcd\u53d7\uff0c\u8fd9\u4e5f\u662f\u5b83\u4eec\u7684\u6709\u6548\u6b66\u5668\u4e4b\u4e00\u3002\u201d</p><p>\u201c\u592a\u6076\u5fc3\u4e86\u3002\u201d\u51db\u6325\u7740\u5c0f\u624b\u5728\u9f3b\u5b50\u524d\u4e0d\u505c\u5730\u6247\u6765\u6247\u53bb\u3002</p><p>\u597d\u5728\u8d5b\u573a\u5de5\u4f5c\u4eba\u5458\u5bf9\u8fd9\u79cd\u60c5\u51b5\u65e9\u5c31\u6709\u51c6\u5907\uff0c\u5f88\u5feb\u51e0\u53ea\u602f\u9b54\u8dd1\u4e0a\u6765\uff0c\u62b1\u7740\u4e00\u79cd\u7c7b\u4f3c\u5438\u5c18\u5668\u7684\u4e1c\u897f\u5728\u64c2\u53f0\u4e0a\u8f6c\u4e86\u4e00\u5708\uff0c\u602a\u5473\u7acb\u523b\u5c31\u6d88\u9664\u4e86\u3002\u63a5\u7740\u8fdb\u884c\u7b2c\u4e8c\u8f6e\u6bd4\u8d5b\u3002</p><p>\u8fd9\u6b21\u7684\u5bf9\u624b\u6709\u4e9b\u9ebb\u70e6\uff0c\u56db\u53ea\u5df4\u5e03\u9b54\uff0c\u8fd9\u5176\u5b9e\u662f\u51d1\u6570\u7684\uff0c\u4e0d\u653e\u5728\u743c\u6069\u7b49\u4eba\u773c\u4e2d\uff0c\u4f46\u53e6\u5916\u4e24\u4e2a\u5bb6\u4f19\u5c31\u5fc5\u987b\u91cd\u89c6\u4e86\uff1a\u4e00\u53ea\u72e9\u9b54\u86db\u548c\u4e00\u53ea\u4e4c\u7279\u7f57\u65af\u9b54\uff0c\u524d\u8005\u5f62\u5982\u5de8\u5927\u8718\u86db\uff0c\u4ee5\u5f31\u5c0f\u6076\u9b54\u4e3a\u98df\u7269\uff0c\u540e\u8005\u6839\u672c\u4e0d\u662f\u6076\u9b54\uff0c\u800c\u662f\u5c24\u683c\u7f57\u65af\u9b54\uff08\u4e00\u79cd\u4ecb\u4e4e\u4e8e\u6076\u9b54\u548c\u9b54\u9b3c\u4e4b\u95f4\u7684\u602a\u7269\uff09\u3002</p><p>\u201c\u4e4c\u7279\u7f57\u65af\u9b54\u7684\u8d44\u6599\u6211\u4e5f\u4e0d\u662f\u7279\u522b\u6e05\u695a\uff0c\u201d\u838e\u73de\u514b\u76b1\u7709\uff0c\u201c\u56e0\u4e3a\u5b83\u4eec\u4e0d\u662f\u6076\u9b54\u3002\u4f46\u53ef\u4ee5\u80af\u5b9a\u7684\u662f\uff0c\u5b83\u4eec\u6700\u64c5\u957f\u7684\u653b\u51fb\u624b\u6bb5\u662f\u50ac\u7720\u6050\u60e7\u8fd9\u79cd\u7cbe\u795e\u6cd5\u672f\uff0c\u4e0d\u64c5\u8089\u640f\u3002\u201d</p><p>\u743c\u6069\u7b49\u4eba\u5546\u91cf\u4e86\u4e00\u4e0b\uff0c\u51b3\u5b9a\u5148\u6d3e\u963f\u5fd2\u59ae\u4e0a\u573a\uff0c\u63a2\u63a2\u5bf9\u624b\u7684\u865a\u5b9e\u3002\u5979\u662f\u7cbe\u7075\u5f13\u7bad\u624b\uff0c\u6b65\u4f10\u7075\u654f\uff0c\u4e07\u4e00\u4e0d\u5229\u4e5f\u76f8\u5bf9\u5bb9\u6613\u8131\u8eab\uff0c\u6bd4\u8d5b\u89c4\u5219\u4e5f\u5f88\u7b80\u5355\uff0c\u5982\u679c\u8981\u653e\u5f03\u7684\u8bdd\uff0c\u76f4\u63a5\u8df3\u4e0b\u64c2\u53f0\u5c31\u884c\u3002\u963f\u5fd2\u59ae\u4e5f\u65e0\u5f02\u8bae\uff0c\u63d0\u8d77\u77ed\u5f13\u7bad\u56ca\u4fbf\u4e0a\u4e86\u573a\u3002</p><p>\u56e0\u4e3a\u662f\u6076\u9b54\u4e16\u754c\u7684\u6bd4\u8d5b\uff0c\u64c2\u53f0\u975e\u5e38\u5927\uff0c\u8f83\u4e4b\u7269\u8d28\u754c\u7684\u89c4\u683c\u81f3\u5c11\u5927\u4e86\u5341\u500d\uff0c\u8fd9\u7ed9\u4e88\u4e86\u963f\u5fd2\u59ae\u8db3\u591f\u7684\u7a7a\u95f4\u548c\u8ddd\u79bb\u3002\u5bf9\u65b9\u5148\u6d3e\u56db\u4e2a\u5df4\u5e03\u9b54\u4e0a\u573a\uff0c\u88ab\u6708\u7cbe\u7075\u8fde\u8fde\u53d1\u7bad\uff0c\u5c3d\u6570\u90fd\u5c04\u4e86\u4e0b\u53bb\u3002</p><p>\u4f46\u7b2c\u4e94\u573a\u6bd4\u8d5b\u5c31\u6bd4\u8f83\u9ebb\u70e6\u4e86\uff0c\u5bf9\u65b9\u6d3e\u51fa\u4e86\u72e9\u9b54\u86db\uff0c\u8fd9\u79cd\u6076\u9b54\u957f\u7740\u516b\u6761\u817f\uff0c\u79fb\u52a8\u901f\u5ea6\u6781\u5feb\uff0c\u800c\u4e14\u8fd8\u80fd\u55b7\u5c04\u86db\u7f51\uff0c\u5168\u8eab\u53c8\u6709\u4e00\u526f\u575a\u786c\u7532\u58f3\uff0c\u963f\u5fd2\u59ae\u7684\u7bad\u77e2\u6839\u672c\u90fd\u5c04\u4e0d\u7a7f\u3002\u52c9\u5f3a\u652f\u6491\u4e86\u4e24\u5206\u949f\uff0c\u8475\u9732\u4fbf\u53d1\u4fe1\u53f7\u8ba9\u5979\u5f03\u6743\u8ba4\u8f93\uff0c\u518d\u6253\u4e0b\u53bb\u6ca1\u6709\u5e0c\u671b\u3002</p><p>\u56e0\u4e3a\u963f\u5fd2\u59ae\u662f\u4e0a\u4e00\u573a\u6bd4\u8d5b\u7684\u80dc\u5229\u8005\uff0c\u6240\u4ee5\u5979\u62e5\u6709\u201c\u6218\u8d25\u201d\u6216\u8005\u201c\u5f03\u6743\u201d\u7684\u8d44\u683c\uff0c\u6240\u4ee5\u8fd9\u573a\u6bd4\u8d5b\u5979\u867d\u7136\u8ba4\u8f93\u4e86\uff0c\u5374\u4e0d\u7b97\u743c\u6069\u8fd9\u4e2a\u961f\u4f0d\u8f93\u4e86\u4e00\u573a\uff0c\u4e5f\u5c31\u662f\u8bf4\u7b2c\u4e94\u573a\u6bd4\u8d5b\u4f9d\u65e7\u8fd8\u6ca1\u5b8c\u2014\u2014\u8fd9\u540c\u6837\u4e5f\u662f\u683c\u62c9\u5179\u7279\u90a3\u4e2a\u5fc3\u7406\u53d8\u6001\u5b9a\u4e0b\u7684\u7ec6\u8282\u89c4\u77e9\u4e4b\u4e00\uff0c\u4e3a\u4e86\u5b9e\u73b0\u4ed6\u6240\u8c13\u7684\u201c\u6709\u6df1\u6e0a\u7279\u8272\u7684\u56e2\u961f\u4e3b\u4e49\u201d\u800c\u6298\u817e\u51fa\u6765\u7684\uff0c\u522b\u8bf4\u743c\u6069\u8fd9\u79cd\u5916\u6765\u4eba\uff0c\u5c31\u8fde\u6076\u9b54\u81ea\u5df1\u5f80\u5f80\u90fd\u88ab\u5f04\u5f97\u5934\u6655\u8111\u6da8\uff0c\u53cd\u6b63\u4e00\u5207\u542c\u88c1\u5224\u7684\u6307\u6325\u5c31\u662f\u3002</p><p>\u6b23\u5e03\u5bf9\u51db\u6084\u58f0\u8bf4\u4e86\u51e0\u53e5\uff0c\u8ba9\u5979\u63a5\u66ff\u963f\u5fd2\u59ae\u4e0a\u573a\uff0c\u743c\u6069\u539f\u672c\u60f3\u81ea\u5df1\u4e0a\uff0c\u89c1\u5979\u4eec\u4f3c\u4e4e\u80f8\u6709\u6210\u7af9\uff0c\u4e5f\u4fbf\u7f62\u4e86\u3002\u4e0d\u6599\u51db\u521a\u521a\u4e0a\u573a\u5c31\u8ba9\u5927\u5bb6\u5403\u4e86\u4e00\u60ca\uff0c\u72e9\u9b54\u86db\u770b\u89c1\u65b0\u7684\u5bf9\u624b\uff0c\u5f20\u5f00\u5927\u53e3\u4fbf\u55b7\u51fa\u4e00\u9053\u86db\u4e1d\u6765\uff0c\u5728\u7a7a\u4e2d\u7ed3\u6210\u5e9e\u5927\u7684\u5de8\u7f51\uff0c\u7167\u7740\u51db\u5f53\u5934\u7f69\u4e0b\u3002\u539f\u672c\u4ee5\u51db\u7684\u8eab\u624b\u654f\u6377\uff0c\u8981\u907f\u8ba9\u4e5f\u4e0d\u662f\u96be\u4e8b\uff0c\u8c01\u77e5\u5979\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u4efb\u51ed\u90a3\u86db\u7f51\u5c31\u7f69\u4e86\u4e0b\u6765\uff0c\u628a\u6574\u4e2a\u4eba\u90fd\u88f9\u5728\u5176\u4e2d\u3002</p><p>\u201c\u51db\uff01\u201d</p><p>\u743c\u6069\u6709\u4e9b\u5403\u60ca\uff0c\u5374\u89c1\u6b23\u5e03\u5728\u65c1\u8fb9\u795e\u60c5\u81ea\u82e5\uff0c\u4e5f\u4fbf\u6682\u65f6\u653e\u4e0b\u5fc3\u6765\u3002\u72e9\u9b54\u86db\u4e0a\u4e00\u573a\u5bf9\u963f\u5fd2\u59ae\u8fde\u55b7\u4e86\u4e24\u6b21\u86db\u4e1d\u90fd\u88ab\u8eb2\u5f00\uff0c\u5982\u4eca\u6362\u4e86\u4e2a\u4eba\uff0c\u89c1\u4e00\u4e3e\u5c31\u56f0\u4f4f\u4e86\u5bf9\u624b\uff0c\u5927\u662f\u5f97\u610f\u3002\u5b83\u4e5f\u77e5\u9053\u80fd\u591f\u6253\u5230\u7b2c\u4e8c\u8f6e\u7684\uff0c\u5e94\u8be5\u4e5f\u4e0d\u4f1a\u662f\u65e0\u80fd\u4e4b\u8f88\uff0c\u5f53\u4e0b\u5de8\u53e3\u4e00\u5f20\uff0c\u53c8\u55b7\u51fa\u4e24\u5927\u56e2\u86db\u4e1d\uff0c\u8d8a\u53d1\u5c06\u51db\u7f20\u5f97\u7d27\u7d27\u3002\u968f\u5373\u8377\u8377\u602a\u543c\u7740\uff0c\u516b\u53ea\u722a\u5b50\u98de\u901f\u79fb\u52a8\uff0c\u987a\u7740\u53e3\u4e2d\u7684\u86db\u4e1d\u4e00\u8def\u524d\u51b2\uff0c\u6253\u7b97\u628a\u730e\u7269\u541e\u4e0b\u53bb\u3002</p><p>\u77ac\u606f\u4e4b\u95f4\uff0c\u5b83\u5df2\u7ecf\u51b2\u5230\u8fd1\u524d\uff0c\u6b63\u8981\u4f4e\u5934\u5f20\u53e3\u54ac\u4e0b\uff0c\u9661\u7136\u4e00\u56e2\u5de8\u5927\u7684\u70c8\u7130\u4ece\u51db\u8eab\u4e0a\u8f70\u5730\u817e\u8d77\uff0c\u51b2\u4e0a\u534a\u7a7a\uff0c\u987a\u7740\u86db\u4e1d\u671d\u72e9\u9b54\u86db\u70e7\u53bb\u3002\u51db\u8eab\u4e0a\u7684\u86db\u4e1d\u88ab\u70e7\u65ad\uff0c\u5979\u5355\u624b\u6491\u5730\uff0c\u4e00\u4e2a\u540e\u7a7a\u7ffb\u8131\u51fa\u4e86\u72e9\u9b54\u86db\u7684\u653b\u51fb\u8303\u56f4\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u4e24\u679a\u7ea2\u5b9d\u77f3\u540c\u65f6\u7838\u4e86\u51fa\u53bb\u3002</p><p>\u5b9d\u77f3\u5316\u4f5c\u706b\u7403\u70b8\u5f00\uff0c\u718a\u718a\u70c8\u7130\u5c06\u72e9\u9b54\u86db\u5b8c\u5168\u541e\u566c\uff0c\u5b83\u60f3\u8981\u9003\u8131\uff0c\u5374\u5df2\u7ecf\u8eab\u9677\u706b\u6d77\u3002\u86db\u4e1d\u662f\u6781\u6613\u71c3\u70e7\u4e4b\u7269\uff0c\u5b83\u53c8\u63a5\u8fde\u55b7\u4e86\u4e24\u6b21\uff0c\u5f04\u5f97\u5c0f\u534a\u4e2a\u64c2\u53f0\u90fd\u662f\uff0c\u5982\u4eca\u5374\u5f53\u771f\u662f\u4f5c\u8327\u81ea\u7f1a\u4e86\u3002\u72e9\u9b54\u86db\u867d\u7136\u76ae\u575a\u7532\u539a\uff0c\u4e0d\u60e7\u5175\u5203\uff0c\u4f46\u5374\u6ca1\u6709\u62b5\u6297\u706b\u7130\u7684\u80fd\u529b\u3002\u5982\u679c\u662f\u76f4\u63a5\u6254\u51e0\u4e2a\u706b\u7403\uff0c\u5b83\u8fd8\u80fd\u51ed\u501f\u6781\u5feb\u7684\u8fd0\u52a8\u901f\u5ea6\u95ea\u907f\u8fc7\u53bb\uff0c\u4f46\u8fd9\u6837\u9677\u5728\u706b\u6d77\u4e2d\uff0c\u4fbf\u662f\u6b7b\u8def\u4e00\u6761\u4e86\u3002</p><p>\u4e0d\u8fc7\u7247\u523b\uff0c\u706b\u7130\u5e73\u606f\uff0c\u72e9\u9b54\u86db\u5df2\u7ecf\u5316\u4f5c\u4e86\u4e00\u6ee9\u7070\u70ec\uff0c\u51db\u4e5f\u4e0d\u7ee7\u7eed\u4e0b\u4e00\u573a\uff0c\u76f4\u63a5\u4e0b\u4e86\u64c2\u53f0\uff0c\u743c\u6069\u8fd9\u8fb9\u4fbf\u7b97\u662f\u8d62\u4e86\u7b2c\u4e94\u573a\u3002\u51db\u662f\u9f99\u8109\u8005\uff0c\u5728\u706b\u7130\u65b9\u9762\u7684\u5929\u8d4b\u662f\u4e0e\u751f\u4ff1\u6765\u7684\uff0c\u81ea\u5df1\u53c8\u9884\u5148\u505a\u4e86\u51c6\u5907\uff0c\u4e1d\u6beb\u6ca1\u6709\u706b\u707c\u4f24\uff0c\u4fbf\u662f\u8863\u670d\u90fd\u6ca1\u70e7\u574f\uff0c\u5012\u662f\u8ba9\u743c\u6069\u89c9\u5f97\u53ef\u60dc\uff0c\u5c11\u4e86\u5927\u9971\u773c\u798f\u7684\u673a\u4f1a\u3002</p><p>\u7b2c\u516d\u573a\u6bd4\u8d5b\uff0c\u5bf9\u65b9\u4ec5\u5b58\u7684\u4e4c\u7279\u7f57\u65af\u9b54\u7f13\u7f13\u8d77\u8eab\uff0c\u6b23\u5e03\u6b63\u51c6\u5907\u4e0a\u573a\uff0c\u5374\u88ab\u743c\u6069\u62e6\u4f4f\u4e86\u3002\u201c\u6211\u6765\u5bf9\u4ed8\u5b83\u3002\u201d\u5deb\u5e08\u8bf4\uff0c\u8d70\u4e0a\u64c2\u53f0\u3002</p></article>"}}}]);
