(window.webpackJsonp=window.webpackJsonp||[]).push([[1532],{1555:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u8282 \u535a\u7231",content:"<article><h2>\u7b2c\u56db\u5341\u8282 \u535a\u7231</h2><p>\u4ece\u5916\u8868\u4e0a\u770b\uff0c\u4e4c\u7279\u7f57\u65af\u9b54\u50cf\u4e2a\u7a7f\u7740\u957f\u888d\u7684\u8001\u4eba\uff0c\u65e2\u9ad8\u4e14\u7626\uff0c\u5934\u90e8\u6241\u5e73\uff0c\u4eff\u4f5b\u5728\u51fa\u751f\u7684\u65f6\u5019\u53d7\u6324\u538b\u8fc7\u5ea6\u800c\u53d8\u5f62\u4e86\u4f3c\u7684\uff0c\u4e00\u5f20\u957f\u8138\u4fbf\u5982\u767d\u677f\uff0c\u6ca1\u6709\u9f3b\u5b50\u5634\u5df4\uff0c\u53ea\u6709\u4e00\u53cc\u4e0d\u65ad\u53d8\u6362\u989c\u8272\u7684\u5de8\u5927\u773c\u7403\uff0c\u76ae\u80a4\u5012\u662f\u5f88\u5149\u6ed1\uff0c\u6ca1\u6709\u76b1\u7eb9\uff0c\u4f46\u5e03\u6ee1\u4e86\u5bc6\u5bc6\u9ebb\u9ebb\u7684\u9ed1\u8272\u6591\u70b9\uff0c\u770b\u8d77\u6765\u5341\u5206\u6050\u6016\u3002</p><p>\u201c\u4e4c\u7279\u7f57\u65af\u9b54\uff0c\u901a\u5e38\u662f\u5c24\u683c\u7f57\u65af\u9b54\u519b\u961f\u4e2d\u519b\u5b98\uff0c\u4e0d\u64c5\u8fd1\u6218\uff0c\u7cbe\u901a\u6218\u7565\uff0c\u64c5\u957f\u50ac\u7720\u3001\u5a9a\u60d1\u3001\u6307\u4f7f\u3001\u6697\u793a\u5404\u79cd\u6cd5\u672f\uff0c\u6709\u77ed\u8ddd\u79bb\u65e0\u9650\u5236\u4f20\u9001\u7684\u5929\u8d4b\u5f02\u80fd\uff0c\u80fd\u591f\u53ec\u5524\u4f4e\u9636\u5c24\u683c\u7f57\u65af\u9b54\u76f8\u52a9\uff0c\u514d\u75ab\u5404\u79cd\u6bd2\u7d20\u548c\u5f3a\u9178\u2026\u2026\u5927\u81f4\u5c31\u8fd9\u4e9b\u4e86\u3002\u201d</p><p>\u5728\u4e0a\u64c2\u53f0\u4e4b\u524d\uff0c\u838e\u73de\u514b\u4f4e\u58f0\u628a\u6700\u65b0\u8d44\u6599\u8ddf\u4ed6\u590d\u8ff0\u4e86\u4e00\u904d\uff0c\u8fd9\u662f\u9b45\u9b54\u521a\u521a\u4ece\u9644\u8fd1\u4e00\u4f4d\u8d5b\u573a\u5de5\u4f5c\u4eba\u5458\u7684\u53e3\u4e2d\u5957\u95ee\u51fa\u6765\u7684\uff0c\u82b1\u8d39\u4e86\u5979\u4e24\u5206\u949f\u7684\u751c\u5a9a\u5fae\u7b11\u548c\u4e00\u4e2a\u70ed\u8fa3\u98de\u543b\u3002\u743c\u6069\u76d8\u7b97\u4e86\u4e00\u4e0b\uff0c\u6709\u6052\u5b9a\u7684\u610f\u5fd7\u5c4f\u969c\u5728\uff0c\u4ed6\u538b\u6839\u4e0d\u5728\u4e4e\u90a3\u4e9b\u50ac\u7720\u5a9a\u60d1\u6307\u4f7f\u6697\u793a\uff0c\u77ed\u8ddd\u79bb\u4f20\u9001\u867d\u7136\u9ebb\u70e6\uff0c\u4f46\u90a3\u662f\u9003\u8dd1\u7528\u7684\uff0c\u4e4c\u7279\u7f57\u65af\u9b54\u53c8\u4e0d\u64c5\u8fd1\u6218\uff0c\u6015\u4ed6\u4f55\u6765\u3002\u81f3\u4e8e\u8bf4\u4ed6\u7cbe\u901a\u6218\u7565\uff0c\u662f\u5c24\u683c\u7f57\u65af\u9b54\u519b\u961f\u4e2d\u519b\u5b98\uff0c\u90a3\u5728\u8fd9\u64c2\u53f0\u6bd4\u6b66\u4e2d\u6beb\u65e0\u7528\u5904\uff0c\u514d\u75ab\u6bd2\u7d20\u548c\u5f3a\u9178\u66f4\u65e0\u6240\u8c13\uff0c\u56e0\u4e3a\u8fd9\u79cd\u7c7b\u578b\u7684\u653b\u51fb\u6cd5\u672f\u4e3b\u8981\u90fd\u662f\u5851\u80fd\u548c\u4ea1\u7075\u5b66\u6d3e\u7684\uff0c\u743c\u6069\u53cd\u6b63\u4e0d\u4f1a\u3002</p><p>\u552f\u4e00\u6709\u4e9b\u9ebb\u70e6\u7684\uff0c\u662f\u4ed6\u4f1a\u53ec\u5524\u4f4e\u9636\u5c24\u683c\u7f57\u65af\u9b54\uff0c\u4f46\u8fd9\u4e16\u754c\u4e0a\u54ea\u6709\u4e07\u65e0\u4e00\u5931\u7a33\u7a33\u514b\u6b7b\u5bf9\u624b\u7684\u597d\u4e8b\uff0c\u80fd\u5927\u5360\u4f18\u52bf\u5df2\u7ecf\u5f88\u4e0d\u9519\u4e86\uff0c\u89c1\u673a\u884c\u4e8b\u4fbf\u662f\u3002</p><p>\u89c1\u743c\u6069\u4e0a\u4e86\u64c2\u53f0\uff0c\u4e4c\u7279\u7f57\u65af\u9b54\u4ece\u957f\u8896\u4e2d\u4f38\u51fa\u4e00\u53cc\u67af\u7626\u624b\u638c\uff0c\u72b9\u5982\u9e1f\u722a\uff0c\u5fae\u5fae\u4e00\u5408\uff0c\u4e0a\u524d\u534a\u6b65\uff0c\u6574\u4e2a\u4eba\u9aa4\u7136\u4ece\u7a7a\u6c14\u4e2d\u6d88\u5931\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\u51fa\u73b0\u5728\u743c\u6069\u5de6\u4fa7\uff0c\u968f\u5373\u659c\u8de8\u534a\u6b65\uff0c\u518d\u5ea6\u6d88\u5931\u3002\u743c\u6069\u4e0d\u53bb\u7406\u776c\uff0c\u4ed6\u521a\u4e0a\u64c2\u53f0\u5c31\u5df2\u7ecf\u6697\u4e2d\u542f\u52a8\u4e86\u6cd5\u672f\u9006\u8f6c\u6212\u6307\uff0c\u968f\u5373\u62bd\u51fa\u7d2b\u6c34\u6676\u6cd5\u6756\uff0c\u8f6c\u77ac\u4e4b\u95f4\u5148\u7ed9\u81ea\u5df1\u53e0\u52a0\u4e86\u4e24\u9053\u9632\u5fa1\u6cd5\u672f\uff0c\u518d\u5ea6\u56de\u8fc7\u8eab\u6765\uff0c\u53d1\u73b0\u4e4c\u7279\u7f57\u65af\u9b54\u6b63\u5728\u80cc\u540e\u4e0d\u8fdc\u5904\uff0c\u4e00\u53cc\u5de8\u5927\u7684\u773c\u73e0\u7cbe\u5149\u56db\u5c04\uff0c\u53d8\u6210\u4e86\u6697\u91d1\u8272\uff0c\u6b63\u6b7b\u6b7b\u76ef\u7740\u4ed6\u3002</p><p>\u743c\u6069\u611f\u89c9\u5230\u4e00\u80a1\u51b0\u51b7\u65e0\u5f62\u7684\u610f\u8bc6\u8bd5\u56fe\u4fb5\u5165\u81ea\u5df1\u8111\u6d77\uff0c\u4f46\u4e00\u89e6\u5373\u9000\uff0c\u4ed6\u6697\u81ea\u51b7\u7b11\u7740\uff0c\u53f3\u624b\u4f4e\u5782\uff0c\u6307\u5c16\u5df2\u7ecf\u631f\u4e0a\u4e00\u679a\u77f3\u5f39\uff0c\u6b63\u8981\u8bd5\u63a2\u6027\u5730\u5f39\u51fa\u3002\u4e4c\u7279\u7f57\u65af\u9b54\u8be7\u5f02\u81ea\u5df1\u7684\u50ac\u7720\u51dd\u89c6\u5168\u7136\u4e0d\u8d77\u6548\u7528\uff0c\u89c1\u4ed6\u624b\u4e00\u52a8\uff0c\u8fd8\u4e0d\u7b49\u770b\u6e05\u662f\u4ec0\u4e48\u6cd5\u672f\uff0c\u7acb\u523b\u53cc\u624b\u4e00\u5408\uff0c\u659c\u8de8\u534a\u6b65\uff0c\u77ac\u95f4\u5230\u4e86\u743c\u6069\u5de6\u4fa7\u3002</p><p>\u743c\u6069\u77f3\u5f39\u8fd8\u672a\u5f39\u51fa\uff0c\u89c1\u4ed6\u53c8\u4f20\u9001\uff0c\u53cd\u624b\u7529\u4e86\u51fa\u6765\uff0c\u8fd8\u4e0d\u7b49\u7838\u4e2d\u76ee\u6807\uff0c\u4e4c\u7279\u7f57\u65af\u9b54\u5f80\u540e\u7a0d\u9000\uff0c\u53c8\u8f6c\u79fb\u5230\u4e86\u743c\u6069\u53f3\u4fa7\u3002</p><p>\u201c\u597d\u5feb\u3002\u201d\u743c\u6069\u5fc3\u60f3\u3002</p><p>\u51db\u7684\u65e0\u9650\u706b\u4e2d\u8df3\u8dc3\u4e5f\u6709\u8fd9\u79cd\u7c7b\u4f3c\u6548\u679c\uff0c\u4f46\u5fc5\u987b\u8981\u6709\u706b\u7130\u4f5c\u4e3a\u5a92\u4ecb\uff0c\u5730\u70b9\u662f\u56fa\u5b9a\u7684\uff0c\u8fdc\u4e0d\u5982\u4ed6\u8fd9\u6837\u968f\u5fc3\u6240\u6b32\u3002\u5e78\u597d\u8fd9\u4e4c\u7279\u7f57\u65af\u9b54\u53ea\u4f1a\u9003\u8dd1\uff0c\u653b\u51fb\u529b\u4e0d\u5f3a\uff0c\u5426\u5219\u5012\u662f\u9ebb\u70e6\u900f\u9876\u3002</p><p>\u4e4c\u7279\u7f57\u65af\u9b54\u540c\u6837\u4e5f\u5728\u5934\u75bc\uff0c\u4ed6\u6700\u64c5\u957f\u7684\u4fbf\u662f\u5404\u79cd\u7cbe\u795e\u63a7\u5236\u6cd5\u672f\uff0c\u5c24\u5176\u662f\u53cc\u773c\u5929\u8d4b\u81ea\u5e26\u7684\u50ac\u7720\u51dd\u89c6\uff0c\u53c8\u7c7b\u4f3c\u7f8e\u675c\u838e\u7684\u6548\u679c\uff0c\u53ea\u8981\u88ab\u4ed6\u51dd\u795e\u77aa\u4e0a\u4e00\u773c\uff0c\u610f\u5fd7\u575a\u5b9a\u8005\u4e5f\u8981\u660f\u660f\u6c89\u6c89\uff0c\u610f\u5fd7\u8584\u5f31\u8005\u76f4\u63a5\u5c31\u547c\u547c\u5927\u7761\uff0c\u7136\u800c\u5bf9\u8fd9\u4e2a\u4eba\u7c7b\u4f3c\u4e4e\u5168\u65e0\u6548\u679c\u4e00\u822c\u3002</p><p>\u201c\u96be\u9053\u4ed6\u5176\u5b9e\u662f\u4e2a\u5deb\u5996\u4f2a\u88c5\u7684\uff1f\u201d</p><p>\u743c\u6069\u53c8\u5f39\u51fa\u4e00\u679a\u77f3\u5f39\u6765\uff0c\u7136\u540e\u4e4c\u7279\u7f57\u65af\u9b54\u53c8\u5728\u88ab\u51fb\u4e2d\u524d\u9003\u4e4b\u592d\u592d\uff0c\u4ed6\u72e1\u733e\u5f97\u7d27\uff0c\u51ed\u501f\u5929\u8d4b\u7684\u65e0\u9650\u4f20\u9001\u80fd\u529b\uff0c\u53ea\u8981\u4e00\u770b\u5230\u5bf9\u65b9\u6709\u52a8\u4f5c\uff0c\u4e0d\u7b49\u770b\u6e05\u7acb\u523b\u5c31\u9003\u3002\u4ed6\u6253\u5f97\u4e3b\u610f\u4e5f\u5f88\u7b80\u5355\uff0c\u5bf9\u624b\u660e\u663e\u662f\u4e2a\u5deb\u5e08\uff0c\u6cd5\u672f\u603b\u6709\u7a77\u5c3d\u7684\u65f6\u5019\uff0c\u53cd\u6b63\u6bd4\u8d5b\u53c8\u6ca1\u6709\u65f6\u95f4\u9650\u5236\uff0c\u5c3d\u7ba1\u5f80\u4e0b\u8017\u7740\u5c31\u662f\u3002</p><p>\u743c\u6069\u63a5\u7740\u53c8\u53d1\u51fa\u4e24\u679a\u77f3\u5f39\uff0c\u4f9d\u65e7\u6ca1\u6709\u4f24\u5230\u5bf9\u624b\u534a\u6839\u6beb\u6bdb\uff0c\u4f46\u5374\u5e76\u975e\u6ca1\u6709\u6536\u83b7\u3002\u4ed6\u6ce8\u610f\u5230\u4e86\u4e24\u70b9\uff1a\u7b2c\u4e00\uff0c\u8fd9\u4e2a\u4e4c\u7279\u7f57\u65af\u9b54\u6bcf\u6b21\u4f20\u9001\u7684\u8ddd\u79bb\u90fd\u975e\u5e38\u77ed\uff1b\u7b2c\u4e8c\uff0c\u5b83\u6bcf\u6b21\u4f20\u9001\u4e4b\u524d\u90fd\u8981\u8fc8\u51fa\u534a\u6b65\uff0c\u800c\u4e14\u8fc8\u6b65\u7684\u65b9\u5411\u5c31\u662f\u4f20\u9001\u7684\u65b9\u5411\u3002</p><p>\u4ed6\u5fc3\u5ff5\u5fae\u52a8\uff0c\u5047\u88c5\u518d\u5ea6\u65bd\u6cd5\u653b\u51fb\uff0c\u4e4c\u7279\u7f57\u65af\u9b54\u89c1\u72b6\uff0c\u4e0d\u5047\u601d\u7d22\u4fbf\u53c8\u77ac\u79fb\u5f00\u6765\uff0c\u743c\u6069\u770b\u51c6\u4ed6\u8fc8\u6b65\u7684\u65b9\u5411\uff0c\u5fc3\u4e2d\u4f30\u7b97\u5927\u81f4\u8ddd\u79bb\uff0c\u6325\u821e\u6cd5\u6756\u5feb\u901f\u5212\u4e86\u4e2a\u7b26\u53f7\uff0c\u9694\u7a7a\u865a\u865a\u4e00\u70b9\u3002</p><p>\u4ee5\u4ed6\u6cd5\u6756\u9065\u6307\u4e00\u70b9\u4e3a\u5706\u5fc3\uff0c\u5468\u56f4\u516d\u5341\u82f1\u5c3a\u5185\u7684\u5ca9\u77f3\u5730\u9762\u50cf\u88ab\u5de8\u529b\u63d0\u62c9\u4f3c\u7684\uff0c\u9aa4\u7136\u5347\u8d77\u5927\u5927\u5c0f\u5c0f\u5341\u51e0\u6839\u5c16\u9510\u77f3\u67f1\u6765\u3002\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u4e4c\u7279\u7f57\u65af\u9b54\u8eab\u5f62\u4ece\u7a7a\u6c14\u4e2d\u95ea\u51fa\uff0c\u5b83\u6b63\u649e\u5728\u5176\u4e2d\u4e00\u6839\u77f3\u67f1\u4e0a\uff0c\u989d\u5934\u4e0a\u987f\u65f6\u9c9c\u8840\u6dcb\u6f13\u4e00\u7247\u3002\u5b83\u672c\u80fd\u5730\u53cd\u5e94\u5c31\u662f\u7acb\u523b\u518d\u9003\uff0c\u4f46\u4e3e\u76ee\u4e00\u770b\uff0c\u5468\u56f4\u5bc6\u5bc6\u9ebb\u9ebb\u90fd\u5e03\u6ee1\u77f3\u67f1\uff0c\u963b\u9694\u89c6\u7ebf\uff0c\u4fbf\u662f\u4f20\u9001\u672f\u4e5f\u65e0\u6cd5\u5b9a\u4f4d\u76ee\u6807\u4e86\u3002</p><p>\u5c31\u662f\u743c\u6069\u81ea\u5df1\uff0c\u4e5f\u5bf9\u8fd9\u4e2a\u6cd5\u672f\u6548\u679c\u5fae\u5fae\u5403\u4e86\u4e00\u60ca\uff0c\u6ca1\u60f3\u5230\u6548\u679c\u4f1a\u8fd9\u6837\u597d\uff0c\u660e\u663e\u8d85\u51fa\u9884\u671f\u3002\u673a\u4e0d\u53ef\u5931\uff0c\u4ed6\u4f38\u624b\u4ece\u6000\u4e2d\u53d6\u51fa\u8428\u74e6\u68cb\u76d8\uff0c\u63b7\u51fa\u4e09\u679a\u98df\u4eba\u9b54\u68cb\u5b50\uff0c\u5316\u4f5c\u9b54\u50cf\u51b2\u8fdb\u4e86\u77f3\u67f1\u6797\u4e2d\u3002</p><p>\u4e4c\u7279\u7f57\u65af\u9b54\u89c1\u60c5\u5f62\u5371\u6025\uff0c\u9003\u9041\u5df2\u7ecf\u6765\u4e0d\u53ca\uff0c\u5589\u5499\u91cc\u5495\u5495\u4f5c\u54cd\uff0c\u53cc\u624b\u4e00\u9635\u6447\u6446\uff0c\u8eab\u65c1\u4e24\u9053\u9ed1\u8272\u4f20\u9001\u95e8\u5212\u5f00\uff0c\u56db\u53ea\u4eba\u5f62\u5927\u5c0f\u7684\u602a\u7269\u4ece\u4e2d\u8df3\u51fa\uff0c\u5b83\u4eec\u4eff\u4f5b\u53d8\u5f02\u7684\u6606\u866b\uff0c\u53c8\u50cf\u662f\u76f4\u7acb\u884c\u8d70\u7684\u8682\u8681\uff0c\u773c\u5192\u706b\u5149\uff0c\u56db\u53ea\u524d\u722a\u6325\u821e\u7740\u8d64\u7ea2\u8272\u4e09\u53c9\u621f\uff0c\u8fce\u4e0a\u4e86\u98df\u4eba\u9b54\u9b54\u50cf\u3002</p><p>\u72fc\u7259\u68d2\u548c\u4e09\u53c9\u621f\u649e\u5728\u4e00\u8d77\uff0c\u7830\u7830\u556a\u556a\u4e00\u9635\u4e71\u6597\uff0c\u6253\u5f97\u77f3\u67f1\u6797\u4e2d\u77f3\u5c51\u7eb7\u6e85\uff0c\u70df\u5c18\u5f25\u6f2b\uff0c\u4e00\u65f6\u95f4\u90fd\u770b\u4e0d\u6e05\u695a\u60c5\u5f62\u4e86\u3002\u743c\u6069\u5524\u8d77\u6597\u7bf7\u5185\u4fa7\u80f8\u9488\u4e2d\u7684\u98de\u884c\u672f\uff0c\u98d8\u4e0a\u77f3\u67f1\u6797\u7684\u4e0a\u7a7a\uff0c\u7b49\u5f85\u7ed3\u679c\u3002</p><p>\u7247\u523b\u4e4b\u540e\uff0c\u70df\u5c18\u6e10\u7184\uff0c\u4e24\u4e2a\u6606\u866b\u602a\u7269\u5df2\u7ecf\u5934\u65ad\u9aa8\u6298\uff0c\u8db4\u5728\u5730\u4e0a\uff0c\u4e09\u4e2a\u98df\u4eba\u9b54\u9b54\u50cf\u4e5f\u90fd\u5168\u8eab\u91cd\u521b\uff0c\u51e0\u4e4e\u65e0\u6cd5\u518d\u52a8\u5f39\u3002\u4e4c\u7279\u7f57\u65af\u9b54\u5728\u8fd9\u6df7\u6218\u4e2d\u5c45\u7136\u65e0\u6059\uff0c\u800c\u4e14\u5df2\u7ecf\u6478\u5230\u4e86\u77f3\u67f1\u6797\u7684\u8fb9\u7f18\uff0c\u773c\u770b\u5c31\u8981\u9003\u51fa\u3002\u5018\u82e5\u8ba9\u5b83\u79bb\u5f00\uff0c\u743c\u6069\u5374\u6ca1\u529e\u6cd5\u518d\u56f0\u4f4f\u5b83\u4e86\u3002</p><p>\u4e00\u9053\u7070\u5149\u4ece\u6cd5\u6756\u9876\u7aef\u5c04\u51fa\uff0c\u51c6\u786e\u5730\u547d\u4e2d\u4e86\u4e4c\u7279\u7f57\u65af\u9b54\uff0c\u5c06\u4ed6\u5316\u4f5c\u77f3\u50cf\u3002</p><p>\u743c\u6069\u843d\u56de\u5730\u9762\uff0c\u5c06\u9b54\u50cf\u53d8\u56de\u68cb\u5b50\uff0c\u6536\u4e86\u56de\u6765\uff0c\u7136\u540e\u8d70\u5230\u77f3\u50cf\u9762\u524d\u8f7b\u8f7b\u4e00\u63a8\u3002\u77f3\u50cf\u7830\u7136\u5012\u5730\uff0c\u788e\u6210\u51e0\u5757\uff0c\u4e4c\u7279\u7f57\u65af\u9b54\u4fbf\u662f\u5f7b\u5e95\u6b7b\u4ea1\uff0c\u5373\u4f7f\u7528\u89e3\u9664\u77f3\u5316\u6cd5\u672f\u4e5f\u6551\u4e0d\u56de\u6765\u4e86\u3002</p><p>\u201c\u5e72\u5f97\u4e0d\u9519\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\u3002\u201d\u8d70\u4e0b\u573a\u7684\u65f6\u5019\uff0c\u6b23\u5e03\u96be\u5f97\u5730\u51b2\u743c\u6069\u7b11\u4e86\u4e00\u7b11\uff0c\u51fa\u8a00\u5938\u5956\u3002</p><p>\u51db\u76f4\u63a5\u6251\u8fc7\u6765\u62b1\u4e86\u4e00\u62b1\uff0c\u201c\u771f\u5389\u5bb3\uff0c\u201d\u5979\u5174\u594b\u5730\u8bf4\uff0c\u201c\u4f60\u56f0\u4f4f\u5b83\u7684\u90a3\u4e2a\u6cd5\u672f\u662f\u4ec0\u4e48\uff0c\u597d\u50cf\u5f88\u6709\u8da3\u7684\u6837\u5b50\uff0c\u6559\u7ed9\u6211\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u90a3\u662f\u77f3\u6797\u672f\uff0c\u5353\u5c14\u53d1\u660e\u7684\u6cd5\u672f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6211\u81ea\u5df1\u4e5f\u6ca1\u60f3\u5230\u6548\u679c\u4f1a\u6709\u90a3\u4e48\u597d\uff0c\u6211\u539f\u672c\u4ee5\u4e3a\u6700\u591a\u80fd\u53ec\u8d77\u4e03\u516b\u6839\u77f3\u67f1\u5c31\u4e0d\u9519\u4e86\uff0c\u6bd4\u6211\u9884\u671f\u5f97\u5f3a\u4e86\u4e00\u534a\u4e0d\u6b62\u3002\u201d</p><p>\u201c\u90a3\u5f88\u6b63\u5e38\u554a\uff0c\u201d\u6b23\u5e03\u8f6c\u8fc7\u5934\u6765\uff0c\u6709\u4e9b\u8be7\u5f02\u5730\u770b\u7740\u4ed6\uff0c\u201c\u8fd9\u91cc\u662f\u6df1\u6e0a\uff0c\u6c38\u6052\u7684\u6df7\u4e71\u548c\u53d8\u5316\u4e4b\u5730\uff0c\u77f3\u6797\u672f\u662f\u53d8\u5316\u6cd5\u672f\uff0c\u6548\u679c\u672c\u6765\u5c31\u4f1a\u5927\u5927\u63d0\u5347\u7684\u3002\u5018\u82e5\u8fd9\u91cc\u4e0d\u662f\u65ad\u57df\u9547\u7684\u8bdd\uff0c\u522b\u8bf4\u4e00\u534a\uff0c\u63d0\u5347\u4e00\u500d\u90fd\u5f88\u6b63\u5e38\u554a\u2014\u2014\u4f60\u96be\u9053\u4e0d\u77e5\u9053\uff1f\u201d</p><p>\u201c\u6709\u8fd9\u56de\u4e8b\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u5426\u5219\u8475\u9732\u4e3a\u4ec0\u4e48\u4f1a\u63d0\u8bae\u627e\u4f60\u53c2\u8d5b\uff0c\u201d\u6b23\u5e03\u4e0d\u4ee5\u4e3a\u7136\u5730\u8bf4\uff0c\u201c\u5982\u679c\u4ec5\u8bba\u4f60\u7684\u672c\u6765\u6c34\u5e73\uff0c\u867d\u7136\u52c9\u5f3a\u591f\u770b\uff0c\u4f46\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\u4e86\u4e0d\u8d77\u7684\u3002\u4f46\u4f60\u662f\u4e2a\u53d8\u5316\u5e08\u5427\uff0c\u5728\u6df1\u6e0a\u8fd9\u91cc\u5c31\u7b49\u4e8e\u51ed\u7a7a\u5f3a\u5316\u4e86\u4e00\u5927\u622a\u554a\u3002\u201d</p><p>\u743c\u6069\u6115\u7136\uff0c\u56de\u8fc7\u5934\u770b\u838e\u73de\u514b\uff0c\u201c\u662f\u8fd9\u6837\u7684\u5417\uff1f\u201d</p><p>\u9b45\u9b54\u70b9\u5934\uff0c\u201c\u6211\u4ee5\u4e3a\u4f60\u77e5\u9053\uff0c\u4e3b\u4eba\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u4ee5\u4e3a\u8fd9\u5bf9\u5deb\u5e08\u6765\u8bf4\u662f\u5e38\u8bc6\u3002\u201d</p><p>\u89c1\u5927\u5bb6\u90fd\u7528\u4e0d\u53ef\u601d\u8bae\u7684\u773c\u5149\u770b\u7740\u81ea\u5df1\uff0c\u743c\u6069\u6709\u4e9b\u65e0\u5948\u5730\u8038\u8038\u80a9\uff0c\u201c\u6211\u786e\u5b9e\u4e0d\u77e5\u9053\u8fd9\u4e2a\u5e38\u8bc6\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6ca1\u5b66\u8fc7\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4eec\u9634\u9b42\u57ce\u7684\u9b54\u6cd5\u6559\u80b2\u771f\u5931\u8d25\u3002\u201d\u6b23\u5e03\u8bc4\u4ef7\u3002</p><p>\u203b\u203b\u203b</p><p>\u5bf9\u4e8e\u9634\u9b42\u57ce\u7684\u9b54\u6cd5\u6559\u80b2\u662f\u5426\u5931\u8d25\u7684\u95ee\u9898\uff0c\u743c\u6069\u6ca1\u5174\u8da3\u7ee7\u7eed\u8ba8\u8bba\u3002\u6bcf\u5929\u6700\u591a\u4e24\u8f6e\u6bd4\u8d5b\uff08\u6709\u53ef\u80fd\u53ea\u5b89\u6392\u4e00\u8f6e\uff0c\u8fd9\u5b8c\u5168\u770b\u4e3b\u529e\u8005\u7684\u5fc3\u60c5\uff09\uff0c\u73b0\u5728\u743c\u6069\u8fd9\u652f\u961f\u4f0d\u5df2\u7ecf\u6210\u529f\u641e\u5b9a\uff0c\u53ef\u4ee5\u6253\u9053\u56de\u5e9c\uff0c\u7b49\u660e\u5929\u518d\u6765\u4e86\u3002\u653e\u773c\u770b\u53bb\uff0c\u5176\u4ed6\u6bd4\u8d5b\u573a\u5730\u90fd\u8fd8\u5728\u6fc0\u70c8\u5730\u8fdb\u884c\u7740\uff0c\u6076\u9b54\u548c\u5404\u79cd\u975e\u6076\u9b54\u5f02\u602a\u6253\u6210\u4e00\u56e2\uff0c\u4e0d\u53ef\u5f00\u4ea4\u3002</p><p>\u5728\u521a\u624d\u6bd4\u8d5b\u7684\u65f6\u5019\uff0c\u8475\u9732\u4e00\u76f4\u5728\u56db\u5904\u89c2\u671b\uff0c\u5bfb\u627e\u8428\u9a6c\u65af\u7279\u7684\u8eab\u5f71\u3002\u6309\u9053\u7406\u8bf4\uff0c\u4ed6\u5e94\u8be5\u4e5f\u4f1a\u6765\u53c2\u8d5b\u624d\u5bf9\uff0c\u4f46\u770b\u904d\u4e86\u6574\u4e2a\u51b3\u6597\u573a\u4e5f\u6ca1\u53d1\u73b0\u4ed6\u7684\u8eab\u5f71\uff0c\u8fd9\u4fbf\u8ba9\u4eba\u89c9\u5f97\u5341\u5206\u5947\u602a\u3002</p><p>\u201c\u96be\u9053\u4ed6\u4e0d\u60f3\u8981\u90a3\u679a\u5370\u7ae0\u4e86\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u76f4\u89c9\u544a\u8bc9\u6211\u4ed6\u5c31\u5728\u8fd9\u91cc\uff0c\u201d\u6b23\u5e03\u8bf4\uff0c\u201c\u6211\u80fd\u80af\u5b9a\u3002\u201d</p><p>\u201c\u90a3\u4e3a\u4ec0\u4e48\u627e\u4e0d\u5230\uff1f\u201d</p><p>\u201c\u4ed6\u6709\u53ef\u80fd\u4f2a\u88c5\u53d8\u5f62\u4e86\u3002\u201d</p><p>\u8428\u9a6c\u65af\u7279\u8fd9\u79cd\u5927\u5deb\u5e08\uff0c\u53d8\u6210\u4ec0\u4e48\u6a21\u6837\u90fd\u8f7b\u800c\u6613\u4e3e\u3002\u573a\u4e0a\u4eba\u5b9e\u5728\u592a\u591a\uff0c\u8475\u9732\u53c8\u4e0d\u53ef\u80fd\u4e00\u4e2a\u4e00\u4e2a\u4ed4\u7ec6\u9274\u522b\u8fc7\u6765\uff0c\u9057\u6f0f\u5f88\u6b63\u5e38\u3002\u5018\u82e5\u5728\u7269\u8d28\u754c\uff0c\u6b23\u5e03\u8fd9\u79cd\u9009\u6c11\u8fd8\u80fd\u8fdc\u8fdc\u5c31\u5bdf\u89c9\u5230\u90aa\u6076\u6c14\u606f\uff0c\u636e\u6b64\u5224\u65ad\u65b9\u4f4d\uff0c\u4f46\u5982\u4eca\u8fd9\u91cc\u662f\u6df1\u6e0a\uff0c\u6bcf\u4e2a\u6076\u9b54\u8eab\u4e0a\u90fd\u90aa\u6c14\u51b2\u5929\uff0c\u6839\u672c\u65e0\u4ece\u5206\u8fa8\u3002</p><p>\u6bd4\u8d5b\u5b8c\u6bd5\uff0c\u743c\u6069\u7b49\u4eba\u9886\u5230\u5956\u91d1\uff0c\u662f\u6ee1\u6ee1\u4e00\u53e3\u888b\u7684\u7075\u9b42\u5b9d\u77f3\uff0c\u636e\u838e\u73de\u514b\u8bf4\uff0c\u867d\u7136\u54c1\u8d28\u4f4e\u52a3\uff0c\u91cc\u9762\u7684\u7075\u9b42\u7d20\u8d28\u5f88\u5dee\uff0c\u4f46\u4e5f\u52c9\u5f3a\u7b97\u662f\u7b14\u5c0f\u8d22\u4e86\u3002\u963f\u5fd2\u59ae\u53bb\u8865\u5145\u4e86\u4e00\u6279\u7206\u88c2\u7bad\u548c\u9b54\u6cd5\u7bad\uff0c\u51db\u53bb\u4ed8\u4e86\u90a3\u4e24\u5e45\u8033\u5760\u7684\u8d26\uff0c\u53c8\u989d\u5916\u4e70\u4e86\u4e24\u5e45\uff0c\u4e8e\u662f\u5927\u5bb6\u7686\u5927\u6b22\u559c\u3002</p><p>\u5728\u63a5\u4e0b\u6765\u7684\u4e09\u5929\u91cc\uff0c\u743c\u6069\u7b49\u4eba\u906d\u9047\u4e86\u5404\u79cd\u5f3a\u654c\uff0c\u5305\u62ec\u6df1\u6e0a\u91cc\u7684\u5404\u79cd\u6076\u9b54\u3001\u5728\u8840\u6218\u53cc\u65b9\u4e4b\u95f4\u6765\u5f80\u8d38\u6613\u7684\u5c24\u683c\u7f57\u65af\u9b54\u3001\u5185\u5c42\u4f4d\u9762\u7684\u5143\u7d20\u9886\u4e3b\u3001\u6765\u81ea\u7269\u8d28\u754c\u7684\u5deb\u5996\u3001\u7075\u5438\u602a\uff0c\u751a\u81f3\u8fd8\u6709\u4e5d\u5c42\u5730\u72f1\u91cc\u7684\u9b54\u9b3c\u3002</p><p>\u5bf9\u4e8e\u6700\u540e\u8fd9\u70b9\uff0c\u743c\u6069\u60ca\u8bb6\u4e07\u5206\uff0c\u79c1\u4e0b\u66fe\u7ecf\u8be2\u95ee\u838e\u73de\u514b\uff0c\u201c\u4f60\u4eec\u6076\u9b54\u548c\u9b54\u9b3c\u4e0d\u662f\u6b7b\u654c\u4e48\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u6211\u8bb0\u5f97\u4f60\u8bf4\u8fc7\u8fd9\u51b3\u6597\u5927\u8d5b\u6b63\u662f\u4e3a\u4e86\u8840\u6218\u505a\u51c6\u5907\u7684\u5427\uff0c\u800c\u4e14\u9a6c\u4e0a\u4f60\u4eec\u5c31\u8981\u548c\u9b54\u9b3c\u5f00\u6218\u4e86\u5bf9\u5427\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4eec\u4e3a\u4ec0\u4e48\u5141\u8bb8\u9b54\u9b3c\u53c2\u8d5b\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u6bd4\u8d5b\u89c4\u5219\u7684\u5236\u5b9a\u8005\u662f\u4e2a\u5fc3\u7406\u53d8\u6001\u554a\u3002\u201d</p><p>\u636e\u8bf4\u683c\u62c9\u5179\u7279\u5f53\u65f6\u63d0\u51fa\u5e94\u8be5\u517c\u5bb9\u5e76\u5305\uff0c\u5141\u8bb8\u6240\u6709\u5b58\u5728\u2014\u2014\u751a\u81f3\u5305\u62ec\u5730\u72f1\u7684\u9b54\u9b3c\u548c\u795e\u754c\u7684\u5929\u4f7f\u6765\u53c2\u8d5b\uff0c\u5927\u5bb6\u90fd\u88ab\u4ed6\u7684\u5f02\u60f3\u5929\u5f00\u5413\u574f\u4e86\u3002\u4f46\u683c\u62c9\u5179\u7279\u51ed\u501f\u4ed6\u51fa\u8272\u7684\u53e3\u624d\u548c\u5f3a\u6709\u529b\u7684\u62f3\u5934\uff08\u4e3b\u8981\u662f\u540e\u8005\uff09\u8bf4\u670d\u4e86\u6240\u6709\u6076\u9b54\uff0c\u6700\u7ec8\u901a\u8fc7\u4e86\u8fd9\u6761\u89c4\u5219\u3002\u81f3\u4e8e\u4ed6\u7684\u7406\u7531\u4e5f\u5f88\u7b80\u5355\uff1a\u6076\u9b54\u7684\u672c\u8d28\u662f\u6df7\u4e71\u53db\u9006\uff0c\u6240\u4ee5\u5c31\u4e0d\u5e94\u8be5\u6709\u95e8\u6237\u4e4b\u89c1\uff0c\u4e0d\u5e94\u8be5\u606a\u5b88\u4ec0\u4e48\u6761\u6761\u6846\u6846\uff0c\u9b54\u9b3c\u600e\u4e48\u4e86\uff0c\u5929\u4f7f\u600e\u4e48\u4e86\uff0c\u5e94\u8be5\u4e00\u89c6\u540c\u4ec1\uff0c\u4e0d\u5fc5\u533a\u522b\u5bf9\u5f85\u3002</p><p>\u201c\u4ed6\u771f\u662f\u535a\u7231\u3002\u201d\u743c\u6069\u611f\u53f9\u3002</p><p>\u201c\u90a3\u81ea\u7136\uff0c\u5728\u6df1\u6e0a\u91cc\u8c01\u4e0d\u77e5\u9053\u4e4c\u9eef\u541b\u4e3b\u683c\u62c9\u5179\u7279\u662f\u6700\u6700\u535a\u7231\u7684\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u6211\u4e3a\u4ec0\u4e48\u89c9\u5f97\u4f60\u7684\u8bdd\u91cc\u597d\u50cf\u6709\u522b\u7684\u610f\u601d\u2026\u2026\u201d</p><p>\u201c\u6ca1\u6709\u554a\uff0c\u6211\u7684\u610f\u601d\u662f\u683c\u62c9\u5179\u7279\u662f\u4e2a\u53cc\u6027\u604b\uff0c\u65e2\u559c\u6b22\u5973\u4eba\u53c8\u559c\u6b22\u7537\u4eba\uff0c\u6240\u4ee5\u786e\u5b9e\u5f88\u535a\u7231\u3002\u610f\u601d\u5f88\u660e\u786e\uff0c\u4f60\u4e0d\u7528\u8bef\u89e3\u3002\u201d</p><p>\u201c\u5514\uff0c\u8fd9\u4e2a\u6211\u5012\u662f\u542c\u8bf4\u8fc7\u3002\u201d</p><p>\u201c\u800c\u4e14\u4ed6\u8fd8\u6709\u79cd\u7656\u597d\uff0c\u559c\u6b22\u53d8\u6210\u5973\u4eba\u53bb\u52fe\u642d\u7537\u4eba\u2026\u2026\u201d</p><p>\u743c\u6069\u6b63\u597d\u5728\u559d\u679c\u6c41\uff0c\u4e00\u53e3\u545b\u4e86\u51fa\u6765\uff0c\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u771f\u7684\uff0c\u4ed6\u66fe\u7ecf\u53d8\u6210\u9b45\u9b54\u5973\u738b\u7f8e\u574e\u4fee\u7279\u7684\u6a21\u6837\uff0c\u52fe\u5f15\u6df1\u6e0a\u7b2c\u4e8c\u5341\u4e09\u5c42\u9886\u4e3b\u79d1\u65af\u5f7b\u5947\uff0c\u628a\u90a3\u7b28\u86cb\u800d\u5f97\u56e2\u56e2\u8f6c\u2014\u2014\u8fd9\u4ef6\u4e8b\u6df1\u6e0a\u91cc\u6ca1\u6709\u8c01\u4e0d\u77e5\u9053\u7684\uff0c\u56e0\u4e3a\u683c\u62c9\u5179\u7279\u81ea\u5df1\u90fd\u5f88\u5f97\u610f\uff0c\u7ecf\u5e38\u62ff\u51fa\u6765\u70ab\u8000\u3002\u201d</p><p>\u201c\u2026\u2026\u73b0\u5728\u6211\u7ec8\u4e8e\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u80fd\u5f53\u4e0a\u6076\u9b54\u541b\u4e3b\u4e86\u3002\u201d</p><p>\u7b2c\u4e09\u5929\u7684\u6bd4\u8d5b\u53ea\u6709\u4e00\u8f6e\uff0c\u63a5\u4e0b\u6765\u5c31\u662f\u516b\u5f3a\u4e89\u593a\u4e86\u3002\u6b23\u5e03\u5927\u5c55\u795e\u5a01\uff0c\u4e09\u4e0b\u4e94\u9664\u4e8c\u5c31\u641e\u5b9a\u4e86\u516d\u4e2a\u5bf9\u624b\uff0c\u5979\u548c\u8475\u9732\u51b3\u5b9a\u8d81\u7740\u73b0\u5728\u9009\u624b\u8f83\u5c11\u7684\u65f6\u5019\u53bb\u522b\u7684\u8d5b\u573a\u8f6c\u8f6c\uff0c\u770b\u80fd\u5426\u627e\u5230\u8428\u9a6c\u65af\u7279\uff0c\u81f3\u5c11\u786e\u5b9a\u76ee\u6807\u3002\u743c\u6069\u548c\u6885\u83f2\u65af\u4e0d\u613f\u591a\u5446\uff0c\u76f4\u63a5\u5148\u56de\u4e86\u65c5\u9986\u3002</p><p>\u5929\u6c14\u708e\u70ed\uff0c\u6885\u83f2\u65af\u5148\u8fdb\u6d74\u5ba4\u6d17\u6fa1\uff0c\u88f9\u7740\u6d74\u5dfe\u51fa\u6765\uff0c\u53d1\u73b0\u743c\u6069\u624b\u4e0a\u62ff\u7740\u4e00\u53ea\u676f\u5b50\uff0c\u89c1\u91cc\u9762\u7684\u6db2\u4f53\u989c\u8272\u6709\u4e9b\u5947\u602a\uff0c\u201c\u90a3\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5a9a\u836f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u521a\u914d\u597d\u7684\uff0c\u8981\u4e0d\u8981\u4eca\u665a\u8bd5\u8bd5\u3002\u201d</p><p>\u6885\u83f2\u65af\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u8fd8\u771f\u662f\u4e0d\u6b7b\u5fc3\u5462\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u90a3\u79cd\u4e1c\u897f\u5bf9\u6211\u6ca1\u4ec0\u4e48\u7528\u554a\u3002\u201d</p><p>\u5a9a\u836f\u7684\u4f5c\u7528\uff0c\u4e3b\u8981\u4e5f\u5c31\u662f\u50ac\u60c5\uff0c\u8ba9\u4eba\u653e\u677e\u8eab\u4f53\uff0c\u6311\u8d77\u6b32\u5ff5\uff0c\u6682\u65f6\u4e27\u5931\u81ea\u5236\u529b\uff0c\u5c5e\u4e8e\u7cbe\u795e\u65b9\u9762\u7684\u5f71\u54cd\uff0c\u4f46\u5723\u6b66\u58eb\u5374\u6b63\u662f\u610f\u5fd7\u575a\u5b9a\u7684\u5178\u8303\u3002\u5728\u5e7d\u6697\u5730\u57df\u91cc\u4f4f\u7684\u8fd9\u51e0\u4e2a\u6708\uff0c\u743c\u6069\u5076\u5c14\u4e5f\u914d\u8fc7\u4e00\u4e9b\u5a9a\u836f\uff0c\u60f3\u589e\u52a0\u95fa\u623f\u95f4\u7684\u4e50\u8da3\uff0c\u53ea\u662f\u5bf9\u6885\u83f2\u65af\u6548\u679c\u90fd\u751a\u5fae\uff0c\u5979\u867d\u7136\u4e0d\u4f1a\u4e3b\u52a8\u53bb\u6297\u62d2\uff0c\u5374\u4e0d\u7531\u81ea\u4e3b\u5730\u5c31\u62b5\u5fa1\u6389\u4e86\u3002\u5f53\u7136\uff0c\u8db3\u591f\u5f3a\u529b\u5230\u80fd\u591f\u6467\u6bc1\u5979\u7684\u610f\u5fd7\u529b\u7684\u5a9a\u836f\uff0c\u743c\u6069\u4e5f\u4e0d\u662f\u914d\u4e0d\u51fa\u6765\uff0c\u53ea\u662f\u90a3\u5c31\u592a\u8fc7\u9738\u9053\uff0c\u4f1a\u635f\u4f24\u8eab\u4f53\u4e86\uff0c\u4e0d\u6562\u4f7f\u7528\u3002</p><p>\u201c\u5514\uff0c\u8fd9\u6b21\u4e0d\u4e00\u6837\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u662f\u6211\u53e6\u5916\u8bf7\u4eba\u914d\u7684\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u4e00\u5411\u5439\u5618\u81ea\u5df1\u662f\u4e00\u6d41\u7684\u836f\u5242\u5e08\u4e48\uff0c\u600e\u4e48\u8fd8\u8981\u627e\u522b\u4eba\u914d\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u672f\u4e1a\u6709\u4e13\u653b\u561b\uff0c\u6211\u6bd5\u7adf\u4e0d\u662f\u4e13\u95e8\u7814\u7a76\u8fd9\u4e2a\u7684\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u662f\u54ea\u4f4d\u4e13\u5bb6\u914d\u7684\u3002\u201d</p><p>\u201c\u838e\u73de\u514b\u3002\u201d</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u76b1\u4e86\u76b1\u7709\uff0c\u201c\u4f60\u522b\u592a\u76f8\u4fe1\u5979\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u867d\u7136\u6709\u7075\u9b42\u5951\u7ea6\u5728\uff0c\u4f46\u7ec8\u7a76\u8fd8\u662f\u8981\u63d0\u9632\u70b9\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u6211\u5411\u5979\u518d\u4e09\u786e\u8ba4\u8fc7\u4e86\uff0c\u8fd9\u526f\u836f\u5242\u6ca1\u95ee\u9898\uff0c\u4e0d\u4f1a\u6709\u4efb\u4f55\u526f\u4f5c\u7528\u548c\u989d\u5916\u5f71\u54cd\u3002\u6211\u95ee\u5f97\u975e\u5e38\u660e\u786e\uff0c\u5979\u6ca1\u529e\u6cd5\u6492\u8c0e\u7684\u3002\u201d</p><p>\u201c\u4f60\u8981\u60f3\u8bd5\u8bd5\uff0c\u90a3\u5c31\u8bd5\u8bd5\u5427\uff0c\u201d\u6885\u83f2\u65af\u4e5f\u6ca1\u592a\u5728\u610f\uff0c\u201c\u53ea\u662f\u5982\u679c\u5230\u65f6\u5019\u53c8\u6ca1\u6548\u679c\uff0c\u522b\u8bf4\u6211\u6027\u51b7\u6de1\u554a\u3002\u201d</p><p>\u201c\u6211\u4ece\u6ca1\u8fd9\u4e48\u8bf4\u8fc7\u5427\u3002\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u6211\u77e5\u9053\u90a3\u662f\u4f60\u7684\u5fc3\u58f0\uff0c\u6211\u66ff\u4f60\u8bf4\u51fa\u6765\u800c\u5df2\u2026\u2026\u5bf9\u4e86\uff0c\u8fd9\u526f\u836f\u662f\u53ea\u5bf9\u5973\u6027\u6709\u7528\uff1f\u201d</p><p>\u201c\u90a3\u5012\u6ca1\u8bf4\uff0c\u5e94\u8be5\u901a\u7528\u5427\u3002\u201d</p><p>\u201c\u90a3\u4f60\u5148\u8bd5\u8bd5\uff0c\u6211\u770b\u770b\u6548\u679c\u3002\u201d</p><p>\u201c\u6211\u610f\u5fd7\u529b\u592a\u5dee\uff0c\u6839\u672c\u6d4b\u8bd5\u4e0d\u51fa\u836f\u5242\u7684\u6548\u679c\u554a\u3002\u201d</p><p>\u201c\u6240\u4ee5\u4f60\u5e94\u8be5\u52a0\u5f3a\u8fd9\u65b9\u9762\u7684\u953b\u70bc\uff0c\u4ee5\u5907\u4e07\u4e00\u3002\u201d</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u968f\u624b\u628a\u676f\u5b50\u653e\u5728\u684c\u4e0a\uff0c\u8d70\u8fc7\u6765\u66ff\u5979\u7528\u6bdb\u5dfe\u64e6\u5e72\u5934\u53d1\uff0c\u201c\u5979\u4eec\u600e\u4e48\u8fd8\u6ca1\u56de\u6765\uff0c\u201d\u743c\u6069\u968f\u53e3\u8bf4\uff0c\u201c\u6309\u9053\u7406\u5e94\u8be5\u4e5f\u5dee\u4e0d\u591a\u2026\u2026\u201d</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u623f\u95e8\u201c\u7830\u201d\u5730\u4e00\u58f0\u88ab\u7528\u529b\u63a8\u5f00\u4e86\uff0c\u743c\u6069\u6709\u4e9b\u607c\u6012\uff0c\u5fc3\u60f3\u662f\u8c01\u8fd9\u4e48\u65e0\u793c\uff0c\u8f6c\u8fc7\u8eab\u4e00\u770b\uff0c\u5374\u662f\u51db\u51b2\u8fdb\u6765\uff0c\u201c\u827e\u5f25\u8587\uff0c\u743c\u6069\uff0c\u91cd\u8981\u6d88\u606f\uff0c\u201d\u5979\u6025\u4fc3\u5730\u8bf4\uff0c\u201c\u6211\u4eec\u53d1\u73b0\u8428\u9a6c\u65af\u7279\u4e86\u3002\u201d</p><p>\u201c\u5728\u54ea\u91cc\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u5728\u7b2c\u4e09\u2026\u2026\u201d</p><p>\u4ece\u51b3\u6597\u573a\u5230\u65c5\u9986\u6709\u5f88\u957f\u4e00\u6bb5\u8ddd\u79bb\uff0c\u51db\u4e00\u8def\u8dd1\u56de\u6765\uff0c\u6c14\u5598\u5401\u5401\uff0c\u4e00\u65f6\u6ca1\u7f13\u8fc7\u52b2\uff0c\u540e\u9762\u7684\u8bdd\u5c31\u63a5\u4e0d\u4e0a\u4e86\uff0c\u6446\u4e86\u6446\u624b\u793a\u610f\u6682\u505c\uff0c\u6b63\u597d\u770b\u89c1\u684c\u5b50\u4e0a\u6709\u676f\u996e\u6599\uff0c\u76f4\u63a5\u62ff\u8fc7\u6765\u5c31\u704c\u4e86\u534a\u676f\uff0c\u201c\u5728\u7b2c\u4e09\u8d5b\u573a\u2026\u2026\u201d</p><p>\u201c\u51db\u2026\u2026\u201d</p><p>\u201c\u522b\u6253\u5c94\uff0c\u542c\u6211\u8bf4\u5b8c\uff0c\u201d\u51db\u4e0d\u9ad8\u5174\uff0c\u201c\u8475\u9732\u5c0f\u59d0\u5728\u7b2c\u4e09\u8d5b\u573a\u53d1\u73b0\u4e86\u8428\u9a6c\u65af\u7279\uff0c\u539f\u6765\u4ed6\u628a\u81ea\u5df1\u4f2a\u88c5\u6210\u4e00\u53ea\u5f17\u6d1b\u9b54\uff0c\u96be\u602a\u6211\u4eec\u627e\u4e0d\u5230\u3002\u73b0\u5728\u4ed6\u4e5f\u5df2\u7ecf\u664b\u7ea7\u524d\u516b\u5f3a\uff0c\u540e\u5929\u6211\u4eec\u5e94\u8be5\u5c31\u9047\u5230\u4e86\u3002\u201d</p><p>\u201c\u51db\uff0c\u4f60\u559d\u7684\u90a3\u676f\u996e\u6599\u2026\u2026\u201d</p><p>\u201c\u4e00\u676f\u996e\u6599\u800c\u5df2\uff0c\u771f\u5c0f\u6c14\uff0c\u56de\u5934\u6211\u8d54\u4f60\u5341\u676f\uff0c\u201d\u51db\u4e00\u8fb9\u8bf4\u7740\u4e00\u8fb9\u5495\u565c\u5495\u565c\u628a\u5269\u4e0b\u7684\u534a\u676f\u4e5f\u559d\u4e86\uff0c\u201c\u5473\u9053\u8fd8\u4e0d\u9519\uff0c\u9178\u9178\u751c\u751c\u7684\uff0c\u4ece\u6ca1\u559d\u8fc7\uff0c\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d</p><p>\u201c\u2026\u2026\u5a9a\u836f\u3002\u201d</p></article>"}}}]);
