(window.webpackJsonp=window.webpackJsonp||[]).push([[1542],{1565:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u8282 \u6740\u4eba",content:"<article><h2>\u7b2c\u4e94\u5341\u8282 \u6740\u4eba</h2><p>\u5f53\u770b\u5230\u2014\u2014\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u201c\u611f\u89c9\u201d\u5230\u2014\u2014\u8428\u9a6c\u65af\u7279\u51fa\u73b0\u65f6\uff0c\u6b23\u5e03\u5c31\u5df2\u7ecf\u610f\u8bc6\u5230\u81ea\u5df1\u53ef\u80fd\u843d\u5165\u4e86\u9677\u9631\u3002\u800c\u5f53\u838e\u73de\u514b\u4ece\u9ad8\u7a7a\u4e2d\u51fa\u73b0\uff0c\u6307\u6325\u6076\u9b54\u536b\u5175\u8f6c\u800c\u56f4\u653b\u5979\u7684\u65f6\u5019\uff0c\u8fd9\u4e2a\u5224\u65ad\u5df2\u7ecf\u786e\u5b9a\u65e0\u7591\u4e86\u3002</p><p>\u51ed\u501f\u9009\u6c11\u7684\u5929\u8d4b\u80fd\u529b\uff0c\u5979\u5411\u8475\u9732\u53d1\u51fa\u4e86\u8b66\u8baf\uff0c\u5e76\u5f97\u77e5\u59b9\u59b9\u6b63\u5728\u5feb\u901f\u8d76\u6765\uff0c\u7136\u800c\u8fd9\u6050\u6015\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\u3002\u8475\u9732\u6ca1\u6709\u795e\u672f\uff0c\u65ad\u57df\u9547\u5185\u66f4\u7981\u6b62\u4f20\u9001\uff0c\u4ece\u5979\u73b0\u5728\u7684\u4f4d\u7f6e\u8d76\u5230\u9ad8\u5854\uff0c\u8fd9\u573a\u6218\u6597\u4e5f\u65e9\u5c31\u8be5\u7ed3\u675f\u4e86\u3002\u5927\u5deb\u5e08\u4eec\u7684\u51b3\u6597\uff0c\u4e0d\u4f1a\u6253\u6210\u65f7\u65e5\u6301\u4e45\uff0c\u4ece\u6765\u90fd\u662f\u77ac\u95f4\u5b9a\u80dc\u8d1f\uff0c\u51e0\u5206\u949f\u5185\u4e00\u5207\u5c31\u5c18\u57c3\u843d\u5b9a\u3002</p><p>\u6b23\u5e03\u7b2c\u4e00\u4e2a\u53cd\u5e94\u662f\u9003\u8dd1\u3002</p><p>\u98ce\u66b4\u5973\u738b\u867d\u7136\u4ee5\u813e\u6c14\u66b4\u8e81\u8457\u79f0\uff0c\u5374\u5e76\u975e\u662f\u6709\u52c7\u65e0\u8c0b\u4e0d\u77e5\u8fdb\u9000\u4e4b\u8f88\uff0c\u5426\u5219\u4e5f\u6d3b\u4e0d\u5230\u4eca\u5929\u4e86\u3002\u65e2\u7136\u5df2\u7ecf\u660e\u6446\u7740\u662f\u9677\u9631\uff0c\u81ea\u7136\u4e0d\u80fd\u518d\u5f80\u91cc\u8e29\uff0c\u65e9\u65e9\u8131\u8eab\u624d\u662f\u6b63\u7ecf\u3002\u5927\u5deb\u5e08\u4eec\u7684\u51b3\u6597\u867d\u7136\u51f6\u9669\uff0c\u4f46\u5386\u53f2\u4e0a\u771f\u56e0\u4e3a\u51b3\u6597\u800c\u6218\u6b7b\u7684\u5927\u5deb\u5e08\u5374\u5e76\u4e0d\u591a\uff0c\u539f\u56e0\u5f88\u7b80\u5355\uff0c\u5927\u5bb6\u90fd\u5f88\u7231\u60dc\u751f\u547d\u5e76\u4e14\u90fd\u5f88\u64c5\u957f\u9003\u8dd1\uff0c\u7a0d\u4e00\u63a5\u89e6\uff0c\u4f30\u6478\u60c5\u51b5\u4e0d\u5bf9\uff0c\u76f4\u63a5\u5c31\u5f00\u6e9c\u4e86\u3002\u9664\u975e\u662f\u9047\u5230\u4e0d\u5f97\u4e0d\u6253\u7684\u60c5\u5f62\uff0c\u5426\u5219\u57fa\u672c\u90fd\u662f\u4ee5\u4e00\u65b9\u7684\u9003\u8dd1\u800c\u7ed3\u675f\u3002</p><p>\u5deb\u5e08\u9003\u8131\uff0c\u6700\u5e38\u7528\u7684\u65b9\u6cd5\u81ea\u7136\u662f\u4f20\u9001\u672f\uff0c\u5305\u62ec\u4efb\u610f\u95e8\u3001\u5b9d\u77f3\u8df3\u8dc3\u7b49\u7b49\u90fd\u662f\u6b64\u7c7b\uff0c\u7136\u800c\u8fd9\u91cc\u662f\u65ad\u57df\u9547\uff0c\u7a7a\u95f4\u67b7\u9501\u628a\u4e00\u5207\u4f20\u9001\u672f\u90fd\u7ed9\u7981\u6b62\u4e86\u3002\u65e2\u7136\u4f20\u9001\u672f\u4e0d\u80fd\u4f7f\u7528\uff0c\u6b64\u65f6\u53c8\u662f\u5728\u9ad8\u5854\u4e4b\u4e0a\uff0c\u90a3\u63a5\u4e0b\u6765\u8003\u8651\u7684\uff0c\u4fbf\u662f\u98de\u884c\u672f\u4e86\u3002</p><p>\u4f46\u6b23\u5e03\u7acb\u523b\u6253\u6d88\u4e86\u8fd9\u4e2a\u5ff5\u5934\uff0c\u56e0\u4e3a\u5c31\u5728\u838e\u73de\u514b\u73b0\u8eab\u7684\u540c\u65f6\uff0c\u9ad8\u5854\u9876\u7aef\u5706\u53f0\u7684\u5468\u56f4\u7a7a\u6c14\u50cf\u6c34\u7eb9\u4e00\u6837\u8fde\u8fde\u6ce2\u6298\uff0c\u98de\u51fa\u5341\u4e8c\u4e2a\u602a\u7269\u3002</p><p>\u516b\u4e2a\u5f17\u6d1b\u9b54\uff0c\u56db\u4e2a\u4e09\u9996\u7ffc\u9f99\u3002</p><p>\u5f17\u6d1b\u9b54\u5e76\u4e0d\u662f\u4ec0\u4e48\u5389\u5bb3\u89d2\u8272\uff0c\u7136\u800c\u5b83\u4eec\u5374\u662f\u6240\u6709\u6076\u9b54\u4e2d\u6700\u64c5\u957f\u7a7a\u6218\u7684\uff0c\u4e09\u9996\u7ffc\u9f99\u5728\u8fd9\u65b9\u9762\u66f4\u80dc\u4e00\u7b79\u3002\u6b23\u5e03\u867d\u7136\u4f1a\u98de\u884c\u672f\uff0c\u5374\u7ec8\u7a76\u4e0d\u662f\u6218\u6597\u673a\uff0c\u5728\u7a7a\u4e2d\u88ab\u8fd9\u4e9b\u602a\u7269\u5355\u72ec\u7f20\u4e0a\u6216\u8bb8\u8fd8\u4e0d\u60e7\uff0c\u4f46\u6709\u8428\u9a6c\u65af\u7279\u8fd9\u79cd\u5bf9\u624b\u7aa5\u4f3a\u5728\u65c1\u8fb9\uff0c\u90a3\u8ddd\u79bb\u6b7b\u4ea1\u5c31\u4e0d\u8fdc\u4e86\u3002</p><p>\u5f88\u663e\u7136\uff0c\u8428\u9a6c\u65af\u7279\u4e3a\u4e86\u5bf9\u4ed8\u6b23\u5e03\uff0c\u505a\u4e86\u5145\u8db3\u7684\u51c6\u5907\uff0c\u501f\u52a9\u65ad\u57df\u9547\u7684\u7a7a\u95f4\u67b7\u9501\u548c\u6697\u4e2d\u57cb\u4f0f\u7684\u602a\u7269\uff0c\u5207\u65ad\u4e86\u5bf9\u624b\u7684\u4e00\u5207\u9000\u8def\uff0c\u903c\u8feb\u5979\u53ea\u80fd\u5e94\u6218\u3002</p><p>\u4ee5\u9b54\u6cd5\u9020\u8be3\u800c\u8bba\uff0c\u8428\u9a6c\u65af\u7279\u539f\u672c\u5c31\u8fdc\u5728\u6b23\u5e03\u4e4b\u4e0a\uff0c\u53ea\u56e0\u4e3a\u4ed6\u754f\u60e7\u94f6\u706b\uff0c\u6240\u4ee5\u624d\u5fcc\u60ee\u4e09\u5206\u3002\u5982\u4eca\u8ba1\u5212\u6210\u529f\uff0c\u6b23\u5e03\u5b64\u8eab\u843d\u5355\uff0c\u8428\u9a6c\u65af\u7279\u9aa4\u7136\u7a81\u88ad\u5df2\u7ecf\u5360\u4e86\u5148\u673a\uff0c\u4e09\u53d1\u6b7b\u4ea1\u4e4b\u6307\u867d\u7136\u6ca1\u80fd\u6210\u529f\u51fb\u6740\u5bf9\u624b\uff0c\u4f46\u5374\u5927\u5927\u524a\u5f31\u4e86\u94f6\u706b\u62a4\u76fe\u3002\u838e\u73de\u514b\u53c8\u547d\u4ee4\u6076\u9b54\u5b88\u536b\u76f8\u52a9\uff0c\u52a0\u4e0a\u743c\u6069\u5728\u65c1\u8fb9\u8f85\u52a9\uff0c\u53ef\u4ee5\u8bf4\u662f\u80dc\u5238\u5728\u63e1\u3002</p><p>\u94f6\u53d1\u7eb7\u4e71\u98de\u626c\uff0c\u9ed1\u888d\u730e\u730e\u4f5c\u54cd\uff0c\u5371\u6025\u5173\u5934\uff0c\u6b23\u5e03\u5c06\u81ea\u5df1\u7684\u529b\u91cf\u53d1\u6325\u5230\u4e86\u6781\u81f4\uff0c\u8fd1\u5341\u79cd\u9632\u62a4\u6cd5\u672f\u88ab\u540c\u65f6\u6fc0\u53d1\u51fa\u6765\uff0c\u95ea\u7535\u3001\u70c8\u7130\u548c\u5bd2\u51b0\u7f16\u7ec7\u6210\u7684\u98ce\u66b4\u7ed5\u8eab\u75be\u65cb\uff0c\u56db\u53ea\u72c2\u6218\u9b54\u8fd8\u672a\u9760\u8fd1\u5c31\u5df2\u7ecf\u88ab\u70e7\u6210\u7070\u70ec\u3001\u70e4\u6210\u7126\u70ad\u6216\u8005\u51bb\u6210\u51b0\u6e23\uff0c\u4e00\u9053\u84dd\u767d\u76f8\u95f4\u7684\u95ea\u7535\u4ece\u4f53\u5185\u8dc3\u51fa\uff0c\u5316\u4f5c\u957f\u67aa\uff0c\u671d\u8428\u9a6c\u65af\u7279\u6fc0\u5c04\u800c\u6765\u3002</p><p>\u96f7\u7535\u67aa\u3002</p><p>\u8fd9\u662f\u9634\u5f71\u8c37\u5927\u8d24\u8005\u4f0a\u5c14\u660e\u65af\u7279\u7684\u5f97\u610f\u53d1\u660e\uff0c\u98de\u626c\u7535\u5c04\uff0c\u8fc5\u82e5\u96f7\u9706\uff0c\u4e00\u65e6\u5c04\u51fa\u4fbf\u4e0d\u4f1a\u843d\u7a7a\uff0c\u4f46\u9664\u4ed6\u4e4b\u5916\u5374\u51e0\u4e4e\u6ca1\u6709\u4eba\u80fd\u591f\u5b66\u4f1a\uff0c\u56e0\u4e3a\u8fd9\u6839\u672c\u5c31\u4e0d\u662f\u6cd5\u672f\uff0c\u8fd9\u662f\u4e00\u79cd\u94f6\u706b\u7684\u8fd0\u7528\u6280\u5de7\u3002\u6b23\u5e03\u548c\u4f0a\u5c14\u660e\u65af\u7279\u5173\u7cfb\u5bc6\u5207\uff0c\u4ece\u4ed6\u90a3\u91cc\u5b66\u5230\u4e86\u8fd9\u4e00\u62db\uff0c\u5018\u82e5\u4e0d\u662f\u5230\u4e86\u7d27\u8981\u5173\u5934\uff0c\u51b3\u4e0d\u4f1a\u8f7b\u6613\u52a8\u7528\u7684\u3002</p><p>\u94f6\u706b\u4e4b\u6240\u4ee5\u5f3a\u5927\uff0c\u5c31\u5728\u4e8e\u5b83\u5176\u5b9e\u662f\u795e\u529b\u800c\u4e0d\u662f\u9b54\u6cd5\uff0c\u6240\u4ee5\u7528\u5e38\u89c4\u7684\u9632\u62a4\u6cd5\u672f\u5f88\u96be\u62b5\u5fa1\u3002\u4e0a\u6b21\u743c\u6069\u548c\u6b23\u5e03\u906d\u9047\uff0c\u4ed6\u660e\u660e\u5df2\u7ecf\u542f\u52a8\u4e86\u6cd5\u672f\u9006\u8f6c\u6212\u6307\uff0c\u5bf9\u94f6\u706b\u5374\u534a\u70b9\u4e0d\u8d77\u4f5c\u7528\uff0c\u7167\u6837\u88ab\u70e7\u4f24\uff0c\u5c31\u662f\u8fd9\u4e2a\u9053\u7406\u3002\u8428\u9a6c\u65af\u7279\u4e4b\u6240\u4ee5\u5fcc\u60ee\u94f6\u706b\uff0c\u56fa\u7136\u56e0\u4e3a\u4ed6\u73b0\u5728\u662f\u4ea1\u7075\uff0c\u4f46\u4e3b\u8981\u7684\u539f\u56e0\u5374\u8fd8\u662f\u5728\u6b64\u3002</p><p>\u96f7\u7535\u67aa\u7834\u7a7a\u5c04\u6765\uff0c\u8428\u9a6c\u65af\u7279\u95ea\u907f\u4e0d\u53ca\uff0c\u4f46\u4ed6\u4e5f\u4e0d\u9700\u8981\u95ea\u907f\uff0c\u56e0\u4e3a\u4ed6\u6709\u8089\u76fe\u3002</p><p>\u65ad\u57df\u9547\u7684\u536b\u5175\u662f\u4e00\u6ce2\u4e00\u6ce2\u63a5\u8e35\u800c\u6765\uff0c\u65e0\u4f11\u65e0\u6b62\u7684\u3002\u56db\u53ea\u72c2\u6218\u9b54\u88ab\u6b23\u5e03\u51fb\u6740\uff0c\u4e0b\u4e00\u77ac\u95f4\u56db\u53ea\u8ff7\u8bf1\u9b54\u95ea\u4eae\u767b\u573a\uff0c\u51fa\u73b0\u5728\u8428\u9a6c\u65af\u7279\u8eab\u5468\uff0c\u53ea\u662f\u8fd8\u4e0d\u7b49\u5b83\u4eec\u53cd\u5e94\u8fc7\u6765\uff0c\u4e00\u56e2\u731b\u70c8\u7684\u65cb\u98ce\u4ece\u5deb\u5996\u7684\u534e\u4e3d\u957f\u888d\u4e2d\u51b2\u51fa\uff0c\u5c06\u8ff7\u8bf1\u9b54\u4eec\u671d\u96f7\u7535\u67aa\u63a8\u53bb\u3002</p><p>\u6ecb\u6ecb\u8fde\u54cd\uff0c\u56db\u53ea\u8ff7\u8bf1\u9b54\u8eab\u4e0d\u7531\u5df1\u5730\u88ab\u5148\u540e\u7529\u51fa\uff0c\u5728\u534a\u79d2\u949f\u5185\u9f50\u9f50\u5316\u4f5c\u7070\u672b\uff0c\u96f7\u7535\u67aa\u53bb\u52bf\u672a\u8870\uff0c\u4f9d\u65e7\u51fb\u4e2d\u4e86\u8428\u9a6c\u65af\u7279\uff0c\u4f46\u5df2\u7ecf\u6ca1\u6709\u9020\u6210\u592a\u591a\u4f24\u5bb3\u4e86\u3002\u6b23\u5e03\u539f\u672c\u4e5f\u5e76\u672a\u6307\u671b\u8fd9\u4e00\u51fb\u5c31\u80fd\u594f\u6548\uff0c\u53ea\u662f\u6682\u65f6\u6e05\u626b\u8eab\u65c1\u7684\u6076\u9b54\u536b\u5175\uff0c\u903c\u4f4f\u8428\u9a6c\u65af\u7279\uff0c\u4e89\u53d6\u5230\u4e86\u4e00\u4e1d\u7a7a\u9699\uff0c\u968f\u5373\u62ac\u624b\u9065\u6307\uff0c\u4e00\u9053\u5bd2\u51b0\u5c04\u7ebf\u4ece\u6307\u5c16\u8ff8\u51fa\uff0c\u7480\u74a8\u8000\u76ee\uff0c\u671d\u9ad8\u7a7a\u4e2d\u7684\u9b45\u9b54\u5c04\u53bb\u3002</p><p>\u5979\u5df2\u7ecf\u770b\u4e86\u51fa\u6765\uff0c\u8fd9\u4e9b\u6076\u9b54\u536b\u5175\u548c\u8428\u9a6c\u65af\u7279\u6ca1\u6709\u5173\u7cfb\uff0c\u7eaf\u7cb9\u662f\u838e\u73de\u514b\u5728\u7528\u90aa\u5fbd\u547d\u4ee4\u3002\u800c\u838e\u73de\u514b\u663e\u7136\u5e76\u4e0d\u80fd\u76f4\u63a5\u6307\u6325\u65ad\u57df\u9547\u5168\u4f53\u6076\u9b54\u536b\u5175\uff0c\u5979\u7684\u547d\u4ee4\u662f\u7279\u5b9a\u800c\u4e34\u65f6\u7684\uff0c\u4e5f\u5c31\u662f\u8bf4\u6bcf\u51fa\u73b0\u4e00\u6ce2\u65b0\u7684\u6076\u9b54\u536b\u5175\uff0c\u9b45\u9b54\u5c31\u8981\u518d\u4e0b\u4e00\u6b21\u547d\u4ee4\u2014\u2014\u90a3\u4e48\u53ea\u8981\u6740\u4e86\u838e\u73de\u514b\uff0c\u65b0\u51fa\u6765\u7684\u6076\u9b54\u536b\u5175\u4fbf\u6ca1\u6709\u4eba\u63a7\u5236\uff0c\u4f9d\u65e7\u4f1a\u6309\u7167\u6700\u521d\u76ee\u7684\u53bb\u653b\u51fb\u8428\u9a6c\u65af\u7279\u3002\u5f53\u7136\uff0c\u6b23\u5e03\u4e0d\u4f1a\u5929\u771f\u5230\u8ba4\u4e3a\u8428\u9a6c\u65af\u7279\u5bf9\u6b64\u5168\u65e0\u9632\u5907\uff0c\u4e5f\u4e0d\u4f1a\u4ee5\u4e3a\u6709\u4e86\u8fd9\u4e9b\u6076\u9b54\u536b\u5175\u7684\u5e2e\u52a9\u5c31\u80fd\u6740\u6389\u4ed6\uff0c\u90a3\u4e0d\u73b0\u5b9e\uff0c\u4f46\u81f3\u5c11\u53ef\u4ee5\u7f20\u4f4f\u4ed6\u7247\u523b\uff0c\u81ea\u5df1\u4fbf\u53ef\u4ee5\u8131\u8eab\u9000\u8d70\u4e86\u3002\u53ea\u8981\u6ca1\u6709\u8428\u9a6c\u65af\u7279\uff0c\u5355\u9760\u90a3\u4e9b\u5f17\u6d1b\u9b54\u548c\u4e09\u9996\u7ffc\u9f99\u8fd8\u6321\u4e0d\u4f4f\u5979\u3002</p><p>\u4f46\u9b45\u9b54\u5e76\u6ca1\u6709\u88ab\u51fb\u4e2d\u3002</p><p>\u838e\u73de\u514b\u663e\u7136\u65e9\u5c31\u9884\u6599\u5230\u4f1a\u88ab\u653b\u51fb\uff0c\u4e00\u89c1\u6b23\u5e03\u4e3e\u52a8\uff0c\u8760\u7ffc\u8212\u5c55\u7acb\u523b\u79fb\u4e86\u5f00\u53bb\uff0c\u582a\u582a\u907f\u5f00\u3002\u5979\u539f\u672c\u5c31\u662f\u6740\u624b\uff0c\u8eab\u624b\u654f\u6377\uff0c\u53cd\u5e94\u8d85\u7fa4\uff0c\u5982\u4eca\u53d8\u6210\u9b45\u9b54\uff0c\u591a\u4e86\u4e00\u53cc\u8759\u8760\u76ae\u7ffc\uff0c\u79fb\u52a8\u901f\u5ea6\u66f4\u662f\u5feb\u5f97\u60ca\u4eba\uff0c\u6b64\u524d\u53c8\u523b\u610f\u4fdd\u6301\u8ddd\u79bb\uff0c\u4fbf\u662f\u6b23\u5e03\u4e00\u65f6\u4e5f\u5948\u4f55\u5979\u4e0d\u5f97\u3002</p><p>\u8428\u9a6c\u65af\u7279\u6ca1\u6709\u518d\u7ed9\u6b23\u5e03\u51fa\u624b\u7684\u673a\u4f1a\uff0c\u56db\u6839\u81ea\u5df1\u7684\u6307\u9aa8\u88ab\u6273\u65ad\u4e0b\u6765\uff0c\u63b7\u843d\u5728\u5730\uff0c\u5316\u4f5c\u56db\u53ea\u5de8\u5927\u7684\u9ab8\u9aa8\u6b66\u58eb\uff0c\u914d\u5408\u65b0\u51fa\u73b0\u7684\u56db\u53ea\u5224\u9b42\u9b54\u536b\u5175\u4e00\u62e5\u800c\u4e0a\u3002\u5f17\u6d1b\u9b54\u548c\u4e09\u9996\u7ffc\u9f99\u4e5f\u540c\u65f6\u903c\u8fd1\uff0c\u867d\u7136\u4e0d\u76f4\u63a5\u53c2\u4e0e\u653b\u51fb\uff0c\u5374\u5c06\u5979\u8eab\u5468\u7a7a\u95f4\u5b8c\u5168\u5c01\u9501\uff0c\u5207\u65ad\u6240\u6709\u53ef\u80fd\u9003\u8dd1\u7684\u8def\u7ebf\u3002\u6b23\u5e03\u63b7\u51fa\u4e00\u679a\u83f1\u5f62\u6c34\u6676\uff0c\u65e0\u6570\u56e2\u7d2b\u8272\u5149\u7403\u98de\u6e85\u800c\u51fa\uff0c\u5c06\u56db\u53ea\u5224\u9b42\u9b54\u77ac\u95f4\u53d8\u6210\u4e86\u4e00\u6ee9\u8840\u6c34\uff0c\u4f46\u9ab8\u9aa8\u6b66\u58eb\u5374\u5e76\u4e0d\u5982\u4f55\u53d7\u5f71\u54cd\uff0c\u4f9d\u65e7\u6325\u821e\u5de8\u5251\u5feb\u901f\u903c\u8fd1\u3002</p><p>\u4e00\u9053\u9053\u4e13\u95e8\u7528\u4e8e\u5f3a\u5316\u4ea1\u7075\u7684\u6cd5\u672f\u88ab\u8428\u9a6c\u65af\u7279\u65bd\u5c55\u51fa\u6765\uff0c\u53e0\u52a0\u5728\u9ab8\u9aa8\u6b66\u58eb\u4e4b\u4e0a\uff0c\u8ba9\u5b83\u4eec\u53d8\u5f97\u9aa8\u9abc\u575a\u786c\u5982\u7cbe\u91d1\uff0c\u5bf9\u5404\u79cd\u5143\u7d20\u653b\u51fb\u90fd\u6709\u5f88\u5f3a\u7684\u62b5\u6297\u80fd\u529b\u3002\u6b23\u5e03\u63a5\u8fde\u4f7f\u7528\u4e86\u5979\u6700\u62ff\u624b\u7684\u4e09\u4e2a\u6740\u4f24\u6cd5\u672f\uff0c\u5374\u4ec5\u4ec5\u53ea\u6467\u6bc1\u4e86\u4e00\u4e2a\u9ab8\u9aa8\u6b66\u58eb\uff0c\u53e6\u5916\u4e09\u540d\u5df2\u7ecf\u8fdb\u5165\u5371\u9669\u7684\u8ddd\u79bb\uff0c\u5979\u65e0\u5948\u4e4b\u4e0b\uff0c\u53ea\u80fd\u518d\u5ea6\u8fd0\u7528\u94f6\u706b\uff0c\u84dd\u767d\u706b\u7130\u4ece\u7834\u65e7\u7684\u9ed1\u888d\u4e2d\u8f70\u7136\u6d8c\u51fa\uff0c\u5c06\u4e09\u540d\u9ab8\u9aa8\u6b66\u58eb\u5c3d\u6570\u5316\u4f5c\u98de\u7070\u3002</p><p>\u8fd9\u6b63\u662f\u8428\u9a6c\u65af\u7279\u6240\u671f\u671b\u7684\u3002</p><p>\u201c\u4f60\u7684\u94f6\u706b\u5df2\u7ecf\u4e0d\u591a\u4e86\uff0c\u201d\u4ed6\u9634\u607b\u607b\u5730\u8bf4\uff0c\u201c\u4e0d\u4f1a\u8d85\u8fc7\u4e94\u5206\u4e4b\u4e00\u3002\u201d</p><p>\u8428\u9a6c\u65af\u7279\u66fe\u7ecf\u5bf9\u743c\u6069\u8bf4\uff1a\u4ed6\u662f\u6709\u53f2\u4ee5\u6765\u5bf9\u94f6\u706b\u6700\u6709\u7814\u7a76\u7684\u51e1\u4eba\uff0c\u8fd9\u5e76\u4e0d\u662f\u865a\u8a00\u3002\u5728\u8428\u9a6c\u65af\u7279\u4e4b\u524d\uff0c\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\u4eec\u53ea\u662f\u628a\u94f6\u706b\u5f53\u4f5c\u4e00\u79cd\u673a\u68b0\u7684\u653b\u51fb\u624b\u6bb5\uff0c\u5e76\u6ca1\u6709\u592a\u591a\u8003\u8651\u5176\u4e2d\u7684\u53d8\u5316\u5965\u5999\uff0c\u662f\u8428\u9a6c\u65af\u7279\u7b2c\u4e00\u4e2a\u5bf9\u94f6\u706b\u7684\u672c\u8d28\u548c\u8fd0\u7528\u8fdb\u884c\u4e86\u6df1\u5165\u7814\u7a76\uff0c\u7531\u6b64\u521b\u9020\u51fa\u5404\u79cd\u7cbe\u5999\u7684\u7528\u6cd5\uff0c\u751a\u81f3\u5c31\u50cf\u6b23\u5e03\u4e00\u5f00\u59cb\u7528\u6765\u62b5\u5fa1\u6b7b\u4ea1\u4e4b\u6307\u7684\u94f6\u706b\u62a4\u76fe\uff0c\u5176\u5b9e\u90fd\u662f\u8428\u9a6c\u65af\u7279\u7684\u53d1\u660e\u3002\u867d\u7136\u4ed6\u73b0\u5728\u5df2\u7ecf\u4e0d\u662f\u9009\u6c11\uff0c\u4f46\u773c\u5149\u4f9d\u65e7\u8fd8\u5728\uff0c\u6e05\u695a\u770b\u51fa\u6b23\u5e03\u5df2\u7ecf\u5230\u4e86\u5f3a\u5f29\u4e4b\u672b\u3002</p><p>\u9009\u6c11\u7684\u94f6\u706b\u4e5f\u662f\u9700\u8981\u65f6\u95f4\u6062\u590d\u7684\uff0c\u6b64\u65f6\u8ddd\u79bb\u660e\u5929\u7684\u51b3\u8d5b\u4e0d\u5230\u5341\u4e2a\u5c0f\u65f6\uff0c\u54ea\u6015\u6b23\u5e03\u6b64\u65f6\u80fd\u591f\u9003\u8131\uff0c\u660e\u5929\u5979\u4e5f\u4e0d\u4f1a\u662f\u8428\u9a6c\u65af\u7279\u7684\u5bf9\u624b\u4e86\uff0c\u8001\u5deb\u5996\u6700\u521d\u6b65\u7684\u76ee\u7684\u5df2\u7ecf\u8fbe\u6210\u3002</p><p>\u6b23\u5e03\u54ac\u7d27\u7259\u5173\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u731b\u7136\u770b\u51c6\u673a\u4f1a\u518d\u5ea6\u53d1\u51fa\u4e86\u96f7\u7535\u67aa\u3002\u8fd9\u6b21\u8428\u9a6c\u65af\u7279\u6ca1\u80fd\u6545\u6280\u91cd\u65bd\uff0c\u56e0\u4e3a\u65b0\u51fa\u73b0\u7684\u6076\u9b54\u536b\u5175\u662f\u516d\u81c2\u86c7\u9b54\uff0c\u529b\u91cf\u975e\u5e38\u5f3a\u5927\uff0c\u8fdc\u8fdc\u80dc\u8fc7\u8ff7\u8bf1\u9b54\uff0c\u7eb5\u7136\u662f\u8428\u9a6c\u65af\u7279\u4e5f\u4e0d\u80fd\u8f7b\u6613\u4e00\u9053\u65cb\u98ce\u5c31\u628a\u5979\u4eec\u63a8\u4e0a\u53bb\u5f53\u76fe\u724c\u3002\u5deb\u5996\u4f38\u624b\u865a\u6293\uff0c\u8ddd\u79bb\u4ed6\u6700\u8fd1\u7684\u4e24\u53ea\u5f17\u6d1b\u9b54\u548c\u4e00\u53ea\u4e09\u9996\u7ffc\u9f99\u4eff\u4f5b\u88ab\u65e0\u5f62\u5de8\u529b\u5438\u5f15\uff0c\u500f\u5ffd\u98de\u81f3\uff0c\u6b63\u649e\u4e0a\u4e86\u96f7\u7535\u67aa\u3002</p><p>\u679c\u7136\u70ae\u7070\u5c31\u662f\u7528\u6765\u6d88\u8017\u7684\u3002</p><p>\u5f17\u6d1b\u9b54\u548c\u4e09\u9996\u7ffc\u9f99\u77ac\u95f4\u7070\u98de\u70df\u706d\uff0c\u4f46\u8fd9\u5df2\u7ecf\u4e3a\u8428\u9a6c\u65af\u7279\u4e89\u53d6\u5230\u4e86\u8db3\u591f\u7684\u65f6\u95f4\uff0c\u4ed6\u6210\u529f\u5730\u7528\u6307\u4f7f\u672f\u63a7\u5236\u4e86\u4e00\u53ea\u86c7\u9b54\uff0c\u8ba9\u5979\u6321\u5728\u81ea\u5df1\u8eab\u524d\u3002\u96f7\u7535\u67aa\u51fb\u4e2d\u86c7\u9b54\uff0c\u7136\u540e\u8f70\u7136\u7206\u70b8\u5f00\u6765\uff0c\u7ffb\u6eda\u7684\u6c14\u6d6a\u70b8\u5f97\u8428\u9a6c\u65af\u7279\u8fde\u8fde\u540e\u9000\uff0c\u8eab\u4e0a\u7684\u9aa8\u9abc\u90fd\u5e94\u8be5\u65ad\u4e86\u51e0\u6839\uff0c\u4f46\u5374\u6321\u4e0d\u4f4f\u4ed6\u7684\u4f4e\u6c89\u7b11\u58f0\u3002</p><p>\u201c\u4f60\u5df2\u7ecf\u6ca1\u6709\u591a\u4f59\u7684\u94f6\u706b\u4e86\uff01\u201d</p><p>\u53e6\u5916\u4e09\u53ea\u516d\u81c2\u86c7\u9b54\u5728\u838e\u73de\u514b\u7684\u547d\u4ee4\u4e0b\u5411\u6b23\u5e03\u51b2\u53bb\uff0c\u5979\u4eec\u662f\u6df1\u6e0a\u4e2d\u6700\u7cbe\u9510\u7684\u6076\u9b54\u4e4b\u4e00\uff0c\u4f4d\u9636\u4ec5\u6b21\u4e8e\u5df4\u6d1b\u708e\u9b54\u7684\u5b58\u5728\uff0c\u867d\u7136\u4e0d\u64c5\u6cd5\u672f\uff0c\u4f46\u5374\u6781\u5584\u8fd1\u6218\uff0c\u516d\u53ea\u624b\u81c2\u540c\u65f6\u6325\u821e\uff0c\u6740\u4eba\u5982\u540c\u780d\u74dc\u5207\u83dc\u4e00\u822c\u3002\u6b23\u5e03\u5982\u4eca\u94f6\u706b\u5df2\u7ecf\u6d88\u8017\u6b86\u5c3d\uff0c\u5f3a\u529b\u6cd5\u672f\u4e5f\u5927\u591a\u7528\u5b8c\uff0c\u5018\u82e5\u88ab\u5979\u4eec\u56f4\u4e0a\uff0c\u7ed3\u679c\u5c31\u6beb\u65e0\u60ac\u5ff5\u4e86\u3002</p><p>\u201c\u563f\uff01\u201d</p><p>\u6b23\u5e03\u6012\u559d\u4e00\u58f0\uff0c\u9ed1\u888d\u9f13\u80c0\uff0c\u94f6\u53d1\u4e0a\u7ad6\uff0c\u53cc\u773c\u540c\u65f6\u5c04\u51fa\u4e00\u9053\u84dd\u5149\uff0c\u5728\u7a7a\u4e2d\u6c47\u805a\u76f8\u649e\uff0c\u7206\u53d1\u51fa\u96f7\u9e23\u5de8\u54cd\u3002\u65e0\u5f62\u7684\u51b2\u51fb\u6ce2\u4ee5\u5979\u4e3a\u4e2d\u5fc3\u8f70\u7136\u56db\u6563\uff0c\u51b2\u5728\u6700\u524d\u9762\u7684\u4e24\u53ea\u516d\u81c2\u86c7\u9b54\u5148\u662f\u5446\u5446\u7ad9\u5b9a\uff0c\u7d27\u63a5\u7740\u5168\u8eab\u98a4\u6296\uff0c\u7136\u540e\u53ee\u53ee\u5f53\u5f53\u4e00\u9635\u4e71\u54cd\uff0c\u6574\u4e2a\u8eaf\u4f53\u5305\u62ec\u6b66\u5668\u3001\u94e0\u7532\uff0c\u5168\u90fd\u53d8\u6210\u4e86\u900f\u660e\u51b0\u96d5\uff0c\u968f\u5373\u788e\u6210\u65e0\u6570\u6676\u5757\uff0c\u6563\u843d\u4e00\u5730\u3002\u6700\u540e\u4e00\u53ea\u516d\u81c2\u86c7\u9b54\u56e0\u4e3a\u8ddd\u79bb\u7a0d\u8fdc\uff0c\u53ea\u662f\u88ab\u51b2\u51fb\u6ce2\u9707\u4f24\uff0c\u7830\u7136\u6454\u98de\uff0c\u5374\u8fd8\u6ca1\u65ad\u6c14\u3002\u6b23\u5e03\u5934\u9876\u548c\u5de6\u4fa7\u56db\u53ea\u5f17\u6d1b\u9b54\u548c\u4e24\u53ea\u4e09\u9996\u7ffc\u9f99\u4e5f\u540c\u6837\u906d\u6b64\u5384\u8fd0\uff0c\u7eb7\u7eb7\u788e\u88c2\uff0c\u5305\u56f4\u5708\u987f\u65f6\u51fa\u73b0\u4e86\u4e00\u4e2a\u5927\u5927\u7684\u7a7a\u9699\u3002</p><p>\u6b23\u5e03\u4e00\u8dc3\u800c\u8d77\uff0c\u51c6\u5907\u4ece\u6b64\u51b2\u51fa\uff0c\u7136\u800c\u773c\u770b\u5979\u5c31\u8981\u51b2\u5230\u5854\u8fb9\uff0c\u811a\u4e0b\u9661\u7136\u4e00\u8f6f\uff0c\u5168\u8eab\u529b\u6c14\u4eff\u4f5b\u5239\u90a3\u95f4\u90fd\u88ab\u62bd\u7a7a\u4f3c\u7684\uff0c\u987f\u65f6\u6454\u5012\u5728\u5730\uff0c\u4e1d\u6beb\u52a8\u5f39\u4e0d\u5f97\u3002</p><p>\u7136\u540e\u5979\u542c\u5230\u4e86\u743c\u6069\u7684\u58f0\u97f3\u3002</p><p>\u203b\u203b\u203b</p><p>\u5728\u521a\u624d\u8428\u9a6c\u65af\u7279\u548c\u6b23\u5e03\u6cd5\u672f\u5927\u6218\u7684\u65f6\u5019\uff0c\u743c\u6069\u975e\u5e38\u5e72\u8106\u5730\u8eb2\u5230\u4e86\u4e00\u8fb9\uff0c\u534a\u70b9\u6ca1\u6709\u63d2\u624b\u3002\u76f4\u81f3\u73b0\u5728\u6218\u5c40\u7ed3\u675f\uff0c\u4ed6\u624d\u4ece\u89d2\u843d\u91cc\u6162\u6162\u8d70\u51fa\u6765\u3002\u201c\u9ad8\u7b49\u964d\u5492\uff1f\u201d\u4ed6\u95ee\u8428\u9a6c\u65af\u7279\uff0c\u201c\u6211\u6ca1\u8ba4\u9519\u5427\u3002\u201d</p><p>\u201c\u6ca1\u9519\u3002\u201d</p><p>\u9ad8\u7b49\u964d\u5492\u662f\u4e00\u79cd\u975e\u5e38\u9ed1\u6697\u7684\u4ea1\u7075\u672f\uff0c\u80fd\u591f\u8ba9\u4eba\u6781\u5ea6\u8870\u5f31\uff0c\u751a\u81f3\u4ece\u6b64\u6210\u4e3a\u5e9f\u4eba\u3002\u4e00\u4e2a\u539f\u672c\u5f3a\u58ee\u7684\u529b\u58eb\uff0c\u6709\u53ef\u80fd\u56e0\u4e3a\u9ad8\u7b49\u964d\u5492\u800c\u53d8\u5f97\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u4e00\u4e2a\u539f\u672c\u806a\u660e\u7684\u5b66\u8005\uff0c\u6709\u53ef\u80fd\u56e0\u4e3a\u9ad8\u7b49\u964d\u5492\u800c\u53d8\u5f97\u611a\u7b28\u75f4\u5446\uff0c\u800c\u4e14\u8fd9\u4e9b\u6548\u679c\u6781\u6709\u53ef\u80fd\u662f\u4f34\u968f\u7ec8\u751f\u7684\u3002\u5f53\u7136\uff0c\u6b23\u5e03\u4e0d\u662f\u5e38\u4eba\uff0c\u7eb5\u7136\u662f\u9ad8\u7b49\u964d\u5492\u4e5f\u4e0d\u53ef\u80fd\u5bf9\u5979\u6c38\u4e45\u8d77\u4f5c\u7528\uff0c\u4f46\u591a\u957f\u65f6\u95f4\u80fd\u591f\u6062\u590d\uff0c\u90a3\u5c31\u5f97\u95ee\u8428\u9a6c\u65af\u7279\u4e86\u3002</p><p>\u201c\u6211\u5728\u8fd9\u4e2a\u6cd5\u672f\u91cc\u7279\u522b\u52a0\u5165\u4e86\u4e00\u4e9b\u53d8\u5316\uff0c\u9632\u6b62\u88ab\u7267\u5e08\u7684\u795e\u672f\u514b\u5236\uff0c\u201d\u8428\u9a6c\u65af\u7279\u56de\u7b54\uff0c\u201c\u9664\u975e\u7528\u94f6\u706b\u6cbb\u7597\uff0c\u5426\u5219\u5979\u5927\u6982\u4f1a\u762b\u75ea\u56db\u5230\u4e94\u5e74\u3002\u201d</p><p>\u743c\u6069\u76b1\u7709\uff0c\u201c\u4f60\u7b54\u5e94\u628a\u5979\u7ed9\u6211\u7684\u3002\u201d</p><p>\u201c\u73b0\u5728\u4e0d\u662f\u5417\uff1f\u5979\u4e00\u6ca1\u6b7b\uff0c\u4e8c\u6ca1\u7f3a\u80f3\u818a\u65ad\u817f\uff0c\u751a\u81f3\u8fde\u8138\u86cb\u90fd\u6ca1\u5212\u7834\uff0c\u53ea\u662f\u4e0d\u80fd\u52a8\u4e86\u800c\u5df2\u3002\u6211\u8fd9\u53ef\u5b8c\u5168\u662f\u4e3a\u4f60\u7740\u60f3\uff0c\u6015\u4f60\u5bf9\u4ed8\u4e0d\u4e86\u5979\uff0c\u5426\u5219\u4f55\u5fc5\u7528\u9ad8\u7b49\u964d\u5492\u8fd9\u79cd\u8d39\u529b\u7684\u6cd5\u672f\uff0c\u76f4\u63a5\u6740\u4e86\u5979\u5b8c\u4e8b\u3002\u201d</p><p>\u56e0\u4e3a\u6210\u529f\u5f97\u624b\uff0c\u8428\u9a6c\u65af\u7279\u663e\u7136\u4e5f\u5fc3\u60c5\u751a\u4f73\uff0c\u8bf4\u8bdd\u8bed\u6c14\u4e5f\u6e29\u548c\u591a\u4e86\u3002\u7136\u800c\u743c\u6069\u5e76\u4e0d\u9886\u60c5\uff0c\u201c\u53ef\u662f\u4e00\u4e2a\u762b\u75ea\u7684\u5973\u4eba\u5e72\u8d77\u6765\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u4ed6\u8d28\u95ee\u3002</p><p>\u8428\u9a6c\u65af\u7279\u644a\u624b\uff0c\u201c\u90a3\u5c31\u4e0d\u662f\u6211\u7684\u95ee\u9898\u4e86\uff0c\u6211\u5b8c\u5168\u5151\u73b0\u4e86\u627f\u8bfa\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u800c\u4e14\u762b\u75ea\u7684\u5973\u4eba\u8bf4\u4e0d\u5b9a\u5e72\u8d77\u6765\u4e5f\u522b\u6709\u6ecb\u5473\uff0c\u4f60\u4e0d\u5c1d\u8fc7\u600e\u4e48\u77e5\u9053\u5462\u3002\u201d</p><p>\u743c\u6069\u76b1\u7709\uff0c\u201c\u827e\u5f25\u8587\u548c\u51db\u5462\uff1f\u201d</p><p>\u8428\u9a6c\u65af\u7279\u770b\u770b\u6b23\u5e03\uff0c\u786e\u5b9a\u5979\u5df2\u7ecf\u65e0\u529b\u53cd\u6297\uff0c\u8fd9\u4e2a\u9ad8\u7b49\u964d\u5492\u662f\u4ed6\u81ea\u5df1\u4e0b\u7684\uff0c\u81ea\u7136\u975e\u5e38\u6e05\u695a\u6548\u529b\u3002\u5c31\u7b97\u6b64\u65f6\u6709\u5176\u4ed6\u9009\u6c11\u5728\u573a\uff0c\u80fd\u7528\u94f6\u706b\u66ff\u5979\u6cbb\u7597\uff0c\u6ca1\u6709\u4e00\u65f6\u534a\u523b\u4e5f\u4e0d\u53ef\u80fd\u6062\u590d\uff0c\u5df2\u7ecf\u4e0d\u5fc5\u62c5\u5fc3\u3002\u201c\u5728\u8fd9\u91cc\u3002\u201d\u4ed6\u8bf4\uff0c\u6325\u624b\u5f80\u7a7a\u6c14\u4e2d\u4e00\u5212\uff0c\u5854\u9876\u5706\u53f0\u8fb9\u51fa\u73b0\u4e00\u9053\u9ed1\u8272\u6b21\u5143\u88c2\u9699\uff0c\u7136\u540e\u4e24\u4f4d\u5c11\u5973\u4ece\u91cc\u9762\u8dcc\u4e86\u51fa\u6765\u3002</p><p>\u201c\u743c\u6069\uff01\u201d</p><p>\u51db\u5df2\u7ecf\u6e05\u9192\uff0c\u4e00\u770b\u89c1\u743c\u6069\u7acb\u523b\u5927\u53eb\u8d77\u6765\uff0c\u8f6c\u773c\u518d\u770b\u5230\u6b23\u5e03\u6454\u5012\u5728\u5730\uff0c\u54ea\u91cc\u8fd8\u4e0d\u660e\u767d\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6025\u6012\u4e4b\u4e0b\u5c31\u8981\u4ece\u53e3\u888b\u4e2d\u53d6\u9b54\u6cd5\u5b9d\u77f3\uff0c\u5374\u88ab\u6885\u83f2\u65af\u62c9\u4f4f\u4e86\u3002\u201c\u522b\u51b2\u52a8\u3002\u201d\u5723\u6b66\u58eb\u6c89\u58f0\u8bf4\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u4ed6\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u76f4\u622a\u4e86\u5f53\u4e00\u8bb0\u624b\u5200\u780d\u5728\u51db\u7684\u80cc\u540e\u8116\u9888\uff0c\u5c06\u5979\u6253\u6655\u8fc7\u53bb\u3002\u5723\u6b66\u58eb\u867d\u7136\u73b0\u5728\u865a\u5f31\uff0c\u591a\u5e74\u52e4\u4fee\u82e6\u7ec3\u7684\u6b66\u6280\u4f9d\u65e7\u5728\uff0c\u51db\u53c8\u534a\u70b9\u4e0d\u66fe\u9632\u5907\uff0c\u987f\u65f6\u4e2d\u62db\u3002\u6885\u83f2\u65af\u62ac\u8d77\u5934\uff0c\u76f4\u76f4\u671d\u743c\u6069\u770b\u8fc7\u6765\uff0c\u5deb\u5e08\u4fa7\u8138\u907f\u5f00\uff0c\u4e0d\u53bb\u770b\u5979\uff0c\u4ece\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u6cd5\u6756\uff0c\u8d70\u4e0a\u524d\u51e0\u6b65\uff0c\u8fdc\u8fdc\u5bf9\u51c6\u6b23\u5e03\u3002\u90a3\u53ea\u4ec5\u5b58\u7684\u516d\u81c2\u86c7\u9b54\u6b63\u597d\u5728\u4ed6\u811a\u8fb9\uff0c\u5b83\u88ab\u6b23\u5e03\u6700\u540e\u7684\u96f7\u9e23\u672f\u9707\u4f24\uff0c\u867d\u7136\u6ca1\u6b7b\uff0c\u5374\u4e5f\u5df2\u7ecf\u5944\u5944\u4e00\u606f\u4e86\u3002</p><p>\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u8428\u9a6c\u65af\u7279\u5947\u602a\u3002</p><p>\u201c\u6211\u5bf9\u762b\u75ea\u7684\u5973\u4eba\u6ca1\u5174\u8da3\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6740\u4e86\u5b8c\u4e8b\uff0c\u514d\u5f97\u9ebb\u70e6\u3002\u201d</p><p>\u8428\u9a6c\u65af\u7279\u8038\u8038\u80a9\uff0c\u201c\u968f\u4fbf\u4f60\u3002\u201d</p><p>\u7d2b\u6c34\u6676\u6cd5\u6756\u4e0a\u7684\u516d\u679a\u7eef\u7ea2\u4e4b\u6cea\u5b9d\u77f3\u4f9d\u6b21\u4eae\u8d77\uff0c\u6bb7\u7ea2\u5982\u8840\u7684\u5149\u8292\u5305\u88f9\u4e86\u743c\u6069\u6574\u4e2a\u53f3\u81c2\uff0c\u201c\u6cd5\u672f\u589e\u5e45\u201d\u5df2\u7ecf\u542f\u52a8\uff0c\u9b54\u6cd5\u80fd\u91cf\u5728\u6cd5\u6756\u9876\u7aef\u98de\u901f\u805a\u96c6\uff0c\u51dd\u805a\u6210\u4e00\u4e2a\u7fe0\u7eff\u5149\u7403\uff0c\u84c4\u52bf\u5f85\u53d1\uff0c\u8fd9\u662f\u89e3\u79bb\u672f\u3002</p><p>\u773c\u770b\u6cd5\u672f\u5c31\u8981\u51fb\u51fa\uff0c\u743c\u6069\u7a81\u7136\u76b1\u4e86\u76b1\u7709\uff0c\u8f6c\u8fc7\u8138\u6765\uff0c\u201c\u8428\u9a6c\u65af\u7279\u5148\u751f\uff0c\u8fd8\u662f\u4f60\u6765\u4e0b\u624b\u5427\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4e0d\u60f3\u60f9\u9ebb\u70e6\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5982\u679c\u6211\u6740\u4e86\u5979\uff0c\u90a3\u4e48\u9884\u8a00\u672f\u5f88\u5bb9\u6613\u786e\u5b9a\u6211\uff0c\u5230\u65f6\u5019\u6211\u53ef\u5c31\u6210\u4e86\u4f60\u7684\u66ff\u7f6a\u7f8a\u4e86\u3002\u201d</p><p>\u8428\u9a6c\u65af\u7279\u7565\u7565\u6c89\u541f\uff0c\u201c\u90a3\u4f60\u8ba9\u5f00\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u6765\u52a8\u624b\u3002\u201d</p><p>\u201c\u80fd\u5426\u7528\u9522\u9b42\u672f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u60a8\u80af\u5b9a\u4f1a\u8fd9\u79cd\u6cd5\u672f\u3002\u201d</p><p>\u9522\u9b42\u672f\u662f\u4ea1\u7075\u5b66\u6d3e\u7684\u7ec8\u6781\u6cd5\u672f\u4e4b\u4e00\uff0c\u4e0d\u4f46\u80fd\u591f\u6740\u4eba\uff0c\u800c\u4e14\u80fd\u591f\u56da\u7981\u7075\u9b42\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u5404\u79cd\u901a\u7075\u672f\u3001\u62db\u9b42\u672f\u90fd\u65e0\u7528\u6b66\u4e4b\u5730\uff0c\u60f3\u5bfb\u627e\u51f6\u624b\u5c31\u5f88\u96be\uff0c\u5c31\u7b97\u662f\u6700\u5f3a\u5927\u7684\u9884\u8a00\u672f\uff0c\u4e5f\u53d1\u73b0\u4e0d\u4e86\u591a\u5c11\u6709\u7528\u7684\u7ebf\u7d22\uff0c\u79f0\u5f97\u4e0a\u662f\u5c45\u5bb6\u65c5\u884c\u6740\u4eba\u706d\u53e3\u9632\u6b62\u62a5\u4ec7\u7684\u6700\u4f73\u6cd5\u672f\u4e4b\u4e00\u3002</p><p>\u8428\u9a6c\u65af\u7279\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u5012\u8c28\u614e\u3002\u201d</p><p>\u201c\u5c0f\u4eba\u7269\uff0c\u4e0d\u5f97\u4e0d\u5982\u6b64\u3002\u201d\u743c\u6069\u8bf4\uff0c\u8eac\u8eab\u9000\u540e\u3002</p><p>\u8428\u9a6c\u65af\u7279\u77a5\u4e86\u573a\u5185\u4e00\u773c\uff0c\u518d\u6b21\u786e\u5b9a\u6ca1\u6709\u4eba\u80fd\u591f\u5a01\u80c1\u5230\u4ed6\uff0c\u5c31\u7b97\u662f\u743c\u6069\u7a81\u7136\u53d1\u96be\uff0c\u4ed6\u7684\u6cd5\u672f\u4e5f\u7edd\u5bf9\u4e0d\u53ef\u80fd\u4f24\u5bb3\u5230\u81ea\u5df1\uff0c\u4e8e\u662f\u5b9a\u4e0b\u5fc3\u6765\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u679a\u9ed1\u8272\u5b9d\u77f3\uff0c\u5f00\u59cb\u8bf5\u5492\u3002</p><p>\u9522\u9b42\u672f\u8fd9\u79cd\u5f3a\u5927\u5230\u6781\u70b9\u7684\u6cd5\u672f\uff0c\u65bd\u6cd5\u65f6\u95f4\u81ea\u7136\u6bd4\u666e\u901a\u7684\u6cd5\u672f\u5197\u957f\u5f88\u591a\uff0c\u4f46\u5728\u8428\u9a6c\u65af\u7279\u8fd9\u79cd\u4ea1\u7075\u5927\u5deb\u5e08\u7528\u51fa\u6765\uff0c\u4e5f\u4e0d\u8fc7\u5c31\u662f\u51e0\u79d2\u949f\u7684\u529f\u592b\u3002\u7136\u800c\u5c31\u5728\u8428\u9a6c\u65af\u7279\u521a\u521a\u5ff5\u51fa\u7b2c\u4e00\u53e5\u5492\u8bed\u65f6\uff0c\u743c\u6069\u731b\u7136\u8f6c\u5934\uff0c\u671d\u6885\u83f2\u65af\u5389\u559d\u4e00\u58f0\uff1a\u201c\u8df3\u4e0b\u53bb\uff01\u201d</p><p>\u6885\u83f2\u65af\u6b64\u65f6\u6b63\u7ad9\u5728\u9ad8\u5854\u8fb9\u7f18\uff0c\u8df3\u4e0b\u53bb\u53ea\u4f1a\u6454\u5f97\u7c89\u8eab\u788e\u9aa8\uff0c\u4f46\u5979\u95fb\u8a00\u4e0d\u5047\u601d\u7d22\uff0c\u62b1\u8d77\u51db\u7ffb\u8eab\u540e\u4ef0\uff0c\u4fbf\u6454\u4e86\u4e0b\u53bb\u3002</p><p>\u4e0e\u6b64\u540c\u65f6\uff0c\u743c\u6069\u7684\u6cd5\u6756\u4e0a\u7fe0\u5149\u8ff8\u5c04\uff0c\u84c4\u52bf\u5df2\u4e45\u7684\u89e3\u79bb\u672f\u8f70\u7136\u53d1\u51fa\uff0c\u76ee\u6807\u5374\u65e2\u4e0d\u662f\u5012\u5728\u5730\u4e0a\u7684\u6b23\u5e03\uff0c\u4e5f\u4e0d\u662f\u6b63\u5728\u65bd\u6cd5\u7684\u8428\u9a6c\u65af\u7279\uff0c\u800c\u662f\u4ed6\u65c1\u8fb9\u90a3\u53ea\u5944\u5944\u4e00\u606f\u7684\u516d\u81c2\u86c7\u9b54\u3002</p></article>"}}}]);
