(window.webpackJsonp=window.webpackJsonp||[]).push([[155],{178:function(p){p.exports={chapterTitle:null,title:"\u7b2c1134\u7ae0 \u8116\u5b50\u4e0a\u7684\u7ef3\u7d22\u6e10\u7d27\u65f6",content:"<article><h2>\u7b2c1134\u7ae0 \u8116\u5b50\u4e0a\u7684\u7ef3\u7d22\u6e10\u7d27\u65f6</h2><p>\u3000\u3000\u5b66\u5e9c\u5df7\u5386\u6765\u90fd\u662f\u71d5\u5f52\u6e56\u533a\u57df\u5546\u4e1a\u6c14\u606f\u6700\u6d53\u90c1\u7684\u533a\u57df\uff0c\u53ea\u662f\u4e5d\u516b\u5e74\u540e\u9644\u8fd1\u9ad8\u6821\u5165\u5b66\u7684\u5b66\u751f\u5f88\u96be\u60f3\u8c61\u4e4b\u524d\u5b66\u5e9c\u5df7\u5728\u7e41\u8363\u8868\u9762\u4e4b\u540e\u63a9\u76d6\u7684\u7834\u65e7\u4e0e\u51cb\u655d\uff0c\u6b64\u65f6\u7684\u5b66\u5e9c\u5df7\u4e3b\u8857\u533a\u662f\u7ea6500\u7c73\u7684\u590d\u5f0f\u8857\u5df7\u7ed3\u5408\u7684\u6797\u836b\u5927\u9053\uff0c\u6709\u7740\u7e41\u8363\u5546\u4e1a\u8857\u533a\u7684\u70ed\u95f9\u4e0e\u55a7\u56a3\uff0c\u4e5f\u6709\u7740\u7ed9\u6d53\u5bc6\u68a7\u6850\u6811\u836b\u906e\u95ed\u4e0b\u7684\u60a0\u95f2\uff0c\u4eff\u53e4\u5f0f\u7684\u98ce\u7535\u8857\u706f\u3001\u98ce\u53f6\u9ad8\u8038\u51fa\u68a2\u5934\uff0c\u706f\u76cf\u82e5\u9690\u82e5\u73b0\u7684\u85cf\u5728\u8302\u5bc6\u7684\u6797\u68a2\u91cc\uff0c\u7ed9\u8857\u533a\u5e26\u6765\u4e30\u5bcc\u591a\u59ff\u7684\u5149\u5f71\u53d8\u5e7b\uff0c\u4e5f\u6210\u4e3a\u5b66\u5e9c\u5df7\u503c\u5f97\u5411\u4eba\u5938\u8000\u7684\u4e00\u666f\uff0c\u5373\u4f7f\u8857\u5fc3\u5df7\u5934\u5df7\u5c3e\u7684\u90a3\u51e0\u5ea7\u4e66\u62a5\u4ead\u4e5f\u5145\u6ee1\u827a\u672f\u611f\u3002</p><p>\u3000\u3000\u4e09\u6708\u6625\u66ae\uff0c\u6811\u53d1\u65b0\u53f6\uff0c\u9e1f\u8bed\u82b1\u9999\uff0c\u5df7\u5357\u5934\u662f\u6a61\u6811\u56ed\u5f00\u653e\u5f0f\u7684\u82f1\u5f0f\u8349\u576a\uff0c\u5e73\u5766\u7684\u8349\u576a\u4e0a\u7eff\u6811\u6210\u836b\uff0c\u521a\u5165\u591c\u7684\u9ec4\u660f\uff0c\u8857\u706f\u4eae\u8d77\uff0c\u5b66\u5e9c\u5df7\u91cc\u884c\u4eba\u5982\u7ec7\uff0c\u80fd\u770b\u5230\u8bb8\u591a\u9752\u6625\u660e\u4e3d\u3001\u7a88\u7a95\u52a8\u4eba\u7684\u5b66\u751f\u5c11\u5973\u7a7f\u884c\u5176\u95f4\uff0c\u5bf9\u6709\u4e9b\u4eba\u6765\u8bf4\uff0c\u8fd9\u624d\u7b97\u6b64\u5730\u6700\u8ff7\u4eba\u7684\u98ce\u666f\u3002</p><p>\u3000\u3000\u5728\u65e0\u6240\u4e8b\u4e8b\u7684\u9ec4\u660f\u5c06\u665a\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u3001\u65f6\u5b66\u658c\u7b49\u4eba\u4e5f\u559c\u6b22\u9009\u4e00\u5904\u7a97\u660e\u51e0\u51c0\u7684\u9910\u5385\uff0c\u5750\u5728\u4e34\u7a97\u7684\u4f4d\u5b50\uff0c\u770b\u7740\u8857\u4e0a\u5a07\u598d\u660e\u5a9a\u3001\u7ea4\u5de7\u8ff7\u4eba\u7684\u5c11\u5973\u8d70\u8fc7\u3002</p><p>\u3000\u3000\u4e0b\u5348\u65f6\u8fd8\u770b\u5230\u9648\u52c7\u8000\u6b66\u626c\u5a01\u7684\u9a7e\u9a76\u65b0\u6b3e\u5965\u8fea\u8f7f\u8f66\u7a7f\u68ad\u5b66\u5e9c\u5df7\u8857\u5934\u5df7\u5c3e\uff0c\u8fd9\u65f6\u5019\u53c8\u770b\u5230\u9648\u52c7\u966a\u540c\u80e1\u91d1\u661f\u7b49\u4eba\u51fa\u73b0\u5728\u9910\u5385\u5916\u7684\u8857\u5bf9\u9762\uff0c\u56f4\u7ad9\u5728\u90a3\u91cc\u8bf4\u7740\u8bdd\uff0c\u4f3c\u4e4e\u5728\u8ba8\u8bba\u6311\u9009\u54ea\u5bb6\u9910\u5385\u7528\u9910\u624d\u5408\u5fc3\u610f\uff0c\u8fd8\u6709\u51e0\u4e2a\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u7ad9\u5728\u4ed6\u4eec\u4e2d\u95f4\uff0c\u795e\u60c5\u9a84\u50b2\u7684\u770b\u7740\u8857\u8fb9\u7684\u98ce\u666f\u4e0e\u884c\u4eba\u3002\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u4f1a\u79bb\u5f00\uff0c\u6ca1\u60f3\u5230\u4ed6\u4eec\u5728\u7a97\u53e3\u6d88\u5931\u4e86\u4e00\u4f1a\u513f\uff0c\u53c8\u4ece\u697c\u68af\u53e3\u8d70\u4e0a\u6765\uff0c\u4ed6\u4eec\u770b\u5230\u5f20\u606a\u3001\u675c\u98de\u7b49\u4eba\u5750\u5230\u91cc\u9762\uff0c\u4e5f\u7a0d\u7a0d\u7684\u4e00\u6014\uff0c\u7ad9\u5728\u697c\u68af\u53e3\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u697c\u4e0a\u4e5f\u5c31\u5f20\u606a\u4ed6\u4eec\u5de6\u624b\u8fb9\u6709\u591a\u4eba\u7684\u5e2d\u4f4d\u7a7a\u7740\uff0c\u4ed6\u4eec\u8138\u4e0a\u6ca1\u6709\u592a\u591a\u8868\u60c5\u7684\u671d\u90a3\u91cc\u8d70\u53bb\u3002</p><p>\u3000\u3000\u5b66\u5e9c\u5df7\u5c31\u90a3\u4e48\u51e0\u5bb6\u9ad8\u7ea7\u9910\u5385\uff0c\u675c\u98de\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u90fd\u662f\u5e38\u5ba2\uff0c\u5728\u9910\u5385\u91cc\u9047\u5230\u80e1\u91d1\u661f\u3001\u9648\u52c7\u4ed6\u4eec\u4e5f\u4e0d\u662f\u4e00\u56de\u4e24\u56de\uff0c\u4e0d\u4f1a\u7279\u610f\u7684\u907f\u5f00\u3002\u518d\u8bf4\u6d77\u7c9f\u79d1\u6280\u6536\u8d2d\u521b\u57df\u7684\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u4e4b\u540e\uff0c\u53cc\u65b9\u7684\u77db\u76fe\u81f3\u5c11\u5728\u660e\u9762\u4e0a\u662f\u7f13\u548c\u4e86\u3002</p><p>\u3000\u3000\u201c\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d\u5f20\u606a\u89c1\u8ddf\u7740\u80e1\u91d1\u661f\u540e\u9762\u4e0a\u697c\u7684\u9752\u5e74\u9762\u751f\uff0c\u5e74\u7eaa\u4e5f\u8981\u6bd4\u80e1\u91d1\u661f\u4ed6\u4eec\u5927\u4e00\u4e9b\uff0c\u6587\u8d28\u5f6c\u5f6c\u7684\u6234\u7740\u4e00\u5e45\u91d1\u8fb9\u5e3d\u773c\u955c\uff0c\u4ed6\u4e0a\u697c\u540e\u8fd8\u671d\u675c\u98de\u4ed6\u4eec\u70b9\u5934\u793a\u610f\u3002</p><p>\u3000\u3000\u201c\u5143\u6c5f\u8bc1\u5238\u7684\u4e00\u540d\u7ecf\u7406\uff0c\u6700\u8fd1\u65f6\u5e38\u8ddf\u4ed6\u4eec\u6df7\u5728\u4e00\u8d77\uff0c\u5143\u6c5f\u8bc1\u5238\u7ed9\u5927\u5ba2\u6237\u63d0\u4f9b\u878d\u8d44\uff0c\u770b\u60c5\u5f62\u9648\u52c7\u4ed6\u4eec\u5728\u6d77\u7c9f\u79d1\u6280\u4e0a\u517b\u6210\u7684\u80c3\u53e3\u4e0d\u5c0f\u2026\u2026\u201d\u675c\u98de\u4f4e\u58f0\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u771f\u662f\u4e00\u70b9\u90fd\u4e0d\u5947\u602a\u554a\uff01\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u4ed6\u4e0e\u675c\u98de\u3001\u65f6\u5b66\u658c\u5750\u5728\u9760\u7a97\u7684\u4f4d\u5b50\uff0c\u5c0f\u58f0\u8bf4\u8bdd\u4e5f\u4e0d\u865e\u80e1\u91d1\u661f\u4ed6\u4eec\u80fd\u542c\u89c1\uff0c\u201c\u6d77\u7c9f\u79d1\u6280\u5728\u516c\u5f00\u589e\u53d1\u65b0\u80a1\u4e4b\u524d\u4e00\u5b9a\u8fd8\u4f1a\u6709\u4e00\u6b21\u5bc6\u96c6\u7684\u6258\u5e02\u64cd\u4f5c\uff0c\u4e0e\u5143\u6c5f\u8bc1\u5238\u8054\u7cfb\uff0c\u6216\u8bb8\u662f\u4ed6\u4eec\u8ba4\u4e3a\u4e4b\u524d\u73a9\u7684\u6709\u4e9b\u653e\u4e0d\u5f00\u3002\u201d</p><p>\u3000\u3000\u201c\u80c3\u53e3\u603b\u662f\u8d8a\u6491\u8d8a\u5927\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u4e5f\u662f\u6709\u6043\u65e0\u6050\u3002\u201d\u675c\u98de\u4e0d\u5c51\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u9648\u52c7\u3001\u80e1\u91d1\u661f\u5750\u4e0b\u6765\u540e\u5c06\u8896\u53e3\u5f80\u4e0a\u634b\uff0c\u9732\u51fa\u956b\u4eae\u8000\u773c\u7684\u91d1\u8868\uff0c\u5fc3\u60f3\u4ed6\u4eec\u8fd9\u4e00\u8eab\u540d\u724c\u88c5\u5907\u52a0\u4e0a\u65b0\u8d2d\u7f6e\u7684\u8c6a\u534e\u8f7f\u8f66\uff0c\u5927\u6982\u82b1\u9500\u4e0d\u5c11\u3002\u4ed6\u65e9\u5c31\u77e5\u9053\u80e1\u91d1\u661f\u4e0e\u9648\u52c7\u5728\u5916\u56f4\u7092\u4f5c\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\u6e14\u5229\uff0c\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\u5728\u4e0d\u5230\u4e00\u5e74\u7684\u65f6\u95f4\u91cc\u4e0a\u6da8\u4e86\u8fd1\u4e09\u5341\u500d\uff0c\u60f3\u6765\u4ed6\u4eec\u5373\u4f7f\u635e\u4e86\u8bb8\u591a\u9ed1\u94b1\u4e5f\u5dee\u4e0d\u591a\u6325\u970d\u4e00\u7a7a\u4e86\u3002</p><p>\u3000\u3000\u770b\u5b8f\u4fe1\u7684\u884c\u4e8b\u98ce\u683c\uff0c\u4ed6\u4eec\u5bf9\u6d77\u7c9f\u79d1\u6280\u7684\u5229\u7528\u4e5f\u662f\u4e00\u6b21\u6027\u7684\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u6d77\u7c9f\u79d1\u6280\u516c\u5f00\u589e\u53d1\u524d\u7684\u6258\u5e02\u64cd\u4f5c\u5c06\u662f\u80e1\u91d1\u661f\u3001\u9648\u52c7\u4ed6\u4eec\u5728\u6d77\u7c9f\u79d1\u6280\u4e0a\u7684\u6700\u540e\u4e00\u6b21\u673a\u4f1a\u3002\u80e1\u91d1\u661f\u591a\u534a\u6e05\u695a\u8fd9\u4e00\u70b9\uff0c\u90a3\u610f\u5473\u7740\u4ed6\u4eec\u5341\u6709\u516b\u4e5d\u8981\u5229\u7528\u6700\u540e\u4e00\u6b21\u673a\u4f1a\u73a9\u4e00\u628a\u5927\u7684\u3002</p><p>\u3000\u3000\u56fd\u5185\u8bb8\u591a\u8bc1\u5238\u516c\u53f8\u5728\u4e1a\u52a1\u64cd\u4f5c\u4e0a\u5f88\u4e0d\u6b63\u89c4\uff0c\u5e38\u5e38\u4ee5\u59d4\u6258\u7406\u8d22\u7684\u65b9\u5f0f\u4ece\u4f01\u4e8b\u4e1a\u5355\u4f4d\u7b79\u63aa\u5927\u989d\u8d44\u91d1\u8f6c\u624b\u653e\u8d37\u7ed9\u5ba2\u6237\u4ece\u4e2d\u5403\u606f\u5dee\uff0c\u5f53\u7136\u4e5f\u6709\u8bc1\u5238\u516c\u53f8\u4eb2\u81ea\u62ab\u7532\u4e0a\u9635\u5e55\u540e\u5750\u5e84\u7092\u4f5c\u80a1\u7968\u3002</p><p>\u3000\u3000\u9648\u52c7\u3001\u80e1\u91d1\u661f\u4ed6\u4eec\u80fd\u603b\u662f\u5728\u4e00\u53ea\u80a1\u7968\u4e0a\u83b7\u5f97\uff0c\u8bc1\u5238\u516c\u53f8\u80af\u5b9a\u4e5f\u4f1a\u6709\u6240\u5bdf\u89c9\uff0c\u80e1\u91d1\u661f\u4e0e\u9648\u52c7\u8ddf\u8bc1\u5238\u516c\u53f8\u7684\u4eba\u8d70\u5230\u4e00\u8d77\uff0c\u4e00\u70b9\u90fd\u4e0d\u8ba9\u5f20\u606a\u89c9\u5f97\u610f\u5916\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u5728\u5916\u56f4\u53d1\u4e9b\u5c0f\u8d22\uff0c\u4f1a\u4e0d\u4f1a\u5f97\u5230\u5b8f\u4fe1\u7684\u9ed8\u8bb8\uff1f\u201d\u675c\u98de\u4f4e\u58f0\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u5f88\u53ef\u80fd\u662f\u4ed6\u4eec\u81ea\u5df1\u5728\u73a9\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6d77\u7c9f\u79d1\u6280\u80a1\u7968\u4ea4\u6613\u6d3b\u8dc3\u7684\u7f51\u70b9\u53ea\u6709\u4e24\u5904\uff0c\u4e00\u5904\u5728\u6587\u821f\uff0c\u4e00\u5904\u5728\u5efa\u90ba\uff0c\u90fd\u662f\u661f\u6cb3\u8bc1\u5238\u65d7\u4e0b\u7684\u8425\u4e1a\u90e8\uff0c\u8ddf\u5143\u6c5f\u8bc1\u5238\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\u2026\u2026\u4ed6\u4eec\u8981\u8fd9\u6b21\u8981\u73a9\u4e00\u628a\u5927\u7684\uff0c\u4ece\u5143\u6c5f\u8bc1\u5238\u65d7\u4e0b\u7684\u8425\u4e1a\u90e8\u8ffd\u770b\uff0c\u80fd\u5f88\u5bb9\u6613\u67e5\u5230\u4ed6\u4eec\u5230\u5e95\u73a9\u591a\u5927\u3002\u201d</p><p>\u3000\u3000\u201c\u539f\u6765\u4f60\u4e5f\u4e00\u76f4\u5728\u5173\u6ce8\u7740\u4ed6\u4eec\u554a\uff1f\u201d\u65f6\u5b66\u658c\u795e\u8272\u5174\u594b\u7684\u8bf4\u9053\uff0c\u201c\u800c\u4e14\u4e0d\u662f\u4e00\u822c\u7684\u5173\u6ce8\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u8fd9\u4eba\u5fc3\u773c\u6bd4\u8f83\u5c0f\uff0c\u201d\u5f20\u606a\u574f\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u5728\u80cc\u540e\u67e5\u9526\u6e56\u6709\u6ca1\u6709\u95ee\u9898\uff0c\u96be\u9053\u5c31\u4e0d\u8bb8\u6211\u4eec\u627e\u4e00\u627e\u4ed6\u4eec\u7684\u628a\u67c4\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u628a\u63e1\u5417\uff1f\u201d\u65f6\u5b66\u658c\u8bf4\u8fd9\u8bdd\u91cc\u60c5\u4e0d\u81ea\u7981\u7684\u66f4\u52a0\u538b\u4f4e\u58f0\u97f3\uff0c\u53c8\u7b11\u9053\uff0c\u201c\u5927\u6982\u6211\u7684\u5fc3\u773c\u4e5f\u4e0d\u5927\uff0c\u603b\u662f\u770b\u4e0d\u5f97\u522b\u4eba\u5f97\u610f\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u96be\u8bf4\u2026\u2026\u201d\u5f20\u606a\u5fae\u62ac\u8d77\u5934\uff0c\u6de1\u7136\u7684\u770b\u7740\u4e0d\u8fdc\u5904\u7684\u80e1\u91d1\u661f\u3001\u9648\u52c7\u3002</p><p>\u3000\u3000\u201c\u90a3\u5c31\u662f\u8bf4\u6709\u620f\uff1f\u201d\u65f6\u5b66\u658c\u773c\u775b\u4e00\u4eae\u3002</p><p>\u3000\u3000\u5f53\u524d\u80a1\u5e02\u65b0\u79d1\u6280\u6982\u5ff5\u80a1\u6c14\u52bf\u5982\u8679\uff0c\u6ca1\u6709\u5448\u73b0\u51fa\u4e1d\u6beb\u7684\u9893\u52bf\u6765\uff0c\u4ed6\u4eec\u90fd\u8ba4\u4e3a\u6ca1\u6709\u5f3a\u52bf\u4eba\u7269\u51fa\u9762\u626f\u540e\u817f\uff0c\u5f88\u96be\u963b\u6b62\u5b8f\u4fe1\u8fd9\u4f19\u4eba\u5229\u7528\u6d77\u7c9f\u79d1\u6280\u5230\u516c\u5f00\u5e02\u573a\u5708\u94b1\u7684\u91ce\u5fc3\u3002\u770b\u5230\u80e1\u91d1\u661f\u3001\u9648\u52c7\u8fd9\u4e9b\u4eba\u7ee7\u7eed\u5f97\u610f\u56a3\u5f20\u4e0b\u53bb\uff0c\u65f6\u5b66\u658c\u5fc3\u91cc\u59cb\u7ec8\u5835\u7740\u4e00\u53e3\u90c1\u6c14\uff0c\u4f46\u662f\u4ed6\u77e5\u9053\uff0c\u5373\u4f7f\u80fd\u62ff\u5230\u5b8f\u4fe1\u8fd9\u4f19\u4eba\u8fdd\u89c4\u64cd\u4f5c\u6d77\u7c9f\u79d1\u6280\u80a1\u7968\u7684\u786e\u5b9e\u8bc1\u636e\uff0c\u5728\u5f53\u524d\u7684\u8bc1\u5238\u5e02\u573a\u6cd5\u5236\u73af\u5883\u4e0b\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5927\u7528\u5904\uff0c\u8981\u662f\u5f20\u606a\u51fa\u624b\uff0c\u60c5\u51b5\u5c31\u5927\u4e3a\u4e0d\u540c\uff0c\u65f6\u5b66\u658c\u60f3\u8c61\u4e0d\u51fa\u56fd\u5185\u8fd8\u6709\u4ec0\u4e48\u79c1\u6709\u8d44\u672c\u52bf\u529b\u80fd\u6b63\u9762\u8ddf\u9526\u6e56\u4e00\u4e89\u9ad8\u4e0b\u3002</p><p>\u3000\u3000\u5b8f\u4fe1\u7684\u60c5\u51b5\u4e5f\u5341\u5206\u7684\u7279\u6b8a\uff0c\u62ff\u53e5\u4e0d\u5ba2\u6c14\u7684\u8bdd\u8bf4\uff0c\u5b8f\u4fe1\u7cfb\u53ef\u4ee5\u8bf4\u662f\u56fd\u5185\u5b98\u50da\u79c1\u6709\u5316\u8d44\u672c\u7684\u4ee3\u8868\u3002</p><p>\u3000\u3000\u201c\u80fd\u501f\u52bf\u624d\u884c\uff0c\u770b\u60c5\u51b5\u5427\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u675c\u98de\u77e5\u9053\u5f20\u606a\u4e0d\u4f1a\u56e0\u4e3a\u6b64\u7c7b\u7684\u6069\u6028\u800c\u6d88\u8017\u9526\u6e56\u7684\u5b9e\u529b\uff0c\u5f71\u54cd\u5230\u9526\u6e56\u7684\u53d1\u5c55\uff0c\u4f46\u662f\u843d\u77f3\u4e0b\u4e95\u7684\u4e8b\u60c5\u4e5f\u4ece\u6765\u90fd\u4e0d\u4f1a\u541d\u556c\u505a\u7684\uff0c\u4ed6\u95ee\u9053\uff1a\u201c\u4e94\u6708\u4e2d\u65ec\u4e4b\u524d\u5f62\u52bf\u4f1a\u6709\u53d8\u5316\uff1f\u201d</p><p>\u3000\u3000\u201c\u96be\u8bf4\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u671d\u80e1\u91d1\u661f\u3001\u9648\u52c7\u90a3\u91cc\u5476\u5476\u5634\uff0c\u538b\u7740\u58f0\u97f3\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u60f3\u73a9\u4e00\u628a\u5927\u7684\uff0c\u53ef\u4ee5\u5411\u5143\u6c5f\u8bc1\u5238\u878d\u8d44\u4e70\u5165\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\u5750\u7b49\u9ad8\u4ef7\u629b\u51fa\uff0c\u5143\u6c5f\u8bc1\u5238\u4e3a\u4e86\u4fdd\u8bc1\u8d44\u91d1\u7684\u5b89\u5168\uff0c\u8981\u6309\u7167\u4e00\u5b9a\u7684\u6bd4\u4f8b\u6536\u8d2d\u4fdd\u8bc1\u91d1\uff0c\u4e00\u65e6\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\u8dcc\u7834\u4fdd\u8bc1\u91d1\u7684\u98ce\u9669\u63a7\u5236\u8303\u56f4\uff0c\u5143\u6c5f\u8bc1\u5238\u4e3a\u4e86\u4fdd\u8bc1\u653e\u8d37\u8d44\u91d1\u7684\u5b89\u5168\uff0c\u5c06\u4f1a\u5f3a\u884c\u66ff\u4ed6\u4eec\u5e73\u4ed3\u2026\u2026\u8fd9\u79cd\u8fdd\u89c4\u64cd\u4f5c\u5728\u56fd\u5185\u5c61\u7981\u4e0d\u6b62\uff0c\u4f46\u662f\u5728\u56fd\u5916\uff0c\u878d\u8d44\u878d\u5238\u5374\u662f\u8f83\u4e3a\u5e38\u89c1\u7684\u91d1\u878d\u6760\u6746\u624b\u6bb5\uff0c\u6295\u8d44\u8005\u80fd\u4ee5\u5c11\u91cf\u7684\u4fdd\u8bc1\u91d1\u6258\u52a8\u4e94\u500d\u3001\u5341\u500d\u4e8e\u4fdd\u8bc1\u91d1\u7684\u5e9e\u5927\u8d44\u672c\u3002\u5728\u8bc1\u5238\u5e02\u573a\u8d8a\u53d1\u8d70\u5411\u6781\u7aef\uff0c\u878d\u8d44\u6216\u878d\u5238\u7684\u884c\u4e3a\u8d8a\u662f\u666e\u904d\uff0c\u8d44\u672c\u5e02\u573a\u7684\u6760\u6746\u8d8a\u53d1\u4e25\u91cd\uff0c\u4f7f\u8574\u542b\u7684\u98ce\u9669\u4e5f\u6025\u5267\u589e\u52a0\u2026\u2026\u5728\u4e34\u754c\u533a\u57df\u7684\u66b4\u6da8\u66b4\u8dcc\uff0c\u4f1a\u8bf1\u53d1\u5927\u91cf\u7684\u5e73\u4ed3\u884c\u4e3a\uff0c\u5e73\u4ed3\u884c\u4e3a\u53c8\u5c06\u52a0\u5267\u6da8\u6216\u8dcc\u7684\u5e45\u5ea6\uff0c\u4f7f\u4e4b\u9677\u5165\u4e00\u79cd\u96be\u4ee5\u904f\u5236\u7684\u6076\u6027\u5faa\u73af\u4e4b\u4e2d\uff0c\u4ece\u800c\u8bf1\u53d1\u80a1\u707e\u4e4b\u7c7b\u7684\u91d1\u878d\u5371\u673a\u3002\u73b0\u5728\u5168\u7403\u91d1\u878d\u5e02\u573a\u5728\u65b0\u79d1\u6280\u6982\u5ff5\u80a1\u9886\u57df\u7684\u6295\u673a\u6c14\u6c1b\u6d53\u90c1\uff0c\u5373\u4f7f\u6ca1\u6709\u5230\u8f6c\u6298\u70b9\uff0c\u4e5f\u79bb\u8f6c\u6298\u70b9\u4e0d\u8fdc\u4e86\uff0c\u6216\u8bb8\u53ea\u662f\u8fd8\u7f3a\u5c11\u8bf1\u53d1\u8f6c\u6298\u7684\u4e00\u4e2a\u52a8\u56e0\uff0c\u81f3\u5c11\u6211\u4eec\u4e0d\u5e94\u8be5\u8fc7\u5206\u7684\u4e50\u89c2\u2026\u2026\u201d</p><p>\u3000\u3000\u675c\u98de\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u5f88\u96be\u5224\u65ad\u5168\u7403\u91d1\u878d\u5e02\u573a\u5728\u65b0\u79d1\u6280\u7ecf\u6d4e\u9886\u57df\u7684\u6295\u673a\u6c14\u6c1b\u4e0e\u98ce\u9669\u7a0b\u5ea6\uff0c\u53ea\u6653\u5f97\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u5728\u8fc7\u53bb\u4e00\u5e74\u5185\u4e0a\u6da8\u4e86117\uff05\uff0c\u4e5f\u4e0d\u662f\u5341\u5206\u7684\u5938\u5f20\uff0c\u4f46\u662f\u653e\u5230\u4e00\u4e2a\u66f4\u957f\u7684\u65f6\u95f4\u8303\u56f4\u6765\u770b\uff0c\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u9664\u4e86\u4e5d\u4e03\u5e74\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u65f6\u671f\u7565\u6709\u56de\u843d\u4e4b\u540e\uff0c\u4ece\u4e5d\u5341\u5e74\u4e2d\u53f6\u5c31\u5f00\u59cb\u4e86\u8fd9\u6b21\u725b\u5e02\u4e4b\u957f\u9014\u65c5\u7a0b\u3002</p><p>\u3000\u3000\u201c\u56fd\u5185\u91d1\u878d\u5e02\u573a\u76f8\u5bf9\u5c01\u95ed\uff0c\u4e5d\u4e03\u3001\u4e5d\u516b\u5e74\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\uff0c\u4e9a\u6d32\u751a\u81f3\u6b27\u7f8e\u4e3b\u8981\u8bc1\u5238\u5e02\u573a\u90fd\u53d7\u5230\u4e0d\u540c\u7a0b\u5ea6\u7684\u62d6\u7d2f\uff0c\u56fd\u5185\u7684\u8bc1\u5238\u5e02\u573a\u53d7\u5f71\u54cd\u5374\u751a\u5fae\uff0c\u4f46\u662f\u8fd9\u6b21\u7565\u6709\u4e0d\u540c\u3002\u65b0\u7ecf\u6d4e\u6d6a\u6f6e\u5e2d\u5377\u5168\u7403\u65f6\uff0c\u56fd\u5185\u4e5f\u662f\u6d77\u5916\u98ce\u9669\u57fa\u91d1\u89c5\u98df\u7684\u4e3b\u8981\u5730\u70b9\uff0c\u5927\u91cf\u6d8c\u8fdb\u7684\u98ce\u6295\u8d44\u91d1\u5c06\u56fd\u5185\u7684\u4e92\u8054\u7f51\u4ea7\u4e1a\u8fc5\u901f\u5439\u81a8\u80c0\u8d77\u6765\uff0c\u4f7f\u5f97\u56fd\u5185\u8bc1\u5238\u5e02\u573a\u91cc\u4e0e\u4e92\u8054\u7f51\u7a0d\u6cbe\u8fb9\u7684\u4e0a\u5e02\u516c\u53f8\u80a1\u4ef7\u98da\u6da8\uff0c\u4eba\u4eec\u5c31\u50cf\u662f\u7740\u4e86\u9b54\u4f3c\u7684\u8ffd\u9010\u7f51\u7edc\u6982\u5ff5\u80a1\uff0c\u5c31\u50cf\u6d77\u7c9f\u79d1\u6280\u5df2\u7ecf\u4e0a\u6da8\u4e86\u8fd1\u4e09\u5341\u500d\u65f6\uff0c\u4ecd\u6709\u5927\u91cf\u4eba\u79ef\u6781\u4e70\u8fdb\uff0c\u5c31\u50cf\u8d4c\u6027\u5341\u8db3\u53c8\u72c2\u5984\u81ea\u5927\u7684\u8d4c\u5f92\uff0c\u4f46\u662f\u4e5f\u5c06\u53d8\u5f97\u66f4\u52a0\u7684\u654f\u611f\uff0c\u5bf9\u6765\u4e34\u7684\u53d8\u5c40\u751a\u81f3\u4f1a\u505a\u51fa\u8fc7\u5ea6\u53cd\u5e94\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5176\u4e2d\u7684\u53d8\u5c40\u4e0d\u96be\u53d1\u73b0\uff0c\u96be\u4ee5\u628a\u63e1\u662f\u53d8\u5c40\u4f55\u65f6\u4f1a\u6765\u4e34\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u4e0d\u96be\uff0c\u8981\u6ca1\u6709\u4f60\u6765\u70b9\u900f\uff0c\u6211\u4eec\u5374\u662f\u770b\u4e0d\u5230\uff0c\u201d\u65f6\u5b66\u658c\u8bf4\u9053\uff0c\u201c\u8981\u80fd\u518d\u9884\u6d4b\u51c6\u65f6\u673a\uff0c\u90a3\u771f\u79f0\u5f97\u4e0a\u795e\u4eba\u4e86\uff0c\u201d\u53c8\u60f3\u5230\u9526\u6e56\u8fd9\u4e9b\u5e74\u6765\u7684\u5d1b\u8d77\u5947\u8ff9\uff0c\u606d\u7ef4\u7684\u8bf4\u4e86\u4e00\u53e5\uff0c\u201c\u4f60\u5c31\u7b97\u4e0d\u662f\u795e\u4eba\uff0c\u4e5f\u76f8\u5dee\u4e0d\u8fdc\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u5934\u800c\u7b11\uff0c\u4e0d\u8ddf\u65f6\u5b66\u658c\u626f\u8fd9\u4e2a\uff0c\u4ed6\u770b\u5230\u80e1\u91d1\u661f\u80f8\u524d\u8fd8\u522b\u7740\u521b\u57df\u7f51\u5427\u5b9e\u4e60\u7ecf\u7406\u7684\u94ed\u724c\uff0c\u4ed6\u5728\u521b\u57df\u7684\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u7ed9\u6d77\u7c9f\u79d1\u6280\u6536\u8d2d\u4e4b\u540e\u5c31\u6ca1\u6709\u7279\u522b\u5173\u5fc3\u8fc7\u5efa\u90ba\u7684\u7f51\u5427\u4ea7\u4e1a\u53d1\u5c55\u72b6\u51b5\uff0c\u4fbf\u95ee\u8d77\u5efa\u90ba\u7684\u7f51\u5427\u4ea7\u4e1a\u53d1\u5c55\u72b6\u51b5\u3002</p><p>\u3000\u3000\u201c\u4e25\u6587\u4ecb\u3001\u6797\u96ea\u8fd9\u4e9b\u4eba\u5c31\u6307\u671b\u7740\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u7ed9\u6d77\u7c9f\u79d1\u6280\u7684\u9ad8\u65b0\u6280\u4ea7\u4e1a\u4e1a\u7ee9\u6491\u95e8\u9762\uff0c\u6295\u5165\u5927\u91cf\u7684\u8d44\u91d1\u5c31\u662f\u671f\u5f85\u6709\u8d85\u8fc7\u9884\u671f\u7684\u56de\u62a5\uff0c\u53ea\u662f\u7f51\u5427\u4ea7\u4e1a\u7684\u8fdb\u5165\u95e8\u69db\u6bd4\u8f83\u4f4e\uff0c\u6d77\u7c9f\u79d1\u6280\u7684\u75af\u72c2\u6269\u5f20\uff0c\u4e5f\u8bf1\u53d1\u76f8\u5f53\u89c4\u6a21\u7684\u8ddf\u98ce\u6f6e\u3002\u6211\u6ca1\u6709\u5173\u5fc3\u6700\u65b0\u7684\u6570\u636e\uff0c\u4f30\u8ba1\u7740\u5728\u8fc7\u53bb\u4e00\u5e74\u91cc\uff0c\u603b\u673a\u53f0\u91cf\u80fd\u7ffb\u4e00\u756a\uff0c\u5e94\u8be5\u8d85\u8fc7\u5341\u4e8c\u4e07\u53f0\u4e86\u3002\u4f60\u4e0d\u8981\u770b\u5b66\u5e9c\u5df7\u8fd9\u8fb9\u4e00\u76f4\u90fd\u53ea\u6709\u4e24\u5bb6\u5927\u578b\u7f51\u5427\uff0c\u90a3\u662f\u56e0\u4e3a\u8fd9\u91cc\u7684\u79df\u91d1\u9ad8\uff0c\u9a6c\u83b2\u8857\u4e0a\u7684\u7f51\u5427\u90fd\u6210\u5806\u4e86\uff0c\u73b0\u5728\u4ed6\u4eec\u6bd4\u8c01\u90fd\u70ed\u5fc3\u89c4\u8303\u7f51\u5427\u4ea7\u4e1a\u3002\u73b0\u5728\u8fd8\u80fd\u7ef4\u6301\u8f83\u9ad8\u7684\u5229\u6da6\uff0c\u8981\u662f\u603b\u673a\u53f0\u89c4\u6a21\u7ee7\u7eed\u4ee5\u8fd9\u79cd\u901f\u5ea6\u53d1\u5c55\u4e0b\u53bb\uff0c\u4e0d\u9700\u8981\u4e00\u5e74\uff0c\u5927\u5bb6\u5c31\u8981\u538b\u4ef7\u62a2\u5ba2\u6e90\u4e86\u2026\u2026\u201d\u675c\u98de\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5bf9\u4e86\uff0c\u8054\u901a\u4e1c\u6d77\u7701\u516c\u53f8\u660e\u5929\u4f1a\u6709\u5bb4\u8bf7\uff0c\u6211\u672c\u6765\u60f3\u63a8\u6389\uff0c\u4e0d\u8fc7\u6211\u542c\u8bf4\u8054\u901a\u4e1c\u6d77\u7701\u516c\u53f8\u6709\u4e00\u4e2a\u6bd4\u8f83\u6709\u610f\u601d\u7684\u6295\u8d44\u9879\u76ee\u8fd1\u671f\u8981\u5b9e\u884c\uff0c\u4f60\u4eec\u4fe9\u966a\u6211\u4e00\u8d77\u8fc7\u53bb\u8e6d\u987f\u5bb4\u5e2d\uff0c\u6216\u8bb8\u4e5f\u80fd\u7ed9\u4ed6\u4eec\u65b0\u6295\u8d44\u9879\u76ee\u4e00\u4e9b\u5efa\u8bae\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u8054\u901a\u53c8\u60f3\u5728\u4e1c\u6d77\u641e\u4ec0\u4e48\u82b1\u6837\uff1f\u201d\u675c\u98de\u95ee\u9053\uff0c\u8054\u901a\u8fd9\u4e9b\u5e74\u5728\u4e3b\u8425\u4e1a\u52a1\u4e0a\u4e00\u76f4\u7ed9\u4e2d\u56fd\u79fb\u52a8\u538b\u5236\uff08\u524d\u8eab\u4e3a\u79fb\u52a8\u901a\u4fe1\u5c40\uff09\uff0c\u65f6\u4e0d\u65f6\u53d1\u5c55\u4e9b\u5176\u4ed6\u9879\u76ee\u589e\u52a0\u8425\u6536\u3002</p><p>\u3000\u3000\u201c\u660e\u5929\u53bb\u4e86\u5c31\u77e5\u9053\u3002\u201d\u5f20\u606a\u5356\u7740\u5173\u5b50\u4e0d\u80af\u8fd9\u65f6\u5019\u5c31\u5c06\u8c1c\u5e95\u63ed\u5f00\u3002</p><p>\u3000\u3000\u80e1\u91d1\u661f\u4e0e\u9648\u52c7\u4ed6\u4eec\u770b\u5230\u5f20\u606a\u3001\u675c\u98de\u3001\u65f6\u5b66\u658c\u4e09\u4e2a\u4eba\u5728\u4e34\u7a97\u7684\u4f4d\u5b50\u4e0a\u7a83\u7a83\u79c1\u8bed\uff0c\u6000\u7591\u662f\u5728\u8bae\u8bba\u4ed6\u4eec\uff0c\u5374\u542c\u4e0d\u6e05\u4ed6\u4eec\u5728\u8bae\u8bba\u4ec0\u4e48\uff0c\u53ea\u662f\u5fcd\u4e0d\u4f4f\u4f1a\u56de\u5934\u770b\u3002\u56de\u5934\u770b\u65f6\uff0c\u5f20\u606a\u8138\u4e0a\u7684\u8868\u60c5\u5f88\u6de1\uff0c\u675c\u98de\u4e0e\u65f6\u5b66\u658c\u8fd8\u8ddf\u4ed6\u4eec\u7b11\u7b11\uff0c\u53ea\u662f\u7b11\u5bb9\u9634\u9634\u7684\uff0c\u5341\u5206\u7684\u8be1\u5f02\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u56db\u6708\u4e00\u65e5\uff0c\u8054\u901a\u4e1c\u6d77\u7701\u5206\u516c\u53f8\u5728\u5efa\u90ba\u54b8\u4ea8\u9152\u5e97\u4e3e\u529e\u7684\u5bb4\u8bf7\u662f\u5e86\u795d\u5efa\u90ba\u3001\u60e0\u5c71\u5730\u533a\u8054\u901a\u65b0\u7528\u6237\u6708\u589e\u6da8\u91cf\u7a81\u7834\u4e09\u4e07\u4eba\uff0c\u4e0e\u4e2d\u56fd\u79fb\u52a8\u5728\u5efa\u90ba\u3001\u60e0\u5c71\u5730\u533a\u7684\u65b0\u589e\u7528\u6237\u89c4\u6a21\u76f8\u5dee\u65e0\u51e0\u3002</p><p>\u3000\u3000\u8054\u901a\u80fd\u5728\u5efa\u90ba\u3001\u60e0\u5c71\u53d6\u5f97\u5982\u6b64\u4e4b\u597d\u7684\u6210\u7ee9\uff0c\u5173\u952e\u8fd8\u662f\u4f9d\u8d56\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\u63a8\u51fa\u5145\u503c\u9001\u624b\u673a\u3001\u65b0\u5f00\u6237\u8d60\u9001\u8d2d\u673a\u4ee3\u91d1\u5238\u7b49\u4fc3\u9500\u6d3b\u52a8\u5438\u5f15\u4e86\u5927\u91cf\u7684\u65b0\u7528\u6237\u3002</p><p>\u3000\u3000\u7531\u4e8e\u8054\u901a\u6709\u9009\u62e9\u7684\u5728\u91cd\u70b9\u57ce\u5e02\u63a8\u5e7f\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\uff0c\u4e2d\u56fd\u79fb\u52a8\u65e0\u6cd5\u7ed9\u4e88\u6574\u4f53\u7684\u5e94\u5bf9\u7b56\u7565\uff0c\u901a\u5e38\u7531\u5404\u5730\u7701\u6216\u5730\u5e02\u5206\u516c\u53f8\u5404\u81ea\u4e3a\u9635\u7684\u6709\u9488\u5bf9\u6027\u7684\u63a8\u51fa\u4f18\u60e0\u653f\u7b56\u8ddf\u8054\u901a\u4e89\u5148\u5438\u6536\u65b0\u7528\u6237\uff0c\u53ea\u662f\u4e2d\u56fd\u79fb\u52a8\u5404\u5730\u7701\u6216\u5730\u5e02\u5206\u516c\u53f8\u7684\u6743\u9650\u53d7\u5230\u9650\u5236\uff0c\u7b49\u603b\u90e8\u5ba1\u6279\u53c8\u4f1a\u6162\u4e00\u62cd\u4e24\u62cd\u3002</p><p>\u3000\u3000\u6ca1\u6709\u4e2d\u56fd\u79fb\u52a8\u603b\u90e8\u7684\u7edf\u4e00\u90e8\u7f72\uff0c\u5404\u5730\u7684\u79fb\u52a8\u5206\u516c\u53f8\u66f4\u52a0\u4e0d\u53ef\u80fd\u4ece\u9526\u6e56\u83b7\u5f97\u4f4e\u4ef7\u4f9b\u5e94\u624b\u673a\u82af\u7247\u7684\u652f\u6301\uff0c\u65e0\u6cd5\u590d\u5236\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\uff0c\u5176\u4ed6\u7684\u4f18\u60e0\u6d3b\u52a8\u53c8\u663e\u5f97\u529b\u5ea6\u4e0d\u8db3\uff0c\u4ece\u800c\u9677\u5165\u88ab\u52a8\u3002</p><p>\u3000\u3000\u4e2d\u56fd\u79fb\u52a8\u5185\u90e8\u6216\u8bb8\u4f1a\u611f\u5230\u5e86\u5e78\uff0c\u8fd9\u6837\u7684\u60c5\u51b5\u773c\u4e0b\u8fd8\u53ea\u53d1\u751f\u5728\u5c11\u6570\u51e0\u5ea7\u57ce\u5e02\uff0c\u65e0\u8bba\u662f\u4ece\u91cf\u4e0a\uff0c\u8fd8\u662f\u4ece\u8d28\u4e0a\uff0c\u8054\u901a\u53d1\u5c55\u65b0\u7528\u6237\u7684\u89c4\u6a21\u8fd8\u8fdc\u8fdc\u4e0d\u8db3\u4ee5\u5a01\u80c1\u5230\u4e2d\u56fd\u79fb\u52a8\u7684\u5730\u4f4d\uff0c\u6240\u4ee5\u5bf9\u8054\u901a\u5728\u6781\u4e2a\u522b\u57ce\u5e02\u7684\u8ffd\u8d76\u95ee\u9898\uff0c\u4e2d\u56fd\u79fb\u52a8\u7684\u9ad8\u5c42\u4e5f\u6ca1\u6709\u7ed9\u4e88\u592a\u591a\u7684\u91cd\u89c6\u3002</p><p>\u3000\u3000\u6768\u84c9\u5e73\u8fd9\u65f6\u5019\u624d\u66f4\u6df1\u523b\u7684\u4f53\u4f1a\u5230\u201c\u7f13\u5175\u4e4b\u8ba1\u201d\u7684\u5999\u5904\uff1a\u9996\u5148\u7f13\u89e3\u8054\u901a\u81ea\u8eab\u56e0\u6b64\u57fa\u7840\u8bbe\u65bd\u5efa\u9020\u4e0d\u8db3\u800c\u4ea7\u751f\u7684\u77db\u76fe\uff1b\u5b9e\u65bd\u5bf9\u4e2d\u5fc3\u57ce\u5e02\u7684\u7a81\u7834\uff0c\u4f7f\u8054\u901a\u53d1\u5c55\u65b0\u7528\u6237\u89c4\u6a21\u4f9d\u65e7\u6709\u76f8\u5f53\u53ef\u89c2\u7684\u6da8\u5e45\uff0c\u540c\u65f6\u53c8\u61c8\u6020\u4e86\u7ade\u4e89\u5bf9\u624b\u7684\u6ce8\u610f\u529b\uff0c\u8d62\u5f97\u66f4\u591a\u7684\u65f6\u95f4\u6765\u52a0\u5f3a\u8054\u901aGSM\u7f51\u7edc\u7684\u5efa\u8bbe\u3002</p><p>\u3000\u3000\u73b0\u5728\u5404\u7701\u7ea7\u5206\u516c\u53f8\u65b0\u7528\u6237\u53d1\u5c55\u7b56\u7565\u7684\u5236\u5b9a\u4e0e\u5b9e\u884c\uff0c\u6768\u84c9\u5e73\u90fd\u4f1a\u76f4\u63a5\u53c2\u4e0e\uff0c\u4e0d\u8fc7\u672c\u7740\u201c\u6253\u67aa\u7684\u4e0d\u8981\u3001\u6084\u6084\u8fdb\u6751\u201d\u7684\u4e3b\u65e8\uff0c\u6768\u84c9\u5e73\u5230\u5730\u65b9\u6765\u90fd\u76f8\u5f53\u4f4e\u8c03\uff0c\u4e0d\u4f1a\u5e7f\u9080\u5a92\u4f53\u8bb0\u8005\u8ffd\u8e2a\u62a5\u9053\uff0c\u5373\u4f7f\u8981\u8bba\u529f\u884c\u8d4f\uff0c\u90a3\u4e5f\u8981\u7b49\u5230\u559d\u5e86\u529f\u9152\u7684\u65f6\u5019\u3002</p><p>\u3000\u3000\u6768\u84c9\u5e73\u8fd9\u6b21\u5230\u5efa\u90ba\u6765\uff0c\u9664\u4e86\u53c2\u52a0\u5185\u90e8\u7684\u5e86\u795d\u5bb4\u4f1a\u4e4b\u5916\uff0c\u8fd8\u8981\u8ddf\u76db\u946b\u7535\u8baf\u8c08\u5408\u4f5c\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u76db\u946b\u7535\u5668\u65e9\u671f\u5728\u4ece\u4e8b\u624b\u673a\u4ee3\u7406\u53ca\u9500\u552e\u65f6\u6700\u65e9\u9080\u8bf7\u90ae\u7535\u901a\u4fe1\u5c40\u5230\u95e8\u5e97\u91cc\u8bbe\u670d\u52a1\u4e13\u67dc\u65b9\u4fbf\u8d2d\u673a\u8005\uff0c\u90ae\u7535\u901a\u4fe1\u5c40\u51e0\u7ecf\u53d8\u9769\uff0c\u73b0\u5728\u5df2\u7ecf\u6539\u4e3a\u4e2d\u56fd\u79fb\u52a8\u5230\u76db\u946b\u7684\u95e8\u5e97\u91cc\u5f00\u8bbe\u670d\u52a1\u4e13\u67dc\uff0c\u8fd9\u4e00\u6a21\u5f0f\u4e5f\u8fc5\u901f\u7ed9\u5176\u4ed6\u624b\u673a\u4e2d\u95f4\u5546\u3001\u9500\u552e\u5546\u6a21\u4eff\u5e76\u53d1\u626c\u5149\u5927\u3002\u8fd9\u4e0d\u4ec5\u4e3a\u624b\u673a\u4e2d\u95f4\u5546\u3001\u9500\u552e\u5546\u63d0\u9ad8\u624b\u673a\u9500\u91cf\u505a\u51fa\u8d21\u732e\uff0c\u4e5f\u4e3a\u4e2d\u56fd\u79fb\u52a8\u53d1\u5c55\u65b0\u7528\u6237\u63d0\u4f9b\u4fbf\u6377\uff0c\u50cf\u76db\u8baf\u7535\u8baf\u65d7\u4e0b\u4e3b\u8981\u76f4\u8425\u95e8\u5e97\u5dee\u4e0d\u591a\u90fd\u8bbe\u6709\u79fb\u52a8\u7684\u670d\u52a1\u4e13\u67dc\uff0c\u8054\u901a\u5728\u8fd9\u65b9\u9762\u505a\u7684\u6bd4\u8f83\u5dee\u52b2\uff0c\u4e5f\u53ea\u6709\u76db\u8baf\u7535\u8baf\u5728\u4e2d\u5fc3\u57ce\u5e02\u7684\u65d7\u8230\u5e97\u624d\u6709\u8054\u901a\u7684\u670d\u52a1\u4e13\u67dc\u3002</p><p>\u3000\u3000\u6768\u84c9\u5e73\u8fd1\u671f\u5c31\u60f3\u7740\u8981\u5982\u4f55\u5f25\u8865\u8fd9\u65b9\u9762\u5de5\u4f5c\u7684\u7f3a\u61be\uff0c\u5f53\u7136\u6700\u4f73\u7684\u7a81\u7834\u53e3\u5c31\u662f\u9526\u6e56\u65d7\u4e0b\u7684\u6210\u5458\u4f01\u4e1a\u76db\u946b\u7535\u8baf\u3002</p><p>\u3000\u3000\u76db\u946b\u7535\u8baf\uff08\u542b\u76db\u946b\u7535\u5668\u5bb6\u573a\u5356\u573a\u5185\u90e8\u7684\u624b\u673a\u4e13\u67dc\uff09\u662f\u56fd\u5185\u89c4\u6a21\u6700\u5927\u7684\u624b\u673a\u96f6\u552e\u4f01\u4e1a\uff0c\u4ece\u4e5d\u4e03\u5e74\u5c31\u5f00\u59cb\u5b9e\u65bd\u201c\u767e\u57ce\u767e\u5e97\u201d\u3001\u201c\u5343\u57ce\u5343\u5e97\u201d\u4e24\u5957\u53d1\u5c55\u65b9\u6848\u6210\u529f\u7684\u5c06\u624b\u673a\u76f4\u8425\u7f51\u7edc\u94fa\u8bbe\u5230\u53bf\u57df\u57ce\u9547\u4e00\u7ea7\uff0c\u5728\u56fd\u5185\u624b\u673a\u96f6\u552e\u4ea7\u4e1a\u91cc\u6240\u5360\u7684\u4efd\u989d\u65e5\u76ca\u5e9e\u5927\u3002</p><p>\u3000\u3000\u4e24\u5343\u5e74\u540e\u4e00\u5230\u4e09\u6708\u4efd\uff0c\u76db\u946b\u7535\u8baf\u5171\u9500\u552e\u624b\u673a\u4e00\u767e\u4e5d\u5341\u4e07\u90e8\u6709\u4f59\uff0c\u5360\u56fd\u5185\u624b\u673a\u5e02\u573a\u7ea618\uff05\u7684\u4efd\u989d\uff0c\u800c\u540c\u65f6\u9526\u6e56\u4e5f\u624d\u751f\u4ea7\u624b\u673a\u4e0d\u5230\u4e00\u767e\u56db\u5341\u4e07\u90e8\u3002\u5728\u76db\u946b\u65b0\u8baf\u4e00\u5230\u4e09\u6708\u4efd\u9500\u552e\u7684\u4e00\u767e\u4e5d\u5341\u4e07\u90e8\u624b\u673a\u91cc\uff0c\u5176\u4e2d\u7ea6\u516d\u6210\u5ba2\u6237\u662f\u9996\u6b21\u8d2d\u4e70\u624b\u673a\uff0c\u8981\u662f\u8054\u901a\u80fd\u5c06\u8fd9\u90e8\u5206\u7528\u6237\u4e89\u53d6\u8fc7\u53bb\uff0c\u5dee\u4e0d\u591a\u5c31\u62b5\u5f97\u4e0a\u8054\u901a\u53bb\u5e74\u5168\u5e74\u7684\u65b0\u5f00\u6237\u89c4\u6a21\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u5177\u4f53\u7684\u4e8b\u60c5\u81ea\u7136\u7531\u90b5\u5fc3\u6587\u53bb\u5934\u75bc\uff0c\u5f20\u606a\u62c9\u7740\u675c\u98de\u3001\u65f6\u5b66\u658c\u51fa\u5e2d\u5bb4\u4f1a\uff0c\u9664\u4e86\u6768\u84c9\u5e73\u4eb2\u81ea\u5230\u5efa\u90ba\u6765\u4e4b\u5916\uff0c\u8fd8\u6709\u5c31\u662f\u8054\u901a\u4e1c\u6d77\u7701\u5206\u516c\u53f8\u6253\u7b97\u5728\u5efa\u90ba\u3001\u60e0\u5c71\u4e24\u5730\u53d1\u5c55\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u3002\u5728\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u4e0a\uff0c\u675c\u98de\u4e0e\u65f6\u5b66\u658c\u90fd\u662f\u80fd\u63d0\u51fa\u6709\u76ca\u610f\u89c1\u7684\uff0c\u867d\u7136\u5f20\u606a\u5f88\u4e0d\u770b\u597d\u8054\u901a\u53d1\u5c55\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\uff0c\u4f46\u662f\u4ed6\u4e0d\u4f1a\u715e\u8d39\u82e6\u5fc3\u7684\u505a\u65e0\u7528\u529f\u53bb\u529d\u963b\u8054\u901a\uff0c\u4ed6\u751a\u81f3\u5e0c\u671b\u8054\u901a\u5728\u5efa\u90ba\u53d1\u5c55\u8fde\u9501\u7f51\u5427\u4ea7\u4e1a\u66f4\u731b\u70c8\u3001\u66f4\u8fc5\u6377\u4e00\u4e9b\uff0c\u81f3\u5c11\u8981\u80fd\u5728\u8fd1\u671f\u8ba9\u6d77\u7c9f\u79d1\u6280\u611f\u5230\u4e9b\u538b\u529b\u3002</p></article>"}}}]);
