(window.webpackJsonp=window.webpackJsonp||[]).push([[155],{177:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u516b\u8282 \u4e5d\u72f1\u4e4b\u4e3b",content:"<article><h2>\u7b2c\u4e8c\u5341\u516b\u8282 \u4e5d\u72f1\u4e4b\u4e3b</h2><p>\u70bc\u9b54\u6b63\u5728\u5168\u529b\u62b5\u6297\u9ece\u660e\u4e4b\u77f3\u7684\u5a01\u538b\uff0c\u9aa4\u7136\u89c1\u6885\u83f2\u65af\u63d0\u5251\u51b2\u6765\uff0c\u4ed3\u4fc3\u95f4\u4e3e\u8d77\u624b\u4e2d\u53f7\u89d2\u4e00\u6321\u3002\u94f6\u5251\u548c\u53f7\u89d2\u649e\u5728\u4e00\u8d77\uff0c\u53d1\u51fa\u9707\u8033\u6b32\u804b\u7684\u7206\u88c2\u58f0\uff0c\u70bc\u9b54\u7684\u53e3\u9f3b\u4e03\u7a8d\u4e2d\u90fd\u55b7\u6d8c\u51fa\u9ed1\u8272\u7684\u84b8\u6c7d\u6765\uff0c\u6d53\u6d53\u7684\u8840\u8165\u5473\u987f\u65f6\u5f25\u6f2b\u4e86\u6574\u4e2a\u5927\u5385\uff0c\u4ed6\u663e\u7136\u4e0d\u662f\u6885\u83f2\u65af\u7684\u5bf9\u624b\uff0c\u6574\u4e2a\u4eba\u8e09\u8dc4\u540e\u9000\uff0c\u540e\u80cc\u649e\u4e0a\u4e86\u73ab\u7470\u8272\u900f\u660e\u7403\u4f53\u7684\u5185\u58c1\uff0c\u65b9\u624d\u52c9\u5f3a\u7a33\u4f4f\u8eab\u5f62\u3002</p><p>\u6885\u83f2\u65af\u4e5f\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u968f\u5373\u518d\u5ea6\u8e0f\u6b65\u4e0a\u524d\uff0c\u94f6\u5251\u9ad8\u4e3e\uff0c\u51c6\u5907\u4e00\u51fb\u628a\u8fd9\u4e2a\u70bc\u9b54\u5e72\u6389\u3002\u70bc\u9b54\u80cc\u9760\u7740\u900f\u660e\u5899\u58c1\uff0c\u5598\u606f\u7740\uff0c\u4ed6\u5728\u6b64\u524d\u548c\u9ece\u660e\u4e4b\u77f3\u7684\u5bf9\u6297\u4e2d\u6d88\u8017\u6389\u592a\u591a\u7684\u529b\u91cf\uff0c\u5df2\u7ecf\u65e0\u6cd5\u548c\u6885\u83f2\u65af\u5bf9\u6297\u3002</p><p>\u4f3c\u4e4e\u53ea\u80fd\u95ed\u76ee\u7b49\u6b7b\u3002</p><p>\u4e00\u53ea\u624b\u7a81\u7136\u4ece\u865a\u7a7a\u4e2d\u4f38\u51fa\u6765\uff0c\u4e00\u76f4\u5750\u5728\u5899\u58c1\u8fb9\u7684\u6b27\u51ef\u4e0d\u77e5\u4f55\u65f6\u7ad9\u5728\u4e86\u796d\u575b\u8fb9\u6cbf\uff0c\u4ed6\u4f38\u51fa\u53f3\u81c2\uff0c\u6beb\u65e0\u969c\u788d\u5730\u7a7f\u8fc7\u4e86\u73ab\u7470\u8272\u7684\u900f\u660e\u7403\u4f53\uff0c\u6293\u4f4f\u70bc\u9b54\u7684\u540e\u9888\uff0c\u4e00\u628a\u5c06\u4ed6\u62c9\u626f\u51fa\u6765\u3002</p><p>\u6885\u83f2\u65af\u6014\u4e86\u4e00\u6014\uff0c\u62a2\u6b65\u4e0a\u524d\u6b63\u8981\u8ffd\u8d76\uff0c\u9661\u7136\u89c9\u5bdf\u5230\u4e00\u80a1\u5de8\u5927\u7684\u5a01\u538b\u4ece\u56db\u9762\u516b\u65b9\u671d\u81ea\u5df1\u6d8c\u6765\u3002\u5979\u60ca\u8bb6\u5730\u62ac\u5934\uff0c\u53d1\u89c9\u81ea\u5df1\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u88ab\u90a3\u73ab\u7470\u8272\u900f\u660e\u7403\u4f53\u7b3c\u7f69\u5f53\u4e2d\uff0c\u796d\u575b\u4e0a\u7684\u9ece\u660e\u4e4b\u77f3\u95ea\u70c1\u7740\uff0c\u4eff\u4f5b\u53d1\u51fa\u4fe1\u53f7\uff0c\u900f\u660e\u7403\u4f53\u5728\u75be\u901f\u671d\u6885\u83f2\u65af\u538b\u7f29\u4e0b\u6765\uff0c\u548c\u521a\u624d\u5bf9\u4ed8\u70bc\u9b54\u7684\u60c5\u5f62\u4e00\u6a21\u4e00\u6837\u3002</p><p>\u4f46\u2026\u2026\u4f46\u4e0d\u662f\u8bf4\u9ece\u660e\u4e4b\u77f3\u611f\u5e94\u5230\u90aa\u6076\u624d\u4f1a\u6fc0\u53d1\u529b\u91cf\u4e48\uff0c\u6ca1\u9053\u7406\u5bf9\u6885\u83f2\u65af\u4e5f\u8fd9\u6837\u554a\u2026\u2026</p><p>\u7136\u800c\u9ece\u660e\u4e4b\u77f3\u4f3c\u4e4e\u5e76\u4e0d\u6253\u7b97\u8bb2\u4ec0\u4e48\u9053\u7406\uff0c\u900f\u660e\u7403\u4f53\u75be\u901f\u538b\u7f29\uff0c\u73ab\u7470\u8272\u7684\u5185\u58c1\u4e0a\u751a\u81f3\u9690\u9690\u6d6e\u73b0\u51fa\u65e0\u6570\u7ec6\u5c0f\u7684\u7b26\u5492\u548c\u94f6\u767d\u8272\u96f7\u5149\u95ea\u7535\u6765\uff0c\u8fd9\u662f\u521a\u624d\u5bf9\u4ed8\u70bc\u9b54\u90fd\u4e0d\u66fe\u6709\u7684\u5f85\u9047\u3002\u6885\u83f2\u65af\u663e\u7136\u4e5f\u610f\u8bc6\u5230\u4e86\u5371\u9669\uff0c\u5979\u60f3\u8981\u51b2\u51fa\u53bb\uff0c\u5374\u9aa4\u7136\u611f\u89c9\u81ea\u5df1\u7684\u8eab\u4f53\u4e0d\u53d7\u63a7\u5236\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002</p><p>\u518d\u4e00\u6b21\uff0c\u9ed1\u8272\u7684\u5de8\u5927\u865a\u50cf\u4ece\u5979\u8eab\u4f53\u4e0a\u6d6e\u73b0\u51fa\u6765\uff0c\u4f9d\u65e7\u662f\u90a3\u4e2a\u72f0\u72de\u7684\u602a\u517d\u6a21\u6837\uff0c\u5468\u8eab\u76ae\u80a4\u706b\u7ea2\uff0c\u5934\u9876\u957f\u7740\u5e9e\u5927\u7684\u5f2f\u66f2\u7284\u89d2\uff0c\u5de8\u773c\u9614\u5634\uff0c\u7360\u7259\u68ee\u68ee\uff0c\u5f13\u5c48\u7684\u80cc\u4e0a\u4e24\u6392\u5de8\u5927\u7684\u9ed1\u8272\u9aa8\u523a\uff0c\u53d1\u7740\u68ee\u68ee\u5bd2\u5149\u3002</p><p>\u73ab\u7470\u8272\u900f\u660e\u7403\u4f53\u538b\u7f29\u4e0b\u6765\uff0c\u602a\u517d\u778b\u76ee\u6012\u543c\u7740\uff0c\u80cc\u4e0a\u7684\u9aa8\u523a\u6839\u6839\u8038\u8d77\uff0c\u5468\u8eab\u70c8\u7130\u817e\u817e\uff0c\u6b63\u4e0e\u4e4b\u76f8\u6297\u3002\u4eff\u4f5b\u65f6\u5149\u5012\u6d41\u4e00\u822c\uff0c\u743c\u6069\u8bb0\u5f97\u4eca\u5929\u4e0b\u5348\u7684\u65f6\u5019\uff0c\u7b2c\u4e00\u6b21\u89c1\u5230\u8fd9\u4e2a\u602a\u517d\u865a\u50cf\uff0c\u5b83\u4e5f\u662f\u6b63\u88ab\u4e00\u4e2a\u94f6\u767d\u8272\u7684\u900f\u660e\u7f69\u5c01\u9501\u7981\u9522\u7740\uff0c\u6700\u540e\u7ec8\u4e8e\u6253\u7834\uff0c\u5374\u53c8\u88ab\u63d0\u5c14\u4e00\u5251\u5288\u9000\u3002\u5982\u4eca\u552f\u4e00\u7684\u6539\u53d8\uff0c\u53ea\u662f\u94f6\u767d\u8272\u900f\u660e\u7f69\u6362\u6210\u4e86\u73ab\u7470\u8272\u3002</p><p>\u6ee1\u8db3\u7684\u53f9\u606f\u58f0\u4ece\u65c1\u8fb9\u4f20\u6765\uff0c\u743c\u6069\u8f6c\u5934\u770b\u53bb\uff0c\u53d1\u73b0\u6b27\u51ef\u4e0d\u77e5\u4f55\u65f6\u53c8\u5df2\u7ecf\u5750\u5230\u8eab\u8fb9\uff0c\u88ab\u4ed6\u626f\u51fa\u6765\u7684\u90a3\u4e2a\u70bc\u9b54\u5374\u4e0d\u89c1\u8e2a\u5f71\u4e86\u3002\u201c\u603b\u7b97\u641e\u5b9a\u4e86\uff0c\u201d\u4ed6\u4e0d\u65e0\u5f97\u610f\u5730\u8bf4\uff0c\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u5bf9\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u7f16\u4e2a\u5267\u672c\u771f\u4e0d\u5bb9\u6613\uff0c\u5bf9\u4e0d\u5bf9\u3002\u201d</p><p>\u743c\u6069\u95ed\u7740\u5634\u3002</p><p>\u6b27\u51ef\u563f\u563f\u7b11\u7740\uff0c\u770b\u7740\u796d\u575b\u4e0a\u5de8\u5927\u7684\u602a\u517d\u865a\u5f71\u6b63\u5168\u529b\u548c\u73ab\u7470\u8272\u7403\u4f53\u5bf9\u6297\uff0c\u201c\u522b\u62c5\u5fc3\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4eec\u4ee5\u540e\u8fd8\u6709\u5f88\u591a\u5408\u4f5c\u7684\u673a\u4f1a\u3002\u800c\u4e14\u4f60\u770b\u5230\u4e86\uff0c\u6211\u662f\u4e2a\u975e\u5e38\u8bda\u5b9e\u800c\u4e14\u5584\u826f\u7684\u4eba\uff0c\u201d\u4ed6\u9732\u51fa\u6d01\u767d\u5f97\u4eff\u4f5b\u72fc\u7259\u4e00\u822c\u7684\u7259\u9f7f\uff0c\u51b2\u7740\u743c\u6069\u5fae\u7b11\uff0c\u201c\u5bf9\u4e0d\u5bf9\u3002\u201d</p><p>\u743c\u6069\u4f9d\u65e7\u95ed\u7740\u5634\uff0c\u867d\u7136\u4ed6\u53d1\u73b0\u81ea\u5df1\u7a81\u7136\u5df2\u7ecf\u53ef\u4ee5\u8bf4\u8bdd\u4e86\uff0c\u4f46\u8eab\u4f53\u4f9d\u65e7\u9178\u9ebb\u65e0\u529b\uff0c\u5b8c\u5168\u6ca1\u6cd5\u52a8\u5f39\u3002</p><p>\u89c1\u743c\u6069\u4e00\u76f4\u4e0d\u80af\u8bf4\u8bdd\uff0c\u6b27\u51ef\u4f3c\u4e4e\u4e5f\u89c9\u5f97\u6709\u4e9b\u65e0\u8da3\uff0c\u4ed6\u626d\u8fc7\u5934\u53bb\uff0c\u5174\u81f4\u52c3\u52c3\u5730\u770b\u7740\u796d\u575b\u3002\u602a\u517d\u548c\u9ece\u660e\u4e4b\u77f3\u7684\u6218\u6597\u4f3c\u4e4e\u5df2\u7ecf\u8fdb\u5165\u6700\u540e\u7684\u767d\u70ed\u5316\u9636\u6bb5\uff0c\u73ab\u7470\u8272\u7403\u4f53\u5185\u58c1\u4e0a\uff0c\u5df2\u7ecf\u5bc6\u5bc6\u531d\u531d\u904d\u5e03\u7740\u5404\u79cd\u7b26\u6587\uff0c\u5f3a\u70c8\u7684\u795e\u5723\u6c14\u606f\u5c31\u8fde\u8fdc\u5904\u7684\u743c\u6069\u90fd\u80fd\u6e05\u695a\u611f\u89c9\u5230\uff0c\u602a\u517d\u8eab\u4e0a\u5219\u6d8c\u73b0\u51fa\u65e0\u7a77\u65e0\u5c3d\u7684\u98ce\u706b\u96f7\u7535\u6765\uff0c\u4f46\u90fd\u88ab\u73ab\u7470\u8272\u7403\u4f53\u7ed9\u538b\u5236\u4f4f\u4e86\uff0c\u534a\u70b9\u65e0\u6cd5\u5916\u6cc4\u3002</p><p>\u4f1a\u4e24\u8d25\u4ff1\u4f24\u540c\u5f52\u4e8e\u5c3d\u4e48\uff1f</p><p>\u6e05\u8106\u7684\u7834\u88c2\u58f0\u9aa4\u7136\u5728\u796d\u575b\u4e0a\u54cd\u8d77\uff0c\u743c\u6069\u5faa\u58f0\u671b\u53bb\uff0c\u60ca\u8bb6\u5730\u53d1\u73b0\u90a3\u9897\u9ece\u660e\u4e4b\u77f3\u4e0a\u5df2\u7ecf\u51fa\u73b0\u4e86\u65e0\u6570\u9053\u7ec6\u5c0f\u7684\u88c2\u7eb9\uff0c\u800c\u4e14\u6b63\u5728\u6025\u901f\u5ef6\u4f38\u6269\u5927\uff0c\u8f6c\u77ac\u4e4b\u95f4\u5df2\u7ecf\u904d\u5e03\u6574\u4e2a\u8868\u9762\uff0c\u968f\u5373\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u76fe\u724c\u5927\u5c0f\u7684\u900f\u660e\u77f3\u5934\u788e\u6210\u65e0\u6570\u6676\u7247\uff0c\u6d12\u843d\u4e00\u5730\u3002</p><p>\u8fd9\u2026\u2026\u8fd9\u795e\u5668\u5c31\u8fd9\u4e48\u6bc1\u4e86\uff1f</p><p>\u867d\u8bf4\u65e9\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u4f46\u5f53\u771f\u53d1\u751f\u7684\u65f6\u5019\uff0c\u743c\u6069\u8fd8\u662f\u4e0d\u514d\u6709\u4e9b\u6115\u7136\u60ca\u8be7\uff0c\u56e0\u4e3a\u8fd9\u4f3c\u4e4e\u4e5f\u6765\u5f97\u592a\u8f7b\u6613\u4e86\u4e9b\u3002\u4ed6\u614c\u5fd9\u62ac\u5934\uff0c\u770b\u5411\u6885\u83f2\u65af\uff0c\u53d1\u73b0\u5c11\u5973\u4f9d\u65e7\u4e00\u52a8\u4e0d\u52a8\u5730\u50f5\u7acb\u7740\uff0c\u73ab\u7470\u8272\u900f\u660e\u7403\u4f53\u6b63\u4ee5\u6781\u5feb\u7684\u901f\u5ea6\u731b\u70c8\u538b\u7f29\u4e0b\u6765\uff0c\u5c06\u602a\u517d\u7684\u865a\u5f71\u6b7b\u6b7b\u7b3c\u7f69\u4f4f\u3002</p><p>\u602a\u517d\u6012\u543c\u7740\uff0c\u6b7b\u6b7b\u6323\u624e\uff0c\u6b63\u5728\u50f5\u6301\u4e0d\u4e0b\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u624b\u4e2d\u7684\u94f6\u5251\u9aa4\u7136\u81ea\u52a8\u5f39\u8df3\u8d77\u6765\uff0c\u8dc3\u5728\u7a7a\u4e2d\uff0c\u5723\u5149\u707f\u70c2\u56db\u5c04\uff0c\u4e00\u5251\u5f53\u5934\u671d\u602a\u517d\u5288\u4e0b\u3002\u602a\u517d\u5389\u5578\u4e00\u58f0\uff0c\u8f70\u7136\u6563\u53bb\uff0c\u73ab\u7470\u8272\u900f\u660e\u7403\u4f53\u4e5f\u7d27\u8ddf\u7740\u649e\u5165\u6885\u83f2\u65af\u4f53\u5185\uff0c\u77ac\u95f4\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u5f53\u5577\u4e00\u58f0\uff0c\u94f6\u5251\u6389\u843d\u5728\u5730\u3002</p><p>\u4e00\u76f4\u7ad9\u7acb\u4e0d\u52a8\u7684\u6885\u83f2\u65af\u9aa4\u7136\u6643\u4e86\u6643\uff0c\u8eab\u5f62\u4e0d\u7a33\uff0c\u8f6f\u8f6f\u5012\u5730\u3002\u6709\u8fc7\u4eca\u5929\u4e0b\u5348\u7684\u7ecf\u5386\uff0c\u743c\u6069\u4e5f\u5e76\u4e0d\u614c\u5f20\uff0c\u5fc3\u4e2d\u9690\u9690\u6709\u4e9b\u660e\u767d\uff0c\u4ed6\u4f11\u606f\u8fd9\u4e48\u4e45\uff0c\u611f\u89c9\u529b\u6c14\u4e5f\u6e10\u6e10\u6062\u590d\u51e0\u5206\uff0c\u4ed6\u7f13\u7f13\u505a\u4e86\u4e24\u4e2a\u6df1\u547c\u5438\uff0c\u6b63\u8981\u7ad9\u8d77\u8eab\uff0c\u5c31\u89c9\u65c1\u8fb9\u6b27\u51ef\u62cd\u4e86\u62cd\u81ea\u5df1\u7684\u80a9\u8180\u3002</p><p>\u201c\u611f\u8c22\u6211\u5427\uff0c\u743c\u6069\u3002\u201d\u6b27\u51ef\u5b09\u76ae\u7b11\u8138\u5730\u8bf4\u3002</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u6b27\u51ef\u6307\u4e86\u6307\u796d\u575b\u4e0a\u7684\u6885\u83f2\u65af\uff0c\u201c\u8fc7\u4e86\u8fd9\u6b21\uff0c\u5979\u518d\u4e5f\u4e0d\u4f1a\u7a81\u7136\u53d1\u75af\u4e86\u554a\u3002\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u201c\u5176\u4ed6\u90a3\u4e9b\u5854\u6d1b\u5a1c\u7684\u4fe1\u5f92\u5462\u3002\u201d\u4ed6\u95ee\uff0c\u867d\u7136\u5df2\u7ecf\u9690\u7ea6\u731c\u5230\u4e86\u7b54\u6848\u3002</p><p>\u201c\u90fd\u6b7b\u4e86\u3002\u201d\u6b27\u51ef\u56de\u7b54\u3002</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8d70\u4e0a\u796d\u575b\u6361\u8d77\u5730\u4e0a\u7684\u94f6\u5251\uff0c\u4e22\u8fdb\u6b21\u5143\u888b\uff0c\u5c06\u6885\u83f2\u65af\u62b1\u8d77\u3002\u65e2\u7136\u5854\u6d1b\u5a1c\u7684\u4fe1\u5f92\u90fd\u5df2\u7ecf\u6b7b\u5149\uff0c\u62c9\u6c83\u514b\u4ea4\u4ee3\u7684\u4efb\u52a1\u5c31\u5df2\u7ecf\u5b8c\u6210\uff1b\u9ece\u660e\u4e4b\u77f3\u4e5f\u5df2\u7ecf\u788e\u88c2\uff0c\u5269\u4e0b\u7684\uff0c\u4f3c\u4e4e\u4e5f\u6ca1\u4ec0\u4e48\u4e8b\u60c5\u53ef\u505a\u4e86\u3002</p><p>\u5f53\u4ed6\u8f6c\u8fc7\u8eab\u6765\u7684\u65f6\u5019\uff0c\u770b\u89c1\u6b27\u51ef\u4f38\u624b\u5728\u7a7a\u6c14\u4e2d\u62c9\u5f00\u4e00\u6247\u4f20\u9001\u95e8\uff0c\u201c\u8bf7\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u62c9\u6c83\u514b\u5148\u751f\u6b63\u5728\u7b49\u4f60\u4eec\u7684\u6d88\u606f\u5462\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u8c22\u8c22\u3002\u201d\u62b1\u8d77\u6885\u83f2\u65af\u8d70\u8fdb\u4f20\u9001\u95e8\u3002</p><p>\u203b\u203b\u203b</p><p>\u6b27\u51ef\u9759\u9759\u5730\u7ad9\u7acb\u7740\uff0c\u76f4\u5230\u5468\u56f4\u4e00\u5207\u90fd\u6c89\u5bc2\u4e0b\u6765\uff0c\u4ed6\u7f13\u7f13\u8d70\u5230\u796d\u575b\u524d\uff0c\u6253\u4e86\u4e2a\u54cd\u6307\u3002\u539f\u672c\u5df2\u7ecf\u6d88\u5931\u7684\u4e5d\u8292\u661f\u9b54\u6cd5\u9635\u518d\u5ea6\u51fa\u73b0\uff0c\u4e5d\u4e2a\u661f\u4f4d\u4e0a\u817e\u8d77\u84dd\u7eff\u8272\u7684\u706b\u7130\u6765\uff0c\u4eff\u4f5b\u86c7\u4e00\u822c\u6e38\u52a8\u7740\u3002\u7d27\u63a5\u7740\uff0c\u5728\u9b54\u6cd5\u9635\u7684\u6b63\u4e2d\uff0c\u7f13\u7f13\u6d6e\u51fa\u4e00\u53ea\u773c\u775b\u6765\u3002</p><p>\u90a3\u662f\u4e00\u53ea\u86c7\u7684\u773c\u775b\uff0c\u5c16\u7ec6\u3001\u72ed\u957f\u3001\u900f\u7740\u51db\u51db\u7684\u5bd2\u5149\uff0c\u4f46\u5e76\u4e0d\u8feb\u4eba\u3002\u5728\u86c7\u773c\u7684\u80cc\u540e\uff0c\u604d\u604d\u60da\u60da\u4eff\u4f5b\u6709\u4e00\u4e2a\u5de8\u5927\u7684\u4eba\u5f71\uff0c\u5934\u751f\u53cc\u89d2\uff0c\u624b\u6301\u4e00\u6839\u6743\u6756\uff0c\u9876\u7aef\u4f3c\u4e4e\u9576\u7740\u9897\u7ea2\u8272\u7684\u5b9d\u77f3\uff0c\u770b\u4e0d\u6e05\u695a\u3002</p><p>\u6b27\u51ef\u5fae\u5fae\u8eac\u8eab\u884c\u793c\uff0c\u201c\u965b\u4e0b\u3002\u201d</p><p>\u86c7\u773c\u7f13\u7f13\u7741\u5f00\uff0c\u770b\u7740\u6b27\u51ef\uff0c\u201c\u4f60\u6539\u53d8\u4e86\u9884\u5b9a\u7684\u8ba1\u5212\uff0c\u201d\u4e00\u4e2a\u6e29\u548c\u4e2d\u5e26\u7740\u4e9b\u8bb8\u51b7\u6f20\u7684\u58f0\u97f3\u4ece\u9b54\u6cd5\u9635\u4e2d\u53d1\u51fa\uff0c\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u96be\u5f97\u5c06\u4e00\u4e2a\u6df1\u72f1\u70bc\u9b54\u9001\u5230\u7269\u8d28\u754c\uff0c\u5c31\u8fd9\u6837\u727a\u7272\u672a\u514d\u592a\u53ef\u60dc\uff0c\u201d\u6b27\u51ef\u5fae\u7b11\u7740\uff0c\u201c\u6240\u4ee5\u6211\u81ea\u4f5c\u4e3b\u5f20\uff0c\u5c06\u4ed6\u6551\u4e86\u4e0b\u6765\u3002\u6211\u60f3\uff0c\u81f3\u5c11\u4ed6\u53ef\u4ee5\u5e2e\u5e2e\u5fd9\uff0c\u965b\u4e0b\u3002\u201d</p><p>\u86c7\u773c\u4e2d\u5bd2\u5149\u5fae\u5fae\u95ea\u70c1\u7740\uff0c\u201c\u4f60\u77e5\u9053\u6211\u5e76\u4e0d\u5173\u5fc3\u8fd9\u4e2a\u3002\u201d</p><p>\u6b27\u51ef\u6c89\u9ed8\u7740\uff0c\u5927\u5385\u4e2d\u4e00\u65f6\u9677\u5165\u6c89\u5bc2\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u518d\u5ea6\u7f13\u7f13\u5f00\u53e3\uff0c\u201c\u9ece\u660e\u4e4b\u77f3\u88ab\u6467\u6bc1\uff0c\u60a8\u548c\u591c\u5973\u58eb\u7684\u534f\u8bae\u4e5f\u5df2\u7ecf\u987a\u5229\u5b8c\u6210\u3002\u6211\u60f3\uff0c\u4e00\u4e9b\u7ec6\u8282\u4e0a\u7684\u6539\u53d8\uff0c\u65e0\u5173\u5927\u5c40\u3002\u201d</p><p>\u86c7\u773c\u76ef\u7740\u6b27\u51ef\uff0c\u6df1\u78a7\u8272\u7684\u773c\u73e0\u5728\u7f13\u7f13\u8f6c\u52a8\u7740\uff0c\u201c\u786e\u5b9e\u65e0\u5173\u7d27\u8981\uff0c\u201d\u5b83\u8bf4\uff0c\u201c\u53ea\u4e0d\u8fc7\u6211\u6709\u70b9\u4e0d\u592a\u660e\u767d\uff0c\u4f60\u4e3a\u4f55\u8fd9\u6837\u770b\u91cd\u90a3\u4e2a\u5deb\u5e08\u3002\u201d</p><p>\u201c\u4ed6\u6709\u4e9b\u806a\u660e\uff0c\u4e5f\u8fd8\u7b97\u4e0d\u4e0a\u771f\u6b63\u7684\u667a\u6167\uff1b\u4ed6\u6709\u70b9\u4f01\u671b\uff0c\u4e5f\u8fd8\u8c08\u4e0d\u4e0a\u771f\u6b63\u7684\u96c4\u5fc3\uff1b\u4ed6\u6709\u70b9\u672c\u4e8b\uff0c\u5374\u4e5f\u8bf4\u4e0d\u4e0a\u591a\u4e48\u4e86\u4e0d\u8d77\u3002\u90a3\u4e48\uff0c\u6b27\u51ef\uff0c\u544a\u8bc9\u6211\u4f60\u5230\u5e95\u770b\u91cd\u4ed6\u54ea\u4e00\u70b9\u5462\uff1f\u201d</p><p>\u6b27\u51ef\u6447\u5934\uff0c\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6b63\u5982\u60a8\u6240\u8bf4\uff0c\u965b\u4e0b\u3002\u6211\u4e5f\u6ca1\u770b\u51fa\u4ed6\u6709\u4ec0\u4e48\u503c\u5f97\u770b\u91cd\u7684\u2026\u2026\u4f46\u662f\u6211\u60f3\uff0c\u80fd\u8ba9\u590f\u591a\u548c\u62c9\u6c83\u514b\u5982\u6b64\u770b\u91cd\u7684\u4eba\uff0c\u603b\u8be5\u662f\u6709\u4e9b\u7279\u522b\u4e4b\u5904\u7684\u3002\u201d</p><p>\u86c7\u773c\u772f\u7f1d\u8d77\u6765\uff0c\u5f62\u6210\u4e00\u6761\u72ed\u957f\u7684\u51b0\u7ebf\uff0c\u201c\u4f60\u8ba4\u4e3a\u4ed6\u4eec\u7684\u773c\u5149\u4f1a\u6bd4\u6211\u4eec\u66f4\u52a0\u51c6\u786e\uff1f\u201d</p><p>\u201c\u5728\u5bf9\u51e1\u4eba\u7684\u8bc4\u4ef7\u4e0a\uff0c\u6211\u8ba4\u4e3a\u662f\u5982\u6b64\uff0c\u201d\u6b27\u51ef\u8bda\u5b9e\u5730\u8bf4\uff0c\u201c\u552f\u6709\u51e1\u4eba\u624d\u80fd\u4e86\u89e3\u51e1\u4eba\u3002\u201d</p><p>\u86c7\u773c\u6c89\u9ed8\u7740\u3002</p><p>\u201c\u60a8\u4e5f\u7f62\uff0c\u6211\u4e5f\u7f62\uff0c\u6216\u8005\u90a3\u4e9b\u795e\u7947\u4e5f\u7f62\uff0c\u76f8\u5bf9\u4e8e\u51e1\u4eba\uff0c\u4ece\u672c\u8d28\u4e0a\u6765\u8bf4\u5e76\u6ca1\u4ec0\u4e48\u533a\u522b\u3002\u6211\u4eec\u6216\u8005\u662f\u4fe1\u5ff5\uff0c\u6216\u8005\u662f\u6000\u7591\uff0c\u6216\u8005\u662f\u7406\u667a\uff0c\u6216\u8005\u662f\u611f\u6027\uff0c\u6211\u4eec\u662f\u51e1\u4eba\u7075\u9b42\u7684\u63d0\u70bc\u3001\u8403\u53d6\u3001\u878d\u5408\u3001\u5347\u534e\uff0c\u6211\u4eec\u7eaf\u7cb9\u800c\u51dd\u7ec3\uff0c\u6240\u4ee5\u6211\u4eec\u8fdc\u8fdc\u8d85\u8d8a\u4e8e\u51e1\u4eba\u4e4b\u4e0a\uff0c\u4f46\u4e5f\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u6211\u4eec\u65e0\u6cd5\u771f\u6b63\u5730\u7406\u89e3\u51e1\u4eba\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u51e1\u4eba\u5e76\u4e0d\u7eaf\u7cb9\u3002\u201d\u86c7\u773c\u6de1\u6de1\u5730\u8bf4\u3002</p><p>\u201c\u6b63\u5982\u60a8\u6240\u8a00\uff0c\u965b\u4e0b\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u4ed6\u76b1\u7740\u7709\u5934\uff0c\u6572\u6253\u7740\u81ea\u5df1\u7684\u8111\u888b\uff0c\u201c\u56e0\u4e3a\u51e1\u4eba\u5e76\u4e0d\u7eaf\u7cb9\uff0c\u4ed6\u4eec\u662f\u6742\u7cc5\u6df7\u5408\u7684\u3002\u4ed6\u4eec\u517c\u6709\u4fe1\u5ff5\u548c\u6000\u7591\uff0c\u4ed6\u4eec\u517c\u6709\u7406\u667a\u4e0e\u611f\u6027\uff0c\u4ed6\u4eec\u65e2\u5584\u826f\u4e5f\u90aa\u6076\uff0c\u65e2\u806a\u660e\u4e5f\u611a\u8822\uff0c\u65e2\u5b88\u5e8f\u4e5f\u53db\u9006\uff0c\u4ed6\u4eec\u2026\u2026\u4e0d\u662f\u6211\u4eec\u6240\u53ef\u4ee5\u7406\u89e3\u7684\uff0c\u965b\u4e0b\u3002\u201d</p><p>\u86c7\u773c\u8f7b\u8f7b\u95ed\u4e0a\uff0c\u773c\u73e0\u4e2d\u900f\u51fa\u7684\u5bd2\u5149\u6d88\u5931\u4e86\uff0c\u9b54\u6cd5\u9635\u6e10\u6e10\u6de1\u53bb\uff0c\u773c\u770b\u5c31\u8981\u5b8c\u5168\u6d88\u5931\u3002\u201c\u8bb0\u4f4f\u4f60\u7684\u804c\u8d23\uff0c\u201d\u86c7\u773c\u6700\u540e\u4e0d\u7d27\u4e0d\u6162\u5730\u8bf4\uff0c\u201c\u8bb0\u4f4f\u4f60\u5bf9\u6211\u7684\u627f\u8bfa\u3002\u201d</p><p>\u201c\u6211\u4f1a\u7262\u8bb0\u6211\u4eec\u7684\u534f\u8bae\uff0c\u965b\u4e0b\u3002\u201d\u6b27\u51ef\u4e0d\u5351\u4e0d\u4ea2\u5730\u56de\u7b54\u3002</p><p>\u201c\u534f\u8bae\u2026\u2026\u201d</p><p>\u86c7\u773c\u91cd\u590d\u7740\u8fd9\u4e2a\u8bcd\uff0c\u4f46\u5b83\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff0c\u968f\u7740\u9b54\u6cd5\u9635\u7684\u5b8c\u5168\u6d88\u6563\uff0c\u86c7\u773c\u548c\u5b83\u80cc\u540e\u7684\u4eba\u5f62\u865a\u50cf\u4e5f\u5feb\u901f\u6d88\u5931\u3002\u5468\u906d\u53c8\u6062\u590d\u4e86\u5e73\u9759\uff0c\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u6b7b\u4e00\u822c\u7684\u6c89\u5bc2\uff0c\u6b27\u51ef\u4f4e\u5934\u770b\u7740\u796d\u575b\uff0c\u9ece\u660e\u4e4b\u77f3\u788e\u88c2\u6210\u7684\u5343\u4e07\u5757\u6676\u7247\u6b63\u50cf\u592a\u9633\u4e0b\u7684\u51b0\u5757\u822c\u7f13\u6162\u878d\u5316\uff0c\u6e10\u6e10\u6d88\u5931\u3002\u8389\u62c9\u516c\u7235\u7684\u5c38\u4f53\u5df2\u7ecf\u53d8\u5f97\u50f5\u786c\uff0c\u9c9c\u8840\u4ece\u5979\u8eab\u4e0a\u7684\u4f24\u53e3\u6d41\u6dcc\u51fa\u6765\uff0c\u6d78\u900f\u4e86\u8eab\u4e0b\u7684\u571f\u5730\u3002</p><p>\u201c\u90a3\u4e48\u2026\u2026\u6682\u65f6\u7b97\u662f\u7ed3\u675f\u4e86\u5427\u3002\u201d</p><p>\u6b27\u51ef\u53f9\u606f\u7740\uff0c\u4f38\u624b\u8638\u4e86\u70b9\u5c1a\u672a\u5e72\u6db8\u7684\u9c9c\u8840\uff0c\u6ef4\u5728\u5730\u4e0a\u3002\u9ed1\u8272\u7684\u718a\u718a\u70c8\u7130\u8f70\u7136\u817e\u8d77\uff0c\u706b\u820c\u7ffb\u5377\uff0c\u5239\u90a3\u95f4\u5e2d\u5377\u6574\u4e2a\u5927\u5385\uff0c\u5c06\u89c6\u91ce\u6240\u53ca\u4e4b\u5185\u53d8\u6210\u4e00\u7247\u707c\u70ed\u706b\u6d77\u3002\u6b27\u51ef\u6309\u4e86\u6309\u5934\u4e0a\u7684\u5bbd\u6cbf\u5e3d\uff0c\u7f13\u6b65\u8d70\u5165\u706b\u6d77\u4e4b\u4e2d\uff0c\u4ed6\u7684\u8eab\u5f71\u77ac\u95f4\u88ab\u70c8\u7130\u541e\u6ca1\uff0c\u6d88\u5931\u4e0d\u89c1\u3002</p></article>"}}}]);
