(window.webpackJsonp=window.webpackJsonp||[]).push([[1553],{1576:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u8282 \u9ed1\u73ab\u7470",content:"<article><h2>\u7b2c\u4e8c\u8282 \u9ed1\u73ab\u7470</h2><p>\u5f53\u743c\u6069\u542c\u5230\u6709\u4e24\u4e2a\u6b7b\u4ea1\u9a91\u58eb\u3001\u56db\u4e2a\u89d2\u9b54\u7684\u65f6\u5019\uff0c\u4ed6\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u4ee5\u81f3\u4e8e\u540e\u9762\u90a3\u53ea\u6b32\u9b54\u90fd\u5ffd\u7565\u4e86\uff0c\u4f46\u5f53\u4ed6\u542c\u5230\u5938\u8d5b\u9b54\u540e\u9762\u7684\u8865\u5145\u65f6\uff0c\u7acb\u523b\u4f38\u624b\u628a\u5c06\u5b83\u4ece\u7a7a\u4e2d\u6293\u8fc7\u6765\uff0c\u91cd\u91cd\u5f80\u767d\u9aa8\u7075\u8f66\u4e0a\u4e00\u6454\u3002</p><p>\u201c\u4e0b\u6b21\u76f4\u63a5\u5148\u8bf4\u91cd\u70b9\uff01\u201d</p><p>\u5938\u8d5b\u9b54\u88ab\u6454\u5f97\u6655\u5934\u8f6c\u5411\uff0c\u5374\u53c8\u4e0d\u6562\u53cd\u9a73\uff0c\u5176\u5b9e\u5b83\u5148\u8bf4\u51fa\u654c\u4eba\u7684\u79cd\u7c7b\u6784\u6210\uff0c\u5e76\u4e0d\u7b97\u662f\u8f7b\u91cd\u4e0d\u5206\uff0c\u4e3b\u8981\u5728\u4e8e\u6ca1\u6709\u4e00\u53e3\u6c14\u8bf4\u5b8c\u3002\u4f46\u5b83\u80fd\u8df3\u8d77\u6765\u6297\u8bae\u4e48\uff1f\u663e\u7136\u4e0d\u80fd\u3002</p><p>\u6076\u9b54\u6ca1\u6709\u4ec0\u4e48\u51c6\u5219\uff0c\u5982\u679c\u52c9\u5f3a\u8bf4\u6709\u7684\u8bdd\uff0c\u90a3\u5c31\u662f\u201c\u5f3a\u6743\u5373\u516c\u7406\u201d\u3002\u8c01\u5f3a\u8c01\u5c31\u6b63\u786e\uff0c\u743c\u6069\u6bd4\u5938\u8d5b\u9b54\u5f3a\uff0c\u6240\u4ee5\u4ed6\u5c31\u6b63\u786e\uff0c\u4ec5\u6b64\u800c\u5df2\u3002</p><p>\u743c\u6069\u867d\u7136\u53d1\u706b\uff0c\u5374\u4e5f\u5b89\u4e0b\u5fc3\u6765\uff0c\u521a\u5f00\u59cb\u4ed6\u786e\u5b9e\u5413\u4e86\u4e00\u8df3\u3002\u6b7b\u4ea1\u9a91\u58eb\u5012\u7f62\u4e86\uff0c\u80af\u5b9a\u662f\u81ea\u5df1\u4eba\uff0c\u4ed6\u624b\u91cc\u6709\u6b4c\u560e\u65af\u4e13\u95e8\u9881\u53d1\u7684\u4ee4\u7b26\uff0c\u4e0d\u7528\u62c5\u5fc3\u88ab\u4ea1\u7075\u653b\u51fb\uff0c\u4f46\u89d2\u9b54\u53ef\u5c31\u4ee4\u4eba\u5934\u75bc\u4e86\uff0c\u8fd9\u662f\u5728\u9b54\u9b3c\u4e2d\u5730\u4f4d\u4ec5\u6b21\u4e8e\u6df1\u72f1\u70bc\u9b54\u7684\u5b58\u5728\uff0c\u5b9e\u529b\u975e\u51e1\uff0c\u5dee\u4e0d\u591a\u76f8\u5f53\u4e8e\u516d\u81c2\u86c7\u9b54\u7684\u7a0b\u5ea6\u3002\u743c\u6069\u8fd9\u51e0\u4e2a\u6708\u6765\u6740\u9b54\u9b3c\u6740\u5230\u624b\u8f6f\uff0c\u4f46\u8fd8\u771f\u6ca1\u548c\u89d2\u9b54\u6253\u8fc7\u4ea4\u9053\u2014\u2014\u6682\u65f6\u4e5f\u4e0d\u60f3\u6253\u4ea4\u9053\u3002\u8fd9\u91cc\u53c8\u4e0d\u662f\u524d\u7ebf\uff0c\u800c\u662f\u9065\u8fdc\u7684\u540e\u65b9\uff0c\u8ddd\u79bb\u6076\u9b54\u519b\u961f\u7684\u65b0\u636e\u70b9\u9ed1\u77f3\u57ce\u5821\u6b65\u884c\u4e5f\u4e0d\u5230\u4e00\u5c0f\u65f6\u7684\u8def\u7a0b\uff0c\u5982\u679c\u8ba9\u838e\u73de\u514b\u5c55\u5f00\u8760\u7ffc\u98de\u7684\u8bdd\u6700\u591a\u5341\u5206\u949f\uff0c\u65e0\u8bba\u600e\u4e48\u7b97\uff0c\u4e5f\u6ca1\u9053\u7406\u4f1a\u649e\u4e0a\u89d2\u9b54\u8fd9\u79cd\u5f3a\u5927\u7684\u9b54\u9b3c\u5427\uff0c\u800c\u4e14\u8fd8\u4e00\u6765\u5c31\u662f\u56db\u53ea\u3002\u81f3\u4e8e\u6700\u540e\u90a3\u4e2a\u4ec0\u4e48\u2026\u2026\u5443\uff0c\u6b32\u9b54\uff0c\u5012\u662f\u4e0d\u7528\u4ecb\u610f\u3002</p><p>\u4e0d\u8fc7\u65e2\u7136\u90fd\u5df2\u7ecf\u6b7b\u4e86\uff0c\u90a3\u5c31\u65e0\u6240\u8c13\u4e86\u3002</p><p>\u838e\u73de\u514b\u632f\u7ffc\u98de\u8d77\uff0c\u524d\u884c\u67e5\u770b\uff0c\u743c\u6069\u4ece\u6597\u7bf7\u5185\u4fa7\u53d6\u51fa\u4e00\u4e2a\u638c\u5fc3\u5927\u5c0f\u7684\u9ed1\u8272\u5934\u9aa8\u5fbd\u7ae0\u6765\uff0c\u671d\u516b\u53ea\u9ab7\u9ac5\u6b66\u58eb\u6643\u4e86\u4e00\u6643\uff0c\u547d\u4ee4\u5176\u4e2d\u56db\u4e2a\u63a8\u8f66\uff0c\u53e6\u5916\u56db\u4e2a\u63d0\u9ad8\u6212\u5907\uff0c\u51c6\u5907\u6218\u6597\u3002\u9ab7\u9ac5\u6b66\u58eb\u548c\u5bfb\u5e38\u7684\u9ab7\u9ac5\u4e0d\u540c\uff0c\u751f\u524d\u5f80\u5f80\u90fd\u662f\u4e0d\u9519\u7684\u6218\u58eb\uff0c\u53d8\u6210\u4ea1\u7075\u540e\u8fd8\u6b8b\u7559\u4e86\u4e00\u4e9b\u57fa\u672c\u7684\u6b66\u6280\u683c\u6597\u80fd\u529b\uff0c\u914d\u4e0a\u7cbe\u826f\u7684\u6b66\u5668\u94e0\u7532\uff0c\u8db3\u4ee5\u548c\u94fe\u9b54\u3001\u9aa8\u9b54\u8fd9\u79cd\u4e2d\u9636\u9b54\u9b3c\u52c9\u5f3a\u6297\u8861\u4e86\uff0c\u5426\u5219\u743c\u6069\u4e5f\u4e0d\u4f1a\u5e26\u51fa\u6765\u3002</p><p>\u8d70\u4e86\u5341\u5206\u949f\uff0c\u679c\u7136\u770b\u5230\u4e03\u516b\u5177\u5c38\u4f53\uff0c\u5176\u4e2d\u4e24\u4e2a\u6700\u5bb9\u6613\u8fa8\u8ba4\uff0c\u5168\u8eab\u91cd\u7532\u7684\u94c1\u7f50\u5934\uff0c\u4e00\u770b\u4fbf\u77e5\u662f\u6b7b\u4ea1\u9a91\u58eb\uff0c\u4ed6\u4eec\u7684\u9ab7\u9ac5\u6218\u9a6c\u4e5f\u88ab\u6253\u6563\u6210\u4e86\u4e00\u5806\u788e\u9aa8\u3002\u5728\u6b7b\u4ea1\u9a91\u58eb\u65c1\u8fb9\uff0c\u8db4\u7740\u56db\u4e2a\u9ed1\u4e4e\u4e4e\u7684\uff0c\u770b\u8d77\u6765\u6709\u4e9b\u8fd1\u4f3c\u77f3\u50cf\u9b3c\u7684\u5bb6\u4f19\uff0c\u5934\u4e0a\u4e24\u6839\u767d\u8272\u957f\u89d2\uff0c\u6b63\u662f\u89d2\u9b54\uff0c\u838e\u73de\u514b\u6b63\u5728\u5b83\u4eec\u8eab\u4e0a\u7ffb\u627e\u4e1c\u897f\u3002\u8fd9\u662f\u5979\u7684\u4e00\u8d2f\u4f5c\u98ce\uff0c\u57fa\u672c\u662f\u770b\u5230\u6bcf\u5177\u5c38\u4f53\u5979\u90fd\u8981\u4e0a\u53bb\u6478\u6478\uff0c\u770b\u80fd\u4e0d\u80fd\u635e\u70b9\u4ec0\u4e48\uff0c\u53ea\u8981\u503c\u94b1\u7684\uff0c\u4e0d\u7ba1\u81ea\u5df1\u6709\u7528\u6ca1\u7528\u4e00\u6982\u90fd\u8981\uff0c\u56de\u53bb\u5356\u7ed9\u5546\u4eba\u6362\u94b1\uff0c\u5982\u679c\u6709\u597d\u7684\u94e0\u7532\u9632\u5177\u4e4b\u7c7b\u5979\u751a\u81f3\u90fd\u4f1a\u76f4\u63a5\u5265\u5149\u3002\u8fd9\u8ba9\u743c\u6069\u60f3\u8d77\u81ea\u5df1\u4ee5\u524d\u73a9\u8fc7\u7684\u7f51\u7edc\u6e38\u620f\uff0c\u62fc\u547d\u6740\u602a\u7206\u6750\u6599\u7206\u88c5\u5907\uff0c\u6ca1\u60f3\u5230\u5728\u73b0\u5b9e\u4e2d\u771f\u89c1\u5230\u4e86\u3002</p><p>\u4ed6\u4ee5\u524d\u548c\u838e\u73de\u514b\u4ea4\u5f80\u4e0d\u591a\uff0c\u76f4\u5230\u53c2\u52a0\u8840\u6218\u4e86\u624d\u53d1\u73b0\uff0c\u5f88\u662f\u5947\u602a\uff0c\u4e5f\u89c9\u5f97\u672a\u514d\u6709\u4e9b\u8fc7\u5206\u4e86\u70b9\uff0c\u6740\u4eba\u5c31\u7b97\u4e86\uff0c\u771f\u662f\u81ea\u5df1\u6709\u7528\u7684\u4e1c\u897f\u62e3\u70b9\u4e5f\u7f62\u4e86\uff0c\u6ca1\u5fc5\u8981\u8fd9\u4e48\u641c\u522e\u3002\u8be2\u95ee\u8d77\u6765\uff0c\u9b45\u9b54\u7684\u56de\u7b54\u4e5f\u5f88\u7b80\u5355\uff0c\u201c\u4e60\u60ef\u4e86\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u4f1a\u517b\u6210\u8fd9\u79cd\u4e60\u60ef\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u5c0f\u65f6\u5019\u7a77\uff0c\u80fd\u6362\u51e0\u4e2a\u94dc\u5e01\u90fd\u662f\u597d\u7684\u3002\u201d</p><p>\u4e8e\u662f\u743c\u6069\u5c31\u518d\u4e0d\u8bf4\u8bdd\u4e86\u3002</p><p>\u8ddd\u79bb\u8f83\u8fdc\u7684\u5730\u65b9\u8fd8\u4fa7\u5367\u7740\u4e00\u4f4d\u5973\u5b50\uff0c\u770b\u4e0d\u6e05\u6b63\u9762\uff0c\u50cf\u662f\u4e2a\u4eba\u7c7b\u6a21\u6837\uff0c\u5e94\u8be5\u5c31\u662f\u90a3\u4f4d\u6b32\u9b54\u4e86\u3002\u743c\u6069\u6ca1\u592a\u5728\u610f\uff0c\u6b32\u9b54\u4e0d\u662f\u4ec0\u4e48\u5389\u5bb3\u89d2\u8272\uff0c\u201c\u786e\u5b9a\u90fd\u6b7b\u4e86\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u5f53\u7136\u3002\u201d</p><p>\u56db\u4e2a\u89d2\u9b54\u7684\u5c38\u4f53\u5012\u8fd8\u5927\u4f53\u5b8c\u6574\uff0c\u5148\u88ab\u838e\u73de\u514b\u68c0\u67e5\u4e86\u4e00\u904d\uff0c\u8fde\u6b66\u5668\u90fd\u6ca1\u6536\u4e86\uff0c\u7136\u540e\u8ba9\u9ab7\u9ac5\u6b66\u58eb\u6254\u5230\u8f66\u4e0a\u53bb\u4e86\uff0c\u8fd9\u79cd\u9ad8\u9636\u90aa\u9b54\u7684\u5668\u5b98\u5176\u5b9e\u4e5f\u662f\u5236\u4f5c\u9b54\u6cd5\u7269\u54c1\u7684\u597d\u6750\u6599\uff0c\u4f8b\u5982\u5934\u4e0a\u90a3\u4e24\u6839\u767d\u8272\u957f\u89d2\uff0c\u4fbf\u5f88\u9002\u5408\u9644\u9b54\u3002\u53ea\u662f\u8fd9\u79cd\u9b54\u6cd5\u7269\u54c1\u4e5f\u6709\u7f3a\u9677\uff0c\u5927\u591a\u53ea\u80fd\u7531\u90aa\u9b54\u4f7f\u7528\uff0c\u51e1\u4eba\u62ff\u4e86\u65e0\u76ca\uff0c\u800c\u4e14\u53ea\u80fd\u5728\u4e0b\u5c42\u754c\u4f7f\u7528\uff0c\u5e26\u56de\u7269\u8d28\u754c\u5c31\u76f4\u63a5\u5316\u4f5c\u98de\u7070\u4e86\u2014\u2014\u636e\u8bf4\u9ad8\u660e\u7684\u70bc\u91d1\u5e08\u6709\u72ec\u7279\u7684\u4fdd\u5b58\u65b9\u6cd5\uff0c\u7136\u800c\u743c\u6069\u662f\u4e0d\u4f1a\u7684\uff0c\u6240\u4ee5\u5174\u8da3\u4e0d\u5927\u3002\u518d\u53bb\u770b\u6b7b\u4ea1\u9a91\u58eb\uff0c\u5012\u662f\u5fae\u5fae\u5403\u4e86\u4e00\u60ca\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7684\u91cd\u5251\u90fd\u65ad\u88c2\u4e86\u3002</p><p>\u201c\u7075\u9b42\u6b66\u5668\u5c45\u7136\u90fd\u6bc1\u4e86\uff1f\u201d\u743c\u6069\u76b1\u7709\u3002</p><p>\u5965\u5580\u65af\u6781\u5176\u559c\u597d\u4ea1\u7075\u672f\uff0c\u6700\u559c\u7231\u7684\u4e8b\u60c5\u5c31\u662f\u53d1\u660e\u5404\u79cd\u65b0\u7684\u4ea1\u7075\u602a\u7269\uff0c\u6b7b\u4ea1\u9a91\u58eb\u4fbf\u662f\u4ed6\u7684\u6770\u4f5c\u4e4b\u4e00\u3002\u4ed6\u4eec\u751f\u524d\u5f80\u5f80\u90fd\u662f\u6b66\u6280\u9ad8\u8d85\u7684\u9a91\u58eb\uff0c\u56e0\u4e3a\u67d0\u4e9b\u539f\u56e0\u5815\u5165\u9ed1\u6697\uff0c\u5c06\u7075\u9b42\u5949\u732e\u7ed9\u6076\u9b54\uff0c\u88ab\u5f3a\u5927\u7684\u4ea1\u7075\u672f\u8f6c\u5316\u6210\u73b0\u5728\u8fd9\u79cd\u5f62\u6001\u3002\u6b7b\u4ea1\u9a91\u58eb\u662f\u9ad8\u9636\u4ea1\u7075\uff0c\u4fdd\u6709\u81ea\u5df1\u7684\u7075\u9b42\uff0c\u6240\u4ee5\u4ed6\u4eec\u4f9d\u65e7\u6709\u7075\u89c9\uff0c\u6709\u795e\u8bc6\uff0c\u6709\u667a\u6167\uff0c\u800c\u4e14\u5bf9\u4e8e\u521b\u9020\u8005\u7edd\u5bf9\u5fe0\u8bda\u3002\u4ed6\u4eec\u7684\u7075\u9b42\u50a8\u5b58\u5728\u81ea\u5df1\u4f7f\u7528\u7684\u6b66\u5668\u4e2d\uff0c\u79f0\u4e3a\u201c\u7075\u9b42\u6b66\u5668\u201d\uff0c\u6709\u70b9\u7c7b\u4f3c\u5deb\u5996\u7684\u201c\u547d\u5323\u201d\uff0c\u4f46\u533a\u522b\u5728\u4e8e\uff0c\u5deb\u5996\u7684\u547d\u5323\u662f\u81f4\u547d\u7684\u5f31\u70b9\uff0c\u6b7b\u4ea1\u9a91\u58eb\u7684\u7075\u9b42\u6b66\u5668\u5374\u662f\u4ed6\u4eec\u6700\u5f3a\u5927\u7684\u529b\u91cf\u3002</p><p>\u5deb\u5996\u7684\u547d\u5323\u662f\u8981\u5c0f\u5fc3\u7ffc\u7ffc\u9690\u85cf\uff0c\u6218\u6218\u5162\u5162\u4fdd\u62a4\uff1b\u6b7b\u4ea1\u9a91\u58eb\u7684\u7075\u9b42\u6b66\u5668\u5374\u662f\u6325\u821e\u5728\u624b\u4e2d\uff0c\u7eb5\u6a2a\u6c99\u573a\uff0c\u65e0\u5f80\u4e0d\u80dc\u7684\u3002\u56e0\u4e3a\u704c\u6ce8\u4e86\u7075\u9b42\u5728\u5185\uff0c\u539f\u672c\u5e73\u5e38\u7684\u6b66\u5668\u4f1a\u53d8\u5f97\u683c\u5916\u5f3a\u5927\u3001\u575a\u56fa\uff0c\u7262\u4e0d\u53ef\u7834\u3002\u800c\u4e14\u53ea\u8981\u7075\u9b42\u6b66\u5668\u4e0d\u7834\uff0c\u5c31\u7b97\u6b7b\u4ea1\u9a91\u58eb\u672c\u8eab\u88ab\u6467\u6bc1\u4e86\uff0c\u4e5f\u8fd8\u662f\u53ef\u4ee5\u7528\u5f3a\u5927\u7684\u4ea1\u7075\u6cd5\u672f\u91cd\u5851\u8eaf\u4f53\uff0c\u518d\u5ea6\u590d\u6d3b\u7684\u3002\u5982\u4eca\u7075\u9b42\u6b66\u5668\u65ad\u88c2\uff0c\u90a3\u8fd9\u4e24\u4e2a\u6b7b\u4ea1\u9a91\u58eb\u5c31\u662f\u5f7b\u5e95\u5b8c\u86cb\u4e86\u3002</p><p>\u8fd9\u4e00\u6218\u5f88\u6fc0\u70c8\u554a\u3002</p><p>\u6b63\u6c89\u541f\u95f4\uff0c\u743c\u6069\u7a81\u7136\u53c8\u60f3\u8d77\u53e6\u5916\u4e00\u4ef6\u8e4a\u8df7\u6765\uff0c\u201c\u838e\u73de\u514b\uff0c\u201d\u4ed6\u76b1\u7709\u95ee\uff0c\u201c\u6b7b\u4ea1\u9a91\u58eb\u600e\u4e48\u4f1a\u8dd1\u5230\u57ce\u5916\u6765\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u9b45\u9b54\u6447\u5934\uff0c\u201c\u6309\u9053\u7406\u8bf4\uff0c\u4ed6\u4eec\u57fa\u672c\u90fd\u88ab\u6b4c\u560e\u65af\u5e26\u53bb\u524d\u7ebf\u4e86\uff0c\u5269\u4e0b\u7684\u90a3\u70b9\uff0c\u4e0d\u90fd\u662f\u57ce\u4e3b\u7684\u8fd1\u536b\u4e48\u3002\u201d</p><p>\u6b4c\u560e\u65af\u6b64\u6b21\u7edf\u5e05\u6076\u9b54\u5927\u519b\u524d\u6765\uff0c\u4e00\u8def\u653b\u57ce\u7565\u5730\uff0c\u987a\u6cb3\u800c\u4e0b\uff0c\u5df2\u7ecf\u5360\u636e\u4e86\u5341\u56db\u5ea7\u57ce\u5821\uff0c\u5206\u522b\u4efb\u547d\u4eb2\u4fe1\u90e8\u4e0b\u7387\u519b\u9a7b\u5b88\u3002\u6b7b\u4ea1\u9a91\u58eb\u4f5c\u4e3a\u7cbe\u82f1\u6218\u529b\uff0c\u7edd\u5927\u90e8\u5206\u90fd\u7531\u6b4c\u560e\u65af\u76f4\u63a5\u6307\u6325\uff0c\u5e26\u5f80\u524d\u7ebf\u4f5c\u6218\uff0c\u5c11\u91cf\u5206\u914d\u7ed9\u8fd9\u4e9b\u65b0\u4e0a\u4efb\u7684\u57ce\u4e3b\u4eec\uff0c\u5145\u5f53\u8fd1\u536b\uff0c\u4e0d\u53ef\u80fd\u968f\u610f\u5230\u5904\u6e9c\u8fbe\u3002\u90a3\u8fd9\u4e24\u4e2a\u6b7b\u4ea1\u9a91\u58eb\u51fa\u73b0\u5728\u8fd9\u91cc\uff0c\u5c31\u6709\u4e9b\u4ee4\u4eba\u5947\u602a\u4e86\u3002</p><p>\u743c\u6069\u4e00\u5f00\u59cb\u770b\u5230\u73b0\u573a\uff0c\u76f4\u63a5\u53cd\u5e94\u662f\u4e00\u573a\u906d\u9047\u6218\uff0c\u4e24\u4e2a\u6b7b\u4ea1\u9a91\u58eb\u5728\u8fd9\u91cc\u649e\u4e0a\u4e86\u56db\u4e2a\u89d2\u9b54\uff0c\u5514\uff0c\u8fd8\u6709\u4e2a\u6b32\u9b54\uff0c\u53cc\u65b9\u5b9e\u529b\u5dee\u4e0d\u591a\u76f8\u5f53\uff0c\u6fc0\u6597\u4e4b\u4e0b\u540c\u5f52\u4e8e\u5c3d\uff0c\u5012\u4e5f\u5f88\u6b63\u5e38\u3002\u4f46\u518d\u4ed4\u7ec6\u4e00\u60f3\uff0c\u5982\u4eca\u524d\u7ebf\u6218\u4e8b\u6b63\u6fc0\u70c8\uff0c\u9b54\u9b3c\u8282\u8282\u8d25\u9000\uff0c\u5f62\u52bf\u4e25\u5cfb\uff0c\u5df2\u7ecf\u88ab\u6253\u5230\u4e86\u201c\u9996\u90fd\u201d\uff0c\u542c\u8bf4\u62dc\u5c14\u9881\u4e0b\u547d\u4ee4\uff0c\u62bd\u8c03\u963f\u5f17\u7eb3\u65af\u5404\u5927\u57ce\u5821\u7684\u7cbe\u9510\u58eb\u5175\u96c6\u5408\uff0c\u51c6\u5907\u4f1a\u6218\uff0c\u89d2\u9b54\u8fd9\u79cd\u9ad8\u9636\u9b54\u9b3c\uff0c\u7406\u5f53\u65e9\u88ab\u6293\u5230\u524d\u7ebf\u53bb\u4e86\uff0c\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\u51fa\u73b0\u3002\u5982\u679c\u8bf4\u662f\u654c\u4eba\u51c6\u5907\u6d3e\u9063\u5206\u961f\u8fdb\u5165\u540e\u65b9\u9a9a\u6270\u7684\u8bdd\uff0c\u4ec5\u4ec5\u56db\u4e2a\u89d2\u9b54\u53c8\u672a\u514d\u592a\u5c11\uff0c\u80fd\u6210\u4ec0\u4e48\u6c14\u5019\u3002\u800c\u8fd9\u4e24\u4e2a\u6b7b\u4ea1\u9a91\u58eb\u4e5f\u5f88\u5947\u602a\uff0c\u6b64\u5730\u65e2\u975e\u524d\u7ebf\uff0c\u53c8\u975e\u57ce\u5185\uff0c\u4ed6\u4eec\u8dd1\u5230\u8fd9\u91cc\u6765\u505a\u4ec0\u4e48\uff0c\u96be\u9053\u6e38\u73a9\u770b\u98ce\u666f\u4e0d\u6210\uff0c\u4ed6\u4eec\u53ef\u662f\u4ee5\u5c3d\u804c\u5c3d\u8d23\u3001\u575a\u5b88\u5c97\u4f4d\u8457\u79f0\u7684\u5427\u3002</p><p>\u201c\u8fd9\u4e24\u4e2a\u6b7b\u4ea1\u9a91\u58eb\u662f\u4ece\u9ed1\u77f3\u57ce\u5821\u7684\u65b9\u5411\u6765\u7684\uff0c\u201d\u838e\u73de\u514b\u67e5\u770b\u4e86\u4e00\u4e0b\u5468\u56f4\u75d5\u8ff9\uff0c\u56de\u62a5\u8bf4\uff0c\u201c\u4e00\u8def\u75be\u9a70\u800c\u6765\uff0c\u50cf\u662f\u8ffd\u8d76\u4ec0\u4e48\u3002\u201d</p><p>\u90a3\u5c31\u5e94\u8be5\u662f\u9ed1\u77f3\u57ce\u5821\u65b0\u4efb\u57ce\u4e3b\u7684\u8fd1\u536b\u961f\u6210\u5458\u4e86\u3002</p><p>\u201c\u4ece\u73b0\u5728\u60c5\u5f62\u770b\uff0c\u8fd9\u4e24\u4e2a\u6b7b\u4ea1\u9a91\u58eb\u5e94\u8be5\u662f\u5949\u4e86\u57ce\u4e3b\u6307\u4ee4\u6765\u8ffd\u8d76\u4ec0\u4e48\u4eba\uff0c\u624d\u5230\u4e86\u8fd9\u91cc\uff0c\u201d\u743c\u6069\u6253\u91cf\u7740\u56db\u5468\uff0c\u201c\u7ed3\u679c\u649e\u4e0a\u8fd9\u56db\u4e2a\u89d2\u9b54\uff0c\u540c\u5f52\u4e8e\u5c3d\u4e86\u2026\u2026\u90a3\u4ed6\u4eec\u672c\u6765\u662f\u60f3\u8ffd\u8c01\uff1f\u201d</p><p>\u81ea\u7136\u4e0d\u4f1a\u662f\u8fd9\u56db\u4e2a\u89d2\u9b54\uff0c\u5426\u5219\u80af\u5b9a\u8981\u52a0\u6d3e\u4eba\u624b\uff0c\u4e0d\u53ef\u80fd\u53ea\u6d3e\u4e24\u4e2a\u6b7b\u4ea1\u9a91\u58eb\u524d\u6765\u3002\u521a\u521a\u5750\u4e0a\u9ed1\u77f3\u57ce\u5821\u57ce\u4e3b\u5b9d\u5ea7\u7684\u8fea\u5185\u7279\u867d\u7136\u4e5f\u662f\u4e2a\u5eb8\u624d\uff0c\u8fd8\u4e0d\u81f3\u4e8e\u611a\u8822\u5230\u8fd9\u79cd\u5730\u6b65\u3002\u90a3\u4e48\u2026\u2026\u96be\u9053\u662f\u90a3\u53ea\u6b32\u9b54\uff1f</p><p>\u743c\u6069\u8f6c\u8fc7\u5934\u53bb\uff0c\u547d\u4ee4\u9ab7\u9ac5\u6b66\u58eb\u628a\u6b32\u9b54\u7684\u5c38\u4f53\u62ac\u8fc7\u6765\uff0c\u8fd1\u524d\u7ec6\u770b\uff0c\u53d1\u73b0\u5979\u5176\u5b9e\u548c\u9b45\u9b54\u8fd1\u4f3c\uff0c\u5916\u5f62\u4e5f\u662f\u4e2a\u6f02\u4eae\u7684\u4eba\u7c7b\u5973\u5b50\uff0c\u8863\u670d\u7a7f\u5f97\u7a0d\u591a\u4e9b\uff0c\u8116\u9888\u4e0a\u6709\u4e00\u4e2a\u76ae\u9879\u5708\uff0c\u80cc\u540e\u4e0d\u662f\u4e00\u5bf9\u8759\u8760\u76ae\u7ffc\uff0c\u800c\u662f\u7c7b\u4f3c\u5929\u4f7f\u822c\u7684\u7fbd\u7ffc\uff0c\u989c\u8272\u9c9c\u7ea2\u5982\u8840\uff0c\u6b64\u5916\u6ca1\u591a\u5c11\u533a\u522b\u2014\u2014\u81f3\u5c11\u4ee5\u4eba\u7c7b\u7684\u773c\u5149\u6765\u770b\u662f\u5982\u6b64\u3002</p><p>\u201c\u5982\u679c\u628a\u7fbd\u7ffc\u989c\u8272\u53d8\u6210\u767d\u8272\uff0c\u76f4\u63a5\u5c31\u53ef\u4ee5\u88c5\u626e\u5929\u4f7f\u4e86\u3002\u201d\u743c\u6069\u968f\u53e3\u8bf4\u3002</p><p>\u201c\u672c\u6765\u5c31\u6709\u4f20\u95fb\uff0c\u8bf4\u6b32\u9b54\u5176\u5b9e\u662f\u5815\u5929\u4f7f\u7684\u4e00\u7c7b\uff0c\u751a\u81f3\u6709\u4eba\u79f0\u547c\u6b32\u9b54\u4e3a\u8840\u5929\u4f7f\u3002\u201d\u838e\u73de\u514b\u56de\u7b54\uff0c\u4ed4\u7ec6\u5728\u5c38\u4f53\u4e0a\u641c\u7d22\u8d77\u6765\uff0c\u60f3\u627e\u51fa\u4ec0\u4e48\u7ebf\u7d22\uff0c\u6216\u8005\u80fd\u641c\u522e\u5230\u4ec0\u4e48\u6218\u5229\u54c1\u4e5f\u597d\u3002</p><p>\u201c\u5947\u602a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u770b\u7740\u6b32\u9b54\u7684\u5c38\u4f53\uff0c\u201c\u89d2\u9b54\u4e5f\u6709\u7fc5\u8180\uff0c\u6b32\u9b54\u4e5f\u6709\u7fc5\u8180\uff0c\u4ed6\u4eec\u65e2\u7136\u90fd\u6709\u7fc5\u8180\uff0c\u90a3\u4f55\u5fc5\u975e\u8981\u5728\u8fd9\u91cc\u6b7b\u6218\uff0c\u9003\u4e86\u5c31\u662f\u3002\u6b7b\u4ea1\u9a91\u58eb\u7684\u6218\u9a6c\u867d\u7136\u80fd\u98de\uff0c\u901f\u5ea6\u53ef\u4e0d\u7b97\u5feb\u3002\u201d</p><p>\u6b63\u5728\u5fd9\u7740\u53d1\u6b7b\u4eba\u8d22\u7684\u9b45\u9b54\u5934\u4e5f\u4e0d\u62ac\u5730\u6307\u4e86\u6307\u5c38\u4f53\u8116\u5b50\u4e0a\u7684\u76ae\u9879\u5708\u3002</p><p>\u201c\u5514\uff0c\u6211\u770b\u5230\u4e86\uff0c\u201d\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u201c\u5f88\u6027\u611f\uff0c\u4e0d\u8fc7\u8fd9\u548c\u6211\u8bf4\u7684\u95ee\u9898\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d</p><p>\u201c\u4f60\u8fc7\u6765\u4ed4\u7ec6\u6478\u6478\u5c31\u77e5\u9053\uff0c\u8fd9\u53ef\u4e0d\u662f\u666e\u901a\u7684\u76ae\u9879\u5708\uff0c\u9644\u9b54\u7684\uff0c\u201d\u9b45\u9b54\u6ca1\u597d\u6c14\u5730\u56de\u7b54\uff0c\u4e00\u8fb9\u8003\u8651\u5982\u4f55\u628a\u8fd9\u9879\u5708\u8131\u4e0b\u6765\uff0c\u201c\u4e0a\u9762\u9644\u7740\u597d\u51e0\u79cd\u6cd5\u672f\uff0c\u5176\u4e2d\u5c31\u6709\u4e00\u9053\u5927\u5730\u675f\u7f1a\u3002\u201d</p><p>\u743c\u6069\u76b1\u7709\uff0c\u8d70\u8fc7\u6765\u4ed4\u7ec6\u67e5\u770b\uff0c\u53d1\u73b0\u679c\u7136\u5982\u6b64\uff0c\u9879\u5708\u4e0a\u9644\u7740\u5404\u79cd\u7981\u9522\u9b54\u6cd5\uff0c\u5168\u662f\u9650\u5236\u6b32\u9b54\u884c\u52a8\u548c\u6218\u6597\u80fd\u529b\u6240\u7528\uff0c\u660e\u663e\u4e0d\u662f\u81ea\u5df1\u6234\u4e0a\u53bb\u7684\u88c5\u9970\u54c1\u3002\u201c\u5979\u4e0d\u80fd\u98de\uff0c\u90a3\u5176\u4ed6\u56db\u4e2a\u89d2\u9b54\u5462\uff1f\u201d</p><p>\u201c\u90a3\u56db\u4e2a\u89d2\u9b54\u662f\u5979\u53ec\u5524\u8fc7\u6765\u7684\uff0c\u81ea\u7136\u8981\u62fc\u6b7b\u4fdd\u62a4\u5979\u3002\u201d</p><p>\u201c\u5979\u53ec\u5524\u7684\uff1f\u5979\u4e00\u4e2a\u6b32\u9b54\uff0c\u5c31\u7b97\u80fd\u53ec\u5524\u89d2\u9b54\uff0c\u4e5f\u6ca1\u9053\u7406\u8ba9\u4ed6\u4eec\u6548\u6b7b\u547d\u5427\uff1f\u201d</p><p>\u201c\u5f88\u7b80\u5355\uff0c\u770b\u770b\u8fd9\u4e2a\u3002\u201d\u838e\u73de\u514b\u626f\u5f00\u6b32\u9b54\u7684\u8863\u9886\uff0c\u8ba9\u540e\u9762\u7684\u743c\u6069\u80fd\u591f\u770b\u5230\u5979\u96ea\u767d\u7684\u80f8\u53e3\uff0c\u5728\u6df1\u68ee\u7684\u6c9f\u58d1\u4e4b\u95f4\uff0c\u6709\u4e00\u4e2a\u523a\u9752\u56fe\u6848\uff0c\u662f\u4e00\u6735\u9ed1\u8272\u7684\u73ab\u7470\u82b1\u3002</p><p>\u201c\u5979\u662f\u9ed1\u73ab\u7470\u7684\u6210\u5458\u3002\u201d\u9b45\u9b54\u8bf4\u3002</p><p>\u203b\u203b\u203b</p><p>\u65e2\u7136\u548c\u62dc\u5c14\u4f5c\u6218\uff0c\u5bf9\u654c\u4eba\u7684\u5e95\u7ec6\u603b\u8981\u6709\u6240\u4e86\u89e3\uff0c\u4f55\u51b5\u90a3\u679a\u5370\u7ae0\u843d\u5165\u8fd9\u4e2a\u9b54\u9b3c\u516c\u7235\u624b\u4e2d\uff0c\u4ee5\u540e\u8bf4\u4e0d\u5b9a\u8981\u6253\u4ea4\u9053\uff0c\u6240\u4ee5\u743c\u6069\u5e73\u65f6\u4e5f\u7559\u5fc3\u6536\u96c6\u8d44\u6599\uff0c\u5bf9\u201c\u9ed1\u73ab\u7470\u201d\u8fd9\u4e2a\u8bcd\u5e76\u4e0d\u964c\u751f\u3002</p><p>\u9eef\u4e4b\u7ffc\u3001\u9690\u5f62\u95ea\u7535\u3001\u9ed1\u73ab\u7470\uff0c\u8fd9\u4e09\u4e2a\u519b\u56e2\u90fd\u521b\u7acb\u4e8e\u516d\u767e\u5e74\u524d\uff0c\u662f\u62dc\u5c14\u771f\u6b63\u7684\u5ae1\u7cfb\uff0c\u662f\u4ed6\u8d56\u4ee5\u8d77\u5bb6\u7684\u8d44\u672c\uff0c\u662f\u80fd\u591f\u7a33\u5750\u5b9d\u5ea7\u81f3\u4eca\u7684\u575a\u5b9e\u4fdd\u969c\u3002</p><p>\u62dc\u5c14\u593a\u5f97\u963f\u5f17\u7eb3\u65af\u5927\u516c\u7235\u4e4b\u4f4d\uff0c\u662f\u5728\u4e09\u767e\u591a\u5e74\u524d\uff0c\u4f46\u4ed6\u5f00\u59cb\u5d2d\u9732\u5934\u89d2\uff0c\u771f\u6b63\u4e3a\u4eba\u6240\u77e5\uff0c\u5219\u662f\u5728\u8ddd\u4eca\u516d\u767e\u5e74\u524d\u7684\u201c\u5929\u754c\u5927\u8fdb\u519b\u201d\u4e4b\u4e2d\u3002\u5f53\u65f6\u5929\u5802\u5c71\u8bf8\u795e\u610f\u56fe\u63d2\u624b\u8840\u6218\uff0c\u6d3e\u9063\u5927\u91cf\u5929\u4f7f\u519b\u56e2\u8fdb\u5165\u5730\u72f1\u7b2c\u4e00\u5c42\uff0c\u5927\u516c\u7235\u624e\u745e\u5c14\u906d\u5230\u90e8\u4e0b\u9b54\u72ac\u5185\u52a0\u5c14\u7684\u80cc\u53db\uff0c\u8fde\u8fde\u6218\u8d25\uff0c\u51e0\u4e4e\u4e22\u5931\u4e86\u6574\u4e2a\u963f\u5f17\u7eb3\u65af\u3002</p><p>\u6b64\u65f6\u7684\u62dc\u5c14\uff0c\u8fd8\u53ea\u662f\u521a\u521a\u664b\u5347\u4e3a\u6df1\u72f1\u70bc\u9b54\uff0c\u5360\u636e\u4e86\u4e00\u5ea7\u504f\u8fdc\u7684\u57ce\u5821\uff0c\u8fd1\u4e4e\u9ed8\u9ed8\u65e0\u95fb\u3002\u4f46\u6b63\u5982\u7269\u8d28\u754c\u7684\u51e1\u4eba\u6240\u8bf4\uff0c\u91d1\u5b50\u653e\u5728\u4efb\u4f55\u5730\u65b9\u90fd\u4f1a\u53d1\u5149\u3002\u4e71\u4e16\u7684\u6765\u4e34\u8ba9\u62dc\u5c14\u7684\u60ca\u4eba\u624d\u534e\u5f97\u5230\u4e86\u6dcb\u6f13\u5c3d\u81f4\u7684\u5c55\u73b0\uff0c\u4ed6\u4e0d\u4f46\u62e5\u6709\u4e2a\u4eba\u7684\u975e\u51e1\u6b66\u52c7\u548c\u9ad8\u8d85\u7684\u6307\u6325\u827a\u672f\uff0c\u66f4\u51fa\u8272\u7684\u662f\u62db\u63fd\u4eba\u624d\u7684\u80fd\u529b\u3002\u4ed6\u9996\u5148\u6210\u529f\u5f15\u8bf1\u4e86\u5927\u91cf\u7684\u5929\u4f7f\u5815\u843d\uff0c\u5176\u4e2d\u751a\u81f3\u5305\u62ec\u4e00\u540d\u66e6\u5929\u4f7f\u3002\u4ee5\u6b64\u4e3a\u57fa\u7840\uff0c\u4ed6\u7ec4\u5efa\u4e86\u201c\u9eef\u4e4b\u7ffc\u201d\u3002\u63a5\u7740\u6536\u7f16\u4e86\u51e0\u4e2a\u5730\u72f1\u732b\u90e8\u843d\uff0c\u7ec4\u6210\u201c\u9690\u5f62\u95ea\u7535\u201d\uff0c\u6700\u540e\u4e0d\u77e5\u9053\u4ece\u54ea\u91cc\u5f04\u6765\u4e86\u4e00\u6279\u5353\u5c14\u5973\u6027\uff0c\u5efa\u7acb\u4e86\u201c\u9ed1\u73ab\u7470\u201d\uff0c\u540e\u6765\u62dc\u5c14\u5c06\u5168\u4f53\u6210\u5458\u90fd\u8f6c\u5316\u6210\u4e86\u6b32\u9b54\uff0c\u4ee5\u4fbf\u83b7\u5f97\u6c38\u6052\u7684\u9752\u6625\uff0c\u4ee5\u53ca\u66f4\u5f3a\u5927\u7684\u529b\u91cf\u3002</p><p>\u6b63\u662f\u501a\u9760\u8fd9\u4e09\u652f\u519b\u56e2\uff0c\u62dc\u5c14\u8fde\u6218\u8fde\u6377\uff0c\u58f0\u540d\u9e4a\u8d77\uff0c\u8fc5\u901f\u6210\u4e3a\u963f\u5f17\u7eb3\u65af\u6700\u6709\u52bf\u529b\u7684\u5927\u9b54\u9b3c\u4e4b\u4e00\u3002\u6700\u7ec8\uff0c\u5929\u4f7f\u519b\u56e2\u88ab\u9a71\u9010\u51fa\u5730\u72f1\uff0c\u62dc\u5c14\u6210\u4e3a\u624e\u745e\u5c14\u7684\u5c06\u519b\uff0c\u4ed6\u5728\u5176\u540e\u7684\u4e24\u767e\u591a\u5e74\u91cc\u7cbe\u5fc3\u7b79\u5212\uff0c\u6210\u529f\u83b7\u5f97\u4e86\u963f\u65af\u8482\u6469\u65af\u548c\u516b\u9b54\u5c06\u7684\u770b\u91cd\uff0c\u6700\u7ec8\u8bbe\u8ba1\u63a8\u7ffb\u624e\u745e\u5c14\uff0c\u53d6\u800c\u4ee3\u4e4b\uff0c\u5750\u4e0a\u4e86\u5927\u516c\u7235\u7684\u5b9d\u5ea7\u3002</p><p>\u5815\u843d\u5929\u4f7f\u80cc\u5f03\u795e\u5723\uff0c\u62e5\u62b1\u90aa\u6076\uff0c\u539f\u672c\u6d01\u767d\u7684\u7fbd\u7ffc\u53d8\u5f97\u7070\u9eef\uff0c\u519b\u56e2\u4ee5\u6b64\u5f97\u540d\u3002\u4ed6\u4eec\u5168\u90fd\u66fe\u7ecf\u662f\u6765\u81ea\u5929\u5802\u5c71\u7684\u7cbe\u9510\u6218\u58eb\uff0c\u867d\u7136\u5df2\u7ecf\u5815\u5165\u9ed1\u6697\uff0c\u4f46\u5f3a\u5927\u7684\u529b\u91cf\u4ecd\u5728\uff0c\u751a\u81f3\u66f4\u8fdb\u4e00\u6b65\uff0c\u800c\u4e14\u4f9d\u65e7\u4fdd\u7559\u4e86\u90e8\u5206\u795e\u5723\u80fd\u529b\uff0c\u6b63\u662f\u90aa\u9b54\u7684\u514b\u661f\u3002\u5730\u72f1\u732b\u5219\u662f\u4e00\u79cd\u5947\u7279\u7684\u9b54\u9b3c\uff0c\u80fd\u591f\u5728\u5149\u4eae\u4e0b\u9690\u5f62\uff0c\u52a8\u4f5c\u8fc5\u5982\u95ea\u7535\uff0c\u5f7c\u6b64\u95f4\u8fd8\u6709\u5fc3\u7075\u611f\u5e94\u80fd\u529b\uff0c\u662f\u7b2c\u4e00\u6d41\u7684\u523a\u5ba2\u3002\u62dc\u5c14\u4ee5\u201c\u9eef\u4e4b\u7ffc\u201d\u4f5c\u4e3a\u4e3b\u6218\u5175\u56e2\uff0c\u653b\u57ce\u7565\u5730\uff0c\u4ee5\u201c\u9690\u5f62\u95ea\u7535\u201d\u4e3a\u6740\u624b\u7ec4\u7ec7\uff0c\u8d1f\u8d23\u76d1\u89c6\u3001\u523a\u63a2\u3001\u6697\u6740\uff0c\u6e05\u9664\u5f02\u5df1\uff1b\u201c\u9ed1\u73ab\u7470\u201d\u7684\u4e3b\u8981\u804c\u80fd\u662f\u5e38\u9a7b\u7269\u8d28\u754c\uff0c\u5f15\u8bf1\u51e1\u4eba\uff0c\u83b7\u53d6\u7075\u9b42\u2014\u2014\u8fd9\u662f\u9b54\u9b3c\u7684\u6700\u91cd\u8981\u201c\u4e1a\u52a1\u201d\u4e4b\u4e00\u3002</p><p>\u201c\u5982\u679c\u662f\u666e\u901a\u7684\u6b32\u9b54\uff0c\u81ea\u7136\u8fdc\u8fdc\u4e0d\u591f\u8d44\u683c\u53bb\u547d\u4ee4\u89d2\u9b54\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4f46\u5979\u662f\u9ed1\u73ab\u7470\uff0c\u90a3\u5c31\u4e0d\u4e00\u6837\u4e86\u3002\u201d</p><p>\u6df1\u6e0a\u548c\u5730\u72f1\u6709\u5f88\u591a\u533a\u522b\uff0c\u5176\u4e2d\u4e00\u4e2a\u6700\u663e\u8457\u7684\uff0c\u5c31\u662f\u6df1\u6e0a\u7684\u201c\u4f4d\u9636\u201d\uff0c\u66f4\u591a\u4ee5\u6218\u529b\u4e3a\u6807\u51c6\uff1b\u800c\u5730\u72f1\u7684\u201c\u4f4d\u9636\u201d\uff0c\u66f4\u8fd1\u4f3c\u4e8e\u6743\u529b\uff0c\u56e0\u4e3a\u5730\u72f1\u6709\u4e25\u683c\u800c\u5f3a\u5927\u7684\u201c\u79e9\u5e8f\u201d\u5b58\u5728\u3002\u62dc\u5c14\u6253\u8d77\u67b6\u6765\u672a\u5fc5\u80dc\u8fc7\u90e8\u5c5e\uff0c\u4f46\u4ed6\u5c31\u662f\u5927\u516c\u7235\u2014\u2014\u8fd9\u662f\u4e5d\u72f1\u4e4b\u4e3b\u3001\u7fa4\u9b54\u9886\u8896\u963f\u65af\u8482\u6469\u65af\u965b\u4e0b\u5c01\u7684\uff0c\u5176\u4ed6\u9b54\u9b3c\u90fd\u5f97\u542c\u547d\uff0c\u8fd9\u5728\u6df1\u6e0a\u4e2d\u662f\u5f88\u96be\u60f3\u8c61\u7684\u4e8b\u60c5\u3002\u540c\u6837\u9053\u7406\uff0c\u9ed1\u73ab\u7470\u4f5c\u4e3a\u62dc\u5c14\u7684\u4eb2\u4fe1\uff0c\u80fd\u529b\u867d\u5f31\uff0c\u5730\u4f4d\u5374\u9ad8\uff0c\u533a\u533a\u89d2\u9b54\u4eec\u81ea\u7136\u65e0\u6cd5\u6297\u547d\u3002</p><p>\u201c\u5514\uff0c\u8fd9\u6837\u4e00\u6765\u4e8b\u60c5\u5c31\u66f4\u53e4\u602a\u4e86\uff0c\u201d\u743c\u6069\u52aa\u529b\u60f3\u628a\u773c\u524d\u7684\u60c5\u5f62\u6574\u7406\u51fa\u4e00\u4e2a\u5934\u7eea\u6765\uff0c\u81f3\u5c11\u6709\u4e2a\u7c97\u7565\u7684\u8f6e\u5ed3\uff0c\u201c\u770b\u8d77\u6765\uff0c\u662f\u8fd9\u4f4d\u6b32\u9b54\u5c0f\u59d0\u4ece\u67d0\u4e2a\u4e0d\u597d\u7684\u5730\u65b9\u9003\u4e86\u51fa\u6765\uff0c\u7136\u540e\u67d0\u4eba\u6d3e\u9063\u4e86\u4e24\u4f4d\u6b7b\u4ea1\u9a91\u58eb\u6765\u8ffd\u8d76\uff0c\u662f\u4ece\u9ed1\u77f3\u57ce\u5821\u7684\u65b9\u5411\u800c\u6765\u2026\u2026\u201d</p><p>\u201c\u6216\u8bb8\u4e0d\u6b62\u4e24\u4f4d\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u6709\u53ef\u80fd\u662f\u6d3e\u9063\u4e86\u5f88\u591a\uff0c\u53ea\u662f\u5206\u5934\u8ffd\u8d76\uff0c\u8fd9\u4e24\u4e2a\u5012\u9709\u5bb6\u4f19\u6070\u597d\u8d70\u5bf9\u4e86\u8def\u7ebf\u3002\u201d</p><p>\u201c\u6709\u53ef\u80fd\uff0c\u201d\u743c\u6069\u8d5e\u6210\uff0c\u201c\u53cd\u6b63\u8fd9\u4e24\u4e2a\u6b7b\u4ea1\u9a91\u58eb\u627e\u5230\u4e86\u76ee\u6807\u2026\u2026\u8fd9\u4f4d\u6b32\u9b54\u5c0f\u59d0\u53ec\u5524\u4e86\u56db\u53ea\u89d2\u9b54\uff0c\u7136\u540e\u567c\u91cc\u556a\u5566\u4e00\u901a\u4e71\u6253\uff0c\u90fd\u5b8c\u86cb\u4e86\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u9b45\u9b54\u70b9\u5934\uff0c\u201c\u5e94\u8be5\u5dee\u4e0d\u591a\u3002\u201d</p><p>\u201c\u90a3\u4e48\u8c01\u56da\u7981\u4e86\u8fd9\u4f4d\u6b32\u9b54\u5c0f\u59d0\uff0c\u8c01\u6709\u8d44\u683c\u6d3e\u9063\u6b7b\u4ea1\u9a91\u58eb\u6765\u8ffd\u8d76\u5462\uff1f\u201d</p><p>\u8fd9\u4e2a\u95ee\u9898\u663e\u800c\u6613\u89c1\uff0c\u6240\u4ee5\u838e\u73de\u514b\u90fd\u61d2\u5f97\u56de\u7b54\uff0c\u201c\u6211\u4eec\u73b0\u5728\u600e\u4e48\u529e\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u90fd\u88c5\u4e0a\u8f66\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5148\u8fd0\u56de\u53bb\u518d\u8bf4\u3002\u201d</p><p>\u4ed6\u62db\u547c\u9ab7\u9ac5\u6b66\u58eb\u8fc7\u6765\uff0c\u547d\u4ee4\u5b83\u4eec\u628a\u6b32\u9b54\u5c38\u4f53\u642c\u4e0a\u8f66\u3002\u838e\u73de\u514b\u6709\u4e9b\u4e0d\u751a\u6109\u5feb\uff0c\u5979\u641c\u7d22\u4e86\u534a\u5929\uff0c\u4e00\u65e0\u6240\u83b7\uff0c\u4fbf\u76ef\u4e0a\u4e86\u6b7b\u8005\u8eab\u4e0a\u552f\u4e00\u503c\u94b1\u7684\u4e1c\u897f\uff0c\u4e5f\u5c31\u662f\u8116\u9888\u4e0a\u7684\u90a3\u4e2a\u9b54\u6cd5\u9879\u5708\u4e86\u3002\u65e0\u5948\u8fd9\u4e1c\u897f\u672c\u6765\u5c31\u662f\u675f\u7f1a\u4eba\u6240\u7528\uff0c\u4e0d\u662f\u9970\u54c1\uff0c\u6ca1\u6709\u4e13\u95e8\u7684\u53e3\u4ee4\u662f\u6253\u4e0d\u5f00\u7684\uff0c\u9664\u975e\u76f4\u63a5\u65a9\u9996\u2026\u2026</p><p>\u201c\u522b\u60f9\u9ebb\u70e6\u4e86\uff0c\u201d\u743c\u6069\u770b\u51fa\u5979\u7684\u5fc3\u601d\uff0c\u201c\u800c\u4e14\u5c31\u7b97\u53d6\u4e0b\u6765\uff0c\u4f60\u6ca1\u6709\u53e3\u4ee4\uff0c\u8fd9\u4e1c\u897f\u5c31\u662f\u5e9f\u7269\uff0c\u8fde\u5356\u94b1\u90fd\u4e0d\u884c\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u4e2a\u70bc\u91d1\u5e08\u5417\uff0c\u96be\u9053\u4e0d\u80fd\u627e\u51fa\u53e3\u4ee4\uff1f\u201d</p><p>\u201c\u6216\u8bb8\uff0c\u4f46\u6211\u73b0\u5728\u53ef\u6ca1\u7a7a\u95f2\u505a\u8fd9\u79cd\u4e8b\u60c5\u3002\u201d</p><p>\u838e\u73de\u514b\u53f9\u53e3\u6c14\uff0c\u6b63\u51c6\u5907\u8d77\u8eab\uff0c\u7a81\u7136\u6014\u4e86\u4e00\u6014\uff0c\u773c\u89d2\u4f59\u5149\u4f3c\u4e4e\u77a5\u5230\u4e86\u4ec0\u4e48\u52a8\u9759\u3002</p><p>\u201c\u5979\u5728\u52a8\uff01\u201d</p></article>"}}}]);
