(window.webpackJsonp=window.webpackJsonp||[]).push([[1555],{1578:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u8282 \u6d88\u606f",content:"<article><h2>\u7b2c\u56db\u8282 \u6d88\u606f</h2><p>\u4e94\u5206\u949f\u540e\uff0c\u4e00\u884c\u4eba\u7ee7\u7eed\u4e0a\u8def\uff0c\u4f9d\u65e7\u662f\u743c\u6069\u548c\u838e\u73de\u514b\u5728\u524d\uff0c\u516b\u4e2a\u9ab7\u9ac5\u6b66\u58eb\u63a8\u7740\u767d\u9aa8\u7075\u8f66\u8ddf\u968f\u5728\u540e\uff0c\u4e00\u53ea\u5938\u8d5b\u9b54\u4f3c\u4e4e\u88ab\u6253\u6655\u4e86\uff0c\u8db4\u5728\u8f66\u4e0a\u4e00\u52a8\u4e0d\u52a8\uff0c\u6b32\u9b54\u5df2\u7ecf\u4e0d\u89c1\u8e2a\u5f71\u3002\u90a3\u4e24\u4e2a\u6b7b\u4ea1\u9a91\u58eb\u548c\u56db\u4e2a\u89d2\u9b54\u7684\u5c38\u4f53\u4e5f\u5168\u90fd\u88ab\u6254\u8fdb\u4e86\u51a5\u6cb3\u91cc\uff0c\u4ece\u6b64\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6d88\u5931\uff0c\u5c31\u7b97\u662f\u8bf8\u795e\u4e5f\u627e\u4e0d\u5230\u4e86\u3002</p><p>\u6bc1\u5c38\u706d\u8ff9\uff0c\u4eff\u4f5b\u521a\u624d\u9047\u5230\u7684\u4e00\u5207\u90fd\u6ca1\u53d1\u751f\u8fc7\u3002</p><p>\u201c\u6211\u5c31\u77e5\u9053\u4f60\u80af\u5b9a\u4e0d\u4f1a\u7b54\u5e94\u3002\u201d\u838e\u73de\u514b\u8bf4\u3002</p><p>\u201c\u4e3a\u4ec0\u4e48\u5462\uff0c\u201d\u743c\u6069\u53cd\u95ee\uff0c\u201c\u5176\u5b9e\u5979\u8bf4\u7684\u5f88\u6709\u9053\u7406\u554a\uff0c\u9b54\u9b3c\u7684\u4fe1\u8a89\uff0c\u6bd4\u6076\u9b54\u53ef\u5f3a\u591a\u4e86\u3002\u628a\u5979\u9001\u5230\u9752\u94dc\u57ce\u5821\uff0c\u6211\u4eec\u4e0d\u8d39\u4ec0\u4e48\u529b\u6c14\uff0c\u5c31\u80fd\u5f97\u5230\u4e30\u539a\u7684\u62a5\u916c\u3002\u8fd9\u79cd\u597d\u4e8b\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8ba4\u4e3a\u6211\u5c31\u80af\u5b9a\u4e0d\u4f1a\u7b54\u5e94\u5462\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u7684\u4e24\u4f4d\u5c0f\u60c5\u4eba\u8fd8\u5728\u9ed1\u77f3\u57ce\u91cc\u5462\uff0c\u201d\u838e\u73de\u514b\u56de\u7b54\uff0c\u201c\u4f60\u4f1a\u6254\u4e0b\u5979\u4eec\u4e0d\u7ba1\uff1f\u201d</p><p>\u201c\u4e0d\u4f1a\u3002\u201d</p><p>\u201c\u90a3\u4e0d\u5c31\u662f\u4e86\u3002\u201d</p><p>\u201c\u5514\uff0c\u4e0d\u8fc7\u4f60\u662f\u4e0d\u662f\u4e0d\u8981\u7528\u8fd9\u79cd\u8bed\u6c14\u8bf4\u8bdd\uff0c\u4f1a\u8ba9\u6211\u4ea7\u751f\u8bef\u4f1a\u7684\u3002\u201d</p><p>\u201c\u8bef\u4f1a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8bef\u4f1a\u4f60\u5ac9\u5992\u4e86\u3002\u201d</p><p>\u9b45\u9b54\u54fc\u4e86\u4e00\u58f0\u3002</p><p>\u201c\u6211\u6709\u522b\u7684\u8003\u8651\uff0c\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u743c\u6069\u4e3b\u52a8\u89e3\u91ca\uff0c\u201c\u9996\u5148\uff0c\u5979\u662f\u4ed8\u5f97\u8d77\u8db3\u591f\u4e30\u539a\u7684\u62a5\u916c\uff0c\u4e5f\u4e0d\u7528\u62c5\u5fc3\u53cd\u6094\u3002\u4f46\u6211\u771f\u6b63\u60f3\u8981\u7684\u4e1c\u897f\uff0c\u5979\u7ed9\u4e0d\u4e86\u3002\u201d</p><p>\u901a\u5e38\u6765\u8bf4\uff0c\u51e1\u4eba\u53c2\u52a0\u5230\u90aa\u9b54\u7684\u8840\u6218\u4e2d\u6765\uff0c\u6240\u4e3a\u7684\u65e0\u975e\u662f\u7269\u8d28\u5229\u76ca\uff0c\u91d1\u94f6\u73e0\u5b9d\u3001\u6743\u52bf\u5730\u4f4d\u4e4b\u7c7b\uff0c\u800c\u8fd9\u4e9b\u6076\u9b54\u80fd\u7ed9\uff0c\u9b54\u9b3c\u4e5f\u80fd\u7ed9\uff0c\u8fd8\u80fd\u7ed9\u5f97\u66f4\u591a\uff0c\u6bd5\u7adf\u5730\u72f1\u662f\u79e9\u5e8f\u793e\u4f1a\uff0c\u79e9\u5e8f\u793e\u4f1a\u624d\u80fd\u6709\u66f4\u591a\u7684\u8d22\u5bcc\u3002\u56e0\u4e3a\u9b54\u9b3c\u7684\u79e9\u5e8f\u672c\u8d28\uff0c\u53ea\u8981\u7b7e\u7f72\u5951\u7ea6\uff0c\u4e5f\u5c31\u4e0d\u6015\u4ed6\u4eec\u8d56\u5e10\u53cd\u6094\u2014\u2014\u5f53\u7136\uff0c\u9b54\u9b3c\u64c5\u957f\u73a9\u6587\u5b57\u6e38\u620f\uff0c\u64c5\u957f\u94bb\u7a7a\u5b50\uff0c\u4f46\u743c\u6069\u5bf9\u6b64\u65e0\u6240\u8c13\uff0c\u4ed6\u4ee5\u524d\u4e5f\u7ecf\u5e38\u73a9\u8fd9\u4e2a\uff0c\u5927\u5bb6\u662f\u540c\u884c\uff0c\u8c01\u6015\u8c01\u3002\u95ee\u9898\u53ea\u5728\u4e8e\uff0c\u4f0a\u84d3\u4e1d\u80fd\u591f\u63d0\u4f9b\u7684\u8fd9\u4e9b\uff0c\u5bf9\u4e8e\u743c\u6069\u800c\u8a00\u6ca1\u6709\u771f\u6b63\u7684\u5438\u5f15\u529b\u3002\u4ed6\u771f\u6b63\u60f3\u8981\u7684\uff0c\u4e00\u662f\u529b\u91cf\uff0c\u4e8c\u662f\u90a3\u679a\u767d\u7389\u5370\u7ae0\u3002\u65e0\u8bba\u54ea\u4e00\u79cd\uff0c\u90fd\u4e0d\u662f\u4f0a\u84d3\u4e1d\u80fd\u7ed9\u5f97\u8d77\u7684\uff0c\u5979\u7eb5\u7136\u518d\u53d7\u4fe1\u91cd\uff0c\u96be\u4e0d\u6210\u8fd8\u80fd\u66ff\u62dc\u5c14\u4f5c\u4e3b\u4e0d\u6210\u3002</p><p>\u201c\u5176\u6b21\uff0c\u73b0\u5728\u5c40\u52bf\u4e0d\u660e\uff0c\u6211\u4eec\u6ca1\u5fc5\u8981\u8d38\u7136\u884c\u4e8b\uff0c\u201d\u743c\u6069\u63a5\u7740\u8bf4\uff0c\u201c\u6b4c\u560e\u65af\u7387\u519b\u653b\u6253\u9752\u94dc\u57ce\u5821\uff0c\u5982\u4eca\u5df2\u7ecf\u662f\u7b2c\u516b\u5929\u4e86\u5427\uff0c\u4e5f\u8be5\u6709\u4e2a\u7ed3\u679c\u4e86\u3002\u5982\u679c\u62dc\u5c14\u5df2\u7ecf\u6218\u8d25\u751a\u81f3\u5b8c\u86cb\u4e86\uff0c\u6211\u4eec\u73b0\u5728\u8fc7\u53bb\uff0c\u5c82\u4e0d\u662f\u81ea\u627e\u9ebb\u70e6\u4e48\u3002\u201d</p><p>\u201c\u90a3\u4f60\u51c6\u5907\u600e\u4e48\u529e\uff1f\u201d</p><p>\u201c\u5148\u56de\u9ed1\u77f3\u57ce\uff0c\u7b49\u524d\u7ebf\u7684\u6d88\u606f\uff0c\u518d\u505a\u51b3\u5b9a\uff0c\u201d\u743c\u6069\u62ac\u5934\u770b\u770b\uff0c\u8fdc\u5904\u5df2\u7ecf\u9690\u9690\u7ea6\u7ea6\u770b\u89c1\u57ce\u5821\u7684\u9ed1\u8272\u57ce\u5899\uff0c\u201c\u9752\u94dc\u57ce\u5821\u662f\u963f\u5f17\u7eb3\u65af\u7684\u4e2d\u5fc3\uff0c\u662f\u62dc\u5c14\u6743\u4f4d\u7684\u57fa\u7840\uff0c\u4e00\u65e6\u4e22\u6389\u9752\u94dc\u57ce\u5821\uff0c\u4ed6\u7684\u5927\u516c\u7235\u8eab\u4efd\u4e5f\u5c31\u540c\u65f6\u88ab\u5265\u593a\u4e86\uff0c\u6240\u4ee5\u4ed6\u51b3\u4e0d\u4f1a\u653e\u5f03\u3002\u8fd9\u4e00\u6218\u7684\u80dc\u8d25\u81f3\u4e3a\u5173\u952e\uff0c\u5982\u679c\u9b54\u9b3c\u4e00\u8d25\u6d82\u5730\uff0c\u6b4c\u560e\u65af\u653b\u4e0b\u4e86\u9752\u94dc\u57ce\u5821\uff0c\u90a3\u81ea\u7136\u6ca1\u4ec0\u4e48\u53ef\u8bf4\u7684\uff0c\u76f4\u63a5\u628a\u5979\u4ea4\u7ed9\u8fea\u5185\u7279\u9886\u8d4f\u5c31\u662f\u3002\u4e0d\u8fc7\u6211\u89c9\u5f97\u8fd9\u79cd\u53ef\u80fd\u6027\u4e0d\u662f\u5f88\u9ad8\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8fd9\u4e00\u8def\u6076\u9b54\u8fdb\u519b\u52bf\u5982\u7834\u7af9\uff0c\u8fde\u6218\u8fde\u514b\uff0c\u4e00\u65b9\u9762\u81ea\u7136\u662f\u56e0\u4e3a\u5175\u529b\u96c4\u539a\uff0c\u795e\u8eaf\u9b54\u50cf\u6240\u5411\u65e0\u654c\uff0c\u53e6\u4e00\u65b9\u9762\u4e5f\u662f\u9b54\u9b3c\u519b\u961f\u5176\u5b9e\u6ca1\u6709\u771f\u6b63\u62b5\u6297\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u6ca1\u770b\u51fa\u6765\u4e48\uff0c\u62dc\u5c14\u7684\u6253\u7b97\uff0c\u5176\u5b9e\u662f\u632b\u9510\u6c14\uff0c\u907f\u950b\u8292\uff0c\u4f60\u522b\u770b\u4ed6\u662f\u8fde\u4e22\u4e86\u5341\u56db\u5ea7\u57ce\u5821\uff0c\u4f46\u5176\u5b9e\u5e76\u6ca1\u6709\u771f\u6b63\u635f\u5931\u591a\u5c11\u519b\u961f\uff0c\u51e0\u4e4e\u6bcf\u4e00\u4f4d\u57ce\u4e3b\u90fd\u662f\u7a0d\u505a\u63a5\u89e6\uff0c\u6218\u8d25\uff0c\u6700\u540e\u7387\u4e3b\u529b\u9000\u8d70\uff0c\u4e22\u4e0b\u7684\u90fd\u662f\u4f4e\u7ea7\u70ae\u7070\u3002\u4ed6\u662f\u628a\u7cbe\u9510\u90fd\u96c6\u4e2d\u5230\u4e86\u9752\u94dc\u57ce\u5821\uff0c\u51c6\u5907\u7740\u8fd9\u4e00\u6218\u7ffb\u76d8\u3002\u4e0e\u4e4b\u76f8\u53cd\uff0c\u6b4c\u560e\u65af\u867d\u7136\u8fde\u4e0b\u5341\u56db\u57ce\uff0c\u770b\u4e0a\u53bb\u98ce\u5149\u65e0\u6bd4\uff0c\u4f46\u4ed6\u8981\u4e0d\u8981\u5206\u5175\u9a7b\u5b88\uff1f\u5fc5\u987b\u9a7b\u5b88\uff0c\u5426\u5219\u5c31\u662f\u5b64\u519b\u6df1\u5165\uff0c\u5f97\u4e0d\u5230\u540e\u65b9\u6e90\u6e90\u4e0d\u65ad\u7684\u4f9b\u5e94\uff0c\u4ed6\u5175\u529b\u518d\u96c4\u539a\u4e5f\u4f1a\u88ab\u62dc\u5c14\u4e00\u70b9\u70b9\u6d88\u78e8\u5149\u3002\u4f46\u8981\u9a7b\u5b88\u7684\u8bdd\u5c31\u5f97\u5206\u6563\u5175\u529b\uff0c\u6bcf\u5ea7\u57ce\u5821\u867d\u7136\u90fd\u4e0d\u591a\uff0c\u5341\u56db\u5ea7\u52a0\u8d77\u6765\u53ef\u5c31\u4e0d\u5c11\u3002\u6211\u6d88\u654c\u957f\uff0c\u62dc\u5c14\u5360\u5c3d\u4e3b\u573a\u4f18\u52bf\uff0c\u6b4c\u560e\u65af\u53c8\u662f\u4e2a\u5eb8\u624d\uff0c\u8fd9\u4e00\u6218\u54ea\u6709\u90a3\u4e48\u5bb9\u6613\u5f97\u80dc\u3002\u8ba9\u6211\u4f30\u8ba1\u7684\u8bdd\uff0c\u6700\u53ef\u80fd\u51fa\u73b0\u7684\u60c5\u5f62\u662f\u53cc\u65b9\u50f5\u6301\u4f4f\u4e86\uff0c\u6b4c\u560e\u65af\u653b\u4e0d\u4e0b\u9752\u94dc\u57ce\u5821\uff0c\u62dc\u5c14\u4e5f\u6253\u4e0d\u9000\u654c\u519b\u3002\u201d</p><p>\u201c\u5982\u679c\u771f\u50f5\u6301\u4f4f\u4e86\uff0c\u4f60\u53c8\u51c6\u5907\u600e\u4e48\u529e\uff1f\u201d</p><p>\u201c\u5c40\u9762\u50f5\u6301\u5bf9\u6211\u4eec\u66f4\u6709\u5229\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u5c31\u50cf\u505a\u751f\u610f\uff0c\u5979\u662f\u4e2a\u91cd\u8981\u4eba\u7269\uff0c\u638c\u63e1\u7740\u91cd\u8981\u4fe1\u606f\uff0c\u80fd\u591f\u5e2e\u52a9\u9b54\u9b3c\u83b7\u80dc\uff0c\u90a3\u4e48\u5979\u5c31\u662f\u4ef6\u5927\u6709\u4ef7\u503c\u7684\u5546\u54c1\uff0c\u73b0\u5728\u6211\u4eec\u72ec\u5bb6\u9500\u552e\uff0c\u5f85\u4ef7\u800c\u6cbd\uff0c\u5c82\u4e0d\u66f4\u597d\u3002\u53cd\u6b63\u6211\u4eec\u53c8\u4e0d\u7528\u7740\u6025\uff0c\u6709\u7684\u662f\u4eba\u7740\u6025\u3002\u5982\u679c\u8fd0\u6c14\u597d\u7684\u8bdd\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u8ba9\u62dc\u5c14\u7528\u90a3\u679a\u5370\u7ae0\u6765\u4ea4\u6362\uff0c\u90a3\u5c31\u6700\u7406\u60f3\u4e0d\u8fc7\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4e07\u4e00\u6076\u9b54\u5927\u8d25\uff0c\u9b54\u9b3c\u83b7\u80dc\u5462\uff1f\u201d</p><p>\u201c\u90a3\u6211\u4eec\u4f9d\u7136\u53ef\u4ee5\u8003\u8651\u548c\u4f0a\u84d3\u4e1d\u5c0f\u59d0\u5408\u4f5c\u554a\uff0c\u522b\u5fd8\u4e86\u73b0\u5728\u5979\u7684\u6027\u547d\u53ef\u662f\u5728\u6211\u4eec\u624b\u91cc\uff0c\u81f3\u5c11\u635e\u70b9\u597d\u5904\u662f\u6ca1\u95ee\u9898\u7684\uff0c\u987a\u4fbf\u4fdd\u8bc1\u6211\u4eec\u7684\u5b89\u5168\uff0c\u201d\u743c\u6069\u987f\u4e86\u987f\uff0c\u201c\u800c\u4e14\u8fd9\u79cd\u60c5\u51b5\u57fa\u672c\u4e0d\u53ef\u80fd\uff0c\u6b4c\u560e\u65af\u867d\u7136\u6ca1\u4ec0\u4e48\u672c\u4e8b\uff0c\u6bd5\u7adf\u5175\u529b\u4e0a\u8fd8\u662f\u8fdc\u5360\u4f18\u52bf\uff0c\u4ea1\u7075\u6307\u6325\u8d77\u6765\u53c8\u5bb9\u6613\uff0c\u800c\u4e14\u8fd8\u6709\u795e\u8eaf\u9b54\u50cf\u5728\u3002\u62dc\u5c14\u5c31\u7b97\u6709\u5343\u822c\u5999\u8ba1\uff0c\u641e\u4e0d\u5b9a\u5b83\uff0c\u7ec8\u7a76\u662f\u4e0d\u6210\u3002\u201d</p><p>\u201c\u4f60\u771f\u9634\u9669\uff0c\u201d\u838e\u73de\u514b\u8bc4\u4ef7\uff0c\u201c\u6211\u73b0\u5728\u90fd\u8981\u6000\u7591\u4f60\u662f\u9b54\u9b3c\u5047\u626e\u7684\u4e86\u3002\u201d</p><p>\u201c\u5c0f\u59d0\uff0c\u5927\u5bb6\u719f\u5f52\u719f\uff0c\u4f60\u4e71\u8bf4\u8bdd\u6211\u4e00\u6837\u544a\u4f60\u8bfd\u8c24\u554a\uff0c\u201d\u743c\u6069\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\uff0c\u201c\u53cd\u6b63\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u5c0f\u5fc3\u8c28\u614e\u70b9\uff0c\u7ad9\u5728\u80dc\u5229\u8005\u4e00\u8fb9\u603b\u662f\u6ca1\u9519\u3002\u800c\u4e14\u8bf4\u5b9e\u8bdd\uff0c\u6211\u8fd8\u771f\u6bd4\u8f83\u671f\u671b\u9b54\u9b3c\u80fd\u83b7\u80dc\u5462\u3002\u201d</p><p>\u201c\u8fd9\u5c31\u662f\u6240\u8c13\u7684\u540c\u7c7b\u76f8\u60dc\u5427\u3002\u201d</p><p>\u201c\u5dee\u4e0d\u591a\uff0c\u6211\u770b\u5230\u9b54\u9b3c\u603b\u6709\u79cd\u7279\u522b\u7684\u4eb2\u5207\u611f\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u610f\u5916\u649e\u5230\u4f0a\u84d3\u4e1d\u8fd9\u7968\u5927\u9c7c\uff0c\u743c\u6069\u5fc3\u60c5\u987f\u65f6\u6109\u5feb\u5f88\u591a\uff0c\u770b\u770b\u767d\u9aa8\u7075\u8f66\u4e0a\u9b54\u9b3c\u5c38\u4f53\u5806\u79ef\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u4efb\u52a1\u65e9\u5c31\u8d85\u6807\u5b8c\u6210\uff0c\u4e5f\u4fbf\u4e0d\u518d\u803d\u6401\uff0c\u5f84\u76f4\u56de\u57ce\uff0c\u7b97\u662f\u7ed9\u81ea\u5df1\u653e\u534a\u5929\u5047\u3002\u4e24\u4eba\u6cbf\u6cb3\u4e00\u8def\u884c\u8d70\uff0c\u534a\u4e2a\u5c0f\u65f6\u540e\u8fdb\u4e86\u9ed1\u77f3\u57ce\u3002</p><p>\u743c\u6069\u7b49\u4eba\u4ece\u5357\u95e8\u5165\u57ce\uff0c\u5148\u628a\u8f66\u4e0a\u90a3\u53ea\u4e0d\u80fd\u52a8\u5f39\u7684\u201c\u5938\u8d5b\u9b54\u201d\u4ea4\u7ed9\u838e\u73de\u514b\uff0c\u8ba9\u5979\u5e26\u56de\u4f4f\u5904\u770b\u7ba1\uff0c\u81ea\u5df1\u9886\u7740\u9ab7\u9ac5\u6b66\u58eb\u63a8\u8f66\u7ee7\u7eed\u524d\u8fdb\uff0c\u76f4\u5230\u4e00\u5904\u5893\u5730\uff0c\u51e0\u5341\u5177\u50f5\u5c38\u6b63\u6447\u6447\u6643\u6643\u56db\u5904\u5de1\u903b\uff0c\u743c\u6069\u53d6\u51fa\u5934\u9aa8\u5fbd\u7ae0\uff0c\u547d\u4ee4\u50f5\u5c38\u4eec\u8ba9\u5f00\u9053\u8def\uff0c\u5f84\u76f4\u8d70\u5230\u4e00\u5ea7\u9ad8\u5927\u7684\u575f\u5893\u9762\u524d\uff0c\u6572\u4e86\u6572\u77f3\u7891\u3002\u4e00\u4e2a\u9ed1\u6c89\u6c89\u7684\u6d1e\u53e3\u6084\u7136\u655e\u5f00\uff0c\u9732\u51fa\u5e73\u7f13\u5f80\u4e0b\u7684\u901a\u9053\uff0c\u743c\u6069\u6325\u624b\u8ba9\u9ab7\u9ac5\u6b66\u58eb\u4eec\u63a8\u7740\u8f66\u5b50\u4e0a\u524d\uff0c\u81ea\u5df1\u8ddf\u968f\u5728\u540e\uff0c\u8d70\u4e86\u7247\u523b\uff0c\u4fbf\u8fdb\u5165\u4e86\u4e00\u5ea7\u5de8\u5927\u7684\u5730\u4e0b\u5bab\u6bbf\u3002</p><p>\u9ed1\u77f3\u7684\u524d\u4efb\u57ce\u4e3b\u53eb\u505a\u827e\u6bd4\u65af\uff0c\u4e5f\u662f\u62dc\u5c14\u7684\u4eb2\u4fe1\uff0c\u5f88\u65e9\u5c31\u8ffd\u968f\u9ebe\u4e0b\uff0c\u7b97\u662f\u5143\u8001\u91cd\u81e3\u4e4b\u4e00\uff0c\u8d44\u5386\u6bd4\u8d77\u9ed1\u73ab\u7470\u7684\u526f\u56e2\u957f\u4f0a\u84d3\u4e1d\u5c0f\u59d0\u4e5f\u4e0d\u5dee\u592a\u591a\u3002\u62dc\u5c14\u5f53\u4e0a\u5927\u516c\u7235\u4e4b\u540e\uff0c\u8d4f\u529f\u916c\u7235\uff0c\u5c06\u4ed6\u5206\u5c01\u5230\u9ed1\u77f3\u57ce\u3002\u827e\u6bd4\u65af\u5f53\u65f6\u7684\u5f62\u6001\u662f\u4e2a\u9aa8\u9b54\uff0c\u8fd9\u662f\u79cd\u5e26\u6709\u9887\u591a\u4ea1\u7075\u4e60\u6027\u7684\u9b54\u9b3c\uff0c\u5168\u8eab\u7626\u524a\u5d99\u5ccb\uff0c\u76ae\u5305\u9aa8\u5934\uff0c\u770b\u8d77\u6765\u5c31\u50cf\u662f\u5177\u9ab7\u9ac5\uff0c\u5f53\u7136\u591a\u4e86\u4e00\u5c42\u76ae\uff0c\u4ed6\u4e0d\u559c\u6b22\u4f4f\u5728\u5730\u8868\u7684\u5efa\u7b51\uff0c\u5728\u5893\u5730\u4e0b\u8425\u5efa\u4e86\u8fd9\u5ea7\u5730\u4e0b\u5bab\u6bbf\u4f5c\u4e3a\u5b98\u90b8\u3002\u540e\u6765\u827e\u6bd4\u65af\u8fdb\u5316\u6210\u4e86\u51b0\u9b54\uff0c\u751f\u6d3b\u4e60\u60ef\u968f\u4e4b\u6539\u53d8\uff0c\u4e8e\u662f\u53c8\u642c\u56de\u4e86\u5730\u9762\uff0c\u91cd\u65b0\u5efa\u9020\u4e86\u4e00\u5ea7\u51b0\u5bab\u5c45\u4f4f\uff0c\u8fd9\u5ea7\u5730\u4e0b\u5bab\u6bbf\u5219\u53d8\u6210\u4e86\u9aa8\u9b54\u4eec\u7684\u519b\u8425\u3002\u6b4c\u560e\u65af\u6253\u4e0b\u9ed1\u77f3\u57ce\u540e\uff0c\u6b64\u5730\u5c31\u4f5c\u4e3a\u795e\u8eaf\u9b54\u50cf\u7684\u7ef4\u62a4\u573a\u6240\u3002</p><p>\u743c\u6069\u8d44\u683c\u4e0d\u591f\uff0c\u53ea\u80fd\u5728\u5916\u56f4\u573a\u6240\u6d3b\u52a8\uff0c\u771f\u6b63\u7684\u6838\u5fc3\u533a\u57df\u662f\u4e0d\u5141\u8bb8\u8fdb\u5165\u7684\uff0c\u4ed6\u4e5f\u4e0d\u60f3\u53bb\u81ea\u627e\u9ebb\u70e6\uff0c\u5230\u6307\u5b9a\u5730\u70b9\u627e\u5230\u4e00\u4e2a\u5deb\u5996\uff0c\u4e0a\u7f34\u201c\u6750\u6599\u201d\uff0c\u7136\u540e\u5e26\u7740\u516b\u4e2a\u9ab7\u9ac5\u6b66\u58eb\u8d70\u51fa\u6765\u3002\u6765\u5230\u5916\u9762\u7684\u8857\u9053\u4e0a\uff0c\u53ea\u89c1\u6ee1\u773c\u7684\u4ea1\u7075\uff0c\u4e00\u961f\u4e00\u961f\u7684\u9ab7\u9ac5\u6b66\u58eb\u56db\u5904\u5de1\u903b\uff0c\u56e0\u4e3a\u65f6\u503c\u9ec4\u660f\uff0c\u5438\u8840\u9b3c\u4e5f\u90fd\u7eb7\u7eb7\u51fa\u6765\u6d3b\u52a8\u4e86\uff0c\u963f\u5f17\u7eb3\u65af\u4e5f\u662f\u6709\u592a\u9633\u7684\uff0c\u867d\u7136\u4e0d\u5982\u4e07\u6e0a\u5e73\u539f\u90a3\u822c\u70bd\u70c8\u8000\u773c\uff0c\u5374\u662f\u80fd\u591f\u5bf9\u5438\u8840\u9b3c\u9020\u6210\u771f\u6b63\u4f24\u5bb3\uff0c\u6240\u4ee5\u4ed6\u4eec\u767d\u5929\u90fd\u4e0d\u51fa\u52a8\uff0c\u9664\u975e\u662f\u50cf\u6b4c\u560e\u65af\u6216\u8005\u8fea\u5185\u7279\u90a3\u6837\uff0c\u5df2\u7ecf\u5230\u4e86\u4eb2\u738b\u4f4d\u9636\uff0c\u53c8\u79f0\u201c\u65e5\u884c\u8005\u201d\uff0c\u5b8c\u5168\u4e0d\u60e7\u9633\u5149\u3002\u4e0d\u8fc7\u963f\u5f17\u7eb3\u65af\u663c\u77ed\u591c\u957f\uff0c\u4e00\u5929\u5f53\u4e2d\u53ea\u6709\u4e0d\u5230\u4e09\u5206\u4e4b\u4e00\u7684\u65f6\u95f4\u5929\u4e0a\u6709\u592a\u9633\uff0c\u800c\u4e14\u5e38\u5e38\u4e4c\u4e91\u5bc6\u5e03\uff0c\u5929\u6c14\u9634\u6c89\uff0c\u6240\u4ee5\u5f71\u54cd\u4e5f\u4e0d\u662f\u5f88\u5927\u3002</p><p>\u4ee5\u5e38\u7406\u800c\u8bba\uff0c\u4ea1\u7075\u5bf9\u4e8e\u751f\u8005\u6709\u4e00\u79cd\u5929\u7136\u7684\u618e\u6068\uff0c\u8fd9\u5df2\u7ecf\u8fd1\u4e4e\u672c\u80fd\u3002\u628a\u4e00\u4e2a\u6d3b\u4eba\u653e\u8fdb\u50f5\u5c38\u7fa4\u91cc\uff0c\u4e0d\u9700\u8981\u4ea1\u7075\u5deb\u5e08\u64cd\u7eb5\uff0c\u50f5\u5c38\u5c31\u4f1a\u4e3b\u52a8\u5730\u653b\u51fb\u4ed6\u3002\u53ea\u6709\u90a3\u4e9b\u9ad8\u9636\u7684\u4ea1\u7075\uff0c\u6709\u7075\u89c9\uff0c\u6709\u610f\u8bc6\uff0c\u6709\u667a\u529b\uff0c\u80fd\u591f\u63a7\u5236\u81ea\u5df1\u7684\u672c\u80fd\u3002\u6b4c\u560e\u65af\u8fdb\u653b\u9752\u94dc\u57ce\u5821\uff0c\u5e26\u8d70\u4e86\u51e0\u4e4e\u6240\u6709\u7684\u6076\u9b54\uff0c\u5982\u4eca\u7684\u9ed1\u77f3\u57ce\u5821\u5df2\u7ecf\u53d8\u6210\u4e86\u4ea1\u7075\u4e50\u56ed\uff0c\u743c\u6069\u4e00\u4e2a\u5927\u6d3b\u4eba\u8d70\u5728\u5176\u4e2d\uff0c\u672c\u5e94\u8be5\u662f\u4f1a\u7acb\u523b\u88ab\u56f4\u653b\u7684\u3002</p><p>\u4f46\u7ed3\u679c\u6b63\u76f8\u53cd\uff0c\u743c\u6069\u4ece\u4ea1\u7075\u7fa4\u4e2d\u7a7f\u8fc7\uff0c\u5982\u5165\u65e0\u4eba\u4e4b\u5883\uff0c\u4ed6\u6240\u5230\u4e4b\u5904\uff0c\u50f5\u5c38\u9ab7\u9ac5\u4e4b\u7c7b\u7eb7\u7eb7\u9000\u907f\uff0c\u8ba9\u5f00\u9053\u8def\uff0c\u5373\u4fbf\u662f\u5438\u8840\u9b3c\u8fd9\u79cd\u9ad8\u9636\u4ea1\u7075\u4e5f\u660e\u663e\u907f\u8ba9\u4e09\u5206\u3002\u4e4b\u6240\u4ee5\u5982\u6b64\uff0c\u5012\u5e76\u975e\u4ed6\u591a\u4e48\u82f1\u660e\u795e\u6b66\uff0c\u6709\u738b\u8005\u4e4b\u6c14\uff0c\u7eaf\u7cb9\u662f\u56e0\u4e3a\u4ed6\u8eab\u4e0a\u643a\u5e26\u4e86\u90a3\u679a\u9ed1\u8272\u5934\u9aa8\u5fbd\u7ae0\u3002</p><p>\u90a3\u662f\u5965\u5580\u65af\u7684\u90aa\u5fbd\uff0c\u8574\u542b\u4e86\u4ea1\u7075\u541b\u738b\u7684\u529b\u91cf\uff0c\u5bf9\u4e8e\u8f83\u5f31\u5c0f\u7684\u4ea1\u7075\uff0c\u53ef\u4ee5\u968f\u610f\u9a71\u9063\u6307\u4f7f\uff0c\u5c31\u4eff\u4f5b\u662f\u5f3a\u5927\u7684\u4ea1\u7075\u5e08\u4e00\u822c\uff0c\u4ed6\u80fd\u591f\u547d\u4ee4\u516b\u4e2a\u9ab7\u9ac5\u6b66\u58eb\u6765\u63a8\u8f66\u5b50\u5f53\u82e6\u529b\uff0c\u9760\u7684\u5c31\u662f\u8fd9\u4e1c\u897f\uff0c\u5f53\u7136\u5bf9\u4e8e\u9ad8\u9636\u4ea1\u7075\u4f5c\u7528\u5c31\u6ca1\u8fd9\u4e48\u597d\u7528\u4e86\uff0c\u4f46\u4e5f\u6709\u4e00\u5b9a\u7a0b\u5ea6\u7684\u538b\u5236\u80fd\u529b\u3002\u7b80\u5355\u70b9\u8bf4\uff0c\u743c\u6069\u62ff\u7740\u5b83\uff0c\u53ea\u8981\u4e0d\u53bb\u62db\u60f9\u90a3\u4e9b\u5934\u76ee\u4eec\uff0c\u57fa\u672c\u4e0a\u5c31\u80fd\u5728\u9ed1\u77f3\u57ce\u91cc\u6a2a\u7740\u8d70\u4e86\u3002</p><p>\u743c\u6069\u4e0d\u559c\u6b22\u4ea1\u7075\u672f\uff0c\u65e9\u65e9\u5c31\u653e\u5f03\u4e86\uff0c\u8fd9\u4e2a\u9009\u62e9\u5012\u65e0\u6240\u8c13\u5bf9\u9519\uff0c\u4e2a\u4eba\u7231\u597d\u7f62\u4e86\uff0c\u4f46\u4e5f\u786e\u5b9e\u56e0\u6b64\u9020\u6210\u4e86\u4e00\u4e9b\u9ebb\u70e6\uff0c\u5bf9\u4ed8\u4ea1\u7075\u7684\u65f6\u5019\u4f1a\u6bd4\u8f83\u5c40\u4fc3\u3002\u5982\u4eca\u6709\u4e86\u8fd9\u679a\u90aa\u5fbd\uff0c\u53ef\u4ee5\u5f88\u5927\u7a0b\u5ea6\u4e0a\u5f25\u8865\u4e0d\u8db3\uff0c\u786e\u5b9e\u662f\u4e2a\u597d\u4e1c\u897f\u2014\u2014\u552f\u4e00\u6709\u4e9b\u9057\u61be\u7684\u662f\uff0c\u8fd9\u79cd\u5b9d\u7269\uff0c\u5e76\u975e\u662f\u901a\u8fc7\u8d2d\u4e70\u3001\u76d7\u7a83\u3001\u62a2\u52ab\u8fd9\u4e9b\u5149\u660e\u6b63\u5927\u7684\u65b9\u5f0f\u5f97\u6765\uff0c\u800c\u662f\u7528\u5973\u4eba\u6362\u6765\u7684\u3002</p><p>\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u7528\u7ea2\u8272\u5bff\u8863\u7684\u4e00\u591c\u6625\u5bb5\u6362\u6765\u7684\u3002</p><p>\u5f53\u743c\u6069\u77e5\u9053\u7ea2\u8272\u5bff\u8863\u5176\u5b9e\u5c31\u662f\u683c\u62c9\u5179\u7279\u65f6\uff0c\u4ed6\u6240\u6709\u7684\u8272\u5fc3\u81ea\u7136\u5168\u90fd\u987f\u65f6\u6253\u6d88\u4e86\u3002\u4f46\u4ed6\u77e5\u9053\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u4e0d\u7b49\u4e8e\u522b\u4eba\u4e5f\u77e5\u9053\u2014\u2014\u8bf4\u4e0d\u5b9a\u5f88\u591a\u4eba\u5c31\u7b97\u77e5\u9053\u4e5f\u4e0d\u5728\u4e4e\u3002\u6240\u4ee5\u4ed6\u4eff\u6548\u524d\u4eba\uff0c\u516c\u5f00\u62cd\u5356\u201c\u548c\u7ea2\u8272\u5bff\u8863\u6625\u5bb5\u4e00\u5ea6\u7684\u826f\u673a\u201d\uff0c\u7ed3\u679c\u573a\u9762\u706b\u7206\u5f97\u4ee4\u4ed6\u81ea\u5df1\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u4f17\u591a\u6076\u9b54\u8702\u62e5\u524d\u6765\uff0c\u5f00\u51fa\u4e86\u79cd\u79cd\u4ee4\u4eba\u5fc3\u52a8\u7684\u9ad8\u4ef7\u3002\u66f4\u4ee4\u743c\u6069\u6ca1\u60f3\u5230\u7684\u662f\uff0c\u6b4c\u560e\u65af\u5c06\u519b\u4e5f\u6765\u7ade\u6807\uff0c\u539f\u6765\u4ed6\u4e5f\u662f\u7ea2\u8272\u5bff\u8863\u77f3\u69b4\u88d9\u4e0b\u7684\u5fe0\u5b9e\u4ef0\u6155\u8005\u4e4b\u4e00\u3002\u8003\u8651\u5230\u5c06\u6765\u5f88\u957f\u4e00\u6bb5\u65f6\u95f4\u8981\u5728\u8fd9\u4e2a\u7cbe\u7075\u5438\u8840\u9b3c\u624b\u4e0b\u5e72\u6d3b\uff0c\u743c\u6069\u5f88\u8bc6\u8da3\u5730\u628a\u673a\u4f1a\u53cc\u624b\u5949\u4e0a\u3002\u6b4c\u560e\u65af\u9ad8\u5174\u4e4b\u4e0b\uff0c\u4e0d\u4f46\u4ed8\u4e86\u4e00\u7b14\u5de8\u6b3e\uff0c\u8fd8\u989d\u5916\u7ed9\u4e86\u8fd9\u679a\u90aa\u5fbd\uff0c\u4f5c\u4e3a\u5956\u8d4f\uff0c\u5e76\u4e14\u4ece\u6b64\u5bf9\u4ed6\u683c\u5916\u9752\u7750\uff0c\u7ed9\u4e88\u79cd\u79cd\u7279\u6743\uff0c\u4e8e\u662f\u53cc\u65b9\u7686\u5927\u6b22\u559c\u3002</p><p>\u201c\u5514\uff0c\u4e00\u4e2a\u516d\u6307\u4eba\u5996\uff0c\u80fd\u6362\u6765\u8fd9\u4e48\u591a\u597d\u4e1c\u897f\uff0c\u8fd9\u7b14\u4ea4\u6613\u771f\u662f\u592a\u5212\u7684\u6765\u4e86\u3002\u53ef\u60dc\u90a3\u51b3\u6597\u5927\u8d5b\u4e0d\u591a\u4e3e\u529e\u51e0\u6b21\uff0c\u5426\u5219\u4ee5\u540e\u6211\u5929\u5929\u53bb\u62ff\u51a0\u519b\u3002\u201d</p><p>\u4e00\u8fb9\u505a\u7740\u4e0d\u5207\u5b9e\u9645\u7684\u7f8e\u68a6\uff0c\u743c\u6069\u56de\u5230\u4f4f\u5904\uff0c\u8fd9\u662f\u4e00\u5ea7\u72ec\u95e8\u72ec\u9662\u7684\u4e24\u5c42\u5c0f\u697c\uff0c\u4e5f\u662f\u4ed6\u5229\u7528\u7279\u6743\u5360\u636e\u7684\uff0c\u539f\u672c\u662f\u4e00\u4e2a\u5409\u65af\u6d0b\u57fa\u5546\u4eba\u7684\u4f4f\u5904\u3002\u5409\u65af\u6d0b\u57fa\u4eba\u548c\u4eba\u7c7b\u751f\u6d3b\u4e60\u6027\u8fd1\u4f3c\uff0c\u5c45\u4f4f\u73af\u5883\u4e5f\u5dee\u4e0d\u591a\uff0c\u5bb9\u6613\u9002\u5e94\u3002\u4ed6\u547d\u4ee4\u9ab7\u9ac5\u6b66\u58eb\u56db\u9762\u7ad9\u5c97\uff0c\u81ea\u5df1\u8fdb\u4e86\u5ba2\u5385\u4e0a\u697c\uff0c\u63a8\u5f00\u623f\u95e8\uff0c\u53d1\u73b0\u6885\u83f2\u65af\u6b63\u5750\u5728\u5e8a\u5934\uff0c\u9759\u9759\u5730\u5728\u50cf\u5728\u601d\u8003\u7740\u4ec0\u4e48\uff0c\u624b\u8fb9\u653e\u7740\u672c\u4e66\u3002\u743c\u6069\u8d70\u5230\u65c1\u8fb9\uff0c\u8f7b\u8f7b\u62b1\u7740\uff0c\u5728\u5979\u989d\u5934\u4e0a\u8f7b\u8f7b\u4eb2\u4e86\u4e00\u4e0b\u3002</p><p>\u201c\u60f3\u4ec0\u4e48\u5462\uff0c\u8fd9\u4e48\u5165\u795e\u3002\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u6885\u83f2\u65af\u4ef0\u8d77\u8138\u5fae\u5fae\u7b11\u7740\uff0c\u201c\u60f3\u4f60\u4e86\u3002\u201d</p><p>\u743c\u6069\u4f4e\u5934\u543b\u4e0a\u5979\u7684\u6a31\u5507\uff0c\u542e\u5438\u67d4\u6ed1\u7684\u820c\u5c16\uff0c\u54c1\u5c1d\u7740\u5c11\u5973\u53e3\u4e2d\u7565\u5e26\u767e\u5408\u82ac\u82b3\u7684\u9999\u751c\u6d25\u6db2\uff0c\u4e0d\u89c9\u4fbf\u6709\u4e9b\u52a8\u60c5\u3002\u8fd9\u51e0\u4e2a\u6708\u6765\u4ed6\u4e00\u76f4\u5fd9\u4e8e\u953b\u70bc\uff0c\u6bcf\u65e5\u7684\u751f\u6d3b\u5c31\u662f\u65e0\u4f11\u6b62\u5730\u6740\u622e\uff0c\u51e0\u4e4e\u90fd\u6ca1\u548c\u6885\u83f2\u65af\u4eb2\u8fd1\u4e86\u3002\u6b63\u8981\u89e3\u5979\u7684\u8863\u6263\uff0c\u7a81\u7136\u8eab\u540e\u4f20\u6765\u8f7b\u8f7b\u7684\u6572\u95e8\u58f0\u3002</p><p>\u201c\u62b1\u6b49\uff0c\u6253\u6270\u4e24\u4f4d\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u6709\u91cd\u8981\u7684\u6d88\u606f\uff0c\u524d\u7ebf\u6218\u8d25\u4e86\u3002\u201d</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u201c\u6218\u8d25\u4e86\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u6076\u9b54\u519b\u961f\u8d25\u9000\uff0c\u73b0\u5728\u6b63\u5728\u8fd4\u56de\u9ed1\u77f3\u57ce\u7684\u9014\u4e2d\uff0c\u6b4c\u560e\u65af\u91cd\u4f24\u3002\u201d</p></article>"}}}]);
