(window.webpackJsonp=window.webpackJsonp||[]).push([[1556],{1579:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u8282 \u7ffb\u724c",content:"<article><h2>\u7b2c\u4e94\u8282 \u7ffb\u724c</h2><p>\u5f53\u6b4c\u560e\u65af\u770b\u89c1\u5f25\u8d5b\u4e9a\u7684\u65f6\u5019\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u8fd9\u6b21\u9047\u4e0a\u5927\u9ebb\u70e6\u4e86\u3002</p><p>\u4f5c\u4e3a\u7edf\u5e05\uff0c\u81ea\u5df1\u5e76\u4e0d\u79f0\u804c\uff0c\u8fd9\u70b9\u6b4c\u560e\u65af\u5f88\u6e05\u695a\uff0c\u4ed6\u6216\u8bb8\u4e0d\u591f\u806a\u660e\uff0c\u4f46\u7edd\u4e0d\u7f3a\u4e4f\u81ea\u77e5\u4e4b\u660e\uff1b\u4f46\u5728\u4e2a\u4eba\u7684\u6b66\u52c7\u4e0a\uff0c\u4ed6\u6781\u6709\u81ea\u4fe1\uff0c\u51e0\u5343\u5e74\u575a\u6301\u4e0d\u8f8d\u7684\u523b\u82e6\u953b\u70bc\uff0c\u5c06\u6b66\u6280\u548c\u7cbe\u7075\u9b54\u6cd5\u5b8c\u7f8e\u5730\u878d\u4e3a\u4e00\u4f53\uff0c\u4ed6\u7684\u529b\u91cf\u53ef\u4ee5\u5ab2\u7f8e\u5de8\u9f99\uff0c\u901f\u5ea6\u80dc\u8fc7\u730e\u8c79\uff0c\u53cd\u5e94\u5feb\u82e5\u96f7\u9706\u3002\u66f4\u91cd\u8981\u7684\u662f\uff0c\u4ed6\u662f\u5438\u8840\u9b3c\uff0c\u800c\u4e14\u5df2\u7ecf\u664b\u5347\u5230\u4e86\u4eb2\u738b\uff0c\u8fd9\u610f\u5473\u7740\u4ed6\u80fd\u5728\u5f3a\u70c8\u7684\u65e5\u5149\u4e0b\u884c\u52a8\u81ea\u5982\uff0c\u4e0d\u4f1a\u53d7\u5230\u4efb\u4f55\u4f24\u5bb3\u548c\u538b\u5236\uff1b\u610f\u5473\u7740\u4ed6\u53ea\u8981\u5934\u9885\u548c\u5fc3\u810f\u4e0d\u53d7\u4f24\u5bb3\uff0c\u54ea\u6015\u56db\u80a2\u5168\u65ad\u751a\u81f3\u534a\u4e2a\u8eab\u4f53\u88ab\u780d\u65ad\u4e86\u4e5f\u80fd\u8fc5\u901f\u590d\u751f\u3002\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u8bf4\uff0c\u4ed6\u5df2\u7ecf\u8fd1\u4e4e\u662f\u4e0d\u6b7b\u4e4b\u8eab\u3002</p><p>\u6bd5\u7adf\uff0c\u80fd\u591f\u62c5\u4efb\u5965\u5580\u65af\u7684\u4f8d\u536b\u957f\u7684\u4eba\u7269\uff0c\u53c8\u5c82\u662f\u5bfb\u5e38\u4e4b\u8f88\u3002</p><p>\u4f46\u5f25\u8d5b\u4e9a\u662f\u66e6\u5929\u4f7f\u3002</p><p>\u5e76\u672a\u5815\u843d\u7684\u66e6\u5929\u4f7f\u3002</p><p>\u4e0b\u5c42\u754c\u7684\u90aa\u9b54\u90fd\u77e5\u9053\uff0c\u62dc\u5c14\u624b\u4e0b\u6709\u4e00\u7fa4\u7531\u5815\u5929\u4f7f\u7ec4\u6210\u7684\u201c\u9eef\u4e4b\u7ffc\u201d\uff0c\u662f\u4ed6\u624b\u4e2d\u7684\u738b\u724c\u6218\u529b\u3002\u4f46\u9664\u6b64\u4e4b\u5916\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u4f20\u8bf4\uff1a\u62dc\u5c14\u8eab\u8fb9\u6709\u4e00\u4f4d\u53eb\u505a\u5f25\u8d5b\u4e9a\u7684\u66e6\u5929\u4f7f\uff0c\u5e73\u5e38\u4ece\u4e0d\u73b0\u8eab\uff0c\u53ea\u5728\u5173\u952e\u65f6\u523b\u4e3a\u4ed6\u51fa\u6218\uff0c\u6267\u884c\u4efb\u52a1\u3002\u548c\u201c\u9eef\u4e4b\u7ffc\u201d\u7684\u5815\u5929\u4f7f\u4eec\u4e0d\u540c\u7684\u662f\uff0c\u5f25\u8d5b\u4e9a\u5e76\u672a\u5815\u843d\uff0c\u5979\u7684\u7fbd\u7ffc\u4f9d\u65e7\u6d01\u767d\u65e0\u6687\uff0c\u51b0\u5251\u4e0a\u4f9d\u65e7\u5723\u5149\u73af\u7ed5\uff0c\u5979\u7684\u795e\u660e\u4f9d\u65e7\u8d50\u4e88\u795e\u529b\uff0c\u8ba9\u5979\u5728\u6218\u573a\u4e0a\u6218\u65e0\u4e0d\u80dc\u3002</p><p>\u51e0\u4e4e\u6240\u6709\u7684\u6076\u9b54\u5bf9\u8fd9\u4e2a\u4f20\u8a00\u90fd\u55e4\u4e4b\u4ee5\u9f3b\uff0c\u5373\u4fbf\u662f\u9b54\u9b3c\uff0c\u5bf9\u4e8e\u8fd9\u79cd\u8bf4\u6cd5\u4e5f\u662f\u62b1\u7740\u8c28\u614e\u7684\u4fdd\u7559\u6001\u5ea6\uff0c\u66f4\u591a\u5f53\u4f5c\u662f\u62dc\u5c14\u5927\u516c\u7235\u7684\u4e00\u79cd\u5ba3\u4f20\u624b\u6bb5\u3002\u9996\u5148\uff0c\u5b83\u4eec\u4e0d\u76f8\u4fe1\u4f1a\u6709\u66e6\u5929\u4f7f\u4f1a\u4e3a\u62dc\u5c14\u6548\u529b\u3002\u5982\u679c\u8bf4\u65e0\u5c3d\u6df1\u6e0a\u548c\u4e5d\u5c42\u5730\u72f1\u662f\u591a\u5143\u5b87\u5b99\u7684\u4e0b\u5c42\u754c\uff0c\u90a3\u4e48\u5929\u5802\u5c71\u5c31\u662f\u81f3\u9ad8\u81f3\u4e0a\u7684\u4f4d\u9762\uff0c\u4ee5\u6b63\u4e49\u4e4b\u795e\u63d0\u5c14\u4e3a\u9996\u7684\u795e\u5723\u8bf8\u795e\u5c45\u4f4f\u5176\u4e2d\u3002\u5929\u5802\u5c71\u4e03\u9636\u795e\u4f8d\uff0c\u66e6\u5929\u4f7f\u4f4d\u5c45\u6700\u4e0a\uff0c\u662f\u8fd1\u4f3c\u795e\u660e\u7684\u5b58\u5728\uff0c\u8fd9\u6837\u7684\u4eba\u7269\u4f1a\u542c\u4ece\u62dc\u5c14\u9a71\u9063\uff1f\u867d\u8bf4\u62dc\u5c14\u5728\u8150\u5316\u5929\u4f7f\u3001\u5f15\u8bf1\u5815\u843d\u8fd9\u65b9\u9762\u786e\u5b9e\u5f88\u6709\u5929\u8d4b\uff0c\u80fd\u591f\u7ec4\u5efa\u8d77\u4e00\u652f\u201c\u9eef\u4e4b\u7ffc\u201d\uff0c\u4f46\u90fd\u662f\u4e9b\u4e2d\u4f4e\u9636\u7684\u5929\u4f7f\uff0c\u519b\u56e2\u957f\u74e6\u5229\u65af\u4e5f\u4e0d\u8fc7\u5c31\u662f\u4e00\u4f4d\u53f7\u89d2\u5929\u4f7f\uff0c\u4f4d\u9636\u8ddd\u79bb\u66e6\u5929\u4f7f\u8fd8\u5dee\u4e86\u4e24\u4e2a\u5c42\u7ea7\u5462\u3002\u5176\u6b21\uff0c\u9000\u4e00\u6b65\u8bf4\uff0c\u7eb5\u7136\u66e6\u5929\u4f7f\u771f\u7684\u80af\u4e3a\u62dc\u5c14\u6548\u529b\uff0c\u90a3\u4e48\u5979\u53c8\u5982\u4f55\u80fd\u591f\u4f9d\u65e7\u4fdd\u6301\u795e\u5723\uff0c\u8fd9\u5b8c\u5168\u8fdd\u80cc\u5e38\u8bc6\uff0c\u5728\u5386\u53f2\u4e0a\u662f\u7edd\u65e0\u5148\u4f8b\u7684\u3002</p><p>\u6b4c\u560e\u65af\u4e5f\u4e0d\u76f8\u4fe1\u8fd9\u79cd\u8352\u8bde\u7684\u4e8b\u60c5\uff0c\u4ed6\u8ba4\u4e3a\u8fd9\u4e0d\u8fc7\u662f\u62dc\u5c14\u7684\u53c8\u4e00\u4e2a\u9634\u8c0b\uff0c\u6216\u8005\u4e00\u4e2a\u865a\u5f20\u58f0\u52bf\uff0c\u4e00\u79cd\u62ac\u9ad8\u81ea\u8eab\u80fd\u529b\uff0c\u589e\u5f3a\u795e\u79d8\u611f\u7684\u7edf\u6cbb\u624b\u6bb5\u3002\u6b64\u6b21\u8840\u6218\u4e00\u6765\uff0c\u5df2\u7ecf\u6253\u4e86\u56db\u4e2a\u6708\uff0c\u9eef\u4e4b\u7ffc\u519b\u56e2\u4e5f\u51e0\u6b21\u5728\u6218\u573a\u4e0a\u906d\u9047\uff0c\u4f46\u5374\u4ece\u6ca1\u89c1\u5230\u90a3\u4f4d\u4f20\u8bf4\u4e2d\u7684\u66e6\u5929\u4f7f\uff0c\u8fd9\u66f4\u8bc1\u660e\u4e86\u6574\u4e2a\u4f20\u95fb\u7684\u5b50\u865a\u4e4c\u6709\uff0c\u5426\u5219\u62dc\u5c14\u4e3a\u4f55\u4e0d\u6d3e\u5979\u51fa\u6218\u3002\u4e00\u4f4d\u66e6\u5929\u4f7f\uff0c\u867d\u7136\u4e0d\u8db3\u4ee5\u5bf9\u6297\u795e\u8eaf\u9b54\u50cf\uff0c\u4f46\u5374\u80fd\u62b5\u5f97\u4e0a\u5c0f\u534a\u4e2a\u9eef\u4e4b\u7ffc\u519b\u56e2\u4e86\u3002</p><p>\u7136\u800c\u2026\u2026\u73b0\u5728\u4e8b\u5b9e\u6446\u5728\u9762\u524d\u3002</p><p>\u4f5c\u4e3a\u5438\u8840\u9b3c\uff0c\u6b4c\u560e\u65af\u6e05\u695a\u5730\u611f\u5e94\u5230\u4e86\u5bf9\u65b9\u8eab\u4e0a\u51db\u51db\u8feb\u4eba\u7684\u795e\u5723\u6c14\u606f\uff0c\u5bd2\u51b0\u53cc\u624b\u5251\u4e0a\u91d1\u5149\u95ea\u70c1\uff0c\u8000\u773c\u751f\u8f89\uff0c\u8ba9\u4ed6\u8fd9\u4f4d\u201c\u4eb2\u738b\u201d\u90fd\u4e0d\u6562\u6b63\u89c6\u3002\u7fbd\u7ffc\u6216\u8bb8\u53ef\u4ee5\u6f02\u767d\uff0c\u5bb9\u8c8c\u6216\u8bb8\u53ef\u4ee5\u53d8\u5316\uff0c\u4f46\u8fd9\u81f3\u7eaf\u81f3\u6b63\u7684\u5723\u5149\uff0c\u5374\u662f\u65e0\u8bba\u5982\u4f55\u4e5f\u4f2a\u9020\u4e0d\u6765\u7684\u3002\u66f4\u4f55\u51b5\uff0c\u521a\u624d\u4e00\u7bad\u5c06\u4e24\u540d\u6b7b\u4ea1\u9a91\u58eb\u5316\u4f5c\u98de\u7070\uff0c\u9664\u4e86\u66e6\u5929\u4f7f\u7684\u77ac\u6740\u7bad\uff0c\u8fd8\u6709\u4ec0\u4e48\u80fd\u591f\u529e\u5230\uff1f</p><p>\u65e0\u6687\u53bb\u601d\u8003\u6700\u795e\u5723\u7684\u66e6\u5929\u4f7f\u4e3a\u4ec0\u4e48\u4f1a\u4e3a\u9b54\u9b3c\u6548\u529b\uff0c\u6b4c\u560e\u65af\u81ea\u9ab7\u9ac5\u6218\u9a6c\u4e0a\u817e\u8eab\u800c\u8d77\uff0c\u78a7\u706b\u83b9\u83b9\u7684\u957f\u5200\u95ea\u7535\u822c\u5411\u524d\u65a9\u51fa\u3002\u8eab\u540e\u7684\u6b7b\u4ea1\u9a91\u58eb\u9f50\u9f50\u6d8c\u4e0a\uff0c\u5341\u51e0\u67c4\u957f\u67aa\u5411\u66e6\u5929\u4f7f\u540c\u65f6\u523a\u53bb\uff0c\u4ed6\u4eec\u751f\u524d\u90fd\u662f\u6770\u51fa\u7684\u6218\u58eb\uff0c\u6b7b\u4ea1\u5e76\u6ca1\u6709\u524a\u5f31\u529b\u91cf\uff0c\u53cd\u800c\u4ee4\u4ed6\u4eec\u66f4\u52a0\u5f3a\u5927\uff0c\u65e0\u6240\u754f\u60e7\u3002</p><p>\u900f\u660e\u7684\u5149\u7f69\u5728\u66e6\u5929\u4f7f\u7684\u8eab\u524d\u6d6e\u73b0\uff0c\u6321\u4f4f\u4e86\u6240\u6709\u653b\u51fb\u3002\u516d\u5bf9\u5de8\u5927\u7684\u900f\u660e\u5149\u7ffc\u81ea\u5979\u80cc\u540e\u91cd\u91cd\u8212\u5c55\uff0c\u539f\u672c\u9634\u973e\u7684\u5929\u7a7a\u5239\u90a3\u95f4\u53d8\u5f97\u660e\u4eae\u8d77\u6765\uff0c\u6c89\u6c89\u4e4c\u4e91\u5411\u56db\u5468\u6025\u901f\u9000\u6563\uff0c\u9732\u51fa\u9752\u84dd\u8272\u7684\u7a7a\u6d1e\uff0c\u707f\u70c2\u7684\u91d1\u8272\u5149\u8f89\u81ea\u5176\u4e2d\u503e\u6cfb\u800c\u4e0b\u3002\u66e6\u5929\u4f7f\u53cc\u624b\u63e1\u5251\uff0c\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u7136\u540e\u5288\u51fa\u3002</p><p>\u7834\u90aa\u65a9\uff01</p><p>\u91d1\u8272\u5723\u5149\u81ea\u5bd2\u51b0\u5251\u4e0a\u8f70\u7136\u7efd\u653e\uff0c\u6c79\u6d8c\u9f13\u8361\uff0c\u5982\u6f6e\u6c34\u822c\u6f8e\u6e43\u800c\u6765\uff0c\u77ac\u95f4\u5c06\u4ea1\u7075\u6df9\u6ca1\u3002\u8ddd\u79bb\u6700\u8fd1\u7684\u56db\u4e2a\u6b7b\u4ea1\u9a91\u58eb\u9996\u5f53\u5176\u51b2\uff0c\u8eaf\u4f53\u3001\u94e0\u7532\u3001\u5750\u9a91\u77ac\u95f4\u5d29\u6563\uff0c\u5316\u4f5c\u98de\u7070\uff0c\u5176\u4f59\u6b7b\u4ea1\u9a91\u58eb\u7eb7\u7eb7\u540e\u9000\uff0c\u7eb5\u7136\u662f\u4ee5\u82f1\u52c7\u5fe0\u8bda\u8457\u79f0\u7684\u4ed6\u4eec\uff0c\u4e5f\u672c\u80fd\u5730\u611f\u53d7\u5230\u4e86\u754f\u60e7\uff0c\u90a3\u662f\u6765\u81ea\u5929\u5802\u7684\u6012\u706b\uff0c\u795e\u660e\u7684\u5a01\u4e25\uff0c\u662f\u4ea1\u7075\u7684\u514b\u661f\u3002</p><p>\u78a7\u706b\u957f\u5200\u6084\u7136\u788e\u88c2\uff0c\u6b4c\u560e\u65af\u539f\u672c\u5c31\u82cd\u767d\u7684\u8138\u8272\u9aa4\u7136\u95f4\u66f4\u52a0\u96be\u770b\u4e86\u51e0\u5206\uff0c\u4ed6\u9661\u7136\u53d1\u51fa\u4e00\u58f0\u523a\u8033\u7684\u5c16\u5578\uff0c\u8840\u8272\u7684\u96fe\u6c14\u4ece\u6bcf\u4e2a\u6bdb\u5b54\u4e2d\u6cdb\u8d77\uff0c\u51dd\u805a\u6210\u76fe\uff0c\u6321\u5728\u8eab\u524d\u3002\u5723\u5149\u7167\u5c04\u5728\u8840\u76fe\u4e0a\uff0c\u53d1\u51fa\u55e4\u55e4\u58f0\u54cd\uff0c\u4eff\u4f5b\u6cb8\u6c34\u878d\u51b0\uff0c\u9877\u523b\u878d\u5316\u3002\u4f46\u6b4c\u560e\u65af\u501f\u7740\u8fd9\u4e00\u963b\u4e4b\u673a\u6025\u6025\u540e\u9000\uff0c\u5df2\u7ecf\u8131\u79bb\u4e86\u5723\u5149\u7684\u7167\u8000\u8303\u56f4\uff0c\u7ffb\u8eab\u5f80\u4e0b\u5760\u53bb\u3002</p><p>\u773c\u770b\u5c31\u8981\u8e0f\u4e0a\u5730\u9762\uff0c\u4e00\u9053\u91d1\u8272\u5229\u77e2\u7834\u7a7a\u5c04\u6765\uff0c\u66e6\u5929\u4f7f\u518d\u5ea6\u53d1\u51fa\u4e86\u77ac\u6740\u7bad\u3002</p><p>\u8fd9\u4e00\u7bad\u8fc5\u82e5\u96f7\u9706\uff0c\u6b4c\u560e\u65af\u7684\u6b66\u5668\u5df2\u7ecf\u88ab\u6467\u6bc1\uff0c\u65e0\u4ece\u683c\u6321\uff0c\u4fbf\u662f\u5316\u8760\u9041\u9003\u4e5f\u5df2\u7ecf\u6765\u4e0d\u53ca\u3002\u4ed6\u5927\u53eb\u4e00\u58f0\uff0c\u5df2\u7ecf\u88ab\u91d1\u7bad\u5c04\u4e2d\uff0c\u5723\u5149\u5feb\u901f\u8513\u5ef6\u800c\u6765\uff0c\u5c06\u4ed6\u7684\u6574\u6761\u53f3\u81c2\u5316\u4f5c\u98de\u7070\u3002</p><p>\u5438\u8840\u9b3c\u90fd\u6709\u8d85\u5f3a\u7684\u81ea\u6108\u80fd\u529b\uff0c\u957f\u8001\u4f4d\u9636\u7684\u5438\u8840\u9b3c\uff0c\u5df2\u7ecf\u8db3\u4ee5\u65ad\u80a2\u91cd\u751f\uff0c\u5230\u4e86\u4eb2\u738b\u4f4d\u9636\u8fd9\u79cd\u5f02\u80fd\u66f4\u52a0\u5f3a\u5927\u60ca\u4eba\u3002\u82e5\u5728\u5f80\u65e5\uff0c\u65ad\u4e86\u4e00\u53ea\u80f3\u818a\uff0c\u6b4c\u560e\u65af\u53ea\u9700\u51e0\u5206\u949f\u5c31\u80fd\u518d\u5ea6\u751f\u957f\u51fa\u6765\uff0c\u6700\u591a\u559d\u70b9\u8840\u6d46\u8865\u5145\u80fd\u91cf\u3002\u7136\u800c\u8fd9\u4e00\u6b21\uff0c\u4ed6\u7684\u81ea\u6108\u5f02\u80fd\u5b8c\u5168\u65e0\u6cd5\u53d1\u6325\u4f5c\u7528\u3002</p><p>\u201c\u8be5\u6b7b\uff01\u201d</p><p>\u8eab\u7ecf\u767e\u6218\u7684\u953b\u70bc\uff0c\u8ba9\u6b4c\u560e\u65af\u8fc5\u901f\u51b7\u9759\u4e0b\u6765\uff0c\u867d\u7136\u610f\u5916\u649e\u4e0a\u66e6\u5929\u4f7f\u8fd9\u79cd\u5f3a\u654c\uff0c\u5931\u53bb\u4e86\u4e00\u53ea\u80f3\u818a\uff0c\u4f46\u5e76\u4e0d\u610f\u5473\u7740\u81ea\u5df1\u5931\u8d25\u3002\u4ed6\u4f9d\u65e7\u610f\u8bc6\u6e05\u9192\uff0c\u53ef\u4ee5\u53d1\u53f7\u65bd\u4ee4\uff0c\u4f5c\u4e3a\u4e3b\u5e05\u8fd9\u5df2\u7ecf\u8db3\u591f\u3002</p><p>\u8fd1\u767e\u540d\u6b7b\u4ea1\u9a91\u58eb\u817e\u7a7a\u800c\u8d77\uff0c\u671d\u66e6\u5929\u4f7f\u6251\u53bb\u3002\u6b4c\u560e\u65af\u7ffb\u8eab\u843d\u5230\u5730\u9762\uff0c\u5feb\u901f\u5224\u65ad\u7740\u5c40\u52bf\uff0c\u795e\u8eaf\u9b54\u50cf\u6b63\u5728\u88ab\u5815\u5929\u4f7f\u548c\u70bc\u72f1\u708e\u9f99\u56f4\u653b\uff0c\u4f46\u5927\u91cf\u7684\u6b7b\u4ea1\u9a91\u58eb\u4e5f\u5df2\u7ecf\u8d76\u5230\uff0c\u5c40\u9762\u7a33\u5360\u4e0a\u98ce\u3002\u4ea1\u7075\u5927\u519b\u5df2\u7ecf\u5b8c\u5168\u96c6\u7ed3\u57ce\u4e0b\uff0c\u53ea\u8981\u5360\u636e\u9752\u94dc\u57ce\u5821\uff0c\u5c31\u610f\u5473\u7740\u5360\u636e\u4e86\u963f\u5f17\u7eb3\u65af\uff0c\u201c\u6df7\u4e71\u201d\u51fb\u8d25\u4e86\u201c\u79e9\u5e8f\u201d\uff0c\u6574\u4e2a\u5730\u72f1\u7b2c\u4e00\u5c42\u7684\u4f4d\u9762\u89c4\u5219\u90fd\u4f1a\u968f\u4e4b\u503e\u659c\u6539\u53d8\uff0c\u6076\u9b54\u5927\u519b\u5c06\u4f1a\u83b7\u5f97\u66f4\u5927\u7684\u4f18\u52bf\u3002</p><p>\u4ed6\u6b63\u8981\u4e0b\u4ee4\u653b\u57ce\uff0c\u9661\u7136\u80cc\u540e\u8f70\u7136\u5de8\u54cd\uff0c\u811a\u4e0b\u90fd\u5267\u70c8\u6447\u6643\u8d77\u6765\uff0c\u6b4c\u560e\u65af\u4e00\u6014\u4e4b\u4e0b\uff0c\u672c\u80fd\u5730\u4ee5\u4e3a\u662f\u5730\u9707\uff0c\u4f46\u7d27\u63a5\u7740\u4fbf\u53d1\u73b0\u4e0d\u5bf9\uff0c\u8fd9\u5de8\u54cd\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\uff0c\u8fde\u7ef5\u4e0d\u65ad\uff0c\u8d8a\u6765\u8d8a\u5bc6\u96c6\uff0c\u5230\u540e\u6765\u5df2\u7ecf\u9707\u8033\u6b32\u804b\u3002\u6c14\u6d6a\u7ffb\u6eda\u4e4b\u4e0b\uff0c\u8fde\u4ed6\u90fd\u7ad9\u7acb\u4e0d\u7a33\uff0c\u6454\u5012\u5728\u5730\u3002\u5438\u8840\u9b3c\u5927\u60ca\uff0c\u4e3e\u76ee\u5f20\u671b\uff0c\u53ea\u89c1\u9752\u94dc\u57ce\u5821\u524d\u7684\u6218\u573a\u4e0a\u9ed1\u70df\u817e\u817e\uff0c\u70c8\u706b\u51b2\u5929\uff0c\u5230\u5904\u90fd\u5728\u53d1\u51fa\u8fde\u7ef5\u5de8\u54cd\uff0c\u4eff\u4f5b\u4e00\u5ea7\u5e9e\u5927\u7684\u706b\u5c71\u7fa4\u6b63\u5728\u55b7\u53d1\u3002\u6210\u5343\u4e0a\u4e07\u7684\u4ea1\u7075\u5927\u519b\uff0c\u8f6c\u773c\u95f4\u9677\u5165\u70bc\u72f1\u706b\u6d77\u3002</p><p>\u201c\u8fd9\u2026\u2026\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u6b4c\u560e\u65af\u65e2\u60ca\u4e14\u9a87\uff0c\u6b63\u4e0d\u77e5\u6240\u4ee5\uff0c\u9661\u7136\u80cc\u540e\u5fae\u5fae\u75bc\u75db\uff0c\u7d27\u63a5\u7740\u4fbf\u662f\u4e00\u9635\u5934\u6655\u76ee\u7729\u88ad\u6765\uff0c\u51e0\u4e4e\u90fd\u8981\u7acb\u8db3\u4e0d\u7a33\u3002</p><p>\u203b\u203b\u203b</p><p>\u501f\u52a9\u8428\u739b\u65af\u7279\u4e4b\u529b\uff0c\u7528\u6539\u9020\u8fc7\u7684\u5c0f\u8303\u56f4\u9f99\u72c2\u8ff7\u9501\u9a71\u52a8\u70bc\u72f1\u706b\u9f99\uff0c\u56f4\u653b\u795e\u8eaf\u9b54\u50cf\uff0c\u8fd9\u662f\u62dc\u5c14\u7684\u7b2c\u4e00\u5f20\u724c\uff1b\u5438\u5f15\u6b4c\u560e\u65af\u4eb2\u8eab\u524d\u6765\uff0c\u4ee5\u66e6\u5929\u4f7f\u5f25\u8d5b\u4e9a\u72d9\u51fb\uff0c\u8fd9\u662f\u62dc\u5c14\u7684\u7b2c\u4e8c\u5f20\u724c\u3002</p><p>\u4f46\u6076\u9b54\u519b\u5e76\u4e0d\u4ec5\u4ec5\u53ea\u6709\u6b4c\u560e\u65af\u548c\u795e\u8eaf\u9b54\u50cf\u3002</p><p>\u6b4c\u560e\u65af\u53ea\u662f\u4e2a\u5e73\u5eb8\u7684\u7edf\u5e05\uff0c\u6709\u4ed6\u6ca1\u4ed6\u533a\u522b\u4e0d\u5927\uff0c\u4f55\u51b5\u5373\u4fbf\u662f\u5f25\u8d5b\u4e9a\u4e5f\u6ca1\u628a\u63e1\u80fd\u591f\u4e00\u4e3e\u51fb\u6740\u3002\u795e\u8eaf\u9b54\u50cf\u867d\u7136\u6700\u68d8\u624b\uff0c\u4f46\u5e76\u4e0d\u662f\u552f\u4e00\uff0c\u6ca1\u4e86\u5b83\uff0c\u6076\u9b54\u519b\u4f9d\u65e7\u8fd8\u662f\u5360\u636e\u4e86\u7edd\u5bf9\u7684\u5175\u529b\u4f18\u52bf\uff0c\u7167\u6837\u80fd\u591f\u8f7b\u6613\u653b\u4e0b\u9752\u94dc\u57ce\u5821\u3002</p><p>\u6240\u4ee5\u62dc\u5c14\u7ffb\u51fa\u4e86\u7b2c\u4e09\u5f20\u724c\u3002</p><p>\u706b\u836f\u3002</p><p>\u5341\u516d\u5e74\u524d\uff0c\u7269\u8d28\u754c\u7684\u70bc\u91d1\u5deb\u5e08\u4eec\u53d1\u660e\u4e86\u53d1\u70df\u706b\u836f\uff0c\u751a\u81f3\u7814\u5236\u51fa\u4e86\u706b\u70ae\uff08\u4f8b\u5982\u9634\u9b42\u57ce\uff09\u548c\u706b\u67aa\uff08\u4f8b\u5982\u9093\u80af\uff09\uff0c\u56e0\u4e3a\u6280\u672f\u8fd8\u4e0d\u6210\u719f\uff0c\u77e5\u6653\u7684\u4eba\u4e0d\u591a\u3002\u6076\u9b54\u4eec\u57fa\u672c\u90fd\u4e0d\u5173\u5fc3\u7269\u8d28\u754c\uff0c\u6210\u5929\u5fd9\u4e8e\u780d\u6765\u6740\u53bb\uff0c\u5bf9\u6b64\u81ea\u7136\u662f\u4e00\u65e0\u6240\u77e5\uff0c\u4f46\u9b54\u9b3c\u5374\u4e0d\u540c\uff0c\u4ed6\u4eec\u5728\u7269\u8d28\u754c\u6d3b\u52a8\u9891\u7e41\uff0c\u5f15\u8bf1\u51e1\u4eba\u7b7e\u8ba2\u7075\u9b42\u5951\u7ea6\uff0c\u6536\u53d6\u5815\u843d\u7075\u9b42\u66f4\u662f\u5730\u72f1\u6700\u8981\u7d27\u7684\u4e00\u9879\u201c\u4e1a\u52a1\u201d\uff0c\u62dc\u5c14\u624b\u4e0b\u4e09\u5927\u5ae1\u7cfb\u519b\u56e2\u4e2d\u7684\u201c\u9ed1\u73ab\u7470\u201d\u5c31\u4e13\u95e8\u8d1f\u8d23\u6b64\u4e8b\uff0c\u5bf9\u4e8e\u51e1\u4eba\u7684\u4fe1\u606f\u52a8\u6001\u975e\u5e38\u5173\u6ce8\u3002\u5f53\u62dc\u5c14\u5f97\u77e5\u53d1\u70df\u706b\u836f\u7684\u6d88\u606f\u4e4b\u540e\uff0c\u7acb\u523b\u6d3e\u624b\u4e0b\u5927\u91cf\u8d2d\u4e70\uff0c\u4e0d\u9650\u6570\u91cf\uff0c\u800c\u4e14\u8fd8\u5f04\u6765\u914d\u65b9\uff0c\u65e5\u591c\u7814\u5236\uff0c\u867d\u7136\u6ca1\u80fd\u53d1\u660e\u51fa\u4ec0\u4e48\u66f4\u5148\u8fdb\u7684\u4e1c\u897f\uff0c\u4f46\u7c7b\u4f3c\u5730\u96f7\u70b8\u5f39\u7684\u73a9\u610f\u8fd8\u662f\u5f04\u51fa\u4e86\u5f88\u591a\uff0c\u5728\u4ed3\u5e93\u91cc\u5806\u79ef\u5982\u5c71\u3002\u62dc\u5c14\u7684\u624b\u4e0b\u4eec\u4e2a\u4e2a\u63d0\u5fc3\u540a\u80c6\uff0c\u751f\u6015\u54ea\u5929\u7206\u70b8\uff0c\u628a\u6574\u4e2a\u9752\u94dc\u57ce\u5821\u90fd\u6bc1\u4e86\uff0c\u5e78\u597d\u53d1\u70df\u706b\u836f\u662f\u9b54\u6cd5\u9020\u7269\uff0c\u4e0d\u662f\u7b80\u5355\u5730\u70b9\u706b\u5c31\u80fd\u70b8\u7684\uff0c\u5b89\u5168\u6027\u5012\u4e0d\u7528\u592a\u62c5\u5fc3\u3002</p><p>\u6b64\u6b21\u8840\u6218\uff0c\u62dc\u5c14\u773c\u89c1\u5bf9\u624b\u5f04\u51fa\u795e\u8eaf\u9b54\u50cf\u8fd9\u79cd\u53d8\u6001\u602a\u7269\u6765\uff0c\u65e0\u6cd5\u62b5\u654c\uff0c\u53ea\u80fd\u6536\u62e2\u5175\u529b\uff0c\u8282\u8282\u540e\u9000\uff0c\u51c6\u5907\u5728\u9752\u94dc\u57ce\u5821\u4e4b\u4e0b\u51b3\u6218\uff0c\u4e00\u4e3e\u7ffb\u76d8\u3002\u4ed6\u628a\u6240\u6709\u7684\u706b\u836f\u5168\u90fd\u57cb\u5728\u57ce\u5821\u524d\u7684\u8352\u91ce\u4e2d\uff0c\u7b49\u5230\u4ea1\u7075\u5927\u519b\u5728\u57ce\u4e0b\u96c6\u7ed3\uff0c\u51c6\u5907\u653b\u57ce\u65f6\uff0c\u4fbf\u7a81\u7136\u5f15\u7206\u4e86\u70b8\u836f\u3002</p><p>\u4e00\u679a\u53d1\u70df\u706b\u836f\u5236\u4f5c\u7684\u5730\u96f7\u5a01\u529b\u5e76\u4e0d\u5927\uff0c\u4f46\u6570\u5341\u4e07\u679a\u5806\u79ef\u5728\u4e00\u8d77\uff0c\u5a01\u529b\u7740\u5b9e\u60ca\u4eba\uff0c\u5c31\u76f8\u5f53\u4e8e\u51e0\u4e07\u4e2a\u6df1\u72f1\u70bc\u9b54\u540c\u65f6\u53d1\u5c04\u706b\u7403\u672f\u2014\u2014\u8981\u77e5\u9053\uff0c\u6574\u4e2a\u4e5d\u5c42\u5730\u72f1\u7684\u70bc\u9b54\u52a0\u8d77\u6765\u4e5f\u672a\u5fc5\u8d85\u8fc7\u4e09\u767e\u4e2a\uff0c\u62dc\u5c14\u4f5c\u4e3a\u8840\u6218\u6307\u6325\u5b98\uff0c\u624b\u4e0b\u7684\u70bc\u9b54\u4e5f\u4e0d\u8db3\u767e\u540d\u3002\u4f4e\u9636\u7684\u4ea1\u7075\uff0c\u5982\u50f5\u5c38\u9ab7\u9ac5\u5c38\u5996\u4e4b\u7c7b\u5982\u4f55\u627f\u53d7\u5f97\u8d77\uff0c\u7eb7\u7eb7\u7c89\u788e\u6e83\u6563\uff0c\u8f6c\u773c\u4e4b\u95f4\u5c31\u635f\u5931\u4e86\u5927\u534a\uff0c\u5373\u4fbf\u662f\u90a3\u4e9b\u9ad8\u9636\u4ea1\u7075\uff0c\u867d\u7136\u8eaf\u4f53\u5f3a\u608d\uff0c\u4f46\u731d\u4e0d\u53ca\u9632\u4e4b\u4e0b\uff0c\u4e5f\u662f\u4f24\u4ea1\u4e0d\u5c0f\u3002</p><p>\u62dc\u5c14\u673a\u8c0b\u5f97\u901e\uff0c\u4e58\u80dc\u51fa\u51fb\uff0c\u800c\u4ea1\u7075\u519b\u961f\u7684\u6700\u5927\u5f0a\u75c5\uff0c\u6b64\u65f6\u4e5f\u5b8c\u5168\u663e\u9732\u51fa\u6765\u3002</p><p>\u4ea1\u7075\u5927\u591a\u6ca1\u81ea\u4e3b\u610f\u8bc6\uff0c\u4eff\u4f5b\u5080\u5121\u4e00\u822c\uff0c\u5b83\u4eec\u4e0d\u4f1a\u80c6\u602f\uff0c\u4e0d\u4f1a\u754f\u7f29\uff0c\u4e0d\u4f1a\u5bb3\u6015\uff0c\u65e0\u79c1\u5fc3\uff0c\u65e0\u6742\u5ff5\uff0c\u7edd\u5bf9\u670d\u4ece\u547d\u4ee4\uff0c\u4e43\u662f\u4e16\u754c\u4e0a\u6700\u4f18\u79c0\u7684\u58eb\u5175\u2014\u2014\u4f46\u53cd\u8fc7\u6765\u8bf4\uff0c\u4e5f\u662f\u6700\u5dee\u52b2\u7684\u58eb\u5175\u3002\u56e0\u4e3a\u5b83\u4eec\u6ca1\u6709\u667a\u529b\uff0c\u53ea\u4f1a\u542c\u547d\u884c\u4e8b\uff0c\u4e00\u65e6\u80fd\u591f\u4e0b\u547d\u4ee4\u7684\u4eba\u88ab\u5e72\u6389\uff0c\u987f\u65f6\u5c31\u662f\u4e4c\u5408\u4e4b\u4f17\uff0c\u4e00\u76d8\u6563\u6c99\uff0c\u4e0d\u8db3\u4e3a\u60e7\u4e86\u3002</p><p>\u541f\u6e38\u8bd7\u4eba\u7684\u4f20\u5947\u6545\u4e8b\u91cc\uff0c\u5e38\u5e38\u6709\u8fd9\u6837\u7684\u5267\u60c5\uff1a\u4ea1\u7075\u5deb\u5e08\u53ec\u96c6\u4e86\u5e9e\u5927\u7684\u519b\u961f\uff0c\u6f2b\u5c71\u904d\u91ce\u7684\u9ab7\u9ac5\u5c06\u4e00\u5207\u62b5\u6297\u8005\u7c89\u788e\uff0c\u82f1\u52c7\u594b\u6218\u7684\u52c7\u58eb\u4eec\u88ab\u6740\u6b7b\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\u722c\u8d77\u6765\u52a0\u5165\u654c\u4eba\u7684\u884c\u5217\uff0c\u6574\u4e2a\u4e16\u754c\u773c\u770b\u5c31\u8981\u88ab\u90aa\u6076\u7684\u4ea1\u7075\u7edf\u6cbb\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u82f1\u96c4\uff08\u6545\u4e8b\u7684\u4e3b\u89d2\uff09\u6a2a\u7a7a\u51fa\u4e16\uff0c\u95ef\u5165\u4ea1\u7075\u5deb\u5e08\u7684\u5bab\u6bbf\uff0c\u51fb\u6740\u8fd9\u7f6a\u9b41\u7978\u9996\uff0c\u5931\u53bb\u7edf\u5e05\u7684\u4ea1\u7075\u5927\u519b\u9877\u523b\u4e4b\u95f4\u5206\u5d29\u79bb\u6790\uff0c\u5c31\u6b64\u706d\u4ea1\u3002</p><p>\u5f53\u7136\uff0c\u6545\u4e8b\u7ec8\u7a76\u662f\u6545\u4e8b\uff0c\u63ba\u6742\u4e86\u5f88\u591a\u827a\u672f\u5938\u5f20\u7684\u6210\u5206\u3002\u4ea1\u7075\u4e5f\u662f\u5206\u5f88\u591a\u79cd\u7684\uff0c\u5e76\u975e\u53ea\u6709\u50f5\u5c38\u9ab7\u9ac5\u8fd9\u79cd\u4f4e\u7ea7\u54c1\u79cd\uff0c\u540c\u6837\u4e5f\u6709\u5deb\u5996\u3001\u5438\u8840\u9b3c\u3001\u6b7b\u4ea1\u9a91\u58eb\u8fd9\u79cd\u6709\u667a\u529b\u7684\u9ad8\u9636\u5b58\u5728\u3002\u4e3b\u5e05\u6b7b\u4e86\uff0c\u603b\u8fd8\u6709\u526f\u624b\uff0c\u526f\u624b\u6b7b\u4e86\uff0c\u603b\u8fd8\u6709\u90e8\u5c06\uff0c\u4e0d\u53ef\u80fd\u771f\u7684\u5c31\u5b8c\u5168\u5d29\u6e83\u3002\u5426\u5219\u7684\u8bdd\uff0c\u62dc\u5c14\u503e\u5c3d\u5168\u529b\u628a\u6b4c\u560e\u65af\u51fb\u6740\uff0c\u4e00\u5207\u5c31\u90fd\u641e\u5b9a\u4e86\uff0c\u54ea\u91cc\u8fd8\u9700\u8981\u5982\u6b64\u8d39\u52b2\u3002\u4f46\u6545\u4e8b\u4e4b\u6240\u4ee5\u80fd\u591f\u6d41\u4f20\uff0c\u4e5f\u662f\u6709\u5b83\u7684\u9053\u7406\u5728\u5185\u7684\u3002</p><p>\u6740\u6389\u4e00\u4e2a\u4e3b\u5e05\uff0c\u5bf9\u4e8e\u6218\u5c40\u610f\u4e49\u4e0d\u5927\uff0c\u4f46\u5982\u679c\u6740\u6389\u5927\u91cf\u519b\u5b98\uff0c\u90a3\u5c31\u662f\u81f4\u547d\u7684\u6253\u51fb\u4e86\u3002</p><p>\u4ea1\u7075\u519b\u961f\u771f\u6b63\u7684\u4e3b\u4f53\uff0c\u662f\u6570\u4e0d\u5c3d\u7684\u4f4e\u9636\u70ae\u7070\uff0c\u5b83\u4eec\u662f\u5fc5\u987b\u6709\u519b\u5b98\u6307\u6325\uff0c\u6709\u9ad8\u9636\u4ea1\u7075\u7edf\u9a6d\u624d\u80fd\u884c\u52a8\u7684\u3002\u6b4c\u560e\u65af\u81ea\u5df1\u662f\u5438\u8840\u9b3c\uff0c\u4ed6\u5f88\u6709\u4e9b\u4efb\u4eba\u552f\u4eb2\uff0c\u624b\u4e0b\u7684\u5c06\u5b98\u5927\u591a\u90fd\u7528\u5438\u8840\u9b3c\uff0c\u6bcf\u5230\u4f5c\u6218\u4e4b\u524d\uff0c\u4fbf\u8ba9\u5deb\u5996\u4eec\u65bd\u6cd5\u53ec\u6765\u91cd\u91cd\u4e4c\u4e91\u7b3c\u7f69\u5929\u7a7a\uff0c\u963b\u9694\u9633\u5149\uff0c\u5438\u8840\u9b3c\u4eec\u4e5f\u5f97\u4ee5\u81ea\u7531\u884c\u52a8\uff0c\u4e0d\u53d7\u538b\u5236\u3002\u57fa\u672c\u4e0a\u6bcf\u4e00\u961f\u9ab7\u9ac5\u3001\u6bcf\u4e00\u961f\u50f5\u5c38\uff0c\u90fd\u662f\u7531\u4e00\u540d\u4f4e\u9636\u5438\u8840\u9b3c\u7edf\u9886\uff0c\u4f4d\u9636\u5927\u81f4\u662f\u5728\u201c\u65b0\u751f\u201d\u6216\u8005\u201c\u6210\u5e74\u201d\uff0c\u4ed6\u4eec\u7684\u4e0a\u7ea7\u662f\u9ad8\u9636\u5438\u8840\u9b3c\uff0c\u800c\u6b4c\u560e\u65af\u81ea\u5df1\u662f\u201c\u4eb2\u738b\u201d\uff0c\u51ed\u501f\u7740\u9ad8\u9636\u5438\u8840\u9b3c\u5bf9\u4f4e\u9636\u7684\u5929\u7136\u4f18\u52bf\uff0c\u6574\u4e2a\u519b\u961f\u81ea\u7136\u5982\u81c2\u6307\u4f7f\uff0c\u53f7\u4ee4\u7edf\u4e00\u3002\u4e00\u652f\u519b\u961f\u81ea\u5df1\u4e0d\u751f\u5185\u4e71\uff0c\u519b\u4ee4\u901a\u8fbe\uff0c\u7eb5\u7136\u4e3b\u5e05\u624d\u5177\u5e73\u5eb8\uff0c\u4e5f\u5df2\u7ecf\u53ef\u4ee5\u7b97\u662f\u5f3a\u5175\u4e86\u3002\u4f46\u53cd\u8fc7\u6765\u8bf4\uff0c\u5982\u679c\u8fd9\u4e9b\u57fa\u5c42\u519b\u5b98\u88ab\u5927\u91cf\u51fb\u6740\uff0c\u4ea1\u7075\u5927\u519b\u987f\u65f6\u5c31\u4f1a\u4e27\u5931\u76f4\u63a5\u7edf\u9886\uff0c\u90a3\u4e9b\u4f4e\u9636\u4ea1\u7075\u4eec\u53ea\u4f1a\u5446\u5446\u7ad9\u7acb\u5728\u706b\u6d77\u4e4b\u4e2d\u88ab\u70e7\u6b7b\uff0c\u8fde\u9003\u8dd1\u90fd\u4e0d\u4f1a\u3002</p><p>\u5f3a\u5f31\u4e4b\u52bf\uff0c\u987f\u65f6\u9006\u8f6c\uff0c\u6076\u9b54\u519b\u5927\u8d25\u6e83\u6563\uff0c\u4e00\u8def\u64a4\u9000\uff0c\u6b63\u5f80\u9ed1\u77f3\u57ce\u800c\u6765\u3002</p><p>\u203b\u203b\u203b</p><p>\u201c\u5c31\u8fd9\u4e48\u8d25\u4e86\uff1f\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u8fd8\u771f\u662f\u51fa\u4e4e\u610f\u6599\u5462\u3002\u201d</p><p>\u201c\u6d88\u606f\u5e94\u8be5\u4e0d\u5047\uff0c\u6211\u662f\u4ece\u51e0\u4e2a\u6076\u9b54\u53e3\u4e2d\u5f97\u77e5\u7684\uff0c\u5b83\u4eec\u521a\u521a\u4ece\u524d\u7ebf\u9003\u56de\u6765\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4e92\u76f8\u5370\u8bc1\u8865\u5145\u8d77\u6765\uff0c\u5927\u81f4\u5c31\u662f\u8fd9\u6837\u4e86\u3002\u201d</p><p>\u201c\u62dc\u5c14\u7528\u9f99\u72c2\u8ff7\u9501\u9a71\u4f7f\u706b\u9f99\uff0c\u800c\u4e14\u90a3\u4e9b\u706b\u9f99\u53ea\u653b\u51fb\u795e\u8eaf\u9b54\u50cf\uff1f\u201d</p><p>\u201c\u5b83\u4eec\u662f\u8fd9\u4e48\u8bf4\u7684\u3002\u201d</p><p>\u743c\u6069\u76b1\u8d77\u7709\u5934\uff0c\u4ed6\u539f\u672c\u4ee5\u4e3a\u4ea1\u7075\u6bd5\u7adf\u5360\u4e86\u6570\u91cf\u4e0a\u7684\u7edd\u5bf9\u4f18\u52bf\uff0c\u53c8\u6709\u795e\u8eaf\u9b54\u50cf\u8fd9\u79cd\u8d85\u7ea7\u6b66\u5668\u5728\uff0c\u5c31\u7b97\u4e0d\u80fd\u4e00\u9f13\u53d6\u80dc\uff0c\u653b\u4e0b\u9752\u94dc\u57ce\u5821\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u6218\u8d25\u3002\u6ca1\u60f3\u5230\u62dc\u5c14\u5c45\u7136\u4e5f\u85cf\u4e86\u51e0\u624b\uff0c\u80fd\u591f\u9a71\u9063\u706b\u9f99\u653b\u51fb\u7279\u5b9a\u76ee\u6807\u7684\u9f99\u72c2\u8ff7\u9501\uff0c\u4f20\u8bf4\u4e2d\u7684\u66e6\u5929\u4f7f\uff0c\u8fd8\u6709\u706b\u836f\u2026\u2026</p><p>\u201c\u4e0d\u4ec5\u5982\u6b64\uff0c\u62dc\u5c14\u8fd8\u6709\u66f4\u9634\u9669\u7684\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6b4c\u560e\u65af\u88ab\u66e6\u5929\u4f7f\u7684\u77ac\u6740\u7bad\u5c04\u4e2d\uff0c\u53ea\u662f\u6bc1\u4e86\u6761\u80f3\u818a\uff0c\u4f46\u636e\u90a3\u4e9b\u6076\u9b54\u8bf4\uff0c\u4ed6\u73b0\u5728\u5df2\u7ecf\u4e2d\u6bd2\uff0c\u6655\u8ff7\u4e0d\u9192\u4e86\u3002\u201d</p><p>\u201c\u600e\u4e48\u4f1a\uff1f\u4ed6\u662f\u4ea1\u7075\uff0c\u600e\u4e48\u53ef\u80fd\u4f1a\u4e2d\u6bd2\u3002\u201d</p><p>\u201c\u4ed6\u662f\u5438\u8840\u9b3c\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u62dc\u5c14\u5236\u4f5c\u90a3\u4e9b\u706b\u836f\u70b8\u5f39\u7684\u65f6\u5019\uff0c\u7528\u7684\u662f\u6700\u7eaf\u7684\u7cbe\u91d1\uff0c\u4f46\u4e0a\u9762\u5374\u5168\u90fd\u9540\u94f6\u4e86\uff0c\u4e13\u95e8\u5c31\u662f\u9488\u5bf9\u5438\u8840\u9b3c\u7684\u3002\u201d</p><p>\u62dc\u5c14\u771f\u662f\u5962\u4f88\u2026\u2026\u6362\u53e5\u8bdd\u8bf4\uff0c\u9b54\u9b3c\u771f\u662f\u5bcc\u88d5\uff0c\u96be\u602a\u5f15\u8bf1\u51e1\u4eba\u7b7e\u7f72\u7075\u9b42\u5951\u7ea6\u7684\u65f6\u5019\uff0c\u8bb8\u8bfa\u8d77\u91d1\u94b1\u8d22\u5bcc\u6765\u4ece\u4e0d\u534a\u70b9\u542b\u7cca\u3002</p><p>\u201c\u7b28\u86cb\uff0c\u201d\u743c\u6069\u8bc4\u4ef7\uff0c\u201c\u8eab\u4e3a\u4e3b\u5e05\uff0c\u5206\u660e\u5c31\u5e94\u8be5\u8eb2\u5728\u5b89\u5168\u7684\u540e\u65b9\u5750\u9547\uff0c\u8eab\u8fb9\u6446\u4e00\u5927\u7968\u62a4\u536b\u624d\u6b63\u786e\uff0c\u504f\u504f\u8981\u4e0a\u524d\u7ebf\u51b2\u950b\uff0c\u81ea\u7136\u4f1a\u88ab\u4eba\u6253\u3002\u201d</p><p>\u201c\u8bdd\u4e5f\u4e0d\u80fd\u8fd9\u4e48\u8bf4\u5427\uff0c\u4e3b\u5e05\u8eab\u5148\u58eb\u5352\uff0c\u662f\u53ef\u4ee5\u6781\u5927\u9f13\u821e\u58eb\u6c14\u7684\u3002\u201d</p><p>\u201c\u95ee\u9898\u662f\u4ea1\u7075\u54ea\u6709\u58eb\u6c14\u8fd9\u79cd\u4e1c\u897f\uff1f\u4f60\u4ec0\u4e48\u65f6\u5019\u89c1\u8fc7\u58eb\u6c14\u9ad8\u6602\u7684\u50f5\u5c38\u9ab7\u9ac5\u4e86\uff1f\u6216\u8005\u4f60\u89c1\u8fc7\u4e00\u4e2a\u70ed\u8840\u6cb8\u817e\u7684\u5deb\u5996\uff1f\u201d</p><p>\u201c\u2026\u2026\u4f3c\u4e4e\u662f\u6ca1\u6709\u3002\u201d</p><p>\u201c\u795e\u8eaf\u9b54\u50cf\u60c5\u51b5\u5982\u4f55\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u4e0d\u6e05\u695a\uff0c\u4f46\u5e94\u8be5\u6ca1\u6709\u5927\u788d\uff0c\u53ea\u662f\u53d7\u635f\u3002\u201d</p><p>\u743c\u6069\u60f3\u4e86\u4e00\u60f3\uff0c\u8ba9\u838e\u73de\u514b\u7ee7\u7eed\u51fa\u53bb\u6253\u63a2\u6d88\u606f\uff0c\u7136\u540e\u628a\u4eca\u5929\u8def\u4e0a\u9047\u5230\u6b32\u9b54\u4f0a\u84d3\u4e1d\u7684\u4e8b\u60c5\u5bf9\u6885\u83f2\u65af\u8bf4\u4e86\u3002\u201c\u672c\u6765\u60f3\u8fd9\u662f\u4e2a\u673a\u4f1a\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u73b0\u5728\u770b\u8d77\u6765\uff0c\u4e8b\u60c5\u8fd8\u6709\u70b9\u590d\u6742\u3002\u201d</p><p>\u6885\u83f2\u65af\u6c89\u541f\u7247\u523b\uff0c\u201c\u4e0d\u7740\u6025\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5148\u7b49\u7b49\uff0c\u6709\u786e\u5207\u6d88\u606f\u518d\u8bf4\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u6b63\u8bf4\u8bdd\u95f4\uff0c\u51db\u8d70\u4e86\u8fdb\u6765\uff0c\u4fcf\u8138\u677f\u7740\uff0c\u6c14\u9f13\u9f13\u5730\u5750\u5728\u6905\u4e2d\uff0c\u4e00\u526f\u4f59\u6012\u672a\u6d88\u7684\u6a21\u6837\u3002\u743c\u6069\u548c\u6885\u83f2\u65af\u5bf9\u89c6\u4e00\u773c\uff0c\u90fd\u6709\u4e9b\u8be7\u5f02\uff0c\u201c\u600e\u4e48\u4e86\uff0c\u51db\uff0c\u201d\u6885\u83f2\u65af\u95ee\uff0c\u201c\u51fa\u4ec0\u4e48\u4e8b\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u201d\u51db\u6446\u6446\u624b\uff0c\u7aef\u8d77\u684c\u4e0a\u7684\u676f\u5b50\u559d\u6c34\uff0c\u201c\u521a\u624d\u51fa\u53bb\u8d70\u8d70\uff0c\u9047\u5230\u4e2a\u8272\u72fc\u3002\u201d</p></article>"}}}]);
