(window.webpackJsonp=window.webpackJsonp||[]).push([[1559],{1582:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516b\u8282 \u795e\u76f8",content:"<article><h2>\u7b2c\u516b\u8282 \u795e\u76f8</h2><p>\u9b54\u9b3c\u4eec\u568e\u53eb\u7740\uff0c\u594b\u4e0d\u987e\u8eab\u5730\u6251\u4e0a\u6765\uff0c\u8981\u7528\u6570\u91cf\u5c06\u8fd9\u602a\u7269\u6df9\u6ca1\uff0c\u51e0\u5341\u540d\u6df1\u72f1\u70bc\u9b54\u5728\u9b54\u50cf\u5468\u56f4\u9aa4\u7136\u51fa\u73b0\uff0c\u65bd\u5c55\u51fa\u5929\u8d4b\u62e5\u6709\u7684\u201c\u6d41\u661f\u7206\u201d\u3002\u96f7\u9e23\u822c\u7684\u5de8\u54cd\u9707\u64bc\u5927\u5730\uff0c\u4e0a\u767e\u9897\u9668\u77f3\u62d6\u66f3\u7740\u957f\u957f\u7684\u706b\u5c3e\u5212\u8fc7\u5929\u9645\uff0c\u8fdc\u8fdc\u671d\u8fd9\u91cc\u5760\u6765\u3002\u9eef\u4e4b\u7ffc\u6b8b\u5b58\u7684\u56db\u5341\u591a\u540d\u5815\u5929\u4f7f\u81ea\u4e91\u7aef\u73b0\u8eab\uff0c\u9ad8\u4e3e\u5341\u5b57\u5bd2\u51b0\u5de8\u5251\uff0c\u51f6\u731b\u6251\u4e0b\u3002</p><p>\u4e00\u58f0\u7a7a\u6d1e\u800c\u5636\u54d1\u7684\u4f4e\u543c\u58f0\u81ea\u9b54\u50cf\u5589\u95f4\u53d1\u51fa\u3002</p><p>\u7d2b\u7ea2\u8272\u7684\u5149\u8292\u81ea\u9b54\u50cf\u5185\u90e8\u900f\u5c04\u51fa\u6765\uff0c\u5f62\u4f53\u6025\u901f\u53d1\u751f\u53d8\u5316\uff0c\u5de8\u5927\u7684\u8eab\u8eaf\u50cf\u4e00\u53ea\u8f6f\u4f53\u52a8\u7269\u822c\u8815\u52a8\u7740\uff0c\u6025\u901f\u626d\u66f2\u6210\u5404\u79cd\u5947\u602a\u7684\u6a21\u6837\u3002\u5b83\u5934\u9876\u7684\u8d64\u7ea2\u8272\u5929\u7a7a\u4e2d\u5760\u4e0b\u65e0\u6570\u9053\u9ed1\u8272\u95ea\u7535\uff0c\u5728\u843d\u5730\u524d\u53c8\u8f6c\u5316\u4e3a\u70bd\u70c8\u7684\u51b0\u96f9\uff0c\u8086\u610f\u5730\u6bc1\u706d\u7740\u4e00\u5207\u3002\u5728\u9b54\u50cf\u9644\u8fd1\u7684\u6240\u6709\u5b58\u5728\uff0c\u4e0d\u7ba1\u662f\u4ea1\u7075\u8fd8\u662f\u9b54\u9b3c\uff0c\u90fd\u5728\u5de8\u5927\u7684\u96f7\u9e23\u58f0\u4e2d\u540c\u6837\u7ecf\u5386\u7740\u75db\u82e6\u800c\u53ef\u6015\u7684\u5f62\u4f53\u53d8\u5316\uff0c\u5f62\u5f62\u8272\u8272\u7684\u80a2\u4f53\u4e94\u5b98\u4e43\u81f3\u5185\u810f\u5728\u5b83\u4eec\u5168\u8eab\u4e0d\u65ad\u5730\u957f\u51fa\u53c8\u6d88\u5931\uff0c\u50cf\u662f\u88ab\u4eba\u634f\u7740\u7684\u9762\u56e2\u4e00\u822c\u5728\u626d\u66f2\u53d8\u5f62\u3002\u800c\u4ed6\u4eec\u811a\u4e0b\u7684\u571f\u5730\uff0c\u6b64\u523b\u5df2\u7ecf\u53d8\u6210\u4e0d\u5b9a\u5f62\u7684\u7c98\u6db2\u72b6\u6001\uff0c\u4eff\u4f5b\u4e00\u56e2\u5de8\u5927\u7684\u8840\u8089\u3002\u4ee5\u795e\u8eaf\u9b54\u50cf\u4e3a\u4e2d\u5fc3\u7684\u6574\u5757\u533a\u57df\uff0c\u5c31\u4eff\u4f5b\u662f\u4e00\u53ea\u9677\u5165\u4e86\u6124\u6012\u72c2\u66b4\u4e2d\u7684\u5de8\u602a\u4e00\u822c\uff0c\u6bcf\u4e2a\u7ec6\u5fae\u90e8\u4f4d\u90fd\u5728\u4ee5\u672b\u65e5\u822c\u7684\u59ff\u6001\u5267\u70c8\u5730\u626d\u66f2\u53d8\u5f62\u3002</p><p>\u5343\u767e\u5f20\u6b64\u8d77\u5f7c\u4f0f\u7684\u9762\u5b54\u5728\u9b54\u50cf\u7684\u8eaf\u4f53\u8868\u9762\u6d6e\u73b0\uff0c\u7528\u4e0a\u5343\u79cd\u5404\u5f02\u7684\u58f0\u54cd\u72c2\u4e71\u5730\u9ad8\u547c\u7740\u4e0d\u540c\u7684\u540d\u5b57\uff0c\u7136\u540e\u9010\u6e10\u7edf\u4e00\u8d77\u6765\uff0c\u6700\u540e\u53ea\u5269\u4e0b\u4e86\u4e00\u4e2a\u9634\u68ee\u4f4e\u6c89\u7684\u542b\u6df7\u95f7\u54cd\u3002</p><p>\u201c\u4e0a\u53e4\u4e4b\u773c\uff08the Elder Eye\uff09\u2026\u2026\u201d</p><p>\u5267\u70c8\u626d\u66f2\u53d8\u5316\u7684\u795e\u8eaf\u9b54\u50cf\u7ec8\u4e8e\u91cd\u65b0\u56fa\u5b9a\u4e86\u5f62\u6001\uff0c\u53d8\u6210\u4e86\u4e00\u53ea\u5e9e\u5927\u7684\u6697\u7d2b\u8272\u80f6\u72b6\u602a\u7269\uff0c\u957f\u7740\u6570\u767e\u53ea\u89e6\u624b\u3002\u5728\u5b83\u7684\u80cc\u4e0a\uff0c\u4e00\u53ea\u5de8\u773c\u7f13\u7f13\u5730\u7741\u5f00\uff0c\u9732\u51fa\u4e86\u6beb\u65e0\u751f\u6c14\u7684\u7d2b\u8272\u773c\u73e0\u3002\u800c\u5728\u5b83\u7684\u811a\u4e0b\uff0c\u5730\u9762\u4e5f\u7ec8\u4e8e\u56fa\u5b9a\u4e86\u4e0b\u6765\uff0c\u90a3\u662f\u4e00\u7247\u5145\u65a5\u7740\u8150\u81ed\u6c14\u5473\uff0c\u5e03\u6ee1\u70c2\u6ce5\u548c\u80f6\u6c60\u7684\u9ed1\u8272\u6cbc\u6cfd\u3002\u8f6f\u6ce5\u602a\u4e00\u822c\u7684\u9b54\u50cf\u5728\u6cbc\u6cfd\u4e2d\u7ffb\u817e\u8815\u52a8\u7740\uff0c\u5b83\u8eab\u4e0a\u65e0\u6570\u6325\u821e\u7740\u7684\u89e6\u624b\u5728\u77ac\u95f4\u4f38\u957f\uff0c\u76f4\u5411\u5929\u7a7a\u4e2d\u63d2\u53bb\uff0c\u5982\u540c\u5de8\u5927\u7684\u957f\u97ad\u4e00\u822c\u6325\u821e\u6a2a\u626b\uff0c\u4e00\u9635\u9635\u8f7b\u70df\u4f34\u7740\u201c\u55e4\u55e4\u201d\u7684\u8150\u8680\u58f0\u4e0d\u65ad\u5730\u5347\u8d77\uff0c\u51e0\u4e4e\u6240\u6709\u88ab\u89e6\u624b\u78b0\u5230\u7684\u6df1\u72f1\u70bc\u9b54\u548c\u5815\u5929\u4f7f\u90fd\u5728\u77ac\u95f4\u88ab\u8150\u8680\u51fa\u4e86\u53ef\u6015\u7684\u4f24\u53e3\u3002\u7136\u800c\u4ed6\u4eec\u7684\u60b2\u60e8\u547d\u8fd0\u4e0d\u6b62\u662f\u5982\u6b64\uff0c\u5f88\u5feb\u5de8\u5927\u7684\u89e6\u624b\u957f\u97ad\u5e2d\u5377\u56de\u6536\uff0c\u5c06\u5929\u7a7a\u4e2d\u7684\u654c\u4eba\u50cf\u6349\u5c0f\u866b\u4e00\u822c\u5377\u8fdb\u4e86\u9b54\u50cf\u7d2b\u8272\u5de8\u5927\u7684\u80f6\u72b6\u8eab\u8eaf\u4e2d\uff0c\u968f\u5373\u4ed6\u4eec\u5c31\u5982\u540c\u88ab\u6df1\u4e0d\u89c1\u5e95\u7684\u6cbc\u6cfd\u541e\u6ca1\u4e86\u4e00\u822c\uff0c\u5c31\u6b64\u5f7b\u5e95\u6d88\u5931\u3002</p><p>\u4e0a\u767e\u9897\u718a\u718a\u71c3\u70e7\u7684\u9668\u77f3\u5168\u6570\u547d\u4e2d\u4e86\u76ee\u6807\uff0c\u9b54\u50cf\u53d7\u4f24\u4e86\uff0c\u5b83\u7684\u89e6\u624b\u88ab\u6253\u65ad\u4e86\u51e0\u5341\u6839\uff0c\u65ad\u53e3\u5904\u55b7\u5c04\u51fa\u9ed1\u8272\u7684\u7c98\u6db2\uff0c\u4eff\u4f5b\u6c61\u8840\u3002\u6df1\u72f1\u70bc\u9b54\u548c\u5815\u5929\u4f7f\u4eec\u75af\u72c2\u6251\u4e0a\uff0c\u7136\u800c\u4ed6\u4eec\u5f88\u5feb\u53d1\u73b0\u4ee5\u8fd9\u8f6f\u6ce5\u602a\u9762\u76ee\u51fa\u73b0\u7684\u795e\u8eaf\u9b54\u50cf\u6bd4\u4ee5\u5f80\u66f4\u68d8\u624b\u3002\u90a3\u67d4\u8f6f\u7684\u80f6\u72b6\u8eab\u8eaf\u5e76\u4e0d\u575a\u786c\uff0c\u4f46\u5374\u6781\u96be\u4f24\u5bb3\uff0c\u7eb5\u4f7f\u80fd\u780d\u4e0b\u5b83\u8eab\u4f53\u7684\u4e00\u90e8\u5206\uff0c\u90a3\u4e00\u5757\u88ab\u780d\u4e0b\u7684\u80f6\u72b6\u7269\u4e5f\u4f1a\u81ea\u5df1\u8815\u52a8\u7740\u722c\u56de\u53bb\uff0c\u91cd\u65b0\u878d\u5165\u9b54\u50cf\u7684\u8eab\u4f53\uff0c\u6216\u8005\u5e72\u8106\u53d8\u6210\u4e00\u53ea\u9ed1\u5e03\u4e01\u602a\u6216\u8d6d\u51bb\u602a\u5411\u4ed6\u4eec\u53d1\u52a8\u653b\u51fb\u3002\u795e\u8eaf\u9b54\u50cf\u5c31\u8fd9\u6837\u5728\u9b54\u9b3c\u4eec\u7684\u675f\u624b\u65e0\u7b56\u4e2d\u8815\u52a8\u7740\u5e9e\u5927\u7684\u6ce5\u602a\u8eab\u8eaf\uff0c\u7f13\u7f13\u524d\u8fdb\u7740\uff0c\u541e\u566c\u4e00\u8def\u4e0a\u4efb\u4f55\u963b\u6321\u5728\u5b83\u9762\u524d\u7684\u751f\u7269\u548c\u7269\u4f53\uff0c\u5c06\u4ed6\u4eec\u6d88\u878d\u8150\u8680\u5f97\u4e00\u5e72\u4e8c\u51c0\u3002</p><p>\u72c2\u8e81\u7684\u543c\u53eb\u58f0\u4ece\u8fdc\u5904\u7684\u5929\u7a7a\u4e2d\u4f20\u6765\uff0c\u70bc\u72f1\u708e\u9f99\u5e9e\u5927\u7684\u8eab\u5f71\u906e\u5929\u853d\u65e5\uff0c\u518d\u5ea6\u7b3c\u7f69\u6218\u573a\u4e0a\u7a7a\u3002</p><p>\u8fd9\u4e9b\u6709\u7740\u9b54\u9b3c\u822c\u5916\u5f62\uff0c\u5934\u80a9\u4f38\u51fa\u4e86\u8bb8\u591a\u72f0\u72de\u9aa8\u523a\u7684\u5de8\u9f99\u4eec\u539f\u672c\u5e94\u8be5\u71c3\u70e7\u7740\u9ec4\u8272\u706b\u7130\u7684\u773c\u775b\u91cc\u6b64\u523b\u653e\u5c04\u7740\u6151\u4eba\u7684\u7ea2\u5149\uff0c\u53ef\u4ee5\u53d8\u5316\u989c\u8272\u7684\u9cde\u7247\u4e0b\u4e0d\u65ad\u5192\u51fa\u6eda\u6eda\u9ed1\u70df\uff0c\u770b\u4e0a\u53bb\u72b9\u5982\u8eab\u62ab\u7740\u4e00\u5c42\u6d41\u52a8\u7684\u7194\u5ca9\uff0c\u786b\u78fa\u7684\u5473\u9053\u548c\u9b54\u50cf\u6ce5\u602a\u8eab\u8eaf\u4e0a\u7684\u8150\u81ed\u4ea4\u6742\u5728\u4e00\u8d77\uff0c\u4f7f\u6574\u4e2a\u6218\u573a\u7b3c\u7f69\u5728\u4e00\u7247\u4ee4\u4eba\u505a\u5455\u7684\u5f02\u5473\u4e2d\u3002\u5df2\u7ecf\u4e27\u5931\u4e86\u4e00\u5207\u751f\u547d\u8ff9\u8c61\u7684\u7d2b\u8272\u773c\u73e0\u673a\u68b0\u800c\u6728\u7136\u5730\u626b\u89c6\u8fc7\u5929\u7a7a\u4e2d\u6b63\u6025\u901f\u903c\u8fd1\u7684\u70bc\u72f1\u708e\u9f99\uff0c\u795e\u8eaf\u9b54\u50cf\u7684\u80f6\u72b6\u8eaf\u4f53\u8868\u9762\u6d8c\u8d77\u4e86\u4e00\u9635\u6d77\u6d6a\u6ce2\u6d9b\u822c\u7684\u6296\u52a8\uff0c\u65e0\u6570\u56e2\u6df1\u7eff\u8272\u7c98\u80f6\u4ece\u5b83\u7684\u89e6\u624b\u4e2d\u5c04\u51fa\uff0c\u5982\u540c\u88ab\u6295\u77f3\u8f66\u6295\u51fa\u7684\u5de8\u77f3\u4e00\u822c\u5212\u7740\u629b\u7269\u7ebf\u8fce\u5934\u5411\u70bc\u72f1\u708e\u9f99\u4eec\u6253\u53bb\u3002\u4e00\u4e9b\u70bc\u72f1\u708e\u9f99\u88ab\u6253\u4e2d\u4e86\uff0c\u5b83\u4eec\u4ece\u7a7a\u4e2d\u5760\u843d\u4e0b\u6765\uff0c\u88ab\u4e00\u5c42\u7eff\u8272\u7684\u80f6\u8d28\u7c98\u5728\u4e86\u5730\u4e0a\uff0c\u5373\u4f7f\u662f\u72c2\u66b4\u7684\u70bc\u72f1\u708e\u9f99\u4e5f\u65e0\u6cd5\u6323\u8131\uff0c\u9178\u6db2\u8150\u8680\u7684\u58f0\u97f3\u4e0d\u65ad\u54cd\u8d77\uff0c\u770b\u4e0a\u53bb\u5c31\u50cf\u88ab\u4e00\u56e2\u53f2\u83b1\u59c6\u5305\u8986\u7684\u52a8\u7269\u3002\u4f46\u66f4\u591a\u7684\u708e\u9f99\u4e0d\u987e\u4e00\u5207\u5730\u98de\u5230\u4e86\u8fd1\u524d\uff0c\u55b7\u51fa\u65e0\u6bd4\u70bd\u70c8\u7684\u706b\u7130\uff0c\u5c06\u795e\u8eaf\u9b54\u50cf\u7f6e\u8eab\u5728\u4e00\u7247\u706b\u6d77\u4e4b\u4e2d\u3002</p><p>\u708e\u9f99\u7684\u5410\u606f\uff0c\u8fdc\u8fdc\u4e0d\u662f\u5bfb\u5e38\u7684\u9b54\u6cd5\u706b\u7130\u53ef\u4ee5\u6bd4\u62df\uff0c\u9b54\u50cf\u5e9e\u5927\u7684\u8f6f\u6ce5\u8eab\u8eaf\u5728\u6cbc\u6cfd\u4e2d\u7ffb\u6eda\u626d\u66f2\u7740\uff0c\u89e6\u624b\u7eb7\u7eb7\u7126\u9ed1\u65ad\u88c2\uff0c\u76ae\u5f00\u8089\u7efd\uff0c\u773c\u770b\u5c31\u8981\u88ab\u70e7\u6bc1\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u9ed1\u77f3\u57ce\u5934\u4e0a\uff0c\u9aa4\u7136\u817e\u8d77\u4e00\u9053\u9b54\u6cd5\u95ea\u5149\u3002</p><p>\u4eff\u4f5b\u63a5\u5230\u547d\u4ee4\u4e00\u822c\uff0c\u795e\u8eaf\u9b54\u50cf\u518d\u4e00\u6b21\u5f00\u59cb\u53d8\u5e7b\u5f62\u4f53\uff0c\u77ed\u77ed\u51e0\u79d2\u949f\u91cc\uff0c\u95ea\u7535\u3001\u51b0\u96f9\u3001\u6cbc\u6cfd\u548c\u8f6f\u6ce5\u602a\u90fd\u5b8c\u5168\u6d88\u5931\u4e86\uff0c\u4e00\u53ea\u7c89\u7ea2\u8272\u7684\u5de8\u9f99\u81ea\u718a\u718a\u706b\u6d77\u4e2d\u817e\u98de\u800c\u8d77\uff0c\u82cd\u8001\u7684\u9762\u5bb9\u4e0a\u6f20\u65e0\u8868\u60c5\uff0c\u5b83\u6df1\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u968f\u5373\u5410\u51fa\u3002\u5343\u4e07\u9897\u8679\u5f69\u5149\u7403\u7eb7\u98de\u800c\u51fa\uff0c\u901f\u5ea6\u4f3c\u7f13\u5b9e\u75be\uff0c\u5239\u90a3\u65f6\u904d\u5e03\u6574\u4e2a\u7a7a\u95f4\u3002\u6240\u6709\u88ab\u5149\u7403\u78b0\u89e6\u5230\u7684\u6df1\u72f1\u70bc\u9b54\u3001\u5815\u5929\u4f7f\u548c\u70bc\u72f1\u708e\u9f99\u90fd\u5728\u77ac\u95f4\u50f5\u76f4\u4e0d\u52a8\uff0c\u968f\u5373\u5728\u534a\u79d2\u949f\u540e\u5316\u4f5c\u98de\u7070\u3002</p><p>\u8f6c\u773c\u4e4b\u95f4\uff0c\u62dc\u5c14\u6700\u7cbe\u9510\u7684\u6218\u529b\u635f\u5931\u5927\u534a\u3002\u5410\u51fa\u8679\u5149\u98ce\u66b4\u7684\u5de8\u9f99\u4f3c\u4e4e\u4e5f\u518d\u4e5f\u65e0\u529b\u652f\u6491\u8eab\u8eaf\uff0c\u8f70\u7136\u5f80\u4e0b\u65b9\u7684\u706b\u6d77\u4e2d\u5760\u53bb\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u4e00\u9053\u8000\u773c\u91d1\u5149\u81ea\u4e91\u7aef\u5c04\u6765\uff0c\u5239\u90a3\u95f4\u7a7f\u900f\u5de8\u9f99\u7684\u80f8\u8179\uff0c\u9732\u51fa\u6df1\u6df1\u8840\u6d1e\u3002</p><p>\u66e6\u5929\u4f7f\xb7\u77ac\u6740\u7bad\uff01</p><p>\u4e00\u9053\u91d1\u8272\u95ea\u7535\u81ea\u9ad8\u7a7a\u4e2d\u5288\u4e0b\uff0c\u5728\u5de8\u9f99\u7684\u4e0a\u65b9\u51dd\u805a\u6210\u578b\uff0c\u66e6\u5929\u4f7f\u5f25\u8d5b\u4e9a\u518d\u5ea6\u51fa\u73b0\uff0c\u5979\u7684\u707f\u91d1\u8272\u957f\u53d1\u8fce\u98ce\u98de\u821e\uff0c\u6d01\u767d\u7fbd\u7ffc\u4e0a\u5149\u534e\u70b9\u70b9\uff0c\u5982\u661f\u8fb0\u7480\u74a8\u95ea\u8000\uff0c\u5bd2\u51b0\u5de8\u5251\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u91d1\u8272\u5723\u5149\u6c79\u6d8c\u6f8e\u6e43\uff0c\u8f70\u7136\u4e0b\u51fb\u3002</p><p>\u4e00\u9053\u6df1\u6df1\u7684\u4f24\u53e3\u81ea\u5de8\u9f99\u80cc\u90e8\u6025\u901f\u5ef6\u4f38\uff0c\u6574\u4e2a\u8eaf\u4f53\u4eff\u4f5b\u90fd\u88ab\u5288\u5f00\u6210\u4e86\u4e24\u534a\u3002\u5b83\u6124\u6012\u5730\u4f4e\u543c\u7740\uff0c\u65e0\u529b\u5730\u62cd\u51fb\u7740\u53cc\u7ffc\u60f3\u8981\u98de\u8d77\uff0c\u4f46\u8eab\u4f53\u8fd8\u662f\u6b62\u4e0d\u4f4f\u5730\u5760\u843d\uff0c\u521a\u624d\u90a3\u4e00\u51fb\u8679\u5f69\u98ce\u66b4\u8ba9\u5b83\u5904\u4e8e\u6700\u865a\u5f31\u7684\u72b6\u6001\uff0c\u518d\u52a0\u4e0a\u66e6\u5929\u4f7f\u7684\u5168\u529b\u5077\u88ad\uff0c\u6b64\u65f6\u9b54\u50cf\u5df2\u7ecf\u5b8c\u5168\u65e0\u6cd5\u63a7\u5236\u81ea\u8eab\u3002\u66e6\u5929\u4f7f\u7684\u51b0\u5251\u4e0a\u6d41\u5149\u95ea\u52a8\uff0c\u518d\u5ea6\u53d8\u6210\u91d1\u8272\u957f\u5f13\uff0c\u62c9\u5f00\u5f13\u5f26\u5c31\u8981\u518d\u5ea6\u5c04\u51fa\u3002</p><p>\u7136\u540e\u5979\u7684\u76ee\u6807\u6d88\u5931\u4e86\u3002</p><p>\u9ed1\u77f3\u57ce\u7684\u4e0a\u7a7a\u518d\u5ea6\u95ea\u8d77\u4e00\u9053\u9b54\u6cd5\u5149\u8f89\uff0c\u5373\u5c06\u5760\u5165\u706b\u6d77\u4e2d\u7684\u5de8\u9f99\u9aa4\u7136\u95f4\u53d8\u5f97\u900f\u660e\u8d77\u6765\uff0c\u539f\u672c\u5b9e\u8d28\u6709\u5f62\u7684\u8eaf\u4f53\u8fc5\u901f\u6de1\u5316\uff0c\u6700\u7ec8\u5b8c\u5168\u6d88\u5931\u3002</p><p>\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u9ed1\u77f3\u57ce\u7684\u5deb\u5996\u4eec\u6536\u56de\u4e86\u795e\u8eaf\u9b54\u50cf\u3002</p><p>\u9b54\u50cf\u91cd\u521b\u88ab\u8feb\u6536\u56de\uff0c\u8fea\u5185\u7279\u5374\u5728\u57ce\u5934\u653e\u58f0\u5927\u7b11\u8d77\u6765\uff0c\u62dc\u5c14\u624b\u4e0b\u7684\u6df1\u72f1\u70bc\u9b54\u4f24\u4ea1\u5927\u534a\uff0c\u70bc\u72f1\u708e\u9f99\u5168\u519b\u8986\u6ca1\uff0c\u4fbf\u662f\u5815\u5929\u4f7f\u4e5f\u5df2\u7ecf\u53ea\u5269\u5341\u4f59\u4eba\u3002\u4ee5\u8fd9\u79cd\u5175\u529b\uff0c\u62dc\u5c14\u5c31\u7b97\u662f\u518d\u5929\u624d\u7684\u5c06\u519b\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5728\u4e00\u65e5\u4e4b\u5185\u653b\u4e0b\u9ed1\u77f3\u57ce\u2014\u2014\u800c\u795e\u8eaf\u9b54\u50cf\u5c31\u7b97\u53d7\u5230\u518d\u4e25\u91cd\u7684\u635f\u4f24\uff0c\u4e5f\u4e0d\u8fc7\u4e00\u65e5\u5c31\u80fd\u590d\u539f\u3002</p><p>\u5f53\u963f\u5f17\u7eb3\u65af\u7684\u592a\u9633\u518d\u5ea6\u4ece\u897f\u65b9\u5347\u8d77\u65f6\uff0c\u9b54\u9b3c\u4eec\u5c31\u8981\u63a5\u53d7\u5931\u8d25\u7684\u547d\u8fd0\u4e86\u3002\u800c\u4ed6\u8fea\u5185\u7279\uff0c\u4e5f\u5c06\u4f5c\u4e3a\u7b2c\u4e00\u4e2a\u771f\u6b63\u51fb\u8d25\u62dc\u5c14\u7684\u5c06\u519b\u800c\u94ed\u8bb0\u4e8e\u8840\u6218\u7684\u5386\u53f2\u4e4b\u4e2d\uff0c\u4e3a\u6076\u9b54\u4f20\u5531\uff0c\u88ab\u9b54\u9b3c\u754f\u60e7\u3002</p><p>\u4ed6\u8e0c\u8e87\u6ee1\u5fd7\u5730\u770b\u7740\u6218\u573a\uff0c\u9b54\u9b3c\u4eec\u8fd8\u5728\u7ee7\u7eed\u653b\u51fb\uff0c\u4f46\u5df2\u7ecf\u4e27\u5931\u4e86\u5f00\u59cb\u65f6\u7684\u9510\u6c14\u3002\u4ea1\u7075\u4eec\u7eb7\u7eb7\u64a4\u56de\u57ce\u5185\uff0c\u51ed\u501f\u7740\u9ad8\u5927\u7684\u57ce\u5899\u548c\u5deb\u5996\u4eec\u5e03\u4e0b\u7684\u9b54\u6cd5\u5c4f\u969c\u62b5\u5fa1\u653b\u51fb\uff0c\u9b54\u9b3c\u63a5\u8fde\u53d1\u8d77\u51e0\u6b21\u653b\u51fb\uff0c\u62dc\u5c14\u751a\u81f3\u4eb2\u81ea\u4e0a\u9635\u51b2\u950b\uff0c\u4f9d\u65e7\u90fd\u88ab\u6253\u9000\u4e86\u4e0b\u53bb\uff0c\u5c40\u9762\u5df2\u7ecf\u53d8\u5f97\u5bf9\u5df1\u65b9\u8d8a\u6765\u8d8a\u6709\u5229\u4e86\u3002</p><p>\u592a\u9633\u5df2\u7ecf\u5347\u4e0a\u534a\u7a7a\uff0c\u660e\u4eae\u7684\u5149\u7ebf\u523a\u5f97\u8fea\u5185\u7279\u6709\u70b9\u773c\u82b1\uff0c\u4ed6\u4e0d\u60e7\u6015\u9633\u5149\uff0c\u4f46\u7ec8\u7a76\u662f\u4e0d\u9002\u5e94\uff0c\u6216\u8005\u8bf4\u672c\u80fd\u5730\u538c\u6076\u3002\u9b54\u9b3c\u4eec\u7684\u653b\u51fb\u8d8a\u6765\u8d8a\u65e0\u529b\uff0c\u6700\u7ec8\u64a4\u9000\u4e86\u4e0b\u53bb\uff0c\u77ed\u65f6\u95f4\u5185\u5e94\u8be5\u662f\u65e0\u6cd5\u518d\u5ea6\u53d1\u8d77\u8fdb\u653b\u4e86\uff0c\u8fea\u5185\u7279\u88f9\u7d27\u8eab\u4e0a\u7684\u6597\u7bf7\uff0c\u51b3\u5b9a\u56de\u5b98\u90b8\u4f11\u606f\u3002</p><p>\u5728\u8d70\u4e0b\u57ce\u5934\u7684\u65f6\u5019\uff0c\u4ed6\u7a81\u7136\u6709\u79cd\u5f02\u6837\u7684\u611f\u89c9\uff0c\u4eff\u4f5b\u6709\u4ec0\u4e48\u4eba\u6b63\u5728\u80cc\u540e\u6ce8\u89c6\u7740\u81ea\u5df1\u3002\u8fea\u5185\u7279\u6014\u4e86\u4e00\u6014\uff0c\u56de\u5934\u770b\u53bb\uff0c\u5374\u5e76\u6ca1\u6709\u53d1\u73b0\u4ec0\u4e48\u5f02\u6837\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u53ef\u7591\u7684\u4eba\u3002\u201c\u592a\u7d27\u5f20\u4e86\u5427\u3002\u201d\u4ed6\u5600\u5495\u7740\uff0c\u5f88\u5feb\u4e5f\u5c31\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002</p><p>\u203b\u203b\u203b</p><p>\u9b54\u9b3c\u7684\u653b\u57ce\uff0c\u8ba9\u743c\u6069\u53d8\u5f97\u6e05\u95f2\u8d77\u6765\uff0c\u4ed6\u65e2\u4e0d\u9700\u8981\u51fa\u53bb\u5bfb\u627e\u201c\u6750\u6599\u201d\uff0c\u53c8\u975e\u6218\u6597\u4eba\u5458\uff0c\u4e00\u7247\u5fd9\u4e71\u4e4b\u95f4\u57fa\u672c\u90fd\u6ca1\u8c01\u60f3\u8d77\u4ed6\uff0c\u6b63\u597d\u8eb2\u5728\u5bb6\u91cc\u7761\u5927\u89c9\u3002\u5f53\u7136\u743c\u6069\u6ca1\u8fd9\u4e48\u505a\uff0c\u800c\u662f\u5728\u8ba4\u771f\u5730\u89c2\u5bdf\u7740\u6218\u51b5\uff0c\u6bd5\u7adf\u8fd9\u4e5f\u5173\u7cfb\u5230\u81ea\u5df1\u7684\u6027\u547d\u5462\u3002</p><p>\u9ed1\u77f3\u57ce\u975e\u5e38\u5927\uff0c\u662f\u963f\u5f17\u7eb3\u65af\u6392\u540d\u524d\u4e94\u7684\u5927\u57ce\u5e02\uff0c\u743c\u6069\u7684\u4f4f\u5904\u5728\u57ce\u5e02\u8f83\u4e2d\u5fc3\u7684\u4f4d\u7f6e\uff0c\u539f\u672c\u662f\u65e0\u6cd5\u76f4\u63a5\u89c2\u5bdf\u5230\u6218\u573a\u7684\uff0c\u6240\u5e78\u4ed6\u6709\u671b\u8fdc\u955c\u3002\u76f4\u63a5\u53ec\u6765\u56db\u53ea\u9ab7\u9ac5\u6b66\u58eb\uff0c\u547d\u4ee4\u5b83\u4eec\u4ee5\u53e0\u7f57\u6c49\u7684\u65b9\u5f0f\u77d7\u5728\u5c4b\u9876\u4e0a\uff0c\u62ff\u7740\u671b\u8fdc\u955c\uff0c\u518d\u914d\u5408\u4e0a\u4e00\u4e9b\u7b80\u5355\u7684\u9884\u8a00\u955c\u50cf\u6cd5\u672f\uff0c\u743c\u6069\u7b49\u4eba\u5c31\u53ef\u4ee5\u60a0\u95f2\u5730\u5728\u623f\u95f4\u91cc\u89c2\u770b\u524d\u7ebf\u6218\u5c40\u4e86\u3002</p><p>\u201c\u9b54\u9b3c\u771f\u5bcc\u88d5\uff0c\u201d\u743c\u6069\u4e00\u8fb9\u770b\u7740\u5730\u9762\u4e0a\u6620\u5c04\u51fa\u6765\u7684\u5f71\u50cf\u4e00\u8fb9\u611f\u53f9\uff0c\u201c\u5c16\u523a\u9b54\u4ee5\u4e0a\u5c31\u57fa\u672c\u90fd\u6709\u5168\u8eab\u94e0\u7532\u4e86\uff0c\u770b\u770b\u6076\u9b54\u8fd9\u8fb9\u5c31\u5bd2\u9178\u8bb8\u591a\uff0c\u53ea\u6709\u6b7b\u4ea1\u9a91\u58eb\u624d\u662f\u94c1\u7f50\u5934\uff0c\u5438\u8840\u9b3c\u5f80\u5f80\u90fd\u53ea\u6709\u4ef6\u76ae\u7532\u3002\u201d</p><p>\u6885\u83f2\u65af\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u5438\u8840\u9b3c\u57fa\u672c\u90fd\u8d70\u7075\u5de7\u8def\u7ebf\uff0c\u5982\u679c\u7a7f\u91cd\u94e0\u5c82\u4e0d\u662f\u81ea\u627e\u9ebb\u70e6\u3002\u201d</p><p>\u201c\u6df1\u6e0a\u91cc\u5176\u4ed6\u6076\u9b54\u4e5f\u6ca1\u89c1\u51e0\u4e2a\u7a7f\u76d4\u7532\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u770b\u5411\u65c1\u8fb9\u7684\u9b45\u9b54\uff0c\u201c\u838e\u73de\u514b\uff0c\u8fd9\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u5c31\u662f\u6076\u9b54\u7684\u98ce\u683c\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5b83\u4eec\u5982\u679c\u53ef\u4ee5\u7684\u8bdd\uff0c\u5b81\u53ef\u591a\u4e70\u51e0\u628a\u5251\u5e26\u5728\u8eab\u4e0a\uff0c\u780d\u574f\u4e86\u968f\u65f6\u53ef\u4ee5\u66f4\u6362\uff0c\u4f46\u5bf9\u4e8e\u7a7f\u76d4\u7532\u57fa\u672c\u90fd\u6ca1\u5174\u8da3\u3002\u56e0\u4e3a\u5b83\u4eec\u90fd\u89c9\u5f97\uff0c\u8fdb\u653b\u8fdb\u653b\u518d\u8fdb\u653b\uff0c\u8fd9\u624d\u662f\u771f\u6b63\u7684\u6218\u6597\u3002\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u518d\u53bb\u770b\u6218\u573a\u60c5\u5f62\uff0c\u9b54\u9b3c\u5df2\u7ecf\u5217\u9635\u5b8c\u6bd5\uff0c\u5f00\u59cb\u53d1\u8d77\u4e86\u51b2\u950b\uff0c\u628a\u4ea1\u7075\u903c\u5f97\u6b65\u6b65\u540e\u9000\u3002\u201c\u62dc\u5c14\u662f\u6253\u7b97\u505a\u6700\u540e\u7684\u52aa\u529b\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5982\u679c\u4eca\u5929\u4e0d\u80fd\u6218\u80dc\uff0c\u4ed6\u4f30\u8ba1\u5c31\u8981\u627f\u8ba4\u5931\u8d25\u4e86\u3002\u201d</p><p>\u201c\u4f60\u89c9\u5f97\u4ed6\u6709\u51e0\u6210\u80dc\u7b97\uff1f\u201d</p><p>\u6885\u83f2\u65af\u6c89\u541f\u7740\uff0c\u6447\u6447\u5934\uff0c\u201c\u6211\u89c9\u5f97\u4e0d\u8d85\u8fc7\u56db\u6210\u3002\u201d</p><p>\u201c\u4e0d\u4e00\u5b9a\u5427\uff0c\u827e\u5f25\u8587\uff0c\u201d\u51db\u5728\u65c1\u8fb9\u63d0\u51fa\u5f02\u8bae\uff0c\u201c\u5982\u679c\u4ed6\u80fd\u518d\u6b21\u91cd\u521b\u795e\u8eaf\u9b54\u50cf\u7684\u8bdd\uff0c\u672a\u5fc5\u4e0d\u80fd\u83b7\u80dc\u3002\u201d</p><p>\u201c\u9b54\u50cf\u662f\u53ef\u4ee5\u4fee\u590d\u7684\uff0c\u62dc\u5c14\u7684\u5175\u529b\u5374\u4e0d\u53ef\u80fd\u8fc5\u901f\u8865\u5145\u3002\u201d</p><p>\u201c\u9b54\u50cf\u4fee\u590d\u9700\u8981\u6750\u6599\u554a\uff0c\u73b0\u5728\u5df2\u7ecf\u662f\u7b2c\u56db\u5929\u4e86\uff0c\u4f60\u4eec\u5f04\u6765\u7684\u90a3\u4e9b\u6750\u6599\u65e9\u5c31\u5e94\u8be5\u7528\u5149\uff0c\u5c31\u7b97\u6ca1\u7528\u5149\uff0c\u73b0\u5728\u4e5f\u8fc7\u671f\u4e0d\u80fd\u4f7f\u7528\u4e86\u3002\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u51db\uff0c\u4f60\u8fd9\u51e0\u5929\u5728\u57ce\u91cc\uff0c\u6ca1\u53d1\u73b0\u6709\u4ec0\u4e48\u5f02\u6837\u4e48\uff1f\u201d</p><p>\u201c\u5f02\u6837\uff1f\u201d\u51db\u83ab\u540d\u5176\u5999\uff0c\u201c\u4f60\u662f\u6307\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u770b\u5230\u7684\u5168\u662f\u4ea1\u7075\uff0c\u6076\u9b54\u6ca1\u6709\u534a\u4e2a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5b83\u4eec\u53bb\u54ea\u91cc\u4e86\uff1f\u201d</p><p>\u9752\u94dc\u57ce\u5821\u4e00\u6218\uff0c\u6076\u9b54\u4eec\u662f\u88ab\u6b4c\u560e\u65af\u6392\u5728\u9635\u524d\uff0c\u6700\u5148\u88ab\u9b54\u9b3c\u51fb\u6e83\uff0c\u4f46\u4e5f\u56e0\u6b64\u9003\u5f97\u6700\u5feb\uff0c\u53cd\u800c\u6709\u5c0f\u534a\u90fd\u9646\u9646\u7eed\u7eed\u8fd4\u56de\u4e86\u9ed1\u77f3\u57ce\u3002\u838e\u73de\u514b\u7684\u6d88\u606f\uff0c\u90fd\u662f\u4ece\u8fd9\u4e9b\u6076\u9b54\u6e83\u5175\u53e3\u4e2d\u6253\u63a2\u6765\u7684\u3002\u7136\u800c\u73b0\u5728\u6218\u573a\u4e0a\u62fc\u6740\u7684\uff0c\u57ce\u5934\u4e0a\u9632\u5b88\u7684\uff0c\u5168\u662f\u4ea1\u7075\uff0c\u4e0d\u89c1\u534a\u4e2a\u6076\u9b54\u3002</p><p>\u201c\u5b83\u4eec\u90fd\u88ab\u2026\u2026\u201d\u51db\u660e\u767d\u4e86\u743c\u6069\u7684\u610f\u601d\u3002</p><p>\u201c\u662f\u554a\uff0c\u201d\u743c\u6069\u53f9\u6c14\uff0c\u201c\u4e0d\u5f97\u4e0d\u8bf4\uff0c\u8fea\u5185\u7279\u867d\u7136\u672a\u5fc5\u6709\u591a\u5c11\u672c\u4e8b\uff0c\u4f46\u8bba\u53ca\u5fc3\u72e0\u624b\u8fa3\uff0c\u884c\u4e8b\u679c\u65ad\uff0c\u90a3\u53ef\u771f\u662f\u6ca1\u8bdd\u8bf4\u3002\u9b54\u9b3c\u8fd8\u6ca1\u5230\u57ce\u4e0b\uff0c\u4ed6\u5c31\u5df2\u7ecf\u5148\u628a\u90a3\u4e9b\u6076\u9b54\u5168\u90fd\u6740\u7684\u6740\uff0c\u56da\u7981\u7684\u56da\u7981\uff0c\u5c31\u7b49\u7740\u5904\u51b3\u7136\u540e\u62ff\u53bb\u4fee\u590d\u9b54\u50cf\u5462\u3002\u5c31\u6211\u8fd9\u4e24\u5929\u7684\u89c2\u5bdf\uff0c\u90a3\u4e9b\u6076\u9b54\u4eec\u8fd8\u6709\u4e00\u5c0f\u534a\u662f\u6d3b\u7684\uff0c\u8db3\u591f\u9b54\u50cf\u5927\u4fee\u4e24\u5230\u4e09\u6b21\u2014\u2014\u62dc\u5c14\u8fd8\u80fd\u91cd\u521b\u9b54\u50cf\u51e0\u6b21\uff1f\u201d</p><p>\u201c\u6700\u591a\u4e24\u6b21\uff0c\u201d\u6885\u83f2\u65af\u4f5c\u51fa\u5224\u65ad\uff0c\u201c\u800c\u4e14\u8fd9\u662f\u5efa\u7acb\u5728\u4ed6\u613f\u610f\u5b64\u6ce8\u4e00\u63b7\uff0c\u628a\u6240\u6709\u7684\u5175\u529b\u90fd\u586b\u8fdb\u53bb\u7684\u524d\u63d0\u4e0a\u3002\u6211\u60f3\u4ed6\u4e0d\u4f1a\u8fd9\u4e48\u505a\uff0c\u5426\u5219\u5c31\u7b97\u6253\u8d62\u773c\u4e0b\u8fd9\u4e00\u4ed7\uff0c\u4e5f\u6321\u4e0d\u4f4f\u540e\u9762\u7684\u6076\u9b54\u5927\u519b\u3002\u6362\u4e86\u6211\u662f\u4ed6\uff0c\u5e94\u8be5\u4f1a\u4eca\u5929\u518d\u62fc\u4e00\u6b21\uff0c\u6210\u529f\u5c31\u884c\uff0c\u4e0d\u6210\u529f\uff0c\u90a3\u5c31\u53ea\u6709\u64a4\u9000\u4e86\u3002\u201d</p><p>\u5927\u5bb6\u90fd\u4e0d\u518d\u8bf4\u8bdd\uff0c\u7ee7\u7eed\u89c2\u5bdf\u6218\u5c40\uff0c\u6ca1\u8fc7\u591a\u4e45\uff0c\u795e\u8eaf\u9b54\u50cf\u518d\u5ea6\u767b\u573a\uff0c\u548c\u70bc\u9b54\u3001\u5815\u5929\u4f7f\u548c\u708e\u9f99\u4e00\u573a\u6076\u6218\uff0c\u6700\u540e\u88ab\u66e6\u5929\u4f7f\u5f25\u8d5b\u4e9a\u8d81\u865a\u800c\u5165\uff0c\u4e00\u51fb\u7834\u90aa\u65a9\u51e0\u4e4e\u6210\u529f\u5f97\u624b\uff0c\u4f46\u5728\u6700\u540e\u5173\u5934\u8fd8\u662f\u529f\u4e8f\u4e00\u7bd1\uff0c\u88ab\u64cd\u63a7\u9b54\u50cf\u7684\u516b\u4f4d\u5927\u5deb\u5996\u53ca\u65f6\u6536\u4e86\u56de\u53bb\u3002</p><p>\u201c\u62dc\u5c14\u592a\u7b28\u4e86\uff0c\u201d\u51db\u53d1\u8868\u8bc4\u8bba\uff0c\u201c\u5176\u5b9e\u8fd9\u9b54\u50cf\u5f88\u597d\u5bf9\u4ed8\uff0c\u4f55\u5fc5\u8fd9\u4e48\u9ebb\u70e6\uff0c\u627e\u4e00\u7fa4\u5deb\u5e08\u5728\u5b83\u811a\u5e95\u4e0b\u653e\u5316\u77f3\u4e3a\u6ce5\uff0c\u628a\u5b83\u9677\u8fdb\u53bb\u4e0d\u5c31\u597d\u4e86\u3002\u201d</p><p>\u201c\u5316\u77f3\u4e3a\u6ce5\u53ef\u662f\u96be\u5ea6\u5f88\u9ad8\u7684\u6cd5\u672f\uff0c\u5fc5\u987b\u51dd\u6210\u771f\u540d\u7684\u9ad8\u9636\u5deb\u5e08\u624d\u80fd\u91ca\u653e\uff0c\u201d\u743c\u6069\u7b11\u8d77\u6765\uff0c\u201c\u800c\u4e14\u8fd9\u53ef\u4e0d\u662f\u666e\u901a\u7684\u9b54\u50cf\uff0c\u529b\u6c14\u6781\u5927\uff0c\u8df3\u5f97\u53c8\u9ad8\uff0c\u53cd\u5e94\u6bd4\u4eba\u8fd8\u7075\u654f\uff0c\u4f60\u8981\u628a\u5b83\u9677\u4f4f\uff0c\u5c11\u8bf4\u4e5f\u5f97\u4e0a\u767e\u540d\u9ad8\u9636\u5deb\u5e08\u3002\u8fd9\u5c31\u7b97\u662f\u5728\u7269\u8d28\u754c\uff0c\u5927\u6982\u4e5f\u53ea\u6709\u9634\u9b42\u57ce\u3001\u7ea2\u888d\u5deb\u5e08\u4f1a\u8fd9\u79cd\u5730\u65b9\u80fd\u529e\u5230\uff0c\u62dc\u5c14\u600e\u4e48\u53ef\u80fd\u6709\u3002\u201d</p><p>\u201c\u5176\u5b9e\u8fd9\u4e2a\u6218\u672f\u6211\u4eec\u7528\u8fc7\u3002\u201d\u5750\u5728\u89d2\u843d\u91cc\u4e00\u76f4\u9ed8\u4e0d\u4f5c\u58f0\u7684\u6b32\u9b54\u4f0a\u84d3\u4e1d\u8bf4\uff0c\u5979\u5f53\u65e5\u9047\u5230\u743c\u6069\uff0c\u88ab\u53d8\u6210\u5938\u8d5b\u9b54\u5e26\u56de\u57ce\u4e2d\uff0c\u7531\u838e\u73de\u514b\u770b\u7ba1\uff0c\u4e00\u76f4\u5904\u4e8e\u8f6f\u7981\u4e4b\u4e0b\u3002\u5012\u662f\u8fd9\u51e0\u65e5\u9b54\u9b3c\u653b\u57ce\u4f5c\u6218\uff0c\u743c\u6069\u628a\u5979\u4e5f\u8bf7\u4e86\u8fc7\u6765\uff0c\u987a\u4fbf\u54a8\u8be2\u610f\u89c1\u3002</p><p>\u201c\u7528\u8fc7\uff1f\u62dc\u5c14\u6709\u8fd9\u4e48\u591a\u5deb\u5e08\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u4f46\u53ef\u4ee5\u8bf7\uff0c\u62dc\u5c14\u5927\u4eba\u82b1\u91cd\u91d1\u4ece\u5c24\u683c\u7f57\u65af\u9b54\u548c\u5409\u65af\u6d0b\u57fa\u4eba\u90a3\u91cc\u8bf7\u5230\u4e86\u4e00\u6279\u5deb\u5e08\uff0c\u540c\u65f6\u91ca\u653e\u5316\u77f3\u4e3a\u6ce5\uff0c\u51c6\u5907\u628a\u8fd9\u9b54\u50cf\u56f0\u4f4f\u3002\u201d</p><p>\u201c\u7ed3\u679c\u5462\uff1f\u201d</p><p>\u201c\u7ed3\u679c\u53d1\u73b0\u5b83\u539f\u6765\u8fd8\u4f1a\u98de\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u5c31\u7b97\u80fd\u9677\u8fdb\u53bb\u4e5f\u6ca1\u7528\uff0c\u5deb\u5996\u662f\u53ef\u4ee5\u968f\u65f6\u628a\u5b83\u6536\u56de\u53bb\u7684\uff0c\u201d\u6885\u83f2\u65af\u63d0\u9192\uff0c\u201c\u4e0d\u4ec5\u5982\u6b64\uff0c\u5b83\u8fd8\u80fd\u53d8\u5e7b\u795e\u76f8\u3002\u201d</p><p>\u201c\u795e\u76f8\uff1f\u201d</p><p>\u201c\u6ca1\u770b\u51fa\u6765\u4e48\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5b83\u65e2\u7136\u53eb\u795e\u8eaf\u9b54\u50cf\uff0c\u90a3\u660e\u663e\u662f\u7528\u795e\u660e\u7684\u5c38\u4f53\u5236\u6210\uff0c\u800c\u4e14\u8fd8\u4e0d\u6b62\u4e00\u4f4d\u795e\u660e\u3002\u6211\u6253\u542c\u8fc7\u9752\u94dc\u57ce\u5821\u90a3\u4e00\u6218\u7684\u60c5\u5f62\uff0c\u5f53\u65f6\u795e\u8eaf\u9b54\u50cf\u53d8\u6210\u4e86\u4e00\u4e2a\u8499\u9762\u5353\u5c14\u6218\u58eb\uff0c\u5341\u6709\u516b\u4e5d\u662f\u7ef4\u7eb6\uff0c\u800c\u5b83\u521a\u624d\u53d8\u5e7b\u6210\u7684\u90a3\u53ea\u5de8\u578b\u8f6f\u6ce5\u602a\uff0c\u5206\u660e\u5c31\u662f\u5173\u7eb3\u5fb7\u3002\u201d</p><p>\u743c\u6069\u4e00\u6014\uff0c\u201c\u5173\u7eb3\u5fb7\uff1f\u4f60\u4e0d\u662f\u8bf4\u4ed6\u9003\u79bb\u4e86\u8718\u86db\u9b54\u7f51\uff0c\u8dd1\u5230\u5e7d\u6df1\u6d1e\u7a9f\u4f4d\u9762\u4e86\u4e48\uff1f\u201d</p><p>\u201c\u90a3\u662f\u534a\u5e74\u524d\u7684\u4e8b\u60c5\u4e86\uff0c\u770b\u73b0\u5728\u8fd9\u6837\u5b50\uff0c\u5b83\u4e5f\u6ca1\u80fd\u9003\u8131\u7f57\u4e1d\u7684\u6bd2\u624b\u3002\u201d</p><p>\u201c\u5514\uff0c\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u5353\u5c14\u516d\u4f4d\u795e\u660e\uff0c\u7ef4\u7eb6\u548c\u9f50\u96c5\u6e29\u5239\u4e3d\u88ab\u7f57\u4e1d\u6467\u6bc1\uff0c\u4f0a\u8389\u4e1d\u7fe0\u6c89\u7720\uff0c\u5982\u4eca\u5173\u7eb3\u5fb7\u4e5f\u5b8c\u86cb\uff0c\u90a3\u5c31\u53ea\u5269\u4e0b\u7f57\u4e1d\u548c\u5979\u7684\u4ec6\u4ece\u5e2d\u6587\u5854\u59c6\u4e86\u3002\u201d</p><p>\u201c\u5bf9\uff0c\u5353\u5c14\u795e\u7cfb\uff0c\u7b97\u662f\u771f\u6b63\u7edf\u4e00\u4e86\u3002\u201d</p><p>\u201c\u770b\u6765\u5965\u5580\u65af\u548c\u7f57\u4e1d\u4e5f\u6709\u52fe\u7ed3\uff1f\u5426\u5219\u7ef4\u7eb6\u548c\u5173\u7eb3\u5fb7\u7684\u795e\u5c38\u600e\u4e48\u90fd\u843d\u5230\u4ed6\u7684\u624b\u91cc\uff0c\u201d\u743c\u6069\u6c89\u541f\u7740\uff0c\u7a81\u7136\u5fc3\u4e2d\u5fae\u5fae\u4e00\u52a8\uff0c\u201c\u5965\u5580\u65af\u3001\u7f57\u4e1d\uff0c\u8fd9\u4e2d\u95f4\u4f3c\u4e4e\u8fd8\u5c11\u4e00\u4e2a\u8054\u7ed3\u8005\u2026\u2026\u662f\u90a3\u4e2a\u5bb6\u4f19\u5427\u3002\u201d</p><p>\u6b63\u81ea\u601d\u5fd6\uff0c\u51db\u7a81\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u50cf\u662f\u53d1\u73b0\u4e86\u4ec0\u4e48\uff0c\u743c\u6069\u987a\u7740\u5979\u7684\u773c\u5149\u671b\u53bb\uff0c\u53ea\u89c1\u9b54\u6cd5\u955c\u50cf\u4e2d\u6b63\u663e\u793a\u51fa\u4e00\u4e2a\u5438\u8840\u9b3c\u7684\u4fa7\u8138\uff0c\u201c\u8fea\u5185\u7279\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u4ed6\u66fe\u7ecf\u8fdc\u8fdc\u89c1\u8fc7\u51e0\u6b21\uff0c\u8fa8\u8ba4\u4e86\u51fa\u6765\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u4ed6\u5c31\u662f\u90a3\u5929\u6211\u9047\u5230\u7684\u90a3\u4e2a\u6df7\u86cb\u8272\u72fc\u3002\u201d</p><p>\u201c\u662f\u4ed6\uff1f\u201d</p><p>\u51db\u80af\u5b9a\u5730\u70b9\u70b9\u5934\u3002</p><p>\u743c\u6069\u5fae\u5fae\u76b1\u7709\uff0c\u7136\u540e\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u770b\u6765\u6211\u4eec\u7684\u65b0\u4efb\u7edf\u5e05\u5927\u4eba\u773c\u5149\u8fd8\u771f\u662f\u4e0d\u5dee\u5462\u3002\u201d</p><p>\u4ed6\u8f6c\u8fc7\u5934\uff0c\u770b\u5411\u5750\u5728\u89d2\u843d\u91cc\u7684\u6b32\u9b54\uff0c\u201c\u73b0\u5728\uff0c\u4f0a\u84d3\u4e1d\u5c0f\u59d0\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u53ef\u4ee5\u518d\u6765\u8c08\u8c08\u5408\u4f5c\u4e8b\u5b9c\u4e86\uff1f\u201d</p></article>"}}}]);
