(window.webpackJsonp=window.webpackJsonp||[]).push([[1565],{1588:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u56db\u8282 \u6218\u80dc",content:"<article><h2>\u7b2c\u5341\u56db\u8282 \u6218\u80dc</h2><p>\u6b63\u5348\u7684\u9633\u5149\u660e\u4eae\u523a\u773c\uff0c\u7167\u5728\u6b4c\u560e\u65af\u7684\u6697\u91d1\u8272\u5934\u53d1\u548c\u76ae\u80a4\u4e0a\u71a0\u71a0\u751f\u5149\uff0c\u4ed6\u4ece\u5e9f\u589f\u91cc\u6447\u6447\u6643\u6643\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u6162\u6162\u73af\u89c6\u56db\u5468\uff0c\u770b\u89c1\u4e86\u743c\u6069\u548c\u62dc\u5c14\u3002</p><p>\u7136\u540e\u4ed6\u4e00\u6b65\u6b65\u7f13\u7f13\u8d70\u8fc7\u6765\u3002</p><p>\u8ddd\u79bb\u6709\u70b9\u8fdc\uff0c\u4f46\u743c\u6069\u7684\u89c6\u529b\u4e0d\u9519\uff0c\u80fd\u591f\u6e05\u695a\u5730\u770b\u89c1\u5438\u8840\u9b3c\u73b0\u5728\u7684\u6a21\u6837\uff0c\u548c\u62dc\u5c14\u4e00\u6837\uff0c\u4ed6\u8eab\u4e0a\u843d\u6ee1\u77f3\u5c51\u5c18\u571f\uff0c\u53f3\u817f\u4f3c\u4e4e\u7638\u4e86\uff0c\u8d70\u8d77\u8def\u6765\u59ff\u52bf\u660e\u663e\u4e0d\u592a\u5bf9\u52b2\uff0c\u91cd\u5fc3\u504f\u79fb\u3002\u53cc\u81c2\u5b8c\u597d\u65e0\u635f\uff0c\u4f46\u80f8\u53e3\u5de6\u4fa7\u5fc3\u810f\u90e8\u4f4d\uff0c\u8d6b\u7136\u5f00\u4e86\u4e00\u4e2a\u5706\u5f62\u5927\u6d1e\uff0c\u50cf\u662f\u88ab\u5de8\u5927\u7684\u7bad\u77e2\u900f\u4f53\u5c04\u7a7f\uff0c\u770b\u8d77\u6765\u9887\u4e3a\u5413\u4eba\u3002</p><p>\u5bf9\u4e8e\u5438\u8840\u9b3c\uff0c\u743c\u6069\u7b97\u4e0d\u4e0a\u5f88\u4e86\u89e3\uff0c\u4f46\u4e5f\u4e0d\u964c\u751f\uff0c\u6bd5\u7adf\u4e5f\u6253\u8fc7\u4e0d\u5c11\u4ea4\u9053\u4e86\u3002\u5728\u62c9\u6c83\u514b\u7684\u5deb\u5e08\u4e4b\u5893\uff0c\u548c\u5438\u8840\u9b3c\u7f8e\u5973\u8389\u6cd5\u5c14\u4e0a\u8fc7\u5e8a\uff0c\u6709\u8fc7\u77ed\u6682\u4f46\u5f88\u6109\u5feb\u7684\u65f6\u5149\uff1b\u5728\u6668\u70bc\u57ce\u7684\u65f6\u5019\uff0c\u548c\u67d0\u4f4d\u53eb\u505a\u5fb7\u53e4\u62c9\u7684\u59b9\u63a7\u76f8\u8c08\u751a\u6b22\uff0c\u4e00\u8d77\u62ff\u671b\u8fdc\u955c\u5077\u7aa5\u8fc7\u8475\u9732\u7684\u88f8\u821e\uff1b\u5c31\u5728\u4e0d\u5230\u534a\u5c0f\u65f6\u4e4b\u524d\uff0c\u4ed6\u521a\u521a\u548c\u540c\u4f34\u6740\u4e86\u4e00\u4e2a\u5438\u8840\u9b3c\uff0c\u8fd8\u662f\u4e2a\u4eb2\u738b\u3002</p><p>\u4e16\u754c\u4e0a\u7684\u4ea1\u7075\u5343\u5947\u767e\u602a\uff0c\u79cd\u7c7b\u4e0d\u53ef\u80dc\u6570\uff0c\u5c31\u4fdd\u5b88\u4f30\u8ba1\u7684\u8bdd\u5e94\u8be5\u4e5f\u6709\u8fd1\u767e\u79cd\uff0c\u5176\u4e2d\u5438\u8840\u9b3c\u548c\u751f\u8005\u6700\u4e3a\u8fd1\u4f3c\uff0c\u4ed6\u4eec\u6709\u7075\u89c9\uff0c\u6709\u610f\u8bc6\uff0c\u6709\u667a\u529b\uff0c\u6709\u8bb0\u5fc6\uff0c\u8fde\u8eaf\u4f53\u5916\u5f62\u90fd\u548c\u6d3b\u7740\u7684\u65f6\u5019\u65e0\u5f02\uff0c\u6700\u591a\u957f\u51fa\u4e24\u9897\u7360\u7259\uff0c\u4f46\u5e73\u65f6\u6536\u8d77\u6765\u4e5f\u5c31\u770b\u4e0d\u5230\u4e86\u3002\u800c\u4e14\u5438\u8840\u9b3c\u8fd8\u5fc5\u987b\u8fdb\u98df\uff08\u5438\u8840\uff09\uff0c\u5426\u5219\u5c31\u4f1a\u865a\u5f31\u4e0b\u53bb\u3002\u6709\u4e00\u4e9b\u5b66\u8005\u751a\u81f3\u8ba4\u4e3a\uff1a\u5438\u8840\u9b3c\u5176\u5b9e\u4e0d\u80fd\u7b97\u662f\u771f\u6b63\u7684\u4ea1\u7075\uff0c\u66f4\u8fd1\u4f3c\u4e8e\u4e00\u79cd\u53d8\u5f02\u7684\u6d3b\u4eba\u3002</p><p>\u9664\u4e86\u5916\u5f62\u8fd1\u4f3c\uff0c\u5438\u8840\u9b3c\u4e5f\u4f9d\u65e7\u79c9\u627f\u4e86\u5f88\u591a\u4eba\u7c7b\u7684\u7279\u5f81\uff0c\u6216\u8005\u8bf4\u5f31\u70b9\u3002\u5176\u4ed6\u4ea1\u7075\uff0c\u4f8b\u5982\u50f5\u5c38\u3001\u6728\u4e43\u4f0a\u4e4b\u7c7b\uff0c\u88ab\u523a\u7a7f\u5fc3\u810f\u6839\u672c\u65e0\u6240\u8c13\uff0c\u53cd\u6b63\u90fd\u5df2\u7ecf\u4e0d\u8df3\u4e86\uff0c\u7eaf\u7cb9\u662f\u4e2a\u6446\u8bbe\uff0c\u81f3\u4e8e\u9ab7\u9ac5\u8fd9\u79cd\u9aa8\u611f\u7c7b\u578b\u7684\u4ea1\u7075\uff0c\u66f4\u538b\u6839\u5c31\u6ca1\u6709\u5fc3\u810f\u8fd9\u79cd\u4e1c\u897f\u3002\u4f46\u5bf9\u4e8e\u5438\u8840\u9b3c\u6765\u8bf4\uff0c\u5fc3\u810f\u548c\u5934\u9885\u4e00\u6837\uff0c\u662f\u771f\u6b63\u81f4\u547d\u4e4b\u5904\uff0c\u5c24\u5176\u662f\u50cf\u6b4c\u560e\u65af\u8fd9\u79cd\u664b\u5347\u5230\u4eb2\u738b\u4f4d\u9636\u7684\u5438\u8840\u9b3c\uff0c\u65ad\u624b\u65ad\u811a\u90fd\u53ea\u89c6\u4f5c\u5bfb\u5e38\uff0c\u534a\u8fb9\u8eab\u4f53\u780d\u6389\u90fd\u53ef\u4ee5\u4e0d\u5f53\u56de\u4e8b\uff0c\u9664\u4e86\u8fd9\u4e24\u4e2a\u5730\u65b9\uff0c\u518d\u6ca1\u5176\u4ed6\u8981\u5bb3\u4e86\u3002\u53cd\u8fc7\u6765\u8bf4\uff0c\u53ea\u8981\u628a\u8111\u888b\u780d\u6389\u6216\u8005\u628a\u5fc3\u810f\u523a\u7a7f\uff0c\u5c31\u7b97\u662f\u6700\u5f3a\u7684\u4eb2\u738b\u5438\u8840\u9b3c\uff0c\u4e5f\u662f\u96be\u9003\u4e00\u6b7b\u7684\u3002</p><p>\u62dc\u5c14\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u66e6\u5929\u4f7f\u4e00\u8bb0\u77ac\u6740\u7bad\u5c04\u7a7f\u4e86\u6b4c\u560e\u65af\u7684\u5fc3\u810f\uff0c\u8fd9\u70b9\u73b0\u5728\u743c\u6069\u4e5f\u4eb2\u773c\u770b\u89c1\u4e86\u3002\u4ee5\u5e38\u7406\u800c\u8bba\uff0c\u8fd9\u5438\u8840\u9b3c\u786e\u5b9e\u662f\u5fc5\u6b7b\u65e0\u7591\u624d\u5bf9\uff0c\u7136\u800c\u4ed6\u4f3c\u4e4e\u5e76\u6ca1\u6709\u5012\u4e0b\u53bb\u7684\u8ff9\u8c61\u3002\u8fd9\u79cd\u60c5\u5f62\uff0c\u4f3c\u4e4e\u53ea\u6709\u4e00\u79cd\u6bd4\u8f83\u5408\u7406\u7684\u89e3\u91ca\u2026\u2026</p><p>\u201c\u96be\u4e0d\u6210\u2026\u2026\u201d</p><p>\u201c\u539f\u6765\u4ed6\u5fc3\u810f\u957f\u5728\u53f3\u8fb9\u3002\u201d</p><p>\u548c\u743c\u6069\u4e00\u6837\uff0c\u62dc\u5c14\u7acb\u523b\u53cd\u5e94\u8fc7\u6765\u662f\u600e\u4e48\u56de\u4e8b\u3002\u5927\u5343\u4e16\u754c\u65e0\u5947\u4e0d\u6709\uff0c\u5fc3\u810f\u8fd9\u79cd\u4e1c\u897f\u901a\u5e38\u90fd\u957f\u5728\u5de6\u8fb9\uff0c\u4f46\u6709\u4e9b\u5bb6\u4f19\u5c31\u662f\u957f\u5728\u53f3\u8fb9\u7684\uff0c\u8ba9\u4eba\u4e5f\u65e0\u53ef\u5948\u4f55\uff0c\u773c\u524d\u7684\u8fd9\u4f4d\u5438\u8840\u9b3c\uff0c\u5e94\u8be5\u5c31\u5c5e\u4e8e\u8fd9\u79cd\u7279\u4f8b\u3002</p><p>\u731c\u51fa\u4e86\u662f\u4ec0\u4e48\u539f\u56e0\uff0c\u4f46\u5bf9\u89e3\u51b3\u9762\u4e34\u7684\u95ee\u9898\u5e76\u6ca1\u6709\u4ec0\u4e48\u5e2e\u52a9\u3002\u6b4c\u560e\u65af\u6b63\u671d\u8fd9\u8fb9\u8d70\u8fc7\u6765\uff0c\u867d\u7136\u660e\u663e\u53d7\u4f24\u672a\u6108\uff0c\u817f\u811a\u4e0d\u7075\uff0c\u884c\u52a8\u8fdc\u4e0d\u5982\u5e73\u65f6\u5feb\u6377\uff0c\u4f46\u518d\u8fc7\u4e00\u4e24\u5206\u949f\u4e5f\u5c31\u8d70\u5230\u8ddf\u524d\u3002\u743c\u6069\u5982\u679c\u8981\u81ea\u5df1\u9003\u547d\uff0c\u90a3\u4e48\u5012\u8fd8\u6765\u5f97\u53ca\uff0c\u7acb\u523b\u542f\u52a8\u98de\u884c\u672f\u5c31\u662f\uff0c\u53cd\u6b63\u6b4c\u560e\u65af\u4e5f\u672a\u5fc5\u4f1a\u7406\u776c\u4ed6\u3002\u4f46\u62dc\u5c14\u5374\u662f\u6b7b\u5b9a\u4e86\uff0c\u4ed6\u4e00\u6761\u817f\u88ab\u538b\u5728\u77f3\u5934\u4e0b\u9762\uff0c\u80f8\u8179\u90e8\u4f4d\u7684\u91cd\u521b\u8fd8\u6ca1\u6062\u590d\uff0c\u6574\u4e2a\u4eba\u52a8\u5f39\u4e0d\u5f97\uff0c\u8fde\u6b66\u5668\u90fd\u4e0d\u77e5\u9053\u4e22\u5728\u54ea\u91cc\uff0c\u6740\u8d77\u6765\u6613\u5982\u53cd\u638c\u3002</p><p>\u62dc\u5c14\u7684\u6b7b\u6d3b\uff0c\u743c\u6069\u539f\u672c\u5012\u4e0d\u7528\u5173\u5fc3\uff0c\u95ee\u9898\u5728\u4e8e\u4ed6\u5982\u679c\u6b7b\u4e86\uff0c\u8c01\u9001\u81ea\u5df1\u7b49\u4eba\u56de\u7269\u8d28\u754c\uff0c\u8bb8\u8bfa\u7684\u90a3\u4e94\u767e\u9897\u4e0a\u7b49\u5207\u5de5\u7684\u7ea2\u5b9d\u77f3\u5c82\u4e0d\u66f4\u6210\u4e86\u6ce1\u5f71\u3002\u603b\u4e0d\u80fd\u8fd8\u56de\u65ad\u57df\u9547\u53bb\u627e\u683c\u62c9\u5179\u7279\u5427\uff0c\u743c\u6069\u5b9e\u5728\u4e0d\u60f3\u548c\u90a3\u4e2a\u516d\u6307\u4eba\u5996\u518d\u6253\u4ea4\u9053\u3002\u4f55\u51b5\u73b0\u5728\u4ece\u6574\u4f53\u5c40\u52bf\u4e0a\u6765\u770b\uff0c\u9b54\u9b3c\u4e00\u65b9\u660e\u663e\u5df2\u7ecf\u5360\u4e86\u4e0a\u98ce\uff0c\u8fde\u795e\u8eaf\u9b54\u50cf\u90fd\u5f04\u6210\u4e86\u4e0d\u5b8c\u5168\u4f53\uff0c\u518d\u4e0d\u590d\u4ee5\u524d\u6a2a\u626b\u516b\u9762\u7684\u795e\u5a01\uff0c\u6076\u9b54\u519b\u8d25\u4ea1\u662f\u6307\u65e5\u53ef\u5f85\u7684\u4e8b\u60c5\uff0c\u8fd9\u79cd\u65f6\u5019\u4e0d\u7ad9\u5728\u80dc\u5229\u8005\u4e00\u8fb9\uff0c\u90a3\u771f\u662f\u8111\u7b4b\u51fa\u95ee\u9898\u4e86\u3002</p><p>\u5f53\u7136\uff0c\u9009\u62e9\u7acb\u573a\u662f\u4e00\u56de\u4e8b\uff0c\u8d81\u706b\u6253\u52ab\u53c8\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u3002</p><p>\u201c\u5c06\u519b\uff0c\u90a3\u4e2a\u5438\u8840\u9b3c\u5df2\u7ecf\u8d70\u8fc7\u6765\u4e86\uff0c\u770b\u6837\u5b50\u4f60\u7684\u5c0f\u547d\u96be\u4fdd\u4e86\u3002\u201d</p><p>\u201c\u54e6\uff0c\u6211\u4e5f\u8fd9\u4e48\u89c9\u5f97\u3002\u201d</p><p>\u201c\u90a3\u4f60\u600e\u4e48\u8fd8\u80fd\u7b11\u5f97\u8fd9\u4e48\u707f\u70c2\u5462\uff0c\u8fd9\u70b9\u5b9e\u5728\u662f\u8ba9\u6211\u5f88\u8d39\u89e3\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6709\u4f60\u5728\u561b\uff0c\u6211\u4eec\u662f\u670b\u53cb\uff0c\u4f60\u603b\u4e0d\u4f1a\u89c1\u6b7b\u4e0d\u6551\u5427\u3002\u201d</p><p>\u201c\u4f60\u5bf9\u6211\u7684\u4eba\u54c1\u8fd9\u4e48\u6709\u4fe1\u5fc3\uff1f\u201d\u743c\u6069\u8be7\u5f02\uff0c\u201c\u597d\u5427\uff0c\u65e2\u7136\u4f60\u90fd\u8fd9\u4e48\u8bf4\u4e86\u2026\u2026\u518d\u52a0\u4e94\u767e\u9897\u7ea2\u5b9d\u77f3\uff0c\u6211\u5e2e\u4f60\u641e\u5b9a\u8fd9\u4e2a\u5438\u8840\u9b3c\uff0c\u5982\u4f55\uff1f\u201d</p><p>\u743c\u6069\u63d0\u51fa\u7684\u6761\u4ef6\uff0c\u4e0d\u80fd\u4e0d\u8bf4\u662f\u5f88\u5bbd\u539a\uff0c\u6216\u8005\u8bf4\u662f\u56e0\u4e3a\u4ed6\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\u60f3\u8981\u7684\u4e1c\u897f\u3002\u94b1\u8d22\u8fd9\u79cd\u4e1c\u897f\u81ea\u7136\u591a\u591a\u76ca\u5584\uff0c\u4f46\u53ea\u8981\u80fd\u8863\u98df\u65e0\u5fe7\uff0c\u4ed6\u4e5f\u5c31\u4e0d\u592a\u770b\u91cd\uff1b\u6743\u52bf\u4e4b\u7c7b\u7684\u5bf9\u4ed6\u4e5f\u6ca1\u4ec0\u4e48\u610f\u4e49\uff0c\u5c31\u7b97\u62dc\u5c14\u5c01\u4ed6\u505a\u57ce\u4e3b\u505a\u5c06\u519b\uff0c\u743c\u6069\u8fd8\u4e0d\u60f3\u5f53\u5462\uff1b\u81f3\u4e8e\u7f8e\u5973\u4e4b\u7c7b\uff0c\u8eab\u8fb9\u7684\u4e00\u7fa4\u90fd\u8fd8\u6ca1\u641e\u5b9a\uff0c\u54ea\u91cc\u987e\u5f97\u4e0a\u5176\u4ed6\u3002\u4e00\u65f6\u4e4b\u95f4\u4e5f\u60f3\u4e0d\u51fa\u6572\u8bc8\u4ec0\u4e48\uff0c\u7d22\u6027\u5c31\u628a\u539f\u672c\u7684\u4ef7\u7801\u518d\u7ffb\u4e00\u500d\uff0c\u62ff\u53bb\u8ba8\u597d\u51db\u5c31\u662f\u3002\u867d\u7136\u4e0d\u6307\u671b\u6709\u591a\u5927\u6548\u679c\uff0c\u4f46\u8fd9\u79cd\u5411\u5973\u5b69\u5b50\u4e3b\u52a8\u732e\u6bb7\u52e4\u7684\u4e8b\u60c5\uff0c\u505a\u4e86\u603b\u662f\u6709\u76ca\u65e0\u5bb3\u3002</p><p>\u201c\u4e94\u767e\u9897\u7ea2\u5b9d\u77f3\u4e48\u2026\u2026\u201d\u62dc\u5c14\u6c89\u541f\uff0c\u201c\u4f3c\u4e4e\u591a\u4e86\u70b9\uff0c\u4e09\u767e\u9897\u5982\u4f55\u3002\u201d</p><p>\u201c\u5c06\u519b\uff0c\u4f60\u672a\u514d\u592a\u5c0f\u6c14\u4e86\u5427\uff0c\u4e00\u4e2a\u4eb2\u738b\u5438\u8840\u9b3c\uff0c\u96be\u9053\u4e0d\u503c\u8fd9\u4e2a\u4ef7\u7801\uff1f\u201d</p><p>\u201c\u4e0d\u4e0d\uff0c\u4f60\u8bef\u4f1a\u4e86\uff0c\u4ed6\u5f53\u7136\u503c\u8fd9\u4e2a\u4ef7\uff0c\u95ee\u9898\u662f\u6211\u8fd9\u6761\u547d\u4e0d\u503c\u90a3\u4e48\u591a\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u4e24\u4eba\u626f\u76ae\u4e4b\u95f4\uff0c\u5438\u8840\u9b3c\u5df2\u7ecf\u8d8a\u8d70\u8d8a\u8fd1\uff0c\u76f8\u8ddd\u4e0d\u5230\u4e8c\u5341\u6b65\u4e86\uff0c\u4ed6\u7684\u817f\u4f24\u5e94\u8be5\u4e5f\u5728\u5feb\u901f\u75ca\u6108\uff0c\u884c\u8d70\u901f\u5ea6\u6e10\u6e10\u52a0\u5feb\uff0c\u9010\u6e10\u6062\u590d\u5982\u5e38\uff0c\u53f3\u624b\u638c\u4e2d\u4e5f\u5df2\u7ecf\u5e7b\u51fa\u78a7\u706b\u83b9\u83b9\u7684\u957f\u5200\u6765\uff0c\u770b\u8d77\u6765\u6740\u6c14\u51db\u51db\u3002\u56db\u53ea\u5c0f\u578b\u98ce\u5143\u7d20\u98de\u65cb\u8fc7\u6765\uff0c\u963b\u6321\u5728\u5438\u8840\u9b3c\u524d\u8fdb\u7684\u8def\u7ebf\u4e0a\uff0c\u5374\u88ab\u4ed6\u624b\u81c2\u4e00\u62ac\uff0c\u78a7\u5149\u95ea\u52a8\uff0c\u743c\u6069\u8fd8\u6ca1\u770b\u6e05\u695a\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u98ce\u5143\u7d20\u5c31\u5df2\u7ecf\u5c3d\u6570\u6e83\u6563\u65e0\u5f62\u4e86\u3002</p><p>\u201c\u5f88\u5389\u5bb3\u554a\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u6240\u4ee5\u4f60\u6709\u4ec0\u4e48\u4fdd\u547d\u62db\u6570\u8d76\u5feb\u7528\u5427\uff0c\u518d\u8fdf\u5c31\u6765\u4e0d\u53ca\u4e86\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u5c31\u80fd\u65ad\u5b9a\u6211\u6709\u4fdd\u547d\u7684\u62db\u6570\u3002\u201d</p><p>\u201c\u5426\u5219\u4f60\u80af\u5b9a\u65e9\u5c31\u9003\u8dd1\u4e86\uff0c\u54ea\u91cc\u8fd8\u4f1a\u7ad9\u5728\u8fd9\u91cc\u548c\u6211\u804a\u5929\u3002\u201d</p><p>\u548c\u806a\u660e\u4eba\u6253\u4ea4\u9053\u5c31\u662f\u7701\u529b\u6c14\uff0c\u743c\u6069\u4e5f\u61d2\u5f97\u518d\u5e9f\u8bdd\uff0c\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u6837\u4e1c\u897f\u6765\uff0c\u9ed1\u6c89\u6c89\u7684\uff0c\u770b\u8d77\u6765\u50cf\u662f\u4eba\u7684\u624b\u638c\uff0c\u5374\u957f\u7740\u516d\u4e2a\u624b\u6307\uff0c\u662f\u4e2a\u9020\u578b\u53e4\u602a\u7684\u5fbd\u7ae0\u3002</p><p>\u201c\u556a\uff01\u201d</p><p>\u743c\u6069\u7528\u529b\u4e00\u634f\uff0c\u770b\u4f3c\u575a\u786c\u7684\u90aa\u5fbd\u767b\u65f6\u5982\u73bb\u7483\u822c\u788e\u88c2\uff0c\u4e00\u9053\u4e4c\u5149\u98de\u5c04\u800c\u51fa\uff0c\u6b63\u4e2d\u5438\u8840\u9b3c\u3002\u6b4c\u560e\u65af\u50cf\u662f\u80f8\u53e3\u88ab\u91cd\u91cd\u7838\u4e86\u4e00\u9524\u4f3c\u7684\uff0c\u8e09\u8dc4\u540e\u9000\u51e0\u6b65\uff0c\u521a\u521a\u7ad9\u7a33\u811a\u8ddf\uff0c\u5c31\u542c\u5f97\u8fde\u58f0\u7206\u54cd\uff0c\u4ed6\u7684\u624b\u81c2\u3001\u53cc\u817f\u548c\u80f8\u8179\u4e0a\uff0c\u540c\u65f6\u70b8\u5f00\u4e86\u5343\u767e\u4e2a\u7ec6\u5c0f\u521b\u53e3\uff0c\u8089\u672b\u6a2a\u98de\uff0c\u9732\u51fa\u91cc\u9762\u7684\u68ee\u68ee\u767d\u9aa8\uff0c\u9ed1\u8272\u7684\u6c61\u8840\u6c69\u6c69\u6d41\u6dcc\uff0c\u6c47\u805a\u6210\u6eaa\uff0c\u6240\u8fc7\u4e4b\u5904\u76ae\u8089\u8fc5\u901f\u5e72\u67af\u77f3\u5316\uff0c\u8f6c\u773c\u95f4\u5927\u534a\u4e2a\u8eab\u8eaf\u90fd\u53d8\u6210\u4e86\u77f3\u50cf\u3002</p><p>\u5e72\u8106\u5229\u843d\u5730\u4e00\u51fb\uff0c\u8fde\u62dc\u5c14\u90fd\u6014\u4f4f\u4e86\uff0c\u867d\u7136\u4ed6\u65e9\u5c31\u731c\u5230\u743c\u6069\u80af\u5b9a\u57cb\u4f0f\u4e86\u540e\u624b\uff0c\u5426\u5219\u4e0d\u4f1a\u5982\u6b64\u60a0\u95f2\u81ea\u82e5\uff0c\u4f46\u5374\u4e5f\u6ca1\u60f3\u5230\u4f1a\u8fd9\u6837\u8f7b\u6613\u5730\u5c31\u6253\u5012\u4e86\u5bf9\u624b\u3002\u201c\u8fd9\u662f\u2026\u2026\u5185\u7206\u672f\uff1f\u4f46\u4e0d\u4f1a\u6709\u77f3\u5316\u6548\u679c\u5427\u3002\u201d</p><p>\u201c\u5c06\u519b\uff0c\u6709\u70b9\u9b54\u6cd5\u5e38\u8bc6\u597d\u4e0d\u597d\uff0c\u5185\u7206\u672f\u662f\u795e\u672f\uff0c\u8fd8\u662f\u6700\u9ad8\u9636\u7684\u90a3\u79cd\uff0c\u6211\u4e00\u4e2a\u5deb\u5e08\u600e\u4e48\u53ef\u80fd\u4f1a\u3002\u201d</p><p>\u5185\u7206\u672f\u662f\u79cd\u975e\u5e38\u5389\u5bb3\u7684\u6cd5\u672f\uff0c\u636e\u8bf4\u80fd\u591f\u8ba9\u4eba\u4ece\u4f53\u5185\u5f80\u5916\u7206\u88c2\u5f00\u6765\uff0c\u51f6\u623e\u9634\u6bd2\uff0c\u5a01\u529b\u65e0\u6bd4\uff0c\u4f46\u90a3\u662f\u7267\u5e08\u624d\u4f1a\u7684\u795e\u672f\uff0c\u743c\u6069\u5f53\u7136\u662f\u4e0d\u4f1a\u7684\u3002\u5b9e\u9645\u4e0a\uff0c\u4ee5\u4ed6\u73b0\u5728\u7684\u9b54\u6cd5\u9020\u8be3\uff0c\u9762\u5bf9\u6b4c\u560e\u65af\u8fd9\u79cd\u4eb2\u738b\u5438\u8840\u9b3c\uff0c\u5982\u679c\u76f4\u63a5\u653b\u51fb\u7684\u8bdd\uff0c\u4e0d\u7ba1\u4f7f\u7528\u4ec0\u4e48\u6cd5\u672f\uff0c\u5341\u6709\u516b\u4e5d\u90fd\u662f\u5b8c\u5168\u65e0\u6548\uff0c\u52c9\u5f3a\u6210\u529f\u4e86\u6740\u4f24\u529b\u4e5f\u8981\u5927\u6253\u6298\u6263\u3002\u6b64\u524d\u5e72\u6389\u8fea\u5185\u7279\uff0c\u771f\u6b63\u7684\u653b\u51fb\u4ea4\u7ed9\u51db\u548c\u6885\u83f2\u65af\u53bb\u505a\uff0c\u4ed6\u53ea\u662f\u8d1f\u8d23\u7834\u9b54\u3001\u538b\u5236\u5bf9\u624b\uff0c\u4fbf\u662f\u8fd9\u4e2a\u9053\u7406\u3002</p><p>\u743c\u6069\u521a\u624d\u6839\u672c\u5c31\u6ca1\u6709\u65bd\u6cd5\uff0c\u4ed6\u53ea\u662f\u634f\u788e\u4e86\u683c\u62c9\u5179\u7279\u7ed9\u838e\u73de\u514b\u7684\u90a3\u679a\u90aa\u5fbd\u800c\u5df2\u3002</p><p>\u65ad\u57df\u9547\u7684\u51b3\u6597\u5927\u8d5b\uff0c\u743c\u6069\u662f\u62ff\u4e86\u7b2c\u4e00\u540d\uff0c\u5956\u54c1\u867d\u7136\u88ab\u62dc\u5c14\u62a2\u8d70\u4e86\uff0c\u4f46\u90a3\u672c\u6765\u5c31\u662f\u4e2a\u8c61\u5f81\u7269\uff0c\u5404\u9879\u9644\u5e26\u7684\u5229\u76ca\u7167\u6837\u5230\u624b\u3002\u5176\u4e2d\u6709\u4e00\u9879\u5956\u52b1\uff0c\u662f\u80fd\u591f\u548c\u65ad\u57df\u9547\u7684\u5973\u4e3b\u4eba\uff0c\u9b45\u9b54\u7ea2\u8272\u5bff\u8863\u4e00\u591c\u6625\u5bb5\uff0c\u5bf9\u6b64\u743c\u6069\u656c\u8c22\u4e0d\u654f\uff0c\u62f1\u624b\u628a\u673a\u4f1a\u8ba9\u7ed9\u4e86\u6b4c\u560e\u65af\u3002</p><p>\u7ea2\u8272\u5bff\u8863\u59ff\u5bb9\u7edd\u8272\uff0c\u636e\u8bf4\u5728\u6df1\u6e0a\u4e2d\u4ec5\u6b21\u4e8e\u9b45\u9b54\u5973\u738b\u7f8e\u574e\u4fee\u7279\uff0c\u80fd\u548c\u8fd9\u6837\u7684\u7f8e\u5973\u4e0a\u5e8a\uff0c\u76f8\u4fe1\u662f\u6240\u6709\u7537\u6027\u7684\u68a6\u60f3\u3002\u4e00\u5915\u6625\u5bb5\uff0c\u5438\u8840\u9b3c\u81ea\u7136\u662f\u5fc3\u6ee1\u610f\u8db3\uff0c\u5374\u4e0d\u77e5\u9053\u5df2\u7ecf\u6084\u6084\u57cb\u4e0b\u6740\u8eab\u4e4b\u7978\u3002</p><p>\u7ea2\u8272\u5bff\u8863\u5176\u5b9e\u662f\u683c\u62c9\u5179\u7279\uff0c\u6b63\u662f\u5965\u5580\u65af\u7684\u6b7b\u654c\uff0c\u5982\u679c\u8bf4\u4ed6\u4e0d\u4f1a\u8d81\u8fd9\u4e2a\u5929\u8d50\u826f\u673a\u6697\u4e2d\u505a\u70b9\u624b\u811a\uff0c\u90a3\u662f\u9b3c\u90fd\u4e0d\u4fe1\u3002\u81ea\u53e4\u4ee5\u6765\uff0c\u7f8e\u4eba\u8ba1\u5c31\u662f\u6700\u597d\u7528\u7684\u4e00\u62db\uff0c\u4e0d\u77e5\u9053\u591a\u5c11\u82f1\u96c4\u8c6a\u6770\u90fd\u683d\u5728\u8fd9\u4e0a\u9762\uff0c\u4f55\u51b5\u4e00\u4e2a\u50bb\u4e4e\u4e4e\u7684\u5438\u8840\u9b3c\u3002\u867d\u8bf4\u5934\u9885\u3001\u5fc3\u810f\u8fd9\u79cd\u5173\u952e\u90e8\u4f4d\uff0c\u5438\u8840\u9b3c\u4f1a\u7279\u522b\u8c28\u614e\u5728\u610f\uff0c\u4e0d\u5bb9\u6613\u4e0b\u624b\uff0c\u4f46\u5728\u5e8a\u4e0a\u98a0\u9e3e\u5012\u51e4\uff0c\u9c7c\u6c34\u4ea4\u6b22\u7684\u65f6\u5019\uff0c\u88ab\u4eba\u5077\u5077\u5728\u8eab\u4f53\u5176\u4ed6\u5730\u65b9\u505a\u70b9\u624b\u811a\uff0c\u6b4c\u560e\u65af\u53c8\u54ea\u91cc\u80fd\u53d1\u89c9\u3002</p><p>\u201c\u6240\u4ee5\u8bf4\uff0c\u4e0a\u5e8a\u4e4b\u524d\uff0c\u4e00\u5b9a\u8981\u770b\u6e05\u695a\u5bf9\u8c61\uff0c\u5018\u82e5\u662f\u6f02\u4eae\u5973\u4eba\u65e0\u6240\u8c13\uff1b\u4f46\u5982\u679c\u662f\u6f02\u4eae\u4eba\u5996\u2026\u2026\u90a3\u5c31\u6709\u5927\u9ebb\u70e6\u4e86\u3002\u201d</p><p>\u743c\u6069\u4e00\u8fb9\u53d1\u8868\u7740\u8bc4\u8bba\uff0c\u624b\u4e0a\u52a8\u4f5c\u4e1d\u6beb\u4e0d\u6162\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u8428\u74e6\u68cb\uff0c\u63b7\u4e0b\u4e00\u679a\u68cb\u5b50\uff0c\u5316\u6210\u5353\u5c14\u6218\u58eb\uff0c\u4f38\u624b\u4e00\u6307\uff0c\u6218\u58eb\u4e00\u8dc3\u4e0a\u524d\uff0c\u91cd\u5251\u6325\u51fa\uff0c\u5c06\u5df2\u7ecf\u5904\u4e8e\u534a\u77f3\u5316\u72b6\u6001\u7684\u5438\u8840\u9b3c\u8111\u888b\u780d\u4e86\u4e0b\u6765\u3002</p><p>\u8111\u888b\u780d\u6389\uff0c\u4efb\u5438\u8840\u9b3c\u6709\u5929\u5927\u7684\u672c\u4e8b\u4e5f\u662f\u6b7b\u5b9a\u4e86\uff0c\u743c\u6069\u6536\u56de\u68cb\u5b50\uff0c\u6b63\u51c6\u5907\u548c\u62dc\u5c14\u8bf4\u8bdd\uff0c\u9661\u7136\u95f4\u542c\u5f97\u8fdc\u5904\u4e00\u58f0\u5c16\u9510\u5389\u5578\u4f20\u6765\uff0c\u7d27\u63a5\u7740\u5927\u5730\u5267\u70c8\u9707\u52a8\u8d77\u4f0f\uff0c\u4eff\u4f5b\u6d77\u6d6a\u4e00\u822c\u3002\u743c\u6069\u7acb\u8db3\u4e0d\u7a33\uff0c\u5306\u5fd9\u6276\u4f4f\u540e\u9762\u7684\u77f3\u67f1\u624d\u6ca1\u6709\u6454\u5012\u5728\u5730\uff0c\u4ed6\u60ca\u9a87\u4e4b\u4e0b\u95fb\u58f0\u671b\u53bb\uff0c\u6b63\u770b\u89c1\u8fdc\u5904\u795e\u8eaf\u9b54\u50cf\u6325\u81c2\u6a2a\u626b\uff0c\u52bf\u9053\u5a01\u731b\uff0c\u66e6\u5929\u4f7f\u582a\u582a\u907f\u8fc7\uff0c\u9b54\u50cf\u5374\u7d27\u8ddf\u7740\u53c8\u662f\u4e00\u62f3\u51fb\u51fa\uff0c\u8fc5\u82e5\u5954\u96f7\uff0c\u8fd9\u6b21\u66e6\u5929\u4f7f\u518d\u4e5f\u8eb2\u95ea\u4e0d\u53ca\uff0c\u53ea\u5f97\u4e3e\u5251\u786c\u683c\uff0c\u987f\u65f6\u4eff\u4f5b\u65ad\u7ebf\u98ce\u7b5d\u822c\u76f4\u76f4\u6454\u98de\u51fa\u53bb\uff0c\u649e\u8fdb\u4e00\u6392\u623f\u5c4b\u4e4b\u4e2d\uff0c\u751f\u6b7b\u4e0d\u77e5\u3002</p><p>\u201c\u5b83\u600e\u4e48\u7a81\u7136\u53c8\u53d8\u5feb\u4e86\uff1f\u201d</p><p>\u6b63\u81ea\u8be7\u5f02\uff0c\u9b54\u50cf\u4e00\u62f3\u51fb\u8d25\u66e6\u5929\u4f7f\uff0c\u4e5f\u4e0d\u8ffd\u51fb\uff0c\u5927\u8e0f\u6b65\u4fbf\u671d\u7740\u4e1c\u65b9\u51b2\u53bb\u3002\u9ed1\u77f3\u57ce\u6cbf\u6cb3\u800c\u5efa\uff0c\u57ce\u5e02\u7684\u4e1c\u3001\u5317\u4e24\u9762\u6ca1\u6709\u57ce\u95e8\uff0c\u5916\u9762\u5c31\u662f\u5bbd\u5e7f\u65e0\u9645\u7684\u51a5\u6cb3\uff0c\u6eda\u6eda\u6ed4\u6ed4\uff0c\u5954\u6d41\u76f4\u4e0b\uff0c\u6c34\u9762\u4e0a\u51f6\u98ce\u6076\u6d6a\uff0c\u8fd8\u4f1a\u65f6\u4e0d\u65f6\u5192\u51fa\u9ad8\u8fbe\u767e\u5c3a\u7684\u9f99\u5377\u6f29\u6da1\u6765\uff0c\u8fde\u90a3\u4e9b\u957f\u7740\u7fc5\u8180\u7684\u90aa\u9b54\u90fd\u4e0d\u6562\u8f7b\u6613\u5728\u4e0a\u9762\u98de\u884c\uff0c\u4e00\u4e0d\u5c0f\u5fc3\u5c31\u4f1a\u88ab\u5438\u8fdb\u53bb\uff0c\u843d\u4e2a\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u7684\u4e0b\u573a\u3002\u6240\u4ee5\u5bf9\u4e8e\u5b88\u57ce\u4e00\u65b9\u6765\u8bf4\uff0c\u9632\u5fa1\u538b\u529b\u4e5f\u5f88\u8f7b\uff0c\u6ca1\u5b89\u6392\u591a\u5c11\u5175\u529b\uff0c\u66f4\u4e0d\u662f\u4e3b\u8981\u6218\u573a\u3002\u9b54\u50cf\u76f4\u76f4\u51b2\u649e\u8fc7\u6765\uff0c\u4e00\u8def\u4e0a\u6467\u67af\u62c9\u673d\uff0c\u4e0d\u77e5\u9053\u6bc1\u6389\u591a\u5c11\u5efa\u7b51\uff0c\u4eff\u4f5b\u731b\u517d\u4e00\u822c\u628a\u57ce\u5899\u649e\u5f00\u4e00\u4e2a\u5927\u7f3a\u53e3\uff0c\u51b2\u5230\u51a5\u6cb3\u91cc\u53bb\u4e86\u3002</p><p>\u4e0d\uff0c\u4e0d\u5bf9\uff0c\u4e0d\u662f\u51b2\u5230\u51a5\u6cb3\u91cc\u2026\u2026</p><p>\u5728\u5373\u5c06\u8e0f\u5165\u51a5\u6cb3\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u9b54\u50cf\u7684\u80cc\u540e\u9661\u7136\u53d1\u751f\u5f02\u53d8\uff0c\u4e24\u5f20\u767d\u68ee\u68ee\u7684\u5de8\u5927\u9aa8\u7ffc\u8f70\u5730\u81ea\u4f53\u5185\u4f38\u51fa\u8212\u5c55\uff0c\u6574\u4e2a\u8eab\u8eaf\u4eff\u4f5b\u5927\u9e1f\u4e00\u822c\u7fe9\u7136\u98de\u8d77\uff0c\u4ece\u51a5\u6cb3\u6c34\u9762\u4e0a\u6d6e\u63a0\u8fc7\u53bb\uff0c\u77ac\u95f4\u5c31\u4e0d\u89c1\u4e86\u8e2a\u5f71\u3002</p><p>\u203b\u203b\u203b</p><p>\u4e8b\u60c5\u5c45\u7136\u53d8\u6210\u8fd9\u526f\u6837\u5b50\uff0c\u5b8c\u5168\u51fa\u4e4e\u6240\u6709\u4eba\u7684\u610f\u6599\u3002\u597d\u5728\u8f6c\u6765\u8f6c\u53bb\uff0c\u6700\u7ec8\u7684\u7ed3\u679c\u8fd8\u7b97\u5dee\u5f3a\u4eba\u610f\uff0c\u9b54\u50cf\u4e34\u9635\u9041\u8d70\uff0c\u4ea1\u7075\u519b\u7684\u4e24\u5927\u7edf\u5e05\u90fd\u5148\u540e\u4e27\u547d\uff0c\u6218\u5c40\u81ea\u7136\u4e00\u6e83\u5343\u91cc\uff0c\u6ca1\u8fc7\u591a\u4e45\u9b54\u9b3c\u519b\u5c31\u51b2\u8fdb\u57ce\u3002\u62dc\u5c14\u867d\u7136\u538b\u7740\u4e0d\u80fd\u52a8\u5f39\uff0c\u4f46\u8fd8\u80fd\u591f\u53d1\u51fa\u4fe1\u53f7\uff0c\u81ea\u7136\u6709\u5fe0\u5fc3\u7684\u90e8\u5c5e\u8fc7\u6765\u5e2e\u5fd9\uff0c\u51e0\u5341\u4e2a\u58ee\u7855\u7684\u9b54\u9b3c\u594b\u529b\u5c06\u5de8\u77f3\u79fb\u5f00\uff0c\u628a\u8fd9\u4f4d\u963f\u5f17\u7eb3\u65af\u7684\u5927\u516c\u7235\u6551\u4e86\u51fa\u6765\u3002</p><p>\u9ed1\u77f3\u57ce\u8fde\u906d\u6218\u706b\uff0c\u53c8\u88ab\u795e\u8eaf\u9b54\u50cf\u8df5\u8e0f\u7834\u574f\uff0c\u57ce\u5899\u5efa\u7b51\u6bc1\u635f\u65e0\u6570\uff0c\u7b80\u76f4\u6210\u4e86\u4e00\u7247\u5e9f\u589f\uff0c\u5df2\u7ecf\u4e0d\u582a\u9a7b\u5b88\u3002\u62dc\u5c14\u6574\u987f\u5927\u519b\uff0c\u51b3\u5b9a\u5148\u8fd4\u56de\u9752\u94dc\u57ce\u518d\u505a\u8ba1\u8f83\u3002\u6b64\u6218\u4e4b\u540e\uff0c\u6076\u9b54\u519b\u4e3b\u529b\u8986\u706d\uff0c\u7cbe\u9510\u5c3d\u5931\uff0c\u5c06\u9886\u519b\u5b98\u6298\u635f\u6b86\u5c3d\uff0c\u5c31\u8fde\u7edf\u5e05\u90fd\u88ab\u780d\u4e86\u8111\u888b\uff0c\u53ef\u4ee5\u7b97\u662f\u4e00\u8d25\u6d82\u5730\u3002\u9b54\u9b3c\u519b\u81ea\u7136\u58eb\u6c14\u5927\u632f\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u559c\u6c14\u6d0b\u6d0b\uff0c\u4eff\u4f5b\u5df2\u7ecf\u80dc\u5238\u5728\u63e1\u4e86\u3002</p><p>\u4e8b\u5b9e\u5f53\u7136\u6ca1\u90a3\u4e48\u7b80\u5355\uff0c\u6076\u9b54\u519b\u5927\u8d25\u662f\u6ca1\u9519\uff0c\u4f46\u5b83\u4eec\u4f9d\u65e7\u8fd8\u5360\u636e\u7740\u51a5\u6cb3\u6cbf\u5cb8\u7684\u524d\u5341\u4e09\u5ea7\u57ce\u5821\uff0c\u9a7b\u5b88\u7684\u5175\u529b\u52a0\u8d77\u6765\u4e5f\u4e0d\u5c11\u3002\u9b54\u9b3c\u519b\u867d\u7136\u6218\u80dc\uff0c\u5374\u4e5f\u5143\u6c14\u5927\u4f24\uff0c\u7cbe\u9510\u6218\u529b\u635f\u5931\u6b86\u5c3d\uff0c\u5815\u5929\u4f7f\u519b\u56e2\u201c\u9eef\u4e4b\u7ffc\u201d\u8fd1\u4e4e\u5168\u4f53\u8986\u706d\u3002\u8981\u60f3\u6536\u590d\u5931\u5730\uff0c\u53ea\u6015\u5e76\u4e0d\u8f7b\u677e\u3002</p><p>\u9664\u6b64\u4e4b\u5916\uff0c\u66f4\u4ee4\u4eba\u62c5\u5fe7\u7684\uff0c\u662f\u795e\u8eaf\u9b54\u50cf\u4e0d\u77e5\u6240\u8e2a\u3002\u9ed1\u77f3\u57ce\u4e00\u6218\uff0c\u9b54\u50cf\u4e34\u9635\u9041\u8d70\uff0c\u4e8b\u540e\u62dc\u5c14\u6d3e\u9063\u4e86\u5927\u91cf\u5c0f\u9b54\u9b3c\u641c\u7d22\uff0c\u8fc4\u4eca\u4e3a\u6b62\u4e00\u65e0\u6240\u83b7\uff0c\u8fd9\u662f\u4ef6\u5f88\u8be1\u5f02\u7684\u4e8b\u60c5\u3002\u8981\u77e5\u9053\u8fd9\u91cc\u662f\u963f\u5f17\u7eb3\u65af\uff0c\u9b54\u9b3c\u7684\u4e3b\u573a\uff0c\u62dc\u5c14\u7684\u5730\u76d8\uff0c\u795e\u8eaf\u9b54\u50cf\u53c8\u662f\u4e2a\u9876\u5929\u7acb\u5730\u7684\u5e9e\u7136\u5927\u7269\uff0c\u5c45\u7136\u5c31\u6b64\u9500\u58f0\u533f\u8ff9\u4e86\uff0c\u7b80\u76f4\u8fdd\u53cd\u5e38\u8bc6\u3002</p><p>\u795e\u8eaf\u9b54\u50cf\u6ca1\u6709\u88ab\u5f7b\u5e95\u6467\u6bc1\uff0c\u9b54\u9b3c\u6240\u8c13\u7684\u201c\u80dc\u5229\u201d\u5176\u5b9e\u5c31\u53ea\u662f\u81ea\u6b3a\u6b3a\u4eba\u3002\u5018\u82e5\u5965\u5580\u65af\u53e6\u5916\u518d\u6d3e\u5deb\u5996\u524d\u6765\uff0c\u63a5\u7ba1\u9b54\u50cf\uff0c\u91cd\u65b0\u4fee\u590d\u4e0a\u9635\uff0c\u62dc\u5c14\u8fd8\u80fd\u62ff\u4ec0\u4e48\u62b5\u6321\uff1f\u9664\u4e86\u6b7b\u6218\u7136\u540e\u6218\u6b7b\u4e4b\u5916\uff0c\u4f3c\u4e4e\u6ca1\u6709\u522b\u7684\u8def\u53ef\u8d70\u4e86\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u4e9b\u662f\u62dc\u5c14\u548c\u4ed6\u7684\u624b\u4e0b\u4eec\u9700\u8981\u5934\u75bc\u7684\u4e8b\u60c5\uff0c\u548c\u743c\u6069\u6ca1\u5173\u7cfb\u3002\u9b54\u9b3c\u5927\u519b\u8fd4\u56de\u9752\u94dc\u57ce\u5821\uff0c\u743c\u6069\u7b49\u4eba\u81ea\u7136\u4e5f\u968f\u884c\u3002\u5927\u6982\u662f\u770b\u5728\u6551\u547d\u4e4b\u6069\u7684\u4efd\u4e0a\uff0c\u62dc\u5c14\u793c\u6570\u5468\u5230\uff0c\u5f85\u4e3a\u4e0a\u5bbe\uff0c\u7528\u838e\u73de\u514b\u7684\u8bdd\u8bf4\uff1a\u201c\u9664\u4e86\u6ca1\u6709\u9001\u4e0a\u51e0\u4e2a\u6b32\u9b54\u7f8e\u5973\u4e4b\u5916\uff0c\u5176\u4ed6\u65e0\u53ef\u6307\u6458\u3002\u201d</p><p>\u6b32\u9b54\u7f8e\u5973\u4ec0\u4e48\u7684\uff0c\u743c\u6069\u5176\u5b9e\u4e5f\u4e0d\u5728\u4e4e\uff0c\u8eab\u8fb9\u7684\u6f02\u4eae\u7f8e\u4eba\u5df2\u7ecf\u8db3\u591f\uff0c\u518d\u591a\u90fd\u8981\u5e94\u4ed8\u4e0d\u8fc7\u6765\u4e86\u3002\u5018\u82e5\u662f\u6f02\u4eae\u5230\u7ea2\u8272\u5bff\u8863\u90a3\u79cd\u7a0b\u5ea6\u7684\u7edd\u8272\u5973\u5b50\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u5174\u8da3\u73a9\u73a9\u4e00\u5915\u4e4b\u6b22\uff0c\u6362\u6362\u53e3\u5473\uff0c\u7136\u800c\u6b32\u9b54\u548c\u9b45\u9b54\u4e0d\u540c\uff0c\u5e76\u4e0d\u4ee5\u7f8e\u8272\u8457\u79f0\uff0c\u62dc\u5c14\u624b\u4e0b\u7684\u201c\u9ed1\u73ab\u7470\u201d\u6210\u5458\uff0c\u743c\u6069\u4e5f\u89c1\u8fc7\u51e0\u4e2a\uff0c\u7f8e\u8c8c\u81ea\u7136\u4e5f\u79f0\u5f97\u4e0a\uff0c\u4f46\u6bd4\u8d77\u6885\u83f2\u65af\u548c\u51db\u6765\u90fd\u8fd8\u660e\u663e\u5dee\u4e86\u4e00\u7b79\u3002\u65e2\u7136\u5982\u6b64\uff0c\u4e5f\u5c31\u6ca1\u591a\u5927\u5174\u8da3\u4e86\u3002</p><p>\u65e5\u5b50\u867d\u7136\u8fc7\u5f97\u8212\u9002\uff0c\u743c\u6069\u5374\u6ca1\u6253\u7b97\u5728\u8fd9\u91cc\u4e45\u4f4f\uff0c\u8fd8\u662f\u65e9\u65e9\u62ff\u4e86\u62a5\u916c\uff0c\u8fd4\u56de\u7269\u8d28\u754c\uff0c\u8fdc\u79bb\u8fd9\u662f\u975e\u4e4b\u5730\u662f\u6b63\u7ecf\u3002\u6709\u5951\u7ea6\u5728\uff0c\u62dc\u5c14\u81ea\u7136\u4e0d\u4f1a\u53cd\u6094\uff0c\u516b\u767e\u9897\u7ea2\u5b9d\u77f3\u5df2\u7ecf\u5151\u73b0\uff0c\u6b64\u65f6\u5c31\u6446\u5728\u743c\u6069\u7684\u623f\u95f4\u91cc\uff0c\u53e6\u5916\u8fd8\u9644\u8d60\u4e86\u4e00\u4e2a\u79d8\u85cf\u7bb1\uff08\u6b21\u5143\u888b\u7684\u8d85\u5927\u5bb9\u91cf\u7248\u672c\uff09\u3002\u901a\u5f80\u6668\u70bc\u57ce\u7684\u4e34\u65f6\u4f20\u9001\u95e8\u4e5f\u5373\u5c06\u6784\u5efa\u5b8c\u6210\uff0c\u9884\u8ba1\u660e\u5929\u5c31\u80fd\u591f\u542f\u7528\u3002</p><p>\u201c\u603b\u7b97\u8981\u56de\u53bb\u4e86\u3002\u201d\u743c\u6069\u611f\u53f9\u3002</p><p>\u4ed6\u5750\u5728\u9752\u94dc\u57ce\u5821\u949f\u697c\u7684\u9876\u4e0a\uff0c\u62ff\u7740\u671b\u8fdc\u955c\u60a0\u95f2\u5730\u56db\u5904\u5f20\u671b\u3002\u51db\u8fd9\u51e0\u65e5\u96be\u5f97\u5730\u52aa\u529b\u7528\u529f\uff0c\u6574\u65e5\u5728\u623f\u95f4\u91cc\u95ed\u95e8\u4e0d\u51fa\uff0c\u6885\u83f2\u65af\u4e00\u65e9\u5c31\u51fa\u95e8\u4e86\uff0c\u4e0d\u77e5\u53bb\u54ea\u91cc\uff0c\u743c\u6069\u4e5f\u6ca1\u591a\u95ee\u3002\u8fde\u65e5\u5f81\u6218\u6740\u4f10\uff0c\u5982\u4eca\u653e\u677e\u4e0b\u6765\uff0c\u770b\u770b\u98ce\u666f\uff0c\u4e5f\u662f\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u838e\u73de\u514b\uff0c\u56de\u5230\u7269\u8d28\u754c\u4ee5\u540e\uff0c\u4f60\u51c6\u5907\u53bb\u54ea\u91cc\u5462\uff1f\u201d</p><p>\u4ed6\u968f\u53e3\u95ee\u5750\u5728\u8eab\u4fa7\u7684\u9b45\u9b54\uff0c\u5f97\u5230\u7684\u56de\u7b54\u5374\u662f\u4e00\u58f0\u8f7b\u7b11\uff0c\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u554a\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u6211\u2026\u2026\u4f3c\u4e4e\u4e5f\u6ca1\u4ec0\u4e48\u5730\u65b9\u53ef\u53bb\u5427\u3002\u201d</p><p>\u743c\u6069\u9ed8\u7136\uff0c\u610f\u8bc6\u5230\u81ea\u5df1\u95ee\u4e86\u4e2a\u611a\u8822\u7684\u95ee\u9898\u3002</p></article>"}}}]);
