(window.webpackJsonp=window.webpackJsonp||[]).push([[1566],{1589:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e94\u8282 \u7269\u4ee5\u7c7b\u805a",content:"<article><h2>\u7b2c\u5341\u4e94\u8282 \u7269\u4ee5\u7c7b\u805a</h2><p>\u8fd9\u51e0\u4e2a\u6708\u7684\u76f8\u5904\uff0c\u5f7c\u6b64\u95f4\u7684\u4e86\u89e3\u589e\u591a\uff0c\u743c\u6069\u4e5f\u5927\u81f4\u6e05\u695a\u838e\u73de\u514b\u7684\u6765\u5386\u3002\u5979\u51fa\u751f\u4e8e\u6cf0\u745f\u5c14\u7684\u4e00\u4e2a\u5c0f\u57ce\u91cc\uff0c\u4e94\u5c81\u7684\u65f6\u5019\u6bcd\u4eb2\u53bb\u4e16\uff0c\u9760\u4e5e\u8ba8\u3001\u76d7\u7a83\u751a\u81f3\u6740\u4eba\u62a2\u52ab\u957f\u5927\uff0c\u90aa\u795e\u7684\u8840\u8109\u8ba9\u5979\u62e5\u6709\u8fdc\u8fdc\u8d85\u4e4e\u5e38\u4eba\u7684\u529b\u91cf\uff0c\u5728\u4e03\u5c81\u7684\u65f6\u5019\u5979\u5c31\u5df2\u7ecf\u5b66\u4f1a\u719f\u7ec3\u5730\u7528\u5315\u9996\u523a\u8fdb\u522b\u4eba\u7684\u5fc3\u810f\u3002\u4e5d\u5c81\u7684\u65f6\u5019\uff0c\u5979\u88ab\u94c1\u738b\u5ea7\uff08Iron Throne\uff09\u7684\u745e\u5854\u5076\u7136\u770b\u4e2d\uff0c\u6536\u4e3a\u517b\u5973\u3002\u5728\u968f\u540e\u7684\u4e94\u5e74\u91cc\uff0c\u838e\u73de\u514b\u5feb\u901f\u6210\u957f\u4e3a\u7b2c\u4e00\u6d41\u7684\u6740\u624b\uff0c\u66ff\u745e\u5854\u65a9\u9664\u65e0\u6570\u5f02\u5df1\u548c\u7ade\u4e89\u5bf9\u624b\uff0c\u5e2e\u4ed6\u6210\u4e3a\u94c1\u738b\u5ea7\u5728\u5b9d\u5251\u897f\u6d77\u5cb8\u5206\u90e8\u7684\u6700\u9ad8\u9886\u8896\u3002\u800c\u5979\u81ea\u5df1\u4e5f\u6210\u4e3a\u94c1\u738b\u5ea7\u4e2d\u9887\u5177\u5f71\u54cd\u529b\u7684\u4eba\u7269\uff0c\u5018\u82e5\u613f\u610f\u66f4\u8fdb\u4e00\u6b65\uff0c\u5047\u4ee5\u65f6\u65e5\uff0c\u53d6\u745e\u5854\u800c\u4ee3\u4e4b\u5e76\u975e\u4ec0\u4e48\u96be\u4e8b\uff0c\u7136\u800c\u838e\u73de\u514b\u5fd7\u4e0d\u5728\u6b64\u3002</p><p>\u5979\u60f3\u6210\u4e3a\u795e\u660e\u3002</p><p>\u4f5c\u4e3a\u5df4\u5c14\u5b50\u55e3\uff0c\u8fd9\u79cd\u60f3\u6cd5\u4e0d\u4f46\u65e0\u53ef\u539a\u975e\uff0c\u7b80\u76f4\u662f\u60c5\u7406\u4e4b\u4e2d\u2014\u2014\u50cf\u6885\u83f2\u65af\u8fd9\u6837\u624d\u5c5e\u4e8e\u4e0d\u6b63\u5e38\u72b6\u6001\u3002\u5728\u9047\u5230\u743c\u6069\u548c\u6885\u83f2\u65af\u4e4b\u524d\uff0c\u838e\u73de\u514b\u5df2\u7ecf\u6740\u6b7b\u4e86\u5341\u591a\u540d\u5df4\u5c14\u5b50\u55e3\uff0c\u878d\u5408\u4ed6\u4eec\u7684\u795e\u529b\uff0c\u58ee\u5927\u81ea\u8eab\uff0c\u671d\u7740\u6740\u622e\u4e4b\u795e\u7684\u76ee\u6807\u4e00\u6b65\u6b65\u524d\u8fdb\u3002</p><p>\u7136\u540e\u5979\u5931\u8d25\u4e86\u3002</p><p>\u5de8\u9b54\u5c71\u8109\u4e00\u6218\uff0c\u838e\u73de\u514b\u8eab\u6b7b\uff0c\u4f53\u5185\u7684\u6740\u622e\u795e\u529b\u4e5f\u5c3d\u6570\u4e27\u5931\uff0c\u88ab\u6885\u83f2\u65af\u6240\u878d\u5408\u3002\u5176\u5b9e\u8fd9\u5bf9\u4e8e\u53cc\u65b9\u6765\u8bf4\u90fd\u4e0d\u662f\u5e0c\u671b\u770b\u89c1\u7684\u7ed3\u679c\uff0c\u838e\u73de\u514b\u56fa\u7136\u524d\u529f\u5c3d\u5f03\uff0c\u767e\u5fd9\u4e00\u573a\uff0c\u6210\u4e3a\u795e\u7947\u7684\u671f\u671b\u5f7b\u5e95\u843d\u7a7a\u3002\u6885\u83f2\u65af\u5374\u4e5f\u9669\u4e9b\u88ab\u5df4\u5c14\u51b2\u7834\u5c01\u5370\uff0c\u518d\u5ea6\u590d\u6d3b\uff0c\u82b1\u4e86\u597d\u5927\u529b\u6c14\u624d\u7b97\u9547\u538b\u4e0b\u6765\u3002\u4e8b\u60c5\u5df2\u7ecf\u53d1\u751f\uff0c\u65f6\u5149\u53c8\u4e0d\u53ef\u80fd\u9006\u8f6c\uff0c\u540e\u6094\u6ca1\u6709\u610f\u4e49\uff0c\u53ea\u80fd\u7ee7\u7eed\u5f80\u524d\u770b\u3002</p><p>\u201c\u5f80\u524d\u770b\uff1f\u201d\u838e\u73de\u514b\u8f7b\u58f0\u53cd\u95ee\uff0c\u201c\u524d\u9762\u53c8\u662f\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u743c\u6069\u4e00\u65f6\u8bed\u585e\u3002</p><p>\u201c\u8981\u5f80\u524d\u770b\uff0c\u90a3\u603b\u5f97\u6709\u4e2a\u76ee\u6807\uff0c\u201d\u838e\u73de\u514b\u671b\u7740\u8fdc\u5904\u7684\u5929\u9645\uff0c\u60a0\u60a0\u8bf4\uff0c\u201c\u6211\u542c\u8bf4\uff0c\u4eba\u751f\u5728\u4e16\uff0c\u90fd\u5e94\u8be5\u6709\u76ee\u6807\uff0c\u4e0d\u7ba1\u662f\u9ad8\u5c1a\u7684\u3001\u5351\u9119\u7684\u3001\u4f1f\u5927\u7684\u3001\u6e3a\u5c0f\u7684\u3001\u957f\u8fdc\u7684\u3001\u773c\u524d\u7684\uff0c\u603b\u5f97\u77e5\u9053\u81ea\u5df1\u8981\u505a\u4ec0\u4e48\uff0c\u8fd9\u6837\u624d\u6709\u52aa\u529b\u7684\u65b9\u5411\uff0c\u6709\u524d\u8fdb\u7684\u9053\u8def\u2026\u2026\u53ef\u662f\u2026\u2026\u53ef\u662f\u6211\u73b0\u5728\u6839\u672c\u4e0d\u77e5\u9053\u6211\u7684\u76ee\u6807\u662f\u4ec0\u4e48\uff0c\u5728\u54ea\u91cc\uff0c\u53c8\u600e\u4e48\u5411\u524d\u770b\u5462\u3002\u201d</p><p>\u201c\u4f60\u96be\u9053\u5c31\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u60f3\u505a\u7684\u4e8b\u60c5\u5417\uff1f\u201d</p><p>\u838e\u73de\u514b\u504f\u5934\u601d\u8003\u4e86\u534a\u664c\uff0c\u6700\u7ec8\u7b11\u7740\u6447\u6447\u5934\uff0c\u201c\u8fd8\u771f\u60f3\u4e0d\u8d77\u6765\u3002\u201d</p><p>\u201c\u6ca1\u6709\u4ec0\u4e48\u60f3\u53bb\u7684\u5730\u65b9\uff0c\u6216\u8005\u60f3\u89c1\u7684\u4eba\uff1f\u201d</p><p>\u201c\u597d\u50cf\u4e5f\u6ca1\u6709\uff0c\u201d\u5979\u81ea\u5632\u5730\u7b11\u7740\uff0c\u201c\u6211\u4e0d\u50cf\u4f60\u4eec\uff0c\u6211\u6ca1\u4eb2\u4eba\uff0c\u6ca1\u670b\u53cb\uff0c\u6ca1\u540c\u4f34\uff0c\u6ca1\u6709\u5bb6\uff0c\u5728\u54ea\u91cc\u90fd\u4e00\u6837\u4e86\u3002\u201d</p><p>\u201c\u94c1\u738b\u5ea7\u5462\u3002\u201d</p><p>\u201c\u90fd\u5df2\u7ecf\u79bb\u5f00\u4e00\u5e74\u591a\u4e86\uff0c\u96be\u9053\u8fd8\u80fd\u56de\u53bb\uff1f\u5c31\u7b97\u56de\u53bb\uff0c\u53c8\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u201d\u838e\u73de\u514b\u6447\u6447\u5934\uff0c\u201c\u7ee7\u7eed\u5f53\u6740\u624b\uff1f\u7b97\u4e86\uff0c\u7d2f\u4e86\u3002\u201d</p><p>\u201c\u90a3\u2026\u2026\u4f60\u4ee5\u540e\u6253\u7b97\u505a\u4ec0\u4e48\uff0c\u603b\u6709\u70b9\u81ea\u5df1\u7684\u60f3\u6cd5\u5427\u3002\u201d</p><p>\u201c\u60f3\u6cd5\u4e48\uff0c\u5f88\u7b80\u5355\uff0c\u201d\u838e\u73de\u514b\u5e72\u8106\u5229\u843d\u5730\u8bf4\uff0c\u201c\u8ddf\u7740\u4f60\u3002\u201d</p><p>\u201c\u6211\u6ca1\u53d1\u89c9\u81ea\u5df1\u539f\u6765\u6709\u8fd9\u4e48\u5927\u7684\u9b45\u529b\u3002\u201d</p><p>\u201c\u8fd9\u548c\u9b45\u529b\u6ca1\u5173\u7cfb\uff0c\u53ea\u548c\u6027\u547d\u6709\u5173\u7cfb\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u4f60\u5fd8\u4e86\uff0c\u6211\u4eec\u53ef\u662f\u6709\u4e00\u4efd\u771f\u540d\u5951\u7ea6\u5728\u7684\uff0c\u201d\u9b45\u9b54\u63d0\u9192\uff0c\u201c\u522b\u7684\u90fd\u65e0\u6240\u8c13\uff0c\u4f46\u6700\u8981\u7d27\u7684\u4e00\u6761\uff0c\u6211\u4eec\u7684\u6027\u547d\u662f\u7ed1\u5b9a\u5728\u4e00\u8d77\u7684\u3002\u6240\u4ee5\u6211\u5f53\u7136\u8981\u8ddf\u5728\u4f60\u8eab\u8fb9\uff0c\u65f6\u523b\u4fdd\u62a4\u4f60\uff0c\u5426\u5219\u88ab\u4f60\u8fde\u7d2f\uff0c\u6b7b\u5f97\u83ab\u540d\u5176\u5999\uff0c\u6211\u53ef\u4e0d\u7518\u5fc3\u554a\u3002\u201d</p><p>\u201c\u7b49\u7b49\uff0c\u201d\u743c\u6069\u6297\u8bae\uff0c\u201c\u4e3a\u4ec0\u4e48\u5c31\u80af\u5b9a\u662f\u6211\u8fde\u7d2f\u4f60\uff0c\u4e0d\u662f\u4f60\u8fde\u7d2f\u6211\u5462\u3002\u201d</p><p>\u838e\u73de\u514b\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u8fd9\u4e0d\u662f\u663e\u800c\u6613\u89c1\u7684\u4e8b\u60c5\u5417\u3002\u662f\u5173\u6ce8\u6211\u7684\u4eba\u591a\uff0c\u8fd8\u662f\u5173\u6ce8\u4f60\u7684\u4eba\u591a\uff1f\u662f\u6211\u4f1a\u9047\u5230\u7684\u5371\u9669\u591a\uff0c\u8fd8\u662f\u4f60\u4f1a\u9047\u5230\u7684\u5371\u9669\u591a\uff1f\u662f\u60f3\u6740\u6211\u7684\u4eba\u591a\uff0c\u8fd8\u662f\u60f3\u6740\u4f60\u7684\u4eba\u591a\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u2026\u2026\u201d</p><p>\u201c\u6211\u662f\u4ec0\u4e48\uff1f\u65e0\u540d\u5c0f\u5352\u4e00\u4e2a\uff0c\u73b0\u5728\u8fde\u90a3\u70b9\u6740\u622e\u795e\u529b\u90fd\u5931\u53bb\u4e86\uff0c\u6839\u672c\u5c31\u4e0d\u4f1a\u6709\u4eba\u653e\u5728\u5fc3\u4e0a\u3002\u5982\u679c\u6211\u53ea\u60f3\u5168\u8eab\u4fdd\u547d\uff0c\u627e\u4e2a\u5730\u65b9\u9690\u5c45\uff0c\u5e73\u5e73\u7a33\u7a33\u5ea6\u8fc7\u4e00\u8f88\u5b50\uff0c\u4e0d\u662f\u4ec0\u4e48\u96be\u4e8b\u5427\u3002\u81f3\u4e8e\u4f60\u4e48\uff0c\u201d\u9b45\u9b54\u5fae\u5fae\u51b7\u7b11\uff0c\u201c\u4eba\u5728\u5c40\u4e2d\uff0c\u8eab\u4e0d\u7531\u5df1\uff0c\u4e0d\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\u5c31\u6ca1\u547d\u4e86\u3002\u4e0d\u662f\u4f60\u8fde\u7d2f\u6211\uff0c\u96be\u9053\u8fd8\u4f1a\u662f\u6211\u8fde\u7d2f\u4f60\uff1f\u5c31\u7b97\u6709\u4eba\u8981\u6740\u6211\uff0c\u90a3\u4e5f\u80af\u5b9a\u662f\u9488\u5bf9\u4f60\u7684\uff0c\u8bf4\u5230\u5e95\u8fd8\u662f\u4f60\u8fde\u7d2f\u6211\u3002\u201d</p><p>\u743c\u6069\u63c9\u63c9\u7709\u89d2\uff0c\u4e00\u65f6\u95f4\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u3002</p><p>\u201c\u4f60\u8fd9\u4e48\u6101\u7709\u82e6\u8138\u505a\u4ec0\u4e48\uff0c\u201d\u838e\u73de\u514b\u7684\u5634\u89d2\u5fae\u5fae\u7fd8\u8d77\uff0c\u201c\u6709\u6f02\u4eae\u5973\u5b69\u5b50\u4e3b\u52a8\u6295\u6000\u9001\u62b1\uff0c\u966a\u4f34\u5de6\u53f3\uff0c\u96be\u9053\u8fd9\u4e0d\u662f\u6bcf\u4e2a\u7537\u4eba\u7684\u68a6\u60f3\u5417\u3002\u987a\u4fbf\u8fd8\u53ef\u4ee5\u73a9\u73a9\u4e3b\u4eba\u5973\u5974\u7684\u89d2\u8272\u626e\u6f14\u6e38\u620f\uff0c\u4e0d\u77e5\u9053\u591a\u5c11\u4eba\u4f1a\u7fa1\u6155\u5f97\u4e24\u773c\u653e\u5149\u5462\u3002\u201d</p><p>\u201c\u8bdd\u662f\u8fd9\u4e48\u8bf4\u2026\u2026\u603b\u89c9\u5f97\u4ec0\u4e48\u5730\u65b9\u4e0d\u592a\u4e00\u6837\u3002\u201d</p><p>\u5230\u5e95\u4ec0\u4e48\u5730\u65b9\u4e0d\u4e00\u6837\uff0c\u743c\u6069\u4e5f\u8bf4\u4e0d\u4e0a\u6765\uff0c\u7d22\u6027\u61d2\u5f97\u53bb\u60f3\u3002\u9633\u5149\u660e\u5a9a\uff0c\u5fae\u98ce\u548c\u7166\uff0c\u5750\u5728\u9ad8\u9ad8\u7684\u949f\u697c\u4e0a\u770b\u98ce\u666f\uff0c\u8eab\u8fb9\u6709\u7f8e\u5973\u966a\u4f34\uff0c\u539f\u672c\u5c31\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\u60c5\uff0c\u6ca1\u5fc5\u8981\u81ea\u5bfb\u70e6\u607c\u3002\u838e\u73de\u514b\u5e76\u4e0d\u662f\u6e29\u67d4\u4f53\u8d34\u5584\u89e3\u4eba\u610f\u7684\u5973\u5b50\uff0c\u5927\u7ea6\u662f\u6740\u624b\u51fa\u8eab\u7684\u7f18\u6545\uff0c\u8a00\u8bed\u95f4\u603b\u5e26\u51e0\u5206\u9510\u5229\uff0c\u4e60\u60ef\u4e86\u51b7\u5632\u70ed\u8bbd\uff0c\u4f46\u8fd9\u51e0\u4e2a\u6708\u6765\u671d\u5915\u76f8\u5904\uff0c\u6253\u4ea4\u9053\u4e45\u4e86\uff0c\u53d1\u73b0\u4f5c\u4e3a\u804a\u5929\u5bf9\u8c61\u800c\u8a00\u4e5f\u662f\u4e2a\u4e0d\u9519\u7684\u9009\u62e9\u3002\u76f8\u6bd4\u8d77\u6765\uff0c\u6885\u83f2\u65af\u8fc7\u4e8e\u5b89\u9759\u6c89\u7a33\uff0c\u51db\u53c8\u592a\u6d3b\u6cfc\u4e86\u70b9\u3002</p><p>\u201c\u4f60\u5462\uff0c\u201d\u838e\u73de\u514b\u7a81\u7136\u53cd\u95ee\uff0c\u201c\u56de\u5230\u7269\u8d28\u754c\uff0c\u4f60\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d</p><p>\u743c\u6069\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u5c31\u50cf\u4f60\u8bf4\u7684\uff0c\u4eba\u5728\u5c40\u4e2d\uff0c\u8eab\u4e0d\u7531\u5df1\u3002\u4f60\u662f\u4e0d\u77e5\u9053\u81ea\u5df1\u60f3\u505a\u4ec0\u4e48\uff0c\u6211\u662f\u6839\u672c\u6ca1\u6cd5\u51b3\u5b9a\u81ea\u5df1\u8981\u505a\u4ec0\u4e48\u3002\u5982\u679c\u6211\u6ca1\u731c\u9519\u7684\u8bdd\uff0c\u53ea\u6015\u4e00\u56de\u53bb\u5c31\u53c8\u63a5\u5230\u4ec0\u4e48\u65b0\u7684\u6307\u4ee4\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u79cd\u751f\u6d3b\u2026\u2026\u542c\u8d77\u6765\u4f3c\u4e4e\u4e5f\u5f88\u7070\u6697\u554a\u3002\u201d</p><p>\u201c\u5514\uff0c\u7070\u6697\u4e5f\u8c08\u4e0d\u4e0a\u5427\u3002\u8fd9\u4e16\u754c\u4e0a\u7684\u6b7b\u4e0a\u73ed\u65cf\u591a\u4e86\u53bb\u4e86\uff0c\u62ff\u85aa\u6c34\uff0c\u542c\u6307\u4f7f\uff0c\u7ed9\u8001\u677f\u8dd1\u817f\u6253\u5de5\uff0c\u517b\u5bb6\u7cca\u53e3\uff0c\u6df7\u5403\u7b49\u6b7b\uff0c\u8fd9\u624d\u662f\u6b63\u5e38\u800c\u666e\u904d\u7684\u4eba\u751f\u561b\u3002\u201d</p><p>\u201c\u542c\u8d77\u6765\u4f60\u5bf9\u81ea\u5df1\u7684\u73b0\u72b6\u5f88\u6ee1\u610f\uff1f\u201d</p><p>\u201c\u6ee1\u610f\u5c31\u6709\u9b3c\u4e86\uff0c\u201d\u743c\u6069\u6ca1\u597d\u6c14\u5730\u8bf4\uff0c\u201c\u4f46\u6211\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d</p><p>\u4e24\u4eba\u90fd\u4e0d\u518d\u8bf4\u8bdd\uff0c\u65f6\u95f4\u9759\u6084\u6084\u5730\u6d41\u8fc7\uff0c\u743c\u6069\u89c9\u5f97\u6709\u4e9b\u5026\u4e86\uff0c\u6b63\u60f3\u8eba\u4e0b\u6765\u4f11\u606f\u4e00\u4f1a\uff0c\u838e\u73de\u514b\u78b0\u4e86\u78b0\u4ed6\uff0c\u628a\u671b\u8fdc\u955c\u9012\u8fc7\u6765\u3002</p><p>\u201c\u4e3b\u4eba\uff0c\u770b\u770b\u90a3\u662f\u8c01\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u743c\u6069\u63a5\u8fc7\u671b\u8fdc\u955c\uff0c\u671d\u7740\u9b45\u9b54\u6240\u6307\u7684\u65b9\u5411\u770b\u53bb\uff0c\u4e24\u4f4d\u5c11\u5973\u7684\u8eab\u5f71\u95ef\u5165\u89c6\u91ce\u3002</p><p>\u90a3\u662f\u4e24\u4e2a\u8eab\u9ad8\u76f8\u4eff\u7684\u5c11\u5973\uff0c\u6b63\u80a9\u5e76\u80a9\u8d70\u5728\u8857\u9053\u4e0a\uff0c\u8def\u4e0a\u9047\u5230\u7684\u9b54\u9b3c\u4eec\u7eb7\u7eb7\u907f\u8ba9\uff0c\u4e0d\u6562\u9760\u8fd1\u3002\u5de6\u4fa7\u7684\u5c11\u5973\u7a7f\u7740\u5929\u84dd\u8272\u886c\u886b\u548c\u7c73\u767d\u8272\u957f\u88e4\uff0c\u660e\u4eae\u7684\u9633\u5149\u7167\u5c04\u5728\u8138\u4e0a\uff0c\u767d\u7699\u7684\u808c\u80a4\u4e2d\u5fae\u5fae\u6cdb\u7740\u7ea2\u6655\uff0c\u78a7\u7eff\u773c\u7738\u4e2d\u6c89\u9759\u65e0\u6ce2\uff0c\u4eff\u4f5b\u5e7d\u6df1\u6e56\u6c34\uff0c\u6b63\u662f\u6885\u83f2\u65af\uff1b\u53f3\u4fa7\u7684\u5c11\u5973\u5168\u8eab\u88f9\u5728\u7070\u8272\u6597\u7bf7\u4e2d\uff0c\u515c\u5e3d\u6380\u5f00\uff0c\u9762\u5bb9\u59e3\u7f8e\uff0c\u6e05\u96c5\u79c0\u4e3d\u4e2d\u900f\u7740\u4e9b\u8bb8\u6de1\u6f20\uff0c\u6709\u79cd\u4e0d\u98df\u4eba\u95f4\u70df\u706b\u7684\u7f25\u6e3a\u6c14\u8d28\uff0c\u91d1\u8272\u7684\u773c\u7738\u7cb2\u7136\u751f\u8f89\uff0c\u8000\u82e5\u661f\u8fb0\uff0c\u662f\u6781\u96be\u5f97\u7684\u7f8e\u4eba\uff0c\u743c\u6069\u5374\u4e0d\u8ba4\u8bc6\u3002</p><p>\u201c\u5947\u602a\uff0c\u5979\u662f\u8c01\uff1f\u201d</p><p>\u201c\u5f25\u8d5b\u4e9a\uff0c\u90a3\u4e2a\u66e6\u5929\u4f7f\u3002\u201d\u838e\u73de\u514b\u5728\u65c1\u8fb9\u63d0\u9192\u3002</p><p>\u201c\u662f\u5979\uff1f\u201d</p><p>\u5728\u9ed1\u77f3\u57ce\u7684\u65f6\u5019\uff0c\u743c\u6069\u89c1\u8fc7\u5f25\u8d5b\u4e9a\u4e00\u9762\uff0c\u4f46\u5f53\u65f6\u6ca1\u770b\u5230\u8138\uff0c\u6240\u4ee5\u4e5f\u6ca1\u4ec0\u4e48\u5370\u8c61\u3002\u5982\u4eca\u7ec6\u770b\uff0c\u53d1\u73b0\u5979\u548c\u6885\u83f2\u65af\u5c45\u7136\u957f\u5f97\u9887\u6709\u51e0\u5206\u76f8\u4f3c\uff0c\u4e24\u4eba\u90fd\u6709\u7740\u4e00\u5934\u6d41\u6cfb\u5982\u7011\u7684\u707f\u91d1\u8272\u957f\u53d1\uff0c\u6bd4\u9633\u5149\u66f4\u52a0\u8000\u773c\u593a\u76ee\uff0c\u540c\u6837\u7684\u8eab\u6750\u9ad8\u6311\uff0c\u82f1\u6b66\u633a\u62d4\uff0c\u660e\u8273\u4e2d\u900f\u7740\u63a9\u85cf\u4e0d\u4f4f\u7684\u9690\u9690\u50b2\u6c14\uff0c\u4eff\u4f5b\u5323\u4e2d\u5229\u5251\uff0c\u867d\u7136\u950b\u8292\u4e0d\u9732\uff0c\u4f9d\u65e7\u51db\u51db\u8feb\u4eba\uff0c\u5018\u82e5\u53ea\u770b\u80cc\u5f71\u7684\u8bdd\uff0c\u5f88\u5bb9\u6613\u4f1a\u8bef\u8ba4\u4e3a\u662f\u4e00\u5bf9\u53cc\u80de\u80ce\u59d0\u59b9\u3002\u5f53\u7136\uff0c\u4ed4\u7ec6\u89c2\u5bdf\u7684\u8bdd\uff0c\u4e24\u4eba\u8fd8\u662f\u6709\u5f88\u5927\u5dee\u522b\u7684\uff0c\u5373\u4fbf\u4e0d\u8003\u8651\u773c\u775b\u989c\u8272\u7684\u8fe5\u5f02\uff0c\u5c31\u6c14\u8d28\u800c\u8a00\uff0c\u6885\u83f2\u65af\u7ec8\u7a76\u5e74\u5e7c\uff0c\u867d\u7136\u79c0\u7f8e\u65e0\u4fe6\uff0c\u7709\u5b87\u95f4\u603b\u8fd8\u5e26\u4e86\u4e9b\u8bb8\u7a1a\u5ae9\uff0c\u76f8\u6bd4\u8d77\u6765\u5f25\u6492\u4e9a\u5219\u663e\u5f97\u6210\u719f\u8bb8\u591a\u3002</p><p>\u201c\u5979\u4eec\u600e\u4e48\u4f1a\u5728\u4e00\u8d77\uff1f\u201d</p><p>\u743c\u6069\u7565\u7565\u6709\u4e9b\u8be7\u5f02\uff0c\u6885\u83f2\u65af\u5e76\u4e0d\u662f\u90a3\u79cd\u6d3b\u6cfc\u5f00\u6717\u7684\u5973\u5b69\u5b50\uff0c\u5e73\u7d20\u95f2\u6687\u65f6\u5927\u591a\u53ea\u662f\u7ec3\u5251\u3001\u8bfb\u4e66\uff0c\u6781\u5c11\u4f1a\u4e3b\u52a8\u548c\u4eba\u4ea4\u5f80\uff0c\u600e\u4e48\u4f1a\u548c\u5f25\u8d5b\u4e9a\u5728\u4e00\u8d77\u901b\u8857\uff0c\u5979\u4eec\u51e0\u5929\u524d\u8fd8\u7d20\u4e0d\u76f8\u8bc6\u7684\u5427\u3002</p><p>\u201c\u5723\u6b66\u58eb\u548c\u66e6\u5929\u4f7f\u5728\u4e00\u8d77\uff0c\u6709\u4ec0\u4e48\u503c\u5f97\u60ca\u8bb6\u7684\u3002\u201d\u838e\u73de\u514b\u6de1\u6de1\u8bf4\u3002</p><p>\u201c\u5514\uff0c\u4e5f\u662f\u554a\u3002\u201d</p><p>\u201c\u5c31\u50cf\u6211\u548c\u4f60\u5728\u8fd9\u91cc\u5077\u7aa5\u4e00\u6837\uff0c\u5f88\u6b63\u5e38\u5566\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u6709\u53ef\u6bd4\u6027\u5417\uff1f\u201d</p><p>\u201c\u6709\u554a\uff0c\u5723\u6b66\u58eb\u548c\u66e6\u5929\u4f7f\uff0c\u6211\u548c\u4f60\uff0c\u8fd9\u5c31\u662f\u6240\u8c13\u7684\u2018\u7269\u4ee5\u7c7b\u805a\uff0c\u4eba\u4ee5\u7fa4\u5206\u2019\uff0c\u4f60\u4e0a\u6b21\u8bf4\u8fc7\u7684\u8c1a\u8bed\u3002\u201d</p><p>\u201c\u4f60\u8bb0\u5fc6\u529b\u771f\u597d\u3002\u201d</p><p>\u201c\u591a\u8c22\u5938\u5956\u3002\u201d</p><p>\u4ece\u671b\u8fdc\u955c\u4e2d\u770b\u53bb\uff0c\u6885\u83f2\u65af\u548c\u5f25\u6492\u4e9a\u5e76\u4e0d\u662f\u5728\u901b\u8857\uff0c\u800c\u662f\u5728\u4ea4\u8c08\uff0c\u743c\u6069\u597d\u5947\u5fc3\u8d77\uff0c\u60f3\u542c\u542c\u5979\u4eec\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4f46\u8ddd\u79bb\u5b9e\u5728\u592a\u8fdc\uff0c\u5c31\u7b97\u7528\u4f20\u8baf\u9b54\u6cd5\u5e94\u8be5\u4e5f\u4e0d\u7ba1\u7528\uff0c\u800c\u4e14\u80af\u5b9a\u4f1a\u88ab\u5bf9\u65b9\u5bdf\u89c9\u3002\u7565\u4e00\u8e0c\u8e87\uff0c\u4e24\u4f4d\u5c11\u5973\u5df2\u7ecf\u8f6c\u8fc7\u8857\u9053\uff0c\u8fdb\u5165\u4e00\u5ea7\u53e4\u6734\u9648\u65e7\u7684\u5b85\u9662\u5f53\u4e2d\u53bb\u4e86\u3002</p></article>"}}}]);
