(window.webpackJsonp=window.webpackJsonp||[]).push([[1570],{1593:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e5d\u8282 \u518d\u6b21\u4ea4\u6613",content:"<article><h2>\u7b2c\u5341\u4e5d\u8282 \u518d\u6b21\u4ea4\u6613</h2><p>\u57ce\u5934\u4e0a\u65e9\u5df2\u5b89\u7f6e\u5b8c\u6bd5\u7684\u4e00\u6392\u6392\u5f29\u70ae\u5f00\u59cb\u6309\u7167\u6b21\u5e8f\u731b\u70c8\u53d1\u5c04\uff0c\u957f\u77db\u822c\u7684\u7bad\u77e2\u4e0a\u954c\u523b\u4e86\u5404\u79cd\u9b54\u6cd5\u7b26\u6587\uff0c\u7206\u88c2\u3001\u51b0\u51bb\u3001\u95ea\u7535\u3001\u97f3\u6ce2\u751a\u81f3\u5723\u5149\u6548\u679c\u6b64\u8d77\u5f7c\u4f0f\uff0c\u77ac\u95f4\u5728\u6f2b\u5c71\u904d\u91ce\u800c\u6765\u7684\u4ea1\u7075\u6d77\u6d0b\u4e2d\u6495\u5f00\u4e86\u4e00\u5757\u5de8\u5927\u7684\u7a7a\u6d1e\u3002</p><p>\u4e58\u5750\u7740\u68a6\u9b47\u7684\u51b0\u9b54\u6325\u821e\u957f\u5200\uff0c\u5f00\u59cb\u56db\u9762\u6e38\u8d70\uff0c\u6740\u4f24\u8fb9\u7f18\u7684\u4ea1\u7075\uff0c\u540c\u65f6\u8c28\u614e\u5730\u548c\u9b54\u50cf\u4fdd\u6301\u8ddd\u79bb\u3002\u624b\u6267\u94a2\u77db\u7684\u9aa8\u9b54\u5217\u9635\u63a8\u8fdb\uff0c\u5b83\u4eec\u867d\u7136\u662f\u9b54\u9b3c\uff0c\u5374\u5728\u5f88\u5927\u7a0b\u5ea6\u4e0a\u8fd1\u4f3c\u4ea1\u7075\uff0c\u53ef\u4ee5\u6709\u6548\u5730\u62b5\u5fa1\u8d1f\u80fd\u91cf\u7684\u4f24\u5bb3\u3002\u6570\u5341\u540d\u5168\u526f\u6b66\u88c5\u7684\u5815\u5929\u4f7f\u548c\u6df1\u72f1\u70bc\u9b54\u81ea\u57ce\u4e2d\u98de\u8d77\uff0c\u4e00\u7fa4\u9b54\u9b3c\u796d\u53f8\u4ece\u63a9\u4f53\u540e\u9762\u8d70\u51fa\uff0c\u9ad8\u4e3e\u7740\u4e0d\u540c\u7684\u90aa\u5fbd\uff0c\u5f00\u59cb\u65bd\u6cd5\u8bf5\u5492\uff0c\u4e3a\u5b83\u4eec\u53e0\u52a0\u5404\u79cd\u9632\u62a4\u548c\u8f85\u52a9\u6cd5\u672f\u3002</p><p>\u62dc\u5c14\u867d\u7136\u4f4d\u5217\u5730\u72f1\u4e5d\u9886\u4e3b\u4e4b\u4e00\uff0c\u5374\u5e76\u975e\u771f\u6b63\u7684\u5927\u9b54\u9b3c\uff0c\u6ca1\u6709\u80fd\u529b\u8d50\u4e88\u795e\u672f\uff0c\u66f4\u6ca1\u6709\u796d\u53f8\uff0c\u4f46\u5176\u4ed6\u516b\u5927\u9886\u4e3b\u662f\u6709\u7684\u3002\u867d\u7136\u6570\u91cf\u4e0d\u591a\uff0c\u52a0\u8d77\u6765\u4e5f\u9887\u4e3a\u53ef\u89c2\uff0c\u6b64\u65f6\u6b63\u597d\u6d3e\u4e0a\u7528\u573a\u3002</p><p>\u8fde\u7eed\u7684\u7a7a\u6c14\u6ce2\u6298\u95ea\u52a8\uff0c\u5815\u5929\u4f7f\u548c\u6df1\u72f1\u70bc\u9b54\u4eec\u7cbe\u786e\u4f20\u9001\u5230\u4e86\u9b54\u50cf\u8eab\u5468\uff0c\u6325\u821e\u7740\u7ecf\u8fc7\u9b54\u6cd5\u5f3a\u5316\u7684\u6b66\u5668\u75af\u72c2\u780d\u5288\uff0c\u8bd5\u56fe\u9020\u6210\u6700\u5927\u7684\u4f24\u5bb3\u3002\u9b54\u50cf\u7684\u4e24\u4e2a\u5934\u9885\u6b64\u65f6\u5b8c\u7f8e\u5730\u53d1\u6325\u4e86\u4f5c\u7528\uff0c\u5de6\u4fa7\u7684\u5353\u5c14\u5973\u795e\u5583\u5583\u4f4e\u8bed\uff0c\u8d64\u88f8\u7684\u624b\u81c2\u5f53\u7a7a\u865a\u753b\uff0c\u4e94\u679a\u94f6\u6212\u4e0a\u5149\u8292\u8f6e\u756a\u95ea\u70c1\uff0c\u91ca\u653e\u51fa\u4e00\u9053\u9053\u90aa\u6076\u7684\u4ea1\u7075\u6cd5\u672f\uff1b\u53f3\u4fa7\u7684\u517d\u5934\u5219\u5728\u5486\u54ee\u6012\u543c\uff0c\u7c97\u58ee\u624b\u81c2\u6a2a\u626b\u76f4\u6325\uff0c\u6307\u5c16\u9aa8\u523a\u5bd2\u5149\u51db\u51db\uff0c\u9510\u5229\u5982\u5200\u3002\u5815\u5929\u4f7f\u548c\u6df1\u72f1\u70bc\u9b54\u4e0d\u6562\u76f4\u9762\u5176\u950b\uff0c\u53ea\u80fd\u51ed\u501f\u7740\u9ad8\u901f\u98de\u884c\u8eb2\u95ea\u8d8b\u907f\uff0c\u8d81\u9699\u5077\u88ad\u3002</p><p>\u9b54\u50cf\u53d8\u5316\u795e\u76f8\uff0c\u884c\u52a8\u901f\u5ea6\u5927\u5e45\u5ea6\u63d0\u5347\uff0c\u4f46\u76f8\u6bd4\u8d77\u5815\u5929\u4f7f\u548c\u70bc\u9b54\u6765\u8fd8\u662f\u660e\u663e\u7b28\u62d9\u5f88\u591a\uff0c\u53f3\u4fa7\u517d\u81c2\u867d\u7136\u529b\u9053\u5a01\u731b\uff0c\u5374\u662f\u5c61\u51fb\u4e0d\u4e2d\uff0c\u53cd\u800c\u8eab\u4e0a\u88ab\u5212\u5f00\u5341\u51e0\u9053\u6df1\u6df1\u4f24\u53e3\uff0c\u4e00\u65f6\u95f4\u5b8c\u5168\u843d\u4e86\u4e0b\u98ce\u3002\u5de6\u4fa7\u7684\u5353\u5c14\u5973\u795e\u89c1\u72b6\uff0c\u6f20\u65e0\u8868\u60c5\u7684\u53cc\u7738\u6728\u7136\u7ffb\u8f6c\uff0c\u7ea2\u5507\u5fae\u5f20\uff0c\u4e00\u70b9\u8f7b\u5fae\u7684\u58f0\u54cd\u81ea\u53e3\u4e2d\u53d1\u51fa\uff0c\u8d77\u521d\u65f6\u4e0d\u8fc7\u4eff\u4f5b\u868a\u866b\u4f4e\u9e23\uff0c\u8f6c\u77ac\u95f4\u5df2\u7ecf\u5c16\u9510\u5982\u9488\uff0c\u523a\u8033\u6b32\u804b\uff0c\u4eff\u4f5b\u5343\u4e07\u4e2a\u6fd2\u6b7b\u8005\u6700\u540e\u7684\u6050\u60e7\u5c16\u53eb\u6c47\u805a\u5728\u4e00\u8d77\uff0c\u6b47\u65af\u5e95\u91cc\u5730\u9661\u7136\u7206\u53d1\u51fa\u6765\u3002</p><p>\u5973\u5996\u4e4b\u568e\uff01</p><p>\u4ea1\u7075\u672f\u7684\u7ec8\u6781\u9b54\u6cd5\u4e4b\u4e00\uff0c\u80fd\u591f\u4ee5\u8574\u542b\u7eaf\u7cb9\u6b7b\u4ea1\u80fd\u91cf\u7684\u97f3\u6ce2\u6740\u4f24\u6240\u6709\u751f\u8005\uff0c\u7531\u66fe\u7ecf\u662f\u5353\u5c14\u4ea1\u7075\u5973\u795e\u7684\u9f50\u96c5\u6e29\u7eb1\u4e3d\u91ca\u653e\u51fa\u6765\uff0c\u5a01\u529b\u8f83\u4e4b\u5bfb\u5e38\u5deb\u5e08\u66f4\u80dc\u5341\u500d\u3002\u6b63\u5728\u56f4\u653b\u9b54\u50cf\u7684\u5815\u5929\u4f7f\u548c\u6df1\u72f1\u70bc\u9b54\u867d\u7136\u90fd\u5df2\u7ecf\u88ab\u9884\u5148\u9644\u52a0\u4e86\u5404\u79cd\u795d\u798f\u6cd5\u672f\uff0c\u72b9\u81ea\u62b5\u5fa1\u4e0d\u4f4f\u8fd9\u90aa\u6076\u6cd5\u672f\u7684\u5a01\u529b\uff0c\u7eb7\u7eb7\u9000\u907f\uff0c\u6709\u4e24\u4e2a\u5815\u5929\u4f7f\u8ddd\u79bb\u592a\u8fd1\uff0c\u8eb2\u95ea\u4e0d\u53ca\uff0c\u53e3\u9f3b\u4e03\u7a8d\u4e2d\u77ac\u95f4\u9c9c\u8840\u55b7\u6d8c\uff0c\u5168\u8eab\u62bd\u6410\uff0c\u76ae\u8089\u5e72\u67af\u53d1\u9ed1\uff0c\u4e00\u5148\u4e00\u540e\u6454\u843d\u5730\u9762\u3002\u9b54\u50cf\u8d81\u52bf\u8fdb\u903c\uff0c\u53f3\u4fa7\u517d\u81c2\u6a2a\u626b\uff0c\u6b63\u635e\u4e2d\u4e00\u53ea\u6df1\u72f1\u70bc\u9b54\u7684\u80f8\u819b\u3002</p><p>\u867d\u7136\u5815\u5929\u4f7f\u548c\u70bc\u9b54\u90fd\u7a7f\u7740\u6700\u7cbe\u826f\u7684\u94e0\u7532\uff0c\u4f46\u5728\u517d\u722a\u7684\u5de8\u529b\u6495\u626f\u4e0b\u8106\u82e5\u8584\u7eb8\uff0c\u4e0d\u582a\u4e00\u51fb\uff0c\u5b8c\u5168\u8d77\u4e0d\u5230\u534a\u70b9\u9632\u5fa1\u4f5c\u7528\u3002\u53ea\u542c\u5f97\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u70bc\u9b54\u7684\u80f8\u53e3\u7834\u5f00\u5927\u6d1e\uff0c\u706b\u7ea2\u5de8\u81c2\u900f\u4f53\u800c\u8fc7\uff0c\u517d\u722a\u4e0a\u8840\u8089\u6dcb\u6f13\uff0c\u6697\u7eff\u8272\u7684\u8150\u81ed\u6db2\u4f53\u70b9\u70b9\u6ef4\u4e0b\uff0c\u5728\u7a7a\u6c14\u4e2d\u5316\u4f5c\u6735\u6735\u7126\u9ed1\u706b\u7130\uff0c\u968f\u5373\u6d88\u6563\u3002</p><p>\u5de8\u5927\u7684\u8f70\u9e23\u58f0\u81ea\u9065\u8fdc\u5904\u9690\u9690\u4f20\u6765\uff0c\u51e0\u5341\u9897\u70bd\u70c8\u7684\u9668\u77f3\u5212\u8fc7\u5929\u9645\uff0c\u9ad8\u901f\u5760\u843d\u7838\u5411\u9b54\u50cf\uff0c\u6df1\u72f1\u70bc\u9b54\u518d\u5ea6\u4f7f\u7528\u51fa\u5929\u8d4b\u7684\u53ec\u5524\u6d41\u661f\u7206\u80fd\u529b\u3002\u5815\u5929\u4f7f\u9f50\u9f50\u9ad8\u4e3e\u5de8\u5251\u540c\u58f0\u8bf5\u5492\uff0c\u9eef\u7070\u7fbd\u7ffc\u5c42\u53e0\u8212\u5f20\uff0c\u65e0\u5f62\u7684\u65cb\u8f6c\u5200\u950b\u4ece\u957f\u888d\u4e2d\u8ff8\u53d1\uff0c\u547c\u5578\u7740\u5207\u5f00\u7a7a\u6c14\uff0c\u6c47\u805a\u6210\u6b7b\u4ea1\u7684\u6f29\u6da1\uff0c\u5c06\u9b54\u50cf\u5e2d\u5377\u5176\u4e2d\u3002\u5353\u5c14\u5973\u795e\u5355\u624b\u62ac\u8d77\uff0c\u70b9\u62b9\u52fe\u52d2\uff0c\u4e00\u9053\u9053\u7ec6\u5c0f\u7684\u7d2b\u8272\u7b26\u6587\u81ea\u6307\u5c16\u98de\u51fa\uff0c\u5728\u8eab\u524d\u4ea4\u7ec7\u7eb7\u9519\uff0c\u8fde\u7f00\u6210\u7247\uff0c\u8361\u6f3e\u865a\u7a7a\uff0c\u5f62\u6210\u4e00\u5708\u6697\u7d2b\u8272\u7684\u5149\u76fe\u7b3c\u7f69\u81ea\u8eab\u3002\u6d41\u661f\u7206\u548c\u5229\u5203\u58c1\u969c\u6c79\u6d8c\u800c\u6765\uff0c\u4e89\u5148\u6050\u540e\u5730\u649e\u4e0a\u5149\u76fe\uff0c\u7136\u540e\u6563\u5f52\u65e0\u5f62\u3002\u6bcf\u649e\u51fb\u4e00\u6b21\uff0c\u62a4\u76fe\u7684\u5149\u8292\u5c31\u660e\u663e\u9eef\u6de1\u5fae\u5f31\u51e0\u5206\uff0c\u4f46\u4f9d\u65e7\u7a33\u7a33\u62b5\u5fa1\u4f4f\u4e86\u6240\u6709\u7684\u51b2\u51fb\uff0c\u9b54\u50cf\u81ea\u8eab\u4e0d\u66fe\u53d7\u5230\u534a\u70b9\u4f24\u5bb3\u3002</p><p>\u6b63\u5f53\u6240\u6709\u4eba\u90fd\u8ba4\u4e3a\u8fd9\u6b21\u653b\u51fb\u5f92\u52b3\u65e0\u529f\u65f6\uff0c\u4e00\u9053\u8000\u773c\u7684\u95ea\u7535\u81ea\u4e1c\u65b9\u5929\u9645\u817e\u8d77\uff0c\u51b2\u7834\u91cd\u91cd\u9634\u973e\uff0c\u73b0\u51fa\u4e00\u4e2a\u7480\u74a8\u593a\u76ee\u7684\u8eab\u5f71\u3002\u66e6\u5929\u4f7f\u5f25\u8d5b\u4e9a\u51cc\u7a7a\u7ad9\u7acb\uff0c\u5de6\u624b\u6301\u91d1\u8272\u957f\u5f13\uff0c\u53f3\u624b\u865a\u62c9\u7a7a\u5f26\uff0c\u84c4\u52bf\u5f85\u53d1\u3002\u4e5d\u4e2a\u8eab\u7a7f\u7ea2\u888d\u7684\u4eba\u5f71\u4ece\u57ce\u4e2d\u98de\u8d77\uff0c\u5347\u4e0a\u9ad8\u7a7a\uff0c\u4e09\u4eba\u5728\u5185\uff0c\u516d\u4eba\u5728\u5916\uff0c\u6784\u6210\u4e24\u9053\u540c\u5fc3\u5706\u73af\uff0c\u6b63\u5c06\u66e6\u5929\u4f7f\u5305\u56f4\u5728\u4e2d\u5fc3\u3002\u5404\u79cd\u989c\u8272\u7684\u7075\u7ebf\u4ece\u7ea2\u888d\u8005\u8eab\u4e0a\u5c04\u51fa\uff0c\u5728\u7a7a\u4e2d\u4ea4\u7ec7\u7eb7\u9519\uff0c\u75be\u901f\u7f16\u7ec7\u6210\u4e00\u5ea7\u7e41\u590d\u65e0\u6bd4\u7684\u5e9e\u5927\u5706\u5f62\u9b54\u6cd5\u9635\u3002</p><p>\u4eff\u4f5b\u53d7\u5230\u611f\u5e94\u4e00\u822c\uff0c\u5728\u66e6\u5929\u4f7f\u7684\u5934\u9876\u4e0a\uff0c\u539f\u672c\u4e4c\u4e91\u56db\u5408\u7684\u9ad8\u7a7a\u8361\u5f00\u4e00\u7ebf\uff0c\u539a\u5bc6\u7684\u91d1\u8272\u4e91\u6c14\u5982\u6d77\u6d6a\u822c\u8dcc\u5b95\u7ffb\u6eda\uff0c\u707f\u5982\u671d\u971e\uff0c\u7d27\u63a5\u7740\u75be\u901f\u65cb\u52a8\u8d77\u6765\uff0c\u5f62\u6210\u5de8\u5927\u7684\u6f29\u6da1\uff0c\u5982\u6f0f\u6597\u822c\u81ea\u5929\u9876\u5d29\u6cc4\u800c\u4e0b\uff0c\u6ce8\u5165\u66e6\u5929\u4f7f\u4f53\u5185\uff0c\u9b54\u6cd5\u9635\u540c\u65f6\u5d29\u6563\uff0c\u5316\u4f5c\u5343\u4e07\u70b9\u661f\u5149\u56db\u6563\u3002\u5f25\u8d5b\u4e9a\u5389\u559d\u4e00\u58f0\uff0c\u91d1\u8272\u7cb2\u7136\u7684\u53cc\u76ee\u9661\u7136\u5f20\u5f00\uff0c\u53f3\u81c2\u4e0a\u5149\u534e\u7efd\u653e\uff0c\u8000\u5982\u5341\u65e5\u9f50\u7167\uff0c\u4e00\u652f\u7531\u7eaf\u7cb9\u5149\u660e\u5723\u529b\u5851\u6210\u7684\u900f\u660e\u957f\u7bad\u81ea\u52a8\u51dd\u805a\u6210\u5f62\uff0c\u671d\u7740\u9b54\u50cf\u7834\u7a7a\u6fc0\u5c04\u3002</p><p>\u5982\u679c\u8bf4\u4ee5\u524d\u66e6\u5929\u4f7f\u5c04\u51fa\u7684\u77ac\u6740\u7bad\u53ea\u662f\u201c\u950b\u9510\u201d\uff0c\u90a3\u4e48\u8fd9\u4e00\u51fb\u4fbf\u662f\u5b8c\u5168\u7684\u52bf\u9053\u96c4\u6d51\uff0c\u900f\u660e\u5149\u7bad\u5438\u4e91\u6c14\uff0c\u631f\u98ce\u96f7\uff0c\u4eff\u4f5b\u957f\u6c5f\u5927\u6cb3\u4e00\u822c\u6c79\u6d8c\u6f8e\u6e43\u6eda\u6eda\u800c\u6765\u3002\u9b54\u50cf\u7684\u7d2b\u8272\u5149\u76fe\u539f\u672c\u5df2\u7ecf\u8fde\u906d\u653b\u51fb\uff0c\u53d8\u5f97\u900f\u660e\u865a\u5316\uff0c\u6beb\u65e0\u60ac\u5ff5\u5730\u88ab\u4e00\u51fb\u9707\u788e\uff0c\u6e83\u6563\u65e0\u5f62\u3002</p><p>\u5149\u7bad\u6467\u7834\u62a4\u76fe\uff0c\u4f59\u52bf\u4e0d\u8870\uff0c\u7cbe\u786e\u5730\u547d\u4e2d\u9b54\u50cf\u5de6\u4fa7\u7684\u5353\u5c14\u5934\u9885\u3002\u4ea1\u7075\u5973\u795e\u5f20\u53e3\u6b32\u547c\uff0c\u5374\u53c8\u53d1\u4e0d\u51fa\u534a\u70b9\u58f0\u97f3\uff0c\u50f5\u7acb\u7247\u523b\uff0c\u4e00\u62b9\u91d1\u5149\u81ea\u7709\u5fc3\u5904\u8dc3\u8d77\uff0c\u5927\u534a\u4e2a\u8111\u888b\u8f70\u7136\u70b8\u88c2\u5f00\u6765\u3002</p><p>\u203b\u203b\u203b</p><p>\u548c\u795e\u8eaf\u9b54\u50cf\u4ea4\u6218\u8fd9\u4e48\u4e45\uff0c\u62dc\u5c14\u548c\u4ed6\u7684\u624b\u4e0b\u4e5f\u9010\u6e10\u6478\u5230\u4e86\u4e00\u4e9b\u95e8\u9053\u3002\u9b54\u50cf\u53d8\u5316\u795e\u76f8\u786e\u5b9e\u975e\u5e38\u68d8\u624b\uff0c\u5f80\u5f80\u80fd\u591f\u91ca\u653e\u51fa\u5a01\u529b\u65e0\u6bd4\u7684\u9b54\u6cd5\u6216\u8005\u5f02\u80fd\uff0c\u4f46\u968f\u540e\u7684\u77ed\u65f6\u95f4\u5185\u5c31\u4f1a\u660e\u663e\u9677\u5165\u975e\u5e38\u865a\u5f31\u7684\u72b6\u6001\u3002\u4fbf\u5982\u4e0a\u6b21\u5728\u9ed1\u77f3\u57ce\uff0c\u9b54\u50cf\u53d8\u5316\u8679\u5f69\u9f99\u795e\u76f8\uff0c\u4e00\u8bb0\u8679\u5149\u6cd5\u7403\u98ce\u66b4\u5f53\u573a\u51fb\u6740\u8fd1\u767e\u540d\u5815\u5929\u4f7f\u3001\u6df1\u72f1\u70bc\u9b54\u548c\u70bc\u72f1\u708e\u9f99\uff0c\u4f46\u7d27\u63a5\u7740\u5c31\u88ab\u57cb\u4f0f\u5df2\u4e45\u7684\u66e6\u5929\u4f7f\u5077\u88ad\uff0c\u5018\u82e5\u4e0d\u662f\u5deb\u5996\u4eec\u53ca\u65f6\u6536\u56de\u9b54\u50cf\uff0c\u51e0\u4e4e\u5c31\u8981\u996e\u6068\u5f53\u573a\u3002</p><p>\u5982\u4eca\u6545\u6280\u91cd\u65bd\uff0c\u8d81\u7740\u5149\u76fe\u5c06\u7834\u672a\u7834\u4e4b\u9645\uff0c\u5353\u5c14\u5973\u795e\u6765\u4e0d\u53ca\u91cd\u65b0\u5f20\u5f00\u62a4\u7f69\uff0c\u66e6\u5929\u4f7f\u5168\u529b\u4e00\u51fb\uff0c\u679c\u7136\u6210\u529f\u5f97\u624b\u3002\u53ea\u662f\u9b54\u50cf\u73b0\u5728\u6709\u4e24\u4e2a\u5934\u9885\uff0c\u8f70\u6740\u5176\u4e00\uff0c\u4f9d\u65e7\u8fd8\u5269\u4e00\u4e2a\uff0c\u800c\u521a\u624d\u90a3\u6837\u77f3\u7834\u5929\u60ca\u7684\u4e00\u7bad\uff0c\u5df2\u7ecf\u8017\u53bb\u66e6\u5929\u4f7f\u5168\u90e8\u529b\u6c14\uff0c\u66f4\u662f\u501a\u9760\u4e86\u9b54\u6cd5\u9635\u7684\u8f85\u52a9\uff0c\u77ed\u65f6\u95f4\u5185\u662f\u518d\u4e5f\u5c04\u4e0d\u51fa\u6765\u4e86\u3002</p><p>\u5c3d\u7ba1\u5982\u6b64\uff0c\u8fd9\u4f9d\u65e7\u662f\u4e00\u4e2a\u6f02\u4eae\u7684\u80dc\u5229\uff0c\u6781\u5927\u5730\u9f13\u821e\u4e86\u9b54\u9b3c\u4eec\u7684\u58eb\u6c14\u3002\u5815\u5929\u4f7f\u548c\u6df1\u72f1\u70bc\u9b54\u8d81\u673a\u518d\u5ea6\u4e0a\u524d\u56f4\u653b\uff0c\u4ed6\u4eec\u7684\u914d\u5408\u5341\u5206\u5a34\u719f\uff0c\u51ed\u501f\u7740\u9ad8\u901f\u7684\u673a\u52a8\u529b\u7ff1\u7fd4\u6e38\u51fb\uff0c\u624b\u4e2d\u7684\u5229\u5251\u5728\u9b54\u50cf\u8eab\u4e0a\u5e26\u51fa\u4e00\u9053\u9053\u4f24\u75d5\uff0c\u653b\u51fb\u7684\u91cd\u70b9\u662f\u5934\u90e8\u548c\u56db\u80a2\u5173\u8282\u90e8\u4f4d\u3002\u9b54\u50cf\u5e76\u975e\u751f\u547d\u4f53\uff0c\u5c31\u7b97\u523a\u7a7f\u5fc3\u810f\u4e5f\u6ca1\u4ec0\u4e48\u610f\u4e49\uff0c\u4f46\u5018\u82e5\u51fb\u6bc1\u5934\u90e8\u5374\u80fd\u5c06\u5b83\u91cd\u521b\uff0c\u9000\u4e00\u6b65\u8bf4\uff0c\u80fd\u591f\u5c06\u56db\u80a2\u5378\u4e0b\uff0c\u540c\u6837\u4e5f\u80fd\u6709\u6548\u5730\u5927\u5e45\u5ea6\u524a\u5f31\u5b83\u7684\u6218\u6597\u529b\u3002\u5b83\u867d\u7136\u6050\u6016\uff0c\u603b\u4e5f\u662f\u4eff\u7167\u4eba\u5f62\u8bbe\u8ba1\uff0c\u5018\u82e5\u65ad\u4e86\u80f3\u818a\u7f3a\u4e86\u817f\uff0c\u8fd8\u80fd\u6709\u591a\u5927\u5a01\u80c1\u3002\u9b54\u50cf\u957f\u81c2\u6325\u821e\uff0c\u5b81\u53ef\u653e\u5f03\u5bf9\u8eaf\u5e72\u90e8\u4f4d\u7684\u9632\u62a4\uff0c\u5bf9\u8fd9\u4e9b\u5173\u952e\u7684\u8981\u5bb3\u5b88\u5fa1\u5f97\u5341\u5206\u4e25\u5bc6\uff0c\u4f46\u53f3\u4fa7\u5353\u5c14\u5973\u795e\u7684\u5934\u9885\u88ab\u5c04\u6bc1\uff0c\u5931\u53bb\u4e86\u9b54\u6cd5\u8f85\u52a9\uff0c\u987f\u65f6\u663e\u5f97\u72ec\u529b\u96be\u652f\u8d77\u6765\u3002</p><p>\u6b63\u5f53\u5c40\u52bf\u4eff\u4f5b\u5f00\u59cb\u597d\u8f6c\u65f6\uff0c\u5f02\u53d8\u518d\u5ea6\u53d1\u751f\u3002</p><p>\u4e00\u9053\u9752\u84dd\u8272\u5149\u97ad\u5728\u9b54\u50cf\u638c\u5fc3\u6d6e\u73b0\uff0c\u5f53\u7a7a\u6a2a\u626b\uff0c\u5c06\u4e00\u540d\u70bc\u9b54\u7262\u7262\u5377\u4f4f\uff0c\u5343\u4e07\u9053\u7ec6\u5c0f\u7684\u9ed1\u8272\u89e6\u987b\u81ea\u5149\u97ad\u4e0a\u5ef6\u4f38\u800c\u51fa\uff0c\u4eff\u4f5b\u7ae0\u9c7c\u4e00\u822c\u900f\u5165\u70bc\u9b54\u4f53\u5185\uff0c\u4ee4\u5b83\u7684\u5e9e\u5927\u8eab\u8eaf\u50cf\u76ae\u7403\u6cc4\u6c14\u4e00\u822c\u77ac\u95f4\u5e72\u67af\u3002\u9b54\u50cf\u4e00\u51fb\u5f97\u624b\uff0c\u53f3\u4fa7\u517d\u5934\u5f20\u5f00\u5927\u5634\uff0c\u4e00\u628a\u5c06\u6302\u5728\u624b\u81c2\u4e0a\u7684\u6b8b\u5c38\u585e\u5165\u53e3\u4e2d\uff0c\u5927\u8086\u5480\u56bc\uff0c\u541e\u54bd\u4e0b\u53bb\u3002\u4e4c\u9ed1\u8272\u5149\u8292\u5982\u6d41\u6c34\u822c\u6f2b\u6ea2\u51fa\u6765\uff0c\u5b83\u8eab\u4f53\u4e0a\u7684\u521b\u53e3\u4ee5\u8089\u773c\u53ef\u89c1\u7684\u901f\u5ea6\u5feb\u901f\u6108\u5408\uff0c\u7247\u523b\u95f4\u5df2\u7ecf\u6062\u590d\u5c0f\u534a\u3002</p><p>\u201c\u5b83\u5c45\u7136\u80fd\u591f\u81ea\u6108\uff01\u201d</p><p>\u89c1\u6b64\u60c5\u5f62\uff0c\u6240\u6709\u4eba\u90fd\u4e0d\u7981\u5012\u5438\u4e00\u53e3\u51b7\u6c14\u3002\u795e\u8eaf\u9b54\u50cf\u4e4b\u6240\u4ee5\u68d8\u624b\uff0c\u5f88\u5927\u7a0b\u5ea6\u5728\u4e8e\u5b83\u80fd\u591f\u4fee\u590d\uff0c\u800c\u9b54\u9b3c\u7684\u5175\u529b\u662f\u6b7b\u4e00\u70b9\u5c11\u4e00\u70b9\u3002\u5982\u4eca\u8fd9\u9b54\u50cf\u5c45\u7136\u80fd\u591f\u5728\u6218\u573a\u4e0a\u9760\u541e\u566c\u5c38\u4f53\u81ea\u7597\uff0c\u90a3\u9664\u975e\u80fd\u591f\u5728\u6781\u77ed\u65f6\u95f4\u5185\u4e00\u53e3\u6c14\u5c06\u5b83\u6467\u6bc1\uff0c\u5426\u5219\u6839\u672c\u5c31\u662f\u65e0\u654c\u7684\u5b58\u5728\u3002</p><p>\u9b54\u50cf\u541e\u4e0b\u70bc\u9b54\u5c38\u4f53\uff0c\u72b9\u4e0d\u6ee1\u8db3\uff0c\u72f0\u72de\u7684\u517d\u5934\u6e38\u76ee\u56db\u987e\uff0c\u9661\u7136\u62ac\u8d77\u5de6\u817f\u91cd\u91cd\u8e29\u4e0b\uff0c\u9ed1\u8272\u7684\u706b\u7130\u4ece\u5730\u5e95\u8f70\u5730\u817e\u8d77\uff0c\u5316\u4f5c\u6570\u5341\u9053\u706b\u86c7\u56db\u6563\u6e38\u8d70\uff0c\u91ca\u653e\u51fa\u6d53\u5bc6\u7684\u70df\u96fe\uff0c\u8f6c\u773c\u95f4\u5c06\u5927\u534a\u4e2a\u6218\u573a\u90fd\u7b3c\u7f69\u5728\u706b\u6d77\u4e4b\u4e2d\u3002\u9b54\u50cf\u7684\u5de8\u53e3\u5f20\u5f00\u5230\u4e0d\u53ef\u601d\u8bae\u7684\u7a0b\u5ea6\uff0c\u4fbf\u4eff\u4f5b\u6df1\u4e0d\u89c1\u5e95\u7684\u9ed1\u6d1e\u4e00\u822c\uff0c\u5468\u906d\u4e00\u5207\u5149\u7ebf\u90fd\u88ab\u6c89\u6c89\u5438\u6536\u8fdb\u53bb\uff0c\u9ec4\u7eff\u8272\u7684\u8150\u8d25\u4e91\u6c14\u5f25\u6563\u800c\u51fa\uff0c\u6d53\u7a20\u51dd\u91cd\u3002</p><p>\u4eff\u4f5b\u957f\u9cb8\u5438\u6c34\u4e00\u822c\uff0c\u6210\u5343\u4e0a\u4e07\u71c3\u70e7\u7684\u5c38\u4f53\u548c\u7126\u9ed1\u7684\u9aa8\u9abc\u5728\u70df\u4e91\u4e2d\u7ffb\u6eda\u7740\uff0c\u88ab\u9b54\u50cf\u541e\u5165\u8179\u5185\u3002\u539f\u672c\u5df2\u7ecf\u5de8\u5927\u7684\u5f62\u4f53\u53d8\u5f97\u66f4\u52a0\u81a8\u80c0\u8d77\u6765\u3002\u4e24\u53ea\u5de8\u5927\u7684\u5f2f\u89d2\u81ea\u517d\u5934\u4e2d\u873f\u8712\u5ef6\u4f38\uff0c\u6ef4\u843d\u7740\u7eff\u8272\u7684\u80c6\u6c41\u4e0e\u9ed1\u8272\u7684\u8840\u6db2\uff0c\u539f\u672c\u5149\u6ed1\u7684\u76ae\u80a4\u5feb\u901f\u5e72\u67af\u4e0b\u53bb\uff0c\u9ec4\u8272\u7684\u8113\u6c34\u4ece\u773c\u7736\u4e2d\u6d8c\u51fa\uff0c\u6d41\u7ecf\u4f53\u8868\u65f6\u53d1\u51fa\u54cd\u4eae\u7684\u567c\u556a\u7206\u88c2\u58f0\uff0c\u4eff\u4f5b\u662f\u65e0\u6570\u7684\u4ea1\u7075\u4eec\u5728\u540c\u65f6\u53d1\u51fa\u602a\u5f02\u7684\u54c0\u53f7\u3002\u516d\u53ea\u7531\u9ab8\u9aa8\u548c\u5c38\u4f53\u6784\u6210\u7684\u5de8\u5927\u89e6\u624b\u81ea\u814b\u4e0b\u4f38\u51fa\uff0c\u56db\u5904\u6325\u821e\uff0c\u6492\u51fa\u4e00\u7247\u7247\u7070\u6697\u7684\u79fd\u6076\u4e4b\u5149\uff0c\u6240\u6709\u88ab\u51fb\u4e2d\u8005\uff0c\u65e0\u8bba\u662f\u4ea1\u7075\uff0c\u662f\u9b54\u9b3c\uff0c\u662f\u5815\u5929\u4f7f\u6216\u8005\u5176\u4ed6\u5b58\u5728\uff0c\u8eab\u8eaf\u7eb7\u7eb7\u788e\u88c2\u5316\u4e3a\u5fae\u5c18\uff0c\u4e00\u56e2\u56e2\u9ed1\u8272\u5149\u7403\u4ece\u4e2d\u5347\u8d77\uff0c\u88ab\u878d\u5408\u541e\u566c\u5230\u795e\u8eaf\u9b54\u50cf\u7684\u8eab\u4f53\u4e2d\uff0c\u8fc5\u901f\u5730\u4fee\u8865\u597d\u4e86\u5b83\u65b0\u53d7\u7684\u521b\u4f24\u3002</p><p>\u9b54\u9b3c\u4eec\u5982\u6f6e\u6c34\u822c\u524d\u8d74\u540e\u7ee7\u5730\u53d1\u8d77\u8fdb\u653b\uff0c\u4f46\u8fd9\u79cd\u82f1\u52c7\u5e76\u6ca1\u6709\u5e26\u6765\u76f8\u5e94\u7684\u56de\u62a5\u3002\u9b54\u50cf\u8fd9\u6b21\u53d8\u5316\u51fa\u7684\u795e\u76f8\u4e43\u662f\u4e24\u4f4d\u795e\u7947\u7684\u878d\u5408\uff0c\u5de6\u4fa7\u7684\u5353\u5c14\u4ea1\u7075\u5973\u795e\u9f50\u96c5\u6e29\u7eb1\u4e3d\u867d\u7136\u88ab\u66e6\u5929\u4f7f\u5c04\u6bc1\u5934\u9885\uff0c\u65e0\u6cd5\u518d\u5ea6\u65bd\u6cd5\uff0c\u4f46\u53f3\u4fa7\u7684\u517d\u5934\u795e\u7947\u6d85\u4f3d\u5c14\u5374\u80fd\u591f\u9760\u7740\u541e\u566c\u4ea1\u7075\u548c\u5c38\u4f53\u4e0d\u65ad\u5730\u4fee\u590d\u81ea\u8eab\uff0c\u751a\u81f3\u589e\u5f3a\u529b\u91cf\u3002\u65e0\u8bba\u9b54\u9b3c\u7ed9\u9b54\u50cf\u9020\u6210\u4e86\u591a\u5c11\u635f\u4f24\uff0c\u5b83\u90fd\u80fd\u5feb\u901f\u5f25\u8865\u56de\u6765\u3002\u66e6\u5929\u4f7f\u52a0\u5165\u4e86\u6218\u56e2\uff0c\u5979\u7684\u51b0\u5251\u4e0a\u5723\u5149\u7f2d\u7ed5\uff0c\u6bcf\u4e00\u51fb\u90fd\u80fd\u591f\u7ed9\u5bf9\u624b\u9020\u6210\u6df1\u6df1\u521b\u4f24\uff0c\u7136\u800c\u9b54\u50cf\u5728\u4e0b\u4e00\u77ac\u95f4\u5c31\u80fd\u5b8c\u5168\u6062\u590d\u3002</p><p>\u6218\u5c40\u9677\u5165\u65e0\u9650\u7684\u62d6\u5ef6\u4e4b\u4e2d\uff0c\u800c\u5f62\u52bf\u5bf9\u9b54\u9b3c\u8d8a\u6765\u8d8a\u4e0d\u5229\u3002</p><p>\u9762\u5bf9\u8fd9\u79cd\u68d8\u624b\u7684\u654c\u4eba\uff0c\u62dc\u5c14\u540c\u6837\u4e5f\u5df2\u7ecf\u65e0\u8ba1\u53ef\u65bd\u3002\u81ea\u4ece\u9b54\u50cf\u5728\u9ed1\u77f3\u57ce\u4e2d\u5947\u602a\u9003\u8d70\uff0c\u4ed6\u5c31\u5df2\u7ecf\u9884\u60f3\u5230\u4f1a\u6709\u8fd9\u4e00\u65e5\uff0c\u4e5f\u5c3d\u6700\u5927\u7684\u52aa\u529b\u505a\u4e86\u51c6\u5907\u3002\u6574\u4e2a\u963f\u5f17\u7eb3\u65af\u6700\u7cbe\u9510\u7684\u58eb\u5175\u5df2\u7ecf\u805a\u96c6\u5728\u6b64\uff0c\u5730\u72f1\u5176\u4ed6\u516b\u5c42\u9886\u4e3b\u7684\u796d\u53f8\u88ab\u4ed6\u901a\u8fc7\u516b\u9b54\u5c06\u8c03\u6765\uff0c\u5305\u62ec\u66e6\u5929\u4f7f\u521a\u51fa\u5c04\u51fa\u7684\u90a3\u4e00\u7bad\uff0c\u4e5f\u662f\u4ed6\u82b1\u91cd\u91d1\u4ece\u585e\u5c14\u9080\u8bf7\u4e86\u4e5d\u4f4d\u9ad8\u9636\u7ea2\u888d\u5deb\u5e08\u524d\u6765\uff0c\u6784\u5efa\u201c\u73af\u4e4b\u6cd5\u9635\u201d\u8f85\u52a9\uff0c\u624d\u80fd\u591f\u6709\u5982\u6b64\u5a01\u52bf\u3002</p><p>\u73af\u4e4b\u6cd5\u9635\u662f\u585e\u5c14\u7ea2\u888d\u5deb\u5e08\u4f1a\u7684\u72ec\u95e8\u79d8\u6280\u4e4b\u4e00\uff0c\u548c\u9b54\u6cd5\u523a\u9752\u5e76\u5217\uff0c\u80fd\u591f\u79ef\u805a\u591a\u540d\u5deb\u5e08\u7684\u529b\u91cf\uff0c\u8ba9\u65bd\u6cd5\u76ee\u6807\u5728\u77ed\u65f6\u95f4\u5185\u83b7\u5f97\u6781\u5ea6\u5f3a\u5316\u3002\u73af\u4e4b\u6cd5\u9635\u53ef\u5927\u53ef\u5c0f\uff0c\u89c6\u4e3b\u6301\u548c\u53c2\u4e0e\u8005\u7684\u80fd\u529b\u800c\u5b9a\uff0c\u4e5d\u4eba\u6cd5\u9635\u5df2\u7ecf\u662f\u6700\u5927\u89c4\u6a21\uff0c\u8f7b\u6613\u51b3\u4e0d\u4f1a\u52a8\u7528\u3002\u62dc\u5c14\u6b64\u6b21\uff0c\u4e5f\u5df2\u7ecf\u7b97\u662f\u4e0b\u8db3\u4e86\u8840\u672c\u3002</p><p>\u7136\u800c\u2026\u2026</p><p>\u201c\u96be\u9053\u6700\u7ec8\u8fd8\u662f\u529f\u4e8f\u4e00\u7bd1\u4e48\u3002\u201d\u62dc\u5c14\u7ad9\u5728\u57ce\u5934\u4e0a\uff0c\u51dd\u89c6\u7740\u6218\u573a\uff0c\u4ed6\u7684\u5c06\u58eb\u4eec\u6b63\u5728\u594b\u52c7\u62fc\u6740\uff0c\u7528\u5c3d\u4e00\u5207\u624b\u6bb5\u653b\u51fb\u7740\u9b54\u50cf\uff0c\u7136\u800c\u8fd9\u79cd\u5f92\u52b3\u7684\u52aa\u529b\u770b\u8d77\u6765\u662f\u90a3\u6837\u8352\u8bde\u53ef\u7b11\uff0c\u6839\u672c\u5c31\u662f\u6beb\u65e0\u610f\u4e49\u7684\u6323\u624e\u3002\u552f\u4e00\u7684\u4f5c\u7528\uff0c\u4f3c\u4e4e\u4e0d\u8fc7\u662f\u5728\u62d6\u5ef6\u65f6\u95f4\u7f62\u4e86\u3002\u5815\u5929\u4f7f\u548c\u6df1\u72f1\u70bc\u9b54\u7684\u6570\u91cf\u4e00\u4e2a\u4e2a\u51cf\u5c11\uff0c\u66e6\u5929\u4f7f\u7684\u8eab\u5f71\u867d\u7136\u4f9d\u65e7\u7075\u6d3b\uff0c\u4f46\u901f\u5ea6\u4e5f\u660e\u663e\u8fdf\u7f13\u4e0b\u6765\u4e86\u3002\u8840\u8089\u4e4b\u8eaf\uff0c\u7ec8\u7a76\u4e0d\u80fd\u548c\u65e0\u751f\u547d\u7684\u9b54\u50cf\u76f8\u6bd4\u3002</p><p>\u4e00\u53ea\u624b\u9759\u9759\u642d\u4e0a\u62dc\u5c14\u7684\u80a9\u8180\uff0c\u9b54\u9b3c\u56de\u8fc7\u5934\uff0c\u53d1\u73b0\u662f\u743c\u6069\u3002</p><p>\u201c\u5f88\u62b1\u6b49\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u62dc\u5c14\u5f6c\u5f6c\u6709\u793c\u5730\u8bf4\uff0c\u201c\u6050\u6015\u6211\u4e0d\u80fd\u591a\u7559\u4f60\u4e86\u3002\u56de\u5230\u7269\u8d28\u754c\u7684\u4f20\u9001\u95e8\u5df2\u7ecf\u5efa\u6210\uff0c\u4f60\u77e5\u9053\u5728\u54ea\u91cc\uff0c\u8d81\u7740\u73b0\u5728\u8fd8\u6709\u65f6\u95f4\uff0c\u8d76\u5feb\u5e26\u4f60\u7684\u540c\u4f34\u79bb\u5f00\u5427\u3002\u201d</p><p>\u201c\u5514\uff0c\u4f46\u6211\u4e0d\u6025\u7740\u56de\u5bb6\uff0c\u201d\u743c\u6069\u5b89\u9759\u5730\u8bf4\uff0c\u201c\u800c\u4e14\u4e5f\u6ca1\u8fd9\u4e2a\u5fc5\u8981\u3002\u201d</p><p>\u62dc\u5c14\u626c\u8d77\u7709\u6bdb\uff0c\u4ed6\u542c\u51fa\u4e86\u5bf9\u65b9\u8bdd\u8bed\u91cc\u7684\u6697\u793a\u3002\u201c\u4f60\u6709\u529e\u6cd5\u5bf9\u4ed8\u5b83\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u6240\u4ee5\u6211\u4eec\u518d\u6765\u505a\u7b14\u4ea4\u6613\u5982\u4f55\uff0c\u5c06\u519b\u3002\u201d</p><p>\u201c\u8bf4\u8bf4\u770b\u3002\u201d</p><p>\u201c\u6211\u5e2e\u4f60\u6253\u53d1\u6389\u8fd9\u5bb6\u4f19\uff0c\u8ba9\u5b83\u4e0d\u4f1a\u518d\u6210\u4e3a\u4f60\u7684\u9ebb\u70e6\uff0c\u800c\u4e14\u4e0d\u9700\u8981\u4ed8\u51fa\u591a\u5927\u4ee3\u4ef7\uff0c\u7edd\u5bf9\u5728\u4f60\u53ef\u4ee5\u627f\u53d7\u7684\u8303\u56f4\u5185\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f5c\u4e3a\u62a5\u916c\uff0c\u6211\u5e0c\u671b\u4f60\u80fd\u5c06\u4e00\u4e9b\u6743\u5229\u8ba9\u6e21\u7ed9\u6211\u3002\u201d</p><p>\u62dc\u5c14\u6709\u4e9b\u8be7\u5f02\uff0c\u201c\u4ec0\u4e48\u6743\u5229\u3002\u201d</p><p>\u201c\u4f60\u5bf9\u5f25\u8d5b\u4e9a\u5c0f\u59d0\u7684\u6240\u6709\u6743\u5229\u3002\u201d</p><p>\u9b54\u9b3c\u7684\u77b3\u5b54\u9661\u7136\u6536\u7f29\uff0c\u5728\u534a\u79d2\u949f\u540e\u6062\u590d\u539f\u72b6\uff0c\u201c\u6211\u9700\u8981\u5148\u542c\u542c\u4f60\u7684\u529e\u6cd5\uff0c\u201d\u4ed6\u6de1\u6de1\u8bf4\uff0c\u201c\u5426\u5219\u6211\u5f88\u96be\u505a\u51fa\u5224\u65ad\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u4e00\u7b11\uff0c\u201c\u529e\u6cd5\u5f88\u7b80\u5355\uff0c\u201d\u4ed6\u6307\u7740\u8fdc\u5904\u7684\u9b54\u50cf\uff0c\u201c\u91ca\u653e\u4e00\u4e2a\u4f4d\u9762\u4f20\u9001\u6cd5\u672f\uff0c\u6253\u5f00\u4f4d\u9762\u901a\u9053\uff0c\u628a\u5b83\u9001\u5230\u53e6\u5916\u7684\u4e16\u754c\u91cc\u53bb\u3002\u6bd4\u5982\u5929\u5802\u5c71\uff0c\u6bd4\u5982\u660e\u6c34\u5883\uff0c\u6bd4\u5982\u706b\u5143\u7d20\u4f4d\u9762\uff0c\u81ea\u7136\u4f1a\u6709\u4eba\u5e2e\u6211\u4eec\u628a\u5b83\u5e72\u6389\u3002\u201d</p><p>\u62dc\u5c14\u95fb\u8a00\uff0c\u8138\u4e0a\u9732\u51fa\u7565\u663e\u5931\u671b\u7684\u795e\u60c5\u6765\uff0c\u201c\u8fd9\u529e\u6cd5\u6211\u4eec\u65e9\u5c31\u8bd5\u8fc7\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u3002\u9b54\u50cf\u4e0d\u662f\u767d\u75f4\uff0c\u5b83\u4e00\u5bdf\u89c9\u5230\u5371\u9669\u5c31\u4f1a\u7acb\u523b\u8fdc\u8fdc\u8eb2\u5f00\uff0c\u4e0d\u4f1a\u50bb\u50bb\u5730\u8d70\u8fdb\u53bb\u3002\u201d</p><p>\u201c\u8c01\u8bf4\u8981\u5b83\u81ea\u5df1\u8d70\u8fdb\u53bb\uff1f\u201d</p><p>\u62dc\u5c14\u76b1\u7709\uff0c\u201c\u6211\u4e0d\u660e\u767d\u4f60\u7684\u610f\u601d\u3002\u201d</p><p>\u201c\u5b83\u81ea\u5df1\u5f53\u7136\u4e0d\u4f1a\u8d70\u8fdb\u53bb\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u4f46\u53ef\u4ee5\u76f4\u63a5\u5728\u5b83\u811a\u4e0b\u653e\u4f4d\u9762\u4f20\u9001\u6cd5\u672f\uff0c\u8ba9\u5b83\u81ea\u5df1\u6389\u8fdb\u53bb\u554a\u3002\u201d</p><p>\u201c\u90a3\u4e5f\u4e0d\u53ef\u80fd\uff0c\u201d\u62dc\u5c14\u8bf4\uff0c\u201c\u8981\u4f20\u9001\u8fd9\u6837\u5e9e\u5927\u7684\u7269\u4f53\uff0c\u53ea\u6709\u5728\u4f4d\u9762\u901a\u9053\u5b8c\u5168\u6253\u5f00\u5230\u6700\u5927\u5316\u65f6\u624d\u6709\u53ef\u80fd\u3002\u800c\u4f4d\u9762\u901a\u9053\u4ece\u5f00\u59cb\u65bd\u6cd5\u5230\u5b8c\u5168\u6253\u5f00\uff0c\u81f3\u5c11\u9700\u8981\u4e09\u5341\u79d2\u949f\u3002\u9664\u975e\u5b83\u5728\u8fd9\u4e09\u5341\u79d2\u949f\u5185\u5b8c\u5168\u4e0d\u52a8\uff0c\u5426\u5219\u53ea\u8981\u968f\u4fbf\u8d70\u51fa\u4e24\u6b65\uff0c\u7acb\u523b\u5c31\u8131\u79bb\u4e86\u4f4d\u9762\u901a\u9053\u7684\u5f71\u54cd\u8303\u56f4\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u6211\u4e0d\u9700\u8981\u4e09\u5341\u79d2\u949f\uff0c\u6211\u53ea\u9700\u8981\u516d\u79d2\u949f\u3002\u201d</p><p>\u62dc\u5c14\u7591\u60d1\u5730\u770b\u7740\u5deb\u5e08\uff0c\u4ed6\u5e76\u975e\u5bf9\u9b54\u6cd5\u4e00\u65e0\u6240\u77e5\u4e4b\u8f88\uff0c\u624b\u4e0b\u4e5f\u6709\u4e24\u4e2a\u5deb\u5996\u3002\u65e2\u7136\u8003\u8651\u8fc7\u8fd9\u79cd\u6218\u672f\uff0c\u5bf9\u76f8\u5173\u7684\u6570\u636e\u81ea\u7136\u5f88\u6e05\u695a\uff0c\u4f46\u4ed6\u5e76\u6ca1\u6709\u548c\u743c\u6069\u4e89\u6267\u8fd9\u4e2a\u95ee\u9898\u3002\u201c\u90a3\u4e5f\u6ca1\u4ec0\u4e48\u533a\u522b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u95ee\u9898\u7684\u5173\u952e\u4e0d\u5728\u4e8e\u6b64\uff0c\u4e09\u5341\u79d2\u949f\u4e5f\u597d\uff0c\u516d\u79d2\u949f\u4e5f\u597d\uff0c\u90fd\u8db3\u591f\u9b54\u50cf\u9003\u51fa\u5f88\u8fdc\u4e86\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u743c\u6069\u5fae\u5fae\u70b9\u5934\uff0c\u201c\u95ee\u9898\u7684\u5173\u952e\u5c31\u5728\u4e8e\u8fd9\u91cc\uff1a\u6211\u4eec\u5f97\u5148\u628a\u9b54\u50cf\u5b9a\u4f4f\uff0c\u4ee4\u5b83\u4e0d\u80fd\u79fb\u52a8\u3002\u201d</p><p>\u201c\u5b9a\u8eab\u9b54\u6cd5\u5bf9\u5b83\u662f\u65e0\u6548\u7684\u3002\u201d\u62dc\u5c14\u63d0\u9192\u3002</p><p>\u201c\u6211\u77e5\u9053\u3002\u201d</p><p>\u201c\u5316\u77f3\u4e3a\u6ce5\u4e5f\u6ca1\u6709\u610f\u4e49\uff0c\u5b83\u4f1a\u98de\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u6211\u81ea\u6709\u529e\u6cd5\u3002\u201d</p><p>\u62dc\u5c14\u6c89\u541f\u7740\uff0c\u4e00\u65f6\u95f4\u6709\u4e9b\u72b9\u8c6b\u4e0d\u51b3\uff0c\u4f46\u5c40\u52bf\u5df2\u7ecf\u5bb9\u4e0d\u5f97\u4ed6\u591a\u505a\u8003\u8651\u4e86\u3002\u201c\u6210\u4ea4\u3002\u201d\u4ed6\u6700\u540e\u8bf4\u3002</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u201c\u597d\u6781\u4e86\uff0c\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u201c\u90a3\u4e48\uff0c\u8bf7\u4e0b\u4ee4\u5427\uff0c\u5c06\u519b\u3002\u6311\u9009\u4f60\u624b\u4e0b\u4f53\u578b\u6700\u5927\u7684\u9b54\u9b3c\uff0c\u7a7f\u4e0a\u6700\u6c89\u91cd\u7684\u7532\u80c4\uff0c\u8ba9\u5b83\u4eec\u96c6\u5408\u2014\u2014\u5982\u679c\u4e0d\u4f1a\u98de\u7684\u8bdd\uff0c\u8bb0\u5f97\u5e26\u74f6\u98de\u884c\u836f\u6c34\u3002\u201d</p></article>"}}}]);
