(window.webpackJsonp=window.webpackJsonp||[]).push([[1577],{1600:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u516d\u8282 \u743c\u6069\u7684\u53d1\u660e",content:"<article><h2>\u7b2c\u4e8c\u5341\u516d\u8282 \u743c\u6069\u7684\u53d1\u660e</h2><p>\u6bd4\u8bd5\u51b3\u6597\uff0c\u8f93\u8d62\u80dc\u8d1f\u662f\u5f88\u5bfb\u5e38\u7684\u4e8b\u60c5\uff0c\u4e0d\u8db3\u4e3a\u5947\uff0c\u6216\u8005\u8bf4\u65e9\u5728\u610f\u6599\u4e4b\u4e2d\u3002\u4f46\u628a\u5973\u5b69\u5b50\u6253\u54ed\u4e86\u2026\u2026\u8fd9\u9ebb\u70e6\u5c31\u6709\u70b9\u5927\u4e86\u3002</p><p>\u51db\u5750\u5728\u5730\u4e0a\uff0c\u53cc\u624b\u6342\u7740\u53f3\u817f\u811a\u8e1d\u90e8\u4f4d\uff0c\u54ed\u5f97\u7a00\u91cc\u54d7\u5566\uff0c\u4eff\u4f5b\u53d7\u4e86\u5929\u5927\u59d4\u5c48\u7684\u5b69\u5b50\uff0c\u743c\u6069\u60f3\u8981\u4e0a\u524d\u5bdf\u770b\u4f24\u52bf\uff0c\u88ab\u5979\u8d1f\u6c14\u4e00\u628a\u63a8\u5f00\u4e86\uff0c\u53ea\u597d\u5c34\u5c2c\u5730\u7ad9\u5728\u65c1\u8fb9\u3002\u6885\u83f2\u65af\u5306\u5fd9\u4ece\u770b\u53f0\u4e0a\u8dd1\u8fc7\u6765\uff0c\u4fef\u8eab\u66ff\u51db\u68c0\u67e5\u4e86\u4e00\u4e0b\uff0c\u201c\u8fd8\u597d\uff0c\u201d\u5979\u677e\u4e86\u53e3\u6c14\uff0c\u201c\u5173\u8282\u6ca1\u6709\u53d8\u5f62\uff0c\u4e5f\u6ca1\u6709\u80bf\uff0c\u5e94\u8be5\u6ca1\u6709\u9aa8\u6298\uff0c\u53ea\u662f\u9aa8\u88c2\u3002\u201d</p><p>\u201c\u53ef\u662f\u5f88\u75bc\u554a\uff01\u201d\u51db\u5e26\u7740\u54ed\u8154\u8bf4\u3002</p><p>\u201c\u6ca1\u4e8b\u6ca1\u4e8b\uff0c\u201d\u6885\u83f2\u65af\u5b89\u6170\uff0c\u201c\u9a6c\u4e0a\u5c31\u4e0d\u75bc\u4e86\u3002\u4f60\u4e5f\u771f\u662f\uff01\u201d\u5979\u8f6c\u8138\u77aa\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u5e72\u561b\u4e0b\u624b\u8fd9\u4e48\u91cd\uff01\u201d</p><p>\u743c\u6069\u6b32\u5f85\u8fa9\u89e3\uff0c\u770b\u89c1\u6885\u83f2\u65af\u4f7f\u4e86\u4e2a\u773c\u8272\uff0c\u968f\u5373\u77e5\u8da3\u5730\u4e56\u4e56\u95ed\u4e0a\u5634\uff0c\u4ece\u6b21\u5143\u888b\u91cc\u53d6\u51fa\u8428\u74e6\u68cb\uff0c\u91ca\u653e\u51fa\u201c\u5973\u796d\u53f8\u201d\u6765\uff0c\u66ff\u51db\u6cbb\u7597\u3002\u53ea\u662f\u8fd9\u8428\u74e6\u68cb\u9b54\u50cf\u662f\u501f\u52a9\u6076\u9b54\u7684\u529b\u91cf\u5236\u6210\uff0c\u201c\u5973\u796d\u53f8\u201d\u867d\u7136\u80fd\u591f\u65bd\u5c55\u6cbb\u7597\u795e\u672f\uff0c\u4f46\u7ec8\u7a76\u7b97\u4e0d\u4e0a\u771f\u6b63\u7684\u4e13\u4e1a\u533b\u7597\u8005\uff0c\u5c31\u50cf\u86db\u540e\u7f57\u4e1d\u7684\u7267\u5e08\u4e00\u6837\uff0c\u6cbb\u75c5\u6551\u4eba\u5176\u5b9e\u662f\u517c\u804c\uff0c\u6325\u821e\u86c7\u9996\u97ad\u6740\u4eba\u624d\u662f\u672c\u884c\u3002\u800c\u4e14\u5c31\u901a\u5e38\u60c5\u51b5\u6765\u8bf4\uff0c\u7267\u5e08\u7684\u6cbb\u7597\u795e\u672f\u4e5f\u591a\u662f\u6cbb\u7597\u76ae\u8089\u5916\u4f24\uff0c\u5bf9\u4e8e\u8fd9\u79cd\u9aa8\u9abc\u53d7\u521b\u6548\u679c\u5e76\u4e0d\u5927\u3002</p><p>\u201c\u6050\u6015\u5f97\u53bb\u627e\u8475\u9732\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5979\u5e94\u8be5\u6709\u75ca\u6108\u9aa8\u9abc\u7684\u6cd5\u672f\u3002\u201d</p><p>\u5c0f\u5fc3\u5730\u628a\u51db\u62ac\u56de\u4f4f\u5904\uff0c\u653e\u5728\u5e8a\u4e0a\uff0c\u8475\u9732\u4e5f\u5df2\u7ecf\u95fb\u8baf\u8d76\u6765\u3002\u7b80\u5355\u8be2\u95ee\u4e86\u60c5\u51b5\uff0c\u518d\u5bdf\u770b\u7247\u523b\u4f24\u52bf\uff0c\u8475\u9732\u5c06\u53f3\u624b\u8f7b\u8f7b\u6309\u5728\u51db\u53d7\u4f24\u7684\u811a\u8e1d\u4e0a\uff0c\u84dd\u767d\u8272\u7684\u94f6\u706b\u81ea\u638c\u5fc3\u6d8c\u51fa\uff0c\u6e17\u5165\u5c0f\u5973\u5deb\u7684\u4f53\u5185\u3002\u201c\u6ca1\u4ec0\u4e48\u5927\u95ee\u9898\uff0c\u201d\u8fc7\u4e86\u51e0\u5206\u949f\uff0c\u8475\u9732\u5c06\u624b\u79fb\u5f00\uff0c\u5bf9\u743c\u6069\u548c\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f46\u8fd9\u6bd5\u7adf\u662f\u4f24\u5230\u4e86\u9aa8\u9abc\uff0c\u5f97\u8981\u5367\u5e8a\u9759\u517b\u51e0\u5929\uff0c\u514d\u5f97\u7559\u4e0b\u4ec0\u4e48\u540e\u9057\u75c7\u3002\u201d</p><p>\u5176\u5b9e\u53d7\u4f24\u91cd\u4e0d\u91cd\uff0c\u51db\u53cd\u5012\u4e0d\u662f\u591a\u4e48\u5728\u610f\uff0c\u5979\u771f\u6b63\u7684\u95ee\u9898\u5728\u4e8e\u6015\u75bc\u3002\u867d\u7136\u7ecf\u8fc7\u6cbb\u7597\uff0c\u811a\u8e1d\u4e0a\u4f9d\u65e7\u4e00\u9635\u9635\u5730\u9690\u9690\u4f5c\u75db\uff0c\u8ba9\u5979\u6b62\u4e0d\u4f4f\u5730\u5012\u5438\u51b7\u6c14\uff0c\u773c\u6cea\u6c6a\u6c6a\u3002\u8475\u9732\u6ca1\u529e\u6cd5\uff0c\u53ea\u597d\u53c8\u91ca\u653e\u4e86\u4e00\u4e2a\u5b89\u5b9a\u5fc3\u795e\u7684\u6cd5\u672f\uff0c\u8ba9\u51db\u6682\u65f6\u7761\u4e86\u8fc7\u53bb\u3002</p><p>\u6885\u83f2\u65af\u66ff\u51db\u76d6\u597d\u6bdb\u6bef\uff0c\u4e09\u4eba\u9000\u51fa\u623f\u95f4\u3002\u8475\u9732\u8fd8\u6709\u4e8b\u60c5\u5728\u8eab\uff0c\u5148\u56de\u4e86\u795e\u6bbf\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u4e5f\u56de\u5230\u81ea\u5df1\u623f\u95f4\u4f11\u606f\u3002\u201c\u90a3\u4e2a\uff0c\u827e\u5f25\u8587\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5176\u5b9e\u6211\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u6446\u6446\u624b\uff0c\u793a\u610f\u4ed6\u4e0d\u7528\u89e3\u91ca\uff0c\u201c\u4f60\u6700\u540e\u7528\u4e86\u4ec0\u4e48\u6cd5\u672f\uff1f\u201d</p><p>\u201c\u4e00\u4e2a\u62a4\u76fe\u672f\uff0c\u4e00\u4e2a\u718a\u4e4b\u5fcd\u8010\uff0c\u8fd8\u6709\u4e00\u4e2a\u94a2\u7b4b\u94c1\u9aa8\u3002\u201d</p><p>\u62a4\u76fe\u672f\u7684\u4f5c\u7528\u662f\u589e\u5f3a\u9632\u5fa1\uff0c\u718a\u4e4b\u5fcd\u8010\u662f\u8ba9\u743c\u6069\u53d8\u5f97\u66f4\u52a0\u6297\u6253\uff0c\u81f3\u4e8e\u94a2\u7b4b\u94c1\u9aa8\uff0c\u90a3\u662f\u53d8\u5316\u5b66\u6d3e\u7684\u8457\u540d\u6cd5\u672f\u4e4b\u4e00\uff0c\u987e\u540d\u601d\u4e49\uff0c\u4f5c\u7528\u662f\u5f3a\u5316\u8eaf\u4f53\u3002\u4e0d\u540c\u9020\u8be3\u7684\u5deb\u5e08\u4f7f\u7528\u51fa\u6765\uff0c\u6548\u679c\u81ea\u7136\u4e5f\u4e0d\u540c\uff0c\u5018\u82e5\u662f\u5e03\u96f7\u7eb3\u65af\u3001\u6b23\u5e03\u8fd9\u79cd\u5927\u5deb\u5e08\u65bd\u5c55\uff0c\u8db3\u4ee5\u8ba9\u81ea\u5df1\u7684\u8840\u8089\u4e4b\u8eaf\u53d8\u5f97\u56fa\u82e5\u7cbe\u91d1\uff0c\u8fd8\u80fd\u9644\u5e26\u5404\u79cd\u5f3a\u5316\u6548\u679c\u548c\u6297\u9b54\u80fd\u529b\u3002\u743c\u6069\u6ca1\u8fd9\u7b49\u672c\u4e8b\uff0c\u4f46\u628a\u81ea\u5df1\u7684\u4e00\u53ea\u80f3\u818a\u6682\u65f6\u53d8\u5f97\u548c\u77f3\u5934\u4e00\u6837\u7ed3\u5b9e\u575a\u786c\uff0c\u8fd8\u662f\u53ef\u4ee5\u529e\u5230\u7684\u3002</p><p>\u65e0\u8bba\u662f\u54ea\u4e00\u79cd\u6cd5\u672f\uff0c\u6548\u679c\u90fd\u53ea\u662f\u7528\u4e8e\u9632\u5fa1\uff0c\u6885\u83f2\u65af\u5f53\u65f6\u89c2\u6218\uff0c\u4e5f\u770b\u5f97\u5206\u660e\uff0c\u743c\u6069\u5e76\u6ca1\u6709\u53cd\u51fb\uff0c\u53ea\u662f\u4e3e\u81c2\u683c\u6321\u4f4f\u51db\u7684\u4fa7\u8e22\u3002\u4e4b\u6240\u4ee5\u5f04\u6210\u8fd9\u526f\u5c40\u9762\uff0c\u5176\u5b9e\u5c31\u50cf\u662f\u51db\u8e22\u5728\u4e00\u5757\u77f3\u5934\u4e0a\uff0c\u53cd\u800c\u628a\u81ea\u5df1\u7ed9\u9707\u4f24\u4e86\u3002\u5018\u82e5\u662f\u522b\u7684\u90e8\u4f4d\u6216\u8bb8\u8fd8\u597d\uff0c\u504f\u504f\u649e\u51fb\u5904\u662f\u811a\u8e1d\uff0c\u90a3\u91cc\u539f\u672c\u5c31\u6700\u7cbe\u81f4\u8106\u5f31\uff0c\u51db\u7684\u9f99\u9cde\u6070\u597d\u53c8\u8986\u76d6\u4e0d\u5230\uff0c\u53ea\u80fd\u8bf4\u8fd0\u6c14\u4e0d\u4f73\u4e86\u3002</p><p>\u4e0d\u8fc7\u2026\u2026\u51db\u662f\u4e0d\u662f\u4e5f\u592a\u7528\u529b\u4e86\u70b9\u3002</p><p>\u201c\u6211\u6ca1\u600e\u4e48\u5f97\u7f6a\u5979\u5427\uff0c\u81f3\u4e8e\u4e0b\u624b\u8fd9\u4e48\u91cd\u4e48\uff0c\u201d\u743c\u6069\u62b1\u6028\uff0c\u201c\u8981\u662f\u6211\u6ca1\u6321\u4f4f\u7684\u8bdd\uff0c\u90a3\u5c31\u4e0d\u662f\u9aa8\u88c2\uff0c\u7edd\u5bf9\u662f\u9aa8\u6298\u2026\u2026\u4e0d\uff0c\u662f\u80f3\u818a\u76f4\u63a5\u65ad\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u6b32\u8a00\u53c8\u6b62\uff0c\u6700\u7ec8\u6ca1\u6709\u8bf4\u8bdd\uff0c\u743c\u6069\u4e5f\u672a\u6ce8\u610f\u3002</p><p>\u51db\u65e2\u7136\u53d7\u4f24\uff0c\u8eba\u5728\u5e8a\u4e0a\u9759\u517b\uff0c\u603b\u5f97\u6709\u4eba\u966a\u4f34\uff0c\u4e0d\u53ef\u80fd\u628a\u5979\u5b64\u96f6\u96f6\u7684\u4e00\u4eba\u4e22\u5728\u623f\u95f4\u91cc\uff0c\u90a3\u6837\u7684\u8bdd\u5c0f\u5973\u5deb\u4e00\u5b9a\u54ed\u5f97\u66f4\u52a0\u4f24\u5fc3\u3002\u8fd9\u70b9\u81ea\u7136\u662f\u610f\u6599\u4e4b\u4e2d\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u65e9\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u4f46\u6ca1\u60f3\u5230\u7684\u662f\uff0c\u51db\u6307\u5b9a\u7684\u966a\u62a4\u8005\u5c45\u7136\u662f\u4ed6\u800c\u4e0d\u662f\u6885\u83f2\u65af\u3002</p><p>\u201c\u5f53\u7136\u5566\uff0c\u201d\u51db\u7406\u76f4\u6c14\u58ee\u5730\u8bf4\uff0c\u201c\u56e0\u4e3a\u662f\u4f60\u628a\u6211\u5f04\u4f24\u4e86\u554a\uff0c\u6240\u4ee5\u4f60\u8981\u8d1f\u8d23\u966a\u6211\u3002\u201d</p><p>\u8fd9\u9053\u7406\u5012\u4e5f\u6ca1\u9519\uff0c\u8087\u4e8b\u8005\u662f\u6709\u4e49\u52a1\u966a\u62a4\u4f24\u5458\uff0c\u867d\u7136\u8fd9\u4ef6\u4e8b\u60c5\u5176\u5b9e\u4e0d\u80fd\u602a\u7f6a\u5230\u743c\u6069\u5934\u4e0a\uff0c\u5b8c\u5168\u662f\u51db\u81ea\u5df1\u7684\u8d23\u4efb\u3002\u4f46\u548c\u4e00\u4e2a\u53d7\u4f24\u8eba\u5728\u5e8a\u4e0a\u7684\u5c0f\u5973\u5b69\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u8ba1\u8f83\u7684\u5462\u3002</p><p>\u201c\u597d\u597d\uff0c\u6211\u966a\u4f60\u5c31\u662f\u3002\u201d</p><p>\u201c\u54fc\uff0c\u8fd9\u8fd8\u5dee\u4e0d\u591a\u3002\u201d</p><p>\u743c\u6069\u6ca1\u6709\u8fc7\u966a\u62a4\u75c5\u4eba\u7684\u7ecf\u9a8c\uff0c\u597d\u5728\u51db\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\u5927\u95ee\u9898\uff0c\u811a\u8e1d\u4f24\u5230\u7684\u9aa8\u9abc\u5df2\u7ecf\u88ab\u8475\u9732\u7684\u94f6\u706b\u6cbb\u7597\u4fee\u590d\uff0c\u4f11\u517b\u51e0\u65e5\u5c31\u53c8\u53ef\u4ee5\u7ee7\u7eed\u6d3b\u8e66\u4e71\u8df3\u4e86\u3002\u771f\u6b63\u7684\u95ee\u9898\u5728\u4e8e\u5979\u662f\u4e2a\u597d\u52a8\u7684\u6027\u5b50\uff0c\u50cf\u6885\u83f2\u65af\u90a3\u6837\u62ff\u672c\u4e66\u5c31\u80fd\u5b89\u5b89\u9759\u9759\u5750\u4e00\u5929\uff0c\u51db\u662f\u7edd\u5bf9\u529e\u4e0d\u5230\u7684\uff0c\u5982\u4eca\u5fc5\u987b\u8eba\u5728\u5e8a\u4e0a\uff0c\u81ea\u7136\u6c14\u95f7\u65e0\u6bd4\uff0c\u9700\u8981\u6709\u4eba\u966a\u7740\u804a\u5929\u8bf4\u8bdd\u2014\u2014\u800c\u8fd9\u5c31\u662f\u743c\u6069\u7684\u4e3b\u8981\u4efb\u52a1\u4e86\u3002</p><p>\u201c\u75bc\uff01\u201d\u5c0f\u5973\u5deb\u6485\u7740\u5634\u3002</p><p>\u5979\u7684\u5e8a\u5934\u653e\u7740\u51e0\u74f6\u836f\u6c34\uff0c\u662f\u743c\u6069\u6628\u665a\u8c03\u914d\u7684\uff0c\u539f\u672c\u5e94\u8be5\u80fd\u591f\u9547\u75db\u6b62\u75bc\uff0c\u65e0\u5948\u5979\u7684\u5de8\u9f99\u8840\u8109\u592a\u5f3a\uff0c\u628a\u836f\u6548\u90fd\u62b5\u6297\u6389\u4e86\uff0c\u57fa\u672c\u4e0d\u8d77\u4f5c\u7528\u3002\u7267\u5e08\u867d\u7136\u6709\u7c7b\u4f3c\u7684\u795e\u672f\uff0c\u4f46\u603b\u4e0d\u80fd\u52a8\u4e0d\u52a8\u8bf7\u8475\u9732\u8fc7\u6765\u65bd\u6cd5\uff0c\u4eba\u5bb6\u4e0d\u77e5\u9053\u6709\u591a\u5c11\u6b63\u4e8b\u8981\u5fd9\u5462\u3002\u504f\u504f\u743c\u6069\u53c8\u4e0d\u77e5\u9053\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u773c\u524d\u8fde\u4e2a\u53d1\u813e\u6c14\u7684\u5bf9\u8c61\u90fd\u6ca1\u6709\uff0c\u5fc3\u60c5\u8d8a\u53d1\u6076\u52a3\u3002\u623f\u95f4\u91cc\u7a7a\u8361\u8361\u7684\uff0c\u5468\u56f4\u5bc2\u9759\u65e0\u58f0\uff0c\u53ea\u89c9\u81ea\u5df1\u50cf\u662f\u88ab\u629b\u5f03\u4e86\u4f3c\u7684\uff0c\u5fc3\u91cc\u4e00\u9635\u51c4\u82e6\uff0c\u611f\u89c9\u811a\u8e1d\u4e0a\u75bc\u75db\u52a0\u5267\uff0c\u51e0\u4e4e\u53c8\u8981\u54ed\u51fa\u6765\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u743c\u6069\u63a8\u95e8\u8fdb\u6765\uff0c\u624b\u4e0a\u8fd8\u63d0\u7740\u4e00\u4e2a\u76ae\u888b\u3002</p><p>\u201c\u4f60\u5e72\u561b\u53bb\u4e86\uff1f\u201d</p><p>\u201c\u7ed9\u4f60\u5f04\u9547\u75db\u7684\u4e1c\u897f\u3002\u201d\u743c\u6069\u8bf4\uff0c\u5750\u5728\u5e8a\u8fb9\u3002</p><p>\u4ed6\u62ff\u6765\u7684\u76ae\u888b\u4e2d\u88c5\u6ee1\u4e86\u6c34\uff0c\u8ba9\u51db\u65bd\u6cd5\u628a\u5b83\u51bb\u6210\u51b0\u5757\u3002\u5c0f\u5973\u5deb\u4e0d\u89e3\u5176\u610f\uff0c\u4f46\u8fd8\u662f\u4f9d\u8a00\u800c\u884c\uff0c\u5979\u5ff5\u4e86\u53e5\u5492\u8bed\uff0c\u53f3\u624b\u98df\u6307\u4e0a\u5f39\u51fa\u4e00\u64ae\u706b\u82d7\u6765\uff0c\u8fce\u98ce\u6643\u4e86\u4e00\u6643\uff0c\u53c8\u5316\u4f5c\u707f\u70c2\u51b0\u6676\u3002\u51db\u6253\u5f00\u6c34\u888b\u7684\u53e3\uff0c\u5c06\u6307\u5c16\u6ca1\u5165\u6c34\u4e2d\uff0c\u987f\u65f6\u5bd2\u6c14\u84b8\u817e\uff0c\u4e1d\u4e1d\u4e4b\u58f0\u4e0d\u7edd\u5165\u8033\uff0c\u5927\u7ea6\u534a\u5206\u949f\u540e\uff0c\u4e00\u888b\u6c34\u5b8c\u5168\u51bb\u7ed3\u6210\u51b0\u3002</p><p>\u201c\u597d\u4e86\uff0c\u201d\u51db\u62bd\u56de\u624b\u6307\uff0c\u201c\u4f60\u5230\u5e95\u60f3\u5e72\u4ec0\u4e48\u554a\u3002\u201d</p><p>\u743c\u6069\u4e0d\u7b54\uff0c\u5c06\u6bdb\u6bef\u6380\u5f00\u4e00\u89d2\uff0c\u9732\u51fa\u51db\u7684\u957f\u817f\u3002\u4ed6\u8f7b\u8f7b\u63e1\u7740\u5c0f\u5973\u5deb\u7684\u53f3\u8db3\uff0c\u5c06\u51b0\u888b\u5c0f\u5fc3\u5730\u6577\u8d34\u5728\u53d7\u4f24\u7684\u811a\u8e1d\u5904\u3002\u201c\u611f\u89c9\u600e\u4e48\u6837\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u597d\u51b0\u2026\u2026\u4e0d\u8fc7\u662f\u4e0d\u600e\u4e48\u75bc\u4e86\u5462\u3002\u201d</p><p>\u201c\u90a3\u5c31\u597d\u3002\u201d</p><p>\u743c\u6069\u8fd9\u4e3b\u610f\u5e76\u975e\u81ea\u521b\uff0c\u800c\u662f\u60f3\u8d77\u4ee5\u524d\u770b\u8db3\u7403\u8d5b\uff0c\u7403\u5458\u817f\u4e0a\u53d7\u4f24\u540e\u90fd\u62ff\u51b0\u888b\u51b7\u6577\u9547\u75db\uff0c\u4e8e\u662f\u8d77\u610f\u8bd5\u8bd5\uff0c\u6ca1\u60f3\u5230\u6548\u679c\u8fd8\u771f\u4e0d\u9519\u3002\u51db\u662f\u5b69\u5b50\u813e\u6c14\uff0c\u75bc\u75db\u4e00\u53bb\uff0c\u5fc3\u60c5\u7acb\u523b\u5c31\u597d\u4e86\u8d77\u6765\uff0c\u4e0d\u518d\u50cf\u521a\u624d\u90a3\u6837\u6101\u7709\u82e6\u8138\u3002\u743c\u6069\u4f4e\u5934\u66ff\u5979\u6309\u7740\u51b0\u888b\uff0c\u987a\u4fbf\u89c2\u8d4f\u7f8e\u8272\uff0c\u51db\u8eab\u6750\u751a\u4f73\uff0c\u867d\u7136\u4e0d\u53ca\u6885\u83f2\u65af\u9ad8\u6311\uff0c\u4f46\u6bd4\u4f8b\u5300\u79f0\uff0c\u4e00\u53cc\u7389\u817f\u7b14\u633a\u4fee\u957f\uff0c\u808c\u80a4\u5149\u6d01\u5168\u65e0\u534a\u70b9\u7455\u75b5\u3002\u56e0\u4e3a\u662f\u5728\u5e8a\u4e0a\uff0c\u6ca1\u7a7f\u978b\u889c\uff0c\u811a\u4e5f\u5341\u5206\u79c0\u6c14\uff0c\u8dbe\u7532\u4e0a\u6676\u6676\u95ea\u95ea\u7684\uff0c\u6cdb\u7740\u591c\u5149\uff0c\u767d\u7389\u822c\u7684\u8db3\u9762\u4e0a\u9690\u7ea6\u6d6e\u73b0\u7740\u51e0\u9053\u9752\u8272\u7ecf\u7edc\uff0c\u7cbe\u81f4\u5f97\u4eff\u4f5b\u827a\u672f\u54c1\u3002\u7ea4\u5de7\u4e4b\u4e2d\u53c8\u5e26\u7740\u4e30\u8174\uff0c\u63e1\u5728\u624b\u4e2d\u5a07\u5ae9\u7ef5\u8f6f\uff0c\u5f53\u771f\u4fbf\u662f\u67d4\u82e5\u65e0\u9aa8\u3002\u743c\u6069\u867d\u7136\u4e0d\u662f\u604b\u8db3\u7656\uff0c\u5374\u4e5f\u7740\u5b9e\u89c9\u5f97\u8d4f\u5fc3\u60a6\u76ee\uff0c\u5fcd\u4e0d\u4f4f\u51fa\u8a00\u79f0\u8d5e\u3002</p><p>\u201c\u771f\u6f02\u4eae\uff0c\u51db\u3002\u201d</p><p>\u201c\u6f02\u4eae\u5427\uff0c\u201d\u51db\u5f88\u5f97\u610f\uff0c\u201c\u827e\u5f25\u8587\u7684\u811a\u5c31\u6ca1\u6709\u6211\u6f02\u4eae\uff0c\u592a\u5927\u4e86\u3002\u201d</p><p>\u201c\u5979\u6bd4\u4f60\u9ad8\u561b\uff0c\u624b\u811a\u5f53\u7136\u4e5f\u76f8\u5e94\u5927\u4e9b\u3002\u201d</p><p>\u51db\u4e0d\u9ad8\u5174\u5730\u6485\u8d77\u5c0f\u5634\uff0c\u201c\u4f60\u81ea\u7136\u662f\u504f\u8892\u5979\u5566\u3002\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u5c31\u4e8b\u8bba\u4e8b\u800c\u5df2\u3002\u201d</p><p>\u201c\u90a3\u4f60\u8bf4\uff0c\u6211\u548c\u827e\u5f25\u8587\u8c01\u6f02\u4eae\uff1f\u201d</p><p>\u201c\u2026\u2026\u600e\u4e48\u7a81\u7136\u95ee\u8fd9\u4e2a\uff1f\u201d</p><p>\u201c\u968f\u53e3\u95ee\u95ee\u800c\u5df2\uff0c\u4f46\u4f60\u4e0d\u80fd\u968f\u53e3\u56de\u7b54\u3002\u201d</p><p>\u201c\u4f60\u4eec\u90fd\u5f88\u6f02\u4eae\u3002\u201d</p><p>\u201c\u4e0d\u884c\uff0c\u6211\u95ee\u7684\u662f\u8c01\u66f4\u6f02\u4eae\uff01\u201d</p><p>\u743c\u6069\u89c9\u5f97\u81ea\u5df1\u7684\u5934\u5f00\u59cb\u9690\u9690\u4f5c\u75bc\u3002</p><p>\u5e73\u5fc3\u800c\u8bba\uff0c\u4e24\u4f4d\u5c11\u5973\u90fd\u662f\u7edd\u8272\uff0c\u76f8\u8c8c\u6c14\u8d28\u867d\u7136\u8fe5\u5f02\uff0c\u4f46\u65e0\u8bba\u653e\u5728\u4f55\u5904\uff0c\u65e0\u8bba\u8ba9\u8c01\u6765\u8bc4\u8bba\uff0c\u90fd\u80af\u5b9a\u662f\u516c\u8ba4\u7684\u7f8e\u4eba\u2014\u2014\u9664\u975e\u6309\u7167\u77ee\u4eba\u6216\u8005\u517d\u4eba\u7684\u5ba1\u7f8e\u6807\u51c6\uff0c\u90a3\u53c8\u53e6\u5f53\u522b\u8bba\u3002\u771f\u8981\u8ba9\u743c\u6069\u8bc4\u4ef7\u4e2a\u9ad8\u4f4e\u51fa\u6765\uff0c\u4ed6\u662f\u6ca1\u6cd5\u8bf4\u7684\u3002\u5f53\u7136\uff0c\u5c31\u4e2a\u4eba\u7684\u611f\u60c5\u800c\u8a00\uff0c\u4ed6\u503e\u5411\u4e8e\u6885\u83f2\u65af\uff0c\u5982\u679c\u662f\u5979\u6765\u95ee\u8fd9\u4e2a\u95ee\u9898\uff0c\u743c\u6069\u80af\u5b9a\u76f4\u63a5\u8bf4\u201c\u4f60\u6bd4\u51db\u66f4\u6f02\u4eae\u201d\u3002\u4f46\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u6885\u83f2\u65af\u5e94\u8be5\u4e5f\u6ca1\u8fd9\u79cd\u5174\u81f4\u3002</p><p>\u201c\u8fd9\u4e2a\u95ee\u9898\u6211\u6ca1\u6cd5\u56de\u7b54\u554a\u3002\u201d\u743c\u6069\u53f9\u6c14\u3002</p><p>\u201c\u4e00\u5b9a\u8981\u56de\u7b54\uff01\u201d</p><p>\u201c\u90a3\u597d\u5427\uff0c\u4f60\u6bd4\u5979\u6f02\u4eae\uff0c\u884c\u4e86\u5427\u3002\u201d</p><p>\u201c\u4e0d\u884c\uff0c\u8fd9\u8bdd\u4e00\u542c\u5c31\u5047\uff0c\u534a\u70b9\u8bda\u610f\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u201c\u2026\u2026\u90a3\u5979\u6bd4\u4f60\u6f02\u4eae\uff1f\u201d</p><p>\u51db\u6284\u8d77\u62b1\u6795\u671d\u4ed6\u7838\u8fc7\u6765\u3002</p><p>\u743c\u6069\u63a5\u8fc7\u62b1\u6795\uff0c\u8f7b\u8f7b\u9012\u8fd8\u7ed9\u5979\uff0c\u201c\u522b\u95f9\u4e86\uff0c\u51db\uff0c\u201d\u4ed6\u8bf4\uff0c\u770b\u770b\u65c1\u8fb9\u7684\u6c99\u6f0f\uff0c\u201c\u5feb\u4e2d\u5348\u4e86\uff0c\u4f60\u60f3\u5403\u4ec0\u4e48\uff0c\u6211\u53bb\u7ed9\u4f60\u5b89\u6392\u3002\u201d</p><p>\u201c\u597d\u5427\uff0c\u6211\u8981\u5403\u82f9\u679c\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u5e7d\u6697\u5730\u57df\u91cc\u8fde\u9633\u5149\u90fd\u6ca1\u6709\uff0c\u600e\u4e48\u957f\u82f9\u679c\uff1f\u201d</p><p>\u201c\u9999\u8549\u4e5f\u884c\u3002\u201d</p><p>\u201c\u4e5f\u6ca1\u6709\u9999\u8549\u3002\u201d</p><p>\u201c\u771f\u9ebb\u70e6\uff0c\u7b97\u4e86\uff0c\u90a3\u5c31\u6a58\u5b50\u5427\u3002\u201d</p><p>\u201c\u5927\u5c0f\u59d0\uff0c\u653e\u8fc7\u6211\u5427\uff0c\u201d\u743c\u6069\u82e6\u7b11\uff0c\u201c\u8fd9\u79cd\u5730\u65b9\uff0c\u6211\u4ece\u54ea\u91cc\u7ed9\u4f60\u5f04\u6a58\u5b50\u53bb\u3002\u201d</p><p>\u201c\u90a3\u6211\u4e0d\u7ba1\uff0c\u53cd\u6b63\u6211\u9700\u8981\u5403\u6c34\u679c\uff0c\u8865\u5145\u8425\u517b\uff0c\u201d\u5979\u6307\u4e86\u6307\u81ea\u5df1\u7684\u811a\u8e1d\uff0c\u201c\u6211\u73b0\u5728\u662f\u75c5\u4eba\u3002\u201d</p><p>\u201c\u6c34\u679c\u6709\u554a\uff0c\u84dd\u6d46\u679c\u3001\u4e4c\u6885\u8fd8\u662f\u8840\u7eb9\u679c\uff1f\u201d</p><p>\u201c\u4e0d\u8981\uff0c\u90a3\u4e9b\u6211\u90fd\u5403\u817b\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4f60\u60f3\u5403\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u51db\u660e\u663e\u8fd8\u5728\u751f\u6c14\uff0c\u201c\u53cd\u6b63\u4f60\u8981\u7167\u987e\u597d\u6211\uff0c\u8981\u8ba9\u6211\u9ad8\u5174\u2014\u2014\u8fd9\u53ef\u662f\u827e\u5f25\u8587\u8bf4\u7684\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u65e0\u5948\uff0c\u5fc3\u60f3\u5973\u5b69\u5b50\u771f\u662f\u96be\u7f20\u3002\u5176\u5b9e\u8bdd\u8bf4\u56de\u6765\uff0c\u5973\u5b69\u5b50\u6492\u6492\u5a07\uff0c\u800d\u70b9\u5c0f\u6027\u5b50\uff0c\u90a3\u662f\u5e38\u6001\uff0c\u751a\u81f3\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\u60c5\uff0c\u53ea\u8981\u77e5\u9053\u5206\u5bf8\uff0c\u4e0d\u5728\u6b63\u4e8b\u4e0a\u80e1\u6405\u86ee\u7f20\u4fbf\u662f\u4e86\u3002\u56de\u5fc6\u81ea\u5df1\u4ee5\u524d\u548c\u5973\u5b69\u5b50\u76f8\u5904\u7684\u7ecf\u9a8c\uff0c\u7a81\u7136\u7075\u5149\u4e00\u95ea\uff0c\u987f\u65f6\u6709\u4e86\u4e3b\u610f\u3002</p><p>\u201c\u597d\u5566\u597d\u5566\uff0c\u51db\uff0c\u522b\u6492\u5a07\u4e86\uff0c\u201d\u743c\u6069\u7ad9\u8d77\u8eab\uff0c\u201c\u6211\u8bf7\u4f60\u5403\u4e00\u6837\u597d\u4e1c\u897f\uff0c\u4fdd\u8bc1\u8ba9\u4f60\u6ee1\u610f\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d</p><p>\u201c\u7b49\u4f1a\u4f60\u5c31\u77e5\u9053\uff0c\u4e0d\u8fc7\u8fd8\u5f97\u4f60\u6765\u5236\u51b0\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5973\u5b69\u5b50\u559c\u6b22\u5403\u7684\u4e1c\u897f\u5f88\u591a\uff0c\u85af\u7247\u3001\u679c\u51bb\u3001\u5de7\u514b\u529b\u3001\u51b0\u6fc0\u51cc\u7b49\u7b49\u90fd\u5e7f\u53d7\u6b22\u8fce\uff0c\u800c\u5728\u8fd9\u4e2a\u4e16\u754c\u90fd\u6ca1\u6709\uff0c\u5b8c\u5168\u53ef\u4ee5\u62ff\u6765\u4f5c\u4e3a\u81ea\u5df1\u7684\u53d1\u660e\u521b\u9020\u3002\u85af\u7247\u679c\u51bb\u743c\u6069\u662f\u4e0d\u4f1a\u505a\uff0c\u5de7\u514b\u529b\u53ea\u77e5\u9053\u662f\u4ece\u53ef\u53ef\u8c46\u53d8\u6765\u7684\uff0c\u4e2d\u95f4\u7684\u8fc7\u7a0b\u4e00\u65e0\u6240\u77e5\uff0c\u4f46\u51b0\u6fc0\u51cc\u5374\u6070\u597d\u662f\u4f1a\u7684\u3002</p><p>\u505a\u51b0\u6fc0\u51cc\u5e76\u4e0d\u96be\uff0c\u6700\u7b80\u5355\u7684\u53ea\u8981\u6709\u7cd6\u6709\u5976\u6cb9\u6709\u725b\u5976\u5c31\u884c\uff0c\u518d\u52a0\u70b9\u679c\u6c41\u8c03\u8272\uff0c\u5207\u4e24\u5757\u4e4c\u6885\u548c\u8840\u7eb9\u679c\u642d\u914d\uff0c\u51b7\u51bb\u5de5\u4f5c\u5219\u7531\u51db\u6765\u627f\u62c5\uff0c\u5979\u53ef\u6bd4\u4ec0\u4e48\u51b0\u7bb1\u90fd\u5f3a\u591a\u4e86\u3002\u4e0d\u8fc7\u534a\u5c0f\u65f6\uff0c\u51db\u5df2\u7ecf\u4e00\u624b\u6258\u7740\u76d8\u5b50\uff0c\u4e00\u624b\u62ff\u7740\u52fa\u5b50\uff0c\u5fc3\u6ee1\u610f\u8db3\u5730\u4eab\u7528\u743c\u6069\u7684\u201c\u65b0\u53d1\u660e\u201d\u3002</p><p>\u201c\u771f\u597d\u5403\uff0c\u201d\u5979\u7b11\u9010\u989c\u5f00\uff0c\u96be\u5f97\u5730\u5bf9\u743c\u6069\u5927\u52a0\u79f0\u8d5e\uff0c\u521a\u624d\u7684\u90a3\u70b9\u4e0d\u5feb\u65e9\u5c31\u4e22\u5230\u4e5d\u9704\u4e91\u5916\u53bb\u4e86\uff0c\u201c\u4ece\u6765\u4e0d\u77e5\u9053\u4f60\u539f\u6765\u8fd8\u6709\u8fd9\u4e00\u624b\u5462\uff0c\u743c\u6069\uff0c\u6211\u73b0\u5728\u5bf9\u4f60\u7684\u8bc4\u4ef7\u63d0\u9ad8\u4e00\u4e2a\u6863\u6b21\u4e86\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u90a3\u771f\u662f\u592a\u611f\u8c22\u4e86\u3002\u201d</p><p>\u743c\u6069\u968f\u53e3\u6577\u884d\uff0c\u5fc3\u60f3\u522b\u4eba\u90fd\u662f\u4ec0\u4e48\u51b6\u91d1\u7855\u58eb\u5316\u5de5\u535a\u58eb\uff0c\u7a7f\u8d8a\u4e86\u76f4\u63a5\u6500\u79d1\u6280\u5f00\u91d1\u624b\u6307\uff0c\u70bc\u94a2\u94c1\u9020\u67aa\u70ae\u6a2a\u626b\u5343\u519b\uff0c\u81ea\u5df1\u5374\u8fde\u706b\u836f\u7684\u6bd4\u4f8b\u90fd\u8bb0\u4e0d\u4f4f\uff0c\u552f\u4e00\u80fd\u505a\u7684\uff0c\u4e5f\u5c31\u662f\u5f04\u70b9\u51b0\u6fc0\u51cc\u54c4\u5973\u5b69\u5b50\u5f00\u5fc3\u4e86\u3002</p><p>\u201c\u8fd9\u4e1c\u897f\u53eb\u4ec0\u4e48\uff1f\u201d\u51db\u95ee\u3002</p><p>\u8fd9\u4e16\u754c\u4e0a\u6b64\u524d\u6839\u672c\u5c31\u6ca1\u6709\u51b0\u6fc0\u51cc\uff0c\u81ea\u7136\u4e5f\u4e0d\u4f1a\u6709\u76f8\u5e94\u7684\u540d\u79f0\uff0c\u743c\u6069\u4e5f\u61d2\u5f97\u81ea\u9020\u8bcd\u6c47\uff0c\u201c\u51b0\u818f\u3002\u201d\u4ed6\u8bf4\uff0c\u8fd9\u5176\u5b9e\u5c31\u662f\u51b0\u6fc0\u51cc\u7684\u76f4\u8bd1\u4e86\u3002</p><p>\u201c\u8fd9\u540d\u5b57\u4e0d\u597d\u542c\uff0c\u201d\u51db\u6487\u6487\u5634\uff0c\u5c06\u76d8\u5b50\u9012\u7ed9\u743c\u6069\uff0c\u201c\u518d\u6765\u4e00\u4efd\u3002\u201d</p><p>\u201c\u8fd9\u4e1c\u897f\u5f88\u51b0\uff0c\u4e0d\u80fd\u591a\u5403\u3002\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u201d\u51db\u6f2b\u4e0d\u5728\u4e4e\uff0c\u201c\u6211\u4f53\u8d28\u597d\uff0c\u522b\u8bf4\u7740\u51c9\uff0c\u4ece\u5c0f\u5230\u5927\u90fd\u6ca1\u611f\u5192\u8fc7\u3002\u201d</p><p>\u201c\u751c\u98df\u5403\u591a\u4e86\u4f1a\u53d8\u80d6\u7684\u3002\u201d</p><p>\u201c\u4e0d\u6015\uff0c\u6211\u5929\u751f\u4e3d\u8d28\u3002\u201d</p><p>\u743c\u6069\u6ca1\u529e\u6cd5\uff0c\u53c8\u7ed9\u5979\u76db\u4e86\u4e00\u4efd\uff0c\u201c\u5c31\u8fd9\u4e48\u591a\u4e86\uff0c\u201d\u4ed6\u8b66\u544a\uff0c\u201c\u518d\u5403\u4f1a\u809a\u5b50\u75bc\u7684\u3002\u201d</p><p>\u201c\u5514\u5514\u3002\u201d</p><p>\u51db\u7ee7\u7eed\u4e13\u5fc3\u5bf9\u4ed8\u7f8e\u98df\uff0c\u54ea\u91cc\u542c\u5f97\u8fdb\u53bb\u4ed6\u8bf4\u4ec0\u4e48\u3002\u743c\u6069\u5750\u5728\u5e8a\u8fb9\uff0c\u89c1\u5979\u5634\u89d2\u8fb9\u6b8b\u7559\u4e86\u4e00\u70b9\uff0c\u62ff\u4e1d\u5dfe\u8f7b\u8f7b\u66ff\u5979\u64e6\u53bb\u3002\u51db\u6014\u4e86\u6014\uff0c\u505c\u4e0b\u52fa\u5b50\uff0c\u62ac\u8d77\u8138\u5b9a\u5b9a\u51dd\u89c6\u7740\u4ed6\uff0c\u201c\u8c22\u8c22\u4f60\uff0c\u743c\u6069\uff0c\u201d\u5979\u8bf4\uff0c\u8bed\u6c14\u91cc\u4f3c\u4e4e\u591a\u4e86\u51e0\u5206\u6000\u5ff5\u7684\u5473\u9053\uff0c\u201c\u4f60\u771f\u597d\u3002\u201d</p><p>\u743c\u6069\u54d1\u7136\u5931\u7b11\uff0c\u5fc3\u60f3\u534a\u5c0f\u65f6\u524d\u8fd8\u8ddf\u6211\u53d1\u813e\u6c14\uff0c\u4e00\u76d8\u51b0\u6fc0\u51cc\u5582\u4e0b\u53bb\uff0c\u6001\u5ea6\u7acb\u523b\u8f6c\u4e86\u4e00\u767e\u516b\u5341\u5ea6\uff0c\u5973\u5b69\u5b50\u8fd8\u771f\u662f\u597d\u54c4\uff0c\u8981\u662f\u6885\u83f2\u65af\u4e5f\u8fd9\u6837\uff0c\u90a3\u751f\u65e5\u793c\u7269\u5012\u662f\u4e0d\u7528\u4f24\u8111\u7b4b\u4e86\u3002</p><p>\u201c\u5c0f\u65f6\u5019\uff0c\u6211\u5403\u4e1c\u897f\uff0c\u4e5f\u7ecf\u5e38\u5f04\u5230\u8138\u4e0a\uff0c\u6bcf\u6b21\u6211\u7236\u4eb2\u90fd\u662f\u8fd9\u6837\u5e2e\u6211\u64e6\u6389\u5462\u3002\u201d</p><p>\u51db\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u773c\u795e\u8ff7\u79bb\uff0c\u663e\u7136\u662f\u60f3\u8d77\u4e86\u5f80\u4e8b\u3002\u5ffd\u7136\u53c8\u751c\u751c\u7b11\u4e86\u4e00\u7b11\uff0c\u5c06\u51b0\u6fc0\u51cc\u653e\u5728\u5e8a\u8fb9\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u743c\u6069\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u521a\u624d\u662f\u6211\u4e0d\u597d\uff0c\u4e0d\u8be5\u5bf9\u4f60\u53d1\u813e\u6c14\u3002\u201d</p><p>\u743c\u6069\u7b11\u8d77\u6765\uff0c\u4f38\u624b\u634f\u4e86\u634f\u51db\u7684\u8138\u86cb\uff0c\u201c\u6ca1\u4e8b\u7684\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u53d7\u4f24\u4e86\u561b\uff0c\u4eba\u81ea\u7136\u5fc3\u60c5\u4e0d\u597d\u3002\u201d</p><p>\u201c\u6211\u81ea\u627e\u7684\u5566\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u90a3\u4e00\u811a\u8e22\u5f97\u592a\u91cd\uff0c\u6240\u4ee5\u624d\u5f04\u6210\u8fd9\u6837\uff0c\u672c\u6765\u5c31\u602a\u4e0d\u5230\u4f60\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u76b1\u7709\uff0c\u201c\u4e00\u573a\u6bd4\u8bd5\u800c\u5df2\uff0c\u5e72\u561b\u90a3\u4e48\u8ba4\u771f\u5462\uff0c\u201d\u4ed6\u534a\u5f00\u73a9\u7b11\u8bf4\uff0c\u201c\u4e0b\u624b\u90a3\u4e48\u91cd\uff0c\u771f\u8981\u628a\u6211\u6253\u4f24\u4e86\uff0c\u4e0d\u6015\u827e\u5f25\u8587\u627e\u4f60\u7b97\u8d26\u554a\u3002\u201d</p><p>\u51db\u563b\u563b\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u624d\u4e0d\u4f1a\u5462\uff0c\u5c31\u662f\u4e3a\u4e86\u66ff\u827e\u5f25\u8587\u62a5\u4ec7\uff0c\u6240\u4ee5\u6211\u624d\u8e22\u5f97\u90a3\u4e48\u91cd\u554a\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d\u743c\u6069\u4e0d\u89e3\u5176\u610f\uff0c\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u8c01\u8ba9\u4f60\u90a3\u4e48\u8fc7\u5206\uff0c\u6b3a\u8d1f\u827e\u5f25\u8587\uff0c\u628a\u5979\u90fd\u5f04\u4f24\u4e86\u3002\u201d</p><p>\u201c\u54ea\u6709\u8fd9\u56de\u4e8b\u2026\u2026\u5443\uff0c\u90a3\u4e2a\u554a\u2026\u2026\u201d</p><p>\u51db\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u6ca1\u51a4\u6789\u4f60\u5427\u3002\u201d</p><p>\u201c\u6ca1\u6709\u2026\u2026\u201d</p><p>\u743c\u6069\u6709\u4e9b\u5c34\u5c2c\uff0c\u51db\u6ca1\u8bf4\u9519\uff0c\u4ed6\u786e\u5b9e\u662f\u628a\u6885\u83f2\u65af\u5f04\u4f24\u4e86\uff0c\u8fd9\u662f\u4ed6\u7684\u4e0d\u5bf9\u3002\u53ea\u662f\u8fd9\u79cd\u95fa\u623f\u79c1\u4e8b\uff0c\u4f3c\u4e4e\u4e5f\u7528\u4e0d\u7740\u5916\u4eba\u6765\u7ba1\u2026\u2026\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u51db\u4e5f\u7b97\u4e0d\u5f97\u5916\u4eba\u3002</p><p>\u4ed6\u4e00\u65f6\u65e0\u8a00\u4ee5\u5bf9\uff0c\u51db\u4e5f\u6ca1\u518d\u8bf4\u8bdd\uff0c\u7ee7\u7eed\u5bf9\u4ed8\u5979\u7684\u51b0\u6fc0\u51cc\u3002\u623f\u95f4\u91cc\u9759\u6084\u6084\u7684\uff0c\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u51db\u653e\u4e0b\u52fa\u5b50\uff0c\u201c\u5582\uff0c\u743c\u6069\u3002\u201d</p><p>\u201c\u55ef\uff0c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u51db\u671f\u671f\u827e\u827e\uff0c\u4fcf\u8138\u4e0a\u7a81\u7136\u6cdb\u8d77\u7ea2\u6655\u6765\uff0c\u770b\u4e86\u4ed6\u4e00\u773c\u53c8\u628a\u5934\u4f4e\u4e0b\u53bb\uff0c\u88c5\u4f5c\u82e5\u65e0\u5176\u4e8b\u5730\u8214\u7740\u52fa\u5b50\uff0c\u201c\u6709\u4ef6\u4e8b\u60c5\uff0c\u6211\u6709\u70b9\u597d\u5947\uff0c\u60f3\u95ee\u95ee\u4f60\u3002\u201d</p><p>\u201c\u8bf4\u5427\uff0c\u201d\u743c\u6069\u5947\u602a\uff0c\u4ed6\u8fd8\u4ece\u6ca1\u89c1\u8fc7\u51db\u8fd9\u79cd\u5a07\u7f9e\u7684\u6837\u5b50\uff0c\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u90a3\u4e2a\u2026\u2026\u50cf\u4f60\u548c\u827e\u5f25\u8587\u90a3\u6837\uff0c\u55ef\uff0c\u771f\u7684\u4f1a\u6709\u5f88\u8212\u670d\u7684\u611f\u89c9\u5417\uff1f\u201d</p></article>"}}}]);
