(window.webpackJsonp=window.webpackJsonp||[]).push([[1582],{1605:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e00\u8282 \u795e\u529b\u5c31\u662f\u4e00\u679a\u91d1\u5e01",content:"<article><h2>\u7b2c\u4e09\u5341\u4e00\u8282 \u795e\u529b\u5c31\u662f\u4e00\u679a\u91d1\u5e01</h2><p>\u52a8\u8361\u5e74\u4ee3\uff0c\u8bf8\u795e\u96c6\u4f53\u4ee5\u5723\u8005\u5f62\u6001\u964d\u4e34\u51e1\u95f4\uff0c\u5176\u4e2d\u6740\u622e\u4e4b\u795e\u5df4\u5c14\u9884\u89c1\u5230\u81ea\u5df1\u7684\u6b7b\u4ea1\uff0c\u7559\u4e0b\u6570\u4ee5\u5343\u8ba1\u7684\u5b50\u55e3\uff0c\u5c06\u795e\u529b\u5206\u6563\u5176\u4e2d\uff0c\u8ba9\u4ed6\u4eec\u5f7c\u6b64\u6b8b\u6740\uff0c\u8bd5\u56fe\u901a\u8fc7\u8fd9\u79cd\u65b9\u5f0f\u518d\u5ea6\u590d\u6d3b\u3002\u5982\u4eca\u5341\u516d\u5e74\u8fc7\u53bb\uff0c\u5df4\u5c14\u5b50\u55e3\u57fa\u672c\u5df2\u7ecf\u6b7b\u4ea1\u6b86\u5c3d\uff0c\u7edd\u5927\u90e8\u5206\u6740\u622e\u795e\u529b\u90fd\u96c6\u4e2d\u5728\u6885\u83f2\u65af\u4f53\u5185\uff0c\u6309\u7167\u6b63\u5e38\u9884\u671f\uff0c\u90aa\u795e\u539f\u672c\u5df2\u7ecf\u590d\u6d3b\u3002</p><p>\u7136\u800c\u6885\u83f2\u65af\u653e\u5f03\u6210\u4e3a\u795e\u660e\u7684\u673a\u4f1a\uff0c\u5e76\u4e14\u6210\u4e3a\u6b63\u4e49\u4e4b\u795e\u7684\u5723\u6b66\u58eb\uff0c\u63d0\u5c14\u8d50\u4e88\u5723\u5251\uff0c\u964d\u4e0b\u795e\u529b\uff0c\u540e\u6765\u5728\u5bf9\u4ed8\u5854\u6d1b\u5a1c\u6559\u5f92\u7684\u8fc7\u7a0b\u4e2d\uff0c\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u7684\u795e\u5668\u9ece\u660e\u4e4b\u77f3\u53c8\u81ea\u52a8\u878d\u5165\u6885\u83f2\u65af\u4f53\u5185\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u7b49\u4e8e\u662f\u4e24\u5927\u9ad8\u7b49\u795e\u8054\u624b\uff0c\u7a33\u7a33\u9547\u538b\u4f4f\u4e86\u6740\u622e\u795e\u529b\u3002</p><p>\u867d\u7136\u5982\u6b64\uff0c\u8fd9\u7ec8\u7a76\u4e0d\u662f\u771f\u6b63\u89e3\u51b3\u4e86\u95ee\u9898\uff0c\u53ea\u80fd\u7b97\u6682\u65f6\u7a33\u5b9a\u4f4f\u5c40\u52bf\uff0c\u9690\u60a3\u4f9d\u65e7\u5b58\u5728\uff0c\u534a\u70b9\u4e0d\u66fe\u524a\u5f31\uff0c\u8bf4\u4e0d\u5b9a\u5728\u5c06\u6765\u7684\u67d0\u4e00\u5929\u5c31\u4f1a\u7a81\u7136\u53c8\u7206\u53d1\u51fa\u6765\u3002\u6700\u597d\u7684\u529e\u6cd5\uff0c\u81ea\u7136\u662f\u65a9\u8349\u9664\u6839\uff0c\u6c38\u7edd\u540e\u60a3\u2014\u2014\u4f46\u95ee\u9898\u5c31\u5728\u4e8e\u529e\u4e0d\u5230\u3002\u5df4\u5c14\u6bd5\u7adf\u4e5f\u66fe\u7ecf\u662f\u56fd\u5ea6\u5185\u8d6b\u8d6b\u6709\u540d\u7684\u795e\u7947\uff0c\u540d\u5217\u201c\u6b7b\u4ea1\u4e09\u795e\u201d\u4e4b\u4e00\uff0c\u4e3a\u4e86\u590d\u6d3b\u57cb\u4e0b\u7684\u540e\u624b\uff0c\u4e0d\u662f\u90a3\u4e48\u7b80\u5355\u5c31\u80fd\u7834\u89e3\u7684\u3002</p><p>\u800c\u4e14\u4ed6\u6709\u4e2a\u597d\u796d\u53f8\u3002</p><p>\u6885\u83f2\u65af\u7684\u6bcd\u4eb2\u5bf9\u5df4\u5c14\u53ef\u8c13\u662f\u5fe0\u5fc3\u803f\u803f\uff0c\u4e3a\u4e86\u4ed6\u7684\u590d\u6d3b\u7aed\u5c3d\u5168\u529b\uff0c\u4e0d\u60dc\u4ed8\u51fa\u4e00\u5207\u4ee3\u4ef7\u3002\u5728\u5f97\u77e5\u795e\u7947\u7684\u8ba1\u5212\u4e4b\u540e\uff0c\u5979\u9996\u5148\u662f\u4ee5\u81ea\u5df1\u4e3a\u6bcd\u4f53\uff0c\u4e0e\u5df4\u5c14\u4ea4\u5408\uff0c\u751f\u4e0b\u795e\u5b50\uff1b\u5728\u6885\u83f2\u65af\u51fa\u751f\u65f6\uff0c\u5979\u53c8\u5b89\u6392\u4e86\u9ed1\u6697\u8840\u8165\u7684\u796d\u7940\u4eea\u5f0f\uff0c\u4ee5\u4e03\u4e2a\u5df4\u5c14\u5b50\u55e3\u4e3a\u796d\u54c1\uff0c\u8ba9\u6740\u622e\u795e\u529b\u548c\u5973\u513f\u7684\u7075\u9b42\u878d\u4e3a\u4e00\u4f53\uff0c\u65e0\u6cd5\u5206\u79bb\u3002\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u8bf4\uff0c\u5979\u7b49\u4e8e\u662f\u628a\u5df4\u5c14\u548c\u6885\u83f2\u65af\u7ed1\u5b9a\u5230\u4e86\u4e00\u8d77\uff0c\u5373\u4fbf\u662f\u63d0\u5c14\u548c\u5170\u68ee\u5fb7\u5c14\u8fd9\u79cd\u9ad8\u7b49\u795e\uff0c\u4e5f\u6ca1\u529e\u6cd5\u5728\u4e0d\u5f7b\u5e95\u6740\u6b7b\u6885\u83f2\u65af\u7684\u524d\u63d0\u4e0b\u62b9\u6d88\u6389\u5979\u4f53\u5185\u7684\u6740\u622e\u795e\u529b\u3002</p><p>\u6240\u4ee5\u8bf4\uff0c\u8981\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\uff0c\u9700\u8981\u7684\u5df2\u7ecf\u4e0d\u662f\u9b54\u6cd5\uff0c\u800c\u662f\u5947\u8ff9\u3002</p><p>\u743c\u6069\u6070\u597d\u5c31\u4f1a\u8fd9\u79cd\u5947\u8ff9\u3002</p><p>\u201c\u5df4\u5c14\u5df2\u7ecf\u6b7b\u4ea1\uff0c\u4ed6\u7684\u6740\u622e\u795e\u4f4d\u88ab\u5e0c\u745e\u514b\u6240\u7ee7\u627f\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ece\u4e25\u683c\u610f\u4e49\u4e0a\u800c\u8a00\uff0c\u4ed6\u5df2\u7ecf\u5e76\u975e\u795e\u7947\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u5bf9\u3002\u201d</p><p>\u5c3d\u7ba1\u4e0d\u660e\u767d\u743c\u6069\u4e3a\u4ec0\u4e48\u7a81\u7136\u628a\u8bdd\u9898\u8f6c\u79fb\u5230\u8fd9\u65b9\u9762\uff0c\u4f46\u6885\u83f2\u65af\u8fd8\u662f\u70b9\u4e86\u70b9\u5934\u3002\u5df4\u5c14\u662f\u6740\u622e\u4e4b\u795e\uff0c\u4f46\u66f4\u51c6\u786e\u5730\u8bf4\u6cd5\uff0c\u662f\u201c\u66fe\u7ecf\u7684\u201d\u6740\u622e\u4e4b\u795e\u3002\u8fd9\u5c31\u76f8\u5f53\u4e8e\u67d0\u4e2a\u5b98\u5458\u5f53\u6743\u591a\u5e74\uff0c\u540e\u6765\u653f\u5c40\u52a8\u8361\uff0c\u4ed6\u88ab\u8d76\u4e0b\u53f0\u4e86\uff0c\u5b98\u4f4d\u7531\u65b0\u4eba\u53d6\u800c\u4ee3\u4e4b\uff0c\u5f80\u65e5\u7684\u6743\u67c4\u81ea\u7136\u4e5f\u968f\u4e4b\u6613\u624b\u3002\u5728\u4e0b\u53f0\u4e4b\u524d\uff0c\u4ed6\u6697\u4e2d\u79ef\u6512\u57f9\u690d\u4e86\u5927\u91cf\u7684\u79c1\u4eba\u52bf\u529b\uff0c\u51c6\u5907\u7740\u6709\u671d\u4e00\u65e5\u4e1c\u5c71\u518d\u8d77\uff0c\u5377\u571f\u91cd\u6765\u2014\u2014\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u5728\u6ca1\u6709\u6210\u529f\u4e4b\u524d\uff0c\u4ed6\u5df2\u7ecf\u53ea\u662f\u5e73\u6c11\u767e\u59d3\u4e00\u4e2a\u4e86\u3002</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u5e72\u6389\u5df4\u5c14\uff0c\u6bd4\u6b63\u5e38\u60c5\u51b5\u4e0b\u6467\u6bc1\u4e00\u4e2a\u795e\u7947\u8981\u5bb9\u6613\u7684\u591a\u3002\u5df4\u5c14\u5982\u4eca\u53ea\u662f\u5bc4\u5c45\u5728\u6885\u83f2\u65af\u4f53\u5185\uff0c\u4ed6\u6ca1\u6709\u72ec\u7acb\u7684\u5b9e\u4f53\uff0c\u529b\u91cf\u88ab\u538b\u5236\uff0c\u751a\u81f3\u8fd8\u6ca1\u6709\u771f\u6b63\u7684\u81ea\u6211\u610f\u8bc6\uff08\u590d\u6d3b\u4e86\u624d\u4f1a\u5177\u6709\uff09\uff0c\u66f4\u591a\u662f\u4e00\u79cd\u201c\u60f3\u8981\u590d\u6d3b\u201d\u7684\u672c\u80fd\u3002\u4ed6\u7684\u6559\u4f1a\u4e5f\u5df2\u7ecf\u70df\u6d88\u4e91\u6563\uff0c\u867d\u7136\u542c\u8bf4\u8fd8\u6709\u4e9b\u4f59\u515a\uff0c\u4f46\u4e5f\u57fa\u672c\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\uff1b\u4ed6\u4e5f\u6ca1\u6709\u795e\u804c\uff0c\u6ca1\u6709\u795e\u57df\uff0c\u6ca1\u6709\u4fe1\u5f92\uff0c\u6ca1\u6709\u76df\u53cb\uff0c\u6ca1\u6709\u52a9\u624b\uff0c\u53ea\u6709\u654c\u4eba\u2014\u2014\u7b80\u800c\u8a00\u4e4b\u4e00\u53e5\u8bdd\uff0c\u4ed6\u65e9\u5df2\u7ecf\u4e0b\u53f0\uff0c\u5982\u4eca\u4e0d\u8fc7\u662f\u4e2a\u68a6\u60f3\u590d\u8f9f\u7684\u5b64\u9b42\u91ce\u9b3c\uff0c\u53ea\u8981\u5e72\u6389\u4ed6\uff0c\u4e00\u5207\u9ebb\u70e6\u5c31\u8fce\u5203\u800c\u89e3\uff0c\u5176\u4ed6\u4ec0\u4e48\u95ee\u9898\u90fd\u4e0d\u7528\u8003\u8651\u3002</p><p>\u201c\u4f46\u4f60\u600e\u4e48\u5bf9\u4ed8\u4ed6\u5462\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u8fd9\u4e0d\u96be\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u6709\u4e00\u79cd\u65b9\u6cd5\uff0c\u80fd\u591f\u5bf9\u4ed8\u795e\u529b\uff0c\u4f60\u77e5\u9053\u7684\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u53ef\u662f\u2026\u2026\u201d\u6885\u83f2\u65af\u76b1\u7709\uff0c\u201c\u8fd9\u6761\u8def\u6211\u4eec\u4ee5\u524d\u4e0d\u4e5f\u60f3\u8fc7\u5417\u3002\u201d</p><p>\u5728\u9634\u9b42\u57ce\u7684\u65f6\u5019\uff0c\u743c\u6069\u66fe\u7ecf\u62ff\u83f2\u5a05\u505a\u8fc7\u8bd5\u9a8c\uff0c\u8bc1\u5b9e\u4ed6\u5b66\u7684\u90a3\u4ec0\u4e48\u91c7\u7389\u8bc0\u5176\u5b9e\u662f\u4e00\u79cd\u5438\u6536\u6d88\u5316\u795e\u529b\u7684\u65b9\u6cd5\u3002\u5f53\u65f6\u4e5f\u52a8\u8fc7\u5ff5\u5934\uff0c\u60f3\u80fd\u591f\u901a\u8fc7\u8fd9\u79cd\u65b9\u6cd5\u5c06\u6885\u83f2\u65af\u4f53\u5185\u7684\u6740\u622e\u795e\u529b\u6d88\u53bb\uff0c\u4f46\u548c\u5979\u4e00\u8c08\u624d\u77e5\u9053\u884c\u4e0d\u901a\u3002\u539f\u56e0\u5f88\u7b80\u5355\uff0c\u6885\u83f2\u65af\u7684\u6bcd\u4eb2\u5728\u4e34\u7ec8\u524d\uff0c\u5728\u5979\u8eab\u4e0a\u4e0b\u4e86\u4e00\u4e2a\u8bc5\u5492\uff0c\u5018\u82e5\u5979\u548c\u743c\u6069\u771f\u6b63\u4ea4\u5408\uff0c\u4f53\u5185\u7684\u6740\u622e\u795e\u529b\u5c31\u4f1a\u81ea\u52a8\u90c1\u79ef\uff0c\u51dd\u7ed3\u6210\u80ce\u513f\u964d\u751f\u2014\u2014\u8fd9\u4e2a\u80ce\u513f\u5c31\u662f\u590d\u6d3b\u7684\u90aa\u795e\u3002</p><p>\u743c\u6069\u66fe\u7ecf\u5f88\u5947\u602a\u4e00\u4ef6\u4e8b\uff1a\u5df4\u5c14\u662f\u6740\u622e\u4e4b\u795e\uff0c\u4f46\u5374\u4f3c\u4e4e\u548c\u201c\u751f\u6b96\u201d\u8054\u7cfb\u5728\u4e00\u8d77\uff0c\u4ed6\u590d\u6d3b\u7684\u624b\u6cd5\uff0c\u5c31\u662f\u5148\u751f\u80b2\u5927\u91cf\u5b50\u55e3\uff0c\u7136\u540e\u4e92\u76f8\u6b8b\u6740\uff1b\u5982\u4eca\u4ed6\u7684\u9009\u6c11\u5bf9\u81ea\u5df1\u5973\u513f\u4e0b\u8bc5\u5492\uff0c\u4e5f\u662f\u4ee5\u4ea4\u5408\u4e3a\u89e6\u53d1\u6761\u4ef6\uff0c\u4ee5\u751f\u80b2\u4e3a\u8868\u73b0\u5f62\u5f0f\u3002\u5bf9\u4e8e\u8fd9\u4e00\u70b9\uff0c\u6885\u83f2\u65af\u5012\u4e5f\u505a\u8fc7\u89e3\u91ca\u3002</p><p>\u201c\u5df4\u5c14\u662f\u6740\u622e\u4e4b\u795e\uff0c\u4f46\u5728\u6709\u4e9b\u5730\u533a\uff0c\u4ed6\u5374\u4e5f\u540c\u65f6\u662f\u88ab\u4f5c\u4e3a\u751f\u6b96\u4e0e\u4e30\u9976\u4e4b\u795e\u6765\u5d07\u62dc\u7684\u3002\u5df4\u5c14\u6559\u4f1a\u7684\u796d\u7940\u548c\u5e86\u5178\uff0c\u5f80\u5f80\u4e5f\u90fd\u6709\u6027\u7231\u72c2\u6b22\u7684\u5185\u5bb9\uff0c\u4ee5\u6b64\u53d6\u60a6\u795e\u7947\u3002\u201d</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u53cd\u6b63\u6885\u83f2\u65af\u7684\u6bcd\u4eb2\u4e34\u7ec8\u524d\u8fd8\u7ed9\u81ea\u5df1\u5973\u513f\u7559\u4e0b\u4e86\u4e00\u4e2a\u5927\u9ebb\u70e6\u3002</p><p>\u4e16\u754c\u4e0a\u7684\u8bc5\u5492\u6709\u5f88\u591a\u79cd\uff0c\u800c\u6885\u83f2\u65af\u8eab\u4e0a\u7684\u8fd9\u4e2a\uff0c\u6beb\u65e0\u7591\u95ee\u662f\u6700\u5f3a\u7684\u90a3\u4e00\u7c7b\u3002\u5979\u6bcd\u4eb2\u662f\u5df4\u5c14\u9009\u6c11\uff0c\u5b9e\u529b\u5f3a\u5927\uff0c\u53c8\u662f\u4e34\u7ec8\u524d\u4ee5\u81ea\u5df1\u7684\u7075\u9b42\u4e3a\u4ee3\u4ef7\u4e0b\u5492\uff0c\u539f\u672c\u5c31\u5df2\u7ecf\u5f88\u68d8\u624b\uff1b\u800c\u4e14\u8bc5\u5492\u8005\u548c\u88ab\u8bc5\u5492\u8005\u53c8\u662f\u6bcd\u5973\uff0c\u662f\u8840\u8109\u81f3\u4eb2\uff0c\u8fd9\u66f4\u8ba9\u8bc5\u5492\u7684\u6548\u529b\u5f97\u5230\u8fdb\u4e00\u6b65\u5f3a\u5316\u2014\u2014\u5728\u9b54\u6cd5\u5b66\u4e0a\u8fd9\u6709\u4e2a\u4e13\u95e8\u7684\u6982\u5ff5\uff0c\u53eb\u505a\u8840\u4eb2\u8bc5\u5492\uff0c\u6bd4\u666e\u901a\u7684\u8bc5\u5492\u66f4\u96be\u5bf9\u4ed8\u5341\u500d\u3002\u968f\u7740\u65f6\u95f4\u7684\u63a8\u79fb\uff0c\u8bc5\u5492\u6df1\u6df1\u6d78\u900f\u8840\u8109\uff0c\u548c\u6740\u622e\u795e\u529b\u878d\u4e3a\u4e00\u4f53\uff0c\u5df2\u7ecf\u662f\u6839\u672c\u65e0\u6cd5\u7834\u9664\u4e86\u3002</p><p>\u5f53\u7136\uff0c\u8bc5\u5492\u6ca1\u6cd5\u76f4\u63a5\u5f3a\u884c\u7834\u9664\uff0c\u4e0d\u7b49\u4e8e\u4e0d\u80fd\u8eb2\u907f\uff0c\u66f4\u4e0d\u7b49\u4e8e\u4e0d\u80fd\u91dc\u5e95\u62bd\u85aa\u3002\u6885\u83f2\u65af\u6bcd\u4eb2\u4e0b\u8bc5\u5492\u7684\u610f\u56fe\uff0c\u5e76\u4e0d\u662f\u771f\u6025\u7740\u60f3\u8ba9\u81ea\u5df1\u5973\u513f\u65e9\u70b9\u6000\u5b55\u751f\u5b50\uff0c\u800c\u662f\u4e3a\u4e86\u8ba9\u5df4\u5c14\u590d\u6d3b\uff0c\u5177\u4f53\u7684\u5f62\u5f0f\u5c31\u662f\u6885\u83f2\u65af\u4f53\u5185\u7684\u6740\u622e\u795e\u529b\u81ea\u52a8\u51dd\u7ed3\u6210\u80ce\u513f\u964d\u751f\u3002\u90a3\u4e48\u53ea\u8981\u628a\u795e\u529b\u6d88\u9664\uff0c\u81ea\u7136\u5c31\u4e00\u5207\u95ee\u9898\u90fd\u4e0d\u5b58\u5728\u4e86\u3002</p><p>\u6839\u636e\u838e\u73de\u514b\u548c\u83f2\u5a05\u8eab\u4e0a\u5f97\u6765\u7684\u7ecf\u9a8c\uff0c\u743c\u6069\u662f\u53ef\u4ee5\u5982\u6cd5\u70ae\u5236\uff0c\u5438\u6536\u6d88\u5316\u6389\u6885\u83f2\u65af\u4f53\u5185\u7684\u795e\u529b\uff0c\u4f46\u8fd9\u4e2a\u8fc7\u7a0b\u4f1a\u5f88\u6f2b\u957f\u3002\u7c97\u7565\u4f30\u7b97\u8d77\u6765\uff0c\u5c11\u8bf4\u4e5f\u5f97\u4e09\u5e74\u4e94\u8f7d\uff0c\u751a\u81f3\u5341\u5e74\u516b\u5e74\u90fd\u6709\u53ef\u80fd\uff0c\u800c\u8bc5\u5492\u4e00\u65e6\u89e6\u53d1\uff0c\u4e00\u5e74\u4e4b\u540e\u90aa\u795e\u80ce\u513f\u4fbf\u4f1a\u964d\u751f\uff0c\u65f6\u95f4\u4e0a\u6839\u672c\u6765\u4e0d\u53ca\u3002</p><p>\u201c\u8fd9\u6837\u662f\u6765\u4e0d\u53ca\uff0c\u4f46\u5982\u679c\u6362\u79cd\u65b9\u5f0f\u5462\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d\u6885\u83f2\u65af\u4e0d\u89e3\u3002</p><p>\u201c\u6211\u638c\u63e1\u7684\u8fd9\u79cd\u65b9\u6cd5\uff0c\u5176\u5b9e\u662f\u6709\u4e24\u4e2a\u90e8\u5206\u7684\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u7b2c\u4e00\u6b65\u662f\u8f6c\u79fb\uff0c\u7b2c\u4e8c\u6b65\u662f\u6d88\u5316\u3002\u201d</p><p>\u201c\u7530\u4f2f\u5149\u201d\u6559\u7ed9\u743c\u6069\u7684\u90a3\u6240\u8c13\u91c7\u7389\u8bc0\uff0c\u6709\u4e24\u4e2a\u6b65\u9aa4\uff0c\u9996\u5148\u8981\u628a\u5973\u6027\u4f53\u5185\u7684\u795e\u529b\u8f6c\u79fb\u5230\u81ea\u8eab\uff0c\u7136\u540e\u518d\u5c06\u795e\u529b\u9010\u6e10\u6d88\u878d\uff0c\u5316\u5f52\u5df1\u8eab\u3002\u8fd9\u201c\u8f6c\u79fb\u201d\u548c\u201c\u6d88\u5316\u201d\u7684\u4e24\u90e8\u5206\u529f\u6cd5\uff0c\u5728\u6b63\u5e38\u60c5\u51b5\u4e0b\u81ea\u7136\u662f\u524d\u540e\u8854\u63a5\uff0c\u914d\u5408\u4f7f\u7528\uff0c\u4f46\u5018\u82e5\u5355\u72ec\u628a\u540e\u8005\u62ff\u51fa\u6765\uff0c\u6559\u7ed9\u6885\u83f2\u65af\u5462\uff1f</p><p>\u7c97\u7565\u6bd4\u65b9\u7684\u8bdd\uff0c\u743c\u6069\u4fbf\u5982\u4e00\u4e2a\u8eab\u65e0\u5206\u6587\u7684\u7a77\u5149\u86cb\uff0c\u5fc5\u987b\u5148\u53bb\u4ed6\u4eba\u8eab\u4e0a\u7a83\u53d6\uff0c\u7136\u540e\u518d\u901a\u8fc7\u624b\u6bb5\u628a\u94b1\u6d17\u767d\uff0c\u65b9\u624d\u53ef\u4ee5\u653e\u5fc3\u5927\u80c6\u5730\u4f7f\u7528\u3002\u4f46\u5bf9\u4e8e\u6885\u83f2\u65af\u800c\u8a00\uff0c\u524d\u4e00\u4e2a\u6b65\u9aa4\u5b8c\u5168\u4e0d\u9700\u8981\uff0c\u5979\u65e9\u5c31\u5750\u62e5\u91d1\u5c71\u94f6\u6d77\uff0c\u53ea\u8981\u628a\u9ed1\u94b1\u6d17\u767d\u5c31\u884c\u3002</p><p>\u62e5\u6709\u540c\u6837\u795e\u529b\u7684\u5df4\u5c14\u5b50\u55e3\u4e4b\u95f4\uff0c\u662f\u80fd\u591f\u76f8\u4e92\u611f\u5e94\u7684\u3002\u743c\u6069\u80fd\u591f\u628a\u838e\u73de\u514b\u548c\u83f2\u5a05\u7684\u795e\u529b\u8f6c\u79fb\u5230\u81ea\u5df1\u4f53\u5185\uff0c\u800c\u6885\u83f2\u65af\u5b8c\u5168\u611f\u5e94\u4e0d\u5230\u534a\u70b9\u6c14\u606f\u2014\u2014\u636e\u6b64\u63a8\u65ad\uff1a\u743c\u6069\u7684\u8fd9\u5957\u91c7\u7389\u8bc0\uff0c\u4e0d\u5149\u6709\u7a83\u53d6\u7684\u4f5c\u7528\uff0c\u8fd8\u6709\u6d17\u94b1\u7684\u529f\u80fd\u3002</p><p>\u201c\u53ef\u662f\uff0c\u201d\u6885\u83f2\u65af\u76b1\u7709\uff0c\u201c\u6211\u8bb0\u5f97\u4f60\u4ee5\u524d\u8bf4\u7684\u662f\uff1a\u56e0\u4e3a\u4f60\u4f53\u8d28\u7279\u6b8a\uff0c\u5bf9\u795e\u672f\u6709\u5929\u751f\u514d\u75ab\u80fd\u529b\uff0c\u6240\u4ee5\u80fd\u591f\u6d88\u878d\u795e\u529b\u2026\u2026\u201d</p><p>\u743c\u6069\u8f7b\u8f7b\u6447\u5934\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u9053\u6b49\uff0c\u201c\u6211\u5f53\u65f6\u6ca1\u6709\u5b8c\u5168\u8bf4\u5b9e\u8bdd\uff0c\u9690\u7792\u4e86\u4e00\u70b9\u3002\u800c\u4e14\u2026\u2026\u5f53\u65f6\u6211\u81ea\u5df1\u4e5f\u4e0d\u80fd\u786e\u5b9a\uff0c\u4e0d\u77e5\u9053\u5230\u5e95\u662f\u56e0\u4e3a\u6211\u7684\u7279\u6b8a\u4f53\u8d28\u7684\u7f18\u6545\uff0c\u8fd8\u662f\u90a3\u65b9\u6cd5\u7684\u7f18\u6545\uff0c\u6216\u8005\u662f\u4e24\u79cd\u540c\u65f6\u4f5c\u7528\u7684\u7ed3\u679c\u3002\u201d</p><p>\u201c\u90a3\u4f60\u73b0\u5728\u80fd\u591f\u786e\u5b9a\u4e86\uff1f\u548c\u4f60\u7684\u4f53\u8d28\u65e0\u5173\uff0c\u53ea\u548c\u65b9\u6cd5\u672c\u8eab\u6709\u5173\uff1f\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\u3002</p><p>\u6885\u83f2\u65af\u5947\u602a\uff0c\u201c\u600e\u4e48\u786e\u5b9a\u7684\u5462\u3002\u201d</p><p>\u201c\u8428\u9a6c\u65af\u7279\u544a\u8bc9\u6211\u7684\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4e0a\u6b21\u5728\u65ad\u57df\u9547\uff0c\u8428\u9a6c\u65af\u7279\u6765\u627e\u6211\uff0c\u8981\u6211\u914d\u5408\u4ed6\u5bf9\u4ed8\u6b23\u5e03\u5973\u58eb\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u5f53\u65f6\u4ed6\u7ed9\u4e86\u6211\u4e00\u5f20\u9ed1\u8272\u5377\u8f74\uff0c\u91cc\u9762\u662f\u4ed6\u7684\u884c\u52a8\u8ba1\u5212\uff0c\u4f46\u5728\u7ed3\u5c3e\u4e4b\u5904\uff0c\u4ed6\u5199\u4e86\u4e00\u4e9b\u7279\u522b\u7684\u4e1c\u897f\u3002\u201d</p><p>\u5728\u90a3\u5f20\u9ed1\u8272\u5377\u8f74\u7684\u6700\u540e\u90e8\u5206\uff0c\u8428\u9a6c\u65af\u7279\u4f20\u6388\u4e86\u4e00\u79cd\u80fd\u591f\u62b5\u5fa1\u7f13\u89e3\u94f6\u706b\u4f24\u5bb3\u7684\u65b9\u6cd5\u3002</p><p>\u8fd9\u65b9\u6cd5\u81ea\u7136\u5f88\u795e\u5999\uff0c\u80fd\u591f\u62b5\u6297\u94f6\u706b\u4e5f\u662f\u4ef6\u5f88\u4e86\u4e0d\u8d77\u7684\u53d1\u660e\uff0c\u4f46\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u4ed6\u771f\u6b63\u9707\u60ca\u7684\uff0c\u662f\u91cc\u9762\u7684\u6587\u5b57\u975e\u5e38\u719f\u6089\uff0c\u5176\u4e2d\u5f88\u591a\u8bcd\u53e5\uff0c\u548c\u201c\u7530\u4f2f\u5149\u201d\u6559\u7ed9\u4ed6\u7684\u90a3\u5957\u4e1c\u897f\u4e00\u6a21\u4e00\u6837\u3002\u743c\u6069\u5f53\u65f6\u8be2\u95ee\u8428\u9a6c\u65af\u7279\uff0c\u5f97\u5230\u7684\u56de\u7b54\u662f\u2014\u2014\u201c\u56e0\u4e3a\u90a3\u672c\u6765\u5c31\u662f\u6211\u7684\u53d1\u660e\uff0c\u662f\u6211\u6559\u7ed9\u5e03\u96f7\u7eb3\u65af\u7684\uff0c\u5f53\u7136\u5e76\u4e0d\u5b8c\u5168\u3002\u201d</p><p>\u636e\u8428\u9a6c\u65af\u7279\u6240\u8bf4\uff0c\u5f53\u5e74\uff08\u8428\u9a6c\u65af\u7279\u8fd8\u662f\u9009\u6c11\u7684\u65f6\u5019\uff09\u4ed6\u53bb\u67d0\u4e2a\u9057\u8ff9\u76d7\u5893\uff0c\u5e03\u96f7\u7eb3\u65af\u6765\u7269\u8d28\u754c\u8003\u53e4\uff0c\u4e24\u4eba\u5076\u7136\u76f8\u9047\uff0c\u76f8\u8c08\u751a\u6b22\u3002\u5e03\u96f7\u7eb3\u65af\u7528\u4e00\u4e9b\u8010\u745f\u745e\u5c14\u7684\u9b54\u6cd5\u8d44\u6599\uff0c\u548c\u8428\u9a6c\u65af\u7279\u4ea4\u6362\u4e86\u4e00\u4e9b\u6709\u5173\u795e\u529b\u672c\u8d28\u548c\u8fd0\u7528\u7684\u5fc3\u5f97\uff0c\u5176\u4e2d\u9644\u6709\u4ed6\u53d1\u660e\u7684\u201c\u6df1\u5ea6\u6697\u793a\u672f\u201d\u3002</p><p>\u6b23\u5e03\u4e5f\u66fe\u7ecf\u8bf4\u8fc7\uff1a\u8428\u9a6c\u65af\u7279\u201c\u4f3c\u4e4e\u662f\u53d1\u660e\u4e86\u67d0\u79cd\u5947\u602a\u7684\u9644\u9b54\u672f\uff0c\u4e00\u79cd\u81ea\u6211\u6697\u793a\u6216\u8005\u8bf4\u5f3a\u5236\u50ac\u7720\u7684\u65b9\u6cd5\u2026\u2026\u6211\u4eec\u6000\u7591\u4ed6\u6700\u7ec8\u6210\u529f\u7a83\u53d6\u94f6\u706b\uff0c\u548c\u8fd9\u79cd\u9644\u9b54\u672f\u4e5f\u4e0d\u65e0\u5173\u7cfb\u3002\u53e6\u5916\uff0c\u4f0a\u5c14\u660e\u65af\u7279\u8ba4\u4e3a\uff0c\u8428\u9a6c\u65af\u7279\u5f88\u6709\u53ef\u80fd\u83b7\u5f97\u4e86\u67d0\u4e9b\u53e4\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u7684\u9b54\u6cd5\u77e5\u8bc6\u3002\u201d</p><p>\u8428\u9a6c\u65af\u7279\u53d1\u660e\u4e86\u201c\u6df1\u5ea6\u6697\u793a\u201d\uff0c\u5728\u6b64\u57fa\u7840\u4e0a\u53c8\u7814\u7a76\u51fa\u4e86\u7be1\u593a\u795e\u529b\u7684\u65b9\u6cd5\u2014\u2014\u8fd9\u548c\u743c\u6069\u6240\u5b66\u4f55\u5176\u76f8\u4f3c\u3002\u6df1\u5ea6\u6697\u793a\u80fd\u591f\u8ba9\u8428\u9a6c\u65af\u7279\u4ece\u4e00\u4e2a\u91cd\u75c7\u75c5\u4eba\u77ac\u95f4\u53d8\u5f97\u7cbe\u795e\u5955\u5955\uff0c\u800c\u743c\u6069\u6240\u5b66\u7684\u201c\u5185\u529f\u201d\uff0c\u4e5f\u6b63\u662f\u80fd\u591f\u8ba9\u4ed6\u603b\u662f\u7cbe\u529b\u5145\u6c9b\uff0c\u51cf\u5c11\u7761\u7720\u2014\u2014\u8fd9\u4e24\u8005\u6839\u672c\u5c31\u662f\u4e00\u56de\u4e8b\uff0c\u672c\u8d28\u4e0a\u90fd\u662f\u8d85\u5f3a\u7684\u81ea\u6211\u50ac\u7720\u672f\uff0c\u4ee5\u900f\u652f\u751f\u547d\u529b\u4e3a\u4ee3\u4ef7\u3002\u800c\u6240\u8c13\u201c\u91c7\u7389\u8bc0\u201d\uff0c\u4e0d\u4e5f\u5c31\u662f\u5728\u201c\u5185\u529f\u201d\u7684\u57fa\u7840\u4e0a\u8f6c\u79fb\u6d88\u878d\u795e\u529b\u7684\u65b9\u6cd5\uff1f</p><p>\u6240\u6709\u7684\u7ebf\u7d22\u8ff9\u8c61\u8054\u63a5\u8d77\u6765\uff0c\u4e00\u4e2a\u4e8b\u5b9e\u5c31\u662d\u7136\u82e5\u63ed\u3002</p><p>\u591a\u5e74\u4ee5\u524d\uff0c\u8428\u9a6c\u65af\u7279\u548c\u5e03\u96f7\u7eb3\u65af\u76f8\u9047\uff0c\u5f7c\u6b64\u4ea4\u6362\u8d44\u6599\u5fc3\u5f97\uff0c\u4f46\u4ed6\u4eec\u90fd\u7559\u4e86\u4e00\u624b\uff0c\u6ca1\u6709\u7ed9\u5b8c\u5168\u7248\u672c\u3002\u4e8b\u540e\u53cc\u65b9\u5404\u81ea\u7814\u7a76\u9610\u53d1\uff0c\u8428\u9a6c\u65af\u7279\u6700\u7ec8\u6210\u529f\u5730\u53d1\u660e\u4e86\u7be1\u593a\u5973\u795e\u94f6\u706b\u7684\u65b9\u6cd5\uff0c\u800c\u9634\u9b42\u57ce\u5219\u5f04\u51fa\u4e86\u80fd\u591f\u5148\u8f6c\u79fb\u518d\u6d88\u878d\u795e\u529b\u7684\u6240\u8c13\u91c7\u7389\u8bc0\u3002</p><p>\u8fd9\u4fbf\u662f\u743c\u6069\u6240\u5b66\u7684\u5185\u529f\u3001\u91c7\u7389\u8bc0\u7684\u771f\u76f8\u3002</p><p>\u5f53\u65f6\u5728\u65ad\u57df\u9547\uff0c\u743c\u6069\u8868\u9762\u4e0a\u5e2e\u52a9\u8428\u9a6c\u65af\u7279\u5bf9\u4ed8\u6b23\u5e03\uff0c\u6700\u540e\u5374\u7a81\u7136\u53cd\u51fb\uff0c\u6574\u4e2a\u8fc7\u7a0b\u4e2d\u4ed6\u5e76\u6ca1\u6709\u5f53\u771f\u548c\u6b23\u5e03\u4ea4\u624b\uff0c\u62b5\u5fa1\u94f6\u706b\u7684\u65b9\u6cd5\u81ea\u7136\u4e5f\u5c31\u534a\u70b9\u6ca1\u7528\u4e0a\u3002\u4f46\u4ece\u5377\u8f74\u672b\u5c3e\u7684\u90a3\u6bb5\u6587\u5b57\u4e2d\uff0c\u743c\u6069\u63a8\u6572\u51fa\u4e86\u66f4\u591a\u7684\u4e1c\u897f\u3002</p><p>\u201c\u8fd9\u51e0\u4e2a\u6708\u6765\uff0c\u6211\u4e00\u76f4\u5728\u7422\u78e8\uff0c\u56de\u5fc6\u81ea\u5df1\u6240\u5b66\uff0c\u548c\u8428\u9a6c\u65af\u7279\u4f20\u6388\u7684\u4e1c\u897f\u76f8\u4e92\u5370\u8bc1\uff0c\u5f7c\u6b64\u53c2\u7167\uff0c\u4ee5\u524d\u5f88\u591a\u4e0d\u660e\u767d\u7684\u4e1c\u897f\u8c41\u7136\u5f00\u6717\uff0c\u603b\u7b97\u4e5f\u6709\u4e86\u70b9\u5fc3\u5f97\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u5fc3\u5f97\uff1f\u201d</p><p>\u743c\u6069\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u679a\u91d1\u5e01\uff0c\u201c\u795e\u529b\u5c31\u50cf\u8fd9\u679a\u91d1\u5e01\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u743c\u6069\u628a\u91d1\u5e01\u6258\u5728\u638c\u5fc3\uff0c\u5c06\u6b63\u9762\u5c55\u793a\u7ed9\u6885\u83f2\u65af\uff0c\u90a3\u91cc\u5370\u7740\u4e00\u53ea\u5c55\u7fc5\u6b32\u98de\u7684\u91d1\u9f99\u56fe\u6848\uff0c\u8fd9\u662f\u6df1\u6c34\u57ce\u53d1\u884c\u7684\u91d1\u9f99\u5e01\u3002\u6df1\u6c34\u57ce\u662f\u8d39\u4f26\u5927\u9646\u7b2c\u4e00\u5927\u57ce\u5e02\uff0c\u7b2c\u4e00\u5927\u6d77\u6e2f\uff0c\u53f7\u79f0\u5149\u8f89\u4e4b\u57ce\uff0c\u91d1\u9f99\u5e01\u4e5f\u56e0\u6b64\u6210\u4e3a\u901a\u884c\u5927\u9646\u7684\u51e0\u79cd\u4e3b\u8981\u8d27\u5e01\u4e4b\u4e00\uff0c\u5176\u5730\u4f4d\u8fd1\u4f3c\u4e8e\u5730\u7403\u4e0a\u7684\u7f8e\u5143\u3002</p><p>\u201c\u8fd9\u662f\u4e00\u679a\u91d1\u9f99\u5e01\uff0c\u901a\u884c\u5927\u9646\uff0c\u56e0\u4e3a\u6709\u6df1\u6c34\u57ce\u653f\u5e9c\u505a\u540e\u76fe\uff0c\u4efb\u4f55\u4eba\u62ff\u7740\u8fd9\u679a\u91d1\u9f99\u5e01\uff0c\u53ea\u8981\u4e0d\u53bb\u90a3\u4e9b\u86ee\u8352\u4e4b\u5730\uff0c\u5c31\u90fd\u80fd\u7528\u4ee5\u8d2d\u7269\u3001\u4ea4\u6613\u3001\u4ed8\u8d26\uff0c\u505a\u4e00\u5207\u80fd\u505a\u7684\u4e8b\u60c5\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u90a3\u4e48\u5982\u679c\u6709\u4e00\u5929\uff0c\u6df1\u6c34\u57ce\u7a81\u7136\u9881\u53d1\u547d\u4ee4\uff0c\u6240\u6709\u7684\u91d1\u9f99\u5e01\u4e00\u6982\u5931\u6548\u4f5c\u5e9f\uff0c\u4e0d\u5f97\u6d41\u901a\uff0c\u90a3\u5b83\u5c31\u5206\u6587\u4e0d\u503c\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u6c89\u541f\u7740\uff0c\u5979\u60f3\u8bf4\u5373\u4f7f\u5982\u6b64\uff0c\u91d1\u9f99\u5e01\u5728\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u8fd8\u662f\u4f1a\u79c1\u4e0b\u6d41\u901a\u7684\uff0c\u4e0d\u4f1a\u5b8c\u5168\u4f5c\u5e9f\uff0c\u4f46\u5979\u660e\u767d\u743c\u6069\u771f\u6b63\u8981\u8868\u8fbe\u7684\u610f\u601d\u3002</p><p>\u201c\u5bf9\uff0c\u7136\u540e\uff1f\u201d</p><p>\u201c\u90a3\u5982\u679c\u6211\u628a\u8fd9\u4e0a\u9762\u7684\u91d1\u9f99\u5370\u8bb0\u62b9\u53bb\u5462\uff1f\u201d</p><p>\u201c\u62b9\u53bb\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u62b9\u53bb\u91d1\u9f99\u5370\u8bb0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5b83\u662f\u91d1\u5e01\uff0c\u662f\u9ec4\u91d1\u6240\u5236\uff0c\u5b83\u672c\u8eab\u5c31\u6709\u4ef7\u503c\u3002\u5c31\u7b97\u5b83\u4e0d\u662f\u91d1\u5e01\uff0c\u53ea\u662f\u4e00\u4e2a\u91d1\u5757\uff0c\u4e5f\u662f\u53ef\u4ee5\u53bb\u8d2d\u7269\uff0c\u53bb\u4ed8\u8d26\u7684\uff0c\u867d\u7136\u53ef\u80fd\u8981\u8d2c\u503c\u4e00\u4e9b\u3002\u6df1\u6c34\u57ce\u53ef\u4ee5\u5e9f\u6b62\u91d1\u9f99\u5e01\uff0c\u4e5f\u53ef\u4ee5\u56de\u6536\uff0c\u4f46\u6211\u4eec\u53ea\u8981\u628a\u5b83\u7684\u5370\u8bb0\u62b9\u53bb\uff0c\u90a3\u4e48\u5b83\u5c31\u548c\u5bfb\u5e38\u7684\u91d1\u5757\u65e0\u5f02\uff0c\u5c5e\u4e8e\u6211\u4eec\u81ea\u5df1\u4e86\u3002\u201d</p><p>\u201c\u795e\u529b\u7684\u9053\u7406\uff0c\u5c31\u548c\u8fd9\u91d1\u5e01\u4e00\u6837\uff0c\u201d\u743c\u6069\u7ee7\u7eed\u8bf4\uff0c\u201c\u6bcf\u4e00\u4f4d\u795e\u7947\u7684\u795e\u529b\uff0c\u51dd\u805a\u4fe1\u4ef0\uff0c\u6e90\u81ea\u795e\u804c\uff0c\u5b83\u4eec\u90fd\u6709\u4e00\u4e2a\u2018\u5370\u8bb0\u2019\u2014\u2014\u8fd9\u662f\u4e00\u4e2a\u5047\u8bbe\uff0c\u6211\u4e0d\u80fd\u8bc1\u660e\u7ed9\u4f60\u770b\uff0c\u4f46\u5c31\u76ee\u524d\u7684\u4fe1\u606f\u6765\u63a8\u65ad\uff0c\u8fd9\u5e94\u8be5\u662f\u6ca1\u9519\u7684\uff0c\u81f3\u5c11\u53ef\u4ee5\u5b8c\u7f8e\u5730\u89e3\u91ca\u4e00\u5207\u3002\u201d</p><p>\u201c\u7ee7\u7eed\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u201c\u90a3\u4e48\u6211\u4eec\u53ef\u4ee5\u505a\u4e00\u4e2a\u6bd4\u55bb\uff0c\u5047\u8bbe\u795e\u7947\u5c31\u662f\u6df1\u6c34\u57ce\u653f\u5e9c\uff0c\u9009\u6c11\u662f\u5b98\u5458\uff0c\u800c\u8d50\u4e88\u7684\u795e\u529b\u5c31\u662f\u91d1\u9f99\u5e01\u3002\u90a3\u4e48\uff0c\u9009\u6c11\u4ece\u795e\u7947\u624b\u4e2d\u9886\u53d6\u4e86\u4e00\u7b14\u7ecf\u8d39\uff0c\u53ef\u4ee5\u7528\u6765\u505a\u5404\u79cd\u4e8b\u60c5\u3002\u91d1\u5e01\u7684\u4f7f\u7528\u6743\u6682\u65f6\u662f\u5b98\u5458\u624b\u4e2d\uff0c\u4f46\u771f\u6b63\u7684\u6240\u6709\u6743\u5219\u8fd8\u662f\u5c5e\u4e8e\u6df1\u6c34\u57ce\u3002\u53ea\u8981\u6df1\u6c34\u57ce\u653f\u5e9c\u613f\u610f\uff0c\u5b83\u5c31\u80fd\u591f\u968f\u65f6\u6536\u56de\u53bb\u3002\u800c\u8428\u9a6c\u65af\u7279\u7684\u53d1\u660e\uff0c\u4ee5\u53ca\u6211\u6240\u5b66\u7684\u65b9\u6cd5\uff0c\u7a76\u5176\u672c\u8d28\uff0c\u5176\u5b9e\u5c31\u662f\u4e00\u79cd\u2018\u62b9\u53bb\u5370\u8bb0\u2019\u7684\u624b\u6bb5\u3002\u62b9\u53bb\u91d1\u5e01\u4e0a\u7684\u91d1\u9f99\u5370\u8bb0\uff0c\u5207\u65ad\u5b83\u548c\u539f\u6240\u6709\u6743\u4eba\u7684\u8054\u7cfb\uff0c\u751a\u81f3\u7838\u788e\u3001\u56de\u7089\u7194\u70bc\uff0c\u53d8\u6210\u81ea\u5df1\u7684\u8d22\u4ea7\u3002\u201d</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u76b1\u7709\uff0c\u201c\u4f60\u8fd9\u4e2a\u5047\u8bbe\u53ef\u4ee5\u89e3\u91ca\u9009\u6c11\u7684\u795e\u529b\u2026\u2026\u4f46\u795e\u5b50\u5462\uff1f\u201d</p><p>\u201c\u9053\u7406\u662f\u4e00\u6837\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u9009\u6c11\u6240\u83b7\u5f97\u7684\u795e\u529b\u4e0a\u6709\u795e\u7684\u2018\u5370\u8bb0\u2019\uff0c\u6ca1\u6709\u6240\u6709\u6743\uff0c\u800c\u795e\u5b50\u6240\u62e5\u6709\u7684\u795e\u529b\uff0c\u662f\u6ca1\u6709\u5370\u8bb0\u7684\uff0c\u6240\u4ee5\u771f\u6b63\u5c5e\u4e8e\u81ea\u5df1\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u91cc\u5c31\u8bf4\u4e0d\u901a\u4e86\uff0c\u201d\u6885\u83f2\u65af\u51b7\u9759\u5730\u6307\u51fa\uff0c\u201c\u5982\u679c\u795e\u5b50\u5177\u6709\u7684\u795e\u529b\u6ca1\u6709\u5370\u8bb0\uff0c\u90a3\u4e48\u4f60\u53ea\u9700\u8981\u505a\u2018\u8f6c\u79fb\u2019\u7684\u5de5\u4f5c\u4e0d\u5c31\u8db3\u591f\u4e86\u5417\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u9700\u8981\u518d\u8fdb\u4e00\u6b65\u2018\u6d88\u878d\u2019\u5462\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u4eec\u662f\u7279\u4f8b\u3002\u201d</p><p>\u201c\u6211\u4eec\uff1f\u201d</p><p>\u201c\u4f60\u3001\u838e\u73de\u514b\u3001\u83f2\u5a05\uff0c\u4ee5\u53ca\u6240\u6709\u7684\u5df4\u5c14\u5b50\u55e3\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u4f60\u4eec\u4e0d\u662f\u901a\u5e38\u610f\u4e49\u4e0a\u7684\u795e\u5b50\uff0c\u800c\u662f\u5df4\u5c14\u4e3a\u4e86\u590d\u6d3b\u800c\u523b\u610f\u521b\u9020\u51fa\u6765\u7684\u3002\u4f60\u4eec\u6240\u62e5\u6709\u7684\u6bcf\u4e00\u4efd\u795e\u529b\u4e4b\u4e2d\uff0c\u90fd\u5fc5\u5b9a\u5bc4\u6258\u7740\u5df4\u5c14\u7684\u795e\u8bc6\uff0c\u954c\u523b\u7740\u5df4\u5c14\u7684\u5370\u8bb0\uff0c\u5426\u5219\u5b83\u5982\u4f55\u80fd\u591f\u81ea\u52a8\u878d\u5408\uff0c\u5982\u4f55\u80fd\u591f\u590d\u751f\uff0c\u5982\u4f55\u80fd\u591f\u4e0d\u53d7\u4f60\u7684\u63a7\u5236\uff1f\u201d</p><p>\u201c\u800c\u4f60\u62b9\u53bb\u4e86\u5370\u8bb0\uff0c\u6240\u4ee5\u5c3d\u7ba1\u5438\u6536\u4e86\u795e\u529b\uff0c\u6211\u5374\u611f\u5e94\u4e0d\u5230\u4e86\uff1f\u201d</p><p>\u743c\u6069\u70b9\u5934\uff0c\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d</p><p>\u6885\u83f2\u65af\u6c89\u601d\u4e0d\u8bed\uff0c\u743c\u6069\u6240\u8a00\uff0c\u786e\u5b9e\u662f\u79cd\u5047\u8bbe\uff0c\u5e76\u975e\u786e\u8bc1\u7684\u4e8b\u5b9e\uff0c\u4f46\u4e5f\u5e76\u975e\u5b8c\u5168\u7684\u51ed\u7a7a\u63e3\u6d4b\uff0c\u5b83\u6709\u6839\u636e\u6709\u6765\u6e90\uff0c\u800c\u4e14\u80fd\u591f\u5b8c\u7f8e\u5730\u89e3\u91ca\u9762\u4e34\u7684\u6240\u6709\u95ee\u9898\u3002\u4e00\u79cd\u7406\u8bba\u5982\u679c\u80fd\u591f\u89e3\u91ca\u6240\u6709\u7684\u95ee\u9898\uff0c\u90a3\u4e48\u5b83\u4e5f\u5c31\u63a5\u8fd1\u751a\u81f3\u7b49\u540c\u4e8e\u771f\u76f8\u4e86\u3002</p><p>\u201c\u7b80\u800c\u8a00\u4e4b\uff0c\u6211\u6709\u4e00\u79cd\u5bf9\u4ed8\u795e\u529b\u7684\u65b9\u6cd5\uff0c\u5206\u4e3a\u524d\u540e\u4e24\u4e2a\u90e8\u5206\uff0c\u662f\u53ef\u4ee5\u76f8\u4e92\u72ec\u7acb\u7684\uff0c\u524d\u9762\u7528\u4ee5\u8f6c\u79fb\uff0c\u540e\u9762\u7528\u4ee5\u6d88\u878d\uff0c\u201d\u743c\u6069\u6700\u540e\u603b\u7ed3\uff0c\u201c\u8fd9\u662f\u5b83\u539f\u672c\u5c31\u5177\u5907\u7684\u529f\u80fd\uff0c\u4e0d\u9700\u8981\u989d\u5916\u501f\u52a9\u4ec0\u4e48\u3002\u5c31\u50cf\u8428\u9a6c\u65af\u7279\uff0c\u4ed6\u80af\u5b9a\u4e5f\u6ca1\u6709\u514d\u75ab\u795e\u672f\u7684\u7279\u6b8a\u4f53\u8d28\uff0c\u5426\u5219\u8475\u9732\u5f53\u65f6\u5c31\u4e0d\u7528\u60cb\u60dc\u81ea\u5df1\u4e0d\u80fd\u4f7f\u7528\u795e\u672f\uff0c\u4f46\u4ed6\u4f9d\u65e7\u4e5f\u662f\u53ef\u4ee5\u62b9\u6d88\u795e\u529b\u5370\u8bb0\u7684\u3002\u201d</p><p>\u6885\u83f2\u65af\u7684\u773c\u795e\u6e10\u6e10\u53d8\u5f97\u9510\u5229\u8d77\u6765\uff0c\u5979\u5df2\u7ecf\u5b8c\u5168\u542c\u660e\u767d\u4e86\u743c\u6069\u7684\u610f\u601d\u3002</p><p>\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u53ea\u8981\u4f60\u628a\u540e\u9762\u8fd9\u90e8\u5206\u7684\u65b9\u6cd5\u6559\u7ed9\u6211\uff0c\u90a3\u4e48\u6211\u5c31\u53ef\u4ee5\u628a\u4f53\u5185\u7684\u6740\u622e\u795e\u529b\u6162\u6162\u6d88\u878d\uff0c\u62b9\u53bb\u5df4\u5c14\u7684\u5370\u8bb0\uff0c\u5f7b\u5e95\u6467\u6bc1\u6389\u4ed6\u7684\u5b58\u5728\u3002\u65e2\u4e0d\u9700\u8981\u501f\u52a9\u5916\u529b\uff0c\u4e5f\u4e0d\u4f1a\u89e6\u53d1\u8bc5\u5492\u2014\u2014\u662f\u8fd9\u6837\u6ca1\u9519\u5427\u3002\u201d</p><p>\u5979\u4ece\u5c0f\u591a\u5386\u8270\u8f9b\uff0c\u6027\u683c\u8f83\u5e38\u4eba\u6c89\u7a33\u5f97\u591a\uff0c\u65e0\u8bba\u9047\u5230\u5929\u5927\u53d8\u6545\u4e5f\u90fd\u662f\u9547\u9759\u81ea\u82e5\uff0c\u4e0d\u6025\u4e0d\u71e5\uff0c\u4f46\u6b64\u523b\u8bed\u6c14\u4e2d\u5374\u6709\u4e00\u4e1d\u538b\u6291\u4e0d\u4f4f\u7684\u6fc0\u52a8\u3002\u6740\u622e\u795e\u529b\uff0c\u90aa\u795e\u8840\u8109\uff0c\u5bf9\u4e8e\u5176\u4ed6\u4eba\u6765\u8bf4\u6216\u8bb8\u662f\u5929\u8d50\u7684\u793c\u7269\uff0c\u5bf9\u4e8e\u5979\u800c\u8a00\u5374\u662f\u65e0\u6cd5\u6446\u8131\u7684\u8bc5\u5492\uff0c\u662f\u7ae5\u5e74\u7684\u68a6\u9b47\u548c\u6c38\u6052\u7684\u6298\u78e8\uff0c\u4f46\u5374\u53c8\u59cb\u7ec8\u65e0\u53ef\u5948\u4f55\uff0c\u5982\u4eca\u542c\u95fb\u5c45\u7136\u5982\u6b64\u7b80\u5355\u5c31\u80fd\u6d88\u53bb\uff0c\u7eb5\u7136\u518d\u514b\u5236\uff0c\u4f9d\u65e7\u4e5f\u662f\u5fc3\u795e\u6fc0\u8361\uff0c\u4e0d\u80fd\u81ea\u6301\u3002\u5f53\u7136\u8fd9\u4e5f\u662f\u56e0\u4e3a\u8eab\u8fb9\u7684\u4eba\u662f\u743c\u6069\u7684\u7f18\u6545\u3002</p><p>\u743c\u6069\u7a0d\u7a0d\u8fdf\u7591\u4e86\u4e00\u4e0b\u3002</p><p>\u201c\u4ece\u7406\u8bba\u4e0a\u6765\u8bf4\uff0c\u5c31\u662f\u5982\u6b64\u3002\u4e0d\u8fc7\uff0c\u827e\u5f25\u8587\uff0c\u8fd9\u91cc\u8fd8\u6709\u4e24\u4e2a\u95ee\u9898\u2014\u2014\u4e24\u4e2a\u5c0f\u95ee\u9898\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u95ee\u9898\uff1f\u201d</p><p>\u201c\u9996\u5148\uff0c\u8fd9\u6bd5\u7adf\u53ea\u662f\u6211\u7684\u63a8\u65ad\u3002\u867d\u7136\u524d\u540e\u63a8\u6572\uff0c\u5e94\u8be5\u6ca1\u9519\uff0c\u4f46\u7ec8\u7a76\u4e0d\u80fd\u80af\u5b9a\u5c31\u4f1a\u6210\u529f\uff0c\u4e07\u4e00\u53d1\u751f\u4ec0\u4e48\u610f\u5916\u540e\u679c\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u4e00\u7b11\uff0c\u534a\u70b9\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u201c\u8fd8\u6709\u5462\uff1f\u201d</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u201c\u8fd8\u6709\u5c31\u662f\u2026\u2026\u8fd9\u662f\u4ee5\u7f29\u77ed\u5bff\u547d\u4e3a\u4ee3\u4ef7\u7684\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u91c7\u7389\u8bc0\u7684\u4f7f\u7528\uff0c\u662f\u5efa\u7acb\u5728\u5185\u529f\u7684\u57fa\u7840\u4e0a\u3002\u5148\u8981\u81ea\u5df1\u6709\u5185\u529b\u505a\u57fa\u7840\uff0c\u624d\u80fd\u591f\u5438\u6536\u878d\u5408\u5176\u4ed6\u5973\u5b50\u7684\u5185\u529b\uff1b\u81ea\u5df1\u7684\u5185\u529f\u8d8a\u5f3a\uff0c\u5219\u4e00\u6b21\u80fd\u591f\u5438\u6536\u7684\u5185\u529b\u8d8a\u591a\uff0c\u878d\u5408\u7684\u901f\u5ea6\u4e5f\u5c31\u8d8a\u5feb\u3002</p><p>\u8fd9\u662f\u743c\u6069\u4ee5\u524d\u6240\u542c\u5230\u7684\u7248\u672c\uff0c\u5982\u4eca\u65e9\u5c31\u660e\u767d\u6240\u8c13\u7684\u201c\u7530\u4f2f\u5149\u201d\u4e0d\u8fc7\u662f\u5e7b\u68a6\u4e00\u573a\u3002\u53ea\u662f\u7530\u4f2f\u5149\u662f\u5047\uff0c\u4e0d\u7b49\u4e8e\u4ed6\u6240\u6559\u7684\u4e1c\u897f\u4e5f\u662f\u5047\uff0c\u6548\u679c\u53ef\u662f\u5b9e\u5b9e\u5728\u5728\u7684\uff0c\u53ea\u662f\u4f2a\u6258\u540d\u5b57\uff0c\u5957\u4e86\u9a6c\u7532\u800c\u5df2\u3002</p><p>\u9634\u9b42\u57ce\u6559\u7ed9\u743c\u6069\u7684\u6240\u8c13\u201c\u5185\u529f\u201d\uff0c\u5b9e\u8d28\u4e0a\u5c31\u662f\u8428\u9a6c\u65af\u7279\u53d1\u660e\u7684\u201c\u6df1\u5ea6\u6697\u793a\u201d\uff0c\u5f53\u7136\u5e76\u4e0d\u5b8c\u5168\u7b49\u540c\uff0c\u5176\u4e2d\u7ecf\u8fc7\u6539\u9020\u3002\u4e0a\u9762\u8fd9\u6bb5\u8bdd\u91cd\u65b0\u7ffb\u8bd1\u8fc7\u6765\uff0c\u610f\u601d\u5c31\u662f\u8bf4\uff1a\u5438\u6536\u878d\u5408\u795e\u529b\u7684\u65b9\u6cd5\uff0c\u4ee5\u6df1\u5ea6\u6697\u793a\u4f5c\u4e3a\u57fa\u7840\u3002\u81ea\u6211\u6697\u793a\u7684\u7a0b\u5ea6\u8d8a\u5f3a\uff0c\u6d88\u5316\u795e\u529b\u7684\u901f\u5ea6\u4e5f\u5c31\u8d8a\u5feb\u3002</p><p>\u6240\u8c13\u6df1\u5ea6\u6697\u793a\uff0c\u4fbf\u662f\u4e00\u79cd\u8d85\u5f3a\u7684\u81ea\u6211\u50ac\u7720\u672f\uff0c\u80fd\u591f\u8ba9\u4eba\u8d85\u8d8a\u8089\u4f53\u6781\u9650\uff0c\u505a\u5230\u79cd\u79cd\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u60c5\u2014\u2014\u5b83\u5e2e\u52a9\u8428\u9a6c\u65af\u7279\u8fd9\u4e2a\u4e25\u91cd\u54ee\u5598\u517c\u795e\u7ecf\u8870\u5f31\u8005\u6210\u4e3a\u5927\u5deb\u5e08\uff0c\u5b83\u8ba9\u743c\u6069\u53ea\u9700\u8981\u4f11\u606f\u4e24\u4e2a\u5c0f\u65f6\u5c31\u80fd\u51c6\u5907\u9b54\u6cd5\uff0c\u800c\u4e0d\u662f\u6b63\u5e38\u7684\u516b\u4e2a\u5c0f\u65f6\u3002\u4f46\u5b83\u4e5f\u662f\u6709\u4ee3\u4ef7\u7684\uff0c\u5c31\u662f\u900f\u652f\u751f\u547d\u529b\u3002</p><p>\u8428\u9a6c\u65af\u7279\u5728\u4e09\u5341\u4e94\u5c81\u7684\u65f6\u5019\u53d1\u660e\u4e86\u8fd9\u79cd\u6df1\u5ea6\u6697\u793a\uff0c\u7136\u540e\u5728\u56db\u5341\u516d\u5c81\u7684\u65f6\u5019\u5c31\u5df2\u7ecf\u6cb9\u5c3d\u706f\u67af\uff0c\u5927\u9650\u5c06\u81f3\u3002\u8001\u5deb\u5996\u8c03\u914d\u4e86\u5404\u79cd\u5f3a\u529b\u7684\u9b54\u6cd5\u836f\u6c34\uff0c\u6bcf\u4e00\u79cd\u90fd\u80fd\u8ba9\u666e\u901a\u4eba\u5ef6\u5bff\u5341\u5e74\uff0c\u4f46\u5728\u4ed6\u8eab\u4e0a\u7684\u6548\u529b\u6700\u591a\u4e0d\u8d85\u8fc7\u5341\u5929\uff0c\u5c31\u8fd9\u6837\u82df\u5ef6\u6b8b\u5598\u5230\u4e86\u4e94\u5341\u5c81\uff0c\u6210\u4e3a\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\uff0c\u624d\u6682\u65f6\u6446\u8131\u4e86\u6b7b\u4ea1\u7684\u5a01\u80c1\u2014\u2014\u867d\u7136\u5230\u6700\u540e\u8fd8\u662f\u6b7b\u4e86\u3002\u743c\u6069\u6240\u5b66\u5df2\u7ecf\u662f\u7ecf\u8fc7\u9634\u9b42\u57ce\u6539\u826f\u8fc7\u7684\u7248\u672c\uff0c\u6548\u529b\u8f83\u5f31\uff0c\u526f\u4f5c\u7528\u4e5f\u76f8\u5bf9\u8f83\u5c0f\uff0c\u636e\u8428\u9a6c\u65af\u7279\u4f30\u8ba1\u80fd\u591f\u6d3b\u5230\u56db\u5341\u5c81\u3002</p><p>\u6b7b\u4ea1\u56fa\u7136\u4ee4\u4eba\u6050\u60e7\uff0c\u4f46\u82e5\u4e0d\u662f\u8feb\u5728\u7709\u776b\uff0c\u743c\u6069\u5012\u4e5f\u4e0d\u90a3\u4e48\u653e\u5728\u5fc3\u4e0a\uff0c\u4ed6\u5982\u4eca\u624d\u5341\u516d\u5c81\uff0c\u56db\u5341\u5c81\u6b7b\uff0c\u6216\u8005\u4e94\u5341\u5c81\u6b7b\uff0c\u6216\u8005\u4e03\u5341\u5c81\u516b\u5341\u5c81\u6b7b\uff0c\u611f\u89c9\u4e5f\u5dee\u4e0d\u591a\uff0c\u53cd\u6b63\u90fd\u662f\u201c\u8fd8\u65e9\u7740\u5462\u201d\u3002\u5c31\u50cf\u5f53\u5e74\u5728\u5b66\u6821\u91cc\u8bfb\u4e66\u65f6\uff0c\u8ddd\u79bb\u671f\u672b\u8003\u8bd5\u8fd8\u6709\u4e09\u4e2a\u6708\uff0c\u6216\u8005\u8fd8\u6709\u4e00\u4e2a\u6708\uff0c\u5927\u5bb6\u90fd\u4e0d\u5728\u610f\uff0c\u53ea\u6709\u7b49\u5230\u660e\u5929\u5c31\u8981\u4e0a\u8003\u573a\u4e86\u624d\u4f1a\u7d27\u5f20\u7740\u5fd9\u3002</p><p>\u6216\u8005\u8bf4\uff0c\u4e8b\u5df2\u81f3\u6b64\uff0c\u5c31\u7b97\u662f\u7740\u6025\u4e5f\u6ca1\u6709\u7528\u4e86\uff0c\u6162\u6162\u518d\u60f3\u529e\u6cd5\u4fbf\u662f\u3002</p><p>\u81ea\u5df1\u7684\u6027\u547d\u53ef\u4ee5\u4e0d\u5728\u4e4e\uff0c\u4e0d\u7b49\u4e8e\u60c5\u4eba\u7684\u751f\u6b7b\u53ef\u4ee5\u4e0d\u770b\u91cd\u3002\u89e3\u51b3\u6885\u83f2\u65af\u4f53\u5185\u9690\u60a3\u7684\u65b9\u6cd5\uff0c\u743c\u6069\u5e76\u4e0d\u662f\u4eca\u5929\u624d\u521a\u521a\u63a8\u65ad\u51fa\u6765\uff0c\u5728\u4e0b\u5c42\u754c\u7684\u8fd9\u51e0\u4e2a\u6708\u4e5f\u4e0d\u662f\u767d\u5f85\u7684\uff0c\u6574\u5929\u9664\u4e86\u6740\u4eba\uff0c\u5c31\u662f\u7422\u78e8\u8fd9\u4ef6\u4e8b\u3002\u4f46\u56e0\u4e3a\u201c\u6df1\u5ea6\u6697\u793a\u201d\u8fd9\u79cd\u5de8\u5927\u7684\u526f\u4f5c\u7528\uff0c\u4ee4\u4ed6\u4e00\u76f4\u72b9\u8c6b\u4e0d\u51b3\uff0c\u4e0d\u6562\u63d0\u8d77\u3002</p><p>\u201c\u90a3\u73b0\u5728\u4e3a\u4ec0\u4e48\u53c8\u544a\u8bc9\u6211\u5462\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u51db\u63d0\u9192\u4e86\u6211\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u5929\u51db\u65e0\u610f\u95f4\u8bf4\u8d77\u4f60\u5bb3\u6015\u5a74\u513f\uff0c\u6211\u624d\u4e0b\u5b9a\u4e86\u51b3\u5fc3\u3002\u201d</p><p>\u201c\u73b0\u5728\u4f60\u5c31\u4e0d\u62c5\u5fc3\u6211\u4f1a\u65e9\u65e9\u6b7b\u53bb\u5417\uff1f\u201d</p><p>\u201c\u4eba\u751f\u767e\u5e74\uff0c\u5306\u5306\u800c\u8fc7\uff0c\u6240\u6c42\u8005\u4e5f\u4e0d\u8fc7\u662f\u987a\u5fc3\u9002\u610f\uff0c\u5766\u7136\u4e0d\u6094\u3002\u90aa\u795e\u7684\u9634\u5f71\uff0c\u7ae5\u5e74\u7684\u5669\u68a6\uff0c\u9ed1\u6697\u7684\u8bc5\u5492\u2014\u2014\u80cc\u8d1f\u7740\u8fd9\u4e9b\uff0c\u4f60\u6c38\u8fdc\u6c38\u8fdc\u90fd\u6ca1\u529e\u6cd5\u771f\u6b63\u6253\u5fc3\u5e95\u5feb\u4e50\u8d77\u6765\u3002\u6d3b\u5f97\u8d8a\u4e45\uff0c\u8d8a\u662f\u6298\u78e8\uff0c\u53c8\u6709\u4ec0\u4e48\u610f\u601d\u3002\u201d</p><p>\u743c\u6069\u8f7b\u8f7b\u63e1\u4f4f\u5c11\u5973\u7684\u624b\uff0c\u201c\u6211\u7231\u4f60\uff0c\u62c5\u5fc3\u4f60\uff0c\u4e0d\u5e0c\u671b\u4f60\u53d7\u5230\u534a\u70b9\u4f24\u5bb3\uff0c\u6240\u4ee5\u6211\u4e00\u76f4\u4e0d\u6562\u628a\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u4f60\u3002\u4f46\u662f\u6211\u5728\u5fc3\u91cc\u95ee\uff1a\u6211\u559c\u6b22\u7684\u90a3\u4e2a\u5973\u5b69\u5b50\uff0c\u6211\u7231\u7740\u7684\u827e\u5f25\u8587\uff0c\u5979\u662f\u613f\u610f\u7ef4\u6301\u73b0\u72b6\uff0c\u7ee7\u7eed\u73b0\u5728\u7684\u751f\u6d3b\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u6218\u6218\u5162\u5162\u5730\u5ea6\u8fc7\u6f2b\u957f\u7684\u4e00\u8f88\u5b50\uff1b\u8fd8\u662f\u5e0c\u671b\u6211\u5e2e\u5979\u4ece\u7ae5\u5e74\u7684\u5669\u68a6\u4e2d\u771f\u6b63\u8d70\u51fa\uff0c\u5feb\u5feb\u4e50\u4e50\u9ad8\u9ad8\u5174\u5174\u5730\u4eab\u53d7\u6bcf\u4e00\u5929\uff0c\u54ea\u6015\u56e0\u6b64\u653e\u5f03\u4e00\u534a\u7684\u5bff\u547d\u5462\uff1f\u7136\u540e\u6211\u53d1\u73b0\uff1a\u5982\u679c\u8ba9\u827e\u5f25\u8587\u6765\u505a\u51b3\u5b9a\u7684\u8bdd\uff0c\u5979\u4e00\u5b9a\u4f1a\u60f3\u90fd\u4e0d\u60f3\u5c31\u9009\u62e9\u540e\u8005\uff0c\u534a\u70b9\u90fd\u4e0d\u72b9\u8c6b\u2026\u2026\u90a3\u4e48\uff0c\u6211\u53c8\u5728\u72b9\u8c6b\u4ec0\u4e48\u5462\u3002\u201d</p><p>\u6885\u83f2\u65af\u9759\u9759\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u201c\u5982\u679c\u4f60\u5bf9\u522b\u7684\u5973\u5b69\u5b50\u4e5f\u8fd9\u4e48\u8bf4\uff0c\u5979\u4e00\u5b9a\u4f1a\u5f88\u751f\u6c14\uff0c\u89c9\u5f97\u4f60\u4e0d\u591f\u7231\u5979\uff0c\u4e0d\u591f\u73cd\u60dc\u5979\u3002\u201d</p><p>\u201c\u5982\u679c\u9762\u5bf9\u522b\u7684\u5973\u5b69\u5b50\uff0c\u6211\u5c31\u4e0d\u4f1a\u8fd9\u4e48\u8bf4\u4e86\uff0c\u201d\u743c\u6069\u8f7b\u58f0\u56de\u7b54\uff0c\u201c\u6211\u4f1a\u76f4\u63a5\u66ff\u5979\u9009\u62e9\uff0c\u66ff\u5979\u51b3\u5b9a\u2014\u2014\u4f46\u4f60\u4e0d\u662f\u522b\u7684\u5973\u5b69\u5b50\uff0c\u4f60\u662f\u827e\u5f25\u8587\xb7\u6885\u83f2\u65af\u3002\u201d</p></article>"}}}]);
