(window.webpackJsonp=window.webpackJsonp||[]).push([[1585],{1608:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u56db\u8282 \u9b45\u9b54\u7684\u7f8e\u5b66",content:"<article><h2>\u7b2c\u4e09\u5341\u56db\u8282 \u9b45\u9b54\u7684\u7f8e\u5b66</h2><p>\u8299\u8389\u5a05\u4e0d\u8ba4\u8bc6\u838e\u73de\u514b\uff0c\u89c1\u5979\u7a81\u7136\u51fa\u73b0\u5728\u5bb6\u4e2d\uff0c\u81ea\u7136\u5341\u5206\u60ca\u8be7\uff0c\u4e00\u65f6\u95f4\u90fd\u5fd8\u4e86\u81ea\u5df1\u8fd8\u5728\u743c\u6069\u6000\u4e2d\uff0c\u6b63\u8981\u8d77\u8eab\u559d\u95ee\uff0c\u5374\u4e0d\u77e5\u600e\u7684\uff0c\u89c6\u7ebf\u548c\u9b45\u9b54\u7b11\u541f\u541f\u7684\u773c\u795e\u78b0\u89e6\uff0c\u987f\u65f6\u4e00\u79cd\u83ab\u540d\u7684\u4eb2\u5207\u611f\u6cb9\u7136\u800c\u751f\uff0c\u4eff\u4f5b\u662f\u9047\u89c1\u4e86\u6545\u4eba\u4f3c\u7684\u3002\u542c\u5230\u5bf9\u65b9\u8be2\u95ee\u201c\u9700\u8981\u6211\u5e2e\u5fd9\u5417\uff1f\u201d\u4e0d\u7531\u81ea\u4e3b\u5730\u4fbf\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u838e\u73de\u514b\u662f\u9b45\u9b54\uff0c\u50cf\u8fd9\u79cd\u8fc5\u901f\u74e6\u89e3\u5fc3\u9632\uff0c\u4ea7\u751f\u597d\u611f\uff0c\u539f\u672c\u662f\u62ff\u624b\u597d\u620f\u3002\u53ea\u662f\u743c\u6069\u6709\u610f\u5fd7\u5c4f\u969c\uff0c\u963b\u9694\u4e00\u5207\u7cbe\u795e\u4fb5\u88ad\uff0c\u6885\u83f2\u65af\u5fc3\u667a\u575a\u6bc5\u5982\u94c1\uff0c\u8fde\u7075\u5438\u602a\u7684\u5fc3\u7075\u9707\u7206\u90fd\u4e0d\u60e7\u6015\uff0c\u51db\u4e5f\u6709\u4e00\u534a\u7684\u5de8\u9f99\u8840\u8109\uff0c\u6297\u9b54\u80fd\u529b\u975e\u5e38\u4f18\u79c0\uff0c\u4ee5\u81f3\u4e8e\u838e\u73de\u514b\u7684\u5a9a\u60d1\u80fd\u529b\u5e73\u65f6\u6839\u672c\u5c31\u6ca1\u6709\u7528\u6b66\u4e4b\u5730\uff0c\u4f46\u8981\u5bf9\u4ed8\u8299\u8389\u5a05\uff0c\u8fd8\u662f\u8db3\u591f\u7684\u3002</p><p>\u5176\u5b9e\u8299\u8389\u5a05\u662f\u591c\u5973\u58eb\u7684\u7267\u5e08\uff0c\u867d\u7136\u540d\u4e49\u4e0a\u8fd8\u53ea\u662f\u201c\u5165\u591c\u8005\u201d\uff08\u89c1\u4e60\u7267\u5e08\uff09\uff0c\u4f46\u65e9\u5df2\u7ecf\u6709\u201c\u5b88\u591c\u8005\u201d\uff08\u6b63\u5f0f\u7267\u5e08\u4e2d\u7684\u521d\u9636\uff09\u7684\u5b9e\u529b\uff0c\u672c\u6765\u4e0d\u81f3\u4e8e\u8fd9\u4e48\u5bb9\u6613\u5c31\u8ba9\u838e\u73de\u514b\u5f97\u624b\u3002\u4f46\u5979\u6b64\u65f6\u88ab\u743c\u6069\u62b1\u4f4f\uff0c\u65e2\u7f9e\u53c8\u607c\uff0c\u504f\u751f\u529b\u6c14\u592a\u5c0f\uff0c\u6323\u8131\u4e0d\u5f00\uff0c\u6b63\u8feb\u5207\u5e0c\u671b\u7740\u6709\u4eba\u6765\u5e2e\u5fd9\uff0c\u838e\u73de\u514b\u7b49\u4e8e\u662f\u5929\u4e0a\u6389\u4e0b\u6765\u7684\u6551\u661f\uff0c\u6f5c\u610f\u8bc6\u91cc\u5c31\u503e\u5411\u76f8\u4fe1\uff0c\u81ea\u7136\u4e00\u62cd\u5373\u5408\uff0c\u6beb\u65e0\u60ac\u5ff5\u3002</p><p>\u7136\u800c\u5979\u6ca1\u6ce8\u610f\u5230\uff0c\u773c\u524d\u7684\u8fd9\u4f4d\u8910\u53d1\u7f8e\u5973\u5e76\u4e0d\u662f\u4eba\u7c7b\uff0c\u800c\u662f\u6076\u9b54\u2026\u2026</p><p>\u838e\u73de\u514b\u8d70\u5230\u5e8a\u8fb9\uff0c\u5374\u5e76\u6ca1\u6709\u5e2e\u5fd9\u63a8\u5f00\u743c\u6069\uff0c\u628a\u8299\u8389\u5a05\u6551\u51fa\u6765\uff0c\u53cd\u800c\u5f00\u59cb\u89e3\u5979\u7761\u8863\u7684\u6263\u5b50\u3002\u8299\u8389\u5a05\u5927\u60ca\u4e4b\u4e0b\uff0c\u6b32\u5f85\u6323\u624e\uff0c\u5374\u53c8\u88ab\u743c\u6069\u7d27\u7d27\u62b1\u7740\uff0c\u9b45\u9b54\u624b\u6307\u7075\u6d3b\uff0c\u52a8\u4f5c\u719f\u7ec3\uff0c\u8f6c\u773c\u95f4\u5df2\u7ecf\u5c06\u4e0a\u8863\u7684\u7ebd\u6263\u89e3\u5f00\u4e09\u7c92\uff0c\u9732\u51fa\u4fcf\u4e3d\u9999\u80a9\u548c\u7cbe\u81f4\u9501\u9aa8\uff0c\u80f8\u53e3\u8361\u8d77\u4e00\u62b9\u7389\u767d\uff0c\u9690\u9690\u7ea6\u7ea6\u80fd\u770b\u5230\u4e24\u9897\u5ae3\u7ea2\u3002</p><p>\u201c\u4e0d\u5c0f\u5462\u3002\u201d\u9b45\u9b54\u79f0\u8d5e\u3002</p><p>\u201c\u2026\u2026\u4f60\u5e72\u4ec0\u4e48\uff1f\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u201d</p><p>\u9707\u60ca\u4e4b\u4e0b\uff0c\u8299\u8389\u5a05\u4ece\u5a9a\u60d1\u4e2d\u6446\u8131\u51fa\u6765\uff0c\u63d0\u51fa\u4e86\u8fdf\u6765\u7684\u7591\u95ee\u3002\u838e\u73de\u514b\u683c\u683c\u5a07\u7b11\uff0c\u201c\u6211\u662f\u6765\u5e2e\u5fd9\u7684\u554a\u3002\u201d\u5979\u4e00\u8fb9\u56de\u7b54\u7740\uff0c\u624b\u4e0a\u52a8\u4f5c\u66f4\u52a0\u5feb\u51e0\u5206\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8299\u8389\u5a05\u7684\u4e0a\u8863\u7ebd\u6263\u5df2\u7ecf\u88ab\u5b8c\u5168\u89e3\u5f00\uff0c\u80f8\u524d\u6625\u8272\u4e00\u89c8\u65e0\u4f59\uff0c\u9b45\u9b54\u4fef\u8eab\u5728\u5979\u5507\u4e0a\u4eb2\u543b\u7740\uff0c\u4e00\u8fb9\u4f38\u624b\u8f7b\u8f7b\u53bb\u626f\u5979\u7684\u7761\u88e4\u3002</p><p>\u201c\u4f60\u4f4f\u624b\u2026\u2026\u5514\u2026\u2026\u201d</p><p>\u8299\u8389\u5a05\u4e00\u5f20\u53e3\uff0c\u9b45\u9b54\u7684\u820c\u5c16\u8d81\u673a\u4fb5\u5165\uff0c\u7075\u6d3b\u5730\u5728\u5979\u7684\u53e3\u8154\u91cc\u6e38\u52a8\u6311\u9017\u7740\uff0c\u52fe\u8d77\u5979\u7684\u6b32\u671b\uff0c\u8fc7\u4e86\u534a\u664c\u624d\u610f\u72b9\u672a\u5c3d\u5730\u5206\u5f00\uff0c\u4e00\u9053\u900f\u660e\u94f6\u4e1d\u8fde\u63a5\u7740\u4e24\u4eba\u7684\u820c\u5c16\uff0c\u95ea\u70c1\u7740\u6deb\u7cdc\u7684\u5149\u8292\u3002\u201c\u4e0d\u7528\u5bb3\u7f9e\uff0c\u83ab\u5c3c\u5361\u5c0f\u59d0\uff0c\u201d\u838e\u73de\u514b\u7b11\u563b\u563b\u5730\u8bf4\uff0c\u201c\u6211\u548c\u4f60\u4e00\u6837\uff0c\u4e5f\u662f\u4ed6\u7684\u60c5\u4eba\u3002\u201d</p><p>\u201c\u8c01\u662f\u4ed6\u7684\u60c5\u4eba\u2026\u2026\u201d</p><p>\u8299\u8389\u5a05\u6b63\u8ba4\u771f\u53cd\u9a73\uff0c\u7a81\u7136\u611f\u89c9\u4e0b\u8eab\u4e00\u51c9\uff0c\u539f\u6765\u838e\u73de\u514b\u8d81\u5979\u8bf4\u8bdd\u5206\u795e\u4e4b\u9645\uff0c\u5df2\u7ecf\u5c06\u5979\u7684\u7761\u88e4\u892a\u4e86\u4e0b\u6765\uff0c\u4e0a\u8863\u4e5f\u5728\u6323\u624e\u4e2d\u88ab\u5b8c\u5168\u655e\u5f00\uff0c\u5168\u8eab\u5df2\u7ecf\u8fd1\u4e4e\u8d64\u88f8\u3002\u201c\u8eab\u6750\u4e0d\u9519\u554a\uff0c\u201d\u9b45\u9b54\u5938\u5956\u7740\uff0c\u201c\u4ece\u5916\u9762\u8fd8\u771f\u770b\u4e0d\u51fa\u6765\u5462\u3002\u201d</p><p>\u201c\u653e\u624b\u2026\u2026\u4f60\u8ba4\u9519\u4eba\u4e86\u2026\u2026\u201d</p><p>\u8299\u8389\u5a05\u8fd8\u5728\u505a\u7740\u5f92\u52b3\u7684\u53cd\u6297\uff0c\u7136\u800c\u838e\u73de\u514b\u6839\u672c\u4e0d\u542c\u5206\u8fa9\uff0c\u4f4e\u5934\u53c8\u543b\u4e0a\u5979\u7684\u6a31\u5507\uff0c\u5c06\u540e\u9762\u7684\u8bdd\u76f4\u63a5\u5c01\u4f4f\u3002\u9b45\u9b54\u4e8e\u7537\u5973\u6027\u7231\u4e00\u9053\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u5404\u79cd\u8c03\u60c5\u624b\u6bb5\u7528\u51fa\u6765\uff0c\u518d\u52a0\u4e0a\u6cd5\u672f\u5a9a\u60d1\uff0c\u54ea\u91cc\u662f\u8299\u8389\u5a05\u80fd\u591f\u62b5\u6321\u7684\uff0c\u6655\u6655\u6c89\u6c89\u4fbf\u4e0d\u77e5\u8eab\u5904\u4f55\u5730\u3002\u597d\u5728\u743c\u6069\u6b64\u65f6\u5df2\u7ecf\u7761\u719f\uff0c\u4e5f\u6ca1\u8d81\u673a\u6765\u6b3a\u8d1f\u5979\u3002\u6b63\u6709\u4e9b\u52a8\u60c5\uff0c\u7a81\u7136\u4e0b\u4f53\u5fae\u5fae\u80c0\u75db\uff0c\u5374\u662f\u9b45\u9b54\u6084\u6084\u5c06\u624b\u6307\u63a2\u5165\u82b1\u854a\u4e2d\u3002\u8299\u8389\u5a05\u8fd8\u662f\u5904\u5b50\uff0c\u4ece\u672a\u7ecf\u4eba\u4e8b\uff0c\u867d\u7136\u56e0\u4e3a\u548c\u59b9\u59b9\u5fc3\u7075\u611f\u5e94\u7684\u5173\u7cfb\uff0c\u7b97\u662f\u95f4\u63a5\u5730\u88ab\u743c\u6069\u6b3a\u8d1f\u8fc7\u5f88\u591a\u6b21\uff0c\u4f46\u6bd5\u7adf\u6ca1\u6709\u771f\u5200\u771f\u67aa\u5730\u63a5\u89e6\uff0c\u8fd9\u4e00\u4e0b\u4fbf\u60ca\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u767b\u65f6\u6e05\u9192\u8fc7\u6765\uff0c\u62fc\u547d\u53d1\u529b\u8981\u5c06\u9b45\u9b54\u63a8\u5f00\u3002\u5979\u529b\u6c14\u592a\u5c0f\uff0c\u8f83\u4e4b\u838e\u73de\u514b\u6839\u672c\u4e0d\u5728\u4e00\u4e2a\u6863\u6b21\uff0c\u539f\u672c\u662f\u63a8\u4e0d\u5f00\u7684\uff0c\u7ed3\u679c\u9b45\u9b54\u5374\u4e5f\u4eff\u4f5b\u6709\u4e9b\u60ca\u8bb6\u7684\u6837\u5b50\uff0c\u81ea\u5df1\u5c06\u624b\u6307\u9000\u4e86\u51fa\u6765\u3002</p><p>\u201c\u4f60\u8fd8\u662f\u5904\u5973\u2026\u2026\u4f60\u4e0d\u662f\u8299\u857e\u72c4\xb7\u83ab\u5c3c\u5361\u5c0f\u59d0\uff1f\u201d</p><p>\u201c\u6211\u662f\u5979\u59d0\u59d0\uff01\u201d\u8299\u8389\u5a05\u59d4\u5c48\u5f97\u51e0\u4e4e\u8981\u54ed\u51fa\u6765\uff0c\u201c\u65e9\u8bf4\u4f60\u8ba4\u9519\u4eba\u4e86\uff01\u201d</p><p>\u201c\u62b1\u6b49\u62b1\u6b49\uff0c\u8bef\u4f1a\u4e86\u3002\u6211\u770b\u4f60\u548c\u4ed6\u62b1\u5728\u4e00\u8d77\uff0c\u8fd8\u4ee5\u4e3a\u662f\u8299\u857e\u72c4\u5c0f\u59d0\u3002\u201d</p><p>\u201c\u8c01\u548c\u4ed6\u62b1\u5728\u4e00\u8d77\u4e86\uff01\u660e\u660e\u662f\u8fd9\u4e2a\u6df7\u86cb\u2026\u2026\u201d</p><p>\u201c\u55ef\u55ef\uff0c\u6ca1\u9519\uff0c\u653e\u7740\u8fd9\u4e48\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u5728\u65c1\u8fb9\uff0c\u81ea\u5df1\u547c\u547c\u5927\u7761\uff0c\u8fd9\u4f55\u6b62\u662f\u6df7\u86cb\uff0c\u7b80\u76f4\u662f\u6ca1\u4eba\u6027\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u9b45\u9b54\u563b\u563b\u7b11\u7740\uff0c\u5c06\u743c\u6069\u7684\u80f3\u818a\u63b0\u5f00\uff0c\u968f\u624b\u62ff\u8d77\u5e8a\u8fb9\u7684\u6bdb\u7ed2\u73a9\u5177\u718a\u585e\u5728\u4ed6\u6000\u91cc\uff0c\u628a\u8299\u8389\u5a05\u89e3\u6551\u51fa\u6765\u3002\u8299\u8389\u5a05\u5168\u8eab\u8d64\u88f8\uff0c\u6ee1\u8138\u901a\u7ea2\uff0c\u72e0\u72e0\u5730\u77aa\u4e86\u838e\u73de\u514b\u4e00\u773c\uff0c\u5306\u5fd9\u7a7f\u597d\u8863\u670d\u8dd1\u51fa\u623f\u95f4\u53bb\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u5f53\u743c\u6069\u518d\u5ea6\u9192\u6765\u65f6\uff0c\u5df2\u7ecf\u662f\u7b2c\u4e8c\u5929\u7684\u4e2d\u5348\uff0c\u4ed6\u7684\u8111\u888b\u4e00\u9635\u9635\u5730\u9690\u9690\u4f5c\u75bc\uff0c\u597d\u50cf\u91cc\u9762\u6709\u4e00\u5343\u4e2a\u5c0f\u77ee\u4eba\u6b63\u5728\u62ff\u7740\u5de5\u5177\u6376\u6376\u6253\u6253\uff0c\u5f00\u51ff\u6d1e\u7a74\u4f3c\u7684\uff0c\u54bd\u5589\u4e5f\u662f\u706b\u8fa3\u8fa3\u5730\u75bc\u3002\u8fd9\u4ee4\u4ed6\u7684\u601d\u7ef4\u4f9d\u65e7\u8fd8\u662f\u5904\u4e8e\u534a\u505c\u6ede\u72b6\u6001\uff0c\u8ff7\u8ff7\u7cca\u7cca\u7741\u5f00\u773c\u770b\u7740\u5929\u82b1\u677f\uff0c\u8fc7\u4e86\u597d\u534a\u5929\u624d\u7a0d\u5fae\u56de\u8fc7\u795e\u6765\uff0c\u8f6c\u8fc7\u8138\u770b\u770b\u56db\u5468\uff0c\u53d1\u73b0\u81ea\u5df1\u4f3c\u4e4e\u662f\u7761\u5728\u5973\u5b69\u5b50\u7684\u95fa\u623f\u91cc\uff0c\u6000\u4e2d\u8fd8\u62b1\u7740\u4ec0\u4e48\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u662f\u4e00\u53ea\u5927\u5927\u7684\u6bdb\u7ed2\u73a9\u5177\u718a\u2026\u2026</p><p>\u4ed6\u5446\u6014\u4e86\u4e00\u4f1a\uff0c\u7136\u540e\u7ec8\u4e8e\u60f3\u8d77\u6628\u5929\u53d1\u751f\u7684\u4e8b\u60c5\u6765\uff1a\u81ea\u5df1\u5148\u662f\u5230\u4e86\u4f0a\u5361\u6c99\u57ce\uff0c\u7136\u540e\u53bb\u4e86\u738b\u5bab\uff0c\u6070\u597d\u649e\u4e0a\u5bb4\u4f1a\uff0c\u9003\u51fa\u6765\u540e\u56de\u5230\u4f4f\u5904\uff0c\u770b\u89c1\u8299\u857e\u72c4\uff0c\u62c9\u5979\u966a\u81ea\u5df1\u4f11\u606f\u2026\u2026\u55ef\uff0c\u8fd9\u4e48\u8bf4\u81ea\u5df1\u73b0\u5728\u662f\u5728\u8299\u857e\u72c4\u7684\u5367\u5ba4\u91cc\uff1f\u7136\u800c\u5c0f\u5973\u5b69\u5230\u54ea\u91cc\u53bb\u4e86\uff1f</p><p>\u6b63\u81ea\u7591\u60d1\uff0c\u623f\u95e8\u88ab\u8f7b\u8f7b\u63a8\u5f00\uff0c\u838e\u73de\u514b\u8d70\u4e86\u8fdb\u6765\uff0c\u201c\u65e9\u5b89\uff0c\u4e3b\u4eba\uff0c\u201d\u5979\u7b11\u7740\u8bf4\uff0c\u4fef\u8eab\u5728\u743c\u6069\u8138\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u201c\u4f60\u9192\u4e86\u554a\u3002\u201d</p><p>\u201c\u5514\uff0c\u62ff\u676f\u6c34\u7ed9\u6211\u3002\u201d</p><p>\u838e\u73de\u514b\u9012\u8fc7\u6c34\u676f\uff0c\u743c\u6069\u559d\u4e86\u4e24\u53e3\uff0c\u51b0\u51c9\u7684\u6db2\u4f53\u6d41\u5165\u4f53\u5185\uff0c\u8ba9\u4ed6\u66f4\u52a0\u6e05\u9192\u4e86\u51e0\u5206\u3002\u6b63\u8981\u8bf4\u8bdd\uff0c\u9b45\u9b54\u5374\u5148\u5f00\u53e3\u4e86\uff0c\u201c\u4e3b\u4eba\uff0c\u4f60\u771f\u4e86\u4e0d\u8d77\u5462\uff0c\u201d\u5979\u7528\u8d5e\u8d4f\u7684\u8bed\u6c14\u8bf4\uff0c\u201c\u4e00\u5bf9\u53cc\u80de\u80ce\uff0c\u73a9\u4e86\u59b9\u59b9\u8fd8\u4e0d\u591f\uff0c\u73b0\u5728\u53c8\u5bf9\u59d0\u59d0\u4e0b\u624b\uff0c\u771f\u662f\u7537\u6027\u7684\u6977\u6a21\uff0c\u8272\u72fc\u7684\u699c\u6837\u554a\u3002\u201d</p><p>\u201c\u54e6\uff0c\u6ca1\u4ec0\u4e48\uff0c\u4f60\u8fc7\u5956\u4e86\uff0c\u59d0\u59b9\u82b1\u672c\u6765\u5c31\u662f\u5e94\u8be5\u8981\u901a\u5403\u7684\u561b\uff0c\u5426\u5219\u5c82\u4e0d\u662f\u592a\u9057\u61be\u4e86\u2026\u2026\u7b49\u4e00\u4e0b\uff0c\u6211\u73a9\u4e86\u59b9\u59b9\u662f\u6ca1\u9519\uff0c\u4f46\u4ec0\u4e48\u65f6\u5019\u5bf9\u59d0\u59d0\u4e0b\u624b\u4e86\uff1f\u201d</p><p>\u201c\u6628\u5929\u554a\u3002\u201d</p><p>\u201c\u6628\u5929\uff1f\u6211\u600e\u4e48\u4e0d\u8bb0\u5f97\u6709\u8fd9\u56de\u4e8b\u2026\u2026\u201d</p><p>\u201c\u8fd9\u6837\u4e0d\u597d\u5427\uff0c\u4e3b\u4eba\uff0c\u201d\u838e\u73de\u514b\u4e25\u8083\u5730\u8bf4\uff0c\u201c\u65e2\u7136\u505a\u90fd\u505a\u4e86\uff0c\u600e\u4e48\u80fd\u4e0d\u627f\u8ba4\u5462\uff0c\u8fd9\u662f\u4e25\u91cd\u7684\u4e0d\u8d1f\u8d23\u4efb\u3002\u201d</p><p>\u743c\u6069\u52aa\u529b\u56de\u5fc6\u7740\uff0c\u7136\u800c\u8111\u4e2d\u4e00\u7247\u6df7\u4e71\uff0c\u770b\u770b\u81ea\u5df1\u8eab\u4e0a\uff0c\u8863\u670d\u4e5f\u8fd8\u662f\u7a7f\u5f97\u597d\u597d\u7684\uff0c\u5e94\u8be5\u5e76\u6ca1\u6709\u505a\u8fc7\u4ec0\u4e48\u624d\u5bf9\u3002\u201c\u6211\u5230\u5e95\u5e72\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u4f60\u628a\u5973\u5b69\u5b50\u62b1\u5728\u6000\u91cc\u62b1\u5f97\u90a3\u4e48\u7d27\uff0c\u62c9\u90fd\u62c9\u4e0d\u5f00\uff0c\u8fd8\u628a\u4eba\u5bb6\u8863\u670d\u90fd\u8131\u5149\u4e86\uff0c\u5c31\u5dee\u6700\u540e\u4e00\u6b65\u6ca1\u4e0a\u2014\u2014\u8fd9\u8fd8\u4e0d\u53eb\u505a\u5bf9\u5979\u4e0b\u624b\u5417\uff1f\u201d</p><p>\u201c\u6211\u62b1\u7684\u662f\u59b9\u59b9\u554a\uff0c\u600e\u4e48\u626f\u5230\u59d0\u59d0\u2026\u2026\u4e0d\u5bf9\uff0c\u201d\u8bf4\u4e86\u534a\u5929\u8bdd\uff0c\u743c\u6069\u7684\u8111\u5b50\u6e10\u6e10\u6e05\u695a\u8fc7\u6765\uff0c\u56de\u5fc6\u8d77\u6628\u5929\u7684\u7ec6\u8282\uff0c\u201c\u597d\u50cf\u6211\u786e\u5b9e\u662f\u5f04\u9519\u4eba\u4e86\uff0c\u628a\u8299\u8389\u5a05\u5f53\u6210\u4e86\u8299\u857e\u72c4\u2026\u2026\u201d</p><p>\u201c\u627f\u8ba4\u4e86\u5427\u3002\u201d</p><p>\u201c\u53ef\u662f\u4f60\u8bf4\u6211\u628a\u5979\u8131\u5149\u4e86\uff1f\u6211\u600e\u4e48\u534a\u70b9\u6ca1\u5370\u8c61\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u8bf4\u9519\u4e86\uff0c\u5979\u8863\u670d\u662f\u6211\u8131\u7684\u3002\u201d</p><p>\u201c\u554a\uff1f\u201d</p><p>\u201c\u6211\u56de\u6765\u7684\u65f6\u5019\uff0c\u542c\u5230\u6709\u52a8\u9759\u5c31\u4e0a\u697c\u770b\u770b\uff0c\u6b63\u770b\u89c1\u4f60\u4eec\u4e24\u4e2a\u4eba\u62b1\u5728\u4e00\u8d77\uff0c\u62c9\u62c9\u626f\u626f\u5730\u5f04\u4e0d\u6e05\u695a\u3002\u4f60\u4ee5\u524d\u4e0d\u662f\u548c\u6211\u63d0\u8fc7\u4e48\uff0c\u8bf4\u5728\u8fd9\u91cc\u6709\u4e2a\u5c0f\u60c5\u4eba\uff0c\u6211\u4ee5\u4e3a\u5c31\u662f\u5979\uff0c\u4e8e\u662f\u95ee\u8981\u4e0d\u8981\u5e2e\u5fd9\uff0c\u5979\u70b9\u5934\u8bf4\u8981\u2026\u2026\u201d</p><p>\u201c\u7136\u540e\u4f60\u5c31\u628a\u5979\u8863\u670d\u8131\u4e86\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u6211\u4ee5\u4e3a\u5979\u8981\u548c\u4f60\u4e0a\u5e8a\u561b\uff0c\u597d\u5fc3\u8fc7\u6765\u5e2e\u5fd9\u3002\u4f60\u5f53\u65f6\u9189\u5f97\u4e0d\u7701\u4eba\u4e8b\uff0c\u5979\u53c8\u4e0d\u613f\u610f\u8bf4\u6e05\u695a\uff0c\u6211\u600e\u4e48\u77e5\u9053\u3002\u6700\u540e\u65e0\u610f\u95f4\u53d1\u73b0\u5979\u8fd8\u662f\u4e2a\u5904\u5973\uff0c\u624d\u77e5\u9053\u5f04\u9519\u4eba\u4e86\u3002\u201d</p><p>\u201c\u4f60\u80af\u5b9a\u662f\u6545\u610f\u7684\u3002\u201d</p><p>\u743c\u6069\u548c\u838e\u73de\u514b\u4ee5\u524d\u804a\u5929\u95f2\u8c08\u65f6\uff0c\u4e5f\u66fe\u7ecf\u8bf4\u8d77\u8fc7\u83ab\u5c3c\u5361\u59d0\u59b9\u7684\u4e8b\u60c5\uff0c\u6240\u4ee5\u9b45\u9b54\u6e05\u695a\u662f\u77e5\u9053\u8299\u857e\u72c4\u8fd8\u6709\u4e2a\u53cc\u80de\u80ce\u59d0\u59d0\u7684\u3002\u867d\u7136\u7d20\u672a\u8c0b\u9762\uff0c\u4f46\u4ee5\u5979\u7684\u673a\u654f\u7075\u53d8\uff0c\u5bdf\u8a00\u89c2\u8272\uff0c\u4e0d\u53ef\u80fd\u5f53\u771f\u8ba4\u9519\u4eba\uff0c\u518d\u52a0\u4e0a\u743c\u6069\u5bf9\u8299\u8389\u5a05\u7684\u4e86\u89e3\uff0c\u6240\u8c13\u201c\u6211\u4ee5\u4e3a\u5979\u8981\u548c\u4f60\u4e0a\u5e8a\u201d\u3001\u201c\u5979\u53c8\u4e0d\u613f\u610f\u8bf4\u6e05\u695a\uff0c\u6211\u600e\u4e48\u77e5\u9053\u201d\u4e4b\u7c7b\u7684\u8bdd\uff0c\u7edd\u5bf9\u662f\u5728\u6b6a\u66f2\u4e8b\u5b9e\u3002</p><p>\u89c1\u88ab\u62c6\u7a7f\uff0c\u9b45\u9b54\u683c\u683c\u5a07\u7b11\uff0c\u201c\u597d\u5566\uff0c\u53ea\u662f\u89c9\u5f97\u6709\u8da3\u561b\uff0c\u9017\u9017\u5979\u800c\u5df2\u3002\u4e0d\u8fc7\u4e3b\u4eba\u554a\uff0c\u5173\u4e8e\u8fd9\u4ef6\u4e8b\uff0c\u6211\u8fd8\u771f\u662f\u633a\u60ca\u8bb6\u7684\u5462\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u8fd9\u6837\u4e00\u4e2a\u5c0f\u7f8e\u4eba\u513f\uff0c\u5728\u4f60\u8eab\u8fb9\u4e5f\u8fd9\u4e48\u4e45\u4e86\uff0c\u4f60\u5c45\u7136\u8fd8\u6ca1\u5bf9\u5979\u4e0b\u624b\uff0c\u771f\u662f\u4e0d\u53ef\u601d\u8bae\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff0c\u6211\u53c8\u4e0d\u662f\u63a8\u571f\u673a\uff0c\u96be\u9053\u89c1\u5230\u6f02\u4eae\u5973\u5b69\u5b50\u5c31\u4e00\u5b9a\u8981\u63a8\u5012\u4e0d\u6210\u3002\u201d</p><p>\u201c\u63a8\u571f\u673a\uff1f\u201d</p><p>\u201c\u63a8\u571f\u673a\u2026\u2026\u5c31\u662f\u4e00\u79cd\u7528\u6765\u63a8\u571f\u7684\u673a\u68b0\uff0c\u6216\u8005\u8bf4\u94c1\u9b54\u50cf\u3002\u603b\u4e4b\u8fd9\u4e2a\u4e0d\u662f\u91cd\u70b9\u5566\uff0c\u4e0d\u7528\u5728\u610f\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u9b45\u9b54\u70b9\u70b9\u5934\uff0c\u201c\u7136\u800c\u6211\u8fd8\u662f\u4e0d\u592a\u80fd\u660e\u767d\u2014\u2014\u4ece\u4f60\u521a\u624d\u7684\u8bdd\u91cc\uff0c\u6211\u662f\u5426\u53ef\u4ee5\u5f97\u51fa\u8fd9\u6837\u4e00\u4e2a\u7ed3\u8bba\uff1a\u4f60\u5e76\u4e0d\u662f\u6ca1\u6709\u80fd\u529b\u53bb\u63a8\u5012\u5979\uff0c\u53ea\u662f\u4e0d\u60f3\u662f\u5427\u3002\u201d</p><p>\u201c\u7b97\u662f\u5427\u3002\u201d</p><p>\u201c\u771f\u7684\u4e0d\u60f3\uff1f\u201d</p><p>\u743c\u6069\u660e\u663e\u72b9\u8c6b\u4e86\u4e00\u4e0b\u3002</p><p>\u201c\u4e00\u5bf9\u8fd9\u4e48\u6f02\u4eae\u7684\u59d0\u59b9\u82b1\u6446\u5728\u9762\u524d\uff0c\u4f60\u5c45\u7136\u80fd\u820d\u5f97\u4e0d\u53bb\u4e0b\u624b\uff1f\u65e2\u7136\u4f60\u5df2\u7ecf\u73a9\u8fc7\u59b9\u59b9\u4e86\uff0c\u96be\u9053\u5c31\u4e0d\u6253\u7b97\u518d\u5c1d\u5c1d\u59d0\u59d0\u7684\u6ecb\u5473\uff1f\u6216\u8005\u8bf4\uff0c\u4f60\u4e0d\u89c9\u5f97\u628a\u59d0\u59b9\u4fe9\u540c\u65f6\u5f04\u4e0a\u5e8a\u662f\u4e00\u4ef6\u975e\u5e38\u6109\u5feb\u7684\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u597d\u50cf\u4e5f\u4e0d\u80fd\u8bf4\u4e0d\u60f3\u2026\u2026\u201d</p><p>\u201c\u4f60\u770b\uff0c\u8fd9\u5c31\u662f\u4e86\uff0c\u201d\u838e\u73de\u514b\u76f4\u63a5\u6253\u65ad\uff0c\u201c\u4f60\u662f\u4e2a\u7537\u4eba\uff0c\u559c\u6b22\u6f02\u4eae\u5973\u5b69\u5b50\uff0c\u8fd9\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\u60c5\u3002\u800c\u5979\u662f\u4e2a\u5973\u4eba\uff0c\u8fd8\u662f\u4e2a\u6807\u81f4\u7684\u5c0f\u7f8e\u4eba\uff0c\u6210\u5929\u5728\u4f60\u9762\u524d\u6643\u6765\u6643\u53bb\uff0c\u5c31\u597d\u50cf\u628a\u4e00\u5757\u8089\u653e\u5728\u72fc\u7684\u9762\u524d\uff0c\u90a3\u4f60\u60f3\u4e0a\u5979\u662f\u7406\u6240\u5f53\u7136\uff0c\u4e0d\u60f3\u4e0a\u624d\u662f\u5c82\u6709\u6b64\u7406\u2014\u2014\u6211\u8bf4\u7684\u6ca1\u9519\u5427\u3002\u201d</p><p>\u201c\u597d\u5427\uff0c\u6ca1\u9519\uff0c\u201d\u743c\u6069\u627f\u8ba4\uff0c\u201c\u6211\u662f\u60f3\u4e0a\u5979\uff0c\u4f46\u90a3\u53c8\u600e\u4e48\u6837\u5462\uff1f\u201d</p><p>\u201c\u600e\u4e48\u6837\uff1f\u201d\u838e\u73de\u514b\u4eff\u4f5b\u542c\u5230\u4e86\u4ec0\u4e48\u6781\u5176\u53ef\u7b11\u7684\u8bdd\uff0c\u201c\u5f53\u7136\u662f\u8d76\u5feb\u53bb\u505a\u554a\u3002\u201d</p><p>\u201c\u4e16\u754c\u4e0a\u54ea\u6709\u8fd9\u4e48\u7b80\u5355\u7684\u4e8b\u60c5\uff0c\u201d\u743c\u6069\u6446\u6446\u624b\uff0c\u6709\u4e9b\u61d2\u5f97\u591a\u8bf4\uff0c\u201c\u4f60\u4e5f\u770b\u5230\u4e86\uff0c\u6211\u548c\u5979\u7684\u5173\u7cfb\uff0c\u5373\u4fbf\u4e0d\u8bf4\u6076\u52a3\uff0c\u81f3\u5c11\u4e5f\u7edd\u5bf9\u7b97\u4e0d\u4e0a\u826f\u597d\u3002\u201d</p><p>\u201c\u90a3\u4e0d\u662f\u6b63\u597d\u5417\uff1f\u201d</p><p>\u201c\u6b63\u597d\uff1f\u201d\u743c\u6069\u6115\u7136\uff0c\u201c\u4ec0\u4e48\u6b63\u597d\uff1f\u201d</p><p>\u201c\u4f60\u4eec\u5173\u7cfb\u4e0d\u4f73\uff0c\u4f60\u53c8\u60f3\u4e0a\u5979\uff0c\u8fd9\u5c31\u6b63\u9002\u5408\u53bb\u5f3a\u5978\u2014\u2014\u6240\u4ee5\u8bf4\u6b63\u597d\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u8fd9\u662f\u4ec0\u4e48\u903b\u8f91\u3002\u201d</p><p>\u201c\u6211\u6709\u4ec0\u4e48\u5730\u65b9\u8bf4\u5f97\u4e0d\u5bf9\u5417\uff1f\u201d\u9b45\u9b54\u53cd\u95ee\uff0c\u201c\u5982\u679c\u6211\u6ca1\u5f04\u9519\u7684\u8bdd\uff0c\u4f60\u5e94\u8be5\u8fd8\u6ca1\u771f\u6b63\u5f3a\u5978\u8fc7\u5973\u4eba\u5427\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6b63\u597d\u5c1d\u8bd5\u4f53\u9a8c\u4e00\u4e0b\u5462\u3002\u201d</p><p>\u201c\u6211\u6ca1\u505a\u8fc7\u7684\u4e8b\u60c5\u591a\u4e86\u53bb\u4e86\uff0c\u96be\u4e0d\u6210\u90fd\u8981\u5c1d\u8bd5\u4f53\u9a8c\u4e00\u4e0b\uff1f\u201d</p><p>\u201c\u90a3\u5f53\u7136\u672a\u5fc5\uff0c\u6709\u4e9b\u4e8b\u60c5\u4f60\u6ca1\u505a\u8fc7\uff0c\u4f46\u4e5f\u6ca1\u529e\u6cd5\u4f53\u9a8c\uff0c\u6bd4\u5982\u628a\u683c\u62c9\u5179\u7279\u75db\u6253\u4e00\u987f\uff1b\u6709\u4e9b\u4e8b\u60c5\u4f60\u6ca1\u505a\u8fc7\uff0c\u4e5f\u6709\u80fd\u529b\u5c1d\u8bd5\uff0c\u4f46\u76f8\u4fe1\u4f60\u4e0d\u4f1a\u6709\u5174\u8da3\uff0c\u6bd4\u5982\u5f3a\u5978\u7537\u4eba\u6216\u8005\u88ab\u7537\u4eba\u5f3a\u5978\u2026\u2026\u201d</p><p>\u743c\u6069\u548c\u838e\u73de\u514b\u76f8\u5904\u51e0\u4e2a\u6708\uff0c\u5bf9\u5979\u7684\u8bf4\u8bdd\u65b9\u5f0f\u65e9\u5df2\u4e60\u60ef\uff0c\u534a\u70b9\u4e0d\u52a8\u58f0\u8272\uff0c\u201c\u90a3\u5f53\u7136\uff0c\u6211\u53ea\u5bf9\u5f3a\u5978\u9b45\u9b54\u6709\u5174\u8da3\u3002\u201d</p><p>\u201c\u968f\u65f6\u6b22\u8fce\uff0c\u201d\u838e\u73de\u514b\u5a07\u7b11\uff0c\u201c\u4e0d\u8fc7\u6211\u4eec\u5148\u8ba8\u8bba\u8fd9\u4e2a\u95ee\u9898\u3002\u4f60\u770b\uff0c\u73b0\u5728\u6709\u4e00\u4e2a\u5c0f\u7f8e\u4eba\u5728\u4f60\u9762\u524d\uff0c\u4f60\u6709\u80fd\u529b\u53bb\u5f3a\u5978\u5979\uff0c\u76f8\u4fe1\u4e5f\u6709\u5174\u8da3\u53bb\u5f3a\u5978\u5979\u2014\u2014\u81f3\u5c11\u4e0d\u4f1a\u53cd\u611f\u5bf9\u5427\uff0c\u90a3\u4e48\u4f60\u5f53\u7136\u5c31\u5e94\u8be5\u53bb\u505a\u624d\u5bf9\uff0c\u8fd8\u72b9\u8c6b\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u6309\u4f60\u7684\u8bf4\u6cd5\uff0c\u51e1\u662f\u6211\u6709\u80fd\u529b\u53bb\u505a\u7684\uff0c\u4e0d\u53cd\u611f\u7684\u4e8b\u60c5\uff0c\u6211\u5c31\u90fd\u5e94\u8be5\u5c1d\u8bd5\u4f53\u9a8c\uff1f\u201d</p><p>\u201c\u5dee\u4e0d\u591a\uff0c\u66f4\u51c6\u786e\u7684\u8bf4\u6cd5\u662f\uff1a\u5982\u679c\u4f60\u6709\u80fd\u529b\uff0c\u90a3\u4e48\u4f60\u5c31\u5e94\u8be5\u503e\u5411\u4e8e\u53bb\u8fd0\u7528\uff0c\u56e0\u4e3a\u8fd9\u672c\u8eab\u5c31\u662f\u4e00\u79cd\u4e50\u8da3\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u201c\u4e0d\u660e\u767d\uff1f\u90a3\u4e48\u6211\u4e3e\u4e2a\u4f8b\u5b50\uff0c\u201d\u9b45\u9b54\u60f3\u4e86\u60f3\uff0c\u201c\u6bd4\u5982\u8bf4\uff0c\u5728\u4e00\u4e2a\u5f8b\u6cd5\u4e25\u660e\u7684\u56fd\u5ea6\uff0c\u672a\u7ecf\u5ba1\u5224\u800c\u6740\u4eba\u663e\u7136\u662f\u4e25\u91cd\u7684\u72af\u7f6a\uff0c\u4eba\u4eba\u5982\u6b64\u3002\u73b0\u5728\u5047\u8bbe\u4f60\u6709\u7279\u6743\uff0c\u53ef\u4ee5\u968f\u610f\u6740\u4eba\u800c\u4e0d\u53d7\u4efb\u4f55\u60e9\u7f5a\uff0c\u90a3\u4e48\u4e5f\u5c31\u662f\u8bf4\uff0c\u4f60\u6bd4\u522b\u4eba\u591a\u62e5\u6709\u4e00\u79cd\u80fd\u529b\uff0c\u90a3\u4e48\u4f60\u4f1a\u4e0d\u4f1a\u60f3\u53bb\u5b9e\u8df5\u8fd0\u7528\u4e00\u4e0b\uff0c\u53bb\u6740\u51e0\u4e2a\u4eba\u611f\u53d7\u611f\u53d7\uff1f\u201d</p><p>\u743c\u6069\u7565\u4f5c\u601d\u8003\uff0c\u201c\u4e0d\u77e5\u9053\u3002\u201d\u4ed6\u56de\u7b54\u3002</p><p>\u8fd9\u4e2a\u7b54\u6848\u663e\u7136\u4e0d\u7b26\u5408\u838e\u73de\u514b\u7684\u9884\u671f\uff0c\u6240\u4ee5\u5979\u6014\u4e86\u6014\uff0c\u968f\u5373\u53f9\u6c14\uff0c\u201c\u597d\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u770b\u6765\u6211\u7528\u4e86\u4e0d\u6b63\u786e\u7684\u4f8b\u5b50\uff0c\u90a3\u4e48\u6362\u4e00\u4e2a\uff0c\u6362\u4e00\u4e2a\u9002\u5408\u4f60\u7684\u3002\u5c31\u62ff\u51db\u6765\u8bf4\uff0c\u4f60\u5e94\u8be5\u8fd8\u6ca1\u6709\u7ed9\u5979\u540e\u9762\u5f00\u82de\u5427\u3002\u201d</p><p>\u201c\u6ca1\u6709\u3002\u201d</p><p>\u201c\u4f46\u4f60\u6709\u8fd9\u4e2a\u6253\u7b97\u5bf9\u5427\u3002\u201d</p><p>\u201c\u662f\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u5f88\u559c\u6b22\uff1f\u201d</p><p>\u201c\u90a3\u5012\u8c08\u4e0d\u4e0a\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u743c\u6069\u8003\u8651\u4e86\u4e00\u4e0b\u63aa\u8f9e\uff0c\u201c\u53cd\u6b63\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u559c\u6b22\u3002\u5982\u679c\u673a\u4f1a\u5408\u9002\uff0c\u90a3\u5c31\u73a9\u73a9\u770b\uff0c\u6362\u6362\u53e3\u5473\u4e5f\u4e0d\u9519\u3002\u201d</p><p>\u9b45\u9b54\u53cc\u624b\u4e00\u62cd\uff0c\u201c\u6ca1\u9519\u554a\uff0c\u5c31\u662f\u8fd9\u4e2a\u9053\u7406\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u53ef\u4ee5\u7528\u5979\u540e\u9762\uff0c\u8fd9\u662f\u4e00\u79cd\u80fd\u529b\uff0c\u4f60\u53ef\u4ee5\u653e\u5f03\uff0c\u4f46\u4f60\u5c31\u4f1a\u503e\u5411\u4e8e\u53bb\u4f7f\u7528\uff0c\u54ea\u6015\u4f60\u5176\u5b9e\u5e76\u4e0d\u662f\u7279\u522b\u559c\u6b22\uff0c\u56e0\u4e3a\u8fd9\u672c\u8eab\u5c31\u662f\u4e00\u79cd\u4e50\u8da3\u6240\u5728\uff0c\u5426\u5219\u5c82\u4e0d\u662f\u592a\u6d6a\u8d39\u4e86\u3002\u201d</p><p>\u201c\u4e5f\u5c31\u662f\u8bf4\uff1a\u6211\u60f3\u4e0a\u8299\u8389\u5a05\uff0c\u4f46\u5979\u4e0d\u613f\u610f\uff0c\u6240\u4ee5\u6211\u53ea\u80fd\u5f3a\u5978\u3002\u800c\u6211\u65e2\u7136\u6709\u8fd9\u4e2a\u80fd\u529b\uff0c\u53c8\u8fd8\u6ca1\u7528\u8fc7\uff0c\u6240\u4ee5\u6b63\u5e94\u8be5\u501f\u8fd9\u6b21\u7684\u673a\u4f1a\u6765\u5c1d\u8bd5\u4f53\u9a8c\uff1f\u201d</p><p>\u201c\u6b63\u786e\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u4e3b\u4eba\uff0c\u4f60\u4e0d\u89c9\u5f97\u8fd9\u6837\u624d\u822c\u914d\u5417\uff1f\u201d</p><p>\u201c\u4ec0\u4e48\u822c\u914d\uff1f\u201d</p><p>\u201c\u4f60\u770b\uff0c\u5979\u4eec\u662f\u4e00\u5bf9\u53cc\u80de\u80ce\u59d0\u59b9\u5bf9\u5427\uff0c\u5bb9\u8c8c\u8eab\u6750\u80af\u5b9a\u662f\u4e00\u6a21\u4e00\u6837\uff0c\u8fde\u4f60\u90fd\u5206\u8fa8\u4e0d\u51fa\u6765\uff0c\u90a3\u533a\u522b\u5c31\u53ea\u5728\u4e8e\u6027\u60c5\u6c14\u8d28\u4e86\u3002\u4f60\u5df2\u7ecf\u73a9\u8fc7\u59b9\u59b9\uff0c\u542c\u8bf4\u662f\u6e29\u9a6f\u4e56\u5de7\u7684\u7c7b\u578b\uff0c\u5bf9\u4f60\u5343\u4f9d\u767e\u987a\uff0c\u5982\u679c\u59d0\u59d0\u4e5f\u8fd9\u6837\uff0c\u6216\u8005\u53d8\u6210\u8fd9\u6837\uff0c\u90a3\u5c82\u4e0d\u662f\u592a\u65e0\u8da3\u4e86\u5417\uff0c\u4e24\u4e2a\u4eba\u4ece\u8eab\u4f53\u5230\u7cbe\u795e\u5b8c\u5168\u91cd\u590d\uff0c\u90a3\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\u3002\u5c31\u8981\u50cf\u73b0\u5728\u8fd9\u6837\u4e24\u4e2a\u6781\u7aef\uff0c\u8fe5\u7136\u76f8\u5f02\uff0c\u5404\u6709\u5343\u79cb\uff0c\u624d\u6bd4\u8f83\u7b26\u5408\u7f8e\u5b66\u561b\u3002\u201d</p><p>\u201c\u542c\u4f60\u7684\u610f\u601d\uff0c\u6211\u4e0d\u4f46\u5e94\u8be5\u53bb\u5f3a\u5978\u8299\u8389\u5a05\uff0c\u800c\u4e14\u8fd8\u662f\u5fc5\u987b\u5982\u6b64\uff0c\u5426\u5219\u5c31\u4e0d\u5408\u7f8e\u5b66\u4e86\uff1f\u201d</p><p>\u201c\u5bf9\u6781\u4e86\uff0c\u201d\u838e\u73de\u514b\u683c\u683c\u5a07\u7b11\uff0c\u201c\u6b63\u662f\u5e94\u8be5\u5982\u6b64\uff0c\u540c\u80de\u53cc\u751f\u7684\u59d0\u59b9\uff0c\u4e00\u6a21\u4e00\u6837\u7684\u8eaf\u4f53\uff0c\u622a\u7136\u76f8\u53cd\u7684\u7075\u9b42\uff0c\u5f7c\u6b64\u6096\u53cd\u7684\u906d\u9047\u548c\u547d\u8fd0\u3002\u4e00\u8fb9\u73a9\u7eaf\u7231\uff0c\u4e00\u8fb9\u73a9\u5f3a\u66b4\uff1b\u4e00\u8fb9\u4eab\u53d7\u6e29\u987a\u7684\u4f8d\u5949\uff0c\u4e00\u8fb9\u4f53\u9a8c\u5f81\u670d\u7684\u6109\u60a6\u2014\u2014\u4e3b\u4eba\uff0c\u8fd9\u624d\u662f\u4f60\u5e94\u8be5\u62e5\u6709\u7684\u4eba\u751f\u554a\u3002\u201d</p><p>\u201c\u5514\uff0c\u542c\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u597d\u50cf\u786e\u5b9e\u6709\u51e0\u5206\u9053\u7406\u7684\u6837\u5b50\u3002\u201d</p><p>\u743c\u6069\u5176\u5b9e\u8fd8\u6ca1\u5b8c\u5168\u6e05\u9192\uff0c\u4e0d\u8fc7\u8bf4\u4e86\u8fd9\u534a\u5929\u8bdd\uff0c\u8111\u4e2d\u6655\u6655\u6c89\u6c89\u7684\u611f\u89c9\u5df2\u7ecf\u6d88\u6563\u5927\u534a\u3002\u838e\u73de\u514b\u8bf4\u5f97\u5934\u5934\u662f\u9053\uff0c\u4ed6\u66f4\u591a\u662f\u968f\u53e3\u6577\u884d\uff0c\u5e76\u672a\u5f53\u771f\u7ec6\u60f3\u3002\u800c\u4e14\uff0c\u771f\u8981\u50cf\u838e\u73de\u514b\u8bf4\u7684\u53bb\u628a\u8299\u8389\u5a05\u5f3a\u66b4\u4e86\uff0c\u90a3\u600e\u4e48\u8ddf\u8299\u857e\u72c4\u89e3\u91ca\u2026\u2026\u7b49\u7b49\uff0c\u8299\u857e\u72c4\uff1f</p><p>\u4eff\u4f5b\u95ea\u7535\u63a0\u8fc7\u8111\u6d77\uff0c\u743c\u6069\u9661\u7136\u53cd\u5e94\u8fc7\u6765\uff0c\u53d1\u73b0\u81ea\u5df1\u5c45\u7136\u4e00\u76f4\u5ffd\u7565\u4e86\u4e00\u4e2a\u91cd\u8981\u95ee\u9898\u3002</p><p>\u8299\u857e\u72c4\u5230\u54ea\u91cc\u53bb\u4e86\uff1f</p></article>"}}}]);
